INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (8,1,'2',2,'Al-Baqarah','البَقَرَة',3,4,1,'الٓمٓۚ ذَٰلِكَ اَ۬لۡكِتَٰبُ لَا رَيۡبَۛ فِيهِۛ هُدٗى لِّلۡمُتَّقِينَ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (9,1,'2',2,'Al-Baqarah','البَقَرَة',4,5,2,'اَ۬لَّذِينَ يُؤۡمِنُونَ بِالۡغَيۡبِ وَيُقِيمُونَ اَ۬لصَّلَوٰةَ وَمِمَّا رَزَقۡنَٰهُمۡ يُنفِقُونَ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (10,1,'2',2,'Al-Baqarah','البَقَرَة',5,6,3,'وَاَلَّذِينَ يُؤۡمِنُونَ بِمَآ أُنزِلَ إِلَيۡكَ وَمَآ أُنزِلَ مِن قَبۡلِكَ وَبِالۡأٓخِرَةِ هُمۡ يُوقِنُونَ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (11,1,'2',2,'Al-Baqarah','البَقَرَة',7,8,4,'أُوْلَٰٓئِكَ عَلَىٰ هُدٗى مِّن رَّبِّهِمۡۖ وَأُوْلَٰٓئِكَ هُمُ اُ۬لۡمُفۡلِحُونَ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (12,1,'3',2,'Al-Baqarah','البَقَرَة',1,2,5,'إِنَّ اَ۬لَّذِينَ كَفَرُواْ سَوَآءٌ عَلَيۡهِمۡ ءَٰا۬نذَرۡتَهُمۡ أَمۡ لَمۡ تُنذِرۡهُمۡ لَا يُؤۡمِنُونَ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (13,1,'3',2,'Al-Baqarah','البَقَرَة',2,3,6,'خَتَمَ اَ۬للَّهُ عَلَىٰ قُلُوبِهِمۡ وَعَلَىٰ سَمۡعِهِمۡۖ وَعَلَىٰٓ أَبۡصٰ۪رِهِمۡ غِشَٰوَةٞۖ وَلَهُمۡ عَذَابٌ عَظِيمٞ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (14,1,'3',2,'Al-Baqarah','البَقَرَة',3,4,7,'وَمِنَ اَ۬لنّ۪اسِ مَن يَقُولُ ءَامَنَّا بِاللَّهِ وَبِالۡيَوۡمِ اِ۬لۡأٓخِرِ وَمَا هُم بِمُؤۡمِنِينَ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (15,1,'3',2,'Al-Baqarah','البَقَرَة',5,6,8,'يُخَٰدِعُونَ اَ۬للَّهَ وَاَلَّذِينَ ءَامَنُواْ وَمَا يُخَٰدِعُونَ إِلَّآ أَنفُسَهُمۡ وَمَا يَشۡعُرُونَ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (16,1,'3',2,'Al-Baqarah','البَقَرَة',6,7,9,'فِي قُلُوبِهِم مَّرَضٞ فَزَادَهُمُ اُ۬للَّهُ مَرَضٗاۖ وَلَهُمۡ عَذَابٌ أَلِيمُۢ بِمَا كَانُواْ يُكَذِّبُونَ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (17,1,'3',2,'Al-Baqarah','البَقَرَة',7,8,10,'وَإِذَا قِيلَ لَهُمۡ لَا تُفۡسِدُواْ فِي اِ۬لۡأَرۡضِ قَالُوٓاْ إِنَّمَا نَحۡنُ مُصۡلِحُونَ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (18,1,'3',2,'Al-Baqarah','البَقَرَة',8,9,11,'أَلَآ إِنَّهُمۡ هُمُ اُ۬لۡمُفۡسِدُونَ وَلَٰكِن لَّا يَشۡعُرُونَ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (19,1,'3',2,'Al-Baqarah','البَقَرَة',9,11,12,'وَإِذَا قِيلَ لَهُمۡ ءَامِنُواْ كَمَآ ءَامَنَ اَ۬لنَّاسُ قَالُوٓاْ أَنُؤۡمِنُ كَمَآ ءَامَنَ اَ۬لسُّفَهَآءُۗ اَ۬لَآ إِنَّهُمۡ هُمُ اُ۬لسُّفَهَآءُ وَلَٰكِن لَّا يَعۡلَمُونَ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (20,1,'3',2,'Al-Baqarah','البَقَرَة',11,13,13,'وَإِذَا لَقُواْ اُ۬لَّذِينَ ءَامَنُواْ قَالُوٓاْ ءَامَنَّا وَإِذَا خَلَوۡاْ إِلَىٰ شَيَٰطِينِهِمۡ قَالُوٓاْ إِنَّا مَعَكُمۡ إِنَّمَا نَحۡنُ مُسۡتَهۡزِءُونَ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (21,1,'3',2,'Al-Baqarah','البَقَرَة',13,14,14,'اَ۬للَّهُ يَسۡتَهۡزِئُ بِهِمۡ وَيَمُدُّهُمۡ فِي طُغۡيَٰنِهِمۡ يَعۡمَهُونَ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (22,1,'3',2,'Al-Baqarah','البَقَرَة',14,15,15,'أُوْلَٰٓئِكَ اَ۬لَّذِينَ اَ۪شۡتَرَوُاْ اُ۬لضَّلَٰلَةَ بِالۡهُدَىٰ فَمَا رَبِحَت تِّجَٰرَتُهُمۡ وَمَا كَانُواْ مُهۡتَدِينَ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (23,1,'4',2,'Al-Baqarah','البَقَرَة',1,2,16,'۞ مَثَلُهُمۡ كَمَثَلِ اِ۬لَّذِي اِ۪سۡتَوۡقَدَ نَارٗا فَلَمَّآ أَضَآءَتۡ مَا حَوۡلَهُۥ ذَهَبَ اَ۬للَّهُ بِنُورِهِمۡ وَتَرَكَهُمۡ فِي ظُلُمَٰتٖ لَّا يُبۡصِرُونَ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (24,1,'4',2,'Al-Baqarah','البَقَرَة',2,3,17,'صُمُّۢ بُكۡمٌ عُمۡيٞ فَهُمۡ لَا يَرۡجِعُونَ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (25,1,'4',2,'Al-Baqarah','البَقَرَة',3,5,18,'أَوۡ كَصَيِّبٖ مِّنَ اَ۬لسَّمَآءِ فِيهِ ظُلُمَٰتٞ وَرَعۡدٞ وَبَرۡقٞ يَجۡعَلُونَ أَصَٰبِعَهُمۡ فِيٓ ءَاذَانِهِم مِّنَ اَ۬لصَّوَٰعِقِ حَذَرَ اَ۬لۡمَوۡتِۚ وَاَللَّهُ مُحِيطُۢ بِالۡكٰ۪فِرِينَ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (26,1,'4',2,'Al-Baqarah','البَقَرَة',5,8,19,'يَكَادُ اُ۬لۡبَرۡقُ يَخۡطَفُ أَبۡصَٰرَهُمۡۖ كُلَّمَآ أَضَآءَ لَهُم مَّشَوۡاْ فِيهِ وَإِذَآ أَظۡلَمَ عَلَيۡهِمۡ قَامُواْۚ وَلَوۡ شَآءَ اَ۬للَّهُ لَذَهَبَ بِسَمۡعِهِمۡ وَأَبۡصٰ۪رِهِمۡۚ إِنَّ اَ۬للَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (27,1,'4',2,'Al-Baqarah','البَقَرَة',8,9,20,'يَٰٓأَيُّهَا اَ۬لنَّاسُ اُ۟عۡبُدُواْ رَبَّكُمُ اُ۬لَّذِي خَلَقَكُمۡ وَاَلَّذِينَ مِن قَبۡلِكُمۡ لَعَلَّكُمۡ تَتَّقُونَ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (28,1,'4',2,'Al-Baqarah','البَقَرَة',9,12,21,'اَ۬لَّذِي جَعَلَ لَكُمُ اُ۬لۡأَرۡضَ فِرَٰشٗا وَاَلسَّمَآءَ بِنَآءٗ وَأَنزَلَ مِنَ اَ۬لسَّمَآءِ مَآءٗ فَأَخۡرَجَ بِهِۦ مِنَ اَ۬لثَّمَرَٰتِ رِزۡقٗا لَّكُمۡۖ فَلَا تَجۡعَلُواْ لِلَّهِ أَندَادٗا وَأَنتُمۡ تَعۡلَمُونَ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (29,1,'4',2,'Al-Baqarah','البَقَرَة',12,14,22,'وَإِن كُنتُمۡ فِي رَيۡبٖ مِّمَّا نَزَّلۡنَا عَلَىٰ عَبۡدِنَا فَأۡتُواْ بِسُورَةٖ مِّن مِّثۡلِهِۦ وَاَدۡعُواْ شُهَدَآءَكُم مِّن دُونِ اِ۬للَّهِ إِن كُنتُمۡ صَٰدِقِينَ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (30,1,'4',2,'Al-Baqarah','البَقَرَة',14,15,23,'فَإِن لَّمۡ تَفۡعَلُواْ وَلَن تَفۡعَلُواْ فَاَتَّقُواْ اُ۬لنَّارَ اَ۬لَّتِي وَقُودُهَا اَ۬لنَّاسُ وَاَلۡحِجَارَةُۖ أُعِدَّتۡ لِلۡكٰ۪فِرِينَ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (31,1,'5',2,'Al-Baqarah','البَقَرَة',1,4,24,'وَبَشِّرِ اِ۬لَّذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ أَنَّ لَهُمۡ جَنَّٰتٖ تَجۡرِي مِن تَحۡتِهَا اَ۬لۡأَنۡهَٰرُۖ كُلَّمَا رُزِقُواْ مِنۡهَا مِن ثَمَرَةٖ رِّزۡقٗا قَالُواْ هَٰذَا اَ۬لَّذِي رُزِقۡنَا مِن قَبۡلُۖ وَأُتُواْ بِهِۦ مُتَشَٰبِهٗاۖ وَلَهُمۡ فِيهَآ أَزۡوَٰجٞ مُّطَهَّرَةٞۖ وَهُمۡ فِيهَا خَٰلِدُونَ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (32,1,'5',2,'Al-Baqarah','البَقَرَة',4,9,25,'۞ إِنَّ اَ۬للَّهَ لَا يَسۡتَحۡيِۦٓ أَن يَضۡرِبَ مَثَلٗا مَّا بَعُوضَةٗ فَمَا فَوۡقَهَاۚ فَأَمَّا اَ۬لَّذِينَ ءَامَنُواْ فَيَعۡلَمُونَ أَنَّهُ اُ۬لۡحَقُّ مِن رَّبِّهِمۡۖ وَأَمَّا اَ۬لَّذِينَ كَفَرُواْ فَيَقُولُونَ مَاذَآ أَرَادَ اَ۬للَّهُ بِهَٰذَا مَثَلٗاۘ يُضِلُّ بِهِۦ كَثِيرٗا وَيَهۡدِي بِهِۦ كَثِيرٗاۚ وَمَا يُضِلُّ بِهِۦٓ إِلَّا اَ۬لۡفَٰسِقِينَ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (33,1,'5',2,'Al-Baqarah','البَقَرَة',9,11,26,'اَ۬لَّذِينَ يَنقُضُونَ عَهۡدَ اَ۬للَّهِ مِنۢ بَعۡدِ مِيثَٰقِهِۦ وَيَقۡطَعُونَ مَآ أَمَرَ اَ۬للَّهُ بِهِۦٓ أَن يُوصَلَ وَيُفۡسِدُونَ فِي اِ۬لۡأَرۡضِۚ أُوْلَٰٓئِكَ هُمُ اُ۬لۡخَٰسِرُونَ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (34,1,'5',2,'Al-Baqarah','البَقَرَة',11,13,27,'كَيۡفَ تَكۡفُرُونَ بِاللَّهِ وَكُنتُمۡ أَمۡوَٰتٗا فَأَحۡيَٰكُمۡۖ ثُمَّ يُمِيتُكُمۡ ثُمَّ يُحۡيِيكُمۡ ثُمَّ إِلَيۡهِ تُرۡجَعُونَ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (35,1,'5',2,'Al-Baqarah','البَقَرَة',13,15,28,'هُوَ اَ۬لَّذِي خَلَقَ لَكُم مَّا فِي اِ۬لۡأَرۡضِ جَمِيعٗا ثُمَّ اَ۪سۡتَوَىٰٓ إِلَى اَ۬لسَّمَآءِ فَسَوَّىٰهُنَّ سَبۡعَ سَمَٰوَٰتٖۚ وَهۡوَ بِكُلِّ شَيۡءٍ عَلِيمٞ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (36,1,'6',2,'Al-Baqarah','البَقَرَة',1,3,29,'وَإِذۡ قَالَ رَبُّكَ لِلۡمَلَٰٓئِكَةِ إِنِّي جَاعِلٞ فِي اِ۬لۡأَرۡضِ خَلِيفَةٗۖ قَالُوٓاْ أَتَجۡعَلُ فِيهَا مَن يُفۡسِدُ فِيهَا وَيَسۡفِكُ اُ۬لدِّمَآءَ وَنَحۡنُ نُسَبِّحُ بِحَمۡدِكَ وَنُقَدِّسُ لَكَۖ قَالَ إِنِّيَ أَعۡلَمُ مَا لَا تَعۡلَمُونَ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (37,1,'6',2,'Al-Baqarah','البَقَرَة',3,5,30,'وَعَلَّمَ ءَادَمَ اَ۬لۡأَسۡمَآءَ كُلَّهَا ثُمَّ عَرَضَهُمۡ عَلَى اَ۬لۡمَلَٰٓئِكَةِ فَقَالَ أَنۢبِـُٔونِي بِأَسۡمَآءِ هَٰٓؤُلَآ إِن كُنتُمۡ صَٰدِقِينَ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (38,1,'6',2,'Al-Baqarah','البَقَرَة',5,6,31,'قَالُواْ سُبۡحَٰنَكَ لَا عِلۡمَ لَنَآ إِلَّا مَا عَلَّمۡتَنَآۖ إِنَّكَ أَنتَ اَ۬لۡعَلِيمُ اُ۬لۡحَكِيمُ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (39,1,'6',2,'Al-Baqarah','البَقَرَة',6,9,32,'قَالَ يَٰٓـَٔادَمُ أَنۢبِئۡهُم بِأَسۡمَآئِهِمۡۖ فَلَمَّآ أَنۢبَأَهُم بِأَسۡمَآئِهِمۡ قَالَ أَلَمۡ أَقُل لَّكُمۡ إِنِّيَ أَعۡلَمُ غَيۡبَ اَ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِ وَأَعۡلَمُ مَا تُبۡدُونَ وَمَا كُنتُمۡ تَكۡتُمُونَ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (40,1,'6',2,'Al-Baqarah','البَقَرَة',9,10,33,'۞ وَإِذۡ قُلۡنَا لِلۡمَلَٰٓئِكَةِ اِ۟سۡجُدُواْ لِأٓدَمَ فَسَجَدُوٓاْ إِلَّآ إِبۡلِيسَ أَبَىٰ وَاَسۡتَكۡبَرَ وَكَانَ مِنَ اَ۬لۡكٰ۪فِرِينَ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (41,1,'6',2,'Al-Baqarah','البَقَرَة',10,12,34,'وَقُلۡنَا يَٰٓـَٔادَمُ اُ۟سۡكُنۡ أَنتَ وَزَوۡجُكَ اَ۬لۡجَنَّةَ وَكُلَا مِنۡهَا رَغَدًا حَيۡثُ شِئۡتُمَا وَلَا تَقۡرَبَا هَٰذِهِ اِ۬لشَّجَرَةَ فَتَكُونَا مِنَ اَ۬لظَّٰلِمِينَ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (42,1,'6',2,'Al-Baqarah','البَقَرَة',12,14,35,'فَأَزَلَّهُمَا اَ۬لشَّيۡطَٰنُ عَنۡهَا فَأَخۡرَجَهُمَا مِمَّا كَانَا فِيهِۖ وَقُلۡنَا اَ۪هۡبِطُواْ بَعۡضُكُمۡ لِبَعۡضٍ عَدُوّٞۖ وَلَكُمۡ فِي اِ۬لۡأَرۡضِ مُسۡتَقَرّٞ وَمَتَٰعٌ إِلَىٰ حِينٖ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (43,1,'6',2,'Al-Baqarah','البَقَرَة',14,15,36,'فَتَلَقَّىٰٓ ءَادَمُ مِن رَّبِّهِۦ كَلِمَٰتٖ فَتَابَ عَلَيۡهِۚ إِنَّهُۥ هُوَ اَ۬لتَّوَّابُ اُ۬لرَّحِيمُ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (44,1,'7',2,'Al-Baqarah','البَقَرَة',1,2,37,'قُلۡنَا اَ۪هۡبِطُواْ مِنۡهَا جَمِيعٗاۖ فَإِمَّا يَأۡتِيَنَّكُم مِّنِّي هُدٗى فَمَن تَبِعَ هُدَايَ فَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (45,1,'7',2,'Al-Baqarah','البَقَرَة',2,3,38,'وَاَلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَآ أُوْلَٰٓئِكَ أَصۡحَٰبُ اُ۬لنّ۪ارِۖ هُمۡ فِيهَا خَٰلِدُونَ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (46,1,'7',2,'Al-Baqarah','البَقَرَة',4,5,39,'يَٰبَنِيٓ إِسۡرَٰٓءِيلَ اَ۟ذۡكُرُواْ نِعۡمَتِيَ اَ۬لَّتِيٓ أَنۡعَمۡتُ عَلَيۡكُمۡ وَأَوۡفُواْ بِعَهۡدِيٓ أُوفِ بِعَهۡدِكُمۡ وَإِيَّٰيَ فَاَرۡهَبُونِ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (47,1,'7',2,'Al-Baqarah','البَقَرَة',5,7,40,'وَءَامِنُواْ بِمَآ أَنزَلۡتُ مُصَدِّقٗا لِّمَا مَعَكُمۡ وَلَا تَكُونُوٓاْ أَوَّلَ كَافِرِۢ بِهِۦۖ وَلَا تَشۡتَرُواْ بِـَٔايَٰتِي ثَمَنٗا قَلِيلٗا وَإِيَّٰيَ فَاَتَّقُونِ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (48,1,'7',2,'Al-Baqarah','البَقَرَة',7,8,41,'وَلَا تَلۡبِسُواْ اُ۬لۡحَقَّ بِالۡبَٰطِلِ وَتَكۡتُمُواْ اُ۬لۡحَقَّ وَأَنتُمۡ تَعۡلَمُونَ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (49,1,'7',2,'Al-Baqarah','البَقَرَة',8,9,42,'وَأَقِيمُواْ اُ۬لصَّلَوٰةَ وَءَاتُواْ اُ۬لزَّكَوٰةَ وَاَرۡكَعُواْ مَعَ اَ۬لرَّٰكِعِينَ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (50,1,'7',2,'Al-Baqarah','البَقَرَة',9,10,43,'۞ أَتَأۡمُرُونَ اَ۬لنَّاسَ بِالۡبِرِّ وَتَنسَوۡنَ أَنفُسَكُمۡ وَأَنتُمۡ تَتۡلُونَ اَ۬لۡكِتَٰبَۚ أَفَلَا تَعۡقِلُونَ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (51,1,'7',2,'Al-Baqarah','البَقَرَة',11,11,44,'وَاَسۡتَعِينُواْ بِالصَّبۡرِ وَاَلصَّلَوٰةِۚ وَإِنَّهَا لَكَبِيرَةٌ إِلَّا عَلَى اَ۬لۡخَٰشِعِينَ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (52,1,'7',2,'Al-Baqarah','البَقَرَة',12,12,45,'اَ۬لَّذِينَ يَظُنُّونَ أَنَّهُم مُّلَٰقُواْ رَبِّهِمۡ وَأَنَّهُمۡ إِلَيۡهِ رَٰجِعُونَ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (53,1,'7',2,'Al-Baqarah','البَقَرَة',13,14,46,'يَٰبَنِيٓ إِسۡرَٰٓءِيلَ اَ۟ذۡكُرُواْ نِعۡمَتِيَ اَ۬لَّتِيٓ أَنۡعَمۡتُ عَلَيۡكُمۡ وَأَنِّي فَضَّلۡتُكُمۡ عَلَى اَ۬لۡعَٰلَمِينَ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (54,1,'7',2,'Al-Baqarah','البَقَرَة',14,15,47,'وَاَتَّقُواْ يَوۡمٗا لَّا تَجۡزِي نَفۡسٌ عَن نَّفۡسٖ شَيۡـٔٗا وَلَا تُقۡبَلُ مِنۡهَا شَفَٰعَةٞ وَلَا يُؤۡخَذُ مِنۡهَا عَدۡلٞ وَلَا هُمۡ يُنصَرُونَ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (55,1,'8',2,'Al-Baqarah','البَقَرَة',1,3,48,'وَإِذۡ نَجَّيۡنَٰكُم مِّنۡ ءَالِ فِرۡعَوۡنَ يَسُومُونَكُمۡ سُوٓءَ اَ۬لۡعَذَابِ يُذَبِّحُونَ أَبۡنَآءَكُمۡ وَيَسۡتَحۡيُونَ نِسَآءَكُمۡۚ وَفِي ذَٰلِكُم بَلَآءٞ مِّن رَّبِّكُمۡ عَظِيمٞ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (56,1,'8',2,'Al-Baqarah','البَقَرَة',3,4,49,'وَإِذۡ فَرَقۡنَا بِكُمُ اُ۬لۡبَحۡرَ فَأَنجَيۡنَٰكُمۡ وَأَغۡرَقۡنَآ ءَالَ فِرۡعَوۡنَ وَأَنتُمۡ تَنظُرُونَ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (57,1,'8',2,'Al-Baqarah','البَقَرَة',4,5,50,'وَإِذۡ وَعَدۡنَا مُوسۭيٰٓ أَرۡبَعِينَ لَيۡلَةٗ ثُمَّ اَ۪تَّخَذتُّمُ اُ۬لۡعِجۡلَ مِنۢ بَعۡدِهِۦ وَأَنتُمۡ ظَٰلِمُونَ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (58,1,'8',2,'Al-Baqarah','البَقَرَة',6,6,51,'ثُمَّ عَفَوۡنَا عَنكُم مِّنۢ بَعۡدِ ذَٰلِكَ لَعَلَّكُمۡ تَشۡكُرُونَ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (59,1,'8',2,'Al-Baqarah','البَقَرَة',7,7,52,'وَإِذۡ ءَاتَيۡنَا مُوسَى اَ۬لۡكِتَٰبَ وَاَلۡفُرۡقَانَ لَعَلَّكُمۡ تَهۡتَدُونَ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (60,1,'8',2,'Al-Baqarah','البَقَرَة',8,11,53,'۞ وَإِذۡ قَالَ مُوسۭيٰ لِقَوۡمِهِۦ يَٰقَوۡمِ إِنَّكُمۡ ظَلَمۡتُمۡ أَنفُسَكُم بِاتِّخَاذِكُمُ اُ۬لۡعِجۡلَ فَتُوبُوٓاْ إِلَىٰ بَارِئۡكُمۡ فَاَقۡتُلُوٓاْ أَنفُسَكُمۡ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ عِندَ بَارِئۡكُمۡ فَتَابَ عَلَيۡكُمۡۚ إِنَّهُۥ هُوَ اَ۬لتَّوَّابُ اُ۬لرَّحِيمُ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (61,1,'8',2,'Al-Baqarah','البَقَرَة',11,12,54,'وَإِذۡ قُلۡتُمۡ يَٰمُوسۭيٰ لَن نُّؤۡمِنَ لَكَ حَتَّىٰ نَرَى اَ۬للَّهَ جَهۡرَةٗ فَأَخَذَتۡكُمُ اُ۬لصَّٰعِقَةُ وَأَنتُمۡ تَنظُرُونَ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (62,1,'8',2,'Al-Baqarah','البَقَرَة',12,13,55,'ثُمَّ بَعَثۡنَٰكُم مِّنۢ بَعۡدِ مَوۡتِكُمۡ لَعَلَّكُمۡ تَشۡكُرُونَ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (63,1,'8',2,'Al-Baqarah','البَقَرَة',13,15,56,'وَظَلَّلۡنَا عَلَيۡكُمُ اُ۬لۡغَمَامَ وَأَنزَلۡنَا عَلَيۡكُمُ اُ۬لۡمَنَّ وَاَلسَّلۡوۭيٰۖ كُلُواْ مِن طَيِّبَٰتِ مَا رَزَقۡنَٰكُمۡۚ وَمَا ظَلَمُونَا وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (64,1,'9',2,'Al-Baqarah','البَقَرَة',1,3,57,'وَإِذۡ قُلۡنَا اَ۟دۡخُلُواْ هَٰذِهِ اِ۬لۡقَرۡيَةَ فَكُلُواْ مِنۡهَا حَيۡثُ شِئۡتُمۡ رَغَدٗا وَاَدۡخُلُواْ اُ۬لۡبَابَ سُجَّدٗا وَقُولُواْ حِطَّةٞ نَّغۡفِر لَّكُمۡ خَطَٰيَٰكُمۡۚ وَسَنَزِيدُ اُ۬لۡمُحۡسِنِينَ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (65,1,'9',2,'Al-Baqarah','البَقَرَة',3,5,58,'فَبَدَّلَ اَ۬لَّذِينَ ظَلَمُواْ قَوۡلًا غَيۡرَ اَ۬لَّذِي قِيلَ لَهُمۡ فَأَنزَلۡنَا عَلَى اَ۬لَّذِينَ ظَلَمُواْ رِجۡزٗا مِّنَ اَ۬لسَّمَآءِ بِمَا كَانُواْ يَفۡسُقُونَ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (66,1,'9',2,'Al-Baqarah','البَقَرَة',5,8,59,'۞ وَإِذِ اِ۪سۡتَسۡقَىٰ مُوسۭيٰ لِقَوۡمِهِۦ فَقُلۡنَا اَ۪ضۡرِب بِّعَصَاكَ اَ۬لۡحَجَرَۖ فَاَنفَجَرَتۡ مِنۡهُ اُ۪ثۡنَتَا عَشۡرَةَ عَيۡنٗاۖ قَدۡ عَلِمَ كُلُّ أُنَاسٖ مَّشۡرَبَهُمۡۖ كُلُواْ وَاَشۡرَبُواْ مِن رِّزۡقِ اِ۬للَّهِ وَلَا تَعۡثَوۡاْ فِي اِ۬لۡأَرۡضِ مُفۡسِدِينَ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (67,1,'9',2,'Al-Baqarah','البَقَرَة',9,15,60,'وَإِذۡ قُلۡتُمۡ يَٰمُوسۭيٰ لَن نَّصۡبِرَ عَلَىٰ طَعَامٖ وَٰحِدٖ فَاَدۡعُ لَنَا رَبَّكَ يُخۡرِجۡ لَنَا مِمَّا تُنۢبِتُ اُ۬لۡأَرۡضُ مِنۢ بَقۡلِهَا وَقِثَّآئِهَا وَفُومِهَا وَعَدَسِهَا وَبَصَلِهَاۖ قَالَ أَتَسۡتَبۡدِلُونَ اَ۬لَّذِي هُوَ أَدۡنَىٰ بِالَّذِي هُوَ خَيۡرٌۚ اِ۪هۡبِطُواْ مِصۡرٗا فَإِنَّ لَكُم مَّا سَأَلۡتُمۡۗ وَضُرِبَتۡ عَلَيۡهِمِ اِ۬لذِّلَّةُ وَاَلۡمَسۡكَنَةُ وَبَآءُو بِغَضَبٖ مِّنَ اَ۬للَّهِۗ ذَٰلِكَ بِأَنَّهُمۡ كَانُواْ يَكۡفُرُونَ بِـَٔايَٰتِ اِ۬للَّهِ وَيَقۡتُلُونَ اَ۬لنَّبِيِّـۧنَ بِغَيۡرِ اِ۬لۡحَقِّۗ ذَٰلِكَ بِمَا عَصَواْ وَّكَانُواْ يَعۡتَدُونَ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (68,1,'10',2,'Al-Baqarah','البَقَرَة',1,3,61,'إِنَّ اَ۬لَّذِينَ ءَامَنُواْ وَاَلَّذِينَ هَادُواْ وَاَلنَّصَٰر۪يٰ وَاَلصَّٰبِـِٔينَ مَنۡ ءَامَنَ بِاللَّهِ وَاَلۡيَوۡمِ اِ۬لۡأٓخِرِ وَعَمِلَ صَٰلِحٗا فَلَهُمۡ أَجۡرُهُمۡ عِندَ رَبِّهِمۡ وَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (69,1,'10',2,'Al-Baqarah','البَقَرَة',3,5,62,'وَإِذۡ أَخَذۡنَا مِيثَٰقَكُمۡ وَرَفَعۡنَا فَوۡقَكُمُ اُ۬لطُّورَ خُذُواْ مَآ ءَاتَيۡنَٰكُم بِقُوَّةٖ وَاَذۡكُرُواْ مَا فِيهِ لَعَلَّكُمۡ تَتَّقُونَ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (70,1,'10',2,'Al-Baqarah','البَقَرَة',5,7,63,'ثُمَّ تَوَلَّيۡتُم مِّنۢ بَعۡدِ ذَٰلِكَۖ فَلَوۡلَا فَضۡلُ اُ۬للَّهِ عَلَيۡكُمۡ وَرَحۡمَتُهُۥ لَكُنتُم مِّنَ اَ۬لۡخَٰسِرِينَ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (71,1,'10',2,'Al-Baqarah','البَقَرَة',7,8,64,'وَلَقَدۡ عَلِمۡتُمُ اُ۬لَّذِينَ اَ۪عۡتَدَوۡاْ مِنكُمۡ فِي اِ۬لسَّبۡتِ فَقُلۡنَا لَهُمۡ كُونُواْ قِرَدَةً خَٰسِـِٔينَ ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (72,1,'10',2,'Al-Baqarah','البَقَرَة',8,9,65,'فَجَعَلۡنَٰهَا نَكَٰلٗا لِّمَا بَيۡنَ يَدَيۡهَا وَمَا خَلۡفَهَا وَمَوۡعِظَةٗ لِّلۡمُتَّقِينَ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (73,1,'10',2,'Al-Baqarah','البَقَرَة',9,11,66,'۞ وَإِذۡ قَالَ مُوسۭيٰ لِقَوۡمِهِۦٓ إِنَّ اَ۬للَّهَ يَأۡمُرۡكُمۡ أَن تَذۡبَحُواْ بَقَرَةٗۖ قَالُوٓاْ أَتَتَّخِذُنَا هُزُؤٗاۖ قَالَ أَعُوذُ بِاللَّهِ أَنۡ أَكُونَ مِنَ اَ۬لۡجَٰهِلِينَ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (74,1,'10',2,'Al-Baqarah','البَقَرَة',12,14,67,'قَالُواْ اُ۟دۡعُ لَنَا رَبَّكَ يُبَيِّن لَّنَا مَا هِيَۚ قَالَ إِنَّهُۥ يَقُولُ إِنَّهَا بَقَرَةٞ لَّا فَارِضٞ وَلَا بِكۡرٌ عَوَانُۢ بَيۡنَ ذَٰلِكَۖ فَاَفۡعَلُواْ مَا تُؤۡمَرُونَ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (75,1,'10',2,'Al-Baqarah','البَقَرَة',14,15,68,'قَالُواْ اُ۟دۡعُ لَنَا رَبَّكَ يُبَيِّن لَّنَا مَا لَوۡنُهَاۚ قَالَ إِنَّهُۥ يَقُولُ إِنَّهَا بَقَرَةٞ صَفۡرَآءُ فَاقِعٞ لَّوۡنُهَا تَسُرُّ اُ۬لنَّٰظِرِينَ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (76,1,'11',2,'Al-Baqarah','البَقَرَة',1,2,69,'قَالُواْ اُ۟دۡعُ لَنَا رَبَّكَ يُبَيِّن لَّنَا مَا هِيَ إِنَّ اَ۬لۡبَقَرَ تَشَٰبَهَ عَلَيۡنَا وَإِنَّآ إِن شَآءَ اَ۬للَّهُ لَمُهۡتَدُونَ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (77,1,'11',2,'Al-Baqarah','البَقَرَة',2,4,70,'قَالَ إِنَّهُۥ يَقُولُ إِنَّهَا بَقَرَةٞ لَّا ذَلُولٞ تُثِيرُ اُ۬لۡأَرۡضَ وَلَا تَسۡقِي اِ۬لۡحَرۡثَ مُسَلَّمَةٞ لَّا شِيَةَ فِيهَاۚ قَالُواْ اُ۬لۡـَٰٔنَ جِئۡتَ بِالۡحَقِّۚ فَذَبَحُوهَا وَمَا كَادُواْ يَفۡعَلُونَ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (78,1,'11',2,'Al-Baqarah','البَقَرَة',4,5,71,'وَإِذۡ قَتَلۡتُمۡ نَفۡسٗا فَاَدَّٰرَٰٔتُمۡ فِيهَاۖ وَاَللَّهُ مُخۡرِجٞ مَّا كُنتُمۡ تَكۡتُمُونَ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (79,1,'11',2,'Al-Baqarah','البَقَرَة',6,7,72,'فَقُلۡنَا اَ۪ضۡرِبُوهُ بِبَعۡضِهَاۚ كَذَٰلِكَ يُحۡيِ اِ۬للَّهُ اُ۬لۡمَوۡتۭيٰ وَيُرِيكُمۡ ءَايَٰتِهِۦ لَعَلَّكُمۡ تَعۡقِلُونَ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (80,1,'11',2,'Al-Baqarah','البَقَرَة',7,10,73,'ثُمَّ قَسَتۡ قُلُوبُكُم مِّنۢ بَعۡدِ ذَٰلِكَ فَهۡيَ كَاَلۡحِجَارَةِ أَوۡ أَشَدُّ قَسۡوَةٗۚ وَإِنَّ مِنَ اَ۬لۡحِجَارَةِ لَمَا يَتَفَجَّرُ مِنۡهُ اُ۬لۡأَنۡهَٰرُۚ وَإِنَّ مِنۡهَا لَمَا يَشَّقَّقُ فَيَخۡرُجُ مِنۡهُ اُ۬لۡمَآءُۚ وَإِنَّ مِنۡهَا لَمَا يَهۡبِطُ مِنۡ خَشۡيَةِ اِ۬للَّهِۗ وَمَا اَ۬للَّهُ بِغَٰفِلٍ عَمَّا تَعۡمَلُونَ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (81,1,'11',2,'Al-Baqarah','البَقَرَة',11,13,74,'۞ أَفَتَطۡمَعُونَ أَن يُؤۡمِنُواْ لَكُمۡ وَقَدۡ كَانَ فَرِيقٞ مِّنۡهُمۡ يَسۡمَعُونَ كَلَٰمَ اَ۬للَّهِ ثُمَّ يُحَرِّفُونَهُۥ مِنۢ بَعۡدِ مَا عَقَلُوهُ وَهُمۡ يَعۡلَمُونَ ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (82,1,'11',2,'Al-Baqarah','البَقَرَة',13,15,75,'وَإِذَا لَقُواْ اُ۬لَّذِينَ ءَامَنُواْ قَالُوٓاْ ءَامَنَّا وَإِذَا خَلَا بَعۡضُهُمۡ إِلَىٰ بَعۡضٖ قَالُوٓاْ أَتُحَدِّثُونَهُم بِمَا فَتَحَ اَ۬للَّهُ عَلَيۡكُمۡ لِيُحَآجُّوكُم بِهِۦ عِندَ رَبِّكُمۡۚ أَفَلَا تَعۡقِلُونَ ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (83,1,'12',2,'Al-Baqarah','البَقَرَة',1,1,76,'أَوَلَا يَعۡلَمُونَ أَنَّ اَ۬للَّهَ يَعۡلَمُ مَا يُسِرُّونَ وَمَا يُعۡلِنُونَ ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (84,1,'12',2,'Al-Baqarah','البَقَرَة',2,3,77,'وَمِنۡهُمۡ أُمِّيُّونَ لَا يَعۡلَمُونَ اَ۬لۡكِتَٰبَ إِلَّآ أَمَانِيَّ وَإِنۡ هُمۡ إِلَّا يَظُنُّونَ ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (85,1,'12',2,'Al-Baqarah','البَقَرَة',3,5,78,'فَوَيۡلٞ لِّلَّذِينَ يَكۡتُبُونَ اَ۬لۡكِتَٰبَ بِأَيۡدِيهِمۡ ثُمَّ يَقُولُونَ هَٰذَا مِنۡ عِندِ اِ۬للَّهِ لِيَشۡتَرُواْ بِهِۦ ثَمَنٗا قَلِيلٗاۖ فَوَيۡلٞ لَّهُم مِّمَّا كَتَبَتۡ أَيۡدِيهِمۡ وَوَيۡلٞ لَّهُم مِّمَّا يَكۡسِبُونَ ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (86,1,'12',2,'Al-Baqarah','البَقَرَة',6,8,79,'وَقَالُواْ لَن تَمَسَّنَا اَ۬لنَّارُ إِلَّآ أَيَّامٗا مَّعۡدُودَةٗۚ قُلۡ أَتَّخَذتُّمۡ عِندَ اَ۬للَّهِ عَهۡدٗا فَلَن يُخۡلِفَ اَ۬للَّهُ عَهۡدَهُۥٓۖ أَمۡ تَقُولُونَ عَلَى اَ۬للَّهِ مَا لَا تَعۡلَمُونَ ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (87,1,'12',2,'Al-Baqarah','البَقَرَة',8,10,80,'بَلَىٰۚ مَن كَسَبَ سَيِّئَةٗ وَأَحَٰطَتۡ بِهِۦ خَطِيٓـَٔتُهُۥ فَأُوْلَٰٓئِكَ أَصۡحَٰبُ اُ۬لنّ۪ارِۖ هُمۡ فِيهَا خَٰلِدُونَ ٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (88,1,'12',2,'Al-Baqarah','البَقَرَة',10,11,81,'وَاَلَّذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ أُوْلَٰٓئِكَ أَصۡحَٰبُ اُ۬لۡجَنَّةِۖ هُمۡ فِيهَا خَٰلِدُونَ ٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (89,1,'12',2,'Al-Baqarah','البَقَرَة',11,15,82,'وَإِذۡ أَخَذۡنَا مِيثَٰقَ بَنِيٓ إِسۡرَٰٓءِيلَ لَا تَعۡبُدُونَ إِلَّا اَ۬للَّهَ وَبِالۡوَٰلِدَيۡنِ إِحۡسَانٗا وَذِي اِ۬لۡقُرۡبۭيٰ وَاَلۡيَتَٰمَىٰ وَاَلۡمَسَٰكِينِ وَقُولُواْ لِلنّ۪اسِ حُسۡنٗا وَأَقِيمُواْ اُ۬لصَّلَوٰةَ وَءَاتُواْ اُ۬لزَّكَوٰةَ ثُمَّ تَوَلَّيۡتُمۡ إِلَّا قَلِيلٗا مِّنكُمۡ وَأَنتُم مُّعۡرِضُونَ ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (90,1,'13',2,'Al-Baqarah','البَقَرَة',1,2,83,'وَإِذۡ أَخَذۡنَا مِيثَٰقَكُمۡ لَا تَسۡفِكُونَ دِمَآءَكُمۡ وَلَا تُخۡرِجُونَ أَنفُسَكُم مِّن دِيٰ۪رِكُمۡ ثُمَّ أَقۡرَرۡتُمۡ وَأَنتُمۡ تَشۡهَدُونَ ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (91,1,'13',2,'Al-Baqarah','البَقَرَة',3,9,84,'۞ ثُمَّ أَنتُمۡ هَٰٓؤُلَآءِ تَقۡتُلُونَ أَنفُسَكُمۡ وَتُخۡرِجُونَ فَرِيقٗا مِّنكُم مِّن دِيٰ۪رِهِمۡ تَظَّٰهَرُونَ عَلَيۡهِم بِالۡإِثۡمِ وَاَلۡعُدۡوَٰنِ وَإِن يَأۡتُوكُمۡ أُسَٰر۪يٰ تَفۡدُوهُمۡ وَهۡوَ مُحَرَّمٌ عَلَيۡكُمۡ إِخۡرَاجُهُمۡۚ أَفَتُؤۡمِنُونَ بِبَعۡضِ اِ۬لۡكِتَٰبِ وَتَكۡفُرُونَ بِبَعۡضٖۚ فَمَا جَزَآءُ مَن يَفۡعَلُ ذَٰلِكَ مِنكُمۡ إِلَّا خِزۡيٞ فِي اِ۬لۡحَيَوٰةِ اِ۬لدُّنۡيۭاۖ وَيَوۡمَ اَ۬لۡقِيَٰمَةِ يُرَدُّونَ إِلَىٰٓ أَشَدِّ اِ۬لۡعَذَابِۗ وَمَا اَ۬للَّهُ بِغَٰفِلٍ عَمَّا تَعۡمَلُونَ ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (92,1,'13',2,'Al-Baqarah','البَقَرَة',9,10,85,'أُوْلَٰٓئِكَ اَ۬لَّذِينَ اَ۪شۡتَرَوُاْ اُ۬لۡحَيَوٰةَ اَ۬لدُّنۡيۭا بِالۡأٓخِرَةِۖ فَلَا يُخَفَّفُ عَنۡهُمُ اُ۬لۡعَذَابُ وَلَا هُمۡ يُنصَرُونَ ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (93,1,'13',2,'Al-Baqarah','البَقَرَة',11,14,86,'وَلَقَدۡ ءَاتَيۡنَا مُوسَى اَ۬لۡكِتَٰبَ وَقَفَّيۡنَا مِنۢ بَعۡدِهِۦ بِالرُّسُلِۖ وَءَاتَيۡنَا عِيسَى اَ۪بۡنَ مَرۡيَمَ اَ۬لۡبَيِّنَٰتِ وَأَيَّدۡنَٰهُ بِرُوحِ اِ۬لۡقُدُسِۗ أَفَكُلَّمَا جَآءَكُمۡ رَسُولُۢ بِمَا لَا تَهۡوَىٰٓ أَنفُسُكُمُ اُ۪سۡتَكۡبَرۡتُمۡ فَفَرِيقٗا كَذَّبۡتُمۡ وَفَرِيقٗا تَقۡتُلُونَ ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (94,1,'13',2,'Al-Baqarah','البَقَرَة',14,15,87,'وَقَالُواْ قُلُوبُنَا غُلۡفُۢۚ بَل لَّعَنَهُمُ اُ۬للَّهُ بِكُفۡرِهِمۡ فَقَلِيلٗا مَّا يُؤۡمِنُونَ ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (95,1,'14',2,'Al-Baqarah','البَقَرَة',1,3,88,'وَلَمَّا جَآءَهُمۡ كِتَٰبٞ مِّنۡ عِندِ اِ۬للَّهِ مُصَدِّقٞ لِّمَا مَعَهُمۡ وَكَانُواْ مِن قَبۡلُ يَسۡتَفۡتِحُونَ عَلَى اَ۬لَّذِينَ كَفَرُواْ فَلَمَّا جَآءَهُم مَّا عَرَفُواْ كَفَرُواْ بِهِۦۚ فَلَعۡنَةُ اُ۬للَّهِ عَلَى اَ۬لۡكٰ۪فِرِينَ ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (96,1,'14',2,'Al-Baqarah','البَقَرَة',4,6,89,'بِئۡسَمَا اَ۪شۡتَرَوۡاْ بِهِۦٓ أَنفُسَهُمۡ أَن يَكۡفُرُواْ بِمَآ أَنزَلَ اَ۬للَّهُ بَغۡيًا أَن يُنزِلَ اَ۬للَّهُ مِن فَضۡلِهِۦ عَلَىٰ مَن يَشَآءُ مِنۡ عِبَادِهِۦۖ فَبَآءُو بِغَضَبٍ عَلَىٰ غَضَبٖۚ وَلِلۡكٰ۪فِرِينَ عَذَابٞ مُّهِينٞ ٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (97,1,'14',2,'Al-Baqarah','البَقَرَة',7,10,90,'وَإِذَا قِيلَ لَهُمۡ ءَامِنُواْ بِمَآ أَنزَلَ اَ۬للَّهُ قَالُواْ نُؤۡمِنُ بِمَآ أُنزِلَ عَلَيۡنَا وَيَكۡفُرُونَ بِمَا وَرَآءَهُۥ وَهۡوَ اَ۬لۡحَقُّ مُصَدِّقٗا لِّمَا مَعَهُمۡۗ قُلۡ فَلِمَ تَقۡتُلُونَ أَنۢبِيَآءَ اَ۬للَّهِ مِن قَبۡلُ إِن كُنتُم مُّؤۡمِنِينَ ٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (98,1,'14',2,'Al-Baqarah','البَقَرَة',10,11,91,'۞ وَلَقَد جَّآءَكُم مُّوسۭيٰ بِالۡبَيِّنَٰتِ ثُمَّ اَ۪تَّخَذتُّمُ اُ۬لۡعِجۡلَ مِنۢ بَعۡدِهِۦ وَأَنتُمۡ ظَٰلِمُونَ ٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (99,1,'14',2,'Al-Baqarah','البَقَرَة',11,15,92,'وَإِذۡ أَخَذۡنَا مِيثَٰقَكُمۡ وَرَفَعۡنَا فَوۡقَكُمُ اُ۬لطُّورَ خُذُواْ مَآ ءَاتَيۡنَٰكُم بِقُوَّةٖ وَاَسۡمَعُواْۖ قَالُواْ سَمِعۡنَا وَعَصَيۡنَا وَأُشۡرِبُواْ فِي قُلُوبِهِمِ اِ۬لۡعِجۡلَ بِكُفۡرِهِمۡۚ قُلۡ بِئۡسَمَا يَأۡمُرۡكُم بِهِۦٓ إِيمَٰنُكُمۡ إِن كُنتُم مُّؤۡمِنِينَ ٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (100,1,'15',2,'Al-Baqarah','البَقَرَة',1,2,93,'قُلۡ إِن كَانَتۡ لَكُمُ اُ۬لدَّارُ اُ۬لۡأٓخِرَةُ عِندَ اَ۬للَّهِ خَالِصَةٗ مِّن دُونِ اِ۬لنّ۪اسِ فَتَمَنَّوُاْ اُ۬لۡمَوۡتَ إِن كُنتُمۡ صَٰدِقِينَ ٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (101,1,'15',2,'Al-Baqarah','البَقَرَة',2,3,94,'وَلَن يَتَمَنَّوۡهُ أَبَدَۢا بِمَا قَدَّمَتۡ أَيۡدِيهِمۡۗ وَاَللَّهُ عَلِيمُۢ بِالظَّٰلِمِينَ ٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (102,1,'15',2,'Al-Baqarah','البَقَرَة',4,6,95,'وَلَتَجِدَنَّهُمۡ أَحۡرَصَ اَ۬لنّ۪اسِ عَلَىٰ حَيَوٰةٖ وَمِنَ اَ۬لَّذِينَ أَشۡرَكُواْۚ يَوَدُّ أَحَدُهُمۡ لَوۡ يُعَمَّرُ أَلۡفَ سَنَةٖ وَمَا هُوَ بِمُزَحۡزِحِهِۦ مِنَ اَ۬لۡعَذَابِ أَن يُعَمَّرَۗ وَاَللَّهُ بَصِيرُۢ بِمَا يَعۡمَلُونَ ٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (103,1,'15',2,'Al-Baqarah','البَقَرَة',6,8,96,'قُلۡ مَن كَانَ عَدُوّٗا لِّـجِبۡرِيلَ فَإِنَّهُۥ نَزَّلَهُۥ عَلَىٰ قَلۡبِكَ بِإِذۡنِ اِ۬للَّهِ مُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِ وَهُدٗى وَبُشۡر۪يٰ لِلۡمُؤۡمِنِينَ ٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (104,1,'15',2,'Al-Baqarah','البَقَرَة',9,10,97,'مَن كَانَ عَدُوّٗا لِّلَّهِ وَمَلَٰٓئِكَتِهِۦ وَرُسُلِهِۦ وَجِبۡرِيلَ وَمِيكَىٰلَ فَإِنَّ اَ۬للَّهَ عَدُوّٞ لِّلۡكٰ۪فِرِينَ ٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (105,1,'15',2,'Al-Baqarah','البَقَرَة',10,11,98,'وَلَقَدۡ أَنزَلۡنَآ إِلَيۡكَ ءَايَٰتِۢ بَيِّنَٰتٖۖ وَمَا يَكۡفُرُ بِهَآ إِلَّا اَ۬لۡفَٰسِقُونَ ٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (106,1,'15',2,'Al-Baqarah','البَقَرَة',12,13,99,'أَوَكُلَّمَا عَٰهَدُواْ عَهۡدٗا نَّبَذَهُۥ فَرِيقٞ مِّنۡهُمۚ بَلۡ أَكۡثَرُهُمۡ لَا يُؤۡمِنُونَ ٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (107,1,'15',2,'Al-Baqarah','البَقَرَة',13,15,100,'۞ وَلَمَّا جَآءَهُمۡ رَسُولٞ مِّنۡ عِندِ اِ۬للَّهِ مُصَدِّقٞ لِّمَا مَعَهُمۡ نَبَذَ فَرِيقٞ مِّنَ اَ۬لَّذِينَ أُوتُواْ اُ۬لۡكِتَٰبَ كِتَٰبَ اَ۬للَّهِ وَرَآءَ ظُهُورِهِمۡ كَأَنَّهُمۡ لَا يَعۡلَمُونَ ١٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (108,1,'16',2,'Al-Baqarah','البَقَرَة',1,9,101,'وَاَتَّبَعُواْ مَا تَتۡلُواْ اُ۬لشَّيَٰطِينُ عَلَىٰ مُلۡكِ سُلَيۡمَٰنَۖ وَمَا كَفَرَ سُلَيۡمَٰنُ وَلَٰكِنَّ اَ۬لشَّيَٰطِينَ كَفَرُواْ يُعَلِّمُونَ اَ۬لنَّاسَ اَ۬لسِّحۡرَ وَمَآ أُنزِلَ عَلَى اَ۬لۡمَلَكَيۡنِ بِبَابِلَ هَٰرُوتَ وَمَٰرُوتَۚ وَمَا يُعَلِّمَانِ مِنۡ أَحَدٍ حَتَّىٰ يَقُولَآ إِنَّمَا نَحۡنُ فِتۡنَةٞ فَلَا تَكۡفُرۡۖ فَيَتَعَلَّمُونَ مِنۡهُمَا مَا يُفَرِّقُونَ بِهِۦ بَيۡنَ اَ۬لۡمَرۡءِ وَزَوۡجِهِۦۚ وَمَا هُم بِضَآرِّينَ بِهِۦ مِنۡ أَحَدٍ إِلَّا بِإِذۡنِ اِ۬للَّهِۚ وَيَتَعَلَّمُونَ مَا يَضُرُّهُمۡ وَلَا يَنفَعُهُمۡۚ وَلَقَدۡ عَلِمُواْ لَمَنِ اِ۪شۡتَر۪ىٰهُ مَا لَهُۥ فِي اِ۬لۡأٓخِرَةِ مِنۡ خَلَٰقٖۚ وَلَبِئۡسَ مَا شَرَوۡاْ بِهِۦٓ أَنفُسَهُمۡۚ لَوۡ كَانُواْ يَعۡلَمُونَ ١٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (109,1,'16',2,'Al-Baqarah','البَقَرَة',9,10,102,'وَلَوۡ أَنَّهُمۡ ءَامَنُواْ وَاَتَّقَوۡاْ لَمَثُوبَةٞ مِّنۡ عِندِ اِ۬للَّهِ خَيۡرٞۚ لَّوۡ كَانُواْ يَعۡلَمُونَ ١٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (110,1,'16',2,'Al-Baqarah','البَقَرَة',11,12,103,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ لَا تَقُولُواْ رَٰعِنَا وَقُولُواْ اُ۟نظُرۡنَا وَاَسۡمَعُواْۗ وَلِلۡكٰ۪فِرِينَ عَذَابٌ أَلِيمٞ ١٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (111,1,'16',2,'Al-Baqarah','البَقَرَة',12,15,104,'مَّا يَوَدُّ اُ۬لَّذِينَ كَفَرُواْ مِنۡ أَهۡلِ اِ۬لۡكِتَٰبِ وَلَا اَ۬لۡمُشۡرِكِينَ أَن يُنزَلَ عَلَيۡكُم مِّنۡ خَيۡرٖ مِّن رَّبِّكُمۡۗ وَاَللَّهُ يَخۡتَصُّ بِرَحۡمَتِهِۦ مَن يَشَآءُۚ وَاَللَّهُ ذُو اُ۬لۡفَضۡلِ اِ۬لۡعَظِيمِ ١٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (112,1,'17',2,'Al-Baqarah','البَقَرَة',1,2,105,'۞ مَا نَنسَخۡ مِنۡ ءَايَةٍ أَوۡ نَنسَـٔۡهَا نَأۡتِ بِخَيۡرٖ مِّنۡهَآ أَوۡ مِثۡلِهَآۗ أَلَمۡ تَعۡلَمۡ أَنَّ اَ۬للَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ ١٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (113,1,'17',2,'Al-Baqarah','البَقَرَة',2,4,106,'أَلَمۡ تَعۡلَمۡ أَنَّ اَ۬للَّهَ لَهُۥ مُلۡكُ اُ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِۗ وَمَا لَكُم مِّن دُونِ اِ۬للَّهِ مِن وَلِيّٖ وَلَا نَصِيرٍ ١٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (114,1,'17',2,'Al-Baqarah','البَقَرَة',4,6,107,'أَمۡ تُرِيدُونَ أَن تَسۡـَٔلُواْ رَسُولَكُمۡ كَمَا سُئِلَ مُوسۭيٰ مِن قَبۡلُۗ وَمَن يَتَبَدَّلِ اِ۬لۡكُفۡرَ بِالۡإِيمَٰنِ فَقَد ضَّلَّ سَوَآءَ اَ۬لسَّبِيلِ ١٠٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (115,1,'17',2,'Al-Baqarah','البَقَرَة',6,10,108,'وَدَّ كَثِيرٞ مِّنۡ أَهۡلِ اِ۬لۡكِتَٰبِ لَوۡ يَرُدُّونَكُم مِّنۢ بَعۡدِ إِيمَٰنِكُمۡ كُفَّارًا حَسَدٗا مِّنۡ عِندِ أَنفُسِهِم مِّنۢ بَعۡدِ مَا تَبَيَّنَ لَهُمُ اُ۬لۡحَقُّۖ فَاَعۡفُواْ وَاَصۡفَحُواْ حَتَّىٰ يَأۡتِيَ اَ۬للَّهُ بِأَمۡرِهِۦٓۚ إِنَّ اَ۬للَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ ١٠٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (116,1,'17',2,'Al-Baqarah','البَقَرَة',10,12,109,'وَأَقِيمُواْ اُ۬لصَّلَوٰةَ وَءَاتُواْ اُ۬لزَّكَوٰةَۚ وَمَا تُقَدِّمُواْ لِأَنفُسِكُم مِّنۡ خَيۡرٖ تَجِدُوهُ عِندَ اَ۬للَّهِۚ إِنَّ اَ۬للَّهَ بِمَا تَعۡمَلُونَ بَصِيرٞ ١٠٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (117,1,'17',2,'Al-Baqarah','البَقَرَة',12,14,110,'وَقَالُواْ لَن يَدۡخُلَ اَ۬لۡجَنَّةَ إِلَّا مَن كَانَ هُودًا أَوۡ نَصَٰر۪يٰۚ تِلۡكَ أَمَانِيُّهُمۡۗ قُلۡ هَاتُواْ بُرۡهَٰنَكُمۡ إِن كُنتُمۡ صَٰدِقِينَ ١١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (118,1,'17',2,'Al-Baqarah','البَقَرَة',14,15,111,'بَلَىٰۚ مَنۡ أَسۡلَمَ وَجۡهَهُۥ لِلَّهِ وَهۡوَ مُحۡسِنٞ فَلَهُۥٓ أَجۡرُهُۥ عِندَ رَبِّهِۦ وَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ ١١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (119,1,'18',2,'Al-Baqarah','البَقَرَة',1,4,112,'وَقَالَتِ اِ۬لۡيَهُودُ لَيۡسَتِ اِ۬لنَّصَٰر۪يٰ عَلَىٰ شَيۡءٖ وَقَالَتِ اِ۬لنَّصَٰر۪يٰ لَيۡسَتِ اِ۬لۡيَهُودُ عَلَىٰ شَيۡءٖ وَهُمۡ يَتۡلُونَ اَ۬لۡكِتَٰبَۗ كَذَٰلِكَ قَالَ اَ۬لَّذِينَ لَا يَعۡلَمُونَ مِثۡلَ قَوۡلِهِمۡۚ فَاَللَّهُ يَحۡكُمُ بَيۡنَهُمۡ يَوۡمَ اَ۬لۡقِيَٰمَةِ فِيمَا كَانُواْ فِيهِ يَخۡتَلِفُونَ ١١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (120,1,'18',2,'Al-Baqarah','البَقَرَة',4,7,113,'۞ وَمَنۡ أَظۡلَمُ مِمَّن مَّنَعَ مَسَٰجِدَ اَ۬للَّهِ أَن يُذۡكَرَ فِيهَا اَ۪سۡمُهُۥ وَسَعَىٰ فِي خَرَابِهَآۚ أُوْلَٰٓئِكَ مَا كَانَ لَهُمۡ أَن يَدۡخُلُوهَآ إِلَّا خَآئِفِينَۚ لَهُمۡ فِي اِ۬لدُّنۡيۭا خِزۡيٞ وَلَهُمۡ فِي اِ۬لۡأٓخِرَةِ عَذَابٌ عَظِيمٞ ١١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (121,1,'18',2,'Al-Baqarah','البَقَرَة',7,8,114,'وَلِلَّهِ اِ۬لۡمَشۡرِقُ وَاَلۡمَغۡرِبُۚ فَأَيۡنَمَا تُوَلُّواْ فَثَمَّ وَجۡهُ اُ۬للَّهِۚ إِنَّ اَ۬للَّهَ وَٰسِعٌ عَلِيمٞ ١١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (122,1,'18',2,'Al-Baqarah','البَقَرَة',9,10,115,'وَقَالُواْ اُ۪تَّخَذَ اَ۬للَّهُ وَلَدٗاۗ سُبۡحَٰنَهُۥۖ بَل لَّهُۥ مَا فِي اِ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِۖ كُلّٞ لَّهُۥ قَٰنِتُونَ ١١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (123,1,'18',2,'Al-Baqarah','البَقَرَة',10,11,116,'بَدِيعُ اُ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِۖ وَإِذَا قَضَىٰٓ أَمۡرٗا فَإِنَّمَا يَقُولُ لَهُۥ كُن فَيَكُونُ ١١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (124,1,'18',2,'Al-Baqarah','البَقَرَة',11,14,117,'وَقَالَ اَ۬لَّذِينَ لَا يَعۡلَمُونَ لَوۡلَا يُكَلِّمُنَا اَ۬للَّهُ أَوۡ تَأۡتِينَآ ءَايَةٞۗ كَذَٰلِكَ قَالَ اَ۬لَّذِينَ مِن قَبۡلِهِم مِّثۡلَ قَوۡلِهِمۡۘ تَشَٰبَهَتۡ قُلُوبُهُمۡۗ قَدۡ بَيَّنَّا اَ۬لۡأٓيَٰتِ لِقَوۡمٖ يُوقِنُونَ ١١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (125,1,'18',2,'Al-Baqarah','البَقَرَة',14,15,118,'إِنَّآ أَرۡسَلۡنَٰكَ بِالۡحَقِّ بَشِيرٗا وَنَذِيرٗاۖ وَلَا تُسۡـَٔلُ عَنۡ أَصۡحَٰبِ اِ۬لۡجَحِيمِ ١١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (126,1,'19',2,'Al-Baqarah','البَقَرَة',1,3,119,'وَلَن تَرۡضَىٰ عَنكَ اَ۬لۡيَهُودُ وَلَا اَ۬لنَّصَٰر۪يٰ حَتَّىٰ تَتَّبِعَ مِلَّتَهُمۡۗ قُلۡ إِنَّ هُدَى اَ۬للَّهِ هُوَ اَ۬لۡهُدَىٰۗ وَلَئِنِ اِ۪تَّبَعۡتَ أَهۡوَآءَهُم بَعۡدَ اَ۬لَّذِي جَآءَكَ مِنَ اَ۬لۡعِلۡمِ مَا لَكَ مِنَ اَ۬للَّهِ مِن وَلِيّٖ وَلَا نَصِيرٍ ١١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (127,1,'19',2,'Al-Baqarah','البَقَرَة',3,5,120,'اِ۬لَّذِينَ ءَاتَيۡنَٰهُمُ اُ۬لۡكِتَٰبَ يَتۡلُونَهُۥ حَقَّ تِلَاوَتِهِۦٓ أُوْلَٰٓئِكَ يُؤۡمِنُونَ بِهِۦۗ وَمَن يَكۡفُرۡ بِهِۦ فَأُوْلَٰٓئِكَ هُمُ اُ۬لۡخَٰسِرُونَ ١٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (128,1,'19',2,'Al-Baqarah','البَقَرَة',5,6,121,'يَٰبَنِيٓ إِسۡرَٰٓءِيلَ اَ۟ذۡكُرُواْ نِعۡمَتِيَ اَ۬لَّتِيٓ أَنۡعَمۡتُ عَلَيۡكُمۡ وَأَنِّي فَضَّلۡتُكُمۡ عَلَى اَ۬لۡعَٰلَمِينَ ١٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (129,1,'19',2,'Al-Baqarah','البَقَرَة',6,8,122,'وَاَتَّقُواْ يَوۡمٗا لَّا تَجۡزِي نَفۡسٌ عَن نَّفۡسٖ شَيۡـٔٗا وَلَا يُقۡبَلُ مِنۡهَا عَدۡلٞ وَلَا تَنفَعُهَا شَفَٰعَةٞ وَلَا هُمۡ يُنصَرُونَ ١٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (130,1,'19',2,'Al-Baqarah','البَقَرَة',8,10,123,'۞ وَإِذِ اِ۪بۡتَلَىٰٓ إِبۡرَٰهِـۧمَ رَبُّهُۥ بِكَلِمَٰتٖ فَأَتَمَّهُنَّۖ قَالَ إِنِّي جَاعِلُكَ لِلنّ۪اسِ إِمَامٗاۖ قَالَ وَمِن ذُرِّيَّتِيۖ قَالَ لَا يَنَالُ عَهۡدِيَ اَ۬لظَّٰلِمِينَ ١٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (131,1,'19',2,'Al-Baqarah','البَقَرَة',10,12,124,'وَإِذ جَّعَلۡنَا اَ۬لۡبَيۡتَ مَثَابَةٗ لِّلنّ۪اسِ وَأَمۡنٗا وَاَتَّخِذُواْ مِن مَّقَامِ إِبۡرَٰهِـۧمَ مُصَلّٗىۖ وَعَهِدۡنَآ إِلَىٰٓ إِبۡرَٰهِـۧمَ وَإِسۡمَٰعِيلَ أَن طَهِّرَا بَيۡتِي لِلطَّآئِفِينَ وَاَلۡعَٰكِفِينَ وَاَلرُّكَّعِ اِ۬لسُّجُودِ ١٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (132,1,'19',2,'Al-Baqarah','البَقَرَة',13,15,125,'وَإِذۡ قَالَ إِبۡرَٰهِـۧمُ رَبِّ اِ۪جۡعَلۡ هَٰذَا بَلَدًا ءَامِنٗا وَاَرۡزُقۡ أَهۡلَهُۥ مِنَ اَ۬لثَّمَرَٰتِ مَنۡ ءَامَنَ مِنۡهُم بِاللَّهِ وَاَلۡيَوۡمِ اِ۬لۡأٓخِرِۖ قَالَ وَمَن كَفَرَ فَأُمَتِّعُهُۥ قَلِيلٗا ثُمَّ أَضۡطَرُّهُۥٓ إِلَىٰ عَذَابِ اِ۬لنّ۪ارِۖ وَبِئۡسَ اَ۬لۡمَصِيرُ ١٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (133,1,'20',2,'Al-Baqarah','البَقَرَة',1,2,126,'وَإِذۡ يَرۡفَعُ إِبۡرَٰهِـۧمُ اُ۬لۡقَوَاعِدَ مِنَ اَ۬لۡبَيۡتِ وَإِسۡمَٰعِيلُ رَبَّنَا تَقَبَّلۡ مِنَّآۖ إِنَّكَ أَنتَ اَ۬لسَّمِيعُ اُ۬لۡعَلِيمُ ١٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (134,1,'20',2,'Al-Baqarah','البَقَرَة',2,4,127,'رَبَّنَا وَاَجۡعَلۡنَا مُسۡلِمَيۡنِ لَكَ وَمِن ذُرِّيَّتِنَآ أُمَّةٗ مُّسۡلِمَةٗ لَّكَ وَأَر۪نَا مَنَاسِكَنَا وَتُبۡ عَلَيۡنَآۖ إِنَّكَ أَنتَ اَ۬لتَّوَّابُ اُ۬لرَّحِيمُ ١٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (135,1,'20',2,'Al-Baqarah','البَقَرَة',4,6,128,'رَبَّنَا وَاَبۡعَثۡ فِيهِمۡ رَسُولٗا مِّنۡهُمۡ يَتۡلُواْ عَلَيۡهِمۡ ءَايَٰتِكَ وَيُعَلِّمُهُمُ اُ۬لۡكِتَٰبَ وَاَلۡحِكۡمَةَ وَيُزَكِّيهِمۡۖ إِنَّكَ أَنتَ اَ۬لۡعَزِيزُ اُ۬لۡحَكِيمُ ١٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (136,1,'20',2,'Al-Baqarah','البَقَرَة',6,8,129,'وَمَن يَرۡغَبُ عَن مِّلَّةِ إِبۡرَٰهِـۧمَ إِلَّا مَن سَفِهَ نَفۡسَهُۥۚ وَلَقَدِ اِ۪صۡطَفَيۡنَٰهُ فِي اِ۬لدُّنۡيۭاۖ وَإِنَّهُۥ فِي اِ۬لۡأٓخِرَةِ لَمِنَ اَ۬لصَّٰلِحِينَ ١٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (137,1,'20',2,'Al-Baqarah','البَقَرَة',8,9,130,'إِذۡ قَالَ لَهُۥ رَبُّهُۥٓ أَسۡلِمۡۖ قَالَ أَسۡلَمۡتُ لِرَبِّ اِ۬لۡعَٰلَمِينَ ١٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (138,1,'20',2,'Al-Baqarah','البَقَرَة',9,11,131,'۞ وَوَصَّىٰ بِهَآ إِبۡرَٰهِـۧمُ بَنِيهِ وَيَعۡقُوبُ يَٰبَنِيَّ إِنَّ اَ۬للَّهَ اَ۪صۡطَفَىٰ لَكُمُ اُ۬لدِّينَ فَلَا تَمُوتُنَّ إِلَّا وَأَنتُم مُّسۡلِمُونَ ١٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (139,1,'20',2,'Al-Baqarah','البَقَرَة',11,14,132,'أَمۡ كُنتُمۡ شُهَدَآءَ ا۪ذۡ حَضَرَ يَعۡقُوبَ اَ۬لۡمَوۡتُ إِذۡ قَالَ لِبَنِيهِ مَا تَعۡبُدُونَ مِنۢ بَعۡدِيۖ قَالُواْ نَعۡبُدُ إِلَٰهَكَ وَإِلَٰهَ ءَابَآئِكَ إِبۡرَٰهِـۧمَ وَإِسۡمَٰعِيلَ وَإِسۡحَٰقَ إِلَٰهٗا وَٰحِدٗا وَنَحۡنُ لَهُۥ مُسۡلِمُونَ ١٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (140,1,'20',2,'Al-Baqarah','البَقَرَة',14,15,133,'تِلۡكَ أُمَّةٞ قَدۡ خَلَتۡۖ لَهَا مَا كَسَبَتۡ وَلَكُم مَّا كَسَبۡتُمۡۖ وَلَا تُسۡـَٔلُونَ عَمَّا كَانُواْ يَعۡمَلُونَ ١٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (141,1,'21',2,'Al-Baqarah','البَقَرَة',1,2,134,'وَقَالُواْ كُونُواْ هُودًا أَوۡ نَصَٰر۪يٰ تَهۡتَدُواْۗ قُلۡ بَلۡ مِلَّةَ إِبۡرَٰهِـۧمَ حَنِيفٗاۖ وَمَا كَانَ مِنَ اَ۬لۡمُشۡرِكِينَ ١٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (142,1,'21',2,'Al-Baqarah','البَقَرَة',2,5,135,'قُولُوٓاْ ءَامَنَّا بِاللَّهِ وَمَآ أُنزِلَ إِلَيۡنَا وَمَآ أُنزِلَ إِلَىٰٓ إِبۡرَٰهِـۧمَ وَإِسۡمَٰعِيلَ وَإِسۡحَٰقَ وَيَعۡقُوبَ وَاَلۡأَسۡبَاطِ وَمَآ أُوتِيَ مُوسۭيٰ وَعِيسۭيٰ وَمَآ أُوتِيَ اَ۬لنَّبِيُّونَ مِن رَّبِّهِمۡ لَا نُفَرِّقُ بَيۡنَ أَحَدٖ مِّنۡهُمۡ وَنَحۡنُ لَهُۥ مُسۡلِمُونَ ١٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (143,1,'21',2,'Al-Baqarah','البَقَرَة',6,7,136,'فَإِنۡ ءَامَنُواْ بِمِثۡلِ مَآ ءَامَنتُم بِهِۦ فَقَدِ اِ۪هۡتَدَواْۖ وَّإِن تَوَلَّوۡاْ فَإِنَّمَا هُمۡ فِي شِقَاقٖۖ فَسَيَكۡفِيكَهُمُ اُ۬للَّهُۚ وَهۡوَ اَ۬لسَّمِيعُ اُ۬لۡعَلِيمُ ١٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (144,1,'21',2,'Al-Baqarah','البَقَرَة',8,9,137,'صِبۡغَةَ اَ۬للَّهِۖ وَمَنۡ أَحۡسَنُ مِنَ اَ۬للَّهِ صِبۡغَةٗۖ وَنَحۡنُ لَهُۥ عَٰبِدُونَ ١٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (145,1,'21',2,'Al-Baqarah','البَقَرَة',9,10,138,'قُلۡ أَتُحَآجُّونَنَا فِي اِ۬للَّهِ وَهۡوَ رَبُّنَا وَرَبُّكُمۡ وَلَنَآ أَعۡمَٰلُنَا وَلَكُمۡ أَعۡمَٰلُكُمۡ وَنَحۡنُ لَهُۥ مُخۡلِصُونَ ١٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (146,1,'21',2,'Al-Baqarah','البَقَرَة',11,14,139,'أَمۡ يَقُولُونَ إِنَّ إِبۡرَٰهِـۧمَ وَإِسۡمَٰعِيلَ وَإِسۡحَٰقَ وَيَعۡقُوبَ وَاَلۡأَسۡبَاطَ كَانُواْ هُودًا أَوۡ نَصَٰر۪يٰۗ قُلۡ ءَٰا۬نتُمۡ أَعۡلَمُ أَمِ اِ۬للَّهُۗ وَمَنۡ أَظۡلَمُ مِمَّن كَتَمَ شَهَٰدَةً عِندَهُۥ مِنَ اَ۬للَّهِۗ وَمَا اَ۬للَّهُ بِغَٰفِلٍ عَمَّا تَعۡمَلُونَ ١٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (147,1,'21',2,'Al-Baqarah','البَقَرَة',14,15,140,'تِلۡكَ أُمَّةٞ قَدۡ خَلَتۡۖ لَهَا مَا كَسَبَتۡ وَلَكُم مَّا كَسَبۡتُمۡۖ وَلَا تُسۡـَٔلُونَ عَمَّا كَانُواْ يَعۡمَلُونَ ١٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (148,2,'22',2,'Al-Baqarah','البَقَرَة',1,3,141,'۞ سَيَقُولُ اُ۬لسُّفَهَآءُ مِنَ اَ۬لنّ۪اسِ مَا وَلَّىٰهُمۡ عَن قِبۡلَتِهِمِ اِ۬لَّتِي كَانُواْ عَلَيۡهَاۚ قُل لِّلَّهِ اِ۬لۡمَشۡرِقُ وَاَلۡمَغۡرِبُۚ يَهۡدِي مَن يَشَآءُ اِ۪لَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ ١٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (149,2,'22',2,'Al-Baqarah','البَقَرَة',3,8,142,'وَكَذَٰلِكَ جَعَلۡنَٰكُمۡ أُمَّةٗ وَسَطٗا لِّتَكُونُواْ شُهَدَآءَ عَلَى اَ۬لنّ۪اسِ وَيَكُونَ اَ۬لرَّسُولُ عَلَيۡكُمۡ شَهِيدٗاۗ وَمَا جَعَلۡنَا اَ۬لۡقِبۡلَةَ اَ۬لَّتِي كُنتَ عَلَيۡهَآ إِلَّا لِنَعۡلَمَ مَن يَتَّبِعُ اُ۬لرَّسُولَ مِمَّن يَنقَلِبُ عَلَىٰ عَقِبَيۡهِۚ وَإِن كَانَتۡ لَكَبِيرَةً إِلَّا عَلَى اَ۬لَّذِينَ هَدَى اَ۬للَّهُۗ وَمَا كَانَ اَ۬للَّهُ لِيُضِيعَ إِيمَٰنَكُمۡۚ إِنَّ اَ۬للَّهَ بِالنّ۪اسِ لَرَؤُفٞ رَّحِيمٞ ١٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (150,2,'22',2,'Al-Baqarah','البَقَرَة',8,12,143,'قَدۡ نَر۪يٰ تَقَلُّبَ وَجۡهِكَ فِي اِ۬لسَّمَآءِۖ فَلَنُوَلِّيَنَّكَ قِبۡلَةٗ تَرۡضَىٰهَاۖ فَوَلِّ وَجۡهَكَ شَطۡرَ اَ۬لۡمَسۡجِدِ اِ۬لۡحَرَامِۚ وَحَيۡثُ مَا كُنتُمۡ فَوَلُّواْ وُجُوهَكُمۡ شَطۡرَهُۥۗ وَإِنَّ اَ۬لَّذِينَ أُوتُواْ اُ۬لۡكِتَٰبَ لَيَعۡلَمُونَ أَنَّهُ اُ۬لۡحَقُّ مِن رَّبِّهِمۡۗ وَمَا اَ۬للَّهُ بِغَٰفِلٍ عَمَّا يَعۡمَلُونَ ١٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (151,2,'22',2,'Al-Baqarah','البَقَرَة',12,15,144,'وَلَئِنۡ أَتَيۡتَ اَ۬لَّذِينَ أُوتُواْ اُ۬لۡكِتَٰبَ بِكُلِّ ءَايَةٖ مَّا تَبِعُواْ قِبۡلَتَكَۚ وَمَآ أَنتَ بِتَابِعٖ قِبۡلَتَهُمۡۚ وَمَا بَعۡضُهُم بِتَابِعٖ قِبۡلَةَ بَعۡضٖۚ وَلَئِنِ اِ۪تَّبَعۡتَ أَهۡوَآءَهُم مِّنۢ بَعۡدِ مَا جَآءَكَ مِنَ اَ۬لۡعِلۡمِ إِنَّكَ إِذٗا لَّمِنَ اَ۬لظَّٰلِمِينَ ١٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (152,2,'23',2,'Al-Baqarah','البَقَرَة',1,2,145,'اَ۬لَّذِينَ ءَاتَيۡنَٰهُمُ اُ۬لۡكِتَٰبَ يَعۡرِفُونَهُۥ كَمَا يَعۡرِفُونَ أَبۡنَآءَهُمۡۖ وَإِنَّ فَرِيقٗا مِّنۡهُمۡ لَيَكۡتُمُونَ اَ۬لۡحَقَّ وَهُمۡ يَعۡلَمُونَ ١٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (153,2,'23',2,'Al-Baqarah','البَقَرَة',2,3,146,'اَ۬لۡحَقُّ مِن رَّبِّكَ فَلَا تَكُونَنَّ مِنَ اَ۬لۡمُمۡتَرِينَ ١٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (154,2,'23',2,'Al-Baqarah','البَقَرَة',3,5,147,'۞ وَلِكُلّٖ وِجۡهَةٌ هُوَ مُوَلِّيهَاۖ فَاَسۡتَبِقُواْ اُ۬لۡخَيۡرَٰتِۚ أَيۡنَ مَا تَكُونُواْ يَأۡتِ بِكُمُ اُ۬للَّهُ جَمِيعًاۚ إِنَّ اَ۬للَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ ١٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (155,2,'23',2,'Al-Baqarah','البَقَرَة',5,7,148,'وَمِنۡ حَيۡثُ خَرَجۡتَ فَوَلِّ وَجۡهَكَ شَطۡرَ اَ۬لۡمَسۡجِدِ اِ۬لۡحَرَامِۖ وَإِنَّهُۥ لَلۡحَقُّ مِن رَّبِّكَۗ وَمَا اَ۬للَّهُ بِغَٰفِلٍ عَمَّا يَعۡمَلُونَ ١٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (156,2,'23',2,'Al-Baqarah','البَقَرَة',7,11,149,'وَمِنۡ حَيۡثُ خَرَجۡتَ فَوَلِّ وَجۡهَكَ شَطۡرَ اَ۬لۡمَسۡجِدِ اِ۬لۡحَرَامِۚ وَحَيۡثُ مَا كُنتُمۡ فَوَلُّواْ وُجُوهَكُمۡ شَطۡرَهُۥ لِئَلَّا يَكُونَ لِلنّ۪اسِ عَلَيۡكُمۡ حُجَّةٌ إِلَّا اَ۬لَّذِينَ ظَلَمُواْ مِنۡهُمۡ فَلَا تَخۡشَوۡهُمۡ وَاَخۡشَوۡنِي وَلِأُتِمَّ نِعۡمَتِي عَلَيۡكُمۡ وَلَعَلَّكُمۡ تَهۡتَدُونَ ١٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (157,2,'23',2,'Al-Baqarah','البَقَرَة',11,13,150,'كَمَآ أَرۡسَلۡنَا فِيكُمۡ رَسُولٗا مِّنكُمۡ يَتۡلُواْ عَلَيۡكُمۡ ءَايَٰتِنَا وَيُزَكِّيكُمۡ وَيُعَلِّمُكُمُ اُ۬لۡكِتَٰبَ وَاَلۡحِكۡمَةَ وَيُعَلِّمُكُم مَّا لَمۡ تَكُونُواْ تَعۡلَمُونَ ١٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (158,2,'23',2,'Al-Baqarah','البَقَرَة',13,14,151,'فَاَذۡكُرُونِيٓ أَذۡكُرۡكُمۡ وَاَشۡكُرُواْ لِي وَلَا تَكۡفُرُونِ ١٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (159,2,'23',2,'Al-Baqarah','البَقَرَة',14,15,152,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ اُ۪سۡتَعِينُواْ بِالصَّبۡرِ وَاَلصَّلَوٰةِۚ إِنَّ اَ۬للَّهَ مَعَ اَ۬لصَّٰبِرِينَ ١٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (160,2,'24',2,'Al-Baqarah','البَقَرَة',1,2,153,'وَلَا تَقُولُواْ لِمَن يُقۡتَلُ فِي سَبِيلِ اِ۬للَّهِ أَمۡوَٰتُۢۚ بَلۡ أَحۡيَآءٞ وَلَٰكِن لَّا تَشۡعُرُونَ ١٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (161,2,'24',2,'Al-Baqarah','البَقَرَة',2,3,154,'وَلَنَبۡلُوَنَّكُم بِشَيۡءٖ مِّنَ اَ۬لۡخَوۡفِ وَاَلۡجُوعِ وَنَقۡصٖ مِّنَ اَ۬لۡأَمۡوَٰلِ وَاَلۡأَنفُسِ وَاَلثَّمَرَٰتِۗ وَبَشِّرِ اِ۬لصَّٰبِرِينَ ١٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (162,2,'24',2,'Al-Baqarah','البَقَرَة',4,4,155,'اَ۬لَّذِينَ إِذَآ أَصَٰبَتۡهُم مُّصِيبَةٞ قَالُوٓاْ إِنَّا لِلَّهِ وَإِنَّآ إِلَيۡهِ رَٰجِعُونَ ١٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (163,2,'24',2,'Al-Baqarah','البَقَرَة',5,6,156,'أُوْلَٰٓئِكَ عَلَيۡهِمۡ صَلَوَٰتٞ مِّن رَّبِّهِمۡ وَرَحۡمَةٞۖ وَأُوْلَٰٓئِكَ هُمُ اُ۬لۡمُهۡتَدُونَ ١٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (164,2,'24',2,'Al-Baqarah','البَقَرَة',6,8,157,'۞ إِنَّ اَ۬لصَّفَا وَاَلۡمَرۡوَةَ مِن شَعَآئِرِ اِ۬للَّهِۖ فَمَنۡ حَجَّ اَ۬لۡبَيۡتَ أَوِ اِ۪عۡتَمَرَ فَلَا جُنَاحَ عَلَيۡهِ أَن يَطَّوَّفَ بِهِمَاۚ وَمَن تَطَوَّعَ خَيۡرٗا فَإِنَّ اَ۬للَّهَ شَاكِرٌ عَلِيمٌ ١٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (165,2,'24',2,'Al-Baqarah','البَقَرَة',8,10,158,'إِنَّ اَ۬لَّذِينَ يَكۡتُمُونَ مَآ أَنزَلۡنَا مِنَ اَ۬لۡبَيِّنَٰتِ وَاَلۡهُدَىٰ مِنۢ بَعۡدِ مَا بَيَّنَّٰهُ لِلنّ۪اسِ فِي اِ۬لۡكِتَٰبِ أُوْلَٰٓئِكَ يَلۡعَنُهُمُ اُ۬للَّهُ وَيَلۡعَنُهُمُ اُ۬للَّٰعِنُونَ ١٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (166,2,'24',2,'Al-Baqarah','البَقَرَة',11,12,159,'إِلَّا اَ۬لَّذِينَ تَابُواْ وَأَصۡلَحُواْ وَبَيَّنُواْ فَأُوْلَٰٓئِكَ أَتُوبُ عَلَيۡهِمۡۚ وَأَنَا اَ۬لتَّوَّابُ اُ۬لرَّحِيمُ ١٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (167,2,'24',2,'Al-Baqarah','البَقَرَة',12,13,160,'إِنَّ اَ۬لَّذِينَ كَفَرُواْ وَمَاتُواْ وَهُمۡ كُفَّارٌ أُوْلَٰٓئِكَ عَلَيۡهِمۡ لَعۡنَةُ اُ۬للَّهِ وَاَلۡمَلَٰٓئِكَةِ وَاَلنّ۪اسِ أَجۡمَعِينَ ١٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (168,2,'24',2,'Al-Baqarah','البَقَرَة',14,14,161,'خَٰلِدِينَ فِيهَا لَا يُخَفَّفُ عَنۡهُمُ اُ۬لۡعَذَابُ وَلَا هُمۡ يُنظَرُونَ ١٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (169,2,'24',2,'Al-Baqarah','البَقَرَة',15,15,162,'وَإِلَٰهُكُمۡ إِلَٰهٞ وَٰحِدٞۖ لَّآ إِلَٰهَ إِلَّا هُوَ اَ۬لرَّحۡمَٰنُ اُ۬لرَّحِيمُ ١٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (170,2,'25',2,'Al-Baqarah','البَقَرَة',1,5,163,'إِنَّ فِي خَلۡقِ اِ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِ وَاَخۡتِلَٰفِ اِ۬لَّيۡلِ وَاَلنَّه۪ارِ وَاَلۡفُلۡكِ اِ۬لَّتِي تَجۡرِي فِي اِ۬لۡبَحۡرِ بِمَا يَنفَعُ اُ۬لنَّاسَ وَمَآ أَنزَلَ اَ۬للَّهُ مِنَ اَ۬لسَّمَآءِ مِن مَّآءٖ فَأَحۡيَا بِهِ اِ۬لۡأَرۡضَ بَعۡدَ مَوۡتِهَا وَبَثَّ فِيهَا مِن كُلِّ دَآبَّةٖ وَتَصۡرِيفِ اِ۬لرِّيَٰحِ وَاَلسَّحَابِ اِ۬لۡمُسَخَّرِ بَيۡنَ اَ۬لسَّمَآءِ وَاَلۡأَرۡضِ لَأٓيَٰتٖ لِّقَوۡمٖ يَعۡقِلُونَ ١٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (171,2,'25',2,'Al-Baqarah','البَقَرَة',5,8,164,'وَمِنَ اَ۬لنّ۪اسِ مَن يَتَّخِذُ مِن دُونِ اِ۬للَّهِ أَندَادٗا يُحِبُّونَهُمۡ كَحُبِّ اِ۬للَّهِۖ وَاَلَّذِينَ ءَامَنُوٓاْ أَشَدُّ حُبّٗا لِّلَّهِۗ وَلَوۡ يَرَى اَ۬لَّذِينَ ظَلَمُوٓاْ إِذۡ يَرَوۡنَ اَ۬لۡعَذَابَ أَنَّ اَ۬لۡقُوَّةَ لِلَّهِ جَمِيعٗا وَأَنَّ اَ۬للَّهَ شَدِيدُ اُ۬لۡعَذَابِ ١٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (172,2,'25',2,'Al-Baqarah','البَقَرَة',9,10,165,'۞ إِذ تَّبَرَّأَ اَ۬لَّذِينَ اَ۟تُّبِعُواْ مِنَ اَ۬لَّذِينَ اَ۪تَّبَعُواْ وَرَأَوُاْ اُ۬لۡعَذَابَ وَتَقَطَّعَتۡ بِهِمِ اِ۬لۡأَسۡبَابُ ١٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (173,2,'25',2,'Al-Baqarah','البَقَرَة',10,12,166,'وَقَالَ اَ۬لَّذِينَ اَ۪تَّبَعُواْ لَوۡ أَنَّ لَنَا كَرَّةٗ فَنَتَبَرَّأَ مِنۡهُمۡ كَمَا تَبَرَّءُواْ مِنَّاۗ كَذَٰلِكَ يُرِيهِمِ اِ۬للَّهُ أَعۡمَٰلَهُمۡ حَسَرَٰتٍ عَلَيۡهِمۡۖ وَمَا هُم بِخَٰرِجِينَ مِنَ اَ۬لنّ۪ارِ ١٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (174,2,'25',2,'Al-Baqarah','البَقَرَة',13,14,167,'يَٰٓأَيُّهَا اَ۬لنَّاسُ كُلُواْ مِمَّا فِي اِ۬لۡأَرۡضِ حَلَٰلٗا طَيِّبٗا وَلَا تَتَّبِعُواْ خُطۡوَٰتِ اِ۬لشَّيۡطَٰنِۚ إِنَّهُۥ لَكُمۡ عَدُوّٞ مُّبِينٌ ١٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (175,2,'25',2,'Al-Baqarah','البَقَرَة',14,15,168,'إِنَّمَا يَأۡمُرۡكُم بِالسُّوٓءِ وَاَلۡفَحۡشَآءِ وَأَن تَقُولُواْ عَلَى اَ۬للَّهِ مَا لَا تَعۡلَمُونَ ١٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (176,2,'26',2,'Al-Baqarah','البَقَرَة',1,3,169,'وَإِذَا قِيلَ لَهُمُ اُ۪تَّبِعُواْ مَآ أَنزَلَ اَ۬للَّهُ قَالُواْ بَلۡ نَتَّبِعُ مَآ أَلۡفَيۡنَا عَلَيۡهِ ءَابَآءَنَآۗ أَوَلَوۡ كَانَ ءَابَآؤُهُمۡ لَا يَعۡقِلُونَ شَيۡـٔٗا وَلَا يَهۡتَدُونَ ١٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (177,2,'26',2,'Al-Baqarah','البَقَرَة',3,4,170,'وَمَثَلُ اُ۬لَّذِينَ كَفَرُواْ كَمَثَلِ اِ۬لَّذِي يَنۡعِقُ بِمَا لَا يَسۡمَعُ إِلَّا دُعَآءٗ وَنِدَآءٗۚ صُمُّۢ بُكۡمٌ عُمۡيٞ فَهُمۡ لَا يَعۡقِلُونَ ١٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (178,2,'26',2,'Al-Baqarah','البَقَرَة',5,6,171,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ كُلُواْ مِن طَيِّبَٰتِ مَا رَزَقۡنَٰكُمۡ وَاَشۡكُرُواْ لِلَّهِ إِن كُنتُمۡ إِيَّاهُ تَعۡبُدُونَ ١٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (179,2,'26',2,'Al-Baqarah','البَقَرَة',6,9,172,'إِنَّمَا حَرَّمَ عَلَيۡكُمُ اُ۬لۡمَيۡتَةَ وَاَلدَّمَ وَلَحۡمَ اَ۬لۡخِنزِيرِ وَمَآ أُهِلَّ بِهِۦ لِغَيۡرِ اِ۬للَّهِۖ فَمَنِ اِ۟ضۡطُرَّ غَيۡرَ بَاغٖ وَلَا عَادٖ فَلَآ إِثۡمَ عَلَيۡهِۚ إِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٌ ١٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (180,2,'26',2,'Al-Baqarah','البَقَرَة',9,12,173,'إِنَّ اَ۬لَّذِينَ يَكۡتُمُونَ مَآ أَنزَلَ اَ۬للَّهُ مِنَ اَ۬لۡكِتَٰبِ وَيَشۡتَرُونَ بِهِۦ ثَمَنٗا قَلِيلًا أُوْلَٰٓئِكَ مَا يَأۡكُلُونَ فِي بُطُونِهِمۡ إِلَّا اَ۬لنَّارَ وَلَا يُكَلِّمُهُمُ اُ۬للَّهُ يَوۡمَ اَ۬لۡقِيَٰمَةِ وَلَا يُزَكِّيهِمۡ وَلَهُمۡ عَذَابٌ أَلِيمٌ ١٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (181,2,'26',2,'Al-Baqarah','البَقَرَة',12,14,174,'أُوْلَٰٓئِكَ اَ۬لَّذِينَ اَ۪شۡتَرَوُاْ اُ۬لضَّلَٰلَةَ بِالۡهُدَىٰ وَاَلۡعَذَابَ بِالۡمَغۡفِرَةِۚ فَمَآ أَصۡبَرَهُمۡ عَلَى اَ۬لنّ۪ارِ ١٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (182,2,'26',2,'Al-Baqarah','البَقَرَة',14,15,175,'ذَٰلِكَ بِأَنَّ اَ۬للَّهَ نَزَّلَ اَ۬لۡكِتَٰبَ بِالۡحَقِّۗ وَإِنَّ اَ۬لَّذِينَ اَ۪خۡتَلَفُواْ فِي اِ۬لۡكِتَٰبِ لَفِي شِقَاقِۢ بَعِيدٖ ١٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (183,2,'27',2,'Al-Baqarah','البَقَرَة',1,7,176,'۞ لَّيۡسَ اَ۬لۡبِرُّ أَن تُوَلُّواْ وُجُوهَكُمۡ قِبَلَ اَ۬لۡمَشۡرِقِ وَاَلۡمَغۡرِبِ وَلَٰكِنَّ اَ۬لۡبِرَّ مَنۡ ءَامَنَ بِاللَّهِ وَاَلۡيَوۡمِ اِ۬لۡأٓخِرِ وَاَلۡمَلَٰٓئِكَةِ وَاَلۡكِتَٰبِ وَاَلنَّبِيِّـۧنَ وَءَاتَى اَ۬لۡمَالَ عَلَىٰ حُبِّهِۦ ذَوِي اِ۬لۡقُرۡبۭيٰ وَاَلۡيَتَٰمَىٰ وَاَلۡمَسَٰكِينَ وَاَبۡنَ اَ۬لسَّبِيلِ وَاَلسَّآئِلِينَ وَفِي اِ۬لرِّقَابِ وَأَقَامَ اَ۬لصَّلَوٰةَ وَءَاتَى اَ۬لزَّكَوٰةَ وَاَلۡمُوفُونَ بِعَهۡدِهِمۡ إِذَا عَٰهَدُواْۖ وَاَلصَّٰبِرِينَ فِي اِ۬لۡبَأۡسَآءِ وَاَلضَّرَّآءِ وَحِينَ اَ۬لۡبَأۡسِۗ أُوْلَٰٓئِكَ اَ۬لَّذِينَ صَدَقُواْۖ وَأُوْلَٰٓئِكَ هُمُ اُ۬لۡمُتَّقُونَ ١٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (184,2,'27',2,'Al-Baqarah','البَقَرَة',7,11,177,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ كُتِبَ عَلَيۡكُمُ اُ۬لۡقِصَاصُ فِي اِ۬لۡقَتۡلَىۖ اَ۬لۡحُرُّ بِالۡحُرِّ وَاَلۡعَبۡدُ بِالۡعَبۡدِ وَاَلۡأُنثۭيٰ بِالۡأُنثۭيٰۚ فَمَنۡ عُفِيَ لَهُۥ مِنۡ أَخِيهِ شَيۡءٞ فَاَتِّبَاعُۢ بِالۡمَعۡرُوفِ وَأَدَآءٌ إِلَيۡهِ بِإِحۡسَٰنٖۗ ذَٰلِكَ تَخۡفِيفٞ مِّن رَّبِّكُمۡ وَرَحۡمَةٞۗ فَمَنِ اِ۪عۡتَدَىٰ بَعۡدَ ذَٰلِكَ فَلَهُۥ عَذَابٌ أَلِيمٞ ١٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (185,2,'27',2,'Al-Baqarah','البَقَرَة',11,12,178,'وَلَكُمۡ فِي اِ۬لۡقِصَاصِ حَيَوٰةٞ يَٰٓأُوْلِي اِ۬لۡأَلۡبَٰبِ لَعَلَّكُمۡ تَتَّقُونَ ١٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (186,2,'27',2,'Al-Baqarah','البَقَرَة',12,14,179,'كُتِبَ عَلَيۡكُمۡ إِذَا حَضَرَ أَحَدَكُمُ اُ۬لۡمَوۡتُ إِن تَرَكَ خَيۡرًا اِ۬لۡوَصِيَّةُ لِلۡوَٰلِدَيۡنِ وَاَلۡأَقۡرَبِينَ بِالۡمَعۡرُوفِۖ حَقًّا عَلَى اَ۬لۡمُتَّقِينَ ١٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (187,2,'27',2,'Al-Baqarah','البَقَرَة',14,15,180,'فَمَنۢ بَدَّلَهُۥ بَعۡدَ مَا سَمِعَهُۥ فَإِنَّمَآ إِثۡمُهُۥ عَلَى اَ۬لَّذِينَ يُبَدِّلُونَهُۥٓۚ إِنَّ اَ۬للَّهَ سَمِيعٌ عَلِيمٞ ١٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (188,2,'28',2,'Al-Baqarah','البَقَرَة',1,2,181,'فَمَنۡ خَافَ مِن مُّوصٖ جَنَفًا أَوۡ إِثۡمٗا فَأَصۡلَحَ بَيۡنَهُمۡ فَلَآ إِثۡمَ عَلَيۡهِۚ إِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٞ ١٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (189,2,'28',2,'Al-Baqarah','البَقَرَة',2,4,182,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ كُتِبَ عَلَيۡكُمُ اُ۬لصِّيَامُ كَمَا كُتِبَ عَلَى اَ۬لَّذِينَ مِن قَبۡلِكُمۡ لَعَلَّكُمۡ تَتَّقُونَ ١٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (190,2,'28',2,'Al-Baqarah','البَقَرَة',4,7,183,'۞ أَيَّامٗا مَّعۡدُودَٰتٖۚ فَمَن كَانَ مِنكُم مَّرِيضًا أَوۡ عَلَىٰ سَفَرٖ فَعِدَّةٞ مِّنۡ أَيَّامٍ أُخَرَۚ وَعَلَى اَ۬لَّذِينَ يُطِيقُونَهُۥ فِدۡيَةٞ طَعَامُ مِسۡكِينٖۖ فَمَن تَطَوَّعَ خَيۡرٗا فَهۡوَ خَيۡرٞ لَّهُۥۚ وَأَن تَصُومُواْ خَيۡرٞ لَّكُمۡۖ إِن كُنتُمۡ تَعۡلَمُونَ ١٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (191,2,'28',2,'Al-Baqarah','البَقَرَة',8,13,184,'شَهۡرُ رَمَضَانَ اَ۬لَّذِيٓ أُنزِلَ فِيهِ اِ۬لۡقُرۡءَانُ هُدٗى لِّلنّ۪اسِ وَبَيِّنَٰتٖ مِّنَ اَ۬لۡهُدَىٰ وَاَلۡفُرۡقَانِۚ فَمَن شَهِدَ مِنكُمُ اُ۬لشَّهۡرَ فَلۡيَصُمۡهُۖ وَمَن كَانَ مَرِيضًا أَوۡ عَلَىٰ سَفَرٖ فَعِدَّةٞ مِّنۡ أَيَّامٍ أُخَرَۗ يُرِيدُ اُ۬للَّهُ بِكُمُ اُ۬لۡيُسۡرَ وَلَا يُرِيدُ بِكُمُ اُ۬لۡعُسۡرَ وَلِتُكۡمِلُواْ اُ۬لۡعِدَّةَ وَلِتُكَبِّرُواْ اُ۬للَّهَ عَلَىٰ مَا هَدَىٰكُمۡ وَلَعَلَّكُمۡ تَشۡكُرُونَ ١٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (192,2,'28',2,'Al-Baqarah','البَقَرَة',13,15,185,'وَإِذَا سَأَلَكَ عِبَادِي عَنِّي فَإِنِّي قَرِيبٌۖ أُجِيبُ دَعۡوَةَ اَ۬لدَّاعِۦٓ إِذَا دَعَانِۦۖ فَلۡيَسۡتَجِيبُواْ لِي وَلۡيُؤۡمِنُواْ بِي لَعَلَّهُمۡ يَرۡشُدُونَ ١٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (193,2,'29',2,'Al-Baqarah','البَقَرَة',1,8,186,'أُحِلَّ لَكُمۡ لَيۡلَةَ اَ۬لصِّيَامِ اِ۬لرَّفَثُ إِلَىٰ نِسَآئِكُمۡۚ هُنَّ لِبَاسٞ لَّكُمۡ وَأَنتُمۡ لِبَاسٞ لَّهُنَّۗ عَلِمَ اَ۬للَّهُ أَنَّكُمۡ كُنتُمۡ تَخۡتَانُونَ أَنفُسَكُمۡ فَتَابَ عَلَيۡكُمۡ وَعَفَا عَنكُمۡۖ فَاَلۡـَٰٔنَ بَٰشِرُوهُنَّ وَاَبۡتَغُواْ مَا كَتَبَ اَ۬للَّهُ لَكُمۡۚ وَكُلُواْ وَاَشۡرَبُواْ حَتَّىٰ يَتَبَيَّنَ لَكُمُ اُ۬لۡخَيۡطُ اُ۬لۡأَبۡيَضُ مِنَ اَ۬لۡخَيۡطِ اِ۬لۡأَسۡوَدِ مِنَ اَ۬لۡفَجۡرِۖ ثُمَّ أَتِمُّواْ اُ۬لصِّيَامَ إِلَى اَ۬لَّيۡلِۚ وَلَا تُبَٰشِرُوهُنَّ وَأَنتُمۡ عَٰكِفُونَ فِي اِ۬لۡمَسَٰجِدِۗ تِلۡكَ حُدُودُ اُ۬للَّهِ فَلَا تَقۡرَبُوهَاۗ كَذَٰلِكَ يُبَيِّنُ اُ۬للَّهُ ءَايَٰتِهِۦ لِلنّ۪اسِ لَعَلَّهُمۡ يَتَّقُونَ ١٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (194,2,'29',2,'Al-Baqarah','البَقَرَة',8,10,187,'وَلَا تَأۡكُلُوٓاْ أَمۡوَٰلَكُم بَيۡنَكُم بِالۡبَٰطِلِ وَتُدۡلُواْ بِهَآ إِلَى اَ۬لۡحُكَّامِ لِتَأۡكُلُواْ فَرِيقٗا مِّنۡ أَمۡوَٰلِ اِ۬لنّ۪اسِ بِالۡإِثۡمِ وَأَنتُمۡ تَعۡلَمُونَ ١٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (195,2,'29',2,'Al-Baqarah','البَقَرَة',11,14,188,'۞ يَسۡـَٔلُونَكَ عَنِ اِ۬لۡأَهِلَّةِۖ قُلۡ هِيَ مَوَٰقِيتُ لِلنّ۪اسِ وَاَلۡحَجِّۗ وَلَيۡسَ اَ۬لۡبِرُّ بِأَن تَأۡتُواْ اُ۬لۡبُيُوتَ مِن ظُهُورِهَا وَلَٰكِنَّ اَ۬لۡبِرَّ مَنِ اِ۪تَّقَىٰۗ وَأۡتُواْ اُ۬لۡبُيُوتَ مِنۡ أَبۡوَٰبِهَاۚ وَاَتَّقُواْ اُ۬للَّهَ لَعَلَّكُمۡ تُفۡلِحُونَ ١٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (196,2,'29',2,'Al-Baqarah','البَقَرَة',14,15,189,'وَقَٰتِلُواْ فِي سَبِيلِ اِ۬للَّهِ اِ۬لَّذِينَ يُقَٰتِلُونَكُمۡ وَلَا تَعۡتَدُوٓاْۚ إِنَّ اَ۬للَّهَ لَا يُحِبُّ اُ۬لۡمُعۡتَدِينَ ١٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (197,2,'30',2,'Al-Baqarah','البَقَرَة',1,3,190,'وَاَقۡتُلُوهُمۡ حَيۡثُ ثَقِفۡتُمُوهُمۡ وَأَخۡرِجُوهُم مِّنۡ حَيۡثُ أَخۡرَجُوكُمۡۚ وَاَلۡفِتۡنَةُ أَشَدُّ مِنَ اَ۬لۡقَتۡلِۚ وَلَا تُقَٰتِلُوهُمۡ عِندَ اَ۬لۡمَسۡجِدِ اِ۬لۡحَرَامِ حَتَّىٰ يُقَٰتِلُوكُمۡ فِيهِۖ فَإِن قَٰتَلُوكُمۡ فَاَقۡتُلُوهُمۡۗ كَذَٰلِكَ جَزَآءُ اُ۬لۡكٰ۪فِرِينَ ١٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (198,2,'30',2,'Al-Baqarah','البَقَرَة',3,4,191,'فَإِنِ اِ۪نتَهَوۡاْ فَإِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٞ ١٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (199,2,'30',2,'Al-Baqarah','البَقَرَة',4,5,192,'وَقَٰتِلُوهُمۡ حَتَّىٰ لَا تَكُونَ فِتۡنَةٞ وَيَكُونَ اَ۬لدِّينُ لِلَّهِۖ فَإِنِ اِ۪نتَهَوۡاْ فَلَا عُدۡوَٰنَ إِلَّا عَلَى اَ۬لظَّٰلِمِينَ ١٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (200,2,'30',2,'Al-Baqarah','البَقَرَة',5,8,193,'اَ۬لشَّهۡرُ اُ۬لۡحَرَامُ بِالشَّهۡرِ اِ۬لۡحَرَامِ وَاَلۡحُرُمَٰتُ قِصَاصٞۚ فَمَنِ اِ۪عۡتَدَىٰ عَلَيۡكُمۡ فَاَعۡتَدُواْ عَلَيۡهِ بِمِثۡلِ مَا اَ۪عۡتَدَىٰ عَلَيۡكُمۡۚ وَاَتَّقُواْ اُ۬للَّهَ وَاَعۡلَمُوٓاْ أَنَّ اَ۬للَّهَ مَعَ اَ۬لۡمُتَّقِينَ ١٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (201,2,'30',2,'Al-Baqarah','البَقَرَة',8,9,194,'وَأَنفِقُواْ فِي سَبِيلِ اِ۬للَّهِ وَلَا تُلۡقُواْ بِأَيۡدِيكُمۡ إِلَى اَ۬لتَّهۡلُكَةِ وَأَحۡسِنُوٓاْۚ إِنَّ اَ۬للَّهَ يُحِبُّ اُ۬لۡمُحۡسِنِينَ ١٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (202,2,'30',2,'Al-Baqarah','البَقَرَة',9,15,195,'وَأَتِمُّواْ اُ۬لۡحَجَّ وَاَلۡعُمۡرَةَ لِلَّهِۚ فَإِنۡ أُحۡصِرۡتُمۡ فَمَا اَ۪سۡتَيۡسَرَ مِنَ اَ۬لۡهَدۡيِۖ وَلَا تَحۡلِقُواْ رُءُوسَكُمۡ حَتَّىٰ يَبۡلُغَ اَ۬لۡهَدۡيُ مَحِلَّهُۥۚ فَمَن كَانَ مِنكُم مَّرِيضًا أَوۡ بِهِۦٓ أَذٗى مِّن رَّأۡسِهِۦ فَفِدۡيَةٞ مِّن صِيَامٍ أَوۡ صَدَقَةٍ أَوۡ نُسُكٖۚ ۞ فَإِذَآ أَمِنتُمۡ فَمَن تَمَتَّعَ بِالۡعُمۡرَةِ إِلَى اَ۬لۡحَجِّ فَمَا اَ۪سۡتَيۡسَرَ مِنَ اَ۬لۡهَدۡيِۚ فَمَن لَّمۡ يَجِدۡ فَصِيَامُ ثَلَٰثَةِ أَيَّامٖ فِي اِ۬لۡحَجِّ وَسَبۡعَةٍ إِذَا رَجَعۡتُمۡۗ تِلۡكَ عَشَرَةٞ كَامِلَةٞۗ ذَٰلِكَ لِمَن لَّمۡ يَكُنۡ أَهۡلُهُۥ حَاضِرِي اِ۬لۡمَسۡجِدِ اِ۬لۡحَرَامِۚ وَاَتَّقُواْ اُ۬للَّهَ وَاَعۡلَمُوٓاْ أَنَّ اَ۬للَّهَ شَدِيدُ اُ۬لۡعِقَابِ ١٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (203,2,'31',2,'Al-Baqarah','البَقَرَة',1,8,196,'اِ۬لۡحَجُّ أَشۡهُرٞ مَّعۡلُومَٰتٞۚ فَمَن فَرَضَ فِيهِنَّ اَ۬لۡحَجَّ فَلَا رَفَثٞ وَلَا فُسُوقٞ وَلَا جِدَالَ فِي اِ۬لۡحَجِّۗ وَمَا تَفۡعَلُواْ مِنۡ خَيۡرٖ يَعۡلَمۡهُ اُ۬للَّهُۗ وَتَزَوَّدُواْ فَإِنَّ خَيۡرَ اَ۬لزَّادِ اِ۬لتَّقۡوۭيٰۚ وَاَتَّقُونِۦ يَٰٓأُوْلِي اِ۬لۡأَلۡبَٰبِۚ لَيۡسَ عَلَيۡكُمۡ جُنَاحٌ أَن تَبۡتَغُواْ فَضۡلٗا مِّن رَّبِّكُمۡۚ فَإِذَآ أَفَضۡتُم مِّنۡ عَرَفَٰتٖ فَاَذۡكُرُواْ اُ۬للَّهَ عِندَ اَ۬لۡمَشۡعَرِ اِ۬لۡحَرَامِۖ وَاَذۡكُرُوهُ كَمَا هَدَىٰكُمۡ وَإِن كُنتُم مِّن قَبۡلِهِۦ لَمِنَ اَ۬لضَّآلِّينَ ١٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (204,2,'31',2,'Al-Baqarah','البَقَرَة',8,9,197,'ثُمَّ أَفِيضُواْ مِنۡ حَيۡثُ أَفَاضَ اَ۬لنَّاسُ وَاَسۡتَغۡفِرُواْ اُ۬للَّهَۚ إِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٞ ١٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (205,2,'31',2,'Al-Baqarah','البَقَرَة',10,12,198,'فَإِذَا قَضَيۡتُم مَّنَٰسِكَكُمۡ فَاَذۡكُرُواْ اُ۬للَّهَ كَذِكۡرِكُمۡ ءَابَآءَكُمۡ أَوۡ أَشَدَّ ذِكۡرٗاۗ فَمِنَ اَ۬لنّ۪اسِ مَن يَقُولُ رَبَّنَآ ءَاتِنَا فِي اِ۬لدُّنۡيۭا وَمَا لَهُۥ فِي اِ۬لۡأٓخِرَةِ مِنۡ خَلَٰقٖ ١٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (206,2,'31',2,'Al-Baqarah','البَقَرَة',13,14,199,'وَمِنۡهُم مَّن يَقُولُ رَبَّنَآ ءَاتِنَا فِي اِ۬لدُّنۡيۭا حَسَنَةٗ وَفِي اِ۬لۡأٓخِرَةِ حَسَنَةٗ وَقِنَا عَذَابَ اَ۬لنّ۪ارِ ١٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (207,2,'31',2,'Al-Baqarah','البَقَرَة',14,15,200,'أُوْلَٰٓئِكَ لَهُمۡ نَصِيبٞ مِّمَّا كَسَبُواْۚ وَاَللَّهُ سَرِيعُ اُ۬لۡحِسَابِ ٢٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (208,2,'32',2,'Al-Baqarah','البَقَرَة',1,3,201,'۞ وَاَذۡكُرُواْ اُ۬للَّهَ فِيٓ أَيَّامٖ مَّعۡدُودَٰتٖۚ فَمَن تَعَجَّلَ فِي يَوۡمَيۡنِ فَلَآ إِثۡمَ عَلَيۡهِ وَمَن تَأَخَّرَ فَلَآ إِثۡمَ عَلَيۡهِۖ لِمَنِ اِ۪تَّقَىٰۗ وَاَتَّقُواْ اُ۬للَّهَ وَاَعۡلَمُوٓاْ أَنَّكُمۡ إِلَيۡهِ تُحۡشَرُونَ ٢٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (209,2,'32',2,'Al-Baqarah','البَقَرَة',4,5,202,'وَمِنَ اَ۬لنّ۪اسِ مَن يُعۡجِبُكَ قَوۡلُهُۥ فِي اِ۬لۡحَيَوٰةِ اِ۬لدُّنۡيۭا وَيُشۡهِدُ اُ۬للَّهَ عَلَىٰ مَا فِي قَلۡبِهِۦ وَهۡوَ أَلَدُّ اُ۬لۡخِصَامِ ٢٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (210,2,'32',2,'Al-Baqarah','البَقَرَة',6,7,203,'وَإِذَا تَوَلَّىٰ سَعَىٰ فِي اِ۬لۡأَرۡضِ لِيُفۡسِدَ فِيهَا وَيُهۡلِكَ اَ۬لۡحَرۡثَ وَاَلنَّسۡلَۗ وَاَللَّهُ لَا يُحِبُّ اُ۬لۡفَسَادَ ٢٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (211,2,'32',2,'Al-Baqarah','البَقَرَة',7,9,204,'وَإِذَا قِيلَ لَهُ اُ۪تَّقِ اِ۬للَّهَ أَخَذَتۡهُ اُ۬لۡعِزَّةُ بِالۡإِثۡمِۚ فَحَسۡبُهُۥ جَهَنَّمُۚ وَلَبِئۡسَ اَ۬لۡمِهَادُ ٢٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (212,2,'32',2,'Al-Baqarah','البَقَرَة',9,10,205,'وَمِنَ اَ۬لنّ۪اسِ مَن يَشۡرِي نَفۡسَهُ اُ۪بۡتِغَآءَ مَرۡضَاتِ اِ۬للَّهِۗ وَاَللَّهُ رَؤُفُۢ بِالۡعِبَادِ ٢٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (213,2,'32',2,'Al-Baqarah','البَقَرَة',10,12,206,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ اُ۟دۡخُلُواْ فِي اِ۬لسِّلۡمِ كَآفَّةٗ وَلَا تَتَّبِعُواْ خُطۡوَٰتِ اِ۬لشَّيۡطَٰنِۚ إِنَّهُۥ لَكُمۡ عَدُوّٞ مُّبِينٞ ٢٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (214,2,'32',2,'Al-Baqarah','البَقَرَة',12,13,207,'فَإِن زَلَلۡتُم مِّنۢ بَعۡدِ مَا جَآءَتۡكُمُ اُ۬لۡبَيِّنَٰتُ فَاَعۡلَمُوٓاْ أَنَّ اَ۬للَّهَ عَزِيزٌ حَكِيمٌ ٢٠٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (215,2,'32',2,'Al-Baqarah','البَقَرَة',14,15,208,'هَلۡ يَنظُرُونَ إِلَّآ أَن يَأۡتِيَهُمُ اُ۬للَّهُ فِي ظُلَلٖ مِّنَ اَ۬لۡغَمَامِ وَاَلۡمَلَٰٓئِكَةُ وَقُضِيَ اَ۬لۡأَمۡرُۚ وَإِلَى اَ۬للَّهِ تُرۡجَعُ اُ۬لۡأُمُورُ ٢٠٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (216,2,'33',2,'Al-Baqarah','البَقَرَة',1,2,209,'سَلۡ بَنِيٓ إِسۡرَٰٓءِيلَ كَمۡ ءَاتَيۡنَٰهُم مِّنۡ ءَايَةِۢ بَيِّنَةٖۗ وَمَن يُبَدِّلۡ نِعۡمَةَ اَ۬للَّهِ مِنۢ بَعۡدِ مَا جَآءَتۡهُ فَإِنَّ اَ۬للَّهَ شَدِيدُ اُ۬لۡعِقَابِ ٢٠٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (217,2,'33',2,'Al-Baqarah','البَقَرَة',2,4,210,'زُيِّنَ لِلَّذِينَ كَفَرُواْ اُ۬لۡحَيَوٰةُ اُ۬لدُّنۡيۭا وَيَسۡخَرُونَ مِنَ اَ۬لَّذِينَ ءَامَنُواْۘ وَاَلَّذِينَ اَ۪تَّقَوۡاْ فَوۡقَهُمۡ يَوۡمَ اَ۬لۡقِيَٰمَةِۗ وَاَللَّهُ يَرۡزُقُ مَن يَشَآءُ بِغَيۡرِ حِسَابٖ ٢١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (218,2,'33',2,'Al-Baqarah','البَقَرَة',5,10,211,'۞ كَانَ اَ۬لنَّاسُ أُمَّةٗ وَٰحِدَةٗ فَبَعَثَ اَ۬للَّهُ اُ۬لنَّبِيِّـۧنَ مُبَشِّرِينَ وَمُنذِرِينَ وَأَنزَلَ مَعَهُمُ اُ۬لۡكِتَٰبَ بِالۡحَقِّ لِيَحۡكُمَ بَيۡنَ اَ۬لنّ۪اسِ فِيمَا اَ۪خۡتَلَفُواْ فِيهِۚ وَمَا اَ۪خۡتَلَفَ فِيهِ إِلَّا اَ۬لَّذِينَ أُوتُوهُ مِنۢ بَعۡدِ مَا جَآءَتۡهُمُ اُ۬لۡبَيِّنَٰتُ بَغۡيَۢا بَيۡنَهُمۡۖ فَهَدَى اَ۬للَّهُ اُ۬لَّذِينَ ءَامَنُواْ لِمَا اَ۪خۡتَلَفُواْ فِيهِ مِنَ اَ۬لۡحَقِّ بِإِذۡنِهِۦۗ وَاَللَّهُ يَهۡدِي مَن يَشَآءُ اِ۪لَىٰ صِرَٰطٖ مُّسۡتَقِيمٍ ٢١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (219,2,'33',2,'Al-Baqarah','البَقَرَة',10,13,212,'أَمۡ حَسِبۡتُمۡ أَن تَدۡخُلُواْ اُ۬لۡجَنَّةَ وَلَمَّا يَأۡتِكُم مَّثَلُ اُ۬لَّذِينَ خَلَوۡاْ مِن قَبۡلِكُمۖ مَّسَّتۡهُمُ اُ۬لۡبَأۡسَآءُ وَاَلضَّرَّآءُ وَزُلۡزِلُواْ حَتَّىٰ يَقُولَ اَ۬لرَّسُولُ وَاَلَّذِينَ ءَامَنُواْ مَعَهُۥ مَتَىٰ نَصۡرُ اُ۬للَّهِۗ أَلَآ إِنَّ نَصۡرَ اَ۬للَّهِ قَرِيبٞ ٢١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (220,2,'33',2,'Al-Baqarah','البَقَرَة',13,15,213,'يَسۡـَٔلُونَكَ مَاذَا يُنفِقُونَۖ قُلۡ مَآ أَنفَقۡتُم مِّنۡ خَيۡرٖ فَلِلۡوَٰلِدَيۡنِ وَاَلۡأَقۡرَبِينَ وَاَلۡيَتَٰمَىٰ وَاَلۡمَسَٰكِينِ وَاَبۡنِ اِ۬لسَّبِيلِۗ وَمَا تَفۡعَلُواْ مِنۡ خَيۡرٖ فَإِنَّ اَ۬للَّهَ بِهِۦ عَلِيمٞ ٢١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (221,2,'34',2,'Al-Baqarah','البَقَرَة',1,3,214,'كُتِبَ عَلَيۡكُمُ اُ۬لۡقِتَالُ وَهۡوَ كُرۡهٞ لَّكُمۡۖ وَعَسَىٰٓ أَن تَكۡرَهُواْ شَيۡـٔٗا وَهۡوَ خَيۡرٞ لَّكُمۡۖ وَعَسَىٰٓ أَن تُحِبُّواْ شَيۡـٔٗا وَهۡوَ شَرّٞ لَّكُمۡۗ وَاَللَّهُ يَعۡلَمُ وَأَنتُمۡ لَا تَعۡلَمُونَ ٢١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (222,2,'34',2,'Al-Baqarah','البَقَرَة',3,10,215,'يَسۡـَٔلُونَكَ عَنِ اِ۬لشَّهۡرِ اِ۬لۡحَرَامِ قِتَالٖ فِيهِۖ قُلۡ قِتَالٞ فِيهِ كَبِيرٞۚ وَصَدٌّ عَن سَبِيلِ اِ۬للَّهِ وَكُفۡرُۢ بِهِۦ وَاَلۡمَسۡجِدِ اِ۬لۡحَرَامِ وَإِخۡرَاجُ أَهۡلِهِۦ مِنۡهُ أَكۡبَرُ عِندَ اَ۬للَّهِۚ وَاَلۡفِتۡنَةُ أَكۡبَرُ مِنَ اَ۬لۡقَتۡلِۗ وَلَا يَزَالُونَ يُقَٰتِلُونَكُمۡ حَتَّىٰ يَرُدُّوكُمۡ عَن دِينِكُمۡ إِنِ اِ۪سۡتَطَٰعُواْۚ وَمَن يَرۡتَدِدۡ مِنكُمۡ عَن دِينِهِۦ فَيَمُتۡ وَهۡوَ كَافِرٞ فَأُوْلَٰٓئِكَ حَبِطَتۡ أَعۡمَٰلُهُمۡ فِي اِ۬لدُّنۡيۭا وَاَلۡأٓخِرَةِۖ وَأُوْلَٰٓئِكَ أَصۡحَٰبُ اُ۬لنّ۪ارِۖ هُمۡ فِيهَا خَٰلِدُونَ ٢١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (223,2,'34',2,'Al-Baqarah','البَقَرَة',10,12,216,'إِنَّ اَ۬لَّذِينَ ءَامَنُواْ وَاَلَّذِينَ هَاجَرُواْ وَجَٰهَدُواْ فِي سَبِيلِ اِ۬للَّهِ أُوْلَٰٓئِكَ يَرۡجُونَ رَحۡمَتَ اَ۬للَّهِۚ وَاَللَّهُ غَفُورٞ رَّحِيمٞ ٢١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (224,2,'34',2,'Al-Baqarah','البَقَرَة',12,14,217,'۞ يَسۡـَٔلُونَكَ عَنِ اِ۬لۡخَمۡرِ وَاَلۡمَيۡسِرِۖ قُلۡ فِيهِمَآ إِثۡمٞ كَبِيرٞ وَمَنَٰفِعُ لِلنّ۪اسِ وَإِثۡمُهُمَآ أَكۡبَرُ مِن نَّفۡعِهِمَاۗ وَيَسۡـَٔلُونَكَ مَاذَا يُنفِقُونَ ٢١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (225,2,'34-35',2,'Al-Baqarah','البَقَرَة',14,3,218,'قُلِ اِ۬لۡعَفۡوُۗ كَذَٰلِكَ يُبَيِّنُ اُ۬للَّهُ لَكُمُ اُ۬لۡأٓيَٰتِ لَعَلَّكُمۡ تَتَفَكَّرُونَ  فِي اِ۬لدُّنۡيۭا وَاَلۡأٓخِرَةِۗ وَيَسۡـَٔلُونَكَ عَنِ اِ۬لۡيَتَٰمَىٰۖ قُلۡ إِصۡلَاحٞ لَّهُمۡ خَيۡرٞۖ وَإِن تُخَالِطُوهُمۡ فَإِخۡوَٰنُكُمۡۚ وَاَللَّهُ يَعۡلَمُ اُ۬لۡمُفۡسِدَ مِنَ اَ۬لۡمُصۡلِحِۚ وَلَوۡ شَآءَ اَ۬للَّهُ لَأَعۡنَتَكُمۡۚ إِنَّ اَ۬للَّهَ عَزِيزٌ حَكِيمٞ ٢١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (226,2,'35',2,'Al-Baqarah','البَقَرَة',4,8,219,'وَلَا تَنكِحُواْ اُ۬لۡمُشۡرِكَٰتِ حَتَّىٰ يُؤۡمِنَّۚ وَلَأَمَةٞ مُّؤۡمِنَةٌ خَيۡرٞ مِّن مُّشۡرِكَةٖ وَلَوۡ أَعۡجَبَتۡكُمۡۗ وَلَا تُنكِحُواْ اُ۬لۡمُشۡرِكِينَ حَتَّىٰ يُؤۡمِنُواْۚ وَلَعَبۡدٞ مُّؤۡمِنٌ خَيۡرٞ مِّن مُّشۡرِكٖ وَلَوۡ أَعۡجَبَكُمۡۗ أُوْلَٰٓئِكَ يَدۡعُونَ إِلَى اَ۬لنّ۪ارِۖ وَاَللَّهُ يَدۡعُوٓاْ إِلَى اَ۬لۡجَنَّةِ وَاَلۡمَغۡفِرَةِ بِإِذۡنِهِۦۖ وَيُبَيِّنُ ءَايَٰتِهِۦ لِلنّ۪اسِ لَعَلَّهُمۡ يَتَذَكَّرُونَ ٢١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (227,2,'35',2,'Al-Baqarah','البَقَرَة',9,11,220,'وَيَسۡـَٔلُونَكَ عَنِ اِ۬لۡمَحِيضِۖ قُلۡ هُوَ أَذٗى فَاَعۡتَزِلُواْ اُ۬لنِّسَآءَ فِي اِ۬لۡمَحِيضِ وَلَا تَقۡرَبُوهُنَّ حَتَّىٰ يَطۡهُرۡنَۖ فَإِذَا تَطَهَّرۡنَ فَأۡتُوهُنَّ مِنۡ حَيۡثُ أَمَرَكُمُ اُ۬للَّهُۚ إِنَّ اَ۬للَّهَ يُحِبُّ اُ۬لتَّوَّٰبِينَ وَيُحِبُّ اُ۬لۡمُتَطَهِّرِينَ ٢٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (228,2,'35',2,'Al-Baqarah','البَقَرَة',12,14,221,'نِسَآؤُكُمۡ حَرۡثٞ لَّكُمۡ فَأۡتُواْ حَرۡثَكُمۡ أَنّۭيٰ شِئۡتُمۡۖ وَقَدِّمُواْ لِأَنفُسِكُمۡۚ وَاَتَّقُواْ اُ۬للَّهَ وَاَعۡلَمُوٓاْ أَنَّكُم مُّلَٰقُوهُۗ وَبَشِّرِ اِ۬لۡمُؤۡمِنِينَ ٢٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (229,2,'35',2,'Al-Baqarah','البَقَرَة',14,15,222,'وَلَا تَجۡعَلُواْ اُ۬للَّهَ عُرۡضَةٗ لِّأَيۡمَٰنِكُمۡ أَن تَبَرُّواْ وَتَتَّقُواْ وَتُصۡلِحُواْ بَيۡنَ اَ۬لنّ۪اسِۗ وَاَللَّهُ سَمِيعٌ عَلِيمٞ ٢٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (230,2,'36',2,'Al-Baqarah','البَقَرَة',1,2,223,'لَّا يُؤَاخِذُكُمُ اُ۬للَّهُ بِاللَّغۡوِ فِيٓ أَيۡمَٰنِكُمۡ وَلَٰكِن يُؤَاخِذُكُم بِمَا كَسَبَتۡ قُلُوبُكُمۡۗ وَاَللَّهُ غَفُورٌ حَلِيمٞ ٢٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (231,2,'36',2,'Al-Baqarah','البَقَرَة',2,3,224,'۞ لِّلَّذِينَ يُؤۡلُونَ مِن نِّسَآئِهِمۡ تَرَبُّصُ أَرۡبَعَةِ أَشۡهُرٖۖ فَإِن فَآءُو فَإِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٞ ٢٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (232,2,'36',2,'Al-Baqarah','البَقَرَة',3,4,225,'وَإِنۡ عَزَمُواْ اُ۬لطَّلَٰقَ فَإِنَّ اَ۬للَّهَ سَمِيعٌ عَلِيمٞ ٢٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (233,2,'36',2,'Al-Baqarah','البَقَرَة',4,8,226,'وَاَلۡمُطَلَّقَٰتُ يَتَرَبَّصۡنَ بِأَنفُسِهِنَّ ثَلَٰثَةَ قُرُوٓءٖۚ وَلَا يَحِلُّ لَهُنَّ أَن يَكۡتُمۡنَ مَا خَلَقَ اَ۬للَّهُ فِيٓ أَرۡحَامِهِنَّ إِن كُنَّ يُؤۡمِنَّ بِاللَّهِ وَاَلۡيَوۡمِ اِ۬لۡأٓخِرِۚ وَبُعُولَتُهُنَّ أَحَقُّ بِرَدِّهِنَّ فِي ذَٰلِكَ إِنۡ أَرَادُوٓاْ إِصۡلَٰحٗاۚ وَلَهُنَّ مِثۡلُ اُ۬لَّذِي عَلَيۡهِنَّ بِالۡمَعۡرُوفِۚ وَلِلرِّجَالِ عَلَيۡهِنَّ دَرَجَةٞۗ وَاَللَّهُ عَزِيزٌ حَكِيمٌ ٢٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (234,2,'36',2,'Al-Baqarah','البَقَرَة',8,13,227,'اِ۬لطَّلَٰقُ مَرَّتَانِۖ فَإِمۡسَاكُۢ بِمَعۡرُوفٍ أَوۡ تَسۡرِيحُۢ بِإِحۡسَٰنٖۗ وَلَا يَحِلُّ لَكُمۡ أَن تَأۡخُذُواْ مِمَّآ ءَاتَيۡتُمُوهُنَّ شَيۡـًٔا إِلَّآ أَن يَخَافَآ أَلَّا يُقِيمَا حُدُودَ اَ۬للَّهِۖ فَإِنۡ خِفۡتُمۡ أَلَّا يُقِيمَا حُدُودَ اَ۬للَّهِ فَلَا جُنَاحَ عَلَيۡهِمَا فِيمَا اَ۪فۡتَدَتۡ بِهِۦۗ تِلۡكَ حُدُودُ اُ۬للَّهِ فَلَا تَعۡتَدُوهَاۚ وَمَن يَتَعَدَّ حُدُودَ اَ۬للَّهِ فَأُوْلَٰٓئِكَ هُمُ اُ۬لظَّٰلِمُونَ ٢٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (235,2,'36',2,'Al-Baqarah','البَقَرَة',13,15,228,'فَإِن طَلَّقَهَا فَلَا تَحِلُّ لَهُۥ مِنۢ بَعۡدُ حَتَّىٰ تَنكِحَ زَوۡجًا غَيۡرَهُۥۗ فَإِن طَلَّقَهَا فَلَا جُنَاحَ عَلَيۡهِمَآ أَن يَتَرَاجَعَآ إِن ظَنَّآ أَن يُقِيمَا حُدُودَ اَ۬للَّهِۗ وَتِلۡكَ حُدُودُ اُ۬للَّهِ يُبَيِّنُهَا لِقَوۡمٖ يَعۡلَمُونَ ٢٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (236,2,'37',2,'Al-Baqarah','البَقَرَة',1,5,229,'وَإِذَا طَلَّقۡتُمُ اُ۬لنِّسَآءَ فَبَلَغۡنَ أَجَلَهُنَّ فَأَمۡسِكُوهُنَّ بِمَعۡرُوفٍ أَوۡ سَرِّحُوهُنَّ بِمَعۡرُوفٖۚ وَلَا تُمۡسِكُوهُنَّ ضِرَارٗا لِّتَعۡتَدُواْۚ وَمَن يَفۡعَلۡ ذَٰلِكَ فَقَد ظَّلَمَ نَفۡسَهُۥۚ وَلَا تَتَّخِذُوٓاْ ءَايَٰتِ اِ۬للَّهِ هُزُؤٗاۚ وَاَذۡكُرُواْ نِعۡمَتَ اَ۬للَّهِ عَلَيۡكُمۡ وَمَآ أَنزَلَ عَلَيۡكُم مِّنَ اَ۬لۡكِتَٰبِ وَاَلۡحِكۡمَةِ يَعِظُكُم بِهِۦۚ وَاَتَّقُواْ اُ۬للَّهَ وَاَعۡلَمُوٓاْ أَنَّ اَ۬للَّهَ بِكُلِّ شَيۡءٍ عَلِيمٞ ٢٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (237,2,'37',2,'Al-Baqarah','البَقَرَة',5,9,230,'وَإِذَا طَلَّقۡتُمُ اُ۬لنِّسَآءَ فَبَلَغۡنَ أَجَلَهُنَّ فَلَا تَعۡضُلُوهُنَّ أَن يَنكِحۡنَ أَزۡوَٰجَهُنَّ إِذَا تَرَٰضَوۡاْ بَيۡنَهُم بِالۡمَعۡرُوفِۗ ذَٰلِكَ يُوعَظُ بِهِۦ مَن كَانَ مِنكُمۡ يُؤۡمِنُ بِاللَّهِ وَاَلۡيَوۡمِ اِ۬لۡأٓخِرِۗ ذَٰلِكُمۡ أَزۡكَىٰ لَكُمۡ وَأَطۡهَرُۗ وَاَللَّهُ يَعۡلَمُ وَأَنتُمۡ لَا تَعۡلَمُونَ ٢٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (238,2,'37',2,'Al-Baqarah','البَقَرَة',9,15,231,'۞ وَاَلۡوَٰلِدَٰتُ يُرۡضِعۡنَ أَوۡلَٰدَهُنَّ حَوۡلَيۡنِ كَامِلَيۡنِۖ لِمَنۡ أَرَادَ أَن يُتِمَّ اَ۬لرَّضَاعَةَۚ وَعَلَى اَ۬لۡمَوۡلُودِ لَهُۥ رِزۡقُهُنَّ وَكِسۡوَتُهُنَّ بِالۡمَعۡرُوفِۚ لَا تُكَلَّفُ نَفۡسٌ إِلَّا وُسۡعَهَاۚ لَا تُضَآرُّ وَٰلِدَةُۢ بِوَلَدِهَا وَلَا مَوۡلُودٞ لَّهُۥ بِوَلَدِهِۦۚ وَعَلَى اَ۬لۡوَارِثِ مِثۡلُ ذَٰلِكَۗ فَإِنۡ أَرَادَا فِصَالًا عَن تَرَاضٖ مِّنۡهُمَا وَتَشَاوُرٖ فَلَا جُنَاحَ عَلَيۡهِمَاۗ وَإِنۡ أَرَدتُّمۡ أَن تَسۡتَرۡضِعُوٓاْ أَوۡلَٰدَكُمۡ فَلَا جُنَاحَ عَلَيۡكُمۡ إِذَا سَلَّمۡتُم مَّآ ءَاتَيۡتُم بِالۡمَعۡرُوفِۗ وَاَتَّقُواْ اُ۬للَّهَ وَاَعۡلَمُوٓاْ أَنَّ اَ۬للَّهَ بِمَا تَعۡمَلُونَ بَصِيرٞ ٢٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (239,2,'38',2,'Al-Baqarah','البَقَرَة',1,3,232,'وَاَلَّذِينَ يُتَوَفَّوۡنَ مِنكُمۡ وَيَذَرُونَ أَزۡوَٰجٗا يَتَرَبَّصۡنَ بِأَنفُسِهِنَّ أَرۡبَعَةَ أَشۡهُرٖ وَعَشۡرٗاۖ فَإِذَا بَلَغۡنَ أَجَلَهُنَّ فَلَا جُنَاحَ عَلَيۡكُمۡ فِيمَا فَعَلۡنَ فِيٓ أَنفُسِهِنَّ بِالۡمَعۡرُوفِۗ وَاَللَّهُ بِمَا تَعۡمَلُونَ خَبِيرٞ ٢٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (240,2,'38',2,'Al-Baqarah','البَقَرَة',4,9,233,'وَلَا جُنَاحَ عَلَيۡكُمۡ فِيمَا عَرَّضۡتُم بِهِۦ مِنۡ خِطۡبَةِ اِ۬لنِّسَآءِ اَ۬وۡ أَكۡنَنتُمۡ فِيٓ أَنفُسِكُمۡۚ عَلِمَ اَ۬للَّهُ أَنَّكُمۡ سَتَذۡكُرُونَهُنَّ وَلَٰكِن لَّا تُوَاعِدُوهُنَّ سِرًّا إِلَّآ أَن تَقُولُواْ قَوۡلٗا مَّعۡرُوفٗاۚ وَلَا تَعۡزِمُواْ عُقۡدَةَ اَ۬لنِّكَاحِ حَتَّىٰ يَبۡلُغَ اَ۬لۡكِتَٰبُ أَجَلَهُۥۚ وَاَعۡلَمُوٓاْ أَنَّ اَ۬للَّهَ يَعۡلَمُ مَا فِيٓ أَنفُسِكُمۡ فَاَحۡذَرُوهُۚ وَاَعۡلَمُوٓاْ أَنَّ اَ۬للَّهَ غَفُورٌ حَلِيمٞ ٢٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (241,2,'38',2,'Al-Baqarah','البَقَرَة',9,12,234,'۞ لَّا جُنَاحَ عَلَيۡكُمۡ إِن طَلَّقۡتُمُ اُ۬لنِّسَآءَ مَا لَمۡ تَمَسُّوهُنَّ أَوۡ تَفۡرِضُواْ لَهُنَّ فَرِيضَةٗۚ وَمَتِّعُوهُنَّ عَلَى اَ۬لۡمُوسِعِ قَدۡرُهُۥ وَعَلَى اَ۬لۡمُقۡتِرِ قَدۡرُهُۥ مَتَٰعَۢا بِالۡمَعۡرُوفِۖ حَقًّا عَلَى اَ۬لۡمُحۡسِنِينَ ٢٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (242,2,'38',2,'Al-Baqarah','البَقَرَة',12,15,235,'وَإِن طَلَّقۡتُمُوهُنَّ مِن قَبۡلِ أَن تَمَسُّوهُنَّ وَقَدۡ فَرَضۡتُمۡ لَهُنَّ فَرِيضَةٗ فَنِصۡفُ مَا فَرَضۡتُمۡ إِلَّآ أَن يَعۡفُونَ أَوۡ يَعۡفُوَاْ اَ۬لَّذِي بِيَدِهِۦ عُقۡدَةُ اُ۬لنِّكَاحِۚ وَأَن تَعۡفُوٓاْ أَقۡرَبُ لِلتَّقۡوۭيٰۚ وَلَا تَنسَوُاْ اُ۬لۡفَضۡلَ بَيۡنَكُمۡۚ إِنَّ اَ۬للَّهَ بِمَا تَعۡمَلُونَ بَصِيرٌ ٢٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (243,2,'39',2,'Al-Baqarah','البَقَرَة',1,2,236,'حَٰفِظُواْ عَلَى اَ۬لصَّلَوَٰتِ وَاَلصَّلَوٰةِ اِ۬لۡوُسۡطۭيٰ وَقُومُواْ لِلَّهِ قَٰنِتِينَ ٢٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (244,2,'39',2,'Al-Baqarah','البَقَرَة',2,3,237,'فَإِنۡ خِفۡتُمۡ فَرِجَالًا أَوۡ رُكۡبَانٗاۖ فَإِذَآ أَمِنتُمۡ فَاَذۡكُرُواْ اُ۬للَّهَ كَمَا عَلَّمَكُم مَّا لَمۡ تَكُونُواْ تَعۡلَمُونَ ٢٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (245,2,'39',2,'Al-Baqarah','البَقَرَة',4,7,238,'وَاَلَّذِينَ يُتَوَفَّوۡنَ مِنكُمۡ وَيَذَرُونَ أَزۡوَٰجٗا وَصِيَّةٗ لِّأَزۡوَٰجِهِم مَّتَٰعًا إِلَى اَ۬لۡحَوۡلِ غَيۡرَ إِخۡرَاجٖۚ فَإِنۡ خَرَجۡنَ فَلَا جُنَاحَ عَلَيۡكُمۡ فِي مَا فَعَلۡنَ فِيٓ أَنفُسِهِنَّ مِن مَّعۡرُوفٖۗ وَاَللَّهُ عَزِيزٌ حَكِيمٞ ٢٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (246,2,'39',2,'Al-Baqarah','البَقَرَة',7,8,239,'وَلِلۡمُطَلَّقَٰتِ مَتَٰعُۢ بِالۡمَعۡرُوفِۖ حَقًّا عَلَى اَ۬لۡمُتَّقِينَ ٢٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (247,2,'39',2,'Al-Baqarah','البَقَرَة',8,9,240,'كَذَٰلِكَ يُبَيِّنُ اُ۬للَّهُ لَكُمۡ ءَايَٰتِهِۦ لَعَلَّكُمۡ تَعۡقِلُونَ ٢٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (248,2,'39',2,'Al-Baqarah','البَقَرَة',9,12,241,'۞ أَلَمۡ تَرَ إِلَى اَ۬لَّذِينَ خَرَجُواْ مِن دِيٰ۪رِهِمۡ وَهُمۡ أُلُوفٌ حَذَرَ اَ۬لۡمَوۡتِ فَقَالَ لَهُمُ اُ۬للَّهُ مُوتُواْ ثُمَّ أَحۡيَٰهُمۡۚ إِنَّ اَ۬للَّهَ لَذُو فَضۡلٍ عَلَى اَ۬لنّ۪اسِ وَلَٰكِنَّ أَكۡثَرَ اَ۬لنّ۪اسِ لَا يَشۡكُرُونَ ٢٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (249,2,'39',2,'Al-Baqarah','البَقَرَة',13,13,242,'وَقَٰتِلُواْ فِي سَبِيلِ اِ۬للَّهِ وَاَعۡلَمُوٓاْ أَنَّ اَ۬للَّهَ سَمِيعٌ عَلِيمٞ ٢٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (250,2,'39',2,'Al-Baqarah','البَقَرَة',13,15,243,'مَّن ذَا اَ۬لَّذِي يُقۡرِضُ اُ۬للَّهَ قَرۡضًا حَسَنٗا فَيُضَٰعِفُهُۥ لَهُۥٓ أَضۡعَافٗا كَثِيرَةٗۚ وَاَللَّهُ يَقۡبِضُ وَيَبۡصُۜطُ وَإِلَيۡهِ تُرۡجَعُونَ ٢٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (251,2,'40',2,'Al-Baqarah','البَقَرَة',1,6,244,'أَلَمۡ تَرَ إِلَى اَ۬لۡمَلَإِ مِنۢ بَنِيٓ إِسۡرَٰٓءِيلَ مِنۢ بَعۡدِ مُوسۭيٰٓ إِذۡ قَالُواْ لِنَبِيّٖ لَّهُمُ اُ۪بۡعَثۡ لَنَا مَلِكٗا نُّقَٰتِلۡ فِي سَبِيلِ اِ۬للَّهِۖ قَالَ هَلۡ عَسَيۡتُمۡ إِن كُتِبَ عَلَيۡكُمُ اُ۬لۡقِتَالُ أَلَّا تُقَٰتِلُواْۖ قَالُواْ وَمَا لَنَآ أَلَّا نُقَٰتِلَ فِي سَبِيلِ اِ۬للَّهِ وَقَدۡ أُخۡرِجۡنَا مِن دِيٰ۪رِنَا وَأَبۡنَآئِنَاۖ فَلَمَّا كُتِبَ عَلَيۡهِمِ اِ۬لۡقِتَالُ تَوَلَّوۡاْ إِلَّا قَلِيلٗا مِّنۡهُمۡۗ وَاَللَّهُ عَلِيمُۢ بِالظَّٰلِمِينَ ٢٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (252,2,'40',2,'Al-Baqarah','البَقَرَة',6,11,245,'وَقَالَ لَهُمۡ نَبِيُّهُمۡ إِنَّ اَ۬للَّهَ قَدۡ بَعَثَ لَكُمۡ طَالُوتَ مَلِكٗاۚ قَالُوٓاْ أَنّۭيٰ يَكُونُ لَهُ اُ۬لۡمُلۡكُ عَلَيۡنَا وَنَحۡنُ أَحَقُّ بِالۡمُلۡكِ مِنۡهُ وَلَمۡ يُؤۡتَ سَعَةٗ مِّنَ اَ۬لۡمَالِۚ قَالَ إِنَّ اَ۬للَّهَ اَ۪صۡطَفَىٰهُ عَلَيۡكُمۡ وَزَادَهُۥ بَسۡطَةٗ فِي اِ۬لۡعِلۡمِ وَاَلۡجِسۡمِۖ وَاَللَّهُ يُؤۡتِي مُلۡكَهُۥ مَن يَشَآءُۚ وَاَللَّهُ وَٰسِعٌ عَلِيمٞ ٢٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (253,2,'40',2,'Al-Baqarah','البَقَرَة',12,15,246,'۞ وَقَالَ لَهُمۡ نَبِيُّهُمۡ إِنَّ ءَايَةَ مُلۡكِهِۦٓ أَن يَأۡتِيَكُمُ اُ۬لتَّابُوتُ فِيهِ سَكِينَةٞ مِّن رَّبِّكُمۡ وَبَقِيَّةٞ مِّمَّا تَرَكَ ءَالُ مُوسۭيٰ وَءَالُ هَٰرُونَ تَحۡمِلُهُ اُ۬لۡمَلَٰٓئِكَةُۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لَّكُمۡ إِن كُنتُم مُّؤۡمِنِينَ ٢٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (254,2,'41',2,'Al-Baqarah','البَقَرَة',1,8,247,'فَلَمَّا فَصَلَ طَالُوتُ بِالۡجُنُودِ قَالَ إِنَّ اَ۬للَّهَ مُبۡتَلِيكُم بِنَهَرٖ فَمَن شَرِبَ مِنۡهُ فَلَيۡسَ مِنِّي وَمَن لَّمۡ يَطۡعَمۡهُ فَإِنَّهُۥ مِنِّيَ إِلَّا مَنِ اِ۪غۡتَرَفَ غَرۡفَةَۢ بِيَدِهِۦۚ فَشَرِبُواْ مِنۡهُ إِلَّا قَلِيلٗا مِّنۡهُمۡۚ فَلَمَّا جَاوَزَهُۥ هُوَ وَاَلَّذِينَ ءَامَنُواْ مَعَهُۥ قَالُواْ لَا طَاقَةَ لَنَا اَ۬لۡيَوۡمَ بِجَالُوتَ وَجُنُودِهِۦۚ قَالَ اَ۬لَّذِينَ يَظُنُّونَ أَنَّهُم مُّلَٰقُواْ اُ۬للَّهِ كَم مِّن فِئَةٖ قَلِيلَةٍ غَلَبَتۡ فِئَةٗ كَثِيرَةَۢ بِإِذۡنِ اِ۬للَّهِۗ وَاَللَّهُ مَعَ اَ۬لصَّٰبِرِينَ ٢٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (255,2,'41',2,'Al-Baqarah','البَقَرَة',8,10,248,'وَلَمَّا بَرَزُواْ لِجَالُوتَ وَجُنُودِهِۦ قَالُواْ رَبَّنَآ أَفۡرِغۡ عَلَيۡنَا صَبۡرٗا وَثَبِّتۡ أَقۡدَامَنَا وَاَنصُرۡنَا عَلَى اَ۬لۡقَوۡمِ اِ۬لۡكٰ۪فِرِينَ ٢٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (256,2,'41',2,'Al-Baqarah','البَقَرَة',10,14,249,'فَهَزَمُوهُم بِإِذۡنِ اِ۬للَّهِ وَقَتَلَ دَاوُۥدُ جَالُوتَ وَءَاتَىٰهُ اُ۬للَّهُ اُ۬لۡمُلۡكَ وَاَلۡحِكۡمَةَ وَعَلَّمَهُۥ مِمَّا يَشَآءُۗ وَلَوۡلَا دَفۡعُ اُ۬للَّهِ اِ۬لنَّاسَ بَعۡضَهُم بِبَعۡضٖ لَّفَسَدَتِ اِ۬لۡأَرۡضُ وَلَٰكِنَّ اَ۬للَّهَ ذُو فَضۡلٍ عَلَى اَ۬لۡعَٰلَمِينَ ٢٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (257,2,'41',2,'Al-Baqarah','البَقَرَة',14,15,250,'تِلۡكَ ءَايَٰتُ اُ۬للَّهِ نَتۡلُوهَا عَلَيۡكَ بِالۡحَقِّۚ وَإِنَّكَ لَمِنَ اَ۬لۡمُرۡسَلِينَ ٢٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (258,3,'42',2,'Al-Baqarah','البَقَرَة',1,6,251,'۞ تِلۡكَ اَ۬لرُّسُلُ فَضَّلۡنَا بَعۡضَهُمۡ عَلَىٰ بَعۡضٖۘ مِّنۡهُم مَّن كَلَّمَ اَ۬للَّهُۖ وَرَفَعَ بَعۡضَهُمۡ دَرَجَٰتٖۚ وَءَاتَيۡنَا عِيسَى اَ۪بۡنَ مَرۡيَمَ اَ۬لۡبَيِّنَٰتِ وَأَيَّدۡنَٰهُ بِرُوحِ اِ۬لۡقُدُسِۗ وَلَوۡ شَآءَ اَ۬للَّهُ مَا اَ۪قۡتَتَلَ اَ۬لَّذِينَ مِنۢ بَعۡدِهِم مِّنۢ بَعۡدِ مَا جَآءَتۡهُمُ اُ۬لۡبَيِّنَٰتُ وَلَٰكِنِ اِ۪خۡتَلَفُواْ فَمِنۡهُم مَّنۡ ءَامَنَ وَمِنۡهُم مَّن كَفَرَۚ وَلَوۡ شَآءَ اَ۬للَّهُ مَا اَ۪قۡتَتَلُواْ وَلَٰكِنَّ اَ۬للَّهَ يَفۡعَلُ مَا يُرِيدُ ٢٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (259,3,'42',2,'Al-Baqarah','البَقَرَة',6,8,252,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُوٓاْ أَنفِقُواْ مِمَّا رَزَقۡنَٰكُم مِّن قَبۡلِ أَن يَأۡتِيَ يَوۡمٞ لَّا بَيۡعَ فِيهِ وَلَا خُلَّةَ وَلَا شَفَٰعَةَۗ وَاَلۡكَٰفِرُونَ هُمُ اُ۬لظَّٰلِمُونَ ٢٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (260,3,'42',2,'Al-Baqarah','البَقَرَة',8,13,253,'اَ۬للَّهُ لَآ إِلَٰهَ إِلَّا هُوَ اَ۬لۡحَيُّ اُ۬لۡقَيُّومُۚ لَا تَأۡخُذُهُۥ سِنَةٞ وَلَا نَوۡمٞۚ لَّهُۥ مَا فِي اِ۬لسَّمَٰوَٰتِ وَمَا فِي اِ۬لۡأَرۡضِۗ مَن ذَا اَ۬لَّذِي يَشۡفَعُ عِندَهُۥٓ إِلَّا بِإِذۡنِهِۦۚ يَعۡلَمُ مَا بَيۡنَ أَيۡدِيهِمۡ وَمَا خَلۡفَهُمۡۖ وَلَا يُحِيطُونَ بِشَيۡءٖ مِّنۡ عِلۡمِهِۦٓ إِلَّا بِمَا شَآءَۚ وَسِعَ كُرۡسِيُّهُ اُ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضَۖ وَلَا يَـُٔودُهُۥ حِفۡظُهُمَاۚ وَهۡوَ اَ۬لۡعَلِيُّ اُ۬لۡعَظِيمُ ٢٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (261,3,'42',2,'Al-Baqarah','البَقَرَة',13,15,254,'لَآ إِكۡرَاهَ فِي اِ۬لدِّينِۖ قَد تَّبَيَّنَ اَ۬لرُّشۡدُ مِنَ اَ۬لۡغَيِّۚ فَمَن يَكۡفُرۡ بِالطَّٰغُوتِ وَيُؤۡمِنۢ بِاللَّهِ فَقَدِ اِ۪سۡتَمۡسَكَ بِالۡعُرۡوَةِ اِ۬لۡوُثۡقۭيٰ لَا اَ۪نفِصَامَ لَهَاۗ وَاَللَّهُ سَمِيعٌ عَلِيمٌ ٢٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (262,3,'43',2,'Al-Baqarah','البَقَرَة',1,1,255,'اِ۬للَّهُ وَلِيُّ اُ۬لَّذِينَ ءَامَنُواْ يُخۡرِجُهُم مِّنَ اَ۬لظُّلُمَٰتِ إِلَى اَ۬لنُّورِ ٢٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (263,3,'43',2,'Al-Baqarah','البَقَرَة',2,4,256,'وَاَلَّذِينَ كَفَرُوٓاْ أَوۡلِيَآؤُهُمُ اُ۬لطَّٰغُوتُ يُخۡرِجُونَهُم مِّنَ اَ۬لنُّورِ إِلَى اَ۬لظُّلُمَٰتِۗ أُوْلَٰٓئِكَ أَصۡحَٰبُ اُ۬لنّ۪ارِۖ هُمۡ فِيهَا خَٰلِدُونَ ٢٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (264,3,'43',2,'Al-Baqarah','البَقَرَة',4,8,257,'۞ أَلَمۡ تَرَ إِلَى اَ۬لَّذِي حَآجَّ إِبۡرَٰهِـۧمَ فِي رَبِّهِۦٓ أَنۡ ءَاتَىٰهُ اُ۬للَّهُ اُ۬لۡمُلۡكَ إِذۡ قَالَ إِبۡرَٰهِـۧمُ رَبِّيَ اَ۬لَّذِي يُحۡيِۦ وَيُمِيتُ قَالَ أَنَا۠ أُحۡيِۦ وَأُمِيتُۖ قَالَ إِبۡرَٰهِـۧمُ فَإِنَّ اَ۬للَّهَ يَأۡتِي بِالشَّمۡسِ مِنَ اَ۬لۡمَشۡرِقِ فَأۡتِ بِهَا مِنَ اَ۬لۡمَغۡرِبِ فَبُهِتَ اَ۬لَّذِي كَفَرَۗ وَاَللَّهُ لَا يَهۡدِي اِ۬لۡقَوۡمَ اَ۬لظَّٰلِمِينَ ٢٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (265,3,'43',2,'Al-Baqarah','البَقَرَة',8,15,258,'أَوۡ كَاَلَّذِي مَرَّ عَلَىٰ قَرۡيَةٖ وَهۡيَ خَاوِيَةٌ عَلَىٰ عُرُوشِهَا قَالَ أَنّۭيٰ يُحۡيِۦ هَٰذِهِ اِ۬للَّهُ بَعۡدَ مَوۡتِهَاۖ فَأَمَاتَهُ اُ۬للَّهُ مِاْئَةَ عَامٖ ثُمَّ بَعَثَهُۥۖ قَالَ كَمۡ لَبِثتَّۖ قَالَ لَبِثتُّ يَوۡمًا أَوۡ بَعۡضَ يَوۡمٖۖ قَالَ بَل لَّبِثتَّ مِاْئَةَ عَامٖ فَاَنظُرۡ إِلَىٰ طَعَامِكَ وَشَرَابِكَ لَمۡ يَتَسَنَّهۡۖ وَاَنظُرۡ إِلَىٰ حِم۪ارِكَ وَلِنَجۡعَلَكَ ءَايَةٗ لِّلنّ۪اسِۖ وَاَنظُرۡ إِلَى اَ۬لۡعِظَامِ كَيۡفَ نُنشِرُهَا ثُمَّ نَكۡسُوهَا لَحۡمٗاۚ فَلَمَّا تَبَيَّنَ لَهُۥ قَالَ أَعۡلَمُ أَنَّ اَ۬للَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ ٢٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (266,3,'44',2,'Al-Baqarah','البَقَرَة',1,4,259,'وَإِذۡ قَالَ إِبۡرَٰهِـۧمُ رَبِّ أَر۪نِي كَيۡفَ تُحۡيِ اِ۬لۡمَوۡتۭيٰۖ قَالَ أَوَلَمۡ تُؤۡمِنۖ قَالَ بَلَىٰ وَلَٰكِن لِّيَطۡمَئِنَّ قَلۡبِيۖ قَالَ فَخُذۡ أَرۡبَعَةٗ مِّنَ اَ۬لطَّيۡرِ فَصُرۡهُنَّ إِلَيۡكَ ثُمَّ اَ۪جۡعَلۡ عَلَىٰ كُلِّ جَبَلٖ مِّنۡهُنَّ جُزۡءٗا ثُمَّ اَ۟دۡعُهُنَّ يَأۡتِينَكَ سَعۡيٗاۚ وَاَعۡلَمۡ أَنَّ اَ۬للَّهَ عَزِيزٌ حَكِيمٞ ٢٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (267,3,'44',2,'Al-Baqarah','البَقَرَة',5,7,260,'۞ مَّثَلُ اُ۬لَّذِينَ يُنفِقُونَ أَمۡوَٰلَهُمۡ فِي سَبِيلِ اِ۬للَّهِ كَمَثَلِ حَبَّةٍ أَنۢبَتَت سَّبۡعَ سَنَابِلَ فِي كُلِّ سُنۢبُلَةٖ مِّاْئَةُ حَبَّةٖۗ وَاَللَّهُ يُضَٰعِفُ لِمَن يَشَآءُۗ وَاَللَّهُ وَٰسِعٌ عَلِيمٌ ٢٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (268,3,'44',2,'Al-Baqarah','البَقَرَة',7,10,261,'اِ۬لَّذِينَ يُنفِقُونَ أَمۡوَٰلَهُمۡ فِي سَبِيلِ اِ۬للَّهِ ثُمَّ لَا يُتۡبِعُونَ مَآ أَنفَقُواْ مَنّٗا وَلَآ أَذٗى لَّهُمۡ أَجۡرُهُمۡ عِندَ رَبِّهِمۡ وَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ ٢٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (269,3,'44',2,'Al-Baqarah','البَقَرَة',10,11,262,'قَوۡلٞ مَّعۡرُوفٞ وَمَغۡفِرَةٌ خَيۡرٞ مِّن صَدَقَةٖ يَتۡبَعُهَآ أَذٗىۗ وَاَللَّهُ غَنِيٌّ حَلِيمٞ ٢٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (270,3,'44',2,'Al-Baqarah','البَقَرَة',11,15,263,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ لَا تُبۡطِلُواْ صَدَقَٰتِكُم بِالۡمَنِّ وَاَلۡأَذَىٰ كَاَلَّذِي يُنفِقُ مَالَهُۥ رِئَآءَ اَ۬لنّ۪اسِ وَلَا يُؤۡمِنُ بِاللَّهِ وَاَلۡيَوۡمِ اِ۬لۡأٓخِرِۖ فَمَثَلُهُۥ كَمَثَلِ صَفۡوَانٍ عَلَيۡهِ تُرَابٞ فَأَصَابَهُۥ وَابِلٞ فَتَرَكَهُۥ صَلۡدٗاۖ لَّا يَقۡدِرُونَ عَلَىٰ شَيۡءٖ مِّمَّا كَسَبُواْۗ وَاَللَّهُ لَا يَهۡدِي اِ۬لۡقَوۡمَ اَ۬لۡكٰ۪فِرِينَ ٢٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (271,3,'45',2,'Al-Baqarah','البَقَرَة',1,4,264,'وَمَثَلُ اُ۬لَّذِينَ يُنفِقُونَ أَمۡوَٰلَهُمُ اُ۪بۡتِغَآءَ مَرۡضَاتِ اِ۬للَّهِ وَتَثۡبِيتٗا مِّنۡ أَنفُسِهِمۡ كَمَثَلِ جَنَّةِۢ بِرُبۡوَةٍ أَصَابَهَا وَابِلٞ فَـَٔاتَتۡ أُكۡلَهَا ضِعۡفَيۡنِ فَإِن لَّمۡ يُصِبۡهَا وَابِلٞ فَطَلّٞۗ وَاَللَّهُ بِمَا تَعۡمَلُونَ بَصِيرٌ ٢٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (272,3,'45',2,'Al-Baqarah','البَقَرَة',4,8,265,'۞ أَيَوَدُّ أَحَدُكُمۡ أَن تَكُونَ لَهُۥ جَنَّةٞ مِّن نَّخِيلٖ وَأَعۡنَابٖ تَجۡرِي مِن تَحۡتِهَا اَ۬لۡأَنۡهَٰرُ لَهُۥ فِيهَا مِن كُلِّ اِ۬لثَّمَرَٰتِ وَأَصَابَهُ اُ۬لۡكِبَرُ وَلَهُۥ ذُرِّيَّةٞ ضُعَفَآءُ فَأَصَابَهَآ إِعۡصَارٞ فِيهِ نَارٞ فَاَحۡتَرَقَتۡۗ كَذَٰلِكَ يُبَيِّنُ اُ۬للَّهُ لَكُمُ اُ۬لۡأٓيَٰتِ لَعَلَّكُمۡ تَتَفَكَّرُونَ ٢٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (273,3,'45',2,'Al-Baqarah','البَقَرَة',8,12,266,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُوٓاْ أَنفِقُواْ مِن طَيِّبَٰتِ مَا كَسَبۡتُمۡ وَمِمَّآ أَخۡرَجۡنَا لَكُم مِّنَ اَ۬لۡأَرۡضِۖ وَلَا تَيَمَّمُواْ اُ۬لۡخَبِيثَ مِنۡهُ تُنفِقُونَ وَلَسۡتُم بِـَٔاخِذِيهِ إِلَّآ أَن تُغۡمِضُواْ فِيهِۚ وَاَعۡلَمُوٓاْ أَنَّ اَ۬للَّهَ غَنِيٌّ حَمِيدٌ ٢٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (274,3,'45',2,'Al-Baqarah','البَقَرَة',12,13,267,'اِ۬لشَّيۡطَٰنُ يَعِدُكُمُ اُ۬لۡفَقۡرَ وَيَأۡمُرۡكُم بِالۡفَحۡشَآءِۖ وَاَللَّهُ يَعِدُكُم مَّغۡفِرَةٗ مِّنۡهُ وَفَضۡلٗاۗ وَاَللَّهُ وَٰسِعٌ عَلِيمٞ ٢٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (275,3,'45',2,'Al-Baqarah','البَقَرَة',14,15,268,'يُؤۡتِي اِ۬لۡحِكۡمَةَ مَن يَشَآءُۚ وَمَن يُؤۡتَ اَ۬لۡحِكۡمَةَ فَقَدۡ أُوتِيَ خَيۡرٗا كَثِيرٗاۗ وَمَا يَذَّكَّرُ إِلَّآ أُوْلُواْ اُ۬لۡأَلۡبَٰبِ ٢٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (276,3,'46',2,'Al-Baqarah','البَقَرَة',1,2,269,'وَمَآ أَنفَقۡتُم مِّن نَّفَقَةٍ أَوۡ نَذَرۡتُم مِّن نَّذۡرٖ فَإِنَّ اَ۬للَّهَ يَعۡلَمُهُۥۗ وَمَا لِلظَّٰلِمِينَ مِنۡ أَنص۪ارٍ ٢٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (277,3,'46',2,'Al-Baqarah','البَقَرَة',2,5,270,'إِن تُبۡدُواْ اُ۬لصَّدَقَٰتِ فَنِع۪مَّا هِيَۖ وَإِن تُخۡفُوهَا وَتُؤۡتُوهَا اَ۬لۡفُقَرَآءَ فَهۡوَ خَيۡرٞ لَّكُمۡۚ وَنُكَفِّرُ عَنكُم مِّن سَيِّـَٔاتِكُمۡۗ وَاَللَّهُ بِمَا تَعۡمَلُونَ خَبِيرٞ ٢٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (278,3,'46',2,'Al-Baqarah','البَقَرَة',5,9,271,'۞ لَّيۡسَ عَلَيۡكَ هُدَىٰهُمۡ وَلَٰكِنَّ اَ۬للَّهَ يَهۡدِي مَن يَشَآءُۗ وَمَا تُنفِقُواْ مِنۡ خَيۡرٖ فَلِأَنفُسِكُمۡۚ وَمَا تُنفِقُونَ إِلَّا اَ۪بۡتِغَآءَ وَجۡهِ اِ۬للَّهِۚ وَمَا تُنفِقُواْ مِنۡ خَيۡرٖ يُوَفَّ إِلَيۡكُمۡ وَأَنتُمۡ لَا تُظۡلَمُونَ ٢٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (279,3,'46',2,'Al-Baqarah','البَقَرَة',9,13,272,'لِلۡفُقَرَآءِ اِ۬لَّذِينَ أُحۡصِرُواْ فِي سَبِيلِ اِ۬للَّهِ لَا يَسۡتَطِيعُونَ ضَرۡبٗا فِي اِ۬لۡأَرۡضِ يَحۡسِبُهُمُ اُ۬لۡجَاهِلُ أَغۡنِيَآءَ مِنَ اَ۬لتَّعَفُّفِ تَعۡرِفُهُم بِسِيمٰۭهُمۡ لَا يَسۡـَٔلُونَ اَ۬لنَّاسَ إِلۡحَافٗاۗ وَمَا تُنفِقُواْ مِنۡ خَيۡرٖ فَإِنَّ اَ۬للَّهَ بِهِۦ عَلِيمٌ ٢٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (280,3,'46',2,'Al-Baqarah','البَقَرَة',13,15,273,'اِ۬لَّذِينَ يُنفِقُونَ أَمۡوَٰلَهُم بِالَّيۡلِ وَاَلنَّه۪ارِ سِرّٗا وَعَلَانِيَةٗ فَلَهُمۡ أَجۡرُهُمۡ عِندَ رَبِّهِمۡ وَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ ٢٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (281,3,'47',2,'Al-Baqarah','البَقَرَة',1,5,274,'اَ۬لَّذِينَ يَأۡكُلُونَ اَ۬لرِّبَوٰاْ لَا يَقُومُونَ إِلَّا كَمَا يَقُومُ اُ۬لَّذِي يَتَخَبَّطُهُ اُ۬لشَّيۡطَٰنُ مِنَ اَ۬لۡمَسِّۚ ذَٰلِكَ بِأَنَّهُمۡ قَالُوٓاْ إِنَّمَا اَ۬لۡبَيۡعُ مِثۡلُ اُ۬لرِّبَوٰاْۗ وَأَحَلَّ اَ۬للَّهُ اُ۬لۡبَيۡعَ وَحَرَّمَ اَ۬لرِّبَوٰاْۚ فَمَن جَآءَهُۥ مَوۡعِظَةٞ مِّن رَّبِّهِۦ فَاَنتَهَىٰ فَلَهُۥ مَا سَلَفَ وَأَمۡرُهُۥٓ إِلَى اَ۬للَّهِۖ وَمَنۡ عَادَ فَأُوْلَٰٓئِكَ أَصۡحَٰبُ اُ۬لنّ۪ارِۖ هُمۡ فِيهَا خَٰلِدُونَ ٢٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (282,3,'47',2,'Al-Baqarah','البَقَرَة',5,6,275,'يَمۡحَقُ اُ۬للَّهُ اُ۬لرِّبَوٰاْ وَيُرۡبِي اِ۬لصَّدَقَٰتِۗ وَاَللَّهُ لَا يُحِبُّ كُلَّ كَفّ۪ارٍ أَثِيمٍ ٢٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (283,3,'47',2,'Al-Baqarah','البَقَرَة',7,9,276,'إِنَّ اَ۬لَّذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ وَأَقَامُواْ اُ۬لصَّلَوٰةَ وَءَاتَوُاْ اُ۬لزَّكَوٰةَ لَهُمۡ أَجۡرُهُمۡ عِندَ رَبِّهِمۡ وَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ ٢٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (284,3,'47',2,'Al-Baqarah','البَقَرَة',9,10,277,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ اُ۪تَّقُواْ اُ۬للَّهَ وَذَرُواْ مَا بَقِيَ مِنَ اَ۬لرِّبَوٰٓاْ إِن كُنتُم مُّؤۡمِنِينَ ٢٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (285,3,'47',2,'Al-Baqarah','البَقَرَة',10,12,278,'فَإِن لَّمۡ تَفۡعَلُواْ فَأۡذَنُواْ بِحَرۡبٖ مِّنَ اَ۬للَّهِ وَرَسُولِهِۦۖ وَإِن تُبۡتُمۡ فَلَكُمۡ رُءُوسُ أَمۡوَٰلِكُمۡ لَا تَظۡلِمُونَ وَلَا تُظۡلَمُونَ ٢٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (286,3,'47',2,'Al-Baqarah','البَقَرَة',12,14,279,'وَإِن كَانَ ذُو عُسۡرَةٖ فَنَظِرَةٌ إِلَىٰ مَيۡسَرَةٖۚ وَأَن تَصَّدَّقُواْ خَيۡرٞ لَّكُمۡۖ إِن كُنتُمۡ تَعۡلَمُونَ ٢٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (287,3,'47',2,'Al-Baqarah','البَقَرَة',14,15,280,'وَاَتَّقُواْ يَوۡمٗا تَرۡجِعُونَ فِيهِ إِلَى اَ۬للَّهِۖ ثُمَّ تُوَفَّىٰ كُلُّ نَفۡسٖ مَّا كَسَبَتۡ وَهُمۡ لَا يُظۡلَمُونَ ٢٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (288,3,'48',2,'Al-Baqarah','البَقَرَة',1,15,281,'۞ يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُوٓاْ إِذَا تَدَايَنتُم بِدَيۡنٍ إِلَىٰٓ أَجَلٖ مُّسَمّٗى فَاَكۡتُبُوهُۚ وَلۡيَكۡتُب بَّيۡنَكُمۡ كَاتِبُۢ بِالۡعَدۡلِۚ وَلَا يَأۡبَ كَاتِبٌ أَن يَكۡتُبَ كَمَا عَلَّمَهُ اُ۬للَّهُۚ فَلۡيَكۡتُبۡ وَلۡيُمۡلِلِ اِ۬لَّذِي عَلَيۡهِ اِ۬لۡحَقُّ وَلۡيَتَّقِ اِ۬للَّهَ رَبَّهُۥ وَلَا يَبۡخَسۡ مِنۡهُ شَيۡـٔٗاۚ فَإِن كَانَ اَ۬لَّذِي عَلَيۡهِ اِ۬لۡحَقُّ سَفِيهًا أَوۡ ضَعِيفًا أَوۡ لَا يَسۡتَطِيعُ أَن يُمِلَّ هُوَ فَلۡيُمۡلِلۡ وَلِيُّهُۥ بِالۡعَدۡلِۚ وَاَسۡتَشۡهِدُواْ شَهِيدَيۡنِ مِن رِّجَالِكُمۡۖ فَإِن لَّمۡ يَكُونَا رَجُلَيۡنِ فَرَجُلٞ وَاَمۡرَأَتَانِ مِمَّن تَرۡضَوۡنَ مِنَ اَ۬لشُّهَدَآءِ اَ۬ن تَضِلَّ إِحۡدۭىٰهُمَا فَتُذۡكِرَ إِحۡدۭىٰهُمَا اَ۬لۡأُخۡر۪يٰۚ وَلَا يَأۡبَ اَ۬لشُّهَدَآءُ اِ۪ذَا مَا دُعُواْۚ وَلَا تَسۡـَٔمُوٓاْ أَن تَكۡتُبُوهُ صَغِيرًا أَوۡ كَبِيرًا إِلَىٰٓ أَجَلِهِۦۚ ذَٰلِكُمۡ أَقۡسَطُ عِندَ اَ۬للَّهِ وَأَقۡوَمُ لِلشَّهَٰدَةِ وَأَدۡنَىٰٓ أَلَّا تَرۡتَابُوٓاْ إِلَّآ أَن تَكُونَ تِجَٰرَةٌ حَاضِرَةٞ تُدِيرُونَهَا بَيۡنَكُمۡ فَلَيۡسَ عَلَيۡكُمۡ جُنَاحٌ أَلَّا تَكۡتُبُوهَاۗ وَأَشۡهِدُوٓاْ إِذَا تَبَايَعۡتُمۡۚ وَلَا يُضَآرَّ كَاتِبٞ وَلَا شَهِيدٞۚ وَإِن تَفۡعَلُواْ فَإِنَّهُۥ فُسُوقُۢ بِكُمۡۗ وَاَتَّقُواْ اُ۬للَّهَۖ وَيُعَلِّمُكُمُ اُ۬للَّهُۗ وَاَللَّهُ بِكُلِّ شَيۡءٍ عَلِيمٞ ٢٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (289,3,'49',2,'Al-Baqarah','البَقَرَة',1,4,282,'وَإِن كُنتُمۡ عَلَىٰ سَفَرٖ وَلَمۡ تَجِدُواْ كَاتِبٗا فَرُهُنٞ مَّقۡبُوضَةٞۖ فَإِنۡ أَمِنَ بَعۡضُكُم بَعۡضٗا فَلۡيُؤَدِّ اِ۬لَّذِي اِ۟ؤۡتُمِنَ أَمَٰنَتَهُۥ وَلۡيَتَّقِ اِ۬للَّهَ رَبَّهُۥۗ وَلَا تَكۡتُمُواْ اُ۬لشَّهَٰدَةَۚ وَمَن يَكۡتُمۡهَا فَإِنَّهُۥٓ ءَاثِمٞ قَلۡبُهُۥۗ وَاَللَّهُ بِمَا تَعۡمَلُونَ عَلِيمٞ ٢٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (290,3,'49',2,'Al-Baqarah','البَقَرَة',4,7,283,'۞ لِّلَّهِ مَا فِي اِ۬لسَّمَٰوَٰتِ وَمَا فِي اِ۬لۡأَرۡضِۗ وَإِن تُبۡدُواْ مَا فِيٓ أَنفُسِكُمۡ أَوۡ تُخۡفُوهُ يُحَاسِبۡكُم بِهِ اِ۬للَّهُ فَيَغۡفِر لِّمَن يَشَآءُ وَيُعَذِّب مَّن يَشَآءُۗ وَاَللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ ٢٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (291,3,'49',2,'Al-Baqarah','البَقَرَة',7,10,284,'ءَامَنَ اَ۬لرَّسُولُ بِمَآ أُنزِلَ إِلَيۡهِ مِن رَّبِّهِۦ وَاَلۡمُؤۡمِنُونَۚ كُلٌّ ءَامَنَ بِاللَّهِ وَمَلَٰٓئِكَتِهِۦ وَكُتُبِهِۦ وَرُسُلِهِۦ لَا نُفَرِّقُ بَيۡنَ أَحَدٖ مِّن رُّسُلِهِۦۚ وَقَالُواْ سَمِعۡنَا وَأَطَعۡنَاۖ غُفۡرَانَكَ رَبَّنَا وَإِلَيۡكَ اَ۬لۡمَصِيرُ ٢٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (292,3,'49',2,'Al-Baqarah','البَقَرَة',10,15,285,'لَا يُكَلِّفُ اُ۬للَّهُ نَفۡسًا إِلَّا وُسۡعَهَاۚ لَهَا مَا كَسَبَتۡ وَعَلَيۡهَا مَا اَ۪كۡتَسَبَتۡۗ رَبَّنَا لَا تُؤَاخِذۡنَآ إِن نَّسِينَآ أَوۡ أَخۡطَأۡنَاۚ رَبَّنَا وَلَا تَحۡمِلۡ عَلَيۡنَآ إِصۡرٗا كَمَا حَمَلۡتَهُۥ عَلَى اَ۬لَّذِينَ مِن قَبۡلِنَاۚ رَبَّنَا وَلَا تُحَمِّلۡنَا مَا لَا طَاقَةَ لَنَا بِهِۦۖ وَاَعۡفُ عَنَّا وَاَغۡفِر لَّنَا وَاَرۡحَمۡنَآۚ أَنتَ مَوۡلَىٰنَا فَاَنصُرۡنَا عَلَى اَ۬لۡقَوۡمِ اِ۬لۡكٰ۪فِرِينَ ٢٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (293,3,'50',3,'Āl-‘Imrān','آل عِمران',3,3,1,'الٓمٓۚ اَ۬للَّهُ لَآ إِلَٰهَ إِلَّا هُوَ اَ۬لۡحَيُّ اُ۬لۡقَيُّومُ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (294,3,'50',3,'Āl-‘Imrān','آل عِمران',3,4,2,'نَزَّلَ عَلَيۡكَ اَ۬لۡكِتَٰبَ بِالۡحَقِّ مُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِۖ وَأَنزَلَ اَ۬لتَّوۡر۪ىٰةَ وَاَلۡإِنجِيلَ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (295,3,'50',3,'Āl-‘Imrān','آل عِمران',4,5,3,'مِن قَبۡلُ هُدٗى لِّلنّ۪اسِ وَأَنزَلَ اَ۬لۡفُرۡقَانَ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (296,3,'50',3,'Āl-‘Imrān','آل عِمران',5,6,4,'إِنَّ اَ۬لَّذِينَ كَفَرُواْ بِـَٔايَٰتِ اِ۬للَّهِ لَهُمۡ عَذَابٞ شَدِيدٞۗ وَاَللَّهُ عَزِيزٞ ذُو اُ۪نتِقَامٍ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (297,3,'50',3,'Āl-‘Imrān','آل عِمران',6,7,5,'إِنَّ اَ۬للَّهَ لَا يَخۡفَىٰ عَلَيۡهِ شَيۡءٞ فِي اِ۬لۡأَرۡضِ وَلَا فِي اِ۬لسَّمَآءِ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (298,3,'50',3,'Āl-‘Imrān','آل عِمران',7,8,6,'هُوَ اَ۬لَّذِي يُصَوِّرُكُمۡ فِي اِ۬لۡأَرۡحَامِ كَيۡفَ يَشَآءُۚ لَآ إِلَٰهَ إِلَّا هُوَ اَ۬لۡعَزِيزُ اُ۬لۡحَكِيمُ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (299,3,'50',3,'Āl-‘Imrān','آل عِمران',8,13,7,'هُوَ اَ۬لَّذِيٓ أَنزَلَ عَلَيۡكَ اَ۬لۡكِتَٰبَ مِنۡهُ ءَايَٰتٞ مُّحۡكَمَٰتٌ هُنَّ أُمُّ اُ۬لۡكِتَٰبِ وَأُخَرُ مُتَشَٰبِهَٰتٞۖ فَأَمَّا اَ۬لَّذِينَ فِي قُلُوبِهِمۡ زَيۡغٞ فَيَتَّبِعُونَ مَا تَشَٰبَهَ مِنۡهُ اُ۪بۡتِغَآءَ اَ۬لۡفِتۡنَةِ وَاَبۡتِغَآءَ تَأۡوِيلِهِۦۖ وَمَا يَعۡلَمُ تَأۡوِيلَهُۥٓ إِلَّا اَ۬للَّهُۗ ۞ وَاَلرَّٰسِخُونَ فِي اِ۬لۡعِلۡمِ يَقُولُونَ ءَامَنَّا بِهِۦ كُلّٞ مِّنۡ عِندِ رَبِّنَاۗ وَمَا يَذَّكَّرُ إِلَّآ أُوْلُواْ اُ۬لۡأَلۡبَٰبِ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (300,3,'50',3,'Āl-‘Imrān','آل عِمران',13,14,8,'رَبَّنَا لَا تُزِغۡ قُلُوبَنَا بَعۡدَ إِذۡ هَدَيۡتَنَا وَهَبۡ لَنَا مِن لَّدُنكَ رَحۡمَةًۚ إِنَّكَ أَنتَ اَ۬لۡوَهَّابُ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (301,3,'50',3,'Āl-‘Imrān','آل عِمران',14,15,9,'رَبَّنَآ إِنَّكَ جَامِعُ اُ۬لنّ۪اسِ لِيَوۡمٖ لَّا رَيۡبَ فِيهِۚ إِنَّ اَ۬للَّهَ لَا يُخۡلِفُ اُ۬لۡمِيعَادَ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (302,3,'51',3,'Āl-‘Imrān','آل عِمران',1,2,10,'إِنَّ اَ۬لَّذِينَ كَفَرُواْ لَن تُغۡنِيَ عَنۡهُمۡ أَمۡوَٰلُهُمۡ وَلَآ أَوۡلَٰدُهُم مِّنَ اَ۬للَّهِ شَيۡـٔٗاۖ وَأُوْلَٰٓئِكَ هُمۡ وَقُودُ اُ۬لنّ۪ارِ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (303,3,'51',3,'Āl-‘Imrān','آل عِمران',2,4,11,'كَدَأۡبِ ءَالِ فِرۡعَوۡنَ وَاَلَّذِينَ مِن قَبۡلِهِمۡۚ كَذَّبُواْ بِـَٔايَٰتِنَا فَأَخَذَهُمُ اُ۬للَّهُ بِذُنُوبِهِمۡۗ وَاَللَّهُ شَدِيدُ اُ۬لۡعِقَابِ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (304,3,'51',3,'Āl-‘Imrān','آل عِمران',4,5,12,'قُل لِّلَّذِينَ كَفَرُواْ سَتُغۡلَبُونَ وَتُحۡشَرُونَ إِلَىٰ جَهَنَّمَۚ وَبِئۡسَ اَ۬لۡمِهَادُ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (305,3,'51',3,'Āl-‘Imrān','آل عِمران',6,9,13,'قَدۡ كَانَ لَكُمۡ ءَايَةٞ فِي فِئَتَيۡنِ اِ۪لۡتَقَتَاۖ فِئَةٞ تُقَٰتِلُ فِي سَبِيلِ اِ۬للَّهِ وَأُخۡر۪يٰ كَافِرَةٞ يَرَوۡنَهُم مِّثۡلَيۡهِمۡ رَأۡيَ اَ۬لۡعَيۡنِۚ وَاَللَّهُ يُؤَيِّدُ بِنَصۡرِهِۦ مَن يَشَآءُۗ اِ۪نَّ فِي ذَٰلِكَ لَعِبۡرَةٗ لِّأُوْلِي اِ۬لۡأَبۡصٰ۪رِ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (306,3,'51',3,'Āl-‘Imrān','آل عِمران',9,12,14,'زُيِّنَ لِلنّ۪اسِ حُبُّ اُ۬لشَّهَوَٰتِ مِنَ اَ۬لنِّسَآءِ وَاَلۡبَنِينَ وَاَلۡقَنَٰطِيرِ اِ۬لۡمُقَنطَرَةِ مِنَ اَ۬لذَّهَبِ وَاَلۡفِضَّةِ وَاَلۡخَيۡلِ اِ۬لۡمُسَوَّمَةِ وَاَلۡأَنۡعَٰمِ وَاَلۡحَرۡثِۗ ذَٰلِكَ مَتَٰعُ اُ۬لۡحَيَوٰةِ اِ۬لدُّنۡيۭاۖ وَاَللَّهُ عِندَهُۥ حُسۡنُ اُ۬لۡمَـَٔابِ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (307,3,'51',3,'Āl-‘Imrān','آل عِمران',12,15,15,'۞ قُلۡ أَو۬نَبِّئُكُم بِخَيۡرٖ مِّن ذَٰلِكُمۡۚ لِلَّذِينَ اَ۪تَّقَوۡاْ عِندَ رَبِّهِمۡ جَنَّٰتٞ تَجۡرِي مِن تَحۡتِهَا اَ۬لۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَا وَأَزۡوَٰجٞ مُّطَهَّرَةٞ وَرِضۡوَٰنٞ مِّنَ اَ۬للَّهِۗ وَاَللَّهُ بَصِيرُۢ بِالۡعِبَادِ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (308,3,'52',3,'Āl-‘Imrān','آل عِمران',1,2,16,'اِ۬لَّذِينَ يَقُولُونَ رَبَّنَآ إِنَّنَآ ءَامَنَّا فَاَغۡفِر لَّنَا ذُنُوبَنَا وَقِنَا عَذَابَ اَ۬لنّ۪ارِ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (309,3,'52',3,'Āl-‘Imrān','آل عِمران',2,3,17,'اِ۬لصَّٰبِرِينَ وَاَلصَّٰدِقِينَ وَاَلۡقَٰنِتِينَ وَاَلۡمُنفِقِينَ وَاَلۡمُسۡتَغۡفِرِينَ بِالۡأَسۡح۪ارِ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (310,3,'52',3,'Āl-‘Imrān','آل عِمران',3,5,18,'شَهِدَ اَ۬للَّهُ أَنَّهُۥ لَآ إِلَٰهَ إِلَّا هُوَ وَاَلۡمَلَٰٓئِكَةُ وَأُوْلُواْ اُ۬لۡعِلۡمِ قَآئِمَۢا بِالۡقِسۡطِۚ لَآ إِلَٰهَ إِلَّا هُوَ اَ۬لۡعَزِيزُ اُ۬لۡحَكِيمُ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (311,3,'52',3,'Āl-‘Imrān','آل عِمران',5,8,19,'إِنَّ اَ۬لدِّينَ عِندَ اَ۬للَّهِ اِ۬لۡإِسۡلَٰمُۗ وَمَا اَ۪خۡتَلَفَ اَ۬لَّذِينَ أُوتُواْ اُ۬لۡكِتَٰبَ إِلَّا مِنۢ بَعۡدِ مَا جَآءَهُمُ اُ۬لۡعِلۡمُ بَغۡيَۢا بَيۡنَهُمۡۗ وَمَن يَكۡفُرۡ بِـَٔايَٰتِ اِ۬للَّهِ فَإِنَّ اَ۬للَّهَ سَرِيعُ اُ۬لۡحِسَابِ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (312,3,'52',3,'Āl-‘Imrān','آل عِمران',8,11,20,'فَإِنۡ حَآجُّوكَ فَقُلۡ أَسۡلَمۡتُ وَجۡهِي لِلَّهِ وَمَنِ اِ۪تَّبَعَنِۦۗ وَقُل لِّلَّذِينَ أُوتُواْ اُ۬لۡكِتَٰبَ وَاَلۡأُمِّيِّـۧنَ ءَٰا۬سۡلَمۡتُمۡۚ فَإِنۡ أَسۡلَمُواْ فَقَدِ اِ۪هۡتَدَواْۖ وَّإِن تَوَلَّوۡاْ فَإِنَّمَا عَلَيۡكَ اَ۬لۡبَلَٰغُۗ وَاَللَّهُ بَصِيرُۢ بِالۡعِبَادِ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (313,3,'52',3,'Āl-‘Imrān','آل عِمران',12,14,21,'إِنَّ اَ۬لَّذِينَ يَكۡفُرُونَ بِـَٔايَٰتِ اِ۬للَّهِ وَيَقۡتُلُونَ اَ۬لنَّبِيِّـۧنَ بِغَيۡرِ حَقّٖ وَيَقۡتُلُونَ اَ۬لَّذِينَ يَأۡمُرُونَ بِالۡقِسۡطِ مِنَ اَ۬لنّ۪اسِ فَبَشِّرۡهُم بِعَذَابٍ أَلِيمٍ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (314,3,'52',3,'Āl-‘Imrān','آل عِمران',14,15,22,'أُوْلَٰٓئِكَ اَ۬لَّذِينَ حَبِطَتۡ أَعۡمَٰلُهُمۡ فِي اِ۬لدُّنۡيۭا وَاَلۡأٓخِرَةِ وَمَا لَهُم مِّن نَّٰصِرِينَ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (315,3,'53',3,'Āl-‘Imrān','آل عِمران',1,2,23,'أَلَمۡ تَرَ إِلَى اَ۬لَّذِينَ أُوتُواْ نَصِيبٗا مِّنَ اَ۬لۡكِتَٰبِ يُدۡعَوۡنَ إِلَىٰ كِتَٰبِ اِ۬للَّهِ لِيَحۡكُمَ بَيۡنَهُمۡ ثُمَّ يَتَوَلَّىٰ فَرِيقٞ مِّنۡهُمۡ وَهُم مُّعۡرِضُونَ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (316,3,'53',3,'Āl-‘Imrān','آل عِمران',3,4,24,'ذَٰلِكَ بِأَنَّهُمۡ قَالُواْ لَن تَمَسَّنَا اَ۬لنَّارُ إِلَّآ أَيَّامٗا مَّعۡدُودَٰتٖۖ وَغَرَّهُمۡ فِي دِينِهِم مَّا كَانُواْ يَفۡتَرُونَ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (317,3,'53',3,'Āl-‘Imrān','آل عِمران',4,6,25,'فَكَيۡفَ إِذَا جَمَعۡنَٰهُمۡ لِيَوۡمٖ لَّا رَيۡبَ فِيهِ وَوُفِّيَتۡ كُلُّ نَفۡسٖ مَّا كَسَبَتۡ وَهُمۡ لَا يُظۡلَمُونَ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (318,3,'53',3,'Āl-‘Imrān','آل عِمران',6,8,26,'۞ قُلِ اِ۬للَّهُمَّ مَٰلِكَ اَ۬لۡمُلۡكِ تُؤۡتِي اِ۬لۡمُلۡكَ مَن تَشَآءُ وَتَنزِعُ اُ۬لۡمُلۡكَ مِمَّن تَشَآءُۖ وَتُعِزُّ مَن تَشَآءُ وَتُذِلُّ مَن تَشَآءُۖ بِيَدِكَ اَ۬لۡخَيۡرُۖ إِنَّكَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (319,3,'53',3,'Āl-‘Imrān','آل عِمران',8,10,27,'تُولِجُ اُ۬لَّيۡلَ فِي اِ۬لنَّه۪ارِ وَتُولِجُ اُ۬لنَّهَارَ فِي اِ۬لَّيۡلِۖ وَتُخۡرِجُ اُ۬لۡحَيَّ مِنَ اَ۬لۡمَيۡتِ وَتُخۡرِجُ اُ۬لۡمَيۡتَ مِنَ اَ۬لۡحَيِّۖ وَتَرۡزُقُ مَن تَشَآءُ بِغَيۡرِ حِسَابٖ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (320,3,'53',3,'Āl-‘Imrān','آل عِمران',11,13,28,'لَّا يَتَّخِذِ اِ۬لۡمُؤۡمِنُونَ اَ۬لۡكٰ۪فِرِينَ أَوۡلِيَآءَ مِن دُونِ اِ۬لۡمُؤۡمِنِينَۖ وَمَن يَفۡعَلۡ ذَٰلِكَ فَلَيۡسَ مِنَ اَ۬للَّهِ فِي شَيۡءٍ إِلَّآ أَن تَتَّقُواْ مِنۡهُمۡ تُقَىٰةٗۗ وَيُحَذِّرُكُمُ اُ۬للَّهُ نَفۡسَهُۥۗ وَإِلَى اَ۬للَّهِ اِ۬لۡمَصِيرُ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (321,3,'53',3,'Āl-‘Imrān','آل عِمران',13,15,29,'قُلۡ إِن تُخۡفُواْ مَا فِي صُدُورِكُمۡ أَوۡ تُبۡدُوهُ يَعۡلَمۡهُ اُ۬للَّهُۗ وَيَعۡلَمُ مَا فِي اِ۬لسَّمَٰوَٰتِ وَمَا فِي اِ۬لۡأَرۡضِۗ وَاَللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (322,3,'54',3,'Āl-‘Imrān','آل عِمران',1,3,30,'يَوۡمَ تَجِدُ كُلُّ نَفۡسٖ مَّا عَمِلَتۡ مِنۡ خَيۡرٖ مُّحۡضَرٗا وَمَا عَمِلَتۡ مِن سُوٓءٖ تَوَدُّ لَوۡ أَنَّ بَيۡنَهَا وَبَيۡنَهُۥٓ أَمَدَۢا بَعِيدٗاۗ وَيُحَذِّرُكُمُ اُ۬للَّهُ نَفۡسَهُۥۗ وَاَللَّهُ رَؤُفُۢ بِالۡعِبَادِ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (323,3,'54',3,'Āl-‘Imrān','آل عِمران',3,5,31,'قُلۡ إِن كُنتُمۡ تُحِبُّونَ اَ۬للَّهَ فَاَتَّبِعُونِي يُحۡبِبۡكُمُ اُ۬للَّهُ وَيَغۡفِر لَّكُمۡ ذُنُوبَكُمۡۗ وَاَللَّهُ غَفُورٞ رَّحِيمٞ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (324,3,'54',3,'Āl-‘Imrān','آل عِمران',5,6,32,'قُلۡ أَطِيعُواْ اُ۬للَّهَ وَاَلرَّسُولَۖ فَإِن تَوَلَّوۡاْ فَإِنَّ اَ۬للَّهَ لَا يُحِبُّ اُ۬لۡكٰ۪فِرِينَ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (325,3,'54',3,'Āl-‘Imrān','آل عِمران',6,7,33,'۞ إِنَّ اَ۬للَّهَ اَ۪صۡطَفَىٰٓ ءَادَمَ وَنُوحٗا وَءَالَ إِبۡرَٰهِيمَ وَءَالَ عِمۡرَٰنَ عَلَى اَ۬لۡعَٰلَمِينَ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (326,3,'54',3,'Āl-‘Imrān','آل عِمران',7,8,34,'ذُرِّيَّةَۢ بَعۡضُهَا مِنۢ بَعۡضٖۗ وَاَللَّهُ سَمِيعٌ عَلِيمٌ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (327,3,'54',3,'Āl-‘Imrān','آل عِمران',8,9,35,'إِذۡ قَالَتِ اِ۪مۡرَأَتُ عِمۡرَٰنَ رَبِّ إِنِّي نَذَرۡتُ لَكَ مَا فِي بَطۡنِي مُحَرَّرٗا فَتَقَبَّلۡ مِنِّيَۖ إِنَّكَ أَنتَ اَ۬لسَّمِيعُ اُ۬لۡعَلِيمُ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (328,3,'54',3,'Āl-‘Imrān','آل عِمران',10,12,36,'فَلَمَّا وَضَعَتۡهَا قَالَتۡ رَبِّ إِنِّي وَضَعۡتُهَآ أُنثۭيٰ وَاَللَّهُ أَعۡلَمُ بِمَا وَضَعَتۡ وَلَيۡسَ اَ۬لذَّكَرُ كَاَلۡأُنثۭيٰۖ وَإِنِّي سَمَّيۡتُهَا مَرۡيَمَ وَإِنِّيٓ أُعِيذُهَا بِكَ وَذُرِّيَّتَهَا مِنَ اَ۬لشَّيۡطَٰنِ اِ۬لرَّجِيمِ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (329,3,'54',3,'Āl-‘Imrān','آل عِمران',12,15,37,'فَتَقَبَّلَهَا رَبُّهَا بِقَبُولٍ حَسَنٖ وَأَنۢبَتَهَا نَبَاتًا حَسَنٗا وَكَفَلَهَا زَكَرِيَّآءُۖ كُلَّمَا دَخَلَ عَلَيۡهَا زَكَرِيَّآءُ اُ۬لۡمِحۡرَابَ وَجَدَ عِندَهَا رِزۡقٗاۖ قَالَ يَٰمَرۡيَمُ أَنّۭيٰ لَكِ هَٰذَاۖ قَالَتۡ هُوَ مِنۡ عِندِ اِ۬للَّهِۖ إِنَّ اَ۬للَّهَ يَرۡزُقُ مَن يَشَآءُ بِغَيۡرِ حِسَابٍ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (330,3,'55',3,'Āl-‘Imrān','آل عِمران',1,2,38,'هُنَالِكَ دَعَا زَكَرِيَّآءُ رَبَّهُۥۖ قَالَ رَبِّ هَبۡ لِي مِن لَّدُنكَ ذُرِّيَّةٗ طَيِّبَةًۖ إِنَّكَ سَمِيعُ اُ۬لدُّعَآءِ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (331,3,'55',3,'Āl-‘Imrān','آل عِمران',2,4,39,'فَنَادَتۡهُ اُ۬لۡمَلَٰٓئِكَةُ وَهۡوَ قَآئِمٞ يُصَلِّي فِي اِ۬لۡمِحۡرَابِ أَنَّ اَ۬للَّهَ يُبَشِّرُكَ بِيَحۡيۭيٰ مُصَدِّقَۢا بِكَلِمَةٖ مِّنَ اَ۬للَّهِ وَسَيِّدٗا وَحَصُورٗا وَنَبِيّٗا مِّنَ اَ۬لصَّٰلِحِينَ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (332,3,'55',3,'Āl-‘Imrān','آل عِمران',4,6,40,'قَالَ رَبِّ أَنّۭيٰ يَكُونُ لِي غُلَٰمٞ وَقَدۡ بَلَغَنِيَ اَ۬لۡكِبَرُ وَاَمۡرَأَتِي عَاقِرٞۖ قَالَ كَذَٰلِكَ اَ۬للَّهُ يَفۡعَلُ مَا يَشَآءُ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (333,3,'55',3,'Āl-‘Imrān','آل عِمران',6,8,41,'قَالَ رَبِّ اِ۪جۡعَل لِّيَ ءَايَةٗۖ قَالَ ءَايَتُكَ أَلَّا تُكَلِّمَ اَ۬لنَّاسَ ثَلَٰثَةَ أَيَّامٍ إِلَّا رَمۡزٗاۗ وَاَذۡكُر رَّبَّكَ كَثِيرٗا وَسَبِّحۡ بِالۡعَشِيِّ وَاَلۡإِبۡكٰ۪رِ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (334,3,'55',3,'Āl-‘Imrān','آل عِمران',8,10,42,'۞ وَإِذۡ قَالَتِ اِ۬لۡمَلَٰٓئِكَةُ يَٰمَرۡيَمُ إِنَّ اَ۬للَّهَ اَ۪صۡطَفَىٰكِ وَطَهَّرَكِ وَاَصۡطَفَىٰكِ عَلَىٰ نِسَآءِ اِ۬لۡعَٰلَمِينَ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (335,3,'55',3,'Āl-‘Imrān','آل عِمران',10,11,43,'يَٰمَرۡيَمُ اُ۟قۡنُتِي لِرَبِّكِ وَاَسۡجُدِي وَاَرۡكَعِي مَعَ اَ۬لرَّٰكِعِينَ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (336,3,'55',3,'Āl-‘Imrān','آل عِمران',11,13,44,'ذَٰلِكَ مِنۡ أَنۢبَآءِ اِ۬لۡغَيۡبِ نُوحِيهِ إِلَيۡكَۚ وَمَا كُنتَ لَدَيۡهِمۡ إِذۡ يُلۡقُونَ أَقۡلَٰمَهُمۡ أَيُّهُمۡ يَكۡفُلُ مَرۡيَمَۖ وَمَا كُنتَ لَدَيۡهِمۡ إِذۡ يَخۡتَصِمُونَ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (337,3,'55',3,'Āl-‘Imrān','آل عِمران',13,15,45,'إِذۡ قَالَتِ اِ۬لۡمَلَٰٓئِكَةُ يَٰمَرۡيَمُ إِنَّ اَ۬للَّهَ يُبَشِّرُكِ بِكَلِمَةٖ مِّنۡهُ اُ۪سۡمُهُ اُ۬لۡمَسِيحُ عِيسَى اَ۪بۡنُ مَرۡيَمَ وَجِيهٗا فِي اِ۬لدُّنۡيۭا وَاَلۡأٓخِرَةِ وَمِنَ اَ۬لۡمُقَرَّبِينَ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (338,3,'56',3,'Āl-‘Imrān','آل عِمران',1,1,46,'وَيُكَلِّمُ اُ۬لنَّاسَ فِي اِ۬لۡمَهۡدِ وَكَهۡلٗا وَمِنَ اَ۬لصَّٰلِحِينَ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (339,3,'56',3,'Āl-‘Imrān','آل عِمران',2,3,47,'قَالَتۡ رَبِّ أَنّۭيٰ يَكُونُ لِي وَلَدٞ وَلَمۡ يَمۡسَسۡنِي بَشَرٞۖ قَالَ كَذَٰلِكِ اِ۬للَّهُ يَخۡلُقُ مَا يَشَآءُۚ اِ۪ذَا قَضَىٰٓ أَمۡرٗا فَإِنَّمَا يَقُولُ لَهُۥ كُن فَيَكُونُ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (340,3,'56',3,'Āl-‘Imrān','آل عِمران',4,9,48,'وَنُعَلِّمُهُ اُ۬لۡكِتَٰبَ وَاَلۡحِكۡمَةَ وَاَلتَّوۡر۪ىٰةَ وَاَلۡإِنجِيلَۚ وَرَسُولًا إِلَىٰ بَنِيٓ إِسۡرَٰٓءِيلَ أَنِّي قَد جِّئۡتُكُم بِـَٔايَةٖ مِّن رَّبِّكُمۡۖ أَنِّيَ أَخۡلُقُ لَكُم مِّنَ اَ۬لطِّينِ كَهَيۡـَٔةِ اِ۬لطَّيۡرِ فَأَنفُخُ فِيهِ فَيَكُونُ طَيۡرَۢا بِإِذۡنِ اِ۬للَّهِۖ وَأُبۡرِئُ اُ۬لۡأَكۡمَهَ وَاَلۡأَبۡرَصَ وَأُحۡيِ اِ۬لۡمَوۡتۭيٰ بِإِذۡنِ اِ۬للَّهِۖ وَأُنَبِّئُكُم بِمَا تَأۡكُلُونَ وَمَا تَدَّخِرُونَ فِي بُيُوتِكُمۡۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لَّكُمۡ إِن كُنتُم مُّؤۡمِنِينَ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (341,3,'56',3,'Āl-‘Imrān','آل عِمران',10,12,49,'وَمُصَدِّقٗا لِّمَا بَيۡنَ يَدَيَّ مِنَ اَ۬لتَّوۡر۪ىٰةِ وَلِأُحِلَّ لَكُم بَعۡضَ اَ۬لَّذِي حُرِّمَ عَلَيۡكُمۡۚ وَجِئۡتُكُم بِـَٔايَةٖ مِّن رَّبِّكُمۡ فَاَتَّقُواْ اُ۬للَّهَ وَأَطِيعُونِ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (342,3,'56',3,'Āl-‘Imrān','آل عِمران',12,13,50,'إِنَّ اَ۬للَّهَ رَبِّي وَرَبُّكُمۡ فَاَعۡبُدُوهُۚ هَٰذَا صِرَٰطٞ مُّسۡتَقِيمٞ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (343,3,'56',3,'Āl-‘Imrān','آل عِمران',13,15,51,'۞ فَلَمَّآ أَحَسَّ عِيسۭيٰ مِنۡهُمُ اُ۬لۡكُفۡرَ قَالَ مَنۡ أَنصَارِيٓ إِلَى اَ۬للَّهِۖ قَالَ اَ۬لۡحَوَارِيُّونَ نَحۡنُ أَنصَارُ اُ۬للَّهِ ءَامَنَّا بِاللَّهِ وَاَشۡهَدۡ بِأَنَّا مُسۡلِمُونَ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (344,3,'57',3,'Āl-‘Imrān','آل عِمران',1,2,52,'رَبَّنَآ ءَامَنَّا بِمَآ أَنزَلۡتَ وَاَتَّبَعۡنَا اَ۬لرَّسُولَ فَاَكۡتُبۡنَا مَعَ اَ۬لشَّٰهِدِينَ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (345,3,'57',3,'Āl-‘Imrān','آل عِمران',2,2,53,'وَمَكَرُواْ وَمَكَرَ اَ۬للَّهُۖ وَاَللَّهُ خَيۡرُ اُ۬لۡمَٰكِرِينَ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (346,3,'57',3,'Āl-‘Imrān','آل عِمران',3,6,54,'إِذۡ قَالَ اَ۬للَّهُ يَٰعِيسۭيٰٓ إِنِّي مُتَوَفِّيكَ وَرَافِعُكَ إِلَيَّ وَمُطَهِّرُكَ مِنَ اَ۬لَّذِينَ كَفَرُواْ وَجَاعِلُ اُ۬لَّذِينَ اَ۪تَّبَعُوكَ فَوۡقَ اَ۬لَّذِينَ كَفَرُوٓاْ إِلَىٰ يَوۡمِ اِ۬لۡقِيَٰمَةِۖ ثُمَّ إِلَيَّ مَرۡجِعُكُمۡ فَأَحۡكُمُ بَيۡنَكُمۡ فِيمَا كُنتُمۡ فِيهِ تَخۡتَلِفُونَ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (347,3,'57',3,'Āl-‘Imrān','آل عِمران',6,8,55,'فَأَمَّا اَ۬لَّذِينَ كَفَرُواْ فَأُعَذِّبُهُمۡ عَذَابٗا شَدِيدٗا فِي اِ۬لدُّنۡيۭا وَاَلۡأٓخِرَةِۖ وَمَا لَهُم مِّن نَّٰصِرِينَ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (348,3,'57',3,'Āl-‘Imrān','آل عِمران',8,9,56,'وَأَمَّا اَ۬لَّذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ فَنُوَفِّيهِمۡ أُجُورَهُمۡۗ وَاَللَّهُ لَا يُحِبُّ اُ۬لظَّٰلِمِينَ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (349,3,'57',3,'Āl-‘Imrān','آل عِمران',9,10,57,'ذَٰلِكَ نَتۡلُوهُ عَلَيۡكَ مِنَ اَ۬لۡأٓيَٰتِ وَاَلذِّكۡرِ اِ۬لۡحَكِيمِ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (350,3,'57',3,'Āl-‘Imrān','آل عِمران',10,12,58,'إِنَّ مَثَلَ عِيسۭيٰ عِندَ اَ۬للَّهِ كَمَثَلِ ءَادَمَۖ خَلَقَهُۥ مِن تُرَابٖ ثُمَّ قَالَ لَهُۥ كُن فَيَكُونُ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (351,3,'57',3,'Āl-‘Imrān','آل عِمران',12,12,59,'اُ۬لۡحَقُّ مِن رَّبِّكَ فَلَا تَكُن مِّنَ اَ۬لۡمُمۡتَرِينَ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (352,3,'57',3,'Āl-‘Imrān','آل عِمران',13,15,60,'فَمَنۡ حَآجَّكَ فِيهِ مِنۢ بَعۡدِ مَا جَآءَكَ مِنَ اَ۬لۡعِلۡمِ فَقُلۡ تَعَالَوۡاْ نَدۡعُ أَبۡنَآءَنَا وَأَبۡنَآءَكُمۡ وَنِسَآءَنَا وَنِسَآءَكُمۡ وَأَنفُسَنَا وَأَنفُسَكُمۡ ثُمَّ نَبۡتَهِلۡ فَنَجۡعَل لَّعۡنَتَ اَ۬للَّهِ عَلَى اَ۬لۡكَٰذِبِينَ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (353,3,'58',3,'Āl-‘Imrān','آل عِمران',1,2,61,'إِنَّ هَٰذَا لَهۡوَ اَ۬لۡقَصَصُ اُ۬لۡحَقُّۚ وَمَا مِنۡ إِلَٰهٍ إِلَّا اَ۬للَّهُۚ وَإِنَّ اَ۬للَّهَ لَهۡوَ اَ۬لۡعَزِيزُ اُ۬لۡحَكِيمُ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (354,3,'58',3,'Āl-‘Imrān','آل عِمران',2,2,62,'فَإِن تَوَلَّوۡاْ فَإِنَّ اَ۬للَّهَ عَلِيمُۢ بِالۡمُفۡسِدِينَ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (355,3,'58',3,'Āl-‘Imrān','آل عِمران',3,6,63,'۞ قُلۡ يَٰٓأَهۡلَ اَ۬لۡكِتَٰبِ تَعَالَوۡاْ إِلَىٰ كَلِمَةٖ سَوَآءِۢ بَيۡنَنَا وَبَيۡنَكُمۡ أَلَّا نَعۡبُدَ إِلَّا اَ۬للَّهَ وَلَا نُشۡرِكَ بِهِۦ شَيۡـٔٗا وَلَا يَتَّخِذَ بَعۡضُنَا بَعۡضًا أَرۡبَابٗا مِّن دُونِ اِ۬للَّهِۚ فَإِن تَوَلَّوۡاْ فَقُولُواْ اُ۪شۡهَدُواْ بِأَنَّا مُسۡلِمُونَ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (356,3,'58',3,'Āl-‘Imrān','آل عِمران',6,7,64,'يَٰٓأَهۡلَ اَ۬لۡكِتَٰبِ لِمَ تُحَآجُّونَ فِيٓ إِبۡرَٰهِيمَ وَمَآ أُنزِلَتِ اِ۬لتَّوۡر۪ىٰةُ وَاَلۡإِنجِيلُ إِلَّا مِنۢ بَعۡدِهِۦٓۚ أَفَلَا تَعۡقِلُونَ ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (357,3,'58',3,'Āl-‘Imrān','آل عِمران',8,10,65,'هَٰٓا۬نتُمۡ هَٰٓؤُلَآءِ حَٰجَجۡتُمۡ فِيمَا لَكُم بِهِۦ عِلۡمٞ فَلِمَ تُحَآجُّونَ فِيمَا لَيۡسَ لَكُم بِهِۦ عِلۡمٞۚ وَاَللَّهُ يَعۡلَمُ وَأَنتُمۡ لَا تَعۡلَمُونَ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (358,3,'58',3,'Āl-‘Imrān','آل عِمران',10,11,66,'مَا كَانَ إِبۡرَٰهِيمُ يَهُودِيّٗا وَلَا نَصۡرَانِيّٗا وَلَٰكِن كَانَ حَنِيفٗا مُّسۡلِمٗا وَمَا كَانَ مِنَ اَ۬لۡمُشۡرِكِينَ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (359,3,'58',3,'Āl-‘Imrān','آل عِمران',12,13,67,'إِنَّ أَوۡلَى اَ۬لنّ۪اسِ بِإِبۡرَٰهِيمَ لَلَّذِينَ اَ۪تَّبَعُوهُ وَهَٰذَا اَ۬لنَّبِيُّ وَاَلَّذِينَ ءَامَنُواْۗ وَاَللَّهُ وَلِيُّ اُ۬لۡمُؤۡمِنِينَ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (360,3,'58',3,'Āl-‘Imrān','آل عِمران',13,14,68,'وَدَّت طَّآئِفَةٞ مِّنۡ أَهۡلِ اِ۬لۡكِتَٰبِ لَوۡ يُضِلُّونَكُمۡۖ وَمَا يُضِلُّونَ إِلَّآ أَنفُسَهُمۡ وَمَا يَشۡعُرُونَ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (361,3,'58',3,'Āl-‘Imrān','آل عِمران',14,15,69,'يَٰٓأَهۡلَ اَ۬لۡكِتَٰبِ لِمَ تَكۡفُرُونَ بِـَٔايَٰتِ اِ۬للَّهِ وَأَنتُمۡ تَشۡهَدُونَ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (362,3,'59',3,'Āl-‘Imrān','آل عِمران',1,2,70,'يَٰٓأَهۡلَ اَ۬لۡكِتَٰبِ لِمَ تَلۡبِسُونَ اَ۬لۡحَقَّ بِالۡبَٰطِلِ وَتَكۡتُمُونَ اَ۬لۡحَقَّ وَأَنتُمۡ تَعۡلَمُونَ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (363,3,'59',3,'Āl-‘Imrān','آل عِمران',2,4,71,'وَقَالَت طَّآئِفَةٞ مِّنۡ أَهۡلِ اِ۬لۡكِتَٰبِ ءَامِنُواْ بِالَّذِيٓ أُنزِلَ عَلَى اَ۬لَّذِينَ ءَامَنُواْ وَجۡهَ اَ۬لنَّه۪ارِ وَاَكۡفُرُوٓاْ ءَاخِرَهُۥ لَعَلَّهُمۡ يَرۡجِعُونَ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (364,3,'59',3,'Āl-‘Imrān','آل عِمران',4,7,72,'وَلَا تُؤۡمِنُوٓاْ إِلَّا لِمَن تَبِعَ دِينَكُمۡ قُلۡ إِنَّ اَ۬لۡهُدَىٰ هُدَى اَ۬للَّهِ أَن يُؤۡتَىٰٓ أَحَدٞ مِّثۡلَ مَآ أُوتِيتُمۡ أَوۡ يُحَآجُّوكُمۡ عِندَ رَبِّكُمۡۗ قُلۡ إِنَّ اَ۬لۡفَضۡلَ بِيَدِ اِ۬للَّهِ يُؤۡتِيهِ مَن يَشَآءُۗ وَاَللَّهُ وَٰسِعٌ عَلِيمٞ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (365,3,'59',3,'Āl-‘Imrān','آل عِمران',7,8,73,'يَخۡتَصُّ بِرَحۡمَتِهِۦ مَن يَشَآءُۗ وَاَللَّهُ ذُو اُ۬لۡفَضۡلِ اِ۬لۡعَظِيمِ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (366,3,'59',3,'Āl-‘Imrān','آل عِمران',8,11,74,'۞ وَمِنۡ أَهۡلِ اِ۬لۡكِتَٰبِ مَنۡ إِن تَأۡمَنۡهُ بِقِنط۪ارٖ يُؤَدِّهۡ إِلَيۡكَ وَمِنۡهُم مَّنۡ إِن تَأۡمَنۡهُ بِدِين۪ارٖ لَّا يُؤَدِّهۡ إِلَيۡكَ إِلَّا مَا دُمۡتَ عَلَيۡهِ قَآئِمٗاۗ ذَٰلِكَ بِأَنَّهُمۡ قَالُواْ لَيۡسَ عَلَيۡنَا فِي اِ۬لۡأُمِّيِّـۧنَ سَبِيلٞ وَيَقُولُونَ عَلَى اَ۬للَّهِ اِ۬لۡكَذِبَ وَهُمۡ يَعۡلَمُونَ ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (367,3,'59',3,'Āl-‘Imrān','آل عِمران',12,12,75,'بَلَىٰۚ مَنۡ أَوۡفَىٰ بِعَهۡدِهِۦ وَاَتَّقَىٰ فَإِنَّ اَ۬للَّهَ يُحِبُّ اُ۬لۡمُتَّقِينَ ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (368,3,'59',3,'Āl-‘Imrān','آل عِمران',13,15,76,'إِنَّ اَ۬لَّذِينَ يَشۡتَرُونَ بِعَهۡدِ اِ۬للَّهِ وَأَيۡمَٰنِهِمۡ ثَمَنٗا قَلِيلًا أُوْلَٰٓئِكَ لَا خَلَٰقَ لَهُمۡ فِي اِ۬لۡأٓخِرَةِ وَلَا يُكَلِّمُهُمُ اُ۬للَّهُ وَلَا يَنظُرُ إِلَيۡهِمۡ يَوۡمَ اَ۬لۡقِيَٰمَةِ وَلَا يُزَكِّيهِمۡ وَلَهُمۡ عَذَابٌ أَلِيمٞ ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (369,3,'60',3,'Āl-‘Imrān','آل عِمران',1,4,77,'وَإِنَّ مِنۡهُمۡ لَفَرِيقٗا يَلۡوُۥنَ أَلۡسِنَتَهُم بِالۡكِتَٰبِ لِتَحۡسِبُوهُ مِنَ اَ۬لۡكِتَٰبِ وَمَا هُوَ مِنَ اَ۬لۡكِتَٰبِ وَيَقُولُونَ هُوَ مِنۡ عِندِ اِ۬للَّهِ وَمَا هُوَ مِنۡ عِندِ اِ۬للَّهِ وَيَقُولُونَ عَلَى اَ۬للَّهِ اِ۬لۡكَذِبَ وَهُمۡ يَعۡلَمُونَ ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (370,3,'60',3,'Āl-‘Imrān','آل عِمران',4,7,78,'مَا كَانَ لِبَشَرٍ أَن يُؤۡتِيَهُ اُ۬للَّهُ اُ۬لۡكِتَٰبَ وَاَلۡحُكۡمَ وَاَلنُّبُوَّةَ ثُمَّ يَقُولَ لِلنّ۪اسِ كُونُواْ عِبَادٗا لِّي مِن دُونِ اِ۬للَّهِ وَلَٰكِن كُونُواْ رَبَّٰنِيِّـۧنَ بِمَا كُنتُمۡ تَعۡلَمُونَ اَ۬لۡكِتَٰبَ وَبِمَا كُنتُمۡ تَدۡرُسُونَ ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (371,3,'60',3,'Āl-‘Imrān','آل عِمران',7,9,79,'وَلَا يَأۡمُرۡكُمۡ أَن تَتَّخِذُواْ اُ۬لۡمَلَٰٓئِكَةَ وَاَلنَّبِيِّـۧنَ أَرۡبَابًاۗ أَيَأۡمُرۡكُم بِالۡكُفۡرِ بَعۡدَ إِذۡ أَنتُم مُّسۡلِمُونَ ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (372,3,'60',3,'Āl-‘Imrān','آل عِمران',9,13,80,'وَإِذۡ أَخَذَ اَ۬للَّهُ مِيثَٰقَ اَ۬لنَّبِيِّـۧنَ لَمَآ ءَاتَيۡتُكُم مِّن كِتَٰبٖ وَحِكۡمَةٖ ثُمَّ جَآءَكُمۡ رَسُولٞ مُّصَدِّقٞ لِّمَا مَعَكُمۡ لَتُؤۡمِنُنَّ بِهِۦ وَلَتَنصُرُنَّهُۥۚ قَالَ ءَٰا۬قۡرَرۡتُمۡ وَأَخَذتُّمۡ عَلَىٰ ذَٰلِكُمۡ إِصۡرِيۖ قَالُوٓاْ أَقۡرَرۡنَاۚ قَالَ فَاَشۡهَدُواْ وَأَنَا۠ مَعَكُم مِّنَ اَ۬لشَّٰهِدِينَ ٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (373,3,'60',3,'Āl-‘Imrān','آل عِمران',13,14,81,'فَمَن تَوَلَّىٰ بَعۡدَ ذَٰلِكَ فَأُوْلَٰٓئِكَ هُمُ اُ۬لۡفَٰسِقُونَ ٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (374,3,'60',3,'Āl-‘Imrān','آل عِمران',14,15,82,'۞ أَفَغَيۡرَ دِينِ اِ۬للَّهِ يَبۡغُونَ وَلَهُۥٓ أَسۡلَمَ مَن فِي اِ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِ طَوۡعٗا وَكَرۡهٗا وَإِلَيۡهِ تُرۡجَعُونَ ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (375,3,'61',3,'Āl-‘Imrān','آل عِمران',1,4,83,'قُلۡ ءَامَنَّا بِاللَّهِ وَمَآ أُنزِلَ عَلَيۡنَا وَمَآ أُنزِلَ عَلَىٰٓ إِبۡرَٰهِيمَ وَإِسۡمَٰعِيلَ وَإِسۡحَٰقَ وَيَعۡقُوبَ وَاَلۡأَسۡبَاطِ وَمَآ أُوتِيَ مُوسۭيٰ وَعِيسۭيٰ وَاَلنَّبِيُّونَ مِن رَّبِّهِمۡ لَا نُفَرِّقُ بَيۡنَ أَحَدٖ مِّنۡهُمۡ وَنَحۡنُ لَهُۥ مُسۡلِمُونَ ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (376,3,'61',3,'Āl-‘Imrān','آل عِمران',4,5,84,'وَمَن يَبۡتَغِ غَيۡرَ اَ۬لۡإِسۡلَٰمِ دِينٗا فَلَن يُقۡبَلَ مِنۡهُ وَهۡوَ فِي اِ۬لۡأٓخِرَةِ مِنَ اَ۬لۡخَٰسِرِينَ ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (377,3,'61',3,'Āl-‘Imrān','آل عِمران',5,8,85,'كَيۡفَ يَهۡدِي اِ۬للَّهُ قَوۡمٗا كَفَرُواْ بَعۡدَ إِيمَٰنِهِمۡ وَشَهِدُوٓاْ أَنَّ اَ۬لرَّسُولَ حَقّٞ وَجَآءَهُمُ اُ۬لۡبَيِّنَٰتُۚ وَاَللَّهُ لَا يَهۡدِي اِ۬لۡقَوۡمَ اَ۬لظَّٰلِمِينَ ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (378,3,'61',3,'Āl-‘Imrān','آل عِمران',8,9,86,'أُوْلَٰٓئِكَ جَزَآؤُهُمۡ أَنَّ عَلَيۡهِمۡ لَعۡنَةَ اَ۬للَّهِ وَاَلۡمَلَٰٓئِكَةِ وَاَلنّ۪اسِ أَجۡمَعِينَ ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (379,3,'61',3,'Āl-‘Imrān','آل عِمران',9,10,87,'خَٰلِدِينَ فِيهَا لَا يُخَفَّفُ عَنۡهُمُ اُ۬لۡعَذَابُ وَلَا هُمۡ يُنظَرُونَ ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (380,3,'61',3,'Āl-‘Imrān','آل عِمران',10,11,88,'إِلَّا اَ۬لَّذِينَ تَابُواْ مِنۢ بَعۡدِ ذَٰلِكَ وَأَصۡلَحُواْ فَإِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٌ ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (381,3,'61',3,'Āl-‘Imrān','آل عِمران',11,13,89,'إِنَّ اَ۬لَّذِينَ كَفَرُواْ بَعۡدَ إِيمَٰنِهِمۡ ثُمَّ اَ۪زۡدَادُواْ كُفۡرٗا لَّن تُقۡبَلَ تَوۡبَتُهُمۡ وَأُوْلَٰٓئِكَ هُمُ اُ۬لضَّآلُّونَ ٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (382,3,'61',3,'Āl-‘Imrān','آل عِمران',13,15,90,'إِنَّ اَ۬لَّذِينَ كَفَرُواْ وَمَاتُواْ وَهُمۡ كُفَّارٞ فَلَن يُقۡبَلَ مِنۡ أَحَدِهِم مِّلۡءُ اُ۬لۡأَرۡضِ ذَهَبٗا وَلَوِ اِ۪فۡتَدَىٰ بِهِۦٓۗ أُوْلَٰٓئِكَ لَهُمۡ عَذَابٌ أَلِيمٞ وَمَا لَهُم مِّن نَّٰصِرِينَ ٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (383,4,'62',3,'Āl-‘Imrān','آل عِمران',1,1,91,'۞ لَن تَنَالُواْ اُ۬لۡبِرَّ حَتَّىٰ تُنفِقُواْ مِمَّا تُحِبُّونَ ٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (384,4,'62',3,'Āl-‘Imrān','آل عِمران',1,2,92,'وَمَا تُنفِقُواْ مِن شَيۡءٖ فَإِنَّ اَ۬للَّهَ بِهِۦ عَلِيمٞ ٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (385,4,'62',3,'Āl-‘Imrān','آل عِمران',2,4,93,'كُلُّ اُ۬لطَّعَامِ كَانَ حِلّٗا لِّبَنِيٓ إِسۡرَٰٓءِيلَ إِلَّا مَا حَرَّمَ إِسۡرَٰٓءِيلُ عَلَىٰ نَفۡسِهِۦ مِن قَبۡلِ أَن تُنزَلَ اَ۬لتَّوۡر۪ىٰةُۚ قُلۡ فَأۡتُواْ بِالتَّوۡر۪ىٰةِ فَاَتۡلُوهَآ إِن كُنتُمۡ صَٰدِقِينَ ٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (386,4,'62',3,'Āl-‘Imrān','آل عِمران',5,6,94,'فَمَنِ اِ۪فۡتَر۪يٰ عَلَى اَ۬للَّهِ اِ۬لۡكَذِبَ مِنۢ بَعۡدِ ذَٰلِكَ فَأُوْلَٰٓئِكَ هُمُ اُ۬لظَّٰلِمُونَ ٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (387,4,'62',3,'Āl-‘Imrān','آل عِمران',6,7,95,'قُلۡ صَدَقَ اَ۬للَّهُۗ فَاَتَّبِعُواْ مِلَّةَ إِبۡرَٰهِيمَ حَنِيفٗا وَمَا كَانَ مِنَ اَ۬لۡمُشۡرِكِينَ ٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (388,4,'62',3,'Āl-‘Imrān','آل عِمران',7,8,96,'إِنَّ أَوَّلَ بَيۡتٖ وُضِعَ لِلنّ۪اسِ لَلَّذِي بِبَكَّةَ مُبَارَكٗا وَهُدٗى لِّلۡعَٰلَمِينَ ٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (389,4,'62',3,'Āl-‘Imrān','آل عِمران',8,10,97,'فِيهِ ءَايَٰتُۢ بَيِّنَٰتٞ مَّقَامُ إِبۡرَٰهِيمَۖ وَمَن دَخَلَهُۥ كَانَ ءَامِنٗاۗ وَلِلَّهِ عَلَى اَ۬لنّ۪اسِ حَجُّ اُ۬لۡبَيۡتِ مَنِ اِ۪سۡتَطَاعَ إِلَيۡهِ سَبِيلٗاۚ وَمَن كَفَرَ فَإِنَّ اَ۬للَّهَ غَنِيٌّ عَنِ اِ۬لۡعَٰلَمِينَ ٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (390,4,'62',3,'Āl-‘Imrān','آل عِمران',11,12,98,'قُلۡ يَٰٓأَهۡلَ اَ۬لۡكِتَٰبِ لِمَ تَكۡفُرُونَ بِـَٔايَٰتِ اِ۬للَّهِ وَاَللَّهُ شَهِيدٌ عَلَىٰ مَا تَعۡمَلُونَ ٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (391,4,'62',3,'Āl-‘Imrān','آل عِمران',12,14,99,'قُلۡ يَٰٓأَهۡلَ اَ۬لۡكِتَٰبِ لِمَ تَصُدُّونَ عَن سَبِيلِ اِ۬للَّهِ مَنۡ ءَامَنَ تَبۡغُونَهَا عِوَجٗا وَأَنتُمۡ شُهَدَآءُۗ وَمَا اَ۬للَّهُ بِغَٰفِلٍ عَمَّا تَعۡمَلُونَ ٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (392,4,'62',3,'Āl-‘Imrān','آل عِمران',14,15,100,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُوٓاْ إِن تُطِيعُواْ فَرِيقٗا مِّنَ اَ۬لَّذِينَ أُوتُواْ اُ۬لۡكِتَٰبَ يَرُدُّوكُم بَعۡدَ إِيمَٰنِكُمۡ كٰ۪فِرِينَ ١٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (393,4,'63',3,'Āl-‘Imrān','آل عِمران',1,2,101,'وَكَيۡفَ تَكۡفُرُونَ وَأَنتُمۡ تُتۡلَىٰ عَلَيۡكُمۡ ءَايَٰتُ اُ۬للَّهِ وَفِيكُمۡ رَسُولُهُۥۗ وَمَن يَعۡتَصِم بِاللَّهِ فَقَدۡ هُدِيَ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ ١٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (394,4,'63',3,'Āl-‘Imrān','آل عِمران',3,4,102,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ اُ۪تَّقُواْ اُ۬للَّهَ حَقَّ تُقَاتِهِۦ وَلَا تَمُوتُنَّ إِلَّا وَأَنتُم مُّسۡلِمُونَ ١٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (395,4,'63',3,'Āl-‘Imrān','آل عِمران',4,8,103,'۞ وَاَعۡتَصِمُواْ بِحَبۡلِ اِ۬للَّهِ جَمِيعٗا وَلَا تَفَرَّقُواْۚ وَاَذۡكُرُواْ نِعۡمَتَ اَ۬للَّهِ عَلَيۡكُمۡ إِذۡ كُنتُمۡ أَعۡدَآءٗ فَأَلَّفَ بَيۡنَ قُلُوبِكُمۡ فَأَصۡبَحۡتُم بِنِعۡمَتِهِۦٓ إِخۡوَٰنٗا وَكُنتُمۡ عَلَىٰ شَفَا حُفۡرَةٖ مِّنَ اَ۬لنّ۪ارِ فَأَنقَذَكُم مِّنۡهَاۗ كَذَٰلِكَ يُبَيِّنُ اُ۬للَّهُ لَكُمۡ ءَايَٰتِهِۦ لَعَلَّكُمۡ تَهۡتَدُونَ ١٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (396,4,'63',3,'Āl-‘Imrān','آل عِمران',8,9,104,'وَلۡتَكُن مِّنكُمۡ أُمَّةٞ يَدۡعُونَ إِلَى اَ۬لۡخَيۡرِ وَيَأۡمُرُونَ بِالۡمَعۡرُوفِ وَيَنۡهَوۡنَ عَنِ اِ۬لۡمُنكَرِۚ وَأُوْلَٰٓئِكَ هُمُ اُ۬لۡمُفۡلِحُونَ ١٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (397,4,'63',3,'Āl-‘Imrān','آل عِمران',10,11,105,'وَلَا تَكُونُواْ كَاَلَّذِينَ تَفَرَّقُواْ وَاَخۡتَلَفُواْ مِنۢ بَعۡدِ مَا جَآءَهُمُ اُ۬لۡبَيِّنَٰتُۚ وَأُوْلَٰٓئِكَ لَهُمۡ عَذَابٌ عَظِيمٞ ١٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (398,4,'63',3,'Āl-‘Imrān','آل عِمران',11,13,106,'يَوۡمَ تَبۡيَضُّ وُجُوهٞ وَتَسۡوَدُّ وُجُوهٞۚ فَأَمَّا اَ۬لَّذِينَ اَ۪سۡوَدَّتۡ وُجُوهُهُمۡ أَكَفَرۡتُم بَعۡدَ إِيمَٰنِكُمۡ فَذُوقُواْ اُ۬لۡعَذَابَ بِمَا كُنتُمۡ تَكۡفُرُونَ ١٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (399,4,'63',3,'Āl-‘Imrān','آل عِمران',13,14,107,'وَأَمَّا اَ۬لَّذِينَ اَ۪بۡيَضَّتۡ وُجُوهُهُمۡ فَفِي رَحۡمَةِ اِ۬للَّهِ هُمۡ فِيهَا خَٰلِدُونَ ١٠٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (400,4,'63',3,'Āl-‘Imrān','آل عِمران',14,15,108,'تِلۡكَ ءَايَٰتُ اُ۬للَّهِ نَتۡلُوهَا عَلَيۡكَ بِالۡحَقِّۗ وَمَا اَ۬للَّهُ يُرِيدُ ظُلۡمٗا لِّلۡعَٰلَمِينَ ١٠٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (401,4,'64',3,'Āl-‘Imrān','آل عِمران',1,1,109,'وَلِلَّهِ مَا فِي اِ۬لسَّمَٰوَٰتِ وَمَا فِي اِ۬لۡأَرۡضِۚ وَإِلَى اَ۬للَّهِ تُرۡجَعُ اُ۬لۡأُمُورُ ١٠٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (402,4,'64',3,'Āl-‘Imrān','آل عِمران',2,5,110,'كُنتُمۡ خَيۡرَ أُمَّةٍ أُخۡرِجَتۡ لِلنّ۪اسِ تَأۡمُرُونَ بِالۡمَعۡرُوفِ وَتَنۡهَوۡنَ عَنِ اِ۬لۡمُنكَرِ وَتُؤۡمِنُونَ بِاللَّهِۗ وَلَوۡ ءَامَنَ أَهۡلُ اُ۬لۡكِتَٰبِ لَكَانَ خَيۡرٗا لَّهُمۚ مِّنۡهُمُ اُ۬لۡمُؤۡمِنُونَ وَأَكۡثَرُهُمُ اُ۬لۡفَٰسِقُونَ ١١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (403,4,'64',3,'Āl-‘Imrān','آل عِمران',5,6,111,'لَن يَضُرُّوكُمۡ إِلَّآ أَذٗىۖ وَإِن يُقَٰتِلُوكُمۡ يُوَلُّوكُمُ اُ۬لۡأَدۡبَارَ ثُمَّ لَا يُنصَرُونَ ١١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (404,4,'64',3,'Āl-‘Imrān','آل عِمران',6,10,112,'ضُرِبَتۡ عَلَيۡهِمِ اِ۬لذِّلَّةُ أَيۡنَ مَا ثُقِفُوٓاْ إِلَّا بِحَبۡلٖ مِّنَ اَ۬للَّهِ وَحَبۡلٖ مِّنَ اَ۬لنّ۪اسِ وَبَآءُو بِغَضَبٖ مِّنَ اَ۬للَّهِ وَضُرِبَتۡ عَلَيۡهِمِ اِ۬لۡمَسۡكَنَةُۚ ذَٰلِكَ بِأَنَّهُمۡ كَانُواْ يَكۡفُرُونَ بِـَٔايَٰتِ اِ۬للَّهِ وَيَقۡتُلُونَ اَ۬لۡأَنۢبِيَآءَ بِغَيۡرِ حَقّٖۚ ذَٰلِكَ بِمَا عَصَواْ وَّكَانُواْ يَعۡتَدُونَ ١١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (405,4,'64',3,'Āl-‘Imrān','آل عِمران',10,12,113,'۞ لَيۡسُواْ سَوَآءٗۗ مِّنۡ أَهۡلِ اِ۬لۡكِتَٰبِ أُمَّةٞ قَآئِمَةٞ يَتۡلُونَ ءَايَٰتِ اِ۬للَّهِ ءَانَآءَ اَ۬لَّيۡلِ وَهُمۡ يَسۡجُدُونَ ١١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (406,4,'64',3,'Āl-‘Imrān','آل عِمران',12,14,114,'يُؤۡمِنُونَ بِاللَّهِ وَاَلۡيَوۡمِ اِ۬لۡأٓخِرِ وَيَأۡمُرُونَ بِالۡمَعۡرُوفِ وَيَنۡهَوۡنَ عَنِ اِ۬لۡمُنكَرِ وَيُسَٰرِعُونَ فِي اِ۬لۡخَيۡرَٰتِ وَأُوْلَٰٓئِكَ مِنَ اَ۬لصَّٰلِحِينَ ١١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (407,4,'64',3,'Āl-‘Imrān','آل عِمران',14,15,115,'وَمَا تَفۡعَلُواْ مِنۡ خَيۡرٖ فَلَن تُكۡفَرُوهُۗ وَاَللَّهُ عَلِيمُۢ بِالۡمُتَّقِينَ ١١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (408,4,'65',3,'Āl-‘Imrān','آل عِمران',1,2,116,'إِنَّ اَ۬لَّذِينَ كَفَرُواْ لَن تُغۡنِيَ عَنۡهُمۡ أَمۡوَٰلُهُمۡ وَلَآ أَوۡلَٰدُهُم مِّنَ اَ۬للَّهِ شَيۡـٔٗاۖ وَأُوْلَٰٓئِكَ أَصۡحَٰبُ اُ۬لنّ۪ارِۖ هُمۡ فِيهَا خَٰلِدُونَ ١١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (409,4,'65',3,'Āl-‘Imrān','آل عِمران',3,5,117,'مَثَلُ مَا يُنفِقُونَ فِي هَٰذِهِ اِ۬لۡحَيَوٰةِ اِ۬لدُّنۡيۭا كَمَثَلِ رِيحٖ فِيهَا صِرٌّ أَصَابَتۡ حَرۡثَ قَوۡمٖ ظَلَمُوٓاْ أَنفُسَهُمۡ فَأَهۡلَكَتۡهُۚ وَمَا ظَلَمَهُمُ اُ۬للَّهُ وَلَٰكِنۡ أَنفُسَهُمۡ يَظۡلِمُونَ ١١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (410,4,'65',3,'Āl-‘Imrān','آل عِمران',5,8,118,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ لَا تَتَّخِذُواْ بِطَانَةٗ مِّن دُونِكُمۡ لَا يَأۡلُونَكُمۡ خَبَالٗا وَدُّواْ مَا عَنِتُّمۡ قَدۡ بَدَتِ اِ۬لۡبَغۡضَآءُ مِنۡ أَفۡوَٰهِهِمۡ وَمَا تُخۡفِي صُدُورُهُمۡ أَكۡبَرُۚ قَدۡ بَيَّنَّا لَكُمُ اُ۬لۡأٓيَٰتِۖ إِن كُنتُمۡ تَعۡقِلُونَ ١١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (411,4,'65',3,'Āl-‘Imrān','آل عِمران',9,12,119,'هَٰٓا۬نتُمۡ أُوْلَآءِ تُحِبُّونَهُمۡ وَلَا يُحِبُّونَكُمۡ وَتُؤۡمِنُونَ بِالۡكِتَٰبِ كُلِّهِۦ وَإِذَا لَقُوكُمۡ قَالُوٓاْ ءَامَنَّا وَإِذَا خَلَوۡاْ عَضُّواْ عَلَيۡكُمُ اُ۬لۡأَنَامِلَ مِنَ اَ۬لۡغَيۡظِۚ قُلۡ مُوتُواْ بِغَيۡظِكُمۡۗ إِنَّ اَ۬للَّهَ عَلِيمُۢ بِذَاتِ اِ۬لصُّدُورِ ١١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (412,4,'65',3,'Āl-‘Imrān','آل عِمران',12,14,120,'إِن تَمۡسَسۡكُمۡ حَسَنَةٞ تَسُؤۡهُمۡ وَإِن تُصِبۡكُمۡ سَيِّئَةٞ يَفۡرَحُواْ بِهَاۖ وَإِن تَصۡبِرُواْ وَتَتَّقُواْ لَا يَضِرۡكُمۡ كَيۡدُهُمۡ شَيۡـًٔاۗ إِنَّ اَ۬للَّهَ بِمَا يَعۡمَلُونَ مُحِيطٞ ١٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (413,4,'65',3,'Āl-‘Imrān','آل عِمران',14,15,121,'۞ وَإِذۡ غَدَوۡتَ مِنۡ أَهۡلِكَ تُبَوِّئُ اُ۬لۡمُؤۡمِنِينَ مَقَٰعِدَ لِلۡقِتَالِۗ وَاَللَّهُ سَمِيعٌ عَلِيمٌ ١٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (414,4,'66',3,'Āl-‘Imrān','آل عِمران',1,2,122,'إِذۡ هَمَّت طَّآئِفَتَانِ مِنكُمۡ أَن تَفۡشَلَا وَاَللَّهُ وَلِيُّهُمَاۗ وَعَلَى اَ۬للَّهِ فَلۡيَتَوَكَّلِ اِ۬لۡمُؤۡمِنُونَ ١٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (415,4,'66',3,'Āl-‘Imrān','آل عِمران',2,3,123,'وَلَقَدۡ نَصَرَكُمُ اُ۬للَّهُ بِبَدۡرٖ وَأَنتُمۡ أَذِلَّةٞۖ فَاَتَّقُواْ اُ۬للَّهَ لَعَلَّكُمۡ تَشۡكُرُونَ ١٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (416,4,'66',3,'Āl-‘Imrān','آل عِمران',3,5,124,'إِذ تَّقُولُ لِلۡمُؤۡمِنِينَ أَلَن يَكۡفِيَكُمۡ أَن يُمِدَّكُمۡ رَبُّكُم بِثَلَٰثَةِ ءَالَٰفٖ مِّنَ اَ۬لۡمَلَٰٓئِكَةِ مُنزَلِينَ ١٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (417,4,'66',3,'Āl-‘Imrān','آل عِمران',5,6,125,'بَلَىٰٓۚ إِن تَصۡبِرُواْ وَتَتَّقُواْ وَيَأۡتُوكُم مِّن فَوۡرِهِمۡ هَٰذَا يُمۡدِدۡكُمۡ رَبُّكُم بِخَمۡسَةِ ءَالَٰفٖ مِّنَ اَ۬لۡمَلَٰٓئِكَةِ مُسَوِّمِينَ ١٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (418,4,'66',3,'Āl-‘Imrān','آل عِمران',7,8,126,'وَمَا جَعَلَهُ اُ۬للَّهُ إِلَّا بُشۡر۪يٰ لَكُمۡ وَلِتَطۡمَئِنَّ قُلُوبُكُم بِهِۦۗ وَمَا اَ۬لنَّصۡرُ إِلَّا مِنۡ عِندِ اِ۬للَّهِ اِ۬لۡعَزِيزِ اِ۬لۡحَكِيمِ ١٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (419,4,'66',3,'Āl-‘Imrān','آل عِمران',8,9,127,'لِيَقۡطَعَ طَرَفٗا مِّنَ اَ۬لَّذِينَ كَفَرُوٓاْ أَوۡ يَكۡبِتَهُمۡ فَيَنقَلِبُواْ خَآئِبِينَ ١٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (420,4,'66',3,'Āl-‘Imrān','آل عِمران',10,11,128,'لَيۡسَ لَكَ مِنَ اَ۬لۡأَمۡرِ شَيۡءٌ أَوۡ يَتُوبَ عَلَيۡهِمۡ أَوۡ يُعَذِّبَهُمۡ فَإِنَّهُمۡ ظَٰلِمُونَ ١٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (421,4,'66',3,'Āl-‘Imrān','آل عِمران',11,12,129,'وَلِلَّهِ مَا فِي اِ۬لسَّمَٰوَٰتِ وَمَا فِي اِ۬لۡأَرۡضِۚ يَغۡفِرُ لِمَن يَشَآءُ وَيُعَذِّبُ مَن يَشَآءُۚ وَاَللَّهُ غَفُورٞ رَّحِيمٞ ١٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (422,4,'66',3,'Āl-‘Imrān','آل عِمران',12,14,130,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ لَا تَأۡكُلُواْ اُ۬لرِّبَوٰٓاْ أَضۡعَٰفٗا مُّضَٰعَفَةٗۖ وَاَتَّقُواْ اُ۬للَّهَ لَعَلَّكُمۡ تُفۡلِحُونَ ١٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (423,4,'66',3,'Āl-‘Imrān','آل عِمران',14,15,131,'وَاَتَّقُواْ اُ۬لنَّارَ اَ۬لَّتِيٓ أُعِدَّتۡ لِلۡكٰ۪فِرِينَ ١٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (424,4,'66',3,'Āl-‘Imrān','آل عِمران',15,15,132,'وَأَطِيعُواْ اُ۬للَّهَ وَاَلرَّسُولَ لَعَلَّكُمۡ تُرۡحَمُونَ ١٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (425,4,'67',3,'Āl-‘Imrān','آل عِمران',1,2,133,'۞ وَسَارِعُوٓاْ إِلَىٰ مَغۡفِرَةٖ مِّن رَّبِّكُمۡ وَجَنَّةٍ عَرۡضُهَا اَ۬لسَّمَٰوَٰتُ وَاَلۡأَرۡضُ أُعِدَّتۡ لِلۡمُتَّقِينَ ١٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (426,4,'67',3,'Āl-‘Imrān','آل عِمران',2,4,134,'اَ۬لَّذِينَ يُنفِقُونَ فِي اِ۬لسَّرَّآءِ وَاَلضَّرَّآءِ وَاَلۡكَٰظِمِينَ اَ۬لۡغَيۡظَ وَاَلۡعَافِينَ عَنِ اِ۬لنّ۪اسِۗ وَاَللَّهُ يُحِبُّ اُ۬لۡمُحۡسِنِينَ ١٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (427,4,'67',3,'Āl-‘Imrān','آل عِمران',4,7,135,'وَاَلَّذِينَ إِذَا فَعَلُواْ فَٰحِشَةً أَوۡ ظَلَمُوٓاْ أَنفُسَهُمۡ ذَكَرُواْ اُ۬للَّهَ فَاَسۡتَغۡفَرُواْ لِذُنُوبِهِمۡ وَمَن يَغۡفِرُ اُ۬لذُّنُوبَ إِلَّا اَ۬للَّهُ وَلَمۡ يُصِرُّواْ عَلَىٰ مَا فَعَلُواْ وَهُمۡ يَعۡلَمُونَ ١٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (428,4,'67',3,'Āl-‘Imrān','آل عِمران',7,9,136,'أُوْلَٰٓئِكَ جَزَآؤُهُم مَّغۡفِرَةٞ مِّن رَّبِّهِمۡ وَجَنَّٰتٞ تَجۡرِي مِن تَحۡتِهَا اَ۬لۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ وَنِعۡمَ أَجۡرُ اُ۬لۡعَٰمِلِينَ ١٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (429,4,'67',3,'Āl-‘Imrān','آل عِمران',9,10,137,'قَدۡ خَلَتۡ مِن قَبۡلِكُمۡ سُنَنٞ فَسِيرُواْ فِي اِ۬لۡأَرۡضِ فَاَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ اُ۬لۡمُكَذِّبِينَ ١٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (430,4,'67',3,'Āl-‘Imrān','آل عِمران',11,11,138,'هَٰذَا بَيَانٞ لِّلنّ۪اسِ وَهُدٗى وَمَوۡعِظَةٞ لِّلۡمُتَّقِينَ ١٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (431,4,'67',3,'Āl-‘Imrān','آل عِمران',12,12,139,'وَلَا تَهِنُواْ وَلَا تَحۡزَنُواْ وَأَنتُمُ اُ۬لۡأَعۡلَوۡنَ إِن كُنتُم مُّؤۡمِنِينَ ١٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (432,4,'67',3,'Āl-‘Imrān','آل عِمران',13,15,140,'إِن يَمۡسَسۡكُمۡ قَرۡحٞ فَقَدۡ مَسَّ اَ۬لۡقَوۡمَ قَرۡحٞ مِّثۡلُهُۥۚ وَتِلۡكَ اَ۬لۡأَيَّامُ نُدَاوِلُهَا بَيۡنَ اَ۬لنّ۪اسِ وَلِيَعۡلَمَ اَ۬للَّهُ اُ۬لَّذِينَ ءَامَنُواْ وَيَتَّخِذَ مِنكُمۡ شُهَدَآءَۗ وَاَللَّهُ لَا يُحِبُّ اُ۬لظَّٰلِمِينَ ١٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (433,4,'68',3,'Āl-‘Imrān','آل عِمران',1,1,141,'وَلِيُمَحِّصَ اَ۬للَّهُ اُ۬لَّذِينَ ءَامَنُواْ وَيَمۡحَقَ اَ۬لۡكٰ۪فِرِينَ ١٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (434,4,'68',3,'Āl-‘Imrān','آل عِمران',1,3,142,'أَمۡ حَسِبۡتُمۡ أَن تَدۡخُلُواْ اُ۬لۡجَنَّةَ وَلَمَّا يَعۡلَمِ اِ۬للَّهُ اُ۬لَّذِينَ جَٰهَدُواْ مِنكُمۡ وَيَعۡلَمَ اَ۬لصَّٰبِرِينَ ١٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (435,4,'68',3,'Āl-‘Imrān','آل عِمران',3,4,143,'وَلَقَدۡ كُنتُمۡ تَمَنَّوۡنَ اَ۬لۡمَوۡتَ مِن قَبۡلِ أَن تَلۡقَوۡهُ فَقَدۡ رَأَيۡتُمُوهُ وَأَنتُمۡ تَنظُرُونَ ١٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (436,4,'68',3,'Āl-‘Imrān','آل عِمران',4,7,144,'۞ وَمَا مُحَمَّدٌ إِلَّا رَسُولٞ قَدۡ خَلَتۡ مِن قَبۡلِهِ اِ۬لرُّسُلُۚ أَفَإِيْن مَّاتَ أَوۡ قُتِلَ اَ۪نقَلَبۡتُمۡ عَلَىٰٓ أَعۡقَٰبِكُمۡۚ وَمَن يَنقَلِبۡ عَلَىٰ عَقِبَيۡهِ فَلَن يَضُرَّ اَ۬للَّهَ شَيۡـٔٗاۗ وَسَيَجۡزِي اِ۬للَّهُ اُ۬لشَّٰكِرِينَ ١٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (437,4,'68',3,'Āl-‘Imrān','آل عِمران',7,10,145,'وَمَا كَانَ لِنَفۡسٍ أَن تَمُوتَ إِلَّا بِإِذۡنِ اِ۬للَّهِ كِتَٰبٗا مُّؤَجَّلٗاۗ وَمَن يُرِد ثَّوَابَ اَ۬لدُّنۡيۭا نُؤۡتِهۡ مِنۡهَا وَمَن يُرِد ثَّوَابَ اَ۬لۡأٓخِرَةِ نُؤۡتِهۡ مِنۡهَاۚ وَسَنَجۡزِي اِ۬لشَّٰكِرِينَ ١٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (438,4,'68',3,'Āl-‘Imrān','آل عِمران',10,12,146,'وَكَأَيِّن مِّن نَّبِيّٖ قُتِلَ مَعَهُۥ رِبِّيُّونَ كَثِيرٞ فَمَا وَهَنُواْ لِمَآ أَصَابَهُمۡ فِي سَبِيلِ اِ۬للَّهِ وَمَا ضَعُفُواْ وَمَا اَ۪سۡتَكَانُواْۗ وَاَللَّهُ يُحِبُّ اُ۬لصَّٰبِرِينَ ١٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (439,4,'68',3,'Āl-‘Imrān','آل عِمران',12,14,147,'وَمَا كَانَ قَوۡلَهُمۡ إِلَّآ أَن قَالُواْ رَبَّنَا اَ۪غۡفِر لَّنَا ذُنُوبَنَا وَإِسۡرَافَنَا فِيٓ أَمۡرِنَا وَثَبِّتۡ أَقۡدَامَنَا وَاَنصُرۡنَا عَلَى اَ۬لۡقَوۡمِ اِ۬لۡكٰ۪فِرِينَ ١٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (440,4,'68',3,'Āl-‘Imrān','آل عِمران',14,15,148,'فَـَٔاتَىٰهُمُ اُ۬للَّهُ ثَوَابَ اَ۬لدُّنۡيۭا وَحُسۡنَ ثَوَابِ اِ۬لۡأٓخِرَةِۗ وَاَللَّهُ يُحِبُّ اُ۬لۡمُحۡسِنِينَ ١٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (441,4,'69',3,'Āl-‘Imrān','آل عِمران',1,2,149,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُوٓاْ إِن تُطِيعُواْ اُ۬لَّذِينَ كَفَرُواْ يَرُدُّوكُمۡ عَلَىٰٓ أَعۡقَٰبِكُمۡ فَتَنقَلِبُواْ خَٰسِرِينَ ١٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (442,4,'69',3,'Āl-‘Imrān','آل عِمران',3,3,150,'بَلِ اِ۬للَّهُ مَوۡلَىٰكُمۡۖ وَهۡوَ خَيۡرُ اُ۬لنَّٰصِرِينَ ١٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (443,4,'69',3,'Āl-‘Imrān','آل عِمران',3,6,151,'سَنُلۡقِي فِي قُلُوبِ اِ۬لَّذِينَ كَفَرُواْ اُ۬لرُّعۡبَ بِمَآ أَشۡرَكُواْ بِاللَّهِ مَا لَمۡ يُنزِلۡ بِهِۦ سُلۡطَٰنٗاۖ وَمَأۡوَىٰهُمُ اُ۬لنَّارُۚ وَبِئۡسَ مَثۡوَى اَ۬لظَّٰلِمِينَ ١٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (444,4,'69',3,'Āl-‘Imrān','آل عِمران',6,11,152,'وَلَقَد صَّدَقَكُمُ اُ۬للَّهُ وَعۡدَهُۥٓ إِذ تَّحُسُّونَهُم بِإِذۡنِهِۦۖ حَتَّىٰٓ إِذَا فَشِلۡتُمۡ وَتَنَٰزَعۡتُمۡ فِي اِ۬لۡأَمۡرِ وَعَصَيۡتُم مِّنۢ بَعۡدِ مَآ أَر۪ىٰكُم مَّا تُحِبُّونَۚ مِنكُم مَّن يُرِيدُ اُ۬لدُّنۡيۭا وَمِنكُم مَّن يُرِيدُ اُ۬لۡأٓخِرَةَۚ ثُمَّ صَرَفَكُمۡ عَنۡهُمۡ لِيَبۡتَلِيَكُمۡۖ وَلَقَدۡ عَفَا عَنكُمۡۗ وَاَللَّهُ ذُو فَضۡلٍ عَلَى اَ۬لۡمُؤۡمِنِينَ ١٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (445,4,'69',3,'Āl-‘Imrān','آل عِمران',12,15,153,'۞ إِذ تُّصۡعِدُونَ وَلَا تَلۡوُۥنَ عَلَىٰٓ أَحَدٖ وَاَلرَّسُولُ يَدۡعُوكُمۡ فِيٓ أُخۡر۪ىٰكُمۡ فَأَثَٰبَكُمۡ غَمَّۢا بِغَمّٖ لِّكَيۡلَا تَحۡزَنُواْ عَلَىٰ مَا فَاتَكُمۡ وَلَا مَآ أَصَٰبَكُمۡۗ وَاَللَّهُ خَبِيرُۢ بِمَا تَعۡمَلُونَ ١٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (446,4,'70',3,'Āl-‘Imrān','آل عِمران',1,8,154,'ثُمَّ أَنزَلَ عَلَيۡكُم مِّنۢ بَعۡدِ اِ۬لۡغَمِّ أَمَنَةٗ نُّعَاسٗا يَغۡشَىٰ طَآئِفَةٗ مِّنكُمۡۖ وَطَآئِفَةٞ قَدۡ أَهَمَّتۡهُمۡ أَنفُسُهُمۡ يَظُنُّونَ بِاللَّهِ غَيۡرَ اَ۬لۡحَقِّ ظَنَّ اَ۬لۡجَٰهِلِيَّةِۖ يَقُولُونَ هَل لَّنَا مِنَ اَ۬لۡأَمۡرِ مِن شَيۡءٖۗ قُلۡ إِنَّ اَ۬لۡأَمۡرَ كُلُّهُۥ لِلَّهِۗ يُخۡفُونَ فِيٓ أَنفُسِهِم مَّا لَا يُبۡدُونَ لَكَۖ يَقُولُونَ لَوۡ كَانَ لَنَا مِنَ اَ۬لۡأَمۡرِ شَيۡءٞ مَّا قُتِلۡنَا هَٰهُنَاۗ قُل لَّوۡ كُنتُمۡ فِي بُيُوتِكُمۡ لَبَرَزَ اَ۬لَّذِينَ كُتِبَ عَلَيۡهِمِ اِ۬لۡقَتۡلُ إِلَىٰ مَضَاجِعِهِمۡۖ وَلِيَبۡتَلِيَ اَ۬للَّهُ مَا فِي صُدُورِكُمۡ وَلِيُمَحِّصَ مَا فِي قُلُوبِكُمۡۗ وَاَللَّهُ عَلِيمُۢ بِذَاتِ اِ۬لصُّدُورِ ١٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (447,4,'70',3,'Āl-‘Imrān','آل عِمران',8,10,155,'إِنَّ اَ۬لَّذِينَ تَوَلَّوۡاْ مِنكُمۡ يَوۡمَ اَ۪لۡتَقَى اَ۬لۡجَمۡعَانِ إِنَّمَا اَ۪سۡتَزَلَّهُمُ اُ۬لشَّيۡطَٰنُ بِبَعۡضِ مَا كَسَبُواْۖ وَلَقَدۡ عَفَا اَ۬للَّهُ عَنۡهُمۡۗ إِنَّ اَ۬للَّهَ غَفُورٌ حَلِيمٞ ١٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (448,4,'70',3,'Āl-‘Imrān','آل عِمران',10,14,156,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ لَا تَكُونُواْ كَاَلَّذِينَ كَفَرُواْ وَقَالُواْ لِإِخۡوَٰنِهِمۡ إِذَا ضَرَبُواْ فِي اِ۬لۡأَرۡضِ أَوۡ كَانُواْ غُزّٗى لَّوۡ كَانُواْ عِندَنَا مَا مَاتُواْ وَمَا قُتِلُواْ لِيَجۡعَلَ اَ۬للَّهُ ذَٰلِكَ حَسۡرَةٗ فِي قُلُوبِهِمۡۗ وَاَللَّهُ يُحۡيِۦ وَيُمِيتُۗ وَاَللَّهُ بِمَا تَعۡمَلُونَ بَصِيرٞ ١٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (449,4,'70',3,'Āl-‘Imrān','آل عِمران',14,15,157,'وَلَئِن قُتِلۡتُمۡ فِي سَبِيلِ اِ۬للَّهِ أَوۡ مُتُّمۡ لَمَغۡفِرَةٞ مِّنَ اَ۬للَّهِ وَرَحۡمَةٌ خَيۡرٞ مِّمَّا تَجۡمَعُونَ ١٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (450,4,'71',3,'Āl-‘Imrān','آل عِمران',1,1,158,'وَلَئِن مُّتُّمۡ أَوۡ قُتِلۡتُمۡ لَإِاْلَى اَ۬للَّهِ تُحۡشَرُونَ ١٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (451,4,'71',3,'Āl-‘Imrān','آل عِمران',1,4,159,'فَبِمَا رَحۡمَةٖ مِّنَ اَ۬للَّهِ لِنتَ لَهُمۡۖ وَلَوۡ كُنتَ فَظًّا غَلِيظَ اَ۬لۡقَلۡبِ لَاَنفَضُّواْ مِنۡ حَوۡلِكَۖ فَاَعۡفُ عَنۡهُمۡ وَاَسۡتَغۡفِر لَّهُمۡ وَشَاوِرۡهُمۡ فِي اِ۬لۡأَمۡرِۖ فَإِذَا عَزَمۡتَ فَتَوَكَّلۡ عَلَى اَ۬للَّهِۚ إِنَّ اَ۬للَّهَ يُحِبُّ اُ۬لۡمُتَوَكِّلِينَ ١٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (452,4,'71',3,'Āl-‘Imrān','آل عِمران',4,6,160,'إِن يَنصُرۡكُمُ اُ۬للَّهُ فَلَا غَالِبَ لَكُمۡۖ وَإِن يَخۡذُلۡكُمۡ فَمَن ذَا اَ۬لَّذِي يَنصُرۡكُم مِّنۢ بَعۡدِهِۦۗ وَعَلَى اَ۬للَّهِ فَلۡيَتَوَكَّلِ اِ۬لۡمُؤۡمِنُونَ ١٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (453,4,'71',3,'Āl-‘Imrān','آل عِمران',6,8,161,'۞ وَمَا كَانَ لِنَبِيٍّ أَن يَغُلَّۚ وَمَن يَغۡلُلۡ يَأۡتِ بِمَا غَلَّ يَوۡمَ اَ۬لۡقِيَٰمَةِۚ ثُمَّ تُوَفَّىٰ كُلُّ نَفۡسٖ مَّا كَسَبَتۡ وَهُمۡ لَا يُظۡلَمُونَ ١٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (454,4,'71',3,'Āl-‘Imrān','آل عِمران',8,9,162,'أَفَمَنِ اِ۪تَّبَعَ رِضۡوَٰنَ اَ۬للَّهِ كَمَنۢ بَآءَ بِسَخَطٖ مِّنَ اَ۬للَّهِ وَمَأۡوَىٰهُ جَهَنَّمُۚ وَبِئۡسَ اَ۬لۡمَصِيرُ ١٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (455,4,'71',3,'Āl-‘Imrān','آل عِمران',10,10,163,'هُمۡ دَرَجَٰتٌ عِندَ اَ۬للَّهِۗ وَاَللَّهُ بَصِيرُۢ بِمَا يَعۡمَلُونَ ١٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (456,4,'71',3,'Āl-‘Imrān','آل عِمران',10,13,164,'لَقَدۡ مَنَّ اَ۬للَّهُ عَلَى اَ۬لۡمُؤۡمِنِينَ إِذۡ بَعَثَ فِيهِمۡ رَسُولٗا مِّنۡ أَنفُسِهِمۡ يَتۡلُواْ عَلَيۡهِمۡ ءَايَٰتِهِۦ وَيُزَكِّيهِمۡ وَيُعَلِّمُهُمُ اُ۬لۡكِتَٰبَ وَاَلۡحِكۡمَةَ وَإِن كَانُواْ مِن قَبۡلُ لَفِي ضَلَٰلٖ مُّبِينٍ ١٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (457,4,'71',3,'Āl-‘Imrān','آل عِمران',13,15,165,'أَوَلَمَّآ أَصَٰبَتۡكُم مُّصِيبَةٞ قَدۡ أَصَبۡتُم مِّثۡلَيۡهَا قُلۡتُمۡ أَنّۭيٰ هَٰذَاۖ قُلۡ هُوَ مِنۡ عِندِ أَنفُسِكُمۡۗ إِنَّ اَ۬للَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ ١٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (458,4,'72',3,'Āl-‘Imrān','آل عِمران',1,1,166,'وَمَآ أَصَٰبَكُمۡ يَوۡمَ اَ۪لۡتَقَى اَ۬لۡجَمۡعَانِ فَبِإِذۡنِ اِ۬للَّهِ وَلِيَعۡلَمَ اَ۬لۡمُؤۡمِنِينَ ١٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (459,4,'72',3,'Āl-‘Imrān','آل عِمران',2,5,167,'وَلِيَعۡلَمَ اَ۬لَّذِينَ نَافَقُواْۚ وَقِيلَ لَهُمۡ تَعَالَوۡاْ قَٰتِلُواْ فِي سَبِيلِ اِ۬للَّهِ أَوِ اِ۪دۡفَعُواْۖ قَالُواْ لَوۡ نَعۡلَمُ قِتَالٗا لَّاَتَّبَعۡنَٰكُمۡۗ هُمۡ لِلۡكُفۡرِ يَوۡمَئِذٍ أَقۡرَبُ مِنۡهُمۡ لِلۡإِيمَٰنِۚ يَقُولُونَ بِأَفۡوَٰهِهِم مَّا لَيۡسَ فِي قُلُوبِهِمۡۗ وَاَللَّهُ أَعۡلَمُ بِمَا يَكۡتُمُونَ ١٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (460,4,'72',3,'Āl-‘Imrān','آل عِمران',5,7,168,'اَ۬لَّذِينَ قَالُواْ لِإِخۡوَٰنِهِمۡ وَقَعَدُواْ لَوۡ أَطَاعُونَا مَا قُتِلُواْۗ قُلۡ فَاَدۡرَءُواْ عَنۡ أَنفُسِكُمُ اُ۬لۡمَوۡتَ إِن كُنتُمۡ صَٰدِقِينَ ١٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (461,4,'72',3,'Āl-‘Imrān','آل عِمران',7,8,169,'وَلَا تَحۡسِبَنَّ اَ۬لَّذِينَ قُتِلُواْ فِي سَبِيلِ اِ۬للَّهِ أَمۡوَٰتَۢاۚ بَلۡ أَحۡيَآءٌ عِندَ رَبِّهِمۡ يُرۡزَقُونَ ١٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (462,4,'72',3,'Āl-‘Imrān','آل عِمران',8,10,170,'فَرِحِينَ بِمَآ ءَاتَىٰهُمُ اُ۬للَّهُ مِن فَضۡلِهِۦ وَيَسۡتَبۡشِرُونَ بِالَّذِينَ لَمۡ يَلۡحَقُواْ بِهِم مِّنۡ خَلۡفِهِمۡ أَلَّا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ ١٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (463,4,'72',3,'Āl-‘Imrān','آل عِمران',10,12,171,'۞ يَسۡتَبۡشِرُونَ بِنِعۡمَةٖ مِّنَ اَ۬للَّهِ وَفَضۡلٖ وَأَنَّ اَ۬للَّهَ لَا يُضِيعُ أَجۡرَ اَ۬لۡمُؤۡمِنِينَ ١٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (464,4,'72',3,'Āl-‘Imrān','آل عِمران',12,13,172,'اَ۬لَّذِينَ اَ۪سۡتَجَابُواْ لِلَّهِ وَاَلرَّسُولِ مِنۢ بَعۡدِ مَآ أَصَابَهُمُ اُ۬لۡقَرۡحُۚ لِلَّذِينَ أَحۡسَنُواْ مِنۡهُمۡ وَاَتَّقَوۡاْ أَجۡرٌ عَظِيمٌ ١٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (465,4,'72',3,'Āl-‘Imrān','آل عِمران',14,15,173,'اِ۬لَّذِينَ قَالَ لَهُمُ اُ۬لنَّاسُ إِنَّ اَ۬لنَّاسَ قَد جَّمَعُواْ لَكُمۡ فَاَخۡشَوۡهُمۡ فَزَادَهُمۡ إِيمَٰنٗا وَقَالُواْ حَسۡبُنَا اَ۬للَّهُ وَنِعۡمَ اَ۬لۡوَكِيلُ ١٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (466,4,'73',3,'Āl-‘Imrān','آل عِمران',1,2,174,'فَاَنقَلَبُواْ بِنِعۡمَةٖ مِّنَ اَ۬للَّهِ وَفَضۡلٖ لَّمۡ يَمۡسَسۡهُمۡ سُوٓءٞ وَاَتَّبَعُواْ رِضۡوَٰنَ اَ۬للَّهِۗ وَاَللَّهُ ذُو فَضۡلٍ عَظِيمٍ ١٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (467,4,'73',3,'Āl-‘Imrān','آل عِمران',2,3,175,'إِنَّمَا ذَٰلِكُمُ اُ۬لشَّيۡطَٰنُ يُخَوِّفُ أَوۡلِيَآءَهُۥ فَلَا تَخَافُوهُمۡ وَخَافُونِۦٓ إِن كُنتُم مُّؤۡمِنِينَ ١٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (468,4,'73',3,'Āl-‘Imrān','آل عِمران',4,6,176,'وَلَا يَحۡزُنكَ اَ۬لَّذِينَ يُسَٰرِعُونَ فِي اِ۬لۡكُفۡرِۚ إِنَّهُمۡ لَن يَضُرُّواْ اُ۬للَّهَ شَيۡـٔٗاۗ يُرِيدُ اُ۬للَّهُ أَلَّا يَجۡعَلَ لَهُمۡ حَظّٗا فِي اِ۬لۡأٓخِرَةِۖ وَلَهُمۡ عَذَابٌ عَظِيمٌ ١٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (469,4,'73',3,'Āl-‘Imrān','آل عِمران',6,7,177,'إِنَّ اَ۬لَّذِينَ اَ۪شۡتَرَوُاْ اُ۬لۡكُفۡرَ بِالۡإِيمَٰنِ لَن يَضُرُّواْ اُ۬للَّهَ شَيۡـٔٗا وَلَهُمۡ عَذَابٌ أَلِيمٞ ١٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (470,4,'73',3,'Āl-‘Imrān','آل عِمران',7,9,178,'وَلَا يَحۡسِبَنَّ اَ۬لَّذِينَ كَفَرُوٓاْ أَنَّمَا نُمۡلِي لَهُمۡ خَيۡرٞ لِّأَنفُسِهِمۡۚ إِنَّمَا نُمۡلِي لَهُمۡ لِيَزۡدَادُوٓاْ إِثۡمٗاۚ وَلَهُمۡ عَذَابٞ مُّهِينٞ ١٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (471,4,'73',3,'Āl-‘Imrān','آل عِمران',9,12,179,'۞ مَّا كَانَ اَ۬للَّهُ لِيَذَرَ اَ۬لۡمُؤۡمِنِينَ عَلَىٰ مَآ أَنتُمۡ عَلَيۡهِ حَتَّىٰ يَمِيزَ اَ۬لۡخَبِيثَ مِنَ اَ۬لطَّيِّبِۗ وَمَا كَانَ اَ۬للَّهُ لِيُطۡلِعَكُمۡ عَلَى اَ۬لۡغَيۡبِ وَلَٰكِنَّ اَ۬للَّهَ يَجۡتَبِي مِن رُّسُلِهِۦ مَن يَشَآءُۖ فَـَٔامِنُواْ بِاللَّهِ وَرُسُلِهِۦۚ وَإِن تُؤۡمِنُواْ وَتَتَّقُواْ فَلَكُمۡ أَجۡرٌ عَظِيمٞ ١٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (472,4,'73',3,'Āl-‘Imrān','آل عِمران',12,15,180,'وَلَا يَحۡسِبَنَّ اَ۬لَّذِينَ يَبۡخَلُونَ بِمَآ ءَاتَىٰهُمُ اُ۬للَّهُ مِن فَضۡلِهِۦ هُوَ خَيۡرٗا لَّهُمۖ بَلۡ هُوَ شَرّٞ لَّهُمۡۖ سَيُطَوَّقُونَ مَا بَخِلُواْ بِهِۦ يَوۡمَ اَ۬لۡقِيَٰمَةِۗ وَلِلَّهِ مِيرَٰثُ اُ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِۗ وَاَللَّهُ بِمَا يَعۡمَلُونَ خَبِيرٞ ١٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (473,4,'74',3,'Āl-‘Imrān','آل عِمران',1,3,181,'لَّقَد سَّمِعَ اَ۬للَّهُ قَوۡلَ اَ۬لَّذِينَ قَالُوٓاْ إِنَّ اَ۬للَّهَ فَقِيرٞ وَنَحۡنُ أَغۡنِيَآءُۘ سَنَكۡتُبُ مَا قَالُواْ وَقَتۡلَهُمُ اُ۬لۡأَنۢبِيَآءَ بِغَيۡرِ حَقّٖ وَنَقُولُ ذُوقُواْ عَذَابَ اَ۬لۡحَرِيقِ ١٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (474,4,'74',3,'Āl-‘Imrān','آل عِمران',3,4,182,'ذَٰلِكَ بِمَا قَدَّمَتۡ أَيۡدِيكُمۡ وَأَنَّ اَ۬للَّهَ لَيۡسَ بِظَلَّامٖ لِّلۡعَبِيدِ ١٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (475,4,'74',3,'Āl-‘Imrān','آل عِمران',4,7,183,'اِ۬لَّذِينَ قَالُوٓاْ إِنَّ اَ۬للَّهَ عَهِدَ إِلَيۡنَآ أَلَّا نُؤۡمِنَ لِرَسُولٍ حَتَّىٰ يَأۡتِيَنَا بِقُرۡبَانٖ تَأۡكُلُهُ اُ۬لنَّارُۗ قُلۡ قَد جَّآءَكُمۡ رُسُلٞ مِّن قَبۡلِي بِالۡبَيِّنَٰتِ وَبِالَّذِي قُلۡتُمۡ فَلِمَ قَتَلۡتُمُوهُمۡ إِن كُنتُمۡ صَٰدِقِينَ ١٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (476,4,'74',3,'Āl-‘Imrān','آل عِمران',8,9,184,'فَإِن كَذَّبُوكَ فَقَدۡ كُذِّبَ رُسُلٞ مِّن قَبۡلِكَ جَآءُو بِالۡبَيِّنَٰتِ وَاَلزُّبُرِ وَاَلۡكِتَٰبِ اِ۬لۡمُنِيرِ ١٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (477,4,'74',3,'Āl-‘Imrān','آل عِمران',9,12,185,'كُلُّ نَفۡسٖ ذَآئِقَةُ اُ۬لۡمَوۡتِۗ وَإِنَّمَا تُوَفَّوۡنَ أُجُورَكُمۡ يَوۡمَ اَ۬لۡقِيَٰمَةِۖ فَمَن زُحۡزِحَ عَنِ اِ۬لنّ۪ارِ وَأُدۡخِلَ اَ۬لۡجَنَّةَ فَقَدۡ فَازَۗ وَمَا اَ۬لۡحَيَوٰةُ اُ۬لدُّنۡيۭآ إِلَّا مَتَٰعُ اُ۬لۡغُرُورِ ١٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (478,4,'74',3,'Āl-‘Imrān','آل عِمران',12,15,186,'۞ لَتُبۡلَوُنَّ فِيٓ أَمۡوَٰلِكُمۡ وَأَنفُسِكُمۡ وَلَتَسۡمَعُنَّ مِنَ اَ۬لَّذِينَ أُوتُواْ اُ۬لۡكِتَٰبَ مِن قَبۡلِكُمۡ وَمِنَ اَ۬لَّذِينَ أَشۡرَكُوٓاْ أَذٗى كَثِيرٗاۚ وَإِن تَصۡبِرُواْ وَتَتَّقُواْ فَإِنَّ ذَٰلِكَ مِنۡ عَزۡمِ اِ۬لۡأُمُورِ ١٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (479,4,'75',3,'Āl-‘Imrān','آل عِمران',1,3,187,'وَإِذۡ أَخَذَ اَ۬للَّهُ مِيثَٰقَ اَ۬لَّذِينَ أُوتُواْ اُ۬لۡكِتَٰبَ لَيُبَيِّنُنَّهُۥ لِلنّ۪اسِ وَلَا يَكۡتُمُونَهُۥ فَنَبَذُوهُ وَرَآءَ ظُهُورِهِمۡ وَاَشۡتَرَوۡاْ بِهِۦ ثَمَنٗا قَلِيلٗاۖ فَبِئۡسَ مَا يَشۡتَرُونَ ١٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (480,4,'75',3,'Āl-‘Imrān','آل عِمران',3,5,188,'لَا يَحۡسِبَنَّ اَ۬لَّذِينَ يَفۡرَحُونَ بِمَآ أَتَواْ وَّيُحِبُّونَ أَن يُحۡمَدُواْ بِمَا لَمۡ يَفۡعَلُواْ فَلَا يَحۡسِبُنَّهُم بِمَفَازَةٖ مِّنَ اَ۬لۡعَذَابِۖ وَلَهُمۡ عَذَابٌ أَلِيمٞ ١٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (481,4,'75',3,'Āl-‘Imrān','آل عِمران',5,6,189,'وَلِلَّهِ مُلۡكُ اُ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِۗ وَاَللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ ١٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (482,4,'75',3,'Āl-‘Imrān','آل عِمران',6,8,190,'إِنَّ فِي خَلۡقِ اِ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِ وَاَخۡتِلَٰفِ اِ۬لَّيۡلِ وَاَلنَّه۪ارِ لَأٓيَٰتٖ لِّأُوْلِي اِ۬لۡأَلۡبَٰبِ ١٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (483,4,'75',3,'Āl-‘Imrān','آل عِمران',8,10,191,'اِ۬لَّذِينَ يَذۡكُرُونَ اَ۬للَّهَ قِيَٰمٗا وَقُعُودٗا وَعَلَىٰ جُنُوبِهِمۡ وَيَتَفَكَّرُونَ فِي خَلۡقِ اِ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِ رَبَّنَا مَا خَلَقۡتَ هَٰذَا بَٰطِلٗا سُبۡحَٰنَكَ فَقِنَا عَذَابَ اَ۬لنّ۪ارِ ١٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (484,4,'75',3,'Āl-‘Imrān','آل عِمران',11,12,192,'رَبَّنَآ إِنَّكَ مَن تُدۡخِلِ اِ۬لنَّارَ فَقَدۡ أَخۡزَيۡتَهُۥۖ وَمَا لِلظَّٰلِمِينَ مِنۡ أَنص۪ارٖ ١٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (485,4,'75',3,'Āl-‘Imrān','آل عِمران',12,14,193,'رَّبَّنَآ إِنَّنَا سَمِعۡنَا مُنَادِيٗا يُنَادِي لِلۡإِيمَٰنِ أَنۡ ءَامِنُواْ بِرَبِّكُمۡ فَـَٔامَنَّاۚ رَبَّنَا فَاَغۡفِر لَّنَا ذُنُوبَنَا وَكَفِّرۡ عَنَّا سَيِّـَٔاتِنَا وَتَوَفَّنَا مَعَ اَ۬لۡأَبۡر۪ارِ ١٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (486,4,'75',3,'Āl-‘Imrān','آل عِمران',14,15,194,'رَبَّنَا وَءَاتِنَا مَا وَعَدتَّنَا عَلَىٰ رُسُلِكَ وَلَا تُخۡزِنَا يَوۡمَ اَ۬لۡقِيَٰمَةِۖ إِنَّكَ لَا تُخۡلِفُ اُ۬لۡمِيعَادَ ١٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (487,4,'76',3,'Āl-‘Imrān','آل عِمران',1,6,195,'۞ فَاَسۡتَجَابَ لَهُمۡ رَبُّهُمۡ أَنِّي لَآ أُضِيعُ عَمَلَ عَٰمِلٖ مِّنكُم مِّن ذَكَرٍ أَوۡ أُنثۭيٰۖ بَعۡضُكُم مِّنۢ بَعۡضٖۖ فَاَلَّذِينَ هَاجَرُواْ وَأُخۡرِجُواْ مِن دِيٰ۪رِهِمۡ وَأُوذُواْ فِي سَبِيلِي وَقَٰتَلُواْ وَقُتِلُواْ لَأُكَفِّرَنَّ عَنۡهُمۡ سَيِّـَٔاتِهِمۡ وَلَأُدۡخِلَنَّهُمۡ جَنَّٰتٖ تَجۡرِي مِن تَحۡتِهَا اَ۬لۡأَنۡهَٰرُ ثَوَابٗا مِّنۡ عِندِ اِ۬للَّهِۗ وَاَللَّهُ عِندَهُۥ حُسۡنُ اُ۬لثَّوَابِ ١٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (488,4,'76',3,'Āl-‘Imrān','آل عِمران',6,7,196,'لَا يَغُرَّنَّكَ تَقَلُّبُ اُ۬لَّذِينَ كَفَرُواْ فِي اِ۬لۡبِلَٰدِ ١٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (489,4,'76',3,'Āl-‘Imrān','آل عِمران',7,7,197,'مَتَٰعٞ قَلِيلٞ ثُمَّ مَأۡوَىٰهُمۡ جَهَنَّمُۚ وَبِئۡسَ اَ۬لۡمِهَادُ ١٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (490,4,'76',3,'Āl-‘Imrān','آل عِمران',8,10,198,'لَٰكِنِ اِ۬لَّذِينَ اَ۪تَّقَوۡاْ رَبَّهُمۡ لَهُمۡ جَنَّٰتٞ تَجۡرِي مِن تَحۡتِهَا اَ۬لۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَا نُزُلٗا مِّنۡ عِندِ اِ۬للَّهِۗ وَمَا عِندَ اَ۬للَّهِ خَيۡرٞ لِّلۡأَبۡر۪ارِ ١٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (491,4,'76',3,'Āl-‘Imrān','آل عِمران',10,14,199,'وَإِنَّ مِنۡ أَهۡلِ اِ۬لۡكِتَٰبِ لَمَن يُؤۡمِنُ بِاللَّهِ وَمَآ أُنزِلَ إِلَيۡكُمۡ وَمَآ أُنزِلَ إِلَيۡهِمۡ خَٰشِعِينَ لِلَّهِ لَا يَشۡتَرُونَ بِـَٔايَٰتِ اِ۬للَّهِ ثَمَنٗا قَلِيلًاۗ أُوْلَٰٓئِكَ لَهُمۡ أَجۡرُهُمۡ عِندَ رَبِّهِمۡۗ إِنَّ اَ۬للَّهَ سَرِيعُ اُ۬لۡحِسَابِ ١٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (492,4,'76',3,'Āl-‘Imrān','آل عِمران',14,15,200,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ اُ۪صۡبِرُواْ وَصَابِرُواْ وَرَابِطُواْ وَاَتَّقُواْ اُ۬للَّهَ لَعَلَّكُمۡ تُفۡلِحُونَ ٢٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (493,4,'77',4,'An-Nisā’','النِّسَاء',3,5,1,'يَٰٓأَيُّهَا اَ۬لنَّاسُ اُ۪تَّقُواْ رَبَّكُمُ اُ۬لَّذِي خَلَقَكُم مِّن نَّفۡسٖ وَٰحِدَةٖ وَخَلَقَ مِنۡهَا زَوۡجَهَا وَبَثَّ مِنۡهُمَا رِجَالٗا كَثِيرٗا وَنِسَآءٗۚ وَاَتَّقُواْ اُ۬للَّهَ اَ۬لَّذِي تَسَّآءَلُونَ بِهِۦ وَاَلۡأَرۡحَامَۚ إِنَّ اَ۬للَّهَ كَانَ عَلَيۡكُمۡ رَقِيبٗا ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (494,4,'77',4,'An-Nisā’','النِّسَاء',5,6,2,'وَءَاتُواْ اُ۬لۡيَتَٰمَىٰٓ أَمۡوَٰلَهُمۡۖ وَلَا تَتَبَدَّلُواْ اُ۬لۡخَبِيثَ بِالطَّيِّبِۖ وَلَا تَأۡكُلُوٓاْ أَمۡوَٰلَهُمۡ إِلَىٰٓ أَمۡوَٰلِكُمۡۚ إِنَّهُۥ كَانَ حُوبٗا كَبِيرٗا ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (495,4,'77',4,'An-Nisā’','النِّسَاء',7,9,3,'وَإِنۡ خِفۡتُمۡ أَلَّا تُقۡسِطُواْ فِي اِ۬لۡيَتَٰمَىٰ فَاَنكِحُواْ مَا طَابَ لَكُم مِّنَ اَ۬لنِّسَآءِ مَثۡنَىٰ وَثُلَٰثَ وَرُبَٰعَۖ فَإِنۡ خِفۡتُمۡ أَلَّا تَعۡدِلُواْ فَوَٰحِدَةً أَوۡ مَا مَلَكَتۡ أَيۡمَٰنُكُمۡۚ ذَٰلِكَ أَدۡنَىٰٓ أَلَّا تَعُولُواْ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (496,4,'77',4,'An-Nisā’','النِّسَاء',9,10,4,'وَءَاتُواْ اُ۬لنِّسَآءَ صَدُقَٰتِهِنَّ نِحۡلَةٗۚ فَإِن طِبۡنَ لَكُمۡ عَن شَيۡءٖ مِّنۡهُ نَفۡسٗا فَكُلُوهُ هَنِيٓـٔٗا مَّرِيٓـٔٗا ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (497,4,'77',4,'An-Nisā’','النِّسَاء',10,12,5,'وَلَا تُؤۡتُواْ اُ۬لسُّفَهَآ أَمۡوَٰلَكُمُ اُ۬لَّتِي جَعَلَ اَ۬للَّهُ لَكُمۡ قِيَٰمٗا وَاَرۡزُقُوهُمۡ فِيهَا وَاَكۡسُوهُمۡ وَقُولُواْ لَهُمۡ قَوۡلٗا مَّعۡرُوفٗا ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (498,4,'77',4,'An-Nisā’','النِّسَاء',12,15,6,'وَاَبۡتَلُواْ اُ۬لۡيَتَٰمَىٰ حَتَّىٰٓ إِذَا بَلَغُواْ اُ۬لنِّكَاحَ فَإِنۡ ءَانَسۡتُم مِّنۡهُمۡ رُشۡدٗا فَاَدۡفَعُوٓاْ إِلَيۡهِمۡ أَمۡوَٰلَهُمۡۖ وَلَا تَأۡكُلُوهَآ إِسۡرَافٗا وَبِدَارًا أَن يَكۡبَرُواْۚ وَمَن كَانَ غَنِيّٗا فَلۡيَسۡتَعۡفِفۡۖ وَمَن كَانَ فَقِيرٗا فَلۡيَأۡكُلۡ بِالۡمَعۡرُوفِۚ فَإِذَا دَفَعۡتُمۡ إِلَيۡهِمۡ أَمۡوَٰلَهُمۡ فَأَشۡهِدُواْ عَلَيۡهِمۡۚ وَكَفَىٰ بِاللَّهِ حَسِيبٗا ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (499,4,'78',4,'An-Nisā’','النِّسَاء',1,3,7,'۞ لِّلرِّجَالِ نَصِيبٞ مِّمَّا تَرَكَ اَ۬لۡوَٰلِدَانِ وَاَلۡأَقۡرَبُونَ وَلِلنِّسَآءِ نَصِيبٞ مِّمَّا تَرَكَ اَ۬لۡوَٰلِدَانِ وَاَلۡأَقۡرَبُونَ مِمَّا قَلَّ مِنۡهُ أَوۡ كَثُرَۚ نَصِيبٗا مَّفۡرُوضٗا ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (500,4,'78',4,'An-Nisā’','النِّسَاء',3,4,8,'وَإِذَا حَضَرَ اَ۬لۡقِسۡمَةَ أُوْلُواْ اُ۬لۡقُرۡبۭيٰ وَاَلۡيَتَٰمَىٰ وَاَلۡمَسَٰكِينُ فَاَرۡزُقُوهُم مِّنۡهُ وَقُولُواْ لَهُمۡ قَوۡلٗا مَّعۡرُوفٗا ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (501,4,'78',4,'An-Nisā’','النِّسَاء',5,6,9,'وَلۡيَخۡشَ اَ۬لَّذِينَ لَوۡ تَرَكُواْ مِنۡ خَلۡفِهِمۡ ذُرِّيَّةٗ ضِعَٰفًا خَافُواْ عَلَيۡهِمۡ فَلۡيَتَّقُواْ اُ۬للَّهَ وَلۡيَقُولُواْ قَوۡلٗا سَدِيدًا ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (502,4,'78',4,'An-Nisā’','النِّسَاء',6,8,10,'إِنَّ اَ۬لَّذِينَ يَأۡكُلُونَ أَمۡوَٰلَ اَ۬لۡيَتَٰمَىٰ ظُلۡمًا إِنَّمَا يَأۡكُلُونَ فِي بُطُونِهِمۡ نَارٗاۖ وَسَيَصۡلَوۡنَ سَعِيرٗا ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (503,4,'78',4,'An-Nisā’','النِّسَاء',8,15,11,'يُوصِيكُمُ اُ۬للَّهُ فِيٓ أَوۡلَٰدِكُمۡۖ لِلذَّكَرِ مِثۡلُ حَظِّ اِ۬لۡأُنثَيَيۡنِۚ فَإِن كُنَّ نِسَآءٗ فَوۡقَ اَ۪ثۡنَتَيۡنِ فَلَهُنَّ ثُلُثَا مَا تَرَكَۖ وَإِن كَانَتۡ وَٰحِدَةٗ فَلَهَا اَ۬لنِّصۡفُۚ وَلِأَبَوَيۡهِ لِكُلِّ وَٰحِدٖ مِّنۡهُمَا اَ۬لسُّدُسُ مِمَّا تَرَكَ إِن كَانَ لَهُۥ وَلَدٞۚ فَإِن لَّمۡ يَكُن لَّهُۥ وَلَدٞ وَوَرِثَهُۥٓ أَبَوَاهُ فَلِأُمِّهِ اِ۬لثُّلُثُۚ فَإِن كَانَ لَهُۥٓ إِخۡوَةٞ فَلِأُمِّهِ اِ۬لسُّدُسُۚ مِنۢ بَعۡدِ وَصِيَّةٖ يُوصِي بِهَآ أَوۡ دَيۡنٍۗ ءَابَآؤُكُمۡ وَأَبۡنَآؤُكُمۡ لَا تَدۡرُونَ أَيُّهُمۡ أَقۡرَبُ لَكُمۡ نَفۡعٗاۚ فَرِيضَةٗ مِّنَ اَ۬للَّهِۗ إِنَّ اَ۬للَّهَ كَانَ عَلِيمًا حَكِيمٗا ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (504,4,'79',4,'An-Nisā’','النِّسَاء',1,11,12,'۞ وَلَكُمۡ نِصۡفُ مَا تَرَكَ أَزۡوَٰجُكُمۡ إِن لَّمۡ يَكُن لَّهُنَّ وَلَدٞۚ فَإِن كَانَ لَهُنَّ وَلَدٞ فَلَكُمُ اُ۬لرُّبُعُ مِمَّا تَرَكۡنَۚ مِنۢ بَعۡدِ وَصِيَّةٖ يُوصِينَ بِهَآ أَوۡ دَيۡنٖۚ وَلَهُنَّ اَ۬لرُّبُعُ مِمَّا تَرَكۡتُمۡ إِن لَّمۡ يَكُن لَّكُمۡ وَلَدٞۚ فَإِن كَانَ لَكُمۡ وَلَدٞ فَلَهُنَّ اَ۬لثُّمُنُ مِمَّا تَرَكۡتُمۚ مِّنۢ بَعۡدِ وَصِيَّةٖ تُوصُونَ بِهَآ أَوۡ دَيۡنٖۗ وَإِن كَانَ رَجُلٞ يُورَثُ كَلَٰلَةً أَوِ اِ۪مۡرَأَةٞ وَلَهُۥٓ أَخٌ أَوۡ أُخۡتٞ فَلِكُلِّ وَٰحِدٖ مِّنۡهُمَا اَ۬لسُّدُسُۚ فَإِن كَانُوٓاْ أَكۡثَرَ مِن ذَٰلِكَ فَهُمۡ شُرَكَآءُ فِي اِ۬لثُّلُثِۚ مِنۢ بَعۡدِ وَصِيَّةٖ يُوصِي بِهَآ أَوۡ دَيۡنٍ غَيۡرَ مُضَآرّٖۚ وَصِيَّةٗ مِّنَ اَ۬للَّهِۗ وَاَللَّهُ عَلِيمٌ حَلِيمٞ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (505,4,'79',4,'An-Nisā’','النِّسَاء',11,13,13,'تِلۡكَ حُدُودُ اُ۬للَّهِۚ وَمَن يُطِعِ اِ۬للَّهَ وَرَسُولَهُۥ يُدۡخِلۡهُ جَنَّٰتٖ تَجۡرِي مِن تَحۡتِهَا اَ۬لۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ وَذَٰلِكَ اَ۬لۡفَوۡزُ اُ۬لۡعَظِيمُ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (506,4,'79',4,'An-Nisā’','النِّسَاء',14,15,14,'وَمَن يَعۡصِ اِ۬للَّهَ وَرَسُولَهُۥ وَيَتَعَدَّ حُدُودَهُۥ يُدۡخِلۡهُ نَارًا خَٰلِدٗا فِيهَا وَلَهُۥ عَذَابٞ مُّهِينٞ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (507,4,'80',4,'An-Nisā’','النِّسَاء',1,3,15,'وَاَلَّٰتِي يَأۡتِينَ اَ۬لۡفَٰحِشَةَ مِن نِّسَآئِكُمۡ فَاَسۡتَشۡهِدُواْ عَلَيۡهِنَّ أَرۡبَعَةٗ مِّنكُمۡۖ فَإِن شَهِدُواْ فَأَمۡسِكُوهُنَّ فِي اِ۬لۡبُيُوتِ حَتَّىٰ يَتَوَفَّىٰهُنَّ اَ۬لۡمَوۡتُ أَوۡ يَجۡعَلَ اَ۬للَّهُ لَهُنَّ سَبِيلٗا ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (508,4,'80',4,'An-Nisā’','النِّسَاء',4,5,16,'وَاَلَّذَانِ يَأۡتِيَٰنِهَا مِنكُمۡ فَـَٔاذُوهُمَاۖ فَإِن تَابَا وَأَصۡلَحَا فَأَعۡرِضُواْ عَنۡهُمَآۗ إِنَّ اَ۬للَّهَ كَانَ تَوَّابٗا رَّحِيمًا ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (509,4,'80',4,'An-Nisā’','النِّسَاء',6,8,17,'إِنَّمَا اَ۬لتَّوۡبَةُ عَلَى اَ۬للَّهِ لِلَّذِينَ يَعۡمَلُونَ اَ۬لسُّوٓءَ بِجَهَٰلَةٖ ثُمَّ يَتُوبُونَ مِن قَرِيبٖ فَأُوْلَٰٓئِكَ يَتُوبُ اُ۬للَّهُ عَلَيۡهِمۡۗ وَكَانَ اَ۬للَّهُ عَلِيمًا حَكِيمٗا ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (510,4,'80',4,'An-Nisā’','النِّسَاء',8,11,18,'۞ وَلَيۡسَتِ اِ۬لتَّوۡبَةُ لِلَّذِينَ يَعۡمَلُونَ اَ۬لسَّيِّـَٔاتِ حَتَّىٰٓ إِذَا حَضَرَ أَحَدَهُمُ اُ۬لۡمَوۡتُ قَالَ إِنِّي تُبۡتُ اُ۬لۡـَٰٔنَ وَلَا اَ۬لَّذِينَ يَمُوتُونَ وَهُمۡ كُفَّارٌۚ أُوْلَٰٓئِكَ أَعۡتَدۡنَا لَهُمۡ عَذَابًا أَلِيمٗا ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (511,4,'80',4,'An-Nisā’','النِّسَاء',11,15,19,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ لَا يَحِلُّ لَكُمۡ أَن تَرِثُواْ اُ۬لنِّسَآءَ كَرۡهٗاۖ وَلَا تَعۡضُلُوهُنَّ لِتَذۡهَبُواْ بِبَعۡضِ مَآ ءَاتَيۡتُمُوهُنَّ إِلَّآ أَن يَأۡتِينَ بِفَٰحِشَةٖ مُّبَيِّنَةٖۚ وَعَاشِرُوهُنَّ بِالۡمَعۡرُوفِۚ فَإِن كَرِهۡتُمُوهُنَّ فَعَسَىٰٓ أَن تَكۡرَهُواْ شَيۡـٔٗا وَيَجۡعَلَ اَ۬للَّهُ فِيهِ خَيۡرٗا كَثِيرٗا ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (512,4,'81',4,'An-Nisā’','النِّسَاء',1,3,20,'وَإِنۡ أَرَدتُّمُ اُ۪سۡتِبۡدَالَ زَوۡجٖ مَّكَانَ زَوۡجٖ وَءَاتَيۡتُمۡ إِحۡدۭىٰهُنَّ قِنطَارٗا فَلَا تَأۡخُذُواْ مِنۡهُ شَيۡـًٔاۚ أَتَأۡخُذُونَهُۥ بُهۡتَٰنٗا وَإِثۡمٗا مُّبِينٗا ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (513,4,'81',4,'An-Nisā’','النِّسَاء',3,4,21,'وَكَيۡفَ تَأۡخُذُونَهُۥ وَقَدۡ أَفۡضَىٰ بَعۡضُكُمۡ إِلَىٰ بَعۡضٖ وَأَخَذۡنَ مِنكُم مِّيثَٰقًا غَلِيظٗا ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (514,4,'81',4,'An-Nisā’','النِّسَاء',5,7,22,'وَلَا تَنكِحُواْ مَا نَكَحَ ءَابَآؤُكُم مِّنَ اَ۬لنِّسَآ إِلَّا مَا قَد سَّلَفَۚ إِنَّهُۥ كَانَ فَٰحِشَةٗ وَمَقۡتٗا وَسَآءَ سَبِيلًا ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (515,4,'81',4,'An-Nisā’','النِّسَاء',7,15,23,'حُرِّمَتۡ عَلَيۡكُمۡ أُمَّهَٰتُكُمۡ وَبَنَاتُكُمۡ وَأَخَوَٰتُكُمۡ وَعَمَّٰتُكُمۡ وَخَٰلَٰتُكُمۡ وَبَنَاتُ اُ۬لۡأَخِ وَبَنَاتُ اُ۬لۡأُخۡتِ وَأُمَّهَٰتُكُمُ اُ۬لَّٰتِيٓ أَرۡضَعۡنَكُمۡ وَأَخَوَٰتُكُم مِّنَ اَ۬لرَّضَٰعَةِ وَأُمَّهَٰتُ نِسَآئِكُمۡ وَرَبَٰٓئِبُكُمُ اُ۬لَّٰتِي فِي حُجُورِكُم مِّن نِّسَآئِكُمُ اُ۬لَّٰتِي دَخَلۡتُم بِهِنَّ فَإِن لَّمۡ تَكُونُواْ دَخَلۡتُم بِهِنَّ فَلَا جُنَاحَ عَلَيۡكُمۡ وَحَلَٰٓئِلُ أَبۡنَآئِكُمُ اُ۬لَّذِينَ مِنۡ أَصۡلَٰبِكُمۡ وَأَن تَجۡمَعُواْ بَيۡنَ اَ۬لۡأُخۡتَيۡنِ إِلَّا مَا قَد سَّلَفَۗ إِنَّ اَ۬للَّهَ كَانَ غَفُورٗا رَّحِيمٗا ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (516,5,'82',4,'An-Nisā’','النِّسَاء',1,6,24,'۞ وَاَلۡمُحۡصَنَٰتُ مِنَ اَ۬لنِّسَآ إِلَّا مَا مَلَكَتۡ أَيۡمَٰنُكُمۡۖ كِتَٰبَ اَ۬للَّهِ عَلَيۡكُمۡۚ وَأَحَلَّ لَكُم مَّا وَرَآءَ ذَٰلِكُمۡ أَن تَبۡتَغُواْ بِأَمۡوَٰلِكُم مُّحۡصِنِينَ غَيۡرَ مُسَٰفِحِينَۚ فَمَا اَ۪سۡتَمۡتَعۡتُم بِهِۦ مِنۡهُنَّ فَـَٔاتُوهُنَّ أُجُورَهُنَّ فَرِيضَةٗۚ وَلَا جُنَاحَ عَلَيۡكُمۡ فِيمَا تَرَٰضَيۡتُم بِهِۦ مِنۢ بَعۡدِ اِ۬لۡفَرِيضَةِۚ إِنَّ اَ۬للَّهَ كَانَ عَلِيمًا حَكِيمٗا ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (517,5,'82',4,'An-Nisā’','النِّسَاء',6,13,25,'وَمَن لَّمۡ يَسۡتَطِعۡ مِنكُمۡ طَوۡلًا أَن يَنكِحَ اَ۬لۡمُحۡصَنَٰتِ اِ۬لۡمُؤۡمِنَٰتِ فَمِن مَّا مَلَكَتۡ أَيۡمَٰنُكُم مِّن فَتَيَٰتِكُمُ اُ۬لۡمُؤۡمِنَٰتِۚ وَاَللَّهُ أَعۡلَمُ بِإِيمَٰنِكُمۚ بَعۡضُكُم مِّنۢ بَعۡضٖۚ فَاَنكِحُوهُنَّ بِإِذۡنِ أَهۡلِهِنَّ وَءَاتُوهُنَّ أُجُورَهُنَّ بِالۡمَعۡرُوفِ مُحۡصَنَٰتٍ غَيۡرَ مُسَٰفِحَٰتٖ وَلَا مُتَّخِذَٰتِ أَخۡدَانٖۚ فَإِذَآ أُحۡصِنَّ فَإِنۡ أَتَيۡنَ بِفَٰحِشَةٖ فَعَلَيۡهِنَّ نِصۡفُ مَا عَلَى اَ۬لۡمُحۡصَنَٰتِ مِنَ اَ۬لۡعَذَابِۚ ذَٰلِكَ لِمَنۡ خَشِيَ اَ۬لۡعَنَتَ مِنكُمۡۚ وَأَن تَصۡبِرُواْ خَيۡرٞ لَّكُمۡۗ وَاَللَّهُ غَفُورٞ رَّحِيمٞ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (518,5,'82',4,'An-Nisā’','النِّسَاء',14,15,26,'يُرِيدُ اُ۬للَّهُ لِيُبَيِّنَ لَكُمۡ وَيَهۡدِيَكُمۡ سُنَنَ اَ۬لَّذِينَ مِن قَبۡلِكُمۡ وَيَتُوبَ عَلَيۡكُمۡۗ وَاَللَّهُ عَلِيمٌ حَكِيمٞ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (519,5,'83',4,'An-Nisā’','النِّسَاء',1,2,27,'وَاَللَّهُ يُرِيدُ أَن يَتُوبَ عَلَيۡكُمۡ وَيُرِيدُ اُ۬لَّذِينَ يَتَّبِعُونَ اَ۬لشَّهَوَٰتِ أَن تَمِيلُواْ مَيۡلًا عَظِيمٗا ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (520,5,'83',4,'An-Nisā’','النِّسَاء',2,3,28,'يُرِيدُ اُ۬للَّهُ أَن يُخَفِّفَ عَنكُمۡۚ وَخُلِقَ اَ۬لۡإِنسَٰنُ ضَعِيفٗا ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (521,5,'83',4,'An-Nisā’','النِّسَاء',3,6,29,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ لَا تَأۡكُلُوٓاْ أَمۡوَٰلَكُم بَيۡنَكُم بِالۡبَٰطِلِ إِلَّآ أَن تَكُونَ تِجَٰرَةٌ عَن تَرَاضٖ مِّنكُمۡۚ وَلَا تَقۡتُلُوٓاْ أَنفُسَكُمۡۚ إِنَّ اَ۬للَّهَ كَانَ بِكُمۡ رَحِيمٗا ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (522,5,'83',4,'An-Nisā’','النِّسَاء',6,8,30,'وَمَن يَفۡعَلۡ ذَٰلِكَ عُدۡوَٰنٗا وَظُلۡمٗا فَسَوۡفَ نُصۡلِيهِ نَارٗاۚ وَكَانَ ذَٰلِكَ عَلَى اَ۬للَّهِ يَسِيرًا ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (523,5,'83',4,'An-Nisā’','النِّسَاء',8,9,31,'۞ إِن تَجۡتَنِبُواْ كَبَآئِرَ مَا تُنۡهَوۡنَ عَنۡهُ نُكَفِّرۡ عَنكُمۡ سَيِّـَٔاتِكُمۡ وَنُدۡخِلۡكُم مُّدۡخَلٗا كَرِيمٗا ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (524,5,'83',4,'An-Nisā’','النِّسَاء',10,13,32,'وَلَا تَتَمَنَّوۡاْ مَا فَضَّلَ اَ۬للَّهُ بِهِۦ بَعۡضَكُمۡ عَلَىٰ بَعۡضٖۚ لِّلرِّجَالِ نَصِيبٞ مِّمَّا اَ۪كۡتَسَبُواْۖ وَلِلنِّسَآءِ نَصِيبٞ مِّمَّا اَ۪كۡتَسَبۡنَۚ وَسۡـَٔلُواْ اُ۬للَّهَ مِن فَضۡلِهِۦٓۗ إِنَّ اَ۬للَّهَ كَانَ بِكُلِّ شَيۡءٍ عَلِيمٗا ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (525,5,'83',4,'An-Nisā’','النِّسَاء',13,15,33,'وَلِكُلّٖ جَعَلۡنَا مَوَٰلِيَ مِمَّا تَرَكَ اَ۬لۡوَٰلِدَانِ وَاَلۡأَقۡرَبُونَۚ وَاَلَّذِينَ عَٰقَدَتۡ أَيۡمَٰنُكُمۡ فَـَٔاتُوهُمۡ نَصِيبَهُمۡۚ إِنَّ اَ۬للَّهَ كَانَ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدًا ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (526,5,'84',4,'An-Nisā’','النِّسَاء',1,6,34,'اِ۬لرِّجَالُ قَوَّٰمُونَ عَلَى اَ۬لنِّسَآءِ بِمَا فَضَّلَ اَ۬للَّهُ بَعۡضَهُمۡ عَلَىٰ بَعۡضٖ وَبِمَآ أَنفَقُواْ مِنۡ أَمۡوَٰلِهِمۡۚ فَاَلصَّٰلِحَٰتُ قَٰنِتَٰتٌ حَٰفِظَٰتٞ لِّلۡغَيۡبِ بِمَا حَفِظَ اَ۬للَّهُۚ وَاَلَّٰتِي تَخَافُونَ نُشُوزَهُنَّ فَعِظُوهُنَّ وَاَهۡجُرُوهُنَّ فِي اِ۬لۡمَضَاجِعِ وَاَضۡرِبُوهُنَّۖ فَإِنۡ أَطَعۡنَكُمۡ فَلَا تَبۡغُواْ عَلَيۡهِنَّ سَبِيلًاۗ إِنَّ اَ۬للَّهَ كَانَ عَلِيّٗا كَبِيرٗا ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (527,5,'84',4,'An-Nisā’','النِّسَاء',6,9,35,'وَإِنۡ خِفۡتُمۡ شِقَاقَ بَيۡنِهِمَا فَاَبۡعَثُواْ حَكَمٗا مِّنۡ أَهۡلِهِۦ وَحَكَمٗا مِّنۡ أَهۡلِهَآ إِن يُرِيدَآ إِصۡلَٰحٗا يُوَفِّقِ اِ۬للَّهُ بَيۡنَهُمَآۗ إِنَّ اَ۬للَّهَ كَانَ عَلِيمًا خَبِيرٗا ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (528,5,'84',4,'An-Nisā’','النِّسَاء',9,13,36,'وَاَعۡبُدُواْ اُ۬للَّهَ وَلَا تُشۡرِكُواْ بِهِۦ شَيۡـٔٗاۖ وَبِالۡوَٰلِدَيۡنِ إِحۡسَٰنٗا وَبِذِي اِ۬لۡقُرۡبۭيٰ وَاَلۡيَتَٰمَىٰ وَاَلۡمَسَٰكِينِ وَاَلۡجَارِ ذِي اِ۬لۡقُرۡبۭيٰ وَاَلۡجَارِ اِ۬لۡجُنُبِ وَاَلصَّاحِبِ بِالۡجَنۢبِ وَاَبۡنِ اِ۬لسَّبِيلِ وَمَا مَلَكَتۡ أَيۡمَٰنُكُمۡۗ إِنَّ اَ۬للَّهَ لَا يُحِبُّ مَن كَانَ مُخۡتَالٗا فَخُورًا ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (529,5,'84',4,'An-Nisā’','النِّسَاء',13,15,37,'اِ۬لَّذِينَ يَبۡخَلُونَ وَيَأۡمُرُونَ اَ۬لنَّاسَ بِالۡبُخۡلِ وَيَكۡتُمُونَ مَآ ءَاتَىٰهُمُ اُ۬للَّهُ مِن فَضۡلِهِۦۗ وَأَعۡتَدۡنَا لِلۡكٰ۪فِرِينَ عَذَابٗا مُّهِينٗا ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (530,5,'85',4,'An-Nisā’','النِّسَاء',1,3,38,'وَاَلَّذِينَ يُنفِقُونَ أَمۡوَٰلَهُمۡ رِئَآءَ اَ۬لنّ۪اسِ وَلَا يُؤۡمِنُونَ بِاللَّهِ وَلَا بِالۡيَوۡمِ اِ۬لۡأٓخِرِۗ وَمَن يَكُنِ اِ۬لشَّيۡطَٰنُ لَهُۥ قَرِينٗا فَسَآءَ قَرِينٗا ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (531,5,'85',4,'An-Nisā’','النِّسَاء',3,4,39,'وَمَاذَا عَلَيۡهِمۡ لَوۡ ءَامَنُواْ بِاللَّهِ وَاَلۡيَوۡمِ اِ۬لۡأٓخِرِ وَأَنفَقُواْ مِمَّا رَزَقَهُمُ اُ۬للَّهُۚ وَكَانَ اَ۬للَّهُ بِهِمۡ عَلِيمًا ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (532,5,'85',4,'An-Nisā’','النِّسَاء',4,6,40,'۞ إِنَّ اَ۬للَّهَ لَا يَظۡلِمُ مِثۡقَالَ ذَرَّةٖۖ وَإِن تَكُ حَسَنَةٗ يُضَٰعِفۡهَا وَيُؤۡتِ مِن لَّدُنۡهُ أَجۡرًا عَظِيمٗا ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (533,5,'85',4,'An-Nisā’','النِّسَاء',6,7,41,'فَكَيۡفَ إِذَا جِئۡنَا مِن كُلِّ أُمَّةِۢ بِشَهِيدٖ وَجِئۡنَا بِكَ عَلَىٰ هَٰٓؤُلَآءِ شَهِيدٗا ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (534,5,'85',4,'An-Nisā’','النِّسَاء',7,9,42,'يَوۡمَئِذٖ يَوَدُّ اُ۬لَّذِينَ كَفَرُواْ وَعَصَوُاْ اُ۬لرَّسُولَ لَوۡ تُسَوَّىٰ بِهِمِ اِ۬لۡأَرۡضُ وَلَا يَكۡتُمُونَ اَ۬للَّهَ حَدِيثٗا ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (535,5,'85',4,'An-Nisā’','النِّسَاء',9,14,43,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ لَا تَقۡرَبُواْ اُ۬لصَّلَوٰةَ وَأَنتُمۡ سُكَٰر۪يٰ حَتَّىٰ تَعۡلَمُواْ مَا تَقُولُونَ وَلَا جُنُبًا إِلَّا عَابِرِي سَبِيلٍ حَتَّىٰ تَغۡتَسِلُواْۚ وَإِن كُنتُم مَّرۡضۭيٰٓ أَوۡ عَلَىٰ سَفَرٍ أَوۡ جَآ أَحَدٞ مِّنكُم مِّنَ اَ۬لۡغَآئِطِ أَوۡ لَٰمَسۡتُمُ اُ۬لنِّسَآءَ فَلَمۡ تَجِدُواْ مَآءٗ فَتَيَمَّمُواْ صَعِيدٗا طَيِّبٗا فَاَمۡسَحُواْ بِوُجُوهِكُمۡ وَأَيۡدِيكُمۡۗ إِنَّ اَ۬للَّهَ كَانَ عَفُوًّا غَفُورًا ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (536,5,'85-86',4,'An-Nisā’','النِّسَاء',14,1,44,'أَلَمۡ تَرَ إِلَى اَ۬لَّذِينَ أُوتُواْ نَصِيبٗا مِّنَ اَ۬لۡكِتَٰبِ يَشۡتَرُونَ اَ۬لضَّلَٰلَةَ وَيُرِيدُونَ أَن تَضِلُّواْ اُ۬لسَّبِيلَ ۚ  وَاَللَّهُ أَعۡلَمُ بِأَعۡدَآئِكُمۡۚ وَكَفَىٰ بِاللَّهِ وَلِيّٗا وَكَفَىٰ بِاللَّهِ نَصِيرٗا ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (537,5,'86',4,'An-Nisā’','النِّسَاء',2,6,45,'مِّنَ اَ۬لَّذِينَ هَادُواْ يُحَرِّفُونَ اَ۬لۡكَلِمَ عَن مَّوَاضِعِهِۦ وَيَقُولُونَ سَمِعۡنَا وَعَصَيۡنَا وَاَسۡمَعۡ غَيۡرَ مُسۡمَعٖ وَرَٰعِنَا لَيَّۢا بِأَلۡسِنَتِهِمۡ وَطَعۡنٗا فِي اِ۬لدِّينِۚ وَلَوۡ أَنَّهُمۡ قَالُواْ سَمِعۡنَا وَأَطَعۡنَا وَاَسۡمَعۡ وَاَنظُرۡنَا لَكَانَ خَيۡرٗا لَّهُمۡ وَأَقۡوَمَ وَلَٰكِن لَّعَنَهُمُ اُ۬للَّهُ بِكُفۡرِهِمۡ فَلَا يُؤۡمِنُونَ إِلَّا قَلِيلٗا ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (538,5,'86',4,'An-Nisā’','النِّسَاء',6,9,46,'يَٰٓأَيُّهَا اَ۬لَّذِينَ أُوتُواْ اُ۬لۡكِتَٰبَ ءَامِنُواْ بِمَا نَزَّلۡنَا مُصَدِّقٗا لِّمَا مَعَكُم مِّن قَبۡلِ أَن نَّطۡمِسَ وُجُوهٗا فَنَرُدَّهَا عَلَىٰٓ أَدۡب۪ارِهَآ أَوۡ نَلۡعَنَهُمۡ كَمَا لَعَنَّآ أَصۡحَٰبَ اَ۬لسَّبۡتِۚ وَكَانَ أَمۡرُ اُ۬للَّهِ مَفۡعُولًا ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (539,5,'86',4,'An-Nisā’','النِّسَاء',9,10,47,'۞ إِنَّ اَ۬للَّهَ لَا يَغۡفِرُ أَن يُشۡرَكَ بِهِۦ وَيَغۡفِرُ مَا دُونَ ذَٰلِكَ لِمَن يَشَآءُۚ وَمَن يُشۡرِكۡ بِاللَّهِ فَقَدِ اِ۪فۡتَر۪يٰٓ إِثۡمًا عَظِيمًا ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (540,5,'86',4,'An-Nisā’','النِّسَاء',11,12,48,'أَلَمۡ تَرَ إِلَى اَ۬لَّذِينَ يُزَكُّونَ أَنفُسَهُمۚ بَلِ اِ۬للَّهُ يُزَكِّي مَن يَشَآءُ وَلَا يُظۡلَمُونَ فَتِيلًا ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (541,5,'86',4,'An-Nisā’','النِّسَاء',12,13,49,'اِ۟نظُرۡ كَيۡفَ يَفۡتَرُونَ عَلَى اَ۬للَّهِ اِ۬لۡكَذِبَۖ وَكَفَىٰ بِهِۦٓ إِثۡمٗا مُّبِينًا ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (542,5,'86',4,'An-Nisā’','النِّسَاء',13,15,50,'أَلَمۡ تَرَ إِلَى اَ۬لَّذِينَ أُوتُواْ نَصِيبٗا مِّنَ اَ۬لۡكِتَٰبِ يُؤۡمِنُونَ بِالۡجِبۡتِ وَاَلطَّٰغُوتِ وَيَقُولُونَ لِلَّذِينَ كَفَرُواْ هَٰٓؤُلَآءِ اَ۬هۡدَىٰ مِنَ اَ۬لَّذِينَ ءَامَنُواْ سَبِيلًا ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (543,5,'87',4,'An-Nisā’','النِّسَاء',1,1,51,'أُوْلَٰٓئِكَ اَ۬لَّذِينَ لَعَنَهُمُ اُ۬للَّهُۖ وَمَن يَلۡعَنِ اِ۬للَّهُ فَلَن تَجِدَ لَهُۥ نَصِيرًا ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (544,5,'87',4,'An-Nisā’','النِّسَاء',2,2,52,'أَمۡ لَهُمۡ نَصِيبٞ مِّنَ اَ۬لۡمُلۡكِ فَإِذٗا لَّا يُؤۡتُونَ اَ۬لنَّاسَ نَقِيرًا ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (545,5,'87',4,'An-Nisā’','النِّسَاء',2,4,53,'أَمۡ يَحۡسُدُونَ اَ۬لنَّاسَ عَلَىٰ مَآ ءَاتَىٰهُمُ اُ۬للَّهُ مِن فَضۡلِهِۦۖ فَقَدۡ ءَاتَيۡنَآ ءَالَ إِبۡرَٰهِيمَ اَ۬لۡكِتَٰبَ وَاَلۡحِكۡمَةَ وَءَاتَيۡنَٰهُم مُّلۡكًا عَظِيمٗا ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (546,5,'87',4,'An-Nisā’','النِّسَاء',5,5,54,'فَمِنۡهُم مَّنۡ ءَامَنَ بِهِۦ وَمِنۡهُم مَّن صَدَّ عَنۡهُۚ وَكَفَىٰ بِجَهَنَّمَ سَعِيرًا ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (547,5,'87',4,'An-Nisā’','النِّسَاء',6,8,55,'إِنَّ اَ۬لَّذِينَ كَفَرُواْ بِـَٔايَٰتِنَا سَوۡفَ نُصۡلِيهِمۡ نَارٗا كُلَّمَا نَضِجَت جُّلُودُهُم بَدَّلۡنَٰهُمۡ جُلُودًا غَيۡرَهَا لِيَذُوقُواْ اُ۬لۡعَذَابَۗ إِنَّ اَ۬للَّهَ كَانَ عَزِيزًا حَكِيمٗا ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (548,5,'87',4,'An-Nisā’','النِّسَاء',8,10,56,'وَاَلَّذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ سَنُدۡخِلُهُمۡ جَنَّٰتٖ تَجۡرِي مِن تَحۡتِهَا اَ۬لۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدٗاۖ لَّهُمۡ فِيهَآ أَزۡوَٰجٞ مُّطَهَّرَةٞۖ وَنُدۡخِلُهُمۡ ظِلّٗا ظَلِيلًا ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (549,5,'87',4,'An-Nisā’','النِّسَاء',10,13,57,'۞ إِنَّ اَ۬للَّهَ يَأۡمُرۡكُمۡ أَن تُؤَدُّواْ اُ۬لۡأَمَٰنَٰتِ إِلَىٰٓ أَهۡلِهَا وَإِذَا حَكَمۡتُم بَيۡنَ اَ۬لنّ۪اسِ أَن تَحۡكُمُواْ بِالۡعَدۡلِۚ إِنَّ اَ۬للَّهَ نِع۪مَّا يَعِظُكُم بِهِۦٓۗ إِنَّ اَ۬للَّهَ كَانَ سَمِيعَۢا بَصِيرٗا ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (550,5,'87',4,'An-Nisā’','النِّسَاء',13,15,58,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُوٓاْ أَطِيعُواْ اُ۬للَّهَ وَأَطِيعُواْ اُ۬لرَّسُولَ وَأُوْلِي اِ۬لۡأَمۡرِ مِنكُمۡۖ فَإِن تَنَٰزَعۡتُمۡ فِي شَيۡءٖ فَرُدُّوهُ إِلَى اَ۬للَّهِ وَاَلرَّسُولِ إِن كُنتُمۡ تُؤۡمِنُونَ بِاللَّهِ وَاَلۡيَوۡمِ اِ۬لۡأٓخِرِۚ ذَٰلِكَ خَيۡرٞ وَأَحۡسَنُ تَأۡوِيلًا ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (551,5,'88',4,'An-Nisā’','النِّسَاء',1,4,59,'أَلَمۡ تَرَ إِلَى اَ۬لَّذِينَ يَزۡعُمُونَ أَنَّهُمۡ ءَامَنُواْ بِمَآ أُنزِلَ إِلَيۡكَ وَمَآ أُنزِلَ مِن قَبۡلِكَ يُرِيدُونَ أَن يَتَحَاكَمُوٓاْ إِلَى اَ۬لطَّٰغُوتِ وَقَدۡ أُمِرُوٓاْ أَن يَكۡفُرُواْ بِهِۦ وَيُرِيدُ اُ۬لشَّيۡطَٰنُ أَن يُضِلَّهُمۡ ضَلَٰلَۢا بَعِيدٗا ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (552,5,'88',4,'An-Nisā’','النِّسَاء',4,6,60,'وَإِذَا قِيلَ لَهُمۡ تَعَالَوۡاْ إِلَىٰ مَآ أَنزَلَ اَ۬للَّهُ وَإِلَى اَ۬لرَّسُولِ رَأَيۡتَ اَ۬لۡمُنَٰفِقِينَ يَصُدُّونَ عَنكَ صُدُودٗا ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (553,5,'88',4,'An-Nisā’','النِّسَاء',6,8,61,'فَكَيۡفَ إِذَآ أَصَٰبَتۡهُم مُّصِيبَةُۢ بِمَا قَدَّمَتۡ أَيۡدِيهِمۡ ثُمَّ جَآءُوكَ يَحۡلِفُونَ بِاللَّهِ إِنۡ أَرَدۡنَآ إِلَّآ إِحۡسَٰنٗا وَتَوۡفِيقًا ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (554,5,'88',4,'An-Nisā’','النِّسَاء',8,10,62,'أُوْلَٰٓئِكَ اَ۬لَّذِينَ يَعۡلَمُ اُ۬للَّهُ مَا فِي قُلُوبِهِمۡ فَأَعۡرِضۡ عَنۡهُمۡ وَعِظۡهُمۡ وَقُل لَّهُمۡ فِيٓ أَنفُسِهِمۡ قَوۡلَۢا بَلِيغٗا ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (555,5,'88',4,'An-Nisā’','النِّسَاء',10,13,63,'وَمَآ أَرۡسَلۡنَا مِن رَّسُولٍ إِلَّا لِيُطَاعَ بِإِذۡنِ اِ۬للَّهِۚ وَلَوۡ أَنَّهُمۡ إِذ ظَّلَمُوٓاْ أَنفُسَهُمۡ جَآءُوكَ فَاَسۡتَغۡفَرُواْ اُ۬للَّهَ وَاَسۡتَغۡفَرَ لَهُمُ اُ۬لرَّسُولُ لَوَجَدُواْ اُ۬للَّهَ تَوَّابٗا رَّحِيمٗا ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (556,5,'88',4,'An-Nisā’','النِّسَاء',13,15,64,'۞ فَلَا وَرَبِّكَ لَا يُؤۡمِنُونَ حَتَّىٰ يُحَكِّمُوكَ فِيمَا شَجَرَ بَيۡنَهُمۡ ثُمَّ لَا يَجِدُواْ فِيٓ أَنفُسِهِمۡ حَرَجٗا مِّمَّا قَضَيۡتَ وَيُسَلِّمُواْ تَسۡلِيمٗا ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (557,5,'89',4,'An-Nisā’','النِّسَاء',1,3,65,'وَلَوۡ أَنَّا كَتَبۡنَا عَلَيۡهِمۡ أَنِ اِ۟قۡتُلُوٓاْ أَنفُسَكُمۡ أَوُ اُ۟خۡرُجُواْ مِن دِيٰ۪رِكُم مَّا فَعَلُوهُ إِلَّا قَلِيلٞ مِّنۡهُمۡۖ وَلَوۡ أَنَّهُمۡ فَعَلُواْ مَا يُوعَظُونَ بِهِۦ لَكَانَ خَيۡرٗا لَّهُمۡ وَأَشَدَّ تَثۡبِيتٗا ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (558,5,'89',4,'An-Nisā’','النِّسَاء',3,4,66,'وَإِذٗا لَّأٓتَيۡنَٰهُم مِّن لَّدُنَّآ أَجۡرًا عَظِيمٗا ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (559,5,'89',4,'An-Nisā’','النِّسَاء',4,4,67,'وَلَهَدَيۡنَٰهُمۡ صِرَٰطٗا مُّسۡتَقِيمٗا ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (560,5,'89',4,'An-Nisā’','النِّسَاء',5,7,68,'وَمَن يُطِعِ اِ۬للَّهَ وَاَلرَّسُولَ فَأُوْلَٰٓئِكَ مَعَ اَ۬لَّذِينَ أَنۡعَمَ اَ۬للَّهُ عَلَيۡهِم مِّنَ اَ۬لنَّبِيِّـۧنَ وَاَلصِّدِّيقِينَ وَاَلشُّهَدَآءِ وَاَلصَّٰلِحِينَۚ وَحَسُنَ أُوْلَٰٓئِكَ رَفِيقٗا ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (561,5,'89',4,'An-Nisā’','النِّسَاء',7,8,69,'ذَٰلِكَ اَ۬لۡفَضۡلُ مِنَ اَ۬للَّهِۚ وَكَفَىٰ بِاللَّهِ عَلِيمٗا ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (562,5,'89',4,'An-Nisā’','النِّسَاء',8,9,70,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ خُذُواْ حِذۡرَكُمۡ فَاَنفِرُواْ ثُبَاتٍ أَوِ اِ۪نفِرُواْ جَمِيعٗا ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (563,5,'89',4,'An-Nisā’','النِّسَاء',9,11,71,'وَإِنَّ مِنكُمۡ لَمَن لَّيُبَطِّئَنَّ فَإِنۡ أَصَٰبَتۡكُم مُّصِيبَةٞ قَالَ قَدۡ أَنۡعَمَ اَ۬للَّهُ عَلَيَّ إِذۡ لَمۡ أَكُن مَّعَهُمۡ شَهِيدٗا ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (564,5,'89',4,'An-Nisā’','النِّسَاء',11,13,72,'وَلَئِنۡ أَصَٰبَكُمۡ فَضۡلٞ مِّنَ اَ۬للَّهِ لَيَقُولَنَّ كَأَن لَّمۡ يَكُنۢ بَيۡنَكُمۡ وَبَيۡنَهُۥ مَوَدَّةٞ يَٰلَيۡتَنِي كُنتُ مَعَهُمۡ فَأَفُوزَ فَوۡزًا عَظِيمٗا ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (565,5,'89',4,'An-Nisā’','النِّسَاء',13,15,73,'۞ فَلۡيُقَٰتِلۡ فِي سَبِيلِ اِ۬للَّهِ اِ۬لَّذِينَ يَشۡرُونَ اَ۬لۡحَيَوٰةَ اَ۬لدُّنۡيۭا بِالۡأٓخِرَةِۚ وَمَن يُقَٰتِلۡ فِي سَبِيلِ اِ۬للَّهِ فَيُقۡتَلۡ أَوۡ يَغۡلِب فَّسَوۡفَ نُؤۡتِيهِ أَجۡرًا عَظِيمٗا ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (566,5,'90',4,'An-Nisā’','النِّسَاء',1,3,74,'وَمَا لَكُمۡ لَا تُقَٰتِلُونَ فِي سَبِيلِ اِ۬للَّهِ وَاَلۡمُسۡتَضۡعَفِينَ مِنَ اَ۬لرِّجَالِ وَاَلنِّسَآءِ وَاَلۡوِلۡدَٰنِ اِ۬لَّذِينَ يَقُولُونَ رَبَّنَآ أَخۡرِجۡنَا مِنۡ هَٰذِهِ اِ۬لۡقَرۡيَةِ اِ۬لظَّالِمِ أَهۡلُهَا وَاَجۡعَل لَّنَا مِن لَّدُنكَ وَلِيّٗا وَاَجۡعَل لَّنَا مِن لَّدُنكَ نَصِيرًا ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (567,5,'90',4,'An-Nisā’','النِّسَاء',4,6,75,'اِ۬لَّذِينَ ءَامَنُواْ يُقَٰتِلُونَ فِي سَبِيلِ اِ۬للَّهِۖ وَاَلَّذِينَ كَفَرُواْ يُقَٰتِلُونَ فِي سَبِيلِ اِ۬لطَّٰغُوتِ فَقَٰتِلُوٓاْ أَوۡلِيَآءَ اَ۬لشَّيۡطَٰنِۖ إِنَّ كَيۡدَ اَ۬لشَّيۡطَٰنِ كَانَ ضَعِيفًا ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (568,5,'90',4,'An-Nisā’','النِّسَاء',6,10,76,'أَلَمۡ تَرَ إِلَى اَ۬لَّذِينَ قِيلَ لَهُمۡ كُفُّوٓاْ أَيۡدِيَكُمۡ وَأَقِيمُواْ اُ۬لصَّلَوٰةَ وَءَاتُواْ اُ۬لزَّكَوٰةَ فَلَمَّا كُتِبَ عَلَيۡهِمِ اِ۬لۡقِتَالُ إِذَا فَرِيقٞ مِّنۡهُمۡ يَخۡشَوۡنَ اَ۬لنَّاسَ كَخَشۡيَةِ اِ۬للَّهِ أَوۡ أَشَدَّ خَشۡيَةٗۚ وَقَالُواْ رَبَّنَا لِمَ كَتَبۡتَ عَلَيۡنَا اَ۬لۡقِتَالَ لَوۡلَآ أَخَّرۡتَنَآ إِلَىٰٓ أَجَلٖ قَرِيبٖۗ قُلۡ مَتَٰعُ اُ۬لدُّنۡيۭا قَلِيلٞ وَاَلۡأٓخِرَةُ خَيۡرٞ لِّمَنِ اِ۪تَّقَىٰ وَلَا تُظۡلَمُونَ فَتِيلًا ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (569,5,'90',4,'An-Nisā’','النِّسَاء',10,14,77,'أَيۡنَمَا تَكُونُواْ يُدۡرِككُّمُ اُ۬لۡمَوۡتُ وَلَوۡ كُنتُمۡ فِي بُرُوجٖ مُّشَيَّدَةٖۗ وَإِن تُصِبۡهُمۡ حَسَنَةٞ يَقُولُواْ هَٰذِهِۦ مِنۡ عِندِ اِ۬للَّهِۖ وَإِن تُصِبۡهُمۡ سَيِّئَةٞ يَقُولُواْ هَٰذِهِۦ مِنۡ عِندِكَۚ قُلۡ كُلّٞ مِّنۡ عِندِ اِ۬للَّهِۖ فَمَالِ هَٰٓؤُلَآءِ اِ۬لۡقَوۡمِ لَا يَكَادُونَ يَفۡقَهُونَ حَدِيثٗا ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (570,5,'90',4,'An-Nisā’','النِّسَاء',14,15,78,'۞ مَّآ أَصَابَكَ مِنۡ حَسَنَةٖ فَمِنَ اَ۬للَّهِۖ وَمَآ أَصَابَكَ مِن سَيِّئَةٖ فَمِن نَّفۡسِكَۚ وَأَرۡسَلۡنَٰكَ لِلنّ۪اسِ رَسُولٗاۚ وَكَفَىٰ بِاللَّهِ شَهِيدٗا ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (571,5,'91',4,'An-Nisā’','النِّسَاء',1,2,79,'مَّن يُطِعِ اِ۬لرَّسُولَ فَقَدۡ أَطَاعَ اَ۬للَّهَۖ وَمَن تَوَلَّىٰ فَمَآ أَرۡسَلۡنَٰكَ عَلَيۡهِمۡ حَفِيظٗا ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (572,5,'91',4,'An-Nisā’','النِّسَاء',2,4,80,'وَيَقُولُونَ طَاعَةٞ فَإِذَا بَرَزُواْ مِنۡ عِندِكَ بَيَّت طَّآئِفَةٞ مِّنۡهُمۡ غَيۡرَ اَ۬لَّذِي تَقُولُۖ وَاَللَّهُ يَكۡتُبُ مَا يُبَيِّتُونَۖ فَأَعۡرِضۡ عَنۡهُمۡ وَتَوَكَّلۡ عَلَى اَ۬للَّهِۚ وَكَفَىٰ بِاللَّهِ وَكِيلًا ٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (573,5,'91',4,'An-Nisā’','النِّسَاء',5,6,81,'أَفَلَا يَتَدَبَّرُونَ اَ۬لۡقُرۡءَانَۚ وَلَوۡ كَانَ مِنۡ عِندِ غَيۡرِ اِ۬للَّهِ لَوَجَدُواْ فِيهِ اِ۪خۡتِلَٰفٗا كَثِيرٗا ٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (574,5,'91',4,'An-Nisā’','النِّسَاء',6,9,82,'وَإِذَا جَآءَهُمۡ أَمۡرٞ مِّنَ اَ۬لۡأَمۡنِ أَوِ اِ۬لۡخَوۡفِ أَذَاعُواْ بِهِۦۖ وَلَوۡ رَدُّوهُ إِلَى اَ۬لرَّسُولِ وَإِلَىٰٓ أُوْلِي اِ۬لۡأَمۡرِ مِنۡهُمۡ لَعَلِمَهُ اُ۬لَّذِينَ يَسۡتَنۢبِطُونَهُۥ مِنۡهُمۡۗ وَلَوۡلَا فَضۡلُ اُ۬للَّهِ عَلَيۡكُمۡ وَرَحۡمَتُهُۥ لَاَتَّبَعۡتُمُ اُ۬لشَّيۡطَٰنَ إِلَّا قَلِيلٗا ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (575,5,'91',4,'An-Nisā’','النِّسَاء',10,12,83,'فَقَٰتِلۡ فِي سَبِيلِ اِ۬للَّهِ لَا تُكَلَّفُ إِلَّا نَفۡسَكَۚ وَحَرِّضِ اِ۬لۡمُؤۡمِنِينَۖ عَسَى اَ۬للَّهُ أَن يَكُفَّ بَأۡسَ اَ۬لَّذِينَ كَفَرُواْۚ وَاَللَّهُ أَشَدُّ بَأۡسٗا وَأَشَدُّ تَنكِيلٗا ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (576,5,'91',4,'An-Nisā’','النِّسَاء',12,14,84,'مَّن يَشۡفَعۡ شَفَٰعَةً حَسَنَةٗ يَكُن لَّهُۥ نَصِيبٞ مِّنۡهَاۖ وَمَن يَشۡفَعۡ شَفَٰعَةٗ سَيِّئَةٗ يَكُن لَّهُۥ كِفۡلٞ مِّنۡهَاۗ وَكَانَ اَ۬للَّهُ عَلَىٰ كُلِّ شَيۡءٖ مُّقِيتٗا ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (577,5,'91',4,'An-Nisā’','النِّسَاء',14,15,85,'وَإِذَا حُيِّيتُم بِتَحِيَّةٖ فَحَيُّواْ بِأَحۡسَنَ مِنۡهَآ أَوۡ رُدُّوهَآۗ إِنَّ اَ۬للَّهَ كَانَ عَلَىٰ كُلِّ شَيۡءٍ حَسِيبًا ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (578,5,'92',4,'An-Nisā’','النِّسَاء',1,2,86,'اِ۬للَّهُ لَآ إِلَٰهَ إِلَّا هُوَۚ لَيَجۡمَعَنَّكُمۡ إِلَىٰ يَوۡمِ اِ۬لۡقِيَٰمَةِ لَا رَيۡبَ فِيهِۗ وَمَنۡ أَصۡدَقُ مِنَ اَ۬للَّهِ حَدِيثٗا ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (579,5,'92',4,'An-Nisā’','النِّسَاء',2,4,87,'۞ فَمَا لَكُمۡ فِي اِ۬لۡمُنَٰفِقِينَ فِئَتَيۡنِ وَاَللَّهُ أَرۡكَسَهُم بِمَا كَسَبُوٓاْۚ أَتُرِيدُونَ أَن تَهۡدُواْ مَنۡ أَضَلَّ اَ۬للَّهُۖ وَمَن يُضۡلِلِ اِ۬للَّهُ فَلَن تَجِدَ لَهُۥ سَبِيلٗا ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (580,5,'92',4,'An-Nisā’','النِّسَاء',4,7,88,'وَدُّواْ لَوۡ تَكۡفُرُونَ كَمَا كَفَرُواْ فَتَكُونُونَ سَوَآءٗۖ فَلَا تَتَّخِذُواْ مِنۡهُمۡ أَوۡلِيَآءَ حَتَّىٰ يُهَاجِرُواْ فِي سَبِيلِ اِ۬للَّهِۚ فَإِن تَوَلَّوۡاْ فَخُذُوهُمۡ وَاَقۡتُلُوهُمۡ حَيۡثُ وَجَدتُّمُوهُمۡۖ وَلَا تَتَّخِذُواْ مِنۡهُمۡ وَلِيّٗا وَلَا نَصِيرًا ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (581,5,'92',4,'An-Nisā’','النِّسَاء',7,11,89,'إِلَّا اَ۬لَّذِينَ يَصِلُونَ إِلَىٰ قَوۡمِۢ بَيۡنَكُمۡ وَبَيۡنَهُم مِّيثَٰقٌ أَوۡ جَآءُوكُمۡ حَصِرَت صُّدُورُهُمۡ أَن يُقَٰتِلُوكُمۡ أَوۡ يُقَٰتِلُواْ قَوۡمَهُمۡۚ وَلَوۡ شَآءَ اَ۬للَّهُ لَسَلَّطَهُمۡ عَلَيۡكُمۡ فَلَقَٰتَلُوكُمۡۚ فَإِنِ اِ۪عۡتَزَلُوكُمۡ فَلَمۡ يُقَٰتِلُوكُمۡ وَأَلۡقَوۡاْ إِلَيۡكُمُ اُ۬لسَّلَمَ فَمَا جَعَلَ اَ۬للَّهُ لَكُمۡ عَلَيۡهِمۡ سَبِيلٗا ٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (582,5,'92',4,'An-Nisā’','النِّسَاء',12,15,90,'سَتَجِدُونَ ءَاخَرِينَ يُرِيدُونَ أَن يَأۡمَنُوكُمۡ وَيَأۡمَنُواْ قَوۡمَهُمۡ كُلَّ مَا رُدُّوٓاْ إِلَى اَ۬لۡفِتۡنَةِ أُرۡكِسُواْ فِيهَاۚ فَإِن لَّمۡ يَعۡتَزِلُوكُمۡ وَيُلۡقُوٓاْ إِلَيۡكُمُ اُ۬لسَّلَمَ وَيَكُفُّوٓاْ أَيۡدِيَهُمۡ فَخُذُوهُمۡ وَاَقۡتُلُوهُمۡ حَيۡثُ ثَقِفۡتُمُوهُمۡۚ وَأُوْلَٰٓئِكُمۡ جَعَلۡنَا لَكُمۡ عَلَيۡهِمۡ سُلۡطَٰنٗا مُّبِينٗا ٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (583,5,'93',4,'An-Nisā’','النِّسَاء',1,8,91,'وَمَا كَانَ لِمُؤۡمِنٍ أَن يَقۡتُلَ مُؤۡمِنًا إِلَّا خَطَـٔٗاۚ وَمَن قَتَلَ مُؤۡمِنًا خَطَـٔٗا فَتَحۡرِيرُ رَقَبَةٖ مُّؤۡمِنَةٖ وَدِيَةٞ مُّسَلَّمَةٌ إِلَىٰٓ أَهۡلِهِۦٓ إِلَّآ أَن يَصَّدَّقُواْۚ فَإِن كَانَ مِن قَوۡمٍ عَدُوّٖ لَّكُمۡ وَهۡوَ مُؤۡمِنٞ فَتَحۡرِيرُ رَقَبَةٖ مُّؤۡمِنَةٖۖ وَإِن كَانَ مِن قَوۡمِۢ بَيۡنَكُمۡ وَبَيۡنَهُم مِّيثَٰقٞ فَدِيَةٞ مُّسَلَّمَةٌ إِلَىٰٓ أَهۡلِهِۦ وَتَحۡرِيرُ رَقَبَةٖ مُّؤۡمِنَةٖۖ فَمَن لَّمۡ يَجِدۡ فَصِيَامُ شَهۡرَيۡنِ مُتَتَابِعَيۡنِ تَوۡبَةٗ مِّنَ اَ۬للَّهِۗ وَكَانَ اَ۬للَّهُ عَلِيمًا حَكِيمٗا ٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (584,5,'93',4,'An-Nisā’','النِّسَاء',8,10,92,'۞ وَمَن يَقۡتُلۡ مُؤۡمِنٗا مُّتَعَمِّدٗا فَجَزَآؤُهُۥ جَهَنَّمُ خَٰلِدٗا فِيهَا وَغَضِبَ اَ۬للَّهُ عَلَيۡهِ وَلَعَنَهُۥ وَأَعَدَّ لَهُۥ عَذَابًا عَظِيمٗا ٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (585,5,'93',4,'An-Nisā’','النِّسَاء',10,15,93,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُوٓاْ إِذَا ضَرَبۡتُمۡ فِي سَبِيلِ اِ۬للَّهِ فَتَبَيَّنُواْ وَلَا تَقُولُواْ لِمَنۡ أَلۡقَىٰٓ إِلَيۡكُمُ اُ۬لسَّلَٰمَ لَسۡتَ مُؤۡمِنٗا تَبۡتَغُونَ عَرَضَ اَ۬لۡحَيَوٰةِ اِ۬لدُّنۡيۭا فَعِندَ اَ۬للَّهِ مَغَانِمُ كَثِيرَةٞۚ كَذَٰلِكَ كُنتُم مِّن قَبۡلُ فَمَنَّ اَ۬للَّهُ عَلَيۡكُمۡ فَتَبَيَّنُوٓاْۚ إِنَّ اَ۬للَّهَ كَانَ بِمَا تَعۡمَلُونَ خَبِيرٗا ٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (586,5,'94',4,'An-Nisā’','النِّسَاء',1,4,94,'لَّا يَسۡتَوِي اِ۬لۡقَٰعِدُونَ مِنَ اَ۬لۡمُؤۡمِنِينَ غَيۡرُ أُوْلِي اِ۬لضَّرَرِ وَاَلۡمُجَٰهِدُونَ فِي سَبِيلِ اِ۬للَّهِ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡۚ فَضَّلَ اَ۬للَّهُ اُ۬لۡمُجَٰهِدِينَ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡ عَلَى اَ۬لۡقَٰعِدِينَ دَرَجَةٗۚ وَكُلّٗا وَعَدَ اَ۬للَّهُ اُ۬لۡحُسۡنۭيٰۚ وَفَضَّلَ اَ۬للَّهُ اُ۬لۡمُجَٰهِدِينَ عَلَى اَ۬لۡقَٰعِدِينَ أَجۡرًا عَظِيمٗا ٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (587,5,'94',4,'An-Nisā’','النِّسَاء',4,5,95,'دَرَجَٰتٖ مِّنۡهُ وَمَغۡفِرَةٗ وَرَحۡمَةٗۚ وَكَانَ اَ۬للَّهُ غَفُورٗا رَّحِيمًا ٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (588,5,'94',4,'An-Nisā’','النِّسَاء',5,8,96,'إِنَّ اَ۬لَّذِينَ تَوَفَّىٰهُمُ اُ۬لۡمَلَٰٓئِكَةُ ظَالِمِيٓ أَنفُسِهِمۡ قَالُواْ فِيمَ كُنتُمۡۖ قَالُواْ كُنَّا مُسۡتَضۡعَفِينَ فِي اِ۬لۡأَرۡضِۚ قَالُوٓاْ أَلَمۡ تَكُنۡ أَرۡضُ اُ۬للَّهِ وَٰسِعَةٗ فَتُهَاجِرُواْ فِيهَاۚ فَأُوْلَٰٓئِكَ مَأۡوَىٰهُمۡ جَهَنَّمُۖ وَسَآءَتۡ مَصِيرًا ٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (589,5,'94',4,'An-Nisā’','النِّسَاء',8,9,97,'إِلَّا اَ۬لۡمُسۡتَضۡعَفِينَ مِنَ اَ۬لرِّجَالِ وَاَلنِّسَآءِ وَاَلۡوِلۡدَٰنِ لَا يَسۡتَطِيعُونَ حِيلَةٗ وَلَا يَهۡتَدُونَ سَبِيلٗا ٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (590,5,'94',4,'An-Nisā’','النِّسَاء',10,10,98,'فَأُوْلَٰٓئِكَ عَسَى اَ۬للَّهُ أَن يَعۡفُوَ عَنۡهُمۡۚ وَكَانَ اَ۬للَّهُ عَفُوًّا غَفُورٗا ٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (591,5,'94',4,'An-Nisā’','النِّسَاء',10,13,99,'۞ وَمَن يُهَاجِرۡ فِي سَبِيلِ اِ۬للَّهِ يَجِدۡ فِي اِ۬لۡأَرۡضِ مُرَٰغَمٗا كَثِيرٗا وَسَعَةٗۚ وَمَن يَخۡرُجۡ مِنۢ بَيۡتِهِۦ مُهَاجِرًا إِلَى اَ۬للَّهِ وَرَسُولِهِۦ ثُمَّ يُدۡرِكۡهُ اُ۬لۡمَوۡتُ فَقَدۡ وَقَعَ أَجۡرُهُۥ عَلَى اَ۬للَّهِۗ وَكَانَ اَ۬للَّهُ غَفُورٗا رَّحِيمٗا ٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (592,5,'94',4,'An-Nisā’','النِّسَاء',13,15,100,'وَإِذَا ضَرَبۡتُمۡ فِي اِ۬لۡأَرۡضِ فَلَيۡسَ عَلَيۡكُمۡ جُنَاحٌ أَن تَقۡصُرُواْ مِنَ اَ۬لصَّلَوٰةِ إِنۡ خِفۡتُمۡ أَن يَفۡتِنَكُمُ اُ۬لَّذِينَ كَفَرُوٓاْۚ إِنَّ اَ۬لۡكٰ۪فِرِينَ كَانُواْ لَكُمۡ عَدُوّٗا مُّبِينٗا ١٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (593,5,'95',4,'An-Nisā’','النِّسَاء',1,8,101,'وَإِذَا كُنتَ فِيهِمۡ فَأَقَمۡتَ لَهُمُ اُ۬لصَّلَوٰةَ فَلۡتَقُمۡ طَآئِفَةٞ مِّنۡهُم مَّعَكَ وَلۡيَأۡخُذُوٓاْ أَسۡلِحَتَهُمۡ فَإِذَا سَجَدُواْ فَلۡيَكُونُواْ مِن وَرَآئِكُمۡ وَلۡتَأۡتِ طَآئِفَةٌ أُخۡر۪يٰ لَمۡ يُصَلُّواْ فَلۡيُصَلُّواْ مَعَكَ وَلۡيَأۡخُذُواْ حِذۡرَهُمۡ وَأَسۡلِحَتَهُمۡۗ وَدَّ اَ۬لَّذِينَ كَفَرُواْ لَوۡ تَغۡفُلُونَ عَنۡ أَسۡلِحَتِكُمۡ وَأَمۡتِعَتِكُمۡ فَيَمِيلُونَ عَلَيۡكُم مَّيۡلَةٗ وَٰحِدَةٗۚ وَلَا جُنَاحَ عَلَيۡكُمۡ إِن كَانَ بِكُمۡ أَذٗى مِّن مَّطَرٍ أَوۡ كُنتُم مَّرۡضۭيٰٓ أَن تَضَعُوٓاْ أَسۡلِحَتَكُمۡۖ وَخُذُواْ حِذۡرَكُمۡۗ إِنَّ اَ۬للَّهَ أَعَدَّ لِلۡكٰ۪فِرِينَ عَذَابٗا مُّهِينٗا ١٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (594,5,'95',4,'An-Nisā’','النِّسَاء',9,11,102,'فَإِذَا قَضَيۡتُمُ اُ۬لصَّلَوٰةَ فَاَذۡكُرُواْ اُ۬للَّهَ قِيَٰمٗا وَقُعُودٗا وَعَلَىٰ جُنُوبِكُمۡۚ فَإِذَا اَ۪طۡمَأۡنَنتُمۡ فَأَقِيمُواْ اُ۬لصَّلَوٰةَۚ إِنَّ اَ۬لصَّلَوٰةَ كَانَتۡ عَلَى اَ۬لۡمُؤۡمِنِينَ كِتَٰبٗا مَّوۡقُوتٗا ١٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (595,5,'95',4,'An-Nisā’','النِّسَاء',11,14,103,'وَلَا تَهِنُواْ فِي اِ۪بۡتِغَآءِ اِ۬لۡقَوۡمِۖ إِن تَكُونُواْ تَأۡلَمُونَ فَإِنَّهُمۡ يَأۡلَمُونَ كَمَا تَأۡلَمُونَۖ وَتَرۡجُونَ مِنَ اَ۬للَّهِ مَا لَا يَرۡجُونَۗ وَكَانَ اَ۬للَّهُ عَلِيمًا حَكِيمًا ١٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (596,5,'95',4,'An-Nisā’','النِّسَاء',14,15,104,'۞ إِنَّآ أَنزَلۡنَآ إِلَيۡكَ اَ۬لۡكِتَٰبَ بِالۡحَقِّ لِتَحۡكُمَ بَيۡنَ اَ۬لنّ۪اسِ بِمَآ أَر۪ىٰكَ اَ۬للَّهُۚ وَلَا تَكُن لِّلۡخَآئِنِينَ خَصِيمٗا ١٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (597,5,'96',4,'An-Nisā’','النِّسَاء',1,1,105,'وَاَسۡتَغۡفِرِ اِ۬للَّهَۖ إِنَّ اَ۬للَّهَ كَانَ غَفُورٗا رَّحِيمٗا ١٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (598,5,'96',4,'An-Nisā’','النِّسَاء',1,3,106,'وَلَا تُجَٰدِلۡ عَنِ اِ۬لَّذِينَ يَخۡتَانُونَ أَنفُسَهُمۡۚ إِنَّ اَ۬للَّهَ لَا يُحِبُّ مَن كَانَ خَوَّانًا أَثِيمٗا ١٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (599,5,'96',4,'An-Nisā’','النِّسَاء',3,5,107,'يَسۡتَخۡفُونَ مِنَ اَ۬لنّ۪اسِ وَلَا يَسۡتَخۡفُونَ مِنَ اَ۬للَّهِ وَهۡوَ مَعَهُمۡ إِذۡ يُبَيِّتُونَ مَا لَا يَرۡضَىٰ مِنَ اَ۬لۡقَوۡلِۚ وَكَانَ اَ۬للَّهُ بِمَا يَعۡمَلُونَ مُحِيطًا ١٠٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (600,5,'96',4,'An-Nisā’','النِّسَاء',5,7,108,'هَٰٓا۬نتُمۡ هَٰٓؤُلَآءِ جَٰدَلۡتُمۡ عَنۡهُمۡ فِي اِ۬لۡحَيَوٰةِ اِ۬لدُّنۡيۭا فَمَن يُجَٰدِلُ اُ۬للَّهَ عَنۡهُمۡ يَوۡمَ اَ۬لۡقِيَٰمَةِ أَم مَّن يَكُونُ عَلَيۡهِمۡ وَكِيلٗا ١٠٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (601,5,'96',4,'An-Nisā’','النِّسَاء',7,9,109,'وَمَن يَعۡمَلۡ سُوٓءًا أَوۡ يَظۡلِمۡ نَفۡسَهُۥ ثُمَّ يَسۡتَغۡفِرِ اِ۬للَّهَ يَجِدِ اِ۬للَّهَ غَفُورٗا رَّحِيمٗا ١٠٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (602,5,'96',4,'An-Nisā’','النِّسَاء',9,10,110,'وَمَن يَكۡسِبۡ إِثۡمٗا فَإِنَّمَا يَكۡسِبُهُۥ عَلَىٰ نَفۡسِهِۦۚ وَكَانَ اَ۬للَّهُ عَلِيمًا حَكِيمٗا ١١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (603,5,'96',4,'An-Nisā’','النِّسَاء',10,11,111,'وَمَن يَكۡسِبۡ خَطِيٓـَٔةً أَوۡ إِثۡمٗا ثُمَّ يَرۡمِ بِهِۦ بَرِيٓـٔٗا فَقَدِ اِ۪حۡتَمَلَ بُهۡتَٰنٗا وَإِثۡمٗا مُّبِينٗا ١١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (604,5,'96',4,'An-Nisā’','النِّسَاء',12,15,112,'وَلَوۡلَا فَضۡلُ اُ۬للَّهِ عَلَيۡكَ وَرَحۡمَتُهُۥ لَهَمَّت طَّآئِفَةٞ مِّنۡهُمۡ أَن يُضِلُّوكَ وَمَا يُضِلُّونَ إِلَّآ أَنفُسَهُمۡۖ وَمَا يَضُرُّونَكَ مِن شَيۡءٖۚ وَأَنزَلَ اَ۬للَّهُ عَلَيۡكَ اَ۬لۡكِتَٰبَ وَاَلۡحِكۡمَةَ وَعَلَّمَكَ مَا لَمۡ تَكُن تَعۡلَمُۚ وَكَانَ فَضۡلُ اُ۬للَّهِ عَلَيۡكَ عَظِيمٗا ١١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (605,5,'97',4,'An-Nisā’','النِّسَاء',1,3,113,'۞ لَّا خَيۡرَ فِي كَثِيرٖ مِّن نَّجۡوۭىٰهُمۡ إِلَّا مَنۡ أَمَرَ بِصَدَقَةٍ أَوۡ مَعۡرُوفٍ أَوۡ إِصۡلَٰحِۢ بَيۡنَ اَ۬لنّ۪اسِۚ وَمَن يَفۡعَلۡ ذَٰلِكَ اَ۪بۡتِغَآءَ مَرۡضَاتِ اِ۬للَّهِ فَسَوۡفَ يُؤۡتِيهِ أَجۡرًا عَظِيمٗا ١١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (606,5,'97',4,'An-Nisā’','النِّسَاء',3,6,114,'وَمَن يُشَاقِقِ اِ۬لرَّسُولَ مِنۢ بَعۡدِ مَا تَبَيَّنَ لَهُ اُ۬لۡهُدَىٰ وَيَتَّبِعۡ غَيۡرَ سَبِيلِ اِ۬لۡمُؤۡمِنِينَ نُوَلِّهۡ مَا تَوَلَّىٰ وَنُصۡلِهۡ جَهَنَّمَۖ وَسَآءَتۡ مَصِيرًا ١١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (607,5,'97',4,'An-Nisā’','النِّسَاء',6,8,115,'إِنَّ اَ۬للَّهَ لَا يَغۡفِرُ أَن يُشۡرَكَ بِهِۦ وَيَغۡفِرُ مَا دُونَ ذَٰلِكَ لِمَن يَشَآءُۚ وَمَن يُشۡرِكۡ بِاللَّهِ فَقَد ضَّلَّ ضَلَٰلَۢا بَعِيدًا ١١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (608,5,'97',4,'An-Nisā’','النِّسَاء',8,9,116,'إِن يَدۡعُونَ مِن دُونِهِۦٓ إِلَّآ إِنَٰثٗا وَإِن يَدۡعُونَ إِلَّا شَيۡطَٰنٗا مَّرِيدٗا ١١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (609,5,'97',4,'An-Nisā’','النِّسَاء',9,10,117,'لَّعَنَهُ اُ۬للَّهُۘ وَقَالَ لَأَتَّخِذَنَّ مِنۡ عِبَادِكَ نَصِيبٗا مَّفۡرُوضٗا ١١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (610,5,'97',4,'An-Nisā’','النِّسَاء',10,13,118,'وَلَأُضِلَّنَّهُمۡ وَلَأُمَنِّيَنَّهُمۡ وَلَأٓمُرَنَّهُمۡ فَلَيُبَتِّكُنَّ ءَاذَانَ اَ۬لۡأَنۡعَٰمِ وَلَأٓمُرَنَّهُمۡ فَلَيُغَيِّرُنَّ خَلۡقَ اَ۬للَّهِۚ وَمَن يَتَّخِذِ اِ۬لشَّيۡطَٰنَ وَلِيّٗا مِّن دُونِ اِ۬للَّهِ فَقَدۡ خَسِرَ خُسۡرَانٗا مُّبِينٗا ١١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (611,5,'97',4,'An-Nisā’','النِّسَاء',13,14,119,'يَعِدُهُمۡ وَيُمَنِّيهِمۡۖ وَمَا يَعِدُهُمُ اُ۬لشَّيۡطَٰنُ إِلَّا غُرُورًا ١١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (612,5,'97',4,'An-Nisā’','النِّسَاء',14,15,120,'أُوْلَٰٓئِكَ مَأۡوَىٰهُمۡ جَهَنَّمُ وَلَا يَجِدُونَ عَنۡهَا مَحِيصٗا ١٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (613,5,'98',4,'An-Nisā’','النِّسَاء',1,3,121,'وَاَلَّذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ سَنُدۡخِلُهُمۡ جَنَّٰتٖ تَجۡرِي مِن تَحۡتِهَا اَ۬لۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدٗاۖ وَعۡدَ اَ۬للَّهِ حَقّٗاۚ وَمَنۡ أَصۡدَقُ مِنَ اَ۬للَّهِ قِيلٗا ١٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (614,5,'98',4,'An-Nisā’','النِّسَاء',3,5,122,'۞ لَّيۡسَ بِأَمَانِيِّكُمۡ وَلَآ أَمَانِيِّ أَهۡلِ اِ۬لۡكِتَٰبِۗ مَن يَعۡمَلۡ سُوٓءٗا يُجۡزَ بِهِۦ وَلَا يَجِدۡ لَهُۥ مِن دُونِ اِ۬للَّهِ وَلِيّٗا وَلَا نَصِيرٗا ١٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (615,5,'98',4,'An-Nisā’','النِّسَاء',5,7,123,'وَمَن يَعۡمَلۡ مِنَ اَ۬لصَّٰلِحَٰتِ مِن ذَكَرٍ أَوۡ أُنثۭيٰ وَهۡوَ مُؤۡمِنٞ فَأُوْلَٰٓئِكَ يُدۡخَلُونَ اَ۬لۡجَنَّةَ وَلَا يُظۡلَمُونَ نَقِيرٗا ١٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (616,5,'98',4,'An-Nisā’','النِّسَاء',7,9,124,'وَمَنۡ أَحۡسَنُ دِينٗا مِّمَّنۡ أَسۡلَمَ وَجۡهَهُۥ لِلَّهِ وَهۡوَ مُحۡسِنٞ وَاَتَّبَعَ مِلَّةَ إِبۡرَٰهِيمَ حَنِيفٗاۗ وَاَتَّخَذَ اَ۬للَّهُ إِبۡرَٰهِيمَ خَلِيلٗا ١٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (617,5,'98',4,'An-Nisā’','النِّسَاء',9,11,125,'وَلِلَّهِ مَا فِي اِ۬لسَّمَٰوَٰتِ وَمَا فِي اِ۬لۡأَرۡضِۚ وَكَانَ اَ۬للَّهُ بِكُلِّ شَيۡءٖ مُّحِيطٗا ١٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (618,5,'98',4,'An-Nisā’','النِّسَاء',11,15,126,'وَيَسۡتَفۡتُونَكَ فِي اِ۬لنِّسَآءِۖ قُلِ اِ۬للَّهُ يُفۡتِيكُمۡ فِيهِنَّ وَمَا يُتۡلَىٰ عَلَيۡكُمۡ فِي اِ۬لۡكِتَٰبِ فِي يَتَٰمَى اَ۬لنِّسَآءِ اِ۬لَّٰتِي لَا تُؤۡتُونَهُنَّ مَا كُتِبَ لَهُنَّ وَتَرۡغَبُونَ أَن تَنكِحُوهُنَّ وَاَلۡمُسۡتَضۡعَفِينَ مِنَ اَ۬لۡوِلۡدَٰنِ وَأَن تَقُومُواْ لِلۡيَتَٰمَىٰ بِالۡقِسۡطِۚ وَمَا تَفۡعَلُواْ مِنۡ خَيۡرٖ فَإِنَّ اَ۬للَّهَ كَانَ بِهِۦ عَلِيمٗا ١٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (619,5,'99',4,'An-Nisā’','النِّسَاء',1,4,127,'وَإِنِ اِ۪مۡرَأَةٌ خَافَتۡ مِنۢ بَعۡلِهَا نُشُوزًا أَوۡ إِعۡرَاضٗا فَلَا جُنَاحَ عَلَيۡهِمَآ أَن يَصَّٰلَحَا بَيۡنَهُمَا صُلۡحٗاۚ وَاَلصُّلۡحُ خَيۡرٞۗ وَأُحۡضِرَتِ اِ۬لۡأَنفُسُ اُ۬لشُّحَّۚ وَإِن تُحۡسِنُواْ وَتَتَّقُواْ فَإِنَّ اَ۬للَّهَ كَانَ بِمَا تَعۡمَلُونَ خَبِيرٗا ١٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (620,5,'99',4,'An-Nisā’','النِّسَاء',4,7,128,'وَلَن تَسۡتَطِيعُوٓاْ أَن تَعۡدِلُواْ بَيۡنَ اَ۬لنِّسَآءِ وَلَوۡ حَرَصۡتُمۡۖ فَلَا تَمِيلُواْ كُلَّ اَ۬لۡمَيۡلِ فَتَذَرُوهَا كَاَلۡمُعَلَّقَةِۚ وَإِن تُصۡلِحُواْ وَتَتَّقُواْ فَإِنَّ اَ۬للَّهَ كَانَ غَفُورٗا رَّحِيمٗا ١٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (621,5,'99',4,'An-Nisā’','النِّسَاء',7,8,129,'۞ وَإِن يَتَفَرَّقَا يُغۡنِ اِ۬للَّهُ كُلّٗا مِّن سَعَتِهِۦۚ وَكَانَ اَ۬للَّهُ وَٰسِعًا حَكِيمٗا ١٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (622,5,'99',4,'An-Nisā’','النِّسَاء',8,11,130,'وَلِلَّهِ مَا فِي اِ۬لسَّمَٰوَٰتِ وَمَا فِي اِ۬لۡأَرۡضِۗ وَلَقَدۡ وَصَّيۡنَا اَ۬لَّذِينَ أُوتُواْ اُ۬لۡكِتَٰبَ مِن قَبۡلِكُمۡ وَإِيَّاكُمۡ أَنِ اِ۪تَّقُواْ اُ۬للَّهَۚ وَإِن تَكۡفُرُواْ فَإِنَّ لِلَّهِ مَا فِي اِ۬لسَّمَٰوَٰتِ وَمَا فِي اِ۬لۡأَرۡضِۚ وَكَانَ اَ۬للَّهُ غَنِيًّا حَمِيدٗا ١٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (623,5,'99',4,'An-Nisā’','النِّسَاء',12,12,131,'وَلِلَّهِ مَا فِي اِ۬لسَّمَٰوَٰتِ وَمَا فِي اِ۬لۡأَرۡضِۚ وَكَفَىٰ بِاللَّهِ وَكِيلًا ١٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (624,5,'99',4,'An-Nisā’','النِّسَاء',13,14,132,'إِن يَشَأۡ يُذۡهِبۡكُمۡ أَيُّهَا اَ۬لنَّاسُ وَيَأۡتِ بِـَٔاخَرِينَۚ وَكَانَ اَ۬للَّهُ عَلَىٰ ذَٰلِكَ قَدِيرٗا ١٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (625,5,'99',4,'An-Nisā’','النِّسَاء',14,15,133,'مَّن كَانَ يُرِيدُ ثَوَابَ اَ۬لدُّنۡيۭا فَعِندَ اَ۬للَّهِ ثَوَابُ اُ۬لدُّنۡيۭا وَاَلۡأٓخِرَةِۚ وَكَانَ اَ۬للَّهُ سَمِيعَۢا بَصِيرٗا ١٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (626,5,'100',4,'An-Nisā’','النِّسَاء',1,4,134,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ كُونُواْ قَوَّٰمِينَ بِالۡقِسۡطِ شُهَدَآءَ لِلَّهِ وَلَوۡ عَلَىٰٓ أَنفُسِكُمۡ أَوِ اِ۬لۡوَٰلِدَيۡنِ وَاَلۡأَقۡرَبِينَۚ إِن يَكُنۡ غَنِيًّا أَوۡ فَقِيرٗا فَاَللَّهُ أَوۡلَىٰ بِهِمَاۖ فَلَا تَتَّبِعُواْ اُ۬لۡهَوَىٰٓ أَن تَعۡدِلُواْۚ وَإِن تَلۡوُۥٓاْ أَوۡ تُعۡرِضُواْ فَإِنَّ اَ۬للَّهَ كَانَ بِمَا تَعۡمَلُونَ خَبِيرٗا ١٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (627,5,'100',4,'An-Nisā’','النِّسَاء',4,8,135,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُوٓاْ ءَامِنُواْ بِاللَّهِ وَرَسُولِهِۦ وَاَلۡكِتَٰبِ اِ۬لَّذِي نُزِّلَ عَلَىٰ رَسُولِهِۦ وَاَلۡكِتَٰبِ اِ۬لَّذِيٓ أُنزِلَ مِن قَبۡلُۚ وَمَن يَكۡفُرۡ بِاللَّهِ وَمَلَٰٓئِكَتِهِۦ وَكُتُبِهِۦ وَرُسُلِهِۦ وَاَلۡيَوۡمِ اِ۬لۡأٓخِرِ فَقَد ضَّلَّ ضَلَٰلَۢا بَعِيدًا ١٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (628,5,'100',4,'An-Nisā’','النِّسَاء',8,10,136,'إِنَّ اَ۬لَّذِينَ ءَامَنُواْ ثُمَّ كَفَرُواْ ثُمَّ ءَامَنُواْ ثُمَّ كَفَرُواْ ثُمَّ اَ۪زۡدَادُواْ كُفۡرٗا لَّمۡ يَكُنِ اِ۬للَّهُ لِيَغۡفِرَ لَهُمۡ وَلَا لِيَهۡدِيَهُمۡ سَبِيلَۢا ١٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (629,5,'100',4,'An-Nisā’','النِّسَاء',10,10,137,'۞ بَشِّرِ اِ۬لۡمُنَٰفِقِينَ بِأَنَّ لَهُمۡ عَذَابًا أَلِيمًا ١٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (630,5,'100',4,'An-Nisā’','النِّسَاء',10,12,138,'اِ۬لَّذِينَ يَتَّخِذُونَ اَ۬لۡكٰ۪فِرِينَ أَوۡلِيَآءَ مِن دُونِ اِ۬لۡمُؤۡمِنِينَۚ أَيَبۡتَغُونَ عِندَهُمُ اُ۬لۡعِزَّةَ فَإِنَّ اَ۬لۡعِزَّةَ لِلَّهِ جَمِيعٗا ١٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (631,5,'100',4,'An-Nisā’','النِّسَاء',12,15,139,'وَقَدۡ نُزِّلَ عَلَيۡكُمۡ فِي اِ۬لۡكِتَٰبِ أَنۡ إِذَا سَمِعۡتُمۡ ءَايَٰتِ اِ۬للَّهِ يُكۡفَرُ بِهَا وَيُسۡتَهۡزَأُ بِهَا فَلَا تَقۡعُدُواْ مَعَهُمۡ حَتَّىٰ يَخُوضُواْ فِي حَدِيثٍ غَيۡرِهِۦٓۚ إِنَّكُمۡ إِذٗا مِّثۡلُهُمۡۗ إِنَّ اَ۬للَّهَ جَامِعُ اُ۬لۡمُنَٰفِقِينَ وَاَلۡكٰ۪فِرِينَ فِي جَهَنَّمَ جَمِيعًا ١٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (632,5,'101',4,'An-Nisā’','النِّسَاء',1,5,140,'اِ۬لَّذِينَ يَتَرَبَّصُونَ بِكُمۡ فَإِن كَانَ لَكُمۡ فَتۡحٞ مِّنَ اَ۬للَّهِ قَالُوٓاْ أَلَمۡ نَكُن مَّعَكُمۡ وَإِن كَانَ لِلۡكٰ۪فِرِينَ نَصِيبٞ قَالُوٓاْ أَلَمۡ نَسۡتَحۡوِذۡ عَلَيۡكُمۡ وَنَمۡنَعۡكُم مِّنَ اَ۬لۡمُؤۡمِنِينَۚ فَاَللَّهُ يَحۡكُمُ بَيۡنَكُمۡ يَوۡمَ اَ۬لۡقِيَٰمَةِۗ وَلَن يَجۡعَلَ اَ۬للَّهُ لِلۡكٰ۪فِرِينَ عَلَى اَ۬لۡمُؤۡمِنِينَ سَبِيلًا ١٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (633,5,'101',4,'An-Nisā’','النِّسَاء',5,7,141,'إِنَّ اَ۬لۡمُنَٰفِقِينَ يُخَٰدِعُونَ اَ۬للَّهَ وَهۡوَ خَٰدِعُهُمۡ وَإِذَا قَامُوٓاْ إِلَى اَ۬لصَّلَوٰةِ قَامُواْ كُسَالَىٰ يُرَآءُونَ اَ۬لنَّاسَ وَلَا يَذۡكُرُونَ اَ۬للَّهَ إِلَّا قَلِيلٗا ١٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (634,5,'101',4,'An-Nisā’','النِّسَاء',7,8,142,'مُّذَبۡذَبِينَ بَيۡنَ ذَٰلِكَ لَآ إِلَىٰ هَٰٓؤُلَآءِ وَلَآ إِلَىٰ هَٰٓؤُلَآءِۚ وَمَن يُضۡلِلِ اِ۬للَّهُ فَلَن تَجِدَ لَهُۥ سَبِيلٗا ١٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (635,5,'101',4,'An-Nisā’','النِّسَاء',8,10,143,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ لَا تَتَّخِذُواْ اُ۬لۡكٰ۪فِرِينَ أَوۡلِيَآءَ مِن دُونِ اِ۬لۡمُؤۡمِنِينَۚ أَتُرِيدُونَ أَن تَجۡعَلُواْ لِلَّهِ عَلَيۡكُمۡ سُلۡطَٰنٗا مُّبِينًا ١٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (636,5,'101',4,'An-Nisā’','النِّسَاء',10,11,144,'إِنَّ اَ۬لۡمُنَٰفِقِينَ فِي اِ۬لدَّرَكِ اِ۬لۡأَسۡفَلِ مِنَ اَ۬لنّ۪ارِ وَلَن تَجِدَ لَهُمۡ نَصِيرًا ١٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (637,5,'101',4,'An-Nisā’','النِّسَاء',12,14,145,'إِلَّا اَ۬لَّذِينَ تَابُواْ وَأَصۡلَحُواْ وَاَعۡتَصَمُواْ بِاللَّهِ وَأَخۡلَصُواْ دِينَهُمۡ لِلَّهِ فَأُوْلَٰٓئِكَ مَعَ اَ۬لۡمُؤۡمِنِينَۖ وَسَوۡفَ يُؤۡتِ اِ۬للَّهُ اُ۬لۡمُؤۡمِنِينَ أَجۡرًا عَظِيمٗا ١٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (638,5,'101',4,'An-Nisā’','النِّسَاء',14,15,146,'مَّا يَفۡعَلُ اُ۬للَّهُ بِعَذَابِكُمۡ إِن شَكَرۡتُمۡ وَءَامَنتُمۡۚ وَكَانَ اَ۬للَّهُ شَاكِرًا عَلِيمٗا ١٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (639,6,'102',4,'An-Nisā’','النِّسَاء',1,2,147,'۞ لَّا يُحِبُّ اُ۬للَّهُ اُ۬لۡجَهۡرَ بِالسُّوٓءِ مِنَ اَ۬لۡقَوۡلِ إِلَّا مَن ظُلِمَۚ وَكَانَ اَ۬للَّهُ سَمِيعًا عَلِيمًا ١٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (640,6,'102',4,'An-Nisā’','النِّسَاء',2,3,148,'إِن تُبۡدُواْ خَيۡرًا أَوۡ تُخۡفُوهُ أَوۡ تَعۡفُواْ عَن سُوٓءٖ فَإِنَّ اَ۬للَّهَ كَانَ عَفُوّٗا قَدِيرًا ١٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (641,6,'102',4,'An-Nisā’','النِّسَاء',3,6,149,'إِنَّ اَ۬لَّذِينَ يَكۡفُرُونَ بِاللَّهِ وَرُسُلِهِۦ وَيُرِيدُونَ أَن يُفَرِّقُواْ بَيۡنَ اَ۬للَّهِ وَرُسُلِهِۦ وَيَقُولُونَ نُؤۡمِنُ بِبَعۡضٖ وَنَكۡفُرُ بِبَعۡضٖ وَيُرِيدُونَ أَن يَتَّخِذُواْ بَيۡنَ ذَٰلِكَ سَبِيلًا ١٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (642,6,'102',4,'An-Nisā’','النِّسَاء',6,7,150,'أُوْلَٰٓئِكَ هُمُ اُ۬لۡكَٰفِرُونَ حَقّٗاۚ وَأَعۡتَدۡنَا لِلۡكٰ۪فِرِينَ عَذَابٗا مُّهِينٗا ١٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (643,6,'102',4,'An-Nisā’','النِّسَاء',7,9,151,'وَاَلَّذِينَ ءَامَنُواْ بِاللَّهِ وَرُسُلِهِۦ وَلَمۡ يُفَرِّقُواْ بَيۡنَ أَحَدٖ مِّنۡهُمۡ أُوْلَٰٓئِكَ سَوۡفَ نُؤۡتِيهِمۡ أُجُورَهُمۡۗ وَكَانَ اَ۬للَّهُ غَفُورٗا رَّحِيمٗا ١٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (644,6,'102',4,'An-Nisā’','النِّسَاء',9,13,152,'يَسۡـَٔلُكَ أَهۡلُ اُ۬لۡكِتَٰبِ أَن تُنزِلَ عَلَيۡهِمۡ كِتَٰبٗا مِّنَ اَ۬لسَّمَآءِۚ فَقَد سَّأَلُواْ مُوسۭيٰٓ أَكۡبَرَ مِن ذَٰلِكَ فَقَالُوٓاْ أَر۪نَا اَ۬للَّهَ جَهۡرَةٗ فَأَخَذَتۡهُمُ اُ۬لصَّٰعِقَةُ بِظُلۡمِهِمۡۚ ثُمَّ اَ۪تَّخَذُواْ اُ۬لۡعِجۡلَ مِنۢ بَعۡدِ مَا جَآءَتۡهُمُ اُ۬لۡبَيِّنَٰتُ فَعَفَوۡنَا عَن ذَٰلِكَۚ وَءَاتَيۡنَا مُوسۭيٰ سُلۡطَٰنٗا مُّبِينٗا ١٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (645,6,'102',4,'An-Nisā’','النِّسَاء',13,15,153,'وَرَفَعۡنَا فَوۡقَهُمُ اُ۬لطُّورَ بِمِيثَٰقِهِمۡ وَقُلۡنَا لَهُمُ اُ۟دۡخُلُواْ اُ۬لۡبَابَ سُجَّدٗا وَقُلۡنَا لَهُمۡ لَا تَعۡدُواْ فِي اِ۬لسَّبۡتِ وَأَخَذۡنَا مِنۡهُم مِّيثَٰقًا غَلِيظٗا ١٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (646,6,'103',4,'An-Nisā’','النِّسَاء',1,3,154,'فَبِمَا نَقۡضِهِم مِّيثَٰقَهُمۡ وَكُفۡرِهِم بِـَٔايَٰتِ اِ۬للَّهِ وَقَتۡلِهِمِ اِ۬لۡأَنۢبِيَآءَ بِغَيۡرِ حَقّٖ وَقَوۡلِهِمۡ قُلُوبُنَا غُلۡفُۢۚ بَلۡ طَبَعَ اَ۬للَّهُ عَلَيۡهَا بِكُفۡرِهِمۡ فَلَا يُؤۡمِنُونَ إِلَّا قَلِيلٗا ١٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (647,6,'103',4,'An-Nisā’','النِّسَاء',3,4,155,'وَبِكُفۡرِهِمۡ وَقَوۡلِهِمۡ عَلَىٰ مَرۡيَمَ بُهۡتَٰنًا عَظِيمٗا ١٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (648,6,'103',4,'An-Nisā’','النِّسَاء',4,7,156,'۞ وَقَوۡلِهِمۡ إِنَّا قَتَلۡنَا اَ۬لۡمَسِيحَ عِيسَى اَ۪بۡنَ مَرۡيَمَ رَسُولَ اَ۬للَّهِ وَمَا قَتَلُوهُ وَمَا صَلَبُوهُ وَلَٰكِن شُبِّهَ لَهُمۡۚ وَإِنَّ اَ۬لَّذِينَ اَ۪خۡتَلَفُواْ فِيهِ لَفِي شَكّٖ مِّنۡهُۚ مَا لَهُم بِهِۦ مِنۡ عِلۡمٍ إِلَّا اَ۪تِّبَاعَ اَ۬لظَّنِّۚ وَمَا قَتَلُوهُ يَقِينَۢا ١٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (649,6,'103',4,'An-Nisā’','النِّسَاء',7,7,157,'بَل رَّفَعَهُ اُ۬للَّهُ إِلَيۡهِۚ وَكَانَ اَ۬للَّهُ عَزِيزًا حَكِيمٗا ١٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (650,6,'103',4,'An-Nisā’','النِّسَاء',8,9,158,'وَإِن مِّنۡ أَهۡلِ اِ۬لۡكِتَٰبِ إِلَّا لَيُؤۡمِنَنَّ بِهِۦ قَبۡلَ مَوۡتِهِۦۖ وَيَوۡمَ اَ۬لۡقِيَٰمَةِ يَكُونُ عَلَيۡهِمۡ شَهِيدٗا ١٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (651,6,'103',4,'An-Nisā’','النِّسَاء',9,11,159,'فَبِظُلۡمٖ مِّنَ اَ۬لَّذِينَ هَادُواْ حَرَّمۡنَا عَلَيۡهِمۡ طَيِّبَٰتٍ أُحِلَّتۡ لَهُمۡ وَبِصَدِّهِمۡ عَن سَبِيلِ اِ۬للَّهِ كَثِيرٗا ١٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (652,6,'103',4,'An-Nisā’','النِّسَاء',11,12,160,'وَأَخۡذِهِمِ اِ۬لرِّبَوٰاْ وَقَدۡ نُهُواْ عَنۡهُ وَأَكۡلِهِمۡ أَمۡوَٰلَ اَ۬لنّ۪اسِ بِالۡبَٰطِلِۚ وَأَعۡتَدۡنَا لِلۡكٰ۪فِرِينَ مِنۡهُمۡ عَذَابًا أَلِيمٗا ١٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (653,6,'103',4,'An-Nisā’','النِّسَاء',12,15,161,'لَّٰكِنِ اِ۬لرَّٰسِخُونَ فِي اِ۬لۡعِلۡمِ مِنۡهُمۡ وَاَلۡمُؤۡمِنُونَ يُؤۡمِنُونَ بِمَآ أُنزِلَ إِلَيۡكَ وَمَآ أُنزِلَ مِن قَبۡلِكَۚ وَاَلۡمُقِيمِينَ اَ۬لصَّلَوٰةَۚ وَاَلۡمُؤۡتُونَ اَ۬لزَّكَوٰةَ وَاَلۡمُؤۡمِنُونَ بِاللَّهِ وَاَلۡيَوۡمِ اِ۬لۡأٓخِرِ أُوْلَٰٓئِكَ سَنُؤۡتِيهِمۡ أَجۡرًا عَظِيمًا ١٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (654,6,'104',4,'An-Nisā’','النِّسَاء',1,4,162,'إِنَّآ أَوۡحَيۡنَآ إِلَيۡكَ كَمَآ أَوۡحَيۡنَآ إِلَىٰ نُوحٖ وَاَلنَّبِيِّـۧنَ مِنۢ بَعۡدِهِۦۚ وَأَوۡحَيۡنَآ إِلَىٰٓ إِبۡرَٰهِيمَ وَإِسۡمَٰعِيلَ وَإِسۡحَٰقَ وَيَعۡقُوبَ وَاَلۡأَسۡبَاطِ وَعِيسۭيٰ وَأَيُّوبَ وَيُونُسَ وَهَٰرُونَ وَسُلَيۡمَٰنَۚ وَءَاتَيۡنَا دَاوُۥدَ زَبُورٗا ١٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (655,6,'104',4,'An-Nisā’','النِّسَاء',4,6,163,'وَرُسُلٗا قَدۡ قَصَصۡنَٰهُمۡ عَلَيۡكَ مِن قَبۡلُ وَرُسُلٗا لَّمۡ نَقۡصُصۡهُمۡ عَلَيۡكَۚ وَكَلَّمَ اَ۬للَّهُ مُوسۭيٰ تَكۡلِيمٗا ١٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (656,6,'104',4,'An-Nisā’','النِّسَاء',6,7,164,'رُّسُلٗا مُّبَشِّرِينَ وَمُنذِرِينَ لِئَلَّا يَكُونَ لِلنّ۪اسِ عَلَى اَ۬للَّهِ حُجَّةُۢ بَعۡدَ اَ۬لرُّسُلِۚ وَكَانَ اَ۬للَّهُ عَزِيزًا حَكِيمٗا ١٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (657,6,'104',4,'An-Nisā’','النِّسَاء',8,9,165,'۞ لَّٰكِنِ اِ۬للَّهُ يَشۡهَدُ بِمَآ أَنزَلَ إِلَيۡكَۖ أَنزَلَهُۥ بِعِلۡمِهِۦۖ وَاَلۡمَلَٰٓئِكَةُ يَشۡهَدُونَۚ وَكَفَىٰ بِاللَّهِ شَهِيدًا ١٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (658,6,'104',4,'An-Nisā’','النِّسَاء',9,10,166,'إِنَّ اَ۬لَّذِينَ كَفَرُواْ وَصَدُّواْ عَن سَبِيلِ اِ۬للَّهِ قَد ضَّلُّواْ ضَلَٰلَۢا بَعِيدًا ١٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (659,6,'104',4,'An-Nisā’','النِّسَاء',10,12,167,'إِنَّ اَ۬لَّذِينَ كَفَرُواْ وَظَلَمُواْ لَمۡ يَكُنِ اِ۬للَّهُ لِيَغۡفِرَ لَهُمۡ وَلَا لِيَهۡدِيَهُمۡ طَرِيقًا ١٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (660,6,'104',4,'An-Nisā’','النِّسَاء',12,13,168,'إِلَّا طَرِيقَ جَهَنَّمَ خَٰلِدِينَ فِيهَآ أَبَدٗاۚ وَكَانَ ذَٰلِكَ عَلَى اَ۬للَّهِ يَسِيرٗا ١٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (661,6,'104',4,'An-Nisā’','النِّسَاء',13,15,169,'يَٰٓأَيُّهَا اَ۬لنَّاسُ قَد جَّآءَكُمُ اُ۬لرَّسُولُ بِالۡحَقِّ مِن رَّبِّكُمۡ فَـَٔامِنُواْ خَيۡرٗا لَّكُمۡۚ وَإِن تَكۡفُرُواْ فَإِنَّ لِلَّهِ مَا فِي اِ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِۚ وَكَانَ اَ۬للَّهُ عَلِيمًا حَكِيمٗا ١٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (662,6,'105',4,'An-Nisā’','النِّسَاء',1,6,170,'يَٰٓأَهۡلَ اَ۬لۡكِتَٰبِ لَا تَغۡلُواْ فِي دِينِكُمۡ وَلَا تَقُولُواْ عَلَى اَ۬للَّهِ إِلَّا اَ۬لۡحَقَّۚ إِنَّمَا اَ۬لۡمَسِيحُ عِيسَى اَ۪بۡنُ مَرۡيَمَ رَسُولُ اُ۬للَّهِ وَكَلِمَتُهُۥٓ أَلۡقَىٰهَآ إِلَىٰ مَرۡيَمَ وَرُوحٞ مِّنۡهُۖ فَـَٔامِنُواْ بِاللَّهِ وَرُسُلِهِۦۖ وَلَا تَقُولُواْ ثَلَٰثَةٌۚ اِ۪نتَهُواْ خَيۡرٗا لَّكُمۡۚ إِنَّمَا اَ۬للَّهُ إِلَٰهٞ وَٰحِدٞۖ سُبۡحَٰنَهُۥٓ أَن يَكُونَ لَهُۥ وَلَدٞۘ لَّهُۥ مَا فِي اِ۬لسَّمَٰوَٰتِ وَمَا فِي اِ۬لۡأَرۡضِۗ وَكَفَىٰ بِاللَّهِ وَكِيلٗا ١٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (663,6,'105',4,'An-Nisā’','النِّسَاء',6,9,171,'۞ لَّن يَسۡتَنكِفَ اَ۬لۡمَسِيحُ أَن يَكُونَ عَبۡدٗا لِّلَّهِ وَلَا اَ۬لۡمَلَٰٓئِكَةُ اُ۬لۡمُقَرَّبُونَۚ وَمَن يَسۡتَنكِفۡ عَنۡ عِبَادَتِهِۦ وَيَسۡتَكۡبِرۡ فَسَيَحۡشُرُهُمۡ إِلَيۡهِ جَمِيعٗا ١٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (664,6,'105',4,'An-Nisā’','النِّسَاء',9,12,172,'فَأَمَّا اَ۬لَّذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ فَيُوَفِّيهِمۡ أُجُورَهُمۡ وَيَزِيدُهُم مِّن فَضۡلِهِۦۖ وَأَمَّا اَ۬لَّذِينَ اَ۪سۡتَنكَفُواْ وَاَسۡتَكۡبَرُواْ فَيُعَذِّبُهُمۡ عَذَابًا أَلِيمٗا وَلَا يَجِدُونَ لَهُم مِّن دُونِ اِ۬للَّهِ وَلِيّٗا وَلَا نَصِيرٗا ١٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (665,6,'105',4,'An-Nisā’','النِّسَاء',12,13,173,'يَٰٓأَيُّهَا اَ۬لنَّاسُ قَد جَّآءَكُم بُرۡهَٰنٞ مِّن رَّبِّكُمۡ وَأَنزَلۡنَآ إِلَيۡكُمۡ نُورٗا مُّبِينٗا ١٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (666,6,'105',4,'An-Nisā’','النِّسَاء',14,15,174,'فَأَمَّا اَ۬لَّذِينَ ءَامَنُواْ بِاللَّهِ وَاَعۡتَصَمُواْ بِهِۦ فَسَيُدۡخِلُهُمۡ فِي رَحۡمَةٖ مِّنۡهُ وَفَضۡلٖ وَيَهۡدِيهِمۡ إِلَيۡهِ صِرَٰطٗا مُّسۡتَقِيمٗا ١٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (667,6,'106',4,'An-Nisā’','النِّسَاء',1,5,175,'يَسۡتَفۡتُونَكَ قُلِ اِ۬للَّهُ يُفۡتِيكُمۡ فِي اِ۬لۡكَلَٰلَةِۚ إِنِ اِ۪مۡرُؤٌاْ هَلَكَ لَيۡسَ لَهُۥ وَلَدٞ وَلَهُۥٓ أُخۡتٞ فَلَهَا نِصۡفُ مَا تَرَكَۚ وَهۡوَ يَرِثُهَآ إِن لَّمۡ يَكُن لَّهَا وَلَدٞۚ فَإِن كَانَتَا اَ۪ثۡنَتَيۡنِ فَلَهُمَا اَ۬لثُّلُثَانِ مِمَّا تَرَكَۚ وَإِن كَانُوٓاْ إِخۡوَةٗ رِّجَالٗا وَنِسَآءٗ فَلِلذَّكَرِ مِثۡلُ حَظِّ اِ۬لۡأُنثَيَيۡنِۗ يُبَيِّنُ اُ۬للَّهُ لَكُمۡ أَن تَضِلُّواْۗ وَاَللَّهُ بِكُلِّ شَيۡءٍ عَلِيمُۢ ١٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (668,6,'106',5,'Al-Mā’idah','المَائدة',8,8,1,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُوٓاْ أَوۡفُواْ بِالۡعُقُودِ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (669,6,'106',5,'Al-Mā’idah','المَائدة',8,10,2,'أُحِلَّتۡ لَكُم بَهِيمَةُ اُ۬لۡأَنۡعَٰمِ إِلَّا مَا يُتۡلَىٰ عَلَيۡكُمۡ غَيۡرَ مُحِلِّي اِ۬لصَّيۡدِ وَأَنتُمۡ حُرُمٌۗ إِنَّ اَ۬للَّهَ يَحۡكُمُ مَا يُرِيدُ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (670,6,'106',5,'Al-Mā’idah','المَائدة',10,15,3,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ لَا تُحِلُّواْ شَعَٰٓئِرَ اَ۬للَّهِ وَلَا اَ۬لشَّهۡرَ اَ۬لۡحَرَامَ وَلَا اَ۬لۡهَدۡيَ وَلَا اَ۬لۡقَلَٰٓئِدَ وَلَآ ءَآمِّينَ اَ۬لۡبَيۡتَ اَ۬لۡحَرَامَ يَبۡتَغُونَ فَضۡلٗا مِّن رَّبِّهِمۡ وَرِضۡوَٰنٗاۚ وَإِذَا حَلَلۡتُمۡ فَاَصۡطَادُواْۚ وَلَا يَجۡرِمَنَّكُمۡ شَنَـَٔانُ قَوۡمٍ إِن صَدُّوكُمۡ عَنِ اِ۬لۡمَسۡجِدِ اِ۬لۡحَرَامِ أَن تَعۡتَدُواْۘ وَتَعَاوَنُواْ عَلَى اَ۬لۡبِرِّ وَاَلتَّقۡوۭيٰۖ وَلَا تَعَاوَنُواْ عَلَى اَ۬لۡإِثۡمِ وَاَلۡعُدۡوَٰنِۚ وَاَتَّقُواْ اُ۬للَّهَۚ إِنَّ اَ۬للَّهَ شَدِيدُ اُ۬لۡعِقَابِ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (671,6,'107',5,'Al-Mā’idah','المَائدة',1,7,4,'۞ حُرِّمَتۡ عَلَيۡكُمُ اُ۬لۡمَيۡتَةُ وَاَلدَّمُ وَلَحۡمُ اُ۬لۡخِنزِيرِ وَمَآ أُهِلَّ لِغَيۡرِ اِ۬للَّهِ بِهِۦ وَاَلۡمُنۡخَنِقَةُ وَاَلۡمَوۡقُوذَةُ وَاَلۡمُتَرَدِّيَةُ وَاَلنَّطِيحَةُ وَمَآ أَكَلَ اَ۬لسَّبُعُ إِلَّا مَا ذَكَّيۡتُمۡ وَمَا ذُبِحَ عَلَى اَ۬لنُّصُبِ وَأَن تَسۡتَقۡسِمُواْ بِالۡأَزۡلَٰمِۚ ذَٰلِكُمۡ فِسۡقٌۗ اِ۬لۡيَوۡمَ يَئِسَ اَ۬لَّذِينَ كَفَرُواْ مِن دِينِكُمۡ فَلَا تَخۡشَوۡهُمۡ وَاَخۡشَوۡنِۚ اِ۬لۡيَوۡمَ أَكۡمَلۡتُ لَكُمۡ دِينَكُمۡ وَأَتۡمَمۡتُ عَلَيۡكُمۡ نِعۡمَتِي وَرَضِيتُ لَكُمُ اُ۬لۡإِسۡلَٰمَ دِينٗاۚ فَمَنِ اِ۟ضۡطُرَّ فِي مَخۡمَصَةٍ غَيۡرَ مُتَجَانِفٖ لِّإِثۡمٖ فَإِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٞ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (672,6,'107',5,'Al-Mā’idah','المَائدة',7,10,5,'يَسۡـَٔلُونَكَ مَاذَآ أُحِلَّ لَهُمۡۖ قُلۡ أُحِلَّ لَكُمُ اُ۬لطَّيِّبَٰتُ وَمَا عَلَّمۡتُم مِّنَ اَ۬لۡجَوَارِحِ مُكَلِّبِينَ تُعَلِّمُونَهُنَّ مِمَّا عَلَّمَكُمُ اُ۬للَّهُۖ فَكُلُواْ مِمَّآ أَمۡسَكۡنَ عَلَيۡكُمۡ وَاَذۡكُرُواْ اُ۪سۡمَ اَ۬للَّهِ عَلَيۡهِۖ وَاَتَّقُواْ اُ۬للَّهَۚ إِنَّ اَ۬للَّهَ سَرِيعُ اُ۬لۡحِسَابِ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (673,6,'107',5,'Al-Mā’idah','المَائدة',11,15,6,'اِ۬لۡيَوۡمَ أُحِلَّ لَكُمُ اُ۬لطَّيِّبَٰتُۖ وَطَعَامُ اُ۬لَّذِينَ أُوتُواْ اُ۬لۡكِتَٰبَ حِلّٞ لَّكُمۡ وَطَعَامُكُمۡ حِلّٞ لَّهُمۡۖ وَاَلۡمُحۡصَنَٰتُ مِنَ اَ۬لۡمُؤۡمِنَٰتِ وَاَلۡمُحۡصَنَٰتُ مِنَ اَ۬لَّذِينَ أُوتُواْ اُ۬لۡكِتَٰبَ مِن قَبۡلِكُمۡ إِذَآ ءَاتَيۡتُمُوهُنَّ أُجُورَهُنَّ مُحۡصِنِينَ غَيۡرَ مُسَٰفِحِينَ وَلَا مُتَّخِذِيٓ أَخۡدَانٖۗ وَمَن يَكۡفُرۡ بِالۡإِيمَٰنِ فَقَدۡ حَبِطَ عَمَلُهُۥ وَهۡوَ فِي اِ۬لۡأٓخِرَةِ مِنَ اَ۬لۡخَٰسِرِينَ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (674,6,'108',5,'Al-Mā’idah','المَائدة',1,8,7,'۞ يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُوٓاْ إِذَا قُمۡتُمۡ إِلَى اَ۬لصَّلَوٰةِ فَاَغۡسِلُواْ وُجُوهَكُمۡ وَأَيۡدِيَكُمۡ إِلَى اَ۬لۡمَرَافِقِ وَاَمۡسَحُواْ بِرُءُوسِكُمۡ وَأَرۡجُلِكُمۡ إِلَى اَ۬لۡكَعۡبَيۡنِۚ وَإِن كُنتُمۡ جُنُبٗا فَاَطَّهَّرُواْۚ وَإِن كُنتُم مَّرۡضۭيٰٓ أَوۡ عَلَىٰ سَفَرٍ أَوۡ جَآ أَحَدٞ مِّنكُم مِّنَ اَ۬لۡغَآئِطِ أَوۡ لَٰمَسۡتُمُ اُ۬لنِّسَآءَ فَلَمۡ تَجِدُواْ مَآءٗ فَتَيَمَّمُواْ صَعِيدٗا طَيِّبٗا فَاَمۡسَحُواْ بِوُجُوهِكُمۡ وَأَيۡدِيكُم مِّنۡهُۚ مَا يُرِيدُ اُ۬للَّهُ لِيَجۡعَلَ عَلَيۡكُم مِّنۡ حَرَجٖ وَلَٰكِن يُرِيدُ لِيُطَهِّرَكُمۡ وَلِيُتِمَّ نِعۡمَتَهُۥ عَلَيۡكُمۡ لَعَلَّكُمۡ تَشۡكُرُونَ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (675,6,'108',5,'Al-Mā’idah','المَائدة',9,11,8,'وَاَذۡكُرُواْ نِعۡمَةَ اَ۬للَّهِ عَلَيۡكُمۡ وَمِيثَٰقَهُ اُ۬لَّذِي وَاثَقَكُم بِهِۦٓ إِذۡ قُلۡتُمۡ سَمِعۡنَا وَأَطَعۡنَاۖ وَاَتَّقُواْ اُ۬للَّهَۚ إِنَّ اَ۬للَّهَ عَلِيمُۢ بِذَاتِ اِ۬لصُّدُورِ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (676,6,'108',5,'Al-Mā’idah','المَائدة',11,14,9,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ كُونُواْ قَوَّٰمِينَ لِلَّهِ شُهَدَآءَ بِالۡقِسۡطِۖ وَلَا يَجۡرِمَنَّكُمۡ شَنَـَٔانُ قَوۡمٍ عَلَىٰٓ أَلَّا تَعۡدِلُواْۚ اُ۪عۡدِلُواْ هُوَ أَقۡرَبُ لِلتَّقۡوۭيٰۖ وَاَتَّقُواْ اُ۬للَّهَۚ إِنَّ اَ۬للَّهَ خَبِيرُۢ بِمَا تَعۡمَلُونَ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (677,6,'108',5,'Al-Mā’idah','المَائدة',14,15,10,'وَعَدَ اَ۬للَّهُ اُ۬لَّذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ لَهُم مَّغۡفِرَةٞ وَأَجۡرٌ عَظِيمٞ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (678,6,'109',5,'Al-Mā’idah','المَائدة',1,2,11,'وَاَلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَآ أُوْلَٰٓئِكَ أَصۡحَٰبُ اُ۬لۡجَحِيمِ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (679,6,'109',5,'Al-Mā’idah','المَائدة',2,5,12,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ اُ۟ذۡكُرُواْ نِعۡمَتَ اَ۬للَّهِ عَلَيۡكُمۡ إِذۡ هَمَّ قَوۡمٌ أَن يَبۡسُطُوٓاْ إِلَيۡكُمۡ أَيۡدِيَهُمۡ فَكَفَّ أَيۡدِيَهُمۡ عَنكُمۡۖ وَاَتَّقُواْ اُ۬للَّهَۚ وَعَلَى اَ۬للَّهِ فَلۡيَتَوَكَّلِ اِ۬لۡمُؤۡمِنُونَ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (680,6,'109',5,'Al-Mā’idah','المَائدة',5,11,13,'۞ وَلَقَدۡ أَخَذَ اَ۬للَّهُ مِيثَٰقَ بَنِيٓ إِسۡرَٰٓءِيلَ وَبَعَثۡنَا مِنۡهُمُ اُ۪ثۡنَيۡ عَشَرَ نَقِيبٗاۖ وَقَالَ اَ۬للَّهُ إِنِّي مَعَكُمۡۖ لَئِنۡ أَقَمۡتُمُ اُ۬لصَّلَوٰةَ وَءَاتَيۡتُمُ اُ۬لزَّكَوٰةَ وَءَامَنتُم بِرُسُلِي وَعَزَّرۡتُمُوهُمۡ وَأَقۡرَضۡتُمُ اُ۬للَّهَ قَرۡضًا حَسَنٗا لَّأُكَفِّرَنَّ عَنكُمۡ سَيِّـَٔاتِكُمۡ وَلَأُدۡخِلَنَّكُمۡ جَنَّٰتٖ تَجۡرِي مِن تَحۡتِهَا اَ۬لۡأَنۡهَٰرُۚ فَمَن كَفَرَ بَعۡدَ ذَٰلِكَ مِنكُمۡ فَقَد ضَّلَّ سَوَآءَ اَ۬لسَّبِيلِ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (681,6,'109',5,'Al-Mā’idah','المَائدة',11,15,14,'فَبِمَا نَقۡضِهِم مِّيثَٰقَهُمۡ لَعَنَّٰهُمۡ وَجَعَلۡنَا قُلُوبَهُمۡ قَٰسِيَةٗۖ يُحَرِّفُونَ اَ۬لۡكَلِمَ عَن مَّوَاضِعِهِۦ وَنَسُواْ حَظّٗا مِّمَّا ذُكِّرُواْ بِهِۦۚ وَلَا تَزَالُ تَطَّلِعُ عَلَىٰ خَآئِنَةٖ مِّنۡهُمۡ إِلَّا قَلِيلٗا مِّنۡهُمۡۖ فَاَعۡفُ عَنۡهُمۡ وَاَصۡفَحۡۚ إِنَّ اَ۬للَّهَ يُحِبُّ اُ۬لۡمُحۡسِنِينَ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (682,6,'110',5,'Al-Mā’idah','المَائدة',1,4,15,'وَمِنَ اَ۬لَّذِينَ قَالُوٓاْ إِنَّا نَصَٰر۪يٰٓ أَخَذۡنَا مِيثَٰقَهُمۡ فَنَسُواْ حَظّٗا مِّمَّا ذُكِّرُواْ بِهِۦ فَأَغۡرَيۡنَا بَيۡنَهُمُ اُ۬لۡعَدَاوَةَ وَاَلۡبَغۡضَآءَ ا۪لَىٰ يَوۡمِ اِ۬لۡقِيَٰمَةِۚ وَسَوۡفَ يُنَبِّئُهُمُ اُ۬للَّهُ بِمَا كَانُواْ يَصۡنَعُونَ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (683,6,'110',5,'Al-Mā’idah','المَائدة',4,6,16,'يَٰٓأَهۡلَ اَ۬لۡكِتَٰبِ قَد جَّآءَكُمۡ رَسُولُنَا يُبَيِّنُ لَكُمۡ كَثِيرٗا مِّمَّا كُنتُمۡ تُخۡفُونَ مِنَ اَ۬لۡكِتَٰبِ وَيَعۡفُواْ عَن كَثِيرٖ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (684,6,'110',5,'Al-Mā’idah','المَائدة',7,7,17,'قَد جَّآءَكُم مِّنَ اَ۬للَّهِ نُورٞ وَكِتَٰبٞ مُّبِينٞ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (685,6,'110',5,'Al-Mā’idah','المَائدة',8,10,18,'يَهۡدِي بِهِ اِ۬للَّهُ مَنِ اِ۪تَّبَعَ رِضۡوَٰنَهُۥ سُبُلَ اَ۬لسَّلَٰمِ وَيُخۡرِجُهُم مِّنَ اَ۬لظُّلُمَٰتِ إِلَى اَ۬لنُّورِ بِإِذۡنِهِۦ وَيَهۡدِيهِمۡ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (686,6,'110',5,'Al-Mā’idah','المَائدة',10,15,19,'لَّقَدۡ كَفَرَ اَ۬لَّذِينَ قَالُوٓاْ إِنَّ اَ۬للَّهَ هُوَ اَ۬لۡمَسِيحُ اُ۪بۡنُ مَرۡيَمَۚ قُلۡ فَمَن يَمۡلِكُ مِنَ اَ۬للَّهِ شَيۡـًٔا إِنۡ أَرَادَ أَن يُهۡلِكَ اَ۬لۡمَسِيحَ اَ۪بۡنَ مَرۡيَمَ وَأُمَّهُۥ وَمَن فِي اِ۬لۡأَرۡضِ جَمِيعٗاۗ وَلِلَّهِ مُلۡكُ اُ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِ وَمَا بَيۡنَهُمَاۚ يَخۡلُقُ مَا يَشَآءُۚ وَاَللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (687,6,'111',5,'Al-Mā’idah','المَائدة',1,4,20,'۞ وَقَالَتِ اِ۬لۡيَهُودُ وَاَلنَّصَٰر۪يٰ نَحۡنُ أَبۡنَٰٓؤُاْ اُ۬للَّهِ وَأَحِبَّٰٓؤُهُۥۚ قُلۡ فَلِمَ يُعَذِّبُكُم بِذُنُوبِكُمۖ بَلۡ أَنتُم بَشَرٞ مِّمَّنۡ خَلَقَۚ يَغۡفِرُ لِمَن يَشَآءُ وَيُعَذِّبُ مَن يَشَآءُۚ وَلِلَّهِ مُلۡكُ اُ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِ وَمَا بَيۡنَهُمَاۖ وَإِلَيۡهِ اِ۬لۡمَصِيرُ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (688,6,'111',5,'Al-Mā’idah','المَائدة',4,7,21,'يَٰٓأَهۡلَ اَ۬لۡكِتَٰبِ قَد جَّآءَكُمۡ رَسُولُنَا يُبَيِّنُ لَكُمۡ عَلَىٰ فَتۡرَةٖ مِّنَ اَ۬لرُّسُلِ أَن تَقُولُواْ مَا جَآءَنَا مِنۢ بَشِيرٖ وَلَا نَذِيرٖۖ فَقَد جَّآءَكُم بَشِيرٞ وَنَذِيرٞۗ وَاَللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (689,6,'111',5,'Al-Mā’idah','المَائدة',7,9,22,'وَإِذۡ قَالَ مُوسۭيٰ لِقَوۡمِهِۦ يَٰقَوۡمِ اِ۟ذۡكُرُواْ نِعۡمَةَ اَ۬للَّهِ عَلَيۡكُمۡ إِذ جَّعَلَ فِيكُمۡ أَنۢبِيَآءَ وَجَعَلَكُم مُّلُوكٗا وَءَاتَىٰكُم مَّا لَمۡ يُؤۡتِ أَحَدٗا مِّنَ اَ۬لۡعَٰلَمِينَ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (690,6,'111',5,'Al-Mā’idah','المَائدة',9,11,23,'يَٰقَوۡمِ اِ۟دۡخُلُواْ اُ۬لۡأَرۡضَ اَ۬لۡمُقَدَّسَةَ اَ۬لَّتِي كَتَبَ اَ۬للَّهُ لَكُمۡ وَلَا تَرۡتَدُّواْ عَلَىٰٓ أَدۡب۪ارِكُمۡ فَتَنقَلِبُواْ خَٰسِرِينَ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (691,6,'111',5,'Al-Mā’idah','المَائدة',11,13,24,'قَالُواْ يَٰمُوسۭيٰٓ إِنَّ فِيهَا قَوۡمٗا جَبَّارِينَ وَإِنَّا لَن نَّدۡخُلَهَا حَتَّىٰ يَخۡرُجُواْ مِنۡهَا فَإِن يَخۡرُجُواْ مِنۡهَا فَإِنَّا دَٰخِلُونَ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (692,6,'111',5,'Al-Mā’idah','المَائدة',13,15,25,'قَالَ رَجُلَانِ مِنَ اَ۬لَّذِينَ يَخَافُونَ أَنۡعَمَ اَ۬للَّهُ عَلَيۡهِمَا اَ۟دۡخُلُواْ عَلَيۡهِمِ اِ۬لۡبَابَ فَإِذَا دَخَلۡتُمُوهُ فَإِنَّكُمۡ غَٰلِبُونَۚ وَعَلَى اَ۬للَّهِ فَتَوَكَّلُوٓاْ إِن كُنتُم مُّؤۡمِنِينَ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (693,6,'112',5,'Al-Mā’idah','المَائدة',1,2,26,'قَالُواْ يَٰمُوسۭيٰٓ إِنَّا لَن نَّدۡخُلَهَآ أَبَدٗا مَّا دَامُواْ فِيهَاۖ فَاَذۡهَبۡ أَنتَ وَرَبُّكَ فَقَٰتِلَآ إِنَّا هَٰهُنَا قَٰعِدُونَ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (694,6,'112',5,'Al-Mā’idah','المَائدة',2,4,27,'قَالَ رَبِّ إِنِّي لَآ أَمۡلِكُ إِلَّا نَفۡسِي وَأَخِيۖ فَاَفۡرُقۡ بَيۡنَنَا وَبَيۡنَ اَ۬لۡقَوۡمِ اِ۬لۡفَٰسِقِينَ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (695,6,'112',5,'Al-Mā’idah','المَائدة',4,5,28,'قَالَ فَإِنَّهَا مُحَرَّمَةٌ عَلَيۡهِمۡۛ أَرۡبَعِينَ سَنَةٗۛ يَتِيهُونَ فِي اِ۬لۡأَرۡضِۚ فَلَا تَأۡسَ عَلَى اَ۬لۡقَوۡمِ اِ۬لۡفَٰسِقِينَ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (696,6,'112',5,'Al-Mā’idah','المَائدة',6,8,29,'۞ وَاَتۡلُ عَلَيۡهِمۡ نَبَأَ اَ۪بۡنَيۡ ءَادَمَ بِالۡحَقِّ إِذۡ قَرَّبَا قُرۡبَانٗا فَتُقُبِّلَ مِنۡ أَحَدِهِمَا وَلَمۡ يُتَقَبَّلۡ مِنَ اَ۬لۡأٓخَرِ قَالَ لَأَقۡتُلَنَّكَۖ قَالَ إِنَّمَا يَتَقَبَّلُ اُ۬للَّهُ مِنَ اَ۬لۡمُتَّقِينَ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (697,6,'112',5,'Al-Mā’idah','المَائدة',8,10,30,'لَئِنۢ بَسَطتَ إِلَيَّ يَدَكَ لِتَقۡتُلَنِي مَآ أَنَا۠ بِبَاسِطٖ يَدِيَ إِلَيۡكَ لِأَقۡتُلَكَۖ إِنِّيَ أَخَافُ اُ۬للَّهَ رَبَّ اَ۬لۡعَٰلَمِينَ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (698,6,'112',5,'Al-Mā’idah','المَائدة',10,11,31,'إِنِّيٓ أُرِيدُ أَن تَبُوٓأَ بِإِثۡمِي وَإِثۡمِكَ فَتَكُونَ مِنۡ أَصۡحَٰبِ اِ۬لنّ۪ارِۚ وَذَٰلِكَ جَزَٰٓؤُاْ اُ۬لظَّٰلِمِينَ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (699,6,'112',5,'Al-Mā’idah','المَائدة',11,12,32,'فَطَوَّعَتۡ لَهُۥ نَفۡسُهُۥ قَتۡلَ أَخِيهِ فَقَتَلَهُۥ فَأَصۡبَحَ مِنَ اَ۬لۡخَٰسِرِينَ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (700,6,'112',5,'Al-Mā’idah','المَائدة',13,15,33,'فَبَعَثَ اَ۬للَّهُ غُرَابٗا يَبۡحَثُ فِي اِ۬لۡأَرۡضِ لِيُرِيَهُۥ كَيۡفَ يُوَٰرِي سَوۡءَةَ أَخِيهِۚ قَالَ يَٰوَيۡلَتۭيٰٓ أَعَجَزۡتُ أَنۡ أَكُونَ مِثۡلَ هَٰذَا اَ۬لۡغُرَابِ فَأُوَٰرِيَ سَوۡءَةَ أَخِيۖ فَأَصۡبَحَ مِنَ اَ۬لنَّٰدِمِينَ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (701,6,'113',5,'Al-Mā’idah','المَائدة',1,5,34,'مِنۡ أَجۡلِ ذَٰلِكَ كَتَبۡنَا عَلَىٰ بَنِيٓ إِسۡرَٰٓءِيلَ أَنَّهُۥ مَن قَتَلَ نَفۡسَۢا بِغَيۡرِ نَفۡسٍ أَوۡ فَسَادٖ فِي اِ۬لۡأَرۡضِ فَكَأَنَّمَا قَتَلَ اَ۬لنَّاسَ جَمِيعٗا وَمَنۡ أَحۡيَاهَا فَكَأَنَّمَآ أَحۡيَا اَ۬لنَّاسَ جَمِيعٗاۚ وَلَقَد جَّآءَتۡهُمۡ رُسۡلُنَا بِالۡبَيِّنَٰتِ ثُمَّ إِنَّ كَثِيرٗا مِّنۡهُم بَعۡدَ ذَٰلِكَ فِي اِ۬لۡأَرۡضِ لَمُسۡرِفُونَ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (702,6,'113',5,'Al-Mā’idah','المَائدة',5,9,35,'۞ إِنَّمَا جَزَٰٓؤُاْ اُ۬لَّذِينَ يُحَارِبُونَ اَ۬للَّهَ وَرَسُولَهُۥ وَيَسۡعَوۡنَ فِي اِ۬لۡأَرۡضِ فَسَادًا أَن يُقَتَّلُوٓاْ أَوۡ يُصَلَّبُوٓاْ أَوۡ تُقَطَّعَ أَيۡدِيهِمۡ وَأَرۡجُلُهُم مِّنۡ خِلَٰفٍ أَوۡ يُنفَوۡاْ مِنَ اَ۬لۡأَرۡضِۚ ذَٰلِكَ لَهُمۡ خِزۡيٞ فِي اِ۬لدُّنۡيۭاۖ وَلَهُمۡ فِي اِ۬لۡأٓخِرَةِ عَذَابٌ عَظِيمٌ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (703,6,'113',5,'Al-Mā’idah','المَائدة',10,11,36,'إِلَّا اَ۬لَّذِينَ تَابُواْ مِن قَبۡلِ أَن تَقۡدِرُواْ عَلَيۡهِمۡۖ فَاَعۡلَمُوٓاْ أَنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٞ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (704,6,'113',5,'Al-Mā’idah','المَائدة',11,13,37,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ اُ۪تَّقُواْ اُ۬للَّهَ وَاَبۡتَغُوٓاْ إِلَيۡهِ اِ۬لۡوَسِيلَةَ وَجَٰهِدُواْ فِي سَبِيلِهِۦ لَعَلَّكُمۡ تُفۡلِحُونَ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (705,6,'113',5,'Al-Mā’idah','المَائدة',13,15,38,'إِنَّ اَ۬لَّذِينَ كَفَرُواْ لَوۡ أَنَّ لَهُم مَّا فِي اِ۬لۡأَرۡضِ جَمِيعٗا وَمِثۡلَهُۥ مَعَهُۥ لِيَفۡتَدُواْ بِهِۦ مِنۡ عَذَابِ يَوۡمِ اِ۬لۡقِيَٰمَةِ مَا تُقُبِّلَ مِنۡهُمۡۖ وَلَهُمۡ عَذَابٌ أَلِيمٞ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (706,6,'114',5,'Al-Mā’idah','المَائدة',1,2,39,'يُرِيدُونَ أَن يَخۡرُجُواْ مِنَ اَ۬لنّ۪ارِ وَمَا هُم بِخَٰرِجِينَ مِنۡهَاۖ وَلَهُمۡ عَذَابٞ مُّقِيمٞ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (707,6,'114',5,'Al-Mā’idah','المَائدة',2,4,40,'وَاَلسَّارِقُ وَاَلسَّارِقَةُ فَاَقۡطَعُوٓاْ أَيۡدِيَهُمَا جَزَآءَۢ بِمَا كَسَبَا نَكَٰلٗا مِّنَ اَ۬للَّهِۗ وَاَللَّهُ عَزِيزٌ حَكِيمٞ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (708,6,'114',5,'Al-Mā’idah','المَائدة',4,5,41,'فَمَن تَابَ مِنۢ بَعۡدِ ظُلۡمِهِۦ وَأَصۡلَحَ فَإِنَّ اَ۬للَّهَ يَتُوبُ عَلَيۡهِۗ إِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٌ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (709,6,'114',5,'Al-Mā’idah','المَائدة',5,7,42,'أَلَمۡ تَعۡلَمۡ أَنَّ اَ۬للَّهَ لَهُۥ مُلۡكُ اُ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِ يُعَذِّبُ مَن يَشَآءُ وَيَغۡفِرُ لِمَن يَشَآءُۗ وَاَللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (710,6,'114',5,'Al-Mā’idah','المَائدة',7,15,43,'۞ يَٰٓأَيُّهَا اَ۬لرَّسُولُ لَا يَحۡزُنكَ اَ۬لَّذِينَ يُسَٰرِعُونَ فِي اِ۬لۡكُفۡرِ مِنَ اَ۬لَّذِينَ قَالُوٓاْ ءَامَنَّا بِأَفۡوَٰهِهِمۡ وَلَمۡ تُؤۡمِن قُلُوبُهُمۡۛ وَمِنَ اَ۬لَّذِينَ هَادُواْۛ سَمَّٰعُونَ لِلۡكَذِبِ سَمَّٰعُونَ لِقَوۡمٍ ءَاخَرِينَ لَمۡ يَأۡتُوكَۖ يُحَرِّفُونَ اَ۬لۡكَلِمَ مِنۢ بَعۡدِ مَوَاضِعِهِۦۖ يَقُولُونَ إِنۡ أُوتِيتُمۡ هَٰذَا فَخُذُوهُ وَإِن لَّمۡ تُؤۡتَوۡهُ فَاَحۡذَرُواْۚ وَمَن يُرِدِ اِ۬للَّهُ فِتۡنَتَهُۥ فَلَن تَمۡلِكَ لَهُۥ مِنَ اَ۬للَّهِ شَيۡـًٔاۚ أُوْلَٰٓئِكَ اَ۬لَّذِينَ لَمۡ يُرِدِ اِ۬للَّهُ أَن يُطَهِّرَ قُلُوبَهُمۡۚ لَهُمۡ فِي اِ۬لدُّنۡيۭا خِزۡيٞۖ وَلَهُمۡ فِي اِ۬لۡأٓخِرَةِ عَذَابٌ عَظِيمٞ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (711,6,'115',5,'Al-Mā’idah','المَائدة',1,4,44,'سَمَّٰعُونَ لِلۡكَذِبِ أَكَّٰلُونَ لِلسُّحُتِۚ فَإِن جَآءُوكَ فَاَحۡكُم بَيۡنَهُمۡ أَوۡ أَعۡرِضۡ عَنۡهُمۡۖ وَإِن تُعۡرِضۡ عَنۡهُمۡ فَلَن يَضُرُّوكَ شَيۡـٔٗاۖ وَإِنۡ حَكَمۡتَ فَاَحۡكُم بَيۡنَهُم بِالۡقِسۡطِۚ إِنَّ اَ۬للَّهَ يُحِبُّ اُ۬لۡمُقۡسِطِينَ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (712,6,'115',5,'Al-Mā’idah','المَائدة',4,6,45,'وَكَيۡفَ يُحَكِّمُونَكَ وَعِندَهُمُ اُ۬لتَّوۡر۪ىٰةُ فِيهَا حُكۡمُ اُ۬للَّهِ ثُمَّ يَتَوَلَّوۡنَ مِنۢ بَعۡدِ ذَٰلِكَۚ وَمَآ أُوْلَٰٓئِكَ بِالۡمُؤۡمِنِينَ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (713,6,'115',5,'Al-Mā’idah','المَائدة',6,11,46,'إِنَّآ أَنزَلۡنَا اَ۬لتَّوۡر۪ىٰةَ فِيهَا هُدٗى وَنُورٞۚ يَحۡكُمُ بِهَا اَ۬لنَّبِيُّونَ اَ۬لَّذِينَ أَسۡلَمُواْ لِلَّذِينَ هَادُواْ وَاَلرَّبَّٰنِيُّونَ وَاَلۡأَحۡبَارُ بِمَا اَ۟سۡتُحۡفِظُواْ مِن كِتَٰبِ اِ۬للَّهِ وَكَانُواْ عَلَيۡهِ شُهَدَآءَۚ فَلَا تَخۡشَوُاْ اُ۬لنَّاسَ وَاَخۡشَوۡنِۦ وَلَا تَشۡتَرُواْ بِـَٔايَٰتِي ثَمَنٗا قَلِيلٗاۚ وَمَن لَّمۡ يَحۡكُم بِمَآ أَنزَلَ اَ۬للَّهُ فَأُوْلَٰٓئِكَ هُمُ اُ۬لۡكَٰفِرُونَ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (714,6,'115',5,'Al-Mā’idah','المَائدة',11,15,47,'۞ وَكَتَبۡنَا عَلَيۡهِمۡ فِيهَآ أَنَّ اَ۬لنَّفۡسَ بِالنَّفۡسِ وَاَلۡعَيۡنَ بِالۡعَيۡنِ وَاَلۡأَنفَ بِالۡأَنفِ وَاَلۡأُذُنَ بِالۡأُذُنِ وَاَلسِّنَّ بِالسِّنِّۚ وَاَلۡجُرُوحُ قِصَاصٞۚ فَمَن تَصَدَّقَ بِهِۦ فَهۡوَ كَفَّارَةٞ لَّهُۥۚ وَمَن لَّمۡ يَحۡكُم بِمَآ أَنزَلَ اَ۬للَّهُ فَأُوْلَٰٓئِكَ هُمُ اُ۬لظَّٰلِمُونَ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (715,6,'116',5,'Al-Mā’idah','المَائدة',1,3,48,'وَقَفَّيۡنَا عَلَىٰٓ ءَاثٰ۪رِهِم بِعِيسَى اَ۪بۡنِ مَرۡيَمَ مُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِ مِنَ اَ۬لتَّوۡر۪ىٰةِۖ وَءَاتَيۡنَٰهُ اُ۬لۡإِنجِيلَ فِيهِ هُدٗى وَنُورٞ وَمُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِ مِنَ اَ۬لتَّوۡر۪ىٰةِ وَهُدٗى وَمَوۡعِظَةٗ لِّلۡمُتَّقِينَ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (716,6,'116',5,'Al-Mā’idah','المَائدة',4,5,49,'وَلۡيَحۡكُمۡ أَهۡلُ اُ۬لۡإِنجِيلِ بِمَآ أَنزَلَ اَ۬للَّهُ فِيهِۚ وَمَن لَّمۡ يَحۡكُم بِمَآ أَنزَلَ اَ۬للَّهُ فَأُوْلَٰٓئِكَ هُمُ اُ۬لۡفَٰسِقُونَ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (717,6,'116',5,'Al-Mā’idah','المَائدة',5,11,50,'وَأَنزَلۡنَآ إِلَيۡكَ اَ۬لۡكِتَٰبَ بِالۡحَقِّ مُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِ مِنَ اَ۬لۡكِتَٰبِ وَمُهَيۡمِنًا عَلَيۡهِۖ فَاَحۡكُم بَيۡنَهُم بِمَآ أَنزَلَ اَ۬للَّهُۖ وَلَا تَتَّبِعۡ أَهۡوَآءَهُمۡ عَمَّا جَآءَكَ مِنَ اَ۬لۡحَقِّۚ لِكُلّٖ جَعَلۡنَا مِنكُمۡ شِرۡعَةٗ وَمِنۡهَاجٗاۚ وَلَوۡ شَآءَ اَ۬للَّهُ لَجَعَلَكُمۡ أُمَّةٗ وَٰحِدَةٗ وَلَٰكِن لِّيَبۡلُوَكُمۡ فِي مَآ ءَاتَىٰكُمۡۖ فَاَسۡتَبِقُواْ اُ۬لۡخَيۡرَٰتِۚ إِلَى اَ۬للَّهِ مَرۡجِعُكُمۡ جَمِيعٗا فَيُنَبِّئُكُم بِمَا كُنتُمۡ فِيهِ تَخۡتَلِفُونَ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (718,6,'116',5,'Al-Mā’idah','المَائدة',11,14,51,'وَأَنِ اِ۟حۡكُم بَيۡنَهُم بِمَآ أَنزَلَ اَ۬للَّهُ وَلَا تَتَّبِعۡ أَهۡوَآءَهُمۡ وَاَحۡذَرۡهُمۡ أَن يَفۡتِنُوكَ عَنۢ بَعۡضِ مَآ أَنزَلَ اَ۬للَّهُ إِلَيۡكَۖ فَإِن تَوَلَّوۡاْ فَاَعۡلَمۡ أَنَّمَا يُرِيدُ اُ۬للَّهُ أَن يُصِيبَهُم بِبَعۡضِ ذُنُوبِهِمۡۗ وَإِنَّ كَثِيرٗا مِّنَ اَ۬لنّ۪اسِ لَفَٰسِقُونَ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (719,6,'116',5,'Al-Mā’idah','المَائدة',14,15,52,'أَفَحُكۡمَ اَ۬لۡجَٰهِلِيَّةِ يَبۡغُونَۚ وَمَنۡ أَحۡسَنُ مِنَ اَ۬للَّهِ حُكۡمٗا لِّقَوۡمٖ يُوقِنُونَ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (720,6,'117',5,'Al-Mā’idah','المَائدة',1,3,53,'۞ يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ لَا تَتَّخِذُواْ اُ۬لۡيَهُودَ وَاَلنَّصَٰر۪يٰٓ أَوۡلِيَآءَۘ بَعۡضُهُمۡ أَوۡلِيَآءُ بَعۡضٖۚ وَمَن يَتَوَلَّهُم مِّنكُمۡ فَإِنَّهُۥ مِنۡهُمۡۗ إِنَّ اَ۬للَّهَ لَا يَهۡدِي اِ۬لۡقَوۡمَ اَ۬لظَّٰلِمِينَ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (721,6,'117',5,'Al-Mā’idah','المَائدة',3,5,54,'فَتَرَى اَ۬لَّذِينَ فِي قُلُوبِهِم مَّرَضٞ يُسَٰرِعُونَ فِيهِمۡ يَقُولُونَ نَخۡشَىٰٓ أَن تُصِيبَنَا دَآئِرَةٞۚ فَعَسَى اَ۬للَّهُ أَن يَأۡتِيَ بِالۡفَتۡحِ أَوۡ أَمۡرٖ مِّنۡ عِندِهِۦ فَيُصۡبِحُواْ عَلَىٰ مَآ أَسَرُّواْ فِيٓ أَنفُسِهِمۡ نَٰدِمِينَ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (722,6,'117',5,'Al-Mā’idah','المَائدة',5,7,55,'وَيَقُولَ اَ۬لَّذِينَ ءَامَنُوٓاْ أَهَٰٓؤُلَآءِ اِ۬لَّذِينَ أَقۡسَمُواْ بِاللَّهِ جَهۡدَ أَيۡمَٰنِهِمۡ إِنَّهُمۡ لَمَعَكُمۡۚ حَبِطَتۡ أَعۡمَٰلُهُمۡ فَأَصۡبَحُواْ خَٰسِرِينَ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (723,6,'117',5,'Al-Mā’idah','المَائدة',7,11,56,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ مَن يَرۡتَدَّ مِنكُمۡ عَن دِينِهِۦ فَسَوۡفَ يَأۡتِي اِ۬للَّهُ بِقَوۡمٖ يُحِبُّهُمۡ وَيُحِبُّونَهُۥٓ أَذِلَّةٍ عَلَى اَ۬لۡمُؤۡمِنِينَ أَعِزَّةٍ عَلَى اَ۬لۡكٰ۪فِرِينَ يُجَٰهِدُونَ فِي سَبِيلِ اِ۬للَّهِ وَلَا يَخَافُونَ لَوۡمَةَ لَآئِمٖۚ ذَٰلِكَ فَضۡلُ اُ۬للَّهِ يُؤۡتِيهِ مَن يَشَآءُۚ وَاَللَّهُ وَٰسِعٌ عَلِيمٌ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (724,6,'117',5,'Al-Mā’idah','المَائدة',11,12,57,'إِنَّمَا وَلِيُّكُمُ اُ۬للَّهُ وَرَسُولُهُۥ وَاَلَّذِينَ ءَامَنُواْ اُ۬لَّذِينَ يُقِيمُونَ اَ۬لصَّلَوٰةَ وَيُؤۡتُونَ اَ۬لزَّكَوٰةَ وَهُمۡ رَٰكِعُونَ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (725,6,'117',5,'Al-Mā’idah','المَائدة',12,13,58,'وَمَن يَتَوَلَّ اَ۬للَّهَ وَرَسُولَهُۥ وَاَلَّذِينَ ءَامَنُواْ فَإِنَّ حِزۡبَ اَ۬للَّهِ هُمُ اُ۬لۡغَٰلِبُونَ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (726,6,'117',5,'Al-Mā’idah','المَائدة',13,15,59,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ لَا تَتَّخِذُواْ اُ۬لَّذِينَ اَ۪تَّخَذُواْ دِينَكُمۡ هُزُؤٗا وَلَعِبٗا مِّنَ اَ۬لَّذِينَ أُوتُواْ اُ۬لۡكِتَٰبَ مِن قَبۡلِكُمۡ وَاَلۡكُفّ۪ارِ أَوۡلِيَآءَۚ وَاَتَّقُواْ اُ۬للَّهَ إِن كُنتُم مُّؤۡمِنِينَ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (727,6,'118',5,'Al-Mā’idah','المَائدة',1,2,60,'وَإِذَا نَادَيۡتُمۡ إِلَى اَ۬لصَّلَوٰةِ اِ۪تَّخَذُوهَا هُزُؤٗا وَلَعِبٗاۚ ذَٰلِكَ بِأَنَّهُمۡ قَوۡمٞ لَّا يَعۡقِلُونَ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (728,6,'118',5,'Al-Mā’idah','المَائدة',2,3,61,'۞ قُلۡ يَٰٓأَهۡلَ اَ۬لۡكِتَٰبِ هَلۡ تَنقِمُونَ مِنَّآ إِلَّآ أَنۡ ءَامَنَّا بِاللَّهِ وَمَآ أُنزِلَ إِلَيۡنَا وَمَآ أُنزِلَ مِن قَبۡلُ وَأَنَّ أَكۡثَرَكُمۡ فَٰسِقُونَ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (729,6,'118',5,'Al-Mā’idah','المَائدة',4,6,62,'قُلۡ هَلۡ أُنَبِّئُكُم بِشَرّٖ مِّن ذَٰلِكَ مَثُوبَةً عِندَ اَ۬للَّهِۚ مَن لَّعَنَهُ اُ۬للَّهُ وَغَضِبَ عَلَيۡهِ وَجَعَلَ مِنۡهُمُ اُ۬لۡقِرَدَةَ وَاَلۡخَنَازِيرَ وَعَبَدَ اَ۬لطَّٰغُوتَۚ أُوْلَٰٓئِكَ شَرّٞ مَّكَانٗا وَأَضَلُّ عَن سَوَآءِ اِ۬لسَّبِيلِ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (730,6,'118',5,'Al-Mā’idah','المَائدة',6,7,63,'وَإِذَا جَآءُوكُمۡ قَالُوٓاْ ءَامَنَّا وَقَد دَّخَلُواْ بِالۡكُفۡرِ وَهُمۡ قَدۡ خَرَجُواْ بِهِۦۚ وَاَللَّهُ أَعۡلَمُ بِمَا كَانُواْ يَكۡتُمُونَ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (731,6,'118',5,'Al-Mā’idah','المَائدة',8,9,64,'وَتَر۪يٰ كَثِيرٗا مِّنۡهُمۡ يُسَٰرِعُونَ فِي اِ۬لۡإِثۡمِ وَاَلۡعُدۡوَٰنِ وَأَكۡلِهِمِ اِ۬لسُّحُتَۚ لَبِئۡسَ مَا كَانُواْ يَعۡمَلُونَ ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (732,6,'118',5,'Al-Mā’idah','المَائدة',9,11,65,'لَوۡلَا يَنۡهَىٰهُمُ اُ۬لرَّبَّٰنِيُّونَ وَاَلۡأَحۡبَارُ عَن قَوۡلِهِمِ اِ۬لۡإِثۡمَ وَأَكۡلِهِمِ اِ۬لسُّحُتَۚ لَبِئۡسَ مَا كَانُواْ يَصۡنَعُونَ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (733,6,'118',5,'Al-Mā’idah','المَائدة',11,15,66,'وَقَالَتِ اِ۬لۡيَهُودُ يَدُ اُ۬للَّهِ مَغۡلُولَةٌۚ غُلَّتۡ أَيۡدِيهِمۡ وَلُعِنُواْ بِمَا قَالُواْۘ بَلۡ يَدَاهُ مَبۡسُوطَتَانِ يُنفِقُ كَيۡفَ يَشَآءُۚ وَلَيَزِيدَنَّ كَثِيرٗا مِّنۡهُم مَّآ أُنزِلَ إِلَيۡكَ مِن رَّبِّكَ طُغۡيَٰنٗا وَكُفۡرٗاۚ وَأَلۡقَيۡنَا بَيۡنَهُمُ اُ۬لۡعَدَٰوَةَ وَاَلۡبَغۡضَآءَ ا۪لَىٰ يَوۡمِ اِ۬لۡقِيَٰمَةِۚ كُلَّمَآ أَوۡقَدُواْ نَارٗا لِّلۡحَرۡبِ أَطۡفَأَهَا اَ۬للَّهُۚ وَيَسۡعَوۡنَ فِي اِ۬لۡأَرۡضِ فَسَادٗاۚ وَاَللَّهُ لَا يُحِبُّ اُ۬لۡمُفۡسِدِينَ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (734,6,'119',5,'Al-Mā’idah','المَائدة',1,2,67,'وَلَوۡ أَنَّ أَهۡلَ اَ۬لۡكِتَٰبِ ءَامَنُواْ وَاَتَّقَوۡاْ لَكَفَّرۡنَا عَنۡهُمۡ سَيِّـَٔاتِهِمۡ وَلَأَدۡخَلۡنَٰهُمۡ جَنَّٰتِ اِ۬لنَّعِيمِ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (735,6,'119',5,'Al-Mā’idah','المَائدة',2,5,68,'وَلَوۡ أَنَّهُمۡ أَقَامُواْ اُ۬لتَّوۡر۪ىٰةَ وَاَلۡإِنجِيلَ وَمَآ أُنزِلَ إِلَيۡهِم مِّن رَّبِّهِمۡ لَأَكَلُواْ مِن فَوۡقِهِمۡ وَمِن تَحۡتِ أَرۡجُلِهِمۚ مِّنۡهُمۡ أُمَّةٞ مُّقۡتَصِدَةٞۖ وَكَثِيرٞ مِّنۡهُمۡ سَآءَ مَا يَعۡمَلُونَ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (736,6,'119',5,'Al-Mā’idah','المَائدة',5,8,69,'۞ يَٰٓأَيُّهَا اَ۬لرَّسُولُ بَلِّغۡ مَآ أُنزِلَ إِلَيۡكَ مِن رَّبِّكَۖ وَإِن لَّمۡ تَفۡعَلۡ فَمَا بَلَّغۡتَ رِسَالَتَهُۥۚ وَاَللَّهُ يَعۡصِمُكَ مِنَ اَ۬لنّ۪اسِۗ إِنَّ اَ۬للَّهَ لَا يَهۡدِي اِ۬لۡقَوۡمَ اَ۬لۡكٰ۪فِرِينَ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (737,6,'119',5,'Al-Mā’idah','المَائدة',8,11,70,'قُلۡ يَٰٓأَهۡلَ اَ۬لۡكِتَٰبِ لَسۡتُمۡ عَلَىٰ شَيۡءٍ حَتَّىٰ تُقِيمُواْ اُ۬لتَّوۡر۪ىٰةَ وَاَلۡإِنجِيلَ وَمَآ أُنزِلَ إِلَيۡكُم مِّن رَّبِّكُمۡۗ وَلَيَزِيدَنَّ كَثِيرٗا مِّنۡهُم مَّآ أُنزِلَ إِلَيۡكَ مِن رَّبِّكَ طُغۡيَٰنٗا وَكُفۡرٗاۖ فَلَا تَأۡسَ عَلَى اَ۬لۡقَوۡمِ اِ۬لۡكٰ۪فِرِينَ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (738,6,'119',5,'Al-Mā’idah','المَائدة',11,13,71,'إِنَّ اَ۬لَّذِينَ ءَامَنُواْ وَاَلَّذِينَ هَادُواْ وَاَلصَّٰبِـُٔونَ وَاَلنَّصَٰر۪يٰ مَنۡ ءَامَنَ بِاللَّهِ وَاَلۡيَوۡمِ اِ۬لۡأٓخِرِ وَعَمِلَ صَٰلِحٗا فَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (739,6,'119',5,'Al-Mā’idah','المَائدة',13,15,72,'لَقَدۡ أَخَذۡنَا مِيثَٰقَ بَنِيٓ إِسۡرَٰٓءِيلَ وَأَرۡسَلۡنَآ إِلَيۡهِمۡ رُسُلٗاۖ كُلَّمَا جَآءَهُمۡ رَسُولُۢ بِمَا لَا تَهۡوَىٰٓ أَنفُسُهُمۡ فَرِيقٗا كَذَّبُواْ وَفَرِيقٗا يَقۡتُلُونَ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (740,6,'120',5,'Al-Mā’idah','المَائدة',1,2,73,'وَحَسِبُوٓاْ أَلَّا تَكُونُ فِتۡنَةٞ فَعَمُواْ وَصَمُّواْ ثُمَّ تَابَ اَ۬للَّهُ عَلَيۡهِمۡ ثُمَّ عَمُواْ وَصَمُّواْ كَثِيرٞ مِّنۡهُمۡۚ وَاَللَّهُ بَصِيرُۢ بِمَا يَعۡمَلُونَ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (741,6,'120',5,'Al-Mā’idah','المَائدة',3,6,74,'لَقَدۡ كَفَرَ اَ۬لَّذِينَ قَالُوٓاْ إِنَّ اَ۬للَّهَ هُوَ اَ۬لۡمَسِيحُ اُ۪بۡنُ مَرۡيَمَۖ وَقَالَ اَ۬لۡمَسِيحُ يَٰبَنِيٓ إِسۡرَٰٓءِيلَ اَ۟عۡبُدُواْ اُ۬للَّهَ رَبِّي وَرَبَّكُمۡۖ إِنَّهُۥ مَن يُشۡرِكۡ بِاللَّهِ فَقَدۡ حَرَّمَ اَ۬للَّهُ عَلَيۡهِ اِ۬لۡجَنَّةَ وَمَأۡوَىٰهُ اُ۬لنَّارُۖ وَمَا لِلظَّٰلِمِينَ مِنۡ أَنص۪ارٖ ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (742,6,'120',5,'Al-Mā’idah','المَائدة',6,8,75,'لَّقَدۡ كَفَرَ اَ۬لَّذِينَ قَالُوٓاْ إِنَّ اَ۬للَّهَ ثَالِثُ ثَلَٰثَةٖۘ وَمَا مِنۡ إِلَٰهٍ إِلَّآ إِلَٰهٞ وَٰحِدٞۚ وَإِن لَّمۡ يَنتَهُواْ عَمَّا يَقُولُونَ لَيَمَسَّنَّ اَ۬لَّذِينَ كَفَرُواْ مِنۡهُمۡ عَذَابٌ أَلِيمٌ ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (743,6,'120',5,'Al-Mā’idah','المَائدة',9,9,76,'أَفَلَا يَتُوبُونَ إِلَى اَ۬للَّهِ وَيَسۡتَغۡفِرُونَهُۥۚ وَاَللَّهُ غَفُورٞ رَّحِيمٞ ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (744,6,'120',5,'Al-Mā’idah','المَائدة',10,12,77,'۞ مَّا اَ۬لۡمَسِيحُ اُ۪بۡنُ مَرۡيَمَ إِلَّا رَسُولٞ قَدۡ خَلَتۡ مِن قَبۡلِهِ اِ۬لرُّسُلُ وَأُمُّهُۥ صِدِّيقَةٞۖ كَانَا يَأۡكُلَانِ اِ۬لطَّعَامَۗ اَ۟نظُرۡ كَيۡفَ نُبَيِّنُ لَهُمُ اُ۬لۡأٓيَٰتِ ثُمَّ اَ۟نظُرۡ أَنّۭيٰ يُؤۡفَكُونَ ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (745,6,'120',5,'Al-Mā’idah','المَائدة',12,13,78,'قُلۡ أَتَعۡبُدُونَ مِن دُونِ اِ۬للَّهِ مَا لَا يَمۡلِكُ لَكُمۡ ضَرّٗا وَلَا نَفۡعٗاۚ وَاَللَّهُ هُوَ اَ۬لسَّمِيعُ اُ۬لۡعَلِيمُ ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (746,6,'120',5,'Al-Mā’idah','المَائدة',13,15,79,'قُلۡ يَٰٓأَهۡلَ اَ۬لۡكِتَٰبِ لَا تَغۡلُواْ فِي دِينِكُمۡ غَيۡرَ اَ۬لۡحَقِّ وَلَا تَتَّبِعُوٓاْ أَهۡوَآءَ قَوۡمٖ قَد ضَّلُّواْ مِن قَبۡلُ وَأَضَلُّواْ كَثِيرٗا وَضَلُّواْ عَن سَوَآءِ اِ۬لسَّبِيلِ ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (747,6,'121',5,'Al-Mā’idah','المَائدة',1,3,80,'لُعِنَ اَ۬لَّذِينَ كَفَرُواْ مِنۢ بَنِيٓ إِسۡرَٰٓءِيلَ عَلَىٰ لِسَانِ دَاوُۥدَ وَعِيسَى اَ۪بۡنِ مَرۡيَمَۚ ذَٰلِكَ بِمَا عَصَواْ وَّكَانُواْ يَعۡتَدُونَ ٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (748,6,'121',5,'Al-Mā’idah','المَائدة',3,4,81,'كَانُواْ لَا يَتَنَاهَوۡنَ عَن مُّنكَرٖ فَعَلُوهُۚ لَبِئۡسَ مَا كَانُواْ يَفۡعَلُونَ ٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (749,6,'121',5,'Al-Mā’idah','المَائدة',4,7,82,'تَر۪يٰ كَثِيرٗا مِّنۡهُمۡ يَتَوَلَّوۡنَ اَ۬لَّذِينَ كَفَرُواْۚ لَبِئۡسَ مَا قَدَّمَتۡ لَهُمۡ أَنفُسُهُمۡ أَن سَخِطَ اَ۬للَّهُ عَلَيۡهِمۡ وَفِي اِ۬لۡعَذَابِ هُمۡ خَٰلِدُونَ ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (750,6,'121',5,'Al-Mā’idah','المَائدة',7,9,83,'وَلَوۡ كَانُواْ يُؤۡمِنُونَ بِاللَّهِ وَاَلنَّبِيِّ وَمَآ أُنزِلَ إِلَيۡهِ مَا اَ۪تَّخَذُوهُمۡ أَوۡلِيَآءَ وَلَٰكِنَّ كَثِيرٗا مِّنۡهُمۡ فَٰسِقُونَ ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (751,6,'121',5,'Al-Mā’idah','المَائدة',9,13,84,'لَتَجِدَنَّ أَشَدَّ اَ۬لنّ۪اسِ عَدَٰوَةٗ لِّلَّذِينَ ءَامَنُواْ اُ۬لۡيَهُودَ وَاَلَّذِينَ أَشۡرَكُواْۖ وَلَتَجِدَنَّ أَقۡرَبَهُم مَّوَدَّةٗ لِّلَّذِينَ ءَامَنُواْ اُ۬لَّذِينَ قَالُوٓاْ إِنَّا نَصَٰر۪يٰۚ ذَٰلِكَ بِأَنَّ مِنۡهُمۡ قِسِّيسِينَ وَرُهۡبَانٗا وَأَنَّهُمۡ لَا يَسۡتَكۡبِرُونَ ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (752,7,'121',5,'Al-Mā’idah','المَائدة',13,15,85,'۞ وَإِذَا سَمِعُواْ مَآ أُنزِلَ إِلَى اَ۬لرَّسُولِ تَر۪يٰٓ أَعۡيُنَهُمۡ تَفِيضُ مِنَ اَ۬لدَّمۡعِ مِمَّا عَرَفُواْ مِنَ اَ۬لۡحَقِّۖ يَقُولُونَ رَبَّنَآ ءَامَنَّا فَاَكۡتُبۡنَا مَعَ اَ۬لشَّٰهِدِينَ ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (753,7,'122',5,'Al-Mā’idah','المَائدة',1,2,86,'وَمَا لَنَا لَا نُؤۡمِنُ بِاللَّهِ وَمَا جَآءَنَا مِنَ اَ۬لۡحَقِّ وَنَطۡمَعُ أَن يُدۡخِلَنَا رَبُّنَا مَعَ اَ۬لۡقَوۡمِ اِ۬لصَّٰلِحِينَ ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (754,7,'122',5,'Al-Mā’idah','المَائدة',2,4,87,'فَأَثَٰبَهُمُ اُ۬للَّهُ بِمَا قَالُواْ جَنَّٰتٖ تَجۡرِي مِن تَحۡتِهَا اَ۬لۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ وَذَٰلِكَ جَزَآءُ اُ۬لۡمُحۡسِنِينَ ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (755,7,'122',5,'Al-Mā’idah','المَائدة',4,5,88,'وَاَلَّذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَآ أُوْلَٰٓئِكَ أَصۡحَٰبُ اُ۬لۡجَحِيمِ ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (756,7,'122',5,'Al-Mā’idah','المَائدة',5,7,89,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ لَا تُحَرِّمُواْ طَيِّبَٰتِ مَآ أَحَلَّ اَ۬للَّهُ لَكُمۡ وَلَا تَعۡتَدُوٓاْۚ إِنَّ اَ۬للَّهَ لَا يُحِبُّ اُ۬لۡمُعۡتَدِينَ ٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (757,7,'122',5,'Al-Mā’idah','المَائدة',7,8,90,'وَكُلُواْ مِمَّا رَزَقَكُمُ اُ۬للَّهُ حَلَٰلٗا طَيِّبٗاۚ وَاَتَّقُواْ اُ۬للَّهَ اَ۬لَّذِيٓ أَنتُم بِهِۦ مُؤۡمِنُونَ ٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (758,7,'122',5,'Al-Mā’idah','المَائدة',8,13,91,'لَا يُؤَاخِذُكُمُ اُ۬للَّهُ بِاللَّغۡوِ فِيٓ أَيۡمَٰنِكُمۡ وَلَٰكِن يُؤَاخِذُكُم بِمَا عَقَّدتُّمُ اُ۬لۡأَيۡمَٰنَۖ فَكَفَّٰرَتُهُۥٓ إِطۡعَامُ عَشَرَةِ مَسَٰكِينَ مِنۡ أَوۡسَطِ مَا تُطۡعِمُونَ أَهۡلِيكُمۡ أَوۡ كِسۡوَتُهُمۡ أَوۡ تَحۡرِيرُ رَقَبَةٖۖ فَمَن لَّمۡ يَجِدۡ فَصِيَامُ ثَلَٰثَةِ أَيَّامٖۚ ذَٰلِكَ كَفَّٰرَةُ أَيۡمَٰنِكُمۡ إِذَا حَلَفۡتُمۡۚ وَاَحۡفَظُوٓاْ أَيۡمَٰنَكُمۡۚ كَذَٰلِكَ يُبَيِّنُ اُ۬للَّهُ لَكُمۡ ءَايَٰتِهِۦ لَعَلَّكُمۡ تَشۡكُرُونَ ٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (759,7,'122',5,'Al-Mā’idah','المَائدة',14,15,92,'۞ يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُوٓاْ إِنَّمَا اَ۬لۡخَمۡرُ وَاَلۡمَيۡسِرُ وَاَلۡأَنصَابُ وَاَلۡأَزۡلَٰمُ رِجۡسٞ مِّنۡ عَمَلِ اِ۬لشَّيۡطَٰنِ فَاَجۡتَنِبُوهُ لَعَلَّكُمۡ تُفۡلِحُونَ ٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (760,7,'123',5,'Al-Mā’idah','المَائدة',1,3,93,'إِنَّمَا يُرِيدُ اُ۬لشَّيۡطَٰنُ أَن يُوقِعَ بَيۡنَكُمُ اُ۬لۡعَدَٰوَةَ وَاَلۡبَغۡضَآءَ فِي اِ۬لۡخَمۡرِ وَاَلۡمَيۡسِرِ وَيَصُدَّكُمۡ عَن ذِكۡرِ اِ۬للَّهِ وَعَنِ اِ۬لصَّلَوٰةِۖ فَهَلۡ أَنتُم مُّنتَهُونَ ٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (761,7,'123',5,'Al-Mā’idah','المَائدة',3,5,94,'وَأَطِيعُواْ اُ۬للَّهَ وَأَطِيعُواْ اُ۬لرَّسُولَ وَاَحۡذَرُواْۚ فَإِن تَوَلَّيۡتُمۡ فَاَعۡلَمُوٓاْ أَنَّمَا عَلَىٰ رَسُولِنَا اَ۬لۡبَلَٰغُ اُ۬لۡمُبِينُ ٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (762,7,'123',5,'Al-Mā’idah','المَائدة',5,8,95,'لَيۡسَ عَلَى اَ۬لَّذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ جُنَاحٞ فِيمَا طَعِمُوٓاْ إِذَا مَا اَ۪تَّقَواْ وَّءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ ثُمَّ اَ۪تَّقَواْ وَّءَامَنُواْ ثُمَّ اَ۪تَّقَواْ وَّأَحۡسَنُواْۗ وَاَللَّهُ يُحِبُّ اُ۬لۡمُحۡسِنِينَ ٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (763,7,'123',5,'Al-Mā’idah','المَائدة',8,10,96,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ لَيَبۡلُوَنَّكُمُ اُ۬للَّهُ بِشَيۡءٖ مِّنَ اَ۬لصَّيۡدِ تَنَالُهُۥٓ أَيۡدِيكُمۡ وَرِمَاحُكُمۡ لِيَعۡلَمَ اَ۬للَّهُ مَن يَخَافُهُۥ بِالۡغَيۡبِۚ فَمَنِ اِ۪عۡتَدَىٰ بَعۡدَ ذَٰلِكَ فَلَهُۥ عَذَابٌ أَلِيمٞ ٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (764,7,'123',5,'Al-Mā’idah','المَائدة',10,15,97,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ لَا تَقۡتُلُواْ اُ۬لصَّيۡدَ وَأَنتُمۡ حُرُمٞۚ وَمَن قَتَلَهُۥ مِنكُم مُّتَعَمِّدٗا فَجَزَآءُ مِثۡلِ مَا قَتَلَ مِنَ اَ۬لنَّعَمِ يَحۡكُمُ بِهِۦ ذَوَا عَدۡلٖ مِّنكُمۡ هَدۡيَۢا بَٰلِغَ اَ۬لۡكَعۡبَةِ أَوۡ كَفَّٰرَةٞ طَعَامُ مَسَٰكِينَ أَوۡ عَدۡلُ ذَٰلِكَ صِيَامٗا لِّيَذُوقَ وَبَالَ أَمۡرِهِۦۗ عَفَا اَ۬للَّهُ عَمَّا سَلَفَۚ وَمَنۡ عَادَ فَيَنتَقِمُ اُ۬للَّهُ مِنۡهُۗ وَاَللَّهُ عَزِيزٞ ذُو اُ۪نتِقَامٍ ٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (765,7,'124',5,'Al-Mā’idah','المَائدة',1,3,98,'أُحِلَّ لَكُمۡ صَيۡدُ اُ۬لۡبَحۡرِ وَطَعَامُهُۥ مَتَٰعٗا لَّكُمۡ وَلِلسَّيَّارَةِۖ وَحُرِّمَ عَلَيۡكُمۡ صَيۡدُ اُ۬لۡبَرِّ مَا دُمۡتُمۡ حُرُمٗاۗ وَاَتَّقُواْ اُ۬للَّهَ اَ۬لَّذِيٓ إِلَيۡهِ تُحۡشَرُونَ ٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (766,7,'124',5,'Al-Mā’idah','المَائدة',3,6,99,'۞ جَعَلَ اَ۬للَّهُ اُ۬لۡكَعۡبَةَ اَ۬لۡبَيۡتَ اَ۬لۡحَرَامَ قِيَٰمٗا لِّلنّ۪اسِ وَاَلشَّهۡرَ اَ۬لۡحَرَامَ وَاَلۡهَدۡيَ وَاَلۡقَلَٰٓئِدَۚ ذَٰلِكَ لِتَعۡلَمُوٓاْ أَنَّ اَ۬للَّهَ يَعۡلَمُ مَا فِي اِ۬لسَّمَٰوَٰتِ وَمَا فِي اِ۬لۡأَرۡضِ وَأَنَّ اَ۬للَّهَ بِكُلِّ شَيۡءٍ عَلِيمٌ ٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (767,7,'124',5,'Al-Mā’idah','المَائدة',6,7,100,'اِ۪عۡلَمُوٓاْ أَنَّ اَ۬للَّهَ شَدِيدُ اُ۬لۡعِقَابِ وَأَنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٞ ١٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (768,7,'124',5,'Al-Mā’idah','المَائدة',7,8,101,'مَّا عَلَى اَ۬لرَّسُولِ إِلَّا اَ۬لۡبَلَٰغُۗ وَاَللَّهُ يَعۡلَمُ مَا تُبۡدُونَ وَمَا تَكۡتُمُونَ ١٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (769,7,'124',5,'Al-Mā’idah','المَائدة',8,10,102,'قُل لَّا يَسۡتَوِي اِ۬لۡخَبِيثُ وَاَلطَّيِّبُ وَلَوۡ أَعۡجَبَكَ كَثۡرَةُ اُ۬لۡخَبِيثِۚ فَاَتَّقُواْ اُ۬للَّهَ يَٰٓأُوْلِي اِ۬لۡأَلۡبَٰبِ لَعَلَّكُمۡ تُفۡلِحُونَ ١٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (770,7,'124',5,'Al-Mā’idah','المَائدة',10,12,103,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ لَا تَسۡـَٔلُواْ عَنۡ أَشۡيَآءَ ا۪ن تُبۡدَ لَكُمۡ تَسُؤۡكُمۡ وَإِن تَسۡـَٔلُواْ عَنۡهَا حِينَ يُنزَلُ اُ۬لۡقُرۡءَانُ تُبۡدَ لَكُمۡ عَفَا اَ۬للَّهُ عَنۡهَاۗ وَاَللَّهُ غَفُورٌ حَلِيمٞ ١٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (771,7,'124',5,'Al-Mā’idah','المَائدة',13,13,104,'قَد سَّأَلَهَا قَوۡمٞ مِّن قَبۡلِكُمۡ ثُمَّ أَصۡبَحُواْ بِهَا كٰ۪فِرِينَ ١٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (772,7,'124',5,'Al-Mā’idah','المَائدة',13,15,105,'مَا جَعَلَ اَ۬للَّهُ مِنۢ بَحِيرَةٖ وَلَا سَآئِبَةٖ وَلَا وَصِيلَةٖ وَلَا حَامٖ وَلَٰكِنَّ اَ۬لَّذِينَ كَفَرُواْ يَفۡتَرُونَ عَلَى اَ۬للَّهِ اِ۬لۡكَذِبَۖ وَأَكۡثَرُهُمۡ لَا يَعۡقِلُونَ ١٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (773,7,'125',5,'Al-Mā’idah','المَائدة',1,3,106,'وَإِذَا قِيلَ لَهُمۡ تَعَالَوۡاْ إِلَىٰ مَآ أَنزَلَ اَ۬للَّهُ وَإِلَى اَ۬لرَّسُولِ قَالُواْ حَسۡبُنَا مَا وَجَدۡنَا عَلَيۡهِ ءَابَآءَنَآۚ أَوَلَوۡ كَانَ ءَابَآؤُهُمۡ لَا يَعۡلَمُونَ شَيۡـٔٗا وَلَا يَهۡتَدُونَ ١٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (774,7,'125',5,'Al-Mā’idah','المَائدة',3,5,107,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ عَلَيۡكُمۡ أَنفُسَكُمۡۖ لَا يَضُرُّكُم مَّن ضَلَّ إِذَا اَ۪هۡتَدَيۡتُمۡۚ إِلَى اَ۬للَّهِ مَرۡجِعُكُمۡ جَمِيعٗا فَيُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ ١٠٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (775,7,'125',5,'Al-Mā’idah','المَائدة',5,10,108,'۞ يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ شَهَٰدَةُ بَيۡنِكُمۡ إِذَا حَضَرَ أَحَدَكُمُ اُ۬لۡمَوۡتُ حِينَ اَ۬لۡوَصِيَّةِ اِ۪ثۡنَانِ ذَوَا عَدۡلٖ مِّنكُمۡ أَوۡ ءَاخَرَانِ مِنۡ غَيۡرِكُمۡ إِنۡ أَنتُمۡ ضَرَبۡتُمۡ فِي اِ۬لۡأَرۡضِ فَأَصَٰبَتۡكُم مُّصِيبَةُ اُ۬لۡمَوۡتِۚ تَحۡبِسُونَهُمَا مِنۢ بَعۡدِ اِ۬لصَّلَوٰةِ فَيُقۡسِمَانِ بِاللَّهِ إِنِ اِ۪رۡتَبۡتُمۡ لَا نَشۡتَرِي بِهِۦ ثَمَنٗا وَلَوۡ كَانَ ذَا قُرۡبۭيٰ وَلَا نَكۡتُمُ شَهَٰدَةَ اَ۬للَّهِ إِنَّآ إِذٗا لَّمِنَ اَ۬لۡأٓثِمِينَ ١٠٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (776,7,'125',5,'Al-Mā’idah','المَائدة',10,13,109,'فَإِنۡ عُثِرَ عَلَىٰٓ أَنَّهُمَا اَ۪سۡتَحَقَّآ إِثۡمٗا فَـَٔاخَرَانِ يَقُومَانِ مَقَامَهُمَا مِنَ اَ۬لَّذِينَ اَ۟سۡتُحِقَّ عَلَيۡهِمِ اِ۬لۡأَوۡلَيَٰنِ فَيُقۡسِمَانِ بِاللَّهِ لَشَهَٰدَتُنَآ أَحَقُّ مِن شَهَٰدَتِهِمَا وَمَا اَ۪عۡتَدَيۡنَآ إِنَّآ إِذٗا لَّمِنَ اَ۬لظَّٰلِمِينَ ١٠٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (777,7,'125',5,'Al-Mā’idah','المَائدة',13,15,110,'ذَٰلِكَ أَدۡنَىٰٓ أَن يَأۡتُواْ بِالشَّهَٰدَةِ عَلَىٰ وَجۡهِهَآ أَوۡ يَخَافُوٓاْ أَن تُرَدَّ أَيۡمَٰنُۢ بَعۡدَ أَيۡمَٰنِهِمۡۗ وَاَتَّقُواْ اُ۬للَّهَ وَاَسۡمَعُواْۗ وَاَللَّهُ لَا يَهۡدِي اِ۬لۡقَوۡمَ اَ۬لۡفَٰسِقِينَ ١١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (778,7,'126',5,'Al-Mā’idah','المَائدة',1,2,111,'يَوۡمَ يَجۡمَعُ اُ۬للَّهُ اُ۬لرُّسُلَ فَيَقُولُ مَاذَآ أُجِبۡتُمۡۖ قَالُواْ لَا عِلۡمَ لَنَآۖ إِنَّكَ أَنتَ عَلَّٰمُ اُ۬لۡغُيُوبِ ١١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (779,7,'126',5,'Al-Mā’idah','المَائدة',2,10,112,'إِذۡ قَالَ اَ۬للَّهُ يَٰعِيسَى اَ۪بۡنَ مَرۡيَمَ اَ۟ذۡكُرۡ نِعۡمَتِي عَلَيۡكَ وَعَلَىٰ وَٰلِدَتِكَ إِذۡ أَيَّدتُّكَ بِرُوحِ اِ۬لۡقُدُسِ تُكَلِّمُ اُ۬لنَّاسَ فِي اِ۬لۡمَهۡدِ وَكَهۡلٗاۖ وَإِذۡ عَلَّمۡتُكَ اَ۬لۡكِتَٰبَ وَاَلۡحِكۡمَةَ وَاَلتَّوۡر۪ىٰةَ وَاَلۡإِنجِيلَۖ وَإِذ تَّخۡلُقُ مِنَ اَ۬لطِّينِ كَهَيۡـَٔةِ اِ۬لطَّيۡرِ بِإِذۡنِي فَتَنفُخُ فِيهَا فَتَكُونُ طَيۡرَۢا بِإِذۡنِيۖ وَتُبۡرِئُ اُ۬لۡأَكۡمَهَ وَاَلۡأَبۡرَصَ بِإِذۡنِيۖ وَإِذ تُّخۡرِجُ اُ۬لۡمَوۡتۭيٰ بِإِذۡنِيۖ وَإِذۡ كَفَفۡتُ بَنِيٓ إِسۡرَٰٓءِيلَ عَنكَ إِذ جِّئۡتَهُم بِالۡبَيِّنَٰتِ فَقَالَ اَ۬لَّذِينَ كَفَرُواْ مِنۡهُمۡ إِنۡ هَٰذَآ إِلَّا سِحۡرٞ مُّبِينٞ ١١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (780,7,'126',5,'Al-Mā’idah','المَائدة',10,11,113,'۞ وَإِذۡ أَوۡحَيۡتُ إِلَى اَ۬لۡحَوَارِيِّـۧنَ أَنۡ ءَامِنُواْ بِي وَبِرَسُولِي قَالُوٓاْ ءَامَنَّا وَاَشۡهَدۡ بِأَنَّنَا مُسۡلِمُونَ ١١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (781,7,'126',5,'Al-Mā’idah','المَائدة',12,14,114,'إِذۡ قَالَ اَ۬لۡحَوَارِيُّونَ يَٰعِيسَى اَ۪بۡنَ مَرۡيَمَ هَلۡ يَسۡتَطِيعُ رَبُّكَ أَن يُنزِلَ عَلَيۡنَا مَآئِدَةٗ مِّنَ اَ۬لسَّمَآءِۖ قَالَ اَ۪تَّقُواْ اُ۬للَّهَ إِن كُنتُم مُّؤۡمِنِينَ ١١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (782,7,'126',5,'Al-Mā’idah','المَائدة',14,15,115,'قَالُواْ نُرِيدُ أَن نَّأۡكُلَ مِنۡهَا وَتَطۡمَئِنَّ قُلُوبُنَا وَنَعۡلَمَ أَن قَد صَّدَقۡتَنَا وَنَكُونَ عَلَيۡهَا مِنَ اَ۬لشَّٰهِدِينَ ١١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (783,7,'127',5,'Al-Mā’idah','المَائدة',1,3,116,'قَالَ عِيسَى اَ۪بۡنُ مَرۡيَمَ اَ۬للَّهُمَّ رَبَّنَآ أَنزِلۡ عَلَيۡنَا مَآئِدَةٗ مِّنَ اَ۬لسَّمَآءِ تَكُونُ لَنَا عِيدٗا لِّأَوَّلِنَا وَءَاخِرِنَا وَءَايَةٗ مِّنكَۖ وَاَرۡزُقۡنَا وَأَنتَ خَيۡرُ اُ۬لرَّٰزِقِينَ ١١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (784,7,'127',5,'Al-Mā’idah','المَائدة',3,4,117,'قَالَ اَ۬للَّهُ إِنِّي مُنزِلُهَا عَلَيۡكُمۡۖ فَمَن يَكۡفُرۡ بَعۡدُ مِنكُمۡ فَإِنِّيٓ أُعَذِّبُهُۥ عَذَابٗا لَّآ أُعَذِّبُهُۥٓ أَحَدٗا مِّنَ اَ۬لۡعَٰلَمِينَ ١١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (785,7,'127',5,'Al-Mā’idah','المَائدة',5,8,118,'وَإِذۡ قَالَ اَ۬للَّهُ يَٰعِيسَى اَ۪بۡنَ مَرۡيَمَ ءَٰا۬نتَ قُلۡتَ لِلنّ۪اسِ اِ۪تَّخِذُونِي وَأُمِّيَ إِلَٰهَيۡنِ مِن دُونِ اِ۬للَّهِۖ قَالَ سُبۡحَٰنَكَ مَا يَكُونُ لِيَ أَنۡ أَقُولَ مَا لَيۡسَ لِي بِحَقٍّۚ إِن كُنتُ قُلۡتُهُۥ فَقَدۡ عَلِمۡتَهُۥۚ تَعۡلَمُ مَا فِي نَفۡسِي وَلَآ أَعۡلَمُ مَا فِي نَفۡسِكَۚ إِنَّكَ أَنتَ عَلَّٰمُ اُ۬لۡغُيُوبِ ١١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (786,7,'127',5,'Al-Mā’idah','المَائدة',8,11,119,'مَا قُلۡتُ لَهُمۡ إِلَّا مَآ أَمَرۡتَنِي بِهِۦٓ أَنِ اِ۟عۡبُدُواْ اُ۬للَّهَ رَبِّي وَرَبَّكُمۡۚ وَكُنتُ عَلَيۡهِمۡ شَهِيدٗا مَّا دُمۡتُ فِيهِمۡۖ فَلَمَّا تَوَفَّيۡتَنِي كُنتَ أَنتَ اَ۬لرَّقِيبَ عَلَيۡهِمۡۚ وَأَنتَ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدٌ ١١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (787,7,'127',5,'Al-Mā’idah','المَائدة',11,12,120,'إِن تُعَذِّبۡهُمۡ فَإِنَّهُمۡ عِبَادُكَۖ وَإِن تَغۡفِر لَّهُمۡ فَإِنَّكَ أَنتَ اَ۬لۡعَزِيزُ اُ۬لۡحَكِيمُ ١٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (788,7,'127',5,'Al-Mā’idah','المَائدة',12,14,121,'قَالَ اَ۬للَّهُ هَٰذَا يَوۡمُ يَنفَعُ اُ۬لصَّٰدِقِينَ صِدۡقُهُمۡۚ لَهُمۡ جَنَّٰتٞ تَجۡرِي مِن تَحۡتِهَا اَ۬لۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدٗاۖ رَّضِيَ اَ۬للَّهُ عَنۡهُمۡ وَرَضُواْ عَنۡهُۚ ذَٰلِكَ اَ۬لۡفَوۡزُ اُ۬لۡعَظِيمُ ١٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (789,7,'127',5,'Al-Mā’idah','المَائدة',14,15,122,'لِلَّهِ مُلۡكُ اُ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِ وَمَا فِيهِنَّۚ وَهۡوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرُۢ ١٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (790,7,'128',6,'Al-An‘ām','الأنعَام',3,4,1,'اِ۬لۡحَمۡدُ لِلَّهِ اِ۬لَّذِي خَلَقَ اَ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضَ وَجَعَلَ اَ۬لظُّلُمَٰتِ وَاَلنُّورَ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (791,7,'128',6,'Al-An‘ām','الأنعَام',4,4,2,'ثُمَّ اَ۬لَّذِينَ كَفَرُواْ بِرَبِّهِمۡ يَعۡدِلُونَ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (792,7,'128',6,'Al-An‘ām','الأنعَام',4,6,3,'هُوَ اَ۬لَّذِي خَلَقَكُم مِّن طِينٖ ثُمَّ قَضَىٰٓ أَجَلٗاۖ وَأَجَلٞ مُّسَمًّى عِندَهُۥۖ ثُمَّ أَنتُمۡ تَمۡتَرُونَ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (793,7,'128',6,'Al-An‘ām','الأنعَام',6,7,4,'وَهۡوَ اَ۬للَّهُ فِي اِ۬لسَّمَٰوَٰتِ وَفِي اِ۬لۡأَرۡضِ يَعۡلَمُ سِرَّكُمۡ وَجَهۡرَكُمۡ وَيَعۡلَمُ مَا تَكۡسِبُونَ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (794,7,'128',6,'Al-An‘ām','الأنعَام',7,8,5,'وَمَا تَأۡتِيهِم مِّنۡ ءَايَةٖ مِّنۡ ءَايَٰتِ رَبِّهِمۡ إِلَّا كَانُواْ عَنۡهَا مُعۡرِضِينَ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (795,7,'128',6,'Al-An‘ām','الأنعَام',8,9,6,'فَقَدۡ كَذَّبُواْ بِالۡحَقِّ لَمَّا جَآءَهُمۡۖ فَسَوۡفَ يَأۡتِيهِمۡ أَنۢبَٰٓؤُاْ مَا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (796,7,'128',6,'Al-An‘ām','الأنعَام',10,13,7,'أَلَمۡ يَرَوۡاْ كَمۡ أَهۡلَكۡنَا مِن قَبۡلِهِم مِّن قَرۡنٖ مَّكَّنَّٰهُمۡ فِي اِ۬لۡأَرۡضِ مَا لَمۡ نُمَكِّن لَّكُمۡ وَأَرۡسَلۡنَا اَ۬لسَّمَآءَ عَلَيۡهِم مِّدۡرَارٗا وَجَعَلۡنَا اَ۬لۡأَنۡهَٰرَ تَجۡرِي مِن تَحۡتِهِمۡ فَأَهۡلَكۡنَٰهُم بِذُنُوبِهِمۡ وَأَنشَأۡنَا مِنۢ بَعۡدِهِمۡ قَرۡنًا ءَاخَرِينَ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (797,7,'128',6,'Al-An‘ām','الأنعَام',13,14,8,'وَلَوۡ نَزَّلۡنَا عَلَيۡكَ كِتَٰبٗا فِي قِرۡطَاسٖ فَلَمَسُوهُ بِأَيۡدِيهِمۡ لَقَالَ اَ۬لَّذِينَ كَفَرُوٓاْ إِنۡ هَٰذَآ إِلَّا سِحۡرٞ مُّبِينٞ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (798,7,'128',6,'Al-An‘ām','الأنعَام',14,15,9,'وَقَالُواْ لَوۡلَآ أُنزِلَ عَلَيۡهِ مَلَكٞۖ وَلَوۡ أَنزَلۡنَا مَلَكٗا لَّقُضِيَ اَ۬لۡأَمۡرُ ثُمَّ لَا يُنظَرُونَ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (799,7,'129',6,'Al-An‘ām','الأنعَام',1,2,10,'وَلَوۡ جَعَلۡنَٰهُ مَلَكٗا لَّجَعَلۡنَٰهُ رَجُلٗا وَلَلَبَسۡنَا عَلَيۡهِم مَّا يَلۡبِسُونَ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (800,7,'129',6,'Al-An‘ām','الأنعَام',2,3,11,'وَلَقَدِ اِ۟سۡتُهۡزِئَ بِرُسُلٖ مِّن قَبۡلِكَ فَحَاقَ بِالَّذِينَ سَخِرُواْ مِنۡهُم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (801,7,'129',6,'Al-An‘ām','الأنعَام',3,4,12,'قُلۡ سِيرُواْ فِي اِ۬لۡأَرۡضِ ثُمَّ اَ۟نظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ اُ۬لۡمُكَذِّبِينَ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (802,7,'129',6,'Al-An‘ām','الأنعَام',5,7,13,'قُل لِّمَن مَّا فِي اِ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِۖ قُل لِّلَّهِۚ كَتَبَ عَلَىٰ نَفۡسِهِ اِ۬لرَّحۡمَةَۚ لَيَجۡمَعَنَّكُمۡ إِلَىٰ يَوۡمِ اِ۬لۡقِيَٰمَةِ لَا رَيۡبَ فِيهِۚ اِ۬لَّذِينَ خَسِرُوٓاْ أَنفُسَهُمۡ فَهُمۡ لَا يُؤۡمِنُونَ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (803,7,'129',6,'Al-An‘ām','الأنعَام',7,8,14,'وَلَهُۥ مَا سَكَنَ فِي اِ۬لَّيۡلِ وَاَلنَّه۪ارِۚ وَهۡوَ اَ۬لسَّمِيعُ اُ۬لۡعَلِيمُ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (804,7,'129',6,'Al-An‘ām','الأنعَام',8,11,15,'۞ قُلۡ أَغَيۡرَ اَ۬للَّهِ أَتَّخِذُ وَلِيّٗا فَاطِرِ اِ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِ وَهۡوَ يُطۡعِمُ وَلَا يُطۡعَمُۗ قُلۡ إِنِّيٓ أُمِرۡتُ أَنۡ أَكُونَ أَوَّلَ مَنۡ أَسۡلَمَۖ وَلَا تَكُونَنَّ مِنَ اَ۬لۡمُشۡرِكِينَ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (805,7,'129',6,'Al-An‘ām','الأنعَام',11,12,16,'قُلۡ إِنِّيَ أَخَافُ إِنۡ عَصَيۡتُ رَبِّي عَذَابَ يَوۡمٍ عَظِيمٖ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (806,7,'129',6,'Al-An‘ām','الأنعَام',12,13,17,'مَّن يُصۡرَفۡ عَنۡهُ يَوۡمَئِذٖ فَقَدۡ رَحِمَهُۥۚ وَذَٰلِكَ اَ۬لۡفَوۡزُ اُ۬لۡمُبِينُ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (807,7,'129',6,'Al-An‘ām','الأنعَام',13,14,18,'وَإِن يَمۡسَسۡكَ اَ۬للَّهُ بِضُرّٖ فَلَا كَاشِفَ لَهُۥٓ إِلَّا هُوَۖ وَإِن يَمۡسَسۡكَ بِخَيۡرٖ فَهۡوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (808,7,'129',6,'Al-An‘ām','الأنعَام',15,15,19,'وَهۡوَ اَ۬لۡقَاهِرُ فَوۡقَ عِبَادِهِۦۚ وَهۡوَ اَ۬لۡحَكِيمُ اُ۬لۡخَبِيرُ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (809,7,'130',6,'Al-An‘ām','الأنعَام',1,4,20,'قُلۡ أَيُّ شَيۡءٍ أَكۡبَرُ شَهَٰدَةٗۖ قُلِ اِ۬للَّهُۖ شَهِيدُۢ بَيۡنِي وَبَيۡنَكُمۡۚ وَأُوحِيَ إِلَيَّ هَٰذَا اَ۬لۡقُرۡءَانُ لِأُنذِرَكُم بِهِۦ وَمَنۢ بَلَغَۚ أَٰى۪نَّكُمۡ لَتَشۡهَدُونَ أَنَّ مَعَ اَ۬للَّهِ ءَالِهَةً أُخۡر۪يٰۚ قُل لَّآ أَشۡهَدُۚ قُلۡ إِنَّمَا هُوَ إِلَٰهٞ وَٰحِدٞ وَإِنَّنِي بَرِيٓءٞ مِّمَّا تُشۡرِكُونَ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (810,7,'130',6,'Al-An‘ām','الأنعَام',4,5,21,'اَ۬لَّذِينَ ءَاتَيۡنَٰهُمُ اُ۬لۡكِتَٰبَ يَعۡرِفُونَهُۥ كَمَا يَعۡرِفُونَ أَبۡنَآءَهُمُۘ اُ۬لَّذِينَ خَسِرُوٓاْ أَنفُسَهُمۡ فَهُمۡ لَا يُؤۡمِنُونَ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (811,7,'130',6,'Al-An‘ām','الأنعَام',5,6,22,'وَمَنۡ أَظۡلَمُ مِمَّنِ اِ۪فۡتَر۪يٰ عَلَى اَ۬للَّهِ كَذِبًا أَوۡ كَذَّبَ بِـَٔايَٰتِهِۦٓۚ إِنَّهُۥ لَا يُفۡلِحُ اُ۬لظَّٰلِمُونَ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (812,7,'130',6,'Al-An‘ām','الأنعَام',6,7,23,'وَيَوۡمَ نَحۡشُرُهُمۡ جَمِيعٗا ثُمَّ نَقُولُ لِلَّذِينَ أَشۡرَكُوٓاْ أَيۡنَ شُرَكَآؤُكُمُ اُ۬لَّذِينَ كُنتُمۡ تَزۡعُمُونَ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (813,7,'130',6,'Al-An‘ām','الأنعَام',8,8,24,'ثُمَّ لَمۡ تَكُن فِتۡنَتَهُمۡ إِلَّآ أَن قَالُواْ وَاَللَّهِ رَبِّنَا مَا كُنَّا مُشۡرِكِينَ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (814,7,'130',6,'Al-An‘ām','الأنعَام',9,9,25,'اَ۟نظُرۡ كَيۡفَ كَذَبُواْ عَلَىٰٓ أَنفُسِهِمۡۚ وَضَلَّ عَنۡهُم مَّا كَانُواْ يَفۡتَرُونَ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (815,7,'130',6,'Al-An‘ām','الأنعَام',10,13,26,'وَمِنۡهُم مَّن يَسۡتَمِعُ إِلَيۡكَۖ وَجَعَلۡنَا عَلَىٰ قُلُوبِهِمۡ أَكِنَّةً أَن يَفۡقَهُوهُ وَفِيٓ ءَاذَانِهِمۡ وَقۡرٗاۚ وَإِن يَرَوۡاْ كُلَّ ءَايَةٖ لَّا يُؤۡمِنُواْ بِهَاۚ حَتَّىٰٓ إِذَا جَآءُوكَ يُجَٰدِلُونَكَ يَقُولُ اُ۬لَّذِينَ كَفَرُوٓاْ إِنۡ هَٰذَآ إِلَّآ أَسَٰطِيرُ اُ۬لۡأَوَّلِينَ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (816,7,'130',6,'Al-An‘ām','الأنعَام',13,14,27,'۞ وَهُمۡ يَنۡهَوۡنَ عَنۡهُ وَيَنۡـَٔوۡنَ عَنۡهُۖ وَإِن يُهۡلِكُونَ إِلَّآ أَنفُسَهُمۡ وَمَا يَشۡعُرُونَ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (817,7,'130',6,'Al-An‘ām','الأنعَام',14,15,28,'وَلَوۡ تَر۪يٰٓ إِذۡ وُقِفُواْ عَلَى اَ۬لنّ۪ارِ فَقَالُواْ يَٰلَيۡتَنَا نُرَدُّ وَلَا نُكَذِّبُ بِـَٔايَٰتِ رَبِّنَا وَنَكُونُ مِنَ اَ۬لۡمُؤۡمِنِينَ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (818,7,'131',6,'Al-An‘ām','الأنعَام',1,2,29,'بَلۡ بَدَا لَهُم مَّا كَانُواْ يُخۡفُونَ مِن قَبۡلُۖ وَلَوۡ رُدُّواْ لَعَادُواْ لِمَا نُهُواْ عَنۡهُ وَإِنَّهُمۡ لَكَٰذِبُونَ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (819,7,'131',6,'Al-An‘ām','الأنعَام',2,3,30,'وَقَالُوٓاْ إِنۡ هِيَ إِلَّا حَيَاتُنَا اَ۬لدُّنۡيۭا وَمَا نَحۡنُ بِمَبۡعُوثِينَ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (820,7,'131',6,'Al-An‘ām','الأنعَام',3,4,31,'وَلَوۡ تَر۪يٰٓ إِذۡ وُقِفُواْ عَلَىٰ رَبِّهِمۡۚ قَالَ أَلَيۡسَ هَٰذَا بِالۡحَقِّۚ قَالُواْ بَلَىٰ وَرَبِّنَاۚ قَالَ فَذُوقُواْ اُ۬لۡعَذَابَ بِمَا كُنتُمۡ تَكۡفُرُونَ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (821,7,'131',6,'Al-An‘ām','الأنعَام',5,7,32,'قَدۡ خَسِرَ اَ۬لَّذِينَ كَذَّبُواْ بِلِقَآءِ اِ۬للَّهِۖ حَتَّىٰٓ إِذَا جَآءَتۡهُمُ اُ۬لسَّاعَةُ بَغۡتَةٗ قَالُواْ يَٰحَسۡرَتَنَا عَلَىٰ مَا فَرَّطۡنَا فِيهَا وَهُمۡ يَحۡمِلُونَ أَوۡزَارَهُمۡ عَلَىٰ ظُهُورِهِمۡۚ أَلَا سَآءَ مَا يَزِرُونَ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (822,7,'131',6,'Al-An‘ām','الأنعَام',7,8,33,'وَمَا اَ۬لۡحَيَوٰةُ اُ۬لدُّنۡيۭآ إِلَّا لَعِبٞ وَلَهۡوٞۖ وَلَلدَّارُ اُ۬لۡأٓخِرَةُ خَيۡرٞ لِّلَّذِينَ يَتَّقُونَۗ أَفَلَا يَعۡقِلُونَ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (823,7,'131',6,'Al-An‘ām','الأنعَام',9,10,34,'قَدۡ نَعۡلَمُ إِنَّهُۥ لَيَحۡزُنُكَ اَ۬لَّذِي يَقُولُونَۖ فَإِنَّهُمۡ لَا يُكَذِّبُونَكَ وَلَٰكِنَّ اَ۬لظَّٰلِمِينَ بِـَٔايَٰتِ اِ۬للَّهِ يَجۡحَدُونَ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (824,7,'131',6,'Al-An‘ām','الأنعَام',10,12,35,'وَلَقَدۡ كُذِّبَتۡ رُسُلٞ مِّن قَبۡلِكَ فَصَبَرُواْ عَلَىٰ مَا كُذِّبُواْ وَأُوذُواْ حَتَّىٰٓ أَتَىٰهُمۡ نَصۡرُنَاۚ وَلَا مُبَدِّلَ لِكَلِمَٰتِ اِ۬للَّهِۚ وَلَقَد جَّآءَكَ مِن نَّبَإِيْ اِ۬لۡمُرۡسَلِينَ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (825,7,'131',6,'Al-An‘ām','الأنعَام',13,15,36,'وَإِن كَانَ كَبُرَ عَلَيۡكَ إِعۡرَاضُهُمۡ فَإِنِ اِ۪سۡتَطَعۡتَ أَن تَبۡتَغِيَ نَفَقٗا فِي اِ۬لۡأَرۡضِ أَوۡ سُلَّمٗا فِي اِ۬لسَّمَآءِ فَتَأۡتِيَهُم بِـَٔايَةٖۚ وَلَوۡ شَآءَ اَ۬للَّهُ لَجَمَعَهُمۡ عَلَى اَ۬لۡهُدَىٰۚ فَلَا تَكُونَنَّ مِنَ اَ۬لۡجَٰهِلِينَ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (826,7,'132',6,'Al-An‘ām','الأنعَام',1,2,37,'۞ إِنَّمَا يَسۡتَجِيبُ اُ۬لَّذِينَ يَسۡمَعُونَۘ وَاَلۡمَوۡتۭيٰ يَبۡعَثُهُمُ اُ۬للَّهُ ثُمَّ إِلَيۡهِ يُرۡجَعُونَ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (827,7,'132',6,'Al-An‘ām','الأنعَام',2,3,38,'وَقَالُواْ لَوۡلَا نُزِّلَ عَلَيۡهِ ءَايَةٞ مِّن رَّبِّهِۦۚ قُلۡ إِنَّ اَ۬للَّهَ قَادِرٌ عَلَىٰٓ أَن يُنَزِّلَ ءَايَةٗ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (828,7,'132',6,'Al-An‘ām','الأنعَام',3,5,39,'وَمَا مِن دَآبَّةٖ فِي اِ۬لۡأَرۡضِ وَلَا طَٰٓئِرٖ يَطِيرُ بِجَنَاحَيۡهِ إِلَّآ أُمَمٌ أَمۡثَالُكُمۚ مَّا فَرَّطۡنَا فِي اِ۬لۡكِتَٰبِ مِن شَيۡءٖۚ ثُمَّ إِلَىٰ رَبِّهِمۡ يُحۡشَرُونَ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (829,7,'132',6,'Al-An‘ām','الأنعَام',6,7,40,'وَاَلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا صُمّٞ وَبُكۡمٞ فِي اِ۬لظُّلُمَٰتِۗ مَن يَشَإِ اِ۬للَّهُ يُضۡلِلۡهُ وَمَن يَشَأۡ يَجۡعَلۡهُ عَلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (830,7,'132',6,'Al-An‘ām','الأنعَام',7,9,41,'قُلۡ أَرَءَيۡتَكُمۡ إِنۡ أَتَىٰكُمۡ عَذَابُ اُ۬للَّهِ أَوۡ أَتَتۡكُمُ اُ۬لسَّاعَةُ أَغَيۡرَ اَ۬للَّهِ تَدۡعُونَ إِن كُنتُمۡ صَٰدِقِينَ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (831,7,'132',6,'Al-An‘ām','الأنعَام',9,10,42,'بَلۡ إِيَّاهُ تَدۡعُونَ فَيَكۡشِفُ مَا تَدۡعُونَ إِلَيۡهِ إِن شَآءَ وَتَنسَوۡنَ مَا تُشۡرِكُونَ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (832,7,'132',6,'Al-An‘ām','الأنعَام',10,12,43,'وَلَقَدۡ أَرۡسَلۡنَآ إِلَىٰٓ أُمَمٖ مِّن قَبۡلِكَ فَأَخَذۡنَٰهُم بِالۡبَأۡسَآءِ وَاَلضَّرَّآءِ لَعَلَّهُمۡ يَتَضَرَّعُونَ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (833,7,'132',6,'Al-An‘ām','الأنعَام',12,13,44,'فَلَوۡلَآ إِذ جَّآءَهُم بَأۡسُنَا تَضَرَّعُواْ وَلَٰكِن قَسَتۡ قُلُوبُهُمۡ وَزَيَّنَ لَهُمُ اُ۬لشَّيۡطَٰنُ مَا كَانُواْ يَعۡمَلُونَ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (834,7,'132',6,'Al-An‘ām','الأنعَام',13,15,45,'فَلَمَّا نَسُواْ مَا ذُكِّرُواْ بِهِۦ فَتَحۡنَا عَلَيۡهِمۡ أَبۡوَٰبَ كُلِّ شَيۡءٍ حَتَّىٰٓ إِذَا فَرِحُواْ بِمَآ أُوتُوٓاْ أَخَذۡنَٰهُم بَغۡتَةٗ فَإِذَا هُم مُّبۡلِسُونَ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (835,7,'133',6,'Al-An‘ām','الأنعَام',1,1,46,'فَقُطِعَ دَابِرُ اُ۬لۡقَوۡمِ اِ۬لَّذِينَ ظَلَمُواْۚ وَاَلۡحَمۡدُ لِلَّهِ رَبِّ اِ۬لۡعَٰلَمِينَ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (836,7,'133',6,'Al-An‘ām','الأنعَام',2,4,47,'قُلۡ أَرَءَيۡتُمۡ إِنۡ أَخَذَ اَ۬للَّهُ سَمۡعَكُمۡ وَأَبۡصَٰرَكُمۡ وَخَتَمَ عَلَىٰ قُلُوبِكُم مَّنۡ إِلَٰهٌ غَيۡرُ اُ۬للَّهِ يَأۡتِيكُم بِهِۗ اِ۟نظُرۡ كَيۡفَ نُصَرِّفُ اُ۬لۡأٓيَٰتِ ثُمَّ هُمۡ يَصۡدِفُونَ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (837,7,'133',6,'Al-An‘ām','الأنعَام',4,5,48,'قُلۡ أَرَءَيۡتَكُمۡ إِنۡ أَتَىٰكُمۡ عَذَابُ اُ۬للَّهِ بَغۡتَةً أَوۡ جَهۡرَةً هَلۡ يُهۡلَكُ إِلَّا اَ۬لۡقَوۡمُ اُ۬لظَّٰلِمُونَ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (838,7,'133',6,'Al-An‘ām','الأنعَام',5,7,49,'۞ وَمَا نُرۡسِلُ اُ۬لۡمُرۡسَلِينَ إِلَّا مُبَشِّرِينَ وَمُنذِرِينَۖ فَمَنۡ ءَامَنَ وَأَصۡلَحَ فَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (839,7,'133',6,'Al-An‘ām','الأنعَام',7,8,50,'وَاَلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا يَمَسُّهُمُ اُ۬لۡعَذَابُ بِمَا كَانُواْ يَفۡسُقُونَ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (840,7,'133',6,'Al-An‘ām','الأنعَام',8,11,51,'قُل لَّآ أَقُولُ لَكُمۡ عِندِي خَزَآئِنُ اُ۬للَّهِ وَلَآ أَعۡلَمُ اُ۬لۡغَيۡبَ وَلَآ أَقُولُ لَكُمۡ إِنِّي مَلَكٌۖ إِنۡ أَتَّبِعُ إِلَّا مَا يُوحَىٰٓ إِلَيَّۚ قُلۡ هَلۡ يَسۡتَوِي اِ۬لۡأَعۡمَىٰ وَاَلۡبَصِيرُۚ أَفَلَا تَتَفَكَّرُونَ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (841,7,'133',6,'Al-An‘ām','الأنعَام',11,12,52,'وَأَنذِرۡ بِهِ اِ۬لَّذِينَ يَخَافُونَ أَن يُحۡشَرُوٓاْ إِلَىٰ رَبِّهِمۡ لَيۡسَ لَهُم مِّن دُونِهِۦ وَلِيّٞ وَلَا شَفِيعٞ لَّعَلَّهُمۡ يَتَّقُونَ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (842,7,'133',6,'Al-An‘ām','الأنعَام',13,15,53,'وَلَا تَطۡرُدِ اِ۬لَّذِينَ يَدۡعُونَ رَبَّهُم بِالۡغَدَوٰةِ وَاَلۡعَشِيِّ يُرِيدُونَ وَجۡهَهُۥۖ مَا عَلَيۡكَ مِنۡ حِسَابِهِم مِّن شَيۡءٖ وَمَا مِنۡ حِسَابِكَ عَلَيۡهِم مِّن شَيۡءٖ فَتَطۡرُدَهُمۡ فَتَكُونَ مِنَ اَ۬لظَّٰلِمِينَ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (843,7,'134',6,'Al-An‘ām','الأنعَام',1,2,54,'وَكَذَٰلِكَ فَتَنَّا بَعۡضَهُم بِبَعۡضٖ لِّيَقُولُوٓاْ أَهَٰٓؤُلَآءِ مَنَّ اَ۬للَّهُ عَلَيۡهِم مِّنۢ بَيۡنِنَآۗ أَلَيۡسَ اَ۬للَّهُ بِأَعۡلَمَ بِالشَّٰكِرِينَ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (844,7,'134',6,'Al-An‘ām','الأنعَام',2,5,55,'وَإِذَا جَآءَكَ اَ۬لَّذِينَ يُؤۡمِنُونَ بِـَٔايَٰتِنَا فَقُلۡ سَلَٰمٌ عَلَيۡكُمۡۖ كَتَبَ رَبُّكُمۡ عَلَىٰ نَفۡسِهِ اِ۬لرَّحۡمَةَۖ إِنَّهُۥ مَنۡ عَمِلَ مِنكُمۡ سُوٓءَۢا بِجَهَٰلَةٖ ثُمَّ تَابَ مِنۢ بَعۡدِهِۦ وَأَصۡلَحَ فَإِنَّهُۥ غَفُورٞ رَّحِيمٞ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (845,7,'134',6,'Al-An‘ām','الأنعَام',6,6,56,'وَكَذَٰلِكَ نُفَصِّلُ اُ۬لۡأٓيَٰتِ وَلِتَسۡتَبِينَ سَبِيلُ اُ۬لۡمُجۡرِمِينَ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (846,7,'134',6,'Al-An‘ām','الأنعَام',7,8,57,'قُلۡ إِنِّي نُهِيتُ أَنۡ أَعۡبُدَ اَ۬لَّذِينَ تَدۡعُونَ مِن دُونِ اِ۬للَّهِۚ قُل لَّآ أَتَّبِعُ أَهۡوَآءَكُمۡ قَد ضَّلَلۡتُ إِذٗا وَمَآ أَنَا۠ مِنَ اَ۬لۡمُهۡتَدِينَ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (847,7,'134',6,'Al-An‘ām','الأنعَام',9,11,58,'قُلۡ إِنِّي عَلَىٰ بَيِّنَةٖ مِّن رَّبِّي وَكَذَّبۡتُم بِهِۦۚ مَا عِندِي مَا تَسۡتَعۡجِلُونَ بِهِۦٓۚ إِنِ اِ۬لۡحُكۡمُ إِلَّا لِلَّهِۖ يَقۡضِ اِ۬لۡحَقَّۖ وَهۡوَ خَيۡرُ اُ۬لۡفَٰصِلِينَ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (848,7,'134',6,'Al-An‘ām','الأنعَام',11,12,59,'قُل لَّوۡ أَنَّ عِندِي مَا تَسۡتَعۡجِلُونَ بِهِۦ لَقُضِيَ اَ۬لۡأَمۡرُ بَيۡنِي وَبَيۡنَكُمۡۗ وَاَللَّهُ أَعۡلَمُ بِالظَّٰلِمِينَ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (849,7,'134',6,'Al-An‘ām','الأنعَام',12,15,60,'۞ وَعِندَهُۥ مَفَاتِحُ اُ۬لۡغَيۡبِ لَا يَعۡلَمُهَآ إِلَّا هُوَۚ وَيَعۡلَمُ مَا فِي اِ۬لۡبَرِّ وَاَلۡبَحۡرِۚ وَمَا تَسۡقُطُ مِن وَرَقَةٍ إِلَّا يَعۡلَمُهَا وَلَا حَبَّةٖ فِي ظُلُمَٰتِ اِ۬لۡأَرۡضِ وَلَا رَطۡبٖ وَلَا يَابِسٍ إِلَّا فِي كِتَٰبٖ مُّبِينٖ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (850,7,'135',6,'Al-An‘ām','الأنعَام',1,3,61,'وَهۡوَ اَ۬لَّذِي يَتَوَفَّىٰكُم بِالَّيۡلِ وَيَعۡلَمُ مَا جَرَحۡتُم بِالنَّه۪ارِ ثُمَّ يَبۡعَثُكُمۡ فِيهِ لِيُقۡضَىٰٓ أَجَلٞ مُّسَمّٗىۖ ثُمَّ إِلَيۡهِ مَرۡجِعُكُمۡ ثُمَّ يُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (851,7,'135',6,'Al-An‘ām','الأنعَام',3,5,62,'وَهۡوَ اَ۬لۡقَاهِرُ فَوۡقَ عِبَادِهِۦۖ وَيُرۡسِلُ عَلَيۡكُمۡ حَفَظَةً حَتَّىٰٓ إِذَا جَآ أَحَدَكُمُ اُ۬لۡمَوۡتُ تَوَفَّتۡهُ رُسۡلُنَا وَهُمۡ لَا يُفَرِّطُونَ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (852,7,'135',6,'Al-An‘ām','الأنعَام',5,6,63,'ثُمَّ رُدُّوٓاْ إِلَى اَ۬للَّهِ مَوۡلَىٰهُمُ اُ۬لۡحَقِّۚ أَلَا لَهُ اُ۬لۡحُكۡمُ وَهۡوَ أَسۡرَعُ اُ۬لۡحَٰسِبِينَ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (853,7,'135',6,'Al-An‘ām','الأنعَام',6,8,64,'قُلۡ مَن يُنَجِّيكُم مِّن ظُلُمَٰتِ اِ۬لۡبَرِّ وَاَلۡبَحۡرِ تَدۡعُونَهُۥ تَضَرُّعٗا وَخُفۡيَةٗ لَّئِنۡ أَنجَيۡتَنَا مِنۡ هَٰذِهِۦ لَنَكُونَنَّ مِنَ اَ۬لشَّٰكِرِينَ ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (854,7,'135',6,'Al-An‘ām','الأنعَام',8,9,65,'قُلِ اِ۬للَّهُ يُنجِيكُم مِّنۡهَا وَمِن كُلِّ كَرۡبٖ ثُمَّ أَنتُمۡ تُشۡرِكُونَ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (855,7,'135',6,'Al-An‘ām','الأنعَام',9,11,66,'قُلۡ هُوَ اَ۬لۡقَادِرُ عَلَىٰٓ أَن يَبۡعَثَ عَلَيۡكُمۡ عَذَابٗا مِّن فَوۡقِكُمۡ أَوۡ مِن تَحۡتِ أَرۡجُلِكُمۡ أَوۡ يَلۡبِسَكُمۡ شِيَعٗا وَيُذِيقَ بَعۡضَكُم بَأۡسَ بَعۡضٍۗ اِ۟نظُرۡ كَيۡفَ نُصَرِّفُ اُ۬لۡأٓيَٰتِ لَعَلَّهُمۡ يَفۡقَهُونَ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (856,7,'135',6,'Al-An‘ām','الأنعَام',11,13,67,'وَكَذَّبَ بِهِۦ قَوۡمُكَ وَهۡوَ اَ۬لۡحَقُّۚ قُل لَّسۡتُ عَلَيۡكُم بِوَكِيلٖۚ لِّكُلِّ نَبَإٖ مُّسۡتَقَرّٞۚ وَسَوۡفَ تَعۡلَمُونَ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (857,7,'135',6,'Al-An‘ām','الأنعَام',13,15,68,'۞ وَإِذَا رَأَيۡتَ اَ۬لَّذِينَ يَخُوضُونَ فِيٓ ءَايَٰتِنَا فَأَعۡرِضۡ عَنۡهُمۡ حَتَّىٰ يَخُوضُواْ فِي حَدِيثٍ غَيۡرِهِۦۚ وَإِمَّا يُنسِيَنَّكَ اَ۬لشَّيۡطَٰنُ فَلَا تَقۡعُدۡ بَعۡدَ اَ۬لذِّكۡر۪يٰ مَعَ اَ۬لۡقَوۡمِ اِ۬لظَّٰلِمِينَ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (858,7,'136',6,'Al-An‘ām','الأنعَام',1,2,69,'وَمَا عَلَى اَ۬لَّذِينَ يَتَّقُونَ مِنۡ حِسَابِهِم مِّن شَيۡءٖ وَلَٰكِن ذِكۡر۪يٰ لَعَلَّهُمۡ يَتَّقُونَ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (859,7,'136',6,'Al-An‘ām','الأنعَام',2,7,70,'وَذَرِ اِ۬لَّذِينَ اَ۪تَّخَذُواْ دِينَهُمۡ لَعِبٗا وَلَهۡوٗا وَغَرَّتۡهُمُ اُ۬لۡحَيَوٰةُ اُ۬لدُّنۡيۭاۚ وَذَكِّرۡ بِهِۦٓ أَن تُبۡسَلَ نَفۡسُۢ بِمَا كَسَبَتۡ لَيۡسَ لَهَا مِن دُونِ اِ۬للَّهِ وَلِيّٞ وَلَا شَفِيعٞ وَإِن تَعۡدِلۡ كُلَّ عَدۡلٖ لَّا يُؤۡخَذۡ مِنۡهَآۗ أُوْلَٰٓئِكَ اَ۬لَّذِينَ أُبۡسِلُواْ بِمَا كَسَبُواْۖ لَهُمۡ شَرَابٞ مِّنۡ حَمِيمٖ وَعَذَابٌ أَلِيمُۢ بِمَا كَانُواْ يَكۡفُرُونَ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (860,7,'136',6,'Al-An‘ām','الأنعَام',7,11,71,'قُلۡ أَنَدۡعُواْ مِن دُونِ اِ۬للَّهِ مَا لَا يَنفَعُنَا وَلَا يَضُرُّنَا وَنُرَدُّ عَلَىٰٓ أَعۡقَابِنَا بَعۡدَ إِذۡ هَدَىٰنَا اَ۬للَّهُ كَاَلَّذِي اِ۪سۡتَهۡوَتۡهُ اُ۬لشَّيَٰطِينُ فِي اِ۬لۡأَرۡضِ حَيۡرَانَ لَهُۥٓ أَصۡحَٰبٞ يَدۡعُونَهُۥٓ إِلَى اَ۬لۡهُدَى اَ۪ئۡتِنَاۗ قُلۡ إِنَّ هُدَى اَ۬للَّهِ هُوَ اَ۬لۡهُدَىٰۖ وَأُمِرۡنَا لِنُسۡلِمَ لِرَبِّ اِ۬لۡعَٰلَمِينَ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (861,7,'136',6,'Al-An‘ām','الأنعَام',11,12,72,'وَأَنۡ أَقِيمُواْ اُ۬لصَّلَوٰةَ وَاَتَّقُوهُۚ وَهۡوَ اَ۬لَّذِيٓ إِلَيۡهِ تُحۡشَرُونَ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (862,7,'136',6,'Al-An‘ām','الأنعَام',12,14,73,'وَهۡوَ اَ۬لَّذِي خَلَقَ اَ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضَ بِالۡحَقِّۖ وَيَوۡمَ يَقُولُ كُن فَيَكُونُ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (863,7,'136',6,'Al-An‘ām','الأنعَام',14,15,74,'قَوۡلُهُ اُ۬لۡحَقُّۚ وَلَهُ اُ۬لۡمُلۡكُ يَوۡمَ يُنفَخُ فِي اِ۬لصُّورِۚ عَٰلِمُ اُ۬لۡغَيۡبِ وَاَلشَّهَٰدَةِۚ وَهۡوَ اَ۬لۡحَكِيمُ اُ۬لۡخَبِيرُ ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (864,7,'137',6,'Al-An‘ām','الأنعَام',1,2,75,'۞ وَإِذۡ قَالَ إِبۡرَٰهِيمُ لِأَبِيهِ ءَازَرَ أَتَتَّخِذُ أَصۡنَامًا ءَالِهَةًۖ إِنِّيَ أَر۪ىٰكَ وَقَوۡمَكَ فِي ضَلَٰلٖ مُّبِينٖ ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (865,7,'137',6,'Al-An‘ām','الأنعَام',2,3,76,'وَكَذَٰلِكَ نُرِيٓ إِبۡرَٰهِيمَ مَلَكُوتَ اَ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِ وَلِيَكُونَ مِنَ اَ۬لۡمُوقِنِينَ ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (866,7,'137',6,'Al-An‘ām','الأنعَام',4,5,77,'فَلَمَّا جَنَّ عَلَيۡهِ اِ۬لَّيۡلُ رَء۪ا كَوۡكَبٗاۖ قَالَ هَٰذَا رَبِّيۖ فَلَمَّآ أَفَلَ قَالَ لَآ أُحِبُّ اُ۬لۡأٓفِلِينَ ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (867,7,'137',6,'Al-An‘ām','الأنعَام',5,7,78,'فَلَمَّا رَءَا اَ۬لۡقَمَرَ بَازِغٗا قَالَ هَٰذَا رَبِّيۖ فَلَمَّآ أَفَلَ قَالَ لَئِن لَّمۡ يَهۡدِنِي رَبِّي لَأَكُونَنَّ مِنَ اَ۬لۡقَوۡمِ اِ۬لضَّآلِّينَ ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (868,7,'137',6,'Al-An‘ām','الأنعَام',7,8,79,'فَلَمَّا رَءَا اَ۬لشَّمۡسَ بَازِغَةٗ قَالَ هَٰذَا رَبِّي هَٰذَآ أَكۡبَرُۖ فَلَمَّآ أَفَلَتۡ قَالَ يَٰقَوۡمِ إِنِّي بَرِيٓءٞ مِّمَّا تُشۡرِكُونَ ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (869,7,'137',6,'Al-An‘ām','الأنعَام',9,10,80,'إِنِّي وَجَّهۡتُ وَجۡهِي لِلَّذِي فَطَرَ اَ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضَ حَنِيفٗاۖ وَمَآ أَنَا۠ مِنَ اَ۬لۡمُشۡرِكِينَ ٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (870,7,'137',6,'Al-An‘ām','الأنعَام',10,13,81,'وَحَآجَّهُۥ قَوۡمُهُۥۚ قَالَ أَتُحَٰٓجُّوٓنِّي فِي اِ۬للَّهِ وَقَدۡ هَدَىٰنِۦۚ وَلَآ أَخَافُ مَا تُشۡرِكُونَ بِهِۦٓ إِلَّآ أَن يَشَآءَ رَبِّي شَيۡـٔٗاۗ وَسِعَ رَبِّي كُلَّ شَيۡءٍ عِلۡمًاۗ أَفَلَا تَتَذَكَّرُونَ ٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (871,7,'137',6,'Al-An‘ām','الأنعَام',13,15,82,'وَكَيۡفَ أَخَافُ مَآ أَشۡرَكۡتُمۡ وَلَا تَخَافُونَ أَنَّكُمۡ أَشۡرَكۡتُم بِاللَّهِ مَا لَمۡ يُنزِلۡ بِهِۦ عَلَيۡكُمۡ سُلۡطَٰنٗاۚ فَأَيُّ اُ۬لۡفَرِيقَيۡنِ أَحَقُّ بِالۡأَمۡنِۖ إِن كُنتُمۡ تَعۡلَمُونَ ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (872,7,'138',6,'Al-An‘ām','الأنعَام',1,2,83,'اَ۬لَّذِينَ ءَامَنُواْ وَلَمۡ يَلۡبِسُوٓاْ إِيمَٰنَهُم بِظُلۡمٍ أُوْلَٰٓئِكَ لَهُمُ اُ۬لۡأَمۡنُ وَهُم مُّهۡتَدُونَ ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (873,7,'138',6,'Al-An‘ām','الأنعَام',2,3,84,'وَتِلۡكَ حُجَّتُنَآ ءَاتَيۡنَٰهَآ إِبۡرَٰهِيمَ عَلَىٰ قَوۡمِهِۦۚ نَرۡفَعُ دَرَجَٰتِ مَن نَّشَآءُۗ اِ۪نَّ رَبَّكَ حَكِيمٌ عَلِيمٞ ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (874,7,'138',6,'Al-An‘ām','الأنعَام',4,6,85,'وَوَهَبۡنَا لَهُۥٓ إِسۡحَٰقَ وَيَعۡقُوبَۚ كُلًّا هَدَيۡنَاۚ وَنُوحًا هَدَيۡنَا مِن قَبۡلُۖ وَمِن ذُرِّيَّتِهِۦ دَاوُۥدَ وَسُلَيۡمَٰنَ وَأَيُّوبَ وَيُوسُفَ وَمُوسۭيٰ وَهَٰرُونَۚ وَكَذَٰلِكَ نَجۡزِي اِ۬لۡمُحۡسِنِينَ ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (875,7,'138',6,'Al-An‘ām','الأنعَام',7,7,86,'وَزَكَرِيَّآءَ وَيَحۡيۭيٰ وَعِيسۭيٰ وَإِلۡيَاسَۖ كُلّٞ مِّنَ اَ۬لصَّٰلِحِينَ ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (876,7,'138',6,'Al-An‘ām','الأنعَام',8,9,87,'وَإِسۡمَٰعِيلَ وَاَلۡيَسَعَ وَيُونُسَ وَلُوطٗاۚ وَكُلّٗا فَضَّلۡنَا عَلَى اَ۬لۡعَٰلَمِينَ ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (877,7,'138',6,'Al-An‘ām','الأنعَام',9,10,88,'۞ وَمِنۡ ءَابَآئِهِمۡ وَذُرِّيَّٰتِهِمۡ وَإِخۡوَٰنِهِمۡۖ وَاَجۡتَبَيۡنَٰهُمۡ وَهَدَيۡنَٰهُمۡ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (878,7,'138',6,'Al-An‘ām','الأنعَام',10,12,89,'ذَٰلِكَ هُدَى اَ۬للَّهِ يَهۡدِي بِهِۦ مَن يَشَآءُ مِنۡ عِبَادِهِۦۚ وَلَوۡ أَشۡرَكُواْ لَحَبِطَ عَنۡهُم مَّا كَانُواْ يَعۡمَلُونَ ٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (879,7,'138',6,'Al-An‘ām','الأنعَام',12,14,90,'أُوْلَٰٓئِكَ اَ۬لَّذِينَ ءَاتَيۡنَٰهُمُ اُ۬لۡكِتَٰبَ وَاَلۡحُكۡمَ وَاَلنُّبُوَّةَۚ فَإِن يَكۡفُرۡ بِهَا هَٰٓؤُلَآءِ فَقَدۡ وَكَّلۡنَا بِهَا قَوۡمٗا لَّيۡسُواْ بِهَا بِكٰ۪فِرِينَ ٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (880,7,'138',6,'Al-An‘ām','الأنعَام',14,15,91,'أُوْلَٰٓئِكَ اَ۬لَّذِينَ هَدَى اَ۬للَّهُۖ فَبِهُدَىٰهُمُ اُ۪قۡتَدِهۡۗ قُل لَّآ أَسۡـَٔلُكُمۡ عَلَيۡهِ أَجۡرًاۖ إِنۡ هُوَ إِلَّا ذِكۡر۪يٰ لِلۡعَٰلَمِينَ ٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (881,7,'139',6,'Al-An‘ām','الأنعَام',1,5,92,'وَمَا قَدَرُواْ اُ۬للَّهَ حَقَّ قَدۡرِهِۦٓ إِذۡ قَالُواْ مَآ أَنزَلَ اَ۬للَّهُ عَلَىٰ بَشَرٖ مِّن شَيۡءٖۗ قُلۡ مَنۡ أَنزَلَ اَ۬لۡكِتَٰبَ اَ۬لَّذِي جَآءَ بِهِۦ مُوسۭيٰ نُورٗا وَهُدٗى لِّلنّ۪اسِۖ يَجۡعَلُونَهُۥ قَرَاطِيسَ يُبۡدُونَهَا وَيُخۡفُونَ كَثِيرٗاۖ وَعُلِّمۡتُم مَّا لَمۡ تَعۡلَمُوٓاْ أَنتُمۡ وَلَآ ءَابَآؤُكُمۡۖ قُلِ اِ۬للَّهُۖ ثُمَّ ذَرۡهُمۡ فِي خَوۡضِهِمۡ يَلۡعَبُونَ ٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (882,7,'139',6,'Al-An‘ām','الأنعَام',5,7,93,'وَهَٰذَا كِتَٰبٌ أَنزَلۡنَٰهُ مُبَارَكٞ مُّصَدِّقُ اُ۬لَّذِي بَيۡنَ يَدَيۡهِ وَلِتُنذِرَ أُمَّ اَ۬لۡقُر۪يٰ وَمَنۡ حَوۡلَهَاۚ وَاَلَّذِينَ يُؤۡمِنُونَ بِالۡأٓخِرَةِ يُؤۡمِنُونَ بِهِۦۖ وَهُمۡ عَلَىٰ صَلَاتِهِمۡ يُحَافِظُونَ ٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (883,7,'139',6,'Al-An‘ām','الأنعَام',7,12,94,'وَمَنۡ أَظۡلَمُ مِمَّنِ اِ۪فۡتَر۪يٰ عَلَى اَ۬للَّهِ كَذِبًا أَوۡ قَالَ أُوحِيَ إِلَيَّ وَلَمۡ يُوحَ إِلَيۡهِ شَيۡءٞ وَمَن قَالَ سَأُنزِلُ مِثۡلَ مَآ أَنزَلَ اَ۬للَّهُۗ وَلَوۡ تَر۪يٰٓ إِذِ اِ۬لظَّٰلِمُونَ فِي غَمَرَٰتِ اِ۬لۡمَوۡتِ وَاَلۡمَلَٰٓئِكَةُ بَاسِطُوٓاْ أَيۡدِيهِمۡ أَخۡرِجُوٓاْ أَنفُسَكُمُۖ اُ۬لۡيَوۡمَ تُجۡزَوۡنَ عَذَابَ اَ۬لۡهُونِ بِمَا كُنتُمۡ تَقُولُونَ عَلَى اَ۬للَّهِ غَيۡرَ اَ۬لۡحَقِّ وَكُنتُمۡ عَنۡ ءَايَٰتِهِۦ تَسۡتَكۡبِرُونَ ٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (884,7,'139',6,'Al-An‘ām','الأنعَام',12,15,95,'وَلَقَد جِّئۡتُمُونَا فُرَٰدَىٰ كَمَا خَلَقۡنَٰكُمۡ أَوَّلَ مَرَّةٖ وَتَرَكۡتُم مَّا خَوَّلۡنَٰكُمۡ وَرَآءَ ظُهُورِكُمۡۖ وَمَا نَر۪يٰ مَعَكُمۡ شُفَعَآءَكُمُ اُ۬لَّذِينَ زَعَمۡتُمۡ أَنَّهُمۡ فِيكُمۡ شُرَكَٰٓؤُاْۚ لَقَد تَّقَطَّعَ بَيۡنُكُمۡ وَضَلَّ عَنكُم مَّا كُنتُمۡ تَزۡعُمُونَ ٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (885,7,'140',6,'Al-An‘ām','الأنعَام',1,2,96,'۞ إِنَّ اَ۬للَّهَ فَالِقُ اُ۬لۡحَبِّ وَاَلنَّوَىٰۖ يُخۡرِجُ اُ۬لۡحَيَّ مِنَ اَ۬لۡمَيۡتِ وَمُخۡرِجُ اُ۬لۡمَيۡتِ مِنَ اَ۬لۡحَيِّۚ ذَٰلِكُمُ اُ۬للَّهُۖ فَأَنّۭيٰ تُؤۡفَكُونَ ٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (886,7,'140',6,'Al-An‘ām','الأنعَام',2,4,97,'فَالِقُ اُ۬لۡإِصۡبَاحِ وَجَٰعِلُ اُ۬لَّيۡلِ سَكَنٗا وَاَلشَّمۡسَ وَاَلۡقَمَرَ حُسۡبَانٗاۚ ذَٰلِكَ تَقۡدِيرُ اُ۬لۡعَزِيزِ اِ۬لۡعَلِيمِ ٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (887,7,'140',6,'Al-An‘ām','الأنعَام',4,5,98,'وَهۡوَ اَ۬لَّذِي جَعَلَ لَكُمُ اُ۬لنُّجُومَ لِتَهۡتَدُواْ بِهَا فِي ظُلُمَٰتِ اِ۬لۡبَرِّ وَاَلۡبَحۡرِۗ قَدۡ فَصَّلۡنَا اَ۬لۡأٓيَٰتِ لِقَوۡمٖ يَعۡلَمُونَ ٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (888,7,'140',6,'Al-An‘ām','الأنعَام',6,7,99,'وَهۡوَ اَ۬لَّذِيٓ أَنشَأَكُم مِّن نَّفۡسٖ وَٰحِدَةٖ فَمُسۡتَقِرّٞ وَمُسۡتَوۡدَعٞۗ قَدۡ فَصَّلۡنَا اَ۬لۡأٓيَٰتِ لِقَوۡمٖ يَفۡقَهُونَ ٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (889,7,'140',6,'Al-An‘ām','الأنعَام',7,12,100,'وَهۡوَ اَ۬لَّذِيٓ أَنزَلَ مِنَ اَ۬لسَّمَآءِ مَآءٗ فَأَخۡرَجۡنَا بِهِۦ نَبَاتَ كُلِّ شَيۡءٖ فَأَخۡرَجۡنَا مِنۡهُ خَضِرٗا نُّخۡرِجُ مِنۡهُ حَبّٗا مُّتَرَاكِبٗا وَمِنَ اَ۬لنَّخۡلِ مِن طَلۡعِهَا قِنۡوَانٞ دَانِيَةٞ وَجَنَّٰتٖ مِّنۡ أَعۡنَابٖ وَاَلزَّيۡتُونَ وَاَلرُّمَّانَ مُشۡتَبِهٗا وَغَيۡرَ مُتَشَٰبِهٍۗ اِ۟نظُرُوٓاْ إِلَىٰ ثَمَرِهِۦٓ إِذَآ أَثۡمَرَ وَيَنۡعِهِۦٓۚ إِنَّ فِي ذَٰلِكُمۡ لَأٓيَٰتٖ لِّقَوۡمٖ يُؤۡمِنُونَ ١٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (890,7,'140',6,'Al-An‘ām','الأنعَام',12,13,101,'وَجَعَلُواْ لِلَّهِ شُرَكَآءَ اَ۬لۡجِنَّ وَخَلَقَهُمۡۖ وَخَرَقُواْ لَهُۥ بَنِينَ وَبَنَٰتِۢ بِغَيۡرِ عِلۡمٖۚ سُبۡحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يَصِفُونَ ١٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (891,7,'140',6,'Al-An‘ām','الأنعَام',14,15,102,'۞ بَدِيعُ اُ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِۖ أَنّۭيٰ يَكُونُ لَهُۥ وَلَدٞ وَلَمۡ تَكُن لَّهُۥ صَٰحِبَةٞۖ وَخَلَقَ كُلَّ شَيۡءٖۖ وَهۡوَ بِكُلِّ شَيۡءٍ عَلِيمٞ ١٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (892,7,'141',6,'Al-An‘ām','الأنعَام',1,2,103,'ذَٰلِكُمُ اُ۬للَّهُ رَبُّكُمۡۖ لَآ إِلَٰهَ إِلَّا هُوَۖ خَٰلِقُ كُلِّ شَيۡءٖ فَاَعۡبُدُوهُۚ وَهۡوَ عَلَىٰ كُلِّ شَيۡءٖ وَكِيلٞ ١٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (893,7,'141',6,'Al-An‘ām','الأنعَام',2,3,104,'لَّا تُدۡرِكُهُ اُ۬لۡأَبۡصَٰرُ وَهۡوَ يُدۡرِكُ اُ۬لۡأَبۡصَٰرَۖ وَهۡوَ اَ۬للَّطِيفُ اُ۬لۡخَبِيرُ ١٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (894,7,'141',6,'Al-An‘ām','الأنعَام',3,5,105,'قَد جَّآءَكُم بَصَآئِرُ مِن رَّبِّكُمۡۖ فَمَنۡ أَبۡصَرَ فَلِنَفۡسِهِۦۖ وَمَنۡ عَمِيَ فَعَلَيۡهَاۚ وَمَآ أَنَا۠ عَلَيۡكُم بِحَفِيظٖ ١٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (895,7,'141',6,'Al-An‘ām','الأنعَام',5,6,106,'وَكَذَٰلِكَ نُصَرِّفُ اُ۬لۡأٓيَٰتِ وَلِيَقُولُواْ دَٰرَسۡتَ وَلِنُبَيِّنَهُۥ لِقَوۡمٖ يَعۡلَمُونَ ١٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (896,7,'141',6,'Al-An‘ām','الأنعَام',6,7,107,'اَ۪تَّبِعۡ مَآ أُوحِيَ إِلَيۡكَ مِن رَّبِّكَۖ لَآ إِلَٰهَ إِلَّا هُوَۖ وَأَعۡرِضۡ عَنِ اِ۬لۡمُشۡرِكِينَ ١٠٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (897,7,'141',6,'Al-An‘ām','الأنعَام',8,9,108,'وَلَوۡ شَآءَ اَ۬للَّهُ مَآ أَشۡرَكُواْۗ وَمَا جَعَلۡنَٰكَ عَلَيۡهِمۡ حَفِيظٗاۖ وَمَآ أَنتَ عَلَيۡهِم بِوَكِيلٖ ١٠٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (898,7,'141',6,'Al-An‘ām','الأنعَام',9,11,109,'وَلَا تَسُبُّواْ اُ۬لَّذِينَ يَدۡعُونَ مِن دُونِ اِ۬للَّهِ فَيَسُبُّواْ اُ۬للَّهَ عَدۡوَۢا بِغَيۡرِ عِلۡمٖۗ كَذَٰلِكَ زَيَّنَّا لِكُلِّ أُمَّةٍ عَمَلَهُمۡ ثُمَّ إِلَىٰ رَبِّهِم مَّرۡجِعُهُمۡ فَيُنَبِّئُهُم بِمَا كَانُواْ يَعۡمَلُونَ ١٠٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (899,7,'141',6,'Al-An‘ām','الأنعَام',12,14,110,'وَأَقۡسَمُواْ بِاللَّهِ جَهۡدَ أَيۡمَٰنِهِمۡ لَئِن جَآءَتۡهُمۡ ءَايَةٞ لَّيُؤۡمِنُنَّ بِهَاۚ قُلۡ إِنَّمَا اَ۬لۡأٓيَٰتُ عِندَ اَ۬للَّهِۖ وَمَا يُشۡعِرۡكُمۡۘ إِنَّهَآ إِذَا جَآءَتۡ لَا يُؤۡمِنُونَ ١١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (900,7,'141',6,'Al-An‘ām','الأنعَام',14,15,111,'وَنُقَلِّبُ أَفۡـِٔدَتَهُمۡ وَأَبۡصَٰرَهُمۡ كَمَا لَمۡ يُؤۡمِنُواْ بِهِۦٓ أَوَّلَ مَرَّةٖ وَنَذَرُهُمۡ فِي طُغۡيَٰنِهِمۡ يَعۡمَهُونَ ١١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (901,8,'142',6,'Al-An‘ām','الأنعَام',1,3,112,'۞ وَلَوۡ أَنَّنَا نَزَّلۡنَآ إِلَيۡهِمِ اِ۬لۡمَلَٰٓئِكَةَ وَكَلَّمَهُمُ اُ۬لۡمَوۡتۭيٰ وَحَشَرۡنَا عَلَيۡهِمۡ كُلَّ شَيۡءٖ قُبُلٗا مَّا كَانُواْ لِيُؤۡمِنُوٓاْ إِلَّآ أَن يَشَآءَ اَ۬للَّهُ وَلَٰكِنَّ أَكۡثَرَهُمۡ يَجۡهَلُونَ ١١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (902,8,'142',6,'Al-An‘ām','الأنعَام',3,6,113,'وَكَذَٰلِكَ جَعَلۡنَا لِكُلِّ نَبِيٍّ عَدُوّٗا شَيَٰطِينَ اَ۬لۡإِنسِ وَاَلۡجِنِّ يُوحِي بَعۡضُهُمۡ إِلَىٰ بَعۡضٖ زُخۡرُفَ اَ۬لۡقَوۡلِ غُرُورٗاۚ وَلَوۡ شَآءَ رَبُّكَ مَا فَعَلُوهُۖ فَذَرۡهُمۡ وَمَا يَفۡتَرُونَ ١١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (903,8,'142',6,'Al-An‘ām','الأنعَام',6,7,114,'وَلِتَصۡغَىٰٓ إِلَيۡهِ أَفۡـِٔدَةُ اُ۬لَّذِينَ لَا يُؤۡمِنُونَ بِالۡأٓخِرَةِ وَلِيَرۡضَوۡهُ وَلِيَقۡتَرِفُواْ مَا هُم مُّقۡتَرِفُونَ ١١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (904,8,'142',6,'Al-An‘ām','الأنعَام',7,10,115,'أَفَغَيۡرَ اَ۬للَّهِ أَبۡتَغِي حَكَمٗا وَهۡوَ اَ۬لَّذِيٓ أَنزَلَ إِلَيۡكُمُ اُ۬لۡكِتَٰبَ مُفَصَّلٗاۚ وَاَلَّذِينَ ءَاتَيۡنَٰهُمُ اُ۬لۡكِتَٰبَ يَعۡلَمُونَ أَنَّهُۥ مُنزَلٞ مِّن رَّبِّكَ بِالۡحَقِّۖ فَلَا تَكُونَنَّ مِنَ اَ۬لۡمُمۡتَرِينَ ١١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (905,8,'142',6,'Al-An‘ām','الأنعَام',10,11,116,'وَتَمَّتۡ كَلِمَٰتُ رَبِّكَ صِدۡقٗا وَعَدۡلٗاۚ لَّا مُبَدِّلَ لِكَلِمَٰتِهِۦۚ وَهۡوَ اَ۬لسَّمِيعُ اُ۬لۡعَلِيمُ ١١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (906,8,'142',6,'Al-An‘ām','الأنعَام',12,13,117,'وَإِن تُطِعۡ أَكۡثَرَ مَن فِي اِ۬لۡأَرۡضِ يُضِلُّوكَ عَن سَبِيلِ اِ۬للَّهِۚ إِن يَتَّبِعُونَ إِلَّا اَ۬لظَّنَّ وَإِنۡ هُمۡ إِلَّا يَخۡرُصُونَ ١١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (907,8,'142',6,'Al-An‘ām','الأنعَام',13,14,118,'إِنَّ رَبَّكَ هُوَ أَعۡلَمُ مَن يَضِلُّ عَن سَبِيلِهِۦۖ وَهۡوَ أَعۡلَمُ بِالۡمُهۡتَدِينَ ١١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (908,8,'142',6,'Al-An‘ām','الأنعَام',14,15,119,'فَكُلُواْ مِمَّا ذُكِرَ اَ۪سۡمُ اُ۬للَّهِ عَلَيۡهِ إِن كُنتُم بِـَٔايَٰتِهِۦ مُؤۡمِنِينَ ١١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (909,8,'143',6,'Al-An‘ām','الأنعَام',1,3,120,'وَمَا لَكُمۡ أَلَّا تَأۡكُلُواْ مِمَّا ذُكِرَ اَ۪سۡمُ اُ۬للَّهِ عَلَيۡهِ وَقَدۡ فُصِّلَ لَكُم مَّا حُرِّمَ عَلَيۡكُمۡ إِلَّا مَا اَ۟ضۡطُرِرۡتُمۡ إِلَيۡهِۗ وَإِنَّ كَثِيرٗا لَّيَضِلُّونَ بِأَهۡوَآئِهِم بِغَيۡرِ عِلۡمٍۗ إِنَّ رَبَّكَ هُوَ أَعۡلَمُ بِالۡمُعۡتَدِينَ ١٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (910,8,'143',6,'Al-An‘ām','الأنعَام',4,5,121,'۞ وَذَرُواْ ظَٰهِرَ اَ۬لۡإِثۡمِ وَبَاطِنَهُۥٓۚ إِنَّ اَ۬لَّذِينَ يَكۡسِبُونَ اَ۬لۡإِثۡمَ سَيُجۡزَوۡنَ بِمَا كَانُواْ يَقۡتَرِفُونَ ١٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (911,8,'143',6,'Al-An‘ām','الأنعَام',5,7,122,'وَلَا تَأۡكُلُواْ مِمَّا لَمۡ يُذۡكَرِ اِ۪سۡمُ اُ۬للَّهِ عَلَيۡهِ وَإِنَّهُۥ لَفِسۡقٞۗ وَإِنَّ اَ۬لشَّيَٰطِينَ لَيُوحُونَ إِلَىٰٓ أَوۡلِيَآئِهِمۡ لِيُجَٰدِلُوكُمۡۖ وَإِنۡ أَطَعۡتُمُوهُمۡ إِنَّكُمۡ لَمُشۡرِكُونَ ١٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (912,8,'143',6,'Al-An‘ām','الأنعَام',8,10,123,'أَوَمَن كَانَ مَيۡتٗا فَأَحۡيَيۡنَٰهُ وَجَعَلۡنَا لَهُۥ نُورٗا يَمۡشِي بِهِۦ فِي اِ۬لنّ۪اسِ كَمَن مَّثَلُهُۥ فِي اِ۬لظُّلُمَٰتِ لَيۡسَ بِخَارِجٖ مِّنۡهَاۚ كَذَٰلِكَ زُيِّنَ لِلۡكٰ۪فِرِينَ مَا كَانُواْ يَعۡمَلُونَ ١٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (913,8,'143',6,'Al-An‘ām','الأنعَام',10,12,124,'وَكَذَٰلِكَ جَعَلۡنَا فِي كُلِّ قَرۡيَةٍ أَكَٰبِرَ مُجۡرِمِيهَا لِيَمۡكُرُواْ فِيهَاۖ وَمَا يَمۡكُرُونَ إِلَّا بِأَنفُسِهِمۡ وَمَا يَشۡعُرُونَ ١٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (914,8,'143',6,'Al-An‘ām','الأنعَام',12,15,125,'وَإِذَا جَآءَتۡهُمۡ ءَايَةٞ قَالُواْ لَن نُّؤۡمِنَ حَتَّىٰ نُؤۡتَىٰ مِثۡلَ مَآ أُوتِيَ رُسُلُ اُ۬للَّهِۘ اِ۬للَّهُ أَعۡلَمُ حَيۡثُ يَجۡعَلُ رِسَٰلَٰتِهِۦۗ سَيُصِيبُ اُ۬لَّذِينَ أَجۡرَمُواْ صَغَارٌ عِندَ اَ۬للَّهِ وَعَذَابٞ شَدِيدُۢ بِمَا كَانُواْ يَمۡكُرُونَ ١٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (915,8,'144',6,'Al-An‘ām','الأنعَام',1,4,126,'فَمَن يُرِدِ اِ۬للَّهُ أَن يَهۡدِيَهُۥ يَشۡرَحۡ صَدۡرَهُۥ لِلۡإِسۡلَٰمِۖ وَمَن يُرِدۡ أَن يُضِلَّهُۥ يَجۡعَلۡ صَدۡرَهُۥ ضَيِّقًا حَرَجٗا كَأَنَّمَا يَصَّعَّدُ فِي اِ۬لسَّمَآءِۚ كَذَٰلِكَ يَجۡعَلُ اُ۬للَّهُ اُ۬لرِّجۡسَ عَلَى اَ۬لَّذِينَ لَا يُؤۡمِنُونَ ١٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (916,8,'144',6,'Al-An‘ām','الأنعَام',4,5,127,'وَهَٰذَا صِرَٰطُ رَبِّكَ مُسۡتَقِيمٗاۗ قَدۡ فَصَّلۡنَا اَ۬لۡأٓيَٰتِ لِقَوۡمٖ يَذَّكَّرُونَ ١٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (917,8,'144',6,'Al-An‘ām','الأنعَام',5,6,128,'لَهُمۡ دَارُ اُ۬لسَّلَٰمِ عِندَ رَبِّهِمۡۖ وَهۡوَ وَلِيُّهُم بِمَا كَانُواْ يَعۡمَلُونَ ١٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (918,8,'144',6,'Al-An‘ām','الأنعَام',6,10,129,'۞ وَيَوۡمَ نَحۡشُرُهُمۡ جَمِيعٗا يَٰمَعۡشَرَ اَ۬لۡجِنِّ قَدِ اِ۪سۡتَكۡثَرۡتُم مِّنَ اَ۬لۡإِنسِۖ وَقَالَ أَوۡلِيَآؤُهُم مِّنَ اَ۬لۡإِنسِ رَبَّنَا اَ۪سۡتَمۡتَعَ بَعۡضُنَا بِبَعۡضٖ وَبَلَغۡنَآ أَجَلَنَا اَ۬لَّذِيٓ أَجَّلۡتَ لَنَاۚ قَالَ اَ۬لنَّارُ مَثۡوَىٰكُمۡ خَٰلِدِينَ فِيهَآ إِلَّا مَا شَآءَ اَ۬للَّهُۗ إِنَّ رَبَّكَ حَكِيمٌ عَلِيمٞ ١٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (919,8,'144',6,'Al-An‘ām','الأنعَام',10,11,130,'وَكَذَٰلِكَ نُوَلِّي بَعۡضَ اَ۬لظَّٰلِمِينَ بَعۡضَۢا بِمَا كَانُواْ يَكۡسِبُونَ ١٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (920,8,'144',6,'Al-An‘ām','الأنعَام',12,15,131,'يَٰمَعۡشَرَ اَ۬لۡجِنِّ وَاَلۡإِنسِ أَلَمۡ يَأۡتِكُمۡ رُسُلٞ مِّنكُمۡ يَقُصُّونَ عَلَيۡكُمۡ ءَايَٰتِي وَيُنذِرُونَكُمۡ لِقَآءَ يَوۡمِكُمۡ هَٰذَاۚ قَالُواْ شَهِدۡنَا عَلَىٰٓ أَنفُسِنَاۖ وَغَرَّتۡهُمُ اُ۬لۡحَيَوٰةُ اُ۬لدُّنۡيۭا وَشَهِدُواْ عَلَىٰٓ أَنفُسِهِمۡ أَنَّهُمۡ كَانُواْ كٰ۪فِرِينَ ١٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (921,8,'145',6,'Al-An‘ām','الأنعَام',1,2,132,'ذَٰلِكَ أَن لَّمۡ يَكُن رَّبُّكَ مُهۡلِكَ اَ۬لۡقُر۪يٰ بِظُلۡمٖ وَأَهۡلُهَا غَٰفِلُونَ ١٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (922,8,'145',6,'Al-An‘ām','الأنعَام',2,3,133,'وَلِكُلّٖ دَرَجَٰتٞ مِّمَّا عَمِلُواْۚ وَمَا رَبُّكَ بِغَٰفِلٍ عَمَّا يَعۡمَلُونَ ١٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (923,8,'145',6,'Al-An‘ām','الأنعَام',3,5,134,'وَرَبُّكَ اَ۬لۡغَنِيُّ ذُو اُ۬لرَّحۡمَةِۚ إِن يَشَأۡ يُذۡهِبۡكُمۡ وَيَسۡتَخۡلِفۡ مِنۢ بَعۡدِكُم مَّا يَشَآءُ كَمَآ أَنشَأَكُم مِّن ذُرِّيَّةِ قَوۡمٍ ءَاخَرِينَ ١٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (924,8,'145',6,'Al-An‘ām','الأنعَام',6,6,135,'إِنَّ مَا تُوعَدُونَ لَأٓتٖۖ وَمَآ أَنتُم بِمُعۡجِزِينَ ١٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (925,8,'145',6,'Al-An‘ām','الأنعَام',6,8,136,'قُلۡ يَٰقَوۡمِ اِ۪عۡمَلُواْ عَلَىٰ مَكَانَتِكُمۡ إِنِّي عَامِلٞۖ فَسَوۡفَ تَعۡلَمُونَ مَن تَكُونُ لَهُۥ عَٰقِبَةُ اُ۬لدّ۪ارِۗ إِنَّهُۥ لَا يُفۡلِحُ اُ۬لظَّٰلِمُونَ ١٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (926,8,'145',6,'Al-An‘ām','الأنعَام',9,12,137,'۞ وَجَعَلُواْ لِلَّهِ مِمَّا ذَرَأَ مِنَ اَ۬لۡحَرۡثِ وَاَلۡأَنۡعَٰمِ نَصِيبٗا فَقَالُواْ هَٰذَا لِلَّهِ بِزَعۡمِهِمۡ وَهَٰذَا لِشُرَكَآئِنَاۖ فَمَا كَانَ لِشُرَكَآئِهِمۡ فَلَا يَصِلُ إِلَى اَ۬للَّهِۖ وَمَا كَانَ لِلَّهِ فَهۡوَ يَصِلُ إِلَىٰ شُرَكَآئِهِمۡۗ سَآءَ مَا يَحۡكُمُونَ ١٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (927,8,'145',6,'Al-An‘ām','الأنعَام',12,15,138,'وَكَذَٰلِكَ زَيَّنَ لِكَثِيرٖ مِّنَ اَ۬لۡمُشۡرِكِينَ قَتۡلَ أَوۡلَٰدِهِمۡ شُرَكَآؤُهُمۡ لِيُرۡدُوهُمۡ وَلِيَلۡبِسُواْ عَلَيۡهِمۡ دِينَهُمۡۖ وَلَوۡ شَآءَ اَ۬للَّهُ مَا فَعَلُوهُۖ فَذَرۡهُمۡ وَمَا يَفۡتَرُونَ ١٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (928,8,'146',6,'Al-An‘ām','الأنعَام',1,4,139,'وَقَالُواْ هَٰذِهِۦٓ أَنۡعَٰمٞ وَحَرۡثٌ حِجۡرٞ لَّا يَطۡعَمُهَآ إِلَّا مَن نَّشَآءُ بِزَعۡمِهِمۡ وَأَنۡعَٰمٌ حُرِّمَت ظُّهُورُهَا وَأَنۡعَٰمٞ لَّا يَذۡكُرُونَ اَ۪سۡمَ اَ۬للَّهِ عَلَيۡهَا اَ۪فۡتِرَآءً عَلَيۡهِۚ سَيَجۡزِيهِم بِمَا كَانُواْ يَفۡتَرُونَ ١٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (929,8,'146',6,'Al-An‘ām','الأنعَام',4,7,140,'وَقَالُواْ مَا فِي بُطُونِ هَٰذِهِ اِ۬لۡأَنۡعَٰمِ خَالِصَةٞ لِّذُكُورِنَا وَمُحَرَّمٌ عَلَىٰٓ أَزۡوَٰجِنَاۖ وَإِن يَكُن مَّيۡتَةٗ فَهُمۡ فِيهِ شُرَكَآءُۚ سَيَجۡزِيهِمۡ وَصۡفَهُمۡۚ إِنَّهُۥ حَكِيمٌ عَلِيمٞ ١٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (930,8,'146',6,'Al-An‘ām','الأنعَام',7,9,141,'قَدۡ خَسِرَ اَ۬لَّذِينَ قَتَلُوٓاْ أَوۡلَٰدَهُمۡ سَفَهَۢا بِغَيۡرِ عِلۡمٖ وَحَرَّمُواْ مَا رَزَقَهُمُ اُ۬للَّهُ اُ۪فۡتِرَآءً عَلَى اَ۬للَّهِۚ قَد ضَّلُّواْ وَمَا كَانُواْ مُهۡتَدِينَ ١٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (931,8,'146',6,'Al-An‘ām','الأنعَام',9,13,142,'۞ وَهۡوَ اَ۬لَّذِيٓ أَنشَأَ جَنَّٰتٖ مَّعۡرُوشَٰتٖ وَغَيۡرَ مَعۡرُوشَٰتٖ وَاَلنَّخۡلَ وَاَلزَّرۡعَ مُخۡتَلِفًا أُكُلُهُۥ وَاَلزَّيۡتُونَ وَاَلرُّمَّانَ مُتَشَٰبِهٗا وَغَيۡرَ مُتَشَٰبِهٖۚ كُلُواْ مِن ثَمَرِهِۦٓ إِذَآ أَثۡمَرَ وَءَاتُواْ حَقَّهُۥ يَوۡمَ حَصَادِهِۦۖ وَلَا تُسۡرِفُوٓاْۚ إِنَّهُۥ لَا يُحِبُّ اُ۬لۡمُسۡرِفِينَ ١٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (932,8,'146',6,'Al-An‘ām','الأنعَام',13,15,143,'وَمِنَ اَ۬لۡأَنۡعَٰمِ حَمُولَةٗ وَفَرۡشٗاۚ كُلُواْ مِمَّا رَزَقَكُمُ اُ۬للَّهُ وَلَا تَتَّبِعُواْ خُطۡوَٰتِ اِ۬لشَّيۡطَٰنِۚ إِنَّهُۥ لَكُمۡ عَدُوّٞ مُّبِينٞ ١٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (933,8,'147',6,'Al-An‘ām','الأنعَام',1,3,144,'ثَمَٰنِيَةَ أَزۡوَٰجٖۖ مِّنَ اَ۬لضَّأۡنِ اِ۪ثۡنَيۡنِ وَمِنَ اَ۬لۡمَعَزِ اِ۪ثۡنَيۡنِۗ قُلۡ ءَآلذَّكَرَيۡنِ حَرَّمَ أَمِ اِ۬لۡأُنثَيَيۡنِ أَمَّا اَ۪شۡتَمَلَتۡ عَلَيۡهِ أَرۡحَامُ اُ۬لۡأُنثَيَيۡنِۖ نَبِّـُٔونِي بِعِلۡمٍ إِن كُنتُمۡ صَٰدِقِينَ ١٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (934,8,'147',6,'Al-An‘ām','الأنعَام',4,8,145,'وَمِنَ اَ۬لۡإِبِلِ اِ۪ثۡنَيۡنِ وَمِنَ اَ۬لۡبَقَرِ اِ۪ثۡنَيۡنِۗ قُلۡ ءَآلذَّكَرَيۡنِ حَرَّمَ أَمِ اِ۬لۡأُنثَيَيۡنِ أَمَّا اَ۪شۡتَمَلَتۡ عَلَيۡهِ أَرۡحَامُ اُ۬لۡأُنثَيَيۡنِۖ أَمۡ كُنتُمۡ شُهَدَآءَ ا۪ذۡ وَصَّىٰكُمُ اُ۬للَّهُ بِهَٰذَاۚ فَمَنۡ أَظۡلَمُ مِمَّنِ اِ۪فۡتَر۪يٰ عَلَى اَ۬للَّهِ كَذِبٗا لِّيُضِلَّ اَ۬لنَّاسَ بِغَيۡرِ عِلۡمٍۗ إِنَّ اَ۬للَّهَ لَا يَهۡدِي اِ۬لۡقَوۡمَ اَ۬لظَّٰلِمِينَ ١٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (935,8,'147',6,'Al-An‘ām','الأنعَام',8,12,146,'۞ قُل لَّآ أَجِدُ فِي مَآ أُوحِيَ إِلَيَّ مُحَرَّمًا عَلَىٰ طَاعِمٖ يَطۡعَمُهُۥٓ إِلَّآ أَن يَكُونَ مَيۡتَةً أَوۡ دَمٗا مَّسۡفُوحًا أَوۡ لَحۡمَ خِنزِيرٖ فَإِنَّهُۥ رِجۡسٌ أَوۡ فِسۡقًا أُهِلَّ لِغَيۡرِ اِ۬للَّهِ بِهِۦۚ فَمَنِ اِ۟ضۡطُرَّ غَيۡرَ بَاغٖ وَلَا عَادٖ فَإِنَّ رَبَّكَ غَفُورٞ رَّحِيمٞ ١٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (936,8,'147',6,'Al-An‘ām','الأنعَام',12,15,147,'وَعَلَى اَ۬لَّذِينَ هَادُواْ حَرَّمۡنَا كُلَّ ذِي ظُفُرٖۖ وَمِنَ اَ۬لۡبَقَرِ وَاَلۡغَنَمِ حَرَّمۡنَا عَلَيۡهِمۡ شُحُومَهُمَآ إِلَّا مَا حَمَلَت ظُّهُورُهُمَآ أَوِ اِ۬لۡحَوَايَآ أَوۡ مَا اَ۪خۡتَلَطَ بِعَظۡمٖۚ ذَٰلِكَ جَزَيۡنَٰهُم بِبَغۡيِهِمۡۖ وَإِنَّا لَصَٰدِقُونَ ١٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (937,8,'148',6,'Al-An‘ām','الأنعَام',1,2,148,'فَإِن كَذَّبُوكَ فَقُل رَّبُّكُمۡ ذُو رَحۡمَةٖ وَٰسِعَةٖ وَلَا يُرَدُّ بَأۡسُهُۥ عَنِ اِ۬لۡقَوۡمِ اِ۬لۡمُجۡرِمِينَ ١٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (938,8,'148',6,'Al-An‘ām','الأنعَام',2,6,149,'سَيَقُولُ اُ۬لَّذِينَ أَشۡرَكُواْ لَوۡ شَآءَ اَ۬للَّهُ مَآ أَشۡرَكۡنَا وَلَآ ءَابَآؤُنَا وَلَا حَرَّمۡنَا مِن شَيۡءٖۚ كَذَٰلِكَ كَذَّبَ اَ۬لَّذِينَ مِن قَبۡلِهِمۡ حَتَّىٰ ذَاقُواْ بَأۡسَنَاۗ قُلۡ هَلۡ عِندَكُم مِّنۡ عِلۡمٖ فَتُخۡرِجُوهُ لَنَآۖ إِن تَتَّبِعُونَ إِلَّا اَ۬لظَّنَّ وَإِنۡ أَنتُمۡ إِلَّا تَخۡرُصُونَ ١٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (939,8,'148',6,'Al-An‘ām','الأنعَام',6,7,150,'قُلۡ فَلِلَّهِ اِ۬لۡحُجَّةُ اُ۬لۡبَٰلِغَةُۖ فَلَوۡ شَآءَ لَهَدَىٰكُمۡ أَجۡمَعِينَ ١٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (940,8,'148',6,'Al-An‘ām','الأنعَام',7,10,151,'قُلۡ هَلُمَّ شُهَدَآءَكُمُ اُ۬لَّذِينَ يَشۡهَدُونَ أَنَّ اَ۬للَّهَ حَرَّمَ هَٰذَاۖ فَإِن شَهِدُواْ فَلَا تَشۡهَدۡ مَعَهُمۡۚ وَلَا تَتَّبِعۡ أَهۡوَآءَ اَ۬لَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَاَلَّذِينَ لَا يُؤۡمِنُونَ بِالۡأٓخِرَةِ وَهُم بِرَبِّهِمۡ يَعۡدِلُونَ ١٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (941,8,'148',6,'Al-An‘ām','الأنعَام',10,15,152,'۞ قُلۡ تَعَالَوۡاْ أَتۡلُ مَا حَرَّمَ رَبُّكُمۡ عَلَيۡكُمۡۖ أَلَّا تُشۡرِكُواْ بِهِۦ شَيۡـٔٗاۖ وَبِالۡوَٰلِدَيۡنِ إِحۡسَٰنٗاۖ وَلَا تَقۡتُلُوٓاْ أَوۡلَٰدَكُم مِّنۡ إِمۡلَٰقٖۖ نَّحۡنُ نَرۡزُقُكُمۡ وَإِيَّاهُمۡۖ وَلَا تَقۡرَبُواْ اُ۬لۡفَوَٰحِشَ مَا ظَهَرَ مِنۡهَا وَمَا بَطَنَۖ وَلَا تَقۡتُلُواْ اُ۬لنَّفۡسَ اَ۬لَّتِي حَرَّمَ اَ۬للَّهُ إِلَّا بِالۡحَقِّۚ ذَٰلِكُمۡ وَصَّىٰكُم بِهِۦ لَعَلَّكُمۡ تَعۡقِلُونَ ١٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (942,8,'149',6,'Al-An‘ām','الأنعَام',1,4,153,'وَلَا تَقۡرَبُواْ مَالَ اَ۬لۡيَتِيمِ إِلَّا بِالَّتِي هِيَ أَحۡسَنُ حَتَّىٰ يَبۡلُغَ أَشُدَّهُۥۖ وَأَوۡفُواْ اُ۬لۡكَيۡلَ وَاَلۡمِيزَانَ بِالۡقِسۡطِۖ لَا نُكَلِّفُ نَفۡسًا إِلَّا وُسۡعَهَاۖ وَإِذَا قُلۡتُمۡ فَاَعۡدِلُواْ وَلَوۡ كَانَ ذَا قُرۡبۭيٰۖ وَبِعَهۡدِ اِ۬للَّهِ أَوۡفُواْۚ ذَٰلِكُمۡ وَصَّىٰكُم بِهِۦ لَعَلَّكُمۡ تَذَّكَّرُونَ ١٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (943,8,'149',6,'Al-An‘ām','الأنعَام',5,7,154,'وَأَنَّ هَٰذَا صِرَٰطِي مُسۡتَقِيمٗا فَاَتَّبِعُوهُۖ وَلَا تَتَّبِعُواْ اُ۬لسُّبُلَ فَتَفَرَّقَ بِكُمۡ عَن سَبِيلِهِۦۚ ذَٰلِكُمۡ وَصَّىٰكُم بِهِۦ لَعَلَّكُمۡ تَتَّقُونَ ١٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (944,8,'149',6,'Al-An‘ām','الأنعَام',7,9,155,'ثُمَّ ءَاتَيۡنَا مُوسَى اَ۬لۡكِتَٰبَ تَمَامًا عَلَى اَ۬لَّذِيٓ أَحۡسَنَ وَتَفۡصِيلٗا لِّكُلِّ شَيۡءٖ وَهُدٗى وَرَحۡمَةٗ لَّعَلَّهُم بِلِقَآءِ رَبِّهِمۡ يُؤۡمِنُونَ ١٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (945,8,'149',6,'Al-An‘ām','الأنعَام',9,10,156,'وَهَٰذَا كِتَٰبٌ أَنزَلۡنَٰهُ مُبَارَكٞ فَاَتَّبِعُوهُ وَاَتَّقُواْ لَعَلَّكُمۡ تُرۡحَمُونَ ١٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (946,8,'149',6,'Al-An‘ām','الأنعَام',10,11,157,'أَن تَقُولُوٓاْ إِنَّمَآ أُنزِلَ اَ۬لۡكِتَٰبُ عَلَىٰ طَآئِفَتَيۡنِ مِن قَبۡلِنَا وَإِن كُنَّا عَن دِرَاسَتِهِمۡ لَغَٰفِلِينَ ١٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (947,8,'149',6,'Al-An‘ām','الأنعَام',12,15,158,'أَوۡ تَقُولُواْ لَوۡ أَنَّآ أُنزِلَ عَلَيۡنَا اَ۬لۡكِتَٰبُ لَكُنَّآ أَهۡدَىٰ مِنۡهُمۡۚ فَقَد جَّآءَكُم بَيِّنَةٞ مِّن رَّبِّكُمۡ وَهُدٗى وَرَحۡمَةٞۚ فَمَنۡ أَظۡلَمُ مِمَّن كَذَّبَ بِـَٔايَٰتِ اِ۬للَّهِ وَصَدَفَ عَنۡهَاۗ سَنَجۡزِي اِ۬لَّذِينَ يَصۡدِفُونَ عَنۡ ءَايَٰتِنَا سُوٓءَ اَ۬لۡعَذَابِ بِمَا كَانُواْ يَصۡدِفُونَ ١٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (948,8,'150',6,'Al-An‘ām','الأنعَام',1,4,159,'۞ هَلۡ يَنظُرُونَ إِلَّآ أَن تَأۡتِيَهُمُ اُ۬لۡمَلَٰٓئِكَةُ أَوۡ يَأۡتِيَ رَبُّكَ أَوۡ يَأۡتِيَ بَعۡضُ ءَايَٰتِ رَبِّكَۗ يَوۡمَ يَأۡتِي بَعۡضُ ءَايَٰتِ رَبِّكَ لَا يَنفَعُ نَفۡسًا إِيمَٰنُهَا لَمۡ تَكُنۡ ءَامَنَتۡ مِن قَبۡلُ أَوۡ كَسَبَتۡ فِيٓ إِيمَٰنِهَا خَيۡرٗاۗ قُلِ اِ۪نتَظِرُوٓاْ إِنَّا مُنتَظِرُونَ ١٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (949,8,'150',6,'Al-An‘ām','الأنعَام',4,5,160,'إِنَّ اَ۬لَّذِينَ فَرَّقُواْ دِينَهُمۡ وَكَانُواْ شِيَعٗا لَّسۡتَ مِنۡهُمۡ فِي شَيۡءٍۚ إِنَّمَآ أَمۡرُهُمۡ إِلَى اَ۬للَّهِ ثُمَّ يُنَبِّئُهُم بِمَا كَانُواْ يَفۡعَلُونَ ١٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (950,8,'150',6,'Al-An‘ām','الأنعَام',6,7,161,'مَن جَآءَ بِالۡحَسَنَةِ فَلَهُۥ عَشۡرُ أَمۡثَالِهَاۖ وَمَن جَآءَ بِالسَّيِّئَةِ فَلَا يُجۡزَىٰٓ إِلَّا مِثۡلَهَا وَهُمۡ لَا يُظۡلَمُونَ ١٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (951,8,'150',6,'Al-An‘ām','الأنعَام',7,8,162,'قُلۡ إِنَّنِي هَدَىٰنِي رَبِّيَ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ ١٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (952,8,'150',6,'Al-An‘ām','الأنعَام',8,9,163,'دِينٗا قَيِّمٗا مِّلَّةَ إِبۡرَٰهِيمَ حَنِيفٗاۚ وَمَا كَانَ مِنَ اَ۬لۡمُشۡرِكِينَ ١٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (953,8,'150',6,'Al-An‘ām','الأنعَام',9,10,164,'قُلۡ إِنَّ صَلَاتِي وَنُسُكِي وَمَحۡيَايَ وَمَمَاتِي لِلَّهِ رَبِّ اِ۬لۡعَٰلَمِينَ ١٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (954,8,'150',6,'Al-An‘ām','الأنعَام',10,10,165,'لَا شَرِيكَ لَهُۥۖ وَبِذَٰلِكَ أُمِرۡتُ وَأَنَا۠ أَوَّلُ اُ۬لۡمُسۡلِمِينَ ١٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (955,8,'150',6,'Al-An‘ām','الأنعَام',11,13,166,'قُلۡ أَغَيۡرَ اَ۬للَّهِ أَبۡغِي رَبّٗا وَهۡوَ رَبُّ كُلِّ شَيۡءٖۚ وَلَا تَكۡسِبُ كُلُّ نَفۡسٍ إِلَّا عَلَيۡهَاۚ وَلَا تَزِرُ وَازِرَةٞ وِزۡرَ أُخۡر۪يٰۚ ثُمَّ إِلَىٰ رَبِّكُم مَّرۡجِعُكُمۡ فَيُنَبِّئُكُم بِمَا كُنتُمۡ فِيهِ تَخۡتَلِفُونَ ١٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (956,8,'150',6,'Al-An‘ām','الأنعَام',13,15,167,'وَهۡوَ اَ۬لَّذِي جَعَلَكُمۡ خَلَٰٓئِفَ اَ۬لۡأَرۡضِ وَرَفَعَ بَعۡضَكُمۡ فَوۡقَ بَعۡضٖ دَرَجَٰتٖ لِّيَبۡلُوَكُمۡ فِي مَآ ءَاتَىٰكُمۡۗ إِنَّ رَبَّكَ سَرِيعُ اُ۬لۡعِقَابِ وَإِنَّهُۥ لَغَفُورٞ رَّحِيمُۢ ١٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (957,8,'151',7,'Al-A‘rāf','الأعرَاف',3,4,1,'الٓمٓصٓۚ كِتَٰبٌ أُنزِلَ إِلَيۡكَ فَلَا يَكُن فِي صَدۡرِكَ حَرَجٞ مِّنۡهُ لِتُنذِرَ بِهِۦ وَذِكۡر۪يٰ لِلۡمُؤۡمِنِينَ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (958,8,'151',7,'Al-A‘rāf','الأعرَاف',4,5,2,'اَ۪تَّبِعُواْ مَآ أُنزِلَ إِلَيۡكُم مِّن رَّبِّكُمۡ وَلَا تَتَّبِعُواْ مِن دُونِهِۦٓ أَوۡلِيَآءَۗ قَلِيلٗا مَّا تَذَّكَّرُونَ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (959,8,'151',7,'Al-A‘rāf','الأعرَاف',6,7,3,'وَكَم مِّن قَرۡيَةٍ أَهۡلَكۡنَٰهَا فَجَآءَهَا بَأۡسُنَا بَيَٰتًا أَوۡ هُمۡ قَآئِلُونَ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (960,8,'151',7,'Al-A‘rāf','الأعرَاف',7,8,4,'فَمَا كَانَ دَعۡوۭىٰهُمۡ إِذ جَّآءَهُم بَأۡسُنَآ إِلَّآ أَن قَالُوٓاْ إِنَّا كُنَّا ظَٰلِمِينَ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (961,8,'151',7,'Al-A‘rāf','الأعرَاف',8,9,5,'فَلَنَسۡـَٔلَنَّ اَ۬لَّذِينَ أُرۡسِلَ إِلَيۡهِمۡ وَلَنَسۡـَٔلَنَّ اَ۬لۡمُرۡسَلِينَ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (962,8,'151',7,'Al-A‘rāf','الأعرَاف',9,9,6,'فَلَنَقُصَّنَّ عَلَيۡهِم بِعِلۡمٖۖ وَمَا كُنَّا غَآئِبِينَ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (963,8,'151',7,'Al-A‘rāf','الأعرَاف',10,11,7,'وَاَلۡوَزۡنُ يَوۡمَئِذٍ اِ۬لۡحَقُّۚ فَمَن ثَقُلَتۡ مَوَٰزِينُهُۥ فَأُوْلَٰٓئِكَ هُمُ اُ۬لۡمُفۡلِحُونَ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (964,8,'151',7,'Al-A‘rāf','الأعرَاف',11,12,8,'وَمَنۡ خَفَّتۡ مَوَٰزِينُهُۥ فَأُوْلَٰٓئِكَ اَ۬لَّذِينَ خَسِرُوٓاْ أَنفُسَهُم بِمَا كَانُواْ بِـَٔايَٰتِنَا يَظۡلِمُونَ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (965,8,'151',7,'Al-A‘rāf','الأعرَاف',12,13,9,'وَلَقَدۡ مَكَّنَّٰكُمۡ فِي اِ۬لۡأَرۡضِ وَجَعَلۡنَا لَكُمۡ فِيهَا مَعَٰيِشَۗ قَلِيلٗا مَّا تَشۡكُرُونَ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (966,8,'151',7,'Al-A‘rāf','الأعرَاف',14,15,10,'وَلَقَدۡ خَلَقۡنَٰكُمۡ ثُمَّ صَوَّرۡنَٰكُمۡ ثُمَّ قُلۡنَا لِلۡمَلَٰٓئِكَةِ اِ۟سۡجُدُواْ لِأٓدَمَ فَسَجَدُوٓاْ إِلَّآ إِبۡلِيسَ لَمۡ يَكُن مِّنَ اَ۬لسَّٰجِدِينَ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (967,8,'152',7,'Al-A‘rāf','الأعرَاف',1,2,11,'قَالَ مَا مَنَعَكَ أَلَّا تَسۡجُدَ إِذۡ أَمَرۡتُكَۖ قَالَ أَنَا۠ خَيۡرٞ مِّنۡهُ خَلَقۡتَنِي مِن نّ۪ارٖ وَخَلَقۡتَهُۥ مِن طِينٖ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (968,8,'152',7,'Al-A‘rāf','الأعرَاف',2,3,12,'قَالَ فَاَهۡبِطۡ مِنۡهَا فَمَا يَكُونُ لَكَ أَن تَتَكَبَّرَ فِيهَا فَاَخۡرُجۡ إِنَّكَ مِنَ اَ۬لصَّٰغِرِينَ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (969,8,'152',7,'Al-A‘rāf','الأعرَاف',3,3,13,'قَالَ أَنظِرۡنِيٓ إِلَىٰ يَوۡمِ يُبۡعَثُونَ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (970,8,'152',7,'Al-A‘rāf','الأعرَاف',4,4,14,'قَالَ إِنَّكَ مِنَ اَ۬لۡمُنظَرِينَ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (971,8,'152',7,'Al-A‘rāf','الأعرَاف',4,5,15,'قَالَ فَبِمَآ أَغۡوَيۡتَنِي لَأَقۡعُدَنَّ لَهُمۡ صِرَٰطَكَ اَ۬لۡمُسۡتَقِيمَ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (972,8,'152',7,'Al-A‘rāf','الأعرَاف',5,6,16,'ثُمَّ لَأٓتِيَنَّهُم مِّنۢ بَيۡنِ أَيۡدِيهِمۡ وَمِنۡ خَلۡفِهِمۡ وَعَنۡ أَيۡمَٰنِهِمۡ وَعَن شَمَآئِلِهِمۡۖ وَلَا تَجِدُ أَكۡثَرَهُمۡ شَٰكِرِينَ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (973,8,'152',7,'Al-A‘rāf','الأعرَاف',6,8,17,'قَالَ اَ۟خۡرُجۡ مِنۡهَا مَذۡءُومٗا مَّدۡحُورٗاۖ لَّمَن تَبِعَكَ مِنۡهُمۡ لَأَمۡلَأَنَّ جَهَنَّمَ مِنكُمۡ أَجۡمَعِينَ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (974,8,'152',7,'Al-A‘rāf','الأعرَاف',8,9,18,'۞ وَيَٰٓـَٔادَمُ اُ۟سۡكُنۡ أَنتَ وَزَوۡجُكَ اَ۬لۡجَنَّةَ فَكُلَا مِنۡ حَيۡثُ شِئۡتُمَا وَلَا تَقۡرَبَا هَٰذِهِ اِ۬لشَّجَرَةَ فَتَكُونَا مِنَ اَ۬لظَّٰلِمِينَ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (975,8,'152',7,'Al-A‘rāf','الأعرَاف',9,12,19,'فَوَسۡوَسَ لَهُمَا اَ۬لشَّيۡطَٰنُ لِيُبۡدِيَ لَهُمَا مَا وُۥرِيَ عَنۡهُمَا مِن سَوۡءَٰتِهِمَا وَقَالَ مَا نَهَىٰكُمَا رَبُّكُمَا عَنۡ هَٰذِهِ اِ۬لشَّجَرَةِ إِلَّآ أَن تَكُونَا مَلَكَيۡنِ أَوۡ تَكُونَا مِنَ اَ۬لۡخَٰلِدِينَ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (976,8,'152',7,'Al-A‘rāf','الأعرَاف',12,12,20,'وَقَاسَمَهُمَآ إِنِّي لَكُمَا لَمِنَ اَ۬لنَّٰصِحِينَ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (977,8,'152',7,'Al-A‘rāf','الأعرَاف',13,15,21,'فَدَلَّىٰهُمَا بِغُرُورٖۚ فَلَمَّا ذَاقَا اَ۬لشَّجَرَةَ بَدَتۡ لَهُمَا سَوۡءَٰتُهُمَا وَطَفِقَا يَخۡصِفَانِ عَلَيۡهِمَا مِن وَرَقِ اِ۬لۡجَنَّةِۖ وَنَادَىٰهُمَا رَبُّهُمَآ أَلَمۡ أَنۡهَكُمَا عَن تِلۡكُمَا اَ۬لشَّجَرَةِ وَأَقُل لَّكُمَآ إِنَّ اَ۬لشَّيۡطَٰنَ لَكُمَا عَدُوّٞ مُّبِينٞ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (978,8,'153',7,'Al-A‘rāf','الأعرَاف',1,2,22,'قَالَا رَبَّنَا ظَلَمۡنَآ أَنفُسَنَا وَإِن لَّمۡ تَغۡفِر لَّنَا وَتَرۡحَمۡنَا لَنَكُونَنَّ مِنَ اَ۬لۡخَٰسِرِينَ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (979,8,'153',7,'Al-A‘rāf','الأعرَاف',2,3,23,'قَالَ اَ۪هۡبِطُواْ بَعۡضُكُمۡ لِبَعۡضٍ عَدُوّٞۖ وَلَكُمۡ فِي اِ۬لۡأَرۡضِ مُسۡتَقَرّٞ وَمَتَٰعٌ إِلَىٰ حِينٖ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (980,8,'153',7,'Al-A‘rāf','الأعرَاف',3,4,24,'قَالَ فِيهَا تَحۡيَوۡنَ وَفِيهَا تَمُوتُونَ وَمِنۡهَا تُخۡرَجُونَ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (981,8,'153',7,'Al-A‘rāf','الأعرَاف',4,6,25,'يَٰبَنِيٓ ءَادَمَ قَدۡ أَنزَلۡنَا عَلَيۡكُمۡ لِبَاسٗا يُوَٰرِي سَوۡءَٰتِكُمۡ وَرِيشٗاۖ وَلِبَاسُ اُ۬لتَّقۡوۭيٰ ذَٰلِكَ خَيۡرٞۚ ذَٰلِكَ مِنۡ ءَايَٰتِ اِ۬للَّهِ لَعَلَّهُمۡ يَذَّكَّرُونَ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (982,8,'153',7,'Al-A‘rāf','الأعرَاف',6,9,26,'يَٰبَنِيٓ ءَادَمَ لَا يَفۡتِنَنَّكُمُ اُ۬لشَّيۡطَٰنُ كَمَآ أَخۡرَجَ أَبَوَيۡكُم مِّنَ اَ۬لۡجَنَّةِ يَنزِعُ عَنۡهُمَا لِبَاسَهُمَا لِيُرِيَهُمَا سَوۡءَٰتِهِمَآۗ إِنَّهُۥ يَر۪ىٰكُمۡ هُوَ وَقَبِيلُهُۥ مِنۡ حَيۡثُ لَا تَرَوۡنَهُمۡۗ إِنَّا جَعَلۡنَا اَ۬لشَّيَٰطِينَ أَوۡلِيَآءَ لِلَّذِينَ لَا يُؤۡمِنُونَ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (983,8,'153',7,'Al-A‘rāf','الأعرَاف',10,11,27,'وَإِذَا فَعَلُواْ فَٰحِشَةٗ قَالُواْ وَجَدۡنَا عَلَيۡهَآ ءَابَآءَنَا وَاَللَّهُ أَمَرَنَا بِهَاۗ قُلۡ إِنَّ اَ۬للَّهَ لَا يَأۡمُرُ بِالۡفَحۡشَآءِۖ اَ۬تَقُولُونَ عَلَى اَ۬للَّهِ مَا لَا تَعۡلَمُونَ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (984,8,'153',7,'Al-A‘rāf','الأعرَاف',12,15,28,'قُلۡ أَمَرَ رَبِّي بِالۡقِسۡطِۖ وَأَقِيمُواْ وُجُوهَكُمۡ عِندَ كُلِّ مَسۡجِدٖ وَاَدۡعُوهُ مُخۡلِصِينَ لَهُ اُ۬لدِّينَۚ كَمَا بَدَأَكُمۡ تَعُودُونَۚ فَرِيقًا هَدَىٰ وَفَرِيقًا حَقَّ عَلَيۡهِمِ اِ۬لضَّلَٰلَةُۚ إِنَّهُمُ اُ۪تَّخَذُواْ اُ۬لشَّيَٰطِينَ أَوۡلِيَآءَ مِن دُونِ اِ۬للَّهِ وَيَحۡسِبُونَ أَنَّهُم مُّهۡتَدُونَ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (985,8,'154',7,'Al-A‘rāf','الأعرَاف',1,2,29,'يَٰبَنِيٓ ءَادَمَ خُذُواْ زِينَتَكُمۡ عِندَ كُلِّ مَسۡجِدٖ وَكُلُواْ وَاَشۡرَبُواْ وَلَا تُسۡرِفُوٓاْۚ إِنَّهُۥ لَا يُحِبُّ اُ۬لۡمُسۡرِفِينَ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (986,8,'154',7,'Al-A‘rāf','الأعرَاف',2,5,30,'۞ قُلۡ مَنۡ حَرَّمَ زِينَةَ اَ۬للَّهِ اِ۬لَّتِيٓ أَخۡرَجَ لِعِبَادِهِۦ وَاَلطَّيِّبَٰتِ مِنَ اَ۬لرِّزۡقِۚ قُلۡ هِيَ لِلَّذِينَ ءَامَنُواْ فِي اِ۬لۡحَيَوٰةِ اِ۬لدُّنۡيۭا خَالِصَةٗ يَوۡمَ اَ۬لۡقِيَٰمَةِۗ كَذَٰلِكَ نُفَصِّلُ اُ۬لۡأٓيَٰتِ لِقَوۡمٖ يَعۡلَمُونَ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (987,8,'154',7,'Al-A‘rāf','الأعرَاف',5,7,31,'قُلۡ إِنَّمَا حَرَّمَ رَبِّيَ اَ۬لۡفَوَٰحِشَ مَا ظَهَرَ مِنۡهَا وَمَا بَطَنَ وَاَلۡإِثۡمَ وَاَلۡبَغۡيَ بِغَيۡرِ اِ۬لۡحَقِّ وَأَن تُشۡرِكُواْ بِاللَّهِ مَا لَمۡ يُنزِلۡ بِهِۦ سُلۡطَٰنٗا وَأَن تَقُولُواْ عَلَى اَ۬للَّهِ مَا لَا تَعۡلَمُونَ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (988,8,'154',7,'Al-A‘rāf','الأعرَاف',7,8,32,'وَلِكُلِّ أُمَّةٍ أَجَلٞۖ فَإِذَا جَآ أَجَلُهُمۡ لَا يَسۡتَأۡخِرُونَ سَاعَةٗ وَلَا يَسۡتَقۡدِمُونَ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (989,8,'154',7,'Al-A‘rāf','الأعرَاف',9,10,33,'يَٰبَنِيٓ ءَادَمَ إِمَّا يَأۡتِيَنَّكُمۡ رُسُلٞ مِّنكُمۡ يَقُصُّونَ عَلَيۡكُمۡ ءَايَٰتِي فَمَنِ اِ۪تَّقَىٰ وَأَصۡلَحَ فَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (990,8,'154',7,'Al-A‘rāf','الأعرَاف',10,12,34,'وَاَلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَاَسۡتَكۡبَرُواْ عَنۡهَآ أُوْلَٰٓئِكَ أَصۡحَٰبُ اُ۬لنّ۪ارِۖ هُمۡ فِيهَا خَٰلِدُونَ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (991,8,'154',7,'Al-A‘rāf','الأعرَاف',12,15,35,'فَمَنۡ أَظۡلَمُ مِمَّنِ اِ۪فۡتَر۪يٰ عَلَى اَ۬للَّهِ كَذِبًا أَوۡ كَذَّبَ بِـَٔايَٰتِهِۦٓۚ أُوْلَٰٓئِكَ يَنَالُهُمۡ نَصِيبُهُم مِّنَ اَ۬لۡكِتَٰبِۖ حَتَّىٰٓ إِذَا جَآءَتۡهُمۡ رُسۡلُنَا يَتَوَفَّوۡنَهُمۡ قَالُوٓاْ أَيۡنَ مَا كُنتُمۡ تَدۡعُونَ مِن دُونِ اِ۬للَّهِۖ قَالُواْ ضَلُّواْ عَنَّا وَشَهِدُواْ عَلَىٰٓ أَنفُسِهِمۡ أَنَّهُمۡ كَانُواْ كٰ۪فِرِينَ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (992,8,'155',7,'Al-A‘rāf','الأعرَاف',1,4,36,'قَالَ اَ۟دۡخُلُواْ فِيٓ أُمَمٖ قَدۡ خَلَتۡ مِن قَبۡلِكُم مِّنَ اَ۬لۡجِنِّ وَاَلۡإِنسِ فِي اِ۬لنّ۪ارِۖ كُلَّمَا دَخَلَتۡ أُمَّةٞ لَّعَنَتۡ أُخۡتَهَاۖ حَتَّىٰٓ إِذَا اَ۪دَّارَكُواْ فِيهَا جَمِيعٗا قَالَتۡ أُخۡر۪ىٰهُمۡ لِأُولۭىٰهُمۡ رَبَّنَا هَٰٓؤُلَآءِ اَ۬ضَلُّونَا فَـَٔاتِهِمۡ عَذَابٗا ضِعۡفٗا مِّنَ اَ۬لنّ۪ارِ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (993,8,'155',7,'Al-A‘rāf','الأعرَاف',4,4,37,'قَالَ لِكُلّٖ ضِعۡفٞ وَلَٰكِن لَّا تَعۡلَمُونَ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (994,8,'155',7,'Al-A‘rāf','الأعرَاف',5,6,38,'۞ وَقَالَتۡ أُولۭىٰهُمۡ لِأُخۡر۪ىٰهُمۡ فَمَا كَانَ لَكُمۡ عَلَيۡنَا مِن فَضۡلٖ فَذُوقُواْ اُ۬لۡعَذَابَ بِمَا كُنتُمۡ تَكۡسِبُونَ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (995,8,'155',7,'Al-A‘rāf','الأعرَاف',6,9,39,'إِنَّ اَ۬لَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَاَسۡتَكۡبَرُواْ عَنۡهَا لَا تُفۡتَحُ لَهُمۡ أَبۡوَٰبُ اُ۬لسَّمَآءِ وَلَا يَدۡخُلُونَ اَ۬لۡجَنَّةَ حَتَّىٰ يَلِجَ اَ۬لۡجَمَلُ فِي سَمِّ اِ۬لۡخِيَاطِۚ وَكَذَٰلِكَ نَجۡزِي اِ۬لۡمُجۡرِمِينَ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (996,8,'155',7,'Al-A‘rāf','الأعرَاف',9,10,40,'لَهُم مِّن جَهَنَّمَ مِهَادٞ وَمِن فَوۡقِهِمۡ غَوَاشٖۚ وَكَذَٰلِكَ نَجۡزِي اِ۬لظَّٰلِمِينَ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (997,8,'155',7,'Al-A‘rāf','الأعرَاف',10,12,41,'وَاَلَّذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ لَا نُكَلِّفُ نَفۡسًا إِلَّا وُسۡعَهَآ أُوْلَٰٓئِكَ أَصۡحَٰبُ اُ۬لۡجَنَّةِۖ هُمۡ فِيهَا خَٰلِدُونَ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (998,8,'155',7,'Al-A‘rāf','الأعرَاف',12,15,42,'وَنَزَعۡنَا مَا فِي صُدُورِهِم مِّنۡ غِلّٖ تَجۡرِي مِن تَحۡتِهِمِ اِ۬لۡأَنۡهَٰرُۖ وَقَالُواْ اُ۬لۡحَمۡدُ لِلَّهِ اِ۬لَّذِي هَدَىٰنَا لِهَٰذَا وَمَا كُنَّا لِنَهۡتَدِيَ لَوۡلَآ أَنۡ هَدَىٰنَا اَ۬للَّهُۖ لَقَد جَّآءَتۡ رُسُلُ رَبِّنَا بِالۡحَقِّۖ وَنُودُوٓاْ أَن تِلۡكُمُ اُ۬لۡجَنَّةُ أُورِثتُّمُوهَا بِمَا كُنتُمۡ تَعۡمَلُونَ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (999,8,'156',7,'Al-A‘rāf','الأعرَاف',1,3,43,'وَنَادَىٰٓ أَصۡحَٰبُ اُ۬لۡجَنَّةِ أَصۡحَٰبَ اَ۬لنّ۪ارِ أَن قَدۡ وَجَدۡنَا مَا وَعَدَنَا رَبُّنَا حَقّٗا فَهَلۡ وَجَدتُّم مَّا وَعَدَ رَبُّكُمۡ حَقّٗاۖ قَالُواْ نَعَمۡۚ فَأَذَّنَ مُؤَذِّنُۢ بَيۡنَهُمۡ أَن لَّعۡنَةُ اُ۬للَّهِ عَلَى اَ۬لظَّٰلِمِينَ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1000,8,'156',7,'Al-A‘rāf','الأعرَاف',3,4,44,'اَ۬لَّذِينَ يَصُدُّونَ عَن سَبِيلِ اِ۬للَّهِ وَيَبۡغُونَهَا عِوَجٗا وَهُم بِالۡأٓخِرَةِ كَٰفِرُونَ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1001,8,'156',7,'Al-A‘rāf','الأعرَاف',4,6,45,'وَبَيۡنَهُمَا حِجَابٞۚ وَعَلَى اَ۬لۡأَعۡرَافِ رِجَالٞ يَعۡرِفُونَ كُلَّۢا بِسِيمۭىٰهُمۡۚ وَنَادَوۡاْ أَصۡحَٰبَ اَ۬لۡجَنَّةِ أَن سَلَٰمٌ عَلَيۡكُمۡۚ لَمۡ يَدۡخُلُوهَا وَهُمۡ يَطۡمَعُونَ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1002,8,'156',7,'Al-A‘rāf','الأعرَاف',7,8,46,'۞ وَإِذَا صُرِفَتۡ أَبۡصَٰرُهُمۡ تِلۡقَآ أَصۡحَٰبِ اِ۬لنّ۪ارِ قَالُواْ رَبَّنَا لَا تَجۡعَلۡنَا مَعَ اَ۬لۡقَوۡمِ اِ۬لظَّٰلِمِينَ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1003,8,'156',7,'Al-A‘rāf','الأعرَاف',8,9,47,'وَنَادَىٰٓ أَصۡحَٰبُ اُ۬لۡأَعۡرَافِ رِجَالٗا يَعۡرِفُونَهُم بِسِيمۭىٰهُمۡ قَالُواْ مَآ أَغۡنَىٰ عَنكُمۡ جَمۡعُكُمۡ وَمَا كُنتُمۡ تَسۡتَكۡبِرُونَ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1004,8,'156',7,'Al-A‘rāf','الأعرَاف',10,11,48,'أَهَٰٓؤُلَآءِ اِ۬لَّذِينَ أَقۡسَمۡتُمۡ لَا يَنَالُهُمُ اُ۬للَّهُ بِرَحۡمَةٍۚ اِ۟دۡخُلُواْ اُ۬لۡجَنَّةَ لَا خَوۡفٌ عَلَيۡكُمۡ وَلَآ أَنتُمۡ تَحۡزَنُونَ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1005,8,'156',7,'Al-A‘rāf','الأعرَاف',11,13,49,'وَنَادَىٰٓ أَصۡحَٰبُ اُ۬لنّ۪ارِ أَصۡحَٰبَ اَ۬لۡجَنَّةِ أَنۡ أَفِيضُواْ عَلَيۡنَا مِنَ اَ۬لۡمَآءِ اَ۬وۡ مِمَّا رَزَقَكُمُ اُ۬للَّهُۚ قَالُوٓاْ إِنَّ اَ۬للَّهَ حَرَّمَهُمَا عَلَى اَ۬لۡكٰ۪فِرِينَ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1006,8,'156',7,'Al-A‘rāf','الأعرَاف',13,15,50,'اَ۬لَّذِينَ اَ۪تَّخَذُواْ دِينَهُمۡ لَهۡوٗا وَلَعِبٗا وَغَرَّتۡهُمُ اُ۬لۡحَيَوٰةُ اُ۬لدُّنۡيۭاۚ فَاَلۡيَوۡمَ نَنسَىٰهُمۡ كَمَا نَسُواْ لِقَآءَ يَوۡمِهِمۡ هَٰذَا وَمَا كَانُواْ بِـَٔايَٰتِنَا يَجۡحَدُونَ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1007,8,'157',7,'Al-A‘rāf','الأعرَاف',1,2,51,'وَلَقَد جِّئۡنَٰهُم بِكِتَٰبٖ فَصَّلۡنَٰهُ عَلَىٰ عِلۡمٍ هُدٗى وَرَحۡمَةٗ لِّقَوۡمٖ يُؤۡمِنُونَ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1008,8,'157',7,'Al-A‘rāf','الأعرَاف',2,6,52,'هَلۡ يَنظُرُونَ إِلَّا تَأۡوِيلَهُۥۚ يَوۡمَ يَأۡتِي تَأۡوِيلُهُۥ يَقُولُ اُ۬لَّذِينَ نَسُوهُ مِن قَبۡلُ قَد جَّآءَتۡ رُسُلُ رَبِّنَا بِالۡحَقِّ فَهَل لَّنَا مِن شُفَعَآءَ فَيَشۡفَعُواْ لَنَآ أَوۡ نُرَدُّ فَنَعۡمَلَ غَيۡرَ اَ۬لَّذِي كُنَّا نَعۡمَلُۚ قَدۡ خَسِرُوٓاْ أَنفُسَهُمۡ وَضَلَّ عَنۡهُم مَّا كَانُواْ يَفۡتَرُونَ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1009,8,'157',7,'Al-A‘rāf','الأعرَاف',6,9,53,'إِنَّ رَبَّكُمُ اُ۬للَّهُ اُ۬لَّذِي خَلَقَ اَ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضَ فِي سِتَّةِ أَيَّامٖ ثُمَّ اَ۪سۡتَوَىٰ عَلَى اَ۬لۡعَرۡشِ يُغۡشِي اِ۬لَّيۡلَ اَ۬لنَّهَارَ يَطۡلُبُهُۥ حَثِيثٗا وَاَلشَّمۡسَ وَاَلۡقَمَرَ وَاَلنُّجُومَ مُسَخَّرَٰتِۢ بِأَمۡرِهِۦٓۗ أَلَا لَهُ اُ۬لۡخَلۡقُ وَاَلۡأَمۡرُۗ تَبَارَكَ اَ۬للَّهُ رَبُّ اُ۬لۡعَٰلَمِينَ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1010,8,'157',7,'Al-A‘rāf','الأعرَاف',10,10,54,'۞ اَ۟دۡعُواْ رَبَّكُمۡ تَضَرُّعٗا وَخُفۡيَةًۚ إِنَّهُۥ لَا يُحِبُّ اُ۬لۡمُعۡتَدِينَ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1011,8,'157',7,'Al-A‘rāf','الأعرَاف',11,12,55,'وَلَا تُفۡسِدُواْ فِي اِ۬لۡأَرۡضِ بَعۡدَ إِصۡلَٰحِهَا وَاَدۡعُوهُ خَوۡفٗا وَطَمَعًاۚ إِنَّ رَحۡمَتَ اَ۬للَّهِ قَرِيبٞ مِّنَ اَ۬لۡمُحۡسِنِينَ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1012,8,'157',7,'Al-A‘rāf','الأعرَاف',12,15,56,'وَهۡوَ اَ۬لَّذِي يُرۡسِلُ اُ۬لرِّيَٰحَ نُشُرَۢا بَيۡنَ يَدَيۡ رَحۡمَتِهِۦۖ حَتَّىٰٓ إِذَآ أَقَلَّت سَّحَابٗا ثِقَالٗا سُقۡنَٰهُ لِبَلَدٖ مَّيۡتٖ فَأَنزَلۡنَا بِهِ اِ۬لۡمَآءَ فَأَخۡرَجۡنَا بِهِۦ مِن كُلِّ اِ۬لثَّمَرَٰتِۚ كَذَٰلِكَ نُخۡرِجُ اُ۬لۡمَوۡتۭيٰ لَعَلَّكُمۡ تَذَّكَّرُونَ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1013,8,'158',7,'Al-A‘rāf','الأعرَاف',1,2,57,'وَاَلۡبَلَدُ اُ۬لطَّيِّبُ يَخۡرُجُ نَبَاتُهُۥ بِإِذۡنِ رَبِّهِۦۖ وَاَلَّذِي خَبُثَ لَا يَخۡرُجُ إِلَّا نَكِدٗاۚ كَذَٰلِكَ نُصَرِّفُ اُ۬لۡأٓيَٰتِ لِقَوۡمٖ يَشۡكُرُونَ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1014,8,'158',7,'Al-A‘rāf','الأعرَاف',3,4,58,'لَقَدۡ أَرۡسَلۡنَا نُوحًا إِلَىٰ قَوۡمِهِۦ فَقَالَ يَٰقَوۡمِ اِ۟عۡبُدُواْ اُ۬للَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥٓۖ إِنِّيَ أَخَافُ عَلَيۡكُمۡ عَذَابَ يَوۡمٍ عَظِيمٖ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1015,8,'158',7,'Al-A‘rāf','الأعرَاف',5,5,59,'قَالَ اَ۬لۡمَلَأُ مِن قَوۡمِهِۦٓ إِنَّا لَنَر۪ىٰكَ فِي ضَلَٰلٖ مُّبِينٖ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1016,8,'158',7,'Al-A‘rāf','الأعرَاف',5,6,60,'قَالَ يَٰقَوۡمِ لَيۡسَ بِي ضَلَٰلَةٞ وَلَٰكِنِّي رَسُولٞ مِّن رَّبِّ اِ۬لۡعَٰلَمِينَ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1017,8,'158',7,'Al-A‘rāf','الأعرَاف',7,8,61,'أُبۡلِغُكُمۡ رِسَٰلَٰتِ رَبِّي وَأَنصَحُ لَكُمۡ وَأَعۡلَمُ مِنَ اَ۬للَّهِ مَا لَا تَعۡلَمُونَ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1018,8,'158',7,'Al-A‘rāf','الأعرَاف',8,9,62,'أَوَعَجِبۡتُمۡ أَن جَآءَكُمۡ ذِكۡرٞ مِّن رَّبِّكُمۡ عَلَىٰ رَجُلٖ مِّنكُمۡ لِيُنذِرَكُمۡ وَلِتَتَّقُواْ وَلَعَلَّكُمۡ تُرۡحَمُونَ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1019,8,'158',7,'Al-A‘rāf','الأعرَاف',10,11,63,'فَكَذَّبُوهُ فَأَنجَيۡنَٰهُ وَاَلَّذِينَ مَعَهُۥ فِي اِ۬لۡفُلۡكِ وَأَغۡرَقۡنَا اَ۬لَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَآۚ إِنَّهُمۡ كَانُواْ قَوۡمًا عَمِينَ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1020,8,'158',7,'Al-A‘rāf','الأعرَاف',11,13,64,'۞ وَإِلَىٰ عَادٍ أَخَاهُمۡ هُودٗاۚ قَالَ يَٰقَوۡمِ اِ۟عۡبُدُواْ اُ۬للَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥٓۚ أَفَلَا تَتَّقُونَ ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1021,8,'158',7,'Al-A‘rāf','الأعرَاف',13,14,65,'قَالَ اَ۬لۡمَلَأُ اُ۬لَّذِينَ كَفَرُواْ مِن قَوۡمِهِۦٓ إِنَّا لَنَر۪ىٰكَ فِي سَفَاهَةٖ وَإِنَّا لَنَظُنُّكَ مِنَ اَ۬لۡكَٰذِبِينَ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1022,8,'158',7,'Al-A‘rāf','الأعرَاف',15,15,66,'قَالَ يَٰقَوۡمِ لَيۡسَ بِي سَفَاهَةٞ وَلَٰكِنِّي رَسُولٞ مِّن رَّبِّ اِ۬لۡعَٰلَمِينَ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1023,8,'159',7,'Al-A‘rāf','الأعرَاف',1,1,67,'أُبۡلِغُكُمۡ رِسَٰلَٰتِ رَبِّي وَأَنَا۠ لَكُمۡ نَاصِحٌ أَمِينٌ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1024,8,'159',7,'Al-A‘rāf','الأعرَاف',1,4,68,'أَوَعَجِبۡتُمۡ أَن جَآءَكُمۡ ذِكۡرٞ مِّن رَّبِّكُمۡ عَلَىٰ رَجُلٖ مِّنكُمۡ لِيُنذِرَكُمۡۚ وَاَذۡكُرُوٓاْ إِذ جَّعَلَكُمۡ خُلَفَآءَ مِنۢ بَعۡدِ قَوۡمِ نُوحٖ وَزَادَكُمۡ فِي اِ۬لۡخَلۡقِ بَصۜۡطَةٗۖ فَاَذۡكُرُوٓاْ ءَالَآءَ اَ۬للَّهِ لَعَلَّكُمۡ تُفۡلِحُونَ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1025,8,'159',7,'Al-A‘rāf','الأعرَاف',5,6,69,'قَالُوٓاْ أَجِئۡتَنَا لِنَعۡبُدَ اَ۬للَّهَ وَحۡدَهُۥ وَنَذَرَ مَا كَانَ يَعۡبُدُ ءَابَآؤُنَاۖ فَأۡتِنَا بِمَا تَعِدُنَآ إِن كُنتَ مِنَ اَ۬لصَّٰدِقِينَ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1026,8,'159',7,'Al-A‘rāf','الأعرَاف',7,10,70,'قَالَ قَدۡ وَقَعَ عَلَيۡكُم مِّن رَّبِّكُمۡ رِجۡسٞ وَغَضَبٌۖ أَتُجَٰدِلُونَنِي فِيٓ أَسۡمَآءٖ سَمَّيۡتُمُوهَآ أَنتُمۡ وَءَابَآؤُكُم مَّا نَزَّلَ اَ۬للَّهُ بِهَا مِن سُلۡطَٰنٖۚ فَاَنتَظِرُوٓاْ إِنِّي مَعَكُم مِّنَ اَ۬لۡمُنتَظِرِينَ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1027,8,'159',7,'Al-A‘rāf','الأعرَاف',10,11,71,'فَأَنجَيۡنَٰهُ وَاَلَّذِينَ مَعَهُۥ بِرَحۡمَةٖ مِّنَّا وَقَطَعۡنَا دَابِرَ اَ۬لَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَاۖ وَمَا كَانُواْ مُؤۡمِنِينَ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1028,8,'159',7,'Al-A‘rāf','الأعرَاف',12,15,72,'وَإِلَىٰ ثَمُودَ أَخَاهُمۡ صَٰلِحٗاۚ قَالَ يَٰقَوۡمِ اِ۟عۡبُدُواْ اُ۬للَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥۖ قَد جَّآءَتۡكُم بَيِّنَةٞ مِّن رَّبِّكُمۡۖ هَٰذِهِۦ نَاقَةُ اُ۬للَّهِ لَكُمۡ ءَايَةٗۖ فَذَرُوهَا تَأۡكُلۡ فِيٓ أَرۡضِ اِ۬للَّهِۖ وَلَا تَمَسُّوهَا بِسُوٓءٖ فَيَأۡخُذَكُمۡ عَذَابٌ أَلِيمٞ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1029,8,'160',7,'Al-A‘rāf','الأعرَاف',1,4,73,'وَاَذۡكُرُوٓاْ إِذ جَّعَلَكُمۡ خُلَفَآءَ مِنۢ بَعۡدِ عَادٖ وَبَوَّأَكُمۡ فِي اِ۬لۡأَرۡضِ تَتَّخِذُونَ مِن سُهُولِهَا قُصُورٗا وَتَنۡحِتُونَ اَ۬لۡجِبَالَ بُيُوتٗاۖ فَاَذۡكُرُوٓاْ ءَالَآءَ اَ۬للَّهِ وَلَا تَعۡثَوۡاْ فِي اِ۬لۡأَرۡضِ مُفۡسِدِينَ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1030,8,'160',7,'Al-A‘rāf','الأعرَاف',4,7,74,'۞ قَالَ اَ۬لۡمَلَأُ اُ۬لَّذِينَ اَ۪سۡتَكۡبَرُواْ مِن قَوۡمِهِۦ لِلَّذِينَ اَ۟سۡتُضۡعِفُواْ لِمَنۡ ءَامَنَ مِنۡهُمۡ أَتَعۡلَمُونَ أَنَّ صَٰلِحٗا مُّرۡسَلٞ مِّن رَّبِّهِۦۚ قَالُوٓاْ إِنَّا بِمَآ أُرۡسِلَ بِهِۦ مُؤۡمِنُونَ ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1031,8,'160',7,'Al-A‘rāf','الأعرَاف',7,8,75,'قَالَ اَ۬لَّذِينَ اَ۪سۡتَكۡبَرُوٓاْ إِنَّا بِالَّذِيٓ ءَامَنتُم بِهِۦ كَٰفِرُونَ ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1032,8,'160',7,'Al-A‘rāf','الأعرَاف',8,10,76,'فَعَقَرُواْ اُ۬لنَّاقَةَ وَعَتَوۡاْ عَنۡ أَمۡرِ رَبِّهِمۡ وَقَالُواْ يَٰصَٰلِحُ اُ۪ئۡتِنَا بِمَا تَعِدُنَآ إِن كُنتَ مِنَ اَ۬لۡمُرۡسَلِينَ ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1033,8,'160',7,'Al-A‘rāf','الأعرَاف',10,11,77,'فَأَخَذَتۡهُمُ اُ۬لرَّجۡفَةُ فَأَصۡبَحُواْ فِي د۪ارِهِمۡ جَٰثِمِينَ ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1034,8,'160',7,'Al-A‘rāf','الأعرَاف',11,12,78,'فَتَوَلَّىٰ عَنۡهُمۡ وَقَالَ يَٰقَوۡمِ لَقَدۡ أَبۡلَغۡتُكُمۡ رِسَالَةَ رَبِّي وَنَصَحۡتُ لَكُمۡ وَلَٰكِن لَّا تُحِبُّونَ اَ۬لنَّٰصِحِينَ ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1035,8,'160',7,'Al-A‘rāf','الأعرَاف',13,14,79,'وَلُوطًا إِذۡ قَالَ لِقَوۡمِهِۦٓ أَتَأۡتُونَ اَ۬لۡفَٰحِشَةَ مَا سَبَقَكُم بِهَا مِنۡ أَحَدٖ مِّنَ اَ۬لۡعَٰلَمِينَ ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1036,8,'160',7,'Al-A‘rāf','الأعرَاف',14,15,80,'أَٰ۟نَّكُمۡ لَتَأۡتُونَ اَ۬لرِّجَالَ شَهۡوَةٗ مِّن دُونِ اِ۬لنِّسَآءِۚ بَلۡ أَنتُمۡ قَوۡمٞ مُّسۡرِفُونَ ٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1037,8,'161',7,'Al-A‘rāf','الأعرَاف',1,2,81,'وَمَا كَانَ جَوَابَ قَوۡمِهِۦٓ إِلَّآ أَن قَالُوٓاْ أَخۡرِجُوهُم مِّن قَرۡيَتِكُمۡۖ إِنَّهُمۡ أُنَاسٞ يَتَطَهَّرُونَ ٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1038,8,'161',7,'Al-A‘rāf','الأعرَاف',2,3,82,'فَأَنجَيۡنَٰهُ وَأَهۡلَهُۥٓ إِلَّا اَ۪مۡرَأَتَهُۥ كَانَتۡ مِنَ اَ۬لۡغَٰبِرِينَ ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1039,8,'161',7,'Al-A‘rāf','الأعرَاف',3,4,83,'وَأَمۡطَرۡنَا عَلَيۡهِم مَّطَرٗاۖ فَاَنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ اُ۬لۡمُجۡرِمِينَ ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1040,8,'161',7,'Al-A‘rāf','الأعرَاف',5,9,84,'وَإِلَىٰ مَدۡيَنَ أَخَاهُمۡ شُعَيۡبٗاۚ قَالَ يَٰقَوۡمِ اِ۟عۡبُدُواْ اُ۬للَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥۖ قَد جَّآءَتۡكُم بَيِّنَةٞ مِّن رَّبِّكُمۡۖ فَأَوۡفُواْ اُ۬لۡكَيۡلَ وَاَلۡمِيزَانَ وَلَا تَبۡخَسُواْ اُ۬لنَّاسَ أَشۡيَآءَهُمۡ وَلَا تُفۡسِدُواْ فِي اِ۬لۡأَرۡضِ بَعۡدَ إِصۡلَٰحِهَاۚ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ إِن كُنتُم مُّؤۡمِنِينَ ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1041,8,'161',7,'Al-A‘rāf','الأعرَاف',9,13,85,'وَلَا تَقۡعُدُواْ بِكُلِّ صِرَٰطٖ تُوعِدُونَ وَتَصُدُّونَ عَن سَبِيلِ اِ۬للَّهِ مَنۡ ءَامَنَ بِهِۦ وَتَبۡغُونَهَا عِوَجٗاۚ وَاَذۡكُرُوٓاْ إِذۡ كُنتُمۡ قَلِيلٗا فَكَثَّرَكُمۡۖ وَاَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ اُ۬لۡمُفۡسِدِينَ ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1042,8,'161',7,'Al-A‘rāf','الأعرَاف',13,15,86,'وَإِن كَانَ طَآئِفَةٞ مِّنكُمۡ ءَامَنُواْ بِالَّذِيٓ أُرۡسِلۡتُ بِهِۦ وَطَآئِفَةٞ لَّمۡ يُؤۡمِنُواْ فَاَصۡبِرُواْ حَتَّىٰ يَحۡكُمَ اَ۬للَّهُ بَيۡنَنَاۚ وَهۡوَ خَيۡرُ اُ۬لۡحَٰكِمِينَ ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1043,9,'162',7,'Al-A‘rāf','الأعرَاف',1,3,87,'۞ قَالَ اَ۬لۡمَلَأُ اُ۬لَّذِينَ اَ۪سۡتَكۡبَرُواْ مِن قَوۡمِهِۦ لَنُخۡرِجَنَّكَ يَٰشُعَيۡبُ وَاَلَّذِينَ ءَامَنُواْ مَعَكَ مِن قَرۡيَتِنَآ أَوۡ لَتَعُودُنَّ فِي مِلَّتِنَاۚ قَالَ أَوَلَوۡ كُنَّا كَٰرِهِينَ ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1044,9,'162',7,'Al-A‘rāf','الأعرَاف',3,6,88,'قَدِ اِ۪فۡتَرَيۡنَا عَلَى اَ۬للَّهِ كَذِبًا إِنۡ عُدۡنَا فِي مِلَّتِكُم بَعۡدَ إِذۡ نَجَّىٰنَا اَ۬للَّهُ مِنۡهَاۚ وَمَا يَكُونُ لَنَآ أَن نَّعُودَ فِيهَآ إِلَّآ أَن يَشَآءَ اَ۬للَّهُ رَبُّنَاۚ وَسِعَ رَبُّنَا كُلَّ شَيۡءٍ عِلۡمًاۚ عَلَى اَ۬للَّهِ تَوَكَّلۡنَاۚ رَبَّنَا اَ۪فۡتَحۡ بَيۡنَنَا وَبَيۡنَ قَوۡمِنَا بِالۡحَقِّ وَأَنتَ خَيۡرُ اُ۬لۡفَٰتِحِينَ ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1045,9,'162',7,'Al-A‘rāf','الأعرَاف',6,7,89,'وَقَالَ اَ۬لۡمَلَأُ اُ۬لَّذِينَ كَفَرُواْ مِن قَوۡمِهِۦ لَئِنِ اِ۪تَّبَعۡتُمۡ شُعَيۡبًا إِنَّكُمۡ إِذٗا لَّخَٰسِرُونَ ٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1046,9,'162',7,'Al-A‘rāf','الأعرَاف',8,8,90,'فَأَخَذَتۡهُمُ اُ۬لرَّجۡفَةُ فَأَصۡبَحُواْ فِي د۪ارِهِمۡ جَٰثِمِينَ ٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1047,9,'162',7,'Al-A‘rāf','الأعرَاف',8,10,91,'اَ۬لَّذِينَ كَذَّبُواْ شُعَيۡبٗا كَأَن لَّمۡ يَغۡنَوۡاْ فِيهَاۚ اَ۬لَّذِينَ كَذَّبُواْ شُعَيۡبٗا كَانُواْ هُمُ اُ۬لۡخَٰسِرِينَ ٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1048,9,'162',7,'Al-A‘rāf','الأعرَاف',10,12,92,'فَتَوَلَّىٰ عَنۡهُمۡ وَقَالَ يَٰقَوۡمِ لَقَدۡ أَبۡلَغۡتُكُمۡ رِسَٰلَٰتِ رَبِّي وَنَصَحۡتُ لَكُمۡۖ فَكَيۡفَ ءَاسَىٰ عَلَىٰ قَوۡمٖ كٰ۪فِرِينَ ٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1049,9,'162',7,'Al-A‘rāf','الأعرَاف',12,13,93,'وَمَآ أَرۡسَلۡنَا فِي قَرۡيَةٖ مِّن نَّبِيٍّ إِلَّآ أَخَذۡنَآ أَهۡلَهَا بِالۡبَأۡسَآءِ وَاَلضَّرَّآءِ لَعَلَّهُمۡ يَضَّرَّعُونَ ٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1050,9,'162',7,'Al-A‘rāf','الأعرَاف',13,15,94,'ثُمَّ بَدَّلۡنَا مَكَانَ اَ۬لسَّيِّئَةِ اِ۬لۡحَسَنَةَ حَتَّىٰ عَفَواْ وَّقَالُواْ قَدۡ مَسَّ ءَابَآءَنَا اَ۬لضَّرَّآءُ وَاَلسَّرَّآءُ فَأَخَذۡنَٰهُم بَغۡتَةٗ وَهُمۡ لَا يَشۡعُرُونَ ٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1051,9,'163',7,'Al-A‘rāf','الأعرَاف',1,3,95,'وَلَوۡ أَنَّ أَهۡلَ اَ۬لۡقُر۪يٰٓ ءَامَنُواْ وَاَتَّقَوۡاْ لَفَتَحۡنَا عَلَيۡهِم بَرَكَٰتٖ مِّنَ اَ۬لسَّمَآءِ وَاَلۡأَرۡضِ وَلَٰكِن كَذَّبُواْ فَأَخَذۡنَٰهُم بِمَا كَانُواْ يَكۡسِبُونَ ٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1052,9,'163',7,'Al-A‘rāf','الأعرَاف',3,4,96,'أَفَأَمِنَ أَهۡلُ اُ۬لۡقُر۪يٰٓ أَن يَأۡتِيَهُم بَأۡسُنَا بَيَٰتٗا وَهُمۡ نَآئِمُونَ ٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1053,9,'163',7,'Al-A‘rāf','الأعرَاف',4,5,97,'أَوَأَمِنَ أَهۡلُ اُ۬لۡقُر۪يٰٓ أَن يَأۡتِيَهُم بَأۡسُنَا ضُحٗى وَهُمۡ يَلۡعَبُونَ ٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1054,9,'163',7,'Al-A‘rāf','الأعرَاف',5,6,98,'أَفَأَمِنُواْ مَكۡرَ اَ۬للَّهِۚ فَلَا يَأۡمَنُ مَكۡرَ اَ۬للَّهِ إِلَّا اَ۬لۡقَوۡمُ اُ۬لۡخَٰسِرُونَ ٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1055,9,'163',7,'Al-A‘rāf','الأعرَاف',6,8,99,'۞ أَوَلَمۡ يَهۡدِ لِلَّذِينَ يَرِثُونَ اَ۬لۡأَرۡضَ مِنۢ بَعۡدِ أَهۡلِهَآ أَن لَّوۡ نَشَآءُ اَ۬صَبۡنَٰهُم بِذُنُوبِهِمۡۚ وَنَطۡبَعُ عَلَىٰ قُلُوبِهِمۡ فَهُمۡ لَا يَسۡمَعُونَ ٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1056,9,'163',7,'Al-A‘rāf','الأعرَاف',9,11,100,'تِلۡكَ اَ۬لۡقُر۪يٰ نَقُصُّ عَلَيۡكَ مِنۡ أَنۢبَآئِهَاۚ وَلَقَد جَّآءَتۡهُمۡ رُسۡلُهُم بِالۡبَيِّنَٰتِ فَمَا كَانُواْ لِيُؤۡمِنُواْ بِمَا كَذَّبُواْ مِن قَبۡلُۚ كَذَٰلِكَ يَطۡبَعُ اُ۬للَّهُ عَلَىٰ قُلُوبِ اِ۬لۡكٰ۪فِرِينَ ١٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1057,9,'163',7,'Al-A‘rāf','الأعرَاف',11,12,101,'وَمَا وَجَدۡنَا لِأَكۡثَرِهِم مِّنۡ عَهۡدٖۖ وَإِن وَجَدۡنَآ أَكۡثَرَهُمۡ لَفَٰسِقِينَ ١٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1058,9,'163',7,'Al-A‘rāf','الأعرَاف',13,14,102,'ثُمَّ بَعَثۡنَا مِنۢ بَعۡدِهِم مُّوسۭيٰ بِـَٔايَٰتِنَآ إِلَىٰ فِرۡعَوۡنَ وَمَلَإِيْهِۦ فَظَلَمُواْ بِهَاۖ فَاَنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ اُ۬لۡمُفۡسِدِينَ ١٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1059,9,'163',7,'Al-A‘rāf','الأعرَاف',15,15,103,'وَقَالَ مُوسۭيٰ يَٰفِرۡعَوۡنُ إِنِّي رَسُولٞ مِّن رَّبِّ اِ۬لۡعَٰلَمِينَ ١٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1060,9,'164',7,'Al-A‘rāf','الأعرَاف',1,2,104,'حَقِيقٌ عَلَىٰٓ أَن لَّآ أَقُولَ عَلَى اَ۬للَّهِ إِلَّا اَ۬لۡحَقَّۚ قَد جِّئۡتُكُم بِبَيِّنَةٖ مِّن رَّبِّكُمۡ فَأَرۡسِلۡ مَعِي بَنِيٓ إِسۡرَٰٓءِيلَ ١٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1061,9,'164',7,'Al-A‘rāf','الأعرَاف',2,3,105,'قَالَ إِن كُنتَ جِئۡتَ بِـَٔايَةٖ فَأۡتِ بِهَآ إِن كُنتَ مِنَ اَ۬لصَّٰدِقِينَ ١٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1062,9,'164',7,'Al-A‘rāf','الأعرَاف',3,4,106,'فَأَلۡقَىٰ عَصَاهُ فَإِذَا هِيَ ثُعۡبَانٞ مُّبِينٞ ١٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1063,9,'164',7,'Al-A‘rāf','الأعرَاف',4,5,107,'وَنَزَعَ يَدَهُۥ فَإِذَا هِيَ بَيۡضَآءُ لِلنَّٰظِرِينَ ١٠٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1064,9,'164',7,'Al-A‘rāf','الأعرَاف',5,6,108,'قَالَ اَ۬لۡمَلَأُ مِن قَوۡمِ فِرۡعَوۡنَ إِنَّ هَٰذَا لَسَٰحِرٌ عَلِيمٞ ١٠٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1065,9,'164',7,'Al-A‘rāf','الأعرَاف',6,6,109,'يُرِيدُ أَن يُخۡرِجَكُم مِّنۡ أَرۡضِكُمۡۖ فَمَاذَا تَأۡمُرُونَ ١٠٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1066,9,'164',7,'Al-A‘rāf','الأعرَاف',7,7,110,'قَالُوٓاْ أَرۡجِـٔۡهُ وَأَخَاهُ وَأَرۡسِلۡ فِي اِ۬لۡمَدَآئِنِ حَٰشِرِينَ ١١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1067,9,'164',7,'Al-A‘rāf','الأعرَاف',7,8,111,'يَأۡتُوكَ بِكُلِّ سَٰحِرٍ عَلِيمٖ ١١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1068,9,'164',7,'Al-A‘rāf','الأعرَاف',8,9,112,'وَجَآءَ اَ۬لسَّحَرَةُ فِرۡعَوۡنَ قَالُوٓاْ أَٰ۟نَّ لَنَا لَأَجۡرًا إِن كُنَّا نَحۡنُ اُ۬لۡغَٰلِبِينَ ١١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1069,9,'164',7,'Al-A‘rāf','الأعرَاف',9,10,113,'قَالَ نَعَمۡ وَإِنَّكُمۡ لَمِنَ اَ۬لۡمُقَرَّبِينَ ١١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1070,9,'164',7,'Al-A‘rāf','الأعرَاف',10,11,114,'قَالُواْ يَٰمُوسۭيٰٓ إِمَّآ أَن تُلۡقِيَ وَإِمَّآ أَن نَّكُونَ نَحۡنُ اُ۬لۡمُلۡقِينَ ١١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1071,9,'164',7,'Al-A‘rāf','الأعرَاف',11,12,115,'قَالَ أَلۡقُواْۖ فَلَمَّآ أَلۡقَوۡاْ سَحَرُوٓاْ أَعۡيُنَ اَ۬لنّ۪اسِ وَاَسۡتَرۡهَبُوهُمۡ وَجَآءُو بِسِحۡرٍ عَظِيمٖ ١١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1072,9,'164',7,'Al-A‘rāf','الأعرَاف',13,13,116,'۞ وَأَوۡحَيۡنَآ إِلَىٰ مُوسۭيٰٓ أَنۡ أَلۡقِ عَصَاكَۖ فَإِذَا هِيَ تَلَقَّفُ مَا يَأۡفِكُونَ ١١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1073,9,'164',7,'Al-A‘rāf','الأعرَاف',14,14,117,'فَوَقَعَ اَ۬لۡحَقُّ وَبَطَلَ مَا كَانُواْ يَعۡمَلُونَ ١١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1074,9,'164',7,'Al-A‘rāf','الأعرَاف',14,15,118,'فَغُلِبُواْ هُنَالِكَ وَاَنقَلَبُواْ صَٰغِرِينَ ١١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1075,9,'164',7,'Al-A‘rāf','الأعرَاف',15,15,119,'وَأُلۡقِيَ اَ۬لسَّحَرَةُ سَٰجِدِينَ ١١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1076,9,'165',7,'Al-A‘rāf','الأعرَاف',1,1,120,'قَالُوٓاْ ءَامَنَّا بِرَبِّ اِ۬لۡعَٰلَمِينَ ١٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1077,9,'165',7,'Al-A‘rāf','الأعرَاف',1,1,121,'رَبِّ مُوسۭيٰ وَهَٰرُونَ ١٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1078,9,'165',7,'Al-A‘rāf','الأعرَاف',1,3,122,'قَالَ فِرۡعَوۡنُ ءَاٰ۬مَنتُم بِهِۦ قَبۡلَ أَنۡ ءَاذَنَ لَكُمۡۖ إِنَّ هَٰذَا لَمَكۡرٞ مَّكَرۡتُمُوهُ فِي اِ۬لۡمَدِينَةِ لِتُخۡرِجُواْ مِنۡهَآ أَهۡلَهَاۖ فَسَوۡفَ تَعۡلَمُونَ ١٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1079,9,'165',7,'Al-A‘rāf','الأعرَاف',4,5,123,'لَأُقَطِّعَنَّ أَيۡدِيَكُمۡ وَأَرۡجُلَكُم مِّنۡ خِلَٰفٖ ثُمَّ لَأُصَلِّبَنَّكُمۡ أَجۡمَعِينَ ١٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1080,9,'165',7,'Al-A‘rāf','الأعرَاف',5,5,124,'قَالُوٓاْ إِنَّآ إِلَىٰ رَبِّنَا مُنقَلِبُونَ ١٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1081,9,'165',7,'Al-A‘rāf','الأعرَاف',5,7,125,'وَمَا تَنقِمُ مِنَّآ إِلَّآ أَنۡ ءَامَنَّا بِـَٔايَٰتِ رَبِّنَا لَمَّا جَآءَتۡنَاۚ رَبَّنَآ أَفۡرِغۡ عَلَيۡنَا صَبۡرٗا وَتَوَفَّنَا مُسۡلِمِينَ ١٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1082,9,'165',7,'Al-A‘rāf','الأعرَاف',7,9,126,'وَقَالَ اَ۬لۡمَلَأُ مِن قَوۡمِ فِرۡعَوۡنَ أَتَذَرُ مُوسۭيٰ وَقَوۡمَهُۥ لِيُفۡسِدُواْ فِي اِ۬لۡأَرۡضِ وَيَذَرَكَ وَءَالِهَتَكَۚ قَالَ سَنُقَتِّلُ أَبۡنَآءَهُمۡ وَنَسۡتَحۡيِۦ نِسَآءَهُمۡ وَإِنَّا فَوۡقَهُمۡ قَٰهِرُونَ ١٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1083,9,'165',7,'Al-A‘rāf','الأعرَاف',10,11,127,'قَالَ مُوسۭيٰ لِقَوۡمِهِ اِ۪سۡتَعِينُواْ بِاللَّهِ وَاَصۡبِرُوٓاْۖ إِنَّ اَ۬لۡأَرۡضَ لِلَّهِ يُورِثُهَا مَن يَشَآءُ مِنۡ عِبَادِهِۦۖ وَاَلۡعَٰقِبَةُ لِلۡمُتَّقِينَ ١٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1084,9,'165',7,'Al-A‘rāf','الأعرَاف',12,14,128,'قَالُوٓاْ أُوذِينَا مِن قَبۡلِ أَن تَأۡتِيَنَا وَمِنۢ بَعۡدِ مَا جِئۡتَنَاۚ قَالَ عَسَىٰ رَبُّكُمۡ أَن يُهۡلِكَ عَدُوَّكُمۡ وَيَسۡتَخۡلِفَكُمۡ فِي اِ۬لۡأَرۡضِ فَيَنظُرَ كَيۡفَ تَعۡمَلُونَ ١٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1085,9,'165',7,'Al-A‘rāf','الأعرَاف',14,15,129,'وَلَقَدۡ أَخَذۡنَآ ءَالَ فِرۡعَوۡنَ بِالسِّنِينَ وَنَقۡصٖ مِّنَ اَ۬لثَّمَرَٰتِ لَعَلَّهُمۡ يَذَّكَّرُونَ ١٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1086,9,'166',7,'Al-A‘rāf','الأعرَاف',1,3,130,'فَإِذَا جَآءَتۡهُمُ اُ۬لۡحَسَنَةُ قَالُواْ لَنَا هَٰذِهِۦۖ وَإِن تُصِبۡهُمۡ سَيِّئَةٞ يَطَّيَّرُواْ بِمُوسۭيٰ وَمَن مَّعَهُۥٓۗ أَلَآ إِنَّمَا طَٰٓئِرُهُمۡ عِندَ اَ۬للَّهِ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ ١٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1087,9,'166',7,'Al-A‘rāf','الأعرَاف',3,4,131,'۞ وَقَالُواْ مَهۡمَا تَأۡتِنَا بِهِۦ مِنۡ ءَايَةٖ لِّتَسۡحَرَنَا بِهَا فَمَا نَحۡنُ لَكَ بِمُؤۡمِنِينَ ١٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1088,9,'166',7,'Al-A‘rāf','الأعرَاف',4,6,132,'فَأَرۡسَلۡنَا عَلَيۡهِمِ اِ۬لطُّوفَانَ وَاَلۡجَرَادَ وَاَلۡقُمَّلَ وَاَلضَّفَادِعَ وَاَلدَّمَ ءَايَٰتٖ مُّفَصَّلَٰتٖ فَاَسۡتَكۡبَرُواْ وَكَانُواْ قَوۡمٗا مُّجۡرِمِينَ ١٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1089,9,'166',7,'Al-A‘rāf','الأعرَاف',7,9,133,'وَلَمَّا وَقَعَ عَلَيۡهِمِ اِ۬لرِّجۡزُ قَالُواْ يَٰمُوسَى اَ۟دۡعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِندَكَۖ لَئِن كَشَفۡتَ عَنَّا اَ۬لرِّجۡزَ لَنُؤۡمِنَنَّ لَكَ وَلَنُرۡسِلَنَّ مَعَكَ بَنِيٓ إِسۡرَٰٓءِيلَ ١٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1090,9,'166',7,'Al-A‘rāf','الأعرَاف',9,10,134,'فَلَمَّا كَشَفۡنَا عَنۡهُمُ اُ۬لرِّجۡزَ إِلَىٰٓ أَجَلٍ هُم بَٰلِغُوهُ إِذَا هُمۡ يَنكُثُونَ ١٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1091,9,'166',7,'Al-A‘rāf','الأعرَاف',10,12,135,'فَاَنتَقَمۡنَا مِنۡهُمۡ فَأَغۡرَقۡنَٰهُمۡ فِي اِ۬لۡيَمِّ بِأَنَّهُمۡ كَذَّبُواْ بِـَٔايَٰتِنَا وَكَانُواْ عَنۡهَا غَٰفِلِينَ ١٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1092,9,'166',7,'Al-A‘rāf','الأعرَاف',12,14,136,'وَأَوۡرَثۡنَا اَ۬لۡقَوۡمَ اَ۬لَّذِينَ كَانُواْ يُسۡتَضۡعَفُونَ مَشَٰرِقَ اَ۬لۡأَرۡضِ وَمَغَٰرِبَهَا اَ۬لَّتِي بَٰرَكۡنَا فِيهَاۖ وَتَمَّتۡ كَلِمَتُ رَبِّكَ اَ۬لۡحُسۡنۭيٰ عَلَىٰ بَنِيٓ إِسۡرَٰٓءِيلَ ١٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1093,9,'166',7,'Al-A‘rāf','الأعرَاف',14,15,137,'بِمَا صَبَرُواْۖ وَدَمَّرۡنَا مَا كَانَ يَصۡنَعُ فِرۡعَوۡنُ وَقَوۡمُهُۥ وَمَا كَانُواْ يَعۡرِشُونَ ١٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1094,9,'167',7,'Al-A‘rāf','الأعرَاف',1,3,138,'وَجَٰوَزۡنَا بِبَنِيٓ إِسۡرَٰٓءِيلَ اَ۬لۡبَحۡرَ فَأَتَوۡاْ عَلَىٰ قَوۡمٖ يَعۡكُفُونَ عَلَىٰٓ أَصۡنَامٖ لَّهُمۡۚ قَالُواْ يَٰمُوسَى اَ۪جۡعَل لَّنَآ إِلَٰهٗا كَمَا لَهُمۡ ءَالِهَةٞۚ قَالَ إِنَّكُمۡ قَوۡمٞ تَجۡهَلُونَ ١٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1095,9,'167',7,'Al-A‘rāf','الأعرَاف',3,4,139,'إِنَّ هَٰٓؤُلَآءِ مُتَبَّرٞ مَّا هُمۡ فِيهِ وَبَٰطِلٞ مَّا كَانُواْ يَعۡمَلُونَ ١٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1096,9,'167',7,'Al-A‘rāf','الأعرَاف',4,5,140,'قَالَ أَغَيۡرَ اَ۬للَّهِ أَبۡغِيكُمۡ إِلَٰهٗا وَهۡوَ فَضَّلَكُمۡ عَلَى اَ۬لۡعَٰلَمِينَ ١٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1097,9,'167',7,'Al-A‘rāf','الأعرَاف',5,8,141,'وَإِذۡ أَنجَيۡنَٰكُم مِّنۡ ءَالِ فِرۡعَوۡنَ يَسُومُونَكُمۡ سُوٓءَ اَ۬لۡعَذَابِ يُقَتِّلُونَ أَبۡنَآءَكُمۡ وَيَسۡتَحۡيُونَ نِسَآءَكُمۡۚ وَفِي ذَٰلِكُم بَلَآءٞ مِّن رَّبِّكُمۡ عَظِيمٞ ١٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1098,9,'167',7,'Al-A‘rāf','الأعرَاف',8,11,142,'۞ وَوَعَدۡنَا مُوسۭيٰ ثَلَٰثِينَ لَيۡلَةٗ وَأَتۡمَمۡنَٰهَا بِعَشۡرٖ فَتَمَّ مِيقَٰتُ رَبِّهِۦٓ أَرۡبَعِينَ لَيۡلَةٗۚ وَقَالَ مُوسۭيٰ لِأَخِيهِ هَٰرُونَ اَ۟خۡلُفۡنِي فِي قَوۡمِي وَأَصۡلِحۡ وَلَا تَتَّبِعۡ سَبِيلَ اَ۬لۡمُفۡسِدِينَ ١٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1099,9,'167',7,'Al-A‘rāf','الأعرَاف',11,15,143,'وَلَمَّا جَآءَ مُوسۭيٰ لِمِيقَٰتِنَا وَكَلَّمَهُۥ رَبُّهُۥ قَالَ رَبِّ أَر۪نِيٓ أَنظُرۡ إِلَيۡكَۚ قَالَ لَن تَر۪ىٰنِي وَلَٰكِنِ اِ۟نظُرۡ إِلَى اَ۬لۡجَبَلِ فَإِنِ اِ۪سۡتَقَرَّ مَكَانَهُۥ فَسَوۡفَ تَر۪ىٰنِيۚ فَلَمَّا تَجَلَّىٰ رَبُّهُۥ لِلۡجَبَلِ جَعَلَهُۥ دَكّٗا وَخَرَّ مُوسۭيٰ صَعِقٗاۚ فَلَمَّآ أَفَاقَ قَالَ سُبۡحَٰنَكَ تُبۡتُ إِلَيۡكَ وَأَنَا۠ أَوَّلُ اُ۬لۡمُؤۡمِنِينَ ١٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1100,9,'168',7,'Al-A‘rāf','الأعرَاف',1,2,144,'قَالَ يَٰمُوسۭيٰٓ إِنِّيَ اَ۪صۡطَفَيۡتُكَ عَلَى اَ۬لنّ۪اسِ بِرِسَٰلَٰتِي وَبِكَلَٰمِي فَخُذۡ مَآ ءَاتَيۡتُكَ وَكُن مِّنَ اَ۬لشَّٰكِرِينَ ١٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1101,9,'168',7,'Al-A‘rāf','الأعرَاف',2,5,145,'وَكَتَبۡنَا لَهُۥ فِي اِ۬لۡأَلۡوَاحِ مِن كُلِّ شَيۡءٖ مَّوۡعِظَةٗ وَتَفۡصِيلٗا لِّكُلِّ شَيۡءٖ فَخُذۡهَا بِقُوَّةٖ وَأۡمُرۡ قَوۡمَكَ يَأۡخُذُواْ بِأَحۡسَنِهَاۚ سَأُوْرِيكُمۡ دَارَ اَ۬لۡفَٰسِقِينَ ١٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1102,9,'168',7,'Al-A‘rāf','الأعرَاف',5,9,146,'سَأَصۡرِفُ عَنۡ ءَايَٰتِيَ اَ۬لَّذِينَ يَتَكَبَّرُونَ فِي اِ۬لۡأَرۡضِ بِغَيۡرِ اِ۬لۡحَقِّ وَإِن يَرَوۡاْ كُلَّ ءَايَةٖ لَّا يُؤۡمِنُواْ بِهَا وَإِن يَرَوۡاْ سَبِيلَ اَ۬لرُّشۡدِ لَا يَتَّخِذُوهُ سَبِيلٗا وَإِن يَرَوۡاْ سَبِيلَ اَ۬لۡغَيِّ يَتَّخِذُوهُ سَبِيلٗاۚ ذَٰلِكَ بِأَنَّهُمۡ كَذَّبُواْ بِـَٔايَٰتِنَا وَكَانُواْ عَنۡهَا غَٰفِلِينَ ١٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1103,9,'168',7,'Al-A‘rāf','الأعرَاف',9,11,147,'وَاَلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَلِقَآءِ اِ۬لۡأٓخِرَةِ حَبِطَتۡ أَعۡمَٰلُهُمۡۚ هَلۡ يُجۡزَوۡنَ إِلَّا مَا كَانُواْ يَعۡمَلُونَ ١٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1104,9,'168',7,'Al-A‘rāf','الأعرَاف',11,13,148,'وَاَتَّخَذَ قَوۡمُ مُوسۭيٰ مِنۢ بَعۡدِهِۦ مِنۡ حُلِيِّهِمۡ عِجۡلٗا جَسَدٗا لَّهُۥ خُوَارٌۚ أَلَمۡ يَرَوۡاْ أَنَّهُۥ لَا يُكَلِّمُهُمۡ وَلَا يَهۡدِيهِمۡ سَبِيلًاۘ اِ۪تَّخَذُوهُ وَكَانُواْ ظَٰلِمِينَ ١٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1105,9,'168',7,'Al-A‘rāf','الأعرَاف',14,15,149,'۞ وَلَمَّا سُقِطَ فِيٓ أَيۡدِيهِمۡ وَرَأَوۡاْ أَنَّهُمۡ قَد ضَّلُّواْ قَالُواْ لَئِن لَّمۡ يَرۡحَمۡنَا رَبُّنَا وَيَغۡفِر لَّنَا لَنَكُونَنَّ مِنَ اَ۬لۡخَٰسِرِينَ ١٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1106,9,'169',7,'Al-A‘rāf','الأعرَاف',1,5,150,'وَلَمَّا رَجَعَ مُوسۭيٰٓ إِلَىٰ قَوۡمِهِۦ غَضۡبَٰنَ أَسِفٗا قَالَ بِئۡسَمَا خَلَفۡتُمُونِي مِنۢ بَعۡدِيَۖ أَعَجِلۡتُمۡ أَمۡرَ رَبِّكُمۡۖ وَأَلۡقَى اَ۬لۡأَلۡوَاحَ وَأَخَذَ بِرَأۡسِ أَخِيهِ يَجُرُّهُۥٓ إِلَيۡهِۚ قَالَ اَ۪بۡنَ أُمَّ إِنَّ اَ۬لۡقَوۡمَ اَ۪سۡتَضۡعَفُونِي وَكَادُواْ يَقۡتُلُونَنِي فَلَا تُشۡمِتۡ بِيَ اَ۬لۡأَعۡدَآءَ وَلَا تَجۡعَلۡنِي مَعَ اَ۬لۡقَوۡمِ اِ۬لظَّٰلِمِينَ ١٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1107,9,'169',7,'Al-A‘rāf','الأعرَاف',5,6,151,'قَالَ رَبِّ اِ۪غۡفِر لِّي وَلِأَخِي وَأَدۡخِلۡنَا فِي رَحۡمَتِكَۖ وَأَنتَ أَرۡحَمُ اُ۬لرَّٰحِمِينَ ١٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1108,9,'169',7,'Al-A‘rāf','الأعرَاف',6,8,152,'إِنَّ اَ۬لَّذِينَ اَ۪تَّخَذُواْ اُ۬لۡعِجۡلَ سَيَنَالُهُمۡ غَضَبٞ مِّن رَّبِّهِمۡ وَذِلَّةٞ فِي اِ۬لۡحَيَوٰةِ اِ۬لدُّنۡيۭاۚ وَكَذَٰلِكَ نَجۡزِي اِ۬لۡمُفۡتَرِينَ ١٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1109,9,'169',7,'Al-A‘rāf','الأعرَاف',8,9,153,'وَاَلَّذِينَ عَمِلُواْ اُ۬لسَّيِّـَٔاتِ ثُمَّ تَابُواْ مِنۢ بَعۡدِهَا وَءَامَنُوٓاْ إِنَّ رَبَّكَ مِنۢ بَعۡدِهَا لَغَفُورٞ رَّحِيمٞ ١٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1110,9,'169',7,'Al-A‘rāf','الأعرَاف',10,11,154,'وَلَمَّا سَكَتَ عَن مُّوسَى اَ۬لۡغَضَبُ أَخَذَ اَ۬لۡأَلۡوَاحَۖ وَفِي نُسۡخَتِهَا هُدٗى وَرَحۡمَةٞ لِّلَّذِينَ هُمۡ لِرَبِّهِمۡ يَرۡهَبُونَ ١٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1111,9,'169',7,'Al-A‘rāf','الأعرَاف',11,15,155,'وَاَخۡتَارَ مُوسۭيٰ قَوۡمَهُۥ سَبۡعِينَ رَجُلٗا لِّمِيقَٰتِنَاۖ فَلَمَّآ أَخَذَتۡهُمُ اُ۬لرَّجۡفَةُ قَالَ رَبِّ لَوۡ شِئۡتَ أَهۡلَكۡتَهُم مِّن قَبۡلُ وَإِيَّٰيَۖ أَتُهۡلِكُنَا بِمَا فَعَلَ اَ۬لسُّفَهَآءُ مِنَّآۖ إِنۡ هِيَ إِلَّا فِتۡنَتُكَ تُضِلُّ بِهَا مَن تَشَآءُ وَتَهۡدِي مَن تَشَآءُۖ اَ۬نتَ وَلِيُّنَا فَاَغۡفِر لَّنَا وَاَرۡحَمۡنَاۖ وَأَنتَ خَيۡرُ اُ۬لۡغَٰفِرِينَ ١٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1112,9,'170',7,'Al-A‘rāf','الأعرَاف',1,4,156,'۞ وَاَكۡتُبۡ لَنَا فِي هَٰذِهِ اِ۬لدُّنۡيۭا حَسَنَةٗ وَفِي اِ۬لۡأٓخِرَةِ إِنَّا هُدۡنَآ إِلَيۡكَۚ قَالَ عَذَابِيٓ أُصِيبُ بِهِۦ مَنۡ أَشَآءُۖ وَرَحۡمَتِي وَسِعَتۡ كُلَّ شَيۡءٖۚ فَسَأَكۡتُبُهَا لِلَّذِينَ يَتَّقُونَ وَيُؤۡتُونَ اَ۬لزَّكَوٰةَ وَاَلَّذِينَ هُم بِـَٔايَٰتِنَا يُؤۡمِنُونَ ١٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1113,9,'170',7,'Al-A‘rāf','الأعرَاف',4,10,157,'اَ۬لَّذِينَ يَتَّبِعُونَ اَ۬لرَّسُولَ اَ۬لنَّبِيَّ اَ۬لۡأُمِّيَّ اَ۬لَّذِي يَجِدُونَهُۥ مَكۡتُوبًا عِندَهُمۡ فِي اِ۬لتَّوۡر۪ىٰةِ وَاَلۡإِنجِيلِ يَأۡمُرۡهُم بِالۡمَعۡرُوفِ وَيَنۡهَىٰهُمۡ عَنِ اِ۬لۡمُنكَرِ وَيُحِلُّ لَهُمُ اُ۬لطَّيِّبَٰتِ وَيُحَرِّمُ عَلَيۡهِمِ اِ۬لۡخَبَٰٓئِثَ وَيَضَعُ عَنۡهُمۡ إِصۡرَهُمۡ وَاَلۡأَغۡلَٰلَ اَ۬لَّتِي كَانَتۡ عَلَيۡهِمۡۚ فَاَلَّذِينَ ءَامَنُواْ بِهِۦ وَعَزَّرُوهُ وَنَصَرُوهُ وَاَتَّبَعُواْ اُ۬لنُّورَ اَ۬لَّذِيٓ أُنزِلَ مَعَهُۥٓ أُوْلَٰٓئِكَ هُمُ اُ۬لۡمُفۡلِحُونَ ١٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1114,9,'170',7,'Al-A‘rāf','الأعرَاف',11,14,158,'قُلۡ يَٰٓأَيُّهَا اَ۬لنَّاسُ إِنِّي رَسُولُ اُ۬للَّهِ إِلَيۡكُمۡ جَمِيعًا اِ۬لَّذِي لَهُۥ مُلۡكُ اُ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِۖ لَآ إِلَٰهَ إِلَّا هُوَ يُحۡيِۦ وَيُمِيتُۖ فَـَٔامِنُواْ بِاللَّهِ وَرَسُولِهِ اِ۬لنَّبِيِّ اِ۬لۡأُمِّيِّ اِ۬لَّذِي يُؤۡمِنُ بِاللَّهِ وَكَلِمَٰتِهِۦ وَاَتَّبِعُوهُ لَعَلَّكُمۡ تَهۡتَدُونَ ١٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1115,9,'170',7,'Al-A‘rāf','الأعرَاف',14,15,159,'وَمِن قَوۡمِ مُوسۭيٰٓ أُمَّةٞ يَهۡدُونَ بِالۡحَقِّ وَبِهِۦ يَعۡدِلُونَ ١٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1116,9,'171',7,'Al-A‘rāf','الأعرَاف',1,6,160,'وَقَطَّعۡنَٰهُمُ اُ۪ثۡنَتَيۡ عَشۡرَةَ أَسۡبَاطًا أُمَمٗاۚ وَأَوۡحَيۡنَآ إِلَىٰ مُوسۭيٰٓ إِذِ اِ۪سۡتَسۡقَىٰهُ قَوۡمُهُۥٓ أَنِ اِ۪ضۡرِب بِّعَصَاكَ اَ۬لۡحَجَرَۖ فَاَنۢبَجَسَتۡ مِنۡهُ اُ۪ثۡنَتَا عَشۡرَةَ عَيۡنٗاۖ قَدۡ عَلِمَ كُلُّ أُنَاسٖ مَّشۡرَبَهُمۡۚ وَظَلَّلۡنَا عَلَيۡهِمِ اِ۬لۡغَمَٰمَ وَأَنزَلۡنَا عَلَيۡهِمِ اِ۬لۡمَنَّ وَاَلسَّلۡوۭيٰۖ كُلُواْ مِن طَيِّبَٰتِ مَا رَزَقۡنَٰكُمۡۚ وَمَا ظَلَمُونَا وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ ١٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1117,9,'171',7,'Al-A‘rāf','الأعرَاف',7,9,161,'وَإِذۡ قِيلَ لَهُمُ اُ۟سۡكُنُواْ هَٰذِهِ اِ۬لۡقَرۡيَةَ وَكُلُواْ مِنۡهَا حَيۡثُ شِئۡتُمۡ وَقُولُواْ حِطَّةٞ وَاَدۡخُلُواْ اُ۬لۡبَابَ سُجَّدٗا نَّغۡفِر لَّكُمۡ خَطَٰيَىٰكُمۡۚ سَنَزِيدُ اُ۬لۡمُحۡسِنِينَ ١٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1118,9,'171',7,'Al-A‘rāf','الأعرَاف',10,12,162,'فَبَدَّلَ اَ۬لَّذِينَ ظَلَمُواْ مِنۡهُمۡ قَوۡلًا غَيۡرَ اَ۬لَّذِي قِيلَ لَهُمۡ فَأَرۡسَلۡنَا عَلَيۡهِمۡ رِجۡزٗا مِّنَ اَ۬لسَّمَآءِ بِمَا كَانُواْ يَظۡلِمُونَ ١٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1119,9,'171',7,'Al-A‘rāf','الأعرَاف',12,15,163,'۞ وَسۡـَٔلۡهُمۡ عَنِ اِ۬لۡقَرۡيَةِ اِ۬لَّتِي كَانَتۡ حَاضِرَةَ اَ۬لۡبَحۡرِ إِذۡ يَعۡدُونَ فِي اِ۬لسَّبۡتِ إِذ تَّأۡتِيهِمۡ حِيتَانُهُمۡ يَوۡمَ سَبۡتِهِمۡ شُرَّعٗا وَيَوۡمَ لَا يَسۡبِتُونَ لَا تَأۡتِيهِمۡۚ كَذَٰلِكَ نَبۡلُوهُم بِمَا كَانُواْ يَفۡسُقُونَ ١٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1120,9,'172',7,'Al-A‘rāf','الأعرَاف',1,2,164,'وَإِذۡ قَالَتۡ أُمَّةٞ مِّنۡهُمۡ لِمَ تَعِظُونَ قَوۡمًا اِ۬للَّهُ مُهۡلِكُهُمۡ أَوۡ مُعَذِّبُهُمۡ عَذَابٗا شَدِيدٗاۖ قَالُواْ مَعۡذِرَةٌ إِلَىٰ رَبِّكُمۡ وَلَعَلَّهُمۡ يَتَّقُونَ ١٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1121,9,'172',7,'Al-A‘rāf','الأعرَاف',3,4,165,'فَلَمَّا نَسُواْ مَا ذُكِّرُواْ بِهِۦٓ أَنجَيۡنَا اَ۬لَّذِينَ يَنۡهَوۡنَ عَنِ اِ۬لسُّوٓءِ وَأَخَذۡنَا اَ۬لَّذِينَ ظَلَمُواْ بِعَذَابِۢ بَـِٔيسِۢ بِمَا كَانُواْ يَفۡسُقُونَ ١٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1122,9,'172',7,'Al-A‘rāf','الأعرَاف',5,5,166,'فَلَمَّا عَتَوۡاْ عَن مَّا نُهُواْ عَنۡهُ قُلۡنَا لَهُمۡ كُونُواْ قِرَدَةً خَٰسِـِٔينَ ١٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1123,9,'172',7,'Al-A‘rāf','الأعرَاف',6,7,167,'وَإِذ تَّأَذَّنَ رَبُّكَ لَيَبۡعَثَنَّ عَلَيۡهِمۡ إِلَىٰ يَوۡمِ اِ۬لۡقِيَٰمَةِ مَن يَسُومُهُمۡ سُوٓءَ اَ۬لۡعَذَابِۗ إِنَّ رَبَّكَ لَسَرِيعُ اُ۬لۡعِقَابِ وَإِنَّهُۥ لَغَفُورٞ رَّحِيمٞ ١٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1124,9,'172',7,'Al-A‘rāf','الأعرَاف',8,10,168,'وَقَطَّعۡنَٰهُمۡ فِي اِ۬لۡأَرۡضِ أُمَمٗاۖ مِّنۡهُمُ اُ۬لصَّٰلِحُونَ وَمِنۡهُمۡ دُونَ ذَٰلِكَۖ وَبَلَوۡنَٰهُم بِالۡحَسَنَٰتِ وَاَلسَّيِّـَٔاتِ لَعَلَّهُمۡ يَرۡجِعُونَ ١٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1125,9,'172',7,'Al-A‘rāf','الأعرَاف',10,14,169,'فَخَلَفَ مِنۢ بَعۡدِهِمۡ خَلۡفٞ وَرِثُواْ اُ۬لۡكِتَٰبَ يَأۡخُذُونَ عَرَضَ هَٰذَا اَ۬لۡأَدۡنَىٰ وَيَقُولُونَ سَيُغۡفَرُ لَنَا وَإِن يَأۡتِهِمۡ عَرَضٞ مِّثۡلُهُۥ يَأۡخُذُوهُۚ أَلَمۡ يُؤۡخَذۡ عَلَيۡهِم مِّيثَٰقُ اُ۬لۡكِتَٰبِ أَن لَّا يَقُولُواْ عَلَى اَ۬للَّهِ إِلَّا اَ۬لۡحَقَّ وَدَرَسُواْ مَا فِيهِۗ وَاَلدَّارُ اُ۬لۡأٓخِرَةُ خَيۡرٞ لِّلَّذِينَ يَتَّقُونَۗ أَفَلَا يَعۡقِلُونَ ١٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1126,9,'172',7,'Al-A‘rāf','الأعرَاف',14,15,170,'وَاَلَّذِينَ يُمَسِّكُونَ بِالۡكِتَٰبِ وَأَقَامُواْ اُ۬لصَّلَوٰةَ إِنَّا لَا نُضِيعُ أَجۡرَ اَ۬لۡمُصۡلِحِينَ ١٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1127,9,'173',7,'Al-A‘rāf','الأعرَاف',1,2,171,'۞ وَإِذۡ نَتَقۡنَا اَ۬لۡجَبَلَ فَوۡقَهُمۡ كَأَنَّهُۥ ظُلَّةٞ وَظَنُّوٓاْ أَنَّهُۥ وَاقِعُۢ بِهِمۡ خُذُواْ مَآ ءَاتَيۡنَٰكُم بِقُوَّةٖ وَاَذۡكُرُواْ مَا فِيهِ لَعَلَّكُمۡ تَتَّقُونَ ١٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1128,9,'173',7,'Al-A‘rāf','الأعرَاف',3,5,172,'وَإِذۡ أَخَذَ رَبُّكَ مِنۢ بَنِيٓ ءَادَمَ مِن ظُهُورِهِمۡ ذُرِّيَّٰتِهِمۡ وَأَشۡهَدَهُمۡ عَلَىٰٓ أَنفُسِهِمۡ أَلَسۡتُ بِرَبِّكُمۡۖ قَالُواْ بَلَىٰ شَهِدۡنَآۚ أَن يَقُولُواْ يَوۡمَ اَ۬لۡقِيَٰمَةِ إِنَّا كُنَّا عَنۡ هَٰذَا غَٰفِلِينَ ١٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1129,9,'173',7,'Al-A‘rāf','الأعرَاف',5,7,173,'أَوۡ يَقُولُوٓاْ إِنَّمَآ أَشۡرَكَ ءَابَآؤُنَا مِن قَبۡلُ وَكُنَّا ذُرِّيَّةٗ مِّنۢ بَعۡدِهِمۡۖ أَفَتُهۡلِكُنَا بِمَا فَعَلَ اَ۬لۡمُبۡطِلُونَ ١٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1130,9,'173',7,'Al-A‘rāf','الأعرَاف',7,8,174,'وَكَذَٰلِكَ نُفَصِّلُ اُ۬لۡأٓيَٰتِ وَلَعَلَّهُمۡ يَرۡجِعُونَ ١٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1131,9,'173',7,'Al-A‘rāf','الأعرَاف',8,9,175,'وَاَتۡلُ عَلَيۡهِمۡ نَبَأَ اَ۬لَّذِيٓ ءَاتَيۡنَٰهُ ءَايَٰتِنَا فَاَنسَلَخَ مِنۡهَا فَأَتۡبَعَهُ اُ۬لشَّيۡطَٰنُ فَكَانَ مِنَ اَ۬لۡغَاوِينَ ١٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1132,9,'173',7,'Al-A‘rāf','الأعرَاف',9,13,176,'وَلَوۡ شِئۡنَا لَرَفَعۡنَٰهُ بِهَا وَلَٰكِنَّهُۥٓ أَخۡلَدَ إِلَى اَ۬لۡأَرۡضِ وَاَتَّبَعَ هَوَىٰهُۚ فَمَثَلُهُۥ كَمَثَلِ اِ۬لۡكَلۡبِ إِن تَحۡمِلۡ عَلَيۡهِ يَلۡهَثۡ أَوۡ تَتۡرُكۡهُ يَلۡهَثۚ ذَّٰلِكَ مَثَلُ اُ۬لۡقَوۡمِ اِ۬لَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَاۚ فَاَقۡصُصِ اِ۬لۡقَصَصَ لَعَلَّهُمۡ يَتَفَكَّرُونَ ١٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1133,9,'173',7,'Al-A‘rāf','الأعرَاف',13,14,177,'سَآءَ مَثَلًا اِ۬لۡقَوۡمُ اُ۬لَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَأَنفُسَهُمۡ كَانُواْ يَظۡلِمُونَ ١٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1134,9,'173',7,'Al-A‘rāf','الأعرَاف',14,15,178,'مَن يَهۡدِ اِ۬للَّهُ فَهۡوَ اَ۬لۡمُهۡتَدِيۖ وَمَن يُضۡلِلۡ فَأُوْلَٰٓئِكَ هُمُ اُ۬لۡخَٰسِرُونَ ١٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1135,9,'174',7,'Al-A‘rāf','الأعرَاف',1,3,179,'وَلَقَد ذَّرَأۡنَا لِجَهَنَّمَ كَثِيرٗا مِّنَ اَ۬لۡجِنِّ وَاَلۡإِنسِۖ لَهُمۡ قُلُوبٞ لَّا يَفۡقَهُونَ بِهَا وَلَهُمۡ أَعۡيُنٞ لَّا يُبۡصِرُونَ بِهَا وَلَهُمۡ ءَاذَانٞ لَّا يَسۡمَعُونَ بِهَآۚ أُوْلَٰٓئِكَ كَاَلۡأَنۡعَٰمِ بَلۡ هُمۡ أَضَلُّۚ أُوْلَٰٓئِكَ هُمُ اُ۬لۡغَٰفِلُونَ ١٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1136,9,'174',7,'Al-A‘rāf','الأعرَاف',3,5,180,'۞ وَلِلَّهِ اِ۬لۡأَسۡمَآءُ اُ۬لۡحُسۡنۭيٰ فَاَدۡعُوهُ بِهَاۖ وَذَرُواْ اُ۬لَّذِينَ يُلۡحِدُونَ فِيٓ أَسۡمَٰٓئِهِۦۚ سَيُجۡزَوۡنَ مَا كَانُواْ يَعۡمَلُونَ ١٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1137,9,'174',7,'Al-A‘rāf','الأعرَاف',5,6,181,'وَمِمَّنۡ خَلَقۡنَآ أُمَّةٞ يَهۡدُونَ بِالۡحَقِّ وَبِهِۦ يَعۡدِلُونَ ١٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1138,9,'174',7,'Al-A‘rāf','الأعرَاف',6,7,182,'وَاَلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا سَنَسۡتَدۡرِجُهُم مِّنۡ حَيۡثُ لَا يَعۡلَمُونَ ١٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1139,9,'174',7,'Al-A‘rāf','الأعرَاف',7,7,183,'وَأُمۡلِي لَهُمۡۚ إِنَّ كَيۡدِي مَتِينٌ ١٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1140,9,'174',7,'Al-A‘rāf','الأعرَاف',7,8,184,'أَوَلَمۡ يَتَفَكَّرُواْۗ مَا بِصَاحِبِهِم مِّن جِنَّةٍۚ إِنۡ هُوَ إِلَّا نَذِيرٞ مُّبِينٌ ١٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1141,9,'174',7,'Al-A‘rāf','الأعرَاف',9,11,185,'أَوَلَمۡ يَنظُرُواْ فِي مَلَكُوتِ اِ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِ وَمَا خَلَقَ اَ۬للَّهُ مِن شَيۡءٖ وَأَنۡ عَسَىٰٓ أَن يَكُونَ قَدِ اِ۪قۡتَرَبَ أَجَلُهُمۡۚ فَبِأَيِّ حَدِيثِۢ بَعۡدَهُۥ يُؤۡمِنُونَ ١٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1142,9,'174',7,'Al-A‘rāf','الأعرَاف',11,12,186,'مَن يُضۡلِلِ اِ۬للَّهُ فَلَا هَادِيَ لَهُۥۚ وَيَذَرُهُمۡ فِي طُغۡيَٰنِهِمۡ يَعۡمَهُونَ ١٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1143,9,'174',7,'Al-A‘rāf','الأعرَاف',12,15,187,'يَسۡـَٔلُونَكَ عَنِ اِ۬لسَّاعَةِ أَيَّانَ مُرۡسَىٰهَاۖ قُلۡ إِنَّمَا عِلۡمُهَا عِندَ رَبِّيۖ لَا يُجَلِّيهَا لِوَقۡتِهَآ إِلَّا هُوَۚ ثَقُلَتۡ فِي اِ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِۚ لَا تَأۡتِيكُمۡ إِلَّا بَغۡتَةٗۗ يَسۡـَٔلُونَكَ كَأَنَّكَ حَفِيٌّ عَنۡهَاۖ قُلۡ إِنَّمَا عِلۡمُهَا عِندَ اَ۬للَّهِ وَلَٰكِنَّ أَكۡثَرَ اَ۬لنّ۪اسِ لَا يَعۡلَمُونَ ١٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1144,9,'175',7,'Al-A‘rāf','الأعرَاف',1,3,188,'قُل لَّآ أَمۡلِكُ لِنَفۡسِي نَفۡعٗا وَلَا ضَرًّا إِلَّا مَا شَآءَ اَ۬للَّهُۚ وَلَوۡ كُنتُ أَعۡلَمُ اُ۬لۡغَيۡبَ لَاَسۡتَكۡثَرۡتُ مِنَ اَ۬لۡخَيۡرِ وَمَا مَسَّنِيَ اَ۬لسُّوٓءُۚ اِ۪نۡ أَنَا۠ إِلَّا نَذِيرٞ وَبَشِيرٞ لِّقَوۡمٖ يُؤۡمِنُونَ ١٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1145,9,'175',7,'Al-A‘rāf','الأعرَاف',3,6,189,'۞ هُوَ اَ۬لَّذِي خَلَقَكُم مِّن نَّفۡسٖ وَٰحِدَةٖ وَجَعَلَ مِنۡهَا زَوۡجَهَا لِيَسۡكُنَ إِلَيۡهَاۖ فَلَمَّا تَغَشَّىٰهَا حَمَلَتۡ حَمۡلًا خَفِيفٗا فَمَرَّتۡ بِهِۦۖ فَلَمَّآ أَثۡقَلَت دَّعَوَا اَ۬للَّهَ رَبَّهُمَا لَئِنۡ ءَاتَيۡتَنَا صَٰلِحٗا لَّنَكُونَنَّ مِنَ اَ۬لشَّٰكِرِينَ ١٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1146,9,'175',7,'Al-A‘rāf','الأعرَاف',7,8,190,'فَلَمَّآ ءَاتَىٰهُمَا صَٰلِحٗا جَعَلَا لَهُۥ شُرَكَآءَ فِيمَآ ءَاتَىٰهُمَاۚ فَتَعَٰلَى اَ۬للَّهُ عَمَّا يُشۡرِكُونَ ١٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1147,9,'175',7,'Al-A‘rāf','الأعرَاف',8,8,191,'أَيُشۡرِكُونَ مَا لَا يَخۡلُقُ شَيۡـٔٗا وَهُمۡ يُخۡلَقُونَ ١٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1148,9,'175',7,'Al-A‘rāf','الأعرَاف',9,9,192,'وَلَا يَسۡتَطِيعُونَ لَهُمۡ نَصۡرٗا وَلَآ أَنفُسَهُمۡ يَنصُرُونَ ١٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1149,9,'175',7,'Al-A‘rāf','الأعرَاف',10,11,193,'وَإِن تَدۡعُوهُمۡ إِلَى اَ۬لۡهُدَىٰ لَا يَتَّبِعُوكُمۡۚ سَوَآءٌ عَلَيۡكُمۡ أَدَعَوۡتُمُوهُمۡ أَمۡ أَنتُمۡ صَٰمِتُونَ ١٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1150,9,'175',7,'Al-A‘rāf','الأعرَاف',11,13,194,'إِنَّ اَ۬لَّذِينَ تَدۡعُونَ مِن دُونِ اِ۬للَّهِ عِبَادٌ أَمۡثَالُكُمۡۖ فَاَدۡعُوهُمۡ فَلۡيَسۡتَجِيبُواْ لَكُمۡ إِن كُنتُمۡ صَٰدِقِينَ ١٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1151,9,'175',7,'Al-A‘rāf','الأعرَاف',13,15,195,'أَلَهُمۡ أَرۡجُلٞ يَمۡشُونَ بِهَآۖ أَمۡ لَهُمۡ أَيۡدٖ يَبۡطِشُونَ بِهَآۖ أَمۡ لَهُمۡ أَعۡيُنٞ يُبۡصِرُونَ بِهَآۖ أَمۡ لَهُمۡ ءَاذَانٞ يَسۡمَعُونَ بِهَاۗ قُلُ اُ۟دۡعُواْ شُرَكَآءَكُمۡ ثُمَّ كِيدُونِۦ فَلَا تُنظِرُونِ ١٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1152,9,'176',7,'Al-A‘rāf','الأعرَاف',1,1,196,'إِنَّ وَلِـِّۧيَ اَ۬للَّهُ اُ۬لَّذِي نَزَّلَ اَ۬لۡكِتَٰبَۖ وَهۡوَ يَتَوَلَّى اَ۬لصَّٰلِحِينَ ١٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1153,9,'176',7,'Al-A‘rāf','الأعرَاف',2,3,197,'وَاَلَّذِينَ تَدۡعُونَ مِن دُونِهِۦ لَا يَسۡتَطِيعُونَ نَصۡرَكُمۡ وَلَآ أَنفُسَهُمۡ يَنصُرُونَ ١٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1154,9,'176',7,'Al-A‘rāf','الأعرَاف',3,4,198,'وَإِن تَدۡعُوهُمۡ إِلَى اَ۬لۡهُدَىٰ لَا يَسۡمَعُواْۖ وَتَر۪ىٰهُمۡ يَنظُرُونَ إِلَيۡكَ وَهُمۡ لَا يُبۡصِرُونَ ١٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1155,9,'176',7,'Al-A‘rāf','الأعرَاف',4,5,199,'۞ خُذِ اِ۬لۡعَفۡوَ وَأۡمُرۡ بِالۡعُرۡفِ وَأَعۡرِضۡ عَنِ اِ۬لۡجَٰهِلِينَ ١٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1156,9,'176',7,'Al-A‘rāf','الأعرَاف',5,6,200,'وَإِمَّا يَنزَغَنَّكَ مِنَ اَ۬لشَّيۡطَٰنِ نَزۡغٞ فَاَسۡتَعِذۡ بِاللَّهِۚ إِنَّهُۥ سَمِيعٌ عَلِيمٌ ٢٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1157,9,'176',7,'Al-A‘rāf','الأعرَاف',6,8,201,'إِنَّ اَ۬لَّذِينَ اَ۪تَّقَوۡاْ إِذَا مَسَّهُمۡ طَيۡفٞ مِّنَ اَ۬لشَّيۡطَٰنِ تَذَكَّرُواْ فَإِذَا هُم مُّبۡصِرُونَ ٢٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1158,9,'176',7,'Al-A‘rāf','الأعرَاف',8,9,202,'وَإِخۡوَٰنُهُمۡ يَمُدُّونَهُمۡ فِي اِ۬لۡغَيِّ ثُمَّ لَا يُقۡصِرُونَ ٢٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1159,9,'176',7,'Al-A‘rāf','الأعرَاف',9,11,203,'وَإِذَا لَمۡ تَأۡتِهِم بِـَٔايَةٖ قَالُواْ لَوۡلَا اَ۪جۡتَبَيۡتَهَاۚ قُلۡ إِنَّمَآ أَتَّبِعُ مَا يُوحَىٰٓ إِلَيَّ مِن رَّبِّيۚ هَٰذَا بَصَآئِرُ مِن رَّبِّكُمۡ وَهُدٗى وَرَحۡمَةٞ لِّقَوۡمٖ يُؤۡمِنُونَ ٢٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1160,9,'176',7,'Al-A‘rāf','الأعرَاف',11,12,204,'وَإِذَا قُرِئَ اَ۬لۡقُرۡءَانُ فَاَسۡتَمِعُواْ لَهُۥ وَأَنصِتُواْ لَعَلَّكُمۡ تُرۡحَمُونَ ٢٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1161,9,'176',7,'Al-A‘rāf','الأعرَاف',12,14,205,'وَاَذۡكُر رَّبَّكَ فِي نَفۡسِكَ تَضَرُّعٗا وَخِيفَةٗ وَدُونَ اَ۬لۡجَهۡرِ مِنَ اَ۬لۡقَوۡلِ بِالۡغُدُوِّ وَاَلۡأٓصَالِ وَلَا تَكُن مِّنَ اَ۬لۡغَٰفِلِينَ ٢٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1162,9,'176',7,'Al-A‘rāf','الأعرَاف',14,15,206,'إِنَّ اَ۬لَّذِينَ عِندَ رَبِّكَ لَا يَسۡتَكۡبِرُونَ عَنۡ عِبَادَتِهِۦ وَيُسَبِّحُونَهُۥ وَلَهُۥ يَسۡجُدُونَۤ۩ ٢٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1163,9,'177',8,'Al-Anfāl','الأنفَال',3,5,1,'يَسۡـَٔلُونَكَ عَنِ اِ۬لۡأَنفَالِۖ قُلِ اِ۬لۡأَنفَالُ لِلَّهِ وَاَلرَّسُولِۖ فَاَتَّقُواْ اُ۬للَّهَ وَأَصۡلِحُواْ ذَاتَ بَيۡنِكُمۡۖ وَأَطِيعُواْ اُ۬للَّهَ وَرَسُولَهُۥٓ إِن كُنتُم مُّؤۡمِنِينَ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1164,9,'177',8,'Al-Anfāl','الأنفَال',5,7,2,'إِنَّمَا اَ۬لۡمُؤۡمِنُونَ اَ۬لَّذِينَ إِذَا ذُكِرَ اَ۬للَّهُ وَجِلَتۡ قُلُوبُهُمۡ وَإِذَا تُلِيَتۡ عَلَيۡهِمۡ ءَايَٰتُهُۥ زَادَتۡهُمۡ إِيمَٰنٗا وَعَلَىٰ رَبِّهِمۡ يَتَوَكَّلُونَ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1165,9,'177',8,'Al-Anfāl','الأنفَال',7,8,3,'اَ۬لَّذِينَ يُقِيمُونَ اَ۬لصَّلَوٰةَ وَمِمَّا رَزَقۡنَٰهُمۡ يُنفِقُونَ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1166,9,'177',8,'Al-Anfāl','الأنفَال',8,9,4,'أُوْلَٰٓئِكَ هُمُ اُ۬لۡمُؤۡمِنُونَ حَقّٗاۚ لَّهُمۡ دَرَجَٰتٌ عِندَ رَبِّهِمۡ وَمَغۡفِرَةٞ وَرِزۡقٞ كَرِيمٞ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1167,9,'177',8,'Al-Anfāl','الأنفَال',9,10,5,'۞ كَمَآ أَخۡرَجَكَ رَبُّكَ مِنۢ بَيۡتِكَ بِالۡحَقِّ وَإِنَّ فَرِيقٗا مِّنَ اَ۬لۡمُؤۡمِنِينَ لَكَٰرِهُونَ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1168,9,'177',8,'Al-Anfāl','الأنفَال',11,12,6,'يُجَٰدِلُونَكَ فِي اِ۬لۡحَقِّ بَعۡدَ مَا تَبَيَّنَ كَأَنَّمَا يُسَاقُونَ إِلَى اَ۬لۡمَوۡتِ وَهُمۡ يَنظُرُونَ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1169,9,'177',8,'Al-Anfāl','الأنفَال',12,14,7,'وَإِذۡ يَعِدُكُمُ اُ۬للَّهُ إِحۡدَى اَ۬لطَّآئِفَتَيۡنِ أَنَّهَا لَكُمۡ وَتَوَدُّونَ أَنَّ غَيۡرَ ذَاتِ اِ۬لشَّوۡكَةِ تَكُونُ لَكُمۡ وَيُرِيدُ اُ۬للَّهُ أَن يُحِقَّ اَ۬لۡحَقَّ بِكَلِمَٰتِهِۦ وَيَقۡطَعَ دَابِرَ اَ۬لۡكٰ۪فِرِينَ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1170,9,'177',8,'Al-Anfāl','الأنفَال',15,15,8,'لِيُحِقَّ اَ۬لۡحَقَّ وَيُبۡطِلَ اَ۬لۡبَٰطِلَ وَلَوۡ كَرِهَ اَ۬لۡمُجۡرِمُونَ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1171,9,'178',8,'Al-Anfāl','الأنفَال',1,2,9,'إِذ تَّسۡتَغِيثُونَ رَبَّكُمۡ فَاَسۡتَجَابَ لَكُمۡ أَنِّي مُمِدُّكُم بِأَلۡفٖ مِّنَ اَ۬لۡمَلَٰٓئِكَةِ مُرۡدِفِينَ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1172,9,'178',8,'Al-Anfāl','الأنفَال',2,4,10,'وَمَا جَعَلَهُ اُ۬للَّهُ إِلَّا بُشۡر۪يٰ وَلِتَطۡمَئِنَّ بِهِۦ قُلُوبُكُمۡۚ وَمَا اَ۬لنَّصۡرُ إِلَّا مِنۡ عِندِ اِ۬للَّهِۚ إِنَّ اَ۬للَّهَ عَزِيزٌ حَكِيمٌ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1173,9,'178',8,'Al-Anfāl','الأنفَال',4,6,11,'إِذۡ يَغۡشَىٰكُمُ اُ۬لنُّعَاسُ أَمَنَةٗ مِّنۡهُ وَيُنزِلُ عَلَيۡكُم مِّنَ اَ۬لسَّمَآءِ مَآءٗ لِّيُطَهِّرَكُم بِهِۦ وَيُذۡهِبَ عَنكُمۡ رِجۡزَ اَ۬لشَّيۡطَٰنِ وَلِيَرۡبِطَ عَلَىٰ قُلُوبِكُمۡ وَيُثَبِّتَ بِهِ اِ۬لۡأَقۡدَامَ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1174,9,'178',8,'Al-Anfāl','الأنفَال',7,9,12,'إِذۡ يُوحِي رَبُّكَ إِلَى اَ۬لۡمَلَٰٓئِكَةِ أَنِّي مَعَكُمۡ فَثَبِّتُواْ اُ۬لَّذِينَ ءَامَنُواْۚ سَأُلۡقِي فِي قُلُوبِ اِ۬لَّذِينَ كَفَرُواْ اُ۬لرُّعۡبَ فَاَضۡرِبُواْ فَوۡقَ اَ۬لۡأَعۡنَاقِ وَاَضۡرِبُواْ مِنۡهُمۡ كُلَّ بَنَانٖ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1175,9,'178',8,'Al-Anfāl','الأنفَال',9,11,13,'ذَٰلِكَ بِأَنَّهُمۡ شَآقُّواْ اُ۬للَّهَ وَرَسُولَهُۥۚ وَمَن يُشَاقِقِ اِ۬للَّهَ وَرَسُولَهُۥ فَإِنَّ اَ۬للَّهَ شَدِيدُ اُ۬لۡعِقَابِ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1176,9,'178',8,'Al-Anfāl','الأنفَال',11,12,14,'ذَٰلِكُمۡ فَذُوقُوهُ وَأَنَّ لِلۡكٰ۪فِرِينَ عَذَابَ اَ۬لنّ۪ارِ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1177,9,'178',8,'Al-Anfāl','الأنفَال',12,13,15,'۞ يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُوٓاْ إِذَا لَقِيتُمُ اُ۬لَّذِينَ كَفَرُواْ زَحۡفٗا فَلَا تُوَلُّوهُمُ اُ۬لۡأَدۡبَارَ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1178,9,'178',8,'Al-Anfāl','الأنفَال',13,15,16,'وَمَن يُوَلِّهِمۡ يَوۡمَئِذٖ دُبُرَهُۥٓ إِلَّا مُتَحَرِّفٗا لِّقِتَالٍ أَوۡ مُتَحَيِّزًا إِلَىٰ فِئَةٖ فَقَدۡ بَآءَ بِغَضَبٖ مِّنَ اَ۬للَّهِ وَمَأۡوَىٰهُ جَهَنَّمُۖ وَبِئۡسَ اَ۬لۡمَصِيرُ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1179,9,'179',8,'Al-Anfāl','الأنفَال',1,3,17,'فَلَمۡ تَقۡتُلُوهُمۡ وَلَٰكِنَّ اَ۬للَّهَ قَتَلَهُمۡۚ وَمَا رَمَيۡتَ إِذۡ رَمَيۡتَ وَلَٰكِنَّ اَ۬للَّهَ رَمَىٰۚ وَلِيُبۡلِيَ اَ۬لۡمُؤۡمِنِينَ مِنۡهُ بَلَآءً حَسَنًاۚ إِنَّ اَ۬للَّهَ سَمِيعٌ عَلِيمٞ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1180,9,'179',8,'Al-Anfāl','الأنفَال',3,4,18,'ذَٰلِكُمۡ وَأَنَّ اَ۬للَّهَ مُوَهِّنٞ كَيۡدَ اَ۬لۡكٰ۪فِرِينَ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1181,9,'179',8,'Al-Anfāl','الأنفَال',4,6,19,'إِن تَسۡتَفۡتِحُواْ فَقَد جَّآءَكُمُ اُ۬لۡفَتۡحُۖ وَإِن تَنتَهُواْ فَهۡوَ خَيۡرٞ لَّكُمۡۖ وَإِن تَعُودُواْ نَعُدۡ وَلَن تُغۡنِيَ عَنكُمۡ فِئَتُكُمۡ شَيۡـٔٗا وَلَوۡ كَثُرَتۡۚ وَإِنَّ اَ۬للَّهَ مَعَ اَ۬لۡمُؤۡمِنِينَ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1182,9,'179',8,'Al-Anfāl','الأنفَال',7,8,20,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُوٓاْ أَطِيعُواْ اُ۬للَّهَ وَرَسُولَهُۥ وَلَا تَوَلَّوۡاْ عَنۡهُ وَأَنتُمۡ تَسۡمَعُونَ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1183,9,'179',8,'Al-Anfāl','الأنفَال',8,9,21,'وَلَا تَكُونُواْ كَاَلَّذِينَ قَالُواْ سَمِعۡنَا وَهُمۡ لَا يَسۡمَعُونَ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1184,9,'179',8,'Al-Anfāl','الأنفَال',9,10,22,'إِنَّ شَرَّ اَ۬لدَّوَآبِّ عِندَ اَ۬للَّهِ اِ۬لصُّمُّ اُ۬لۡبُكۡمُ اُ۬لَّذِينَ لَا يَعۡقِلُونَ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1185,9,'179',8,'Al-Anfāl','الأنفَال',10,11,23,'وَلَوۡ عَلِمَ اَ۬للَّهُ فِيهِمۡ خَيۡرٗا لَّأَسۡمَعَهُمۡۖ وَلَوۡ أَسۡمَعَهُمۡ لَتَوَلَّواْ وَّهُم مُّعۡرِضُونَ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1186,9,'179',8,'Al-Anfāl','الأنفَال',11,14,24,'۞ يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ اُ۪سۡتَجِيبُواْ لِلَّهِ وَلِلرَّسُولِ إِذَا دَعَاكُمۡ لِمَا يُحۡيِيكُمۡۖ وَاَعۡلَمُوٓاْ أَنَّ اَ۬للَّهَ يَحُولُ بَيۡنَ اَ۬لۡمَرۡءِ وَقَلۡبِهِۦ وَأَنَّهُۥٓ إِلَيۡهِ تُحۡشَرُونَ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1187,9,'179',8,'Al-Anfāl','الأنفَال',14,15,25,'وَاَتَّقُواْ فِتۡنَةٗ لَّا تُصِيبَنَّ اَ۬لَّذِينَ ظَلَمُواْ مِنكُمۡ خَآصَّةٗۖ وَاَعۡلَمُوٓاْ أَنَّ اَ۬للَّهَ شَدِيدُ اُ۬لۡعِقَابِ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1188,9,'180',8,'Al-Anfāl','الأنفَال',1,3,26,'وَاَذۡكُرُوٓاْ إِذۡ أَنتُمۡ قَلِيلٞ مُّسۡتَضۡعَفُونَ فِي اِ۬لۡأَرۡضِ تَخَافُونَ أَن يَتَخَطَّفَكُمُ اُ۬لنَّاسُ فَـَٔاوَىٰكُمۡ وَأَيَّدَكُم بِنَصۡرِهِۦ وَرَزَقَكُم مِّنَ اَ۬لطَّيِّبَٰتِ لَعَلَّكُمۡ تَشۡكُرُونَ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1189,9,'180',8,'Al-Anfāl','الأنفَال',3,4,27,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ لَا تَخُونُواْ اُ۬للَّهَ وَاَلرَّسُولَ وَتَخُونُوٓاْ أَمَٰنَٰتِكُمۡ وَأَنتُمۡ تَعۡلَمُونَ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1190,9,'180',8,'Al-Anfāl','الأنفَال',5,6,28,'وَاَعۡلَمُوٓاْ أَنَّمَآ أَمۡوَٰلُكُمۡ وَأَوۡلَٰدُكُمۡ فِتۡنَةٞ وَأَنَّ اَ۬للَّهَ عِندَهُۥٓ أَجۡرٌ عَظِيمٞ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1191,9,'180',8,'Al-Anfāl','الأنفَال',6,8,29,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُوٓاْ إِن تَتَّقُواْ اُ۬للَّهَ يَجۡعَل لَّكُمۡ فُرۡقَانٗا وَيُكَفِّرۡ عَنكُمۡ سَيِّـَٔاتِكُمۡ وَيَغۡفِر لَّكُمۡۗ وَاَللَّهُ ذُو اُ۬لۡفَضۡلِ اِ۬لۡعَظِيمِ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1192,9,'180',8,'Al-Anfāl','الأنفَال',8,10,30,'وَإِذۡ يَمۡكُرُ بِكَ اَ۬لَّذِينَ كَفَرُواْ لِيُثۡبِتُوكَ أَوۡ يَقۡتُلُوكَ أَوۡ يُخۡرِجُوكَۚ وَيَمۡكُرُونَ وَيَمۡكُرُ اُ۬للَّهُۖ وَاَللَّهُ خَيۡرُ اُ۬لۡمَٰكِرِينَ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1193,9,'180',8,'Al-Anfāl','الأنفَال',10,12,31,'وَإِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَٰتُنَا قَالُواْ قَد سَّمِعۡنَا لَوۡ نَشَآءُ لَقُلۡنَا مِثۡلَ هَٰذَآ إِنۡ هَٰذَآ إِلَّآ أَسَٰطِيرُ اُ۬لۡأَوَّلِينَ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1194,9,'180',8,'Al-Anfāl','الأنفَال',12,14,32,'۞ وَإِذۡ قَالُواْ اُ۬للَّهُمَّ إِن كَانَ هَٰذَا هُوَ اَ۬لۡحَقَّ مِنۡ عِندِكَ فَأَمۡطِرۡ عَلَيۡنَا حِجَارَةٗ مِّنَ اَ۬لسَّمَآءِ اَ۬وِ اِ۪ئۡتِنَا بِعَذَابٍ أَلِيمٖ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1195,9,'180',8,'Al-Anfāl','الأنفَال',14,15,33,'وَمَا كَانَ اَ۬للَّهُ لِيُعَذِّبَهُمۡ وَأَنتَ فِيهِمۡۚ وَمَا كَانَ اَ۬للَّهُ مُعَذِّبَهُمۡ وَهُمۡ يَسۡتَغۡفِرُونَ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1196,9,'181',8,'Al-Anfāl','الأنفَال',1,3,34,'وَمَا لَهُمۡ أَلَّا يُعَذِّبَهُمُ اُ۬للَّهُ وَهُمۡ يَصُدُّونَ عَنِ اِ۬لۡمَسۡجِدِ اِ۬لۡحَرَامِ وَمَا كَانُوٓاْ أَوۡلِيَآءَهُۥٓۚ إِنۡ أَوۡلِيَآؤُهُۥٓ إِلَّا اَ۬لۡمُتَّقُونَ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1197,9,'181',8,'Al-Anfāl','الأنفَال',3,5,35,'وَمَا كَانَ صَلَاتُهُمۡ عِندَ اَ۬لۡبَيۡتِ إِلَّا مُكَآءٗ وَتَصۡدِيَةٗۚ فَذُوقُواْ اُ۬لۡعَذَابَ بِمَا كُنتُمۡ تَكۡفُرُونَ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1198,9,'181',8,'Al-Anfāl','الأنفَال',5,8,36,'إِنَّ اَ۬لَّذِينَ كَفَرُواْ يُنفِقُونَ أَمۡوَٰلَهُمۡ لِيَصُدُّواْ عَن سَبِيلِ اِ۬للَّهِۚ فَسَيُنفِقُونَهَا ثُمَّ تَكُونُ عَلَيۡهِمۡ حَسۡرَةٗ ثُمَّ يُغۡلَبُونَۗ وَاَلَّذِينَ كَفَرُوٓاْ إِلَىٰ جَهَنَّمَ يُحۡشَرُونَ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1199,9,'181',8,'Al-Anfāl','الأنفَال',8,10,37,'لِيَمِيزَ اَ۬للَّهُ اُ۬لۡخَبِيثَ مِنَ اَ۬لطَّيِّبِ وَيَجۡعَلَ اَ۬لۡخَبِيثَ بَعۡضَهُۥ عَلَىٰ بَعۡضٖ فَيَرۡكُمَهُۥ جَمِيعٗا فَيَجۡعَلَهُۥ فِي جَهَنَّمَۚ أُوْلَٰٓئِكَ هُمُ اُ۬لۡخَٰسِرُونَ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1200,9,'181',8,'Al-Anfāl','الأنفَال',10,12,38,'قُل لِّلَّذِينَ كَفَرُوٓاْ إِن يَنتَهُواْ يُغۡفَر لَّهُم مَّا قَد سَّلَفَ وَإِن يَعُودُواْ فَقَدۡ مَضَت سُّنَّتُ اُ۬لۡأَوَّلِينَ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1201,9,'181',8,'Al-Anfāl','الأنفَال',12,14,39,'وَقَٰتِلُوهُمۡ حَتَّىٰ لَا تَكُونَ فِتۡنَةٞ وَيَكُونَ اَ۬لدِّينُ كُلُّهُۥ لِلَّهِۚ فَإِنِ اِ۪نتَهَوۡاْ فَإِنَّ اَ۬للَّهَ بِمَا يَعۡمَلُونَ بَصِيرٞ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1202,9,'181',8,'Al-Anfāl','الأنفَال',14,15,40,'وَإِن تَوَلَّوۡاْ فَاَعۡلَمُوٓاْ أَنَّ اَ۬للَّهَ مَوۡلَىٰكُمۡۚ نِعۡمَ اَ۬لۡمَوۡلَىٰ وَنِعۡمَ اَ۬لنَّصِيرُ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1203,10,'182',8,'Al-Anfāl','الأنفَال',1,4,41,'۞ وَاَعۡلَمُوٓاْ أَنَّمَا غَنِمۡتُم مِّن شَيۡءٖ فَأَنَّ لِلَّهِ خُمُسَهُۥ وَلِلرَّسُولِ وَلِذِي اِ۬لۡقُرۡبۭيٰ وَاَلۡيَتَٰمَىٰ وَاَلۡمَسَٰكِينِ وَاَبۡنِ اِ۬لسَّبِيلِ إِن كُنتُمۡ ءَامَنتُم بِاللَّهِ وَمَآ أَنزَلۡنَا عَلَىٰ عَبۡدِنَا يَوۡمَ اَ۬لۡفُرۡقَانِ يَوۡمَ اَ۪لۡتَقَى اَ۬لۡجَمۡعَانِۗ وَاَللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1204,10,'182',8,'Al-Anfāl','الأنفَال',4,7,42,'إِذۡ أَنتُم بِالۡعِدۡوَةِ اِ۬لدُّنۡيۭا وَهُم بِالۡعِدۡوَةِ اِ۬لۡقُصۡوۭيٰ وَاَلرَّكۡبُ أَسۡفَلَ مِنكُمۡۚ وَلَوۡ تَوَاعَدتُّمۡ لَاَخۡتَلَفۡتُمۡ فِي اِ۬لۡمِيعَٰدِ وَلَٰكِن لِّيَقۡضِيَ اَ۬للَّهُ أَمۡرٗا كَانَ مَفۡعُولٗا ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1205,10,'182',8,'Al-Anfāl','الأنفَال',7,9,43,'لِّيَهۡلِكَ مَنۡ هَلَكَ عَنۢ بَيِّنَةٖ وَيَحۡيَىٰ مَنۡ حَيَّ عَنۢ بَيِّنَةٖۗ وَإِنَّ اَ۬للَّهَ لَسَمِيعٌ عَلِيمٌ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1206,10,'182',8,'Al-Anfāl','الأنفَال',9,11,44,'إِذۡ يُرِيكَهُمُ اُ۬للَّهُ فِي مَنَامِكَ قَلِيلٗاۖ وَلَوۡ أَر۪ىٰكَهُمۡ كَثِيرٗا لَّفَشِلۡتُمۡ وَلَتَنَٰزَعۡتُمۡ فِي اِ۬لۡأَمۡرِ وَلَٰكِنَّ اَ۬للَّهَ سَلَّمَۗ إِنَّهُۥ عَلِيمُۢ بِذَاتِ اِ۬لصُّدُورِ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1207,10,'182',8,'Al-Anfāl','الأنفَال',11,14,45,'وَإِذۡ يُرِيكُمُوهُمۡ إِذِ اِ۪لۡتَقَيۡتُمۡ فِيٓ أَعۡيُنِكُمۡ قَلِيلٗا وَيُقَلِّلُكُمۡ فِيٓ أَعۡيُنِهِمۡ لِيَقۡضِيَ اَ۬للَّهُ أَمۡرٗا كَانَ مَفۡعُولٗاۗ وَإِلَى اَ۬للَّهِ تُرۡجَعُ اُ۬لۡأُمُورُ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1208,10,'182',8,'Al-Anfāl','الأنفَال',14,15,46,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُوٓاْ إِذَا لَقِيتُمۡ فِئَةٗ فَاَثۡبُتُواْ وَاَذۡكُرُواْ اُ۬للَّهَ كَثِيرٗا لَّعَلَّكُمۡ تُفۡلِحُونَ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1209,10,'183',8,'Al-Anfāl','الأنفَال',1,2,47,'وَأَطِيعُواْ اُ۬للَّهَ وَرَسُولَهُۥ وَلَا تَنَٰزَعُواْ فَتَفۡشَلُواْ وَتَذۡهَبَ رِيحُكُمۡۖ وَاَصۡبِرُواْۚ إِنَّ اَ۬للَّهَ مَعَ اَ۬لصَّٰبِرِينَ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1210,10,'183',8,'Al-Anfāl','الأنفَال',2,4,48,'وَلَا تَكُونُواْ كَاَلَّذِينَ خَرَجُواْ مِن دِيٰ۪رِهِم بَطَرٗا وَرِئَآءَ اَ۬لنّ۪اسِ وَيَصُدُّونَ عَن سَبِيلِ اِ۬للَّهِۚ وَاَللَّهُ بِمَا يَعۡمَلُونَ مُحِيطٞ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1211,10,'183',8,'Al-Anfāl','الأنفَال',4,8,49,'۞ وَإِذ زَّيَّنَ لَهُمُ اُ۬لشَّيۡطَٰنُ أَعۡمَٰلَهُمۡ وَقَالَ لَا غَالِبَ لَكُمُ اُ۬لۡيَوۡمَ مِنَ اَ۬لنّ۪اسِ وَإِنِّي جَارٞ لَّكُمۡۖ فَلَمَّا تَرَآءَتِ اِ۬لۡفِئَتَانِ نَكَصَ عَلَىٰ عَقِبَيۡهِ وَقَالَ إِنِّي بَرِيٓءٞ مِّنكُمۡ إِنِّيَ أَر۪يٰ مَا لَا تَرَوۡنَ إِنِّيَ أَخَافُ اُ۬للَّهَۚ وَاَللَّهُ شَدِيدُ اُ۬لۡعِقَابِ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1212,10,'183',8,'Al-Anfāl','الأنفَال',8,10,50,'إِذۡ يَقُولُ اُ۬لۡمُنَٰفِقُونَ وَاَلَّذِينَ فِي قُلُوبِهِم مَّرَضٌ غَرَّ هَٰٓؤُلَآءِ دِينُهُمۡۗ وَمَن يَتَوَكَّلۡ عَلَى اَ۬للَّهِ فَإِنَّ اَ۬للَّهَ عَزِيزٌ حَكِيمٞ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1213,10,'183',8,'Al-Anfāl','الأنفَال',10,12,51,'وَلَوۡ تَر۪يٰٓ إِذۡ يَتَوَفَّى اَ۬لَّذِينَ كَفَرُواْ اُ۬لۡمَلَٰٓئِكَةُ يَضۡرِبُونَ وُجُوهَهُمۡ وَأَدۡبَٰرَهُمۡ وَذُوقُواْ عَذَابَ اَ۬لۡحَرِيقِ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1214,10,'183',8,'Al-Anfāl','الأنفَال',12,13,52,'ذَٰلِكَ بِمَا قَدَّمَتۡ أَيۡدِيكُمۡ وَأَنَّ اَ۬للَّهَ لَيۡسَ بِظَلَّٰمٖ لِّلۡعَبِيدِ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1215,10,'183',8,'Al-Anfāl','الأنفَال',14,15,53,'كَدَأۡبِ ءَالِ فِرۡعَوۡنَ وَاَلَّذِينَ مِن قَبۡلِهِمۡۚ كَفَرُواْ بِـَٔايَٰتِ اِ۬للَّهِ فَأَخَذَهُمُ اُ۬للَّهُ بِذُنُوبِهِمۡۚ إِنَّ اَ۬للَّهَ قَوِيّٞ شَدِيدُ اُ۬لۡعِقَابِ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1216,10,'184',8,'Al-Anfāl','الأنفَال',1,2,54,'ذَٰلِكَ بِأَنَّ اَ۬للَّهَ لَمۡ يَكُ مُغَيِّرٗا نِّعۡمَةً أَنۡعَمَهَا عَلَىٰ قَوۡمٍ حَتَّىٰ يُغَيِّرُواْ مَا بِأَنفُسِهِمۡ وَأَنَّ اَ۬للَّهَ سَمِيعٌ عَلِيمٞ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1217,10,'184',8,'Al-Anfāl','الأنفَال',2,4,55,'كَدَأۡبِ ءَالِ فِرۡعَوۡنَ وَاَلَّذِينَ مِن قَبۡلِهِمۡۚ كَذَّبُواْ بِـَٔايَٰتِ رَبِّهِمۡ فَأَهۡلَكۡنَٰهُم بِذُنُوبِهِمۡ وَأَغۡرَقۡنَآ ءَالَ فِرۡعَوۡنَۚ وَكُلّٞ كَانُواْ ظَٰلِمِينَ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1218,10,'184',8,'Al-Anfāl','الأنفَال',5,5,56,'إِنَّ شَرَّ اَ۬لدَّوَآبِّ عِندَ اَ۬للَّهِ اِ۬لَّذِينَ كَفَرُواْ فَهُمۡ لَا يُؤۡمِنُونَ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1219,10,'184',8,'Al-Anfāl','الأنفَال',6,7,57,'اَ۬لَّذِينَ عَٰهَدتَّ مِنۡهُمۡ ثُمَّ يَنقُضُونَ عَهۡدَهُمۡ فِي كُلِّ مَرَّةٖ وَهُمۡ لَا يَتَّقُونَ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1220,10,'184',8,'Al-Anfāl','الأنفَال',7,8,58,'فَإِمَّا تَثۡقَفَنَّهُمۡ فِي اِ۬لۡحَرۡبِ فَشَرِّدۡ بِهِم مَّنۡ خَلۡفَهُمۡ لَعَلَّهُمۡ يَذَّكَّرُونَ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1221,10,'184',8,'Al-Anfāl','الأنفَال',8,9,59,'وَإِمَّا تَخَافَنَّ مِن قَوۡمٍ خِيَانَةٗ فَاَنۢبِذۡ إِلَيۡهِمۡ عَلَىٰ سَوَآءٍۚ إِنَّ اَ۬للَّهَ لَا يُحِبُّ اُ۬لۡخَآئِنِينَ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1222,10,'184',8,'Al-Anfāl','الأنفَال',10,10,60,'وَلَا تَحۡسِبَنَّ اَ۬لَّذِينَ كَفَرُواْ سَبَقُوٓاْۚ إِنَّهُمۡ لَا يُعۡجِزُونَ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1223,10,'184',8,'Al-Anfāl','الأنفَال',11,14,61,'۞ وَأَعِدُّواْ لَهُم مَّا اَ۪سۡتَطَعۡتُم مِّن قُوَّةٖ وَمِن رِّبَاطِ اِ۬لۡخَيۡلِ تُرۡهِبُونَ بِهِۦ عَدُوَّ اَ۬للَّهِ وَعَدُوَّكُمۡ وَءَاخَرِينَ مِن دُونِهِمۡ لَا تَعۡلَمُونَهُمُ اُ۬للَّهُ يَعۡلَمُهُمۡۚ وَمَا تُنفِقُواْ مِن شَيۡءٖ فِي سَبِيلِ اِ۬للَّهِ يُوَفَّ إِلَيۡكُمۡ وَأَنتُمۡ لَا تُظۡلَمُونَ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1224,10,'184',8,'Al-Anfāl','الأنفَال',14,15,62,'وَإِن جَنَحُواْ لِلسَّلۡمِ فَاَجۡنَحۡ لَهَا وَتَوَكَّلۡ عَلَى اَ۬للَّهِۚ إِنَّهُۥ هُوَ اَ۬لسَّمِيعُ اُ۬لۡعَلِيمُ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1225,10,'185',8,'Al-Anfāl','الأنفَال',1,2,63,'وَإِن يُرِيدُوٓاْ أَن يَخۡدَعُوكَ فَإِنَّ حَسۡبَكَ اَ۬للَّهُۚ هُوَ اَ۬لَّذِيٓ أَيَّدَكَ بِنَصۡرِهِۦ وَبِالۡمُؤۡمِنِينَ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1226,10,'185',8,'Al-Anfāl','الأنفَال',2,4,64,'وَأَلَّفَ بَيۡنَ قُلُوبِهِمۡۚ لَوۡ أَنفَقۡتَ مَا فِي اِ۬لۡأَرۡضِ جَمِيعٗا مَّآ أَلَّفۡتَ بَيۡنَ قُلُوبِهِمۡ وَلَٰكِنَّ اَ۬للَّهَ أَلَّفَ بَيۡنَهُمۡۚ إِنَّهُۥ عَزِيزٌ حَكِيمٞ ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1227,10,'185',8,'Al-Anfāl','الأنفَال',4,5,65,'يَٰٓأَيُّهَا اَ۬لنَّبِيُّ حَسۡبُكَ اَ۬للَّهُ وَمَنِ اِ۪تَّبَعَكَ مِنَ اَ۬لۡمُؤۡمِنِينَ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1228,10,'185',8,'Al-Anfāl','الأنفَال',5,8,66,'يَٰٓأَيُّهَا اَ۬لنَّبِيُّ حَرِّضِ اِ۬لۡمُؤۡمِنِينَ عَلَى اَ۬لۡقِتَالِۚ إِن يَكُن مِّنكُمۡ عِشۡرُونَ صَٰبِرُونَ يَغۡلِبُواْ مِاْئَتَيۡنِۚ وَإِن يَكُن مِّنكُم مِّاْئَةٞ يَغۡلِبُوٓاْ أَلۡفٗا مِّنَ اَ۬لَّذِينَ كَفَرُواْ بِأَنَّهُمۡ قَوۡمٞ لَّا يَفۡقَهُونَ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1229,10,'185',8,'Al-Anfāl','الأنفَال',8,11,67,'اَ۬لۡـَٰٔنَ خَفَّفَ اَ۬للَّهُ عَنكُمۡ وَعَلِمَ أَنَّ فِيكُمۡ ضُعۡفٗاۚ فَإِن تَكُن مِّنكُم مِّاْئَةٞ صَابِرَةٞ يَغۡلِبُواْ مِاْئَتَيۡنِۚ وَإِن يَكُن مِّنكُمۡ أَلۡفٞ يَغۡلِبُوٓاْ أَلۡفَيۡنِ بِإِذۡنِ اِ۬للَّهِۗ وَاَللَّهُ مَعَ اَ۬لصَّٰبِرِينَ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1230,10,'185',8,'Al-Anfāl','الأنفَال',11,13,68,'مَا كَانَ لِنَبِيٍّ أَن تَكُونَ لَهُۥٓ أَسۡر۪يٰ حَتَّىٰ يُثۡخِنَ فِي اِ۬لۡأَرۡضِۚ تُرِيدُونَ عَرَضَ اَ۬لدُّنۡيۭا وَاَللَّهُ يُرِيدُ اُ۬لۡأٓخِرَةَۗ وَاَللَّهُ عَزِيزٌ حَكِيمٞ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1231,10,'185',8,'Al-Anfāl','الأنفَال',13,14,69,'لَّوۡلَا كِتَٰبٞ مِّنَ اَ۬للَّهِ سَبَقَ لَمَسَّكُمۡ فِيمَآ أَخَذتُّمۡ عَذَابٌ عَظِيمٞ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1232,10,'185',8,'Al-Anfāl','الأنفَال',14,15,70,'فَكُلُواْ مِمَّا غَنِمۡتُمۡ حَلَٰلٗا طَيِّبٗاۚ وَاَتَّقُواْ اُ۬للَّهَۚ إِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٞ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1233,10,'186',8,'Al-Anfāl','الأنفَال',1,3,71,'۞ يَٰٓأَيُّهَا اَ۬لنَّبِيُّ قُل لِّمَن فِيٓ أَيۡدِيكُم مِّنَ اَ۬لۡأُسَٰر۪يٰٓ إِن يَعۡلَمِ اِ۬للَّهُ فِي قُلُوبِكُمۡ خَيۡرٗا يُؤۡتِكُمۡ خَيۡرٗا مِّمَّآ أُخِذَ مِنكُمۡ وَيَغۡفِر لَّكُمۡۗ وَاَللَّهُ غَفُورٞ رَّحِيمٞ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1234,10,'186',8,'Al-Anfāl','الأنفَال',3,4,72,'وَإِن يُرِيدُواْ خِيَانَتَكَ فَقَدۡ خَانُواْ اُ۬للَّهَ مِن قَبۡلُ فَأَمۡكَنَ مِنۡهُمۡۗ وَاَللَّهُ عَلِيمٌ حَكِيمٌ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1235,10,'186',8,'Al-Anfāl','الأنفَال',4,9,73,'إِنَّ اَ۬لَّذِينَ ءَامَنُواْ وَهَاجَرُواْ وَجَٰهَدُواْ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡ فِي سَبِيلِ اِ۬للَّهِ وَاَلَّذِينَ ءَاوَواْ وَّنَصَرُوٓاْ أُوْلَٰٓئِكَ بَعۡضُهُمۡ أَوۡلِيَآءُ بَعۡضٖۚ وَاَلَّذِينَ ءَامَنُواْ وَلَمۡ يُهَاجِرُواْ مَا لَكُم مِّن وَلَٰيَتِهِم مِّن شَيۡءٍ حَتَّىٰ يُهَاجِرُواْۚ وَإِنِ اِ۪سۡتَنصَرُوكُمۡ فِي اِ۬لدِّينِ فَعَلَيۡكُمُ اُ۬لنَّصۡرُ إِلَّا عَلَىٰ قَوۡمِۢ بَيۡنَكُمۡ وَبَيۡنَهُم مِّيثَٰقٞۗ وَاَللَّهُ بِمَا تَعۡمَلُونَ بَصِيرٞ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1236,10,'186',8,'Al-Anfāl','الأنفَال',9,11,74,'وَاَلَّذِينَ كَفَرُواْ بَعۡضُهُمۡ أَوۡلِيَآءُ بَعۡضٍۚ إِلَّا تَفۡعَلُوهُ تَكُن فِتۡنَةٞ فِي اِ۬لۡأَرۡضِ وَفَسَادٞ كَبِيرٞ ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1237,10,'186',8,'Al-Anfāl','الأنفَال',11,13,75,'وَاَلَّذِينَ ءَامَنُواْ وَهَاجَرُواْ وَجَٰهَدُواْ فِي سَبِيلِ اِ۬للَّهِ وَاَلَّذِينَ ءَاوَواْ وَّنَصَرُوٓاْ أُوْلَٰٓئِكَ هُمُ اُ۬لۡمُؤۡمِنُونَ حَقّٗاۚ لَّهُم مَّغۡفِرَةٞ وَرِزۡقٞ كَرِيمٞ ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1238,10,'186',8,'Al-Anfāl','الأنفَال',13,15,76,'وَاَلَّذِينَ ءَامَنُواْ مِنۢ بَعۡدُ وَهَاجَرُواْ وَجَٰهَدُواْ مَعَكُمۡ فَأُوْلَٰٓئِكَ مِنكُمۡۚ وَأُوْلُواْ اُ۬لۡأَرۡحَامِ بَعۡضُهُمۡ أَوۡلَىٰ بِبَعۡضٖ فِي كِتَٰبِ اِ۬للَّهِۗ إِنَّ اَ۬للَّهَ بِكُلِّ شَيۡءٍ عَلِيمُۢ ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1239,10,'187',9,'At-Taubah','التوبَة',2,2,1,'بَرَآءَةٞ مِّنَ اَ۬للَّهِ وَرَسُولِهِۦٓ إِلَى اَ۬لَّذِينَ عَٰهَدتُّم مِّنَ اَ۬لۡمُشۡرِكِينَ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1240,10,'187',9,'At-Taubah','التوبَة',3,4,2,'فَسِيحُواْ فِي اِ۬لۡأَرۡضِ أَرۡبَعَةَ أَشۡهُرٖ وَاَعۡلَمُوٓاْ أَنَّكُمۡ غَيۡرُ مُعۡجِزِي اِ۬للَّهِ وَأَنَّ اَ۬للَّهَ مُخۡزِي اِ۬لۡكٰ۪فِرِينَ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1241,10,'187',9,'At-Taubah','التوبَة',4,7,3,'وَأَذَٰنٞ مِّنَ اَ۬للَّهِ وَرَسُولِهِۦٓ إِلَى اَ۬لنّ۪اسِ يَوۡمَ اَ۬لۡحَجِّ اِ۬لۡأَكۡبَرِ أَنَّ اَ۬للَّهَ بَرِيٓءٞ مِّنَ اَ۬لۡمُشۡرِكِينَ وَرَسُولُهُۥۚ فَإِن تُبۡتُمۡ فَهۡوَ خَيۡرٞ لَّكُمۡۖ وَإِن تَوَلَّيۡتُمۡ فَاَعۡلَمُوٓاْ أَنَّكُمۡ غَيۡرُ مُعۡجِزِي اِ۬للَّهِۗ وَبَشِّرِ اِ۬لَّذِينَ كَفَرُواْ بِعَذَابٍ أَلِيمٍ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1242,10,'187',9,'At-Taubah','التوبَة',8,10,4,'إِلَّا اَ۬لَّذِينَ عَٰهَدتُّم مِّنَ اَ۬لۡمُشۡرِكِينَ ثُمَّ لَمۡ يَنقُصُوكُمۡ شَيۡـٔٗا وَلَمۡ يُظَٰهِرُواْ عَلَيۡكُمۡ أَحَدٗا فَأَتِمُّوٓاْ إِلَيۡهِمۡ عَهۡدَهُمۡ إِلَىٰ مُدَّتِهِمۡۚ إِنَّ اَ۬للَّهَ يُحِبُّ اُ۬لۡمُتَّقِينَ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1243,10,'187',9,'At-Taubah','التوبَة',10,13,5,'فَإِذَا اَ۪نسَلَخَ اَ۬لۡأَشۡهُرُ اُ۬لۡحُرُمُ فَاَقۡتُلُواْ اُ۬لۡمُشۡرِكِينَ حَيۡثُ وَجَدتُّمُوهُمۡ وَخُذُوهُمۡ وَاَحۡصُرُوهُمۡ وَاَقۡعُدُواْ لَهُمۡ كُلَّ مَرۡصَدٖۚ فَإِن تَابُواْ وَأَقَامُواْ اُ۬لصَّلَوٰةَ وَءَاتَوُاْ اُ۬لزَّكَوٰةَ فَخَلُّواْ سَبِيلَهُمۡۚ إِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٞ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1244,10,'187',9,'At-Taubah','التوبَة',13,15,6,'وَإِنۡ أَحَدٞ مِّنَ اَ۬لۡمُشۡرِكِينَ اَ۪سۡتَجَارَكَ فَأَجِرۡهُ حَتَّىٰ يَسۡمَعَ كَلَٰمَ اَ۬للَّهِ ثُمَّ أَبۡلِغۡهُ مَأۡمَنَهُۥۚ ذَٰلِكَ بِأَنَّهُمۡ قَوۡمٞ لَّا يَعۡلَمُونَ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1245,10,'188',9,'At-Taubah','التوبَة',1,3,7,'كَيۡفَ يَكُونُ لِلۡمُشۡرِكِينَ عَهۡدٌ عِندَ اَ۬للَّهِ وَعِندَ رَسُولِهِۦٓ إِلَّا اَ۬لَّذِينَ عَٰهَدتُّمۡ عِندَ اَ۬لۡمَسۡجِدِ اِ۬لۡحَرَامِۖ فَمَا اَ۪سۡتَقَٰمُواْ لَكُمۡ فَاَسۡتَقِيمُواْ لَهُمۡۚ إِنَّ اَ۬للَّهَ يُحِبُّ اُ۬لۡمُتَّقِينَ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1246,10,'188',9,'At-Taubah','التوبَة',4,6,8,'كَيۡفَ وَإِن يَظۡهَرُواْ عَلَيۡكُمۡ لَا يَرۡقُبُواْ فِيكُمۡ إِلّٗا وَلَا ذِمَّةٗۚ يُرۡضُونَكُم بِأَفۡوَٰهِهِمۡ وَتَأۡبَىٰ قُلُوبُهُمۡ وَأَكۡثَرُهُمۡ فَٰسِقُونَ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1247,10,'188',9,'At-Taubah','التوبَة',6,7,9,'۞ اَ۪شۡتَرَوۡاْ بِـَٔايَٰتِ اِ۬للَّهِ ثَمَنٗا قَلِيلٗا فَصَدُّواْ عَن سَبِيلِهِۦٓۚ إِنَّهُمۡ سَآءَ مَا كَانُواْ يَعۡمَلُونَ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1248,10,'188',9,'At-Taubah','التوبَة',7,8,10,'لَا يَرۡقُبُونَ فِي مُؤۡمِنٍ إِلّٗا وَلَا ذِمَّةٗۚ وَأُوْلَٰٓئِكَ هُمُ اُ۬لۡمُعۡتَدُونَ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1249,10,'188',9,'At-Taubah','التوبَة',8,10,11,'فَإِن تَابُواْ وَأَقَامُواْ اُ۬لصَّلَوٰةَ وَءَاتَوُاْ اُ۬لزَّكَوٰةَ فَإِخۡوَٰنُكُمۡ فِي اِ۬لدِّينِۗ وَنُفَصِّلُ اُ۬لۡأٓيَٰتِ لِقَوۡمٖ يَعۡلَمُونَ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1250,10,'188',9,'At-Taubah','التوبَة',10,13,12,'وَإِن نَّكَثُوٓاْ أَيۡمَٰنَهُم مِّنۢ بَعۡدِ عَهۡدِهِمۡ وَطَعَنُواْ فِي دِينِكُمۡ فَقَٰتِلُوٓاْ أَى۪مَّةَ اَ۬لۡكُفۡرِ إِنَّهُمۡ لَآ أَيۡمَٰنَ لَهُمۡ لَعَلَّهُمۡ يَنتَهُونَ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1251,10,'188',9,'At-Taubah','التوبَة',13,15,13,'أَلَا تُقَٰتِلُونَ قَوۡمٗا نَّكَثُوٓاْ أَيۡمَٰنَهُمۡ وَهَمُّواْ بِإِخۡرَاجِ اِ۬لرَّسُولِ وَهُم بَدَءُوكُمۡ أَوَّلَ مَرَّةٍۚ أَتَخۡشَوۡنَهُمۡۚ فَاَللَّهُ أَحَقُّ أَن تَخۡشَوۡهُ إِن كُنتُم مُّؤۡمِنِينَ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1252,10,'189',9,'At-Taubah','التوبَة',1,2,14,'قَٰتِلُوهُمۡ يُعَذِّبۡهُمُ اُ۬للَّهُ بِأَيۡدِيكُمۡ وَيُخۡزِهِمۡ وَيَنصُرۡكُمۡ عَلَيۡهِمۡ وَيَشۡفِ صُدُورَ قَوۡمٖ مُّؤۡمِنِينَ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1253,10,'189',9,'At-Taubah','التوبَة',2,3,15,'وَيُذۡهِبۡ غَيۡظَ قُلُوبِهِمۡۗ وَيَتُوبُ اُ۬للَّهُ عَلَىٰ مَن يَشَآءُۗ وَاَللَّهُ عَلِيمٌ حَكِيمٌ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1254,10,'189',9,'At-Taubah','التوبَة',4,6,16,'أَمۡ حَسِبۡتُمۡ أَن تُتۡرَكُواْ وَلَمَّا يَعۡلَمِ اِ۬للَّهُ اُ۬لَّذِينَ جَٰهَدُواْ مِنكُمۡ وَلَمۡ يَتَّخِذُواْ مِن دُونِ اِ۬للَّهِ وَلَا رَسُولِهِۦ وَلَا اَ۬لۡمُؤۡمِنِينَ وَلِيجَةٗۚ وَاَللَّهُ خَبِيرُۢ بِمَا تَعۡمَلُونَ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1255,10,'189',9,'At-Taubah','التوبَة',6,8,17,'مَا كَانَ لِلۡمُشۡرِكِينَ أَن يَعۡمُرُواْ مَسۡجِدَ اَ۬للَّهِ شَٰهِدِينَ عَلَىٰٓ أَنفُسِهِم بِالۡكُفۡرِۚ أُوْلَٰٓئِكَ حَبِطَتۡ أَعۡمَٰلُهُمۡ وَفِي اِ۬لنّ۪ارِ هُمۡ خَٰلِدُونَ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1256,10,'189',9,'At-Taubah','التوبَة',8,11,18,'إِنَّمَا يَعۡمُرُ مَسَٰجِدَ اَ۬للَّهِ مَنۡ ءَامَنَ بِاللَّهِ وَاَلۡيَوۡمِ اِ۬لۡأٓخِرِ وَأَقَامَ اَ۬لصَّلَوٰةَ وَءَاتَى اَ۬لزَّكَوٰةَ وَلَمۡ يَخۡشَ إِلَّا اَ۬للَّهَۖ فَعَسَىٰٓ أُوْلَٰٓئِكَ أَن يَكُونُواْ مِنَ اَ۬لۡمُهۡتَدِينَ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1257,10,'189',9,'At-Taubah','التوبَة',11,13,19,'۞ أَجَعَلۡتُمۡ سِقَايَةَ اَ۬لۡحَآجِّ وَعِمَارَةَ اَ۬لۡمَسۡجِدِ اِ۬لۡحَرَامِ كَمَنۡ ءَامَنَ بِاللَّهِ وَاَلۡيَوۡمِ اِ۬لۡأٓخِرِ وَجَٰهَدَ فِي سَبِيلِ اِ۬للَّهِۚ لَا يَسۡتَوُۥنَ عِندَ اَ۬للَّهِۗ وَاَللَّهُ لَا يَهۡدِي اِ۬لۡقَوۡمَ اَ۬لظَّٰلِمِينَ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1258,10,'189',9,'At-Taubah','التوبَة',14,15,20,'اَ۬لَّذِينَ ءَامَنُواْ وَهَاجَرُواْ وَجَٰهَدُواْ فِي سَبِيلِ اِ۬للَّهِ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡ أَعۡظَمُ دَرَجَةً عِندَ اَ۬للَّهِۚ وَأُوْلَٰٓئِكَ هُمُ اُ۬لۡفَآئِزُونَ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1259,10,'190',9,'At-Taubah','التوبَة',1,2,21,'يُبَشِّرُهُمۡ رَبُّهُم بِرَحۡمَةٖ مِّنۡهُ وَرِضۡوَٰنٖ وَجَنَّٰتٖ لَّهُمۡ فِيهَا نَعِيمٞ مُّقِيمٌ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1260,10,'190',9,'At-Taubah','التوبَة',2,3,22,'خَٰلِدِينَ فِيهَآ أَبَدًاۚ إِنَّ اَ۬للَّهَ عِندَهُۥٓ أَجۡرٌ عَظِيمٞ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1261,10,'190',9,'At-Taubah','التوبَة',3,5,23,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ لَا تَتَّخِذُوٓاْ ءَابَآءَكُمۡ وَإِخۡوَٰنَكُمۡ أَوۡلِيَآءَ ا۪نِ اِ۪سۡتَحَبُّواْ اُ۬لۡكُفۡرَ عَلَى اَ۬لۡإِيمَٰنِۚ وَمَن يَتَوَلَّهُم مِّنكُمۡ فَأُوْلَٰٓئِكَ هُمُ اُ۬لظَّٰلِمُونَ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1262,10,'190',9,'At-Taubah','التوبَة',5,10,24,'قُلۡ إِن كَانَ ءَابَآؤُكُمۡ وَأَبۡنَآؤُكُمۡ وَإِخۡوَٰنُكُمۡ وَأَزۡوَٰجُكُمۡ وَعَشِيرَتُكُمۡ وَأَمۡوَٰلٌ اِ۪قۡتَرَفۡتُمُوهَا وَتِجَٰرَةٞ تَخۡشَوۡنَ كَسَادَهَا وَمَسَٰكِنُ تَرۡضَوۡنَهَآ أَحَبَّ إِلَيۡكُم مِّنَ اَ۬للَّهِ وَرَسُولِهِۦ وَجِهَادٖ فِي سَبِيلِهِۦ فَتَرَبَّصُواْ حَتَّىٰ يَأۡتِيَ اَ۬للَّهُ بِأَمۡرِهِۦۗ وَاَللَّهُ لَا يَهۡدِي اِ۬لۡقَوۡمَ اَ۬لۡفَٰسِقِينَ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1263,10,'190',9,'At-Taubah','التوبَة',10,13,25,'لَقَدۡ نَصَرَكُمُ اُ۬للَّهُ فِي مَوَاطِنَ كَثِيرَةٖ وَيَوۡمَ حُنَيۡنٍ إِذۡ أَعۡجَبَتۡكُمۡ كَثۡرَتُكُمۡ فَلَمۡ تُغۡنِ عَنكُمۡ شَيۡـٔٗا وَضَاقَتۡ عَلَيۡكُمُ اُ۬لۡأَرۡضُ بِمَا رَحُبَت ثُّمَّ وَلَّيۡتُم مُّدۡبِرِينَ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1264,10,'190',9,'At-Taubah','التوبَة',13,15,26,'ثُمَّ أَنزَلَ اَ۬للَّهُ سَكِينَتَهُۥ عَلَىٰ رَسُولِهِۦ وَعَلَى اَ۬لۡمُؤۡمِنِينَ وَأَنزَلَ جُنُودٗا لَّمۡ تَرَوۡهَا وَعَذَّبَ اَ۬لَّذِينَ كَفَرُواْۚ وَذَٰلِكَ جَزَآءُ اُ۬لۡكٰ۪فِرِينَ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1265,10,'191',9,'At-Taubah','التوبَة',1,2,27,'ثُمَّ يَتُوبُ اُ۬للَّهُ مِنۢ بَعۡدِ ذَٰلِكَ عَلَىٰ مَن يَشَآءُۗ وَاَللَّهُ غَفُورٞ رَّحِيمٞ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1266,10,'191',9,'At-Taubah','التوبَة',2,5,28,'۞ يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُوٓاْ إِنَّمَا اَ۬لۡمُشۡرِكُونَ نَجَسٞ فَلَا يَقۡرَبُواْ اُ۬لۡمَسۡجِدَ اَ۬لۡحَرَامَ بَعۡدَ عَامِهِمۡ هَٰذَاۚ وَإِنۡ خِفۡتُمۡ عَيۡلَةٗ فَسَوۡفَ يُغۡنِيكُمُ اُ۬للَّهُ مِن فَضۡلِهِۦٓ إِن شَآءَۚ ا۪نَّ اَ۬للَّهَ عَلِيمٌ حَكِيمٞ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1267,10,'191',9,'At-Taubah','التوبَة',5,9,29,'قَٰتِلُواْ اُ۬لَّذِينَ لَا يُؤۡمِنُونَ بِاللَّهِ وَلَا بِالۡيَوۡمِ اِ۬لۡأٓخِرِ وَلَا يُحَرِّمُونَ مَا حَرَّمَ اَ۬للَّهُ وَرَسُولُهُۥ وَلَا يَدِينُونَ دِينَ اَ۬لۡحَقِّ مِنَ اَ۬لَّذِينَ أُوتُواْ اُ۬لۡكِتَٰبَ حَتَّىٰ يُعۡطُواْ اُ۬لۡجِزۡيَةَ عَن يَدٖ وَهُمۡ صَٰغِرُونَ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1268,10,'191',9,'At-Taubah','التوبَة',9,12,30,'وَقَالَتِ اِ۬لۡيَهُودُ عُزَيۡرُ اُ۪بۡنُ اُ۬للَّهِ وَقَالَتِ اِ۬لنَّصَٰرَى اَ۬لۡمَسِيحُ اُ۪بۡنُ اُ۬للَّهِۖ ذَٰلِكَ قَوۡلُهُم بِأَفۡوَٰهِهِمۡۖ يُضَٰهُونَ قَوۡلَ اَ۬لَّذِينَ كَفَرُواْ مِن قَبۡلُۚ قَٰتَلَهُمُ اُ۬للَّهُۚ أَنّۭيٰ يُؤۡفَكُونَ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1269,10,'191',9,'At-Taubah','التوبَة',12,15,31,'اَ۪تَّخَذُوٓاْ أَحۡبَارَهُمۡ وَرُهۡبَٰنَهُمۡ أَرۡبَابٗا مِّن دُونِ اِ۬للَّهِ وَاَلۡمَسِيحَ اَ۪بۡنَ مَرۡيَمَ وَمَآ أُمِرُوٓاْ إِلَّا لِيَعۡبُدُوٓاْ إِلَٰهٗا وَٰحِدٗاۖ لَّآ إِلَٰهَ إِلَّا هُوَۚ سُبۡحَٰنَهُۥ عَمَّا يُشۡرِكُونَ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1270,10,'192',9,'At-Taubah','التوبَة',1,2,32,'يُرِيدُونَ أَن يُطۡفِـُٔواْ نُورَ اَ۬للَّهِ بِأَفۡوَٰهِهِمۡ وَيَأۡبَى اَ۬للَّهُ إِلَّآ أَن يُتِمَّ نُورَهُۥ وَلَوۡ كَرِهَ اَ۬لۡكَٰفِرُونَ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1271,10,'192',9,'At-Taubah','التوبَة',2,4,33,'هُوَ اَ۬لَّذِيٓ أَرۡسَلَ رَسُولَهُۥ بِالۡهُدَىٰ وَدِينِ اِ۬لۡحَقِّ لِيُظۡهِرَهُۥ عَلَى اَ۬لدِّينِ كُلِّهِۦ وَلَوۡ كَرِهَ اَ۬لۡمُشۡرِكُونَ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1272,10,'192',9,'At-Taubah','التوبَة',4,8,34,'۞ يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُوٓاْ إِنَّ كَثِيرٗا مِّنَ اَ۬لۡأَحۡب۪ارِ وَاَلرُّهۡبَانِ لَيَأۡكُلُونَ أَمۡوَٰلَ اَ۬لنّ۪اسِ بِالۡبَٰطِلِ وَيَصُدُّونَ عَن سَبِيلِ اِ۬للَّهِۗ وَاَلَّذِينَ يَكۡنِزُونَ اَ۬لذَّهَبَ وَاَلۡفِضَّةَ وَلَا يُنفِقُونَهَا فِي سَبِيلِ اِ۬للَّهِ فَبَشِّرۡهُم بِعَذَابٍ أَلِيمٖ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1273,10,'192',9,'At-Taubah','التوبَة',8,11,35,'يَوۡمَ يُحۡمَىٰ عَلَيۡهَا فِي ن۪ارِ جَهَنَّمَ فَتُكۡوَىٰ بِهَا جِبَاهُهُمۡ وَجُنُوبُهُمۡ وَظُهُورُهُمۡۖ هَٰذَا مَا كَنَزۡتُمۡ لِأَنفُسِكُمۡ فَذُوقُواْ مَا كُنتُمۡ تَكۡنِزُونَ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1274,10,'192',9,'At-Taubah','التوبَة',11,15,36,'إِنَّ عِدَّةَ اَ۬لشُّهُورِ عِندَ اَ۬للَّهِ اِ۪ثۡنَا عَشَرَ شَهۡرٗا فِي كِتَٰبِ اِ۬للَّهِ يَوۡمَ خَلَقَ اَ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضَ مِنۡهَآ أَرۡبَعَةٌ حُرُمٞۚ ذَٰلِكَ اَ۬لدِّينُ اُ۬لۡقَيِّمُۚ فَلَا تَظۡلِمُواْ فِيهِنَّ أَنفُسَكُمۡۚ وَقَٰتِلُواْ اُ۬لۡمُشۡرِكِينَ كَآفَّةٗ كَمَا يُقَٰتِلُونَكُمۡ كَآفَّةٗۚ وَاَعۡلَمُوٓاْ أَنَّ اَ۬للَّهَ مَعَ اَ۬لۡمُتَّقِينَ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1275,10,'193',9,'At-Taubah','التوبَة',1,4,37,'إِنَّمَا اَ۬لنَّسِيٓءُ زِيَادَةٞ فِي اِ۬لۡكُفۡرِۖ يَضِلُّ بِهِ اِ۬لَّذِينَ كَفَرُواْ يُحِلُّونَهُۥ عَامٗا وَيُحَرِّمُونَهُۥ عَامٗا لِّيُوَاطِـُٔواْ عِدَّةَ مَا حَرَّمَ اَ۬للَّهُ فَيُحِلُّواْ مَا حَرَّمَ اَ۬للَّهُۚ زُيِّنَ لَهُمۡ سُوٓءُ اَ۬عۡمَٰلِهِمۡۗ وَاَللَّهُ لَا يَهۡدِي اِ۬لۡقَوۡمَ اَ۬لۡكٰ۪فِرِينَ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1276,10,'193',9,'At-Taubah','التوبَة',5,8,38,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ مَا لَكُمۡ إِذَا قِيلَ لَكُمُ اُ۪نفِرُواْ فِي سَبِيلِ اِ۬للَّهِ اِ۪ثَّاقَلۡتُمۡ إِلَى اَ۬لۡأَرۡضِۚ أَرَضِيتُم بِالۡحَيَوٰةِ اِ۬لدُّنۡيۭا مِنَ اَ۬لۡأٓخِرَةِۚ فَمَا مَتَٰعُ اُ۬لۡحَيَوٰةِ اِ۬لدُّنۡيۭا فِي اِ۬لۡأٓخِرَةِ إِلَّا قَلِيلٌ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1277,10,'193',9,'At-Taubah','التوبَة',8,10,39,'إِلَّا تَنفِرُواْ يُعَذِّبۡكُمۡ عَذَابًا أَلِيمٗا وَيَسۡتَبۡدِلۡ قَوۡمًا غَيۡرَكُمۡ وَلَا تَضُرُّوهُ شَيۡـٔٗاۗ وَاَللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1278,10,'193',9,'At-Taubah','التوبَة',10,15,40,'۞ إِلَّا تَنصُرُوهُ فَقَدۡ نَصَرَهُ اُ۬للَّهُ إِذۡ أَخۡرَجَهُ اُ۬لَّذِينَ كَفَرُواْ ثَانِيَ اَ۪ثۡنَيۡنِ إِذۡ هُمَا فِي اِ۬لۡغ۪ارِ إِذۡ يَقُولُ لِصَٰحِبِهِۦ لَا تَحۡزَنۡ إِنَّ اَ۬للَّهَ مَعَنَاۖ فَأَنزَلَ اَ۬للَّهُ سَكِينَتَهُۥ عَلَيۡهِ وَأَيَّدَهُۥ بِجُنُودٖ لَّمۡ تَرَوۡهَا وَجَعَلَ كَلِمَةَ اَ۬لَّذِينَ كَفَرُواْ اُ۬لسُّفۡلۭيٰۗ وَكَلِمَةُ اُ۬للَّهِ هِيَ اَ۬لۡعُلۡيۭاۗ وَاَللَّهُ عَزِيزٌ حَكِيمٌ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1279,10,'194',9,'At-Taubah','التوبَة',1,2,41,'اِ۪نفِرُواْ خِفَافٗا وَثِقَالٗا وَجَٰهِدُواْ بِأَمۡوَٰلِكُمۡ وَأَنفُسِكُمۡ فِي سَبِيلِ اِ۬للَّهِۚ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1280,10,'194',9,'At-Taubah','التوبَة',3,6,42,'لَوۡ كَانَ عَرَضٗا قَرِيبٗا وَسَفَرٗا قَاصِدٗا لَّاَتَّبَعُوكَ وَلَٰكِنۢ بَعُدَتۡ عَلَيۡهِمِ اِ۬لشُّقَّةُۚ وَسَيَحۡلِفُونَ بِاللَّهِ لَوِ اِ۪سۡتَطَعۡنَا لَخَرَجۡنَا مَعَكُمۡ يُهۡلِكُونَ أَنفُسَهُمۡ وَاَللَّهُ يَعۡلَمُ إِنَّهُمۡ لَكَٰذِبُونَ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1281,10,'194',9,'At-Taubah','التوبَة',6,7,43,'عَفَا اَ۬للَّهُ عَنكَ لِمَ أَذِنتَ لَهُمۡ حَتَّىٰ يَتَبَيَّنَ لَكَ اَ۬لَّذِينَ صَدَقُواْ وَتَعۡلَمَ اَ۬لۡكَٰذِبِينَ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1282,10,'194',9,'At-Taubah','التوبَة',8,9,44,'لَا يَسۡتَـٔۡذِنُكَ اَ۬لَّذِينَ يُؤۡمِنُونَ بِاللَّهِ وَاَلۡيَوۡمِ اِ۬لۡأٓخِرِ أَن يُجَٰهِدُواْ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡۗ وَاَللَّهُ عَلِيمُۢ بِالۡمُتَّقِينَ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1283,10,'194',9,'At-Taubah','التوبَة',10,11,45,'إِنَّمَا يَسۡتَـٔۡذِنُكَ اَ۬لَّذِينَ لَا يُؤۡمِنُونَ بِاللَّهِ وَاَلۡيَوۡمِ اِ۬لۡأٓخِرِ وَاَرۡتَابَتۡ قُلُوبُهُمۡ فَهُمۡ فِي رَيۡبِهِمۡ يَتَرَدَّدُونَ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1284,10,'194',9,'At-Taubah','التوبَة',11,13,46,'۞ وَلَوۡ أَرَادُواْ اُ۬لۡخُرُوجَ لَأَعَدُّواْ لَهُۥ عُدَّةٗ وَلَٰكِن كَرِهَ اَ۬للَّهُ اُ۪نۢبِعَاثَهُمۡ فَثَبَّطَهُمۡ وَقِيلَ اَ۟قۡعُدُواْ مَعَ اَ۬لۡقَٰعِدِينَ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1285,10,'194',9,'At-Taubah','التوبَة',13,15,47,'لَوۡ خَرَجُواْ فِيكُم مَّا زَادُوكُمۡ إِلَّا خَبَالٗا وَلَأَاْوۡضَعُواْ خِلَٰلَكُمۡ يَبۡغُونَكُمُ اُ۬لۡفِتۡنَةَ وَفِيكُمۡ سَمَّٰعُونَ لَهُمۡۗ وَاَللَّهُ عَلِيمُۢ بِالظَّٰلِمِينَ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1286,10,'195',9,'At-Taubah','التوبَة',1,2,48,'لَقَدِ اِ۪بۡتَغَوُاْ اُ۬لۡفِتۡنَةَ مِن قَبۡلُ وَقَلَّبُواْ لَكَ اَ۬لۡأُمُورَ حَتَّىٰ جَآءَ اَ۬لۡحَقُّ وَظَهَرَ أَمۡرُ اُ۬للَّهِ وَهُمۡ كَٰرِهُونَ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1287,10,'195',9,'At-Taubah','التوبَة',2,4,49,'وَمِنۡهُم مَّن يَقُولُ اُ۪ئۡذَن لِّي وَلَا تَفۡتِنِّيٓۚ أَلَا فِي اِ۬لۡفِتۡنَةِ سَقَطُواْۗ وَإِنَّ جَهَنَّمَ لَمُحِيطَةُۢ بِالۡكٰ۪فِرِينَ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1288,10,'195',9,'At-Taubah','التوبَة',4,6,50,'إِن تُصِبۡكَ حَسَنَةٞ تَسُؤۡهُمۡۖ وَإِن تُصِبۡكَ مُصِيبَةٞ يَقُولُواْ قَدۡ أَخَذۡنَآ أَمۡرَنَا مِن قَبۡلُ وَيَتَوَلَّواْ وَّهُمۡ فَرِحُونَ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1289,10,'195',9,'At-Taubah','التوبَة',6,8,51,'قُل لَّن يُصِيبَنَآ إِلَّا مَا كَتَبَ اَ۬للَّهُ لَنَا هُوَ مَوۡلَىٰنَاۚ وَعَلَى اَ۬للَّهِ فَلۡيَتَوَكَّلِ اِ۬لۡمُؤۡمِنُونَ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1290,10,'195',9,'At-Taubah','التوبَة',8,11,52,'قُلۡ هَلۡ تَرَبَّصُونَ بِنَآ إِلَّآ إِحۡدَى اَ۬لۡحُسۡنَيَيۡنِۖ وَنَحۡنُ نَتَرَبَّصُ بِكُمۡ أَن يُصِيبَكُمُ اُ۬للَّهُ بِعَذَابٖ مِّنۡ عِندِهِۦٓ أَوۡ بِأَيۡدِينَاۖ فَتَرَبَّصُوٓاْ إِنَّا مَعَكُم مُّتَرَبِّصُونَ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1291,10,'195',9,'At-Taubah','التوبَة',11,12,53,'قُلۡ أَنفِقُواْ طَوۡعًا أَوۡ كَرۡهٗا لَّن يُتَقَبَّلَ مِنكُمۡۖ إِنَّكُمۡ كُنتُمۡ قَوۡمٗا فَٰسِقِينَ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1292,10,'195',9,'At-Taubah','التوبَة',12,15,54,'۞ وَمَا مَنَعَهُمۡ أَن تُقۡبَلَ مِنۡهُمۡ نَفَقَٰتُهُمۡ إِلَّآ أَنَّهُمۡ كَفَرُواْ بِاللَّهِ وَبِرَسُولِهِۦ وَلَا يَأۡتُونَ اَ۬لصَّلَوٰةَ إِلَّا وَهُمۡ كُسَالَىٰ وَلَا يُنفِقُونَ إِلَّا وَهُمۡ كَٰرِهُونَ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1293,10,'196',9,'At-Taubah','التوبَة',1,2,55,'فَلَا تُعۡجِبۡكَ أَمۡوَٰلُهُمۡ وَلَآ أَوۡلَٰدُهُمۡۚ إِنَّمَا يُرِيدُ اُ۬للَّهُ لِيُعَذِّبَهُم بِهَا فِي اِ۬لۡحَيَوٰةِ اِ۬لدُّنۡيۭا وَتَزۡهَقَ أَنفُسُهُمۡ وَهُمۡ كَٰفِرُونَ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1294,10,'196',9,'At-Taubah','التوبَة',3,4,56,'وَيَحۡلِفُونَ بِاللَّهِ إِنَّهُمۡ لَمِنكُمۡ وَمَا هُم مِّنكُمۡ وَلَٰكِنَّهُمۡ قَوۡمٞ يَفۡرَقُونَ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1295,10,'196',9,'At-Taubah','التوبَة',4,5,57,'لَوۡ يَجِدُونَ مَلۡجَـًٔا أَوۡ مَغَٰرَٰتٍ أَوۡ مُدَّخَلٗا لَّوَلَّوۡاْ إِلَيۡهِ وَهُمۡ يَجۡمَحُونَ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1296,10,'196',9,'At-Taubah','التوبَة',5,7,58,'وَمِنۡهُم مَّن يَلۡمِزُكَ فِي اِ۬لصَّدَقَٰتِ فَإِنۡ أُعۡطُواْ مِنۡهَا رَضُواْ وَإِن لَّمۡ يُعۡطَوۡاْ مِنۡهَآ إِذَا هُمۡ يَسۡخَطُونَ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1297,10,'196',9,'At-Taubah','التوبَة',7,9,59,'وَلَوۡ أَنَّهُمۡ رَضُواْ مَآ ءَاتَىٰهُمُ اُ۬للَّهُ وَرَسُولُهُۥ وَقَالُواْ حَسۡبُنَا اَ۬للَّهُ سَيُؤۡتِينَا اَ۬للَّهُ مِن فَضۡلِهِۦ وَرَسُولُهُۥٓ إِنَّآ إِلَى اَ۬للَّهِ رَٰغِبُونَ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1298,10,'196',9,'At-Taubah','التوبَة',9,12,60,'إِنَّمَا اَ۬لصَّدَقَٰتُ لِلۡفُقَرَآءِ وَاَلۡمَسَٰكِينِ وَاَلۡعَٰمِلِينَ عَلَيۡهَا وَاَلۡمُؤَلَّفَةِ قُلُوبُهُمۡ وَفِي اِ۬لرِّقَابِ وَاَلۡغَٰرِمِينَ وَفِي سَبِيلِ اِ۬للَّهِ وَاَبۡنِ اِ۬لسَّبِيلِۖ فَرِيضَةٗ مِّنَ اَ۬للَّهِۗ وَاَللَّهُ عَلِيمٌ حَكِيمٞ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1299,10,'196',9,'At-Taubah','التوبَة',12,15,61,'۞ وَمِنۡهُمُ اُ۬لَّذِينَ يُؤۡذُونَ اَ۬لنَّبِيَّ وَيَقُولُونَ هُوَ أُذُنٞۚ قُلۡ أُذُنُ خَيۡرٖ لَّكُمۡ يُؤۡمِنُ بِاللَّهِ وَيُؤۡمِنُ لِلۡمُؤۡمِنِينَ وَرَحۡمَةٞ لِّلَّذِينَ ءَامَنُواْ مِنكُمۡۚ وَاَلَّذِينَ يُؤۡذُونَ رَسُولَ اَ۬للَّهِ لَهُمۡ عَذَابٌ أَلِيمٞ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1300,10,'197',9,'At-Taubah','التوبَة',1,2,62,'يَحۡلِفُونَ بِاللَّهِ لَكُمۡ لِيُرۡضُوكُمۡ وَاَللَّهُ وَرَسُولُهُۥٓ أَحَقُّ أَن يُرۡضُوهُ إِن كَانُواْ مُؤۡمِنِينَ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1301,10,'197',9,'At-Taubah','التوبَة',2,4,63,'أَلَمۡ يَعۡلَمُوٓاْ أَنَّهُۥ مَن يُحَادِدِ اِ۬للَّهَ وَرَسُولَهُۥ فَأَنَّ لَهُۥ نَارَ جَهَنَّمَ خَٰلِدٗا فِيهَاۚ ذَٰلِكَ اَ۬لۡخِزۡيُ اُ۬لۡعَظِيمُ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1302,10,'197',9,'At-Taubah','التوبَة',4,6,64,'يَحۡذَرُ اُ۬لۡمُنَٰفِقُونَ أَن تُنزَلَ عَلَيۡهِمۡ سُورَةٞ تُنَبِّئُهُم بِمَا فِي قُلُوبِهِمۡۚ قُلِ اِ۪سۡتَهۡزِءُوٓاْ إِنَّ اَ۬للَّهَ مُخۡرِجٞ مَّا تَحۡذَرُونَ ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1303,10,'197',9,'At-Taubah','التوبَة',6,8,65,'وَلَئِن سَأَلۡتَهُمۡ لَيَقُولُنَّ إِنَّمَا كُنَّا نَخُوضُ وَنَلۡعَبُۚ قُلۡ أَبِاللَّهِ وَءَايَٰتِهِۦ وَرَسُولِهِۦ كُنتُمۡ تَسۡتَهۡزِءُونَ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1304,10,'197',9,'At-Taubah','التوبَة',8,10,66,'لَا تَعۡتَذِرُواْ قَدۡ كَفَرۡتُم بَعۡدَ إِيمَٰنِكُمۡۚ إِن يُعۡفَ عَن طَآئِفَةٖ مِّنكُمۡ تُعَذَّبۡ طَآئِفَةُۢ بِأَنَّهُمۡ كَانُواْ مُجۡرِمِينَ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1305,10,'197',9,'At-Taubah','التوبَة',10,13,67,'اَ۬لۡمُنَٰفِقُونَ وَاَلۡمُنَٰفِقَٰتُ بَعۡضُهُم مِّنۢ بَعۡضٖۚ يَأۡمُرُونَ بِالۡمُنكَرِ وَيَنۡهَوۡنَ عَنِ اِ۬لۡمَعۡرُوفِ وَيَقۡبِضُونَ أَيۡدِيَهُمۡۚ نَسُواْ اُ۬للَّهَ فَنَسِيَهُمۡۗ إِنَّ اَ۬لۡمُنَٰفِقِينَ هُمُ اُ۬لۡفَٰسِقُونَ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1306,10,'197',9,'At-Taubah','التوبَة',13,15,68,'وَعَدَ اَ۬للَّهُ اُ۬لۡمُنَٰفِقِينَ وَاَلۡمُنَٰفِقَٰتِ وَاَلۡكُفَّارَ نَارَ جَهَنَّمَ خَٰلِدِينَ فِيهَاۚ هِيَ حَسۡبُهُمۡۚ وَلَعَنَهُمُ اُ۬للَّهُۖ وَلَهُمۡ عَذَابٞ مُّقِيمٞ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1307,10,'198',9,'At-Taubah','التوبَة',1,5,69,'كَاَلَّذِينَ مِن قَبۡلِكُمۡ كَانُوٓاْ أَشَدَّ مِنكُمۡ قُوَّةٗ وَأَكۡثَرَ أَمۡوَٰلٗا وَأَوۡلَٰدٗا فَاَسۡتَمۡتَعُواْ بِخَلَٰقِهِمۡ فَاَسۡتَمۡتَعۡتُم بِخَلَٰقِكُمۡ كَمَا اَ۪سۡتَمۡتَعَ اَ۬لَّذِينَ مِن قَبۡلِكُم بِخَلَٰقِهِمۡ وَخُضۡتُمۡ كَاَلَّذِي خَاضُوٓاْۚ أُوْلَٰٓئِكَ حَبِطَتۡ أَعۡمَٰلُهُمۡ فِي اِ۬لدُّنۡيۭا وَاَلۡأٓخِرَةِۖ وَأُوْلَٰٓئِكَ هُمُ اُ۬لۡخَٰسِرُونَ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1308,10,'198',9,'At-Taubah','التوبَة',5,6,70,'۞ أَلَمۡ يَأۡتِهِمۡ نَبَأُ اُ۬لَّذِينَ مِن قَبۡلِهِمۡ قَوۡمِ نُوحٖ وَعَادٖ وَثَمُودَ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1309,10,'198',9,'At-Taubah','التوبَة',6,9,71,'وَقَوۡمِ إِبۡرَٰهِيمَ وَأَصۡحَٰبِ مَدۡيَنَ وَاَلۡمُؤۡتَفِكَٰتِۚ أَتَتۡهُمۡ رُسۡلُهُم بِالۡبَيِّنَٰتِۖ فَمَا كَانَ اَ۬للَّهُ لِيَظۡلِمَهُمۡ وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1310,10,'198',9,'At-Taubah','التوبَة',9,12,72,'وَاَلۡمُؤۡمِنُونَ وَاَلۡمُؤۡمِنَٰتُ بَعۡضُهُمۡ أَوۡلِيَآءُ بَعۡضٖۚ يَأۡمُرُونَ بِالۡمَعۡرُوفِ وَيَنۡهَوۡنَ عَنِ اِ۬لۡمُنكَرِ وَيُقِيمُونَ اَ۬لصَّلَوٰةَ وَيُؤۡتُونَ اَ۬لزَّكَوٰةَ وَيُطِيعُونَ اَ۬للَّهَ وَرَسُولَهُۥٓۚ أُوْلَٰٓئِكَ سَيَرۡحَمُهُمُ اُ۬للَّهُۗ إِنَّ اَ۬للَّهَ عَزِيزٌ حَكِيمٞ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1311,10,'198',9,'At-Taubah','التوبَة',13,15,73,'وَعَدَ اَ۬للَّهُ اُ۬لۡمُؤۡمِنِينَ وَاَلۡمُؤۡمِنَٰتِ جَنَّٰتٖ تَجۡرِي مِن تَحۡتِهَا اَ۬لۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَا وَمَسَٰكِنَ طَيِّبَةٗ فِي جَنَّٰتِ عَدۡنٖۚ وَرِضۡوَٰنٞ مِّنَ اَ۬للَّهِ أَكۡبَرُۚ ذَٰلِكَ هُوَ اَ۬لۡفَوۡزُ اُ۬لۡعَظِيمُ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1312,10,'199',9,'At-Taubah','التوبَة',1,2,74,'يَٰٓأَيُّهَا اَ۬لنَّبِيُّ جَٰهِدِ اِ۬لۡكُفَّارَ وَاَلۡمُنَٰفِقِينَ وَاَغۡلُظۡ عَلَيۡهِمۡۚ وَمَأۡوَىٰهُمۡ جَهَنَّمُۖ وَبِئۡسَ اَ۬لۡمَصِيرُ ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1313,10,'199',9,'At-Taubah','التوبَة',2,7,75,'يَحۡلِفُونَ بِاللَّهِ مَا قَالُواْ وَلَقَدۡ قَالُواْ كَلِمَةَ اَ۬لۡكُفۡرِ وَكَفَرُواْ بَعۡدَ إِسۡلَٰمِهِمۡ وَهَمُّواْ بِمَا لَمۡ يَنَالُواْۚ وَمَا نَقَمُوٓاْ إِلَّآ أَنۡ أَغۡنَىٰهُمُ اُ۬للَّهُ وَرَسُولُهُۥ مِن فَضۡلِهِۦۚ فَإِن يَتُوبُواْ يَكُ خَيۡرٗا لَّهُمۡۖ وَإِن يَتَوَلَّوۡاْ يُعَذِّبۡهُمُ اُ۬للَّهُ عَذَابًا أَلِيمٗا فِي اِ۬لدُّنۡيۭا وَاَلۡأٓخِرَةِۚ وَمَا لَهُمۡ فِي اِ۬لۡأَرۡضِ مِن وَلِيّٖ وَلَا نَصِيرٖ ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1314,10,'199',9,'At-Taubah','التوبَة',7,8,76,'۞ وَمِنۡهُم مَّنۡ عَٰهَدَ اَ۬للَّهَ لَئِنۡ ءَاتَىٰنَا مِن فَضۡلِهِۦ لَنَصَّدَّقَنَّ وَلَنَكُونَنَّ مِنَ اَ۬لصَّٰلِحِينَ ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1315,10,'199',9,'At-Taubah','التوبَة',9,10,77,'فَلَمَّآ ءَاتَىٰهُم مِّن فَضۡلِهِۦ بَخِلُواْ بِهِۦ وَتَوَلَّواْ وَّهُم مُّعۡرِضُونَ ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1316,10,'199',9,'At-Taubah','التوبَة',10,11,78,'فَأَعۡقَبَهُمۡ نِفَاقٗا فِي قُلُوبِهِمۡ إِلَىٰ يَوۡمِ يَلۡقَوۡنَهُۥ بِمَآ أَخۡلَفُواْ اُ۬للَّهَ مَا وَعَدُوهُ وَبِمَا كَانُواْ يَكۡذِبُونَ ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1317,10,'199',9,'At-Taubah','التوبَة',12,13,79,'أَلَمۡ يَعۡلَمُوٓاْ أَنَّ اَ۬للَّهَ يَعۡلَمُ سِرَّهُمۡ وَنَجۡوۭىٰهُمۡ وَأَنَّ اَ۬للَّهَ عَلَّٰمُ اُ۬لۡغُيُوبِ ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1318,10,'199',9,'At-Taubah','التوبَة',13,15,80,'اِ۬لَّذِينَ يَلۡمِزُونَ اَ۬لۡمُطَّوِّعِينَ مِنَ اَ۬لۡمُؤۡمِنِينَ فِي اِ۬لصَّدَقَٰتِ وَاَلَّذِينَ لَا يَجِدُونَ إِلَّا جُهۡدَهُمۡ فَيَسۡخَرُونَ مِنۡهُمۡ سَخِرَ اَ۬للَّهُ مِنۡهُمۡ وَلَهُمۡ عَذَابٌ أَلِيمٌ ٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1319,10,'200',9,'At-Taubah','التوبَة',1,3,81,'اِ۪سۡتَغۡفِر لَّهُمۡ أَوۡ لَا تَسۡتَغۡفِر لَّهُمۡ إِن تَسۡتَغۡفِر لَّهُمۡ سَبۡعِينَ مَرَّةٗ فَلَن يَغۡفِرَ اَ۬للَّهُ لَهُمۡۚ ذَٰلِكَ بِأَنَّهُمۡ كَفَرُواْ بِاللَّهِ وَرَسُولِهِۦۗ وَاَللَّهُ لَا يَهۡدِي اِ۬لۡقَوۡمَ اَ۬لۡفَٰسِقِينَ ٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1320,10,'200',9,'At-Taubah','التوبَة',3,6,82,'فَرِحَ اَ۬لۡمُخَلَّفُونَ بِمَقۡعَدِهِمۡ خِلَٰفَ رَسُولِ اِ۬للَّهِ وَكَرِهُوٓاْ أَن يُجَٰهِدُواْ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡ فِي سَبِيلِ اِ۬للَّهِ وَقَالُواْ لَا تَنفِرُواْ فِي اِ۬لۡحَرِّۗ قُلۡ نَارُ جَهَنَّمَ أَشَدُّ حَرّٗاۚ لَّوۡ كَانُواْ يَفۡقَهُونَ ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1321,10,'200',9,'At-Taubah','التوبَة',6,7,83,'فَلۡيَضۡحَكُواْ قَلِيلٗا وَلۡيَبۡكُواْ كَثِيرٗا جَزَآءَۢ بِمَا كَانُواْ يَكۡسِبُونَ ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1322,10,'200',9,'At-Taubah','التوبَة',7,10,84,'فَإِن رَّجَعَكَ اَ۬للَّهُ إِلَىٰ طَآئِفَةٖ مِّنۡهُمۡ فَاَسۡتَـٔۡذَنُوكَ لِلۡخُرُوجِ فَقُل لَّن تَخۡرُجُواْ مَعِيَ أَبَدٗا وَلَن تُقَٰتِلُواْ مَعِي عَدُوًّاۖ إِنَّكُمۡ رَضِيتُم بِالۡقُعُودِ أَوَّلَ مَرَّةٖ فَاَقۡعُدُواْ مَعَ اَ۬لۡخَٰلِفِينَ ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1323,10,'200',9,'At-Taubah','التوبَة',10,11,85,'۞ وَلَا تُصَلِّ عَلَىٰٓ أَحَدٖ مِّنۡهُم مَّاتَ أَبَدٗا وَلَا تَقُمۡ عَلَىٰ قَبۡرِهِۦٓۖ إِنَّهُمۡ كَفَرُواْ بِاللَّهِ وَرَسُولِهِۦ وَمَاتُواْ وَهُمۡ فَٰسِقُونَ ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1324,10,'200',9,'At-Taubah','التوبَة',12,13,86,'وَلَا تُعۡجِبۡكَ أَمۡوَٰلُهُمۡ وَأَوۡلَٰدُهُمۡۚ إِنَّمَا يُرِيدُ اُ۬للَّهُ أَن يُعَذِّبَهُم بِهَا فِي اِ۬لدُّنۡيۭا وَتَزۡهَقَ أَنفُسُهُمۡ وَهُمۡ كَٰفِرُونَ ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1325,10,'200',9,'At-Taubah','التوبَة',13,15,87,'وَإِذَآ أُنزِلَت سُّورَةٌ أَنۡ ءَامِنُواْ بِاللَّهِ وَجَٰهِدُواْ مَعَ رَسُولِهِ اِ۪سۡتَـٔۡذَنَكَ أُوْلُواْ اُ۬لطَّوۡلِ مِنۡهُمۡ وَقَالُواْ ذَرۡنَا نَكُن مَّعَ اَ۬لۡقَٰعِدِينَ ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1326,10,'201',9,'At-Taubah','التوبَة',1,2,88,'رَضُواْ بِأَن يَكُونُواْ مَعَ اَ۬لۡخَوَالِفِ وَطُبِعَ عَلَىٰ قُلُوبِهِمۡ فَهُمۡ لَا يَفۡقَهُونَ ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1327,10,'201',9,'At-Taubah','التوبَة',2,4,89,'لَٰكِنِ اِ۬لرَّسُولُ وَاَلَّذِينَ ءَامَنُواْ مَعَهُۥ جَٰهَدُواْ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡۚ وَأُوْلَٰٓئِكَ لَهُمُ اُ۬لۡخَيۡرَٰتُۖ وَأُوْلَٰٓئِكَ هُمُ اُ۬لۡمُفۡلِحُونَ ٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1328,10,'201',9,'At-Taubah','التوبَة',4,5,90,'أَعَدَّ اَ۬للَّهُ لَهُمۡ جَنَّٰتٖ تَجۡرِي مِن تَحۡتِهَا اَ۬لۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَاۚ ذَٰلِكَ اَ۬لۡفَوۡزُ اُ۬لۡعَظِيمُ ٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1329,10,'201',9,'At-Taubah','التوبَة',6,8,91,'وَجَآءَ اَ۬لۡمُعَذِّرُونَ مِنَ اَ۬لۡأَعۡرَابِ لِيُؤۡذَنَ لَهُمۡ وَقَعَدَ اَ۬لَّذِينَ كَذَبُواْ اُ۬للَّهَ وَرَسُولَهُۥۚ سَيُصِيبُ اُ۬لَّذِينَ كَفَرُواْ مِنۡهُمۡ عَذَابٌ أَلِيمٞ ٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1330,10,'201',9,'At-Taubah','التوبَة',8,10,92,'لَّيۡسَ عَلَى اَ۬لضُّعَفَآءِ وَلَا عَلَى اَ۬لۡمَرۡضۭيٰ وَلَا عَلَى اَ۬لَّذِينَ لَا يَجِدُونَ مَا يُنفِقُونَ حَرَجٌ إِذَا نَصَحُواْ لِلَّهِ وَرَسُولِهِۦۚ مَا عَلَى اَ۬لۡمُحۡسِنِينَ مِن سَبِيلٖۚ وَاَللَّهُ غَفُورٞ رَّحِيمٞ ٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1331,11,'201',9,'At-Taubah','التوبَة',11,13,93,'وَلَا عَلَى اَ۬لَّذِينَ إِذَا مَآ أَتَوۡكَ لِتَحۡمِلَهُمۡ قُلۡتَ لَآ أَجِدُ مَآ أَحۡمِلُكُمۡ عَلَيۡهِ تَوَلَّواْ وَّأَعۡيُنُهُمۡ تَفِيضُ مِنَ اَ۬لدَّمۡعِ حَزَنًا أَلَّا يَجِدُواْ مَا يُنفِقُونَ ٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1332,11,'201',9,'At-Taubah','التوبَة',13,15,94,'إِنَّمَا اَ۬لسَّبِيلُ عَلَى اَ۬لَّذِينَ يَسۡتَـٔۡذِنُونَكَ وَهُمۡ أَغۡنِيَآءُۚ رَضُواْ بِأَن يَكُونُواْ مَعَ اَ۬لۡخَوَالِفِ وَطَبَعَ اَ۬للَّهُ عَلَىٰ قُلُوبِهِمۡ فَهُمۡ لَا يَعۡلَمُونَ ٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1333,11,'202',9,'At-Taubah','التوبَة',1,4,95,'۞ يَعۡتَذِرُونَ إِلَيۡكُمۡ إِذَا رَجَعۡتُمۡ إِلَيۡهِمۡۚ قُل لَّا تَعۡتَذِرُواْ لَن نُّؤۡمِنَ لَكُمۡۖ قَدۡ نَبَّأَنَا اَ۬للَّهُ مِنۡ أَخۡب۪ارِكُمۡۚ وَسَيَرَى اَ۬للَّهُ عَمَلَكُمۡ وَرَسُولُهُۥ ثُمَّ تُرَدُّونَ إِلَىٰ عَٰلِمِ اِ۬لۡغَيۡبِ وَاَلشَّهَٰدَةِ فَيُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ ٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1334,11,'202',9,'At-Taubah','التوبَة',4,7,96,'سَيَحۡلِفُونَ بِاللَّهِ لَكُمۡ إِذَا اَ۪نقَلَبۡتُمۡ إِلَيۡهِمۡ لِتُعۡرِضُواْ عَنۡهُمۡۖ فَأَعۡرِضُواْ عَنۡهُمۡۖ إِنَّهُمۡ رِجۡسٞۖ وَمَأۡوَىٰهُمۡ جَهَنَّمُ جَزَآءَۢ بِمَا كَانُواْ يَكۡسِبُونَ ٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1335,11,'202',9,'At-Taubah','التوبَة',7,8,97,'يَحۡلِفُونَ لَكُمۡ لِتَرۡضَوۡاْ عَنۡهُمۡۖ فَإِن تَرۡضَوۡاْ عَنۡهُمۡ فَإِنَّ اَ۬للَّهَ لَا يَرۡضَىٰ عَنِ اِ۬لۡقَوۡمِ اِ۬لۡفَٰسِقِينَ ٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1336,11,'202',9,'At-Taubah','التوبَة',9,10,98,'اَ۬لۡأَعۡرَابُ أَشَدُّ كُفۡرٗا وَنِفَاقٗا وَأَجۡدَرُ أَلَّا يَعۡلَمُواْ حُدُودَ مَآ أَنزَلَ اَ۬للَّهُ عَلَىٰ رَسُولِهِۦۗ وَاَللَّهُ عَلِيمٌ حَكِيمٞ ٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1337,11,'202',9,'At-Taubah','التوبَة',10,12,99,'وَمِنَ اَ۬لۡأَعۡرَابِ مَن يَتَّخِذُ مَا يُنفِقُ مَغۡرَمٗا وَيَتَرَبَّصُ بِكُمُ اُ۬لدَّوَآئِرَۚ عَلَيۡهِمۡ دَآئِرَةُ اُ۬لسُّوٓءِۗ وَاَللَّهُ سَمِيعٌ عَلِيمٞ ٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1338,11,'202',9,'At-Taubah','التوبَة',12,15,100,'وَمِنَ اَ۬لۡأَعۡرَابِ مَن يُؤۡمِنُ بِاللَّهِ وَاَلۡيَوۡمِ اِ۬لۡأٓخِرِ وَيَتَّخِذُ مَا يُنفِقُ قُرُبَٰتٍ عِندَ اَ۬للَّهِ وَصَلَوَٰتِ اِ۬لرَّسُولِۚ أَلَآ إِنَّهَا قُرۡبَةٞ لَّهُمۡۚ سَيُدۡخِلُهُمُ اُ۬للَّهُ فِي رَحۡمَتِهِۦٓۚ إِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٞ ١٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1339,11,'203',9,'At-Taubah','التوبَة',1,4,101,'وَاَلسَّٰبِقُونَ اَ۬لۡأَوَّلُونَ مِنَ اَ۬لۡمُهَٰجِرِينَ وَاَلۡأَنص۪ارِ وَاَلَّذِينَ اَ۪تَّبَعُوهُم بِإِحۡسَٰنٖ رَّضِيَ اَ۬للَّهُ عَنۡهُمۡ وَرَضُواْ عَنۡهُ وَأَعَدَّ لَهُمۡ جَنَّٰتٖ تَجۡرِي تَحۡتَهَا اَ۬لۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدٗاۚ ذَٰلِكَ اَ۬لۡفَوۡزُ اُ۬لۡعَظِيمُ ١٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1340,11,'203',9,'At-Taubah','التوبَة',4,7,102,'۞ وَمِمَّنۡ حَوۡلَكُم مِّنَ اَ۬لۡأَعۡرَابِ مُنَٰفِقُونَۖ وَمِنۡ أَهۡلِ اِ۬لۡمَدِينَةِ مَرَدُواْ عَلَى اَ۬لنِّفَاقِ لَا تَعۡلَمُهُمۡۖ نَحۡنُ نَعۡلَمُهُمۡۚ سَنُعَذِّبُهُم مَّرَّتَيۡنِ ثُمَّ يُرَدُّونَ إِلَىٰ عَذَابٍ عَظِيمٖ ١٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1341,11,'203',9,'At-Taubah','التوبَة',7,8,103,'وَءَاخَرُونَ اَ۪عۡتَرَفُواْ بِذُنُوبِهِمۡ خَلَطُواْ عَمَلٗا صَٰلِحٗا وَءَاخَرَ سَيِّئًا عَسَى اَ۬للَّهُ أَن يَتُوبَ عَلَيۡهِمۡۚ إِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٌ ١٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1342,11,'203',9,'At-Taubah','التوبَة',9,10,104,'خُذۡ مِنۡ أَمۡوَٰلِهِمۡ صَدَقَةٗ تُطَهِّرُهُمۡ وَتُزَكِّيهِم بِهَا وَصَلِّ عَلَيۡهِمۡۖ إِنَّ صَلَوَٰتِكَ سَكَنٞ لَّهُمۡۗ وَاَللَّهُ سَمِيعٌ عَلِيمٌ ١٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1343,11,'203',9,'At-Taubah','التوبَة',10,12,105,'أَلَمۡ يَعۡلَمُوٓاْ أَنَّ اَ۬للَّهَ هُوَ يَقۡبَلُ اُ۬لتَّوۡبَةَ عَنۡ عِبَادِهِۦ وَيَأۡخُذُ اُ۬لصَّدَقَٰتِ وَأَنَّ اَ۬للَّهَ هُوَ اَ۬لتَّوَّابُ اُ۬لرَّحِيمُ ١٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1344,11,'203',9,'At-Taubah','التوبَة',12,14,106,'وَقُلِ اِ۪عۡمَلُواْ فَسَيَرَى اَ۬للَّهُ عَمَلَكُمۡ وَرَسُولُهُۥ وَاَلۡمُؤۡمِنُونَۖ وَسَتُرَدُّونَ إِلَىٰ عَٰلِمِ اِ۬لۡغَيۡبِ وَاَلشَّهَٰدَةِ فَيُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ ١٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1345,11,'203',9,'At-Taubah','التوبَة',14,15,107,'وَءَاخَرُونَ مُرۡجَـُٔونَ لِأَمۡرِ اِ۬للَّهِ إِمَّا يُعَذِّبُهُمۡ وَإِمَّا يَتُوبُ عَلَيۡهِمۡۗ وَاَللَّهُ عَلِيمٌ حَكِيمٞ ١٠٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1346,11,'204',9,'At-Taubah','التوبَة',1,4,108,'وَاَلَّذِينَ اَ۪تَّخَذُواْ مَسۡجِدٗا ضِرَارٗا وَكُفۡرٗا وَتَفۡرِيقَۢا بَيۡنَ اَ۬لۡمُؤۡمِنِينَ وَإِرۡصَادٗا لِّمَنۡ حَارَبَ اَ۬للَّهَ وَرَسُولَهُۥ مِن قَبۡلُۚ وَلَيَحۡلِفُنَّ إِنۡ أَرَدۡنَآ إِلَّا اَ۬لۡحُسۡنۭيٰۖ وَاَللَّهُ يَشۡهَدُ إِنَّهُمۡ لَكَٰذِبُونَ ١٠٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1347,11,'204',9,'At-Taubah','التوبَة',4,6,109,'لَا تَقُمۡ فِيهِ أَبَدٗاۚ لَّمَسۡجِدٌ أُسِّسَ عَلَى اَ۬لتَّقۡوۭيٰ مِنۡ أَوَّلِ يَوۡمٍ أَحَقُّ أَن تَقُومَ فِيهِۚ فِيهِ رِجَالٞ يُحِبُّونَ أَن يَتَطَهَّرُواْۚ وَاَللَّهُ يُحِبُّ اُ۬لۡمُطَّهِّرِينَ ١٠٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1348,11,'204',9,'At-Taubah','التوبَة',6,9,110,'أَفَمَنۡ أَسَّسَ بُنۡيَٰنَهُۥ عَلَىٰ تَقۡوۭيٰ مِنَ اَ۬للَّهِ وَرِضۡوَٰنٍ خَيۡرٌ أَم مَّنۡ أَسَّسَ بُنۡيَٰنَهُۥ عَلَىٰ شَفَا جُرُفٍ ه۪ارٖ فَاَنۡهَارَ بِهِۦ فِي ن۪ارِ جَهَنَّمَۗ وَاَللَّهُ لَا يَهۡدِي اِ۬لۡقَوۡمَ اَ۬لظَّٰلِمِينَ ١١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1349,11,'204',9,'At-Taubah','التوبَة',9,10,111,'لَا يَزَالُ بُنۡيَٰنُهُمُ اُ۬لَّذِي بَنَوۡاْ رِيبَةٗ فِي قُلُوبِهِمۡ إِلَّآ أَن تُقَطَّعَ قُلُوبُهُمۡۗ وَاَللَّهُ عَلِيمٌ حَكِيمٌ ١١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1350,11,'204',9,'At-Taubah','التوبَة',11,15,112,'۞ إِنَّ اَ۬للَّهَ اَ۪شۡتَر۪يٰ مِنَ اَ۬لۡمُؤۡمِنِينَ أَنفُسَهُمۡ وَأَمۡوَٰلَهُم بِأَنَّ لَهُمُ اُ۬لۡجَنَّةَۚ يُقَٰتِلُونَ فِي سَبِيلِ اِ۬للَّهِ فَيَقۡتُلُونَ وَيُقۡتَلُونَۖ وَعۡدًا عَلَيۡهِ حَقّٗا فِي اِ۬لتَّوۡر۪ىٰةِ وَاَلۡإِنجِيلِ وَاَلۡقُرۡءَانِۚ وَمَنۡ أَوۡفَىٰ بِعَهۡدِهِۦ مِنَ اَ۬للَّهِۚ فَاَسۡتَبۡشِرُواْ بِبَيۡعِكُمُ اُ۬لَّذِي بَايَعۡتُم بِهِۦۚ وَذَٰلِكَ هُوَ اَ۬لۡفَوۡزُ اُ۬لۡعَظِيمُ ١١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1351,11,'205',9,'At-Taubah','التوبَة',1,4,113,'اُ۬لتَّٰٓئِبُونَ اَ۬لۡعَٰبِدُونَ اَ۬لۡحَٰمِدُونَ اَ۬لسَّٰٓئِحُونَ اَ۬لرَّٰكِعُونَ اَ۬لسَّٰجِدُونَ اَ۬لۡأٓمِرُونَ بِالۡمَعۡرُوفِ وَاَلنَّاهُونَ عَنِ اِ۬لۡمُنكَرِ وَاَلۡحَٰفِظُونَ لِحُدُودِ اِ۬للَّهِۗ وَبَشِّرِ اِ۬لۡمُؤۡمِنِينَ ١١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1352,11,'205',9,'At-Taubah','التوبَة',4,6,114,'مَا كَانَ لِلنَّبِيِّ وَاَلَّذِينَ ءَامَنُوٓاْ أَن يَسۡتَغۡفِرُواْ لِلۡمُشۡرِكِينَ وَلَوۡ كَانُوٓاْ أُوْلِي قُرۡبۭيٰ مِنۢ بَعۡدِ مَا تَبَيَّنَ لَهُمۡ أَنَّهُمۡ أَصۡحَٰبُ اُ۬لۡجَحِيمِ ١١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1353,11,'205',9,'At-Taubah','التوبَة',6,9,115,'وَمَا كَانَ اَ۪سۡتِغۡفَارُ إِبۡرَٰهِيمَ لِأَبِيهِ إِلَّا عَن مَّوۡعِدَةٖ وَعَدَهَآ إِيَّاهُ فَلَمَّا تَبَيَّنَ لَهُۥٓ أَنَّهُۥ عَدُوّٞ لِّلَّهِ تَبَرَّأَ مِنۡهُۚ إِنَّ إِبۡرَٰهِيمَ لَأَوَّٰهٌ حَلِيمٞ ١١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1354,11,'205',9,'At-Taubah','التوبَة',9,11,116,'وَمَا كَانَ اَ۬للَّهُ لِيُضِلَّ قَوۡمَۢا بَعۡدَ إِذۡ هَدَىٰهُمۡ حَتَّىٰ يُبَيِّنَ لَهُم مَّا يَتَّقُونَۚ إِنَّ اَ۬للَّهَ بِكُلِّ شَيۡءٍ عَلِيمٌ ١١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1355,11,'205',9,'At-Taubah','التوبَة',11,12,117,'إِنَّ اَ۬للَّهَ لَهُۥ مُلۡكُ اُ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِۖ يُحۡيِۦ وَيُمِيتُۚ وَمَا لَكُم مِّن دُونِ اِ۬للَّهِ مِن وَلِيّٖ وَلَا نَصِيرٖ ١١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1356,11,'205',9,'At-Taubah','التوبَة',13,15,118,'۞ لَّقَد تَّابَ اَ۬للَّهُ عَلَى اَ۬لنَّبِيِّ وَاَلۡمُهَٰجِرِينَ وَاَلۡأَنص۪ارِ اِ۬لَّذِينَ اَ۪تَّبَعُوهُ فِي سَاعَةِ اِ۬لۡعُسۡرَةِ مِنۢ بَعۡدِ مَا كَادَ تَزِيغُ قُلُوبُ فَرِيقٖ مِّنۡهُمۡ ثُمَّ تَابَ عَلَيۡهِمۡۚ إِنَّهُۥ بِهِمۡ رَؤُفٞ رَّحِيمٞ ١١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1357,11,'206',9,'At-Taubah','التوبَة',1,4,119,'وَعَلَى اَ۬لثَّلَٰثَةِ اِ۬لَّذِينَ خُلِّفُواْ حَتَّىٰٓ إِذَا ضَاقَتۡ عَلَيۡهِمِ اِ۬لۡأَرۡضُ بِمَا رَحُبَتۡ وَضَاقَتۡ عَلَيۡهِمۡ أَنفُسُهُمۡ وَظَنُّوٓاْ أَن لَّا مَلۡجَأَ مِنَ اَ۬للَّهِ إِلَّآ إِلَيۡهِ ثُمَّ تَابَ عَلَيۡهِمۡ لِيَتُوبُوٓاْۚ إِنَّ اَ۬للَّهَ هُوَ اَ۬لتَّوَّابُ اُ۬لرَّحِيمُ ١١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1358,11,'206',9,'At-Taubah','التوبَة',4,5,120,'يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ اُ۪تَّقُواْ اُ۬للَّهَ وَكُونُواْ مَعَ اَ۬لصَّٰدِقِينَ ١٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1359,11,'206',9,'At-Taubah','التوبَة',5,10,121,'مَا كَانَ لِأَهۡلِ اِ۬لۡمَدِينَةِ وَمَنۡ حَوۡلَهُم مِّنَ اَ۬لۡأَعۡرَابِ أَن يَتَخَلَّفُواْ عَن رَّسُولِ اِ۬للَّهِ وَلَا يَرۡغَبُواْ بِأَنفُسِهِمۡ عَن نَّفۡسِهِۦۚ ذَٰلِكَ بِأَنَّهُمۡ لَا يُصِيبُهُمۡ ظَمَأٞ وَلَا نَصَبٞ وَلَا مَخۡمَصَةٞ فِي سَبِيلِ اِ۬للَّهِ وَلَا يَطَـُٔونَ مَوۡطِئٗا يَغِيظُ اُ۬لۡكُفَّارَ وَلَا يَنَالُونَ مِنۡ عَدُوّٖ نَّيۡلًا إِلَّا كُتِبَ لَهُم بِهِۦ عَمَلٞ صَٰلِحٌۚ إِنَّ اَ۬للَّهَ لَا يُضِيعُ أَجۡرَ اَ۬لۡمُحۡسِنِينَ ١٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1360,11,'206',9,'At-Taubah','التوبَة',11,13,122,'وَلَا يُنفِقُونَ نَفَقَةٗ صَغِيرَةٗ وَلَا كَبِيرَةٗ وَلَا يَقۡطَعُونَ وَادِيًا إِلَّا كُتِبَ لَهُمۡ لِيَجۡزِيَهُمُ اُ۬للَّهُ أَحۡسَنَ مَا كَانُواْ يَعۡمَلُونَ ١٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1361,11,'206',9,'At-Taubah','التوبَة',13,15,123,'وَمَا كَانَ اَ۬لۡمُؤۡمِنُونَ لِيَنفِرُواْ كَآفَّةٗۚ فَلَوۡلَا نَفَرَ مِن كُلِّ فِرۡقَةٖ مِّنۡهُمۡ طَآئِفَةٞ لِّيَتَفَقَّهُواْ فِي اِ۬لدِّينِ وَلِيُنذِرُواْ قَوۡمَهُمۡ إِذَا رَجَعُوٓاْ إِلَيۡهِمۡ لَعَلَّهُمۡ يَحۡذَرُونَ ١٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1362,11,'207',9,'At-Taubah','التوبَة',1,3,124,'۞ يَٰٓأَيُّهَا اَ۬لَّذِينَ ءَامَنُواْ قَٰتِلُواْ اُ۬لَّذِينَ يَلُونَكُم مِّنَ اَ۬لۡكُفّ۪ارِ وَلۡيَجِدُواْ فِيكُمۡ غِلۡظَةٗۚ وَاَعۡلَمُوٓاْ أَنَّ اَ۬للَّهَ مَعَ اَ۬لۡمُتَّقِينَ ١٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1363,11,'207',9,'At-Taubah','التوبَة',3,5,125,'وَإِذَا مَآ أُنزِلَت سُّورَةٞ فَمِنۡهُم مَّن يَقُولُ أَيُّكُمۡ زَادَتۡهُ هَٰذِهِۦٓ إِيمَٰنٗاۚ فَأَمَّا اَ۬لَّذِينَ ءَامَنُواْ فَزَادَتۡهُمۡ إِيمَٰنٗا وَهُمۡ يَسۡتَبۡشِرُونَ ١٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1364,11,'207',9,'At-Taubah','التوبَة',6,7,126,'وَأَمَّا اَ۬لَّذِينَ فِي قُلُوبِهِم مَّرَضٞ فَزَادَتۡهُمۡ رِجۡسًا إِلَىٰ رِجۡسِهِمۡ وَمَاتُواْ وَهُمۡ كَٰفِرُونَ ١٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1365,11,'207',9,'At-Taubah','التوبَة',7,9,127,'أَوَلَا يَرَوۡنَ أَنَّهُمۡ يُفۡتَنُونَ فِي كُلِّ عَامٖ مَّرَّةً أَوۡ مَرَّتَيۡنِ ثُمَّ لَا يَتُوبُونَ وَلَا هُمۡ يَذَّكَّرُونَ ١٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1366,11,'207',9,'At-Taubah','التوبَة',9,12,128,'وَإِذَا مَآ أُنزِلَت سُّورَةٞ نَّظَرَ بَعۡضُهُمۡ إِلَىٰ بَعۡضٍ هَلۡ يَر۪ىٰكُم مِّنۡ أَحَدٖ ثُمَّ اَ۪نصَرَفُواْۚ صَرَفَ اَ۬للَّهُ قُلُوبَهُم بِأَنَّهُمۡ قَوۡمٞ لَّا يَفۡقَهُونَ ١٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1367,11,'207',9,'At-Taubah','التوبَة',12,14,129,'لَقَد جَّآءَكُمۡ رَسُولٞ مِّنۡ أَنفُسِكُمۡ عَزِيزٌ عَلَيۡهِ مَا عَنِتُّمۡ حَرِيصٌ عَلَيۡكُم بِالۡمُؤۡمِنِينَ رَؤُفٞ رَّحِيمٞ ١٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1368,11,'207',9,'At-Taubah','التوبَة',14,15,130,'فَإِن تَوَلَّوۡاْ فَقُلۡ حَسۡبِيَ اَ۬للَّهُ لَآ إِلَٰهَ إِلَّا هُوَۖ عَلَيۡهِ تَوَكَّلۡتُۖ وَهۡوَ رَبُّ اُ۬لۡعَرۡشِ اِ۬لۡعَظِيمِ ١٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1369,11,'208',10,'Yūnus','يُونس',3,3,1,'الٓر۪ۚ تِلۡكَ ءَايَٰتُ اُ۬لۡكِتَٰبِ اِ۬لۡحَكِيمِ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1370,11,'208',10,'Yūnus','يُونس',3,6,2,'أَكَانَ لِلنّ۪اسِ عَجَبًا أَنۡ أَوۡحَيۡنَآ إِلَىٰ رَجُلٖ مِّنۡهُمۡ أَنۡ أَنذِرِ اِ۬لنَّاسَ وَبَشِّرِ اِ۬لَّذِينَ ءَامَنُوٓاْ أَنَّ لَهُمۡ قَدَمَ صِدۡقٍ عِندَ رَبِّهِمۡۗ قَالَ اَ۬لۡكَٰفِرُونَ إِنَّ هَٰذَا لَسِحۡرٞ مُّبِينٌ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1371,11,'208',10,'Yūnus','يُونس',6,8,3,'إِنَّ رَبَّكُمُ اُ۬للَّهُ اُ۬لَّذِي خَلَقَ اَ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضَ فِي سِتَّةِ أَيَّامٖ ثُمَّ اَ۪سۡتَوَىٰ عَلَى اَ۬لۡعَرۡشِۖ يُدَبِّرُ اُ۬لۡأَمۡرَۖ مَا مِن شَفِيعٍ إِلَّا مِنۢ بَعۡدِ إِذۡنِهِۦۚ ذَٰلِكُمُ اُ۬للَّهُ رَبُّكُمۡ فَاَعۡبُدُوهُۚ أَفَلَا تَذَّكَّرُونَ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1372,11,'208',10,'Yūnus','يُونس',9,11,4,'۞ إِلَيۡهِ مَرۡجِعُكُمۡ جَمِيعٗاۖ وَعۡدَ اَ۬للَّهِ حَقًّاۚ إِنَّهُۥ يَبۡدَؤُاْ اُ۬لۡخَلۡقَ ثُمَّ يُعِيدُهُۥ لِيَجۡزِيَ اَ۬لَّذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ بِالۡقِسۡطِۚ وَاَلَّذِينَ كَفَرُواْ لَهُمۡ شَرَابٞ مِّنۡ حَمِيمٖ وَعَذَابٌ أَلِيمُۢ بِمَا كَانُواْ يَكۡفُرُونَ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1373,11,'208',10,'Yūnus','يُونس',12,14,5,'هُوَ اَ۬لَّذِي جَعَلَ اَ۬لشَّمۡسَ ضِيَآءٗ وَاَلۡقَمَرَ نُورٗا وَقَدَّرَهُۥ مَنَازِلَ لِتَعۡلَمُواْ عَدَدَ اَ۬لسِّنِينَ وَاَلۡحِسَابَۚ مَا خَلَقَ اَ۬للَّهُ ذَٰلِكَ إِلَّا بِالۡحَقِّۚ يُفَصِّلُ اُ۬لۡأٓيَٰتِ لِقَوۡمٖ يَعۡلَمُونَ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1374,11,'208',10,'Yūnus','يُونس',14,15,6,'إِنَّ فِي اِ۪خۡتِلَٰفِ اِ۬لَّيۡلِ وَاَلنَّه۪ارِ وَمَا خَلَقَ اَ۬للَّهُ فِي اِ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِ لَأٓيَٰتٖ لِّقَوۡمٖ يَتَّقُونَ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1375,11,'209',10,'Yūnus','يُونس',1,2,7,'إِنَّ اَ۬لَّذِينَ لَا يَرۡجُونَ لِقَآءَنَا وَرَضُواْ بِالۡحَيَوٰةِ اِ۬لدُّنۡيۭا وَاَطۡمَأَنُّواْ بِهَا وَاَلَّذِينَ هُمۡ عَنۡ ءَايَٰتِنَا غَٰفِلُونَ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1376,11,'209',10,'Yūnus','يُونس',2,3,8,'أُوْلَٰٓئِكَ مَأۡوَىٰهُمُ اُ۬لنَّارُ بِمَا كَانُواْ يَكۡسِبُونَ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1377,11,'209',10,'Yūnus','يُونس',3,5,9,'إِنَّ اَ۬لَّذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ يَهۡدِيهِمۡ رَبُّهُم بِإِيمَٰنِهِمۡۖ تَجۡرِي مِن تَحۡتِهِمِ اِ۬لۡأَنۡهَٰرُ فِي جَنَّٰتِ اِ۬لنَّعِيمِ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1378,11,'209',10,'Yūnus','يُونس',5,7,10,'دَعۡوۭىٰهُمۡ فِيهَا سُبۡحَٰنَكَ اَ۬للَّهُمَّ وَتَحِيَّتُهُمۡ فِيهَا سَلَٰمٞۚ وَءَاخِرُ دَعۡوۭىٰهُمۡ أَنِ اِ۬لۡحَمۡدُ لِلَّهِ رَبِّ اِ۬لۡعَٰلَمِينَ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1379,11,'209',10,'Yūnus','يُونس',7,9,11,'۞ وَلَوۡ يُعَجِّلُ اُ۬للَّهُ لِلنّ۪اسِ اِ۬لشَّرَّ اَ۪سۡتِعۡجَالَهُم بِالۡخَيۡرِ لَقُضِيَ إِلَيۡهِمۡ أَجَلُهُمۡۚ فَنَذَرُ اُ۬لَّذِينَ لَا يَرۡجُونَ لِقَآءَنَا فِي طُغۡيَٰنِهِمۡ يَعۡمَهُونَ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1380,11,'209',10,'Yūnus','يُونس',9,12,12,'وَإِذَا مَسَّ اَ۬لۡإِنسَٰنَ اَ۬لضُّرُّ دَعَانَا لِجَنۢبِهِۦٓ أَوۡ قَاعِدًا أَوۡ قَآئِمٗا فَلَمَّا كَشَفۡنَا عَنۡهُ ضُرَّهُۥ مَرَّ كَأَن لَّمۡ يَدۡعُنَآ إِلَىٰ ضُرّٖ مَّسَّهُۥۚ كَذَٰلِكَ زُيِّنَ لِلۡمُسۡرِفِينَ مَا كَانُواْ يَعۡمَلُونَ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1381,11,'209',10,'Yūnus','يُونس',12,14,13,'وَلَقَدۡ أَهۡلَكۡنَا اَ۬لۡقُرُونَ مِن قَبۡلِكُمۡ لَمَّا ظَلَمُواْ وَجَآءَتۡهُمۡ رُسۡلُهُم بِالۡبَيِّنَٰتِ وَمَا كَانُواْ لِيُؤۡمِنُواْۚ كَذَٰلِكَ نَجۡزِي اِ۬لۡقَوۡمَ اَ۬لۡمُجۡرِمِينَ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1382,11,'209',10,'Yūnus','يُونس',14,15,14,'ثُمَّ جَعَلۡنَٰكُمۡ خَلَٰٓئِفَ فِي اِ۬لۡأَرۡضِ مِنۢ بَعۡدِهِمۡ لِنَنظُرَ كَيۡفَ تَعۡمَلُونَ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1383,11,'210',10,'Yūnus','يُونس',1,4,15,'وَإِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَاتُنَا بَيِّنَٰتٖ قَالَ اَ۬لَّذِينَ لَا يَرۡجُونَ لِقَآءَنَا اَ۪ئۡتِ بِقُرۡءَانٍ غَيۡرِ هَٰذَآ أَوۡ بَدِّلۡهُۚ قُلۡ مَا يَكُونُ لِيَ أَنۡ أُبَدِّلَهُۥ مِن تِلۡقَآيِٕ نَفۡسِيَۖ إِنۡ أَتَّبِعُ إِلَّا مَا يُوحَىٰٓ إِلَيَّۖ إِنِّيَ أَخَافُ إِنۡ عَصَيۡتُ رَبِّي عَذَابَ يَوۡمٍ عَظِيمٖ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1384,11,'210',10,'Yūnus','يُونس',4,6,16,'قُل لَّوۡ شَآءَ اَ۬للَّهُ مَا تَلَوۡتُهُۥ عَلَيۡكُمۡ وَلَآ أَدۡر۪ىٰكُم بِهِۦۖ فَقَدۡ لَبِثتُّ فِيكُمۡ عُمُرٗا مِّن قَبۡلِهِۦٓۚ أَفَلَا تَعۡقِلُونَ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1385,11,'210',10,'Yūnus','يُونس',7,8,17,'فَمَنۡ أَظۡلَمُ مِمَّنِ اِ۪فۡتَر۪يٰ عَلَى اَ۬للَّهِ كَذِبًا أَوۡ كَذَّبَ بِـَٔايَٰتِهِۦٓۚ إِنَّهُۥ لَا يُفۡلِحُ اُ۬لۡمُجۡرِمُونَ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1386,11,'210',10,'Yūnus','يُونس',8,11,18,'وَيَعۡبُدُونَ مِن دُونِ اِ۬للَّهِ مَا لَا يَضُرُّهُمۡ وَلَا يَنفَعُهُمۡ وَيَقُولُونَ هَٰٓؤُلَآءِ شُفَعَٰٓؤُنَا عِندَ اَ۬للَّهِۚ قُلۡ أَتُنَبِّـُٔونَ اَ۬للَّهَ بِمَا لَا يَعۡلَمُ فِي اِ۬لسَّمَٰوَٰتِ وَلَا فِي اِ۬لۡأَرۡضِۚ سُبۡحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يُشۡرِكُونَ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1387,11,'210',10,'Yūnus','يُونس',11,13,19,'وَمَا كَانَ اَ۬لنَّاسُ إِلَّآ أُمَّةٗ وَٰحِدَةٗ فَاَخۡتَلَفُواْۚ وَلَوۡلَا كَلِمَةٞ سَبَقَتۡ مِن رَّبِّكَ لَقُضِيَ بَيۡنَهُمۡ فِيمَا فِيهِ يَخۡتَلِفُونَ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1388,11,'210',10,'Yūnus','يُونس',14,15,20,'وَيَقُولُونَ لَوۡلَآ أُنزِلَ عَلَيۡهِ ءَايَةٞ مِّن رَّبِّهِۦۖ فَقُلۡ إِنَّمَا اَ۬لۡغَيۡبُ لِلَّهِ فَاَنتَظِرُوٓاْ إِنِّي مَعَكُم مِّنَ اَ۬لۡمُنتَظِرِينَ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1389,11,'211',10,'Yūnus','يُونس',1,2,21,'وَإِذَآ أَذَقۡنَا اَ۬لنَّاسَ رَحۡمَةٗ مِّنۢ بَعۡدِ ضَرَّآءَ مَسَّتۡهُمۡ إِذَا لَهُم مَّكۡرٞ فِيٓ ءَايَاتِنَاۚ قُلِ اِ۬للَّهُ أَسۡرَعُ مَكۡرًاۚ إِنَّ رُسۡلَنَا يَكۡتُبُونَ مَا تَمۡكُرُونَ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1390,11,'211',10,'Yūnus','يُونس',3,7,22,'۞ هُوَ اَ۬لَّذِي يُسَيِّرُكُمۡ فِي اِ۬لۡبَرِّ وَاَلۡبَحۡرِۖ حَتَّىٰٓ إِذَا كُنتُمۡ فِي اِ۬لۡفُلۡكِ وَجَرَيۡنَ بِهِم بِرِيحٖ طَيِّبَةٖ وَفَرِحُواْ بِهَا جَآءَتۡهَا رِيحٌ عَاصِفٞ وَجَآءَهُمُ اُ۬لۡمَوۡجُ مِن كُلِّ مَكَانٖ وَظَنُّوٓاْ أَنَّهُمۡ أُحِيطَ بِهِمۡ دَعَوُاْ اُ۬للَّهَ مُخۡلِصِينَ لَهُ اُ۬لدِّينَ لَئِنۡ أَنجَيۡتَنَا مِنۡ هَٰذِهِۦ لَنَكُونَنَّ مِنَ اَ۬لشَّٰكِرِينَ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1391,11,'211',10,'Yūnus','يُونس',7,9,23,'فَلَمَّآ أَنجَىٰهُمۡ إِذَا هُمۡ يَبۡغُونَ فِي اِ۬لۡأَرۡضِ بِغَيۡرِ اِ۬لۡحَقِّۗ يَٰٓأَيُّهَا اَ۬لنَّاسُ إِنَّمَا بَغۡيُكُمۡ عَلَىٰٓ أَنفُسِكُمۖ مَّتَٰعُ اُ۬لۡحَيَوٰةِ اِ۬لدُّنۡيۭاۖ ثُمَّ إِلَيۡنَا مَرۡجِعُكُمۡ فَنُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1392,11,'211',10,'Yūnus','يُونس',10,14,24,'إِنَّمَا مَثَلُ اُ۬لۡحَيَوٰةِ اِ۬لدُّنۡيۭا كَمَآءٍ أَنزَلۡنَٰهُ مِنَ اَ۬لسَّمَآءِ فَاَخۡتَلَطَ بِهِۦ نَبَاتُ اُ۬لۡأَرۡضِ مِمَّا يَأۡكُلُ اُ۬لنَّاسُ وَاَلۡأَنۡعَٰمُ حَتَّىٰٓ إِذَآ أَخَذَتِ اِ۬لۡأَرۡضُ زُخۡرُفَهَا وَاَزَّيَّنَتۡ وَظَنَّ أَهۡلُهَآ أَنَّهُمۡ قَٰدِرُونَ عَلَيۡهَآ أَتَىٰهَآ أَمۡرُنَا لَيۡلًا أَوۡ نَهَارٗا فَجَعَلۡنَٰهَا حَصِيدٗا كَأَن لَّمۡ تَغۡنَ بِالۡأَمۡسِۚ كَذَٰلِكَ نُفَصِّلُ اُ۬لۡأٓيَٰتِ لِقَوۡمٖ يَتَفَكَّرُونَ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1393,11,'211',10,'Yūnus','يُونس',14,15,25,'وَاَللَّهُ يَدۡعُوٓاْ إِلَىٰ د۪ارِ اِ۬لسَّلَٰمِ وَيَهۡدِي مَن يَشَآءُ اِ۪لَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1394,11,'212',10,'Yūnus','يُونس',1,2,26,'لِّلَّذِينَ أَحۡسَنُواْ اُ۬لۡحُسۡنۭيٰ وَزِيَادَةٞۖ وَلَا يَرۡهَقُ وُجُوهَهُمۡ قَتَرٞ وَلَا ذِلَّةٌۚ أُوْلَٰٓئِكَ أَصۡحَٰبُ اُ۬لۡجَنَّةِۖ هُمۡ فِيهَا خَٰلِدُونَ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1395,11,'212',10,'Yūnus','يُونس',2,5,27,'وَاَلَّذِينَ كَسَبُواْ اُ۬لسَّيِّـَٔاتِ جَزَآءُ سَيِّئَةِۢ بِمِثۡلِهَا وَتَرۡهَقُهُمۡ ذِلَّةٞۖ مَّا لَهُم مِّنَ اَ۬للَّهِ مِنۡ عَاصِمٖۖ كَأَنَّمَآ أُغۡشِيَتۡ وُجُوهُهُمۡ قِطَعٗا مِّنَ اَ۬لَّيۡلِ مُظۡلِمًاۚ أُوْلَٰٓئِكَ أَصۡحَٰبُ اُ۬لنّ۪ارِۖ هُمۡ فِيهَا خَٰلِدُونَ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1396,11,'212',10,'Yūnus','يُونس',5,7,28,'وَيَوۡمَ نَحۡشُرُهُمۡ جَمِيعٗا ثُمَّ نَقُولُ لِلَّذِينَ أَشۡرَكُواْ مَكَانَكُمۡ أَنتُمۡ وَشُرَكَآؤُكُمۡۚ فَزَيَّلۡنَا بَيۡنَهُمۡۖ وَقَالَ شُرَكَآؤُهُم مَّا كُنتُمۡ إِيَّانَا تَعۡبُدُونَ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1397,11,'212',10,'Yūnus','يُونس',7,8,29,'فَكَفَىٰ بِاللَّهِ شَهِيدَۢا بَيۡنَنَا وَبَيۡنَكُمۡ إِن كُنَّا عَنۡ عِبَادَتِكُمۡ لَغَٰفِلِينَ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1398,11,'212',10,'Yūnus','يُونس',9,10,30,'هُنَالِكَ تَبۡلُواْ كُلُّ نَفۡسٖ مَّآ أَسۡلَفَتۡۚ وَرُدُّوٓاْ إِلَى اَ۬للَّهِ مَوۡلَىٰهُمُ اُ۬لۡحَقِّۖ وَضَلَّ عَنۡهُم مَّا كَانُواْ يَفۡتَرُونَ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1399,11,'212',10,'Yūnus','يُونس',10,13,31,'۞ قُلۡ مَن يَرۡزُقُكُم مِّنَ اَ۬لسَّمَآءِ وَاَلۡأَرۡضِ أَمَّن يَمۡلِكُ اُ۬لسَّمۡعَ وَاَلۡأَبۡصَٰرَ وَمَن يُخۡرِجُ اُ۬لۡحَيَّ مِنَ اَ۬لۡمَيۡتِ وَيُخۡرِجُ اُ۬لۡمَيۡتَ مِنَ اَ۬لۡحَيِّ وَمَن يُدَبِّرُ اُ۬لۡأَمۡرَۚ فَسَيَقُولُونَ اَ۬للَّهُۚ فَقُلۡ أَفَلَا تَتَّقُونَ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1400,11,'212',10,'Yūnus','يُونس',13,14,32,'فَذَٰلِكُمُ اُ۬للَّهُ رَبُّكُمُ اُ۬لۡحَقُّۖ فَمَاذَا بَعۡدَ اَ۬لۡحَقِّ إِلَّا اَ۬لضَّلَٰلُۖ فَأَنّۭيٰ تُصۡرَفُونَ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1401,11,'212',10,'Yūnus','يُونس',14,15,33,'كَذَٰلِكَ حَقَّتۡ كَلِمَتُ رَبِّكَ عَلَى اَ۬لَّذِينَ فَسَقُوٓاْ أَنَّهُمۡ لَا يُؤۡمِنُونَ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1402,11,'213',10,'Yūnus','يُونس',1,2,34,'قُلۡ هَلۡ مِن شُرَكَآئِكُم مَّن يَبۡدَؤُاْ اُ۬لۡخَلۡقَ ثُمَّ يُعِيدُهُۥۚ قُلِ اِ۬للَّهُ يَبۡدَؤُاْ اُ۬لۡخَلۡقَ ثُمَّ يُعِيدُهُۥۖ فَأَنّۭيٰ تُؤۡفَكُونَ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1403,11,'213',10,'Yūnus','يُونس',2,4,35,'قُلۡ هَلۡ مِن شُرَكَآئِكُم مَّن يَهۡدِيٓ إِلَى اَ۬لۡحَقِّۚ قُلِ اِ۬للَّهُ يَهۡدِي لِلۡحَقِّۗ أَفَمَن يَهۡدِيٓ إِلَى اَ۬لۡحَقِّ أَحَقُّ أَن يُتَّبَعَ أَمَّن لَّا يَه۬دِّيٓ إِلَّآ أَن يُهۡدَىٰۖ فَمَا لَكُمۡ كَيۡفَ تَحۡكُمُونَ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1404,11,'213',10,'Yūnus','يُونس',5,6,36,'وَمَا يَتَّبِعُ أَكۡثَرُهُمۡ إِلَّا ظَنًّاۚ إِنَّ اَ۬لظَّنَّ لَا يُغۡنِي مِنَ اَ۬لۡحَقِّ شَيۡـًٔاۚ إِنَّ اَ۬للَّهَ عَلِيمُۢ بِمَا يَفۡعَلُونَ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1405,11,'213',10,'Yūnus','يُونس',6,8,37,'وَمَا كَانَ هَٰذَا اَ۬لۡقُرۡءَانُ أَن يُفۡتَر۪يٰ مِن دُونِ اِ۬للَّهِ وَلَٰكِن تَصۡدِيقَ اَ۬لَّذِي بَيۡنَ يَدَيۡهِ وَتَفۡصِيلَ اَ۬لۡكِتَٰبِ لَا رَيۡبَ فِيهِ مِن رَّبِّ اِ۬لۡعَٰلَمِينَ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1406,11,'213',10,'Yūnus','يُونس',8,9,38,'أَمۡ يَقُولُونَ اَ۪فۡتَر۪ىٰهُۖ قُلۡ فَأۡتُواْ بِسُورَةٖ مِّثۡلِهِۦ وَاَدۡعُواْ مَنِ اِ۪سۡتَطَعۡتُم مِّن دُونِ اِ۬للَّهِ إِن كُنتُمۡ صَٰدِقِينَ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1407,11,'213',10,'Yūnus','يُونس',10,11,39,'بَلۡ كَذَّبُواْ بِمَا لَمۡ يُحِيطُواْ بِعِلۡمِهِۦ وَلَمَّا يَأۡتِهِمۡ تَأۡوِيلُهُۥۚ كَذَٰلِكَ كَذَّبَ اَ۬لَّذِينَ مِن قَبۡلِهِمۡۖ فَاَنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ اُ۬لظَّٰلِمِينَ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1408,11,'213',10,'Yūnus','يُونس',12,13,40,'۞ وَمِنۡهُم مَّن يُؤۡمِنُ بِهِۦ وَمِنۡهُم مَّن لَّا يُؤۡمِنُ بِهِۦۚ وَرَبُّكَ أَعۡلَمُ بِالۡمُفۡسِدِينَ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1409,11,'213',10,'Yūnus','يُونس',13,14,41,'وَإِن كَذَّبُوكَ فَقُل لِّي عَمَلِي وَلَكُمۡ عَمَلُكُمۡۖ أَنتُم بَرِيٓـُٔونَ مِمَّآ أَعۡمَلُ وَأَنَا۠ بَرِيٓءٞ مِّمَّا تَعۡمَلُونَ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1410,11,'213',10,'Yūnus','يُونس',14,15,42,'وَمِنۡهُم مَّن يَسۡتَمِعُونَ إِلَيۡكَۚ أَفَأَنتَ تُسۡمِعُ اُ۬لصُّمَّ وَلَوۡ كَانُواْ لَا يَعۡقِلُونَ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1411,11,'214',10,'Yūnus','يُونس',1,1,43,'وَمِنۡهُم مَّن يَنظُرُ إِلَيۡكَۚ أَفَأَنتَ تَهۡدِي اِ۬لۡعُمۡيَ وَلَوۡ كَانُواْ لَا يُبۡصِرُونَ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1412,11,'214',10,'Yūnus','يُونس',2,3,44,'إِنَّ اَ۬للَّهَ لَا يَظۡلِمُ اُ۬لنَّاسَ شَيۡـٔٗا وَلَٰكِنَّ اَ۬لنَّاسَ أَنفُسَهُمۡ يَظۡلِمُونَ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1413,11,'214',10,'Yūnus','يُونس',3,5,45,'وَيَوۡمَ نَحۡشُرُهُمۡ كَأَن لَّمۡ يَلۡبَثُوٓاْ إِلَّا سَاعَةٗ مِّنَ اَ۬لنَّه۪ارِ يَتَعَارَفُونَ بَيۡنَهُمۡۚ قَدۡ خَسِرَ اَ۬لَّذِينَ كَذَّبُواْ بِلِقَآءِ اِ۬للَّهِ وَمَا كَانُواْ مُهۡتَدِينَ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1414,11,'214',10,'Yūnus','يُونس',5,6,46,'وَإِمَّا نُرِيَنَّكَ بَعۡضَ اَ۬لَّذِي نَعِدُهُمۡ أَوۡ نَتَوَفَّيَنَّكَ فَإِلَيۡنَا مَرۡجِعُهُمۡ ثُمَّ اَ۬للَّهُ شَهِيدٌ عَلَىٰ مَا يَفۡعَلُونَ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1415,11,'214',10,'Yūnus','يُونس',6,8,47,'وَلِكُلِّ أُمَّةٖ رَّسُولٞۖ فَإِذَا جَآءَ رَسُولُهُمۡ قُضِيَ بَيۡنَهُم بِالۡقِسۡطِ وَهُمۡ لَا يُظۡلَمُونَ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1416,11,'214',10,'Yūnus','يُونس',8,8,48,'وَيَقُولُونَ مَتَىٰ هَٰذَا اَ۬لۡوَعۡدُ إِن كُنتُمۡ صَٰدِقِينَ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1417,11,'214',10,'Yūnus','يُونس',9,10,49,'قُل لَّآ أَمۡلِكُ لِنَفۡسِي ضَرّٗا وَلَا نَفۡعًا إِلَّا مَا شَآءَ اَ۬للَّهُۗ لِكُلِّ أُمَّةٍ أَجَلٌۖ إِذَا جَآ أَجَلُهُمۡ فَلَا يَسۡتَـٔۡخِرُونَ سَاعَةٗ وَلَا يَسۡتَقۡدِمُونَ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1418,11,'214',10,'Yūnus','يُونس',11,12,50,'قُلۡ أَرَءَيۡتُمۡ إِنۡ أَتَىٰكُمۡ عَذَابُهُۥ بَيَٰتًا أَوۡ نَهَارٗا مَّاذَا يَسۡتَعۡجِلُ مِنۡهُ اُ۬لۡمُجۡرِمُونَ ٥٠');
