INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1420,11,'214',10,'Yūnus','يُونس',13,14,52,'ثُمَّ قِيلَ لِلَّذِينَ ظَلَمُواْ ذُوقُواْ عَذَابَ اَ۬لۡخُلۡدِ هَلۡ تُجۡزَوۡنَ إِلَّا بِمَا كُنتُمۡ تَكۡسِبُونَ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1421,11,'214',10,'Yūnus','يُونس',14,15,53,'۞ وَيَسۡتَنۢبِـُٔونَكَ أَحَقٌّ هُوَۖ قُلۡ إِي وَرَبِّيَ إِنَّهُۥ لَحَقّٞۖ وَمَآ أَنتُم بِمُعۡجِزِينَ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1422,11,'215',10,'Yūnus','يُونس',1,3,54,'وَلَوۡ أَنَّ لِكُلِّ نَفۡسٖ ظَلَمَتۡ مَا فِي اِ۬لۡأَرۡضِ لَاَفۡتَدَتۡ بِهِۦۗ وَأَسَرُّواْ اُ۬لنَّدَامَةَ لَمَّا رَأَوُاْ اُ۬لۡعَذَابَۖ وَقُضِيَ بَيۡنَهُم بِالۡقِسۡطِۚ وَهُمۡ لَا يُظۡلَمُونَ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1423,11,'215',10,'Yūnus','يُونس',3,4,55,'أَلَآ إِنَّ لِلَّهِ مَا فِي اِ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِۗ أَلَآ إِنَّ وَعۡدَ اَ۬للَّهِ حَقّٞ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1424,11,'215',10,'Yūnus','يُونس',4,5,56,'هُوَ يُحۡيِۦ وَيُمِيتُ وَإِلَيۡهِ تُرۡجَعُونَ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1425,11,'215',10,'Yūnus','يُونس',5,6,57,'يَٰٓأَيُّهَا اَ۬لنَّاسُ قَد جَّآءَتۡكُم مَّوۡعِظَةٞ مِّن رَّبِّكُمۡ وَشِفَآءٞ لِّمَا فِي اِ۬لصُّدُورِ وَهُدٗى وَرَحۡمَةٞ لِّلۡمُؤۡمِنِينَ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1426,11,'215',10,'Yūnus','يُونس',7,8,58,'قُلۡ بِفَضۡلِ اِ۬للَّهِ وَبِرَحۡمَتِهِۦ فَبِذَٰلِكَ فَلۡيَفۡرَحُواْ هُوَ خَيۡرٞ مِّمَّا يَجۡمَعُونَ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1427,11,'215',10,'Yūnus','يُونس',8,10,59,'قُلۡ أَرَءَيۡتُم مَّآ أَنزَلَ اَ۬للَّهُ لَكُم مِّن رِّزۡقٖ فَجَعَلۡتُم مِّنۡهُ حَرَامٗا وَحَلَٰلٗا قُلۡ ءَآللَّهُ أَذِنَ لَكُمۡۖ أَمۡ عَلَى اَ۬للَّهِ تَفۡتَرُونَ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1428,11,'215',10,'Yūnus','يُونس',10,12,60,'وَمَا ظَنُّ اُ۬لَّذِينَ يَفۡتَرُونَ عَلَى اَ۬للَّهِ اِ۬لۡكَذِبَ يَوۡمَ اَ۬لۡقِيَٰمَةِۗ إِنَّ اَ۬للَّهَ لَذُو فَضۡلٍ عَلَى اَ۬لنّ۪اسِ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَشۡكُرُونَ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1429,11,'215',10,'Yūnus','يُونس',12,15,61,'وَمَا تَكُونُ فِي شَأۡنٖ وَمَا تَتۡلُواْ مِنۡهُ مِن قُرۡءَانٖ وَلَا تَعۡمَلُونَ مِنۡ عَمَلٍ إِلَّا كُنَّا عَلَيۡكُمۡ شُهُودًا إِذ تُّفِيضُونَ فِيهِۚ وَمَا يَعۡزُبُ عَن رَّبِّكَ مِن مِّثۡقَالِ ذَرَّةٖ فِي اِ۬لۡأَرۡضِ وَلَا فِي اِ۬لسَّمَآءِ وَلَآ أَصۡغَرَ مِن ذَٰلِكَ وَلَآ أَكۡبَرَ إِلَّا فِي كِتَٰبٖ مُّبِينٍ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1430,11,'216',10,'Yūnus','يُونس',1,1,62,'۞ أَلَآ إِنَّ أَوۡلِيَآءَ اَ۬للَّهِ لَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1431,11,'216',10,'Yūnus','يُونس',2,2,63,'اَ۬لَّذِينَ ءَامَنُواْ وَكَانُواْ يَتَّقُونَ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1432,11,'216',10,'Yūnus','يُونس',2,4,64,'لَهُمُ اُ۬لۡبُشۡر۪يٰ فِي اِ۬لۡحَيَوٰةِ اِ۬لدُّنۡيۭا وَفِي اِ۬لۡأٓخِرَةِۚ لَا تَبۡدِيلَ لِكَلِمَٰتِ اِ۬للَّهِۚ ذَٰلِكَ هُوَ اَ۬لۡفَوۡزُ اُ۬لۡعَظِيمُ ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1433,11,'216',10,'Yūnus','يُونس',4,5,65,'وَلَا يَحۡزُنكَ قَوۡلُهُمۡۘ إِنَّ اَ۬لۡعِزَّةَ لِلَّهِ جَمِيعًاۚ هُوَ اَ۬لسَّمِيعُ اُ۬لۡعَلِيمُ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1434,11,'216',10,'Yūnus','يُونس',5,8,66,'أَلَآ إِنَّ لِلَّهِ مَن فِي اِ۬لسَّمَٰوَٰتِ وَمَن فِي اِ۬لۡأَرۡضِۗ وَمَا يَتَّبِعُ اُ۬لَّذِينَ يَدۡعُونَ مِن دُونِ اِ۬للَّهِ شُرَكَآءَۚ ا۪ن يَتَّبِعُونَ إِلَّا اَ۬لظَّنَّ وَإِنۡ هُمۡ إِلَّا يَخۡرُصُونَ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1435,11,'216',10,'Yūnus','يُونس',8,10,67,'هُوَ اَ۬لَّذِي جَعَلَ لَكُمُ اُ۬لَّيۡلَ لِتَسۡكُنُواْ فِيهِ وَاَلنَّهَارَ مُبۡصِرًاۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَسۡمَعُونَ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1436,11,'216',10,'Yūnus','يُونس',10,13,68,'قَالُواْ اُ۪تَّخَذَ اَ۬للَّهُ وَلَدٗاۗ سُبۡحَٰنَهُۥۖ هُوَ اَ۬لۡغَنِيُّۖ لَهُۥ مَا فِي اِ۬لسَّمَٰوَٰتِ وَمَا فِي اِ۬لۡأَرۡضِۚ إِنۡ عِندَكُم مِّن سُلۡطَٰنِۢ بِهَٰذَآۚ أَتَقُولُونَ عَلَى اَ۬للَّهِ مَا لَا تَعۡلَمُونَ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1437,11,'216',10,'Yūnus','يُونس',13,14,69,'قُلۡ إِنَّ اَ۬لَّذِينَ يَفۡتَرُونَ عَلَى اَ۬للَّهِ اِ۬لۡكَذِبَ لَا يُفۡلِحُونَ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1438,11,'216',10,'Yūnus','يُونس',14,15,70,'مَتَٰعٞ فِي اِ۬لدُّنۡيۭا ثُمَّ إِلَيۡنَا مَرۡجِعُهُمۡ ثُمَّ نُذِيقُهُمُ اُ۬لۡعَذَابَ اَ۬لشَّدِيدَ بِمَا كَانُواْ يَكۡفُرُونَ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1439,11,'217',10,'Yūnus','يُونس',1,4,71,'۞ وَاَتۡلُ عَلَيۡهِمۡ نَبَأَ نُوحٍ إِذۡ قَالَ لِقَوۡمِهِۦ يَٰقَوۡمِ إِن كَانَ كَبُرَ عَلَيۡكُم مَّقَامِي وَتَذۡكِيرِي بِـَٔايَٰتِ اِ۬للَّهِ فَعَلَى اَ۬للَّهِ تَوَكَّلۡتُ فَأَجۡمِعُوٓاْ أَمۡرَكُمۡ وَشُرَكَآءَكُمۡ ثُمَّ لَا يَكُنۡ أَمۡرُكُمۡ عَلَيۡكُمۡ غُمَّةٗ ثُمَّ اَ۪قۡضُوٓاْ إِلَيَّ وَلَا تُنظِرُونِ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1440,11,'217',10,'Yūnus','يُونس',4,5,72,'فَإِن تَوَلَّيۡتُمۡ فَمَا سَأَلۡتُكُم مِّنۡ أَجۡرٍۖ إِنۡ أَجۡرِيَ إِلَّا عَلَى اَ۬للَّهِۖ وَأُمِرۡتُ أَنۡ أَكُونَ مِنَ اَ۬لۡمُسۡلِمِينَ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1441,11,'217',10,'Yūnus','يُونس',6,7,73,'فَكَذَّبُوهُ فَنَجَّيۡنَٰهُ وَمَن مَّعَهُۥ فِي اِ۬لۡفُلۡكِ وَجَعَلۡنَٰهُمۡ خَلَٰٓئِفَ وَأَغۡرَقۡنَا اَ۬لَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَاۖ فَاَنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ اُ۬لۡمُنذَرِينَ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1442,11,'217',10,'Yūnus','يُونس',8,10,74,'ثُمَّ بَعَثۡنَا مِنۢ بَعۡدِهِۦ رُسُلًا إِلَىٰ قَوۡمِهِمۡ فَجَآءُوهُم بِالۡبَيِّنَٰتِ فَمَا كَانُواْ لِيُؤۡمِنُواْ بِمَا كَذَّبُواْ بِهِۦ مِن قَبۡلُۚ كَذَٰلِكَ نَطۡبَعُ عَلَىٰ قُلُوبِ اِ۬لۡمُعۡتَدِينَ ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1443,11,'217',10,'Yūnus','يُونس',10,11,75,'ثُمَّ بَعَثۡنَا مِنۢ بَعۡدِهِم مُّوسۭيٰ وَهَٰرُونَ إِلَىٰ فِرۡعَوۡنَ وَمَلَإِيْهِۦ بِـَٔايَٰتِنَا فَاَسۡتَكۡبَرُواْ وَكَانُواْ قَوۡمٗا مُّجۡرِمِينَ ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1444,11,'217',10,'Yūnus','يُونس',12,12,76,'فَلَمَّا جَآءَهُمُ اُ۬لۡحَقُّ مِنۡ عِندِنَا قَالُوٓاْ إِنَّ هَٰذَا لَسِحۡرٞ مُّبِينٞ ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1445,11,'217',10,'Yūnus','يُونس',13,14,77,'قَالَ مُوسۭيٰٓ أَتَقُولُونَ لِلۡحَقِّ لَمَّا جَآءَكُمۡۖ أَسِحۡرٌ هَٰذَا وَلَا يُفۡلِحُ اُ۬لسَّٰحِرُونَ ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1446,11,'217',10,'Yūnus','يُونس',14,15,78,'قَالُوٓاْ أَجِئۡتَنَا لِتَلۡفِتَنَا عَمَّا وَجَدۡنَا عَلَيۡهِ ءَابَآءَنَا وَتَكُونَ لَكُمَا اَ۬لۡكِبۡرِيَآءُ فِي اِ۬لۡأَرۡضِ وَمَا نَحۡنُ لَكُمَا بِمُؤۡمِنِينَ ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1447,11,'218',10,'Yūnus','يُونس',1,1,79,'وَقَالَ فِرۡعَوۡنُ اُ۪ئۡتُونِي بِكُلِّ سَٰحِرٍ عَلِيمٖ ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1448,11,'218',10,'Yūnus','يُونس',1,2,80,'فَلَمَّا جَآءَ اَ۬لسَّحَرَةُ قَالَ لَهُم مُّوسۭيٰٓ أَلۡقُواْ مَآ أَنتُم مُّلۡقُونَ ٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1449,11,'218',10,'Yūnus','يُونس',2,4,81,'فَلَمَّآ أَلۡقَوۡاْ قَالَ مُوسۭيٰ مَا جِئۡتُم بِهِۦٓۚ ءَآلسِّحۡرُۖ إِنَّ اَ۬للَّهَ سَيُبۡطِلُهُۥٓۖ إِنَّ اَ۬للَّهَ لَا يُصۡلِحُ عَمَلَ اَ۬لۡمُفۡسِدِينَ ٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1450,11,'218',10,'Yūnus','يُونس',4,5,82,'وَيُحِقُّ اُ۬للَّهُ اُ۬لۡحَقَّ بِكَلِمَٰتِهِۦ وَلَوۡ كَرِهَ اَ۬لۡمُجۡرِمُونَ ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1451,11,'218',10,'Yūnus','يُونس',5,7,83,'۞ فَمَآ ءَامَنَ لِمُوسۭيٰٓ إِلَّا ذُرِّيَّةٞ مِّن قَوۡمِهِۦ عَلَىٰ خَوۡفٖ مِّن فِرۡعَوۡنَ وَمَلَإِيْهِمۡ أَن يَفۡتِنَهُمۡۚ وَإِنَّ فِرۡعَوۡنَ لَعَالٖ فِي اِ۬لۡأَرۡضِ وَإِنَّهُۥ لَمِنَ اَ۬لۡمُسۡرِفِينَ ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1452,11,'218',10,'Yūnus','يُونس',7,8,84,'وَقَالَ مُوسۭيٰ يَٰقَوۡمِ إِن كُنتُمۡ ءَامَنتُم بِاللَّهِ فَعَلَيۡهِ تَوَكَّلُوٓاْ إِن كُنتُم مُّسۡلِمِينَ ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1453,11,'218',10,'Yūnus','يُونس',9,9,85,'فَقَالُواْ عَلَى اَ۬للَّهِ تَوَكَّلۡنَا رَبَّنَا لَا تَجۡعَلۡنَا فِتۡنَةٗ لِّلۡقَوۡمِ اِ۬لظَّٰلِمِينَ ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1454,11,'218',10,'Yūnus','يُونس',10,10,86,'وَنَجِّنَا بِرَحۡمَتِكَ مِنَ اَ۬لۡقَوۡمِ اِ۬لۡكٰ۪فِرِينَ ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1455,11,'218',10,'Yūnus','يُونس',10,12,87,'وَأَوۡحَيۡنَآ إِلَىٰ مُوسۭيٰ وَأَخِيهِ أَن تَبَوَّءَا لِقَوۡمِكُمَا بِمِصۡرَ بُيُوتٗا وَاَجۡعَلُواْ بُيُوتَكُمۡ قِبۡلَةٗ وَأَقِيمُواْ اُ۬لصَّلَوٰةَۗ وَبَشِّرِ اِ۬لۡمُؤۡمِنِينَ ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1456,11,'218',10,'Yūnus','يُونس',12,15,88,'وَقَالَ مُوسۭيٰ رَبَّنَآ إِنَّكَ ءَاتَيۡتَ فِرۡعَوۡنَ وَمَلَأَهُۥ زِينَةٗ وَأَمۡوَٰلٗا فِي اِ۬لۡحَيَوٰةِ اِ۬لدُّنۡيۭا رَبَّنَا لِيَضِلُّواْ عَن سَبِيلِكَۖ رَبَّنَا اَ۪طۡمِسۡ عَلَىٰٓ أَمۡوَٰلِهِمۡ وَاَشۡدُدۡ عَلَىٰ قُلُوبِهِمۡ فَلَا يُؤۡمِنُواْ حَتَّىٰ يَرَوُاْ اُ۬لۡعَذَابَ اَ۬لۡأَلِيمَ ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1457,11,'219',10,'Yūnus','يُونس',1,2,89,'قَالَ قَدۡ أُجِيبَت دَّعۡوَتُكُمَا فَاَسۡتَقِيمَا وَلَا تَتَّبِعَآنِّ سَبِيلَ اَ۬لَّذِينَ لَا يَعۡلَمُونَ ٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1458,11,'219',10,'Yūnus','يُونس',2,5,90,'وَجَٰوَزۡنَا بِبَنِيٓ إِسۡرَٰٓءِيلَ اَ۬لۡبَحۡرَ فَأَتۡبَعَهُمۡ فِرۡعَوۡنُ وَجُنُودُهُۥ بَغۡيٗا وَعَدۡوًاۖ حَتَّىٰٓ إِذَآ أَدۡرَكَهُ اُ۬لۡغَرَقُ قَالَ ءَامَنتُ أَنَّهُۥ لَآ إِلَٰهَ إِلَّا اَ۬لَّذِيٓ ءَامَنَتۡ بِهِۦ بَنُوٓاْ إِسۡرَٰٓءِيلَ وَأَنَا۠ مِنَ اَ۬لۡمُسۡلِمِينَ ٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1459,11,'219',10,'Yūnus','يُونس',5,6,91,'ءَآلۡـَٰٔنَ وَقَدۡ عَصَيۡتَ قَبۡلُ وَكُنتَ مِنَ اَ۬لۡمُفۡسِدِينَ ٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1460,11,'219',10,'Yūnus','يُونس',6,7,92,'فَاَلۡيَوۡمَ نُنَجِّيكَ بِبَدَنِكَ لِتَكُونَ لِمَنۡ خَلۡفَكَ ءَايَةٗۚ وَإِنَّ كَثِيرٗا مِّنَ اَ۬لنّ۪اسِ عَنۡ ءَايَٰتِنَا لَغَٰفِلُونَ ٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1461,11,'219',10,'Yūnus','يُونس',8,10,93,'۞ وَلَقَدۡ بَوَّأۡنَا بَنِيٓ إِسۡرَٰٓءِيلَ مُبَوَّأَ صِدۡقٖ وَرَزَقۡنَٰهُم مِّنَ اَ۬لطَّيِّبَٰتِ فَمَا اَ۪خۡتَلَفُواْ حَتَّىٰ جَآءَهُمُ اُ۬لۡعِلۡمُۚ إِنَّ رَبَّكَ يَقۡضِي بَيۡنَهُمۡ يَوۡمَ اَ۬لۡقِيَٰمَةِ فِيمَا كَانُواْ فِيهِ يَخۡتَلِفُونَ ٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1462,11,'219',10,'Yūnus','يُونس',10,12,94,'فَإِن كُنتَ فِي شَكّٖ مِّمَّآ أَنزَلۡنَآ إِلَيۡكَ فَسۡـَٔلِ اِ۬لَّذِينَ يَقۡرَءُونَ اَ۬لۡكِتَٰبَ مِن قَبۡلِكَۚ لَقَد جَّآءَكَ اَ۬لۡحَقُّ مِن رَّبِّكَ فَلَا تَكُونَنَّ مِنَ اَ۬لۡمُمۡتَرِينَ ٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1463,11,'219',10,'Yūnus','يُونس',13,13,95,'وَلَا تَكُونَنَّ مِنَ اَ۬لَّذِينَ كَذَّبُواْ بِـَٔايَٰتِ اِ۬للَّهِ فَتَكُونَ مِنَ اَ۬لۡخَٰسِرِينَ ٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1464,11,'219',10,'Yūnus','يُونس',14,14,96,'إِنَّ اَ۬لَّذِينَ حَقَّتۡ عَلَيۡهِمۡ كَلِمَتُ رَبِّكَ لَا يُؤۡمِنُونَ ٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1465,11,'219',10,'Yūnus','يُونس',15,15,97,'وَلَوۡ جَآءَتۡهُمۡ كُلُّ ءَايَةٍ حَتَّىٰ يَرَوُاْ اُ۬لۡعَذَابَ اَ۬لۡأَلِيمَ ٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1466,11,'220',10,'Yūnus','يُونس',1,3,98,'فَلَوۡلَا كَانَتۡ قَرۡيَةٌ ءَامَنَتۡ فَنَفَعَهَآ إِيمَٰنُهَآ إِلَّا قَوۡمَ يُونُسَ لَمَّآ ءَامَنُواْ كَشَفۡنَا عَنۡهُمۡ عَذَابَ اَ۬لۡخِزۡيِ فِي اِ۬لۡحَيَوٰةِ اِ۬لدُّنۡيۭا وَمَتَّعۡنَٰهُمۡ إِلَىٰ حِينٖ ٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1467,11,'220',10,'Yūnus','يُونس',3,4,99,'وَلَوۡ شَآءَ رَبُّكَ لَأٓمَنَ مَن فِي اِ۬لۡأَرۡضِ كُلُّهُمۡ جَمِيعًاۚ أَفَأَنتَ تُكۡرِهُ اُ۬لنَّاسَ حَتَّىٰ يَكُونُواْ مُؤۡمِنِينَ ٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1468,11,'220',10,'Yūnus','يُونس',5,6,100,'وَمَا كَانَ لِنَفۡسٍ أَن تُؤۡمِنَ إِلَّا بِإِذۡنِ اِ۬للَّهِۚ وَيَجۡعَلُ اُ۬لرِّجۡسَ عَلَى اَ۬لَّذِينَ لَا يَعۡقِلُونَ ١٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1469,11,'220',10,'Yūnus','يُونس',6,7,101,'قُلُ اُ۟نظُرُواْ مَاذَا فِي اِ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِۚ وَمَا تُغۡنِي اِ۬لۡأٓيَٰتُ وَاَلنُّذُرُ عَن قَوۡمٖ لَّا يُؤۡمِنُونَ ١٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1470,11,'220',10,'Yūnus','يُونس',8,9,102,'فَهَلۡ يَنتَظِرُونَ إِلَّا مِثۡلَ أَيَّامِ اِ۬لَّذِينَ خَلَوۡاْ مِن قَبۡلِهِمۡۚ قُلۡ فَاَنتَظِرُوٓاْ إِنِّي مَعَكُم مِّنَ اَ۬لۡمُنتَظِرِينَ ١٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1471,11,'220',10,'Yūnus','يُونس',9,10,103,'ثُمَّ نُنَجِّي رُسۡلَنَا وَاَلَّذِينَ ءَامَنُواْۚ كَذَٰلِكَ حَقًّا عَلَيۡنَا نُنَجِّ اِ۬لۡمُؤۡمِنِينَ ١٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1472,11,'220',10,'Yūnus','يُونس',11,13,104,'۞ قُلۡ يَٰٓأَيُّهَا اَ۬لنَّاسُ إِن كُنتُمۡ فِي شَكّٖ مِّن دِينِي فَلَآ أَعۡبُدُ اُ۬لَّذِينَ تَعۡبُدُونَ مِن دُونِ اِ۬للَّهِ وَلَٰكِنۡ أَعۡبُدُ اُ۬للَّهَ اَ۬لَّذِي يَتَوَفَّىٰكُمۡۖ وَأُمِرۡتُ أَنۡ أَكُونَ مِنَ اَ۬لۡمُؤۡمِنِينَ ١٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1473,11,'220',10,'Yūnus','يُونس',13,14,105,'وَأَنۡ أَقِمۡ وَجۡهَكَ لِلدِّينِ حَنِيفٗا وَلَا تَكُونَنَّ مِنَ اَ۬لۡمُشۡرِكِينَ ١٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1474,11,'220',10,'Yūnus','يُونس',14,15,106,'وَلَا تَدۡعُ مِن دُونِ اِ۬للَّهِ مَا لَا يَنفَعُكَ وَلَا يَضُرُّكَۖ فَإِن فَعَلۡتَ فَإِنَّكَ إِذٗا مِّنَ اَ۬لظَّٰلِمِينَ ١٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1475,11,'221',10,'Yūnus','يُونس',1,3,107,'وَإِن يَمۡسَسۡكَ اَ۬للَّهُ بِضُرّٖ فَلَا كَاشِفَ لَهُۥٓ إِلَّا هُوَۖ وَإِن يُرِدۡكَ بِخَيۡرٖ فَلَا رَآدَّ لِفَضۡلِهِۦۚ يُصِيبُ بِهِۦ مَن يَشَآءُ مِنۡ عِبَادِهِۦۚ وَهۡوَ اَ۬لۡغَفُورُ اُ۬لرَّحِيمُ ١٠٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1476,11,'221',10,'Yūnus','يُونس',3,5,108,'قُلۡ يَٰٓأَيُّهَا اَ۬لنَّاسُ قَد جَّآءَكُمُ اُ۬لۡحَقُّ مِن رَّبِّكُمۡۖ فَمَنِ اِ۪هۡتَدَىٰ فَإِنَّمَا يَهۡتَدِي لِنَفۡسِهِۦۖ وَمَن ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيۡهَاۖ وَمَآ أَنَا۠ عَلَيۡكُم بِوَكِيلٖ ١٠٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1477,11,'221',10,'Yūnus','يُونس',5,6,109,'وَاَتَّبِعۡ مَا يُوحَىٰٓ إِلَيۡكَ وَاَصۡبِرۡ حَتَّىٰ يَحۡكُمَ اَ۬للَّهُۚ وَهۡوَ خَيۡرُ اُ۬لۡحَٰكِمِينَ ١٠٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1478,11,'221',11,'Hūd','هُود',9,9,1,'الٓر۪ۚ كِتَٰبٌ أُحۡكِمَتۡ ءَايَٰتُهُۥ ثُمَّ فُصِّلَتۡ مِن لَّدُنۡ حَكِيمٍ خَبِيرٍ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1479,11,'221',11,'Hūd','هُود',10,10,2,'أَلَّا تَعۡبُدُوٓاْ إِلَّا اَ۬للَّهَۚ إِنَّنِي لَكُم مِّنۡهُ نَذِيرٞ وَبَشِيرٞ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1480,11,'221',11,'Hūd','هُود',10,13,3,'وَأَنِ اِ۪سۡتَغۡفِرُواْ رَبَّكُمۡ ثُمَّ تُوبُوٓاْ إِلَيۡهِ يُمَتِّعۡكُم مَّتَٰعًا حَسَنًا إِلَىٰٓ أَجَلٖ مُّسَمّٗى وَيُؤۡتِ كُلَّ ذِي فَضۡلٖ فَضۡلَهُۥۖ وَإِن تَوَلَّوۡاْ فَإِنِّيَ أَخَافُ عَلَيۡكُمۡ عَذَابَ يَوۡمٖ كَبِيرٍ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1481,11,'221',11,'Hūd','هُود',13,13,4,'إِلَى اَ۬للَّهِ مَرۡجِعُكُمۡۖ وَهۡوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1482,11,'221',11,'Hūd','هُود',13,15,5,'أَلَآ إِنَّهُمۡ يَثۡنُونَ صُدُورَهُمۡ لِيَسۡتَخۡفُواْ مِنۡهُۚ أَلَا حِينَ يَسۡتَغۡشُونَ ثِيَابَهُمۡ يَعۡلَمُ مَا يُسِرُّونَ وَمَا يُعۡلِنُونَۚ إِنَّهُۥ عَلِيمُۢ بِذَاتِ اِ۬لصُّدُورِ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1483,12,'222',11,'Hūd','هُود',1,2,6,'۞ وَمَا مِن دَآبَّةٖ فِي اِ۬لۡأَرۡضِ إِلَّا عَلَى اَ۬للَّهِ رِزۡقُهَا وَيَعۡلَمُ مُسۡتَقَرَّهَا وَمُسۡتَوۡدَعَهَاۚ كُلّٞ فِي كِتَٰبٖ مُّبِينٖ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1484,12,'222',11,'Hūd','هُود',2,6,7,'وَهۡوَ اَ۬لَّذِي خَلَقَ اَ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضَ فِي سِتَّةِ أَيَّامٖ وَكَانَ عَرۡشُهُۥ عَلَى اَ۬لۡمَآءِ لِيَبۡلُوَكُمۡ أَيُّكُمۡ أَحۡسَنُ عَمَلٗاۗ وَلَئِن قُلۡتَ إِنَّكُم مَّبۡعُوثُونَ مِنۢ بَعۡدِ اِ۬لۡمَوۡتِ لَيَقُولَنَّ اَ۬لَّذِينَ كَفَرُوٓاْ إِنۡ هَٰذَآ إِلَّا سِحۡرٞ مُّبِينٞ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1485,12,'222',11,'Hūd','هُود',6,8,8,'وَلَئِنۡ أَخَّرۡنَا عَنۡهُمُ اُ۬لۡعَذَابَ إِلَىٰٓ أُمَّةٖ مَّعۡدُودَةٖ لَّيَقُولُنَّ مَا يَحۡبِسُهُۥٓۗ أَلَا يَوۡمَ يَأۡتِيهِمۡ لَيۡسَ مَصۡرُوفًا عَنۡهُمۡ وَحَاقَ بِهِم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1486,12,'222',11,'Hūd','هُود',9,10,9,'وَلَئِنۡ أَذَقۡنَا اَ۬لۡإِنسَٰنَ مِنَّا رَحۡمَةٗ ثُمَّ نَزَعۡنَٰهَا مِنۡهُ إِنَّهُۥ لَيَـُٔوسٞ كَفُورٞ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1487,12,'222',11,'Hūd','هُود',10,11,10,'وَلَئِنۡ أَذَقۡنَٰهُ نَعۡمَآءَ بَعۡدَ ضَرَّآءَ مَسَّتۡهُ لَيَقُولَنَّ ذَهَبَ اَ۬لسَّيِّـَٔاتُ عَنِّيَۚ إِنَّهُۥ لَفَرِحٞ فَخُورٌ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1488,12,'222',11,'Hūd','هُود',12,13,11,'إِلَّا اَ۬لَّذِينَ صَبَرُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ أُوْلَٰٓئِكَ لَهُم مَّغۡفِرَةٞ وَأَجۡرٞ كَبِيرٞ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1489,12,'222',11,'Hūd','هُود',13,15,12,'فَلَعَلَّكَ تَارِكُۢ بَعۡضَ مَا يُوحَىٰٓ إِلَيۡكَ وَضَآئِقُۢ بِهِۦ صَدۡرُكَ أَن يَقُولُواْ لَوۡلَآ أُنزِلَ عَلَيۡهِ كَنزٌ أَوۡ جَآءَ مَعَهُۥ مَلَكٌۚ إِنَّمَآ أَنتَ نَذِيرٞۚ وَاَللَّهُ عَلَىٰ كُلِّ شَيۡءٖ وَكِيلٌ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1490,12,'223',11,'Hūd','هُود',1,2,13,'أَمۡ يَقُولُونَ اَ۪فۡتَر۪ىٰهُۖ قُلۡ فَأۡتُواْ بِعَشۡرِ سُوَرٖ مِّثۡلِهِۦ مُفۡتَرَيَٰتٖ وَاَدۡعُواْ مَنِ اِ۪سۡتَطَعۡتُم مِّن دُونِ اِ۬للَّهِ إِن كُنتُمۡ صَٰدِقِينَ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1491,12,'223',11,'Hūd','هُود',3,4,14,'۞ فَإِلَّمۡ يَسۡتَجِيبُواْ لَكُمۡ فَاَعۡلَمُوٓاْ أَنَّمَآ أُنزِلَ بِعِلۡمِ اِ۬للَّهِ وَأَن لَّآ إِلَٰهَ إِلَّا هُوَۖ فَهَلۡ أَنتُم مُّسۡلِمُونَ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1492,12,'223',11,'Hūd','هُود',4,6,15,'مَن كَانَ يُرِيدُ اُ۬لۡحَيَوٰةَ اَ۬لدُّنۡيۭا وَزِينَتَهَا نُوَفِّ إِلَيۡهِمۡ أَعۡمَٰلَهُمۡ فِيهَا وَهُمۡ فِيهَا لَا يُبۡخَسُونَ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1493,12,'223',11,'Hūd','هُود',6,7,16,'أُوْلَٰٓئِكَ اَ۬لَّذِينَ لَيۡسَ لَهُمۡ فِي اِ۬لۡأٓخِرَةِ إِلَّا اَ۬لنَّارُۖ وَحَبِطَ مَا صَنَعُواْ فِيهَا وَبَٰطِلٞ مَّا كَانُواْ يَعۡمَلُونَ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1494,12,'223',11,'Hūd','هُود',8,11,17,'أَفَمَن كَانَ عَلَىٰ بَيِّنَةٖ مِّن رَّبِّهِۦ وَيَتۡلُوهُ شَاهِدٞ مِّنۡهُ وَمِن قَبۡلِهِۦ كِتَٰبُ مُوسۭيٰٓ إِمَامٗا وَرَحۡمَةًۚ أُوْلَٰٓئِكَ يُؤۡمِنُونَ بِهِۦۚ وَمَن يَكۡفُرۡ بِهِۦ مِنَ اَ۬لۡأَحۡزَابِ فَاَلنَّارُ مَوۡعِدُهُۥۚ فَلَا تَكُ فِي مِرۡيَةٖ مِّنۡهُۚ إِنَّهُ اُ۬لۡحَقُّ مِن رَّبِّكَ وَلَٰكِنَّ أَكۡثَرَ اَ۬لنّ۪اسِ لَا يُؤۡمِنُونَ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1495,12,'223',11,'Hūd','هُود',11,14,18,'وَمَنۡ أَظۡلَمُ مِمَّنِ اِ۪فۡتَر۪يٰ عَلَى اَ۬للَّهِ كَذِبًاۚ أُوْلَٰٓئِكَ يُعۡرَضُونَ عَلَىٰ رَبِّهِمۡ وَيَقُولُ اُ۬لۡأَشۡهَٰدُ هَٰٓؤُلَآءِ اِ۬لَّذِينَ كَذَبُواْ عَلَىٰ رَبِّهِمۡۚ أَلَا لَعۡنَةُ اُ۬للَّهِ عَلَى اَ۬لظَّٰلِمِينَ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1496,12,'223',11,'Hūd','هُود',14,15,19,'اَ۬لَّذِينَ يَصُدُّونَ عَن سَبِيلِ اِ۬للَّهِ وَيَبۡغُونَهَا عِوَجٗا وَهُم بِالۡأٓخِرَةِ هُمۡ كَٰفِرُونَ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1497,12,'224',11,'Hūd','هُود',1,3,20,'أُوْلَٰٓئِكَ لَمۡ يَكُونُواْ مُعۡجِزِينَ فِي اِ۬لۡأَرۡضِ وَمَا كَانَ لَهُم مِّن دُونِ اِ۬للَّهِ مِنۡ أَوۡلِيَآءَۘ يُضَٰعَفُ لَهُمُ اُ۬لۡعَذَابُۚ مَا كَانُواْ يَسۡتَطِيعُونَ اَ۬لسَّمۡعَ وَمَا كَانُواْ يُبۡصِرُونَ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1498,12,'224',11,'Hūd','هُود',3,4,21,'أُوْلَٰٓئِكَ اَ۬لَّذِينَ خَسِرُوٓاْ أَنفُسَهُمۡ وَضَلَّ عَنۡهُم مَّا كَانُواْ يَفۡتَرُونَ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1499,12,'224',11,'Hūd','هُود',4,5,22,'لَا جَرَمَ أَنَّهُمۡ فِي اِ۬لۡأٓخِرَةِ هُمُ اُ۬لۡأَخۡسَرُونَ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1500,12,'224',11,'Hūd','هُود',5,7,23,'إِنَّ اَ۬لَّذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ وَأَخۡبَتُوٓاْ إِلَىٰ رَبِّهِمۡ أُوْلَٰٓئِكَ أَصۡحَٰبُ اُ۬لۡجَنَّةِۖ هُمۡ فِيهَا خَٰلِدُونَ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1501,12,'224',11,'Hūd','هُود',7,8,24,'۞ مَثَلُ اُ۬لۡفَرِيقَيۡنِ كَاَلۡأَعۡمَىٰ وَاَلۡأَصَمِّ وَاَلۡبَصِيرِ وَاَلسَّمِيعِۚ هَلۡ يَسۡتَوِيَانِ مَثَلًاۚ أَفَلَا تَذَّكَّرُونَ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1502,12,'224',11,'Hūd','هُود',9,9,25,'وَلَقَدۡ أَرۡسَلۡنَا نُوحًا إِلَىٰ قَوۡمِهِۦٓ أَنِّي لَكُمۡ نَذِيرٞ مُّبِينٌ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1503,12,'224',11,'Hūd','هُود',10,10,26,'أَن لَّا تَعۡبُدُوٓاْ إِلَّا اَ۬للَّهَۖ إِنِّيَ أَخَافُ عَلَيۡكُمۡ عَذَابَ يَوۡمٍ أَلِيمٖ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1504,12,'224',11,'Hūd','هُود',11,13,27,'فَقَالَ اَ۬لۡمَلَأُ اُ۬لَّذِينَ كَفَرُواْ مِن قَوۡمِهِۦ مَا نَر۪ىٰكَ إِلَّا بَشَرٗا مِّثۡلَنَا وَمَا نَر۪ىٰكَ اَ۪تَّبَعَكَ إِلَّا اَ۬لَّذِينَ هُمۡ أَرَاذِلُنَا بَادِئَ اَ۬لرَّأۡيِ وَمَا نَر۪يٰ لَكُمۡ عَلَيۡنَا مِن فَضۡلِۢ بَلۡ نَظُنُّكُمۡ كَٰذِبِينَ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1505,12,'224',11,'Hūd','هُود',14,15,28,'قَالَ يَٰقَوۡمِ أَرَءَيۡتُمۡ إِن كُنتُ عَلَىٰ بَيِّنَةٖ مِّن رَّبِّي وَءَاتَىٰنِي رَحۡمَةٗ مِّنۡ عِندِهِۦ فَعَمِيَتۡ عَلَيۡكُمۡ أَنُلۡزِمُكُمُوهَا وَأَنتُمۡ لَهَا كَٰرِهُونَ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1506,12,'225',11,'Hūd','هُود',1,3,29,'وَيَٰقَوۡمِ لَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مَالًاۖ إِنۡ أَجۡرِيَ إِلَّا عَلَى اَ۬للَّهِۚ وَمَآ أَنَا۠ بِطَارِدِ اِ۬لَّذِينَ ءَامَنُوٓاْۚ إِنَّهُم مُّلَٰقُواْ رَبِّهِمۡ وَلَٰكِنِّيَ أَر۪ىٰكُمۡ قَوۡمٗا تَجۡهَلُونَ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1507,12,'225',11,'Hūd','هُود',3,4,30,'وَيَٰقَوۡمِ مَن يَنصُرُنِي مِنَ اَ۬للَّهِ إِن طَرَدتُّهُمۡۚ أَفَلَا تَذَّكَّرُونَ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1508,12,'225',11,'Hūd','هُود',4,7,31,'وَلَآ أَقُولُ لَكُمۡ عِندِي خَزَآئِنُ اُ۬للَّهِ وَلَآ أَعۡلَمُ اُ۬لۡغَيۡبَ وَلَآ أَقُولُ إِنِّي مَلَكٞ وَلَآ أَقُولُ لِلَّذِينَ تَزۡدَرِيٓ أَعۡيُنُكُمۡ لَن يُؤۡتِيَهُمُ اُ۬للَّهُ خَيۡرًاۖ اِ۬للَّهُ أَعۡلَمُ بِمَا فِيٓ أَنفُسِهِمۡ إِنِّيَ إِذٗا لَّمِنَ اَ۬لظَّٰلِمِينَ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1509,12,'225',11,'Hūd','هُود',7,8,32,'۞ قَالُواْ يَٰنُوحُ قَد جَّٰدَلۡتَنَا فَأَكۡثَرۡتَ جِدَٰلَنَا فَأۡتِنَا بِمَا تَعِدُنَآ إِن كُنتَ مِنَ اَ۬لصَّٰدِقِينَ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1510,12,'225',11,'Hūd','هُود',8,9,33,'قَالَ إِنَّمَا يَأۡتِيكُم بِهِ اِ۬للَّهُ إِن شَآءَ وَمَآ أَنتُم بِمُعۡجِزِينَ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1511,12,'225',11,'Hūd','هُود',9,11,34,'وَلَا يَنفَعُكُمۡ نُصۡحِيَ إِنۡ أَرَدتُّ أَنۡ أَنصَحَ لَكُمۡ إِن كَانَ اَ۬للَّهُ يُرِيدُ أَن يُغۡوِيَكُمۡۚ هُوَ رَبُّكُمۡ وَإِلَيۡهِ تُرۡجَعُونَ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1512,12,'225',11,'Hūd','هُود',11,12,35,'أَمۡ يَقُولُونَ اَ۪فۡتَر۪ىٰهُۖ قُلۡ إِنِ اِ۪فۡتَرَيۡتُهُۥ فَعَلَيَّ إِجۡرَامِي وَأَنَا۠ بَرِيٓءٞ مِّمَّا تُجۡرِمُونَ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1513,12,'225',11,'Hūd','هُود',13,14,36,'وَأُوحِيَ إِلَىٰ نُوحٍ أَنَّهُۥ لَن يُؤۡمِنَ مِن قَوۡمِكَ إِلَّا مَن قَدۡ ءَامَنَ فَلَا تَبۡتَئِسۡ بِمَا كَانُواْ يَفۡعَلُونَ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1514,12,'225',11,'Hūd','هُود',14,15,37,'وَاَصۡنَعِ اِ۬لۡفُلۡكَ بِأَعۡيُنِنَا وَوَحۡيِنَا وَلَا تُخَٰطِبۡنِي فِي اِ۬لَّذِينَ ظَلَمُوٓاْۚ إِنَّهُم مُّغۡرَقُونَ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1515,12,'226',11,'Hūd','هُود',1,2,38,'وَيَصۡنَعُ اُ۬لۡفُلۡكَ وَكُلَّمَا مَرَّ عَلَيۡهِ مَلَأٞ مِّن قَوۡمِهِۦ سَخِرُواْ مِنۡهُۚ قَالَ إِن تَسۡخَرُواْ مِنَّا فَإِنَّا نَسۡخَرُ مِنكُمۡ كَمَا تَسۡخَرُونَ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1516,12,'226',11,'Hūd','هُود',3,4,39,'فَسَوۡفَ تَعۡلَمُونَ مَن يَأۡتِيهِ عَذَابٞ يُخۡزِيهِ وَيَحِلُّ عَلَيۡهِ عَذَابٞ مُّقِيمٌ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1517,12,'226',11,'Hūd','هُود',4,6,40,'حَتَّىٰٓ إِذَا جَآ أَمۡرُنَا وَفَارَ اَ۬لتَّنُّورُ قُلۡنَا اَ۪حۡمِلۡ فِيهَا مِن كُلِّ زَوۡجَيۡنِ اِ۪ثۡنَيۡنِ وَأَهۡلَكَ إِلَّا مَن سَبَقَ عَلَيۡهِ اِ۬لۡقَوۡلُ وَمَنۡ ءَامَنَۚ وَمَآ ءَامَنَ مَعَهُۥٓ إِلَّا قَلِيلٞ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1518,12,'226',11,'Hūd','هُود',6,7,41,'وَقَالَ اَ۪رۡكَبُواْ فِيهَا بِسۡمِ اِ۬للَّهِ مُجۡر۪ىٰهَا وَمُرۡسَىٰهَآۚ إِنَّ رَبِّي لَغَفُورٞ رَّحِيمٞ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1519,12,'226',11,'Hūd','هُود',8,9,42,'۞ وَهۡيَ تَجۡرِي بِهِمۡ فِي مَوۡجٖ كَاَلۡجِبَالِ وَنَادَىٰ نُوحٌ اِ۪بۡنَهُۥ وَكَانَ فِي مَعۡزِلٖ يَٰبُنَيِّ اِ۪رۡكَب مَّعَنَا وَلَا تَكُن مَّعَ اَ۬لۡكٰ۪فِرِينَ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1520,12,'226',11,'Hūd','هُود',10,12,43,'قَالَ سَـَٔاوِيٓ إِلَىٰ جَبَلٖ يَعۡصِمُنِي مِنَ اَ۬لۡمَآءِۚ قَالَ لَا عَاصِمَ اَ۬لۡيَوۡمَ مِنۡ أَمۡرِ اِ۬للَّهِ إِلَّا مَن رَّحِمَۚ وَحَالَ بَيۡنَهُمَا اَ۬لۡمَوۡجُ فَكَانَ مِنَ اَ۬لۡمُغۡرَقِينَ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1521,12,'226',11,'Hūd','هُود',12,14,44,'وَقِيلَ يَٰٓأَرۡضُ اُ۪بۡلَعِي مَآءَكِ وَيَٰسَمَآءُ اَ۬قۡلِعِي وَغِيضَ اَ۬لۡمَآءُ وَقُضِيَ اَ۬لۡأَمۡرُ وَاَسۡتَوَتۡ عَلَى اَ۬لۡجُودِيِّۖ وَقِيلَ بُعۡدٗا لِّلۡقَوۡمِ اِ۬لظَّٰلِمِينَ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1522,12,'226',11,'Hūd','هُود',14,15,45,'وَنَادَىٰ نُوحٞ رَّبَّهُۥ فَقَالَ رَبِّ إِنَّ اَ۪بۡنِي مِنۡ أَهۡلِي وَإِنَّ وَعۡدَكَ اَ۬لۡحَقُّ وَأَنتَ أَحۡكَمُ اُ۬لۡحَٰكِمِينَ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1523,12,'227',11,'Hūd','هُود',1,2,46,'قَالَ يَٰنُوحُ إِنَّهُۥ لَيۡسَ مِنۡ أَهۡلِكَۖ إِنَّهُۥ عَمَلٌ غَيۡرُ صَٰلِحٖۖ فَلَا تَسۡـَٔلۡنِۦ مَا لَيۡسَ لَكَ بِهِۦ عِلۡمٌۖ إِنِّيَ أَعِظُكَ أَن تَكُونَ مِنَ اَ۬لۡجَٰهِلِينَ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1524,12,'227',11,'Hūd','هُود',3,4,47,'قَالَ رَبِّ إِنِّيَ أَعُوذُ بِكَ أَنۡ أَسۡـَٔلَكَ مَا لَيۡسَ لِي بِهِۦ عِلۡمٞۖ وَإِلَّا تَغۡفِر لِّي وَتَرۡحَمۡنِيٓ أَكُن مِّنَ اَ۬لۡخَٰسِرِينَ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1525,12,'227',11,'Hūd','هُود',4,6,48,'قِيلَ يَٰنُوحُ اُ۪هۡبِطۡ بِسَلَٰمٖ مِّنَّا وَبَرَكَٰتٍ عَلَيۡكَ وَعَلَىٰٓ أُمَمٖ مِّمَّن مَّعَكَۚ وَأُمَمٞ سَنُمَتِّعُهُمۡ ثُمَّ يَمَسُّهُم مِّنَّا عَذَابٌ أَلِيمٞ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1526,12,'227',11,'Hūd','هُود',6,8,49,'تِلۡكَ مِنۡ أَنۢبَآءِ اِ۬لۡغَيۡبِ نُوحِيهَآ إِلَيۡكَۖ مَا كُنتَ تَعۡلَمُهَآ أَنتَ وَلَا قَوۡمُكَ مِن قَبۡلِ هَٰذَاۖ فَاَصۡبِرۡۖ إِنَّ اَ۬لۡعَٰقِبَةَ لِلۡمُتَّقِينَ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1527,12,'227',11,'Hūd','هُود',9,10,50,'۞ وَإِلَىٰ عَادٍ أَخَاهُمۡ هُودٗاۚ قَالَ يَٰقَوۡمِ اِ۟عۡبُدُواْ اُ۬للَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥٓۖ إِنۡ أَنتُمۡ إِلَّا مُفۡتَرُونَ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1528,12,'227',11,'Hūd','هُود',10,11,51,'يَٰقَوۡمِ لَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ أَجۡرًاۖ إِنۡ أَجۡرِيَ إِلَّا عَلَى اَ۬لَّذِي فَطَرَنِيٓۚ أَفَلَا تَعۡقِلُونَ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1529,12,'227',11,'Hūd','هُود',12,14,52,'وَيَٰقَوۡمِ اِ۪سۡتَغۡفِرُواْ رَبَّكُمۡ ثُمَّ تُوبُوٓاْ إِلَيۡهِ يُرۡسِلِ اِ۬لسَّمَآءَ عَلَيۡكُم مِّدۡرَارٗا وَيَزِدۡكُمۡ قُوَّةً إِلَىٰ قُوَّتِكُمۡ وَلَا تَتَوَلَّوۡاْ مُجۡرِمِينَ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1530,12,'227',11,'Hūd','هُود',14,15,53,'قَالُواْ يَٰهُودُ مَا جِئۡتَنَا بِبَيِّنَةٖ وَمَا نَحۡنُ بِتَارِكِيٓ ءَالِهَتِنَا عَن قَوۡلِكَ وَمَا نَحۡنُ لَكَ بِمُؤۡمِنِينَ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1531,12,'228',11,'Hūd','هُود',1,3,54,'إِن نَّقُولُ إِلَّا اَ۪عۡتَر۪ىٰكَ بَعۡضُ ءَالِهَتِنَا بِسُوٓءٖۗ قَالَ إِنِّيٓ أُشۡهِدُ اُ۬للَّهَ وَاَشۡهَدُوٓاْ أَنِّي بَرِيٓءٞ مِّمَّا تُشۡرِكُونَ مِن دُونِهِۦۖ فَكِيدُونِي جَمِيعٗا ثُمَّ لَا تُنظِرُونِ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1532,12,'228',11,'Hūd','هُود',3,4,55,'إِنِّي تَوَكَّلۡتُ عَلَى اَ۬للَّهِ رَبِّي وَرَبِّكُمۚ مَّا مِن دَآبَّةٍ إِلَّا هُوَ ءَاخِذُۢ بِنَاصِيَتِهَآۚ إِنَّ رَبِّي عَلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1533,12,'228',11,'Hūd','هُود',5,6,56,'فَإِن تَوَلَّوۡاْ فَقَدۡ أَبۡلَغۡتُكُم مَّآ أُرۡسِلۡتُ بِهِۦٓ إِلَيۡكُمۡۚ وَيَسۡتَخۡلِفُ رَبِّي قَوۡمًا غَيۡرَكُمۡ وَلَا تَضُرُّونَهُۥ شَيۡـًٔاۚ إِنَّ رَبِّي عَلَىٰ كُلِّ شَيۡءٍ حَفِيظٞ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1534,12,'228',11,'Hūd','هُود',7,8,57,'وَلَمَّا جَآ أَمۡرُنَا نَجَّيۡنَا هُودٗا وَاَلَّذِينَ ءَامَنُواْ مَعَهُۥ بِرَحۡمَةٖ مِّنَّا وَنَجَّيۡنَٰهُم مِّنۡ عَذَابٍ غَلِيظٖ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1535,12,'228',11,'Hūd','هُود',8,9,58,'وَتِلۡكَ عَادٞۖ جَحَدُواْ بِـَٔايَٰتِ رَبِّهِمۡ وَعَصَوۡاْ رُسُلَهُۥ وَاَتَّبَعُوٓاْ أَمۡرَ كُلِّ جَبّ۪ارٍ عَنِيدٖ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1536,12,'228',11,'Hūd','هُود',9,11,59,'وَأُتۡبِعُواْ فِي هَٰذِهِ اِ۬لدُّنۡيۭا لَعۡنَةٗ وَيَوۡمَ اَ۬لۡقِيَٰمَةِۗ أَلَآ إِنَّ عَادٗا كَفَرُواْ رَبَّهُمۡۗ أَلَا بُعۡدٗا لِّعَادٖ قَوۡمِ هُودٖ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1537,12,'228',11,'Hūd','هُود',11,13,60,'وَإِلَىٰ ثَمُودَ أَخَاهُمۡ صَٰلِحٗاۚ قَالَ يَٰقَوۡمِ اِ۟عۡبُدُواْ اُ۬للَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥۖ هُوَ أَنشَأَكُم مِّنَ اَ۬لۡأَرۡضِ وَاَسۡتَعۡمَرَكُمۡ فِيهَا فَاَسۡتَغۡفِرُوهُ ثُمَّ تُوبُوٓاْ إِلَيۡهِۚ إِنَّ رَبِّي قَرِيبٞ مُّجِيبٞ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1538,12,'228',11,'Hūd','هُود',14,15,61,'۞ قَالُواْ يَٰصَٰلِحُ قَدۡ كُنتَ فِينَا مَرۡجُوّٗا قَبۡلَ هَٰذَآۖ أَتَنۡهَىٰنَآ أَن نَّعۡبُدَ مَا يَعۡبُدُ ءَابَآؤُنَا وَإِنَّنَا لَفِي شَكّٖ مِّمَّا تَدۡعُونَآ إِلَيۡهِ مُرِيبٖ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1539,12,'229',11,'Hūd','هُود',1,3,62,'قَالَ يَٰقَوۡمِ أَرَءَيۡتُمۡ إِن كُنتُ عَلَىٰ بَيِّنَةٖ مِّن رَّبِّي وَءَاتَىٰنِي مِنۡهُ رَحۡمَةٗ فَمَن يَنصُرُنِي مِنَ اَ۬للَّهِ إِنۡ عَصَيۡتُهُۥۖ فَمَا تَزِيدُونَنِي غَيۡرَ تَخۡسِيرٖ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1540,12,'229',11,'Hūd','هُود',3,5,63,'وَيَٰقَوۡمِ هَٰذِهِۦ نَاقَةُ اُ۬للَّهِ لَكُمۡ ءَايَةٗ فَذَرُوهَا تَأۡكُلۡ فِيٓ أَرۡضِ اِ۬للَّهِ وَلَا تَمَسُّوهَا بِسُوٓءٖ فَيَأۡخُذَكُمۡ عَذَابٞ قَرِيبٞ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1541,12,'229',11,'Hūd','هُود',5,6,64,'فَعَقَرُوهَا فَقَالَ تَمَتَّعُواْ فِي د۪ارِكُمۡ ثَلَٰثَةَ أَيَّامٖۖ ذَٰلِكَ وَعۡدٌ غَيۡرُ مَكۡذُوبٖ ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1542,12,'229',11,'Hūd','هُود',6,8,65,'فَلَمَّا جَآ أَمۡرُنَا نَجَّيۡنَا صَٰلِحٗا وَاَلَّذِينَ ءَامَنُواْ مَعَهُۥ بِرَحۡمَةٖ مِّنَّا وَمِنۡ خِزۡيِ يَوۡمِئِذٍۚ إِنَّ رَبَّكَ هُوَ اَ۬لۡقَوِيُّ اُ۬لۡعَزِيزُ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1543,12,'229',11,'Hūd','هُود',8,9,66,'وَأَخَذَ اَ۬لَّذِينَ ظَلَمُواْ اُ۬لصَّيۡحَةُ فَأَصۡبَحُواْ فِي دِيٰ۪رِهِمۡ جَٰثِمِينَ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1544,12,'229',11,'Hūd','هُود',10,11,67,'كَأَن لَّمۡ يَغۡنَوۡاْ فِيهَآۗ أَلَآ إِنَّ ثَمُودٗا كَفَرُواْ رَبَّهُمۡۗ أَلَا بُعۡدٗا لِّثَمُودَ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1545,12,'229',11,'Hūd','هُود',11,12,68,'وَلَقَد جَّآءَتۡ رُسۡلُنَآ إِبۡرَٰهِيمَ بِالۡبُشۡر۪يٰ قَالُواْ سَلَٰمٗاۖ قَالَ سَلَٰمٞۖ فَمَا لَبِثَ أَن جَآءَ بِعِجۡلٍ حَنِيذٖ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1546,12,'229',11,'Hūd','هُود',12,14,69,'فَلَمَّا رَء۪آ أَيۡدِيَهُمۡ لَا تَصِلُ إِلَيۡهِ نَكِرَهُمۡ وَأَوۡجَسَ مِنۡهُمۡ خِيفَةٗۚ قَالُواْ لَا تَخَفۡ إِنَّآ أُرۡسِلۡنَآ إِلَىٰ قَوۡمِ لُوطٖ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1547,12,'229',11,'Hūd','هُود',14,15,70,'وَاَمۡرَأَتُهُۥ قَآئِمَةٞ فَضَحِكَتۡ فَبَشَّرۡنَٰهَا بِإِسۡحَٰقَ وَمِن وَرَآ إِسۡحَٰقَ يَعۡقُوبُ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1548,12,'230',11,'Hūd','هُود',1,2,71,'قَالَتۡ يَٰوَيۡلَتۭيٰٓ ءَٰا۬لِدُ وَأَنَا۠ عَجُوزٞ وَهَٰذَا بَعۡلِي شَيۡخًاۖ إِنَّ هَٰذَا لَشَيۡءٌ عَجِيبٞ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1549,12,'230',11,'Hūd','هُود',2,3,72,'۞ قَالُوٓاْ أَتَعۡجَبِينَ مِنۡ أَمۡرِ اِ۬للَّهِۖ رَحۡمَتُ اُ۬للَّهِ وَبَرَكَٰتُهُۥ عَلَيۡكُمۡ أَهۡلَ اَ۬لۡبَيۡتِۚ إِنَّهُۥ حَمِيدٞ مَّجِيدٞ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1550,12,'230',11,'Hūd','هُود',3,4,73,'فَلَمَّا ذَهَبَ عَنۡ إِبۡرَٰهِيمَ اَ۬لرَّوۡعُ وَجَآءَتۡهُ اُ۬لۡبُشۡر۪يٰ يُجَٰدِلُنَا فِي قَوۡمِ لُوطٍ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1551,12,'230',11,'Hūd','هُود',5,5,74,'إِنَّ إِبۡرَٰهِيمَ لَحَلِيمٌ أَوَّٰهٞ مُّنِيبٞ ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1552,12,'230',11,'Hūd','هُود',5,6,75,'يَٰٓإِبۡرَٰهِيمُ أَعۡرِضۡ عَنۡ هَٰذَآۖ إِنَّهُۥ قَد جَّآ أَمۡرُ رَبِّكَۖ وَإِنَّهُمۡ ءَاتِيهِمۡ عَذَابٌ غَيۡرُ مَرۡدُودٖ ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1553,12,'230',11,'Hūd','هُود',6,8,76,'وَلَمَّا جَآءَتۡ رُسۡلُنَا لُوطٗا سِيٓءَ بِهِمۡ وَضَاقَ بِهِمۡ ذَرۡعٗا وَقَالَ هَٰذَا يَوۡمٌ عَصِيبٞ ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1554,12,'230',11,'Hūd','هُود',8,10,77,'وَجَآءَهُۥ قَوۡمُهُۥ يُهۡرَعُونَ إِلَيۡهِ وَمِن قَبۡلُ كَانُواْ يَعۡمَلُونَ اَ۬لسَّيِّـَٔاتِۚ قَالَ يَٰقَوۡمِ هَٰٓؤُلَآءِ بَنَاتِي هُنَّ أَطۡهَرُ لَكُمۡۖ فَاَتَّقُواْ اُ۬للَّهَ وَلَا تُخۡزُونِۦ فِي ضَيۡفِيَۖ أَلَيۡسَ مِنكُمۡ رَجُلٞ رَّشِيدٞ ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1555,12,'230',11,'Hūd','هُود',11,11,78,'قَالُواْ لَقَدۡ عَلِمۡتَ مَا لَنَا فِي بَنَاتِكَ مِنۡ حَقّٖ وَإِنَّكَ لَتَعۡلَمُ مَا نُرِيدُ ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1556,12,'230',11,'Hūd','هُود',12,12,79,'قَالَ لَوۡ أَنَّ لِي بِكُمۡ قُوَّةً أَوۡ ءَاوِيٓ إِلَىٰ رُكۡنٖ شَدِيدٖ ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1557,12,'230',11,'Hūd','هُود',12,15,80,'قَالُواْ يَٰلُوطُ إِنَّا رُسُلُ رَبِّكَ لَن يَصِلُوٓاْ إِلَيۡكَۖ فَأَسۡرِ بِأَهۡلِكَ بِقِطۡعٖ مِّنَ اَ۬لَّيۡلِ وَلَا يَلۡتَفِتۡ مِنكُمۡ أَحَدٌ إِلَّا اَ۪مۡرَأَتُكَۖ إِنَّهُۥ مُصِيبُهَا مَآ أَصَابَهُمۡۚ إِنَّ مَوۡعِدَهُمُ اُ۬لصُّبۡحُۚ أَلَيۡسَ اَ۬لصُّبۡحُ بِقَرِيبٖ ٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1558,12,'231',11,'Hūd','هُود',1,2,81,'فَلَمَّا جَآ أَمۡرُنَا جَعَلۡنَا عَٰلِيَهَا سَافِلَهَا وَأَمۡطَرۡنَا عَلَيۡهَا حِجَارَةٗ مِّن سِجِّيلٖ مَّنضُودٖ ٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1559,12,'231',11,'Hūd','هُود',2,3,82,'مُّسَوَّمَةً عِندَ رَبِّكَۖ وَمَا هِيَ مِنَ اَ۬لظَّٰلِمِينَ بِبَعِيدٖ ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1560,12,'231',11,'Hūd','هُود',3,6,83,'۞ وَإِلَىٰ مَدۡيَنَ أَخَاهُمۡ شُعَيۡبٗاۚ قَالَ يَٰقَوۡمِ اِ۟عۡبُدُواْ اُ۬للَّهَ مَا لَكُم مِّنۡ إِلَٰهٍ غَيۡرُهُۥۖ وَلَا تَنقُصُواْ اُ۬لۡمِكۡيَالَ وَاَلۡمِيزَانَۖ إِنِّيَ أَر۪ىٰكُم بِخَيۡرٖ وَإِنِّيَ أَخَافُ عَلَيۡكُمۡ عَذَابَ يَوۡمٖ مُّحِيطٖ ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1561,12,'231',11,'Hūd','هُود',6,8,84,'وَيَٰقَوۡمِ أَوۡفُواْ اُ۬لۡمِكۡيَالَ وَاَلۡمِيزَانَ بِالۡقِسۡطِۖ وَلَا تَبۡخَسُواْ اُ۬لنَّاسَ أَشۡيَآءَهُمۡ وَلَا تَعۡثَوۡاْ فِي اِ۬لۡأَرۡضِ مُفۡسِدِينَ ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1562,12,'231',11,'Hūd','هُود',8,9,85,'بَقِيَّتُ اُ۬للَّهِ خَيۡرٞ لَّكُمۡ إِن كُنتُم مُّؤۡمِنِينَ ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1563,12,'231',11,'Hūd','هُود',9,10,86,'وَمَآ أَنَا۠ عَلَيۡكُم بِحَفِيظٖ ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1564,12,'231',11,'Hūd','هُود',10,12,87,'قَالُواْ يَٰشُعَيۡبُ أَصَلَوَٰتُكَ تَأۡمُرُكَ أَن نَّتۡرُكَ مَا يَعۡبُدُ ءَابَآؤُنَآ أَوۡ أَن نَّفۡعَلَ فِيٓ أَمۡوَٰلِنَا مَا نَشَٰٓؤُاْۖ اِ۪نَّكَ لَأَنتَ اَ۬لۡحَلِيمُ اُ۬لرَّشِيدُ ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1565,12,'231',11,'Hūd','هُود',12,15,88,'قَالَ يَٰقَوۡمِ أَرَءَيۡتُمۡ إِن كُنتُ عَلَىٰ بَيِّنَةٖ مِّن رَّبِّي وَرَزَقَنِي مِنۡهُ رِزۡقًا حَسَنٗاۚ وَمَآ أُرِيدُ أَنۡ أُخَالِفَكُمۡ إِلَىٰ مَآ أَنۡهَىٰكُمۡ عَنۡهُۚ إِنۡ أُرِيدُ إِلَّا اَ۬لۡإِصۡلَٰحَ مَا اَ۪سۡتَطَعۡتُۚ وَمَا تَوۡفِيقِيَ إِلَّا بِاللَّهِۚ عَلَيۡهِ تَوَكَّلۡتُ وَإِلَيۡهِ أُنِيبُ ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1566,12,'232',11,'Hūd','هُود',1,3,89,'وَيَٰقَوۡمِ لَا يَجۡرِمَنَّكُمۡ شِقَاقِيَ أَن يُصِيبَكُم مِّثۡلُ مَآ أَصَابَ قَوۡمَ نُوحٍ أَوۡ قَوۡمَ هُودٍ أَوۡ قَوۡمَ صَٰلِحٖۚ وَمَا قَوۡمُ لُوطٖ مِّنكُم بِبَعِيدٖ ٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1567,12,'232',11,'Hūd','هُود',3,4,90,'وَاَسۡتَغۡفِرُواْ رَبَّكُمۡ ثُمَّ تُوبُوٓاْ إِلَيۡهِۚ إِنَّ رَبِّي رَحِيمٞ وَدُودٞ ٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1568,12,'232',11,'Hūd','هُود',4,6,91,'قَالُواْ يَٰشُعَيۡبُ مَا نَفۡقَهُ كَثِيرٗا مِّمَّا تَقُولُ وَإِنَّا لَنَر۪ىٰكَ فِينَا ضَعِيفٗاۖ وَلَوۡلَا رَهۡطُكَ لَرَجَمۡنَٰكَۖ وَمَآ أَنتَ عَلَيۡنَا بِعَزِيزٖ ٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1569,12,'232',11,'Hūd','هُود',6,8,92,'قَالَ يَٰقَوۡمِ أَرَهۡطِيَ أَعَزُّ عَلَيۡكُم مِّنَ اَ۬للَّهِ وَاَتَّخَذتُّمُوهُ وَرَآءَكُمۡ ظِهۡرِيًّاۖ إِنَّ رَبِّي بِمَا تَعۡمَلُونَ مُحِيطٞ ٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1570,12,'232',11,'Hūd','هُود',8,10,93,'وَيَٰقَوۡمِ اِ۪عۡمَلُواْ عَلَىٰ مَكَانَتِكُمۡ إِنِّي عَٰمِلٞۖ سَوۡفَ تَعۡلَمُونَ مَن يَأۡتِيهِ عَذَابٞ يُخۡزِيهِ وَمَنۡ هُوَ كَٰذِبٞۖ وَاَرۡتَقِبُوٓاْ إِنِّي مَعَكُمۡ رَقِيبٞ ٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1571,12,'232',11,'Hūd','هُود',10,12,94,'۞ وَلَمَّا جَآ أَمۡرُنَا نَجَّيۡنَا شُعَيۡبٗا وَاَلَّذِينَ ءَامَنُواْ مَعَهُۥ بِرَحۡمَةٖ مِّنَّا وَأَخَذَتِ اِ۬لَّذِينَ ظَلَمُواْ اُ۬لصَّيۡحَةُ فَأَصۡبَحُواْ فِي دِيٰ۪رِهِمۡ جَٰثِمِينَ ٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1572,12,'232',11,'Hūd','هُود',13,13,95,'كَأَن لَّمۡ يَغۡنَوۡاْ فِيهَآۗ أَلَا بُعۡدٗا لِّمَدۡيَنَ كَمَا بَعِدَت ثَّمُودُ ٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1573,12,'232',11,'Hūd','هُود',14,14,96,'وَلَقَدۡ أَرۡسَلۡنَا مُوسۭيٰ بِـَٔايَٰتِنَا وَسُلۡطَٰنٖ مُّبِينٍ ٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1574,12,'232',11,'Hūd','هُود',14,15,97,'إِلَىٰ فِرۡعَوۡنَ وَمَلَإِيْهِۦ فَاَتَّبَعُوٓاْ أَمۡرَ فِرۡعَوۡنَۖ وَمَآ أَمۡرُ فِرۡعَوۡنَ بِرَشِيدٖ ٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1575,12,'233',11,'Hūd','هُود',1,2,98,'يَقۡدُمُ قَوۡمَهُۥ يَوۡمَ اَ۬لۡقِيَٰمَةِ فَأَوۡرَدَهُمُ اُ۬لنَّارَۖ وَبِئۡسَ اَ۬لۡوِرۡدُ اُ۬لۡمَوۡرُودُ ٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1576,12,'233',11,'Hūd','هُود',2,3,99,'وَأُتۡبِعُواْ فِي هَٰذِهِۦ لَعۡنَةٗ وَيَوۡمَ اَ۬لۡقِيَٰمَةِۚ بِئۡسَ اَ۬لرِّفۡدُ اُ۬لۡمَرۡفُودُ ٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1577,12,'233',11,'Hūd','هُود',3,4,100,'ذَٰلِكَ مِنۡ أَنۢبَآءِ اِ۬لۡقُر۪يٰ نَقُصُّهُۥ عَلَيۡكَۖ مِنۡهَا قَآئِمٞ وَحَصِيدٞ ١٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1578,12,'233',11,'Hūd','هُود',4,6,101,'وَمَا ظَلَمۡنَٰهُمۡ وَلَٰكِن ظَلَمُوٓاْ أَنفُسَهُمۡۖ فَمَآ أَغۡنَتۡ عَنۡهُمۡ ءَالِهَتُهُمُ اُ۬لَّتِي يَدۡعُونَ مِن دُونِ اِ۬للَّهِ مِن شَيۡءٖ لَّمَّا جَآ أَمۡرُ رَبِّكَۖ وَمَا زَادُوهُمۡ غَيۡرَ تَتۡبِيبٖ ١٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1579,12,'233',11,'Hūd','هُود',7,8,102,'وَكَذَٰلِكَ أَخۡذُ رَبِّكَ إِذَآ أَخَذَ اَ۬لۡقُر۪يٰ وَهۡيَ ظَٰلِمَةٌۚ إِنَّ أَخۡذَهُۥٓ أَلِيمٞ شَدِيدٌ ١٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1580,12,'233',11,'Hūd','هُود',8,9,103,'إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّمَنۡ خَافَ عَذَابَ اَ۬لۡأٓخِرَةِۚ ذَٰلِكَ يَوۡمٞ مَّجۡمُوعٞ لَّهُ اُ۬لنَّاسُ وَذَٰلِكَ يَوۡمٞ مَّشۡهُودٞ ١٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1581,12,'233',11,'Hūd','هُود',10,10,104,'وَمَا نُؤَخِّرُهُۥٓ إِلَّا لِأَجَلٖ مَّعۡدُودٖ ١٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1582,12,'233',11,'Hūd','هُود',10,11,105,'يَوۡمَ يَأۡتِۦ لَا تَكَلَّمُ نَفۡسٌ إِلَّا بِإِذۡنِهِۦۚ فَمِنۡهُمۡ شَقِيّٞ وَسَعِيدٞ ١٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1583,12,'233',11,'Hūd','هُود',11,12,106,'فَأَمَّا اَ۬لَّذِينَ شَقُواْ فَفِي اِ۬لنّ۪ارِ لَهُمۡ فِيهَا زَفِيرٞ وَشَهِيقٌ ١٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1584,12,'233',11,'Hūd','هُود',12,13,107,'خَٰلِدِينَ فِيهَا مَا دَامَتِ اِ۬لسَّمَٰوَٰتُ وَاَلۡأَرۡضُ إِلَّا مَا شَآءَ رَبُّكَۚ إِنَّ رَبَّكَ فَعَّالٞ لِّمَا يُرِيدُ ١٠٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1585,12,'233',11,'Hūd','هُود',14,15,108,'۞ وَأَمَّا اَ۬لَّذِينَ سَعِدُواْ فَفِي اِ۬لۡجَنَّةِ خَٰلِدِينَ فِيهَا مَا دَامَتِ اِ۬لسَّمَٰوَٰتُ وَاَلۡأَرۡضُ إِلَّا مَا شَآءَ رَبُّكَۖ عَطَآءً غَيۡرَ مَجۡذُوذٖ ١٠٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1586,12,'234',11,'Hūd','هُود',1,2,109,'فَلَا تَكُ فِي مِرۡيَةٖ مِّمَّا يَعۡبُدُ هَٰٓؤُلَآءِۚ مَا يَعۡبُدُونَ إِلَّا كَمَا يَعۡبُدُ ءَابَآؤُهُم مِّن قَبۡلُۚ وَإِنَّا لَمُوَفُّوهُمۡ نَصِيبَهُمۡ غَيۡرَ مَنقُوصٖ ١٠٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1587,12,'234',11,'Hūd','هُود',3,4,110,'وَلَقَدۡ ءَاتَيۡنَا مُوسَى اَ۬لۡكِتَٰبَ فَاَخۡتُلِفَ فِيهِۚ وَلَوۡلَا كَلِمَةٞ سَبَقَتۡ مِن رَّبِّكَ لَقُضِيَ بَيۡنَهُمۡۚ وَإِنَّهُمۡ لَفِي شَكّٖ مِّنۡهُ مُرِيبٖ ١١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1588,12,'234',11,'Hūd','هُود',5,6,111,'وَإِنَّ كُلّٗا لَّمَا لَيُوَفِّيَنَّهُمۡ رَبُّكَ أَعۡمَٰلَهُمۡۚ إِنَّهُۥ بِمَا يَعۡمَلُونَ خَبِيرٞ ١١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1589,12,'234',11,'Hūd','هُود',6,7,112,'فَاَسۡتَقِمۡ كَمَآ أُمِرۡتَ وَمَن تَابَ مَعَكَ وَلَا تَطۡغَوۡاْۚ إِنَّهُۥ بِمَا تَعۡمَلُونَ بَصِيرٞ ١١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1590,12,'234',11,'Hūd','هُود',7,9,113,'وَلَا تَرۡكَنُوٓاْ إِلَى اَ۬لَّذِينَ ظَلَمُواْ فَتَمَسَّكُمُ اُ۬لنَّارُ وَمَا لَكُم مِّن دُونِ اِ۬للَّهِ مِنۡ أَوۡلِيَآءَ ثُمَّ لَا تُنصَرُونَ ١١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1591,12,'234',11,'Hūd','هُود',9,11,114,'وَأَقِمِ اِ۬لصَّلَوٰةَ طَرَفَيِ اِ۬لنَّه۪ارِ وَزُلَفٗا مِّنَ اَ۬لَّيۡلِۚ إِنَّ اَ۬لۡحَسَنَٰتِ يُذۡهِبۡنَ اَ۬لسَّيِّـَٔاتِۚ ذَٰلِكَ ذِكۡر۪يٰ لِلذَّٰكِرِينَ ١١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1592,12,'234',11,'Hūd','هُود',11,11,115,'وَاَصۡبِرۡ فَإِنَّ اَ۬للَّهَ لَا يُضِيعُ أَجۡرَ اَ۬لۡمُحۡسِنِينَ ١١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1593,12,'234',11,'Hūd','هُود',12,14,116,'فَلَوۡلَا كَانَ مِنَ اَ۬لۡقُرُونِ مِن قَبۡلِكُمۡ أُوْلُواْ بَقِيَّةٖ يَنۡهَوۡنَ عَنِ اِ۬لۡفَسَادِ فِي اِ۬لۡأَرۡضِ إِلَّا قَلِيلٗا مِّمَّنۡ أَنجَيۡنَا مِنۡهُمۡۗ وَاَتَّبَعَ اَ۬لَّذِينَ ظَلَمُواْ مَآ أُتۡرِفُواْ فِيهِ وَكَانُواْ مُجۡرِمِينَ ١١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1594,12,'234',11,'Hūd','هُود',14,15,117,'وَمَا كَانَ رَبُّكَ لِيُهۡلِكَ اَ۬لۡقُر۪يٰ بِظُلۡمٖ وَأَهۡلُهَا مُصۡلِحُونَ ١١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1595,12,'235',11,'Hūd','هُود',1,3,118,'وَلَوۡ شَآءَ رَبُّكَ لَجَعَلَ اَ۬لنَّاسَ أُمَّةٗ وَٰحِدَةٗۖ وَلَا يَزَالُونَ مُخۡتَلِفِينَ إِلَّا مَن رَّحِمَ رَبُّكَۚ وَلِذَٰلِكَ خَلَقَهُمۡۗ وَتَمَّتۡ كَلِمَةُ رَبِّكَ لَأَمۡلَأَنَّ جَهَنَّمَ مِنَ اَ۬لۡجِنَّةِ وَاَلنّ۪اسِ أَجۡمَعِينَ ١١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1596,12,'235',11,'Hūd','هُود',3,5,119,'۞ وَكُلّٗا نَّقُصُّ عَلَيۡكَ مِنۡ أَنۢبَآءِ اِ۬لرُّسُلِ مَا نُثَبِّتُ بِهِۦ فُؤَادَكَۚ وَجَآءَكَ فِي هَٰذِهِ اِ۬لۡحَقُّ وَمَوۡعِظَةٞ وَذِكۡر۪يٰ لِلۡمُؤۡمِنِينَ ١١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1597,12,'235',11,'Hūd','هُود',5,6,120,'وَقُل لِّلَّذِينَ لَا يُؤۡمِنُونَ اَ۪عۡمَلُواْ عَلَىٰ مَكَانَتِكُمۡ إِنَّا عَٰمِلُونَ ١٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1598,12,'235',11,'Hūd','هُود',6,6,121,'وَاَنتَظِرُوٓاْ إِنَّا مُنتَظِرُونَ ١٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1599,12,'235',11,'Hūd','هُود',7,8,122,'وَلِلَّهِ غَيۡبُ اُ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِ وَإِلَيۡهِ يَرۡجِعُ اُ۬لۡأَمۡرُ كُلُّهُۥ فَاَعۡبُدۡهُ وَتَوَكَّلۡ عَلَيۡهِۚ وَمَا رَبُّكَ بِغَٰفِلٍ عَمَّا يَعۡمَلُونَ ١٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1600,12,'235',12,'Yūsuf','يُوسُف',11,11,1,'الٓر۪ۚ تِلۡكَ ءَايَٰتُ اُ۬لۡكِتَٰبِ اِ۬لۡمُبِينِ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1601,12,'235',12,'Yūsuf','يُوسُف',11,12,2,'إِنَّآ أَنزَلۡنَٰهُ قُرۡءَٰنًا عَرَبِيّٗا لَّعَلَّكُمۡ تَعۡقِلُونَ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1602,12,'235',12,'Yūsuf','يُوسُف',12,14,3,'نَحۡنُ نَقُصُّ عَلَيۡكَ أَحۡسَنَ اَ۬لۡقَصَصِ بِمَآ أَوۡحَيۡنَآ إِلَيۡكَ هَٰذَا اَ۬لۡقُرۡءَانَ وَإِن كُنتَ مِن قَبۡلِهِۦ لَمِنَ اَ۬لۡغَٰفِلِينَ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1603,12,'235',12,'Yūsuf','يُوسُف',14,15,4,'إِذۡ قَالَ يُوسُفُ لِأَبِيهِ يَٰٓأَبَتِ إِنِّي رَأَيۡتُ أَحَدَ عَشَرَ كَوۡكَبٗا وَاَلشَّمۡسَ وَاَلۡقَمَرَ رَأَيۡتُهُمۡ لِي سَٰجِدِينَ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1604,12,'236',12,'Yūsuf','يُوسُف',1,2,5,'قَالَ يَٰبُنَيِّ لَا تَقۡصُصۡ رُءۡيۭاكَ عَلَىٰٓ إِخۡوَتِكَ فَيَكِيدُواْ لَكَ كَيۡدًاۖ إِنَّ اَ۬لشَّيۡطَٰنَ لِلۡإِنسَٰنِ عَدُوّٞ مُّبِينٞ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1605,12,'236',12,'Yūsuf','يُوسُف',2,5,6,'وَكَذَٰلِكَ يَجۡتَبِيكَ رَبُّكَ وَيُعَلِّمُكَ مِن تَأۡوِيلِ اِ۬لۡأَحَادِيثِ وَيُتِمُّ نِعۡمَتَهُۥ عَلَيۡكَ وَعَلَىٰٓ ءَالِ يَعۡقُوبَ كَمَآ أَتَمَّهَا عَلَىٰٓ أَبَوَيۡكَ مِن قَبۡلُ إِبۡرَٰهِيمَ وَإِسۡحَٰقَۚ إِنَّ رَبَّكَ عَلِيمٌ حَكِيمٞ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1606,12,'236',12,'Yūsuf','يُوسُف',5,6,7,'۞ لَّقَدۡ كَانَ فِي يُوسُفَ وَإِخۡوَتِهِۦٓ ءَايَٰتٞ لِّلسَّآئِلِينَ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1607,12,'236',12,'Yūsuf','يُوسُف',6,7,8,'إِذۡ قَالُواْ لَيُوسُفُ وَأَخُوهُ أَحَبُّ إِلَىٰٓ أَبِينَا مِنَّا وَنَحۡنُ عُصۡبَةٌ إِنَّ أَبَانَا لَفِي ضَلَٰلٖ مُّبِينٍ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1608,12,'236',12,'Yūsuf','يُوسُف',8,9,9,'اِ۟قۡتُلُواْ يُوسُفَ أَوِ اِ۪طۡرَحُوهُ أَرۡضٗا يَخۡلُ لَكُمۡ وَجۡهُ أَبِيكُمۡ وَتَكُونُواْ مِنۢ بَعۡدِهِۦ قَوۡمٗا صَٰلِحِينَ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1609,12,'236',12,'Yūsuf','يُوسُف',9,11,10,'قَالَ قَآئِلٞ مِّنۡهُمۡ لَا تَقۡتُلُواْ يُوسُفَ وَأَلۡقُوهُ فِي غَيَٰبَتِ اِ۬لۡجُبِّ يَلۡتَقِطۡهُ بَعۡضُ اُ۬لسَّيَّارَةِ إِن كُنتُمۡ فَٰعِلِينَ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1610,12,'236',12,'Yūsuf','يُوسُف',11,12,11,'قَالُواْ يَٰٓأَبَانَا مَا لَكَ لَا تَأۡمَ۬نَّا عَلَىٰ يُوسُفَ وَإِنَّا لَهُۥ لَنَٰصِحُونَ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1611,12,'236',12,'Yūsuf','يُوسُف',12,13,12,'أَرۡسِلۡهُ مَعَنَا غَدٗا نَّرۡتَعۡ وَنَلۡعَبۡ وَإِنَّا لَهُۥ لَحَٰفِظُونَ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1612,12,'236',12,'Yūsuf','يُوسُف',13,14,13,'قَالَ إِنِّي لَيَحۡزُنُنِيٓ أَن تَذۡهَبُواْ بِهِۦ وَأَخَافُ أَن يَأۡكُلَهُ اُ۬لذِّئۡبُ وَأَنتُمۡ عَنۡهُ غَٰفِلُونَ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1613,12,'236',12,'Yūsuf','يُوسُف',14,15,14,'قَالُواْ لَئِنۡ أَكَلَهُ اُ۬لذِّئۡبُ وَنَحۡنُ عُصۡبَةٌ إِنَّآ إِذٗا لَّخَٰسِرُونَ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1614,12,'237',12,'Yūsuf','يُوسُف',1,2,15,'فَلَمَّا ذَهَبُواْ بِهِۦ وَأَجۡمَعُوٓاْ أَن يَجۡعَلُوهُ فِي غَيَٰبَتِ اِ۬لۡجُبِّۚ وَأَوۡحَيۡنَآ إِلَيۡهِ لَتُنَبِّئَنَّهُم بِأَمۡرِهِمۡ هَٰذَا وَهُمۡ لَا يَشۡعُرُونَ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1615,12,'237',12,'Yūsuf','يُوسُف',2,3,16,'وَجَآءُوٓ أَبَاهُمۡ عِشَآءٗ يَبۡكُونَ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1616,12,'237',12,'Yūsuf','يُوسُف',3,5,17,'قَالُواْ يَٰٓأَبَانَآ إِنَّا ذَهَبۡنَا نَسۡتَبِقُ وَتَرَكۡنَا يُوسُفَ عِندَ مَتَٰعِنَا فَأَكَلَهُ اُ۬لذِّئۡبُۖ وَمَآ أَنتَ بِمُؤۡمِنٖ لَّنَا وَلَوۡ كُنَّا صَٰدِقِينَ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1617,12,'237',12,'Yūsuf','يُوسُف',5,7,18,'وَجَآءُو عَلَىٰ قَمِيصِهِۦ بِدَمٖ كَذِبٖۚ قَالَ بَلۡ سَوَّلَتۡ لَكُمۡ أَنفُسُكُمۡ أَمۡرٗاۖ فَصَبۡرٞ جَمِيلٞۖ وَاَللَّهُ اُ۬لۡمُسۡتَعَانُ عَلَىٰ مَا تَصِفُونَ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1618,12,'237',12,'Yūsuf','يُوسُف',7,9,19,'وَجَآءَت سَّيَّارَةٞ فَأَرۡسَلُواْ وَارِدَهُمۡ فَأَدۡلَىٰ دَلۡوَهُۥۖ قَالَ يَٰبُشۡرَٰيَ هَٰذَا غُلَٰمٞۚ وَأَسَرُّوهُ بِضَٰعَةٗۚ وَاَللَّهُ عَلِيمُۢ بِمَا يَعۡمَلُونَ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1619,12,'237',12,'Yūsuf','يُوسُف',9,10,20,'۞ وَشَرَوۡهُ بِثَمَنِۢ بَخۡسٖ دَرَٰهِمَ مَعۡدُودَةٖ وَكَانُواْ فِيهِ مِنَ اَ۬لزَّٰهِدِينَ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1620,12,'237',12,'Yūsuf','يُوسُف',10,14,21,'وَقَالَ اَ۬لَّذِي اِ۪شۡتَر۪ىٰهُ مِن مِّصۡرَ لِاِمۡرَأَتِهِۦٓ أَكۡرِمِي مَثۡوَىٰهُ عَسَىٰٓ أَن يَنفَعَنَآ أَوۡ نَتَّخِذَهُۥ وَلَدٗاۚ وَكَذَٰلِكَ مَكَّنَّا لِيُوسُفَ فِي اِ۬لۡأَرۡضِ وَلِنُعَلِّمَهُۥ مِن تَأۡوِيلِ اِ۬لۡأَحَادِيثِۚ وَاَللَّهُ غَالِبٌ عَلَىٰٓ أَمۡرِهِۦ وَلَٰكِنَّ أَكۡثَرَ اَ۬لنّ۪اسِ لَا يَعۡلَمُونَ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1621,12,'237',12,'Yūsuf','يُوسُف',14,15,22,'وَلَمَّا بَلَغَ أَشُدَّهُۥٓ ءَاتَيۡنَٰهُ حُكۡمٗا وَعِلۡمٗاۚ وَكَذَٰلِكَ نَجۡزِي اِ۬لۡمُحۡسِنِينَ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1622,12,'238',12,'Yūsuf','يُوسُف',1,3,23,'وَرَٰوَدَتۡهُ اُ۬لَّتِي هُوَ فِي بَيۡتِهَا عَن نَّفۡسِهِۦ وَغَلَّقَتِ اِ۬لۡأَبۡوَٰبَ وَقَالَتۡ هَيۡتَ لَكَۚ قَالَ مَعَاذَ اَ۬للَّهِۖ إِنَّهُۥ رَبِّيَ أَحۡسَنَ مَثۡوَايَۖ إِنَّهُۥ لَا يُفۡلِحُ اُ۬لظَّٰلِمُونَ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1623,12,'238',12,'Yūsuf','يُوسُف',3,5,24,'وَلَقَدۡ هَمَّتۡ بِهِۦۖ وَهَمَّ بِهَا لَوۡلَآ أَن رَّء۪ا بُرۡهَٰنَ رَبِّهِۦۚ كَذَٰلِكَ لِنَصۡرِفَ عَنۡهُ اُ۬لسُّوٓءَ وَاَلۡفَحۡشَآءَۚ ا۪نَّهُۥ مِنۡ عِبَادِنَا اَ۬لۡمُخۡلِصِينَ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1624,12,'238',12,'Yūsuf','يُوسُف',5,8,25,'وَاَسۡتَبَقَا اَ۬لۡبَابَ وَقَدَّتۡ قَمِيصَهُۥ مِن دُبُرٖ وَأَلۡفَيَا سَيِّدَهَا لَدَا اَ۬لۡبَابِۚ قَالَتۡ مَا جَزَآءُ مَنۡ أَرَادَ بِأَهۡلِكَ سُوٓءًا إِلَّآ أَن يُسۡجَنَ أَوۡ عَذَابٌ أَلِيمٞ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1625,12,'238',12,'Yūsuf','يُوسُف',8,10,26,'قَالَ هِيَ رَٰوَدَتۡنِي عَن نَّفۡسِيۚ وَشَهِدَ شَاهِدٞ مِّنۡ أَهۡلِهَآ إِن كَانَ قَمِيصُهُۥ قُدَّ مِن قُبُلٖ فَصَدَقَتۡ وَهۡوَ مِنَ اَ۬لۡكَٰذِبِينَ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1626,12,'238',12,'Yūsuf','يُوسُف',10,11,27,'وَإِن كَانَ قَمِيصُهُۥ قُدَّ مِن دُبُرٖ فَكَذَبَتۡ وَهۡوَ مِنَ اَ۬لصَّٰدِقِينَ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1627,12,'238',12,'Yūsuf','يُوسُف',11,12,28,'فَلَمَّا رَء۪ا قَمِيصَهُۥ قُدَّ مِن دُبُرٖ قَالَ إِنَّهُۥ مِن كَيۡدِكُنَّۖ إِنَّ كَيۡدَكُنَّ عَظِيمٞ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1628,12,'238',12,'Yūsuf','يُوسُف',12,13,29,'يُوسُفُ أَعۡرِضۡ عَنۡ هَٰذَاۚ وَاَسۡتَغۡفِرِي لِذَنۢبِكِۖ إِنَّكِ كُنتِ مِنَ اَ۬لۡخَاطِـِٔينَ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1629,12,'238',12,'Yūsuf','يُوسُف',14,15,30,'وَقَالَ نِسۡوَةٞ فِي اِ۬لۡمَدِينَةِ اِ۪مۡرَأَتُ اُ۬لۡعَزِيزِ تُرَٰوِدُ فَتَىٰهَا عَن نَّفۡسِهِۦۖ قَد شَّغَفَهَا حُبًّاۖ إِنَّا لَنَر۪ىٰهَا فِي ضَلَٰلٖ مُّبِينٖ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1630,12,'239',12,'Yūsuf','يُوسُف',1,4,31,'فَلَمَّا سَمِعَتۡ بِمَكۡرِهِنَّ أَرۡسَلَتۡ إِلَيۡهِنَّ وَأَعۡتَدَتۡ لَهُنَّ مُتَّكَـٔٗا وَءَاتَتۡ كُلَّ وَٰحِدَةٖ مِّنۡهُنَّ سِكِّينٗا وَقَالَتِ اِ۟خۡرُجۡ عَلَيۡهِنَّۖ فَلَمَّا رَأَيۡنَهُۥٓ أَكۡبَرۡنَهُۥ وَقَطَّعۡنَ أَيۡدِيَهُنَّ وَقُلۡنَ حَٰشَٰ لِلَّهِ مَا هَٰذَا بَشَرًا إِنۡ هَٰذَآ إِلَّا مَلَكٞ كَرِيمٞ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1631,12,'239',12,'Yūsuf','يُوسُف',4,6,32,'۞ قَالَتۡ فَذَٰلِكُنَّ اَ۬لَّذِي لُمۡتُنَّنِي فِيهِۖ وَلَقَدۡ رَٰوَدتُّهُۥ عَن نَّفۡسِهِۦ فَاَسۡتَعۡصَمَۖ وَلَئِن لَّمۡ يَفۡعَلۡ مَآ ءَامُرُهُۥ لَيُسۡجَنَنَّ وَلَيَكُونٗا مِّنَ اَ۬لصَّٰغِرِينَ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1632,12,'239',12,'Yūsuf','يُوسُف',6,7,33,'قَالَ رَبِّ اِ۬لسِّجۡنُ أَحَبُّ إِلَيَّ مِمَّا يَدۡعُونَنِيٓ إِلَيۡهِۖ وَإِلَّا تَصۡرِفۡ عَنِّي كَيۡدَهُنَّ أَصۡبُ إِلَيۡهِنَّ وَأَكُن مِّنَ اَ۬لۡجَٰهِلِينَ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1633,12,'239',12,'Yūsuf','يُوسُف',8,9,34,'فَاَسۡتَجَابَ لَهُۥ رَبُّهُۥ فَصَرَفَ عَنۡهُ كَيۡدَهُنَّۚ إِنَّهُۥ هُوَ اَ۬لسَّمِيعُ اُ۬لۡعَلِيمُ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1634,12,'239',12,'Yūsuf','يُوسُف',9,10,35,'ثُمَّ بَدَا لَهُم مِّنۢ بَعۡدِ مَا رَأَوُاْ اُ۬لۡأٓيَٰتِ لَيَسۡجُنُنَّهُۥ حَتَّىٰ حِينٖ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1635,12,'239',12,'Yūsuf','يُوسُف',10,13,36,'وَدَخَلَ مَعَهُ اُ۬لسِّجۡنَ فَتَيَانِۖ قَالَ أَحَدُهُمَآ إِنِّيَ أَر۪ىٰنِيَ أَعۡصِرُ خَمۡرٗاۖ وَقَالَ اَ۬لۡأٓخَرُ إِنِّيَ أَر۪ىٰنِيَ أَحۡمِلُ فَوۡقَ رَأۡسِي خُبۡزٗا تَأۡكُلُ اُ۬لطَّيۡرُ مِنۡهُۖ نَبِّئۡنَا بِتَأۡوِيلِهِۦٓۖ إِنَّا نَر۪ىٰكَ مِنَ اَ۬لۡمُحۡسِنِينَ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1636,12,'239',12,'Yūsuf','يُوسُف',13,15,37,'قَالَ لَا يَأۡتِيكُمَا طَعَامٞ تُرۡزَقَانِهِۦٓ إِلَّا نَبَّأۡتُكُمَا بِتَأۡوِيلِهِۦ قَبۡلَ أَن يَأۡتِيَكُمَاۚ ذَٰلِكُمَا مِمَّا عَلَّمَنِي رَبِّيَۚ إِنِّي تَرَكۡتُ مِلَّةَ قَوۡمٖ لَّا يُؤۡمِنُونَ بِاللَّهِ وَهُم بِالۡأٓخِرَةِ هُمۡ كَٰفِرُونَ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1637,12,'240',12,'Yūsuf','يُوسُف',1,3,38,'وَاَتَّبَعۡتُ مِلَّةَ ءَابَآءِيَ إِبۡرَٰهِيمَ وَإِسۡحَٰقَ وَيَعۡقُوبَۚ مَا كَانَ لَنَآ أَن نُّشۡرِكَ بِاللَّهِ مِن شَيۡءٖۚ ذَٰلِكَ مِن فَضۡلِ اِ۬للَّهِ عَلَيۡنَا وَعَلَى اَ۬لنّ۪اسِ وَلَٰكِنَّ أَكۡثَرَ اَ۬لنّ۪اسِ لَا يَشۡكُرُونَ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1638,12,'240',12,'Yūsuf','يُوسُف',3,4,39,'يَٰصَٰحِبَيِ اِ۬لسِّجۡنِ ءَٰا۬رۡبَابٞ مُّتَفَرِّقُونَ خَيۡرٌ أَمِ اِ۬للَّهُ اُ۬لۡوَٰحِدُ اُ۬لۡقَهَّارُ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1639,12,'240',12,'Yūsuf','يُوسُف',5,8,40,'مَا تَعۡبُدُونَ مِن دُونِهِۦٓ إِلَّآ أَسۡمَآءٗ سَمَّيۡتُمُوهَآ أَنتُمۡ وَءَابَآؤُكُم مَّآ أَنزَلَ اَ۬للَّهُ بِهَا مِن سُلۡطَٰنٍۚ إِنِ اِ۬لۡحُكۡمُ إِلَّا لِلَّهِۚ أَمَرَ أَلَّا تَعۡبُدُوٓاْ إِلَّآ إِيَّاهُۚ ذَٰلِكَ اَ۬لدِّينُ اُ۬لۡقَيِّمُ وَلَٰكِنَّ أَكۡثَرَ اَ۬لنّ۪اسِ لَا يَعۡلَمُونَ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1640,12,'240',12,'Yūsuf','يُوسُف',8,10,41,'۞ يَٰصَٰحِبَيِ اِ۬لسِّجۡنِ أَمَّآ أَحَدُكُمَا فَيَسۡقِي رَبَّهُۥ خَمۡرٗاۖ وَأَمَّا اَ۬لۡأٓخَرُ فَيُصۡلَبُ فَتَأۡكُلُ اُ۬لطَّيۡرُ مِن رَّأۡسِهِۦۚ قُضِيَ اَ۬لۡأَمۡرُ اُ۬لَّذِي فِيهِ تَسۡتَفۡتِيَانِ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1641,12,'240',12,'Yūsuf','يُوسُف',10,12,42,'وَقَالَ لِلَّذِي ظَنَّ أَنَّهُۥ نَاجٖ مِّنۡهُمَا اَ۟ذۡكُرۡنِي عِندَ رَبِّكَ فَأَنسَىٰهُ اُ۬لشَّيۡطَٰنُ ذِكۡرَ رَبِّهِۦ فَلَبِثَ فِي اِ۬لسِّجۡنِ بِضۡعَ سِنِينَ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1642,12,'240',12,'Yūsuf','يُوسُف',13,15,43,'وَقَالَ اَ۬لۡمَلِكُ إِنِّيَ أَر۪يٰ سَبۡعَ بَقَرَٰتٖ سِمَانٖ يَأۡكُلُهُنَّ سَبۡعٌ عِجَافٞ وَسَبۡعَ سُنۢبُلَٰتٍ خُضۡرٖ وَأُخَرَ يَابِسَٰتٖۖ يَٰٓأَيُّهَا اَ۬لۡمَلَأُ اَ۬فۡتُونِي فِي رُءۡيٰۭيَ إِن كُنتُمۡ لِلرُّءۡيۭا تَعۡبُرُونَ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1643,12,'241',12,'Yūsuf','يُوسُف',1,1,44,'قَالُوٓاْ أَضۡغَٰثُ أَحۡلَٰمٖۖ وَمَا نَحۡنُ بِتَأۡوِيلِ اِ۬لۡأَحۡلَٰمِ بِعَٰلِمِينَ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1644,12,'241',12,'Yūsuf','يُوسُف',2,3,45,'وَقَالَ اَ۬لَّذِي نَجَا مِنۡهُمَا وَاَدَّكَرَ بَعۡدَ أُمَّةٍ أَنَا۠ أُنَبِّئُكُم بِتَأۡوِيلِهِۦ فَأَرۡسِلُونِ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1645,12,'241',12,'Yūsuf','يُوسُف',3,5,46,'يُوسُفُ أَيُّهَا اَ۬لصِّدِّيقُ أَفۡتِنَا فِي سَبۡعِ بَقَرَٰتٖ سِمَانٖ يَأۡكُلُهُنَّ سَبۡعٌ عِجَافٞ وَسَبۡعِ سُنۢبُلَٰتٍ خُضۡرٖ وَأُخَرَ يَابِسَٰتٖ لَّعَلِّيَ أَرۡجِعُ إِلَى اَ۬لنّ۪اسِ لَعَلَّهُمۡ يَعۡلَمُونَ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1646,12,'241',12,'Yūsuf','يُوسُف',5,7,47,'قَالَ تَزۡرَعُونَ سَبۡعَ سِنِينَ دَأۡبٗا فَمَا حَصَدتُّمۡ فَذَرُوهُ فِي سُنۢبُلِهِۦٓ إِلَّا قَلِيلٗا مِّمَّا تَأۡكُلُونَ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1647,12,'241',12,'Yūsuf','يُوسُف',7,8,48,'ثُمَّ يَأۡتِي مِنۢ بَعۡدِ ذَٰلِكَ سَبۡعٞ شِدَادٞ يَأۡكُلۡنَ مَا قَدَّمۡتُمۡ لَهُنَّ إِلَّا قَلِيلٗا مِّمَّا تُحۡصِنُونَ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1648,12,'241',12,'Yūsuf','يُوسُف',8,9,49,'ثُمَّ يَأۡتِي مِنۢ بَعۡدِ ذَٰلِكَ عَامٞ فِيهِ يُغَاثُ اُ۬لنَّاسُ وَفِيهِ يَعۡصِرُونَ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1649,12,'241',12,'Yūsuf','يُوسُف',9,11,50,'وَقَالَ اَ۬لۡمَلِكُ اُ۪ئۡتُونِي بِهِۦۖ فَلَمَّا جَآءَهُ اُ۬لرَّسُولُ قَالَ اَ۪رۡجِعۡ إِلَىٰ رَبِّكَ فَسۡـَٔلۡهُ مَا بَالُ اُ۬لنِّسۡوَةِ اِ۬لَّٰتِي قَطَّعۡنَ أَيۡدِيَهُنَّۚ إِنَّ رَبِّي بِكَيۡدِهِنَّ عَلِيمٞ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1650,12,'241',12,'Yūsuf','يُوسُف',12,14,51,'قَالَ مَا خَطۡبُكُنَّ إِذۡ رَٰوَدتُّنَّ يُوسُفَ عَن نَّفۡسِهِۦۚ قُلۡنَ حَٰشَٰ لِلَّهِ مَا عَلِمۡنَا عَلَيۡهِ مِن سُوٓءٖۚ قَالَتِ اِ۪مۡرَأَتُ اُ۬لۡعَزِيزِ اِ۬لۡـَٰٔنَ حَصۡحَصَ اَ۬لۡحَقُّ أَنَا۠ رَٰوَدتُّهُۥ عَن نَّفۡسِهِۦ وَإِنَّهُۥ لَمِنَ اَ۬لصَّٰدِقِينَ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1651,12,'241',12,'Yūsuf','يُوسُف',14,15,52,'ذَٰلِكَ لِيَعۡلَمَ أَنِّي لَمۡ أَخُنۡهُ بِالۡغَيۡبِ وَأَنَّ اَ۬للَّهَ لَا يَهۡدِي كَيۡدَ اَ۬لۡخَآئِنِينَ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1652,13,'242',12,'Yūsuf','يُوسُف',1,2,53,'۞ وَمَآ أُبَرِّئُ نَفۡسِيَۚ إِنَّ اَ۬لنَّفۡسَ لَأَمَّارَةُۢ بِالسُّوٓ إِلَّا مَا رَحِمَ رَبِّيَۚ إِنَّ رَبِّي غَفُورٞ رَّحِيمٞ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1653,13,'242',12,'Yūsuf','يُوسُف',2,3,54,'وَقَالَ اَ۬لۡمَلِكُ اُ۪ئۡتُونِي بِهِۦٓ أَسۡتَخۡلِصۡهُ لِنَفۡسِيۖ فَلَمَّا كَلَّمَهُۥ قَالَ إِنَّكَ اَ۬لۡيَوۡمَ لَدَيۡنَا مَكِينٌ أَمِينٞ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1654,13,'242',12,'Yūsuf','يُوسُف',4,4,55,'قَالَ اَ۪جۡعَلۡنِي عَلَىٰ خَزَآئِنِ اِ۬لۡأَرۡضِۖ إِنِّي حَفِيظٌ عَلِيمٞ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1655,13,'242',12,'Yūsuf','يُوسُف',4,6,56,'وَكَذَٰلِكَ مَكَّنَّا لِيُوسُفَ فِي اِ۬لۡأَرۡضِ يَتَبَوَّأُ مِنۡهَا حَيۡثُ يَشَآءُۚ نُصِيبُ بِرَحۡمَتِنَا مَن نَّشَآءُۖ وَلَا نُضِيعُ أَجۡرَ اَ۬لۡمُحۡسِنِينَ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1656,13,'242',12,'Yūsuf','يُوسُف',6,7,57,'وَلَأَجۡرُ اُ۬لۡأٓخِرَةِ خَيۡرٞ لِّلَّذِينَ ءَامَنُواْ وَكَانُواْ يَتَّقُونَ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1657,13,'242',12,'Yūsuf','يُوسُف',7,8,58,'وَجَآءَ ا۪خۡوَةُ يُوسُفَ فَدَخَلُواْ عَلَيۡهِ فَعَرَفَهُمۡ وَهُمۡ لَهُۥ مُنكِرُونَ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1658,13,'242',12,'Yūsuf','يُوسُف',9,10,59,'وَلَمَّا جَهَّزَهُم بِجَهَازِهِمۡ قَالَ اَ۪ئۡتُونِي بِأَخٖ لَّكُم مِّنۡ أَبِيكُمۡۚ أَلَا تَرَوۡنَ أَنِّيٓ أُوفِي اِ۬لۡكَيۡلَ وَأَنَا۠ خَيۡرُ اُ۬لۡمُنزِلِينَ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1659,13,'242',12,'Yūsuf','يُوسُف',10,11,60,'فَإِن لَّمۡ تَأۡتُونِي بِهِۦ فَلَا كَيۡلَ لَكُمۡ عِندِي وَلَا تَقۡرَبُونِ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1660,13,'242',12,'Yūsuf','يُوسُف',11,12,61,'قَالُواْ سَنُرَٰوِدُ عَنۡهُ أَبَاهُ وَإِنَّا لَفَٰعِلُونَ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1661,13,'242',12,'Yūsuf','يُوسُف',12,13,62,'وَقَالَ لِفِتۡيَتِهِ اِ۪جۡعَلُواْ بِضَٰعَتَهُمۡ فِي رِحَالِهِمۡ لَعَلَّهُمۡ يَعۡرِفُونَهَآ إِذَا اَ۪نقَلَبُوٓاْ إِلَىٰٓ أَهۡلِهِمۡ لَعَلَّهُمۡ يَرۡجِعُونَ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1662,13,'242',12,'Yūsuf','يُوسُف',14,15,63,'فَلَمَّا رَجَعُوٓاْ إِلَىٰٓ أَبِيهِمۡ قَالُواْ يَٰٓأَبَانَا مُنِعَ مِنَّا اَ۬لۡكَيۡلُ فَأَرۡسِلۡ مَعَنَآ أَخَانَا نَكۡتَلۡ وَإِنَّا لَهُۥ لَحَٰفِظُونَ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1663,13,'243',12,'Yūsuf','يُوسُف',1,2,64,'قَالَ هَلۡ ءَامَنُكُمۡ عَلَيۡهِ إِلَّا كَمَآ أَمِنتُكُمۡ عَلَىٰٓ أَخِيهِ مِن قَبۡلُۖ فَاَللَّهُ خَيۡرٌ حِفۡظٗاۖ وَهۡوَ أَرۡحَمُ اُ۬لرَّٰحِمِينَ ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1664,13,'243',12,'Yūsuf','يُوسُف',2,5,65,'۞ وَلَمَّا فَتَحُواْ مَتَٰعَهُمۡ وَجَدُواْ بِضَٰعَتَهُمۡ رُدَّتۡ إِلَيۡهِمۡۖ قَالُواْ يَٰٓأَبَانَا مَا نَبۡغِيۖ هَٰذِهِۦ بِضَٰعَتُنَا رُدَّتۡ إِلَيۡنَاۖ وَنَمِيرُ أَهۡلَنَا وَنَحۡفَظُ أَخَانَا وَنَزۡدَادُ كَيۡلَ بَعِيرٖۖ ذَٰلِكَ كَيۡلٞ يَسِيرٞ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1665,13,'243',12,'Yūsuf','يُوسُف',5,8,66,'قَالَ لَنۡ أُرۡسِلَهُۥ مَعَكُمۡ حَتَّىٰ تُؤۡتُونِۦ مَوۡثِقٗا مِّنَ اَ۬للَّهِ لَتَأۡتُنَّنِي بِهِۦٓ إِلَّآ أَن يُحَاطَ بِكُمۡۖ فَلَمَّآ ءَاتَوۡهُ مَوۡثِقَهُمۡ قَالَ اَ۬للَّهُ عَلَىٰ مَا نَقُولُ وَكِيلٞ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1666,13,'243',12,'Yūsuf','يُوسُف',8,11,67,'وَقَالَ يَٰبَنِيَّ لَا تَدۡخُلُواْ مِنۢ بَابٖ وَٰحِدٖ وَاَدۡخُلُواْ مِنۡ أَبۡوَٰبٖ مُّتَفَرِّقَةٖۖ وَمَآ أُغۡنِي عَنكُم مِّنَ اَ۬للَّهِ مِن شَيۡءٍۖ إِنِ اِ۬لۡحُكۡمُ إِلَّا لِلَّهِۖ عَلَيۡهِ تَوَكَّلۡتُۖ وَعَلَيۡهِ فَلۡيَتَوَكَّلِ اِ۬لۡمُتَوَكِّلُونَ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1667,13,'243',12,'Yūsuf','يُوسُف',11,14,68,'وَلَمَّا دَخَلُواْ مِنۡ حَيۡثُ أَمَرَهُمۡ أَبُوهُم مَّا كَانَ يُغۡنِي عَنۡهُم مِّنَ اَ۬للَّهِ مِن شَيۡءٍ إِلَّا حَاجَةٗ فِي نَفۡسِ يَعۡقُوبَ قَضَىٰهَاۚ وَإِنَّهُۥ لَذُو عِلۡمٖ لِّمَا عَلَّمۡنَٰهُ وَلَٰكِنَّ أَكۡثَرَ اَ۬لنّ۪اسِ لَا يَعۡلَمُونَ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1668,13,'243',12,'Yūsuf','يُوسُف',14,15,69,'وَلَمَّا دَخَلُواْ عَلَىٰ يُوسُفَ ءَاوَىٰٓ إِلَيۡهِ أَخَاهُۖ قَالَ إِنِّيَ أَنَا۠ أَخُوكَ فَلَا تَبۡتَئِسۡ بِمَا كَانُواْ يَعۡمَلُونَ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1669,13,'244',12,'Yūsuf','يُوسُف',1,2,70,'فَلَمَّا جَهَّزَهُم بِجَهَازِهِمۡ جَعَلَ اَ۬لسِّقَايَةَ فِي رَحۡلِ أَخِيهِ ثُمَّ أَذَّنَ مُؤَذِّنٌ أَيَّتُهَا اَ۬لۡعِيرُ إِنَّكُمۡ لَسَٰرِقُونَ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1670,13,'244',12,'Yūsuf','يُوسُف',2,3,71,'قَالُواْ وَأَقۡبَلُواْ عَلَيۡهِم مَّاذَا تَفۡقِدُونَ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1671,13,'244',12,'Yūsuf','يُوسُف',3,4,72,'قَالُواْ نَفۡقِدُ صُوَاعَ اَ۬لۡمَلِكِ وَلِمَن جَآءَ بِهِۦ حِمۡلُ بَعِيرٖ وَأَنَا۠ بِهِۦ زَعِيمٞ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1672,13,'244',12,'Yūsuf','يُوسُف',4,5,73,'قَالُواْ تَاَللَّهِ لَقَدۡ عَلِمۡتُم مَّا جِئۡنَا لِنُفۡسِدَ فِي اِ۬لۡأَرۡضِ وَمَا كُنَّا سَٰرِقِينَ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1673,13,'244',12,'Yūsuf','يُوسُف',6,6,74,'قَالُواْ فَمَا جَزَٰٓؤُهُۥٓ إِن كُنتُمۡ كَٰذِبِينَ ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1674,13,'244',12,'Yūsuf','يُوسُف',6,7,75,'قَالُواْ جَزَٰٓؤُهُۥ مَن وُجِدَ فِي رَحۡلِهِۦ فَهۡوَ جَزَٰٓؤُهُۥۚ كَذَٰلِكَ نَجۡزِي اِ۬لظَّٰلِمِينَ ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1675,13,'244',12,'Yūsuf','يُوسُف',8,11,76,'فَبَدَأَ بِأَوۡعِيَتِهِمۡ قَبۡلَ وِعَآءِ اَ۬خِيهِ ثُمَّ اَ۪سۡتَخۡرَجَهَا مِن وِعَآءِ اَ۬خِيهِۚ كَذَٰلِكَ كِدۡنَا لِيُوسُفَۖ مَا كَانَ لِيَأۡخُذَ أَخَاهُ فِي دِينِ اِ۬لۡمَلِكِ إِلَّآ أَن يَشَآءَ اَ۬للَّهُۚ نَرۡفَعُ دَرَجَٰتِ مَن نَّشَآءُۗ وَفَوۡقَ كُلِّ ذِي عِلۡمٍ عَلِيمٞ ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1676,13,'244',12,'Yūsuf','يُوسُف',11,14,77,'۞ قَالُوٓاْ إِن يَسۡرِقۡ فَقَد سَّرَقَ أَخٞ لَّهُۥ مِن قَبۡلُۚ فَأَسَرَّهَا يُوسُفُ فِي نَفۡسِهِۦ وَلَمۡ يُبۡدِهَا لَهُمۡۚ قَالَ أَنتُمۡ شَرّٞ مَّكَانٗاۖ وَاَللَّهُ أَعۡلَمُ بِمَا تَصِفُونَ ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1677,13,'244',12,'Yūsuf','يُوسُف',14,15,78,'قَالُواْ يَٰٓأَيُّهَا اَ۬لۡعَزِيزُ إِنَّ لَهُۥٓ أَبٗا شَيۡخٗا كَبِيرٗا فَخُذۡ أَحَدَنَا مَكَانَهُۥٓۖ إِنَّا نَر۪ىٰكَ مِنَ اَ۬لۡمُحۡسِنِينَ ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1678,13,'245',12,'Yūsuf','يُوسُف',1,2,79,'قَالَ مَعَاذَ اَ۬للَّهِ أَن نَّأۡخُذَ إِلَّا مَن وَجَدۡنَا مَتَٰعَنَا عِندَهُۥٓ إِنَّآ إِذٗا لَّظَٰلِمُونَ ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1679,13,'245',12,'Yūsuf','يُوسُف',2,5,80,'فَلَمَّا اَ۪سۡتَيۡـَٔسُواْ مِنۡهُ خَلَصُواْ نَجِيّٗاۖ قَالَ كَبِيرُهُمۡ أَلَمۡ تَعۡلَمُوٓاْ أَنَّ أَبَاكُمۡ قَدۡ أَخَذَ عَلَيۡكُم مَّوۡثِقٗا مِّنَ اَ۬للَّهِ وَمِن قَبۡلُ مَا فَرَّطتُمۡ فِي يُوسُفَۖ فَلَنۡ أَبۡرَحَ اَ۬لۡأَرۡضَ حَتَّىٰ يَأۡذَنَ لِيَ أَبِيَ أَوۡ يَحۡكُمَ اَ۬للَّهُ لِيۖ وَهۡوَ خَيۡرُ اُ۬لۡحَٰكِمِينَ ٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1680,13,'245',12,'Yūsuf','يُوسُف',6,7,81,'اَ۪رۡجِعُوٓاْ إِلَىٰٓ أَبِيكُمۡ فَقُولُواْ يَٰٓأَبَانَآ إِنَّ اَ۪بۡنَكَ سَرَقَ وَمَا شَهِدۡنَآ إِلَّا بِمَا عَلِمۡنَا وَمَا كُنَّا لِلۡغَيۡبِ حَٰفِظِينَ ٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1681,13,'245',12,'Yūsuf','يُوسُف',8,9,82,'وَسۡـَٔلِ اِ۬لۡقَرۡيَةَ اَ۬لَّتِي كُنَّا فِيهَا وَاَلۡعِيرَ اَ۬لَّتِيٓ أَقۡبَلۡنَا فِيهَاۖ وَإِنَّا لَصَٰدِقُونَ ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1682,13,'245',12,'Yūsuf','يُوسُف',9,11,83,'قَالَ بَلۡ سَوَّلَتۡ لَكُمۡ أَنفُسُكُمۡ أَمۡرٗاۖ فَصَبۡرٞ جَمِيلٌۖ عَسَى اَ۬للَّهُ أَن يَأۡتِيَنِي بِهِمۡ جَمِيعًاۚ إِنَّهُۥ هُوَ اَ۬لۡعَلِيمُ اُ۬لۡحَكِيمُ ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1683,13,'245',12,'Yūsuf','يُوسُف',11,12,84,'وَتَوَلَّىٰ عَنۡهُمۡ وَقَالَ يَٰٓأَسَفۭيٰ عَلَىٰ يُوسُفَ وَاَبۡيَضَّتۡ عَيۡنَاهُ مِنَ اَ۬لۡحُزۡنِ فَهۡوَ كَظِيمٞ ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1684,13,'245',12,'Yūsuf','يُوسُف',13,14,85,'قَالُواْ تَاَللَّهِ تَفۡتَؤُاْ تَذۡكُرُ يُوسُفَ حَتَّىٰ تَكُونَ حَرَضًا أَوۡ تَكُونَ مِنَ اَ۬لۡهَٰلِكِينَ ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1685,13,'245',12,'Yūsuf','يُوسُف',14,15,86,'قَالَ إِنَّمَآ أَشۡكُواْ بَثِّي وَحُزۡنِيَ إِلَى اَ۬للَّهِ وَأَعۡلَمُ مِنَ اَ۬للَّهِ مَا لَا تَعۡلَمُونَ ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1686,13,'246',12,'Yūsuf','يُوسُف',1,3,87,'يَٰبَنِيَّ اَ۪ذۡهَبُواْ فَتَحَسَّسُواْ مِن يُوسُفَ وَأَخِيهِ وَلَا تَاْيۡـَٔسُواْ مِن رَّوۡحِ اِ۬للَّهِۖ إِنَّهُۥ لَا يَاْيۡـَٔسُ مِن رَّوۡحِ اِ۬للَّهِ إِلَّا اَ۬لۡقَوۡمُ اُ۬لۡكَٰفِرُونَ ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1687,13,'246',12,'Yūsuf','يُوسُف',3,5,88,'۞ فَلَمَّا دَخَلُواْ عَلَيۡهِ قَالُواْ يَٰٓأَيُّهَا اَ۬لۡعَزِيزُ مَسَّنَا وَأَهۡلَنَا اَ۬لضُّرُّ وَجِئۡنَا بِبِضَٰعَةٖ مُّزۡجَىٰةٖ فَأَوۡفِ لَنَا اَ۬لۡكَيۡلَ وَتَصَدَّقۡ عَلَيۡنَآۖ إِنَّ اَ۬للَّهَ يَجۡزِي اِ۬لۡمُتَصَدِّقِينَ ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1688,13,'246',12,'Yūsuf','يُوسُف',6,7,89,'قَالَ هَلۡ عَلِمۡتُم مَّا فَعَلۡتُم بِيُوسُفَ وَأَخِيهِ إِذۡ أَنتُمۡ جَٰهِلُونَ ٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1689,13,'246',12,'Yūsuf','يُوسُف',7,9,90,'قَالُوٓاْ أَٰ۟نَّكَ لَأَنتَ يُوسُفُۖ قَالَ أَنَا۠ يُوسُفُ وَهَٰذَآ أَخِيۖ قَدۡ مَنَّ اَ۬للَّهُ عَلَيۡنَآۖ إِنَّهُۥ مَن يَتَّقِ وَيَصۡبِرۡ فَإِنَّ اَ۬للَّهَ لَا يُضِيعُ أَجۡرَ اَ۬لۡمُحۡسِنِينَ ٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1690,13,'246',12,'Yūsuf','يُوسُف',9,10,91,'قَالُواْ تَاَللَّهِ لَقَدۡ ءَاثَرَكَ اَ۬للَّهُ عَلَيۡنَا وَإِن كُنَّا لَخَٰطِـِٔينَ ٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1691,13,'246',12,'Yūsuf','يُوسُف',10,11,92,'قَالَ لَا تَثۡرِيبَ عَلَيۡكُمُ اُ۬لۡيَوۡمَۖ يَغۡفِرُ اُ۬للَّهُ لَكُمۡۖ وَهۡوَ أَرۡحَمُ اُ۬لرَّٰحِمِينَ ٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1692,13,'246',12,'Yūsuf','يُوسُف',12,13,93,'اَ۪ذۡهَبُواْ بِقَمِيصِي هَٰذَا فَأَلۡقُوهُ عَلَىٰ وَجۡهِ أَبِي يَأۡتِ بَصِيرٗا وَأۡتُونِي بِأَهۡلِكُمۡ أَجۡمَعِينَ ٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1693,13,'246',12,'Yūsuf','يُوسُف',13,15,94,'وَلَمَّا فَصَلَتِ اِ۬لۡعِيرُ قَالَ أَبُوهُمۡ إِنِّي لَأَجِدُ رِيحَ يُوسُفَۖ لَوۡلَآ أَن تُفَنِّدُونِ ٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1694,13,'246',12,'Yūsuf','يُوسُف',15,15,95,'قَالُواْ تَاَللَّهِ إِنَّكَ لَفِي ضَلَٰلِكَ اَ۬لۡقَدِيمِ ٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1695,13,'247',12,'Yūsuf','يُوسُف',1,2,96,'فَلَمَّآ أَن جَآءَ اَ۬لۡبَشِيرُ أَلۡقَىٰهُ عَلَىٰ وَجۡهِهِۦ فَاَرۡتَدَّ بَصِيرٗاۖ قَالَ أَلَمۡ أَقُل لَّكُمۡ إِنِّيَ أَعۡلَمُ مِنَ اَ۬للَّهِ مَا لَا تَعۡلَمُونَ ٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1696,13,'247',12,'Yūsuf','يُوسُف',2,3,97,'قَالُواْ يَٰٓأَبَانَا اَ۪سۡتَغۡفِر لَّنَا ذُنُوبَنَآ إِنَّا كُنَّا خَٰطِـِٔينَ ٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1697,13,'247',12,'Yūsuf','يُوسُف',3,4,98,'قَالَ سَوۡفَ أَسۡتَغۡفِرُ لَكُمۡ رَبِّيَۖ إِنَّهُۥ هُوَ اَ۬لۡغَفُورُ اُ۬لرَّحِيمُ ٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1698,13,'247',12,'Yūsuf','يُوسُف',4,6,99,'فَلَمَّا دَخَلُواْ عَلَىٰ يُوسُفَ ءَاوَىٰٓ إِلَيۡهِ أَبَوَيۡهِ وَقَالَ اَ۟دۡخُلُواْ مِصۡرَ إِن شَآءَ اَ۬للَّهُ ءَامِنِينَ ٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1699,13,'247',12,'Yūsuf','يُوسُف',6,10,100,'وَرَفَعَ أَبَوَيۡهِ عَلَى اَ۬لۡعَرۡشِ وَخَرُّواْ لَهُۥ سُجَّدٗاۖ وَقَالَ يَٰٓأَبَتِ هَٰذَا تَأۡوِيلُ رُءۡيٰۭيَ مِن قَبۡلُ قَد جَّعَلَهَا رَبِّي حَقّٗاۖ وَقَدۡ أَحۡسَنَ بِيَ إِذۡ أَخۡرَجَنِي مِنَ اَ۬لسِّجۡنِ وَجَآءَ بِكُم مِّنَ اَ۬لۡبَدۡوِ مِنۢ بَعۡدِ أَن نَّزَغَ اَ۬لشَّيۡطَٰنُ بَيۡنِي وَبَيۡنَ إِخۡوَتِيٓۚ إِنَّ رَبِّي لَطِيفٞ لِّمَا يَشَآءُۚ اِ۪نَّهُۥ هُوَ اَ۬لۡعَلِيمُ اُ۬لۡحَكِيمُ ١٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1700,13,'247',12,'Yūsuf','يُوسُف',10,13,101,'۞ رَبِّ قَدۡ ءَاتَيۡتَنِي مِنَ اَ۬لۡمُلۡكِ وَعَلَّمۡتَنِي مِن تَأۡوِيلِ اِ۬لۡأَحَادِيثِۚ فَاطِرَ اَ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِ أَنتَ وَلِيِّۦ فِي اِ۬لدُّنۡيۭا وَاَلۡأٓخِرَةِۖ تَوَفَّنِي مُسۡلِمٗا وَأَلۡحِقۡنِي بِالصَّٰلِحِينَ ١٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1701,13,'247',12,'Yūsuf','يُوسُف',13,15,102,'ذَٰلِكَ مِنۡ أَنۢبَآءِ اِ۬لۡغَيۡبِ نُوحِيهِ إِلَيۡكَۖ وَمَا كُنتَ لَدَيۡهِمۡ إِذۡ أَجۡمَعُوٓاْ أَمۡرَهُمۡ وَهُمۡ يَمۡكُرُونَ ١٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1702,13,'247',12,'Yūsuf','يُوسُف',15,15,103,'وَمَآ أَكۡثَرُ اُ۬لنّ۪اسِ وَلَوۡ حَرَصۡتَ بِمُؤۡمِنِينَ ١٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1703,13,'248',12,'Yūsuf','يُوسُف',1,1,104,'وَمَا تَسۡـَٔلُهُمۡ عَلَيۡهِ مِنۡ أَجۡرٍۚ إِنۡ هُوَ إِلَّا ذِكۡرٞ لِّلۡعَٰلَمِينَ ١٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1704,13,'248',12,'Yūsuf','يُوسُف',2,3,105,'وَكَأَيِّن مِّنۡ ءَايَةٖ فِي اِ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِ يَمُرُّونَ عَلَيۡهَا وَهُمۡ عَنۡهَا مُعۡرِضُونَ ١٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1705,13,'248',12,'Yūsuf','يُوسُف',3,4,106,'وَمَا يُؤۡمِنُ أَكۡثَرُهُم بِاللَّهِ إِلَّا وَهُم مُّشۡرِكُونَ ١٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1706,13,'248',12,'Yūsuf','يُوسُف',4,5,107,'أَفَأَمِنُوٓاْ أَن تَأۡتِيَهُمۡ غَٰشِيَةٞ مِّنۡ عَذَابِ اِ۬للَّهِ أَوۡ تَأۡتِيَهُمُ اُ۬لسَّاعَةُ بَغۡتَةٗ وَهُمۡ لَا يَشۡعُرُونَ ١٠٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1707,13,'248',12,'Yūsuf','يُوسُف',5,7,108,'قُلۡ هَٰذِهِۦ سَبِيلِيٓ أَدۡعُوٓاْ إِلَى اَ۬للَّهِۚ عَلَىٰ بَصِيرَةٍ أَنَا۠ وَمَنِ اِ۪تَّبَعَنِيۖ وَسُبۡحَٰنَ اَ۬للَّهِ وَمَآ أَنَا۠ مِنَ اَ۬لۡمُشۡرِكِينَ ١٠٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1708,13,'248',12,'Yūsuf','يُوسُف',7,10,109,'وَمَآ أَرۡسَلۡنَا مِن قَبۡلِكَ إِلَّا رِجَالٗا يُوحَىٰٓ إِلَيۡهِم مِّنۡ أَهۡلِ اِ۬لۡقُر۪يٰٓۗ أَفَلَمۡ يَسِيرُواْ فِي اِ۬لۡأَرۡضِ فَيَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ اُ۬لَّذِينَ مِن قَبۡلِهِمۡۗ وَلَدَارُ اُ۬لۡأٓخِرَةِ خَيۡرٞ لِّلَّذِينَ اَ۪تَّقَوۡاْۗ أَفَلَا يَعۡقِلُونَ ١٠٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1709,13,'248',12,'Yūsuf','يُوسُف',11,13,110,'حَتَّىٰٓ إِذَا اَ۪سۡتَيۡـَٔسَ اَ۬لرُّسُلُ وَظَنُّوٓاْ أَنَّهُمۡ قَدۡ كُذِّبُواْ جَآءَهُمۡ نَصۡرُنَا فَنُـۨجِي مَن نَّشَآءُۖ وَلَا يُرَدُّ بَأۡسُنَا عَنِ اِ۬لۡقَوۡمِ اِ۬لۡمُجۡرِمِينَ ١١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1710,13,'248',12,'Yūsuf','يُوسُف',13,15,111,'۞ لَقَدۡ كَانَ فِي قَصَصِهِمۡ عِبۡرَةٞ لِّأُوْلِي اِ۬لۡأَلۡبَٰبِۗ مَا كَانَ حَدِيثٗا يُفۡتَر۪يٰ وَلَٰكِن تَصۡدِيقَ اَ۬لَّذِي بَيۡنَ يَدَيۡهِ وَتَفۡصِيلَ كُلِّ شَيۡءٖ وَهُدٗى وَرَحۡمَةٗ لِّقَوۡمٖ يُؤۡمِنُونَ ١١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1711,13,'249',13,'Ar-Ra‘d','الرَّعد',3,4,1,'الٓمٓر۪ۚ تِلۡكَ ءَايَٰتُ اُ۬لۡكِتَٰبِۗ وَاَلَّذِيٓ أُنزِلَ إِلَيۡكَ مِن رَّبِّكَ اَ۬لۡحَقُّ وَلَٰكِنَّ أَكۡثَرَ اَ۬لنّ۪اسِ لَا يُؤۡمِنُونَ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1712,13,'249',13,'Ar-Ra‘d','الرَّعد',4,7,2,'اَ۬للَّهُ اُ۬لَّذِي رَفَعَ اَ۬لسَّمَٰوَٰتِ بِغَيۡرِ عَمَدٖ تَرَوۡنَهَاۖ ثُمَّ اَ۪سۡتَوَىٰ عَلَى اَ۬لۡعَرۡشِۖ وَسَخَّرَ اَ۬لشَّمۡسَ وَاَلۡقَمَرَۖ كُلّٞ يَجۡرِي لِأَجَلٖ مُّسَمّٗىۚ يُدَبِّرُ اُ۬لۡأَمۡرَ يُفَصِّلُ اُ۬لۡأٓيَٰتِ لَعَلَّكُم بِلِقَآءِ رَبِّكُمۡ تُوقِنُونَ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1713,13,'249',13,'Ar-Ra‘d','الرَّعد',7,9,3,'وَهۡوَ اَ۬لَّذِي مَدَّ اَ۬لۡأَرۡضَ وَجَعَلَ فِيهَا رَوَٰسِيَ وَأَنۡهَٰرٗاۖ وَمِن كُلِّ اِ۬لثَّمَرَٰتِ جَعَلَ فِيهَا زَوۡجَيۡنِ اِ۪ثۡنَيۡنِۖ يُغۡشِي اِ۬لَّيۡلَ اَ۬لنَّهَارَۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَتَفَكَّرُونَ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1714,13,'249',13,'Ar-Ra‘d','الرَّعد',9,12,4,'وَفِي اِ۬لۡأَرۡضِ قِطَعٞ مُّتَجَٰوِرَٰتٞ وَجَنَّٰتٞ مِّنۡ أَعۡنَٰبٖ وَزَرۡعٞ وَنَخِيلٞ صِنۡوَانٞ وَغَيۡرُ صِنۡوَانٖ تُسۡقَىٰ بِمَآءٖ وَٰحِدٖ وَنُفَضِّلُ بَعۡضَهَا عَلَىٰ بَعۡضٖ فِي اِ۬لۡأُكُلِۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَعۡقِلُونَ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1715,13,'249',13,'Ar-Ra‘d','الرَّعد',12,13,5,'وَإِن تَعۡجَب فَّعَجَبٞ قَوۡلُهُمۡ أَٰ۟ذَا كُنَّا تُرَٰبًا أَٰ۟نَّا لَفِي خَلۡقٖ جَدِيدٍ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1716,13,'249',13,'Ar-Ra‘d','الرَّعد',14,15,6,'أُوْلَٰٓئِكَ اَ۬لَّذِينَ كَفَرُواْ بِرَبِّهِمۡۖ وَأُوْلَٰٓئِكَ اَ۬لۡأَغۡلَٰلُ فِيٓ أَعۡنَاقِهِمۡۖ وَأُوْلَٰٓئِكَ أَصۡحَٰبُ اُ۬لنّ۪ارِۖ هُمۡ فِيهَا خَٰلِدُونَ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1717,13,'250',13,'Ar-Ra‘d','الرَّعد',1,3,7,'۞ وَيَسۡتَعۡجِلُونَكَ بِالسَّيِّئَةِ قَبۡلَ اَ۬لۡحَسَنَةِ وَقَدۡ خَلَتۡ مِن قَبۡلِهِمِ اِ۬لۡمَثُلَٰتُۗ وَإِنَّ رَبَّكَ لَذُو مَغۡفِرَةٖ لِّلنّ۪اسِ عَلَىٰ ظُلۡمِهِمۡۖ وَإِنَّ رَبَّكَ لَشَدِيدُ اُ۬لۡعِقَابِ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1718,13,'250',13,'Ar-Ra‘d','الرَّعد',3,5,8,'وَيَقُولُ اُ۬لَّذِينَ كَفَرُواْ لَوۡلَآ أُنزِلَ عَلَيۡهِ ءَايَةٞ مِّن رَّبِّهِۦٓۗ إِنَّمَآ أَنتَ مُنذِرٞۖ وَلِكُلِّ قَوۡمٍ هَادٍ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1719,13,'250',13,'Ar-Ra‘d','الرَّعد',5,6,9,'اِ۬للَّهُ يَعۡلَمُ مَا تَحۡمِلُ كُلُّ أُنثۭيٰ وَمَا تَغِيضُ اُ۬لۡأَرۡحَامُ وَمَا تَزۡدَادُۖ وَكُلُّ شَيۡءٍ عِندَهُۥ بِمِقۡد۪ارٍ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1720,13,'250',13,'Ar-Ra‘d','الرَّعد',6,7,10,'عَٰلِمُ اُ۬لۡغَيۡبِ وَاَلشَّهَٰدَةِ اِ۬لۡكَبِيرُ اُ۬لۡمُتَعَالِ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1721,13,'250',13,'Ar-Ra‘d','الرَّعد',7,9,11,'سَوَآءٞ مِّنكُم مَّنۡ أَسَرَّ اَ۬لۡقَوۡلَ وَمَن جَهَرَ بِهِۦ وَمَنۡ هُوَ مُسۡتَخۡفِۢ بِالَّيۡلِ وَسَارِبُۢ بِالنَّه۪ارِ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1722,13,'250',13,'Ar-Ra‘d','الرَّعد',9,12,12,'لَهُۥ مُعَقِّبَٰتٞ مِّنۢ بَيۡنِ يَدَيۡهِ وَمِنۡ خَلۡفِهِۦ يَحۡفَظُونَهُۥ مِنۡ أَمۡرِ اِ۬للَّهِۗ إِنَّ اَ۬للَّهَ لَا يُغَيِّرُ مَا بِقَوۡمٍ حَتَّىٰ يُغَيِّرُواْ مَا بِأَنفُسِهِمۡۗ وَإِذَآ أَرَادَ اَ۬للَّهُ بِقَوۡمٖ سُوٓءٗا فَلَا مَرَدَّ لَهُۥۚ وَمَا لَهُم مِّن دُونِهِۦ مِن وَالٍ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1723,13,'250',13,'Ar-Ra‘d','الرَّعد',12,13,13,'هُوَ اَ۬لَّذِي يُرِيكُمُ اُ۬لۡبَرۡقَ خَوۡفٗا وَطَمَعٗا وَيُنشِئُ اُ۬لسَّحَابَ اَ۬لثِّقَالَ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1724,13,'250',13,'Ar-Ra‘d','الرَّعد',13,15,14,'وَيُسَبِّحُ اُ۬لرَّعۡدُ بِحَمۡدِهِۦ وَاَلۡمَلَٰٓئِكَةُ مِنۡ خِيفَتِهِۦ وَيُرۡسِلُ اُ۬لصَّوَٰعِقَ فَيُصِيبُ بِهَا مَن يَشَآءُ وَهُمۡ يُجَٰدِلُونَ فِي اِ۬للَّهِ وَهۡوَ شَدِيدُ اُ۬لۡمِحَالِ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1725,13,'251',13,'Ar-Ra‘d','الرَّعد',1,3,15,'۞ لَهُۥ دَعۡوَةُ اُ۬لۡحَقِّۚ وَاَلَّذِينَ يَدۡعُونَ مِن دُونِهِۦ لَا يَسۡتَجِيبُونَ لَهُم بِشَيۡءٍ إِلَّا كَبَٰسِطِ كَفَّيۡهِ إِلَى اَ۬لۡمَآءِ لِيَبۡلُغَ فَاهُ وَمَا هُوَ بِبَٰلِغِهِۦۚ وَمَا دُعَآءُ اُ۬لۡكٰ۪فِرِينَ إِلَّا فِي ضَلَٰلٖ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1726,13,'251',13,'Ar-Ra‘d','الرَّعد',3,4,16,'وَلِلَّهِۤ يَسۡجُدُۤ مَن فِي اِ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِ طَوۡعٗا وَكَرۡهٗا وَظِلَٰلُهُم بِالۡغُدُوِّ وَاَلۡأٓصَالِ۩ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1727,13,'251',13,'Ar-Ra‘d','الرَّعد',4,7,17,'قُلۡ مَن رَّبُّ اُ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِ قُلِ اِ۬للَّهُۚ قُلۡ أَفَاَتَّخَذتُّم مِّن دُونِهِۦٓ أَوۡلِيَآءَ لَا يَمۡلِكُونَ لِأَنفُسِهِمۡ نَفۡعٗا وَلَا ضَرّٗاۚ قُلۡ هَلۡ يَسۡتَوِي اِ۬لۡأَعۡمَىٰ وَاَلۡبَصِيرُ أَمۡ هَلۡ تَسۡتَوِي اِ۬لظُّلُمَٰتُ وَاَلنُّورُ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1728,13,'251',13,'Ar-Ra‘d','الرَّعد',7,8,18,'أَمۡ جَعَلُواْ لِلَّهِ شُرَكَآءَ خَلَقُواْ كَخَلۡقِهِۦ فَتَشَٰبَهَ اَ۬لۡخَلۡقُ عَلَيۡهِمۡۚ قُلِ اِ۬للَّهُ خَٰلِقُ كُلِّ شَيۡءٖ وَهۡوَ اَ۬لۡوَٰحِدُ اُ۬لۡقَهَّٰرُ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1729,13,'251',13,'Ar-Ra‘d','الرَّعد',8,13,19,'أَنزَلَ مِنَ اَ۬لسَّمَآءِ مَآءٗ فَسَالَتۡ أَوۡدِيَةُۢ بِقَدَرِهَا فَاَحۡتَمَلَ اَ۬لسَّيۡلُ زَبَدٗا رَّابِيٗاۖ وَمِمَّا تُوقِدُونَ عَلَيۡهِ فِي اِ۬لنّ۪ارِ اِ۪بۡتِغَآءَ حِلۡيَةٍ أَوۡ مَتَٰعٖ زَبَدٞ مِّثۡلُهُۥۚ كَذَٰلِكَ يَضۡرِبُ اُ۬للَّهُ اُ۬لۡحَقَّ وَاَلۡبَٰطِلَۚ فَأَمَّا اَ۬لزَّبَدُ فَيَذۡهَبُ جُفَآءٗۖ وَأَمَّا مَا يَنفَعُ اُ۬لنَّاسَ فَيَمۡكُثُ فِي اِ۬لۡأَرۡضِۚ كَذَٰلِكَ يَضۡرِبُ اُ۬للَّهُ اُ۬لۡأَمۡثَالَ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1730,13,'251',13,'Ar-Ra‘d','الرَّعد',13,15,20,'لِلَّذِينَ اَ۪سۡتَجَابُواْ لِرَبِّهِمِ اِ۬لۡحُسۡنۭيٰۚ وَاَلَّذِينَ لَمۡ يَسۡتَجِيبُواْ لَهُۥ لَوۡ أَنَّ لَهُم مَّا فِي اِ۬لۡأَرۡضِ جَمِيعٗا وَمِثۡلَهُۥ مَعَهُۥ لَاَفۡتَدَوۡاْ بِهِۦٓۚ أُوْلَٰٓئِكَ لَهُمۡ سُوٓءُ اُ۬لۡحِسَابِ وَمَأۡوَىٰهُمۡ جَهَنَّمُۖ وَبِئۡسَ اَ۬لۡمِهَادُ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1731,13,'252',13,'Ar-Ra‘d','الرَّعد',1,2,21,'۞ أَفَمَن يَعۡلَمُ أَنَّمَآ أُنزِلَ إِلَيۡكَ مِن رَّبِّكَ اَ۬لۡحَقُّ كَمَنۡ هُوَ أَعۡمَىٰٓۚ إِنَّمَا يَتَذَكَّرُ أُوْلُواْ اُ۬لۡأَلۡبَٰبِ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1732,13,'252',13,'Ar-Ra‘d','الرَّعد',2,2,22,'اِ۬لَّذِينَ يُوفُونَ بِعَهۡدِ اِ۬للَّهِ وَلَا يَنقُضُونَ اَ۬لۡمِيثَٰقَ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1733,13,'252',13,'Ar-Ra‘d','الرَّعد',3,4,23,'وَاَلَّذِينَ يَصِلُونَ مَآ أَمَرَ اَ۬للَّهُ بِهِۦٓ أَن يُوصَلَ وَيَخۡشَوۡنَ رَبَّهُمۡ وَيَخَافُونَ سُوٓءَ اَ۬لۡحِسَابِ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1734,13,'252',13,'Ar-Ra‘d','الرَّعد',4,6,24,'وَاَلَّذِينَ صَبَرُواْ اُ۪بۡتِغَآءَ وَجۡهِ رَبِّهِمۡ وَأَقَامُواْ اُ۬لصَّلَوٰةَ وَأَنفَقُواْ مِمَّا رَزَقۡنَٰهُمۡ سِرّٗا وَعَلَانِيَةٗ وَيَدۡرَءُونَ بِالۡحَسَنَةِ اِ۬لسَّيِّئَةَ أُوْلَٰٓئِكَ لَهُمۡ عُقۡبَى اَ۬لدّ۪ارِ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1735,13,'252',13,'Ar-Ra‘d','الرَّعد',6,8,25,'جَنَّٰتُ عَدۡنٖ يَدۡخُلُونَهَا وَمَن صَلَحَ مِنۡ ءَابَآئِهِمۡ وَأَزۡوَٰجِهِمۡ وَذُرِّيَّٰتِهِمۡۖ وَاَلۡمَلَٰٓئِكَةُ يَدۡخُلُونَ عَلَيۡهِم مِّن كُلِّ بَابٖ سَلَٰمٌ عَلَيۡكُم بِمَا صَبَرۡتُمۡۚ فَنِعۡمَ عُقۡبَى اَ۬لدّ۪ارِ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1736,13,'252',13,'Ar-Ra‘d','الرَّعد',9,11,26,'وَاَلَّذِينَ يَنقُضُونَ عَهۡدَ اَ۬للَّهِ مِنۢ بَعۡدِ مِيثَٰقِهِۦ وَيَقۡطَعُونَ مَآ أَمَرَ اَ۬للَّهُ بِهِۦٓ أَن يُوصَلَ وَيُفۡسِدُونَ فِي اِ۬لۡأَرۡضِ أُوْلَٰٓئِكَ لَهُمُ اُ۬للَّعۡنَةُ وَلَهُمۡ سُوٓءُ اُ۬لدّ۪ارِ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1737,13,'252',13,'Ar-Ra‘d','الرَّعد',11,13,27,'اِ۬للَّهُ يَبۡسُطُ اُ۬لرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُۚ وَفَرِحُواْ بِالۡحَيَوٰةِ اِ۬لدُّنۡيۭا وَمَا اَ۬لۡحَيَوٰةُ اُ۬لدُّنۡيۭا فِي اِ۬لۡأٓخِرَةِ إِلَّا مَتَٰعٞ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1738,13,'252',13,'Ar-Ra‘d','الرَّعد',13,14,28,'وَيَقُولُ اُ۬لَّذِينَ كَفَرُواْ لَوۡلَآ أُنزِلَ عَلَيۡهِ ءَايَةٞ مِّن رَّبِّهِۦۗ قُلۡ إِنَّ اَ۬للَّهَ يُضِلُّ مَن يَشَآءُ وَيَهۡدِيٓ إِلَيۡهِ مَنۡ أَنَابَ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1739,13,'252',13,'Ar-Ra‘d','الرَّعد',14,15,29,'اَ۬لَّذِينَ ءَامَنُواْ وَتَطۡمَئِنُّ قُلُوبُهُم بِذِكۡرِ اِ۬للَّهِۗ أَلَا بِذِكۡرِ اِ۬للَّهِ تَطۡمَئِنُّ اُ۬لۡقُلُوبُ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1740,13,'253',13,'Ar-Ra‘d','الرَّعد',1,1,30,'اُ۬لَّذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ طُوبۭيٰ لَهُمۡ وَحُسۡنُ مَـَٔابٖ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1741,13,'253',13,'Ar-Ra‘d','الرَّعد',2,4,31,'۞ كَذَٰلِكَ أَرۡسَلۡنَٰكَ فِيٓ أُمَّةٖ قَدۡ خَلَتۡ مِن قَبۡلِهَآ أُمَمٞ لِّتَتۡلُوَاْ عَلَيۡهِمِ اِ۬لَّذِيٓ أَوۡحَيۡنَآ إِلَيۡكَ وَهُمۡ يَكۡفُرُونَ بِالرَّحۡمَٰنِۚ قُلۡ هُوَ رَبِّي لَآ إِلَٰهَ إِلَّا هُوَ عَلَيۡهِ تَوَكَّلۡتُ وَإِلَيۡهِ مَتَابِ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1742,13,'253',13,'Ar-Ra‘d','الرَّعد',4,9,32,'وَلَوۡ أَنَّ قُرۡءَانٗا سُيِّرَتۡ بِهِ اِ۬لۡجِبَالُ أَوۡ قُطِّعَتۡ بِهِ اِ۬لۡأَرۡضُ أَوۡ كُلِّمَ بِهِ اِ۬لۡمَوۡتۭيٰۗ بَل لِّلَّهِ اِ۬لۡأَمۡرُ جَمِيعًاۗ أَفَلَمۡ يَاْيۡـَٔسِ اِ۬لَّذِينَ ءَامَنُوٓاْ أَن لَّوۡ يَشَآءُ اُ۬للَّهُ لَهَدَى اَ۬لنَّاسَ جَمِيعٗاۗ وَلَا يَزَالُ اُ۬لَّذِينَ كَفَرُواْ تُصِيبُهُم بِمَا صَنَعُواْ قَارِعَةٌ أَوۡ تَحُلُّ قَرِيبٗا مِّن د۪ارِهِمۡ حَتَّىٰ يَأۡتِيَ وَعۡدُ اُ۬للَّهِۚ إِنَّ اَ۬للَّهَ لَا يُخۡلِفُ اُ۬لۡمِيعَادَ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1743,13,'253',13,'Ar-Ra‘d','الرَّعد',9,11,33,'وَلَقَدِ اِ۟سۡتُهۡزِئَ بِرُسُلٖ مِّن قَبۡلِكَ فَأَمۡلَيۡتُ لِلَّذِينَ كَفَرُواْ ثُمَّ أَخَذتُّهُمۡۖ فَكَيۡفَ كَانَ عِقَابِ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1744,13,'253',13,'Ar-Ra‘d','الرَّعد',11,14,34,'أَفَمَنۡ هُوَ قَآئِمٌ عَلَىٰ كُلِّ نَفۡسِۢ بِمَا كَسَبَتۡۗ وَجَعَلُواْ لِلَّهِ شُرَكَآءَ قُلۡ سَمُّوهُمۡۚ أَمۡ تُنَبِّـُٔونَهُۥ بِمَا لَا يَعۡلَمُ فِي اِ۬لۡأَرۡضِ أَم بِظَٰهِرٖ مِّنَ اَ۬لۡقَوۡلِۗ بَلۡ زُيِّنَ لِلَّذِينَ كَفَرُواْ مَكۡرُهُمۡ وَصَدُّواْ عَنِ اِ۬لسَّبِيلِۗ وَمَن يُضۡلِلِ اِ۬للَّهُ فَمَا لَهُۥ مِنۡ هَادٖ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1745,13,'253',13,'Ar-Ra‘d','الرَّعد',14,15,35,'لَّهُمۡ عَذَابٞ فِي اِ۬لۡحَيَوٰةِ اِ۬لدُّنۡيۭاۖ وَلَعَذَابُ اُ۬لۡأٓخِرَةِ أَشَقُّۖ وَمَا لَهُم مِّنَ اَ۬للَّهِ مِن وَاقٖ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1746,13,'254',13,'Ar-Ra‘d','الرَّعد',1,3,36,'۞ مَّثَلُ اُ۬لۡجَنَّةِ اِ۬لَّتِي وُعِدَ اَ۬لۡمُتَّقُونَۖ تَجۡرِي مِن تَحۡتِهَا اَ۬لۡأَنۡهَٰرُۖ أُكۡلُهَا دَآئِمٞ وَظِلُّهَاۚ تِلۡكَ عُقۡبَى اَ۬لَّذِينَ اَ۪تَّقَواْۚ وَّعُقۡبَى اَ۬لۡكٰ۪فِرِينَ اَ۬لنَّارُ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1747,13,'254',13,'Ar-Ra‘d','الرَّعد',3,5,37,'وَاَلَّذِينَ ءَاتَيۡنَٰهُمُ اُ۬لۡكِتَٰبَ يَفۡرَحُونَ بِمَآ أُنزِلَ إِلَيۡكَۖ وَمِنَ اَ۬لۡأَحۡزَابِ مَن يُنكِرُ بَعۡضَهُۥۚ قُلۡ إِنَّمَآ أُمِرۡتُ أَنۡ أَعۡبُدَ اَ۬للَّهَ وَلَآ أُشۡرِكَ بِهِۦٓۚ إِلَيۡهِ أَدۡعُواْ وَإِلَيۡهِ مَـَٔابِ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1748,13,'254',13,'Ar-Ra‘d','الرَّعد',6,7,38,'وَكَذَٰلِكَ أَنزَلۡنَٰهُ حُكۡمًا عَرَبِيّٗاۚ وَلَئِنِ اِ۪تَّبَعۡتَ أَهۡوَآءَهُم بَعۡدَ مَا جَآءَكَ مِنَ اَ۬لۡعِلۡمِ مَا لَكَ مِنَ اَ۬للَّهِ مِن وَلِيّٖ وَلَا وَاقٖ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1749,13,'254',13,'Ar-Ra‘d','الرَّعد',7,9,39,'وَلَقَدۡ أَرۡسَلۡنَا رُسُلٗا مِّن قَبۡلِكَ وَجَعَلۡنَا لَهُمۡ أَزۡوَٰجٗا وَذُرِّيَّةٗۚ وَمَا كَانَ لِرَسُولٍ أَن يَأۡتِيَ بِـَٔايَةٍ إِلَّا بِإِذۡنِ اِ۬للَّهِۗ لِكُلِّ أَجَلٖ كِتَابٞ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1750,13,'254',13,'Ar-Ra‘d','الرَّعد',10,10,40,'يَمۡحُواْ اُ۬للَّهُ مَا يَشَآءُ وَيُثۡبِتُۖ وَعِندَهُۥٓ أُمُّ اُ۬لۡكِتَٰبِ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1751,13,'254',13,'Ar-Ra‘d','الرَّعد',10,12,41,'وَإِن مَّا نُرِيَنَّكَ بَعۡضَ اَ۬لَّذِي نَعِدُهُمۡ أَوۡ نَتَوَفَّيَنَّكَ فَإِنَّمَا عَلَيۡكَ اَ۬لۡبَلَٰغُ وَعَلَيۡنَا اَ۬لۡحِسَابُ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1752,13,'254',13,'Ar-Ra‘d','الرَّعد',12,14,42,'أَوَلَمۡ يَرَوۡاْ أَنَّا نَأۡتِي اِ۬لۡأَرۡضَ نَنقُصُهَا مِنۡ أَطۡرَافِهَاۚ وَاَللَّهُ يَحۡكُمُ لَا مُعَقِّبَ لِحُكۡمِهِۦۚ وَهۡوَ سَرِيعُ اُ۬لۡحِسَابِ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1753,13,'254',13,'Ar-Ra‘d','الرَّعد',14,15,43,'وَقَدۡ مَكَرَ اَ۬لَّذِينَ مِن قَبۡلِهِمۡ فَلِلَّهِ اِ۬لۡمَكۡرُ جَمِيعٗاۖ يَعۡلَمُ مَا تَكۡسِبُ كُلُّ نَفۡسٖۗ وَسَيَعۡلَمُ اُ۬لۡكَٰفِرُ لِمَنۡ عُقۡبَى اَ۬لدّ۪ارِ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1754,13,'255',13,'Ar-Ra‘d','الرَّعد',1,2,44,'وَيَقُولُ اُ۬لَّذِينَ كَفَرُواْ لَسۡتَ مُرۡسَلٗاۚ قُلۡ كَفَىٰ بِاللَّهِ شَهِيدَۢا بَيۡنِي وَبَيۡنَكُمۡ وَمَنۡ عِندَهُۥ عِلۡمُ اُ۬لۡكِتَٰبِ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1755,13,'255',14,'Ibrāhīm','إبراهِيم',5,6,1,'الٓر۪ۚ كِتَٰبٌ أَنزَلۡنَٰهُ إِلَيۡكَ لِتُخۡرِجَ اَ۬لنَّاسَ مِنَ اَ۬لظُّلُمَٰتِ إِلَى اَ۬لنُّورِ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1756,13,'255',14,'Ibrāhīm','إبراهِيم',6,6,2,'بِإِذۡنِ رَبِّهِمۡ إِلَىٰ صِرَٰطِ اِ۬لۡعَزِيزِ اِ۬لۡحَمِيدِ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1757,13,'255',14,'Ibrāhīm','إبراهِيم',7,8,3,'اِ۬للَّهِ اِ۬لَّذِي لَهُۥ مَا فِي اِ۬لسَّمَٰوَٰتِ وَمَا فِي اِ۬لۡأَرۡضِۗ وَوَيۡلٞ لِّلۡكٰ۪فِرِينَ مِنۡ عَذَابٖ شَدِيدٍ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1758,13,'255',14,'Ibrāhīm','إبراهِيم',8,10,4,'اِ۬لَّذِينَ يَسۡتَحِبُّونَ اَ۬لۡحَيَوٰةَ اَ۬لدُّنۡيۭا عَلَى اَ۬لۡأٓخِرَةِ وَيَصُدُّونَ عَن سَبِيلِ اِ۬للَّهِ وَيَبۡغُونَهَا عِوَجًاۚ أُوْلَٰٓئِكَ فِي ضَلَٰلِۢ بَعِيدٖ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1759,13,'255',14,'Ibrāhīm','إبراهِيم',10,13,5,'وَمَآ أَرۡسَلۡنَا مِن رَّسُولٍ إِلَّا بِلِسَانِ قَوۡمِهِۦ لِيُبَيِّنَ لَهُمۡۖ فَيُضِلُّ اُ۬للَّهُ مَن يَشَآءُ وَيَهۡدِي مَن يَشَآءُۚ وَهۡوَ اَ۬لۡعَزِيزُ اُ۬لۡحَكِيمُ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1760,13,'255',14,'Ibrāhīm','إبراهِيم',13,14,6,'وَلَقَدۡ أَرۡسَلۡنَا مُوسۭيٰ بِـَٔايَٰتِنَآ أَنۡ أَخۡرِجۡ قَوۡمَكَ مِنَ اَ۬لظُّلُمَٰتِ إِلَى اَ۬لنُّورِ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1761,13,'255',14,'Ibrāhīm','إبراهِيم',14,15,7,'وَذَكِّرۡهُم بِأَيَّىٰمِ اِ۬للَّهِۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّكُلِّ صَبّ۪ارٖ شَكُورٖ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1762,13,'256',14,'Ibrāhīm','إبراهِيم',1,4,8,'وَإِذۡ قَالَ مُوسۭيٰ لِقَوۡمِهِ اِ۟ذۡكُرُواْ نِعۡمَةَ اَ۬للَّهِ عَلَيۡكُمۡ إِذۡ أَنجَىٰكُم مِّنۡ ءَالِ فِرۡعَوۡنَ يَسُومُونَكُمۡ سُوٓءَ اَ۬لۡعَذَابِ وَيُذَبِّحُونَ أَبۡنَآءَكُمۡ وَيَسۡتَحۡيُونَ نِسَآءَكُمۡۚ وَفِي ذَٰلِكُم بَلَآءٞ مِّن رَّبِّكُمۡ عَظِيمٞ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1763,13,'256',14,'Ibrāhīm','إبراهِيم',4,6,9,'وَإِذ تَّأَذَّنَ رَبُّكُمۡ لَئِن شَكَرۡتُمۡ لَأَزِيدَنَّكُمۡۖ وَلَئِن كَفَرۡتُمۡ إِنَّ عَذَابِي لَشَدِيدٞ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1764,13,'256',14,'Ibrāhīm','إبراهِيم',6,7,10,'وَقَالَ مُوسۭيٰٓ إِن تَكۡفُرُوٓاْ أَنتُمۡ وَمَن فِي اِ۬لۡأَرۡضِ جَمِيعٗا فَإِنَّ اَ۬للَّهَ لَغَنِيٌّ حَمِيدٌ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1765,13,'256',14,'Ibrāhīm','إبراهِيم',7,8,11,'أَلَمۡ يَأۡتِكُمۡ نَبَؤُاْ اُ۬لَّذِينَ مِن قَبۡلِكُمۡ قَوۡمِ نُوحٖ وَعَادٖ وَثَمُودَ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1766,13,'256',14,'Ibrāhīm','إبراهِيم',8,11,12,'وَاَلَّذِينَ مِنۢ بَعۡدِهِمۡ لَا يَعۡلَمُهُمۡ إِلَّا اَ۬للَّهُۚ جَآءَتۡهُمۡ رُسۡلُهُم بِالۡبَيِّنَٰتِ فَرَدُّوٓاْ أَيۡدِيَهُمۡ فِيٓ أَفۡوَٰهِهِمۡ وَقَالُوٓاْ إِنَّا كَفَرۡنَا بِمَآ أُرۡسِلۡتُم بِهِۦ وَإِنَّا لَفِي شَكّٖ مِّمَّا تَدۡعُونَنَآ إِلَيۡهِ مُرِيبٖ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1767,13,'256',14,'Ibrāhīm','إبراهِيم',12,15,13,'۞ قَالَتۡ رُسۡلُهُمۡ أَفِي اِ۬للَّهِ شَكّٞ فَاطِرِ اِ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِۖ يَدۡعُوكُمۡ لِيَغۡفِرَ لَكُم مِّن ذُنُوبِكُمۡ وَيُؤَخِّرَكُمۡ إِلَىٰٓ أَجَلٖ مُّسَمّٗىۚ قَالُوٓاْ إِنۡ أَنتُمۡ إِلَّا بَشَرٞ مِّثۡلُنَا تُرِيدُونَ أَن تَصُدُّونَا عَمَّا كَانَ يَعۡبُدُ ءَابَآؤُنَا فَأۡتُونَا بِسُلۡطَٰنٖ مُّبِينٖ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1768,13,'257',14,'Ibrāhīm','إبراهِيم',1,3,14,'قَالَتۡ لَهُمۡ رُسۡلُهُمۡ إِن نَّحۡنُ إِلَّا بَشَرٞ مِّثۡلُكُمۡ وَلَٰكِنَّ اَ۬للَّهَ يَمُنُّ عَلَىٰ مَن يَشَآءُ مِنۡ عِبَادِهِۦۖ وَمَا كَانَ لَنَآ أَن نَّأۡتِيَكُم بِسُلۡطَٰنٍ إِلَّا بِإِذۡنِ اِ۬للَّهِۚ وَعَلَى اَ۬للَّهِ فَلۡيَتَوَكَّلِ اِ۬لۡمُؤۡمِنُونَ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1769,13,'257',14,'Ibrāhīm','إبراهِيم',4,5,15,'وَمَا لَنَآ أَلَّا نَتَوَكَّلَ عَلَى اَ۬للَّهِ وَقَدۡ هَدَىٰنَا سُبۡلَنَاۚ وَلَنَصۡبِرَنَّ عَلَىٰ مَآ ءَاذَيۡتُمُونَاۚ وَعَلَى اَ۬للَّهِ فَلۡيَتَوَكَّلِ اِ۬لۡمُتَوَكِّلُونَ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1770,13,'257',14,'Ibrāhīm','إبراهِيم',6,8,16,'وَقَالَ اَ۬لَّذِينَ كَفَرُواْ لِرُسۡلِهِمۡ لَنُخۡرِجَنَّكُم مِّنۡ أَرۡضِنَآ أَوۡ لَتَعُودُنَّ فِي مِلَّتِنَاۖ فَأَوۡحَىٰٓ إِلَيۡهِمۡ رَبُّهُمۡ لَنُهۡلِكَنَّ اَ۬لظَّٰلِمِينَ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1771,13,'257',14,'Ibrāhīm','إبراهِيم',8,9,17,'وَلَنُسۡكِنَنَّكُمُ اُ۬لۡأَرۡضَ مِنۢ بَعۡدِهِمۡۚ ذَٰلِكَ لِمَنۡ خَافَ مَقَامِي وَخَافَ وَعِيدِ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1772,13,'257',14,'Ibrāhīm','إبراهِيم',9,10,18,'وَاَسۡتَفۡتَحُواْ وَخَابَ كُلُّ جَبّ۪ارٍ عَنِيدٖ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1773,13,'257',14,'Ibrāhīm','إبراهِيم',10,11,19,'مِّن وَرَآئِهِۦ جَهَنَّمُ وَيُسۡقَىٰ مِن مَّآءٖ صَدِيدٖ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1774,13,'257',14,'Ibrāhīm','إبراهِيم',11,13,20,'يَتَجَرَّعُهُۥ وَلَا يَكَادُ يُسِيغُهُۥ وَيَأۡتِيهِ اِ۬لۡمَوۡتُ مِن كُلِّ مَكَانٖ وَمَا هُوَ بِمَيِّتٖۖ وَمِن وَرَآئِهِۦ عَذَابٌ غَلِيظٞ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1775,13,'257',14,'Ibrāhīm','إبراهِيم',13,15,21,'مَّثَلُ اُ۬لَّذِينَ كَفَرُواْ بِرَبِّهِمۡۖ أَعۡمَٰلُهُمۡ كَرَمَادٍ اِ۪شۡتَدَّتۡ بِهِ اِ۬لرِّيحُ فِي يَوۡمٍ عَاصِفٖۖ لَّا يَقۡدِرُونَ مِمَّا كَسَبُواْ عَلَىٰ شَيۡءٖۚ ذَٰلِكَ هُوَ اَ۬لضَّلَٰلُ اُ۬لۡبَعِيدُ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1776,13,'258',14,'Ibrāhīm','إبراهِيم',1,2,22,'أَلَمۡ تَرَ أَنَّ اَ۬للَّهَ خَلَقَ اَ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضَ بِالۡحَقِّۚ إِن يَشَأۡ يُذۡهِبۡكُمۡ وَيَأۡتِ بِخَلۡقٖ جَدِيدٖ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1777,13,'258',14,'Ibrāhīm','إبراهِيم',2,2,23,'وَمَا ذَٰلِكَ عَلَى اَ۬للَّهِ بِعَزِيزٖ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1778,13,'258',14,'Ibrāhīm','إبراهِيم',3,6,24,'۞ وَبَرَزُواْ لِلَّهِ جَمِيعٗا فَقَالَ اَ۬لضُّعَفَٰٓؤُاْ لِلَّذِينَ اَ۪سۡتَكۡبَرُوٓاْ إِنَّا كُنَّا لَكُمۡ تَبَعٗا فَهَلۡ أَنتُم مُّغۡنُونَ عَنَّا مِنۡ عَذَابِ اِ۬للَّهِ مِن شَيۡءٖۚ قَالُواْ لَوۡ هَدَىٰنَا اَ۬للَّهُ لَهَدَيۡنَٰكُمۡۖ سَوَآءٌ عَلَيۡنَآ أَجَزِعۡنَآ أَمۡ صَبَرۡنَا مَا لَنَا مِن مَّحِيصٖ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1779,13,'258',14,'Ibrāhīm','إبراهِيم',6,12,25,'وَقَالَ اَ۬لشَّيۡطَٰنُ لَمَّا قُضِيَ اَ۬لۡأَمۡرُ إِنَّ اَ۬للَّهَ وَعَدَكُمۡ وَعۡدَ اَ۬لۡحَقِّ وَوَعَدتُّكُمۡ فَأَخۡلَفۡتُكُمۡۖ وَمَا كَانَ لِي عَلَيۡكُم مِّن سُلۡطَٰنٍ إِلَّآ أَن دَعَوۡتُكُمۡ فَاَسۡتَجَبۡتُمۡ لِيۖ فَلَا تَلُومُونِي وَلُومُوٓاْ أَنفُسَكُمۖ مَّآ أَنَا۠ بِمُصۡرِخِكُمۡ وَمَآ أَنتُم بِمُصۡرِخِيَّ إِنِّي كَفَرۡتُ بِمَآ أَشۡرَكۡتُمُونِۦ مِن قَبۡلُۗ إِنَّ اَ۬لظَّٰلِمِينَ لَهُمۡ عَذَابٌ أَلِيمٞ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1780,13,'258',14,'Ibrāhīm','إبراهِيم',12,14,26,'وَأُدۡخِلَ اَ۬لَّذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ جَنَّٰتٖ تَجۡرِي مِن تَحۡتِهَا اَ۬لۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَا بِإِذۡنِ رَبِّهِمۡۖ تَحِيَّتُهُمۡ فِيهَا سَلَٰمٌ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1781,13,'258-259',14,'Ibrāhīm','إبراهِيم',14,2,27,'أَلَمۡ تَرَ كَيۡفَ ضَرَبَ اَ۬للَّهُ مَثَلٗا كَلِمَةٗ طَيِّبَةٗ كَشَجَرَةٖ طَيِّبَةٍ أَصۡلُهَا ثَابِتٞ وَفَرۡعُهَا فِي اِ۬لسَّمَآءِ  تُؤۡتِيٓ أُكۡلَهَا كُلَّ حِينِۢ بِإِذۡنِ رَبِّهَاۗ وَيَضۡرِبُ اُ۬للَّهُ اُ۬لۡأَمۡثَالَ لِلنّ۪اسِ لَعَلَّهُمۡ يَتَذَكَّرُونَ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1782,13,'259',14,'Ibrāhīm','إبراهِيم',2,4,28,'وَمَثَلُ كَلِمَةٍ خَبِيثَةٖ كَشَجَرَةٍ خَبِيثَةٍ اِ۟جۡتُثَّتۡ مِن فَوۡقِ اِ۬لۡأَرۡضِ مَا لَهَا مِن قَر۪ارٖ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1783,13,'259',14,'Ibrāhīm','إبراهِيم',4,6,29,'يُثَبِّتُ اُ۬للَّهُ اُ۬لَّذِينَ ءَامَنُواْ بِالۡقَوۡلِ اِ۬لثَّابِتِ فِي اِ۬لۡحَيَوٰةِ اِ۬لدُّنۡيۭا وَفِي اِ۬لۡأٓخِرَةِۖ وَيُضِلُّ اُ۬للَّهُ اُ۬لظَّٰلِمِينَۚ وَيَفۡعَلُ اُ۬للَّهُ مَا يَشَآءُ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1784,13,'259',14,'Ibrāhīm','إبراهِيم',6,7,30,'۞ اَ۬لَمۡ تَرَ إِلَى اَ۬لَّذِينَ بَدَّلُواْ نِعۡمَتَ اَ۬للَّهِ كُفۡرٗا وَأَحَلُّواْ قَوۡمَهُمۡ دَارَ اَ۬لۡبَو۪ارِ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1785,13,'259',14,'Ibrāhīm','إبراهِيم',7,8,31,'جَهَنَّمَ يَصۡلَوۡنَهَاۖ وَبِئۡسَ اَ۬لۡقَرَارُ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1786,13,'259',14,'Ibrāhīm','إبراهِيم',8,9,32,'وَجَعَلُواْ لِلَّهِ أَندَادٗا لِّيَضِلُّواْ عَن سَبِيلِهِۦۗ قُلۡ تَمَتَّعُواْ فَإِنَّ مَصِيرَكُمۡ إِلَى اَ۬لنّ۪ارِ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1787,13,'259',14,'Ibrāhīm','إبراهِيم',9,11,33,'قُل لِّعِبَادِيَ اَ۬لَّذِينَ ءَامَنُواْ يُقِيمُواْ اُ۬لصَّلَوٰةَ وَيُنفِقُواْ مِمَّا رَزَقۡنَٰهُمۡ سِرّٗا وَعَلَانِيَةٗ مِّن قَبۡلِ أَن يَأۡتِيَ يَوۡمٞ لَّا بَيۡعَ فِيهِ وَلَا خِلَٰلَ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1788,13,'259',14,'Ibrāhīm','إبراهِيم',11,14,34,'اَ۬للَّهُ اُ۬لَّذِي خَلَقَ اَ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضَ وَأَنزَلَ مِنَ اَ۬لسَّمَآءِ مَآءٗ فَأَخۡرَجَ بِهِۦ مِنَ اَ۬لثَّمَرَٰتِ رِزۡقٗا لَّكُمۡۖ وَسَخَّرَ لَكُمُ اُ۬لۡفُلۡكَ لِتَجۡرِيَ فِي اِ۬لۡبَحۡرِ بِأَمۡرِهِۦۖ وَسَخَّرَ لَكُمُ اُ۬لۡأَنۡهَٰرَ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1789,13,'259',14,'Ibrāhīm','إبراهِيم',14,15,35,'وَسَخَّرَ لَكُمُ اُ۬لشَّمۡسَ وَاَلۡقَمَرَ دَآئِبَيۡنِۖ وَسَخَّرَ لَكُمُ اُ۬لَّيۡلَ وَاَلنَّهَارَ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1790,13,'260',14,'Ibrāhīm','إبراهِيم',1,2,36,'وَءَاتَىٰكُم مِّن كُلِّ مَا سَأَلۡتُمُوهُۚ وَإِن تَعُدُّواْ نِعۡمَتَ اَ۬للَّهِ لَا تُحۡصُوهَآۗ إِنَّ اَ۬لۡإِنسَٰنَ لَظَلُومٞ كَفَّارٞ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1791,13,'260',14,'Ibrāhīm','إبراهِيم',2,4,37,'وَإِذۡ قَالَ إِبۡرَٰهِيمُ رَبِّ اِ۪جۡعَلۡ هَٰذَا اَ۬لۡبَلَدَ ءَامِنٗا وَاَجۡنُبۡنِي وَبَنِيَّ أَن نَّعۡبُدَ اَ۬لۡأَصۡنَامَ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1792,13,'260',14,'Ibrāhīm','إبراهِيم',4,5,38,'رَبِّ إِنَّهُنَّ أَضۡلَلۡنَ كَثِيرٗا مِّنَ اَ۬لنّ۪اسِۖ فَمَن تَبِعَنِي فَإِنَّهُۥ مِنِّيۖ وَمَنۡ عَصَانِي فَإِنَّكَ غَفُورٞ رَّحِيمٞ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1793,13,'260',14,'Ibrāhīm','إبراهِيم',5,8,39,'رَّبَّنَآ إِنِّيَ أَسۡكَنتُ مِن ذُرِّيَّتِي بِوَادٍ غَيۡرِ ذِي زَرۡعٍ عِندَ بَيۡتِكَ اَ۬لۡمُحَرَّمِ رَبَّنَا لِيُقِيمُواْ اُ۬لصَّلَوٰةَ فَاَجۡعَلۡ أَفۡـِٔدَةٗ مِّنَ اَ۬لنّ۪اسِ تَهۡوِيٓ إِلَيۡهِمۡ وَاَرۡزُقۡهُم مِّنَ اَ۬لثَّمَرَٰتِ لَعَلَّهُمۡ يَشۡكُرُونَ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1794,13,'260',14,'Ibrāhīm','إبراهِيم',9,10,40,'رَبَّنَآ إِنَّكَ تَعۡلَمُ مَا نُخۡفِي وَمَا نُعۡلِنُۗ وَمَا يَخۡفَىٰ عَلَى اَ۬للَّهِ مِن شَيۡءٖ فِي اِ۬لۡأَرۡضِ وَلَا فِي اِ۬لسَّمَآءِ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1795,13,'260',14,'Ibrāhīm','إبراهِيم',10,12,41,'۞ اِ۬لۡحَمۡدُ لِلَّهِ اِ۬لَّذِي وَهَبَ لِي عَلَى اَ۬لۡكِبَرِ إِسۡمَٰعِيلَ وَإِسۡحَٰقَۚ إِنَّ رَبِّي لَسَمِيعُ اُ۬لدُّعَآءِ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1796,13,'260',14,'Ibrāhīm','إبراهِيم',12,13,42,'رَبِّ اِ۪جۡعَلۡنِي مُقِيمَ اَ۬لصَّلَوٰةِ وَمِن ذُرِّيَّتِيۚ رَبَّنَا وَتَقَبَّلۡ دُعَآءِۦ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1797,13,'260',14,'Ibrāhīm','إبراهِيم',13,14,43,'رَبَّنَا اَ۪غۡفِر لِّي وَلِوَٰلِدَيَّ وَلِلۡمُؤۡمِنِينَ يَوۡمَ يَقُومُ اُ۬لۡحِسَابُ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1798,13,'260',14,'Ibrāhīm','إبراهِيم',14,15,44,'وَلَا تَحۡسِبَنَّ اَ۬للَّهَ غَٰفِلًا عَمَّا يَعۡمَلُ اُ۬لظَّٰلِمُونَۚ إِنَّمَا يُؤَخِّرُهُمۡ لِيَوۡمٖ تَشۡخَصُ فِيهِ اِ۬لۡأَبۡصَٰرُ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1799,13,'261',14,'Ibrāhīm','إبراهِيم',1,2,45,'مُهۡطِعِينَ مُقۡنِعِي رُءُوسِهِمۡ لَا يَرۡتَدُّ إِلَيۡهِمۡ طَرۡفُهُمۡۖ وَأَفۡـِٔدَتُهُمۡ هَوَآءٞ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1800,13,'261',14,'Ibrāhīm','إبراهِيم',2,5,46,'وَأَنذِرِ اِ۬لنَّاسَ يَوۡمَ يَأۡتِيهِمِ اِ۬لۡعَذَابُ فَيَقُولُ اُ۬لَّذِينَ ظَلَمُواْ رَبَّنَآ أَخِّرۡنَآ إِلَىٰٓ أَجَلٖ قَرِيبٖ نُّجِبۡ دَعۡوَتَكَ وَنَتَّبِعِ اِ۬لرُّسُلَۗ أَوَلَمۡ تَكُونُوٓاْ أَقۡسَمۡتُم مِّن قَبۡلُ مَا لَكُم مِّن زَوَالٖ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1801,13,'261',14,'Ibrāhīm','إبراهِيم',5,7,47,'وَسَكَنتُمۡ فِي مَسَٰكِنِ اِ۬لَّذِينَ ظَلَمُوٓاْ أَنفُسَهُمۡ وَتَبَيَّنَ لَكُمۡ كَيۡفَ فَعَلۡنَا بِهِمۡ وَضَرَبۡنَا لَكُمُ اُ۬لۡأَمۡثَالَ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1802,13,'261',14,'Ibrāhīm','إبراهِيم',7,8,48,'وَقَدۡ مَكَرُواْ مَكۡرَهُمۡ وَعِندَ اَ۬للَّهِ مَكۡرُهُمۡ وَإِن كَانَ مَكۡرُهُمۡ لِتَزُولَ مِنۡهُ اُ۬لۡجِبَالُ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1803,13,'261',14,'Ibrāhīm','إبراهِيم',8,10,49,'فَلَا تَحۡسِبَنَّ اَ۬للَّهَ مُخۡلِفَ وَعۡدِهِۦ رُسُلَهُۥٓۚ إِنَّ اَ۬للَّهَ عَزِيزٞ ذُو اُ۪نتِقَامٖ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1804,13,'261',14,'Ibrāhīm','إبراهِيم',10,11,50,'يَوۡمَ تُبَدَّلُ اُ۬لۡأَرۡضُ غَيۡرَ اَ۬لۡأَرۡضِ وَاَلسَّمَٰوَٰتُۖ وَبَرَزُواْ لِلَّهِ اِ۬لۡوَٰحِدِ اِ۬لۡقَهّ۪ارِ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1805,13,'261',14,'Ibrāhīm','إبراهِيم',11,12,51,'وَتَرَى اَ۬لۡمُجۡرِمِينَ يَوۡمَئِذٖ مُّقَرَّنِينَ فِي اِ۬لۡأَصۡفَادِ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1806,13,'261',14,'Ibrāhīm','إبراهِيم',12,13,52,'سَرَابِيلُهُم مِّن قَطِرَانٖ وَتَغۡشَىٰ وُجُوهَهُمُ اُ۬لنَّارُ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1807,13,'261',14,'Ibrāhīm','إبراهِيم',13,14,53,'لِيَجۡزِيَ اَ۬للَّهُ كُلَّ نَفۡسٖ مَّا كَسَبَتۡۚ إِنَّ اَ۬للَّهَ سَرِيعُ اُ۬لۡحِسَابِ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1808,13,'261',14,'Ibrāhīm','إبراهِيم',14,15,54,'هَٰذَا بَلَٰغٞ لِّلنّ۪اسِ وَلِيُنذَرُواْ بِهِۦ وَلِيَعۡلَمُوٓاْ أَنَّمَا هُوَ إِلَٰهٞ وَٰحِدٞ وَلِيَذَّكَّرَ أُوْلُواْ اُ۬لۡأَلۡبَٰبِ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1809,14,'262',15,'Al-Ḥijr','الحِجر',3,3,1,'الٓر۪ۚ تِلۡكَ ءَايَٰتُ اُ۬لۡكِتَٰبِ وَقُرۡءَانٖ مُّبِينٖ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1810,14,'262',15,'Al-Ḥijr','الحِجر',3,4,2,'رُّبَّمَا يَوَدُّ اُ۬لَّذِينَ كَفَرُواْ لَوۡ كَانُواْ مُسۡلِمِينَ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1811,14,'262',15,'Al-Ḥijr','الحِجر',4,5,3,'ذَرۡهُمۡ يَأۡكُلُواْ وَيَتَمَتَّعُواْ وَيُلۡهِهِمِ اِ۬لۡأَمَلُۖ فَسَوۡفَ يَعۡلَمُونَ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1812,14,'262',15,'Al-Ḥijr','الحِجر',5,6,4,'وَمَآ أَهۡلَكۡنَا مِن قَرۡيَةٍ إِلَّا وَلَهَا كِتَابٞ مَّعۡلُومٞ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1813,14,'262',15,'Al-Ḥijr','الحِجر',6,7,5,'مَّا تَسۡبِقُ مِنۡ أُمَّةٍ أَجَلَهَا وَمَا يَسۡتَـٔۡخِرُونَ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1814,14,'262',15,'Al-Ḥijr','الحِجر',7,8,6,'وَقَالُواْ يَٰٓأَيُّهَا اَ۬لَّذِي نُزِّلَ عَلَيۡهِ اِ۬لذِّكۡرُ إِنَّكَ لَمَجۡنُونٞ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1815,14,'262',15,'Al-Ḥijr','الحِجر',8,9,7,'لَّوۡمَا تَأۡتِينَا بِالۡمَلَٰٓئِكَةِ إِن كُنتَ مِنَ اَ۬لصَّٰدِقِينَ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1816,14,'262',15,'Al-Ḥijr','الحِجر',9,10,8,'مَا تَنَزَّلُ اُ۬لۡمَلَٰٓئِكَةُ إِلَّا بِالۡحَقِّ وَمَا كَانُوٓاْ إِذٗا مُّنظَرِينَ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1817,14,'262',15,'Al-Ḥijr','الحِجر',10,10,9,'إِنَّا نَحۡنُ نَزَّلۡنَا اَ۬لذِّكۡرَ وَإِنَّا لَهُۥ لَحَٰفِظُونَ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1818,14,'262',15,'Al-Ḥijr','الحِجر',11,11,10,'وَلَقَدۡ أَرۡسَلۡنَا مِن قَبۡلِكَ فِي شِيَعِ اِ۬لۡأَوَّلِينَ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1819,14,'262',15,'Al-Ḥijr','الحِجر',11,12,11,'وَمَا يَأۡتِيهِم مِّن رَّسُولٍ إِلَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1820,14,'262',15,'Al-Ḥijr','الحِجر',12,13,12,'كَذَٰلِكَ نَسۡلُكُهُۥ فِي قُلُوبِ اِ۬لۡمُجۡرِمِينَ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1821,14,'262',15,'Al-Ḥijr','الحِجر',13,13,13,'لَا يُؤۡمِنُونَ بِهِۦ وَقَدۡ خَلَت سُّنَّةُ اُ۬لۡأَوَّلِينَ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1822,14,'262',15,'Al-Ḥijr','الحِجر',14,14,14,'وَلَوۡ فَتَحۡنَا عَلَيۡهِم بَابٗا مِّنَ اَ۬لسَّمَآءِ فَظَلُّواْ فِيهِ يَعۡرُجُونَ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1823,14,'262',15,'Al-Ḥijr','الحِجر',15,15,15,'لَقَالُوٓاْ إِنَّمَا سُكِّرَتۡ أَبۡصَٰرُنَا بَلۡ نَحۡنُ قَوۡمٞ مَّسۡحُورُونَ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1824,14,'263',15,'Al-Ḥijr','الحِجر',1,1,16,'وَلَقَد جَّعَلۡنَا فِي اِ۬لسَّمَآءِ بُرُوجٗا وَزَيَّنَّٰهَا لِلنَّٰظِرِينَ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1825,14,'263',15,'Al-Ḥijr','الحِجر',2,2,17,'وَحَفِظۡنَٰهَا مِن كُلِّ شَيۡطَٰنٖ رَّجِيمٍ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1826,14,'263',15,'Al-Ḥijr','الحِجر',2,3,18,'إِلَّا مَنِ اِ۪سۡتَرَقَ اَ۬لسَّمۡعَ فَأَتۡبَعَهُۥ شِهَابٞ مُّبِينٞ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1827,14,'263',15,'Al-Ḥijr','الحِجر',3,4,19,'وَاَلۡأَرۡضَ مَدَدۡنَٰهَا وَأَلۡقَيۡنَا فِيهَا رَوَٰسِيَ وَأَنۢبَتۡنَا فِيهَا مِن كُلِّ شَيۡءٖ مَّوۡزُونٖ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1828,14,'263',15,'Al-Ḥijr','الحِجر',4,5,20,'وَجَعَلۡنَا لَكُمۡ فِيهَا مَعَٰيِشَ وَمَن لَّسۡتُمۡ لَهُۥ بِرَٰزِقِينَ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1829,14,'263',15,'Al-Ḥijr','الحِجر',5,6,21,'وَإِن مِّن شَيۡءٍ إِلَّا عِندَنَا خَزَآئِنُهُۥ وَمَا نُنَزِّلُهُۥٓ إِلَّا بِقَدَرٖ مَّعۡلُومٖ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1830,14,'263',15,'Al-Ḥijr','الحِجر',6,8,22,'وَأَرۡسَلۡنَا اَ۬لرِّيَٰحَ لَوَٰقِحَ فَأَنزَلۡنَا مِنَ اَ۬لسَّمَآءِ مَآءٗ فَأَسۡقَيۡنَٰكُمُوهُ وَمَآ أَنتُمۡ لَهُۥ بِخَٰزِنِينَ ٢٢');
