INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2017,14,'279',16,'An-Naḥl','النَّحل',13,15,110,'ثُمَّ إِنَّ رَبَّكَ لِلَّذِينَ هَاجَرُواْ مِنۢ بَعۡدِ مَا فُتِنُواْ ثُمَّ جَٰهَدُواْ وَصَبَرُوٓاْ إِنَّ رَبَّكَ مِنۢ بَعۡدِهَا لَغَفُورٞ رَّحِيمٞ ١١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2018,14,'280',16,'An-Naḥl','النَّحل',1,2,111,'يَوۡمَ تَأۡتِي كُلُّ نَفۡسٖ تُجَٰدِلُ عَن نَّفۡسِهَا وَتُوَفَّىٰ كُلُّ نَفۡسٖ مَّا عَمِلَتۡ وَهُمۡ لَا يُظۡلَمُونَ ١١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2019,14,'280',16,'An-Naḥl','النَّحل',2,5,112,'۞ وَضَرَبَ اَ۬للَّهُ مَثَلٗا قَرۡيَةٗ كَانَتۡ ءَامِنَةٗ مُّطۡمَئِنَّةٗ يَأۡتِيهَا رِزۡقُهَا رَغَدٗا مِّن كُلِّ مَكَانٖ فَكَفَرَتۡ بِأَنۡعُمِ اِ۬للَّهِ فَأَذَٰقَهَا اَ۬للَّهُ لِبَاسَ اَ۬لۡجُوعِ وَاَلۡخَوۡفِ بِمَا كَانُواْ يَصۡنَعُونَ ١١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2020,14,'280',16,'An-Naḥl','النَّحل',5,6,113,'وَلَقَد جَّآءَهُمۡ رَسُولٞ مِّنۡهُمۡ فَكَذَّبُوهُ فَأَخَذَهُمُ اُ۬لۡعَذَابُ وَهُمۡ ظَٰلِمُونَ ١١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2021,14,'280',16,'An-Naḥl','النَّحل',7,8,114,'فَكُلُواْ مِمَّا رَزَقَكُمُ اُ۬للَّهُ حَلَٰلٗا طَيِّبٗا وَاَشۡكُرُواْ نِعۡمَتَ اَ۬للَّهِ إِن كُنتُمۡ إِيَّاهُ تَعۡبُدُونَ ١١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2022,14,'280',16,'An-Naḥl','النَّحل',8,11,115,'إِنَّمَا حَرَّمَ عَلَيۡكُمُ اُ۬لۡمَيۡتَةَ وَاَلدَّمَ وَلَحۡمَ اَ۬لۡخِنزِيرِ وَمَآ أُهِلَّ لِغَيۡرِ اِ۬للَّهِ بِهِۦۖ فَمَنِ اِ۟ضۡطُرَّ غَيۡرَ بَاغٖ وَلَا عَادٖ فَإِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٞ ١١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2023,14,'280',16,'An-Naḥl','النَّحل',11,13,116,'وَلَا تَقُولُواْ لِمَا تَصِفُ أَلۡسِنَتُكُمُ اُ۬لۡكَذِبَ هَٰذَا حَلَٰلٞ وَهَٰذَا حَرَامٞ لِّتَفۡتَرُواْ عَلَى اَ۬للَّهِ اِ۬لۡكَذِبَۚ إِنَّ اَ۬لَّذِينَ يَفۡتَرُونَ عَلَى اَ۬للَّهِ اِ۬لۡكَذِبَ لَا يُفۡلِحُونَ ١١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2024,14,'280',16,'An-Naḥl','النَّحل',13,14,117,'مَتَٰعٞ قَلِيلٞ وَلَهُمۡ عَذَابٌ أَلِيمٞ ١١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2025,14,'280',16,'An-Naḥl','النَّحل',14,15,118,'وَعَلَى اَ۬لَّذِينَ هَادُواْ حَرَّمۡنَا مَا قَصَصۡنَا عَلَيۡكَ مِن قَبۡلُۖ وَمَا ظَلَمۡنَٰهُمۡ وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ ١١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2026,14,'281',16,'An-Naḥl','النَّحل',1,2,119,'ثُمَّ إِنَّ رَبَّكَ لِلَّذِينَ عَمِلُواْ اُ۬لسُّوٓءَ بِجَهَٰلَةٖ ثُمَّ تَابُواْ مِنۢ بَعۡدِ ذَٰلِكَ وَأَصۡلَحُوٓاْ إِنَّ رَبَّكَ مِنۢ بَعۡدِهَا لَغَفُورٞ رَّحِيمٌ ١١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2027,14,'281',16,'An-Naḥl','النَّحل',2,3,120,'۞ إِنَّ إِبۡرَٰهِيمَ كَانَ أُمَّةٗ قَانِتٗا لِّلَّهِ حَنِيفٗا وَلَمۡ يَكُ مِنَ اَ۬لۡمُشۡرِكِينَ ١٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2028,14,'281',16,'An-Naḥl','النَّحل',4,4,121,'شَاكِرٗا لِّأَنۡعُمِهِۚ اِ۪جۡتَبَىٰهُ وَهَدَىٰهُ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ ١٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2029,14,'281',16,'An-Naḥl','النَّحل',5,5,122,'وَءَاتَيۡنَٰهُ فِي اِ۬لدُّنۡيۭا حَسَنَةٗۖ وَإِنَّهُۥ فِي اِ۬لۡأٓخِرَةِ لَمِنَ اَ۬لصَّٰلِحِينَ ١٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2030,14,'281',16,'An-Naḥl','النَّحل',6,7,123,'ثُمَّ أَوۡحَيۡنَآ إِلَيۡكَ أَنِ اِ۪تَّبِعۡ مِلَّةَ إِبۡرَٰهِيمَ حَنِيفٗاۖ وَمَا كَانَ مِنَ اَ۬لۡمُشۡرِكِينَ ١٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2031,14,'281',16,'An-Naḥl','النَّحل',7,9,124,'إِنَّمَا جُعِلَ اَ۬لسَّبۡتُ عَلَى اَ۬لَّذِينَ اَ۪خۡتَلَفُواْ فِيهِۚ وَإِنَّ رَبَّكَ لَيَحۡكُمُ بَيۡنَهُمۡ يَوۡمَ اَ۬لۡقِيَٰمَةِ فِيمَا كَانُواْ فِيهِ يَخۡتَلِفُونَ ١٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2032,14,'281',16,'An-Naḥl','النَّحل',9,11,125,'اَ۟دۡعُ إِلَىٰ سَبِيلِ رَبِّكَ بِالۡحِكۡمَةِ وَاَلۡمَوۡعِظَةِ اِ۬لۡحَسَنَةِۖ وَجَٰدِلۡهُم بِالَّتِي هِيَ أَحۡسَنُۚ إِنَّ رَبَّكَ هُوَ أَعۡلَمُ بِمَن ضَلَّ عَن سَبِيلِهِۦ وَهۡوَ أَعۡلَمُ بِالۡمُهۡتَدِينَ ١٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2033,14,'281',16,'An-Naḥl','النَّحل',12,13,126,'وَإِنۡ عَاقَبۡتُمۡ فَعَاقِبُواْ بِمِثۡلِ مَا عُوقِبۡتُم بِهِۦۖ وَلَئِن صَبَرۡتُمۡ لَهۡوَ خَيۡرٞ لِّلصَّٰبِرِينَ ١٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2034,14,'281',16,'An-Naḥl','النَّحل',13,14,127,'وَاَصۡبِرۡ وَمَا صَبۡرُكَ إِلَّا بِاللَّهِۚ وَلَا تَحۡزَنۡ عَلَيۡهِمۡ وَلَا تَكُ فِي ضَيۡقٖ مِّمَّا يَمۡكُرُونَ ١٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2035,14,'281',16,'An-Naḥl','النَّحل',15,15,128,'إِنَّ اَ۬للَّهَ مَعَ اَ۬لَّذِينَ اَ۪تَّقَواْ وَّاَلَّذِينَ هُم مُّحۡسِنُونَ ١٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2036,15,'282',17,'Al-Isrā’','الإسرَاء',3,5,1,'سُبۡحَٰنَ اَ۬لَّذِيٓ أَسۡر۪يٰ بِعَبۡدِهِۦ لَيۡلٗا مِّنَ اَ۬لۡمَسۡجِدِ اِ۬لۡحَرَامِ إِلَى اَ۬لۡمَسۡجِدِ اِ۬لۡأَقۡصَا اَ۬لَّذِي بَٰرَكۡنَا حَوۡلَهُۥ لِنُرِيَهُۥ مِنۡ ءَايَٰتِنَآۚ إِنَّهُۥ هُوَ اَ۬لسَّمِيعُ اُ۬لۡبَصِيرُ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2037,15,'282',17,'Al-Isrā’','الإسرَاء',5,6,2,'وَءَاتَيۡنَا مُوسَى اَ۬لۡكِتَٰبَ وَجَعَلۡنَٰهُ هُدٗى لِّبَنِيٓ إِسۡرَٰٓءِيلَ أَلَّا يَتَّخِذُواْ مِن دُونِي وَكِيلٗا ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2038,15,'282',17,'Al-Isrā’','الإسرَاء',7,7,3,'ذُرِّيَّةَ مَنۡ حَمَلۡنَا مَعَ نُوحٍۚ إِنَّهُۥ كَانَ عَبۡدٗا شَكُورٗا ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2039,15,'282',17,'Al-Isrā’','الإسرَاء',8,9,4,'وَقَضَيۡنَآ إِلَىٰ بَنِيٓ إِسۡرَٰٓءِيلَ فِي اِ۬لۡكِتَٰبِ لَتُفۡسِدُنَّ فِي اِ۬لۡأَرۡضِ مَرَّتَيۡنِ وَلَتَعۡلُنَّ عُلُوّٗا كَبِيرٗا ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2040,15,'282',17,'Al-Isrā’','الإسرَاء',9,11,5,'فَإِذَا جَآءَ وَعۡدُ أُولۭىٰهُمَا بَعَثۡنَا عَلَيۡكُمۡ عِبَادٗا لَّنَآ أُوْلِي بَأۡسٖ شَدِيدٖ فَجَاسُواْ خِلَٰلَ اَ۬لدِّي۪ارِۚ وَكَانَ وَعۡدٗا مَّفۡعُولٗا ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2041,15,'282',17,'Al-Isrā’','الإسرَاء',11,12,6,'ثُمَّ رَدَدۡنَا لَكُمُ اُ۬لۡكَرَّةَ عَلَيۡهِمۡ وَأَمۡدَدۡنَٰكُم بِأَمۡوَٰلٖ وَبَنِينَ وَجَعَلۡنَٰكُمۡ أَكۡثَرَ نَفِيرًا ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2042,15,'282',17,'Al-Isrā’','الإسرَاء',13,15,7,'إِنۡ أَحۡسَنتُمۡ أَحۡسَنتُمۡ لِأَنفُسِكُمۡۖ وَإِنۡ أَسَأۡتُمۡ فَلَهَاۚ فَإِذَا جَآءَ وَعۡدُ اُ۬لۡأٓخِرَةِ لِيَسُ‍ُٔواْ وُجُوهَكُمۡ وَلِيَدۡخُلُواْ اُ۬لۡمَسۡجِدَ كَمَا دَخَلُوهُ أَوَّلَ مَرَّةٖ وَلِيُتَبِّرُواْ مَا عَلَوۡاْ تَتۡبِيرًا ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2043,15,'283',17,'Al-Isrā’','الإسرَاء',1,2,8,'عَسَىٰ رَبُّكُمۡ أَن يَرۡحَمَكُمۡۚ وَإِنۡ عُدتُّمۡ عُدۡنَاۘ وَجَعَلۡنَا جَهَنَّمَ لِلۡكٰ۪فِرِينَ حَصِيرًا ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2044,15,'283',17,'Al-Isrā’','الإسرَاء',2,3,9,'إِنَّ هَٰذَا اَ۬لۡقُرۡءَانَ يَهۡدِي لِلَّتِي هِيَ أَقۡوَمُ وَيُبَشِّرُ اُ۬لۡمُؤۡمِنِينَ اَ۬لَّذِينَ يَعۡمَلُونَ اَ۬لصَّٰلِحَٰتِ أَنَّ لَهُمۡ أَجۡرٗا كَبِيرٗا ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2045,15,'283',17,'Al-Isrā’','الإسرَاء',4,4,10,'وَأَنَّ اَ۬لَّذِينَ لَا يُؤۡمِنُونَ بِالۡأٓخِرَةِ أَعۡتَدۡنَا لَهُمۡ عَذَابًا أَلِيمٗا ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2046,15,'283',17,'Al-Isrā’','الإسرَاء',5,5,11,'۞ وَيَدۡعُ اُ۬لۡإِنسَٰنُ بِالشَّرِّ دُعَآءَهُۥ بِالۡخَيۡرِۖ وَكَانَ اَ۬لۡإِنسَٰنُ عَجُولٗا ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2047,15,'283',17,'Al-Isrā’','الإسرَاء',6,8,12,'وَجَعَلۡنَا اَ۬لَّيۡلَ وَاَلنَّهَارَ ءَايَتَيۡنِۖ فَمَحَوۡنَآ ءَايَةَ اَ۬لَّيۡلِ وَجَعَلۡنَآ ءَايَةَ اَ۬لنَّه۪ارِ مُبۡصِرَةٗ لِّتَبۡتَغُواْ فَضۡلٗا مِّن رَّبِّكُمۡ وَلِتَعۡلَمُواْ عَدَدَ اَ۬لسِّنِينَ وَاَلۡحِسَابَۚ وَكُلَّ شَيۡءٖ فَصَّلۡنَٰهُ تَفۡصِيلٗا ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2048,15,'283',17,'Al-Isrā’','الإسرَاء',8,10,13,'وَكُلَّ إِنسَٰنٍ أَلۡزَمۡنَٰهُ طَٰٓئِرَهُۥ فِي عُنُقِهِۦۖ وَنُخۡرِجُ لَهُۥ يَوۡمَ اَ۬لۡقِيَٰمَةِ كِتَٰبٗا يَلۡقَىٰهُ مَنشُورًا ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2049,15,'283',17,'Al-Isrā’','الإسرَاء',10,10,14,'اِ۪قۡرَأۡ كِتَٰبَكَ كَفَىٰ بِنَفۡسِكَ اَ۬لۡيَوۡمَ عَلَيۡكَ حَسِيبٗا ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2050,15,'283',17,'Al-Isrā’','الإسرَاء',11,13,15,'مَّنِ اِ۪هۡتَدَىٰ فَإِنَّمَا يَهۡتَدِي لِنَفۡسِهِۦۖ وَمَن ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيۡهَاۚ وَلَا تَزِرُ وَازِرَةٞ وِزۡرَ أُخۡر۪يٰۗ وَمَا كُنَّا مُعَذِّبِينَ حَتَّىٰ نَبۡعَثَ رَسُولٗا ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2051,15,'283',17,'Al-Isrā’','الإسرَاء',13,14,16,'وَإِذَآ أَرَدۡنَآ أَن نُّهۡلِكَ قَرۡيَةً أَمَرۡنَا مُتۡرَفِيهَا فَفَسَقُواْ فِيهَا فَحَقَّ عَلَيۡهَا اَ۬لۡقَوۡلُ فَدَمَّرۡنَٰهَا تَدۡمِيرٗا ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2052,15,'283',17,'Al-Isrā’','الإسرَاء',14,15,17,'وَكَمۡ أَهۡلَكۡنَا مِنَ اَ۬لۡقُرُونِ مِنۢ بَعۡدِ نُوحٖۗ وَكَفَىٰ بِرَبِّكَ بِذُنُوبِ عِبَادِهِۦ خَبِيرَۢا بَصِيرٗا ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2053,15,'284',17,'Al-Isrā’','الإسرَاء',1,2,18,'مَّن كَانَ يُرِيدُ اُ۬لۡعَاجِلَةَ عَجَّلۡنَا لَهُۥ فِيهَا مَا نَشَآءُ لِمَن نُّرِيدُ ثُمَّ جَعَلۡنَا لَهُۥ جَهَنَّمَ يَصۡلَىٰهَا مَذۡمُومٗا مَّدۡحُورٗا ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2054,15,'284',17,'Al-Isrā’','الإسرَاء',2,4,19,'وَمَنۡ أَرَادَ اَ۬لۡأٓخِرَةَ وَسَعَىٰ لَهَا سَعۡيَهَا وَهۡوَ مُؤۡمِنٞ فَأُوْلَٰٓئِكَ كَانَ سَعۡيُهُم مَّشۡكُورٗا ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2055,15,'284',17,'Al-Isrā’','الإسرَاء',4,5,20,'كُلّٗا نُّمِدُّ هَٰٓؤُلَآءِ وَهَٰٓؤُلَآءِ مِنۡ عَطَآءِ رَبِّكَۚ وَمَا كَانَ عَطَآءُ رَبِّكَ مَحۡظُورًا ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2056,15,'284',17,'Al-Isrā’','الإسرَاء',5,7,21,'اِ۟نظُرۡ كَيۡفَ فَضَّلۡنَا بَعۡضَهُمۡ عَلَىٰ بَعۡضٖۚ وَلَلۡأٓخِرَةُ أَكۡبَرُ دَرَجَٰتٖ وَأَكۡبَرُ تَفۡضِيلٗا ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2057,15,'284',17,'Al-Isrā’','الإسرَاء',7,8,22,'لَّا تَجۡعَلۡ مَعَ اَ۬للَّهِ إِلَٰهًا ءَاخَرَ فَتَقۡعُدَ مَذۡمُومٗا مَّخۡذُولٗا ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2058,15,'284',17,'Al-Isrā’','الإسرَاء',8,10,23,'۞ وَقَضَىٰ رَبُّكَ أَلَّا تَعۡبُدُوٓاْ إِلَّآ إِيَّاهُ وَبِالۡوَٰلِدَيۡنِ إِحۡسَٰنًاۚ إِمَّا يَبۡلُغَنَّ عِندَكَ اَ۬لۡكِبَرَ أَحَدُهُمَآ أَوۡ كِلَاهُمَا فَلَا تَقُل لَّهُمَآ أُفِّ وَلَا تَنۡهَرۡهُمَا وَقُل لَّهُمَا قَوۡلٗا كَرِيمٗا ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2059,15,'284',17,'Al-Isrā’','الإسرَاء',10,12,24,'وَاَخۡفِضۡ لَهُمَا جَنَاحَ اَ۬لذُّلِّ مِنَ اَ۬لرَّحۡمَةِ وَقُل رَّبِّ اِ۪رۡحَمۡهُمَا كَمَا رَبَّيَانِي صَغِيرٗا ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2060,15,'284',17,'Al-Isrā’','الإسرَاء',12,13,25,'رَّبُّكُمۡ أَعۡلَمُ بِمَا فِي نُفُوسِكُمۡۚ إِن تَكُونُواْ صَٰلِحِينَ فَإِنَّهُۥ كَانَ لِلۡأَوَّٰبِينَ غَفُورٗا ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2061,15,'284',17,'Al-Isrā’','الإسرَاء',13,14,26,'وَءَاتِ ذَا اَ۬لۡقُرۡبۭيٰ حَقَّهُۥ وَاَلۡمِسۡكِينَ وَاَبۡنَ اَ۬لسَّبِيلِ وَلَا تُبَذِّرۡ تَبۡذِيرًا ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2062,15,'284',17,'Al-Isrā’','الإسرَاء',14,15,27,'إِنَّ اَ۬لۡمُبَذِّرِينَ كَانُوٓاْ إِخۡوَٰنَ اَ۬لشَّيَٰطِينِۖ وَكَانَ اَ۬لشَّيۡطَٰنُ لِرَبِّهِۦ كَفُورٗا ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2063,15,'285',17,'Al-Isrā’','الإسرَاء',1,2,28,'وَإِمَّا تُعۡرِضَنَّ عَنۡهُمُ اُ۪بۡتِغَآءَ رَحۡمَةٖ مِّن رَّبِّكَ تَرۡجُوهَا فَقُل لَّهُمۡ قَوۡلٗا مَّيۡسُورٗا ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2064,15,'285',17,'Al-Isrā’','الإسرَاء',2,3,29,'وَلَا تَجۡعَلۡ يَدَكَ مَغۡلُولَةً إِلَىٰ عُنُقِكَ وَلَا تَبۡسُطۡهَا كُلَّ اَ۬لۡبَسۡطِ فَتَقۡعُدَ مَلُومٗا مَّحۡسُورًا ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2065,15,'285',17,'Al-Isrā’','الإسرَاء',3,4,30,'إِنَّ رَبَّكَ يَبۡسُطُ اُ۬لرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُۚ إِنَّهُۥ كَانَ بِعِبَادِهِۦ خَبِيرَۢا بَصِيرٗا ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2066,15,'285',17,'Al-Isrā’','الإسرَاء',4,6,31,'وَلَا تَقۡتُلُوٓاْ أَوۡلَٰدَكُمۡ خَشۡيَةَ إِمۡلَٰقٖۖ نَّحۡنُ نَرۡزُقُهُمۡ وَإِيَّاكُمۡۚ إِنَّ قَتۡلَهُمۡ كَانَ خِطۡـٔٗا كَبِيرٗا ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2067,15,'285',17,'Al-Isrā’','الإسرَاء',6,7,32,'وَلَا تَقۡرَبُواْ اُ۬لزِّنَىٰٓۖ إِنَّهُۥ كَانَ فَٰحِشَةٗ وَسَآءَ سَبِيلٗا ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2068,15,'285',17,'Al-Isrā’','الإسرَاء',7,9,33,'وَلَا تَقۡتُلُواْ اُ۬لنَّفۡسَ اَ۬لَّتِي حَرَّمَ اَ۬للَّهُ إِلَّا بِالۡحَقِّۗ وَمَن قُتِلَ مَظۡلُومٗا فَقَد جَّعَلۡنَا لِوَلِيِّهِۦ سُلۡطَٰنٗا فَلَا يُسۡرِف فِّي اِ۬لۡقَتۡلِۖ إِنَّهُۥ كَانَ مَنصُورٗا ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2069,15,'285',17,'Al-Isrā’','الإسرَاء',9,11,34,'وَلَا تَقۡرَبُواْ مَالَ اَ۬لۡيَتِيمِ إِلَّا بِالَّتِي هِيَ أَحۡسَنُ حَتَّىٰ يَبۡلُغَ أَشُدَّهُۥۚ وَأَوۡفُواْ بِالۡعَهۡدِۖ إِنَّ اَ۬لۡعَهۡدَ كَانَ مَسۡـُٔولٗا ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2070,15,'285',17,'Al-Isrā’','الإسرَاء',11,12,35,'وَأَوۡفُواْ اُ۬لۡكَيۡلَ إِذَا كِلۡتُمۡ وَزِنُواْ بِالۡقُسۡطَاسِ اِ۬لۡمُسۡتَقِيمِۚ ذَٰلِكَ خَيۡرٞ وَأَحۡسَنُ تَأۡوِيلٗا ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2071,15,'285',17,'Al-Isrā’','الإسرَاء',12,13,36,'۞ وَلَا تَقۡفُ مَا لَيۡسَ لَكَ بِهِۦ عِلۡمٌۚ إِنَّ اَ۬لسَّمۡعَ وَاَلۡبَصَرَ وَاَلۡفُؤَادَ كُلُّ أُوْلَٰٓئِكَ كَانَ عَنۡهُ مَسۡـُٔولٗا ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2072,15,'285',17,'Al-Isrā’','الإسرَاء',14,15,37,'وَلَا تَمۡشِ فِي اِ۬لۡأَرۡضِ مَرَحًاۖ إِنَّكَ لَن تَخۡرِقَ اَ۬لۡأَرۡضَ وَلَن تَبۡلُغَ اَ۬لۡجِبَالَ طُولٗا ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2073,15,'285',17,'Al-Isrā’','الإسرَاء',15,15,38,'كُلُّ ذَٰلِكَ كَانَ سَيِّئَةً عِندَ رَبِّكَ مَكۡرُوهٗا ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2074,15,'286',17,'Al-Isrā’','الإسرَاء',1,2,39,'ذَٰلِكَ مِمَّآ أَوۡحَىٰٓ إِلَيۡكَ رَبُّكَ مِنَ اَ۬لۡحِكۡمَةِۗ وَلَا تَجۡعَلۡ مَعَ اَ۬للَّهِ إِلَٰهًا ءَاخَرَ فَتُلۡقَىٰ فِي جَهَنَّمَ مَلُومٗا مَّدۡحُورًا ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2075,15,'286',17,'Al-Isrā’','الإسرَاء',2,3,40,'أَفَأَصۡفَىٰكُمۡ رَبُّكُم بِالۡبَنِينَ وَاَتَّخَذَ مِنَ اَ۬لۡمَلَٰٓئِكَةِ إِنَٰثًاۚ إِنَّكُمۡ لَتَقُولُونَ قَوۡلًا عَظِيمٗا ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2076,15,'286',17,'Al-Isrā’','الإسرَاء',4,4,41,'وَلَقَد صَّرَّفۡنَا فِي هَٰذَا اَ۬لۡقُرۡءَانِ لِيَذَّكَّرُواْ وَمَا يَزِيدُهُمۡ إِلَّا نُفُورٗا ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2077,15,'286',17,'Al-Isrā’','الإسرَاء',5,5,42,'قُل لَّوۡ كَانَ مَعَهُۥٓ ءَالِهَةٞ كَمَا تَقُولُونَ إِذٗا لَّاَبۡتَغَوۡاْ إِلَىٰ ذِي اِ۬لۡعَرۡشِ سَبِيلٗا ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2078,15,'286',17,'Al-Isrā’','الإسرَاء',6,6,43,'سُبۡحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يَقُولُونَ عُلُوّٗا كَبِيرٗا ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2079,15,'286',17,'Al-Isrā’','الإسرَاء',6,8,44,'تُسَبِّحُ لَهُ اُ۬لسَّمَٰوَٰتُ اُ۬لسَّبۡعُ وَاَلۡأَرۡضُ وَمَن فِيهِنَّۚ وَإِن مِّن شَيۡءٍ إِلَّا يُسَبِّحُ بِحَمۡدِهِۦ وَلَٰكِن لَّا تَفۡقَهُونَ تَسۡبِيحَهُمۡۗ إِنَّهُۥ كَانَ حَلِيمًا غَفُورٗا ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2080,15,'286',17,'Al-Isrā’','الإسرَاء',8,10,45,'وَإِذَا قَرَأۡتَ اَ۬لۡقُرۡءَانَ جَعَلۡنَا بَيۡنَكَ وَبَيۡنَ اَ۬لَّذِينَ لَا يُؤۡمِنُونَ بِالۡأٓخِرَةِ حِجَابٗا مَّسۡتُورٗا ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2081,15,'286',17,'Al-Isrā’','الإسرَاء',10,11,46,'وَجَعَلۡنَا عَلَىٰ قُلُوبِهِمۡ أَكِنَّةً أَن يَفۡقَهُوهُ وَفِيٓ ءَاذَانِهِمۡ وَقۡرٗاۚ وَإِذَا ذَكَرۡتَ رَبَّكَ فِي اِ۬لۡقُرۡءَانِ وَحۡدَهُۥ وَلَّوۡاْ عَلَىٰٓ أَدۡبٰ۪رِهِمۡ نُفُورٗا ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2082,15,'286',17,'Al-Isrā’','الإسرَاء',12,13,47,'نَّحۡنُ أَعۡلَمُ بِمَا يَسۡتَمِعُونَ بِهِۦٓ إِذۡ يَسۡتَمِعُونَ إِلَيۡكَ وَإِذۡ هُمۡ نَجۡوۭيٰٓ إِذۡ يَقُولُ اُ۬لظَّٰلِمُونَ إِن تَتَّبِعُونَ إِلَّا رَجُلٗا مَّسۡحُورًا ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2083,15,'286',17,'Al-Isrā’','الإسرَاء',13,14,48,'اِ۟نظُرۡ كَيۡفَ ضَرَبُواْ لَكَ اَ۬لۡأَمۡثَالَ فَضَلُّواْ فَلَا يَسۡتَطِيعُونَ سَبِيلٗا ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2084,15,'286',17,'Al-Isrā’','الإسرَاء',15,15,49,'وَقَالُوٓاْ أَٰ۟ذَا كُنَّا عِظَٰمٗا وَرُفَٰتًا أَٰ۟نَّا لَمَبۡعُوثُونَ خَلۡقٗا جَدِيدٗا ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2085,15,'287',17,'Al-Isrā’','الإسرَاء',1,1,50,'۞ قُلۡ كُونُواْ حِجَارَةً أَوۡ حَدِيدًا ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2086,15,'287',17,'Al-Isrā’','الإسرَاء',1,4,51,'أَوۡ خَلۡقٗا مِّمَّا يَكۡبُرُ فِي صُدُورِكُمۡۚ فَسَيَقُولُونَ مَن يُعِيدُنَاۖ قُلِ اِ۬لَّذِي فَطَرَكُمۡ أَوَّلَ مَرَّةٖۚ فَسَيُنۡغِضُونَ إِلَيۡكَ رُءُوسَهُمۡ وَيَقُولُونَ مَتَىٰ هُوَۖ قُلۡ عَسَىٰٓ أَن يَكُونَ قَرِيبٗا ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2087,15,'287',17,'Al-Isrā’','الإسرَاء',4,5,52,'يَوۡمَ يَدۡعُوكُمۡ فَتَسۡتَجِيبُونَ بِحَمۡدِهِۦ وَتَظُنُّونَ إِن لَّبِثتُّمۡ إِلَّا قَلِيلٗا ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2088,15,'287',17,'Al-Isrā’','الإسرَاء',5,7,53,'وَقُل لِّعِبَادِي يَقُولُواْ اُ۬لَّتِي هِيَ أَحۡسَنُۚ إِنَّ اَ۬لشَّيۡطَٰنَ يَنزَغُ بَيۡنَهُمۡۚ إِنَّ اَ۬لشَّيۡطَٰنَ كَانَ لِلۡإِنسَٰنِ عَدُوّٗا مُّبِينٗا ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2089,15,'287',17,'Al-Isrā’','الإسرَاء',7,8,54,'رَّبُّكُمۡ أَعۡلَمُ بِكُمۡۖ إِن يَشَأۡ يَرۡحَمۡكُمۡ أَوۡ إِن يَشَأۡ يُعَذِّبۡكُمۡۚ وَمَآ أَرۡسَلۡنَٰكَ عَلَيۡهِمۡ وَكِيلٗا ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2090,15,'287',17,'Al-Isrā’','الإسرَاء',8,10,55,'وَرَبُّكَ أَعۡلَمُ بِمَن فِي اِ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِۗ وَلَقَدۡ فَضَّلۡنَا بَعۡضَ اَ۬لنَّبِيِّـۧنَ عَلَىٰ بَعۡضٖۖ وَءَاتَيۡنَا دَاوُۥدَ زَبُورٗا ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2091,15,'287',17,'Al-Isrā’','الإسرَاء',10,11,56,'قُلُ اُ۟دۡعُواْ اُ۬لَّذِينَ زَعَمۡتُم مِّن دُونِهِۦ فَلَا يَمۡلِكُونَ كَشۡفَ اَ۬لضُّرِّ عَنكُمۡ وَلَا تَحۡوِيلًا ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2092,15,'287',17,'Al-Isrā’','الإسرَاء',11,14,57,'أُوْلَٰٓئِكَ اَ۬لَّذِينَ يَدۡعُونَ يَبۡتَغُونَ إِلَىٰ رَبِّهِمِ اِ۬لۡوَسِيلَةَ أَيُّهُمۡ أَقۡرَبُ وَيَرۡجُونَ رَحۡمَتَهُۥ وَيَخَافُونَ عَذَابَهُۥٓۚ إِنَّ عَذَابَ رَبِّكَ كَانَ مَحۡذُورٗا ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2093,15,'287',17,'Al-Isrā’','الإسرَاء',14,15,58,'وَإِن مِّن قَرۡيَةٍ إِلَّا نَحۡنُ مُهۡلِكُوهَا قَبۡلَ يَوۡمِ اِ۬لۡقِيَٰمَةِ أَوۡ مُعَذِّبُوهَا عَذَابٗا شَدِيدٗاۚ كَانَ ذَٰلِكَ فِي اِ۬لۡكِتَٰبِ مَسۡطُورٗا ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2094,15,'288',17,'Al-Isrā’','الإسرَاء',1,3,59,'۞ وَمَا مَنَعَنَآ أَن نُّرۡسِلَ بِالۡأٓيَٰتِ إِلَّآ أَن كَذَّبَ بِهَا اَ۬لۡأَوَّلُونَۚ وَءَاتَيۡنَا ثَمُودَ اَ۬لنَّاقَةَ مُبۡصِرَةٗ فَظَلَمُواْ بِهَاۚ وَمَا نُرۡسِلُ بِالۡأٓيَٰتِ إِلَّا تَخۡوِيفٗا ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2095,15,'288',17,'Al-Isrā’','الإسرَاء',3,5,60,'وَإِذۡ قُلۡنَا لَكَ إِنَّ رَبَّكَ أَحَاطَ بِالنّ۪اسِۚ وَمَا جَعَلۡنَا اَ۬لرُّءۡيَا اَ۬لَّتِيٓ أَرَيۡنَٰكَ إِلَّا فِتۡنَةٗ لِّلنّ۪اسِ وَاَلشَّجَرَةَ اَ۬لۡمَلۡعُونَةَ فِي اِ۬لۡقُرۡءَانِۚ وَنُخَوِّفُهُمۡ فَمَا يَزِيدُهُمۡ إِلَّا طُغۡيَٰنٗا كَبِيرٗا ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2096,15,'288',17,'Al-Isrā’','الإسرَاء',6,7,61,'وَإِذۡ قُلۡنَا لِلۡمَلَٰٓئِكَةِ اِ۟سۡجُدُواْ لِأٓدَمَ فَسَجَدُوٓاْ إِلَّآ إِبۡلِيسَ قَالَ ءَٰا۬سۡجُدُ لِمَنۡ خَلَقۡتَ طِينٗا ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2097,15,'288',17,'Al-Isrā’','الإسرَاء',7,9,62,'قَالَ أَرَءَيۡتَكَ هَٰذَا اَ۬لَّذِي كَرَّمۡتَ عَلَيَّ لَئِنۡ أَخَّرۡتَنِۦٓ إِلَىٰ يَوۡمِ اِ۬لۡقِيَٰمَةِ لَأَحۡتَنِكَنَّ ذُرِّيَّتَهُۥٓ إِلَّا قَلِيلٗا ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2098,15,'288',17,'Al-Isrā’','الإسرَاء',9,10,63,'قَالَ اَ۪ذۡهَب فَّمَن تَبِعَكَ مِنۡهُمۡ فَإِنَّ جَهَنَّمَ جَزَآؤُكُمۡ جَزَآءٗ مَّوۡفُورٗا ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2099,15,'288',17,'Al-Isrā’','الإسرَاء',10,13,64,'وَاَسۡتَفۡزِزۡ مَنِ اِ۪سۡتَطَعۡتَ مِنۡهُم بِصَوۡتِكَ وَأَجۡلِبۡ عَلَيۡهِم بِخَيۡلِكَ وَرَجۡلِكَ وَشَارِكۡهُمۡ فِي اِ۬لۡأَمۡوَٰلِ وَاَلۡأَوۡلَٰدِ وَعِدۡهُمۡۚ وَمَا يَعِدُهُمُ اُ۬لشَّيۡطَٰنُ إِلَّا غُرُورًا ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2100,15,'288',17,'Al-Isrā’','الإسرَاء',13,14,65,'إِنَّ عِبَادِي لَيۡسَ لَكَ عَلَيۡهِمۡ سُلۡطَٰنٞۚ وَكَفَىٰ بِرَبِّكَ وَكِيلٗا ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2101,15,'288',17,'Al-Isrā’','الإسرَاء',14,15,66,'رَّبُّكُمُ اُ۬لَّذِي يُزۡجِي لَكُمُ اُ۬لۡفُلۡكَ فِي اِ۬لۡبَحۡرِ لِتَبۡتَغُواْ مِن فَضۡلِهِۦٓۚ إِنَّهُۥ كَانَ بِكُمۡ رَحِيمٗا ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2102,15,'289',17,'Al-Isrā’','الإسرَاء',1,2,67,'وَإِذَا مَسَّكُمُ اُ۬لضُّرُّ فِي اِ۬لۡبَحۡرِ ضَلَّ مَن تَدۡعُونَ إِلَّآ إِيَّاهُۖ فَلَمَّا نَجَّىٰكُمۡ إِلَى اَ۬لۡبَرِّ أَعۡرَضۡتُمۡۚ وَكَانَ اَ۬لۡإِنسَٰنُ كَفُورًا ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2103,15,'289',17,'Al-Isrā’','الإسرَاء',2,4,68,'أَفَأَمِنتُمۡ أَن نَّخۡسِفَ بِكُمۡ جَانِبَ اَ۬لۡبَرِّ أَوۡ نُرۡسِلَ عَلَيۡكُمۡ حَاصِبٗا ثُمَّ لَا تَجِدُواْ لَكُمۡ وَكِيلًا ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2104,15,'289',17,'Al-Isrā’','الإسرَاء',4,6,69,'أَمۡ أَمِنتُمۡ أَن نُّعِيدَكُمۡ فِيهِ تَارَةً أُخۡر۪يٰ فَنُرۡسِلَ عَلَيۡكُمۡ قَاصِفٗا مِّنَ اَ۬لرِّيحِ فَنُغۡرِقَكُم بِمَا كَفَرۡتُمۡ ثُمَّ لَا تَجِدُواْ لَكُمۡ عَلَيۡنَا بِهِۦ تَبِيعٗا ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2105,15,'289',17,'Al-Isrā’','الإسرَاء',6,8,70,'۞ وَلَقَدۡ كَرَّمۡنَا بَنِيٓ ءَادَمَ وَحَمَلۡنَٰهُمۡ فِي اِ۬لۡبَرِّ وَاَلۡبَحۡرِ وَرَزَقۡنَٰهُم مِّنَ اَ۬لطَّيِّبَٰتِ وَفَضَّلۡنَٰهُمۡ عَلَىٰ كَثِيرٖ مِّمَّنۡ خَلَقۡنَا تَفۡضِيلٗا ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2106,15,'289',17,'Al-Isrā’','الإسرَاء',8,10,71,'يَوۡمَ نَدۡعُواْ كُلَّ أُنَاسِۢ بِإِمَٰمِهِمۡۖ فَمَنۡ أُوتِيَ كِتَٰبَهُۥ بِيَمِينِهِۦ فَأُوْلَٰٓئِكَ يَقۡرَءُونَ كِتَٰبَهُمۡ وَلَا يُظۡلَمُونَ فَتِيلٗا ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2107,15,'289',17,'Al-Isrā’','الإسرَاء',10,11,72,'وَمَن كَانَ فِي هَٰذِهِۦٓ أَعۡم۪يٰ فَهۡوَ فِي اِ۬لۡأٓخِرَةِ أَعۡمَىٰ وَأَضَلُّ سَبِيلٗا ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2108,15,'289',17,'Al-Isrā’','الإسرَاء',11,13,73,'وَإِن كَادُواْ لَيَفۡتِنُونَكَ عَنِ اِ۬لَّذِيٓ أَوۡحَيۡنَآ إِلَيۡكَ لِتَفۡتَرِيَ عَلَيۡنَا غَيۡرَهُۥۖ وَإِذٗا لَّاَتَّخَذُوكَ خَلِيلٗا ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2109,15,'289',17,'Al-Isrā’','الإسرَاء',13,14,74,'وَلَوۡلَآ أَن ثَبَّتۡنَٰكَ لَقَدۡ كِدتَّ تَرۡكَنُ إِلَيۡهِمۡ شَيۡـٔٗا قَلِيلًا ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2110,15,'289',17,'Al-Isrā’','الإسرَاء',14,15,75,'إِذٗا لَّأَذَقۡنَٰكَ ضِعۡفَ اَ۬لۡحَيَوٰةِ وَضِعۡفَ اَ۬لۡمَمَاتِ ثُمَّ لَا تَجِدُ لَكَ عَلَيۡنَا نَصِيرٗا ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2111,15,'290',17,'Al-Isrā’','الإسرَاء',1,2,76,'وَإِن كَادُواْ لَيَسۡتَفِزُّونَكَ مِنَ اَ۬لۡأَرۡضِ لِيُخۡرِجُوكَ مِنۡهَاۖ وَإِذٗا لَّا يَلۡبَثُونَ خَلۡفَكَ إِلَّا قَلِيلٗا ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2112,15,'290',17,'Al-Isrā’','الإسرَاء',2,3,77,'سُنَّةَ مَن قَدۡ أَرۡسَلۡنَا قَبۡلَكَ مِن رُّسۡلِنَاۖ وَلَا تَجِدُ لِسُنَّتِنَا تَحۡوِيلًا ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2113,15,'290',17,'Al-Isrā’','الإسرَاء',3,5,78,'أَقِمِ اِ۬لصَّلَوٰةَ لِدُلُوكِ اِ۬لشَّمۡسِ إِلَىٰ غَسَقِ اِ۬لَّيۡلِ وَقُرۡءَانَ اَ۬لۡفَجۡرِۖ إِنَّ قُرۡءَانَ اَ۬لۡفَجۡرِ كَانَ مَشۡهُودٗا ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2114,15,'290',17,'Al-Isrā’','الإسرَاء',5,6,79,'وَمِنَ اَ۬لَّيۡلِ فَتَهَجَّدۡ بِهِۦ نَافِلَةٗ لَّكَ عَسَىٰٓ أَن يَبۡعَثَكَ رَبُّكَ مَقَامٗا مَّحۡمُودٗا ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2115,15,'290',17,'Al-Isrā’','الإسرَاء',7,8,80,'وَقُل رَّبِّ أَدۡخِلۡنِي مُدۡخَلَ صِدۡقٖ وَأَخۡرِجۡنِي مُخۡرَجَ صِدۡقٖ وَاَجۡعَل لِّي مِن لَّدُنكَ سُلۡطَٰنٗا نَّصِيرٗا ٨٠');
