INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2117,15,'290',17,'Al-Isrā’','الإسرَاء',9,10,82,'وَنُنزِلُ مِنَ اَ۬لۡقُرۡءَانِ مَا هُوَ شِفَآءٞ وَرَحۡمَةٞ لِّلۡمُؤۡمِنِينَ وَلَا يَزِيدُ اُ۬لظَّٰلِمِينَ إِلَّا خَسَارٗا ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2118,15,'290',17,'Al-Isrā’','الإسرَاء',11,12,83,'وَإِذَآ أَنۡعَمۡنَا عَلَى اَ۬لۡإِنسَٰنِ أَعۡرَضَ وَنَـَٔا بِجَانِبِهِۦ وَإِذَا مَسَّهُ اُ۬لشَّرُّ كَانَ يَـُٔوسٗا ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2119,15,'290',17,'Al-Isrā’','الإسرَاء',12,13,84,'قُلۡ كُلّٞ يَعۡمَلُ عَلَىٰ شَاكِلَتِهِۦ فَرَبُّكُمۡ أَعۡلَمُ بِمَنۡ هُوَ أَهۡدَىٰ سَبِيلٗا ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2120,15,'290',17,'Al-Isrā’','الإسرَاء',13,14,85,'وَيَسۡـَٔلُونَكَ عَنِ اِ۬لرُّوحِۖ قُلِ اِ۬لرُّوحُ مِنۡ أَمۡرِ رَبِّي وَمَآ أُوتِيتُم مِّنَ اَ۬لۡعِلۡمِ إِلَّا قَلِيلٗا ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2121,15,'290',17,'Al-Isrā’','الإسرَاء',14,15,86,'وَلَئِن شِئۡنَا لَنَذۡهَبَنَّ بِالَّذِيٓ أَوۡحَيۡنَآ إِلَيۡكَ ثُمَّ لَا تَجِدُ لَكَ بِهِۦ عَلَيۡنَا وَكِيلًا ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2122,15,'291',17,'Al-Isrā’','الإسرَاء',1,1,87,'إِلَّا رَحۡمَةٗ مِّن رَّبِّكَۚ إِنَّ فَضۡلَهُۥ كَانَ عَلَيۡكَ كَبِيرٗا ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2123,15,'291',17,'Al-Isrā’','الإسرَاء',1,3,88,'۞ قُل لَّئِنِ اِ۪جۡتَمَعَتِ اِ۬لۡإِنسُ وَاَلۡجِنُّ عَلَىٰٓ أَن يَأۡتُواْ بِمِثۡلِ هَٰذَا اَ۬لۡقُرۡءَانِ لَا يَأۡتُونَ بِمِثۡلِهِۦ وَلَوۡ كَانَ بَعۡضُهُمۡ لِبَعۡضٖ ظَهِيرٗا ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2124,15,'291',17,'Al-Isrā’','الإسرَاء',4,5,89,'وَلَقَد صَّرَّفۡنَا لِلنّ۪اسِ فِي هَٰذَا اَ۬لۡقُرۡءَانِ مِن كُلِّ مَثَلٖ فَأَبَىٰٓ أَكۡثَرُ اُ۬لنّ۪اسِ إِلَّا كُفُورٗا ٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2125,15,'291',17,'Al-Isrā’','الإسرَاء',5,6,90,'وَقَالُواْ لَن نُّؤۡمِنَ لَكَ حَتَّىٰ تُفَجِّرَ لَنَا مِنَ اَ۬لۡأَرۡضِ يَنۢبُوعًا ٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2126,15,'291',17,'Al-Isrā’','الإسرَاء',6,7,91,'أَوۡ تَكُونَ لَكَ جَنَّةٞ مِّن نَّخِيلٖ وَعِنَبٖ فَتُفَجِّرَ اَ۬لۡأَنۡهَٰرَ خِلَٰلَهَا تَفۡجِيرًا ٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2127,15,'291',17,'Al-Isrā’','الإسرَاء',7,9,92,'أَوۡ تُسۡقِطَ اَ۬لسَّمَآءَ كَمَا زَعَمۡتَ عَلَيۡنَا كِسۡفًا أَوۡ تَأۡتِيَ بِاللَّهِ وَاَلۡمَلَٰٓئِكَةِ قَبِيلًا ٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2128,15,'291',17,'Al-Isrā’','الإسرَاء',9,11,93,'أَوۡ يَكُونَ لَكَ بَيۡتٞ مِّن زُخۡرُفٍ أَوۡ تَرۡقَىٰ فِي اِ۬لسَّمَآءِ وَلَن نُّؤۡمِنَ لِرُقِيِّكَ حَتَّىٰ تُنزِلَ عَلَيۡنَا كِتَٰبٗا نَّقۡرَؤُهُۥۗ قُلۡ سُبۡحَانَ رَبِّي هَلۡ كُنتُ إِلَّا بَشَرٗا رَّسُولٗا ٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2129,15,'291',17,'Al-Isrā’','الإسرَاء',11,13,94,'وَمَا مَنَعَ اَ۬لنَّاسَ أَن يُؤۡمِنُوٓاْ إِذ جَّآءَهُمُ اُ۬لۡهُدَىٰٓ إِلَّآ أَن قَالُوٓاْ أَبَعَثَ اَ۬للَّهُ بَشَرٗا رَّسُولٗا ٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2130,15,'291',17,'Al-Isrā’','الإسرَاء',13,14,95,'قُل لَّوۡ كَانَ فِي اِ۬لۡأَرۡضِ مَلَٰٓئِكَةٞ يَمۡشُونَ مُطۡمَئِنِّينَ لَنَزَّلۡنَا عَلَيۡهِم مِّنَ اَ۬لسَّمَآءِ مَلَكٗا رَّسُولٗا ٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2131,15,'291',17,'Al-Isrā’','الإسرَاء',14,15,96,'قُلۡ كَفَىٰ بِاللَّهِ شَهِيدَۢا بَيۡنِي وَبَيۡنَكُمۡۚ إِنَّهُۥ كَانَ بِعِبَادِهِۦ خَبِيرَۢا بَصِيرٗا ٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2132,15,'292',17,'Al-Isrā’','الإسرَاء',1,3,97,'وَمَن يَهۡدِ اِ۬للَّهُ فَهۡوَ اَ۬لۡمُهۡتَدِۦۖ وَمَن يُضۡلِلۡ فَلَن تَجِدَ لَهُمۡ أَوۡلِيَآءَ مِن دُونِهِۦۖ وَنَحۡشُرُهُمۡ يَوۡمَ اَ۬لۡقِيَٰمَةِ عَلَىٰ وُجُوهِهِمۡ عُمۡيٗا وَبُكۡمٗا وَصُمّٗاۖ مَّأۡوَىٰهُمۡ جَهَنَّمُۖ كُلَّمَا خَبَت زِّدۡنَٰهُمۡ سَعِيرٗا ٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2133,15,'292',17,'Al-Isrā’','الإسرَاء',4,5,98,'ذَٰلِكَ جَزَآؤُهُم بِأَنَّهُمۡ كَفَرُواْ بِـَٔايَٰتِنَا وَقَالُوٓاْ أَٰ۟ذَا كُنَّا عِظَٰمٗا وَرُفَٰتًا أَٰ۟نَّا لَمَبۡعُوثُونَ خَلۡقٗا جَدِيدًا ٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2134,15,'292',17,'Al-Isrā’','الإسرَاء',5,7,99,'أَوَلَمۡ يَرَوۡاْ أَنَّ اَ۬للَّهَ اَ۬لَّذِي خَلَقَ اَ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضَ قَادِرٌ عَلَىٰٓ أَن يَخۡلُقَ مِثۡلَهُمۡ وَجَعَلَ لَهُمۡ أَجَلٗا لَّا رَيۡبَ فِيهِ فَأَبَى اَ۬لظَّٰلِمُونَ إِلَّا كُفُورٗا ٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2135,15,'292',17,'Al-Isrā’','الإسرَاء',8,9,100,'قُل لَّوۡ أَنتُمۡ تَمۡلِكُونَ خَزَآئِنَ رَحۡمَةِ رَبِّيَ إِذٗا لَّأَمۡسَكۡتُمۡ خَشۡيَةَ اَ۬لۡإِنفَاقِۚ وَكَانَ اَ۬لۡإِنسَٰنُ قَتُورٗا ١٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2136,15,'292',17,'Al-Isrā’','الإسرَاء',9,11,101,'۞ وَلَقَدۡ ءَاتَيۡنَا مُوسۭيٰ تِسۡعَ ءَايَٰتِۢ بَيِّنَٰتٖۖ فَسۡـَٔلۡ بَنِيٓ إِسۡرَٰٓءِيلَ إِذ جَّآءَهُمۡ فَقَالَ لَهُۥ فِرۡعَوۡنُ إِنِّي لَأَظُنُّكَ يَٰمُوسۭيٰ مَسۡحُورٗا ١٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2137,15,'292',17,'Al-Isrā’','الإسرَاء',11,13,102,'قَالَ لَقَدۡ عَلِمۡتَ مَآ أَنزَلَ هَٰٓؤُلَآ إِلَّا رَبُّ اُ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِ بَصَآئِرَ وَإِنِّي لَأَظُنُّكَ يَٰفِرۡعَوۡنُ مَثۡبُورٗا ١٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2138,15,'292',17,'Al-Isrā’','الإسرَاء',13,14,103,'فَأَرَادَ أَن يَسۡتَفِزَّهُم مِّنَ اَ۬لۡأَرۡضِ فَأَغۡرَقۡنَٰهُ وَمَن مَّعَهُۥ جَمِيعٗا ١٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2139,15,'292',17,'Al-Isrā’','الإسرَاء',14,15,104,'وَقُلۡنَا مِنۢ بَعۡدِهِۦ لِبَنِيٓ إِسۡرَٰٓءِيلَ اَ۟سۡكُنُواْ اُ۬لۡأَرۡضَ فَإِذَا جَآءَ وَعۡدُ اُ۬لۡأٓخِرَةِ جِئۡنَا بِكُمۡ لَفِيفٗا ١٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2140,15,'293',17,'Al-Isrā’','الإسرَاء',1,1,105,'وَبِالۡحَقِّ أَنزَلۡنَٰهُ وَبِالۡحَقِّ نَزَلَۗ وَمَآ أَرۡسَلۡنَٰكَ إِلَّا مُبَشِّرٗا وَنَذِيرٗا ١٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2141,15,'293',17,'Al-Isrā’','الإسرَاء',2,2,106,'وَقُرۡءَانٗا فَرَقۡنَٰهُ لِتَقۡرَأَهُۥ عَلَى اَ۬لنّ۪اسِ عَلَىٰ مُكۡثٖ وَنَزَّلۡنَٰهُ تَنزِيلٗا ١٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2142,15,'293',17,'Al-Isrā’','الإسرَاء',3,5,107,'قُلۡ ءَامِنُواْ بِهِۦٓ أَوۡ لَا تُؤۡمِنُوٓاْۚ إِنَّ اَ۬لَّذِينَ أُوتُواْ اُ۬لۡعِلۡمَ مِن قَبۡلِهِۦٓ إِذَا يُتۡلَىٰ عَلَيۡهِمۡ يَخِرُّونَۤ لِلۡأَذۡقَانِۤ سُجَّدٗاۤ وَيَقُولُونَ سُبۡحَٰنَ رَبِّنَآ إِن كَانَ وَعۡدُ رَبِّنَا لَمَفۡعُولٗا ١٠٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2143,15,'293',17,'Al-Isrā’','الإسرَاء',5,6,108,'وَيَخِرُّونَ لِلۡأَذۡقَانِ يَبۡكُونَ وَيَزِيدُهُمۡ خُشُوعٗا۩ ١٠٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2144,15,'293',17,'Al-Isrā’','الإسرَاء',6,8,109,'قُلُ اُ۟دۡعُواْ اُ۬للَّهَ أَوُ اُ۟دۡعُواْ اُ۬لرَّحۡمَٰنَۖ أَيّٗا مَّا تَدۡعُواْ فَلَهُ اُ۬لۡأَسۡمَآءُ اُ۬لۡحُسۡنۭيٰۚ وَلَا تَجۡهَرۡ بِصَلَاتِكَ وَلَا تُخَافِتۡ بِهَا وَاَبۡتَغِ بَيۡنَ ذَٰلِكَ سَبِيلٗا ١٠٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2145,15,'293',17,'Al-Isrā’','الإسرَاء',8,9,110,'وَقُلِ اِ۬لۡحَمۡدُ لِلَّهِ اِ۬لَّذِي لَمۡ يَتَّخِذۡ وَلَدٗا وَلَمۡ يَكُن لَّهُۥ شَرِيكٞ فِي اِ۬لۡمُلۡكِ وَلَمۡ يَكُن لَّهُۥ وَلِيّٞ مِّنَ اَ۬لذُّلِّۖ وَكَبِّرۡهُ تَكۡبِيرَۢا ١١٠');
