INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3169,19,'378',27,'An-Naml','النَّمل',2,3,15,'وَلَقَدۡ ءَاتَيۡنَا دَاوُۥدَ وَسُلَيۡمَٰنَ عِلۡمٗاۖ وَقَالَا اَ۬لۡحَمۡدُ لِلَّهِ اِ۬لَّذِي فَضَّلَنَا عَلَىٰ كَثِيرٖ مِّنۡ عِبَادِهِ اِ۬لۡمُؤۡمِنِينَ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3170,19,'378',27,'An-Naml','النَّمل',4,5,16,'۞ وَوَرِثَ سُلَيۡمَٰنُ دَاوُۥدَۖ وَقَالَ يَٰٓأَيُّهَا اَ۬لنَّاسُ عُلِّمۡنَا مَنطِقَ اَ۬لطَّيۡرِ وَأُوتِينَا مِن كُلِّ شَيۡءٍۖ إِنَّ هَٰذَا لَهۡوَ اَ۬لۡفَضۡلُ اُ۬لۡمُبِينُ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3171,19,'378',27,'An-Naml','النَّمل',6,7,17,'وَحُشِرَ لِسُلَيۡمَٰنَ جُنُودُهُۥ مِنَ اَ۬لۡجِنِّ وَاَلۡإِنسِ وَاَلطَّيۡرِ فَهُمۡ يُوزَعُونَ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3172,19,'378',27,'An-Naml','النَّمل',7,9,18,'حَتَّىٰٓ إِذَآ أَتَوۡاْ عَلَىٰ وَادِ اِ۬لنَّمۡلِ قَالَتۡ نَمۡلَةٞ يَٰٓأَيُّهَا اَ۬لنَّمۡلُ اُ۟دۡخُلُواْ مَسَٰكِنَكُمۡ لَا يَحۡطِمَنَّكُمۡ سُلَيۡمَٰنُ وَجُنُودُهُۥ وَهُمۡ لَا يَشۡعُرُونَ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3173,19,'378',27,'An-Naml','النَّمل',9,11,19,'فَتَبَسَّمَ ضَاحِكٗا مِّن قَوۡلِهَا وَقَالَ رَبِّ أَوۡزِعۡنِيٓ أَنۡ أَشۡكُرَ نِعۡمَتَكَ اَ۬لَّتِيٓ أَنۡعَمۡتَ عَلَيَّ وَعَلَىٰ وَٰلِدَيَّ وَأَنۡ أَعۡمَلَ صَٰلِحٗا تَرۡضَىٰهُ وَأَدۡخِلۡنِي بِرَحۡمَتِكَ فِي عِبَادِكَ اَ۬لصَّٰلِحِينَ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3174,19,'378',27,'An-Naml','النَّمل',12,13,20,'وَتَفَقَّدَ اَ۬لطَّيۡرَ فَقَالَ مَالِي لَآ أَرَى اَ۬لۡهُدۡهُدَ أَمۡ كَانَ مِنَ اَ۬لۡغَآئِبِينَ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3175,19,'378',27,'An-Naml','النَّمل',13,14,21,'لَأُعَذِّبَنَّهُۥ عَذَابٗا شَدِيدًا أَوۡ لَأَاْذۡبَحَنَّهُۥٓ أَوۡ لَيَأۡتِيَنِّي بِسُلۡطَٰنٖ مُّبِينٖ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3176,19,'378',27,'An-Naml','النَّمل',14,15,22,'فَمَكُثَ غَيۡرَ بَعِيدٖ فَقَالَ أَحَطتُ بِمَا لَمۡ تُحِطۡ بِهِۦ وَجِئۡتُكَ مِن سَبَأَ بِنَبَإٖ يَقِينٍ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3177,19,'379',27,'An-Naml','النَّمل',1,2,23,'إِنِّي وَجَدتُّ اُ۪مۡرَأَةٗ تَمۡلِكُهُمۡ وَأُوتِيَتۡ مِن كُلِّ شَيۡءٖ وَلَهَا عَرۡشٌ عَظِيمٞ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3178,19,'379',27,'An-Naml','النَّمل',2,4,24,'وَجَدتُّهَا وَقَوۡمَهَا يَسۡجُدُونَ لِلشَّمۡسِ مِن دُونِ اِ۬للَّهِ وَزَيَّنَ لَهُمُ اُ۬لشَّيۡطَٰنُ أَعۡمَٰلَهُمۡ فَصَدَّهُمۡ عَنِ اِ۬لسَّبِيلِ فَهُمۡ لَا يَهۡتَدُونَ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3179,19,'379',27,'An-Naml','النَّمل',4,5,25,'أَلَّاۤ يَسۡجُدُواْۤ لِلَّهِ اِ۬لَّذِي يُخۡرِجُ اُ۬لۡخَبۡءَ فِي اِ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِ وَيَعۡلَمُ مَا يُخۡفُونَ وَمَا يُعۡلِنُونَ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3180,19,'379',27,'An-Naml','النَّمل',5,6,26,'اَ۬للَّهُ لَآ إِلَٰهَ إِلَّا هُوَ رَبُّ اُ۬لۡعَرۡشِ اِ۬لۡعَظِيمِ۩ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3181,19,'379',27,'An-Naml','النَّمل',6,7,27,'۞ قَالَ سَنَنظُرُ أَصَدَقۡتَ أَمۡ كُنتَ مِنَ اَ۬لۡكَٰذِبِينَ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3182,19,'379',27,'An-Naml','النَّمل',7,8,28,'اَ۪ذۡهَب بِّكِتَٰبِي هَٰذَا فَأَلۡقِهۡ إِلَيۡهِمۡ ثُمَّ تَوَلَّ عَنۡهُمۡ فَاَنظُرۡ مَاذَا يَرۡجِعُونَ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3183,19,'379',27,'An-Naml','النَّمل',8,9,29,'قَالَتۡ يَٰٓأَيُّهَا اَ۬لۡمَلَؤُاْ اِ۪نِّيٓ أُلۡقِيَ إِلَيَّ كِتَٰبٞ كَرِيمٌ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3184,19,'379',27,'An-Naml','النَّمل',9,10,30,'إِنَّهُۥ مِن سُلَيۡمَٰنَ وَإِنَّهُۥ بِسۡمِ اِ۬للَّهِ اِ۬لرَّحۡمَٰنِ اِ۬لرَّحِيمِ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3185,19,'379',27,'An-Naml','النَّمل',10,10,31,'أَلَّا تَعۡلُواْ عَلَيَّ وَأۡتُونِي مُسۡلِمِينَ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3186,19,'379',27,'An-Naml','النَّمل',11,12,32,'قَالَتۡ يَٰٓأَيُّهَا اَ۬لۡمَلَؤُاْ اَ۬فۡتُونِي فِيٓ أَمۡرِي مَا كُنتُ قَاطِعَةً أَمۡرًا حَتَّىٰ تَشۡهَدُونِ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3187,19,'379',27,'An-Naml','النَّمل',12,12,33,'قَالُواْ نَحۡنُ أُوْلُواْ قُوَّةٖ وَأُوْلُواْ بَأۡسٖ شَدِيدٖ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3188,19,'379',27,'An-Naml','النَّمل',12,13,34,'وَاَلۡأَمۡرُ إِلَيۡكِ فَاَنظُرِي مَاذَا تَأۡمُرِينَ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3189,19,'379',27,'An-Naml','النَّمل',13,14,35,'قَالَتۡ إِنَّ اَ۬لۡمُلُوكَ إِذَا دَخَلُواْ قَرۡيَةً أَفۡسَدُوهَا وَجَعَلُوٓاْ أَعِزَّةَ أَهۡلِهَآ أَذِلَّةٗۖ وَكَذَٰلِكَ يَفۡعَلُونَ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3190,19,'379',27,'An-Naml','النَّمل',15,15,36,'وَإِنِّي مُرۡسِلَةٌ إِلَيۡهِم بِهَدِيَّةٖ فَنَاظِرَةُۢ بِمَ يَرۡجِعُ اُ۬لۡمُرۡسَلُونَ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3191,19,'380',27,'An-Naml','النَّمل',1,2,37,'فَلَمَّا جَآءَ سُلَيۡمَٰنَ قَالَ أَتُمِدُّونَنِۦ بِمَالٖ فَمَآ ءَاتَىٰنِۦَ اَ۬للَّهُ خَيۡرٞ مِّمَّآ ءَاتَىٰكُمۚ بَلۡ أَنتُم بِهَدِيَّتِكُمۡ تَفۡرَحُونَ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3192,19,'380',27,'An-Naml','النَّمل',2,3,38,'اَ۪رۡجِعۡ إِلَيۡهِمۡ فَلَنَأۡتِيَنَّهُم بِجُنُودٖ لَّا قِبَلَ لَهُم بِهَا وَلَنُخۡرِجَنَّهُم مِّنۡهَآ أَذِلَّةٗ وَهُمۡ صَٰغِرُونَ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3193,19,'380',27,'An-Naml','النَّمل',4,4,39,'قَالَ يَٰٓأَيُّهَا اَ۬لۡمَلَؤُاْ اَ۬يُّكُمۡ يَأۡتِينِي بِعَرۡشِهَا قَبۡلَ أَن يَأۡتُونِي مُسۡلِمِينَ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3194,19,'380',27,'An-Naml','النَّمل',5,6,40,'قَالَ عِفۡرِيتٞ مِّنَ اَ۬لۡجِنِّ أَنَا۠ ءَاتِيكَ بِهِۦ قَبۡلَ أَن تَقُومَ مِن مَّقَامِكَۖ وَإِنِّي عَلَيۡهِ لَقَوِيٌّ أَمِينٞ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3195,19,'380',27,'An-Naml','النَّمل',6,9,41,'قَالَ اَ۬لَّذِي عِندَهُۥ عِلۡمٞ مِّنَ اَ۬لۡكِتَٰبِ أَنَا۠ ءَاتِيكَ بِهِۦ قَبۡلَ أَن يَرۡتَدَّ إِلَيۡكَ طَرۡفُكَۚ فَلَمَّا رَء۪اهُ مُسۡتَقِرًّا عِندَهُۥ قَالَ هَٰذَا مِن فَضۡلِ رَبِّي لِيَبۡلُوَنِيٓ ءَٰا۬شۡكُرُ أَمۡ أَكۡفُرُۖ وَمَن شَكَرَ فَإِنَّمَا يَشۡكُرُ لِنَفۡسِهِۦۖ وَمَن كَفَرَ فَإِنَّ رَبِّي غَنِيّٞ كَرِيمٞ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3196,19,'380',27,'An-Naml','النَّمل',9,10,42,'۞ قَالَ نَكِّرُواْ لَهَا عَرۡشَهَا نَنظُرۡ أَتَهۡتَدِيٓ أَمۡ تَكُونُ مِنَ اَ۬لَّذِينَ لَا يَهۡتَدُونَ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3197,19,'380',27,'An-Naml','النَّمل',10,12,43,'فَلَمَّا جَآءَتۡ قِيلَ أَهَٰكَذَا عَرۡشُكِۖ قَالَتۡ كَأَنَّهُۥ هُوَۚ وَأُوتِينَا اَ۬لۡعِلۡمَ مِن قَبۡلِهَا وَكُنَّا مُسۡلِمِينَ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3198,19,'380',27,'An-Naml','النَّمل',12,13,44,'وَصَدَّهَا مَا كَانَت تَّعۡبُدُ مِن دُونِ اِ۬للَّهِۖ إِنَّهَا كَانَتۡ مِن قَوۡمٖ كٰ۪فِرِينَ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3199,19,'380',27,'An-Naml','النَّمل',13,14,45,'قِيلَ لَهَا اَ۟دۡخُلِي اِ۬لصَّرۡحَۖ فَلَمَّا رَأَتۡهُ حَسِبَتۡهُ لُجَّةٗ وَكَشَفَتۡ عَن سَاقَيۡهَاۚ قَالَ إِنَّهُۥ صَرۡحٞ مُّمَرَّدٞ مِّن قَوَارِيرَ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3200,19,'380',27,'An-Naml','النَّمل',14,15,46,'قَالَتۡ رَبِّ إِنِّي ظَلَمۡتُ نَفۡسِي وَأَسۡلَمۡتُ مَعَ سُلَيۡمَٰنَ لِلَّهِ رَبِّ اِ۬لۡعَٰلَمِينَ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3201,19,'381',27,'An-Naml','النَّمل',1,2,47,'وَلَقَدۡ أَرۡسَلۡنَآ إِلَىٰ ثَمُودَ أَخَاهُمۡ صَٰلِحًا أَنِ اِ۟عۡبُدُواْ اُ۬للَّهَ فَإِذَا هُمۡ فَرِيقَانِ يَخۡتَصِمُونَ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3202,19,'381',27,'An-Naml','النَّمل',2,4,48,'قَالَ يَٰقَوۡمِ لِمَ تَسۡتَعۡجِلُونَ بِالسَّيِّئَةِ قَبۡلَ اَ۬لۡحَسَنَةِۖ لَوۡلَا تَسۡتَغۡفِرُونَ اَ۬للَّهَ لَعَلَّكُمۡ تُرۡحَمُونَ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3203,19,'381',27,'An-Naml','النَّمل',4,5,49,'قَالُواْ اُ۪طَّيَّرۡنَا بِكَ وَبِمَن مَّعَكَۚ قَالَ طَٰٓئِرُكُمۡ عِندَ اَ۬للَّهِۖ بَلۡ أَنتُمۡ قَوۡمٞ تُفۡتَنُونَ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3204,19,'381',27,'An-Naml','النَّمل',5,6,50,'وَكَانَ فِي اِ۬لۡمَدِينَةِ تِسۡعَةُ رَهۡطٖ يُفۡسِدُونَ فِي اِ۬لۡأَرۡضِ وَلَا يُصۡلِحُونَ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3205,19,'381',27,'An-Naml','النَّمل',7,8,51,'قَالُواْ تَقَاسَمُواْ بِاللَّهِ لَنُبَيِّتَنَّهُۥ وَأَهۡلَهُۥ ثُمَّ لَنَقُولَنَّ لِوَلِيِّهِۦ مَا شَهِدۡنَا مُهۡلَكَ أَهۡلِهِۦ وَإِنَّا لَصَٰدِقُونَ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3206,19,'381',27,'An-Naml','النَّمل',8,9,52,'وَمَكَرُواْ مَكۡرٗا وَمَكَرۡنَا مَكۡرٗا وَهُمۡ لَا يَشۡعُرُونَ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3207,19,'381',27,'An-Naml','النَّمل',9,11,53,'فَاَنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ مَكۡرِهِمۡۚ إِنَّا دَمَّرۡنَٰهُمۡ وَقَوۡمَهُمۡ أَجۡمَعِينَ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3208,19,'381',27,'An-Naml','النَّمل',11,12,54,'فَتِلۡكَ بُيُوتُهُمۡ خَاوِيَةَۢ بِمَا ظَلَمُوٓاْۗ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّقَوۡمٖ يَعۡلَمُونَ ٥٤');
