INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3210,19,'381',27,'An-Naml','النَّمل',13,14,56,'وَلُوطًا إِذۡ قَالَ لِقَوۡمِهِۦٓ أَتَأۡتُونَ اَ۬لۡفَٰحِشَةَ وَأَنتُمۡ تُبۡصِرُونَ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3211,19,'381',27,'An-Naml','النَّمل',14,15,57,'أَٰى۪نَّكُمۡ لَتَأۡتُونَ اَ۬لرِّجَالَ شَهۡوَةٗ مِّن دُونِ اِ۬لنِّسَآءِۚ بَلۡ أَنتُمۡ قَوۡمٞ تَجۡهَلُونَ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3212,20,'382',27,'An-Naml','النَّمل',1,2,58,'۞ فَمَا كَانَ جَوَابَ قَوۡمِهِۦٓ إِلَّآ أَن قَالُوٓاْ أَخۡرِجُوٓاْ ءَالَ لُوطٖ مِّن قَرۡيَتِكُمۡۖ إِنَّهُمۡ أُنَاسٞ يَتَطَهَّرُونَ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3213,20,'382',27,'An-Naml','النَّمل',2,3,59,'فَأَنجَيۡنَٰهُ وَأَهۡلَهُۥٓ إِلَّا اَ۪مۡرَأَتَهُۥ قَدَّرۡنَٰهَا مِنَ اَ۬لۡغَٰبِرِينَ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3214,20,'382',27,'An-Naml','النَّمل',3,4,60,'وَأَمۡطَرۡنَا عَلَيۡهِم مَّطَرٗاۖ فَسَآءَ مَطَرُ اُ۬لۡمُنذَرِينَ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3215,20,'382',27,'An-Naml','النَّمل',4,5,61,'قُلِ اِ۬لۡحَمۡدُ لِلَّهِ وَسَلَٰمٌ عَلَىٰ عِبَادِهِ اِ۬لَّذِينَ اَ۪صۡطَفَىٰٓۗ ءَآللَّهُ خَيۡرٌ أَمَّا يُشۡرِكُونَ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3216,20,'382',27,'An-Naml','النَّمل',6,8,62,'أَمَّنۡ خَلَقَ اَ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضَ وَأَنزَلَ لَكُم مِّنَ اَ۬لسَّمَآءِ مَآءٗ فَأَنۢبَتۡنَا بِهِۦ حَدَآئِقَ ذَاتَ بَهۡجَةٖ مَّا كَانَ لَكُمۡ أَن تُنۢبِتُواْ شَجَرَهَآۗ أَٰ۟لَٰهٞ مَّعَ اَ۬للَّهِۚ بَلۡ هُمۡ قَوۡمٞ يَعۡدِلُونَ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3217,20,'382',27,'An-Naml','النَّمل',9,11,63,'أَمَّن جَعَلَ اَ۬لۡأَرۡضَ قَرَارٗا وَجَعَلَ خِلَٰلَهَآ أَنۡهَٰرٗا وَجَعَلَ لَهَا رَوَٰسِيَ وَجَعَلَ بَيۡنَ اَ۬لۡبَحۡرَيۡنِ حَاجِزًاۗ أَٰ۟لَٰهٞ مَّعَ اَ۬للَّهِۚ بَلۡ أَكۡثَرُهُمۡ لَا يَعۡلَمُونَ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3218,20,'382',27,'An-Naml','النَّمل',11,13,64,'أَمَّن يُجِيبُ اُ۬لۡمُضۡطَرَّ إِذَا دَعَاهُ وَيَكۡشِفُ اُ۬لسُّوٓءَ وَيَجۡعَلُكُمۡ خُلَفَآءَ اَ۬لۡأَرۡضِۗ أَٰ۟لَٰهٞ مَّعَ اَ۬للَّهِۚ قَلِيلٗا مَّا يَذَّكَّرُونَ ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3219,20,'382',27,'An-Naml','النَّمل',13,15,65,'أَمَّن يَهۡدِيكُمۡ فِي ظُلُمَٰتِ اِ۬لۡبَرِّ وَاَلۡبَحۡرِ وَمَن يُرۡسِلُ اُ۬لرِّيَٰحَ نُشُرَۢا بَيۡنَ يَدَيۡ رَحۡمَتِهِۦٓۗ أَٰ۟لَٰهٞ مَّعَ اَ۬للَّهِۚ تَعَٰلَى اَ۬للَّهُ عَمَّا يُشۡرِكُونَ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3220,20,'383',27,'An-Naml','النَّمل',1,2,66,'أَمَّن يَبۡدَؤُاْ اُ۬لۡخَلۡقَ ثُمَّ يُعِيدُهُۥ وَمَن يَرۡزُقُكُم مِّنَ اَ۬لسَّمَآءِ وَاَلۡأَرۡضِۗ أَٰ۟لَٰهٞ مَّعَ اَ۬للَّهِۚ قُلۡ هَاتُواْ بُرۡهَٰنَكُمۡ إِن كُنتُمۡ صَٰدِقِينَ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3221,20,'383',27,'An-Naml','النَّمل',2,4,67,'قُل لَّا يَعۡلَمُ مَن فِي اِ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِ اِ۬لۡغَيۡبَ إِلَّا اَ۬للَّهُۚ وَمَا يَشۡعُرُونَ أَيَّانَ يُبۡعَثُونَ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3222,20,'383',27,'An-Naml','النَّمل',4,5,68,'بَلۡ أَدۡرَكَ عِلۡمُهُمۡ فِي اِ۬لۡأٓخِرَةِۚ بَلۡ هُمۡ فِي شَكّٖ مِّنۡهَاۖ بَلۡ هُم مِّنۡهَا عَمُونَ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3223,20,'383',27,'An-Naml','النَّمل',5,6,69,'۞ وَقَالَ اَ۬لَّذِينَ كَفَرُوٓاْ أَٰ۟ذَا كُنَّا تُرَٰبٗا وَءَابَآؤُنَآ أَٰى۪نَّا لَمُخۡرَجُونَ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3224,20,'383',27,'An-Naml','النَّمل',6,7,70,'لَقَدۡ وُعِدۡنَا هَٰذَا نَحۡنُ وَءَابَآؤُنَا مِن قَبۡلُ إِنۡ هَٰذَآ إِلَّآ أَسَٰطِيرُ اُ۬لۡأَوَّلِينَ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3225,20,'383',27,'An-Naml','النَّمل',8,8,71,'قُلۡ سِيرُواْ فِي اِ۬لۡأَرۡضِ فَاَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ اُ۬لۡمُجۡرِمِينَ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3226,20,'383',27,'An-Naml','النَّمل',9,9,72,'وَلَا تَحۡزَنۡ عَلَيۡهِمۡ وَلَا تَكُن فِي ضَيۡقٖ مِّمَّا يَمۡكُرُونَ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3227,20,'383',27,'An-Naml','النَّمل',10,10,73,'وَيَقُولُونَ مَتَىٰ هَٰذَا اَ۬لۡوَعۡدُ إِن كُنتُمۡ صَٰدِقِينَ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3228,20,'383',27,'An-Naml','النَّمل',10,11,74,'قُلۡ عَسَىٰٓ أَن يَكُونَ رَدِفَ لَكُم بَعۡضُ اُ۬لَّذِي تَسۡتَعۡجِلُونَ ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3229,20,'383',27,'An-Naml','النَّمل',11,12,75,'وَإِنَّ رَبَّكَ لَذُو فَضۡلٍ عَلَى اَ۬لنّ۪اسِ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَشۡكُرُونَ ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3230,20,'383',27,'An-Naml','النَّمل',12,13,76,'وَإِنَّ رَبَّكَ لَيَعۡلَمُ مَا تُكِنُّ صُدُورُهُمۡ وَمَا يُعۡلِنُونَ ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3231,20,'383',27,'An-Naml','النَّمل',13,14,77,'وَمَا مِنۡ غَآئِبَةٖ فِي اِ۬لسَّمَآءِ وَاَلۡأَرۡضِ إِلَّا فِي كِتَٰبٖ مُّبِينٍ ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3232,20,'383',27,'An-Naml','النَّمل',14,15,78,'إِنَّ هَٰذَا اَ۬لۡقُرۡءَانَ يَقُصُّ عَلَىٰ بَنِيٓ إِسۡرَٰٓءِيلَ أَكۡثَرَ اَ۬لَّذِي هُمۡ فِيهِ يَخۡتَلِفُونَ ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3233,20,'384',27,'An-Naml','النَّمل',1,1,79,'وَإِنَّهُۥ لَهُدٗى وَرَحۡمَةٞ لِّلۡمُؤۡمِنِينَ ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3234,20,'384',27,'An-Naml','النَّمل',1,2,80,'إِنَّ رَبَّكَ يَقۡضِي بَيۡنَهُم بِحُكۡمِهِۦۚ وَهۡوَ اَ۬لۡعَزِيزُ اُ۬لۡعَلِيمُ ٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3235,20,'384',27,'An-Naml','النَّمل',2,3,81,'فَتَوَكَّلۡ عَلَى اَ۬للَّهِۖ إِنَّكَ عَلَى اَ۬لۡحَقِّ اِ۬لۡمُبِينِ ٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3236,20,'384',27,'An-Naml','النَّمل',3,4,82,'إِنَّكَ لَا تُسۡمِعُ اُ۬لۡمَوۡتۭيٰ وَلَا تُسۡمِعُ اُ۬لصُّمَّ اَ۬لدُّعَآءَ ا۪ذَا وَلَّوۡاْ مُدۡبِرِينَ ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3237,20,'384',27,'An-Naml','النَّمل',4,5,83,'وَمَآ أَنتَ بِهَٰدِي اِ۬لۡعُمۡيِ عَن ضَلَٰلَتِهِمۡۖ إِن تُسۡمِعُ إِلَّا مَن يُؤۡمِنُ بِـَٔايَٰتِنَا فَهُم مُّسۡلِمُونَ ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3238,20,'384',27,'An-Naml','النَّمل',5,7,84,'۞ وَإِذَا وَقَعَ اَ۬لۡقَوۡلُ عَلَيۡهِمۡ أَخۡرَجۡنَا لَهُمۡ دَآبَّةٗ مِّنَ اَ۬لۡأَرۡضِ تُكَلِّمُهُمۡۚ إِنَّ اَ۬لنَّاسَ كَانُواْ بِـَٔايَٰتِنَا لَا يُوقِنُونَ ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3239,20,'384',27,'An-Naml','النَّمل',7,8,85,'وَيَوۡمَ نَحۡشُرُ مِن كُلِّ أُمَّةٖ فَوۡجٗا مِّمَّن يُكَذِّبُ بِـَٔايَٰتِنَا فَهُمۡ يُوزَعُونَ ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3240,20,'384',27,'An-Naml','النَّمل',8,9,86,'حَتَّىٰٓ إِذَا جَآءُو قَالَ أَكَذَّبۡتُم بِـَٔايَٰتِي وَلَمۡ تُحِيطُواْ بِهَا عِلۡمًا أَمَّاذَا كُنتُمۡ تَعۡمَلُونَ ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3241,20,'384',27,'An-Naml','النَّمل',10,10,87,'وَوَقَعَ اَ۬لۡقَوۡلُ عَلَيۡهِم بِمَا ظَلَمُواْ فَهُمۡ لَا يَنطِقُونَ ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3242,20,'384',27,'An-Naml','النَّمل',10,12,88,'أَلَمۡ يَرَوۡاْ أَنَّا جَعَلۡنَا اَ۬لَّيۡلَ لِيَسۡكُنُواْ فِيهِ وَاَلنَّهَارَ مُبۡصِرًاۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يُؤۡمِنُونَ ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3243,20,'384',27,'An-Naml','النَّمل',12,14,89,'وَيَوۡمَ يُنفَخُ فِي اِ۬لصُّورِ فَفَزِعَ مَن فِي اِ۬لسَّمَٰوَٰتِ وَمَن فِي اِ۬لۡأَرۡضِ إِلَّا مَن شَآءَ اَ۬للَّهُۚ وَكُلٌّ ءَاتُوهُ دَٰخِرِينَ ٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3244,20,'384',27,'An-Naml','النَّمل',14,15,90,'وَتَرَى اَ۬لۡجِبَالَ تَحۡسِبُهَا جَامِدَةٗ وَهۡيَ تَمُرُّ مَرَّ اَ۬لسَّحَابِۚ صُنۡعَ اَ۬للَّهِ اِ۬لَّذِيٓ أَتۡقَنَ كُلَّ شَيۡءٍۚ إِنَّهُۥ خَبِيرُۢ بِمَا يَفۡعَلُونَ ٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3245,20,'385',27,'An-Naml','النَّمل',1,1,91,'مَن جَآءَ بِالۡحَسَنَةِ فَلَهُۥ خَيۡرٞ مِّنۡهَا وَهُم مِّن فَزَعِ يَوۡمِئِذٍ ءَامِنُونَ ٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3246,20,'385',27,'An-Naml','النَّمل',2,3,92,'وَمَن جَآءَ بِالسَّيِّئَةِ فَكُبَّتۡ وُجُوهُهُمۡ فِي اِ۬لنّ۪ارِ هَلۡ تُجۡزَوۡنَ إِلَّا مَا كُنتُمۡ تَعۡمَلُونَ ٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3247,20,'385',27,'An-Naml','النَّمل',3,4,93,'إِنَّمَآ أُمِرۡتُ أَنۡ أَعۡبُدَ رَبَّ هَٰذِهِ اِ۬لۡبَلۡدَةِ اِ۬لَّذِي حَرَّمَهَا وَلَهُۥ كُلُّ شَيۡءٖۖ وَأُمِرۡتُ أَنۡ أَكُونَ مِنَ اَ۬لۡمُسۡلِمِينَ ٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3248,20,'385',27,'An-Naml','النَّمل',5,6,94,'وَأَنۡ أَتۡلُوَاْ اَ۬لۡقُرۡءَانَۖ فَمَنِ اِ۪هۡتَدَىٰ فَإِنَّمَا يَهۡتَدِي لِنَفۡسِهِۦۖ وَمَن ضَلَّ فَقُلۡ إِنَّمَآ أَنَا۠ مِنَ اَ۬لۡمُنذِرِينَ ٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3249,20,'385',27,'An-Naml','النَّمل',6,7,95,'وَقُلِ اِ۬لۡحَمۡدُ لِلَّهِ سَيُرِيكُمۡ ءَايَٰتِهِۦ فَتَعۡرِفُونَهَاۚ وَمَا رَبُّكَ بِغَٰفِلٍ عَمَّا يَعۡمَلُونَ ٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3250,20,'385',28,'Al-Qaṣaṣ','القَصَص',10,10,1,'طسٓمٓۚ تِلۡكَ ءَايَٰتُ اُ۬لۡكِتَٰبِ اِ۬لۡمُبِينِ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3251,20,'385',28,'Al-Qaṣaṣ','القَصَص',10,11,2,'نَتۡلُواْ عَلَيۡكَ مِن نَّبَإِ مُوسۭيٰ وَفِرۡعَوۡنَ بِالۡحَقِّ لِقَوۡمٖ يُؤۡمِنُونَ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3252,20,'385',28,'Al-Qaṣaṣ','القَصَص',11,14,3,'إِنَّ فِرۡعَوۡنَ عَلَا فِي اِ۬لۡأَرۡضِ وَجَعَلَ أَهۡلَهَا شِيَعٗا يَسۡتَضۡعِفُ طَآئِفَةٗ مِّنۡهُمۡ يُذَبِّحُ أَبۡنَآءَهُمۡ وَيَسۡتَحۡيِۦ نِسَآءَهُمۡۚ إِنَّهُۥ كَانَ مِنَ اَ۬لۡمُفۡسِدِينَ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3253,20,'385',28,'Al-Qaṣaṣ','القَصَص',14,15,4,'وَنُرِيدُ أَن نَّمُنَّ عَلَى اَ۬لَّذِينَ اَ۟سۡتُضۡعِفُواْ فِي اِ۬لۡأَرۡضِ وَنَجۡعَلَهُمۡ أَى۪مَّةٗ وَنَجۡعَلَهُمُ اُ۬لۡوَٰرِثِينَ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3254,20,'386',28,'Al-Qaṣaṣ','القَصَص',1,2,5,'وَنُمَكِّنَ لَهُمۡ فِي اِ۬لۡأَرۡضِ وَنُرِيَ فِرۡعَوۡنَ وَهَٰمَٰنَ وَجُنُودَهُمَا مِنۡهُم مَّا كَانُواْ يَحۡذَرُونَ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3255,20,'386',28,'Al-Qaṣaṣ','القَصَص',2,4,6,'وَأَوۡحَيۡنَآ إِلَىٰٓ أُمِّ مُوسۭيٰٓ أَنۡ أَرۡضِعِيهِۖ فَإِذَا خِفۡتِ عَلَيۡهِ فَأَلۡقِيهِ فِي اِ۬لۡيَمِّ وَلَا تَخَافِي وَلَا تَحۡزَنِيٓۖ إِنَّا رَآدُّوهُ إِلَيۡكِ وَجَاعِلُوهُ مِنَ اَ۬لۡمُرۡسَلِينَ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3256,20,'386',28,'Al-Qaṣaṣ','القَصَص',5,6,7,'فَاَلۡتَقَطَهُۥٓ ءَالُ فِرۡعَوۡنَ لِيَكُونَ لَهُمۡ عَدُوّٗا وَحَزَنًاۗ إِنَّ فِرۡعَوۡنَ وَهَٰمَٰنَ وَجُنُودَهُمَا كَانُواْ خَٰطِـِٔينَ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3257,20,'386',28,'Al-Qaṣaṣ','القَصَص',7,8,8,'وَقَالَتِ اِ۪مۡرَأَتُ فِرۡعَوۡنَ قُرَّتُ عَيۡنٖ لِّي وَلَكَۖ لَا تَقۡتُلُوهُ عَسَىٰٓ أَن يَنفَعَنَآ أَوۡ نَتَّخِذَهُۥ وَلَدٗا وَهُمۡ لَا يَشۡعُرُونَ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3258,20,'386',28,'Al-Qaṣaṣ','القَصَص',9,10,9,'وَأَصۡبَحَ فُؤَادُ أُمِّ مُوسۭيٰ فَٰرِغًاۖ إِن كَادَتۡ لَتُبۡدِي بِهِۦ لَوۡلَآ أَن رَّبَطۡنَا عَلَىٰ قَلۡبِهَا لِتَكُونَ مِنَ اَ۬لۡمُؤۡمِنِينَ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3259,20,'386',28,'Al-Qaṣaṣ','القَصَص',10,11,10,'وَقَالَتۡ لِأُخۡتِهِۦ قُصِّيهِۖ فَبَصُرَتۡ بِهِۦ عَن جُنُبٖ وَهُمۡ لَا يَشۡعُرُونَ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3260,20,'386',28,'Al-Qaṣaṣ','القَصَص',12,13,11,'۞ وَحَرَّمۡنَا عَلَيۡهِ اِ۬لۡمَرَاضِعَ مِن قَبۡلُ فَقَالَتۡ هَلۡ أَدُلُّكُمۡ عَلَىٰٓ أَهۡلِ بَيۡتٖ يَكۡفُلُونَهُۥ لَكُمۡ وَهُمۡ لَهُۥ نَٰصِحُونَ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3261,20,'386',28,'Al-Qaṣaṣ','القَصَص',14,15,12,'فَرَدَدۡنَٰهُ إِلَىٰٓ أُمِّهِۦ كَيۡ تَقَرَّ عَيۡنُهَا وَلَا تَحۡزَنَ وَلِتَعۡلَمَ أَنَّ وَعۡدَ اَ۬للَّهِ حَقّٞ وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3262,20,'387',28,'Al-Qaṣaṣ','القَصَص',1,2,13,'وَلَمَّا بَلَغَ أَشُدَّهُۥ وَاَسۡتَوَىٰٓ ءَاتَيۡنَٰهُ حُكۡمٗا وَعِلۡمٗاۚ وَكَذَٰلِكَ نَجۡزِي اِ۬لۡمُحۡسِنِينَ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3263,20,'387',28,'Al-Qaṣaṣ','القَصَص',2,6,14,'وَدَخَلَ اَ۬لۡمَدِينَةَ عَلَىٰ حِينِ غَفۡلَةٖ مِّنۡ أَهۡلِهَا فَوَجَدَ فِيهَا رَجُلَيۡنِ يَقۡتَتِلَانِ هَٰذَا مِن شِيعَتِهِۦ وَهَٰذَا مِنۡ عَدُوِّهِۦۖ فَاَسۡتَغَٰثَهُ اُ۬لَّذِي مِن شِيعَتِهِۦ عَلَى اَ۬لَّذِي مِنۡ عَدُوِّهِۦ فَوَكَزَهُۥ مُوسۭيٰ فَقَضَىٰ عَلَيۡهِۖ قَالَ هَٰذَا مِنۡ عَمَلِ اِ۬لشَّيۡطَٰنِۖ إِنَّهُۥ عَدُوّٞ مُّضِلّٞ مُّبِينٞ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3264,20,'387',28,'Al-Qaṣaṣ','القَصَص',6,7,15,'قَالَ رَبِّ إِنِّي ظَلَمۡتُ نَفۡسِي فَاَغۡفِر لِّي فَغَفَرَ لَهُۥٓۚ إِنَّهُۥ هُوَ اَ۬لۡغَفُورُ اُ۬لرَّحِيمُ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3265,20,'387',28,'Al-Qaṣaṣ','القَصَص',7,8,16,'قَالَ رَبِّ بِمَآ أَنۡعَمۡتَ عَلَيَّ فَلَنۡ أَكُونَ ظَهِيرٗا لِّلۡمُجۡرِمِينَ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3266,20,'387',28,'Al-Qaṣaṣ','القَصَص',8,10,17,'فَأَصۡبَحَ فِي اِ۬لۡمَدِينَةِ خَآئِفٗا يَتَرَقَّبُ فَإِذَا اَ۬لَّذِي اِ۪سۡتَنصَرَهُۥ بِالۡأَمۡسِ يَسۡتَصۡرِخُهُۥۚ قَالَ لَهُۥ مُوسۭيٰٓ إِنَّكَ لَغَوِيّٞ مُّبِينٞ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3267,20,'387',28,'Al-Qaṣaṣ','القَصَص',10,13,18,'فَلَمَّآ أَنۡ أَرَادَ أَن يَبۡطِشَ بِالَّذِي هُوَ عَدُوّٞ لَّهُمَا قَالَ يَٰمُوسۭيٰٓ أَتُرِيدُ أَن تَقۡتُلَنِي كَمَا قَتَلۡتَ نَفۡسَۢا بِالۡأَمۡسِۖ إِن تُرِيدُ إِلَّآ أَن تَكُونَ جَبَّارٗا فِي اِ۬لۡأَرۡضِ وَمَا تُرِيدُ أَن تَكُونَ مِنَ اَ۬لۡمُصۡلِحِينَ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3268,20,'387',28,'Al-Qaṣaṣ','القَصَص',13,14,19,'وَجَآءَ رَجُلٞ مِّنۡ أَقۡصَا اَ۬لۡمَدِينَةِ يَسۡعَىٰ قَالَ يَٰمُوسۭيٰٓ إِنَّ اَ۬لۡمَلَأَ يَأۡتَمِرُونَ بِكَ لِيَقۡتُلُوكَ فَاَخۡرُجۡ إِنِّي لَكَ مِنَ اَ۬لنَّٰصِحِينَ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3269,20,'387',28,'Al-Qaṣaṣ','القَصَص',15,15,20,'فَخَرَجَ مِنۡهَا خَآئِفٗا يَتَرَقَّبُۖ قَالَ رَبِّ نَجِّنِي مِنَ اَ۬لۡقَوۡمِ اِ۬لظَّٰلِمِينَ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3270,20,'388',28,'Al-Qaṣaṣ','القَصَص',1,2,21,'۞ وَلَمَّا تَوَجَّهَ تِلۡقَآءَ مَدۡيَنَ قَالَ عَسَىٰ رَبِّيَ أَن يَهۡدِيَنِي سَوَآءَ اَ۬لسَّبِيلِ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3271,20,'388',28,'Al-Qaṣaṣ','القَصَص',2,3,22,'وَلَمَّا وَرَدَ مَآءَ مَدۡيَنَ وَجَدَ عَلَيۡهِ أُمَّةٗ مِّنَ اَ۬لنّ۪اسِ يَسۡقُونَ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3272,20,'388',28,'Al-Qaṣaṣ','القَصَص',3,5,23,'وَوَجَدَ مِن دُونِهِمِ اِ۪مۡرَأَتَيۡنِ تَذُودَانِۖ قَالَ مَا خَطۡبُكُمَاۖ قَالَتَا لَا نَسۡقِي حَتَّىٰ يَصۡدُرَ اَ۬لرِّعَآءُۖ وَأَبُونَا شَيۡخٞ كَبِيرٞ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3273,20,'388',28,'Al-Qaṣaṣ','القَصَص',5,6,24,'فَسَقَىٰ لَهُمَا ثُمَّ تَوَلَّىٰٓ إِلَى اَ۬لظِّلِّ فَقَالَ رَبِّ إِنِّي لِمَآ أَنزَلۡتَ إِلَيَّ مِنۡ خَيۡرٖ فَقِيرٞ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3274,20,'388',28,'Al-Qaṣaṣ','القَصَص',6,9,25,'فَجَآءَتۡهُ إِحۡدۭىٰهُمَا تَمۡشِي عَلَى اَ۪سۡتِحۡيَآءٖ قَالَتۡ إِنَّ أَبِي يَدۡعُوكَ لِيَجۡزِيَكَ أَجۡرَ مَا سَقَيۡتَ لَنَاۚ فَلَمَّا جَآءَهُۥ وَقَصَّ عَلَيۡهِ اِ۬لۡقَصَصَ قَالَ لَا تَخَفۡۖ نَجَوۡتَ مِنَ اَ۬لۡقَوۡمِ اِ۬لظَّٰلِمِينَ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3275,20,'388',28,'Al-Qaṣaṣ','القَصَص',9,10,26,'قَالَتۡ إِحۡدۭىٰهُمَا يَٰٓأَبَتِ اِ۪سۡتَـٔۡجِرۡهُۖ إِنَّ خَيۡرَ مَنِ اِ۪سۡتَـٔۡجَرۡتَ اَ۬لۡقَوِيُّ اُ۬لۡأَمِينُ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3276,20,'388',28,'Al-Qaṣaṣ','القَصَص',11,14,27,'قَالَ إِنِّيٓ أُرِيدُ أَنۡ أُنكِحَكَ إِحۡدَى اَ۪بۡنَتَيَّ هَٰتَيۡنِ عَلَىٰٓ أَن تَأۡجُرَنِي ثَمَٰنِيَ حِجَجٖۖ فَإِنۡ أَتۡمَمۡتَ عَشۡرٗا فَمِنۡ عِندِكَۖ وَمَآ أُرِيدُ أَنۡ أَشُقَّ عَلَيۡكَۚ سَتَجِدُنِيٓ إِن شَآءَ اَ۬للَّهُ مِنَ اَ۬لصَّٰلِحِينَ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3277,20,'388',28,'Al-Qaṣaṣ','القَصَص',14,15,28,'قَالَ ذَٰلِكَ بَيۡنِي وَبَيۡنَكَۖ أَيَّمَا اَ۬لۡأَجَلَيۡنِ قَضَيۡتُ فَلَا عُدۡوَٰنَ عَلَيَّۖ وَاَللَّهُ عَلَىٰ مَا نَقُولُ وَكِيلٞ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3278,20,'389',28,'Al-Qaṣaṣ','القَصَص',1,3,29,'۞ فَلَمَّا قَضَىٰ مُوسَى اَ۬لۡأَجَلَ وَسَارَ بِأَهۡلِهِۦٓ ءَانَسَ مِن جَانِبِ اِ۬لطُّورِ نَارٗاۖ قَالَ لِأَهۡلِهِ اِ۟مۡكُثُوٓاْ إِنِّيَ ءَانَسۡتُ نَارٗا لَّعَلِّيَ ءَاتِيكُم مِّنۡهَا بِخَبَرٍ أَوۡ جِذۡوَةٖ مِّنَ اَ۬لنّ۪ارِ لَعَلَّكُمۡ تَصۡطَلُونَ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3279,20,'389',28,'Al-Qaṣaṣ','القَصَص',4,6,30,'فَلَمَّآ أَتَىٰهَا نُودِيَ مِن شَٰطِيِٕ اِ۬لۡوَادِ اِ۬لۡأَيۡمَنِ فِي اِ۬لۡبُقۡعَةِ اِ۬لۡمُبَٰرَكَةِ مِنَ اَ۬لشَّجَرَةِ أَن يَٰمُوسۭيٰٓ إِنِّيَ أَنَا اَ۬للَّهُ رَبُّ اُ۬لۡعَٰلَمِينَ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3280,20,'389',28,'Al-Qaṣaṣ','القَصَص',6,8,31,'وَأَنۡ أَلۡقِ عَصَاكَۖ فَلَمَّا رَء۪اهَا تَهۡتَزُّ كَأَنَّهَا جَآنّٞ وَلَّىٰ مُدۡبِرٗا وَلَمۡ يُعَقِّبۡۚ يَٰمُوسۭيٰٓ أَقۡبِلۡ وَلَا تَخَفۡۖ إِنَّكَ مِنَ اَ۬لۡأٓمِنِينَ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3281,20,'389',28,'Al-Qaṣaṣ','القَصَص',8,11,32,'اَ۟سۡلُكۡ يَدَكَ فِي جَيۡبِكَ تَخۡرُجۡ بَيۡضَآءَ مِنۡ غَيۡرِ سُوٓءٖ وَاَضۡمُمۡ إِلَيۡكَ جَنَاحَكَ مِنَ اَ۬لرَّهَبِۖ فَذَٰٓنِّكَ بُرۡهَٰنَانِ مِن رَّبِّكَ إِلَىٰ فِرۡعَوۡنَ وَمَلَإِيْهِۦٓۚ إِنَّهُمۡ كَانُواْ قَوۡمٗا فَٰسِقِينَ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3282,20,'389',28,'Al-Qaṣaṣ','القَصَص',11,12,33,'قَالَ رَبِّ إِنِّي قَتَلۡتُ مِنۡهُمۡ نَفۡسٗا فَأَخَافُ أَن يَقۡتُلُونِ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3283,20,'389',28,'Al-Qaṣaṣ','القَصَص',12,13,34,'وَأَخِي هَٰرُونُ هُوَ أَفۡصَحُ مِنِّي لِسَانٗا فَأَرۡسِلۡهُ مَعِي رِدۡءٗا يُصَدِّقۡنِيٓۖ إِنِّيَ أَخَافُ أَن يُكَذِّبُونِ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3284,20,'389',28,'Al-Qaṣaṣ','القَصَص',14,15,35,'قَالَ سَنَشُدُّ عَضُدَكَ بِأَخِيكَ وَنَجۡعَلُ لَكُمَا سُلۡطَٰنٗا فَلَا يَصِلُونَ إِلَيۡكُمَاۚ بِـَٔايَٰتِنَآ أَنتُمَا وَمَنِ اِ۪تَّبَعَكُمَا اَ۬لۡغَٰلِبُونَ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3285,20,'390',28,'Al-Qaṣaṣ','القَصَص',1,2,36,'فَلَمَّا جَآءَهُم مُّوسۭيٰ بِـَٔايَٰتِنَا بَيِّنَٰتٖ قَالُواْ مَا هَٰذَآ إِلَّا سِحۡرٞ مُّفۡتَرٗى وَمَا سَمِعۡنَا بِهَٰذَا فِيٓ ءَابَآئِنَا اَ۬لۡأَوَّلِينَ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3286,20,'390',28,'Al-Qaṣaṣ','القَصَص',3,4,37,'وَقَالَ مُوسۭيٰ رَبِّيَ أَعۡلَمُ بِمَن جَآءَ بِالۡهُدَىٰ مِنۡ عِندِهِۦ وَمَن تَكُونُ لَهُۥ عَٰقِبَةُ اُ۬لدّ۪ارِۖ إِنَّهُۥ لَا يُفۡلِحُ اُ۬لظَّٰلِمُونَ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3287,20,'390',28,'Al-Qaṣaṣ','القَصَص',5,7,38,'وَقَالَ فِرۡعَوۡنُ يَٰٓأَيُّهَا اَ۬لۡمَلَأُ مَا عَلِمۡتُ لَكُم مِّنۡ إِلَٰهٍ غَيۡرِي فَأَوۡقِدۡ لِي يَٰهَٰمَٰنُ عَلَى اَ۬لطِّينِ فَاَجۡعَل لِّي صَرۡحٗا لَّعَلِّيَ أَطَّلِعُ إِلَىٰٓ إِلَٰهِ مُوسۭيٰ وَإِنِّي لَأَظُنُّهُۥ مِنَ اَ۬لۡكَٰذِبِينَ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3288,20,'390',28,'Al-Qaṣaṣ','القَصَص',8,9,39,'۞ وَاَسۡتَكۡبَرَ هُوَ وَجُنُودُهُۥ فِي اِ۬لۡأَرۡضِ بِغَيۡرِ اِ۬لۡحَقِّ وَظَنُّوٓاْ أَنَّهُمۡ إِلَيۡنَا لَا يُرۡجَعُونَ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3289,20,'390',28,'Al-Qaṣaṣ','القَصَص',9,10,40,'فَأَخَذۡنَٰهُ وَجُنُودَهُۥ فَنَبَذۡنَٰهُمۡ فِي اِ۬لۡيَمِّۖ فَاَنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ اُ۬لظَّٰلِمِينَ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3290,20,'390',28,'Al-Qaṣaṣ','القَصَص',11,12,41,'وَجَعَلۡنَٰهُمۡ أَى۪مَّةٗ يَدۡعُونَ إِلَى اَ۬لنّ۪ارِۖ وَيَوۡمَ اَ۬لۡقِيَٰمَةِ لَا يُنصَرُونَ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3291,20,'390',28,'Al-Qaṣaṣ','القَصَص',12,13,42,'وَأَتۡبَعۡنَٰهُمۡ فِي هَٰذِهِ اِ۬لدُّنۡيۭا لَعۡنَةٗۖ وَيَوۡمَ اَ۬لۡقِيَٰمَةِ هُم مِّنَ اَ۬لۡمَقۡبُوحِينَ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3292,20,'390',28,'Al-Qaṣaṣ','القَصَص',13,15,43,'وَلَقَدۡ ءَاتَيۡنَا مُوسَى اَ۬لۡكِتَٰبَ مِنۢ بَعۡدِ مَآ أَهۡلَكۡنَا اَ۬لۡقُرُونَ اَ۬لۡأُولۭيٰ بَصَآئِرَ لِلنّ۪اسِ وَهُدٗى وَرَحۡمَةٗ لَّعَلَّهُمۡ يَتَذَكَّرُونَ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3293,20,'391',28,'Al-Qaṣaṣ','القَصَص',1,2,44,'وَمَا كُنتَ بِجَانِبِ اِ۬لۡغَرۡبِيِّ إِذۡ قَضَيۡنَآ إِلَىٰ مُوسَى اَ۬لۡأَمۡرَ وَمَا كُنتَ مِنَ اَ۬لشَّٰهِدِينَ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3294,20,'391',28,'Al-Qaṣaṣ','القَصَص',2,4,45,'وَلَٰكِنَّآ أَنشَأۡنَا قُرُونٗا فَتَطَاوَلَ عَلَيۡهِمِ اِ۬لۡعُمُرُۚ وَمَا كُنتَ ثَاوِيٗا فِيٓ أَهۡلِ مَدۡيَنَ تَتۡلُواْ عَلَيۡهِمۡ ءَايَٰتِنَا وَلَٰكِنَّا كُنَّا مُرۡسِلِينَ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3295,20,'391',28,'Al-Qaṣaṣ','القَصَص',4,6,46,'وَمَا كُنتَ بِجَانِبِ اِ۬لطُّورِ إِذۡ نَادَيۡنَا وَلَٰكِن رَّحۡمَةٗ مِّن رَّبِّكَ لِتُنذِرَ قَوۡمٗا مَّآ أَتَىٰهُم مِّن نَّذِيرٖ مِّن قَبۡلِكَ لَعَلَّهُمۡ يَتَذَكَّرُونَ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3296,20,'391',28,'Al-Qaṣaṣ','القَصَص',7,9,47,'وَلَوۡلَآ أَن تُصِيبَهُم مُّصِيبَةُۢ بِمَا قَدَّمَتۡ أَيۡدِيهِمۡ فَيَقُولُواْ رَبَّنَا لَوۡلَآ أَرۡسَلۡتَ إِلَيۡنَا رَسُولٗا فَنَتَّبِعَ ءَايَٰتِكَ وَنَكُونَ مِنَ اَ۬لۡمُؤۡمِنِينَ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3297,20,'391',28,'Al-Qaṣaṣ','القَصَص',9,11,48,'فَلَمَّا جَآءَهُمُ اُ۬لۡحَقُّ مِنۡ عِندِنَا قَالُواْ لَوۡلَآ أُوتِيَ مِثۡلَ مَآ أُوتِيَ مُوسۭيٰٓۚ أَوَلَمۡ يَكۡفُرُواْ بِمَآ أُوتِيَ مُوسۭيٰ مِن قَبۡلُۖ قَالُواْ سَٰحِرَانِ تَظَٰهَرَا وَقَالُوٓاْ إِنَّا بِكُلّٖ كَٰفِرُونَ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3298,20,'391',28,'Al-Qaṣaṣ','القَصَص',12,13,49,'قُلۡ فَأۡتُواْ بِكِتَٰبٖ مِّنۡ عِندِ اِ۬للَّهِ هُوَ أَهۡدَىٰ مِنۡهُمَآ أَتَّبِعۡهُ إِن كُنتُمۡ صَٰدِقِينَ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3299,20,'391',28,'Al-Qaṣaṣ','القَصَص',13,15,50,'فَإِن لَّمۡ يَسۡتَجِيبُواْ لَكَ فَاَعۡلَمۡ أَنَّمَا يَتَّبِعُونَ أَهۡوَآءَهُمۡۚ وَمَنۡ أَضَلُّ مِمَّنِ اِ۪تَّبَعَ هَوَىٰهُ بِغَيۡرِ هُدٗى مِّنَ اَ۬للَّهِۚ إِنَّ اَ۬للَّهَ لَا يَهۡدِي اِ۬لۡقَوۡمَ اَ۬لظَّٰلِمِينَ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3300,20,'392',28,'Al-Qaṣaṣ','القَصَص',1,1,51,'۞ وَلَقَدۡ وَصَّلۡنَا لَهُمُ اُ۬لۡقَوۡلَ لَعَلَّهُمۡ يَتَذَكَّرُونَ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3301,20,'392',28,'Al-Qaṣaṣ','القَصَص',1,2,52,'اَ۬لَّذِينَ ءَاتَيۡنَٰهُمُ اُ۬لۡكِتَٰبَ مِن قَبۡلِهِۦ هُم بِهِۦ يُؤۡمِنُونَ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3302,20,'392',28,'Al-Qaṣaṣ','القَصَص',2,4,53,'وَإِذَا يُتۡلَىٰ عَلَيۡهِمۡ قَالُوٓاْ ءَامَنَّا بِهِۦٓ إِنَّهُ اُ۬لۡحَقُّ مِن رَّبِّنَآ إِنَّا كُنَّا مِن قَبۡلِهِۦ مُسۡلِمِينَ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3303,20,'392',28,'Al-Qaṣaṣ','القَصَص',4,5,54,'أُوْلَٰٓئِكَ يُؤۡتَوۡنَ أَجۡرَهُم مَّرَّتَيۡنِ بِمَا صَبَرُواْ وَيَدۡرَءُونَ بِالۡحَسَنَةِ اِ۬لسَّيِّئَةَ وَمِمَّا رَزَقۡنَٰهُمۡ يُنفِقُونَ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3304,20,'392',28,'Al-Qaṣaṣ','القَصَص',5,7,55,'وَإِذَا سَمِعُواْ اُ۬للَّغۡوَ أَعۡرَضُواْ عَنۡهُ وَقَالُواْ لَنَآ أَعۡمَٰلُنَا وَلَكُمۡ أَعۡمَٰلُكُمۡ سَلَٰمٌ عَلَيۡكُمۡ لَا نَبۡتَغِي اِ۬لۡجَٰهِلِينَ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3305,20,'392',28,'Al-Qaṣaṣ','القَصَص',7,8,56,'إِنَّكَ لَا تَهۡدِي مَنۡ أَحۡبَبۡتَ وَلَٰكِنَّ اَ۬للَّهَ يَهۡدِي مَن يَشَآءُۚ وَهۡوَ أَعۡلَمُ بِالۡمُهۡتَدِينَ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3306,20,'392',28,'Al-Qaṣaṣ','القَصَص',9,11,57,'وَقَالُوٓاْ إِن نَّتَّبِعِ اِ۬لۡهُدَىٰ مَعَكَ نُتَخَطَّفۡ مِنۡ أَرۡضِنَآۚ أَوَلَمۡ نُمَكِّن لَّهُمۡ حَرَمًا ءَامِنٗا يُجۡبَىٰٓ إِلَيۡهِ ثَمَرَٰتُ كُلِّ شَيۡءٖ رِّزۡقٗا مِّن لَّدُنَّا وَلَٰكِنَّ أَكۡثَرَهُمۡ لَا يَعۡلَمُونَ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3307,20,'392',28,'Al-Qaṣaṣ','القَصَص',11,13,58,'وَكَمۡ أَهۡلَكۡنَا مِن قَرۡيَةِۢ بَطِرَتۡ مَعِيشَتَهَاۖ فَتِلۡكَ مَسَٰكِنُهُمۡ لَمۡ تُسۡكَن مِّنۢ بَعۡدِهِمۡ إِلَّا قَلِيلٗاۖ وَكُنَّا نَحۡنُ اُ۬لۡوَٰرِثِينَ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3308,20,'392',28,'Al-Qaṣaṣ','القَصَص',13,15,59,'وَمَا كَانَ رَبُّكَ مُهۡلِكَ اَ۬لۡقُر۪يٰ حَتَّىٰ يَبۡعَثَ فِيٓ أُمِّهَا رَسُولٗا يَتۡلُواْ عَلَيۡهِمۡ ءَايَٰتِنَاۚ وَمَا كُنَّا مُهۡلِكِي اِ۬لۡقُر۪يٰٓ إِلَّا وَأَهۡلُهَا ظَٰلِمُونَ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3309,20,'393',28,'Al-Qaṣaṣ','القَصَص',1,2,60,'وَمَآ أُوتِيتُم مِّن شَيۡءٖ فَمَتَٰعُ اُ۬لۡحَيَوٰةِ اِ۬لدُّنۡيۭا وَزِينَتُهَاۚ وَمَا عِندَ اَ۬للَّهِ خَيۡرٞ وَأَبۡقَىٰٓۚ أَفَلَا يَعۡقِلُونَ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3310,20,'393',28,'Al-Qaṣaṣ','القَصَص',2,4,61,'أَفَمَن وَعَدۡنَٰهُ وَعۡدًا حَسَنٗا فَهۡوَ لَٰقِيهِ كَمَن مَّتَّعۡنَٰهُ مَتَٰعَ اَ۬لۡحَيَوٰةِ اِ۬لدُّنۡيۭا ثُمَّ هُوَ يَوۡمَ اَ۬لۡقِيَٰمَةِ مِنَ اَ۬لۡمُحۡضَرِينَ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3311,20,'393',28,'Al-Qaṣaṣ','القَصَص',4,5,62,'وَيَوۡمَ يُنَادِيهِمۡ فَيَقُولُ أَيۡنَ شُرَكَآءِيَ اَ۬لَّذِينَ كُنتُمۡ تَزۡعُمُونَ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3312,20,'393',28,'Al-Qaṣaṣ','القَصَص',5,7,63,'۞ قَالَ اَ۬لَّذِينَ حَقَّ عَلَيۡهِمِ اِ۬لۡقَوۡلُ رَبَّنَا هَٰٓؤُلَآءِ اِ۬لَّذِينَ أَغۡوَيۡنَآ أَغۡوَيۡنَٰهُمۡ كَمَا غَوَيۡنَاۖ تَبَرَّأۡنَآ إِلَيۡكَۖ مَا كَانُوٓاْ إِيَّانَا يَعۡبُدُونَ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3313,20,'393',28,'Al-Qaṣaṣ','القَصَص',7,8,64,'وَقِيلَ اَ۟دۡعُواْ شُرَكَآءَكُمۡ فَدَعَوۡهُمۡ فَلَمۡ يَسۡتَجِيبُواْ لَهُمۡ وَرَأَوُاْ اُ۬لۡعَذَابَۚ لَوۡ أَنَّهُمۡ كَانُواْ يَهۡتَدُونَ ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3314,20,'393',28,'Al-Qaṣaṣ','القَصَص',9,9,65,'وَيَوۡمَ يُنَادِيهِمۡ فَيَقُولُ مَاذَآ أَجَبۡتُمُ اُ۬لۡمُرۡسَلِينَ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3315,20,'393',28,'Al-Qaṣaṣ','القَصَص',9,10,66,'فَعَمِيَتۡ عَلَيۡهِمِ اِ۬لۡأَنۢبَآءُ يَوۡمَئِذٖ فَهُمۡ لَا يَتَسَآءَلُونَ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3316,20,'393',28,'Al-Qaṣaṣ','القَصَص',10,11,67,'فَأَمَّا مَن تَابَ وَءَامَنَ وَعَمِلَ صَٰلِحٗا فَعَسَىٰٓ أَن يَكُونَ مِنَ اَ۬لۡمُفۡلِحِينَ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3317,20,'393',28,'Al-Qaṣaṣ','القَصَص',12,13,68,'وَرَبُّكَ يَخۡلُقُ مَا يَشَآءُ وَيَخۡتَارُۗ مَا كَانَ لَهُمُ اُ۬لۡخِيَرَةُۚ سُبۡحَٰنَ اَ۬للَّهِ وَتَعَٰلَىٰ عَمَّا يُشۡرِكُونَ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3318,20,'393',28,'Al-Qaṣaṣ','القَصَص',13,14,69,'وَرَبُّكَ يَعۡلَمُ مَا تُكِنُّ صُدُورُهُمۡ وَمَا يُعۡلِنُونَ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3319,20,'393',28,'Al-Qaṣaṣ','القَصَص',14,15,70,'وَهۡوَ اَ۬للَّهُ لَآ إِلَٰهَ إِلَّا هُوَۖ لَهُ اُ۬لۡحَمۡدُ فِي اِ۬لۡأُولۭيٰ وَاَلۡأٓخِرَةِۖ وَلَهُ اُ۬لۡحُكۡمُ وَإِلَيۡهِ تُرۡجَعُونَ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3320,20,'394',28,'Al-Qaṣaṣ','القَصَص',1,2,71,'قُلۡ أَرَءَيۡتُمۡ إِن جَعَلَ اَ۬للَّهُ عَلَيۡكُمُ اُ۬لَّيۡلَ سَرۡمَدًا إِلَىٰ يَوۡمِ اِ۬لۡقِيَٰمَةِ مَنۡ إِلَٰهٌ غَيۡرُ اُ۬للَّهِ يَأۡتِيكُم بِضِيَآءٍۚ أَفَلَا تَسۡمَعُونَ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3321,20,'394',28,'Al-Qaṣaṣ','القَصَص',3,5,72,'قُلۡ أَرَءَيۡتُمۡ إِن جَعَلَ اَ۬للَّهُ عَلَيۡكُمُ اُ۬لنَّهَارَ سَرۡمَدًا إِلَىٰ يَوۡمِ اِ۬لۡقِيَٰمَةِ مَنۡ إِلَٰهٌ غَيۡرُ اُ۬للَّهِ يَأۡتِيكُم بِلَيۡلٖ تَسۡكُنُونَ فِيهِۚ أَفَلَا تُبۡصِرُونَ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3322,20,'394',28,'Al-Qaṣaṣ','القَصَص',5,7,73,'وَمِن رَّحۡمَتِهِۦ جَعَلَ لَكُمُ اُ۬لَّيۡلَ وَاَلنَّهَارَ لِتَسۡكُنُواْ فِيهِ وَلِتَبۡتَغُواْ مِن فَضۡلِهِۦ وَلَعَلَّكُمۡ تَشۡكُرُونَ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3323,20,'394',28,'Al-Qaṣaṣ','القَصَص',7,8,74,'وَيَوۡمَ يُنَادِيهِمۡ فَيَقُولُ أَيۡنَ شُرَكَآءِيَ اَ۬لَّذِينَ كُنتُمۡ تَزۡعُمُونَ ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3324,20,'394',28,'Al-Qaṣaṣ','القَصَص',8,10,75,'وَنَزَعۡنَا مِن كُلِّ أُمَّةٖ شَهِيدٗا فَقُلۡنَا هَاتُواْ بُرۡهَٰنَكُمۡ فَعَلِمُوٓاْ أَنَّ اَ۬لۡحَقَّ لِلَّهِ وَضَلَّ عَنۡهُم مَّا كَانُواْ يَفۡتَرُونَ ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3325,20,'394',28,'Al-Qaṣaṣ','القَصَص',10,13,76,'۞ إِنَّ قَٰرُونَ كَانَ مِن قَوۡمِ مُوسۭيٰ فَبَغَىٰ عَلَيۡهِمۡۖ وَءَاتَيۡنَٰهُ مِنَ اَ۬لۡكُنُوزِ مَآ إِنَّ مَفَاتِحَهُۥ لَتَنُوٓأُ بِالۡعُصۡبَةِ أُوْلِي اِ۬لۡقُوَّةِ إِذۡ قَالَ لَهُۥ قَوۡمُهُۥ لَا تَفۡرَحۡۖ إِنَّ اَ۬للَّهَ لَا يُحِبُّ اُ۬لۡفَرِحِينَ ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3326,20,'394',28,'Al-Qaṣaṣ','القَصَص',13,15,77,'وَاَبۡتَغِ فِيمَآ ءَاتَىٰكَ اَ۬للَّهُ اُ۬لدَّارَ اَ۬لۡأٓخِرَةَۖ وَلَا تَنسَ نَصِيبَكَ مِنَ اَ۬لدُّنۡيۭاۖ وَأَحۡسِن كَمَآ أَحۡسَنَ اَ۬للَّهُ إِلَيۡكَۖ وَلَا تَبۡغِ اِ۬لۡفَسَادَ فِي اِ۬لۡأَرۡضِۖ إِنَّ اَ۬للَّهَ لَا يُحِبُّ اُ۬لۡمُفۡسِدِينَ ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3327,20,'395',28,'Al-Qaṣaṣ','القَصَص',1,3,78,'قَالَ إِنَّمَآ أُوتِيتُهُۥ عَلَىٰ عِلۡمٍ عِندِيَۚ أَوَلَمۡ يَعۡلَمۡ أَنَّ اَ۬للَّهَ قَدۡ أَهۡلَكَ مِن قَبۡلِهِۦ مِنَ اَ۬لۡقُرُونِ مَنۡ هُوَ أَشَدُّ مِنۡهُ قُوَّةٗ وَأَكۡثَرُ جَمۡعٗاۚ وَلَا يُسۡـَٔلُ عَن ذُنُوبِهِمِ اِ۬لۡمُجۡرِمُونَ ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3328,20,'395',28,'Al-Qaṣaṣ','القَصَص',3,5,79,'فَخَرَجَ عَلَىٰ قَوۡمِهِۦ فِي زِينَتِهِۦۖ قَالَ اَ۬لَّذِينَ يُرِيدُونَ اَ۬لۡحَيَوٰةَ اَ۬لدُّنۡيۭا يَٰلَيۡتَ لَنَا مِثۡلَ مَآ أُوتِيَ قَٰرُونُ إِنَّهُۥ لَذُو حَظٍّ عَظِيمٖ ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3329,20,'395',28,'Al-Qaṣaṣ','القَصَص',5,7,80,'وَقَالَ اَ۬لَّذِينَ أُوتُواْ اُ۬لۡعِلۡمَ وَيۡلَكُمۡ ثَوَابُ اُ۬للَّهِ خَيۡرٞ لِّمَنۡ ءَامَنَ وَعَمِلَ صَٰلِحٗاۚ وَلَا يُلَقَّىٰهَآ إِلَّا اَ۬لصَّٰبِرُونَ ٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3330,20,'395',28,'Al-Qaṣaṣ','القَصَص',7,9,81,'فَخَسَفۡنَا بِهِۦ وَبِد۪ارِهِ اِ۬لۡأَرۡضَ فَمَا كَانَ لَهُۥ مِن فِئَةٖ يَنصُرُونَهُۥ مِن دُونِ اِ۬للَّهِ وَمَا كَانَ مِنَ اَ۬لۡمُنتَصِرِينَ ٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3331,20,'395',28,'Al-Qaṣaṣ','القَصَص',9,12,82,'وَأَصۡبَحَ اَ۬لَّذِينَ تَمَنَّوۡاْ مَكَانَهُۥ بِالۡأَمۡسِ يَقُولُونَ وَيۡكَأَنَّ اَ۬للَّهَ يَبۡسُطُ اُ۬لرِّزۡقَ لِمَن يَشَآءُ مِنۡ عِبَادِهِۦ وَيَقۡدِرُۖ لَوۡلَآ أَن مَّنَّ اَ۬للَّهُ عَلَيۡنَا لَخُسِفَ بِنَاۖ وَيۡكَأَنَّهُۥ لَا يُفۡلِحُ اُ۬لۡكَٰفِرُونَ ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3332,20,'395',28,'Al-Qaṣaṣ','القَصَص',12,13,83,'۞ تِلۡكَ اَ۬لدَّارُ اُ۬لۡأٓخِرَةُ نَجۡعَلُهَا لِلَّذِينَ لَا يُرِيدُونَ عُلُوّٗا فِي اِ۬لۡأَرۡضِ وَلَا فَسَادٗاۚ وَاَلۡعَٰقِبَةُ لِلۡمُتَّقِينَ ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3333,20,'395',28,'Al-Qaṣaṣ','القَصَص',14,15,84,'مَن جَآءَ بِالۡحَسَنَةِ فَلَهُۥ خَيۡرٞ مِّنۡهَاۖ وَمَن جَآءَ بِالسَّيِّئَةِ فَلَا يُجۡزَى اَ۬لَّذِينَ عَمِلُواْ اُ۬لسَّيِّـَٔاتِ إِلَّا مَا كَانُواْ يَعۡمَلُونَ ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3334,20,'396',28,'Al-Qaṣaṣ','القَصَص',1,2,85,'إِنَّ اَ۬لَّذِي فَرَضَ عَلَيۡكَ اَ۬لۡقُرۡءَانَ لَرَآدُّكَ إِلَىٰ مَعَادٖۚ قُل رَّبِّيَ أَعۡلَمُ مَن جَآءَ بِالۡهُدَىٰ وَمَنۡ هُوَ فِي ضَلَٰلٖ مُّبِينٖ ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3335,20,'396',28,'Al-Qaṣaṣ','القَصَص',2,4,86,'وَمَا كُنتَ تَرۡجُوٓاْ أَن يُلۡقَىٰٓ إِلَيۡكَ اَ۬لۡكِتَٰبُ إِلَّا رَحۡمَةٗ مِّن رَّبِّكَۖ فَلَا تَكُونَنَّ ظَهِيرٗا لِّلۡكٰ۪فِرِينَ ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3336,20,'396',28,'Al-Qaṣaṣ','القَصَص',4,6,87,'وَلَا يَصُدُّنَّكَ عَنۡ ءَايَٰتِ اِ۬للَّهِ بَعۡدَ إِذۡ أُنزِلَتۡ إِلَيۡكَۖ وَاَدۡعُ إِلَىٰ رَبِّكَۖ وَلَا تَكُونَنَّ مِنَ اَ۬لۡمُشۡرِكِينَ ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3337,20,'396',28,'Al-Qaṣaṣ','القَصَص',6,7,88,'وَلَا تَدۡعُ مَعَ اَ۬للَّهِ إِلَٰهًا ءَاخَرَۘ لَآ إِلَٰهَ إِلَّا هُوَۚ كُلُّ شَيۡءٍ هَالِكٌ إِلَّا وَجۡهَهُۥۚ لَهُ اُ۬لۡحُكۡمُ وَإِلَيۡهِ تُرۡجَعُونَ ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3338,20,'396',29,'Al-‘Ankabūt','العَنكبُوت',10,11,1,'الٓمٓۚ أَحَسِبَ اَ۬لنَّاسُ أَن يُتۡرَكُوٓاْ أَن يَقُولُوٓاْ ءَامَنَّا وَهُمۡ لَا يُفۡتَنُونَ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3339,20,'396',29,'Al-‘Ankabūt','العَنكبُوت',11,12,2,'وَلَقَدۡ فَتَنَّا اَ۬لَّذِينَ مِن قَبۡلِهِمۡۖ فَلَيَعۡلَمَنَّ اَ۬للَّهُ اُ۬لَّذِينَ صَدَقُواْ وَلَيَعۡلَمَنَّ اَ۬لۡكَٰذِبِينَ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3340,20,'396',29,'Al-‘Ankabūt','العَنكبُوت',12,13,3,'أَمۡ حَسِبَ اَ۬لَّذِينَ يَعۡمَلُونَ اَ۬لسَّيِّـَٔاتِ أَن يَسۡبِقُونَاۚ سَآءَ مَا يَحۡكُمُونَ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3341,20,'396',29,'Al-‘Ankabūt','العَنكبُوت',13,14,4,'مَن كَانَ يَرۡجُواْ لِقَآءَ اَ۬للَّهِ فَإِنَّ أَجَلَ اَ۬للَّهِ لَأٓتٖۚ وَهۡوَ اَ۬لسَّمِيعُ اُ۬لۡعَلِيمُ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3342,20,'396',29,'Al-‘Ankabūt','العَنكبُوت',14,15,5,'وَمَن جَٰهَدَ فَإِنَّمَا يُجَٰهِدُ لِنَفۡسِهِۦٓۚ إِنَّ اَ۬للَّهَ لَغَنِيٌّ عَنِ اِ۬لۡعَٰلَمِينَ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3343,20,'397',29,'Al-‘Ankabūt','العَنكبُوت',1,2,6,'وَاَلَّذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ لَنُكَفِّرَنَّ عَنۡهُمۡ سَيِّـَٔاتِهِمۡ وَلَنَجۡزِيَنَّهُمۡ أَحۡسَنَ اَ۬لَّذِي كَانُواْ يَعۡمَلُونَ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3344,20,'397',29,'Al-‘Ankabūt','العَنكبُوت',2,4,7,'۞ وَوَصَّيۡنَا اَ۬لۡإِنسَٰنَ بِوَٰلِدَيۡهِ حُسۡنٗاۖ وَإِن جَٰهَدَاكَ لِتُشۡرِكَ بِي مَا لَيۡسَ لَكَ بِهِۦ عِلۡمٞ فَلَا تُطِعۡهُمَآۚ إِلَيَّ مَرۡجِعُكُمۡ فَأُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3345,20,'397',29,'Al-‘Ankabūt','العَنكبُوت',5,5,8,'وَاَلَّذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ لَنُدۡخِلَنَّهُمۡ فِي اِ۬لصَّٰلِحِينَ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3346,20,'397',29,'Al-‘Ankabūt','العَنكبُوت',6,8,9,'وَمِنَ اَ۬لنّ۪اسِ مَن يَقُولُ ءَامَنَّا بِاللَّهِ فَإِذَآ أُوذِيَ فِي اِ۬للَّهِ جَعَلَ فِتۡنَةَ اَ۬لنّ۪اسِ كَعَذَابِ اِ۬للَّهِۖ وَلَئِن جَآءَ نَصۡرٞ مِّن رَّبِّكَ لَيَقُولُنَّ إِنَّا كُنَّا مَعَكُمۡۚ أَوَلَيۡسَ اَ۬للَّهُ بِأَعۡلَمَ بِمَا فِي صُدُورِ اِ۬لۡعَٰلَمِينَ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3347,20,'397',29,'Al-‘Ankabūt','العَنكبُوت',9,9,10,'وَلَيَعۡلَمَنَّ اَ۬للَّهُ اُ۬لَّذِينَ ءَامَنُواْ وَلَيَعۡلَمَنَّ اَ۬لۡمُنَٰفِقِينَ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3348,20,'397',29,'Al-‘Ankabūt','العَنكبُوت',10,12,11,'وَقَالَ اَ۬لَّذِينَ كَفَرُواْ لِلَّذِينَ ءَامَنُواْ اُ۪تَّبِعُواْ سَبِيلَنَا وَلۡنَحۡمِلۡ خَطَٰيَٰكُمۡ وَمَا هُم بِحَٰمِلِينَ مِنۡ خَطَٰيَٰهُم مِّن شَيۡءٍۖ إِنَّهُمۡ لَكَٰذِبُونَ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3349,20,'397',29,'Al-‘Ankabūt','العَنكبُوت',12,13,12,'وَلَيَحۡمِلُنَّ أَثۡقَالَهُمۡ وَأَثۡقَالٗا مَّعَ أَثۡقَالِهِمۡۖ وَلَيُسۡـَٔلُنَّ يَوۡمَ اَ۬لۡقِيَٰمَةِ عَمَّا كَانُواْ يَفۡتَرُونَ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3350,20,'397',29,'Al-‘Ankabūt','العَنكبُوت',14,15,13,'وَلَقَدۡ أَرۡسَلۡنَا نُوحًا إِلَىٰ قَوۡمِهِۦ فَلَبِثَ فِيهِمۡ أَلۡفَ سَنَةٍ إِلَّا خَمۡسِينَ عَامٗا فَأَخَذَهُمُ اُ۬لطُّوفَانُ وَهُمۡ ظَٰلِمُونَ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3351,20,'398',29,'Al-‘Ankabūt','العَنكبُوت',1,1,14,'فَأَنجَيۡنَٰهُ وَأَصۡحَٰبَ اَ۬لسَّفِينَةِ وَجَعَلۡنَٰهَآ ءَايَةٗ لِّلۡعَٰلَمِينَ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3352,20,'398',29,'Al-‘Ankabūt','العَنكبُوت',2,3,15,'وَإِبۡرَٰهِيمَ إِذۡ قَالَ لِقَوۡمِهِ اِ۟عۡبُدُواْ اُ۬للَّهَ وَاَتَّقُوهُۖ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ إِن كُنتُمۡ تَعۡلَمُونَ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3353,20,'398',29,'Al-‘Ankabūt','العَنكبُوت',3,6,16,'۞ إِنَّمَا تَعۡبُدُونَ مِن دُونِ اِ۬للَّهِ أَوۡثَٰنٗا وَتَخۡلُقُونَ إِفۡكًاۚ إِنَّ اَ۬لَّذِينَ تَعۡبُدُونَ مِن دُونِ اِ۬للَّهِ لَا يَمۡلِكُونَ لَكُمۡ رِزۡقٗا فَاَبۡتَغُواْ عِندَ اَ۬للَّهِ اِ۬لرِّزۡقَ وَاَعۡبُدُوهُ وَاَشۡكُرُواْ لَهُۥٓۖ إِلَيۡهِ تُرۡجَعُونَ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3354,20,'398',29,'Al-‘Ankabūt','العَنكبُوت',6,8,17,'وَإِن تُكَذِّبُواْ فَقَدۡ كَذَّبَ أُمَمٞ مِّن قَبۡلِكُمۡۖ وَمَا عَلَى اَ۬لرَّسُولِ إِلَّا اَ۬لۡبَلَٰغُ اُ۬لۡمُبِينُ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3355,20,'398',29,'Al-‘Ankabūt','العَنكبُوت',8,9,18,'أَوَلَمۡ يَرَوۡاْ كَيۡفَ يُبۡدِئُ اُ۬للَّهُ اُ۬لۡخَلۡقَ ثُمَّ يُعِيدُهُۥٓۚ إِنَّ ذَٰلِكَ عَلَى اَ۬للَّهِ يَسِيرٞ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3356,20,'398',29,'Al-‘Ankabūt','العَنكبُوت',9,11,19,'قُلۡ سِيرُواْ فِي اِ۬لۡأَرۡضِ فَاَنظُرُواْ كَيۡفَ بَدَأَ اَ۬لۡخَلۡقَۚ ثُمَّ اَ۬للَّهُ يُنشِئُ اُ۬لنَّشَآءَةَ اَ۬لۡأٓخِرَةَۚ إِنَّ اَ۬للَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3357,20,'398',29,'Al-‘Ankabūt','العَنكبُوت',11,12,20,'يُعَذِّبُ مَن يَشَآءُ وَيَرۡحَمُ مَن يَشَآءُۖ وَإِلَيۡهِ تُقۡلَبُونَ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3358,20,'398',29,'Al-‘Ankabūt','العَنكبُوت',12,14,21,'وَمَآ أَنتُم بِمُعۡجِزِينَ فِي اِ۬لۡأَرۡضِ وَلَا فِي اِ۬لسَّمَآءِۖ وَمَا لَكُم مِّن دُونِ اِ۬للَّهِ مِن وَلِيّٖ وَلَا نَصِيرٖ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3359,20,'398',29,'Al-‘Ankabūt','العَنكبُوت',14,15,22,'وَاَلَّذِينَ كَفَرُواْ بِـَٔايَٰتِ اِ۬للَّهِ وَلِقَآئِهِۦٓ أُوْلَٰٓئِكَ يَئِسُواْ مِن رَّحۡمَتِي وَأُوْلَٰٓئِكَ لَهُمۡ عَذَابٌ أَلِيمٞ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3360,20,'399',29,'Al-‘Ankabūt','العَنكبُوت',1,2,23,'فَمَا كَانَ جَوَابَ قَوۡمِهِۦٓ إِلَّآ أَن قَالُواْ اُ۟قۡتُلُوهُ أَوۡ حَرِّقُوهُ فَأَنجَىٰهُ اُ۬للَّهُ مِنَ اَ۬لنّ۪ارِۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يُؤۡمِنُونَ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3361,20,'399',29,'Al-‘Ankabūt','العَنكبُوت',3,6,24,'وَقَالَ إِنَّمَا اَ۪تَّخَذتُّم مِّن دُونِ اِ۬للَّهِ أَوۡثَٰنٗا مَّوَدَّةُ بَيۡنِكُمۡ فِي اِ۬لۡحَيَوٰةِ اِ۬لدُّنۡيۭاۖ ثُمَّ يَوۡمَ اَ۬لۡقِيَٰمَةِ يَكۡفُرُ بَعۡضُكُم بِبَعۡضٖ وَيَلۡعَنُ بَعۡضُكُم بَعۡضٗا وَمَأۡوَىٰكُمُ اُ۬لنَّارُ وَمَا لَكُم مِّن نَّٰصِرِينَ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3362,20,'399',29,'Al-‘Ankabūt','العَنكبُوت',6,7,25,'فَـَٔامَنَ لَهُۥ لُوطٞۘ وَقَالَ إِنِّي مُهَاجِرٌ إِلَىٰ رَبِّيَۖ إِنَّهُۥ هُوَ اَ۬لۡعَزِيزُ اُ۬لۡحَكِيمُ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3363,20,'399',29,'Al-‘Ankabūt','العَنكبُوت',8,10,26,'۞ وَوَهَبۡنَا لَهُۥٓ إِسۡحَٰقَ وَيَعۡقُوبَ وَجَعَلۡنَا فِي ذُرِّيَّتِهِ اِ۬لنُّبُوَّةَ وَاَلۡكِتَٰبَ وَءَاتَيۡنَٰهُ أَجۡرَهُۥ فِي اِ۬لدُّنۡيۭاۖ وَإِنَّهُۥ فِي اِ۬لۡأٓخِرَةِ لَمِنَ اَ۬لصَّٰلِحِينَ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3364,20,'399',29,'Al-‘Ankabūt','العَنكبُوت',10,12,27,'وَلُوطًا إِذۡ قَالَ لِقَوۡمِهِۦٓ أَٰ۟نَّكُمۡ لَتَأۡتُونَ اَ۬لۡفَٰحِشَةَ مَا سَبَقَكُم بِهَا مِنۡ أَحَدٖ مِّنَ اَ۬لۡعَٰلَمِينَ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3365,20,'399',29,'Al-‘Ankabūt','العَنكبُوت',12,13,28,'أَٰى۪نَّكُمۡ لَتَأۡتُونَ اَ۬لرِّجَالَ وَتَقۡطَعُونَ اَ۬لسَّبِيلَ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3366,20,'399',29,'Al-‘Ankabūt','العَنكبُوت',13,15,29,'وَتَأۡتُونَ فِي نَادِيكُمُ اُ۬لۡمُنكَرَۖ فَمَا كَانَ جَوَابَ قَوۡمِهِۦٓ إِلَّآ أَن قَالُواْ اُ۪ئۡتِنَا بِعَذَابِ اِ۬للَّهِ إِن كُنتَ مِنَ اَ۬لصَّٰدِقِينَ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3367,20,'399',29,'Al-‘Ankabūt','العَنكبُوت',15,15,30,'قَالَ رَبِّ اِ۟نصُرۡنِي عَلَى اَ۬لۡقَوۡمِ اِ۬لۡمُفۡسِدِينَ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3368,20,'400',29,'Al-‘Ankabūt','العَنكبُوت',1,2,31,'وَلَمَّا جَآءَتۡ رُسۡلُنَآ إِبۡرَٰهِيمَ بِالۡبُشۡر۪يٰ قَالُوٓاْ إِنَّا مُهۡلِكُوٓاْ أَهۡلِ هَٰذِهِ اِ۬لۡقَرۡيَةِۖ إِنَّ أَهۡلَهَا كَانُواْ ظَٰلِمِينَ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3369,20,'400',29,'Al-‘Ankabūt','العَنكبُوت',3,4,32,'قَالَ إِنَّ فِيهَا لُوطٗاۚ قَالُواْ نَحۡنُ أَعۡلَمُ بِمَن فِيهَاۖ لَنُنَجِّيَنَّهُۥ وَأَهۡلَهُۥٓ إِلَّا اَ۪مۡرَأَتَهُۥ كَانَتۡ مِنَ اَ۬لۡغَٰبِرِينَ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3370,20,'400',29,'Al-‘Ankabūt','العَنكبُوت',4,7,33,'وَلَمَّآ أَن جَآءَتۡ رُسۡلُنَا لُوطٗا سِيٓءَ بِهِمۡ وَضَاقَ بِهِمۡ ذَرۡعٗا وَقَالُواْ لَا تَخَفۡ وَلَا تَحۡزَنۡۖ إِنَّا مُنَجُّوكَ وَأَهۡلَكَ إِلَّا اَ۪مۡرَأَتَكَ كَانَتۡ مِنَ اَ۬لۡغَٰبِرِينَ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3371,20,'400',29,'Al-‘Ankabūt','العَنكبُوت',7,8,34,'إِنَّا مُنزِلُونَ عَلَىٰٓ أَهۡلِ هَٰذِهِ اِ۬لۡقَرۡيَةِ رِجۡزٗا مِّنَ اَ۬لسَّمَآءِ بِمَا كَانُواْ يَفۡسُقُونَ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3372,20,'400',29,'Al-‘Ankabūt','العَنكبُوت',9,9,35,'وَلَقَد تَّرَكۡنَا مِنۡهَآ ءَايَةَۢ بَيِّنَةٗ لِّقَوۡمٖ يَعۡقِلُونَ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3373,20,'400',29,'Al-‘Ankabūt','العَنكبُوت',10,11,36,'وَإِلَىٰ مَدۡيَنَ أَخَاهُمۡ شُعَيۡبٗا فَقَالَ يَٰقَوۡمِ اِ۟عۡبُدُواْ اُ۬للَّهَ وَاَرۡجُواْ اُ۬لۡيَوۡمَ اَ۬لۡأٓخِرَ وَلَا تَعۡثَوۡاْ فِي اِ۬لۡأَرۡضِ مُفۡسِدِينَ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3374,20,'400',29,'Al-‘Ankabūt','العَنكبُوت',12,13,37,'فَكَذَّبُوهُ فَأَخَذَتۡهُمُ اُ۬لرَّجۡفَةُ فَأَصۡبَحُواْ فِي د۪ارِهِمۡ جَٰثِمِينَ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3375,20,'400',29,'Al-‘Ankabūt','العَنكبُوت',13,15,38,'۞ وَعَادٗا وَثَمُودٗا وَقَد تَّبَيَّنَ لَكُم مِّن مَّسَٰكِنِهِمۡۖ وَزَيَّنَ لَهُمُ اُ۬لشَّيۡطَٰنُ أَعۡمَٰلَهُمۡ فَصَدَّهُمۡ عَنِ اِ۬لسَّبِيلِ وَكَانُواْ مُسۡتَبۡصِرِينَ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3376,20,'401',29,'Al-‘Ankabūt','العَنكبُوت',1,2,39,'وَقَٰرُونَ وَفِرۡعَوۡنَ وَهَٰمَٰنَۖ وَلَقَد جَّآءَهُم مُّوسۭيٰ بِالۡبَيِّنَٰتِ فَاَسۡتَكۡبَرُواْ فِي اِ۬لۡأَرۡضِ وَمَا كَانُواْ سَٰبِقِينَ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3377,20,'401',29,'Al-‘Ankabūt','العَنكبُوت',3,6,40,'فَكُلًّا أَخَذۡنَا بِذَنۢبِهِۦۖ فَمِنۡهُم مَّنۡ أَرۡسَلۡنَا عَلَيۡهِ حَاصِبٗا وَمِنۡهُم مَّنۡ أَخَذَتۡهُ اُ۬لصَّيۡحَةُ وَمِنۡهُم مَّنۡ خَسَفۡنَا بِهِ اِ۬لۡأَرۡضَ وَمِنۡهُم مَّنۡ أَغۡرَقۡنَاۚ وَمَا كَانَ اَ۬للَّهُ لِيَظۡلِمَهُمۡ وَلَٰكِن كَانُوٓاْ أَنفُسَهُمۡ يَظۡلِمُونَ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3378,20,'401',29,'Al-‘Ankabūt','العَنكبُوت',6,9,41,'مَثَلُ اُ۬لَّذِينَ اَ۪تَّخَذُواْ مِن دُونِ اِ۬للَّهِ أَوۡلِيَآءَ كَمَثَلِ اِ۬لۡعَنكَبُوتِ اِ۪تَّخَذَتۡ بَيۡتٗاۖ وَإِنَّ أَوۡهَنَ اَ۬لۡبُيُوتِ لَبَيۡتُ اُ۬لۡعَنكَبُوتِۚ لَوۡ كَانُواْ يَعۡلَمُونَ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3379,20,'401',29,'Al-‘Ankabūt','العَنكبُوت',9,10,42,'إِنَّ اَ۬للَّهَ يَعۡلَمُ مَا يَدۡعُونَ مِن دُونِهِۦ مِن شَيۡءٖۚ وَهۡوَ اَ۬لۡعَزِيزُ اُ۬لۡحَكِيمُ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3380,20,'401',29,'Al-‘Ankabūt','العَنكبُوت',10,11,43,'وَتِلۡكَ اَ۬لۡأَمۡثَٰلُ نَضۡرِبُهَا لِلنّ۪اسِۖ وَمَا يَعۡقِلُهَآ إِلَّا اَ۬لۡعَٰلِمُونَ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3381,20,'401',29,'Al-‘Ankabūt','العَنكبُوت',12,13,44,'خَلَقَ اَ۬للَّهُ اُ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضَ بِالۡحَقِّۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّلۡمُؤۡمِنِينَ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3382,20,'401',29,'Al-‘Ankabūt','العَنكبُوت',13,15,45,'اَ۟تۡلُ مَآ أُوحِيَ إِلَيۡكَ مِنَ اَ۬لۡكِتَٰبِ وَأَقِمِ اِ۬لصَّلَوٰةَۖ إِنَّ اَ۬لصَّلَوٰةَ تَنۡهَىٰ عَنِ اِ۬لۡفَحۡشَآءِ وَاَلۡمُنكَرِۗ وَلَذِكۡرُ اُ۬للَّهِ أَكۡبَرُۗ وَاَللَّهُ يَعۡلَمُ مَا تَصۡنَعُونَ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3383,21,'402',29,'Al-‘Ankabūt','العَنكبُوت',1,3,46,'۞ وَلَا تُجَٰدِلُوٓاْ أَهۡلَ اَ۬لۡكِتَٰبِ إِلَّا بِالَّتِي هِيَ أَحۡسَنُ إِلَّا اَ۬لَّذِينَ ظَلَمُواْ مِنۡهُمۡۖ وَقُولُوٓاْ ءَامَنَّا بِالَّذِيٓ أُنزِلَ إِلَيۡنَا وَأُنزِلَ إِلَيۡكُمۡ وَإِلَٰهُنَا وَإِلَٰهُكُمۡ وَٰحِدٞ وَنَحۡنُ لَهُۥ مُسۡلِمُونَ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3384,21,'402',29,'Al-‘Ankabūt','العَنكبُوت',4,6,47,'وَكَذَٰلِكَ أَنزَلۡنَآ إِلَيۡكَ اَ۬لۡكِتَٰبَۚ فَاَلَّذِينَ ءَاتَيۡنَٰهُمُ اُ۬لۡكِتَٰبَ يُؤۡمِنُونَ بِهِۦۖ وَمِنۡ هَٰٓؤُلَآءِ مَن يُؤۡمِنُ بِهِۦۚ وَمَا يَجۡحَدُ بِـَٔايَٰتِنَآ إِلَّا اَ۬لۡكَٰفِرُونَ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3385,21,'402',29,'Al-‘Ankabūt','العَنكبُوت',6,8,48,'وَمَا كُنتَ تَتۡلُواْ مِن قَبۡلِهِۦ مِن كِتَٰبٖ وَلَا تَخُطُّهُۥ بِيَمِينِكَۖ إِذٗا لَّاَرۡتَابَ اَ۬لۡمُبۡطِلُونَ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3386,21,'402',29,'Al-‘Ankabūt','العَنكبُوت',8,9,49,'بَلۡ هُوَ ءَايَٰتُۢ بَيِّنَٰتٞ فِي صُدُورِ اِ۬لَّذِينَ أُوتُواْ اُ۬لۡعِلۡمَۚ وَمَا يَجۡحَدُ بِـَٔايَٰتِنَآ إِلَّا اَ۬لظَّٰلِمُونَ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3387,21,'402',29,'Al-‘Ankabūt','العَنكبُوت',9,11,50,'وَقَالُواْ لَوۡلَآ أُنزِلَ عَلَيۡهِ ءَايَٰتٞ مِّن رَّبِّهِۦۖ قُلۡ إِنَّمَا اَ۬لۡأٓيَٰتُ عِندَ اَ۬للَّهِ وَإِنَّمَآ أَنَا۠ نَذِيرٞ مُّبِينٌ ٥٠');
