INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3422,21,'406',30,'Ar-Rūm','الرُّوم',2,3,16,'فَسُبۡحَٰنَ اَ۬للَّهِ حِينَ تُمۡسُونَ وَحِينَ تُصۡبِحُونَ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3423,21,'406',30,'Ar-Rūm','الرُّوم',3,4,17,'وَلَهُ اُ۬لۡحَمۡدُ فِي اِ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِ وَعَشِيّٗا وَحِينَ تُظۡهِرُونَ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3424,21,'406',30,'Ar-Rūm','الرُّوم',4,5,18,'يُخۡرِجُ اُ۬لۡحَيَّ مِنَ اَ۬لۡمَيۡتِ وَيُخۡرِجُ اُ۬لۡمَيۡتَ مِنَ اَ۬لۡحَيِّ وَيُحۡيِ اِ۬لۡأَرۡضَ بَعۡدَ مَوۡتِهَاۚ وَكَذَٰلِكَ تُخۡرَجُونَ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3425,21,'406',30,'Ar-Rūm','الرُّوم',6,7,19,'وَمِنۡ ءَايَٰتِهِۦٓ أَنۡ خَلَقَكُم مِّن تُرَابٖ ثُمَّ إِذَآ أَنتُم بَشَرٞ تَنتَشِرُونَ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3426,21,'406',30,'Ar-Rūm','الرُّوم',7,9,20,'وَمِنۡ ءَايَٰتِهِۦٓ أَنۡ خَلَقَ لَكُم مِّنۡ أَنفُسِكُمۡ أَزۡوَٰجٗا لِّتَسۡكُنُوٓاْ إِلَيۡهَا وَجَعَلَ بَيۡنَكُم مَّوَدَّةٗ وَرَحۡمَةًۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَتَفَكَّرُونَ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3427,21,'406',30,'Ar-Rūm','الرُّوم',9,11,21,'وَمِنۡ ءَايَٰتِهِۦ خَلۡقُ اُ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِ وَاَخۡتِلَٰفُ أَلۡسِنَتِكُمۡ وَأَلۡوَٰنِكُمۡۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّلۡعَٰلَمِينَ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3428,21,'406',30,'Ar-Rūm','الرُّوم',11,13,22,'۞ وَمِنۡ ءَايَٰتِهِۦ مَنَامُكُم بِالَّيۡلِ وَاَلنَّه۪ارِ وَاَبۡتِغَآؤُكُم مِّن فَضۡلِهِۦٓۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَسۡمَعُونَ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3429,21,'406',30,'Ar-Rūm','الرُّوم',13,15,23,'وَمِنۡ ءَايَٰتِهِۦ يُرِيكُمُ اُ۬لۡبَرۡقَ خَوۡفٗا وَطَمَعٗا وَيُنزِلُ مِنَ اَ۬لسَّمَآءِ مَآءٗ فَيُحۡيِۦ بِهِ اِ۬لۡأَرۡضَ بَعۡدَ مَوۡتِهَآۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَعۡقِلُونَ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3430,21,'407',30,'Ar-Rūm','الرُّوم',1,2,24,'وَمِنۡ ءَايَٰتِهِۦٓ أَن تَقُومَ اَ۬لسَّمَآءُ وَاَلۡأَرۡضُ بِأَمۡرِهِۦۚ ثُمَّ إِذَا دَعَاكُمۡ دَعۡوَةٗ مِّنَ اَ۬لۡأَرۡضِ إِذَآ أَنتُمۡ تَخۡرُجُونَ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3431,21,'407',30,'Ar-Rūm','الرُّوم',2,3,25,'وَلَهُۥ مَن فِي اِ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِۖ كُلّٞ لَّهُۥ قَٰنِتُونَ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3432,21,'407',30,'Ar-Rūm','الرُّوم',3,5,26,'وَهۡوَ اَ۬لَّذِي يَبۡدَؤُاْ اُ۬لۡخَلۡقَ ثُمَّ يُعِيدُهُۥ وَهۡوَ أَهۡوَنُ عَلَيۡهِۚ وَلَهُ اُ۬لۡمَثَلُ اُ۬لۡأَعۡلَىٰ فِي اِ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِۚ وَهۡوَ اَ۬لۡعَزِيزُ اُ۬لۡحَكِيمُ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3433,21,'407',30,'Ar-Rūm','الرُّوم',5,9,27,'ضَرَبَ لَكُم مَّثَلٗا مِّنۡ أَنفُسِكُمۡۖ هَل لَّكُم مِّن مَّا مَلَكَتۡ أَيۡمَٰنُكُم مِّن شُرَكَآءَ فِي مَا رَزَقۡنَٰكُمۡ فَأَنتُمۡ فِيهِ سَوَآءٞ تَخَافُونَهُمۡ كَخِيفَتِكُمۡ أَنفُسَكُمۡۚ كَذَٰلِكَ نُفَصِّلُ اُ۬لۡأٓيَٰتِ لِقَوۡمٖ يَعۡقِلُونَ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3434,21,'407',30,'Ar-Rūm','الرُّوم',9,10,28,'بَلِ اِ۪تَّبَعَ اَ۬لَّذِينَ ظَلَمُوٓاْ أَهۡوَآءَهُم بِغَيۡرِ عِلۡمٖۖ فَمَن يَهۡدِي مَنۡ أَضَلَّ اَ۬للَّهُۖ وَمَا لَهُم مِّن نَّٰصِرِينَ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3435,21,'407',30,'Ar-Rūm','الرُّوم',10,13,29,'فَأَقِمۡ وَجۡهَكَ لِلدِّينِ حَنِيفٗاۚ فِطۡرَتَ اَ۬للَّهِ اِ۬لَّتِي فَطَرَ اَ۬لنَّاسَ عَلَيۡهَاۚ لَا تَبۡدِيلَ لِخَلۡقِ اِ۬للَّهِۚ ذَٰلِكَ اَ۬لدِّينُ اُ۬لۡقَيِّمُ وَلَٰكِنَّ أَكۡثَرَ اَ۬لنّ۪اسِ لَا يَعۡلَمُونَ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3436,21,'407',30,'Ar-Rūm','الرُّوم',13,14,30,'۞ مُنِيبِينَ إِلَيۡهِ وَاَتَّقُوهُ وَأَقِيمُواْ اُ۬لصَّلَوٰةَ وَلَا تَكُونُواْ مِنَ اَ۬لۡمُشۡرِكِينَ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3437,21,'407',30,'Ar-Rūm','الرُّوم',14,15,31,'مِنَ اَ۬لَّذِينَ فَرَّقُواْ دِينَهُمۡ وَكَانُواْ شِيَعٗاۖ كُلُّ حِزۡبِۢ بِمَا لَدَيۡهِمۡ فَرِحُونَ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3438,21,'408',30,'Ar-Rūm','الرُّوم',1,2,32,'وَإِذَا مَسَّ اَ۬لنَّاسَ ضُرّٞ دَعَوۡاْ رَبَّهُم مُّنِيبِينَ إِلَيۡهِ ثُمَّ إِذَآ أَذَاقَهُم مِّنۡهُ رَحۡمَةً إِذَا فَرِيقٞ مِّنۡهُم بِرَبِّهِمۡ يُشۡرِكُونَ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3439,21,'408',30,'Ar-Rūm','الرُّوم',2,3,33,'لِيَكۡفُرُواْ بِمَآ ءَاتَيۡنَٰهُمۡۚ فَتَمَتَّعُواْ فَسَوۡفَ تَعۡلَمُونَ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3440,21,'408',30,'Ar-Rūm','الرُّوم',3,4,34,'أَمۡ أَنزَلۡنَا عَلَيۡهِمۡ سُلۡطَٰنٗا فَهۡوَ يَتَكَلَّمُ بِمَا كَانُواْ بِهِۦ يُشۡرِكُونَ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3441,21,'408',30,'Ar-Rūm','الرُّوم',4,6,35,'وَإِذَآ أَذَقۡنَا اَ۬لنَّاسَ رَحۡمَةٗ فَرِحُواْ بِهَاۖ وَإِن تُصِبۡهُمۡ سَيِّئَةُۢ بِمَا قَدَّمَتۡ أَيۡدِيهِمۡ إِذَا هُمۡ يَقۡنِطُونَ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3442,21,'408',30,'Ar-Rūm','الرُّوم',6,7,36,'أَوَلَمۡ يَرَوۡاْ أَنَّ اَ۬للَّهَ يَبۡسُطُ اُ۬لرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يُؤۡمِنُونَ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3443,21,'408',30,'Ar-Rūm','الرُّوم',7,9,37,'فَـَٔاتِ ذَا اَ۬لۡقُرۡبۭيٰ حَقَّهُۥ وَاَلۡمِسۡكِينَ وَاَبۡنَ اَ۬لسَّبِيلِۚ ذَٰلِكَ خَيۡرٞ لِّلَّذِينَ يُرِيدُونَ وَجۡهَ اَ۬للَّهِۖ وَأُوْلَٰٓئِكَ هُمُ اُ۬لۡمُفۡلِحُونَ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3444,21,'408',30,'Ar-Rūm','الرُّوم',9,11,38,'وَمَآ ءَاتَيۡتُم مِّن رِّبٗا لِّيَرۡبُوَاْ فِيٓ أَمۡوَٰلِ اِ۬لنّ۪اسِ فَلَا يَرۡبُواْ عِندَ اَ۬للَّهِۖ وَمَآ ءَاتَيۡتُم مِّن زَكَوٰةٖ تُرِيدُونَ وَجۡهَ اَ۬للَّهِ فَأُوْلَٰٓئِكَ هُمُ اُ۬لۡمُضۡعِفُونَ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3445,21,'408',30,'Ar-Rūm','الرُّوم',12,14,39,'اَ۬للَّهُ اُ۬لَّذِي خَلَقَكُمۡ ثُمَّ رَزَقَكُمۡ ثُمَّ يُمِيتُكُمۡ ثُمَّ يُحۡيِيكُمۡۖ هَلۡ مِن شُرَكَآئِكُم مَّن يَفۡعَلُ مِن ذَٰلِكُم مِّن شَيۡءٖۚ سُبۡحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يُشۡرِكُونَ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3446,21,'408',30,'Ar-Rūm','الرُّوم',14,15,40,'ظَهَرَ اَ۬لۡفَسَادُ فِي اِ۬لۡبَرِّ وَاَلۡبَحۡرِ بِمَا كَسَبَتۡ أَيۡدِي اِ۬لنّ۪اسِ لِيُذِيقَهُم بَعۡضَ اَ۬لَّذِي عَمِلُواْ لَعَلَّهُمۡ يَرۡجِعُونَ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3447,21,'409',30,'Ar-Rūm','الرُّوم',1,2,41,'قُلۡ سِيرُواْ فِي اِ۬لۡأَرۡضِ فَاَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ اُ۬لَّذِينَ مِن قَبۡلُۚ كَانَ أَكۡثَرُهُم مُّشۡرِكِينَ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3448,21,'409',30,'Ar-Rūm','الرُّوم',2,3,42,'فَأَقِمۡ وَجۡهَكَ لِلدِّينِ اِ۬لۡقَيِّمِ مِن قَبۡلِ أَن يَأۡتِيَ يَوۡمٞ لَّا مَرَدَّ لَهُۥ مِنَ اَ۬للَّهِۖ يَوۡمَئِذٖ يَصَّدَّعُونَ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3449,21,'409',30,'Ar-Rūm','الرُّوم',3,4,43,'مَن كَفَرَ فَعَلَيۡهِ كُفۡرُهُۥۖ وَمَنۡ عَمِلَ صَٰلِحٗا فَلِأَنفُسِهِمۡ يَمۡهَدُونَ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3450,21,'409',30,'Ar-Rūm','الرُّوم',5,6,44,'لِيَجۡزِيَ اَ۬لَّذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ مِن فَضۡلِهِۦٓۚ إِنَّهُۥ لَا يُحِبُّ اُ۬لۡكٰ۪فِرِينَ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3451,21,'409',30,'Ar-Rūm','الرُّوم',6,8,45,'۞ وَمِنۡ ءَايَٰتِهِۦٓ أَن يُرۡسِلَ اَ۬لرِّيَاحَ مُبَشِّرَٰتٖ وَلِيُذِيقَكُم مِّن رَّحۡمَتِهِۦ وَلِتَجۡرِيَ اَ۬لۡفُلۡكُ بِأَمۡرِهِۦ وَلِتَبۡتَغُواْ مِن فَضۡلِهِۦ وَلَعَلَّكُمۡ تَشۡكُرُونَ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3452,21,'409',30,'Ar-Rūm','الرُّوم',8,10,46,'وَلَقَدۡ أَرۡسَلۡنَا مِن قَبۡلِكَ رُسُلًا إِلَىٰ قَوۡمِهِمۡ فَجَآءُوهُم بِالۡبَيِّنَٰتِ فَاَنتَقَمۡنَا مِنَ اَ۬لَّذِينَ أَجۡرَمُواْۖ وَكَانَ حَقًّا عَلَيۡنَا نَصۡرُ اُ۬لۡمُؤۡمِنِينَ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3453,21,'409',30,'Ar-Rūm','الرُّوم',10,12,47,'اَ۬للَّهُ اُ۬لَّذِي يُرۡسِلُ اُ۬لرِّيَٰحَ فَتُثِيرُ سَحَابٗا فَيَبۡسُطُهُۥ فِي اِ۬لسَّمَآءِ كَيۡفَ يَشَآءُ وَيَجۡعَلُهُۥ كِسَفٗا فَتَرَى اَ۬لۡوَدۡقَ يَخۡرُجُ مِنۡ خِلَٰلِهِۦۖ فَإِذَآ أَصَابَ بِهِۦ مَن يَشَآءُ مِنۡ عِبَادِهِۦٓ إِذَا هُمۡ يَسۡتَبۡشِرُونَ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3454,21,'409',30,'Ar-Rūm','الرُّوم',13,13,48,'وَإِن كَانُواْ مِن قَبۡلِ أَن يُنزَلَ عَلَيۡهِم مِّن قَبۡلِهِۦ لَمُبۡلِسِينَ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3455,21,'409',30,'Ar-Rūm','الرُّوم',14,15,49,'فَاَنظُرۡ إِلَىٰٓ أَثَرِ رَحۡمَتِ اِ۬للَّهِ كَيۡفَ يُحۡيِ اِ۬لۡأَرۡضَ بَعۡدَ مَوۡتِهَآۚ إِنَّ ذَٰلِكَ لَمُحۡيِ اِ۬لۡمَوۡتۭيٰۖ وَهۡوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3456,21,'410',30,'Ar-Rūm','الرُّوم',1,1,50,'وَلَئِنۡ أَرۡسَلۡنَا رِيحٗا فَرَأَوۡهُ مُصۡفَرّٗا لَّظَلُّواْ مِنۢ بَعۡدِهِۦ يَكۡفُرُونَ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3457,21,'410',30,'Ar-Rūm','الرُّوم',2,3,51,'فَإِنَّكَ لَا تُسۡمِعُ اُ۬لۡمَوۡتۭيٰ وَلَا تُسۡمِعُ اُ۬لصُّمَّ اَ۬لدُّعَآءَ ا۪ذَا وَلَّوۡاْ مُدۡبِرِينَ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3458,21,'410',30,'Ar-Rūm','الرُّوم',3,4,52,'وَمَآ أَنتَ بِهَٰدِ اِ۬لۡعُمۡيِ عَن ضَلَٰلَتِهِمۡۖ إِن تُسۡمِعُ إِلَّا مَن يُؤۡمِنُ بِـَٔايَٰتِنَا فَهُم مُّسۡلِمُونَ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3459,21,'410',30,'Ar-Rūm','الرُّوم',4,6,53,'۞ اَ۬للَّهُ اُ۬لَّذِي خَلَقَكُم مِّن ضُعۡفٖ ثُمَّ جَعَلَ مِنۢ بَعۡدِ ضُعۡفٖ قُوَّةٗ ثُمَّ جَعَلَ مِنۢ بَعۡدِ قُوَّةٖ ضُعۡفٗا وَشَيۡبَةٗۚ يَخۡلُقُ مَا يَشَآءُۖ وَهۡوَ اَ۬لۡعَلِيمُ اُ۬لۡقَدِيرُ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3460,21,'410',30,'Ar-Rūm','الرُّوم',7,7,54,'وَيَوۡمَ تَقُومُ اُ۬لسَّاعَةُ يُقۡسِمُ اُ۬لۡمُجۡرِمُونَ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3461,21,'410',30,'Ar-Rūm','الرُّوم',7,8,55,'مَا لَبِثُواْ غَيۡرَ سَاعَةٖۚ كَذَٰلِكَ كَانُواْ يُؤۡفَكُونَ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3462,21,'410',30,'Ar-Rūm','الرُّوم',8,10,56,'وَقَالَ اَ۬لَّذِينَ أُوتُواْ اُ۬لۡعِلۡمَ وَاَلۡإِيمَٰنَ لَقَدۡ لَبِثتُّمۡ فِي كِتَٰبِ اِ۬للَّهِ إِلَىٰ يَوۡمِ اِ۬لۡبَعۡثِۖ فَهَٰذَا يَوۡمُ اُ۬لۡبَعۡثِ وَلَٰكِنَّكُمۡ كُنتُمۡ لَا تَعۡلَمُونَ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3463,21,'410',30,'Ar-Rūm','الرُّوم',10,11,57,'فَيَوۡمَئِذٖ لَّا تَنفَعُ اُ۬لَّذِينَ ظَلَمُواْ مَعۡذِرَتُهُمۡ وَلَا هُمۡ يُسۡتَعۡتَبُونَ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3464,21,'410',30,'Ar-Rūm','الرُّوم',12,14,58,'وَلَقَد ضَّرَبۡنَا لِلنّ۪اسِ فِي هَٰذَا اَ۬لۡقُرۡءَانِ مِن كُلِّ مَثَلٖۚ وَلَئِن جِئۡتَهُم بِـَٔايَةٖ لَّيَقُولَنَّ اَ۬لَّذِينَ كَفَرُوٓاْ إِنۡ أَنتُمۡ إِلَّا مُبۡطِلُونَ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3465,21,'410',30,'Ar-Rūm','الرُّوم',14,14,59,'كَذَٰلِكَ يَطۡبَعُ اُ۬للَّهُ عَلَىٰ قُلُوبِ اِ۬لَّذِينَ لَا يَعۡلَمُونَ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3466,21,'410',30,'Ar-Rūm','الرُّوم',15,15,60,'فَاَصۡبِرۡ إِنَّ وَعۡدَ اَ۬للَّهِ حَقّٞۖ وَلَا يَسۡتَخِفَّنَّكَ اَ۬لَّذِينَ لَا يُوقِنُونَ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3467,21,'411',31,'Luqmān','لُقمَان',3,3,1,'الٓمٓۚ تِلۡكَ ءَايَٰتُ اُ۬لۡكِتَٰبِ اِ۬لۡحَكِيمِ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3468,21,'411',31,'Luqmān','لُقمَان',3,4,2,'هُدٗى وَرَحۡمَةٗ لِّلۡمُحۡسِنِينَ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3469,21,'411',31,'Luqmān','لُقمَان',4,5,3,'اَ۬لَّذِينَ يُقِيمُونَ اَ۬لصَّلَوٰةَ وَيُؤۡتُونَ اَ۬لزَّكَوٰةَ وَهُم بِالۡأٓخِرَةِ هُمۡ يُوقِنُونَ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3470,21,'411',31,'Luqmān','لُقمَان',5,6,4,'أُوْلَٰٓئِكَ عَلَىٰ هُدٗى مِّن رَّبِّهِمۡۖ وَأُوْلَٰٓئِكَ هُمُ اُ۬لۡمُفۡلِحُونَ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3471,21,'411',31,'Luqmān','لُقمَان',6,8,5,'وَمِنَ اَ۬لنّ۪اسِ مَن يَشۡتَرِي لَهۡوَ اَ۬لۡحَدِيثِ لِيَضِلَّ عَن سَبِيلِ اِ۬للَّهِ بِغَيۡرِ عِلۡمٖ وَيَتَّخِذُهَا هُزُؤًاۚ أُوْلَٰٓئِكَ لَهُمۡ عَذَابٞ مُّهِينٞ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3472,21,'411',31,'Luqmān','لُقمَان',8,9,6,'وَإِذَا تُتۡلَىٰ عَلَيۡهِ ءَايَٰتُنَا وَلَّىٰ مُسۡتَكۡبِرٗا كَأَن لَّمۡ يَسۡمَعۡهَا كَأَنَّ فِيٓ أُذُنَيۡهِ وَقۡرٗاۖ فَبَشِّرۡهُ بِعَذَابٍ أَلِيمٍ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3473,21,'411',31,'Luqmān','لُقمَان',10,10,7,'إِنَّ اَ۬لَّذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ لَهُمۡ جَنَّٰتُ اُ۬لنَّعِيمِ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3474,21,'411',31,'Luqmān','لُقمَان',11,11,8,'خَٰلِدِينَ فِيهَاۖ وَعۡدَ اَ۬للَّهِ حَقّٗاۚ وَهۡوَ اَ۬لۡعَزِيزُ اُ۬لۡحَكِيمُ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3475,21,'411',31,'Luqmān','لُقمَان',11,14,9,'خَلَقَ اَ۬لسَّمَٰوَٰتِ بِغَيۡرِ عَمَدٖ تَرَوۡنَهَاۖ وَأَلۡقَىٰ فِي اِ۬لۡأَرۡضِ رَوَٰسِيَ أَن تَمِيدَ بِكُمۡ وَبَثَّ فِيهَا مِن كُلِّ دَآبَّةٖۚ وَأَنزَلۡنَا مِنَ اَ۬لسَّمَآءِ مَآءٗ فَأَنۢبَتۡنَا فِيهَا مِن كُلِّ زَوۡجٖ كَرِيمٍ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3476,21,'411',31,'Luqmān','لُقمَان',14,15,10,'هَٰذَا خَلۡقُ اُ۬للَّهِ فَأَرُونِي مَاذَا خَلَقَ اَ۬لَّذِينَ مِن دُونِهِۦۚ بَلِ اِ۬لظَّٰلِمُونَ فِي ضَلَٰلٖ مُّبِينٖ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3477,21,'412',31,'Luqmān','لُقمَان',1,2,11,'۞ وَلَقَدۡ ءَاتَيۡنَا لُقۡمَٰنَ اَ۬لۡحِكۡمَةَ أَنِ اِ۟شۡكُر لِّلَّهِۚ وَمَن يَشۡكُرۡ فَإِنَّمَا يَشۡكُرُ لِنَفۡسِهِۦۖ وَمَن كَفَرَ فَإِنَّ اَ۬للَّهَ غَنِيٌّ حَمِيدٞ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3478,21,'412',31,'Luqmān','لُقمَان',2,4,12,'وَإِذۡ قَالَ لُقۡمَٰنُ لِاِبۡنِهِۦ وَهۡوَ يَعِظُهُۥ يَٰبُنَيِّ لَا تُشۡرِكۡ بِاللَّهِۖ إِنَّ اَ۬لشِّرۡكَ لَظُلۡمٌ عَظِيمٞ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3479,21,'412',31,'Luqmān','لُقمَان',4,6,13,'وَوَصَّيۡنَا اَ۬لۡإِنسَٰنَ بِوَٰلِدَيۡهِ حَمَلَتۡهُ أُمُّهُۥ وَهۡنًا عَلَىٰ وَهۡنٖ وَفِصَٰلُهُۥ فِي عَامَيۡنِ أَنِ اِ۟شۡكُر لِّي وَلِوَٰلِدَيۡكَ إِلَيَّ اَ۬لۡمَصِيرُ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3480,21,'412',31,'Luqmān','لُقمَان',6,9,14,'وَإِن جَٰهَدَاكَ عَلَىٰٓ أَن تُشۡرِكَ بِي مَا لَيۡسَ لَكَ بِهِۦ عِلۡمٞ فَلَا تُطِعۡهُمَاۖ وَصَاحِبۡهُمَا فِي اِ۬لدُّنۡيۭا مَعۡرُوفٗاۖ وَاَتَّبِعۡ سَبِيلَ مَنۡ أَنَابَ إِلَيَّۚ ثُمَّ إِلَيَّ مَرۡجِعُكُمۡ فَأُنَبِّئُكُم بِمَا كُنتُمۡ تَعۡمَلُونَ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3481,21,'412',31,'Luqmān','لُقمَان',9,11,15,'يَٰبُنَيِّ إِنَّهَآ إِن تَكُ مِثۡقَالَ حَبَّةٖ مِّنۡ خَرۡدَلٖ فَتَكُن فِي صَخۡرَةٍ أَوۡ فِي اِ۬لسَّمَٰوَٰتِ أَوۡ فِي اِ۬لۡأَرۡضِ يَأۡتِ بِهَا اَ۬للَّهُۚ إِنَّ اَ۬للَّهَ لَطِيفٌ خَبِيرٞ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3482,21,'412',31,'Luqmān','لُقمَان',11,13,16,'يَٰبُنَيِّ أَقِمِ اِ۬لصَّلَوٰةَ وَأۡمُرۡ بِالۡمَعۡرُوفِ وَاَنۡهَ عَنِ اِ۬لۡمُنكَرِ وَاَصۡبِرۡ عَلَىٰ مَآ أَصَابَكَۖ إِنَّ ذَٰلِكَ مِنۡ عَزۡمِ اِ۬لۡأُمُورِ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3483,21,'412',31,'Luqmān','لُقمَان',13,14,17,'وَلَا تُصَٰعِرۡ خَدَّكَ لِلنّ۪اسِ وَلَا تَمۡشِ فِي اِ۬لۡأَرۡضِ مَرَحًاۖ إِنَّ اَ۬للَّهَ لَا يُحِبُّ كُلَّ مُخۡتَالٖ فَخُورٖ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3484,21,'412',31,'Luqmān','لُقمَان',14,15,18,'وَاَقۡصِدۡ فِي مَشۡيِكَ وَاَغۡضُضۡ مِن صَوۡتِكَۚ إِنَّ أَنكَرَ اَ۬لۡأَصۡوَٰتِ لَصَوۡتُ اُ۬لۡحَمِيرِ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3485,21,'413',31,'Luqmān','لُقمَان',1,3,19,'أَلَمۡ تَرَوۡاْ أَنَّ اَ۬للَّهَ سَخَّرَ لَكُم مَّا فِي اِ۬لسَّمَٰوَٰتِ وَمَا فِي اِ۬لۡأَرۡضِ وَأَسۡبَغَ عَلَيۡكُمۡ نِعَمَهُۥ ظَٰهِرَةٗ وَبَاطِنَةٗۗ وَمِنَ اَ۬لنّ۪اسِ مَن يُجَٰدِلُ فِي اِ۬للَّهِ بِغَيۡرِ عِلۡمٖ وَلَا هُدٗى وَلَا كِتَٰبٖ مُّنِيرٖ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3486,21,'413',31,'Luqmān','لُقمَان',3,5,20,'وَإِذَا قِيلَ لَهُمُ اُ۪تَّبِعُواْ مَآ أَنزَلَ اَ۬للَّهُ قَالُواْ بَلۡ نَتَّبِعُ مَا وَجَدۡنَا عَلَيۡهِ ءَابَآءَنَآۚ أَوَلَوۡ كَانَ اَ۬لشَّيۡطَٰنُ يَدۡعُوهُمۡ إِلَىٰ عَذَابِ اِ۬لسَّعِيرِ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3487,21,'413',31,'Luqmān','لُقمَان',5,7,21,'۞ وَمَن يُسۡلِمۡ وَجۡهَهُۥٓ إِلَى اَ۬للَّهِ وَهۡوَ مُحۡسِنٞ فَقَدِ اِ۪سۡتَمۡسَكَ بِالۡعُرۡوَةِ اِ۬لۡوُثۡقۭيٰۗ وَإِلَى اَ۬للَّهِ عَٰقِبَةُ اُ۬لۡأُمُورِ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3488,21,'413',31,'Luqmān','لُقمَان',7,8,22,'وَمَن كَفَرَ فَلَا يَحۡزُنكَ كُفۡرُهُۥٓۚ إِلَيۡنَا مَرۡجِعُهُمۡ فَنُنَبِّئُهُم بِمَا عَمِلُوٓاْۚ إِنَّ اَ۬للَّهَ عَلِيمُۢ بِذَاتِ اِ۬لصُّدُورِ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3489,21,'413',31,'Luqmān','لُقمَان',9,9,23,'نُمَتِّعُهُمۡ قَلِيلٗا ثُمَّ نَضۡطَرُّهُمۡ إِلَىٰ عَذَابٍ غَلِيظٖ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3490,21,'413',31,'Luqmān','لُقمَان',10,11,24,'وَلَئِن سَأَلۡتَهُم مَّنۡ خَلَقَ اَ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضَ لَيَقُولُنَّ اَ۬للَّهُۚ قُلِ اِ۬لۡحَمۡدُ لِلَّهِۚ بَلۡ أَكۡثَرُهُمۡ لَا يَعۡلَمُونَ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3491,21,'413',31,'Luqmān','لُقمَان',11,12,25,'لِلَّهِ مَا فِي اِ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِۚ إِنَّ اَ۬للَّهَ هُوَ اَ۬لۡغَنِيُّ اُ۬لۡحَمِيدُ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3492,21,'413',31,'Luqmān','لُقمَان',12,14,26,'وَلَوۡ أَنَّمَا فِي اِ۬لۡأَرۡضِ مِن شَجَرَةٍ أَقۡلَٰمٞ وَاَلۡبَحۡرَ يَمُدُّهُۥ مِنۢ بَعۡدِهِۦ سَبۡعَةُ أَبۡحُرٖ مَّا نَفِدَتۡ كَلِمَٰتُ اُ۬للَّهِۗ إِنَّ اَ۬للَّهَ عَزِيزٌ حَكِيمٞ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3493,21,'413',31,'Luqmān','لُقمَان',14,15,27,'مَّا خَلۡقُكُمۡ وَلَا بَعۡثُكُمۡ إِلَّا كَنَفۡسٖ وَٰحِدَةٍۗ إِنَّ اَ۬للَّهَ سَمِيعُۢ بَصِيرٌ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3494,21,'414',31,'Luqmān','لُقمَان',1,3,28,'أَلَمۡ تَرَ أَنَّ اَ۬للَّهَ يُولِجُ اُ۬لَّيۡلَ فِي اِ۬لنَّه۪ارِ وَيُولِجُ اُ۬لنَّهَارَ فِي اِ۬لَّيۡلِ وَسَخَّرَ اَ۬لشَّمۡسَ وَاَلۡقَمَرَۖ كُلّٞ يَجۡرِيٓ إِلَىٰٓ أَجَلٖ مُّسَمّٗى وَأَنَّ اَ۬للَّهَ بِمَا تَعۡمَلُونَ خَبِيرٞ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3495,21,'414',31,'Luqmān','لُقمَان',4,5,29,'ذَٰلِكَ بِأَنَّ اَ۬للَّهَ هُوَ اَ۬لۡحَقُّ وَأَنَّ مَا يَدۡعُونَ مِن دُونِهِ اِ۬لۡبَٰطِلُ وَأَنَّ اَ۬للَّهَ هُوَ اَ۬لۡعَلِيُّ اُ۬لۡكَبِيرُ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3496,21,'414',31,'Luqmān','لُقمَان',5,7,30,'أَلَمۡ تَرَ أَنَّ اَ۬لۡفُلۡكَ تَجۡرِي فِي اِ۬لۡبَحۡرِ بِنِعۡمَتِ اِ۬للَّهِ لِيُرِيَكُم مِّنۡ ءَايَٰتِهِۦٓۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّكُلِّ صَبّ۪ارٖ شَكُورٖ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3497,21,'414',31,'Luqmān','لُقمَان',7,9,31,'وَإِذَا غَشِيَهُم مَّوۡجٞ كَاَلظُّلَلِ دَعَوُاْ اُ۬للَّهَ مُخۡلِصِينَ لَهُ اُ۬لدِّينَ فَلَمَّا نَجَّىٰهُمۡ إِلَى اَ۬لۡبَرِّ فَمِنۡهُم مُّقۡتَصِدٞۚ وَمَا يَجۡحَدُ بِـَٔايَٰتِنَآ إِلَّا كُلُّ خَتّ۪ارٖ كَفُورٖ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3498,21,'414',31,'Luqmān','لُقمَان',10,13,32,'۞ يَٰٓأَيُّهَا اَ۬لنَّاسُ اُ۪تَّقُواْ رَبَّكُمۡ وَاَخۡشَوۡاْ يَوۡمٗا لَّا يَجۡزِي وَالِدٌ عَن وَلَدِهِۦ وَلَا مَوۡلُودٌ هُوَ جَازٍ عَن وَالِدِهِۦ شَيۡـًٔاۚ إِنَّ وَعۡدَ اَ۬للَّهِ حَقّٞۖ فَلَا تَغُرَّنَّكُمُ اُ۬لۡحَيَوٰةُ اُ۬لدُّنۡيۭا وَلَا يَغُرَّنَّكُم بِاللَّهِ اِ۬لۡغَرُورُ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3499,21,'414',31,'Luqmān','لُقمَان',13,15,33,'إِنَّ اَ۬للَّهَ عِندَهُۥ عِلۡمُ اُ۬لسَّاعَةِ وَيُنزِلُ اُ۬لۡغَيۡثَ وَيَعۡلَمُ مَا فِي اِ۬لۡأَرۡحَامِۖ وَمَا تَدۡرِي نَفۡسٞ مَّاذَا تَكۡسِبُ غَدٗاۖ وَمَا تَدۡرِي نَفۡسُۢ بِأَيِّ أَرۡضٖ تَمُوتُۚ إِنَّ اَ۬للَّهَ عَلِيمٌ خَبِيرُۢ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3500,21,'415',32,'As-Sajdah','السَّجدة',3,3,1,'الٓمٓۚ تَنزِيلُ اُ۬لۡكِتَٰبِ لَا رَيۡبَ فِيهِ مِن رَّبِّ اِ۬لۡعَٰلَمِينَ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3501,21,'415',32,'As-Sajdah','السَّجدة',4,5,2,'أَمۡ يَقُولُونَ اَ۪فۡتَر۪ىٰهُۚ بَلۡ هُوَ اَ۬لۡحَقُّ مِن رَّبِّكَ لِتُنذِرَ قَوۡمٗا مَّآ أَتَىٰهُم مِّن نَّذِيرٖ مِّن قَبۡلِكَ لَعَلَّهُمۡ يَهۡتَدُونَ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3502,21,'415',32,'As-Sajdah','السَّجدة',5,7,3,'اَ۬للَّهُ اُ۬لَّذِي خَلَقَ اَ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضَ وَمَا بَيۡنَهُمَا فِي سِتَّةِ أَيَّامٖ ثُمَّ اَ۪سۡتَوَىٰ عَلَى اَ۬لۡعَرۡشِۖ مَا لَكُم مِّن دُونِهِۦ مِن وَلِيّٖ وَلَا شَفِيعٍۚ أَفَلَا تَتَذَكَّرُونَ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3503,21,'415',32,'As-Sajdah','السَّجدة',8,9,4,'يُدَبِّرُ اُ۬لۡأَمۡرَ مِنَ اَ۬لسَّمَآ إِلَى اَ۬لۡأَرۡضِ ثُمَّ يَعۡرُجُ إِلَيۡهِ فِي يَوۡمٖ كَانَ مِقۡدَارُهُۥٓ أَلۡفَ سَنَةٖ مِّمَّا تَعُدُّونَ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3504,21,'415',32,'As-Sajdah','السَّجدة',9,10,5,'ذَٰلِكَ عَٰلِمُ اُ۬لۡغَيۡبِ وَاَلشَّهَٰدَةِ اِ۬لۡعَزِيزُ اُ۬لرَّحِيمُ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3505,21,'415',32,'As-Sajdah','السَّجدة',10,11,6,'اُ۬لَّذِيٓ أَحۡسَنَ كُلَّ شَيۡءٍ خَلۡقَهُۥۖ وَبَدَأَ خَلۡقَ اَ۬لۡإِنسَٰنِ مِن طِينٖ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3506,21,'415',32,'As-Sajdah','السَّجدة',11,11,7,'ثُمَّ جَعَلَ نَسۡلَهُۥ مِن سُلَٰلَةٖ مِّن مَّآءٖ مَّهِينٖ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3507,21,'415',32,'As-Sajdah','السَّجدة',12,13,8,'ثُمَّ سَوَّىٰهُ وَنَفَخَ فِيهِ مِن رُّوحِهِۦۖ وَجَعَلَ لَكُمُ اُ۬لسَّمۡعَ وَاَلۡأَبۡصَٰرَ وَاَلۡأَفۡـِٔدَةَۚ قَلِيلٗا مَّا تَشۡكُرُونَ ٨');
