INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4082,24,'462',39,'Az-Zumar','الزُّمَر',1,2,31,'۞ فَمَنۡ أَظۡلَمُ مِمَّن كَذَبَ عَلَى اَ۬للَّهِ وَكَذَّبَ بِالصِّدۡقِ إِذ جَّآءَهُۥٓۚ أَلَيۡسَ فِي جَهَنَّمَ مَثۡوٗى لِّلۡكٰ۪فِرِينَ ٣١');
