INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4093,24,'463',39,'Az-Zumar','الزُّمَر',9,11,42,'۞ وَإِذَا ذُكِرَ اَ۬للَّهُ وَحۡدَهُ اُ۪شۡمَأَزَّتۡ قُلُوبُ اُ۬لَّذِينَ لَا يُؤۡمِنُونَ بِالۡأٓخِرَةِۖ وَإِذَا ذُكِرَ اَ۬لَّذِينَ مِن دُونِهِۦٓ إِذَا هُمۡ يَسۡتَبۡشِرُونَ ٤٢');
