INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4128,24,'467',40,'Ghāfir','غَافِر',11,12,5,'وَكَذَٰلِكَ حَقَّتۡ كَلِمَتُ رَبِّكَ عَلَى اَ۬لَّذِينَ كَفَرُوٓاْ أَنَّهُمۡ أَصۡحَٰبُ اُ۬لنّ۪ارِ ٥');
