INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4142,24,'469',40,'Ghāfir','غَافِر',4,4,19,'يَعۡلَمُ خَآئِنَةَ اَ۬لۡأَعۡيُنِ وَمَا تُخۡفِي اِ۬لصُّدُورُ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4143,24,'469',40,'Ghāfir','غَافِر',4,6,20,'وَاَللَّهُ يَقۡضِي بِالۡحَقِّۖ وَاَلَّذِينَ يَدۡعُونَ مِن دُونِهِۦ لَا يَقۡضُونَ بِشَيۡءٍۗ إِنَّ اَ۬للَّهَ هُوَ اَ۬لسَّمِيعُ اُ۬لۡبَصِيرُ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4144,24,'469',40,'Ghāfir','غَافِر',6,9,21,'۞ أَوَلَمۡ يَسِيرُواْ فِي اِ۬لۡأَرۡضِ فَيَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ اُ۬لَّذِينَ كَانُواْ مِن قَبۡلِهِمۡۚ كَانُواْ هُمۡ أَشَدَّ مِنۡهُمۡ قُوَّةٗ وَءَاثَارٗا فِي اِ۬لۡأَرۡضِ فَأَخَذَهُمُ اُ۬للَّهُ بِذُنُوبِهِمۡ وَمَا كَانَ لَهُم مِّنَ اَ۬للَّهِ مِن وَاقٖ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4145,24,'469',40,'Ghāfir','غَافِر',9,11,22,'ذَٰلِكَ بِأَنَّهُمۡ كَانَت تَّأۡتِيهِمۡ رُسۡلُهُم بِالۡبَيِّنَٰتِ فَكَفَرُواْ فَأَخَذَهُمُ اُ۬للَّهُۚ إِنَّهُۥ قَوِيّٞ شَدِيدُ اُ۬لۡعِقَابِ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4146,24,'469',40,'Ghāfir','غَافِر',11,12,23,'وَلَقَدۡ أَرۡسَلۡنَا مُوسۭيٰ بِـَٔايَٰتِنَا وَسُلۡطَٰنٖ مُّبِينٍ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4147,24,'469',40,'Ghāfir','غَافِر',12,13,24,'إِلَىٰ فِرۡعَوۡنَ وَهَٰمَٰنَ وَقَٰرُونَ فَقَالُواْ سَٰحِرٞ كَذَّابٞ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4148,24,'469',40,'Ghāfir','غَافِر',13,15,25,'فَلَمَّا جَآءَهُم بِالۡحَقِّ مِنۡ عِندِنَا قَالُواْ اُ۟قۡتُلُوٓاْ أَبۡنَآءَ اَ۬لَّذِينَ ءَامَنُواْ مَعَهُۥ وَاَسۡتَحۡيُواْ نِسَآءَهُمۡۚ وَمَا كَيۡدُ اُ۬لۡكٰ۪فِرِينَ إِلَّا فِي ضَلَٰلٖ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4149,24,'470',40,'Ghāfir','غَافِر',1,2,26,'وَقَالَ فِرۡعَوۡنُ ذَرُونِيٓ أَقۡتُلۡ مُوسۭيٰ وَلۡيَدۡعُ رَبَّهُۥٓۖ إِنِّيَ أَخَافُ أَن يُبَدِّلَ دِينَكُمۡ وَأَن يُظۡهِرَ فِي اِ۬لۡأَرۡضِ اِ۬لۡفَسَادَ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4150,24,'470',40,'Ghāfir','غَافِر',3,4,27,'وَقَالَ مُوسۭيٰٓ إِنِّي عُذتُّ بِرَبِّي وَرَبِّكُم مِّن كُلِّ مُتَكَبِّرٖ لَّا يُؤۡمِنُ بِيَوۡمِ اِ۬لۡحِسَابِ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4151,24,'470',40,'Ghāfir','غَافِر',4,8,28,'وَقَالَ رَجُلٞ مُّؤۡمِنٞ مِّنۡ ءَالِ فِرۡعَوۡنَ يَكۡتُمُ إِيمَٰنَهُۥٓ أَتَقۡتُلُونَ رَجُلًا أَن يَقُولَ رَبِّيَ اَ۬للَّهُ وَقَد جَّآءَكُم بِالۡبَيِّنَٰتِ مِن رَّبِّكُمۡۖ وَإِن يَكُ كَٰذِبٗا فَعَلَيۡهِ كَذِبُهُۥۖ وَإِن يَكُ صَادِقٗا يُصِبۡكُم بَعۡضُ اُ۬لَّذِي يَعِدُكُمۡۖ إِنَّ اَ۬للَّهَ لَا يَهۡدِي مَنۡ هُوَ مُسۡرِفٞ كَذَّابٞ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4152,24,'470',40,'Ghāfir','غَافِر',8,11,29,'يَٰقَوۡمِ لَكُمُ اُ۬لۡمُلۡكُ اُ۬لۡيَوۡمَ ظَٰهِرِينَ فِي اِ۬لۡأَرۡضِ فَمَن يَنصُرُنَا مِنۢ بَأۡسِ اِ۬للَّهِ إِن جَآءَنَاۚ قَالَ فِرۡعَوۡنُ مَآ أُرِيكُمۡ إِلَّا مَآ أَر۪يٰ وَمَآ أَهۡدِيكُمۡ إِلَّا سَبِيلَ اَ۬لرَّشَادِ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4153,24,'470',40,'Ghāfir','غَافِر',11,12,30,'۞ وَقَالَ اَ۬لَّذِيٓ ءَامَنَ يَٰقَوۡمِ إِنِّيَ أَخَافُ عَلَيۡكُم مِّثۡلَ يَوۡمِ اِ۬لۡأَحۡزَابِ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4154,24,'470',40,'Ghāfir','غَافِر',12,13,31,'مِثۡلَ دَأۡبِ قَوۡمِ نُوحٖ وَعَادٖ وَثَمُودَ وَاَلَّذِينَ مِنۢ بَعۡدِهِمۡۚ وَمَا اَ۬للَّهُ يُرِيدُ ظُلۡمٗا لِّلۡعِبَادِ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4155,24,'470',40,'Ghāfir','غَافِر',14,14,32,'وَيَٰقَوۡمِ إِنِّيَ أَخَافُ عَلَيۡكُمۡ يَوۡمَ اَ۬لتَّنَادِ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4156,24,'470',40,'Ghāfir','غَافِر',14,15,33,'يَوۡمَ تُوَلُّونَ مُدۡبِرِينَ مَا لَكُم مِّنَ اَ۬للَّهِ مِنۡ عَاصِمٖۗ وَمَن يُضۡلِلِ اِ۬للَّهُ فَمَا لَهُۥ مِنۡ هَادٖ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4157,24,'471',40,'Ghāfir','غَافِر',1,4,34,'وَلَقَد جَّآءَكُمۡ يُوسُفُ مِن قَبۡلُ بِالۡبَيِّنَٰتِ فَمَا زِلۡتُمۡ فِي شَكّٖ مِّمَّا جَآءَكُم بِهِۦۖ حَتَّىٰٓ إِذَا هَلَكَ قُلۡتُمۡ لَن يَبۡعَثَ اَ۬للَّهُ مِنۢ بَعۡدِهِۦ رَسُولٗاۚ كَذَٰلِكَ يُضِلُّ اُ۬للَّهُ مَنۡ هُوَ مُسۡرِفٞ مُّرۡتَابٌ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4158,24,'471',40,'Ghāfir','غَافِر',4,6,35,'اِ۬لَّذِينَ يُجَٰدِلُونَ فِيٓ ءَايَٰتِ اِ۬للَّهِ بِغَيۡرِ سُلۡطَٰنٍ أَتَىٰهُمۡۖ كَبُرَ مَقۡتًا عِندَ اَ۬للَّهِ وَعِندَ اَ۬لَّذِينَ ءَامَنُواْۚ كَذَٰلِكَ يَطۡبَعُ اُ۬للَّهُ عَلَىٰ كُلِّ قَلۡبٖ مُّتَكَبِّرٖ جَبّ۪ارٖ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4159,24,'471',40,'Ghāfir','غَافِر',6,7,36,'وَقَالَ فِرۡعَوۡنُ يَٰهَٰمَٰنُ اُ۪بۡنِ لِي صَرۡحٗا لَّعَلِّيَ أَبۡلُغُ اُ۬لۡأَسۡبَٰبَ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4160,24,'471',40,'Ghāfir','غَافِر',7,10,37,'أَسۡبَٰبَ اَ۬لسَّمَٰوَٰتِ فَأَطَّلِعُ إِلَىٰٓ إِلَٰهِ مُوسۭيٰ وَإِنِّي لَأَظُنُّهُۥ كَٰذِبٗاۚ وَكَذَٰلِكَ زُيِّنَ لِفِرۡعَوۡنَ سُوٓءُ عَمَلِهِۦ وَصَدَّ عَنِ اِ۬لسَّبِيلِۚ وَمَا كَيۡدُ فِرۡعَوۡنَ إِلَّا فِي تَبَابٖ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4161,24,'471',40,'Ghāfir','غَافِر',10,11,38,'وَقَالَ اَ۬لَّذِيٓ ءَامَنَ يَٰقَوۡمِ اِ۪تَّبِعُونِۦٓ أَهۡدِكُمۡ سَبِيلَ اَ۬لرَّشَادِ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4162,24,'471',40,'Ghāfir','غَافِر',11,13,39,'يَٰقَوۡمِ إِنَّمَا هَٰذِهِ اِ۬لۡحَيَوٰةُ اُ۬لدُّنۡيۭا مَتَٰعٞ وَإِنَّ اَ۬لۡأٓخِرَةَ هِيَ دَارُ اُ۬لۡقَر۪ارِ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4163,24,'471',40,'Ghāfir','غَافِر',13,15,40,'مَنۡ عَمِلَ سَيِّئَةٗ فَلَا يُجۡزَىٰٓ إِلَّا مِثۡلَهَاۖ وَمَنۡ عَمِلَ صَٰلِحٗا مِّن ذَكَرٍ أَوۡ أُنثۭيٰ وَهۡوَ مُؤۡمِنٞ فَأُوْلَٰٓئِكَ يُدۡخَلُونَ اَ۬لۡجَنَّةَ يُرۡزَقُونَ فِيهَا بِغَيۡرِ حِسَابٖ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4164,24,'472',40,'Ghāfir','غَافِر',1,1,41,'۞ وَيَٰقَوۡمِ مَا لِيَ أَدۡعُوكُمۡ إِلَى اَ۬لنَّجَوٰةِ وَتَدۡعُونَنِيٓ إِلَى اَ۬لنّ۪ارِ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4165,24,'472',40,'Ghāfir','غَافِر',2,3,42,'تَدۡعُونَنِي لِأَكۡفُرَ بِاللَّهِ وَأُشۡرِكَ بِهِۦ مَا لَيۡسَ لِي بِهِۦ عِلۡمٞ وَأَنَا۠ أَدۡعُوكُمۡ إِلَى اَ۬لۡعَزِيزِ اِ۬لۡغَفّٰ۪رِ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4166,24,'472',40,'Ghāfir','غَافِر',3,5,43,'لَا جَرَمَ أَنَّمَا تَدۡعُونَنِيٓ إِلَيۡهِ لَيۡسَ لَهُۥ دَعۡوَةٞ فِي اِ۬لدُّنۡيۭا وَلَا فِي اِ۬لۡأٓخِرَةِ وَأَنَّ مَرَدَّنَآ إِلَى اَ۬للَّهِ وَأَنَّ اَ۬لۡمُسۡرِفِينَ هُمۡ أَصۡحَٰبُ اُ۬لنّ۪ارِ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4167,24,'472',40,'Ghāfir','غَافِر',6,7,44,'فَسَتَذۡكُرُونَ مَآ أَقُولُ لَكُمۡۚ وَأُفَوِّضُ أَمۡرِيَ إِلَى اَ۬للَّهِۚ إِنَّ اَ۬للَّهَ بَصِيرُۢ بِالۡعِبَادِ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4168,24,'472',40,'Ghāfir','غَافِر',7,8,45,'فَوَقَىٰهُ اُ۬للَّهُ سَيِّـَٔاتِ مَا مَكَرُواْۖ وَحَاقَ بِـَٔالِ فِرۡعَوۡنَ سُوٓءُ اُ۬لۡعَذَابِ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4169,24,'472',40,'Ghāfir','غَافِر',8,10,46,'اِ۬لنَّارُ يُعۡرَضُونَ عَلَيۡهَا غُدُوّٗا وَعَشِيّٗاۖ وَيَوۡمَ تَقُومُ اُ۬لسَّاعَةُ اُ۟دۡخُلُوٓاْ ءَالَ فِرۡعَوۡنَ أَشَدَّ اَ۬لۡعَذَابِ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4170,24,'472',40,'Ghāfir','غَافِر',10,12,47,'وَإِذۡ يَتَحَآجُّونَ فِي اِ۬لنّ۪ارِ فَيَقُولُ اُ۬لضُّعَفَٰٓؤُاْ لِلَّذِينَ اَ۪سۡتَكۡبَرُوٓاْ إِنَّا كُنَّا لَكُمۡ تَبَعٗا فَهَلۡ أَنتُم مُّغۡنُونَ عَنَّا نَصِيبٗا مِّنَ اَ۬لنّ۪ارِ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4171,24,'472',40,'Ghāfir','غَافِر',12,14,48,'قَالَ اَ۬لَّذِينَ اَ۪سۡتَكۡبَرُوٓاْ إِنَّا كُلّٞ فِيهَآ إِنَّ اَ۬للَّهَ قَدۡ حَكَمَ بَيۡنَ اَ۬لۡعِبَادِ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4172,24,'472',40,'Ghāfir','غَافِر',14,15,49,'وَقَالَ اَ۬لَّذِينَ فِي اِ۬لنّ۪ارِ لِخَزَنَةِ جَهَنَّمَ اَ۟دۡعُواْ رَبَّكُمۡ يُخَفِّفۡ عَنَّا يَوۡمٗا مِّنَ اَ۬لۡعَذَابِ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4173,24,'473',40,'Ghāfir','غَافِر',1,2,50,'قَالُوٓاْ أَوَلَمۡ تَكُ تَأۡتِيكُمۡ رُسۡلُكُم بِالۡبَيِّنَٰتِۖ قَالُواْ بَلَىٰۚ قَالُواْ فَاَدۡعُواْۗ وَمَا دُعَٰٓؤُاْ اُ۬لۡكٰ۪فِرِينَ إِلَّا فِي ضَلَٰلٍ ٥٠');
