INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4210,24,'477',41,'Fuṣṣilat','فُصِّلَت',4,5,3,'بَشِيرٗا وَنَذِيرٗا فَأَعۡرَضَ أَكۡثَرُهُمۡ فَهُمۡ لَا يَسۡمَعُونَ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4211,24,'477',41,'Fuṣṣilat','فُصِّلَت',5,6,4,'وَقَالُواْ قُلُوبُنَا فِيٓ أَكِنَّةٖ مِّمَّا تَدۡعُونَآ إِلَيۡهِ وَفِيٓ ءَاذَانِنَا وَقۡرٞ وَمِنۢ بَيۡنِنَا وَبَيۡنِكَ حِجَابٞ فَاَعۡمَلۡ إِنَّنَا عَٰمِلُونَ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4212,24,'477',41,'Fuṣṣilat','فُصِّلَت',7,8,5,'قُلۡ إِنَّمَآ أَنَا۠ بَشَرٞ مِّثۡلُكُمۡ يُوحَىٰٓ إِلَيَّ أَنَّمَآ إِلَٰهُكُمۡ إِلَٰهٞ وَٰحِدٞ فَاَسۡتَقِيمُوٓاْ إِلَيۡهِ وَاَسۡتَغۡفِرُوهُۗ وَوَيۡلٞ لِّلۡمُشۡرِكِينَ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4213,24,'477',41,'Fuṣṣilat','فُصِّلَت',8,9,6,'اَ۬لَّذِينَ لَا يُؤۡتُونَ اَ۬لزَّكَوٰةَ وَهُم بِالۡأٓخِرَةِ هُمۡ كَٰفِرُونَ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4214,24,'477',41,'Fuṣṣilat','فُصِّلَت',9,10,7,'إِنَّ اَ۬لَّذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ لَهُمۡ أَجۡرٌ غَيۡرُ مَمۡنُونٖ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4215,24,'477',41,'Fuṣṣilat','فُصِّلَت',10,12,8,'۞ قُلۡ أَٰى۪نَّكُمۡ لَتَكۡفُرُونَ بِالَّذِي خَلَقَ اَ۬لۡأَرۡضَ فِي يَوۡمَيۡنِ وَتَجۡعَلُونَ لَهُۥٓ أَندَادٗاۚ ذَٰلِكَ رَبُّ اُ۬لۡعَٰلَمِينَ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4216,24,'477',41,'Fuṣṣilat','فُصِّلَت',12,14,9,'وَجَعَلَ فِيهَا رَوَٰسِيَ مِن فَوۡقِهَا وَبَٰرَكَ فِيهَا وَقَدَّرَ فِيهَآ أَقۡوَٰتَهَا فِيٓ أَرۡبَعَةِ أَيَّامٖ سَوَآءٗ لِّلسَّآئِلِينَ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4217,24,'477',41,'Fuṣṣilat','فُصِّلَت',14,15,10,'ثُمَّ اَ۪سۡتَوَىٰٓ إِلَى اَ۬لسَّمَآءِ وَهۡيَ دُخَانٞ فَقَالَ لَهَا وَلِلۡأَرۡضِ اِ۪ئۡتِيَا طَوۡعًا أَوۡ كَرۡهٗاۖ قَالَتَآ أَتَيۡنَا طَآئِعِينَ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4218,24,'478',41,'Fuṣṣilat','فُصِّلَت',1,3,11,'فَقَضَىٰهُنَّ سَبۡعَ سَمَٰوَاتٖ فِي يَوۡمَيۡنِ وَأَوۡحَىٰ فِي كُلِّ سَمَآءٍ أَمۡرَهَاۚ وَزَيَّنَّا اَ۬لسَّمَآءَ اَ۬لدُّنۡيۭا بِمَصَٰبِيحَ وَحِفۡظٗاۚ ذَٰلِكَ تَقۡدِيرُ اُ۬لۡعَزِيزِ اِ۬لۡعَلِيمِ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4219,24,'478',41,'Fuṣṣilat','فُصِّلَت',3,4,12,'فَإِنۡ أَعۡرَضُواْ فَقُلۡ أَنذَرۡتُكُمۡ صَٰعِقَةٗ مِّثۡلَ صَٰعِقَةِ عَادٖ وَثَمُودَ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4220,24,'478',41,'Fuṣṣilat','فُصِّلَت',4,6,13,'إِذ جَّآءَتۡهُمُ اُ۬لرُّسُلُ مِنۢ بَيۡنِ أَيۡدِيهِمۡ وَمِنۡ خَلۡفِهِمۡ أَلَّا تَعۡبُدُوٓاْ إِلَّا اَ۬للَّهَۖ قَالُواْ لَوۡ شَآءَ رَبُّنَا لَأَنزَلَ مَلَٰٓئِكَةٗ فَإِنَّا بِمَآ أُرۡسِلۡتُم بِهِۦ كَٰفِرُونَ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4221,24,'478',41,'Fuṣṣilat','فُصِّلَت',6,8,14,'فَأَمَّا عَادٞ فَاَسۡتَكۡبَرُواْ فِي اِ۬لۡأَرۡضِ بِغَيۡرِ اِ۬لۡحَقِّ وَقَالُواْ مَنۡ أَشَدُّ مِنَّا قُوَّةًۖ أَوَلَمۡ يَرَوۡاْ أَنَّ اَ۬للَّهَ اَ۬لَّذِي خَلَقَهُمۡ هُوَ أَشَدُّ مِنۡهُمۡ قُوَّةٗۖ وَكَانُواْ بِـَٔايَٰتِنَا يَجۡحَدُونَ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4222,24,'478',41,'Fuṣṣilat','فُصِّلَت',9,11,15,'فَأَرۡسَلۡنَا عَلَيۡهِمۡ رِيحٗا صَرۡصَرٗا فِيٓ أَيَّامٖ نَّحۡسَاتٖ لِّنُذِيقَهُمۡ عَذَابَ اَ۬لۡخِزۡيِ فِي اِ۬لۡحَيَوٰةِ اِ۬لدُّنۡيۭاۖ وَلَعَذَابُ اُ۬لۡأٓخِرَةِ أَخۡزَىٰۖ وَهُمۡ لَا يُنصَرُونَ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4223,24,'478',41,'Fuṣṣilat','فُصِّلَت',11,12,16,'۞ وَأَمَّا ثَمُودُ فَهَدَيۡنَٰهُمۡ فَاَسۡتَحَبُّواْ اُ۬لۡعَمَىٰ عَلَى اَ۬لۡهُدَىٰ فَأَخَذَتۡهُمۡ صَٰعِقَةُ اُ۬لۡعَذَابِ اِ۬لۡهُونِ بِمَا كَانُواْ يَكۡسِبُونَ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4224,24,'478',41,'Fuṣṣilat','فُصِّلَت',13,13,17,'وَنَجَّيۡنَا اَ۬لَّذِينَ ءَامَنُواْ وَكَانُواْ يَتَّقُونَ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4225,24,'478',41,'Fuṣṣilat','فُصِّلَت',13,14,18,'وَيَوۡمَ يُحۡشَرُ أَعۡدَآءُ اُ۬للَّهِ إِلَى اَ۬لنّ۪ارِ فَهُمۡ يُوزَعُونَ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4226,24,'478',41,'Fuṣṣilat','فُصِّلَت',14,15,19,'حَتَّىٰٓ إِذَا مَا جَآءُوهَا شَهِدَ عَلَيۡهِمۡ سَمۡعُهُمۡ وَأَبۡصَٰرُهُمۡ وَجُلُودُهُم بِمَا كَانُواْ يَعۡمَلُونَ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4227,24,'479',41,'Fuṣṣilat','فُصِّلَت',1,2,20,'وَقَالُواْ لِجُلُودِهِمۡ لِمَ شَهِدتُّمۡ عَلَيۡنَاۖ قَالُوٓاْ أَنطَقَنَا اَ۬للَّهُ اُ۬لَّذِيٓ أَنطَقَ كُلَّ شَيۡءٖۚ وَهۡوَ خَلَقَكُمۡ أَوَّلَ مَرَّةٖ وَإِلَيۡهِ تُرۡجَعُونَ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4228,24,'479',41,'Fuṣṣilat','فُصِّلَت',3,4,21,'وَمَا كُنتُمۡ تَسۡتَتِرُونَ أَن يَشۡهَدَ عَلَيۡكُمۡ سَمۡعُكُمۡ وَلَآ أَبۡصَٰرُكُمۡ وَلَا جُلُودُكُمۡ وَلَٰكِن ظَنَنتُمۡ أَنَّ اَ۬للَّهَ لَا يَعۡلَمُ كَثِيرٗا مِّمَّا تَعۡمَلُونَ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4229,24,'479',41,'Fuṣṣilat','فُصِّلَت',5,6,22,'وَذَٰلِكُمۡ ظَنُّكُمُ اُ۬لَّذِي ظَنَنتُم بِرَبِّكُمۡ أَرۡدَىٰكُمۡ فَأَصۡبَحۡتُم مِّنَ اَ۬لۡخَٰسِرِينَ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4230,24,'479',41,'Fuṣṣilat','فُصِّلَت',6,7,23,'فَإِن يَصۡبِرُواْ فَاَلنَّارُ مَثۡوٗى لَّهُمۡۖ وَإِن يَسۡتَعۡتِبُواْ فَمَا هُم مِّنَ اَ۬لۡمُعۡتَبِينَ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4231,24,'479',41,'Fuṣṣilat','فُصِّلَت',7,9,24,'۞ وَقَيَّضۡنَا لَهُمۡ قُرَنَآءَ فَزَيَّنُواْ لَهُم مَّا بَيۡنَ أَيۡدِيهِمۡ وَمَا خَلۡفَهُمۡ وَحَقَّ عَلَيۡهِمِ اِ۬لۡقَوۡلُ فِيٓ أُمَمٖ قَدۡ خَلَتۡ مِن قَبۡلِهِم مِّنَ اَ۬لۡجِنِّ وَاَلۡإِنسِۖ إِنَّهُمۡ كَانُواْ خَٰسِرِينَ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4232,24,'479',41,'Fuṣṣilat','فُصِّلَت',10,11,25,'وَقَالَ اَ۬لَّذِينَ كَفَرُواْ لَا تَسۡمَعُواْ لِهَٰذَا اَ۬لۡقُرۡءَانِ وَاَلۡغَوۡاْ فِيهِ لَعَلَّكُمۡ تَغۡلِبُونَ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4233,24,'479',41,'Fuṣṣilat','فُصِّلَت',11,12,26,'فَلَنُذِيقَنَّ اَ۬لَّذِينَ كَفَرُواْ عَذَابٗا شَدِيدٗا وَلَنَجۡزِيَنَّهُمۡ أَسۡوَأَ اَ۬لَّذِي كَانُواْ يَعۡمَلُونَ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4234,24,'479',41,'Fuṣṣilat','فُصِّلَت',12,13,27,'ذَٰلِكَ جَزَآءُ اَ۬عۡدَآءِ اِ۬للَّهِ اِ۬لنَّارُۖ لَهُمۡ فِيهَا دَارُ اُ۬لۡخُلۡدِۖ جَزَآءَۢ بِمَا كَانُواْ بِـَٔايَٰتِنَا يَجۡحَدُونَ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4235,24,'479',41,'Fuṣṣilat','فُصِّلَت',14,15,28,'وَقَالَ اَ۬لَّذِينَ كَفَرُواْ رَبَّنَآ أَر۪نَا اَ۬لَّذَيۡنِ أَضَلَّانَا مِنَ اَ۬لۡجِنِّ وَاَلۡإِنسِ نَجۡعَلۡهُمَا تَحۡتَ أَقۡدَامِنَا لِيَكُونَا مِنَ اَ۬لۡأَسۡفَلِينَ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4236,24,'480',41,'Fuṣṣilat','فُصِّلَت',1,3,29,'إِنَّ اَ۬لَّذِينَ قَالُواْ رَبُّنَا اَ۬للَّهُ ثُمَّ اَ۪سۡتَقَٰمُواْ تَتَنَزَّلُ عَلَيۡهِمِ اِ۬لۡمَلَٰٓئِكَةُ أَلَّا تَخَافُواْ وَلَا تَحۡزَنُواْ وَأَبۡشِرُواْ بِالۡجَنَّةِ اِ۬لَّتِي كُنتُمۡ تُوعَدُونَ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4237,24,'480',41,'Fuṣṣilat','فُصِّلَت',3,5,30,'نَحۡنُ أَوۡلِيَآؤُكُمۡ فِي اِ۬لۡحَيَوٰةِ اِ۬لدُّنۡيۭا وَفِي اِ۬لۡأٓخِرَةِۖ وَلَكُمۡ فِيهَا مَا تَشۡتَهِيٓ أَنفُسُكُمۡ وَلَكُمۡ فِيهَا مَا تَدَّعُونَ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4238,24,'480',41,'Fuṣṣilat','فُصِّلَت',5,5,31,'نُزُلٗا مِّنۡ غَفُورٖ رَّحِيمٖ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4239,24,'480',41,'Fuṣṣilat','فُصِّلَت',5,7,32,'وَمَنۡ أَحۡسَنُ قَوۡلٗا مِّمَّن دَعَآ إِلَى اَ۬للَّهِ وَعَمِلَ صَٰلِحٗا وَقَالَ إِنَّنِي مِنَ اَ۬لۡمُسۡلِمِينَ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4240,24,'480',41,'Fuṣṣilat','فُصِّلَت',7,9,33,'وَلَا تَسۡتَوِي اِ۬لۡحَسَنَةُ وَلَا اَ۬لسَّيِّئَةُۚ اُ۪دۡفَعۡ بِالَّتِي هِيَ أَحۡسَنُ فَإِذَا اَ۬لَّذِي بَيۡنَكَ وَبَيۡنَهُۥ عَدَٰوَةٞ كَأَنَّهُۥ وَلِيٌّ حَمِيمٞ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4241,24,'480',41,'Fuṣṣilat','فُصِّلَت',9,10,34,'وَمَا يُلَقَّىٰهَآ إِلَّا اَ۬لَّذِينَ صَبَرُواْ وَمَا يُلَقَّىٰهَآ إِلَّا ذُو حَظٍّ عَظِيمٖ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4242,24,'480',41,'Fuṣṣilat','فُصِّلَت',10,11,35,'وَإِمَّا يَنزَغَنَّكَ مِنَ اَ۬لشَّيۡطَٰنِ نَزۡغٞ فَاَسۡتَعِذۡ بِاللَّهِۖ إِنَّهُۥ هُوَ اَ۬لسَّمِيعُ اُ۬لۡعَلِيمُ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4243,24,'480',41,'Fuṣṣilat','فُصِّلَت',11,14,36,'۞ وَمِنۡ ءَايَٰتِهِ اِ۬لَّيۡلُ وَاَلنَّهَارُ وَاَلشَّمۡسُ وَاَلۡقَمَرُۚ لَا تَسۡجُدُواْ لِلشَّمۡسِ وَلَا لِلۡقَمَرِ وَاَسۡجُدُواْۤ لِلَّهِۤ اِ۬لَّذِي خَلَقَهُنَّ إِن كُنتُمۡ إِيَّاهُ تَعۡبُدُونَ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4244,24,'480',41,'Fuṣṣilat','فُصِّلَت',14,15,37,'فَإِنِ اِ۪سۡتَكۡبَرُواْ فَاَلَّذِينَ عِندَ رَبِّكَ يُسَبِّحُونَ لَهُۥ بِالَّيۡلِ وَاَلنَّه۪ارِ وَهُمۡ لَا يَسۡـَٔمُونَ۩ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4245,24,'481',41,'Fuṣṣilat','فُصِّلَت',1,3,38,'وَمِنۡ ءَايَٰتِهِۦٓ أَنَّكَ تَرَى اَ۬لۡأَرۡضَ خَٰشِعَةٗ فَإِذَآ أَنزَلۡنَا عَلَيۡهَا اَ۬لۡمَآءَ اَ۪هۡتَزَّتۡ وَرَبَتۡۚ إِنَّ اَ۬لَّذِيٓ أَحۡيَاهَا لَمُحۡيِ اِ۬لۡمَوۡتۭيٰٓۚ إِنَّهُۥ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4246,24,'481',41,'Fuṣṣilat','فُصِّلَت',3,5,39,'إِنَّ اَ۬لَّذِينَ يُلۡحِدُونَ فِيٓ ءَايَٰتِنَا لَا يَخۡفَوۡنَ عَلَيۡنَآۗ أَفَمَن يُلۡقَىٰ فِي اِ۬لنّ۪ارِ خَيۡرٌ أَم مَّن يَأۡتِيٓ ءَامِنٗا يَوۡمَ اَ۬لۡقِيَٰمَةِۚ اِ۪عۡمَلُواْ مَا شِئۡتُمۡ إِنَّهُۥ بِمَا تَعۡمَلُونَ بَصِيرٌ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4247,24,'481',41,'Fuṣṣilat','فُصِّلَت',5,6,40,'إِنَّ اَ۬لَّذِينَ كَفَرُواْ بِالذِّكۡرِ لَمَّا جَآءَهُمۡۖ وَإِنَّهُۥ لَكِتَٰبٌ عَزِيزٞ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4248,24,'481',41,'Fuṣṣilat','فُصِّلَت',6,7,41,'لَّا يَأۡتِيهِ اِ۬لۡبَٰطِلُ مِنۢ بَيۡنِ يَدَيۡهِ وَلَا مِنۡ خَلۡفِهِۦۖ تَنزِيلٞ مِّنۡ حَكِيمٍ حَمِيدٖ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4249,24,'481',41,'Fuṣṣilat','فُصِّلَت',7,8,42,'مَّا يُقَالُ لَكَ إِلَّا مَا قَدۡ قِيلَ لِلرُّسُلِ مِن قَبۡلِكَۚ إِنَّ رَبَّكَ لَذُو مَغۡفِرَةٖ وَذُو عِقَابٍ أَلِيمٖ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4250,24,'481',41,'Fuṣṣilat','فُصِّلَت',9,12,43,'وَلَوۡ جَعَلۡنَٰهُ قُرۡءَانًا أَعۡجَمِيّٗا لَّقَالُواْ لَوۡلَا فُصِّلَتۡ ءَايَٰتُهُۥٓۖ ءَٰا۬عۡجَمِيّٞ وَعَرَبِيّٞۗ قُلۡ هُوَ لِلَّذِينَ ءَامَنُواْ هُدٗى وَشِفَآءٞۚ وَاَلَّذِينَ لَا يُؤۡمِنُونَ فِيٓ ءَاذَانِهِمۡ وَقۡرٞ وَهۡوَ عَلَيۡهِمۡ عَمًىۚ أُوْلَٰٓئِكَ يُنَادَوۡنَ مِن مَّكَانِۢ بَعِيدٖ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4251,24,'481',41,'Fuṣṣilat','فُصِّلَت',12,14,44,'وَلَقَدۡ ءَاتَيۡنَا مُوسَى اَ۬لۡكِتَٰبَ فَاَخۡتُلِفَ فِيهِۗ وَلَوۡلَا كَلِمَةٞ سَبَقَتۡ مِن رَّبِّكَ لَقُضِيَ بَيۡنَهُمۡۚ وَإِنَّهُمۡ لَفِي شَكّٖ مِّنۡهُ مُرِيبٖ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4252,24,'481',41,'Fuṣṣilat','فُصِّلَت',14,15,45,'مَّنۡ عَمِلَ صَٰلِحٗا فَلِنَفۡسِهِۦۖ وَمَنۡ أَسَآءَ فَعَلَيۡهَاۗ وَمَا رَبُّكَ بِظَلَّٰمٖ لِّلۡعَبِيدِ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4253,25,'482',41,'Fuṣṣilat','فُصِّلَت',1,3,46,'۞ إِلَيۡهِ يُرَدُّ عِلۡمُ اُ۬لسَّاعَةِۚ وَمَا تَخۡرُجُ مِن ثَمَرَتٖ مِّنۡ أَكۡمَامِهَا وَمَا تَحۡمِلُ مِنۡ أُنثۭيٰ وَلَا تَضَعُ إِلَّا بِعِلۡمِهِۦۚ وَيَوۡمَ يُنَادِيهِمۡ أَيۡنَ شُرَكَآءِي قَالُوٓاْ ءَاذَنَّٰكَ مَا مِنَّا مِن شَهِيدٖ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4254,25,'482',41,'Fuṣṣilat','فُصِّلَت',3,4,47,'وَضَلَّ عَنۡهُم مَّا كَانُواْ يَدۡعُونَ مِن قَبۡلُۖ وَظَنُّواْ مَا لَهُم مِّن مَّحِيصٖ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4255,25,'482',41,'Fuṣṣilat','فُصِّلَت',5,6,48,'لَّا يَسۡـَٔمُ اُ۬لۡإِنسَٰنُ مِن دُعَآءِ اِ۬لۡخَيۡرِ وَإِن مَّسَّهُ اُ۬لشَّرُّ فَيَـُٔوسٞ قَنُوطٞ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4256,25,'482',41,'Fuṣṣilat','فُصِّلَت',6,9,49,'وَلَئِنۡ أَذَقۡنَٰهُ رَحۡمَةٗ مِّنَّا مِنۢ بَعۡدِ ضَرَّآءَ مَسَّتۡهُ لَيَقُولَنَّ هَٰذَا لِي وَمَآ أَظُنُّ اُ۬لسَّاعَةَ قَآئِمَةٗ وَلَئِن رُّجِعۡتُ إِلَىٰ رَبِّيَ إِنَّ لِي عِندَهُۥ لَلۡحُسۡنۭيٰۚ فَلَنُنَبِّئَنَّ اَ۬لَّذِينَ كَفَرُواْ بِمَا عَمِلُواْ وَلَنُذِيقَنَّهُم مِّنۡ عَذَابٍ غَلِيظٖ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4257,25,'482',41,'Fuṣṣilat','فُصِّلَت',9,11,50,'وَإِذَآ أَنۡعَمۡنَا عَلَى اَ۬لۡإِنسَٰنِ أَعۡرَضَ وَنَـَٔابِجَانِبِهِۦ وَإِذَا مَسَّهُ اُ۬لشَّرُّ فَذُو دُعَآءٍ عَرِيضٖ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4258,25,'482',41,'Fuṣṣilat','فُصِّلَت',11,12,51,'قُلۡ أَرَءَيۡتُمۡ إِن كَانَ مِنۡ عِندِ اِ۬للَّهِ ثُمَّ كَفَرۡتُم بِهِۦ مَنۡ أَضَلُّ مِمَّنۡ هُوَ فِي شِقَاقِۢ بَعِيدٖ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4259,25,'482',41,'Fuṣṣilat','فُصِّلَت',12,14,52,'سَنُرِيهِمۡ ءَايَٰتِنَا فِي اِ۬لۡأٓفَاقِ وَفِيٓ أَنفُسِهِمۡ حَتَّىٰ يَتَبَيَّنَ لَهُمۡ أَنَّهُ اُ۬لۡحَقُّۗ أَوَلَمۡ يَكۡفِ بِرَبِّكَ أَنَّهُۥ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدٌ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4260,25,'482',41,'Fuṣṣilat','فُصِّلَت',14,15,53,'أَلَآ إِنَّهُمۡ فِي مِرۡيَةٖ مِّن لِّقَآءِ رَبِّهِمۡۗ أَلَآ إِنَّهُۥ بِكُلِّ شَيۡءٖ مُّحِيطُۢ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4261,25,'483',42,'Ash-Shūra','الشُّوري',3,4,1,'حۭمٓ عٓسٓقٓۚ كَذَٰلِكَ يُوحِيٓ إِلَيۡكَ وَإِلَى اَ۬لَّذِينَ مِن قَبۡلِكَ اَ۬للَّهُ اُ۬لۡعَزِيزُ اُ۬لۡحَكِيمُ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4262,25,'483',42,'Ash-Shūra','الشُّوري',4,5,2,'لَهُۥ مَا فِي اِ۬لسَّمَٰوَٰتِ وَمَا فِي اِ۬لۡأَرۡضِۖ وَهۡوَ اَ۬لۡعَلِيُّ اُ۬لۡعَظِيمُ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4263,25,'483',42,'Ash-Shūra','الشُّوري',5,7,3,'۞ تَكَادُ اُ۬لسَّمَٰوَٰتُ يَنفَطِرۡنَ مِن فَوۡقِهِنَّۚ وَاَلۡمَلَٰٓئِكَةُ يُسَبِّحُونَ بِحَمۡدِ رَبِّهِمۡ وَيَسۡتَغۡفِرُونَ لِمَن فِي اِ۬لۡأَرۡضِۗ أَلَآ إِنَّ اَ۬للَّهَ هُوَ اَ۬لۡغَفُورُ اُ۬لرَّحِيمُ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4264,25,'483',42,'Ash-Shūra','الشُّوري',7,8,4,'وَاَلَّذِينَ اَ۪تَّخَذُواْ مِن دُونِهِۦٓ أَوۡلِيَآءَ اَ۬للَّهُ حَفِيظٌ عَلَيۡهِمۡ وَمَآ أَنتَ عَلَيۡهِم بِوَكِيلٖ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4265,25,'483',42,'Ash-Shūra','الشُّوري',9,11,5,'وَكَذَٰلِكَ أَوۡحَيۡنَآ إِلَيۡكَ قُرۡءَانًا عَرَبِيّٗا لِّتُنذِرَ أُمَّ اَ۬لۡقُر۪يٰ وَمَنۡ حَوۡلَهَا وَتُنذِرَ يَوۡمَ اَ۬لۡجَمۡعِ لَا رَيۡبَ فِيهِۚ فَرِيقٞ فِي اِ۬لۡجَنَّةِ وَفَرِيقٞ فِي اِ۬لسَّعِيرِ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4266,25,'483',42,'Ash-Shūra','الشُّوري',11,12,6,'وَلَوۡ شَآءَ اَ۬للَّهُ لَجَعَلَهُمۡ أُمَّةٗ وَٰحِدَةٗ وَلَٰكِن يُدۡخِلُ مَن يَشَآءُ فِي رَحۡمَتِهِۦۚ وَاَلظَّٰلِمُونَ مَا لَهُم مِّن وَلِيّٖ وَلَا نَصِيرٍ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4267,25,'483',42,'Ash-Shūra','الشُّوري',12,14,7,'أَمِ اِ۪تَّخَذُواْ مِن دُونِهِۦٓ أَوۡلِيَآءَۖ فَاَللَّهُ هُوَ اَ۬لۡوَلِيُّ وَهۡوَ يُحۡيِ اِ۬لۡمَوۡتۭيٰ وَهۡوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4268,25,'483',42,'Ash-Shūra','الشُّوري',14,15,8,'وَمَا اَ۪خۡتَلَفۡتُمۡ فِيهِ مِن شَيۡءٖ فَحُكۡمُهُۥٓ إِلَى اَ۬للَّهِۚ ذَٰلِكُمُ اُ۬للَّهُ رَبِّي عَلَيۡهِ تَوَكَّلۡتُ وَإِلَيۡهِ أُنِيبُ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4269,25,'484',42,'Ash-Shūra','الشُّوري',1,3,9,'فَاطِرُ اُ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِۚ جَعَلَ لَكُم مِّنۡ أَنفُسِكُمۡ أَزۡوَٰجٗا وَمِنَ اَ۬لۡأَنۡعَٰمِ أَزۡوَٰجٗا يَذۡرَؤُكُمۡ فِيهِۚ لَيۡسَ كَمِثۡلِهِۦ شَيۡءٞۖ وَهۡوَ اَ۬لسَّمِيعُ اُ۬لۡبَصِيرُ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4270,25,'484',42,'Ash-Shūra','الشُّوري',3,4,10,'لَهُۥ مَقَالِيدُ اُ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِۖ يَبۡسُطُ اُ۬لرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُۚ إِنَّهُۥ بِكُلِّ شَيۡءٍ عَلِيمٞ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4271,25,'484',42,'Ash-Shūra','الشُّوري',4,8,11,'۞ شَرَعَ لَكُم مِّنَ اَ۬لدِّينِ مَا وَصَّىٰ بِهِۦ نُوحٗا وَاَلَّذِيٓ أَوۡحَيۡنَآ إِلَيۡكَ وَمَا وَصَّيۡنَا بِهِۦٓ إِبۡرَٰهِيمَ وَمُوسۭيٰ وَعِيسۭيٰٓ أَنۡ أَقِيمُواْ اُ۬لدِّينَ وَلَا تَتَفَرَّقُواْ فِيهِۚ كَبُرَ عَلَى اَ۬لۡمُشۡرِكِينَ مَا تَدۡعُوهُمۡ إِلَيۡهِۚ اِ۬للَّهُ يَجۡتَبِيٓ إِلَيۡهِ مَن يَشَآءُ وَيَهۡدِيٓ إِلَيۡهِ مَن يُنِيبُ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4272,25,'484',42,'Ash-Shūra','الشُّوري',8,11,12,'وَمَا تَفَرَّقُوٓاْ إِلَّا مِنۢ بَعۡدِ مَا جَآءَهُمُ اُ۬لۡعِلۡمُ بَغۡيَۢا بَيۡنَهُمۡۚ وَلَوۡلَا كَلِمَةٞ سَبَقَتۡ مِن رَّبِّكَ إِلَىٰٓ أَجَلٖ مُّسَمّٗى لَّقُضِيَ بَيۡنَهُمۡۚ وَإِنَّ اَ۬لَّذِينَ أُورِثُواْ اُ۬لۡكِتَٰبَ مِنۢ بَعۡدِهِمۡ لَفِي شَكّٖ مِّنۡهُ مُرِيبٖ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4273,25,'484',42,'Ash-Shūra','الشُّوري',11,15,13,'فَلِذَٰلِكَ فَاَدۡعُۖ وَاَسۡتَقِمۡ كَمَآ أُمِرۡتَۖ وَلَا تَتَّبِعۡ أَهۡوَآءَهُمۡۖ وَقُلۡ ءَامَنتُ بِمَآ أَنزَلَ اَ۬للَّهُ مِن كِتَٰبٖۖ وَأُمِرۡتُ لِأَعۡدِلَ بَيۡنَكُمُۖ اُ۬للَّهُ رَبُّنَا وَرَبُّكُمۡۖ لَنَآ أَعۡمَٰلُنَا وَلَكُمۡ أَعۡمَٰلُكُمۡۖ لَا حُجَّةَ بَيۡنَنَا وَبَيۡنَكُمُۖ اُ۬للَّهُ يَجۡمَعُ بَيۡنَنَاۖ وَإِلَيۡهِ اِ۬لۡمَصِيرُ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4274,25,'485',42,'Ash-Shūra','الشُّوري',1,2,14,'وَاَلَّذِينَ يُحَآجُّونَ فِي اِ۬للَّهِ مِنۢ بَعۡدِ مَا اَ۟سۡتُجِيبَ لَهُۥ حُجَّتُهُمۡ دَاحِضَةٌ عِندَ رَبِّهِمۡ وَعَلَيۡهِمۡ غَضَبٞ وَلَهُمۡ عَذَابٞ شَدِيدٌ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4275,25,'485',42,'Ash-Shūra','الشُّوري',3,4,15,'اِ۬للَّهُ اُ۬لَّذِيٓ أَنزَلَ اَ۬لۡكِتَٰبَ بِالۡحَقِّ وَاَلۡمِيزَانَۗ وَمَا يُدۡرِيكَ لَعَلَّ اَ۬لسَّاعَةَ قَرِيبٞ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4276,25,'485',42,'Ash-Shūra','الشُّوري',4,6,16,'يَسۡتَعۡجِلُ بِهَا اَ۬لَّذِينَ لَا يُؤۡمِنُونَ بِهَاۖ وَاَلَّذِينَ ءَامَنُواْ مُشۡفِقُونَ مِنۡهَا وَيَعۡلَمُونَ أَنَّهَا اَ۬لۡحَقُّۗ أَلَآ إِنَّ اَ۬لَّذِينَ يُمَارُونَ فِي اِ۬لسَّاعَةِ لَفِي ضَلَٰلِۢ بَعِيدٍ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4277,25,'485',42,'Ash-Shūra','الشُّوري',7,7,17,'۞ اِ۬للَّهُ لَطِيفُۢ بِعِبَادِهِۦ يَرۡزُقُ مَن يَشَآءُۖ وَهۡوَ اَ۬لۡقَوِيُّ اُ۬لۡعَزِيزُ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4278,25,'485',42,'Ash-Shūra','الشُّوري',8,10,18,'مَن كَانَ يُرِيدُ حَرۡثَ اَ۬لۡأٓخِرَةِ نَزِدۡ لَهُۥ فِي حَرۡثِهِۦۖ وَمَن كَانَ يُرِيدُ حَرۡثَ اَ۬لدُّنۡيۭا نُؤۡتِهۡ مِنۡهَا وَمَا لَهُۥ فِي اِ۬لۡأٓخِرَةِ مِن نَّصِيبٍ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4279,25,'485',42,'Ash-Shūra','الشُّوري',10,12,19,'أَمۡ لَهُمۡ شُرَكَٰٓؤُاْ شَرَعُواْ لَهُم مِّنَ اَ۬لدِّينِ مَا لَمۡ يَأۡذَنۢ بِهِ اِ۬للَّهُۚ وَلَوۡلَا كَلِمَةُ اُ۬لۡفَصۡلِ لَقُضِيَ بَيۡنَهُمۡۗ وَإِنَّ اَ۬لظَّٰلِمِينَ لَهُمۡ عَذَابٌ أَلِيمٞ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4280,25,'485',42,'Ash-Shūra','الشُّوري',12,15,20,'تَرَى اَ۬لظَّٰلِمِينَ مُشۡفِقِينَ مِمَّا كَسَبُواْ وَهۡوَ وَاقِعُۢ بِهِمۡۗ وَاَلَّذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ فِي رَوۡضَاتِ اِ۬لۡجَنَّاتِۖ لَهُم مَّا يَشَآءُونَ عِندَ رَبِّهِمۡۚ ذَٰلِكَ هُوَ اَ۬لۡفَضۡلُ اُ۬لۡكَبِيرُ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4281,25,'486',42,'Ash-Shūra','الشُّوري',1,3,21,'ذَٰلِكَ اَ۬لَّذِي يَبۡشُرُ اُ۬للَّهُ عِبَادَهُ اُ۬لَّذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِۗ قُل لَّآ أَسۡـَٔلُكُمۡ عَلَيۡهِ أَجۡرًا إِلَّا اَ۬لۡمَوَدَّةَ فِي اِ۬لۡقُرۡبۭيٰۗ وَمَن يَقۡتَرِفۡ حَسَنَةٗ نَّزِدۡ لَهُۥ فِيهَا حُسۡنًاۚ إِنَّ اَ۬للَّهَ غَفُورٞ شَكُورٌ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4282,25,'486',42,'Ash-Shūra','الشُّوري',3,5,22,'أَمۡ يَقُولُونَ اَ۪فۡتَر۪يٰ عَلَى اَ۬للَّهِ كَذِبٗاۖ فَإِن يَشَإِ اِ۬للَّهُ يَخۡتِمۡ عَلَىٰ قَلۡبِكَۗ وَيَمۡحُ اُ۬للَّهُ اُ۬لۡبَٰطِلَ وَيُحِقُّ اُ۬لۡحَقَّ بِكَلِمَٰتِهِۦٓۚ إِنَّهُۥ عَلِيمُۢ بِذَاتِ اِ۬لصُّدُورِ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4283,25,'486',42,'Ash-Shūra','الشُّوري',6,7,23,'وَهۡوَ اَ۬لَّذِي يَقۡبَلُ اُ۬لتَّوۡبَةَ عَنۡ عِبَادِهِۦ وَيَعۡفُواْ عَنِ اِ۬لسَّيِّـَٔاتِ وَيَعۡلَمُ مَا يَفۡعَلُونَ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4284,25,'486',42,'Ash-Shūra','الشُّوري',7,9,24,'وَيَسۡتَجِيبُ اُ۬لَّذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ وَيَزِيدُهُم مِّن فَضۡلِهِۦۚ وَاَلۡكَٰفِرُونَ لَهُمۡ عَذَابٞ شَدِيدٞ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4285,25,'486',42,'Ash-Shūra','الشُّوري',9,10,25,'وَلَوۡ بَسَطَ اَ۬للَّهُ اُ۬لرِّزۡقَ لِعِبَادِهِۦ لَبَغَوۡاْ فِي اِ۬لۡأَرۡضِ وَلَٰكِن يُنزِلُ بِقَدَرٖ مَّا يَشَآءُۚ اِ۪نَّهُۥ بِعِبَادِهِۦ خَبِيرُۢ بَصِيرٞ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4286,25,'486',42,'Ash-Shūra','الشُّوري',10,11,26,'وَهۡوَ اَ۬لَّذِي يُنزِلُ اُ۬لۡغَيۡثَ مِنۢ بَعۡدِ مَا قَنَطُواْ وَيَنشُرُ رَحۡمَتَهُۥۚ وَهۡوَ اَ۬لۡوَلِيُّ اُ۬لۡحَمِيدُ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4287,25,'486',42,'Ash-Shūra','الشُّوري',12,13,27,'وَمِنۡ ءَايَٰتِهِۦ خَلۡقُ اُ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِ وَمَا بَثَّ فِيهِمَا مِن دَآبَّةٖۚ وَهۡوَ عَلَىٰ جَمۡعِهِمۡ إِذَا يَشَآءُ قَدِيرٞ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4288,25,'486',42,'Ash-Shūra','الشُّوري',13,14,28,'وَمَآ أَصَٰبَكُم مِّن مُّصِيبَةٖ فَبِمَا كَسَبَتۡ أَيۡدِيكُمۡ وَيَعۡفُواْ عَن كَثِيرٖ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4289,25,'486',42,'Ash-Shūra','الشُّوري',14,15,29,'وَمَآ أَنتُم بِمُعۡجِزِينَ فِي اِ۬لۡأَرۡضِۖ وَمَا لَكُم مِّن دُونِ اِ۬للَّهِ مِن وَلِيّٖ وَلَا نَصِيرٖ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4290,25,'487',42,'Ash-Shūra','الشُّوري',1,2,30,'۞ وَمِنۡ ءَايَٰتِهِ اِ۬لۡجَوَارِۦ فِي اِ۬لۡبَحۡرِ كَاَلۡأَعۡلَٰمِۚ إِن يَشَأۡ يُسۡكِنِ اِ۬لرِّيحَ فَيَظۡلَلۡنَ رَوَاكِدَ عَلَىٰ ظَهۡرِهِۦٓۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّكُلِّ صَبّ۪ارٖ شَكُورٍ ٣٠');
