INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4292,25,'487',42,'Ash-Shūra','الشُّوري',3,4,32,'وَيَعۡلَمَ اَ۬لَّذِينَ يُجَٰدِلُونَ فِيٓ ءَايَٰتِنَا مَا لَهُم مِّن مَّحِيصٖ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4293,25,'487',42,'Ash-Shūra','الشُّوري',4,6,33,'فَمَآ أُوتِيتُم مِّن شَيۡءٖ فَمَتَٰعُ اُ۬لۡحَيَوٰةِ اِ۬لدُّنۡيۭاۖ وَمَا عِندَ اَ۬للَّهِ خَيۡرٞ وَأَبۡقَىٰ لِلَّذِينَ ءَامَنُواْ وَعَلَىٰ رَبِّهِمۡ يَتَوَكَّلُونَ ٣٣');
