INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4297,25,'487',42,'Ash-Shūra','الشُّوري',9,10,37,'وَجَزَٰٓؤُاْ سَيِّئَةٖ سَيِّئَةٞ مِّثۡلُهَاۖ فَمَنۡ عَفَا وَأَصۡلَحَ فَأَجۡرُهُۥ عَلَى اَ۬للَّهِۚ إِنَّهُۥ لَا يُحِبُّ اُ۬لظَّٰلِمِينَ ٣٧');
