INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4299,25,'487',42,'Ash-Shūra','الشُّوري',11,13,39,'إِنَّمَا اَ۬لسَّبِيلُ عَلَى اَ۬لَّذِينَ يَظۡلِمُونَ اَ۬لنَّاسَ وَيَبۡغُونَ فِي اِ۬لۡأَرۡضِ بِغَيۡرِ اِ۬لۡحَقِّۚ أُوْلَٰٓئِكَ لَهُمۡ عَذَابٌ أَلِيمٞ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4300,25,'487',42,'Ash-Shūra','الشُّوري',13,14,40,'۞ وَلَمَن صَبَرَ وَغَفَرَ إِنَّ ذَٰلِكَ لَمِنۡ عَزۡمِ اِ۬لۡأُمُورِ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4301,25,'487',42,'Ash-Shūra','الشُّوري',14,15,41,'وَمَن يُضۡلِلِ اِ۬للَّهُ فَمَا لَهُۥ مِن وَلِيّٖ مِّنۢ بَعۡدِهِۦۗ وَتَرَى اَ۬لظَّٰلِمِينَ لَمَّا رَأَوُاْ اُ۬لۡعَذَابَ يَقُولُونَ هَلۡ إِلَىٰ مَرَدّٖ مِّن سَبِيلٖ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4302,25,'488',42,'Ash-Shūra','الشُّوري',1,4,42,'وَتَر۪ىٰهُمۡ يُعۡرَضُونَ عَلَيۡهَا خَٰشِعِينَ مِنَ اَ۬لذُّلِّ يَنظُرُونَ مِن طَرۡفٍ خَفِيّٖۗ وَقَالَ اَ۬لَّذِينَ ءَامَنُوٓاْ إِنَّ اَ۬لۡخَٰسِرِينَ اَ۬لَّذِينَ خَسِرُوٓاْ أَنفُسَهُمۡ وَأَهۡلِيهِمۡ يَوۡمَ اَ۬لۡقِيَٰمَةِۗ أَلَآ إِنَّ اَ۬لظَّٰلِمِينَ فِي عَذَابٖ مُّقِيمٖ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4303,25,'488',42,'Ash-Shūra','الشُّوري',4,5,43,'وَمَا كَانَ لَهُم مِّنۡ أَوۡلِيَآءَ يَنصُرُونَهُم مِّن دُونِ اِ۬للَّهِۗ وَمَن يُضۡلِلِ اِ۬للَّهُ فَمَا لَهُۥ مِن سَبِيلٍ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4304,25,'488',42,'Ash-Shūra','الشُّوري',5,7,44,'اِ۪سۡتَجِيبُواْ لِرَبِّكُم مِّن قَبۡلِ أَن يَأۡتِيَ يَوۡمٞ لَّا مَرَدَّ لَهُۥ مِنَ اَ۬للَّهِۚ مَا لَكُم مِّن مَّلۡجَإٖ يَوۡمَئِذٖ وَمَا لَكُم مِّن نَّكِيرٖ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4305,25,'488',42,'Ash-Shūra','الشُّوري',7,10,45,'فَإِنۡ أَعۡرَضُواْ فَمَآ أَرۡسَلۡنَٰكَ عَلَيۡهِمۡ حَفِيظًاۖ إِنۡ عَلَيۡكَ إِلَّا اَ۬لۡبَلَٰغُۗ وَإِنَّآ إِذَآ أَذَقۡنَا اَ۬لۡإِنسَٰنَ مِنَّا رَحۡمَةٗ فَرِحَ بِهَاۖ وَإِن تُصِبۡهُمۡ سَيِّئَةُۢ بِمَا قَدَّمَتۡ أَيۡدِيهِمۡ فَإِنَّ اَ۬لۡإِنسَٰنَ كَفُورٞ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4306,25,'488',42,'Ash-Shūra','الشُّوري',10,12,46,'لِّلَّهِ مُلۡكُ اُ۬لسَّمَٰوَٰتِ وَاَلۡأَرۡضِۚ يَخۡلُقُ مَا يَشَآءُۚ يَهَبُ لِمَن يَشَآءُ اِ۪نَٰثٗا وَيَهَبُ لِمَن يَشَآءُ اُ۬لذُّكُورَ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4307,25,'488',42,'Ash-Shūra','الشُّوري',12,13,47,'أَوۡ يُزَوِّجُهُمۡ ذُكۡرَانٗا وَإِنَٰثٗاۖ وَيَجۡعَلُ مَن يَشَآءُ عَقِيمًاۚ إِنَّهُۥ عَلِيمٞ قَدِيرٞ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4308,25,'488',42,'Ash-Shūra','الشُّوري',13,15,48,'۞ وَمَا كَانَ لِبَشَرٍ أَن يُكَلِّمَهُ اُ۬للَّهُ إِلَّا وَحۡيًا أَوۡ مِن وَرَآيِٕ حِجَابٍ أَوۡ يُرۡسِلَ رَسُولٗا فَيُوحِيَ بِإِذۡنِهِۦ مَا يَشَآءُۚ اِ۪نَّهُۥ عَلِيٌّ حَكِيمٞ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4309,25,'489',42,'Ash-Shūra','الشُّوري',1,3,49,'وَكَذَٰلِكَ أَوۡحَيۡنَآ إِلَيۡكَ رُوحٗا مِّنۡ أَمۡرِنَاۚ مَا كُنتَ تَدۡرِي مَا اَ۬لۡكِتَٰبُ وَلَا اَ۬لۡإِيمَٰنُ وَلَٰكِن جَعَلۡنَٰهُ نُورٗا نَّهۡدِي بِهِۦ مَن نَّشَآءُ مِنۡ عِبَادِنَاۚ وَإِنَّكَ لَتَهۡدِيٓ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4310,25,'489',42,'Ash-Shūra','الشُّوري',3,4,50,'صِرَٰطِ اِ۬للَّهِ اِ۬لَّذِي لَهُۥ مَا فِي اِ۬لسَّمَٰوَٰتِ وَمَا فِي اِ۬لۡأَرۡضِۗ أَلَآ إِلَى اَ۬للَّهِ تَصِيرُ اُ۬لۡأُمُورُ ٥٠');
