INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (1896,14,15,'Al-Ḥijr','الحِجر',267,2,3,94,'فَٱصۡدَعۡ بِمَا تُؤۡمَرُ وَأَعۡرِضۡ عَنِ ٱلۡمُشۡرِكِينَ ٩٤','فاصدع بما تؤمر وأعرض عن المشركين');
