INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2693,18,23,'Al-Mu’minūn','المؤمنُون',343,3,4,20,'وَشَجَرَةٗ تَخۡرُجُ مِن طُورِ سَيۡنَآءَ تَنۢبُتُ بِٱلدُّهۡنِ وَصِبۡغٖ لِّلۡأٓكِلِينَ ٢٠','وشجرة تخرج من طور سيناء تنبت بالدهن وصبغ للآكلين');
