INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2697,18,23,'Al-Mu’minūn','المؤمنُون',343,8,11,24,'فَقَالَ ٱلۡمَلَؤُاْ ٱلَّذِينَ كَفَرُواْ مِن قَوۡمِهِۦ مَا هَٰذَآ إِلَّا بَشَرٞ مِّثۡلُكُمۡ يُرِيدُ أَن يَتَفَضَّلَ عَلَيۡكُمۡ وَلَوۡ شَآءَ ٱللَّهُ لَأَنزَلَ مَلَٰٓئِكَةٗ مَّا سَمِعۡنَا بِهَٰذَا فِيٓ ءَابَآئِنَا ٱلۡأَوَّلِينَ ٢٤','فقال الملأ الذين كفروا من قومه ما هذا إلا بشر مثلكم يريد أن يتفضل عليكم ولو شاء الله لأنزل ملائكة ما سمعنا بهذا في آبائنا الأولين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2698,18,23,'Al-Mu’minūn','المؤمنُون',343,11,11,25,'إِنۡ هُوَ إِلَّا رَجُلُۢ بِهِۦ جِنَّةٞ فَتَرَبَّصُواْ بِهِۦ حَتَّىٰ حِينٖ ٢٥','إن هو إلا رجل به جنة فتربصوا به حتى حين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2699,18,23,'Al-Mu’minūn','المؤمنُون',343,12,12,26,'قَالَ رَبِّ ٱنصُرۡنِي بِمَا كَذَّبُونِ ٢٦','قال رب انصرني بما كذبون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2700,18,23,'Al-Mu’minūn','المؤمنُون',343,12,15,27,'فَأَوۡحَيۡنَآ إِلَيۡهِ أَنِ ٱصۡنَعِ ٱلۡفُلۡكَ بِأَعۡيُنِنَا وَوَحۡيِنَا فَإِذَا جَآءَ أَمۡرُنَا وَفَارَ ٱلتَّنُّورُ فَٱسۡلُكۡ فِيهَا مِن كُلّٖ زَوۡجَيۡنِ ٱثۡنَيۡنِ وَأَهۡلَكَ إِلَّا مَن سَبَقَ عَلَيۡهِ ٱلۡقَوۡلُ مِنۡهُمۡۖ وَلَا تُخَٰطِبۡنِي فِي ٱلَّذِينَ ظَلَمُوٓاْ إِنَّهُم مُّغۡرَقُونَ ٢٧','فأوحينا إليه أن اصنع الفلك بأعيننا ووحينا فإذا جاء أمرنا وفار التنور فاسلك فيها من كل زوجين اثنين وأهلك إلا من سبق عليه القول منهم ولا تخاطبني في الذين ظلموا إنهم مغرقون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2701,18,23,'Al-Mu’minūn','المؤمنُون',344,1,2,28,'فَإِذَا ٱسۡتَوَيۡتَ أَنتَ وَمَن مَّعَكَ عَلَى ٱلۡفُلۡكِ فَقُلِ ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي نَجَّىٰنَا مِنَ ٱلۡقَوۡمِ ٱلظَّٰلِمِينَ ٢٨','فإذا استويت أنت ومن معك على الفلك فقل الحمد لله الذي نجانا من القوم الظالمين');
