INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2746,18,23,'Al-Mu’minūn','المؤمنُون',346,14,14,73,'وَإِنَّكَ لَتَدۡعُوهُمۡ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ ٧٣','وإنك لتدعوهم إلى صراط مستقيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2747,18,23,'Al-Mu’minūn','المؤمنُون',346,15,15,74,'وَإِنَّ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ عَنِ ٱلصِّرَٰطِ لَنَٰكِبُونَ ٧٤','وإن الذين لا يؤمنون بالآخرة عن الصراط لناكبون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2748,18,23,'Al-Mu’minūn','المؤمنُون',347,1,2,75,'۞ وَلَوۡ رَحِمۡنَٰهُمۡ وَكَشَفۡنَا مَا بِهِم مِّن ضُرّٖ لَّلَجُّواْ فِي طُغۡيَٰنِهِمۡ يَعۡمَهُونَ ٧٥','ولو رحمناهم وكشفنا ما بهم من ضر للجوا في طغيانهم يعمهون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2749,18,23,'Al-Mu’minūn','المؤمنُون',347,2,3,76,'وَلَقَدۡ أَخَذۡنَٰهُم بِٱلۡعَذَابِ فَمَا ٱسۡتَكَانُواْ لِرَبِّهِمۡ وَمَا يَتَضَرَّعُونَ ٧٦','ولقد أخذناهم بالعذاب فما استكانوا لربهم وما يتضرعون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2750,18,23,'Al-Mu’minūn','المؤمنُون',347,3,4,77,'حَتَّىٰٓ إِذَا فَتَحۡنَا عَلَيۡهِم بَابٗا ذَا عَذَابٖ شَدِيدٍ إِذَا هُمۡ فِيهِ مُبۡلِسُونَ ٧٧','حتى إذا فتحنا عليهم بابا ذا عذاب شديد إذا هم فيه مبلسون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2751,18,23,'Al-Mu’minūn','المؤمنُون',347,4,5,78,'وَهُوَ ٱلَّذِيٓ أَنشَأَ لَكُمُ ٱلسَّمۡعَ وَٱلۡأَبۡصَٰرَ وَٱلۡأَفۡـِٔدَةَۚ قَلِيلٗا مَّا تَشۡكُرُونَ ٧٨','وهو الذي أنشأ لكم السمع والأبصار والأفئدة قليلا ما تشكرون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2752,18,23,'Al-Mu’minūn','المؤمنُون',347,5,6,79,'وَهُوَ ٱلَّذِي ذَرَأَكُمۡ فِي ٱلۡأَرۡضِ وَإِلَيۡهِ تُحۡشَرُونَ ٧٩','وهو الذي ذرأكم في الأرض وإليه تحشرون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2753,18,23,'Al-Mu’minūn','المؤمنُون',347,6,7,80,'وَهُوَ ٱلَّذِي يُحۡيِۦ وَيُمِيتُ وَلَهُ ٱخۡتِلَٰفُ ٱلَّيۡلِ وَٱلنَّهَارِۚ أَفَلَا تَعۡقِلُونَ ٨٠','وهو الذي يحيي ويميت وله اختلاف الليل والنهار أفلا تعقلون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2754,18,23,'Al-Mu’minūn','المؤمنُون',347,7,8,81,'بَلۡ قَالُواْ مِثۡلَ مَا قَالَ ٱلۡأَوَّلُونَ ٨١','بل قالوا مثل ما قال الأولون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2755,18,23,'Al-Mu’minūn','المؤمنُون',347,8,9,82,'قَالُوٓاْ أَءِذَا مِتۡنَا وَكُنَّا تُرَابٗا وَعِظَٰمًا أَءِنَّا لَمَبۡعُوثُونَ ٨٢','قالوا أئذا متنا وكنا ترابا وعظاما أئنا لمبعوثون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2756,18,23,'Al-Mu’minūn','المؤمنُون',347,9,10,83,'لَقَدۡ وُعِدۡنَا نَحۡنُ وَءَابَآؤُنَا هَٰذَا مِن قَبۡلُ إِنۡ هَٰذَآ إِلَّآ أَسَٰطِيرُ ٱلۡأَوَّلِينَ ٨٣','لقد وعدنا نحن وآباؤنا هذا من قبل إن هذا إلا أساطير الأولين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2757,18,23,'Al-Mu’minūn','المؤمنُون',347,10,11,84,'قُل لِّمَنِ ٱلۡأَرۡضُ وَمَن فِيهَآ إِن كُنتُمۡ تَعۡلَمُونَ ٨٤','قل لمن الأرض ومن فيها إن كنتم تعلمون');
