INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2779,18,23,'Al-Mu’minūn','المؤمنُون',349,2,2,106,'قَالُواْ رَبَّنَا غَلَبَتۡ عَلَيۡنَا شِقۡوَتُنَا وَكُنَّا قَوۡمٗا ضَآلِّينَ ١٠٦','قالوا ربنا غلبت علينا شقوتنا وكنا قوما ضالين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2780,18,23,'Al-Mu’minūn','المؤمنُون',349,3,3,107,'رَبَّنَآ أَخۡرِجۡنَا مِنۡهَا فَإِنۡ عُدۡنَا فَإِنَّا ظَٰلِمُونَ ١٠٧','ربنا أخرجنا منها فإن عدنا فإنا ظالمون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2781,18,23,'Al-Mu’minūn','المؤمنُون',349,4,4,108,'قَالَ ٱخۡسَـُٔواْ فِيهَا وَلَا تُكَلِّمُونِ ١٠٨','قال اخسئوا فيها ولا تكلمون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2782,18,23,'Al-Mu’minūn','المؤمنُون',349,4,6,109,'إِنَّهُۥ كَانَ فَرِيقٞ مِّنۡ عِبَادِي يَقُولُونَ رَبَّنَآ ءَامَنَّا فَٱغۡفِرۡ لَنَا وَٱرۡحَمۡنَا وَأَنتَ خَيۡرُ ٱلرَّٰحِمِينَ ١٠٩','إنه كان فريق من عبادي يقولون ربنا آمنا فاغفر لنا وارحمنا وأنت خير الراحمين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2783,18,23,'Al-Mu’minūn','المؤمنُون',349,6,7,110,'فَٱتَّخَذۡتُمُوهُمۡ سِخۡرِيًّا حَتَّىٰٓ أَنسَوۡكُمۡ ذِكۡرِي وَكُنتُم مِّنۡهُمۡ تَضۡحَكُونَ ١١٠','فاتخذتموهم سخريا حتى أنسوكم ذكري وكنتم منهم تضحكون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2784,18,23,'Al-Mu’minūn','المؤمنُون',349,7,8,111,'إِنِّي جَزَيۡتُهُمُ ٱلۡيَوۡمَ بِمَا صَبَرُوٓاْ أَنَّهُمۡ هُمُ ٱلۡفَآئِزُونَ ١١١','إني جزيتهم اليوم بما صبروا أنهم هم الفائزون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2785,18,23,'Al-Mu’minūn','المؤمنُون',349,8,9,112,'قَٰلَ كَمۡ لَبِثۡتُمۡ فِي ٱلۡأَرۡضِ عَدَدَ سِنِينَ ١١٢','قال كم لبثتم في الأرض عدد سنين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2786,18,23,'Al-Mu’minūn','المؤمنُون',349,9,9,113,'قَالُواْ لَبِثۡنَا يَوۡمًا أَوۡ بَعۡضَ يَوۡمٖ فَسۡـَٔلِ ٱلۡعَآدِّينَ ١١٣','قالوا لبثنا يوما أو بعض يوم فاسأل العادين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2787,18,23,'Al-Mu’minūn','المؤمنُون',349,10,10,114,'قَٰلَ إِن لَّبِثۡتُمۡ إِلَّا قَلِيلٗاۖ لَّوۡ أَنَّكُمۡ كُنتُمۡ تَعۡلَمُونَ ١١٤','قال إن لبثتم إلا قليلا لو أنكم كنتم تعلمون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2788,18,23,'Al-Mu’minūn','المؤمنُون',349,11,11,115,'أَفَحَسِبۡتُمۡ أَنَّمَا خَلَقۡنَٰكُمۡ عَبَثٗا وَأَنَّكُمۡ إِلَيۡنَا لَا تُرۡجَعُونَ ١١٥','أفحسبتم أنما خلقناكم عبثا وأنكم إلينا لا ترجعون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2789,18,23,'Al-Mu’minūn','المؤمنُون',349,12,12,116,'فَتَعَٰلَى ٱللَّهُ ٱلۡمَلِكُ ٱلۡحَقُّۖ لَآ إِلَٰهَ إِلَّا هُوَ رَبُّ ٱلۡعَرۡشِ ٱلۡكَرِيمِ ١١٦','فتعالى الله الملك الحق لا إله إلا هو رب العرش الكريم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2790,18,23,'Al-Mu’minūn','المؤمنُون',349,13,14,117,'وَمَن يَدۡعُ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ لَا بُرۡهَٰنَ لَهُۥ بِهِۦ فَإِنَّمَا حِسَابُهُۥ عِندَ رَبِّهِۦٓۚ إِنَّهُۥ لَا يُفۡلِحُ ٱلۡكَٰفِرُونَ ١١٧','ومن يدع مع الله إلها آخر لا برهان له به فإنما حسابه عند ربه إنه لا يفلح الكافرون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2791,18,23,'Al-Mu’minūn','المؤمنُون',349,15,15,118,'وَقُل رَّبِّ ٱغۡفِرۡ وَٱرۡحَمۡ وَأَنتَ خَيۡرُ ٱلرَّٰحِمِينَ ١١٨','وقل رب اغفر وارحم وأنت خير الراحمين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2792,18,24,'An-Nūr','النور',350,3,3,1,'سُورَةٌ أَنزَلۡنَٰهَا وَفَرَضۡنَٰهَا وَأَنزَلۡنَا فِيهَآ ءَايَٰتِۭ بَيِّنَٰتٖ لَّعَلَّكُمۡ تَذَكَّرُونَ ١','سورة أنزلناها وفرضناها وأنزلنا فيها آيات بينات لعلكم تذكرون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2793,18,24,'An-Nūr','النور',350,4,6,2,'ٱلزَّانِيَةُ وَٱلزَّانِي فَٱجۡلِدُواْ كُلَّ وَٰحِدٖ مِّنۡهُمَا مِاْئَةَ جَلۡدَةٖۖ وَلَا تَأۡخُذۡكُم بِهِمَا رَأۡفَةٞ فِي دِينِ ٱللَّهِ إِن كُنتُمۡ تُؤۡمِنُونَ بِٱللَّهِ وَٱلۡيَوۡمِ ٱلۡأٓخِرِۖ وَلۡيَشۡهَدۡ عَذَابَهُمَا طَآئِفَةٞ مِّنَ ٱلۡمُؤۡمِنِينَ ٢','الزانية والزاني فاجلدوا كل واحد منهما مائة جلدة ولا تأخذكم بهما رأفة في دين الله إن كنتم تؤمنون بالله واليوم الآخر وليشهد عذابهما طائفة من المؤمنين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2794,18,24,'An-Nūr','النور',350,6,7,3,'ٱلزَّانِي لَا يَنكِحُ إِلَّا زَانِيَةً أَوۡ مُشۡرِكَةٗ وَٱلزَّانِيَةُ لَا يَنكِحُهَآ إِلَّا زَانٍ أَوۡ مُشۡرِكٞۚ وَحُرِّمَ ذَٰلِكَ عَلَى ٱلۡمُؤۡمِنِينَ ٣','الزاني لا ينكح إلا زانية أو مشركة والزانية لا ينكحها إلا زان أو مشرك وحرم ذلك على المؤمنين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2795,18,24,'An-Nūr','النور',350,7,9,4,'وَٱلَّذِينَ يَرۡمُونَ ٱلۡمُحۡصَنَٰتِ ثُمَّ لَمۡ يَأۡتُواْ بِأَرۡبَعَةِ شُهَدَآءَ فَٱجۡلِدُوهُمۡ ثَمَٰنِينَ جَلۡدَةٗ وَلَا تَقۡبَلُواْ لَهُمۡ شَهَٰدَةً أَبَدٗاۚ وَأُوْلَٰٓئِكَ هُمُ ٱلۡفَٰسِقُونَ ٤','والذين يرمون المحصنات ثم لم يأتوا بأربعة شهداء فاجلدوهم ثمانين جلدة ولا تقبلوا لهم شهادة أبدا وأولئك هم الفاسقون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2796,18,24,'An-Nūr','النور',350,9,10,5,'إِلَّا ٱلَّذِينَ تَابُواْ مِنۢ بَعۡدِ ذَٰلِكَ وَأَصۡلَحُواْ فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ ٥','إلا الذين تابوا من بعد ذلك وأصلحوا فإن الله غفور رحيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2797,18,24,'An-Nūr','النور',350,10,12,6,'وَٱلَّذِينَ يَرۡمُونَ أَزۡوَٰجَهُمۡ وَلَمۡ يَكُن لَّهُمۡ شُهَدَآءُ إِلَّآ أَنفُسُهُمۡ فَشَهَٰدَةُ أَحَدِهِمۡ أَرۡبَعُ شَهَٰدَٰتِۭ بِٱللَّهِ إِنَّهُۥ لَمِنَ ٱلصَّٰدِقِينَ ٦','والذين يرمون أزواجهم ولم يكن لهم شهداء إلا أنفسهم فشهادة أحدهم أربع شهادات بالله إنه لمن الصادقين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2798,18,24,'An-Nūr','النور',350,12,13,7,'وَٱلۡخَٰمِسَةُ أَنَّ لَعۡنَتَ ٱللَّهِ عَلَيۡهِ إِن كَانَ مِنَ ٱلۡكَٰذِبِينَ ٧','والخامسة أن لعنة الله عليه إن كان من الكاذبين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2799,18,24,'An-Nūr','النور',350,13,14,8,'وَيَدۡرَؤُاْ عَنۡهَا ٱلۡعَذَابَ أَن تَشۡهَدَ أَرۡبَعَ شَهَٰدَٰتِۭ بِٱللَّهِ إِنَّهُۥ لَمِنَ ٱلۡكَٰذِبِينَ ٨','ويدرأ عنها العذاب أن تشهد أربع شهادات بالله إنه لمن الكاذبين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2800,18,24,'An-Nūr','النور',350,14,15,9,'وَٱلۡخَٰمِسَةَ أَنَّ غَضَبَ ٱللَّهِ عَلَيۡهَآ إِن كَانَ مِنَ ٱلصَّٰدِقِينَ ٩','والخامسة أن غضب الله عليها إن كان من الصادقين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2801,18,24,'An-Nūr','النور',350,15,15,10,'وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَيۡكُمۡ وَرَحۡمَتُهُۥ وَأَنَّ ٱللَّهَ تَوَّابٌ حَكِيمٌ ١٠','ولولا فضل الله عليكم ورحمته وأن الله تواب حكيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2802,18,24,'An-Nūr','النور',351,1,3,11,'إِنَّ ٱلَّذِينَ جَآءُو بِٱلۡإِفۡكِ عُصۡبَةٞ مِّنكُمۡۚ لَا تَحۡسَبُوهُ شَرّٗا لَّكُمۖ بَلۡ هُوَ خَيۡرٞ لَّكُمۡۚ لِكُلِّ ٱمۡرِيٕٖ مِّنۡهُم مَّا ٱكۡتَسَبَ مِنَ ٱلۡإِثۡمِۚ وَٱلَّذِي تَوَلَّىٰ كِبۡرَهُۥ مِنۡهُمۡ لَهُۥ عَذَابٌ عَظِيمٞ ١١','إن الذين جاءوا بالإفك عصبة منكم لا تحسبوه شرا لكم بل هو خير لكم لكل امرئ منهم ما اكتسب من الإثم والذي تولى كبره منهم له عذاب عظيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2803,18,24,'An-Nūr','النور',351,3,4,12,'لَّوۡلَآ إِذۡ سَمِعۡتُمُوهُ ظَنَّ ٱلۡمُؤۡمِنُونَ وَٱلۡمُؤۡمِنَٰتُ بِأَنفُسِهِمۡ خَيۡرٗا وَقَالُواْ هَٰذَآ إِفۡكٞ مُّبِينٞ ١٢','لولا إذ سمعتموه ظن المؤمنون والمؤمنات بأنفسهم خيرا وقالوا هذا إفك مبين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2804,18,24,'An-Nūr','النور',351,4,6,13,'لَّوۡلَا جَآءُو عَلَيۡهِ بِأَرۡبَعَةِ شُهَدَآءَۚ فَإِذۡ لَمۡ يَأۡتُواْ بِٱلشُّهَدَآءِ فَأُوْلَٰٓئِكَ عِندَ ٱللَّهِ هُمُ ٱلۡكَٰذِبُونَ ١٣','لولا جاءوا عليه بأربعة شهداء فإذ لم يأتوا بالشهداء فأولئك عند الله هم الكاذبون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2805,18,24,'An-Nūr','النور',351,6,7,14,'وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَيۡكُمۡ وَرَحۡمَتُهُۥ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِ لَمَسَّكُمۡ فِي مَآ أَفَضۡتُمۡ فِيهِ عَذَابٌ عَظِيمٌ ١٤','ولولا فضل الله عليكم ورحمته في الدنيا والآخرة لمسكم في ما أفضتم فيه عذاب عظيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2806,18,24,'An-Nūr','النور',351,8,9,15,'إِذۡ تَلَقَّوۡنَهُۥ بِأَلۡسِنَتِكُمۡ وَتَقُولُونَ بِأَفۡوَاهِكُم مَّا لَيۡسَ لَكُم بِهِۦ عِلۡمٞ وَتَحۡسَبُونَهُۥ هَيِّنٗا وَهُوَ عِندَ ٱللَّهِ عَظِيمٞ ١٥','إذ تلقونه بألسنتكم وتقولون بأفواهكم ما ليس لكم به علم وتحسبونه هينا وهو عند الله عظيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2807,18,24,'An-Nūr','النور',351,9,11,16,'وَلَوۡلَآ إِذۡ سَمِعۡتُمُوهُ قُلۡتُم مَّا يَكُونُ لَنَآ أَن نَّتَكَلَّمَ بِهَٰذَا سُبۡحَٰنَكَ هَٰذَا بُهۡتَٰنٌ عَظِيمٞ ١٦','ولولا إذ سمعتموه قلتم ما يكون لنا أن نتكلم بهذا سبحانك هذا بهتان عظيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2808,18,24,'An-Nūr','النور',351,11,12,17,'يَعِظُكُمُ ٱللَّهُ أَن تَعُودُواْ لِمِثۡلِهِۦٓ أَبَدًا إِن كُنتُم مُّؤۡمِنِينَ ١٧','يعظكم الله أن تعودوا لمثله أبدا إن كنتم مؤمنين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2809,18,24,'An-Nūr','النور',351,12,12,18,'وَيُبَيِّنُ ٱللَّهُ لَكُمُ ٱلۡأٓيَٰتِۚ وَٱللَّهُ عَلِيمٌ حَكِيمٌ ١٨','ويبين الله لكم الآيات والله عليم حكيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2810,18,24,'An-Nūr','النور',351,12,14,19,'إِنَّ ٱلَّذِينَ يُحِبُّونَ أَن تَشِيعَ ٱلۡفَٰحِشَةُ فِي ٱلَّذِينَ ءَامَنُواْ لَهُمۡ عَذَابٌ أَلِيمٞ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِۚ وَٱللَّهُ يَعۡلَمُ وَأَنتُمۡ لَا تَعۡلَمُونَ ١٩','إن الذين يحبون أن تشيع الفاحشة في الذين آمنوا لهم عذاب أليم في الدنيا والآخرة والله يعلم وأنتم لا تعلمون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2811,18,24,'An-Nūr','النور',351,14,15,20,'وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَيۡكُمۡ وَرَحۡمَتُهُۥ وَأَنَّ ٱللَّهَ رَءُوفٞ رَّحِيمٞ ٢٠','ولولا فضل الله عليكم ورحمته وأن الله رءوف رحيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2812,18,24,'An-Nūr','النور',352,1,4,21,'۞ يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَتَّبِعُواْ خُطُوَٰتِ ٱلشَّيۡطَٰنِۚ وَمَن يَتَّبِعۡ خُطُوَٰتِ ٱلشَّيۡطَٰنِ فَإِنَّهُۥ يَأۡمُرُ بِٱلۡفَحۡشَآءِ وَٱلۡمُنكَرِۚ وَلَوۡلَا فَضۡلُ ٱللَّهِ عَلَيۡكُمۡ وَرَحۡمَتُهُۥ مَا زَكَىٰ مِنكُم مِّنۡ أَحَدٍ أَبَدٗا وَلَٰكِنَّ ٱللَّهَ يُزَكِّي مَن يَشَآءُۗ وَٱللَّهُ سَمِيعٌ عَلِيمٞ ٢١','ياأيها الذين آمنوا لا تتبعوا خطوات الشيطان ومن يتبع خطوات الشيطان فإنه يأمر بالفحشاء والمنكر ولولا فضل الله عليكم ورحمته ما زكا منكم من أحد أبدا ولكن الله يزكي من يشاء والله سميع عليم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2813,18,24,'An-Nūr','النور',352,4,7,22,'وَلَا يَأۡتَلِ أُوْلُواْ ٱلۡفَضۡلِ مِنكُمۡ وَٱلسَّعَةِ أَن يُؤۡتُوٓاْ أُوْلِي ٱلۡقُرۡبَىٰ وَٱلۡمَسَٰكِينَ وَٱلۡمُهَٰجِرِينَ فِي سَبِيلِ ٱللَّهِۖ وَلۡيَعۡفُواْ وَلۡيَصۡفَحُوٓاْۗ أَلَا تُحِبُّونَ أَن يَغۡفِرَ ٱللَّهُ لَكُمۡۚ وَٱللَّهُ غَفُورٞ رَّحِيمٌ ٢٢','ولا يأتل أولو الفضل منكم والسعة أن يؤتوا أولي القربى والمساكين والمهاجرين في سبيل الله وليعفوا وليصفحوا ألا تحبون أن يغفر الله لكم والله غفور رحيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2814,18,24,'An-Nūr','النور',352,7,9,23,'إِنَّ ٱلَّذِينَ يَرۡمُونَ ٱلۡمُحۡصَنَٰتِ ٱلۡغَٰفِلَٰتِ ٱلۡمُؤۡمِنَٰتِ لُعِنُواْ فِي ٱلدُّنۡيَا وَٱلۡأٓخِرَةِ وَلَهُمۡ عَذَابٌ عَظِيمٞ ٢٣','إن الذين يرمون المحصنات الغافلات المؤمنات لعنوا في الدنيا والآخرة ولهم عذاب عظيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2815,18,24,'An-Nūr','النور',352,9,10,24,'يَوۡمَ تَشۡهَدُ عَلَيۡهِمۡ أَلۡسِنَتُهُمۡ وَأَيۡدِيهِمۡ وَأَرۡجُلُهُم بِمَا كَانُواْ يَعۡمَلُونَ ٢٤','يوم تشهد عليهم ألسنتهم وأيديهم وأرجلهم بما كانوا يعملون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2816,18,24,'An-Nūr','النور',352,10,11,25,'يَوۡمَئِذٖ يُوَفِّيهِمُ ٱللَّهُ دِينَهُمُ ٱلۡحَقَّ وَيَعۡلَمُونَ أَنَّ ٱللَّهَ هُوَ ٱلۡحَقُّ ٱلۡمُبِينُ ٢٥','يومئذ يوفيهم الله دينهم الحق ويعلمون أن الله هو الحق المبين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2817,18,24,'An-Nūr','النور',352,11,13,26,'ٱلۡخَبِيثَٰتُ لِلۡخَبِيثِينَ وَٱلۡخَبِيثُونَ لِلۡخَبِيثَٰتِۖ وَٱلطَّيِّبَٰتُ لِلطَّيِّبِينَ وَٱلطَّيِّبُونَ لِلطَّيِّبَٰتِۚ أُوْلَٰٓئِكَ مُبَرَّءُونَ مِمَّا يَقُولُونَۖ لَهُم مَّغۡفِرَةٞ وَرِزۡقٞ كَرِيمٞ ٢٦','الخبيثات للخبيثين والخبيثون للخبيثات والطيبات للطيبين والطيبون للطيبات أولئك مبرءون مما يقولون لهم مغفرة ورزق كريم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2818,18,24,'An-Nūr','النور',352,13,15,27,'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَدۡخُلُواْ بُيُوتًا غَيۡرَ بُيُوتِكُمۡ حَتَّىٰ تَسۡتَأۡنِسُواْ وَتُسَلِّمُواْ عَلَىٰٓ أَهۡلِهَاۚ ذَٰلِكُمۡ خَيۡرٞ لَّكُمۡ لَعَلَّكُمۡ تَذَكَّرُونَ ٢٧','ياأيها الذين آمنوا لا تدخلوا بيوتا غير بيوتكم حتى تستأنسوا وتسلموا على أهلها ذلكم خير لكم لعلكم تذكرون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2819,18,24,'An-Nūr','النور',353,1,3,28,'فَإِن لَّمۡ تَجِدُواْ فِيهَآ أَحَدٗا فَلَا تَدۡخُلُوهَا حَتَّىٰ يُؤۡذَنَ لَكُمۡۖ وَإِن قِيلَ لَكُمُ ٱرۡجِعُواْ فَٱرۡجِعُواْۖ هُوَ أَزۡكَىٰ لَكُمۡۚ وَٱللَّهُ بِمَا تَعۡمَلُونَ عَلِيمٞ ٢٨','فإن لم تجدوا فيها أحدا فلا تدخلوها حتى يؤذن لكم وإن قيل لكم ارجعوا فارجعوا هو أزكى لكم والله بما تعملون عليم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2820,18,24,'An-Nūr','النور',353,3,5,29,'لَّيۡسَ عَلَيۡكُمۡ جُنَاحٌ أَن تَدۡخُلُواْ بُيُوتًا غَيۡرَ مَسۡكُونَةٖ فِيهَا مَتَٰعٞ لَّكُمۡۚ وَٱللَّهُ يَعۡلَمُ مَا تُبۡدُونَ وَمَا تَكۡتُمُونَ ٢٩','ليس عليكم جناح أن تدخلوا بيوتا غير مسكونة فيها متاع لكم والله يعلم ما تبدون وما تكتمون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2821,18,24,'An-Nūr','النور',353,5,6,30,'قُل لِّلۡمُؤۡمِنِينَ يَغُضُّواْ مِنۡ أَبۡصَٰرِهِمۡ وَيَحۡفَظُواْ فُرُوجَهُمۡۚ ذَٰلِكَ أَزۡكَىٰ لَهُمۡۚ إِنَّ ٱللَّهَ خَبِيرُۢ بِمَا يَصۡنَعُونَ ٣٠','قل للمؤمنين يغضوا من أبصارهم ويحفظوا فروجهم ذلك أزكى لهم إن الله خبير بما يصنعون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2822,18,24,'An-Nūr','النور',353,7,15,31,'وَقُل لِّلۡمُؤۡمِنَٰتِ يَغۡضُضۡنَ مِنۡ أَبۡصَٰرِهِنَّ وَيَحۡفَظۡنَ فُرُوجَهُنَّ وَلَا يُبۡدِينَ زِينَتَهُنَّ إِلَّا مَا ظَهَرَ مِنۡهَاۖ وَلۡيَضۡرِبۡنَ بِخُمُرِهِنَّ عَلَىٰ جُيُوبِهِنَّۖ وَلَا يُبۡدِينَ زِينَتَهُنَّ إِلَّا لِبُعُولَتِهِنَّ أَوۡ ءَابَآئِهِنَّ أَوۡ ءَابَآءِ بُعُولَتِهِنَّ أَوۡ أَبۡنَآئِهِنَّ أَوۡ أَبۡنَآءِ بُعُولَتِهِنَّ أَوۡ إِخۡوَٰنِهِنَّ أَوۡ بَنِيٓ إِخۡوَٰنِهِنَّ أَوۡ بَنِيٓ أَخَوَٰتِهِنَّ أَوۡ نِسَآئِهِنَّ أَوۡ مَا مَلَكَتۡ أَيۡمَٰنُهُنَّ أَوِ ٱلتَّٰبِعِينَ غَيۡرِ أُوْلِي ٱلۡإِرۡبَةِ مِنَ ٱلرِّجَالِ أَوِ ٱلطِّفۡلِ ٱلَّذِينَ لَمۡ يَظۡهَرُواْ عَلَىٰ عَوۡرَٰتِ ٱلنِّسَآءِۖ وَلَا يَضۡرِبۡنَ بِأَرۡجُلِهِنَّ لِيُعۡلَمَ مَا يُخۡفِينَ مِن زِينَتِهِنَّۚ وَتُوبُوٓاْ إِلَى ٱللَّهِ جَمِيعًا أَيُّهَ ٱلۡمُؤۡمِنُونَ لَعَلَّكُمۡ تُفۡلِحُونَ ٣١','وقل للمؤمنات يغضضن من أبصارهن ويحفظن فروجهن ولا يبدين زينتهن إلا ما ظهر منها وليضربن بخمرهن على جيوبهن ولا يبدين زينتهن إلا لبعولتهن أو آبائهن أو آباء بعولتهن أو أبنائهن أو أبناء بعولتهن أو إخوانهن أو بني إخوانهن أو بني أخواتهن أو نسائهن أو ما ملكت أيمانهن أو التابعين غير أولي الإربة من الرجال أو الطفل الذين لم يظهروا على عورات النساء ولا يضربن بأرجلهن ليعلم ما يخفين من زينتهن وتوبوا إلى الله جميعا أيها المؤمنون لعلكم تفلحون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2823,18,24,'An-Nūr','النور',354,1,2,32,'وَأَنكِحُواْ ٱلۡأَيَٰمَىٰ مِنكُمۡ وَٱلصَّٰلِحِينَ مِنۡ عِبَادِكُمۡ وَإِمَآئِكُمۡۚ إِن يَكُونُواْ فُقَرَآءَ يُغۡنِهِمُ ٱللَّهُ مِن فَضۡلِهِۦۗ وَٱللَّهُ وَٰسِعٌ عَلِيمٞ ٣٢','وأنكحوا الأيامى منكم والصالحين من عبادكم وإمائكم إن يكونوا فقراء يغنهم الله من فضله والله واسع عليم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2824,18,24,'An-Nūr','النور',354,3,7,33,'وَلۡيَسۡتَعۡفِفِ ٱلَّذِينَ لَا يَجِدُونَ نِكَاحًا حَتَّىٰ يُغۡنِيَهُمُ ٱللَّهُ مِن فَضۡلِهِۦۗ وَٱلَّذِينَ يَبۡتَغُونَ ٱلۡكِتَٰبَ مِمَّا مَلَكَتۡ أَيۡمَٰنُكُمۡ فَكَاتِبُوهُمۡ إِنۡ عَلِمۡتُمۡ فِيهِمۡ خَيۡرٗاۖ وَءَاتُوهُم مِّن مَّالِ ٱللَّهِ ٱلَّذِيٓ ءَاتَىٰكُمۡۚ وَلَا تُكۡرِهُواْ فَتَيَٰتِكُمۡ عَلَى ٱلۡبِغَآءِ إِنۡ أَرَدۡنَ تَحَصُّنٗا لِّتَبۡتَغُواْ عَرَضَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۚ وَمَن يُكۡرِههُّنَّ فَإِنَّ ٱللَّهَ مِنۢ بَعۡدِ إِكۡرَٰهِهِنَّ غَفُورٞ رَّحِيمٞ ٣٣','وليستعفف الذين لا يجدون نكاحا حتى يغنيهم الله من فضله والذين يبتغون الكتاب مما ملكت أيمانكم فكاتبوهم إن علمتم فيهم خيرا وآتوهم من مال الله الذي آتاكم ولا تكرهوا فتياتكم على البغاء إن أردن تحصنا لتبتغوا عرض الحياة الدنيا ومن يكرهن فإن الله من بعد إكراههن غفور رحيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2825,18,24,'An-Nūr','النور',354,8,9,34,'وَلَقَدۡ أَنزَلۡنَآ إِلَيۡكُمۡ ءَايَٰتٖ مُّبَيِّنَٰتٖ وَمَثَلٗا مِّنَ ٱلَّذِينَ خَلَوۡاْ مِن قَبۡلِكُمۡ وَمَوۡعِظَةٗ لِّلۡمُتَّقِينَ ٣٤','ولقد أنزلنا إليكم آيات مبينات ومثلا من الذين خلوا من قبلكم وموعظة للمتقين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2826,18,24,'An-Nūr','النور',354,9,14,35,'۞ ٱللَّهُ نُورُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ مَثَلُ نُورِهِۦ كَمِشۡكَوٰةٖ فِيهَا مِصۡبَاحٌۖ ٱلۡمِصۡبَاحُ فِي زُجَاجَةٍۖ ٱلزُّجَاجَةُ كَأَنَّهَا كَوۡكَبٞ دُرِّيّٞ يُوقَدُ مِن شَجَرَةٖ مُّبَٰرَكَةٖ زَيۡتُونَةٖ لَّا شَرۡقِيَّةٖ وَلَا غَرۡبِيَّةٖ يَكَادُ زَيۡتُهَا يُضِيٓءُ وَلَوۡ لَمۡ تَمۡسَسۡهُ نَارٞۚ نُّورٌ عَلَىٰ نُورٖۚ يَهۡدِي ٱللَّهُ لِنُورِهِۦ مَن يَشَآءُۚ وَيَضۡرِبُ ٱللَّهُ ٱلۡأَمۡثَٰلَ لِلنَّاسِۗ وَٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمٞ ٣٥','الله نور السموات والأرض مثل نوره كمشكاة فيها مصباح المصباح في زجاجة الزجاجة كأنها كوكب دري يوقد من شجرة مباركة زيتونة لا شرقية ولا غربية يكاد زيتها يضيء ولو لم تمسسه نار نور على نور يهدي الله لنوره من يشاء ويضرب الله الأمثال للناس والله بكل شيء عليم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2827,18,24,'An-Nūr','النور',354,14,15,36,'فِي بُيُوتٍ أَذِنَ ٱللَّهُ أَن تُرۡفَعَ وَيُذۡكَرَ فِيهَا ٱسۡمُهُۥ يُسَبِّحُ لَهُۥ فِيهَا بِٱلۡغُدُوِّ وَٱلۡأٓصَالِ ٣٦','في بيوت أذن الله أن ترفع ويذكر فيها اسمه يسبح له فيها بالغدو والآصال');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2828,18,24,'An-Nūr','النور',355,1,2,37,'رِجَالٞ لَّا تُلۡهِيهِمۡ تِجَٰرَةٞ وَلَا بَيۡعٌ عَن ذِكۡرِ ٱللَّهِ وَإِقَامِ ٱلصَّلَوٰةِ وَإِيتَآءِ ٱلزَّكَوٰةِ يَخَافُونَ يَوۡمٗا تَتَقَلَّبُ فِيهِ ٱلۡقُلُوبُ وَٱلۡأَبۡصَٰرُ ٣٧','رجال لا تلهيهم تجارة ولا بيع عن ذكر الله وإقام الصلاة وإيتاء الزكاة يخافون يوما تتقلب فيه القلوب والأبصار');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2829,18,24,'An-Nūr','النور',355,3,4,38,'لِيَجۡزِيَهُمُ ٱللَّهُ أَحۡسَنَ مَا عَمِلُواْ وَيَزِيدَهُم مِّن فَضۡلِهِۦۗ وَٱللَّهُ يَرۡزُقُ مَن يَشَآءُ بِغَيۡرِ حِسَابٖ ٣٨','ليجزيهم الله أحسن ما عملوا ويزيدهم من فضله والله يرزق من يشاء بغير حساب');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2830,18,24,'An-Nūr','النور',355,4,6,39,'وَٱلَّذِينَ كَفَرُوٓاْ أَعۡمَٰلُهُمۡ كَسَرَابِۭ بِقِيعَةٖ يَحۡسَبُهُ ٱلظَّمۡـَٔانُ مَآءً حَتَّىٰٓ إِذَا جَآءَهُۥ لَمۡ يَجِدۡهُ شَيۡـٔٗا وَوَجَدَ ٱللَّهَ عِندَهُۥ فَوَفَّىٰهُ حِسَابَهُۥۗ وَٱللَّهُ سَرِيعُ ٱلۡحِسَابِ ٣٩','والذين كفروا أعمالهم كسراب بقيعة يحسبه الظمآن ماء حتى إذا جاءه لم يجده شيئا ووجد الله عنده فوفاه حسابه والله سريع الحساب');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2831,18,24,'An-Nūr','النور',355,7,9,40,'أَوۡ كَظُلُمَٰتٖ فِي بَحۡرٖ لُّجِّيّٖ يَغۡشَىٰهُ مَوۡجٞ مِّن فَوۡقِهِۦ مَوۡجٞ مِّن فَوۡقِهِۦ سَحَابٞۚ ظُلُمَٰتُۢ بَعۡضُهَا فَوۡقَ بَعۡضٍ إِذَآ أَخۡرَجَ يَدَهُۥ لَمۡ يَكَدۡ يَرَىٰهَاۗ وَمَن لَّمۡ يَجۡعَلِ ٱللَّهُ لَهُۥ نُورٗا فَمَا لَهُۥ مِن نُّورٍ ٤٠','أو كظلمات في بحر لجي يغشاه موج من فوقه موج من فوقه سحاب ظلمات بعضها فوق بعض إذا أخرج يده لم يكد يراها ومن لم يجعل الله له نورا فما له من نور');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2832,18,24,'An-Nūr','النور',355,9,11,41,'أَلَمۡ تَرَ أَنَّ ٱللَّهَ يُسَبِّحُ لَهُۥ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَٱلطَّيۡرُ صَٰٓفَّٰتٖۖ كُلّٞ قَدۡ عَلِمَ صَلَاتَهُۥ وَتَسۡبِيحَهُۥۗ وَٱللَّهُ عَلِيمُۢ بِمَا يَفۡعَلُونَ ٤١','ألم تر أن الله يسبح له من في السموات والأرض والطير صافات كل قد علم صلاته وتسبيحه والله عليم بما يفعلون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2833,18,24,'An-Nūr','النور',355,11,12,42,'وَلِلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ وَإِلَى ٱللَّهِ ٱلۡمَصِيرُ ٤٢','ولله ملك السموات والأرض وإلى الله المصير');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2834,18,24,'An-Nūr','النور',355,12,15,43,'أَلَمۡ تَرَ أَنَّ ٱللَّهَ يُزۡجِي سَحَابٗا ثُمَّ يُؤَلِّفُ بَيۡنَهُۥ ثُمَّ يَجۡعَلُهُۥ رُكَامٗا فَتَرَى ٱلۡوَدۡقَ يَخۡرُجُ مِنۡ خِلَٰلِهِۦ وَيُنَزِّلُ مِنَ ٱلسَّمَآءِ مِن جِبَالٖ فِيهَا مِنۢ بَرَدٖ فَيُصِيبُ بِهِۦ مَن يَشَآءُ وَيَصۡرِفُهُۥ عَن مَّن يَشَآءُۖ يَكَادُ سَنَا بَرۡقِهِۦ يَذۡهَبُ بِٱلۡأَبۡصَٰرِ ٤٣','ألم تر أن الله يزجي سحابا ثم يؤلف بينه ثم يجعله ركاما فترى الودق يخرج من خلاله وينزل من السماء من جبال فيها من برد فيصيب به من يشاء ويصرفه عن من يشاء يكاد سنا برقه يذهب بالأبصار');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2835,18,24,'An-Nūr','النور',356,1,1,44,'يُقَلِّبُ ٱللَّهُ ٱلَّيۡلَ وَٱلنَّهَارَۚ إِنَّ فِي ذَٰلِكَ لَعِبۡرَةٗ لِّأُوْلِي ٱلۡأَبۡصَٰرِ ٤٤','يقلب الله الليل والنهار إن في ذلك لعبرة لأولي الأبصار');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2836,18,24,'An-Nūr','النور',356,2,4,45,'وَٱللَّهُ خَلَقَ كُلَّ دَآبَّةٖ مِّن مَّآءٖۖ فَمِنۡهُم مَّن يَمۡشِي عَلَىٰ بَطۡنِهِۦ وَمِنۡهُم مَّن يَمۡشِي عَلَىٰ رِجۡلَيۡنِ وَمِنۡهُم مَّن يَمۡشِي عَلَىٰٓ أَرۡبَعٖۚ يَخۡلُقُ ٱللَّهُ مَا يَشَآءُۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ ٤٥','والله خلق كل دابة من ماء فمنهم من يمشي على بطنه ومنهم من يمشي على رجلين ومنهم من يمشي على أربع يخلق الله ما يشاء إن الله على كل شيء قدير');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2837,18,24,'An-Nūr','النور',356,4,5,46,'لَّقَدۡ أَنزَلۡنَآ ءَايَٰتٖ مُّبَيِّنَٰتٖۚ وَٱللَّهُ يَهۡدِي مَن يَشَآءُ إِلَىٰ صِرَٰطٖ مُّسۡتَقِيمٖ ٤٦','لقد أنزلنا آيات مبينات والله يهدي من يشاء إلى صراط مستقيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2838,18,24,'An-Nūr','النور',356,5,7,47,'وَيَقُولُونَ ءَامَنَّا بِٱللَّهِ وَبِٱلرَّسُولِ وَأَطَعۡنَا ثُمَّ يَتَوَلَّىٰ فَرِيقٞ مِّنۡهُم مِّنۢ بَعۡدِ ذَٰلِكَۚ وَمَآ أُوْلَٰٓئِكَ بِٱلۡمُؤۡمِنِينَ ٤٧','ويقولون آمنا بالله وبالرسول وأطعنا ثم يتولى فريق منهم من بعد ذلك وما أولئك بالمؤمنين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2839,18,24,'An-Nūr','النور',356,7,8,48,'وَإِذَا دُعُوٓاْ إِلَى ٱللَّهِ وَرَسُولِهِۦ لِيَحۡكُمَ بَيۡنَهُمۡ إِذَا فَرِيقٞ مِّنۡهُم مُّعۡرِضُونَ ٤٨','وإذا دعوا إلى الله ورسوله ليحكم بينهم إذا فريق منهم معرضون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2840,18,24,'An-Nūr','النور',356,8,9,49,'وَإِن يَكُن لَّهُمُ ٱلۡحَقُّ يَأۡتُوٓاْ إِلَيۡهِ مُذۡعِنِينَ ٤٩','وإن يكن لهم الحق يأتوا إليه مذعنين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2841,18,24,'An-Nūr','النور',356,9,10,50,'أَفِي قُلُوبِهِم مَّرَضٌ أَمِ ٱرۡتَابُوٓاْ أَمۡ يَخَافُونَ أَن يَحِيفَ ٱللَّهُ عَلَيۡهِمۡ وَرَسُولُهُۥۚ بَلۡ أُوْلَٰٓئِكَ هُمُ ٱلظَّٰلِمُونَ ٥٠','أفي قلوبهم مرض أم ارتابوا أم يخافون أن يحيف الله عليهم ورسوله بل أولئك هم الظالمون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2842,18,24,'An-Nūr','النور',356,10,12,51,'إِنَّمَا كَانَ قَوۡلَ ٱلۡمُؤۡمِنِينَ إِذَا دُعُوٓاْ إِلَى ٱللَّهِ وَرَسُولِهِۦ لِيَحۡكُمَ بَيۡنَهُمۡ أَن يَقُولُواْ سَمِعۡنَا وَأَطَعۡنَاۚ وَأُوْلَٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ ٥١','إنما كان قول المؤمنين إذا دعوا إلى الله ورسوله ليحكم بينهم أن يقولوا سمعنا وأطعنا وأولئك هم المفلحون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2843,18,24,'An-Nūr','النور',356,12,13,52,'وَمَن يُطِعِ ٱللَّهَ وَرَسُولَهُۥ وَيَخۡشَ ٱللَّهَ وَيَتَّقۡهِ فَأُوْلَٰٓئِكَ هُمُ ٱلۡفَآئِزُونَ ٥٢','ومن يطع الله ورسوله ويخش الله ويتقه فأولئك هم الفائزون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2844,18,24,'An-Nūr','النور',356,14,15,53,'۞ وَأَقۡسَمُواْ بِٱللَّهِ جَهۡدَ أَيۡمَٰنِهِمۡ لَئِنۡ أَمَرۡتَهُمۡ لَيَخۡرُجُنَّۖ قُل لَّا تُقۡسِمُواْۖ طَاعَةٞ مَّعۡرُوفَةٌۚ إِنَّ ٱللَّهَ خَبِيرُۢ بِمَا تَعۡمَلُونَ ٥٣','وأقسموا بالله جهد أيمانهم لئن أمرتهم ليخرجن قل لا تقسموا طاعة معروفة إن الله خبير بما تعملون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2845,18,24,'An-Nūr','النور',357,1,3,54,'قُلۡ أَطِيعُواْ ٱللَّهَ وَأَطِيعُواْ ٱلرَّسُولَۖ فَإِن تَوَلَّوۡاْ فَإِنَّمَا عَلَيۡهِ مَا حُمِّلَ وَعَلَيۡكُم مَّا حُمِّلۡتُمۡۖ وَإِن تُطِيعُوهُ تَهۡتَدُواْۚ وَمَا عَلَى ٱلرَّسُولِ إِلَّا ٱلۡبَلَٰغُ ٱلۡمُبِينُ ٥٤','قل أطيعوا الله وأطيعوا الرسول فإن تولوا فإنما عليه ما حمل وعليكم ما حملتم وإن تطيعوه تهتدوا وما على الرسول إلا البلاغ المبين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2846,18,24,'An-Nūr','النور',357,3,7,55,'وَعَدَ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ مِنكُمۡ وَعَمِلُواْ ٱلصَّٰلِحَٰتِ لَيَسۡتَخۡلِفَنَّهُمۡ فِي ٱلۡأَرۡضِ كَمَا ٱسۡتَخۡلَفَ ٱلَّذِينَ مِن قَبۡلِهِمۡ وَلَيُمَكِّنَنَّ لَهُمۡ دِينَهُمُ ٱلَّذِي ٱرۡتَضَىٰ لَهُمۡ وَلَيُبَدِّلَنَّهُم مِّنۢ بَعۡدِ خَوۡفِهِمۡ أَمۡنٗاۚ يَعۡبُدُونَنِي لَا يُشۡرِكُونَ بِي شَيۡـٔٗاۚ وَمَن كَفَرَ بَعۡدَ ذَٰلِكَ فَأُوْلَٰٓئِكَ هُمُ ٱلۡفَٰسِقُونَ ٥٥','وعد الله الذين آمنوا منكم وعملوا الصالحات ليستخلفنهم في الأرض كما استخلف الذين من قبلهم وليمكنن لهم دينهم الذي ارتضى لهم وليبدلنهم من بعد خوفهم أمنا يعبدونني لا يشركون بي شيئا ومن كفر بعد ذلك فأولئك هم الفاسقون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2847,18,24,'An-Nūr','النور',357,8,9,56,'وَأَقِيمُواْ ٱلصَّلَوٰةَ وَءَاتُواْ ٱلزَّكَوٰةَ وَأَطِيعُواْ ٱلرَّسُولَ لَعَلَّكُمۡ تُرۡحَمُونَ ٥٦','وأقيموا الصلاة وآتوا الزكاة وأطيعوا الرسول لعلكم ترحمون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2848,18,24,'An-Nūr','النور',357,9,10,57,'لَا تَحۡسَبَنَّ ٱلَّذِينَ كَفَرُواْ مُعۡجِزِينَ فِي ٱلۡأَرۡضِۚ وَمَأۡوَىٰهُمُ ٱلنَّارُۖ وَلَبِئۡسَ ٱلۡمَصِيرُ ٥٧','لا تحسبن الذين كفروا معجزين في الأرض ومأواهم النار ولبئس المصير');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2849,18,24,'An-Nūr','النور',357,10,15,58,'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لِيَسۡتَـٔۡذِنكُمُ ٱلَّذِينَ مَلَكَتۡ أَيۡمَٰنُكُمۡ وَٱلَّذِينَ لَمۡ يَبۡلُغُواْ ٱلۡحُلُمَ مِنكُمۡ ثَلَٰثَ مَرَّٰتٖۚ مِّن قَبۡلِ صَلَوٰةِ ٱلۡفَجۡرِ وَحِينَ تَضَعُونَ ثِيَابَكُم مِّنَ ٱلظَّهِيرَةِ وَمِنۢ بَعۡدِ صَلَوٰةِ ٱلۡعِشَآءِۚ ثَلَٰثُ عَوۡرَٰتٖ لَّكُمۡۚ لَيۡسَ عَلَيۡكُمۡ وَلَا عَلَيۡهِمۡ جُنَاحُۢ بَعۡدَهُنَّۚ طَوَّٰفُونَ عَلَيۡكُم بَعۡضُكُمۡ عَلَىٰ بَعۡضٖۚ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمُ ٱلۡأٓيَٰتِۗ وَٱللَّهُ عَلِيمٌ حَكِيمٞ ٥٨','ياأيها الذين آمنوا ليستأذنكم الذين ملكت أيمانكم والذين لم يبلغوا الحلم منكم ثلاث مرات من قبل صلاة الفجر وحين تضعون ثيابكم من الظهيرة ومن بعد صلاة العشاء ثلاث عورات لكم ليس عليكم ولا عليهم جناح بعدهن طوافون عليكم بعضكم على بعض كذلك يبين الله لكم الآيات والله عليم حكيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2850,18,24,'An-Nūr','النور',358,1,3,59,'وَإِذَا بَلَغَ ٱلۡأَطۡفَٰلُ مِنكُمُ ٱلۡحُلُمَ فَلۡيَسۡتَـٔۡذِنُواْ كَمَا ٱسۡتَـٔۡذَنَ ٱلَّذِينَ مِن قَبۡلِهِمۡۚ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمۡ ءَايَٰتِهِۦۗ وَٱللَّهُ عَلِيمٌ حَكِيمٞ ٥٩','وإذا بلغ الأطفال منكم الحلم فليستأذنوا كما استأذن الذين من قبلهم كذلك يبين الله لكم آياته والله عليم حكيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2851,18,24,'An-Nūr','النور',358,3,6,60,'وَٱلۡقَوَٰعِدُ مِنَ ٱلنِّسَآءِ ٱلَّٰتِي لَا يَرۡجُونَ نِكَاحٗا فَلَيۡسَ عَلَيۡهِنَّ جُنَاحٌ أَن يَضَعۡنَ ثِيَابَهُنَّ غَيۡرَ مُتَبَرِّجَٰتِۭ بِزِينَةٖۖ وَأَن يَسۡتَعۡفِفۡنَ خَيۡرٞ لَّهُنَّۗ وَٱللَّهُ سَمِيعٌ عَلِيمٞ ٦٠','والقواعد من النساء اللاتي لا يرجون نكاحا فليس عليهن جناح أن يضعن ثيابهن غير متبرجات بزينة وأن يستعففن خير لهن والله سميع عليم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2852,18,24,'An-Nūr','النور',358,6,15,61,'لَّيۡسَ عَلَى ٱلۡأَعۡمَىٰ حَرَجٞ وَلَا عَلَى ٱلۡأَعۡرَجِ حَرَجٞ وَلَا عَلَى ٱلۡمَرِيضِ حَرَجٞ وَلَا عَلَىٰٓ أَنفُسِكُمۡ أَن تَأۡكُلُواْ مِنۢ بُيُوتِكُمۡ أَوۡ بُيُوتِ ءَابَآئِكُمۡ أَوۡ بُيُوتِ أُمَّهَٰتِكُمۡ أَوۡ بُيُوتِ إِخۡوَٰنِكُمۡ أَوۡ بُيُوتِ أَخَوَٰتِكُمۡ أَوۡ بُيُوتِ أَعۡمَٰمِكُمۡ أَوۡ بُيُوتِ عَمَّٰتِكُمۡ أَوۡ بُيُوتِ أَخۡوَٰلِكُمۡ أَوۡ بُيُوتِ خَٰلَٰتِكُمۡ أَوۡ مَا مَلَكۡتُم مَّفَاتِحَهُۥٓ أَوۡ صَدِيقِكُمۡۚ لَيۡسَ عَلَيۡكُمۡ جُنَاحٌ أَن تَأۡكُلُواْ جَمِيعًا أَوۡ أَشۡتَاتٗاۚ فَإِذَا دَخَلۡتُم بُيُوتٗا فَسَلِّمُواْ عَلَىٰٓ أَنفُسِكُمۡ تَحِيَّةٗ مِّنۡ عِندِ ٱللَّهِ مُبَٰرَكَةٗ طَيِّبَةٗۚ كَذَٰلِكَ يُبَيِّنُ ٱللَّهُ لَكُمُ ٱلۡأٓيَٰتِ لَعَلَّكُمۡ تَعۡقِلُونَ ٦١','ليس على الأعمى حرج ولا على الأعرج حرج ولا على المريض حرج ولا على أنفسكم أن تأكلوا من بيوتكم أو بيوت آبائكم أو بيوت أمهاتكم أو بيوت إخوانكم أو بيوت أخواتكم أو بيوت أعمامكم أو بيوت عماتكم أو بيوت أخوالكم أو بيوت خالاتكم أو ما ملكتم مفاتحه أو صديقكم ليس عليكم جناح أن تأكلوا جميعا أو أشتاتا فإذا دخلتم بيوتا فسلموا على أنفسكم تحية من عند الله مباركة طيبة كذلك يبين الله لكم الآيات لعلكم تعقلون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2853,18,24,'An-Nūr','النور',359,1,5,62,'إِنَّمَا ٱلۡمُؤۡمِنُونَ ٱلَّذِينَ ءَامَنُواْ بِٱللَّهِ وَرَسُولِهِۦ وَإِذَا كَانُواْ مَعَهُۥ عَلَىٰٓ أَمۡرٖ جَامِعٖ لَّمۡ يَذۡهَبُواْ حَتَّىٰ يَسۡتَـٔۡذِنُوهُۚ إِنَّ ٱلَّذِينَ يَسۡتَـٔۡذِنُونَكَ أُوْلَٰٓئِكَ ٱلَّذِينَ يُؤۡمِنُونَ بِٱللَّهِ وَرَسُولِهِۦۚ فَإِذَا ٱسۡتَـٔۡذَنُوكَ لِبَعۡضِ شَأۡنِهِمۡ فَأۡذَن لِّمَن شِئۡتَ مِنۡهُمۡ وَٱسۡتَغۡفِرۡ لَهُمُ ٱللَّهَۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ ٦٢','إنما المؤمنون الذين آمنوا بالله ورسوله وإذا كانوا معه على أمر جامع لم يذهبوا حتى يستأذنوه إن الذين يستأذنونك أولئك الذين يؤمنون بالله ورسوله فإذا استأذنوك لبعض شأنهم فأذن لمن شئت منهم واستغفر لهم الله إن الله غفور رحيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2854,18,24,'An-Nūr','النور',359,5,8,63,'لَّا تَجۡعَلُواْ دُعَآءَ ٱلرَّسُولِ بَيۡنَكُمۡ كَدُعَآءِ بَعۡضِكُم بَعۡضٗاۚ قَدۡ يَعۡلَمُ ٱللَّهُ ٱلَّذِينَ يَتَسَلَّلُونَ مِنكُمۡ لِوَاذٗاۚ فَلۡيَحۡذَرِ ٱلَّذِينَ يُخَالِفُونَ عَنۡ أَمۡرِهِۦٓ أَن تُصِيبَهُمۡ فِتۡنَةٌ أَوۡ يُصِيبَهُمۡ عَذَابٌ أَلِيمٌ ٦٣','لا تجعلوا دعاء الرسول بينكم كدعاء بعضكم بعضا قد يعلم الله الذين يتسللون منكم لواذا فليحذر الذين يخالفون عن أمره أن تصيبهم فتنة أو يصيبهم عذاب أليم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2855,18,24,'An-Nūr','النور',359,8,10,64,'أَلَآ إِنَّ لِلَّهِ مَا فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ قَدۡ يَعۡلَمُ مَآ أَنتُمۡ عَلَيۡهِ وَيَوۡمَ يُرۡجَعُونَ إِلَيۡهِ فَيُنَبِّئُهُم بِمَا عَمِلُواْۗ وَٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمُۢ ٦٤','ألا إن لله ما في السموات والأرض قد يعلم ما أنتم عليه ويوم يرجعون إليه فينبئهم بما عملوا والله بكل شيء عليم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2856,18,25,'Al-Furqān','الفُرقَان',359,13,13,1,'تَبَارَكَ ٱلَّذِي نَزَّلَ ٱلۡفُرۡقَانَ عَلَىٰ عَبۡدِهِۦ لِيَكُونَ لِلۡعَٰلَمِينَ نَذِيرًا ١','تبارك الذي نزل الفرقان على عبده ليكون للعالمين نذيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2857,18,25,'Al-Furqān','الفُرقَان',359,14,15,2,'ٱلَّذِي لَهُۥ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَلَمۡ يَتَّخِذۡ وَلَدٗا وَلَمۡ يَكُن لَّهُۥ شَرِيكٞ فِي ٱلۡمُلۡكِ وَخَلَقَ كُلَّ شَيۡءٖ فَقَدَّرَهُۥ تَقۡدِيرٗا ٢','الذي له ملك السموات والأرض ولم يتخذ ولدا ولم يكن له شريك في الملك وخلق كل شيء فقدره تقديرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2858,18,25,'Al-Furqān','الفُرقَان',360,1,3,3,'وَٱتَّخَذُواْ مِن دُونِهِۦٓ ءَالِهَةٗ لَّا يَخۡلُقُونَ شَيۡـٔٗا وَهُمۡ يُخۡلَقُونَ وَلَا يَمۡلِكُونَ لِأَنفُسِهِمۡ ضَرّٗا وَلَا نَفۡعٗا وَلَا يَمۡلِكُونَ مَوۡتٗا وَلَا حَيَوٰةٗ وَلَا نُشُورٗا ٣','واتخذوا من دونه آلهة لا يخلقون شيئا وهم يخلقون ولا يملكون لأنفسهم ضرا ولا نفعا ولا يملكون موتا ولا حياة ولا نشورا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2859,18,25,'Al-Furqān','الفُرقَان',360,3,5,4,'وَقَالَ ٱلَّذِينَ كَفَرُوٓاْ إِنۡ هَٰذَآ إِلَّآ إِفۡكٌ ٱفۡتَرَىٰهُ وَأَعَانَهُۥ عَلَيۡهِ قَوۡمٌ ءَاخَرُونَۖ فَقَدۡ جَآءُو ظُلۡمٗا وَزُورٗا ٤','وقال الذين كفروا إن هذا إلا إفك افتراه وأعانه عليه قوم آخرون فقد جاءوا ظلما وزورا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2860,18,25,'Al-Furqān','الفُرقَان',360,5,6,5,'وَقَالُوٓاْ أَسَٰطِيرُ ٱلۡأَوَّلِينَ ٱكۡتَتَبَهَا فَهِيَ تُمۡلَىٰ عَلَيۡهِ بُكۡرَةٗ وَأَصِيلٗا ٥','وقالوا أساطير الأولين اكتتبها فهي تملى عليه بكرة وأصيلا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2861,18,25,'Al-Furqān','الفُرقَان',360,6,7,6,'قُلۡ أَنزَلَهُ ٱلَّذِي يَعۡلَمُ ٱلسِّرَّ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ إِنَّهُۥ كَانَ غَفُورٗا رَّحِيمٗا ٦','قل أنزله الذي يعلم السر في السموات والأرض إنه كان غفورا رحيما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2862,18,25,'Al-Furqān','الفُرقَان',360,8,9,7,'وَقَالُواْ مَالِ هَٰذَا ٱلرَّسُولِ يَأۡكُلُ ٱلطَّعَامَ وَيَمۡشِي فِي ٱلۡأَسۡوَاقِ لَوۡلَآ أُنزِلَ إِلَيۡهِ مَلَكٞ فَيَكُونَ مَعَهُۥ نَذِيرًا ٧','وقالوا مال هذا الرسول يأكل الطعام ويمشي في الأسواق لولا أنزل إليه ملك فيكون معه نذيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2863,18,25,'Al-Furqān','الفُرقَان',360,10,11,8,'أَوۡ يُلۡقَىٰٓ إِلَيۡهِ كَنزٌ أَوۡ تَكُونُ لَهُۥ جَنَّةٞ يَأۡكُلُ مِنۡهَاۚ وَقَالَ ٱلظَّٰلِمُونَ إِن تَتَّبِعُونَ إِلَّا رَجُلٗا مَّسۡحُورًا ٨','أو يلقى إليه كنز أو تكون له جنة يأكل منها وقال الظالمون إن تتبعون إلا رجلا مسحورا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2864,18,25,'Al-Furqān','الفُرقَان',360,11,13,9,'ٱنظُرۡ كَيۡفَ ضَرَبُواْ لَكَ ٱلۡأَمۡثَٰلَ فَضَلُّواْ فَلَا يَسۡتَطِيعُونَ سَبِيلٗا ٩','انظر كيف ضربوا لك الأمثال فضلوا فلا يستطيعون سبيلا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2865,18,25,'Al-Furqān','الفُرقَان',360,13,14,10,'تَبَارَكَ ٱلَّذِيٓ إِن شَآءَ جَعَلَ لَكَ خَيۡرٗا مِّن ذَٰلِكَ جَنَّٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ وَيَجۡعَل لَّكَ قُصُورَۢا ١٠','تبارك الذي إن شاء جعل لك خيرا من ذلك جنات تجري من تحتها الأنهار ويجعل لك قصورا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2866,18,25,'Al-Furqān','الفُرقَان',360,14,15,11,'بَلۡ كَذَّبُواْ بِٱلسَّاعَةِۖ وَأَعۡتَدۡنَا لِمَن كَذَّبَ بِٱلسَّاعَةِ سَعِيرًا ١١','بل كذبوا بالساعة وأعتدنا لمن كذب بالساعة سعيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2867,18,25,'Al-Furqān','الفُرقَان',361,1,1,12,'إِذَا رَأَتۡهُم مِّن مَّكَانِۭ بَعِيدٖ سَمِعُواْ لَهَا تَغَيُّظٗا وَزَفِيرٗا ١٢','إذا رأتهم من مكان بعيد سمعوا لها تغيظا وزفيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2868,18,25,'Al-Furqān','الفُرقَان',361,2,2,13,'وَإِذَآ أُلۡقُواْ مِنۡهَا مَكَانٗا ضَيِّقٗا مُّقَرَّنِينَ دَعَوۡاْ هُنَالِكَ ثُبُورٗا ١٣','وإذا ألقوا منها مكانا ضيقا مقرنين دعوا هنالك ثبورا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2869,18,25,'Al-Furqān','الفُرقَان',361,3,3,14,'لَّا تَدۡعُواْ ٱلۡيَوۡمَ ثُبُورٗا وَٰحِدٗا وَٱدۡعُواْ ثُبُورٗا كَثِيرٗا ١٤','لا تدعوا اليوم ثبورا واحدا وادعوا ثبورا كثيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2870,18,25,'Al-Furqān','الفُرقَان',361,4,5,15,'قُلۡ أَذَٰلِكَ خَيۡرٌ أَمۡ جَنَّةُ ٱلۡخُلۡدِ ٱلَّتِي وُعِدَ ٱلۡمُتَّقُونَۚ كَانَتۡ لَهُمۡ جَزَآءٗ وَمَصِيرٗا ١٥','قل أذلك خير أم جنة الخلد التي وعد المتقون كانت لهم جزاء ومصيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2871,18,25,'Al-Furqān','الفُرقَان',361,5,6,16,'لَّهُمۡ فِيهَا مَا يَشَآءُونَ خَٰلِدِينَۚ كَانَ عَلَىٰ رَبِّكَ وَعۡدٗا مَّسۡـُٔولٗا ١٦','لهم فيها ما يشاءون خالدين كان على ربك وعدا مسئولا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2872,18,25,'Al-Furqān','الفُرقَان',361,6,8,17,'وَيَوۡمَ يَحۡشُرُهُمۡ وَمَا يَعۡبُدُونَ مِن دُونِ ٱللَّهِ فَيَقُولُ ءَأَنتُمۡ أَضۡلَلۡتُمۡ عِبَادِي هَٰٓؤُلَآءِ أَمۡ هُمۡ ضَلُّواْ ٱلسَّبِيلَ ١٧','ويوم يحشرهم وما يعبدون من دون الله فيقول أأنتم أضللتم عبادي هؤلاء أم هم ضلوا السبيل');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2873,18,25,'Al-Furqān','الفُرقَان',361,8,10,18,'قَالُواْ سُبۡحَٰنَكَ مَا كَانَ يَنۢبَغِي لَنَآ أَن نَّتَّخِذَ مِن دُونِكَ مِنۡ أَوۡلِيَآءَ وَلَٰكِن مَّتَّعۡتَهُمۡ وَءَابَآءَهُمۡ حَتَّىٰ نَسُواْ ٱلذِّكۡرَ وَكَانُواْ قَوۡمَۢا بُورٗا ١٨','قالوا سبحانك ما كان ينبغي لنا أن نتخذ من دونك من أولياء ولكن متعتهم وآباءهم حتى نسوا الذكر وكانوا قوما بورا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2874,18,25,'Al-Furqān','الفُرقَان',361,11,12,19,'فَقَدۡ كَذَّبُوكُم بِمَا تَقُولُونَ فَمَا تَسۡتَطِيعُونَ صَرۡفٗا وَلَا نَصۡرٗاۚ وَمَن يَظۡلِم مِّنكُمۡ نُذِقۡهُ عَذَابٗا كَبِيرٗا ١٩','فقد كذبوكم بما تقولون فما تستطيعون صرفا ولا نصرا ومن يظلم منكم نذقه عذابا كبيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2875,18,25,'Al-Furqān','الفُرقَان',361,13,15,20,'وَمَآ أَرۡسَلۡنَا قَبۡلَكَ مِنَ ٱلۡمُرۡسَلِينَ إِلَّآ إِنَّهُمۡ لَيَأۡكُلُونَ ٱلطَّعَامَ وَيَمۡشُونَ فِي ٱلۡأَسۡوَاقِۗ وَجَعَلۡنَا بَعۡضَكُمۡ لِبَعۡضٖ فِتۡنَةً أَتَصۡبِرُونَۗ وَكَانَ رَبُّكَ بَصِيرٗا ٢٠','وما أرسلنا قبلك من المرسلين إلا إنهم ليأكلون الطعام ويمشون في الأسواق وجعلنا بعضكم لبعض فتنة أتصبرون وكان ربك بصيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2876,19,25,'Al-Furqān','الفُرقَان',362,1,2,21,'۞ وَقَالَ ٱلَّذِينَ لَا يَرۡجُونَ لِقَآءَنَا لَوۡلَآ أُنزِلَ عَلَيۡنَا ٱلۡمَلَٰٓئِكَةُ أَوۡ نَرَىٰ رَبَّنَاۗ لَقَدِ ٱسۡتَكۡبَرُواْ فِيٓ أَنفُسِهِمۡ وَعَتَوۡ عُتُوّٗا كَبِيرٗا ٢١','وقال الذين لا يرجون لقاءنا لولا أنزل علينا الملائكة أو نرى ربنا لقد استكبروا في أنفسهم وعتوا عتوا كبيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2877,19,25,'Al-Furqān','الفُرقَان',362,3,4,22,'يَوۡمَ يَرَوۡنَ ٱلۡمَلَٰٓئِكَةَ لَا بُشۡرَىٰ يَوۡمَئِذٖ لِّلۡمُجۡرِمِينَ وَيَقُولُونَ حِجۡرٗا مَّحۡجُورٗا ٢٢','يوم يرون الملائكة لا بشرى يومئذ للمجرمين ويقولون حجرا محجورا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2878,19,25,'Al-Furqān','الفُرقَان',362,4,5,23,'وَقَدِمۡنَآ إِلَىٰ مَا عَمِلُواْ مِنۡ عَمَلٖ فَجَعَلۡنَٰهُ هَبَآءٗ مَّنثُورًا ٢٣','وقدمنا إلى ما عملوا من عمل فجعلناه هباء منثورا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2879,19,25,'Al-Furqān','الفُرقَان',362,5,6,24,'أَصۡحَٰبُ ٱلۡجَنَّةِ يَوۡمَئِذٍ خَيۡرٞ مُّسۡتَقَرّٗا وَأَحۡسَنُ مَقِيلٗا ٢٤','أصحاب الجنة يومئذ خير مستقرا وأحسن مقيلا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2880,19,25,'Al-Furqān','الفُرقَان',362,6,7,25,'وَيَوۡمَ تَشَقَّقُ ٱلسَّمَآءُ بِٱلۡغَمَٰمِ وَنُزِّلَ ٱلۡمَلَٰٓئِكَةُ تَنزِيلًا ٢٥','ويوم تشقق السماء بالغمام ونزل الملائكة تنزيلا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2881,19,25,'Al-Furqān','الفُرقَان',362,7,8,26,'ٱلۡمُلۡكُ يَوۡمَئِذٍ ٱلۡحَقُّ لِلرَّحۡمَٰنِۚ وَكَانَ يَوۡمًا عَلَى ٱلۡكَٰفِرِينَ عَسِيرٗا ٢٦','الملك يومئذ الحق للرحمن وكان يوما على الكافرين عسيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2882,19,25,'Al-Furqān','الفُرقَان',362,8,9,27,'وَيَوۡمَ يَعَضُّ ٱلظَّالِمُ عَلَىٰ يَدَيۡهِ يَقُولُ يَٰلَيۡتَنِي ٱتَّخَذۡتُ مَعَ ٱلرَّسُولِ سَبِيلٗا ٢٧','ويوم يعض الظالم على يديه يقول ياليتني اتخذت مع الرسول سبيلا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2883,19,25,'Al-Furqān','الفُرقَان',362,9,10,28,'يَٰوَيۡلَتَىٰ لَيۡتَنِي لَمۡ أَتَّخِذۡ فُلَانًا خَلِيلٗا ٢٨','ياويلتى ليتني لم أتخذ فلانا خليلا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2884,19,25,'Al-Furqān','الفُرقَان',362,10,11,29,'لَّقَدۡ أَضَلَّنِي عَنِ ٱلذِّكۡرِ بَعۡدَ إِذۡ جَآءَنِيۗ وَكَانَ ٱلشَّيۡطَٰنُ لِلۡإِنسَٰنِ خَذُولٗا ٢٩','لقد أضلني عن الذكر بعد إذ جاءني وكان الشيطان للإنسان خذولا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2885,19,25,'Al-Furqān','الفُرقَان',362,11,12,30,'وَقَالَ ٱلرَّسُولُ يَٰرَبِّ إِنَّ قَوۡمِي ٱتَّخَذُواْ هَٰذَا ٱلۡقُرۡءَانَ مَهۡجُورٗا ٣٠','وقال الرسول يارب إن قومي اتخذوا هذا القرآن مهجورا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2886,19,25,'Al-Furqān','الفُرقَان',362,12,14,31,'وَكَذَٰلِكَ جَعَلۡنَا لِكُلِّ نَبِيٍّ عَدُوّٗا مِّنَ ٱلۡمُجۡرِمِينَۗ وَكَفَىٰ بِرَبِّكَ هَادِيٗا وَنَصِيرٗا ٣١','وكذلك جعلنا لكل نبي عدوا من المجرمين وكفى بربك هاديا ونصيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2887,19,25,'Al-Furqān','الفُرقَان',362,14,15,32,'وَقَالَ ٱلَّذِينَ كَفَرُواْ لَوۡلَا نُزِّلَ عَلَيۡهِ ٱلۡقُرۡءَانُ جُمۡلَةٗ وَٰحِدَةٗۚ كَذَٰلِكَ لِنُثَبِّتَ بِهِۦ فُؤَادَكَۖ وَرَتَّلۡنَٰهُ تَرۡتِيلٗا ٣٢','وقال الذين كفروا لولا نزل عليه القرآن جملة واحدة كذلك لنثبت به فؤادك ورتلناه ترتيلا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2888,19,25,'Al-Furqān','الفُرقَان',363,1,1,33,'وَلَا يَأۡتُونَكَ بِمَثَلٍ إِلَّا جِئۡنَٰكَ بِٱلۡحَقِّ وَأَحۡسَنَ تَفۡسِيرًا ٣٣','ولا يأتونك بمثل إلا جئناك بالحق وأحسن تفسيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2889,19,25,'Al-Furqān','الفُرقَان',363,2,3,34,'ٱلَّذِينَ يُحۡشَرُونَ عَلَىٰ وُجُوهِهِمۡ إِلَىٰ جَهَنَّمَ أُوْلَٰٓئِكَ شَرّٞ مَّكَانٗا وَأَضَلُّ سَبِيلٗا ٣٤','الذين يحشرون على وجوههم إلى جهنم أولئك شر مكانا وأضل سبيلا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2890,19,25,'Al-Furqān','الفُرقَان',363,3,4,35,'وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ وَجَعَلۡنَا مَعَهُۥٓ أَخَاهُ هَٰرُونَ وَزِيرٗا ٣٥','ولقد آتينا موسى الكتاب وجعلنا معه أخاه هارون وزيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2891,19,25,'Al-Furqān','الفُرقَان',363,4,5,36,'فَقُلۡنَا ٱذۡهَبَآ إِلَى ٱلۡقَوۡمِ ٱلَّذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا فَدَمَّرۡنَٰهُمۡ تَدۡمِيرٗا ٣٦','فقلنا اذهبا إلى القوم الذين كذبوا بآياتنا فدمرناهم تدميرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2892,19,25,'Al-Furqān','الفُرقَان',363,6,7,37,'وَقَوۡمَ نُوحٖ لَّمَّا كَذَّبُواْ ٱلرُّسُلَ أَغۡرَقۡنَٰهُمۡ وَجَعَلۡنَٰهُمۡ لِلنَّاسِ ءَايَةٗۖ وَأَعۡتَدۡنَا لِلظَّٰلِمِينَ عَذَابًا أَلِيمٗا ٣٧','وقوم نوح لما كذبوا الرسل أغرقناهم وجعلناهم للناس آية وأعتدنا للظالمين عذابا أليما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2893,19,25,'Al-Furqān','الفُرقَان',363,7,8,38,'وَعَادٗا وَثَمُودَاْ وَأَصۡحَٰبَ ٱلرَّسِّ وَقُرُونَۢا بَيۡنَ ذَٰلِكَ كَثِيرٗا ٣٨','وعادا وثمود وأصحاب الرس وقرونا بين ذلك كثيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2894,19,25,'Al-Furqān','الفُرقَان',363,8,9,39,'وَكُلّٗا ضَرَبۡنَا لَهُ ٱلۡأَمۡثَٰلَۖ وَكُلّٗا تَبَّرۡنَا تَتۡبِيرٗا ٣٩','وكلا ضربنا له الأمثال وكلا تبرنا تتبيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2895,19,25,'Al-Furqān','الفُرقَان',363,9,11,40,'وَلَقَدۡ أَتَوۡاْ عَلَى ٱلۡقَرۡيَةِ ٱلَّتِيٓ أُمۡطِرَتۡ مَطَرَ ٱلسَّوۡءِۚ أَفَلَمۡ يَكُونُواْ يَرَوۡنَهَاۚ بَلۡ كَانُواْ لَا يَرۡجُونَ نُشُورٗا ٤٠','ولقد أتوا على القرية التي أمطرت مطر السوء أفلم يكونوا يرونها بل كانوا لا يرجون نشورا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2896,19,25,'Al-Furqān','الفُرقَان',363,11,12,41,'وَإِذَا رَأَوۡكَ إِن يَتَّخِذُونَكَ إِلَّا هُزُوًا أَهَٰذَا ٱلَّذِي بَعَثَ ٱللَّهُ رَسُولًا ٤١','وإذا رأوك إن يتخذونك إلا هزوا أهذا الذي بعث الله رسولا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2897,19,25,'Al-Furqān','الفُرقَان',363,12,14,42,'إِن كَادَ لَيُضِلُّنَا عَنۡ ءَالِهَتِنَا لَوۡلَآ أَن صَبَرۡنَا عَلَيۡهَاۚ وَسَوۡفَ يَعۡلَمُونَ حِينَ يَرَوۡنَ ٱلۡعَذَابَ مَنۡ أَضَلُّ سَبِيلًا ٤٢','إن كاد ليضلنا عن آلهتنا لولا أن صبرنا عليها وسوف يعلمون حين يرون العذاب من أضل سبيلا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2898,19,25,'Al-Furqān','الفُرقَان',363,14,15,43,'أَرَءَيۡتَ مَنِ ٱتَّخَذَ إِلَٰهَهُۥ هَوَىٰهُ أَفَأَنتَ تَكُونُ عَلَيۡهِ وَكِيلًا ٤٣','أرأيت من اتخذ إلهه هواه أفأنت تكون عليه وكيلا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2899,19,25,'Al-Furqān','الفُرقَان',364,1,2,44,'أَمۡ تَحۡسَبُ أَنَّ أَكۡثَرَهُمۡ يَسۡمَعُونَ أَوۡ يَعۡقِلُونَۚ إِنۡ هُمۡ إِلَّا كَٱلۡأَنۡعَٰمِ بَلۡ هُمۡ أَضَلُّ سَبِيلًا ٤٤','أم تحسب أن أكثرهم يسمعون أو يعقلون إن هم إلا كالأنعام بل هم أضل سبيلا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2900,19,25,'Al-Furqān','الفُرقَان',364,2,3,45,'أَلَمۡ تَرَ إِلَىٰ رَبِّكَ كَيۡفَ مَدَّ ٱلظِّلَّ وَلَوۡ شَآءَ لَجَعَلَهُۥ سَاكِنٗا ثُمَّ جَعَلۡنَا ٱلشَّمۡسَ عَلَيۡهِ دَلِيلٗا ٤٥','ألم تر إلى ربك كيف مد الظل ولو شاء لجعله ساكنا ثم جعلنا الشمس عليه دليلا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2901,19,25,'Al-Furqān','الفُرقَان',364,4,4,46,'ثُمَّ قَبَضۡنَٰهُ إِلَيۡنَا قَبۡضٗا يَسِيرٗا ٤٦','ثم قبضناه إلينا قبضا يسيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2902,19,25,'Al-Furqān','الفُرقَان',364,4,5,47,'وَهُوَ ٱلَّذِي جَعَلَ لَكُمُ ٱلَّيۡلَ لِبَاسٗا وَٱلنَّوۡمَ سُبَاتٗا وَجَعَلَ ٱلنَّهَارَ نُشُورٗا ٤٧','وهو الذي جعل لكم الليل لباسا والنوم سباتا وجعل النهار نشورا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2903,19,25,'Al-Furqān','الفُرقَان',364,5,7,48,'وَهُوَ ٱلَّذِيٓ أَرۡسَلَ ٱلرِّيَٰحَ بُشۡرَۢا بَيۡنَ يَدَيۡ رَحۡمَتِهِۦۚ وَأَنزَلۡنَا مِنَ ٱلسَّمَآءِ مَآءٗ طَهُورٗا ٤٨','وهو الذي أرسل الرياح بشرا بين يدي رحمته وأنزلنا من السماء ماء طهورا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2904,19,25,'Al-Furqān','الفُرقَان',364,7,8,49,'لِّنُحۡـِۧيَ بِهِۦ بَلۡدَةٗ مَّيۡتٗا وَنُسۡقِيَهُۥ مِمَّا خَلَقۡنَآ أَنۡعَٰمٗا وَأَنَاسِيَّ كَثِيرٗا ٤٩','لنحيي به بلدة ميتا ونسقيه مما خلقنا أنعاما وأناسي كثيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2905,19,25,'Al-Furqān','الفُرقَان',364,8,9,50,'وَلَقَدۡ صَرَّفۡنَٰهُ بَيۡنَهُمۡ لِيَذَّكَّرُواْ فَأَبَىٰٓ أَكۡثَرُ ٱلنَّاسِ إِلَّا كُفُورٗا ٥٠','ولقد صرفناه بينهم ليذكروا فأبى أكثر الناس إلا كفورا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2906,19,25,'Al-Furqān','الفُرقَان',364,9,10,51,'وَلَوۡ شِئۡنَا لَبَعَثۡنَا فِي كُلِّ قَرۡيَةٖ نَّذِيرٗا ٥١','ولو شئنا لبعثنا في كل قرية نذيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2907,19,25,'Al-Furqān','الفُرقَان',364,10,11,52,'فَلَا تُطِعِ ٱلۡكَٰفِرِينَ وَجَٰهِدۡهُم بِهِۦ جِهَادٗا كَبِيرٗا ٥٢','فلا تطع الكافرين وجاهدهم به جهادا كبيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2908,19,25,'Al-Furqān','الفُرقَان',364,11,13,53,'۞ وَهُوَ ٱلَّذِي مَرَجَ ٱلۡبَحۡرَيۡنِ هَٰذَا عَذۡبٞ فُرَاتٞ وَهَٰذَا مِلۡحٌ أُجَاجٞ وَجَعَلَ بَيۡنَهُمَا بَرۡزَخٗا وَحِجۡرٗا مَّحۡجُورٗا ٥٣','وهو الذي مرج البحرين هذا عذب فرات وهذا ملح أجاج وجعل بينهما برزخا وحجرا محجورا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2909,19,25,'Al-Furqān','الفُرقَان',364,13,14,54,'وَهُوَ ٱلَّذِي خَلَقَ مِنَ ٱلۡمَآءِ بَشَرٗا فَجَعَلَهُۥ نَسَبٗا وَصِهۡرٗاۗ وَكَانَ رَبُّكَ قَدِيرٗا ٥٤','وهو الذي خلق من الماء بشرا فجعله نسبا وصهرا وكان ربك قديرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2910,19,25,'Al-Furqān','الفُرقَان',364,14,15,55,'وَيَعۡبُدُونَ مِن دُونِ ٱللَّهِ مَا لَا يَنفَعُهُمۡ وَلَا يَضُرُّهُمۡۗ وَكَانَ ٱلۡكَافِرُ عَلَىٰ رَبِّهِۦ ظَهِيرٗا ٥٥','ويعبدون من دون الله ما لا ينفعهم ولا يضرهم وكان الكافر على ربه ظهيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2911,19,25,'Al-Furqān','الفُرقَان',365,1,1,56,'وَمَآ أَرۡسَلۡنَٰكَ إِلَّا مُبَشِّرٗا وَنَذِيرٗا ٥٦','وما أرسلناك إلا مبشرا ونذيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2912,19,25,'Al-Furqān','الفُرقَان',365,1,2,57,'قُلۡ مَآ أَسۡـَٔلُكُمۡ عَلَيۡهِ مِنۡ أَجۡرٍ إِلَّا مَن شَآءَ أَن يَتَّخِذَ إِلَىٰ رَبِّهِۦ سَبِيلٗا ٥٧','قل ما أسألكم عليه من أجر إلا من شاء أن يتخذ إلى ربه سبيلا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2913,19,25,'Al-Furqān','الفُرقَان',365,2,4,58,'وَتَوَكَّلۡ عَلَى ٱلۡحَيِّ ٱلَّذِي لَا يَمُوتُ وَسَبِّحۡ بِحَمۡدِهِۦۚ وَكَفَىٰ بِهِۦ بِذُنُوبِ عِبَادِهِۦ خَبِيرًا ٥٨','وتوكل على الحي الذي لا يموت وسبح بحمده وكفى به بذنوب عباده خبيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2914,19,25,'Al-Furqān','الفُرقَان',365,4,6,59,'ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَا فِي سِتَّةِ أَيَّامٖ ثُمَّ ٱسۡتَوَىٰ عَلَى ٱلۡعَرۡشِۖ ٱلرَّحۡمَٰنُ فَسۡـَٔلۡ بِهِۦ خَبِيرٗا ٥٩','الذي خلق السموات والأرض وما بينهما في ستة أيام ثم استوى على العرش الرحمن فاسأل به خبيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2915,19,25,'Al-Furqān','الفُرقَان',365,6,7,60,'وَإِذَا قِيلَ لَهُمُ ٱسۡجُدُواْۤ لِلرَّحۡمَٰنِ قَالُواْ وَمَا ٱلرَّحۡمَٰنُ أَنَسۡجُدُ لِمَا تَأۡمُرُنَا وَزَادَهُمۡ نُفُورٗا۩ ٦٠','وإذا قيل لهم اسجدوا للرحمن قالوا وما الرحمن أنسجد لما تأمرنا وزادهم نفورا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2916,19,25,'Al-Furqān','الفُرقَان',365,7,9,61,'تَبَارَكَ ٱلَّذِي جَعَلَ فِي ٱلسَّمَآءِ بُرُوجٗا وَجَعَلَ فِيهَا سِرَٰجٗا وَقَمَرٗا مُّنِيرٗا ٦١','تبارك الذي جعل في السماء بروجا وجعل فيها سراجا وقمرا منيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2917,19,25,'Al-Furqān','الفُرقَان',365,9,10,62,'وَهُوَ ٱلَّذِي جَعَلَ ٱلَّيۡلَ وَٱلنَّهَارَ خِلۡفَةٗ لِّمَنۡ أَرَادَ أَن يَذَّكَّرَ أَوۡ أَرَادَ شُكُورٗا ٦٢','وهو الذي جعل الليل والنهار خلفة لمن أراد أن يذكر أو أراد شكورا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2918,19,25,'Al-Furqān','الفُرقَان',365,10,11,63,'وَعِبَادُ ٱلرَّحۡمَٰنِ ٱلَّذِينَ يَمۡشُونَ عَلَى ٱلۡأَرۡضِ هَوۡنٗا وَإِذَا خَاطَبَهُمُ ٱلۡجَٰهِلُونَ قَالُواْ سَلَٰمٗا ٦٣','وعباد الرحمن الذين يمشون على الأرض هونا وإذا خاطبهم الجاهلون قالوا سلاما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2919,19,25,'Al-Furqān','الفُرقَان',365,12,12,64,'وَٱلَّذِينَ يَبِيتُونَ لِرَبِّهِمۡ سُجَّدٗا وَقِيَٰمٗا ٦٤','والذين يبيتون لربهم سجدا وقياما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2920,19,25,'Al-Furqān','الفُرقَان',365,12,14,65,'وَٱلَّذِينَ يَقُولُونَ رَبَّنَا ٱصۡرِفۡ عَنَّا عَذَابَ جَهَنَّمَۖ إِنَّ عَذَابَهَا كَانَ غَرَامًا ٦٥','والذين يقولون ربنا اصرف عنا عذاب جهنم إن عذابها كان غراما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2921,19,25,'Al-Furqān','الفُرقَان',365,14,14,66,'إِنَّهَا سَآءَتۡ مُسۡتَقَرّٗا وَمُقَامٗا ٦٦','إنها ساءت مستقرا ومقاما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2922,19,25,'Al-Furqān','الفُرقَان',365,14,15,67,'وَٱلَّذِينَ إِذَآ أَنفَقُواْ لَمۡ يُسۡرِفُواْ وَلَمۡ يَقۡتُرُواْ وَكَانَ بَيۡنَ ذَٰلِكَ قَوَامٗا ٦٧','والذين إذا أنفقوا لم يسرفوا ولم يقتروا وكان بين ذلك قواما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2923,19,25,'Al-Furqān','الفُرقَان',366,1,3,68,'وَٱلَّذِينَ لَا يَدۡعُونَ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ وَلَا يَقۡتُلُونَ ٱلنَّفۡسَ ٱلَّتِي حَرَّمَ ٱللَّهُ إِلَّا بِٱلۡحَقِّ وَلَا يَزۡنُونَۚ وَمَن يَفۡعَلۡ ذَٰلِكَ يَلۡقَ أَثَامٗا ٦٨','والذين لا يدعون مع الله إلها آخر ولا يقتلون النفس التي حرم الله إلا بالحق ولا يزنون ومن يفعل ذلك يلق أثاما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2924,19,25,'Al-Furqān','الفُرقَان',366,3,4,69,'يُضَٰعَفۡ لَهُ ٱلۡعَذَابُ يَوۡمَ ٱلۡقِيَٰمَةِ وَيَخۡلُدۡ فِيهِۦ مُهَانًا ٦٩','يضاعف له العذاب يوم القيامة ويخلد فيه مهانا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2925,19,25,'Al-Furqān','الفُرقَان',366,4,6,70,'إِلَّا مَن تَابَ وَءَامَنَ وَعَمِلَ عَمَلٗا صَٰلِحٗا فَأُوْلَٰٓئِكَ يُبَدِّلُ ٱللَّهُ سَيِّـَٔاتِهِمۡ حَسَنَٰتٖۗ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمٗا ٧٠','إلا من تاب وآمن وعمل عملا صالحا فأولئك يبدل الله سيئاتهم حسنات وكان الله غفورا رحيما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2926,19,25,'Al-Furqān','الفُرقَان',366,7,7,71,'وَمَن تَابَ وَعَمِلَ صَٰلِحٗا فَإِنَّهُۥ يَتُوبُ إِلَى ٱللَّهِ مَتَابٗا ٧١','ومن تاب وعمل صالحا فإنه يتوب إلى الله متابا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2927,19,25,'Al-Furqān','الفُرقَان',366,8,8,72,'وَٱلَّذِينَ لَا يَشۡهَدُونَ ٱلزُّورَ وَإِذَا مَرُّواْ بِٱللَّغۡوِ مَرُّواْ كِرَامٗا ٧٢','والذين لا يشهدون الزور وإذا مروا باللغو مروا كراما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2928,19,25,'Al-Furqān','الفُرقَان',366,9,10,73,'وَٱلَّذِينَ إِذَا ذُكِّرُواْ بِـَٔايَٰتِ رَبِّهِمۡ لَمۡ يَخِرُّواْ عَلَيۡهَا صُمّٗا وَعُمۡيَانٗا ٧٣','والذين إذا ذكروا بآيات ربهم لم يخروا عليها صما وعميانا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2929,19,25,'Al-Furqān','الفُرقَان',366,10,12,74,'وَٱلَّذِينَ يَقُولُونَ رَبَّنَا هَبۡ لَنَا مِنۡ أَزۡوَٰجِنَا وَذُرِّيَّٰتِنَا قُرَّةَ أَعۡيُنٖ وَٱجۡعَلۡنَا لِلۡمُتَّقِينَ إِمَامًا ٧٤','والذين يقولون ربنا هب لنا من أزواجنا وذرياتنا قرة أعين واجعلنا للمتقين إماما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2930,19,25,'Al-Furqān','الفُرقَان',366,12,13,75,'أُوْلَٰٓئِكَ يُجۡزَوۡنَ ٱلۡغُرۡفَةَ بِمَا صَبَرُواْ وَيُلَقَّوۡنَ فِيهَا تَحِيَّةٗ وَسَلَٰمًا ٧٥','أولئك يجزون الغرفة بما صبروا ويلقون فيها تحية وسلاما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2931,19,25,'Al-Furqān','الفُرقَان',366,13,14,76,'خَٰلِدِينَ فِيهَاۚ حَسُنَتۡ مُسۡتَقَرّٗا وَمُقَامٗا ٧٦','خالدين فيها حسنت مستقرا ومقاما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2932,19,25,'Al-Furqān','الفُرقَان',366,14,15,77,'قُلۡ مَا يَعۡبَؤُاْ بِكُمۡ رَبِّي لَوۡلَا دُعَآؤُكُمۡۖ فَقَدۡ كَذَّبۡتُمۡ فَسَوۡفَ يَكُونُ لِزَامَۢا ٧٧','قل ما يعبأ بكم ربي لولا دعاؤكم فقد كذبتم فسوف يكون لزاما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2933,19,26,'Ash-Shu‘arā’','الشعراء',367,3,3,1,'طسٓمٓ ١','طسم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2934,19,26,'Ash-Shu‘arā’','الشعراء',367,3,3,2,'تِلۡكَ ءَايَٰتُ ٱلۡكِتَٰبِ ٱلۡمُبِينِ ٢','تلك آيات الكتاب المبين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2935,19,26,'Ash-Shu‘arā’','الشعراء',367,3,4,3,'لَعَلَّكَ بَٰخِعٞ نَّفۡسَكَ أَلَّا يَكُونُواْ مُؤۡمِنِينَ ٣','لعلك باخع نفسك ألا يكونوا مؤمنين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2936,19,26,'Ash-Shu‘arā’','الشعراء',367,4,5,4,'إِن نَّشَأۡ نُنَزِّلۡ عَلَيۡهِم مِّنَ ٱلسَّمَآءِ ءَايَةٗ فَظَلَّتۡ أَعۡنَٰقُهُمۡ لَهَا خَٰضِعِينَ ٤','إن نشأ ننزل عليهم من السماء آية فظلت أعناقهم لها خاضعين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2937,19,26,'Ash-Shu‘arā’','الشعراء',367,5,6,5,'وَمَا يَأۡتِيهِم مِّن ذِكۡرٖ مِّنَ ٱلرَّحۡمَٰنِ مُحۡدَثٍ إِلَّا كَانُواْ عَنۡهُ مُعۡرِضِينَ ٥','وما يأتيهم من ذكر من الرحمن محدث إلا كانوا عنه معرضين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2938,19,26,'Ash-Shu‘arā’','الشعراء',367,6,7,6,'فَقَدۡ كَذَّبُواْ فَسَيَأۡتِيهِمۡ أَنۢبَٰٓؤُاْ مَا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ ٦','فقد كذبوا فسيأتيهم أنباء ما كانوا به يستهزئون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2939,19,26,'Ash-Shu‘arā’','الشعراء',367,7,7,7,'أَوَلَمۡ يَرَوۡاْ إِلَى ٱلۡأَرۡضِ كَمۡ أَنۢبَتۡنَا فِيهَا مِن كُلِّ زَوۡجٖ كَرِيمٍ ٧','أو لم يروا إلى الأرض كم أنبتنا فيها من كل زوج كريم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2940,19,26,'Ash-Shu‘arā’','الشعراء',367,8,8,8,'إِنَّ فِي ذَٰلِكَ لَأٓيَةٗۖ وَمَا كَانَ أَكۡثَرُهُم مُّؤۡمِنِينَ ٨','إن في ذلك لآية وما كان أكثرهم مؤمنين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2941,19,26,'Ash-Shu‘arā’','الشعراء',367,8,9,9,'وَإِنَّ رَبَّكَ لَهُوَ ٱلۡعَزِيزُ ٱلرَّحِيمُ ٩','وإن ربك لهو العزيز الرحيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2942,19,26,'Ash-Shu‘arā’','الشعراء',367,9,9,10,'وَإِذۡ نَادَىٰ رَبُّكَ مُوسَىٰٓ أَنِ ٱئۡتِ ٱلۡقَوۡمَ ٱلظَّٰلِمِينَ ١٠','وإذ نادى ربك موسى أن ائت القوم الظالمين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2943,19,26,'Ash-Shu‘arā’','الشعراء',367,9,10,11,'قَوۡمَ فِرۡعَوۡنَۚ أَلَا يَتَّقُونَ ١١','قوم فرعون ألا يتقون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2944,19,26,'Ash-Shu‘arā’','الشعراء',367,10,10,12,'قَالَ رَبِّ إِنِّيٓ أَخَافُ أَن يُكَذِّبُونِ ١٢','قال رب إني أخاف أن يكذبون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2945,19,26,'Ash-Shu‘arā’','الشعراء',367,10,11,13,'وَيَضِيقُ صَدۡرِي وَلَا يَنطَلِقُ لِسَانِي فَأَرۡسِلۡ إِلَىٰ هَٰرُونَ ١٣','ويضيق صدري ولا ينطلق لساني فأرسل إلى هارون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2946,19,26,'Ash-Shu‘arā’','الشعراء',367,11,12,14,'وَلَهُمۡ عَلَيَّ ذَنۢبٞ فَأَخَافُ أَن يَقۡتُلُونِ ١٤','ولهم علي ذنب فأخاف أن يقتلون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2947,19,26,'Ash-Shu‘arā’','الشعراء',367,12,12,15,'قَالَ كـَلَّاۖ فَٱذۡهَبَا بِـَٔايَٰتِنَآۖ إِنَّا مَعَكُم مُّسۡتَمِعُونَ ١٥','قال كلا فاذهبا بآياتنا إنا معكم مستمعون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2948,19,26,'Ash-Shu‘arā’','الشعراء',367,13,13,16,'فَأۡتِيَا فِرۡعَوۡنَ فَقُولَآ إِنَّا رَسُولُ رَبِّ ٱلۡعَٰلَمِينَ ١٦','فأتيا فرعون فقولا إنا رسول رب العالمين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2949,19,26,'Ash-Shu‘arā’','الشعراء',367,13,14,17,'أَنۡ أَرۡسِلۡ مَعَنَا بَنِيٓ إِسۡرَٰٓءِيلَ ١٧','أن أرسل معنا بني إسرائيل');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2950,19,26,'Ash-Shu‘arā’','الشعراء',367,14,15,18,'قَالَ أَلَمۡ نُرَبِّكَ فِينَا وَلِيدٗا وَلَبِثۡتَ فِينَا مِنۡ عُمُرِكَ سِنِينَ ١٨','قال ألم نربك فينا وليدا ولبثت فينا من عمرك سنين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2951,19,26,'Ash-Shu‘arā’','الشعراء',367,15,15,19,'وَفَعَلۡتَ فَعۡلَتَكَ ٱلَّتِي فَعَلۡتَ وَأَنتَ مِنَ ٱلۡكَٰفِرِينَ ١٩','وفعلت فعلتك التي فعلت وأنت من الكافرين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2952,19,26,'Ash-Shu‘arā’','الشعراء',368,1,1,20,'قَالَ فَعَلۡتُهَآ إِذٗا وَأَنَا۠ مِنَ ٱلضَّآلِّينَ ٢٠','قال فعلتها إذا وأنا من الضالين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2953,19,26,'Ash-Shu‘arā’','الشعراء',368,1,2,21,'فَفَرَرۡتُ مِنكُمۡ لَمَّا خِفۡتُكُمۡ فَوَهَبَ لِي رَبِّي حُكۡمٗا وَجَعَلَنِي مِنَ ٱلۡمُرۡسَلِينَ ٢١','ففررت منكم لما خفتكم فوهب لي ربي حكما وجعلني من المرسلين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2954,19,26,'Ash-Shu‘arā’','الشعراء',368,2,3,22,'وَتِلۡكَ نِعۡمَةٞ تَمُنُّهَا عَلَيَّ أَنۡ عَبَّدتَّ بَنِيٓ إِسۡرَٰٓءِيلَ ٢٢','وتلك نعمة تمنها علي أن عبدت بني إسرائيل');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (2955,19,26,'Ash-Shu‘arā’','الشعراء',368,3,3,23,'قَالَ فِرۡعَوۡنُ وَمَا رَبُّ ٱلۡعَٰلَمِينَ ٢٣','قال فرعون وما رب العالمين');
