INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4475,25,45,'Al-Jāthiyah','الجاثِية',499,3,3,2,'تَنزِيلُ ٱلۡكِتَٰبِ مِنَ ٱللَّهِ ٱلۡعَزِيزِ ٱلۡحَكِيمِ ٢','تنزيل الكتاب من الله العزيز الحكيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4476,25,45,'Al-Jāthiyah','الجاثِية',499,3,4,3,'إِنَّ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ لَأٓيَٰتٖ لِّلۡمُؤۡمِنِينَ ٣','إن في السموات والأرض لآيات للمؤمنين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4477,25,45,'Al-Jāthiyah','الجاثِية',499,4,5,4,'وَفِي خَلۡقِكُمۡ وَمَا يَبُثُّ مِن دَآبَّةٍ ءَايَٰتٞ لِّقَوۡمٖ يُوقِنُونَ ٤','وفي خلقكم وما يبث من دابة آيات لقوم يوقنون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4478,25,45,'Al-Jāthiyah','الجاثِية',499,5,6,5,'وَٱخۡتِلَٰفِ ٱلَّيۡلِ وَٱلنَّهَارِ وَمَآ أَنزَلَ ٱللَّهُ مِنَ ٱلسَّمَآءِ مِن رِّزۡقٖ فَأَحۡيَا بِهِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَا وَتَصۡرِيفِ ٱلرِّيَٰحِ ءَايَٰتٞ لِّقَوۡمٖ يَعۡقِلُونَ ٥','واختلاف الليل والنهار وما أنزل الله من السماء من رزق فأحيا به الأرض بعد موتها وتصريف الرياح آيات لقوم يعقلون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4479,25,45,'Al-Jāthiyah','الجاثِية',499,7,8,6,'تِلۡكَ ءَايَٰتُ ٱللَّهِ نَتۡلُوهَا عَلَيۡكَ بِٱلۡحَقِّۖ فَبِأَيِّ حَدِيثِۭ بَعۡدَ ٱللَّهِ وَءَايَٰتِهِۦ يُؤۡمِنُونَ ٦','تلك آيات الله نتلوها عليك بالحق فبأي حديث بعد الله وآياته يؤمنون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4480,25,45,'Al-Jāthiyah','الجاثِية',499,8,8,7,'وَيۡلٞ لِّكُلِّ أَفَّاكٍ أَثِيمٖ ٧','ويل لكل أفاك أثيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4481,25,45,'Al-Jāthiyah','الجاثِية',499,8,9,8,'يَسۡمَعُ ءَايَٰتِ ٱللَّهِ تُتۡلَىٰ عَلَيۡهِ ثُمَّ يُصِرُّ مُسۡتَكۡبِرٗا كَأَن لَّمۡ يَسۡمَعۡهَاۖ فَبَشِّرۡهُ بِعَذَابٍ أَلِيمٖ ٨','يسمع آيات الله تتلى عليه ثم يصر مستكبرا كأن لم يسمعها فبشره بعذاب أليم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4482,25,45,'Al-Jāthiyah','الجاثِية',499,9,10,9,'وَإِذَا عَلِمَ مِنۡ ءَايَٰتِنَا شَيۡـًٔا ٱتَّخَذَهَا هُزُوًاۚ أُوْلَٰٓئِكَ لَهُمۡ عَذَابٞ مُّهِينٞ ٩','وإذا علم من آياتنا شيئا اتخذها هزوا أولئك لهم عذاب مهين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4483,25,45,'Al-Jāthiyah','الجاثِية',499,10,12,10,'مِّن وَرَآئِهِمۡ جَهَنَّمُۖ وَلَا يُغۡنِي عَنۡهُم مَّا كَسَبُواْ شَيۡـٔٗا وَلَا مَا ٱتَّخَذُواْ مِن دُونِ ٱللَّهِ أَوۡلِيَآءَۖ وَلَهُمۡ عَذَابٌ عَظِيمٌ ١٠','من ورائهم جهنم ولا يغني عنهم ما كسبوا شيئا ولا ما اتخذوا من دون الله أولياء ولهم عذاب عظيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4484,25,45,'Al-Jāthiyah','الجاثِية',499,12,13,11,'هَٰذَا هُدٗىۖ وَٱلَّذِينَ كَفَرُواْ بِـَٔايَٰتِ رَبِّهِمۡ لَهُمۡ عَذَابٞ مِّن رِّجۡزٍ أَلِيمٌ ١١','هذا هدى والذين كفروا بآيات ربهم لهم عذاب من رجز أليم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4485,25,45,'Al-Jāthiyah','الجاثِية',499,13,14,12,'۞ ٱللَّهُ ٱلَّذِي سَخَّرَ لَكُمُ ٱلۡبَحۡرَ لِتَجۡرِيَ ٱلۡفُلۡكُ فِيهِ بِأَمۡرِهِۦ وَلِتَبۡتَغُواْ مِن فَضۡلِهِۦ وَلَعَلَّكُمۡ تَشۡكُرُونَ ١٢','الله الذي سخر لكم البحر لتجري الفلك فيه بأمره ولتبتغوا من فضله ولعلكم تشكرون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4486,25,45,'Al-Jāthiyah','الجاثِية',499,14,15,13,'وَسَخَّرَ لَكُم مَّا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ جَمِيعٗا مِّنۡهُۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَتَفَكَّرُونَ ١٣','وسخر لكم ما في السموات وما في الأرض جميعا منه إن في ذلك لآيات لقوم يتفكرون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4487,25,45,'Al-Jāthiyah','الجاثِية',500,1,2,14,'قُل لِّلَّذِينَ ءَامَنُواْ يَغۡفِرُواْ لِلَّذِينَ لَا يَرۡجُونَ أَيَّامَ ٱللَّهِ لِيَجۡزِيَ قَوۡمَۢا بِمَا كَانُواْ يَكۡسِبُونَ ١٤','قل للذين آمنوا يغفروا للذين لا يرجون أيام الله ليجزي قوما بما كانوا يكسبون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4488,25,45,'Al-Jāthiyah','الجاثِية',500,2,3,15,'مَنۡ عَمِلَ صَٰلِحٗا فَلِنَفۡسِهِۦۖ وَمَنۡ أَسَآءَ فَعَلَيۡهَاۖ ثُمَّ إِلَىٰ رَبِّكُمۡ تُرۡجَعُونَ ١٥','من عمل صالحا فلنفسه ومن أساء فعليها ثم إلى ربكم ترجعون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4489,25,45,'Al-Jāthiyah','الجاثِية',500,3,5,16,'وَلَقَدۡ ءَاتَيۡنَا بَنِيٓ إِسۡرَٰٓءِيلَ ٱلۡكِتَٰبَ وَٱلۡحُكۡمَ وَٱلنُّبُوَّةَ وَرَزَقۡنَٰهُم مِّنَ ٱلطَّيِّبَٰتِ وَفَضَّلۡنَٰهُمۡ عَلَى ٱلۡعَٰلَمِينَ ١٦','ولقد آتينا بني إسرائيل الكتاب والحكم والنبوة ورزقناهم من الطيبات وفضلناهم على العالمين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4490,25,45,'Al-Jāthiyah','الجاثِية',500,5,7,17,'وَءَاتَيۡنَٰهُم بَيِّنَٰتٖ مِّنَ ٱلۡأَمۡرِۖ فَمَا ٱخۡتَلَفُوٓاْ إِلَّا مِنۢ بَعۡدِ مَا جَآءَهُمُ ٱلۡعِلۡمُ بَغۡيَۢا بَيۡنَهُمۡۚ إِنَّ رَبَّكَ يَقۡضِي بَيۡنَهُمۡ يَوۡمَ ٱلۡقِيَٰمَةِ فِيمَا كَانُواْ فِيهِ يَخۡتَلِفُونَ ١٧','وآتيناهم بينات من الأمر فما اختلفوا إلا من بعد ما جاءهم العلم بغيا بينهم إن ربك يقضي بينهم يوم القيامة فيما كانوا فيه يختلفون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4491,25,45,'Al-Jāthiyah','الجاثِية',500,8,9,18,'ثُمَّ جَعَلۡنَٰكَ عَلَىٰ شَرِيعَةٖ مِّنَ ٱلۡأَمۡرِ فَٱتَّبِعۡهَا وَلَا تَتَّبِعۡ أَهۡوَآءَ ٱلَّذِينَ لَا يَعۡلَمُونَ ١٨','ثم جعلناك على شريعة من الأمر فاتبعها ولا تتبع أهواء الذين لا يعلمون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4492,25,45,'Al-Jāthiyah','الجاثِية',500,9,10,19,'إِنَّهُمۡ لَن يُغۡنُواْ عَنكَ مِنَ ٱللَّهِ شَيۡـٔٗاۚ وَإِنَّ ٱلظَّٰلِمِينَ بَعۡضُهُمۡ أَوۡلِيَآءُ بَعۡضٖۖ وَٱللَّهُ وَلِيُّ ٱلۡمُتَّقِينَ ١٩','إنهم لن يغنوا عنك من الله شيئا وإن الظالمين بعضهم أولياء بعض والله ولي المتقين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4493,25,45,'Al-Jāthiyah','الجاثِية',500,11,11,20,'هَٰذَا بَصَٰٓئِرُ لِلنَّاسِ وَهُدٗى وَرَحۡمَةٞ لِّقَوۡمٖ يُوقِنُونَ ٢٠','هذا بصائر للناس وهدى ورحمة لقوم يوقنون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4494,25,45,'Al-Jāthiyah','الجاثِية',500,12,14,21,'أَمۡ حَسِبَ ٱلَّذِينَ ٱجۡتَرَحُواْ ٱلسَّيِّـَٔاتِ أَن نَّجۡعَلَهُمۡ كَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّٰلِحَٰتِ سَوَآءٗ مَّحۡيَاهُمۡ وَمَمَاتُهُمۡۚ سَآءَ مَا يَحۡكُمُونَ ٢١','أم حسب الذين اجترحوا السيئات أن نجعلهم كالذين آمنوا وعملوا الصالحات سواء محياهم ومماتهم ساء ما يحكمون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4495,25,45,'Al-Jāthiyah','الجاثِية',500,14,15,22,'وَخَلَقَ ٱللَّهُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ بِٱلۡحَقِّ وَلِتُجۡزَىٰ كُلُّ نَفۡسِۭ بِمَا كَسَبَتۡ وَهُمۡ لَا يُظۡلَمُونَ ٢٢','وخلق الله السموات والأرض بالحق ولتجزى كل نفس بما كسبت وهم لا يظلمون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4496,25,45,'Al-Jāthiyah','الجاثِية',501,1,3,23,'أَفَرَءَيۡتَ مَنِ ٱتَّخَذَ إِلَٰهَهُۥ هَوَىٰهُ وَأَضَلَّهُ ٱللَّهُ عَلَىٰ عِلۡمٖ وَخَتَمَ عَلَىٰ سَمۡعِهِۦ وَقَلۡبِهِۦ وَجَعَلَ عَلَىٰ بَصَرِهِۦ غِشَٰوَةٗ فَمَن يَهۡدِيهِ مِنۢ بَعۡدِ ٱللَّهِۚ أَفَلَا تَذَكَّرُونَ ٢٣','أفرأيت من اتخذ إلهه هواه وأضله الله على علم وختم على سمعه وقلبه وجعل على بصره غشاوة فمن يهديه من بعد الله أفلا تذكرون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4497,25,45,'Al-Jāthiyah','الجاثِية',501,3,4,24,'وَقَالُواْ مَا هِيَ إِلَّا حَيَاتُنَا ٱلدُّنۡيَا نَمُوتُ وَنَحۡيَا وَمَا يُهۡلِكُنَآ إِلَّا ٱلدَّهۡرُۚ وَمَا لَهُم بِذَٰلِكَ مِنۡ عِلۡمٍۖ إِنۡ هُمۡ إِلَّا يَظُنُّونَ ٢٤','وقالوا ما هي إلا حياتنا الدنيا نموت ونحيا وما يهلكنا إلا الدهر وما لهم بذلك من علم إن هم إلا يظنون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4498,25,45,'Al-Jāthiyah','الجاثِية',501,4,6,25,'وَإِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَٰتُنَا بَيِّنَٰتٖ مَّا كَانَ حُجَّتَهُمۡ إِلَّآ أَن قَالُواْ ٱئۡتُواْ بِـَٔابَآئِنَآ إِن كُنتُمۡ صَٰدِقِينَ ٢٥','وإذا تتلى عليهم آياتنا بينات ما كان حجتهم إلا أن قالوا ائتوا بآبائنا إن كنتم صادقين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4499,25,45,'Al-Jāthiyah','الجاثِية',501,6,7,26,'قُلِ ٱللَّهُ يُحۡيِيكُمۡ ثُمَّ يُمِيتُكُمۡ ثُمَّ يَجۡمَعُكُمۡ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ لَا رَيۡبَ فِيهِ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ ٢٦','قل الله يحييكم ثم يميتكم ثم يجمعكم إلى يوم القيامة لا ريب فيه ولكن أكثر الناس لا يعلمون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4500,25,45,'Al-Jāthiyah','الجاثِية',501,7,8,27,'وَلِلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَيَوۡمَ تَقُومُ ٱلسَّاعَةُ يَوۡمَئِذٖ يَخۡسَرُ ٱلۡمُبۡطِلُونَ ٢٧','ولله ملك السموات والأرض ويوم تقوم الساعة يومئذ يخسر المبطلون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4501,25,45,'Al-Jāthiyah','الجاثِية',501,9,10,28,'وَتَرَىٰ كُلَّ أُمَّةٖ جَاثِيَةٗۚ كُلُّ أُمَّةٖ تُدۡعَىٰٓ إِلَىٰ كِتَٰبِهَا ٱلۡيَوۡمَ تُجۡزَوۡنَ مَا كُنتُمۡ تَعۡمَلُونَ ٢٨','وترى كل أمة جاثية كل أمة تدعى إلى كتابها اليوم تجزون ما كنتم تعملون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4502,25,45,'Al-Jāthiyah','الجاثِية',501,10,11,29,'هَٰذَا كِتَٰبُنَا يَنطِقُ عَلَيۡكُم بِٱلۡحَقِّۚ إِنَّا كُنَّا نَسۡتَنسِخُ مَا كُنتُمۡ تَعۡمَلُونَ ٢٩','هذا كتابنا ينطق عليكم بالحق إنا كنا نستنسخ ما كنتم تعملون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4503,25,45,'Al-Jāthiyah','الجاثِية',501,11,12,30,'فَأَمَّا ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّٰلِحَٰتِ فَيُدۡخِلُهُمۡ رَبُّهُمۡ فِي رَحۡمَتِهِۦۚ ذَٰلِكَ هُوَ ٱلۡفَوۡزُ ٱلۡمُبِينُ ٣٠','فأما الذين آمنوا وعملوا الصالحات فيدخلهم ربهم في رحمته ذلك هو الفوز المبين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4504,25,45,'Al-Jāthiyah','الجاثِية',501,12,14,31,'وَأَمَّا ٱلَّذِينَ كَفَرُوٓاْ أَفَلَمۡ تَكُنۡ ءَايَٰتِي تُتۡلَىٰ عَلَيۡكُمۡ فَٱسۡتَكۡبَرۡتُمۡ وَكُنتُمۡ قَوۡمٗا مُّجۡرِمِينَ ٣١','وأما الذين كفروا أفلم تكن آياتي تتلى عليكم فاستكبرتم وكنتم قوما مجرمين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4505,25,45,'Al-Jāthiyah','الجاثِية',501,14,15,32,'وَإِذَا قِيلَ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞ وَٱلسَّاعَةُ لَا رَيۡبَ فِيهَا قُلۡتُم مَّا نَدۡرِي مَا ٱلسَّاعَةُ إِن نَّظُنُّ إِلَّا ظَنّٗا وَمَا نَحۡنُ بِمُسۡتَيۡقِنِينَ ٣٢','وإذا قيل إن وعد الله حق والساعة لا ريب فيها قلتم ما ندري ما الساعة إن نظن إلا ظنا وما نحن بمستيقنين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4506,25,45,'Al-Jāthiyah','الجاثِية',502,1,1,33,'وَبَدَا لَهُمۡ سَيِّـَٔاتُ مَا عَمِلُواْ وَحَاقَ بِهِم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ ٣٣','وبدا لهم سيئات ما عملوا وحاق بهم ما كانوا به يستهزئون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4507,25,45,'Al-Jāthiyah','الجاثِية',502,2,3,34,'وَقِيلَ ٱلۡيَوۡمَ نَنسَىٰكُمۡ كَمَا نَسِيتُمۡ لِقَآءَ يَوۡمِكُمۡ هَٰذَا وَمَأۡوَىٰكُمُ ٱلنَّارُ وَمَا لَكُم مِّن نَّٰصِرِينَ ٣٤','وقيل اليوم ننساكم كما نسيتم لقاء يومكم هذا ومأواكم النار وما لكم من ناصرين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4508,25,45,'Al-Jāthiyah','الجاثِية',502,3,4,35,'ذَٰلِكُم بِأَنَّكُمُ ٱتَّخَذۡتُمۡ ءَايَٰتِ ٱللَّهِ هُزُوٗا وَغَرَّتۡكُمُ ٱلۡحَيَوٰةُ ٱلدُّنۡيَاۚ فَٱلۡيَوۡمَ لَا يُخۡرَجُونَ مِنۡهَا وَلَا هُمۡ يُسۡتَعۡتَبُونَ ٣٥','ذلكم بأنكم اتخذتم آيات الله هزوا وغرتكم الحياة الدنيا فاليوم لا يخرجون منها ولا هم يستعتبون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4509,25,45,'Al-Jāthiyah','الجاثِية',502,5,5,36,'فَلِلَّهِ ٱلۡحَمۡدُ رَبِّ ٱلسَّمَٰوَٰتِ وَرَبِّ ٱلۡأَرۡضِ رَبِّ ٱلۡعَٰلَمِينَ ٣٦','فلله الحمد رب السموات ورب الأرض رب العالمين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4510,25,45,'Al-Jāthiyah','الجاثِية',502,6,6,37,'وَلَهُ ٱلۡكِبۡرِيَآءُ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ ٣٧','وله الكبرياء في السموات والأرض وهو العزيز الحكيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4511,26,46,'Al-Aḥqāf','الأحقَاف',502,9,9,1,'حمٓ ١','حم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4512,26,46,'Al-Aḥqāf','الأحقَاف',502,9,9,2,'تَنزِيلُ ٱلۡكِتَٰبِ مِنَ ٱللَّهِ ٱلۡعَزِيزِ ٱلۡحَكِيمِ ٢','تنزيل الكتاب من الله العزيز الحكيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4513,26,46,'Al-Aḥqāf','الأحقَاف',502,9,11,3,'مَا خَلَقۡنَا ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَمَا بَيۡنَهُمَآ إِلَّا بِٱلۡحَقِّ وَأَجَلٖ مُّسَمّٗىۚ وَٱلَّذِينَ كَفَرُواْ عَمَّآ أُنذِرُواْ مُعۡرِضُونَ ٣','ما خلقنا السموات والأرض وما بينهما إلا بالحق وأجل مسمى والذين كفروا عما أنذروا معرضون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4514,26,46,'Al-Aḥqāf','الأحقَاف',502,11,14,4,'قُلۡ أَرَءَيۡتُم مَّا تَدۡعُونَ مِن دُونِ ٱللَّهِ أَرُونِي مَاذَا خَلَقُواْ مِنَ ٱلۡأَرۡضِ أَمۡ لَهُمۡ شِرۡكٞ فِي ٱلسَّمَٰوَٰتِۖ ٱئۡتُونِي بِكِتَٰبٖ مِّن قَبۡلِ هَٰذَآ أَوۡ أَثَٰرَةٖ مِّنۡ عِلۡمٍ إِن كُنتُمۡ صَٰدِقِينَ ٤','قل أرأيتم ما تدعون من دون الله أروني ماذا خلقوا من الأرض أم لهم شرك في السموات ائتوني بكتاب من قبل هذا أو أثارة من علم إن كنتم صادقين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4515,26,46,'Al-Aḥqāf','الأحقَاف',502,14,15,5,'وَمَنۡ أَضَلُّ مِمَّن يَدۡعُواْ مِن دُونِ ٱللَّهِ مَن لَّا يَسۡتَجِيبُ لَهُۥٓ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ وَهُمۡ عَن دُعَآئِهِمۡ غَٰفِلُونَ ٥','ومن أضل ممن يدعو من دون الله من لا يستجيب له إلى يوم القيامة وهم عن دعائهم غافلون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4516,26,46,'Al-Aḥqāf','الأحقَاف',503,1,1,6,'وَإِذَا حُشِرَ ٱلنَّاسُ كَانُواْ لَهُمۡ أَعۡدَآءٗ وَكَانُواْ بِعِبَادَتِهِمۡ كَٰفِرِينَ ٦','وإذا حشر الناس كانوا لهم أعداء وكانوا بعبادتهم كافرين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4517,26,46,'Al-Aḥqāf','الأحقَاف',503,1,3,7,'وَإِذَا تُتۡلَىٰ عَلَيۡهِمۡ ءَايَٰتُنَا بَيِّنَٰتٖ قَالَ ٱلَّذِينَ كَفَرُواْ لِلۡحَقِّ لَمَّا جَآءَهُمۡ هَٰذَا سِحۡرٞ مُّبِينٌ ٧','وإذا تتلى عليهم آياتنا بينات قال الذين كفروا للحق لما جاءهم هذا سحر مبين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4518,26,46,'Al-Aḥqāf','الأحقَاف',503,3,5,8,'أَمۡ يَقُولُونَ ٱفۡتَرَىٰهُۖ قُلۡ إِنِ ٱفۡتَرَيۡتُهُۥ فَلَا تَمۡلِكُونَ لِي مِنَ ٱللَّهِ شَيۡـًٔاۖ هُوَ أَعۡلَمُ بِمَا تُفِيضُونَ فِيهِۚ كَفَىٰ بِهِۦ شَهِيدَۢا بَيۡنِي وَبَيۡنَكُمۡۖ وَهُوَ ٱلۡغَفُورُ ٱلرَّحِيمُ ٨','أم يقولون افتراه قل إن افتريته فلا تملكون لي من الله شيئا هو أعلم بما تفيضون فيه كفى به شهيدا بيني وبينكم وهو الغفور الرحيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4519,26,46,'Al-Aḥqāf','الأحقَاف',503,5,7,9,'قُلۡ مَا كُنتُ بِدۡعٗا مِّنَ ٱلرُّسُلِ وَمَآ أَدۡرِي مَا يُفۡعَلُ بِي وَلَا بِكُمۡۖ إِنۡ أَتَّبِعُ إِلَّا مَا يُوحَىٰٓ إِلَيَّ وَمَآ أَنَا۠ إِلَّا نَذِيرٞ مُّبِينٞ ٩','قل ما كنت بدعا من الرسل وما أدري ما يفعل بي ولا بكم إن أتبع إلا ما يوحى إلي وما أنا إلا نذير مبين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4520,26,46,'Al-Aḥqāf','الأحقَاف',503,7,9,10,'قُلۡ أَرَءَيۡتُمۡ إِن كَانَ مِنۡ عِندِ ٱللَّهِ وَكَفَرۡتُم بِهِۦ وَشَهِدَ شَاهِدٞ مِّنۢ بَنِيٓ إِسۡرَٰٓءِيلَ عَلَىٰ مِثۡلِهِۦ فَـَٔامَنَ وَٱسۡتَكۡبَرۡتُمۡۚ إِنَّ ٱللَّهَ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّٰلِمِينَ ١٠','قل أرأيتم إن كان من عند الله وكفرتم به وشهد شاهد من بني إسرائيل على مثله فآمن واستكبرتم إن الله لا يهدي القوم الظالمين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4521,26,46,'Al-Aḥqāf','الأحقَاف',503,9,11,11,'وَقَالَ ٱلَّذِينَ كَفَرُواْ لِلَّذِينَ ءَامَنُواْ لَوۡ كَانَ خَيۡرٗا مَّا سَبَقُونَآ إِلَيۡهِۚ وَإِذۡ لَمۡ يَهۡتَدُواْ بِهِۦ فَسَيَقُولُونَ هَٰذَآ إِفۡكٞ قَدِيمٞ ١١','وقال الذين كفروا للذين آمنوا لو كان خيرا ما سبقونا إليه وإذ لم يهتدوا به فسيقولون هذا إفك قديم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4522,26,46,'Al-Aḥqāf','الأحقَاف',503,11,13,12,'وَمِن قَبۡلِهِۦ كِتَٰبُ مُوسَىٰٓ إِمَامٗا وَرَحۡمَةٗۚ وَهَٰذَا كِتَٰبٞ مُّصَدِّقٞ لِّسَانًا عَرَبِيّٗا لِّيُنذِرَ ٱلَّذِينَ ظَلَمُواْ وَبُشۡرَىٰ لِلۡمُحۡسِنِينَ ١٢','ومن قبله كتاب موسى إماما ورحمة وهذا كتاب مصدق لسانا عربيا لينذر الذين ظلموا وبشرى للمحسنين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4523,26,46,'Al-Aḥqāf','الأحقَاف',503,13,14,13,'إِنَّ ٱلَّذِينَ قَالُواْ رَبُّنَا ٱللَّهُ ثُمَّ ٱسۡتَقَٰمُواْ فَلَا خَوۡفٌ عَلَيۡهِمۡ وَلَا هُمۡ يَحۡزَنُونَ ١٣','إن الذين قالوا ربنا الله ثم استقاموا فلا خوف عليهم ولا هم يحزنون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4524,26,46,'Al-Aḥqāf','الأحقَاف',503,15,15,14,'أُوْلَٰٓئِكَ أَصۡحَٰبُ ٱلۡجَنَّةِ خَٰلِدِينَ فِيهَا جَزَآءَۢ بِمَا كَانُواْ يَعۡمَلُونَ ١٤','أولئك أصحاب الجنة خالدين فيها جزاء بما كانوا يعملون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4525,26,46,'Al-Aḥqāf','الأحقَاف',504,1,5,15,'وَوَصَّيۡنَا ٱلۡإِنسَٰنَ بِوَٰلِدَيۡهِ إِحۡسَٰنًاۖ حَمَلَتۡهُ أُمُّهُۥ كُرۡهٗا وَوَضَعَتۡهُ كُرۡهٗاۖ وَحَمۡلُهُۥ وَفِصَٰلُهُۥ ثَلَٰثُونَ شَهۡرًاۚ حَتَّىٰٓ إِذَا بَلَغَ أَشُدَّهُۥ وَبَلَغَ أَرۡبَعِينَ سَنَةٗ قَالَ رَبِّ أَوۡزِعۡنِيٓ أَنۡ أَشۡكُرَ نِعۡمَتَكَ ٱلَّتِيٓ أَنۡعَمۡتَ عَلَيَّ وَعَلَىٰ وَٰلِدَيَّ وَأَنۡ أَعۡمَلَ صَٰلِحٗا تَرۡضَىٰهُ وَأَصۡلِحۡ لِي فِي ذُرِّيَّتِيٓۖ إِنِّي تُبۡتُ إِلَيۡكَ وَإِنِّي مِنَ ٱلۡمُسۡلِمِينَ ١٥','ووصينا الإنسان بوالديه إحسانا حملته أمه كرها ووضعته كرها وحمله وفصاله ثلاثون شهرا حتى إذا بلغ أشده وبلغ أربعين سنة قال رب أوزعني أن أشكر نعمتك التي أنعمت علي وعلى والدي وأن أعمل صالحا ترضاه وأصلح لي في ذريتي إني تبت إليك وإني من المسلمين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4526,26,46,'Al-Aḥqāf','الأحقَاف',504,5,7,16,'أُوْلَٰٓئِكَ ٱلَّذِينَ نَتَقَبَّلُ عَنۡهُمۡ أَحۡسَنَ مَا عَمِلُواْ وَنَتَجَاوَزُ عَن سَيِّـَٔاتِهِمۡ فِيٓ أَصۡحَٰبِ ٱلۡجَنَّةِۖ وَعۡدَ ٱلصِّدۡقِ ٱلَّذِي كَانُواْ يُوعَدُونَ ١٦','أولئك الذين نتقبل عنهم أحسن ما عملوا ونتجاوز عن سيئاتهم في أصحاب الجنة وعد الصدق الذي كانوا يوعدون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4527,26,46,'Al-Aḥqāf','الأحقَاف',504,7,10,17,'وَٱلَّذِي قَالَ لِوَٰلِدَيۡهِ أُفّٖ لَّكُمَآ أَتَعِدَانِنِيٓ أَنۡ أُخۡرَجَ وَقَدۡ خَلَتِ ٱلۡقُرُونُ مِن قَبۡلِي وَهُمَا يَسۡتَغِيثَانِ ٱللَّهَ وَيۡلَكَ ءَامِنۡ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞ فَيَقُولُ مَا هَٰذَآ إِلَّآ أَسَٰطِيرُ ٱلۡأَوَّلِينَ ١٧','والذي قال لوالديه أف لكما أتعدانني أن أخرج وقد خلت القرون من قبلي وهما يستغيثان الله ويلك آمن إن وعد الله حق فيقول ما هذا إلا أساطير الأولين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4528,26,46,'Al-Aḥqāf','الأحقَاف',504,10,12,18,'أُوْلَٰٓئِكَ ٱلَّذِينَ حَقَّ عَلَيۡهِمُ ٱلۡقَوۡلُ فِيٓ أُمَمٖ قَدۡ خَلَتۡ مِن قَبۡلِهِم مِّنَ ٱلۡجِنِّ وَٱلۡإِنسِۖ إِنَّهُمۡ كَانُواْ خَٰسِرِينَ ١٨','أولئك الذين حق عليهم القول في أمم قد خلت من قبلهم من الجن والإنس إنهم كانوا خاسرين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4529,26,46,'Al-Aḥqāf','الأحقَاف',504,12,13,19,'وَلِكُلّٖ دَرَجَٰتٞ مِّمَّا عَمِلُواْۖ وَلِيُوَفِّيَهُمۡ أَعۡمَٰلَهُمۡ وَهُمۡ لَا يُظۡلَمُونَ ١٩','ولكل درجات مما عملوا وليوفيهم أعمالهم وهم لا يظلمون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4530,26,46,'Al-Aḥqāf','الأحقَاف',504,13,15,20,'وَيَوۡمَ يُعۡرَضُ ٱلَّذِينَ كَفَرُواْ عَلَى ٱلنَّارِ أَذۡهَبۡتُمۡ طَيِّبَٰتِكُمۡ فِي حَيَاتِكُمُ ٱلدُّنۡيَا وَٱسۡتَمۡتَعۡتُم بِهَا فَٱلۡيَوۡمَ تُجۡزَوۡنَ عَذَابَ ٱلۡهُونِ بِمَا كُنتُمۡ تَسۡتَكۡبِرُونَ فِي ٱلۡأَرۡضِ بِغَيۡرِ ٱلۡحَقِّ وَبِمَا كُنتُمۡ تَفۡسُقُونَ ٢٠','ويوم يعرض الذين كفروا على النار أذهبتم طيباتكم في حياتكم الدنيا واستمتعتم بها فاليوم تجزون عذاب الهون بما كنتم تستكبرون في الأرض بغير الحق وبما كنتم تفسقون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4531,26,46,'Al-Aḥqāf','الأحقَاف',505,1,3,21,'۞ وَٱذۡكُرۡ أَخَا عَادٍ إِذۡ أَنذَرَ قَوۡمَهُۥ بِٱلۡأَحۡقَافِ وَقَدۡ خَلَتِ ٱلنُّذُرُ مِنۢ بَيۡنِ يَدَيۡهِ وَمِنۡ خَلۡفِهِۦٓ أَلَّا تَعۡبُدُوٓاْ إِلَّا ٱللَّهَ إِنِّيٓ أَخَافُ عَلَيۡكُمۡ عَذَابَ يَوۡمٍ عَظِيمٖ ٢١','واذكر أخا عاد إذ أنذر قومه بالأحقاف وقد خلت النذر من بين يديه ومن خلفه ألا تعبدوا إلا الله إني أخاف عليكم عذاب يوم عظيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4532,26,46,'Al-Aḥqāf','الأحقَاف',505,3,4,22,'قَالُوٓاْ أَجِئۡتَنَا لِتَأۡفِكَنَا عَنۡ ءَالِهَتِنَا فَأۡتِنَا بِمَا تَعِدُنَآ إِن كُنتَ مِنَ ٱلصَّٰدِقِينَ ٢٢','قالوا أجئتنا لتأفكنا عن آلهتنا فأتنا بما تعدنا إن كنت من الصادقين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4533,26,46,'Al-Aḥqāf','الأحقَاف',505,4,5,23,'قَالَ إِنَّمَا ٱلۡعِلۡمُ عِندَ ٱللَّهِ وَأُبَلِّغُكُم مَّآ أُرۡسِلۡتُ بِهِۦ وَلَٰكِنِّيٓ أَرَىٰكُمۡ قَوۡمٗا تَجۡهَلُونَ ٢٣','قال إنما العلم عند الله وأبلغكم ما أرسلت به ولكني أراكم قوما تجهلون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4534,26,46,'Al-Aḥqāf','الأحقَاف',505,5,7,24,'فَلَمَّا رَأَوۡهُ عَارِضٗا مُّسۡتَقۡبِلَ أَوۡدِيَتِهِمۡ قَالُواْ هَٰذَا عَارِضٞ مُّمۡطِرُنَاۚ بَلۡ هُوَ مَا ٱسۡتَعۡجَلۡتُم بِهِۦۖ رِيحٞ فِيهَا عَذَابٌ أَلِيمٞ ٢٤','فلما رأوه عارضا مستقبل أوديتهم قالوا هذا عارض ممطرنا بل هو ما استعجلتم به ريح فيها عذاب أليم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4535,26,46,'Al-Aḥqāf','الأحقَاف',505,7,9,25,'تُدَمِّرُ كُلَّ شَيۡءِۭ بِأَمۡرِ رَبِّهَا فَأَصۡبَحُواْ لَا يُرَىٰٓ إِلَّا مَسَٰكِنُهُمۡۚ كَذَٰلِكَ نَجۡزِي ٱلۡقَوۡمَ ٱلۡمُجۡرِمِينَ ٢٥','تدمر كل شيء بأمر ربها فأصبحوا لا يرى إلا مساكنهم كذلك نجزي القوم المجرمين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4536,26,46,'Al-Aḥqāf','الأحقَاف',505,9,12,26,'وَلَقَدۡ مَكَّنَّٰهُمۡ فِيمَآ إِن مَّكَّنَّٰكُمۡ فِيهِ وَجَعَلۡنَا لَهُمۡ سَمۡعٗا وَأَبۡصَٰرٗا وَأَفۡـِٔدَةٗ فَمَآ أَغۡنَىٰ عَنۡهُمۡ سَمۡعُهُمۡ وَلَآ أَبۡصَٰرُهُمۡ وَلَآ أَفۡـِٔدَتُهُم مِّن شَيۡءٍ إِذۡ كَانُواْ يَجۡحَدُونَ بِـَٔايَٰتِ ٱللَّهِ وَحَاقَ بِهِم مَّا كَانُواْ بِهِۦ يَسۡتَهۡزِءُونَ ٢٦','ولقد مكناهم فيما إن مكناكم فيه وجعلنا لهم سمعا وأبصارا وأفئدة فما أغنى عنهم سمعهم ولا أبصارهم ولا أفئدتهم من شيء إذ كانوا يجحدون بآيات الله وحاق بهم ما كانوا به يستهزئون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4537,26,46,'Al-Aḥqāf','الأحقَاف',505,12,13,27,'وَلَقَدۡ أَهۡلَكۡنَا مَا حَوۡلَكُم مِّنَ ٱلۡقُرَىٰ وَصَرَّفۡنَا ٱلۡأٓيَٰتِ لَعَلَّهُمۡ يَرۡجِعُونَ ٢٧','ولقد أهلكنا ما حولكم من القرى وصرفنا الآيات لعلهم يرجعون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4538,26,46,'Al-Aḥqāf','الأحقَاف',505,14,15,28,'فَلَوۡلَا نَصَرَهُمُ ٱلَّذِينَ ٱتَّخَذُواْ مِن دُونِ ٱللَّهِ قُرۡبَانًا ءَالِهَةَۢۖ بَلۡ ضَلُّواْ عَنۡهُمۡۚ وَذَٰلِكَ إِفۡكُهُمۡ وَمَا كَانُواْ يَفۡتَرُونَ ٢٨','فلولا نصرهم الذين اتخذوا من دون الله قربانا آلهة بل ضلوا عنهم وذلك إفكهم وما كانوا يفترون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4539,26,46,'Al-Aḥqāf','الأحقَاف',506,1,3,29,'وَإِذۡ صَرَفۡنَآ إِلَيۡكَ نَفَرٗا مِّنَ ٱلۡجِنِّ يَسۡتَمِعُونَ ٱلۡقُرۡءَانَ فَلَمَّا حَضَرُوهُ قَالُوٓاْ أَنصِتُواْۖ فَلَمَّا قُضِيَ وَلَّوۡاْ إِلَىٰ قَوۡمِهِم مُّنذِرِينَ ٢٩','وإذ صرفنا إليك نفرا من الجن يستمعون القرآن فلما حضروه قالوا أنصتوا فلما قضي ولوا إلى قومهم منذرين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4540,26,46,'Al-Aḥqāf','الأحقَاف',506,3,5,30,'قَالُواْ يَٰقَوۡمَنَآ إِنَّا سَمِعۡنَا كِتَٰبًا أُنزِلَ مِنۢ بَعۡدِ مُوسَىٰ مُصَدِّقٗا لِّمَا بَيۡنَ يَدَيۡهِ يَهۡدِيٓ إِلَى ٱلۡحَقِّ وَإِلَىٰ طَرِيقٖ مُّسۡتَقِيمٖ ٣٠','قالوا ياقومنا إنا سمعنا كتابا أنزل من بعد موسى مصدقا لما بين يديه يهدي إلى الحق وإلى طريق مستقيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4541,26,46,'Al-Aḥqāf','الأحقَاف',506,5,6,31,'يَٰقَوۡمَنَآ أَجِيبُواْ دَاعِيَ ٱللَّهِ وَءَامِنُواْ بِهِۦ يَغۡفِرۡ لَكُم مِّن ذُنُوبِكُمۡ وَيُجِرۡكُم مِّنۡ عَذَابٍ أَلِيمٖ ٣١','ياقومنا أجيبوا داعي الله وآمنوا به يغفر لكم من ذنوبكم ويجركم من عذاب أليم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4542,26,46,'Al-Aḥqāf','الأحقَاف',506,7,8,32,'وَمَن لَّا يُجِبۡ دَاعِيَ ٱللَّهِ فَلَيۡسَ بِمُعۡجِزٖ فِي ٱلۡأَرۡضِ وَلَيۡسَ لَهُۥ مِن دُونِهِۦٓ أَوۡلِيَآءُۚ أُوْلَٰٓئِكَ فِي ضَلَٰلٖ مُّبِينٍ ٣٢','ومن لا يجب داعي الله فليس بمعجز في الأرض وليس له من دونه أولياء أولئك في ضلال مبين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4543,26,46,'Al-Aḥqāf','الأحقَاف',506,8,10,33,'أَوَلَمۡ يَرَوۡاْ أَنَّ ٱللَّهَ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَلَمۡ يَعۡيَ بِخَلۡقِهِنَّ بِقَٰدِرٍ عَلَىٰٓ أَن يُحۡـِۧيَ ٱلۡمَوۡتَىٰۚ بَلَىٰٓۚ إِنَّهُۥ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ ٣٣','أو لم يروا أن الله الذي خلق السموات والأرض ولم يعي بخلقهن بقادر على أن يحيي الموتى بلى إنه على كل شيء قدير');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4544,26,46,'Al-Aḥqāf','الأحقَاف',506,11,12,34,'وَيَوۡمَ يُعۡرَضُ ٱلَّذِينَ كَفَرُواْ عَلَى ٱلنَّارِ أَلَيۡسَ هَٰذَا بِٱلۡحَقِّۖ قَالُواْ بَلَىٰ وَرَبِّنَاۚ قَالَ فَذُوقُواْ ٱلۡعَذَابَ بِمَا كُنتُمۡ تَكۡفُرُونَ ٣٤','ويوم يعرض الذين كفروا على النار أليس هذا بالحق قالوا بلى وربنا قال فذوقوا العذاب بما كنتم تكفرون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4545,26,46,'Al-Aḥqāf','الأحقَاف',506,13,15,35,'فَٱصۡبِرۡ كَمَا صَبَرَ أُوْلُواْ ٱلۡعَزۡمِ مِنَ ٱلرُّسُلِ وَلَا تَسۡتَعۡجِل لَّهُمۡۚ كَأَنَّهُمۡ يَوۡمَ يَرَوۡنَ مَا يُوعَدُونَ لَمۡ يَلۡبَثُوٓاْ إِلَّا سَاعَةٗ مِّن نَّهَارِۭۚ بَلَٰغٞۚ فَهَلۡ يُهۡلَكُ إِلَّا ٱلۡقَوۡمُ ٱلۡفَٰسِقُونَ ٣٥','فاصبر كما صبر أولو العزم من الرسل ولا تستعجل لهم كأنهم يوم يرون ما يوعدون لم يلبثوا إلا ساعة من نهار بلاغ فهل يهلك إلا القوم الفاسقون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4546,26,47,'Muḥammad','مُحمد',507,3,3,1,'ٱلَّذِينَ كَفَرُواْ وَصَدُّواْ عَن سَبِيلِ ٱللَّهِ أَضَلَّ أَعۡمَٰلَهُمۡ ١','الذين كفروا وصدوا عن سبيل الله أضل أعمالهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4547,26,47,'Muḥammad','مُحمد',507,3,5,2,'وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّٰلِحَٰتِ وَءَامَنُواْ بِمَا نُزِّلَ عَلَىٰ مُحَمَّدٖ وَهُوَ ٱلۡحَقُّ مِن رَّبِّهِمۡ كَفَّرَ عَنۡهُمۡ سَيِّـَٔاتِهِمۡ وَأَصۡلَحَ بَالَهُمۡ ٢','والذين آمنوا وعملوا الصالحات وآمنوا بما نزل على محمد وهو الحق من ربهم كفر عنهم سيئاتهم وأصلح بالهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4548,26,47,'Muḥammad','مُحمد',507,5,6,3,'ذَٰلِكَ بِأَنَّ ٱلَّذِينَ كَفَرُواْ ٱتَّبَعُواْ ٱلۡبَٰطِلَ وَأَنَّ ٱلَّذِينَ ءَامَنُواْ ٱتَّبَعُواْ ٱلۡحَقَّ مِن رَّبِّهِمۡۚ كَذَٰلِكَ يَضۡرِبُ ٱللَّهُ لِلنَّاسِ أَمۡثَٰلَهُمۡ ٣','ذلك بأن الذين كفروا اتبعوا الباطل وأن الذين آمنوا اتبعوا الحق من ربهم كذلك يضرب الله للناس أمثالهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4549,26,47,'Muḥammad','مُحمد',507,7,10,4,'فَإِذَا لَقِيتُمُ ٱلَّذِينَ كَفَرُواْ فَضَرۡبَ ٱلرِّقَابِ حَتَّىٰٓ إِذَآ أَثۡخَنتُمُوهُمۡ فَشُدُّواْ ٱلۡوَثَاقَ فَإِمَّا مَنَّۢا بَعۡدُ وَإِمَّا فِدَآءً حَتَّىٰ تَضَعَ ٱلۡحَرۡبُ أَوۡزَارَهَاۚ ذَٰلِكَۖ وَلَوۡ يَشَآءُ ٱللَّهُ لَٱنتَصَرَ مِنۡهُمۡ وَلَٰكِن لِّيَبۡلُوَاْ بَعۡضَكُم بِبَعۡضٖۗ وَٱلَّذِينَ قُتِلُواْ فِي سَبِيلِ ٱللَّهِ فَلَن يُضِلَّ أَعۡمَٰلَهُمۡ ٤','فإذا لقيتم الذين كفروا فضرب الرقاب حتى إذا أثخنتموهم فشدوا الوثاق فإما منا بعد وإما فداء حتى تضع الحرب أوزارها ذلك ولو يشاء الله لانتصر منهم ولكن ليبلو بعضكم ببعض والذين قتلوا في سبيل الله فلن يضل أعمالهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4550,26,47,'Muḥammad','مُحمد',507,10,10,5,'سَيَهۡدِيهِمۡ وَيُصۡلِحُ بَالَهُمۡ ٥','سيهديهم ويصلح بالهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4551,26,47,'Muḥammad','مُحمد',507,10,10,6,'وَيُدۡخِلُهُمُ ٱلۡجَنَّةَ عَرَّفَهَا لَهُمۡ ٦','ويدخلهم الجنة عرفها لهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4552,26,47,'Muḥammad','مُحمد',507,11,11,7,'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِن تَنصُرُواْ ٱللَّهَ يَنصُرۡكُمۡ وَيُثَبِّتۡ أَقۡدَامَكُمۡ ٧','ياأيها الذين آمنوا إن تنصروا الله ينصركم ويثبت أقدامكم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4553,26,47,'Muḥammad','مُحمد',507,12,12,8,'وَٱلَّذِينَ كَفَرُواْ فَتَعۡسٗا لَّهُمۡ وَأَضَلَّ أَعۡمَٰلَهُمۡ ٨','والذين كفروا فتعسا لهم وأضل أعمالهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4554,26,47,'Muḥammad','مُحمد',507,12,13,9,'ذَٰلِكَ بِأَنَّهُمۡ كَرِهُواْ مَآ أَنزَلَ ٱللَّهُ فَأَحۡبَطَ أَعۡمَٰلَهُمۡ ٩','ذلك بأنهم كرهوا ما أنزل الله فأحبط أعمالهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4555,26,47,'Muḥammad','مُحمد',507,13,14,10,'۞ أَفَلَمۡ يَسِيرُواْ فِي ٱلۡأَرۡضِ فَيَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبۡلِهِمۡۖ دَمَّرَ ٱللَّهُ عَلَيۡهِمۡۖ وَلِلۡكَٰفِرِينَ أَمۡثَٰلُهَا ١٠','أفلم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم دمر الله عليهم وللكافرين أمثالها');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4556,26,47,'Muḥammad','مُحمد',507,15,15,11,'ذَٰلِكَ بِأَنَّ ٱللَّهَ مَوۡلَى ٱلَّذِينَ ءَامَنُواْ وَأَنَّ ٱلۡكَٰفِرِينَ لَا مَوۡلَىٰ لَهُمۡ ١١','ذلك بأن الله مولى الذين آمنوا وأن الكافرين لا مولى لهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4557,26,47,'Muḥammad','مُحمد',508,1,3,12,'إِنَّ ٱللَّهَ يُدۡخِلُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّٰلِحَٰتِ جَنَّٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۖ وَٱلَّذِينَ كَفَرُواْ يَتَمَتَّعُونَ وَيَأۡكُلُونَ كَمَا تَأۡكُلُ ٱلۡأَنۡعَٰمُ وَٱلنَّارُ مَثۡوٗى لَّهُمۡ ١٢','إن الله يدخل الذين آمنوا وعملوا الصالحات جنات تجري من تحتها الأنهار والذين كفروا يتمتعون ويأكلون كما تأكل الأنعام والنار مثوى لهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4558,26,47,'Muḥammad','مُحمد',508,3,4,13,'وَكَأَيِّن مِّن قَرۡيَةٍ هِيَ أَشَدُّ قُوَّةٗ مِّن قَرۡيَتِكَ ٱلَّتِيٓ أَخۡرَجَتۡكَ أَهۡلَكۡنَٰهُمۡ فَلَا نَاصِرَ لَهُمۡ ١٣','وكأين من قرية هي أشد قوة من قريتك التي أخرجتك أهلكناهم فلا ناصر لهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4559,26,47,'Muḥammad','مُحمد',508,4,5,14,'أَفَمَن كَانَ عَلَىٰ بَيِّنَةٖ مِّن رَّبِّهِۦ كَمَن زُيِّنَ لَهُۥ سُوٓءُ عَمَلِهِۦ وَٱتَّبَعُوٓاْ أَهۡوَآءَهُم ١٤','أفمن كان على بينة من ربه كمن زين له سوء عمله واتبعوا أهواءهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4560,26,47,'Muḥammad','مُحمد',508,5,9,15,'مَّثَلُ ٱلۡجَنَّةِ ٱلَّتِي وُعِدَ ٱلۡمُتَّقُونَۖ فِيهَآ أَنۡهَٰرٞ مِّن مَّآءٍ غَيۡرِ ءَاسِنٖ وَأَنۡهَٰرٞ مِّن لَّبَنٖ لَّمۡ يَتَغَيَّرۡ طَعۡمُهُۥ وَأَنۡهَٰرٞ مِّنۡ خَمۡرٖ لَّذَّةٖ لِّلشَّٰرِبِينَ وَأَنۡهَٰرٞ مِّنۡ عَسَلٖ مُّصَفّٗىۖ وَلَهُمۡ فِيهَا مِن كُلِّ ٱلثَّمَرَٰتِ وَمَغۡفِرَةٞ مِّن رَّبِّهِمۡۖ كَمَنۡ هُوَ خَٰلِدٞ فِي ٱلنَّارِ وَسُقُواْ مَآءً حَمِيمٗا فَقَطَّعَ أَمۡعَآءَهُمۡ ١٥','مثل الجنة التي وعد المتقون فيها أنهار من ماء غير آسن وأنهار من لبن لم يتغير طعمه وأنهار من خمر لذة للشاربين وأنهار من عسل مصفى ولهم فيها من كل الثمرات ومغفرة من ربهم كمن هو خالد في النار وسقوا ماء حميما فقطع أمعاءهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4561,26,47,'Muḥammad','مُحمد',508,9,11,16,'وَمِنۡهُم مَّن يَسۡتَمِعُ إِلَيۡكَ حَتَّىٰٓ إِذَا خَرَجُواْ مِنۡ عِندِكَ قَالُواْ لِلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ مَاذَا قَالَ ءَانِفًاۚ أُوْلَٰٓئِكَ ٱلَّذِينَ طَبَعَ ٱللَّهُ عَلَىٰ قُلُوبِهِمۡ وَٱتَّبَعُوٓاْ أَهۡوَآءَهُمۡ ١٦','ومنهم من يستمع إليك حتى إذا خرجوا من عندك قالوا للذين أوتوا العلم ماذا قال آنفا أولئك الذين طبع الله على قلوبهم واتبعوا أهواءهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4562,26,47,'Muḥammad','مُحمد',508,11,12,17,'وَٱلَّذِينَ ٱهۡتَدَوۡاْ زَادَهُمۡ هُدٗى وَءَاتَىٰهُمۡ تَقۡوَىٰهُمۡ ١٧','والذين اهتدوا زادهم هدى وآتاهم تقواهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4563,26,47,'Muḥammad','مُحمد',508,12,14,18,'فَهَلۡ يَنظُرُونَ إِلَّا ٱلسَّاعَةَ أَن تَأۡتِيَهُم بَغۡتَةٗۖ فَقَدۡ جَآءَ أَشۡرَاطُهَاۚ فَأَنَّىٰ لَهُمۡ إِذَا جَآءَتۡهُمۡ ذِكۡرَىٰهُمۡ ١٨','فهل ينظرون إلا الساعة أن تأتيهم بغتة فقد جاء أشراطها فأنى لهم إذا جاءتهم ذكراهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4564,26,47,'Muḥammad','مُحمد',508,14,15,19,'فَٱعۡلَمۡ أَنَّهُۥ لَآ إِلَٰهَ إِلَّا ٱللَّهُ وَٱسۡتَغۡفِرۡ لِذَنۢبِكَ وَلِلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِۗ وَٱللَّهُ يَعۡلَمُ مُتَقَلَّبَكُمۡ وَمَثۡوَىٰكُمۡ ١٩','فاعلم أنه لا إله إلا الله واستغفر لذنبك وللمؤمنين والمؤمنات والله يعلم متقلبكم ومثواكم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4565,26,47,'Muḥammad','مُحمد',509,1,3,20,'وَيَقُولُ ٱلَّذِينَ ءَامَنُواْ لَوۡلَا نُزِّلَتۡ سُورَةٞۖ فَإِذَآ أُنزِلَتۡ سُورَةٞ مُّحۡكَمَةٞ وَذُكِرَ فِيهَا ٱلۡقِتَالُ رَأَيۡتَ ٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٞ يَنظُرُونَ إِلَيۡكَ نَظَرَ ٱلۡمَغۡشِيِّ عَلَيۡهِ مِنَ ٱلۡمَوۡتِۖ فَأَوۡلَىٰ لَهُمۡ ٢٠','ويقول الذين آمنوا لولا نزلت سورة فإذا أنزلت سورة محكمة وذكر فيها القتال رأيت الذين في قلوبهم مرض ينظرون إليك نظر المغشي عليه من الموت فأولى لهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4566,26,47,'Muḥammad','مُحمد',509,4,5,21,'طَاعَةٞ وَقَوۡلٞ مَّعۡرُوفٞۚ فَإِذَا عَزَمَ ٱلۡأَمۡرُ فَلَوۡ صَدَقُواْ ٱللَّهَ لَكَانَ خَيۡرٗا لَّهُمۡ ٢١','طاعة وقول معروف فإذا عزم الأمر فلو صدقوا الله لكان خيرا لهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4567,26,47,'Muḥammad','مُحمد',509,5,6,22,'فَهَلۡ عَسَيۡتُمۡ إِن تَوَلَّيۡتُمۡ أَن تُفۡسِدُواْ فِي ٱلۡأَرۡضِ وَتُقَطِّعُوٓاْ أَرۡحَامَكُمۡ ٢٢','فهل عسيتم إن توليتم أن تفسدوا في الأرض وتقطعوا أرحامكم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4568,26,47,'Muḥammad','مُحمد',509,6,7,23,'أُوْلَٰٓئِكَ ٱلَّذِينَ لَعَنَهُمُ ٱللَّهُ فَأَصَمَّهُمۡ وَأَعۡمَىٰٓ أَبۡصَٰرَهُمۡ ٢٣','أولئك الذين لعنهم الله فأصمهم وأعمى أبصارهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4569,26,47,'Muḥammad','مُحمد',509,7,8,24,'أَفَلَا يَتَدَبَّرُونَ ٱلۡقُرۡءَانَ أَمۡ عَلَىٰ قُلُوبٍ أَقۡفَالُهَآ ٢٤','أفلا يتدبرون القرآن أم على قلوب أقفالها');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4570,26,47,'Muḥammad','مُحمد',509,8,10,25,'إِنَّ ٱلَّذِينَ ٱرۡتَدُّواْ عَلَىٰٓ أَدۡبَٰرِهِم مِّنۢ بَعۡدِ مَا تَبَيَّنَ لَهُمُ ٱلۡهُدَى ٱلشَّيۡطَٰنُ سَوَّلَ لَهُمۡ وَأَمۡلَىٰ لَهُمۡ ٢٥','إن الذين ارتدوا على أدبارهم من بعد ما تبين لهم الهدى الشيطان سول لهم وأملى لهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4571,26,47,'Muḥammad','مُحمد',509,10,11,26,'ذَٰلِكَ بِأَنَّهُمۡ قَالُواْ لِلَّذِينَ كَرِهُواْ مَا نَزَّلَ ٱللَّهُ سَنُطِيعُكُمۡ فِي بَعۡضِ ٱلۡأَمۡرِۖ وَٱللَّهُ يَعۡلَمُ إِسۡرَارَهُمۡ ٢٦','ذلك بأنهم قالوا للذين كرهوا ما نزل الله سنطيعكم في بعض الأمر والله يعلم إسرارهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4572,26,47,'Muḥammad','مُحمد',509,12,13,27,'فَكَيۡفَ إِذَا تَوَفَّتۡهُمُ ٱلۡمَلَٰٓئِكَةُ يَضۡرِبُونَ وُجُوهَهُمۡ وَأَدۡبَٰرَهُمۡ ٢٧','فكيف إذا توفتهم الملائكة يضربون وجوههم وأدبارهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4573,26,47,'Muḥammad','مُحمد',509,13,14,28,'ذَٰلِكَ بِأَنَّهُمُ ٱتَّبَعُواْ مَآ أَسۡخَطَ ٱللَّهَ وَكَرِهُواْ رِضۡوَٰنَهُۥ فَأَحۡبَطَ أَعۡمَٰلَهُمۡ ٢٨','ذلك بأنهم اتبعوا ما أسخط الله وكرهوا رضوانه فأحبط أعمالهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4574,26,47,'Muḥammad','مُحمد',509,14,15,29,'أَمۡ حَسِبَ ٱلَّذِينَ فِي قُلُوبِهِم مَّرَضٌ أَن لَّن يُخۡرِجَ ٱللَّهُ أَضۡغَٰنَهُمۡ ٢٩','أم حسب الذين في قلوبهم مرض أن لن يخرج الله أضغانهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4575,26,47,'Muḥammad','مُحمد',510,1,2,30,'وَلَوۡ نَشَآءُ لَأَرَيۡنَٰكَهُمۡ فَلَعَرَفۡتَهُم بِسِيمَٰهُمۡۚ وَلَتَعۡرِفَنَّهُمۡ فِي لَحۡنِ ٱلۡقَوۡلِۚ وَٱللَّهُ يَعۡلَمُ أَعۡمَٰلَكُمۡ ٣٠','ولو نشاء لأريناكهم فلعرفتهم بسيماهم ولتعرفنهم في لحن القول والله يعلم أعمالكم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4576,26,47,'Muḥammad','مُحمد',510,2,3,31,'وَلَنَبۡلُوَنَّكُمۡ حَتَّىٰ نَعۡلَمَ ٱلۡمُجَٰهِدِينَ مِنكُمۡ وَٱلصَّٰبِرِينَ وَنَبۡلُوَاْ أَخۡبَارَكُمۡ ٣١','ولنبلونكم حتى نعلم المجاهدين منكم والصابرين ونبلوا أخباركم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4577,26,47,'Muḥammad','مُحمد',510,3,6,32,'إِنَّ ٱلَّذِينَ كَفَرُواْ وَصَدُّواْ عَن سَبِيلِ ٱللَّهِ وَشَآقُّواْ ٱلرَّسُولَ مِنۢ بَعۡدِ مَا تَبَيَّنَ لَهُمُ ٱلۡهُدَىٰ لَن يَضُرُّواْ ٱللَّهَ شَيۡـٔٗا وَسَيُحۡبِطُ أَعۡمَٰلَهُمۡ ٣٢','إن الذين كفروا وصدوا عن سبيل الله وشاقوا الرسول من بعد ما تبين لهم الهدى لن يضروا الله شيئا وسيحبط أعمالهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4578,26,47,'Muḥammad','مُحمد',510,6,7,33,'۞ يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ أَطِيعُواْ ٱللَّهَ وَأَطِيعُواْ ٱلرَّسُولَ وَلَا تُبۡطِلُوٓاْ أَعۡمَٰلَكُمۡ ٣٣','ياأيها الذين آمنوا أطيعوا الله وأطيعوا الرسول ولا تبطلوا أعمالكم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4579,26,47,'Muḥammad','مُحمد',510,7,8,34,'إِنَّ ٱلَّذِينَ كَفَرُواْ وَصَدُّواْ عَن سَبِيلِ ٱللَّهِ ثُمَّ مَاتُواْ وَهُمۡ كُفَّارٞ فَلَن يَغۡفِرَ ٱللَّهُ لَهُمۡ ٣٤','إن الذين كفروا وصدوا عن سبيل الله ثم ماتوا وهم كفار فلن يغفر الله لهم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4580,26,47,'Muḥammad','مُحمد',510,8,10,35,'فَلَا تَهِنُواْ وَتَدۡعُوٓاْ إِلَى ٱلسَّلۡمِ وَأَنتُمُ ٱلۡأَعۡلَوۡنَ وَٱللَّهُ مَعَكُمۡ وَلَن يَتِرَكُمۡ أَعۡمَٰلَكُمۡ ٣٥','فلا تهنوا وتدعوا إلى السلم وأنتم الأعلون والله معكم ولن يتركم أعمالكم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4581,26,47,'Muḥammad','مُحمد',510,10,11,36,'إِنَّمَا ٱلۡحَيَوٰةُ ٱلدُّنۡيَا لَعِبٞ وَلَهۡوٞۚ وَإِن تُؤۡمِنُواْ وَتَتَّقُواْ يُؤۡتِكُمۡ أُجُورَكُمۡ وَلَا يَسۡـَٔلۡكُمۡ أَمۡوَٰلَكُمۡ ٣٦','إنما الحياة الدنيا لعب ولهو وإن تؤمنوا وتتقوا يؤتكم أجوركم ولا يسألكم أموالكم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4582,26,47,'Muḥammad','مُحمد',510,11,12,37,'إِن يَسۡـَٔلۡكُمُوهَا فَيُحۡفِكُمۡ تَبۡخَلُواْ وَيُخۡرِجۡ أَضۡغَٰنَكُمۡ ٣٧','إن يسألكموها فيحفكم تبخلوا ويخرج أضغانكم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4583,26,47,'Muḥammad','مُحمد',510,12,15,38,'هَٰٓأَنتُمۡ هَٰٓؤُلَآءِ تُدۡعَوۡنَ لِتُنفِقُواْ فِي سَبِيلِ ٱللَّهِ فَمِنكُم مَّن يَبۡخَلُۖ وَمَن يَبۡخَلۡ فَإِنَّمَا يَبۡخَلُ عَن نَّفۡسِهِۦۚ وَٱللَّهُ ٱلۡغَنِيُّ وَأَنتُمُ ٱلۡفُقَرَآءُۚ وَإِن تَتَوَلَّوۡاْ يَسۡتَبۡدِلۡ قَوۡمًا غَيۡرَكُمۡ ثُمَّ لَا يَكُونُوٓاْ أَمۡثَٰلَكُم ٣٨','هاأنتم هؤلاء تدعون لتنفقوا في سبيل الله فمنكم من يبخل ومن يبخل فإنما يبخل عن نفسه والله الغني وأنتم الفقراء وإن تتولوا يستبدل قوما غيركم ثم لا يكونوا أمثالكم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4584,26,48,'Al-Fatḥ','الفَتح',511,3,3,1,'إِنَّا فَتَحۡنَا لَكَ فَتۡحٗا مُّبِينٗا ١','إنا فتحنا لك فتحا مبينا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4585,26,48,'Al-Fatḥ','الفَتح',511,3,4,2,'لِّيَغۡفِرَ لَكَ ٱللَّهُ مَا تَقَدَّمَ مِن ذَنۢبِكَ وَمَا تَأَخَّرَ وَيُتِمَّ نِعۡمَتَهُۥ عَلَيۡكَ وَيَهۡدِيَكَ صِرَٰطٗا مُّسۡتَقِيمٗا ٢','ليغفر لك الله ما تقدم من ذنبك وما تأخر ويتم نعمته عليك ويهديك صراطا مستقيما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4586,26,48,'Al-Fatḥ','الفَتح',511,5,5,3,'وَيَنصُرَكَ ٱللَّهُ نَصۡرًا عَزِيزًا ٣','وينصرك الله نصرا عزيزا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4587,26,48,'Al-Fatḥ','الفَتح',511,5,7,4,'هُوَ ٱلَّذِيٓ أَنزَلَ ٱلسَّكِينَةَ فِي قُلُوبِ ٱلۡمُؤۡمِنِينَ لِيَزۡدَادُوٓاْ إِيمَٰنٗا مَّعَ إِيمَٰنِهِمۡۗ وَلِلَّهِ جُنُودُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَكَانَ ٱللَّهُ عَلِيمًا حَكِيمٗا ٤','هو الذي أنزل السكينة في قلوب المؤمنين ليزدادوا إيمانا مع إيمانهم ولله جنود السموات والأرض وكان الله عليما حكيما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4588,26,48,'Al-Fatḥ','الفَتح',511,7,9,5,'لِّيُدۡخِلَ ٱلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِ جَنَّٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُ خَٰلِدِينَ فِيهَا وَيُكَفِّرَ عَنۡهُمۡ سَيِّـَٔاتِهِمۡۚ وَكَانَ ذَٰلِكَ عِندَ ٱللَّهِ فَوۡزًا عَظِيمٗا ٥','ليدخل المؤمنين والمؤمنات جنات تجري من تحتها الأنهار خالدين فيها ويكفر عنهم سيئاتهم وكان ذلك عند الله فوزا عظيما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4589,26,48,'Al-Fatḥ','الفَتح',511,9,12,6,'وَيُعَذِّبَ ٱلۡمُنَٰفِقِينَ وَٱلۡمُنَٰفِقَٰتِ وَٱلۡمُشۡرِكِينَ وَٱلۡمُشۡرِكَٰتِ ٱلظَّآنِّينَ بِٱللَّهِ ظَنَّ ٱلسَّوۡءِۚ عَلَيۡهِمۡ دَآئِرَةُ ٱلسَّوۡءِۖ وَغَضِبَ ٱللَّهُ عَلَيۡهِمۡ وَلَعَنَهُمۡ وَأَعَدَّ لَهُمۡ جَهَنَّمَۖ وَسَآءَتۡ مَصِيرٗا ٦','ويعذب المنافقين والمنافقات والمشركين والمشركات الظانين بالله ظن السوء عليهم دائرة السوء وغضب الله عليهم ولعنهم وأعد لهم جهنم وساءت مصيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4590,26,48,'Al-Fatḥ','الفَتح',511,12,13,7,'وَلِلَّهِ جُنُودُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَكَانَ ٱللَّهُ عَزِيزًا حَكِيمًا ٧','ولله جنود السموات والأرض وكان الله عزيزا حكيما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4591,26,48,'Al-Fatḥ','الفَتح',511,13,14,8,'إِنَّآ أَرۡسَلۡنَٰكَ شَٰهِدٗا وَمُبَشِّرٗا وَنَذِيرٗا ٨','إنا أرسلناك شاهدا ومبشرا ونذيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4592,26,48,'Al-Fatḥ','الفَتح',511,14,15,9,'لِّتُؤۡمِنُواْ بِٱللَّهِ وَرَسُولِهِۦ وَتُعَزِّرُوهُ وَتُوَقِّرُوهُۚ وَتُسَبِّحُوهُ بُكۡرَةٗ وَأَصِيلًا ٩','لتؤمنوا بالله ورسوله وتعزروه وتوقروه وتسبحوه بكرة وأصيلا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4593,26,48,'Al-Fatḥ','الفَتح',512,1,3,10,'إِنَّ ٱلَّذِينَ يُبَايِعُونَكَ إِنَّمَا يُبَايِعُونَ ٱللَّهَ يَدُ ٱللَّهِ فَوۡقَ أَيۡدِيهِمۡۚ فَمَن نَّكَثَ فَإِنَّمَا يَنكُثُ عَلَىٰ نَفۡسِهِۦۖ وَمَنۡ أَوۡفَىٰ بِمَا عَٰهَدَ عَلَيۡهُ ٱللَّهَ فَسَيُؤۡتِيهِ أَجۡرًا عَظِيمٗا ١٠','إن الذين يبايعونك إنما يبايعون الله يد الله فوق أيديهم فمن نكث فإنما ينكث على نفسه ومن أوفى بما عاهد عليه الله فسيؤتيه أجرا عظيما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4594,26,48,'Al-Fatḥ','الفَتح',512,3,7,11,'سَيَقُولُ لَكَ ٱلۡمُخَلَّفُونَ مِنَ ٱلۡأَعۡرَابِ شَغَلَتۡنَآ أَمۡوَٰلُنَا وَأَهۡلُونَا فَٱسۡتَغۡفِرۡ لَنَاۚ يَقُولُونَ بِأَلۡسِنَتِهِم مَّا لَيۡسَ فِي قُلُوبِهِمۡۚ قُلۡ فَمَن يَمۡلِكُ لَكُم مِّنَ ٱللَّهِ شَيۡـًٔا إِنۡ أَرَادَ بِكُمۡ ضَرًّا أَوۡ أَرَادَ بِكُمۡ نَفۡعَۢاۚ بَلۡ كَانَ ٱللَّهُ بِمَا تَعۡمَلُونَ خَبِيرَۢا ١١','سيقول لك المخلفون من الأعراب شغلتنا أموالنا وأهلونا فاستغفر لنا يقولون بألسنتهم ما ليس في قلوبهم قل فمن يملك لكم من الله شيئا إن أراد بكم ضرا أو أراد بكم نفعا بل كان الله بما تعملون خبيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4595,26,48,'Al-Fatḥ','الفَتح',512,7,9,12,'بَلۡ ظَنَنتُمۡ أَن لَّن يَنقَلِبَ ٱلرَّسُولُ وَٱلۡمُؤۡمِنُونَ إِلَىٰٓ أَهۡلِيهِمۡ أَبَدٗا وَزُيِّنَ ذَٰلِكَ فِي قُلُوبِكُمۡ وَظَنَنتُمۡ ظَنَّ ٱلسَّوۡءِ وَكُنتُمۡ قَوۡمَۢا بُورٗا ١٢','بل ظننتم أن لن ينقلب الرسول والمؤمنون إلى أهليهم أبدا وزين ذلك في قلوبكم وظننتم ظن السوء وكنتم قوما بورا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4596,26,48,'Al-Fatḥ','الفَتح',512,9,10,13,'وَمَن لَّمۡ يُؤۡمِنۢ بِٱللَّهِ وَرَسُولِهِۦ فَإِنَّآ أَعۡتَدۡنَا لِلۡكَٰفِرِينَ سَعِيرٗا ١٣','ومن لم يؤمن بالله ورسوله فإنا أعتدنا للكافرين سعيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4597,26,48,'Al-Fatḥ','الفَتح',512,10,12,14,'وَلِلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ يَغۡفِرُ لِمَن يَشَآءُ وَيُعَذِّبُ مَن يَشَآءُۚ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمٗا ١٤','ولله ملك السموات والأرض يغفر لمن يشاء ويعذب من يشاء وكان الله غفورا رحيما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4598,26,48,'Al-Fatḥ','الفَتح',512,12,15,15,'سَيَقُولُ ٱلۡمُخَلَّفُونَ إِذَا ٱنطَلَقۡتُمۡ إِلَىٰ مَغَانِمَ لِتَأۡخُذُوهَا ذَرُونَا نَتَّبِعۡكُمۡۖ يُرِيدُونَ أَن يُبَدِّلُواْ كَلَٰمَ ٱللَّهِۚ قُل لَّن تَتَّبِعُونَا كَذَٰلِكُمۡ قَالَ ٱللَّهُ مِن قَبۡلُۖ فَسَيَقُولُونَ بَلۡ تَحۡسُدُونَنَاۚ بَلۡ كَانُواْ لَا يَفۡقَهُونَ إِلَّا قَلِيلٗا ١٥','سيقول المخلفون إذا انطلقتم إلى مغانم لتأخذوها ذرونا نتبعكم يريدون أن يبدلوا كلام الله قل لن تتبعونا كذلكم قال الله من قبل فسيقولون بل تحسدوننا بل كانوا لا يفقهون إلا قليلا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4599,26,48,'Al-Fatḥ','الفَتح',513,1,3,16,'قُل لِّلۡمُخَلَّفِينَ مِنَ ٱلۡأَعۡرَابِ سَتُدۡعَوۡنَ إِلَىٰ قَوۡمٍ أُوْلِي بَأۡسٖ شَدِيدٖ تُقَٰتِلُونَهُمۡ أَوۡ يُسۡلِمُونَۖ فَإِن تُطِيعُواْ يُؤۡتِكُمُ ٱللَّهُ أَجۡرًا حَسَنٗاۖ وَإِن تَتَوَلَّوۡاْ كَمَا تَوَلَّيۡتُم مِّن قَبۡلُ يُعَذِّبۡكُمۡ عَذَابًا أَلِيمٗا ١٦','قل للمخلفين من الأعراب ستدعون إلى قوم أولي بأس شديد تقاتلونهم أو يسلمون فإن تطيعوا يؤتكم الله أجرا حسنا وإن تتولوا كما توليتم من قبل يعذبكم عذابا أليما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4600,26,48,'Al-Fatḥ','الفَتح',513,3,6,17,'لَّيۡسَ عَلَى ٱلۡأَعۡمَىٰ حَرَجٞ وَلَا عَلَى ٱلۡأَعۡرَجِ حَرَجٞ وَلَا عَلَى ٱلۡمَرِيضِ حَرَجٞۗ وَمَن يُطِعِ ٱللَّهَ وَرَسُولَهُۥ يُدۡخِلۡهُ جَنَّٰتٖ تَجۡرِي مِن تَحۡتِهَا ٱلۡأَنۡهَٰرُۖ وَمَن يَتَوَلَّ يُعَذِّبۡهُ عَذَابًا أَلِيمٗا ١٧','ليس على الأعمى حرج ولا على الأعرج حرج ولا على المريض حرج ومن يطع الله ورسوله يدخله جنات تجري من تحتها الأنهار ومن يتول يعذبه عذابا أليما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4601,26,48,'Al-Fatḥ','الفَتح',513,6,8,18,'۞ لَّقَدۡ رَضِيَ ٱللَّهُ عَنِ ٱلۡمُؤۡمِنِينَ إِذۡ يُبَايِعُونَكَ تَحۡتَ ٱلشَّجَرَةِ فَعَلِمَ مَا فِي قُلُوبِهِمۡ فَأَنزَلَ ٱلسَّكِينَةَ عَلَيۡهِمۡ وَأَثَٰبَهُمۡ فَتۡحٗا قَرِيبٗا ١٨','لقد رضي الله عن المؤمنين إذ يبايعونك تحت الشجرة فعلم ما في قلوبهم فأنزل السكينة عليهم وأثابهم فتحا قريبا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4602,26,48,'Al-Fatḥ','الفَتح',513,8,9,19,'وَمَغَانِمَ كَثِيرَةٗ يَأۡخُذُونَهَاۗ وَكَانَ ٱللَّهُ عَزِيزًا حَكِيمٗا ١٩','ومغانم كثيرة يأخذونها وكان الله عزيزا حكيما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4603,26,48,'Al-Fatḥ','الفَتح',513,9,12,20,'وَعَدَكُمُ ٱللَّهُ مَغَانِمَ كَثِيرَةٗ تَأۡخُذُونَهَا فَعَجَّلَ لَكُمۡ هَٰذِهِۦ وَكَفَّ أَيۡدِيَ ٱلنَّاسِ عَنكُمۡ وَلِتَكُونَ ءَايَةٗ لِّلۡمُؤۡمِنِينَ وَيَهۡدِيَكُمۡ صِرَٰطٗا مُّسۡتَقِيمٗا ٢٠','وعدكم الله مغانم كثيرة تأخذونها فعجل لكم هذه وكف أيدي الناس عنكم ولتكون آية للمؤمنين ويهديكم صراطا مستقيما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4604,26,48,'Al-Fatḥ','الفَتح',513,12,13,21,'وَأُخۡرَىٰ لَمۡ تَقۡدِرُواْ عَلَيۡهَا قَدۡ أَحَاطَ ٱللَّهُ بِهَاۚ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٗا ٢١','وأخرى لم تقدروا عليها قد أحاط الله بها وكان الله على كل شيء قديرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4605,26,48,'Al-Fatḥ','الفَتح',513,13,14,22,'وَلَوۡ قَٰتَلَكُمُ ٱلَّذِينَ كَفَرُواْ لَوَلَّوُاْ ٱلۡأَدۡبَٰرَ ثُمَّ لَا يَجِدُونَ وَلِيّٗا وَلَا نَصِيرٗا ٢٢','ولو قاتلكم الذين كفروا لولوا الأدبار ثم لا يجدون وليا ولا نصيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4606,26,48,'Al-Fatḥ','الفَتح',513,14,15,23,'سُنَّةَ ٱللَّهِ ٱلَّتِي قَدۡ خَلَتۡ مِن قَبۡلُۖ وَلَن تَجِدَ لِسُنَّةِ ٱللَّهِ تَبۡدِيلٗا ٢٣','سنة الله التي قد خلت من قبل ولن تجد لسنة الله تبديلا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4607,26,48,'Al-Fatḥ','الفَتح',514,1,2,24,'وَهُوَ ٱلَّذِي كَفَّ أَيۡدِيَهُمۡ عَنكُمۡ وَأَيۡدِيَكُمۡ عَنۡهُم بِبَطۡنِ مَكَّةَ مِنۢ بَعۡدِ أَنۡ أَظۡفَرَكُمۡ عَلَيۡهِمۡۚ وَكَانَ ٱللَّهُ بِمَا تَعۡمَلُونَ بَصِيرًا ٢٤','وهو الذي كف أيديهم عنكم وأيديكم عنهم ببطن مكة من بعد أن أظفركم عليهم وكان الله بما تعملون بصيرا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4608,26,48,'Al-Fatḥ','الفَتح',514,3,7,25,'هُمُ ٱلَّذِينَ كَفَرُواْ وَصَدُّوكُمۡ عَنِ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ وَٱلۡهَدۡيَ مَعۡكُوفًا أَن يَبۡلُغَ مَحِلَّهُۥۚ وَلَوۡلَا رِجَالٞ مُّؤۡمِنُونَ وَنِسَآءٞ مُّؤۡمِنَٰتٞ لَّمۡ تَعۡلَمُوهُمۡ أَن تَطَـُٔوهُمۡ فَتُصِيبَكُم مِّنۡهُم مَّعَرَّةُۢ بِغَيۡرِ عِلۡمٖۖ لِّيُدۡخِلَ ٱللَّهُ فِي رَحۡمَتِهِۦ مَن يَشَآءُۚ لَوۡ تَزَيَّلُواْ لَعَذَّبۡنَا ٱلَّذِينَ كَفَرُواْ مِنۡهُمۡ عَذَابًا أَلِيمًا ٢٥','هم الذين كفروا وصدوكم عن المسجد الحرام والهدي معكوفا أن يبلغ محله ولولا رجال مؤمنون ونساء مؤمنات لم تعلموهم أن تطئوهم فتصيبكم منهم معرة بغير علم ليدخل الله في رحمته من يشاء لو تزيلوا لعذبنا الذين كفروا منهم عذابا أليما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4609,26,48,'Al-Fatḥ','الفَتح',514,7,10,26,'إِذۡ جَعَلَ ٱلَّذِينَ كَفَرُواْ فِي قُلُوبِهِمُ ٱلۡحَمِيَّةَ حَمِيَّةَ ٱلۡجَٰهِلِيَّةِ فَأَنزَلَ ٱللَّهُ سَكِينَتَهُۥ عَلَىٰ رَسُولِهِۦ وَعَلَى ٱلۡمُؤۡمِنِينَ وَأَلۡزَمَهُمۡ كَلِمَةَ ٱلتَّقۡوَىٰ وَكَانُوٓاْ أَحَقَّ بِهَا وَأَهۡلَهَاۚ وَكَانَ ٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمٗا ٢٦','إذ جعل الذين كفروا في قلوبهم الحمية حمية الجاهلية فأنزل الله سكينته على رسوله وعلى المؤمنين وألزمهم كلمة التقوى وكانوا أحق بها وأهلها وكان الله بكل شيء عليما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4610,26,48,'Al-Fatḥ','الفَتح',514,11,14,27,'لَّقَدۡ صَدَقَ ٱللَّهُ رَسُولَهُ ٱلرُّءۡيَا بِٱلۡحَقِّۖ لَتَدۡخُلُنَّ ٱلۡمَسۡجِدَ ٱلۡحَرَامَ إِن شَآءَ ٱللَّهُ ءَامِنِينَ مُحَلِّقِينَ رُءُوسَكُمۡ وَمُقَصِّرِينَ لَا تَخَافُونَۖ فَعَلِمَ مَا لَمۡ تَعۡلَمُواْ فَجَعَلَ مِن دُونِ ذَٰلِكَ فَتۡحٗا قَرِيبًا ٢٧','لقد صدق الله رسوله الرؤيا بالحق لتدخلن المسجد الحرام إن شاء الله آمنين محلقين رءوسكم ومقصرين لا تخافون فعلم ما لم تعلموا فجعل من دون ذلك فتحا قريبا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4611,26,48,'Al-Fatḥ','الفَتح',514,14,15,28,'هُوَ ٱلَّذِيٓ أَرۡسَلَ رَسُولَهُۥ بِٱلۡهُدَىٰ وَدِينِ ٱلۡحَقِّ لِيُظۡهِرَهُۥ عَلَى ٱلدِّينِ كُلِّهِۦۚ وَكَفَىٰ بِٱللَّهِ شَهِيدٗا ٢٨','هو الذي أرسل رسوله بالهدى ودين الحق ليظهره على الدين كله وكفى بالله شهيدا');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4612,26,48,'Al-Fatḥ','الفَتح',515,1,6,29,'مُّحَمَّدٞ رَّسُولُ ٱللَّهِۚ وَٱلَّذِينَ مَعَهُۥٓ أَشِدَّآءُ عَلَى ٱلۡكُفَّارِ رُحَمَآءُ بَيۡنَهُمۡۖ تَرَىٰهُمۡ رُكَّعٗا سُجَّدٗا يَبۡتَغُونَ فَضۡلٗا مِّنَ ٱللَّهِ وَرِضۡوَٰنٗاۖ سِيمَاهُمۡ فِي وُجُوهِهِم مِّنۡ أَثَرِ ٱلسُّجُودِۚ ذَٰلِكَ مَثَلُهُمۡ فِي ٱلتَّوۡرَىٰةِۚ وَمَثَلُهُمۡ فِي ٱلۡإِنجِيلِ كَزَرۡعٍ أَخۡرَجَ شَطۡـَٔهُۥ فَـَٔازَرَهُۥ فَٱسۡتَغۡلَظَ فَٱسۡتَوَىٰ عَلَىٰ سُوقِهِۦ يُعۡجِبُ ٱلزُّرَّاعَ لِيَغِيظَ بِهِمُ ٱلۡكُفَّارَۗ وَعَدَ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّٰلِحَٰتِ مِنۡهُم مَّغۡفِرَةٗ وَأَجۡرًا عَظِيمَۢا ٢٩','محمد رسول الله والذين معه أشداء على الكفار رحماء بينهم تراهم ركعا سجدا يبتغون فضلا من الله ورضوانا سيماهم في وجوههم من أثر السجود ذلك مثلهم في التوراة ومثلهم في الإنجيل كزرع أخرج شطأه فآزره فاستغلظ فاستوى على سوقه يعجب الزراع ليغيظ بهم الكفار وعد الله الذين آمنوا وعملوا الصالحات منهم مغفرة وأجرا عظيما');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4613,26,49,'Al-Ḥujurāt','الحُجُرَات',515,9,10,1,'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تُقَدِّمُواْ بَيۡنَ يَدَيِ ٱللَّهِ وَرَسُولِهِۦۖ وَٱتَّقُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ سَمِيعٌ عَلِيمٞ ١','ياأيها الذين آمنوا لا تقدموا بين يدي الله ورسوله واتقوا الله إن الله سميع عليم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4614,26,49,'Al-Ḥujurāt','الحُجُرَات',515,10,12,2,'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا تَرۡفَعُوٓاْ أَصۡوَٰتَكُمۡ فَوۡقَ صَوۡتِ ٱلنَّبِيِّ وَلَا تَجۡهَرُواْ لَهُۥ بِٱلۡقَوۡلِ كَجَهۡرِ بَعۡضِكُمۡ لِبَعۡضٍ أَن تَحۡبَطَ أَعۡمَٰلُكُمۡ وَأَنتُمۡ لَا تَشۡعُرُونَ ٢','ياأيها الذين آمنوا لا ترفعوا أصواتكم فوق صوت النبي ولا تجهروا له بالقول كجهر بعضكم لبعض أن تحبط أعمالكم وأنتم لا تشعرون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4615,26,49,'Al-Ḥujurāt','الحُجُرَات',515,12,14,3,'إِنَّ ٱلَّذِينَ يَغُضُّونَ أَصۡوَٰتَهُمۡ عِندَ رَسُولِ ٱللَّهِ أُوْلَٰٓئِكَ ٱلَّذِينَ ٱمۡتَحَنَ ٱللَّهُ قُلُوبَهُمۡ لِلتَّقۡوَىٰۚ لَهُم مَّغۡفِرَةٞ وَأَجۡرٌ عَظِيمٌ ٣','إن الذين يغضون أصواتهم عند رسول الله أولئك الذين امتحن الله قلوبهم للتقوى لهم مغفرة وأجر عظيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4616,26,49,'Al-Ḥujurāt','الحُجُرَات',515,14,15,4,'إِنَّ ٱلَّذِينَ يُنَادُونَكَ مِن وَرَآءِ ٱلۡحُجُرَٰتِ أَكۡثَرُهُمۡ لَا يَعۡقِلُونَ ٤','إن الذين ينادونك من وراء الحجرات أكثرهم لا يعقلون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4617,26,49,'Al-Ḥujurāt','الحُجُرَات',516,1,2,5,'وَلَوۡ أَنَّهُمۡ صَبَرُواْ حَتَّىٰ تَخۡرُجَ إِلَيۡهِمۡ لَكَانَ خَيۡرٗا لَّهُمۡۚ وَٱللَّهُ غَفُورٞ رَّحِيمٞ ٥','ولو أنهم صبروا حتى تخرج إليهم لكان خيرا لهم والله غفور رحيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4618,26,49,'Al-Ḥujurāt','الحُجُرَات',516,2,3,6,'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓاْ إِن جَآءَكُمۡ فَاسِقُۢ بِنَبَإٖ فَتَبَيَّنُوٓاْ أَن تُصِيبُواْ قَوۡمَۢا بِجَهَٰلَةٖ فَتُصۡبِحُواْ عَلَىٰ مَا فَعَلۡتُمۡ نَٰدِمِينَ ٦','ياأيها الذين آمنوا إن جاءكم فاسق بنبإ فتبينوا أن تصيبوا قوما بجهالة فتصبحوا على ما فعلتم نادمين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4619,26,49,'Al-Ḥujurāt','الحُجُرَات',516,4,6,7,'وَٱعۡلَمُوٓاْ أَنَّ فِيكُمۡ رَسُولَ ٱللَّهِۚ لَوۡ يُطِيعُكُمۡ فِي كَثِيرٖ مِّنَ ٱلۡأَمۡرِ لَعَنِتُّمۡ وَلَٰكِنَّ ٱللَّهَ حَبَّبَ إِلَيۡكُمُ ٱلۡإِيمَٰنَ وَزَيَّنَهُۥ فِي قُلُوبِكُمۡ وَكَرَّهَ إِلَيۡكُمُ ٱلۡكُفۡرَ وَٱلۡفُسُوقَ وَٱلۡعِصۡيَانَۚ أُوْلَٰٓئِكَ هُمُ ٱلرَّٰشِدُونَ ٧','واعلموا أن فيكم رسول الله لو يطيعكم في كثير من الأمر لعنتم ولكن الله حبب إليكم الإيمان وزينه في قلوبكم وكره إليكم الكفر والفسوق والعصيان أولئك هم الراشدون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4620,26,49,'Al-Ḥujurāt','الحُجُرَات',516,7,7,8,'فَضۡلٗا مِّنَ ٱللَّهِ وَنِعۡمَةٗۚ وَٱللَّهُ عَلِيمٌ حَكِيمٞ ٨','فضلا من الله ونعمة والله عليم حكيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4621,26,49,'Al-Ḥujurāt','الحُجُرَات',516,7,10,9,'وَإِن طَآئِفَتَانِ مِنَ ٱلۡمُؤۡمِنِينَ ٱقۡتَتَلُواْ فَأَصۡلِحُواْ بَيۡنَهُمَاۖ فَإِنۢ بَغَتۡ إِحۡدَىٰهُمَا عَلَى ٱلۡأُخۡرَىٰ فَقَٰتِلُواْ ٱلَّتِي تَبۡغِي حَتَّىٰ تَفِيٓءَ إِلَىٰٓ أَمۡرِ ٱللَّهِۚ فَإِن فَآءَتۡ فَأَصۡلِحُواْ بَيۡنَهُمَا بِٱلۡعَدۡلِ وَأَقۡسِطُوٓاْۖ إِنَّ ٱللَّهَ يُحِبُّ ٱلۡمُقۡسِطِينَ ٩','وإن طائفتان من المؤمنين اقتتلوا فأصلحوا بينهما فإن بغت إحداهما على الأخرى فقاتلوا التي تبغي حتى تفيء إلى أمر الله فإن فاءت فأصلحوا بينهما بالعدل وأقسطوا إن الله يحب المقسطين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4622,26,49,'Al-Ḥujurāt','الحُجُرَات',516,11,12,10,'إِنَّمَا ٱلۡمُؤۡمِنُونَ إِخۡوَةٞ فَأَصۡلِحُواْ بَيۡنَ أَخَوَيۡكُمۡۚ وَٱتَّقُواْ ٱللَّهَ لَعَلَّكُمۡ تُرۡحَمُونَ ١٠','إنما المؤمنون إخوة فأصلحوا بين أخويكم واتقوا الله لعلكم ترحمون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4623,26,49,'Al-Ḥujurāt','الحُجُرَات',516,12,15,11,'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ لَا يَسۡخَرۡ قَوۡمٞ مِّن قَوۡمٍ عَسَىٰٓ أَن يَكُونُواْ خَيۡرٗا مِّنۡهُمۡ وَلَا نِسَآءٞ مِّن نِّسَآءٍ عَسَىٰٓ أَن يَكُنَّ خَيۡرٗا مِّنۡهُنَّۖ وَلَا تَلۡمِزُوٓاْ أَنفُسَكُمۡ وَلَا تَنَابَزُواْ بِٱلۡأَلۡقَٰبِۖ بِئۡسَ ٱلِٱسۡمُ ٱلۡفُسُوقُ بَعۡدَ ٱلۡإِيمَٰنِۚ وَمَن لَّمۡ يَتُبۡ فَأُوْلَٰٓئِكَ هُمُ ٱلظَّٰلِمُونَ ١١','ياأيها الذين آمنوا لا يسخر قوم من قوم عسى أن يكونوا خيرا منهم ولا نساء من نساء عسى أن يكن خيرا منهن ولا تلمزوا أنفسكم ولا تنابزوا بالألقاب بئس الاسم الفسوق بعد الإيمان ومن لم يتب فأولئك هم الظالمون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4624,26,49,'Al-Ḥujurāt','الحُجُرَات',517,1,4,12,'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُواْ ٱجۡتَنِبُواْ كَثِيرٗا مِّنَ ٱلظَّنِّ إِنَّ بَعۡضَ ٱلظَّنِّ إِثۡمٞۖ وَلَا تَجَسَّسُواْ وَلَا يَغۡتَب بَّعۡضُكُم بَعۡضًاۚ أَيُحِبُّ أَحَدُكُمۡ أَن يَأۡكُلَ لَحۡمَ أَخِيهِ مَيۡتٗا فَكَرِهۡتُمُوهُۚ وَٱتَّقُواْ ٱللَّهَۚ إِنَّ ٱللَّهَ تَوَّابٞ رَّحِيمٞ ١٢','ياأيها الذين آمنوا اجتنبوا كثيرا من الظن إن بعض الظن إثم ولا تجسسوا ولا يغتب بعضكم بعضا أيحب أحدكم أن يأكل لحم أخيه ميتا فكرهتموه واتقوا الله إن الله تواب رحيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4625,26,49,'Al-Ḥujurāt','الحُجُرَات',517,4,6,13,'يَٰٓأَيُّهَا ٱلنَّاسُ إِنَّا خَلَقۡنَٰكُم مِّن ذَكَرٖ وَأُنثَىٰ وَجَعَلۡنَٰكُمۡ شُعُوبٗا وَقَبَآئِلَ لِتَعَارَفُوٓاْۚ إِنَّ أَكۡرَمَكُمۡ عِندَ ٱللَّهِ أَتۡقَىٰكُمۡۚ إِنَّ ٱللَّهَ عَلِيمٌ خَبِيرٞ ١٣','ياأيها الناس إنا خلقناكم من ذكر وأنثى وجعلناكم شعوبا وقبائل لتعارفوا إن أكرمكم عند الله أتقاكم إن الله عليم خبير');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4626,26,49,'Al-Ḥujurāt','الحُجُرَات',517,6,8,14,'۞ قَالَتِ ٱلۡأَعۡرَابُ ءَامَنَّاۖ قُل لَّمۡ تُؤۡمِنُواْ وَلَٰكِن قُولُوٓاْ أَسۡلَمۡنَا وَلَمَّا يَدۡخُلِ ٱلۡإِيمَٰنُ فِي قُلُوبِكُمۡۖ وَإِن تُطِيعُواْ ٱللَّهَ وَرَسُولَهُۥ لَا يَلِتۡكُم مِّنۡ أَعۡمَٰلِكُمۡ شَيۡـًٔاۚ إِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٌ ١٤','قالت الأعراب آمنا قل لم تؤمنوا ولكن قولوا أسلمنا ولما يدخل الإيمان في قلوبكم وإن تطيعوا الله ورسوله لا يلتكم من أعمالكم شيئا إن الله غفور رحيم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4627,26,49,'Al-Ḥujurāt','الحُجُرَات',517,9,11,15,'إِنَّمَا ٱلۡمُؤۡمِنُونَ ٱلَّذِينَ ءَامَنُواْ بِٱللَّهِ وَرَسُولِهِۦ ثُمَّ لَمۡ يَرۡتَابُواْ وَجَٰهَدُواْ بِأَمۡوَٰلِهِمۡ وَأَنفُسِهِمۡ فِي سَبِيلِ ٱللَّهِۚ أُوْلَٰٓئِكَ هُمُ ٱلصَّٰدِقُونَ ١٥','إنما المؤمنون الذين آمنوا بالله ورسوله ثم لم يرتابوا وجاهدوا بأموالهم وأنفسهم في سبيل الله أولئك هم الصادقون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4628,26,49,'Al-Ḥujurāt','الحُجُرَات',517,11,12,16,'قُلۡ أَتُعَلِّمُونَ ٱللَّهَ بِدِينِكُمۡ وَٱللَّهُ يَعۡلَمُ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۚ وَٱللَّهُ بِكُلِّ شَيۡءٍ عَلِيمٞ ١٦','قل أتعلمون الله بدينكم والله يعلم ما في السموات وما في الأرض والله بكل شيء عليم');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4629,26,49,'Al-Ḥujurāt','الحُجُرَات',517,12,14,17,'يَمُنُّونَ عَلَيۡكَ أَنۡ أَسۡلَمُواْۖ قُل لَّا تَمُنُّواْ عَلَيَّ إِسۡلَٰمَكُمۖ بَلِ ٱللَّهُ يَمُنُّ عَلَيۡكُمۡ أَنۡ هَدَىٰكُمۡ لِلۡإِيمَٰنِ إِن كُنتُمۡ صَٰدِقِينَ ١٧','يمنون عليك أن أسلموا قل لا تمنوا علي إسلامكم بل الله يمن عليكم أن هداكم للإيمان إن كنتم صادقين');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4630,26,49,'Al-Ḥujurāt','الحُجُرَات',517,14,15,18,'إِنَّ ٱللَّهَ يَعۡلَمُ غَيۡبَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَٱللَّهُ بَصِيرُۢ بِمَا تَعۡمَلُونَ ١٨','إن الله يعلم غيب السموات والأرض والله بصير بما تعملون');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4631,26,50,'Qāf','قٓ',518,3,3,1,'قٓۚ وَٱلۡقُرۡءَانِ ٱلۡمَجِيدِ ١','ق والقرآن المجيد');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4632,26,50,'Qāf','قٓ',518,3,4,2,'بَلۡ عَجِبُوٓاْ أَن جَآءَهُم مُّنذِرٞ مِّنۡهُمۡ فَقَالَ ٱلۡكَٰفِرُونَ هَٰذَا شَيۡءٌ عَجِيبٌ ٢','بل عجبوا أن جاءهم منذر منهم فقال الكافرون هذا شيء عجيب');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4633,26,50,'Qāf','قٓ',518,4,5,3,'أَءِذَا مِتۡنَا وَكُنَّا تُرَابٗاۖ ذَٰلِكَ رَجۡعُۢ بَعِيدٞ ٣','أئذا متنا وكنا ترابا ذلك رجع بعيد');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4634,26,50,'Qāf','قٓ',518,5,6,4,'قَدۡ عَلِمۡنَا مَا تَنقُصُ ٱلۡأَرۡضُ مِنۡهُمۡۖ وَعِندَنَا كِتَٰبٌ حَفِيظُۢ ٤','قد علمنا ما تنقص الأرض منهم وعندنا كتاب حفيظ');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4635,26,50,'Qāf','قٓ',518,6,6,5,'بَلۡ كَذَّبُواْ بِٱلۡحَقِّ لَمَّا جَآءَهُمۡ فَهُمۡ فِيٓ أَمۡرٖ مَّرِيجٍ ٥','بل كذبوا بالحق لما جاءهم فهم في أمر مريج');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4636,26,50,'Qāf','قٓ',518,7,8,6,'أَفَلَمۡ يَنظُرُوٓاْ إِلَى ٱلسَّمَآءِ فَوۡقَهُمۡ كَيۡفَ بَنَيۡنَٰهَا وَزَيَّنَّٰهَا وَمَا لَهَا مِن فُرُوجٖ ٦','أفلم ينظروا إلى السماء فوقهم كيف بنيناها وزيناها وما لها من فروج');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4637,26,50,'Qāf','قٓ',518,8,9,7,'وَٱلۡأَرۡضَ مَدَدۡنَٰهَا وَأَلۡقَيۡنَا فِيهَا رَوَٰسِيَ وَأَنۢبَتۡنَا فِيهَا مِن كُلِّ زَوۡجِۭ بَهِيجٖ ٧','والأرض مددناها وألقينا فيها رواسي وأنبتنا فيها من كل زوج بهيج');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4638,26,50,'Qāf','قٓ',518,9,10,8,'تَبۡصِرَةٗ وَذِكۡرَىٰ لِكُلِّ عَبۡدٖ مُّنِيبٖ ٨','تبصرة وذكرى لكل عبد منيب');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4639,26,50,'Qāf','قٓ',518,10,11,9,'وَنَزَّلۡنَا مِنَ ٱلسَّمَآءِ مَآءٗ مُّبَٰرَكٗا فَأَنۢبَتۡنَا بِهِۦ جَنَّٰتٖ وَحَبَّ ٱلۡحَصِيدِ ٩','ونزلنا من السماء ماء مباركا فأنبتنا به جنات وحب الحصيد');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4640,26,50,'Qāf','قٓ',518,11,11,10,'وَٱلنَّخۡلَ بَاسِقَٰتٖ لَّهَا طَلۡعٞ نَّضِيدٞ ١٠','والنخل باسقات لها طلع نضيد');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4641,26,50,'Qāf','قٓ',518,11,12,11,'رِّزۡقٗا لِّلۡعِبَادِۖ وَأَحۡيَيۡنَا بِهِۦ بَلۡدَةٗ مَّيۡتٗاۚ كَذَٰلِكَ ٱلۡخُرُوجُ ١١','رزقا للعباد وأحيينا به بلدة ميتا كذلك الخروج');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4642,26,50,'Qāf','قٓ',518,12,13,12,'كَذَّبَتۡ قَبۡلَهُمۡ قَوۡمُ نُوحٖ وَأَصۡحَٰبُ ٱلرَّسِّ وَثَمُودُ ١٢','كذبت قبلهم قوم نوح وأصحاب الرس وثمود');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4643,26,50,'Qāf','قٓ',518,13,13,13,'وَعَادٞ وَفِرۡعَوۡنُ وَإِخۡوَٰنُ لُوطٖ ١٣','وعاد وفرعون وإخوان لوط');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4644,26,50,'Qāf','قٓ',518,14,14,14,'وَأَصۡحَٰبُ ٱلۡأَيۡكَةِ وَقَوۡمُ تُبَّعٖۚ كُلّٞ كَذَّبَ ٱلرُّسُلَ فَحَقَّ وَعِيدِ ١٤','وأصحاب الأيكة وقوم تبع كل كذب الرسل فحق وعيد');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4645,26,50,'Qāf','قٓ',518,15,15,15,'أَفَعَيِينَا بِٱلۡخَلۡقِ ٱلۡأَوَّلِۚ بَلۡ هُمۡ فِي لَبۡسٖ مِّنۡ خَلۡقٖ جَدِيدٖ ١٥','أفعيينا بالخلق الأول بل هم في لبس من خلق جديد');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4646,26,50,'Qāf','قٓ',519,1,2,16,'وَلَقَدۡ خَلَقۡنَا ٱلۡإِنسَٰنَ وَنَعۡلَمُ مَا تُوَسۡوِسُ بِهِۦ نَفۡسُهُۥۖ وَنَحۡنُ أَقۡرَبُ إِلَيۡهِ مِنۡ حَبۡلِ ٱلۡوَرِيدِ ١٦','ولقد خلقنا الإنسان ونعلم ما توسوس به نفسه ونحن أقرب إليه من حبل الوريد');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4647,26,50,'Qāf','قٓ',519,2,3,17,'إِذۡ يَتَلَقَّى ٱلۡمُتَلَقِّيَانِ عَنِ ٱلۡيَمِينِ وَعَنِ ٱلشِّمَالِ قَعِيدٞ ١٧','إذ يتلقى المتلقيان عن اليمين وعن الشمال قعيد');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4648,26,50,'Qāf','قٓ',519,3,3,18,'مَّا يَلۡفِظُ مِن قَوۡلٍ إِلَّا لَدَيۡهِ رَقِيبٌ عَتِيدٞ ١٨','ما يلفظ من قول إلا لديه رقيب عتيد');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4649,26,50,'Qāf','قٓ',519,3,4,19,'وَجَآءَتۡ سَكۡرَةُ ٱلۡمَوۡتِ بِٱلۡحَقِّۖ ذَٰلِكَ مَا كُنتَ مِنۡهُ تَحِيدُ ١٩','وجاءت سكرة الموت بالحق ذلك ما كنت منه تحيد');
INSERT INTO `` (`id`,`jozz`,`sora`,`sora_name_en`,`sora_name_ar`,`page`,`line_start`,`line_end`,`aya_no`,`aya_text`,`aya_text_emlaey`) VALUES (4650,26,50,'Qāf','قٓ',519,4,5,20,'وَنُفِخَ فِي ٱلصُّورِۚ ذَٰلِكَ يَوۡمُ ٱلۡوَعِيدِ ٢٠','ونفخ في الصور ذلك يوم الوعيد');
