INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (7,1,'1',1,'Al-Fātiḥah','الفَاتِحة',7,8,7,'غَيْرِ اِ۬لْمَغْضُوبِ عَلَيْهِمْ وَلَا اَ۬لضَّآلِّينَۖ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (8,1,'2',2,'Al-Baqarah','البَقَرَة',3,4,1,'أَلَٓمِّٓۖ ذَٰلِكَ اَ۬لْكِتَٰبُ لَا رَيْبَۖ فِيهِ هُدىٗ لِّلْمُتَّقِينَ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (9,1,'2',2,'Al-Baqarah','البَقَرَة',4,5,2,'اَ۬لذِينَ يُؤْمِنُونَ بِالْغَيْبِ وَيُقِيمُونَ اَ۬لصَّلَوٰةَ وَمِمَّا رَزَقْنَٰهُمْ يُنفِقُونَۖ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (10,1,'2',2,'Al-Baqarah','البَقَرَة',5,6,3,'وَالذِينَ يُؤْمِنُونَ بِمَا أُنزِلَ إِلَيْكَ وَمَا أُنزِلَ مِن قَبْلِكَ وَبِالْأٓخِرَةِ هُمْ يُوقِنُونَ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (11,1,'2',2,'Al-Baqarah','البَقَرَة',7,8,4,'أُوْلَٰٓئِكَ عَلَىٰ هُدىٗ مِّن رَّبِّهِمْۖ وَأُوْلَٰٓئِكَ هُمُ اُ۬لْمُفْلِحُونَۖ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (12,1,'3',2,'Al-Baqarah','البَقَرَة',1,2,5,'إِنَّ اَ۬لذِينَ كَفَرُواْ سَوَآءٌ عَلَيْهِمْ ءَٰا۬نذَرْتَهُمْ أَمْ لَمْ تُنذِرْهُمْ لَا يُؤْمِنُونَۖ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (13,1,'3',2,'Al-Baqarah','البَقَرَة',2,3,6,'خَتَمَ اَ۬للَّهُ عَلَىٰ قُلُوبِهِمْ وَعَلَىٰ سَمْعِهِمْۖ وَعَلَىٰ أَبْصَٰرِهِمْ غِشَٰوَةٞۖ وَلَهُمْ عَذَابٌ عَظِيمٞۖ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (14,1,'3',2,'Al-Baqarah','البَقَرَة',3,4,7,'وَمِنَ اَ۬لنَّاسِ مَنْ يَّقُولُ ءَامَنَّا بِاللَّهِ وَبِالْيَوْمِ اِ۬لْأٓخِرِ وَمَا هُم بِمُؤْمِنِينَۖ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (15,1,'3',2,'Al-Baqarah','البَقَرَة',5,6,8,'يُخَٰدِعُونَ اَ۬للَّهَ وَالذِينَ ءَامَنُواْ وَمَا يُخَٰدِعُونَ إِلَّا أَنفُسَهُمْ وَمَا يَشْعُرُونَۖ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (16,1,'3',2,'Al-Baqarah','البَقَرَة',6,7,9,'فِے قُلُوبِهِم مَّرَضٞ فَزَادَهُمُ اُ۬للَّهُ مَرَضاٗۖ وَلَهُمْ عَذَابٌ أَلِيمُۢ بِمَا كَانُواْ يُكَذِّبُونَۖ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (17,1,'3',2,'Al-Baqarah','البَقَرَة',7,8,10,'وَإِذَا قِيلَ لَهُمْ لَا تُفْسِدُواْ فِے اِ۬لْأَرْضِ قَالُواْ إِنَّمَا نَحْنُ مُصْلِحُونَۖ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (18,1,'3',2,'Al-Baqarah','البَقَرَة',8,9,11,'أَلَا إِنَّهُمْ هُمُ اُ۬لْمُفْسِدُونَ وَلَٰكِن لَّا يَشْعُرُونَۖ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (19,1,'3',2,'Al-Baqarah','البَقَرَة',9,11,12,'وَإِذَا قِيلَ لَهُمْ ءَامِنُواْ كَمَا ءَامَنَ اَ۬لنَّاسُ قَالُواْ أَنُؤْمِنُ كَمَا ءَامَنَ اَ۬لسُّفَهَآءُۖ اَ۬لَا إِنَّهُمْ هُمُ اُ۬لسُّفَهَآءُ وَلَٰكِن لَّا يَعْلَمُونَۖ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (20,1,'3',2,'Al-Baqarah','البَقَرَة',11,13,13,'۞ وَإِذَا لَقُواْ اُ۬لذِينَ ءَامَنُواْ قَالُواْ ءَامَنَّاۖ وَإِذَا خَلَوْاْ إِلَىٰ شَيَٰطِينِهِمْ قَالُواْ إِنَّا مَعَكُمْ إِنَّمَا نَحْنُ مُسْتَهْزِءُونَۖ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (21,1,'3',2,'Al-Baqarah','البَقَرَة',13,14,14,'اَ۬للَّهُ يَسْتَهْزِۓُ بِهِمْ وَيَمُدُّهُمْ فِے طُغْيَٰنِهِمْ يَعْمَهُونَۖ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (22,1,'3',2,'Al-Baqarah','البَقَرَة',14,15,15,'أُوْلَٰٓئِكَ اَ۬لذِينَ اَ۪شْتَرَوُاْ اُ۬لضَّلَٰلَةَ بِالْهُدَىٰ فَمَا رَبِحَت تِّجَٰرَتُهُمْ وَمَا كَانُواْ مُهْتَدِينَۖ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (23,1,'4',2,'Al-Baqarah','البَقَرَة',1,2,16,'۞ مَثَلُهُمْ كَمَثَلِ اِ۬لذِے اِ۪سْتَوْقَدَ نَاراٗ فَلَمَّا أَضَآءَتْ مَا حَوْلَهُۥ ذَهَبَ اَ۬للَّهُ بِنُورِهِمْ وَتَرَكَهُمْ فِے ظُلُمَٰتٖ لَّا يُبْصِرُونَۖ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (24,1,'4',2,'Al-Baqarah','البَقَرَة',2,3,17,'صُمُّۢ بُكْمٌ عُمْيٞ فَهُمْ لَا يَرْجِعُونَۖ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (25,1,'4',2,'Al-Baqarah','البَقَرَة',3,5,18,'أَوْ كَصَيِّبٖ مِّنَ اَ۬لسَّمَآءِ فِيهِ ظُلُمَٰتٞ وَرَعْدٞ وَبَرْقٞۖ يَجْعَلُونَ أَصَٰبِعَهُمْ فِے ءَاذَانِهِم مِّنَ اَ۬لصَّوَٰعِقِ حَذَرَ اَ۬لْمَوْتِۖ وَاللَّهُ مُحِيطُۢ بِالْكَٰفِرِينَۖ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (26,1,'4',2,'Al-Baqarah','البَقَرَة',5,7,19,'يَكَادُ اُ۬لْبَرْقُ يَخْطَفُ أَبْصَٰرَهُمْۖ كُلَّمَا أَضَآءَ لَهُم مَّشَوْاْ فِيهِۖ وَإِذَا أَظْلَمَ عَلَيْهِمْ قَامُواْۖ وَلَوْ شَآءَ اَ۬للَّهُ لَذَهَبَ بِسَمْعِهِمْ وَأَبْصَٰرِهِمْۖ إِنَّ اَ۬للَّهَ عَلَىٰ كُلِّ شَےْءٖ قَدِيرٞۖ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (27,1,'4',2,'Al-Baqarah','البَقَرَة',8,9,20,'يَٰأَيُّهَا اَ۬لنَّاسُ اُ۟عْبُدُواْ رَبَّكُمُ اُ۬لذِے خَلَقَكُمْ وَالذِينَ مِن قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُونَۖ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (28,1,'4',2,'Al-Baqarah','البَقَرَة',9,12,21,'اَ۬لذِے جَعَلَ لَكُمُ اُ۬لْأَرْضَ فِرَٰشاٗ وَالسَّمَآءَ بِنَآءٗ وَأَنزَلَ مِنَ اَ۬لسَّمَآءِ مَآءٗ فَأَخْرَجَ بِهِۦ مِنَ اَ۬لثَّمَرَٰتِ رِزْقاٗ لَّكُمْۖ فَلَا تَجْعَلُواْ لِلهِ أَندَاداٗ وَأَنتُمْ تَعْلَمُونَۖ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (29,1,'4',2,'Al-Baqarah','البَقَرَة',12,14,22,'وَإِن كُنتُمْ فِے رَيْبٖ مِّمَّا نَزَّلْنَا عَلَىٰ عَبْدِنَا فَأْتُواْ بِسُورَةٖ مِّن مِّثْلِهِۦ وَادْعُواْ شُهَدَآءَكُم مِّن دُونِ اِ۬للَّهِ إِن كُنتُمْ صَٰدِقِينَۖ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (30,1,'4',2,'Al-Baqarah','البَقَرَة',14,15,23,'فَإِن لَّمْ تَفْعَلُواْ وَلَن تَفْعَلُواْ فَاتَّقُواْ اُ۬لنَّارَ اَ۬لتِے وَقُودُهَا اَ۬لنَّاسُ وَالْحِجَارَةُۖ أُعِدَّتْ لِلْكَٰفِرِينَۖ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (31,1,'5',2,'Al-Baqarah','البَقَرَة',1,4,24,'وَبَشِّرِ اِ۬لذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ أَنَّ لَهُمْ جَنَّٰتٖ تَجْرِے مِن تَحْتِهَا اَ۬لْأَنْهَٰرُۖ كُلَّمَا رُزِقُواْ مِنْهَا مِن ثَمَرَةٖ رِّزْقاٗ قَالُواْ هَٰذَا اَ۬لذِے رُزِقْنَا مِن قَبْلُۖ وَأُتُواْ بِهِۦ مُتَشَٰبِهاٗۖ وَلَهُمْ فِيهَا أَزْوَٰجٞ مُّطَهَّرَةٞۖ وَهُمْ فِيهَا خَٰلِدُونَۖ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (32,1,'5',2,'Al-Baqarah','البَقَرَة',4,9,25,'۞ إِنَّ اَ۬للَّهَ لَا يَسْتَحْيِۦ أَنْ يَّضْرِبَ مَثَلاٗ مَّا بَعُوضَةٗ فَمَا فَوْقَهَاۖ فَأَمَّا اَ۬لذِينَ ءَامَنُواْ فَيَعْلَمُونَ أَنَّهُ اُ۬لْحَقُّ مِن رَّبِّهِمْۖ وَأَمَّا اَ۬لذِينَ كَفَرُواْ فَيَقُولُونَ مَاذَا أَرَادَ اَ۬للَّهُ بِهَٰذَا مَثَلاٗۖ يُضِلُّ بِهِۦ كَثِيراٗ وَيَهْدِے بِهِۦ كَثِيراٗۖ وَمَا يُضِلُّ بِهِۦ إِلَّا اَ۬لْفَٰسِقِينَ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (33,1,'5',2,'Al-Baqarah','البَقَرَة',9,11,26,'اَ۬لذِينَ يَنقُضُونَ عَهْدَ اَ۬للَّهِ مِنۢ بَعْدِ مِيثَٰقِهِۦ وَيَقْطَعُونَ مَا أَمَرَ اَ۬للَّهُ بِهِۦ أَنْ يُّوصَلَ وَيُفْسِدُونَ فِے اِ۬لْأَرْضِۖ أُوْلَٰٓئِكَ هُمُ اُ۬لْخَٰسِرُونَۖ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (34,1,'5',2,'Al-Baqarah','البَقَرَة',11,13,27,'كَيْفَ تَكْفُرُونَ بِاللَّهِ وَكُنتُمْ أَمْوَٰتاٗ فَأَحْيَاكُمْۖ ثُمَّ يُمِيتُكُمْ ثُمَّ يُحْيِيكُمْ ثُمَّ إِلَيْهِ تُرْجَعُونَۖ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (35,1,'5',2,'Al-Baqarah','البَقَرَة',13,15,28,'هُوَ اَ۬لذِے خَلَقَ لَكُم مَّا فِے اِ۬لْأَرْضِ جَمِيعاٗۖ ثُمَّ اَ۪سْتَوَىٰ إِلَى اَ۬لسَّمَآءِ فَسَوَّيٰهُنَّ سَبْعَ سَمَٰوَٰتٖۖ وَهْوَ بِكُلِّ شَےْءٍ عَلِيمٞۖ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (36,1,'6',2,'Al-Baqarah','البَقَرَة',1,3,29,'وَإِذْ قَالَ رَبُّكَ لِلْمَلَٰٓئِكَةِ إِنِّے جَاعِلٞ فِے اِ۬لْأَرْضِ خَلِيفَةٗۖ قَالُواْ أَتَجْعَلُ فِيهَا مَنْ يُّفْسِدُ فِيهَا وَيَسْفِكُ اُ۬لدِّمَآءَ وَنَحْنُ نُسَبِّحُ بِحَمْدِكَ وَنُقَدِّسُ لَكَۖ قَالَ إِنِّيَ أَعْلَمُ مَا لَا تَعْلَمُونَۖ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (37,1,'6',2,'Al-Baqarah','البَقَرَة',4,5,30,'وَعَلَّمَ ءَادَمَ اَ۬لْأَسْمَآءَ كُلَّهَا ثُمَّ عَرَضَهُمْ عَلَى اَ۬لْمَلَٰٓئِكَةِ فَقَالَ أَنۢبِـُٔونِے بِأَسْمَآءِ هَٰؤُلَآ۟ إِن كُنتُمْ صَٰدِقِينَۖ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (38,1,'6',2,'Al-Baqarah','البَقَرَة',5,6,31,'قَالُواْ سُبْحَٰنَكَ لَا عِلْمَ لَنَا إِلَّا مَا عَلَّمْتَنَا إِنَّكَ أَنتَ اَ۬لْعَلِيمُ اُ۬لْحَكِيمُۖ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (39,1,'6',2,'Al-Baqarah','البَقَرَة',6,9,32,'قَالَ يَٰـَٔادَمُ أَنۢبِئْهُم بِأَسْمَآئِهِمْۖ فَلَمَّا أَنۢبَأَهُم بِأَسْمَآئِهِمْ قَالَ أَلَمْ أَقُل لَّكُمْ إِنِّيَ أَعْلَمُ غَيْبَ اَ۬لسَّمَٰوَٰتِ وَالْأَرْضِ وَأَعْلَمُ مَا تُبْدُونَ وَمَا كُنتُمْ تَكْتُمُونَۖ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (40,1,'6',2,'Al-Baqarah','البَقَرَة',9,10,33,'۞ وَإِذْ قُلْنَا لِلْمَلَٰٓئِكَةِ اِ۟سْجُدُواْ لِأٓدَمَ فَسَجَدُواْ إِلَّا إِبْلِيسَ أَبَىٰ وَاسْتَكْبَرَ وَكَانَ مِنَ اَ۬لْكَٰفِرِينَۖ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (41,1,'6',2,'Al-Baqarah','البَقَرَة',10,12,34,'وَقُلْنَا يَٰـَٔادَمُ اُ۟سْكُنْ أَنتَ وَزَوْجُكَ اَ۬لْجَنَّةَ وَكُلَا مِنْهَا رَغَداً حَيْثُ شِئْتُمَاۖ وَلَا تَقْرَبَا هَٰذِهِ اِ۬لشَّجَرَةَ فَتَكُونَا مِنَ اَ۬لظَّٰلِمِينَۖ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (42,1,'6',2,'Al-Baqarah','البَقَرَة',12,14,35,'فَأَزَلَّهُمَا اَ۬لشَّيْطَٰنُ عَنْهَا فَأَخْرَجَهُمَا مِمَّا كَانَا فِيهِۖ وَقُلْنَا اَ۪هْبِطُواْۖ بَعْضُكُمْ لِبَعْضٍ عَدُوّٞۖ وَلَكُمْ فِے اِ۬لْأَرْضِ مُسْتَقَرّٞ وَمَتَٰعٌ إِلَىٰ حِينٖۖ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (43,1,'6',2,'Al-Baqarah','البَقَرَة',14,15,36,'فَتَلَقَّىٰ ءَادَمُ مِن رَّبِّهِۦ كَلِمَٰتٖ فَتَابَ عَلَيْهِۖ إِنَّهُۥ هُوَ اَ۬لتَّوَّابُ اُ۬لرَّحِيمُۖ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (44,1,'7',2,'Al-Baqarah','البَقَرَة',1,2,37,'قُلْنَا اَ۪هْبِطُواْ مِنْهَا جَمِيعاٗۖ فَإِمَّا يَأْتِيَنَّكُم مِّنِّے هُدىٗ فَمَن تَبِعَ هُدَايَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَۖ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (45,1,'7',2,'Al-Baqarah','البَقَرَة',2,3,38,'وَالذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَا أُوْلَٰٓئِكَ أَصْحَٰبُ اُ۬لنَّارِ هُمْ فِيهَا خَٰلِدُونَۖ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (46,1,'7',2,'Al-Baqarah','البَقَرَة',3,5,39,'يَٰبَنِے إِسْرَآءِيلَ اَ۟ذْكُرُواْ نِعْمَتِيَ اَ۬لتِے أَنْعَمْتُ عَلَيْكُمْ وَأَوْفُواْ بِعَهْدِے أُوفِ بِعَهْدِكُمْ وَإِيَّٰيَ فَارْهَبُونِۖ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (47,1,'7',2,'Al-Baqarah','البَقَرَة',5,7,40,'وَءَامِنُواْ بِمَا أَنزَلْتُ مُصَدِّقاٗ لِّمَا مَعَكُمْ وَلَا تَكُونُواْ أَوَّلَ كَافِرِۢ بِهِۦۖ وَلَا تَشْتَرُواْ بِـَٔايَٰتِے ثَمَناٗ قَلِيلاٗ وَإِيَّٰيَ فَاتَّقُونِۖ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (48,1,'7',2,'Al-Baqarah','البَقَرَة',7,8,41,'وَلَا تَلْبِسُواْ اُ۬لْحَقَّ بِالْبَٰطِلِ وَتَكْتُمُواْ اُ۬لْحَقَّ وَأَنتُمْ تَعْلَمُونَۖ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (49,1,'7',2,'Al-Baqarah','البَقَرَة',8,9,42,'وَأَقِيمُواْ اُ۬لصَّلَوٰةَ وَءَاتُواْ اُ۬لزَّكَوٰةَۖ وَارْكَعُواْ مَعَ اَ۬لرَّٰكِعِينَۖ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (50,1,'7',2,'Al-Baqarah','البَقَرَة',9,10,43,'۞ أَتَأْمُرُونَ اَ۬لنَّاسَ بِالْبِرِّ وَتَنسَوْنَ أَنفُسَكُمْ وَأَنتُمْ تَتْلُونَ اَ۬لْكِتَٰبَۖ أَفَلَا تَعْقِلُونَۖ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (51,1,'7',2,'Al-Baqarah','البَقَرَة',10,11,44,'وَاسْتَعِينُواْ بِالصَّبْرِ وَالصَّلَوٰةِۖ وَإِنَّهَا لَكَبِيرَةٌ إِلَّا عَلَى اَ۬لْخَٰشِعِينَ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (52,1,'7',2,'Al-Baqarah','البَقَرَة',11,12,45,'اَ۬لذِينَ يَظُنُّونَ أَنَّهُم مُّلَٰقُواْ رَبِّهِمْ وَأَنَّهُمْ إِلَيْهِ رَٰجِعُونَۖ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (53,1,'7',2,'Al-Baqarah','البَقَرَة',12,14,46,'يَٰبَنِے إِسْرَآءِيلَ اَ۟ذْكُرُواْ نِعْمَتِيَ اَ۬لتِے أَنْعَمْتُ عَلَيْكُمْ وَأَنِّے فَضَّلْتُكُمْ عَلَى اَ۬لْعَٰلَمِينَۖ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (54,1,'7',2,'Al-Baqarah','البَقَرَة',14,15,47,'وَاتَّقُواْ يَوْماٗ لَّا تَجْزِے نَفْسٌ عَن نَّفْسٖ شَئْاٗ وَلَا يُقْبَلُ مِنْهَا شَفَٰعَةٞ وَلَا يُؤْخَذُ مِنْهَا عَدْلٞ وَلَا هُمْ يُنصَرُونَۖ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (55,1,'8',2,'Al-Baqarah','البَقَرَة',1,3,48,'وَإِذْ نَجَّيْنَٰكُم مِّنْ ءَالِ فِرْعَوْنَ يَسُومُونَكُمْ سُوٓءَ اَ۬لْعَذَابِ يُذَبِّحُونَ أَبْنَآءَكُمْ وَيَسْتَحْيُونَ نِسَآءَكُمْۖ وَفِے ذَٰلِكُم بَلَآءٞ مِّن رَّبِّكُمْ عَظِيمٞۖ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (56,1,'8',2,'Al-Baqarah','البَقَرَة',3,4,49,'وَإِذْ فَرَقْنَا بِكُمُ اُ۬لْبَحْرَ فَأَنجَيْنَٰكُمْ وَأَغْرَقْنَا ءَالَ فِرْعَوْنَ وَأَنتُمْ تَنظُرُونَۖ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (57,1,'8',2,'Al-Baqarah','البَقَرَة',4,5,50,'وَإِذْ وَٰعَدْنَا مُوسَىٰ أَرْبَعِينَ لَيْلَةٗ ثُمَّ اَ۪تَّخَذتُّمُ اُ۬لْعِجْلَ مِنۢ بَعْدِهِۦ وَأَنتُمْ ظَٰلِمُونَۖ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (58,1,'8',2,'Al-Baqarah','البَقَرَة',6,6,51,'ثُمَّ عَفَوْنَا عَنكُم مِّنۢ بَعْدِ ذَٰلِكَ لَعَلَّكُمْ تَشْكُرُونَۖ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (59,1,'8',2,'Al-Baqarah','البَقَرَة',7,7,52,'وَإِذْ ءَاتَيْنَا مُوسَى اَ۬لْكِتَٰبَۖ وَالْفُرْقَانَ لَعَلَّكُمْ تَهْتَدُونَۖ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (60,1,'8',2,'Al-Baqarah','البَقَرَة',8,11,53,'۞ وَإِذْ قَالَ مُوسَىٰ لِقَوْمِهِۦ يَٰقَوْمِ إِنَّكُمْ ظَلَمْتُمْ أَنفُسَكُم بِاتِّخَاذِكُمُ اُ۬لْعِجْلَ فَتُوبُواْ إِلَىٰ بَارِئِكُمْ فَاقْتُلُواْ أَنفُسَكُمْۖ ذَٰلِكُمْ خَيْرٞ لَّكُمْ عِندَ بَارِئِكُمْۖ فَتَابَ عَلَيْكُمْۖ إِنَّهُۥ هُوَ اَ۬لتَّوَّابُ اُ۬لرَّحِيمُۖ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (61,1,'8',2,'Al-Baqarah','البَقَرَة',11,12,54,'وَإِذْ قُلْتُمْ يَٰمُوسَىٰ لَن نُّؤْمِنَ لَكَ حَتَّىٰ نَرَى اَ۬للَّهَ جَهْرَةٗ فَأَخَذَتْكُمُ اُ۬لصَّٰعِقَةُ وَأَنتُمْ تَنظُرُونَۖ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (62,1,'8',2,'Al-Baqarah','البَقَرَة',12,13,55,'ثُمَّ بَعَثْنَٰكُم مِّنۢ بَعْدِ مَوْتِكُمْ لَعَلَّكُمْ تَشْكُرُونَۖ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (63,1,'8',2,'Al-Baqarah','البَقَرَة',13,15,56,'وَظَلَّلْنَا عَلَيْكُمُ اُ۬لْغَمَٰمَ وَأَنزَلْنَا عَلَيْكُمُ اُ۬لْمَنَّ وَالسَّلْوَىٰۖ كُلُواْ مِن طَيِّبَٰتِ مَا رَزَقْنَٰكُمْۖ وَمَا ظَلَمُونَاۖ وَلَٰكِن كَانُواْ أَنفُسَهُمْ يَظْلِمُونَۖ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (64,1,'9',2,'Al-Baqarah','البَقَرَة',1,3,57,'وَإِذْ قُلْنَا اَ۟دْخُلُواْ هَٰذِهِ اِ۬لْقَرْيَةَ فَكُلُواْ مِنْهَا حَيْثُ شِئْتُمْ رَغَداٗ وَادْخُلُواْ اُ۬لْبَابَ سُجَّداٗ وَقُولُواْ حِطَّةٞ يُغْفَرْ لَكُمْ خَطَٰيَٰكُمْۖ وَسَنَزِيدُ اُ۬لْمُحْسِنِينَۖ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (65,1,'9',2,'Al-Baqarah','البَقَرَة',3,5,58,'فَبَدَّلَ اَ۬لذِينَ ظَلَمُواْ قَوْلاً غَيْرَ اَ۬لذِے قِيلَ لَهُمْ فَأَنزَلْنَا عَلَى اَ۬لذِينَ ظَلَمُواْ رِجْزاٗ مِّنَ اَ۬لسَّمَآءِ بِمَا كَانُواْ يَفْسُقُونَۖ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (66,1,'9',2,'Al-Baqarah','البَقَرَة',5,8,59,'۞ وَإِذِ اِ۪سْتَسْقَىٰ مُوسَىٰ لِقَوْمِهِۦ فَقُلْنَا اَ۪ضْرِب بِّعَصَاكَ اَ۬لْحَجَرَۖ فَانفَجَرَتْ مِنْهُ اُ۪ثْنَتَا عَشْرَةَ عَيْناٗۖ قَدْ عَلِمَ كُلُّ أُنَاسٖ مَّشْرَبَهُمْۖ كُلُواْ وَاشْرَبُواْ مِن رِّزْقِ اِ۬للَّهِۖ وَلَا تَعْثَوْاْ فِے اِ۬لْأَرْضِ مُفْسِدِينَۖ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (67,1,'9',2,'Al-Baqarah','البَقَرَة',9,15,60,'وَإِذْ قُلْتُمْ يَٰمُوسَىٰ لَن نَّصْبِرَ عَلَىٰ طَعَامٖ وَٰحِدٖ فَادْعُ لَنَا رَبَّكَ يُخْرِجْ لَنَا مِمَّا تُنۢبِتُ اُ۬لْأَرْضُ مِنۢ بَقْلِهَا وَقِثَّآئِهَا وَفُومِهَا وَعَدَسِهَا وَبَصَلِهَاۖ قَالَ أَتَسْتَبْدِلُونَ اَ۬لذِے هُوَ أَدْنَىٰ بِالذِے هُوَ خَيْرٌۖ اِ۪هْبِطُواْ مِصْراٗ فَإِنَّ لَكُم مَّا سَأَلْتُمْۖ وَضُرِبَتْ عَلَيْهِمُ اُ۬لذِّلَّةُ وَالْمَسْكَنَةُ وَبَآءُو بِغَضَبٖ مِّنَ اَ۬للَّهِۖ ذَٰلِكَ بِأَنَّهُمْ كَانُواْ يَكْفُرُونَ بِـَٔايَٰتِ اِ۬للَّهِ وَيَقْتُلُونَ اَ۬لنَّبِيٓـِٕۧنَ بِغَيْرِ اِ۬لْحَقِّۖ ذَٰلِكَ بِمَا عَصَواْ وَّكَانُواْ يَعْتَدُونَۖ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (68,1,'10',2,'Al-Baqarah','البَقَرَة',1,3,61,'إِنَّ اَ۬لذِينَ ءَامَنُواْ وَالذِينَ هَادُواْ وَالنَّصَٰرَىٰ وَالصَّٰبِينَ مَنْ ءَامَنَ بِاللَّهِ وَالْيَوْمِ اِ۬لْأٓخِرِ وَعَمِلَ صَٰلِحاٗ فَلَهُمْ أَجْرُهُمْ عِندَ رَبِّهِمْ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَۖ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (69,1,'10',2,'Al-Baqarah','البَقَرَة',3,5,62,'وَإِذْ أَخَذْنَا مِيثَٰقَكُمْ وَرَفَعْنَا فَوْقَكُمُ اُ۬لطُّورَۖ خُذُواْ مَا ءَاتَيْنَٰكُم بِقُوَّةٖ وَاذْكُرُواْ مَا فِيهِ لَعَلَّكُمْ تَتَّقُونَۖ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (70,1,'10',2,'Al-Baqarah','البَقَرَة',5,7,63,'ثُمَّ تَوَلَّيْتُم مِّنۢ بَعْدِ ذَٰلِكَۖ فَلَوْلَا فَضْلُ اُ۬للَّهِ عَلَيْكُمْ وَرَحْمَتُهُۥ لَكُنتُم مِّنَ اَ۬لْخَٰسِرِينَۖ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (71,1,'10',2,'Al-Baqarah','البَقَرَة',7,8,64,'وَلَقَدْ عَلِمْتُمُ اُ۬لذِينَ اَ۪عْتَدَوْاْ مِنكُمْ فِے اِ۬لسَّبْتِ فَقُلْنَا لَهُمْ كُونُواْ قِرَدَةً خَٰسِـِٕينَۖ ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (72,1,'10',2,'Al-Baqarah','البَقَرَة',8,9,65,'فَجَعَلْنَٰهَا نَكَٰلاٗ لِّمَا بَيْنَ يَدَيْهَا وَمَا خَلْفَهَا وَمَوْعِظَةٗ لِّلْمُتَّقِينَۖ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (73,1,'10',2,'Al-Baqarah','البَقَرَة',9,11,66,'۞ وَإِذْ قَالَ مُوسَىٰ لِقَوْمِهِۦ إِنَّ اَ۬للَّهَ يَأْمُرُكُمْ أَن تَذْبَحُواْ بَقَرَةٗۖ قَالُواْ أَتَتَّخِذُنَا هُزُؤاٗۖ قَالَ أَعُوذُ بِاللَّهِ أَنْ أَكُونَ مِنَ اَ۬لْجَٰهِلِينَۖ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (74,1,'10',2,'Al-Baqarah','البَقَرَة',12,14,67,'قَالُواْ اُ۟دْعُ لَنَا رَبَّكَ يُبَيِّن لَّنَا مَا هِيَۖ قَالَ إِنَّهُۥ يَقُولُ إِنَّهَا بَقَرَةٞ لَّا فَارِضٞ وَلَا بِكْرٌ عَوَانُۢ بَيْنَ ذَٰلِكَۖ فَافْعَلُواْ مَا تُؤْمَرُونَۖ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (75,1,'10',2,'Al-Baqarah','البَقَرَة',14,15,68,'قَالُواْ اُ۟دْعُ لَنَا رَبَّكَ يُبَيِّن لَّنَا مَا لَوْنُهَاۖ قَالَ إِنَّهُۥ يَقُولُ إِنَّهَا بَقَرَةٞ صَفْرَآءُ فَاقِعٞ لَّوْنُهَاۖ تَسُرُّ اُ۬لنَّٰظِرِينَۖ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (76,1,'11',2,'Al-Baqarah','البَقَرَة',1,2,69,'قَالُواْ اُ۟دْعُ لَنَا رَبَّكَ يُبَيِّن لَّنَا مَا هِيَ إِنَّ اَ۬لْبَقَرَ تَشَٰبَهَ عَلَيْنَا وَإِنَّا إِن شَآءَ اَ۬للَّهُ لَمُهْتَدُونَۖ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (77,1,'11',2,'Al-Baqarah','البَقَرَة',2,4,70,'قَالَ إِنَّهُۥ يَقُولُ إِنَّهَا بَقَرَةٞ لَّا ذَلُولٞ تُثِيرُ اُ۬لْأَرْضَ وَلَا تَسْقِے اِ۬لْحَرْثَۖ مُسَلَّمَةٞ لَّا شِيَةَ فِيهَاۖ قَالُواْ اُ۬ءَلْٰنَ جِئْتَ بِالْحَقِّۖ فَذَبَحُوهَا وَمَا كَادُواْ يَفْعَلُونَۖ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (78,1,'11',2,'Al-Baqarah','البَقَرَة',4,5,71,'وَإِذْ قَتَلْتُمْ نَفْساٗ فَادَّٰرَٰٔتُمْ فِيهَاۖ وَاللَّهُ مُخْرِجٞ مَّا كُنتُمْ تَكْتُمُونَۖ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (79,1,'11',2,'Al-Baqarah','البَقَرَة',6,7,72,'فَقُلْنَا اَ۪ضْرِبُوهُ بِبَعْضِهَاۖ كَذَٰلِكَ يُحْيِ اِ۬للَّهُ اُ۬لْمَوْتَىٰ وَيُرِيكُمْ ءَايَٰتِهِۦ لَعَلَّكُمْ تَعْقِلُونَۖ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (80,1,'11',2,'Al-Baqarah','البَقَرَة',7,10,73,'ثُمَّ قَسَتْ قُلُوبُكُم مِّنۢ بَعْدِ ذَٰلِكَ فَهْيَ كَالْحِجَارَةِ أَوْ أَشَدُّ قَسْوَةٗۖ وَإِنَّ مِنَ اَ۬لْحِجَارَةِ لَمَا يَتَفَجَّرُ مِنْهُ اُ۬لْأَنْهَٰرُۖ وَإِنَّ مِنْهَا لَمَا يَشَّقَّقُ فَيَخْرُجُ مِنْهُ اُ۬لْمَآءُۖ وَإِنَّ مِنْهَا لَمَا يَهْبِطُ مِنْ خَشْيَةِ اِ۬للَّهِۖ وَمَا اَ۬للَّهُ بِغَٰفِلٍ عَمَّا تَعْمَلُونَۖ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (81,1,'11',2,'Al-Baqarah','البَقَرَة',11,13,74,'أَفَتَطْمَعُونَ أَنْ يُّؤْمِنُواْ لَكُمْ وَقَدْ كَانَ فَرِيقٞ مِّنْهُمْ يَسْمَعُونَ كَلَٰمَ اَ۬للَّهِ ثُمَّ يُحَرِّفُونَهُۥ مِنۢ بَعْدِ مَا عَقَلُوهُ وَهُمْ يَعْلَمُونَۖ ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (82,1,'11',2,'Al-Baqarah','البَقَرَة',13,15,75,'۞ وَإِذَا لَقُواْ اُ۬لذِينَ ءَامَنُواْ قَالُواْ ءَامَنَّاۖ وَإِذَا خَلَا بَعْضُهُمْ إِلَىٰ بَعْضٖ قَالُواْ أَتُحَدِّثُونَهُم بِمَا فَتَحَ اَ۬للَّهُ عَلَيْكُمْ لِيُحَآجُّوكُم بِهِۦ عِندَ رَبِّكُمْۖ أَفَلَا تَعْقِلُونَۖ ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (83,1,'12',2,'Al-Baqarah','البَقَرَة',1,1,76,'أَوَلَا يَعْلَمُونَ أَنَّ اَ۬للَّهَ يَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَۖ ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (84,1,'12',2,'Al-Baqarah','البَقَرَة',2,3,77,'وَمِنْهُمْ أُمِّيُّونَ لَا يَعْلَمُونَ اَ۬لْكِتَٰبَ إِلَّا أَمَانِيَّۖ وَإِنْ هُمْ إِلَّا يَظُنُّونَۖ ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (85,1,'12',2,'Al-Baqarah','البَقَرَة',3,5,78,'فَوَيْلٞ لِّلذِينَ يَكْتُبُونَ اَ۬لْكِتَٰبَ بِأَيْدِيهِمْ ثُمَّ يَقُولُونَ هَٰذَا مِنْ عِندِ اِ۬للَّهِ لِيَشْتَرُواْ بِهِۦ ثَمَناٗ قَلِيلاٗۖ فَوَيْلٞ لَّهُم مِّمَّا كَتَبَتْ أَيْدِيهِمْۖ وَوَيْلٞ لَّهُم مِّمَّا يَكْسِبُونَۖ ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (86,1,'12',2,'Al-Baqarah','البَقَرَة',6,8,79,'وَقَالُواْ لَن تَمَسَّنَا اَ۬لنَّارُ إِلَّا أَيَّاماٗ مَّعْدُودَةٗۖ قُلْ أَتَّخَذتُّمْ عِندَ اَ۬للَّهِ عَهْداٗ فَلَنْ يُّخْلِفَ اَ۬للَّهُ عَهْدَهُۥۖ أَمْ تَقُولُونَ عَلَى اَ۬للَّهِ مَا لَا تَعْلَمُونَۖ ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (87,1,'12',2,'Al-Baqarah','البَقَرَة',8,10,80,'بَلَىٰ مَن كَسَبَ سَيِّئَةٗ وَأَحَٰطَتْ بِهِۦ خَطِيٓـَٰٔتُهُۥ فَأُوْلَٰٓئِكَ أَصْحَٰبُ اُ۬لنَّارِ هُمْ فِيهَا خَٰلِدُونَۖ ٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (88,1,'12',2,'Al-Baqarah','البَقَرَة',10,11,81,'وَالذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ أُوْلَٰٓئِكَ أَصْحَٰبُ اُ۬لْجَنَّةِ هُمْ فِيهَا خَٰلِدُونَۖ ٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (89,1,'12',2,'Al-Baqarah','البَقَرَة',11,15,82,'وَإِذْ أَخَذْنَا مِيثَٰقَ بَنِے إِسْرَآءِيلَ لَا تَعْبُدُونَ إِلَّا اَ۬للَّهَۖ وَبِالْوَٰلِدَيْنِ إِحْسَٰناٗ وَذِے اِ۬لْقُرْبَىٰ وَالْيَتَٰمَىٰ وَالْمَسَٰكِينِۖ وَقُولُواْ لِلنَّاسِ حُسْناٗۖ وَأَقِيمُواْ اُ۬لصَّلَوٰةَ وَءَاتُواْ اُ۬لزَّكَوٰةَۖ ثُمَّ تَوَلَّيْتُمْ إِلَّا قَلِيلاٗ مِّنكُمْ وَأَنتُم مُّعْرِضُونَۖ ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (90,1,'13',2,'Al-Baqarah','البَقَرَة',1,2,83,'وَإِذْ أَخَذْنَا مِيثَٰقَكُمْ لَا تَسْفِكُونَ دِمَآءَكُمْ وَلَا تُخْرِجُونَ أَنفُسَكُم مِّن دِيَٰرِكُمْ ثُمَّ أَقْرَرْتُمْ وَأَنتُمْ تَشْهَدُونَۖ ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (91,1,'13',2,'Al-Baqarah','البَقَرَة',3,9,84,'ثُمَّ أَنتُمْ هَٰؤُلَآءِ تَقْتُلُونَ أَنفُسَكُمْ وَتُخْرِجُونَ فَرِيقاٗ مِّنكُم مِّن دِيَٰرِهِمْ تَظَّٰهَرُونَ عَلَيْهِم بِالْإِثْمِ وَالْعُدْوَٰنِۖ ۞ وَإِنْ يَّأْتُوكُمْ أُسَٰرَىٰ تُفَٰدُوهُمْ وَهْوَ مُحَرَّمٌ عَلَيْكُمْ إِخْرَاجُهُمْۖ أَفَتُؤْمِنُونَ بِبَعْضِ اِ۬لْكِتَٰبِ وَتَكْفُرُونَ بِبَعْضٖۖ فَمَا جَزَآءُ مَنْ يَّفْعَلُ ذَٰلِكَ مِنكُمْ إِلَّا خِزْيٞ فِے اِ۬لْحَيَوٰةِ اِ۬لدُّنْيَاۖ وَيَوْمَ اَ۬لْقِيَٰمَةِ يُرَدُّونَ إِلَىٰ أَشَدِّ اِ۬لْعَذَابِۖ وَمَا اَ۬للَّهُ بِغَٰفِلٍ عَمَّا يَعْمَلُونَۖ ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (92,1,'13',2,'Al-Baqarah','البَقَرَة',9,10,85,'أُوْلَٰٓئِكَ اَ۬لذِينَ اَ۪شْتَرَوُاْ اُ۬لْحَيَوٰةَ اَ۬لدُّنْيَا بِالْأٓخِرَةِ فَلَا يُخَفَّفُ عَنْهُمُ اُ۬لْعَذَابُ وَلَا هُمْ يُنصَرُونَۖ ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (93,1,'13',2,'Al-Baqarah','البَقَرَة',11,14,86,'وَلَقَدْ ءَاتَيْنَا مُوسَى اَ۬لْكِتَٰبَ وَقَفَّيْنَا مِنۢ بَعْدِهِۦ بِالرُّسُلِۖ وَءَاتَيْنَا عِيسَى اَ۪بْنَ مَرْيَمَ اَ۬لْبَيِّنَٰتِ وَأَيَّدْنَٰهُ بِرُوحِ اِ۬لْقُدُسِۖ أَفَكُلَّمَا جَآءَكُمْ رَسُولُۢ بِمَا لَا تَهْوَىٰ أَنفُسُكُمُ اُ۪سْتَكْبَرْتُمْۖ فَفَرِيقاٗ كَذَّبْتُمْ وَفَرِيقاٗ تَقْتُلُونَۖ ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (94,1,'13',2,'Al-Baqarah','البَقَرَة',14,15,87,'وَقَالُواْ قُلُوبُنَا غُلْفُۢۖ بَل لَّعَنَهُمُ اُ۬للَّهُ بِكُفْرِهِمْ فَقَلِيلاٗ مَّا يُؤْمِنُونَۖ ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (95,1,'14',2,'Al-Baqarah','البَقَرَة',1,3,88,'وَلَمَّا جَآءَهُمْ كِتَٰبٞ مِّنْ عِندِ اِ۬للَّهِ مُصَدِّقٞ لِّمَا مَعَهُمْ وَكَانُواْ مِن قَبْلُ يَسْتَفْتِحُونَ عَلَى اَ۬لذِينَ كَفَرُواْۖ فَلَمَّا جَآءَهُم مَّا عَرَفُواْ كَفَرُواْ بِهِۦۖ فَلَعْنَةُ اُ۬للَّهِ عَلَى اَ۬لْكَٰفِرِينَۖ ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (96,1,'14',2,'Al-Baqarah','البَقَرَة',4,6,89,'بِئْسَمَا اَ۪شْتَرَوْاْ بِهِۦ أَنفُسَهُمْ أَنْ يَّكْفُرُواْ بِمَا أَنزَلَ اَ۬للَّهُ بَغْياً أَنْ يُّنَزِّلَ اَ۬للَّهُ مِن فَضْلِهِۦ عَلَىٰ مَنْ يَّشَآءُ مِنْ عِبَادِهِۦۖ فَبَآءُو بِغَضَبٍ عَلَىٰ غَضَبٖۖ وَلِلْكَٰفِرِينَ عَذَابٞ مُّهِينٞۖ ٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (97,1,'14',2,'Al-Baqarah','البَقَرَة',7,10,90,'وَإِذَا قِيلَ لَهُمْ ءَامِنُواْ بِمَا أَنزَلَ اَ۬للَّهُ قَالُواْ نُؤْمِنُ بِمَا أُنزِلَ عَلَيْنَا وَيَكْفُرُونَ بِمَا وَرَآءَهُۥ وَهْوَ اَ۬لْحَقُّ مُصَدِّقاٗ لِّمَا مَعَهُمْۖ قُلْ فَلِمَ تَقْتُلُونَ أَنۢبِئَآءَ اَ۬للَّهِ مِن قَبْلُ إِن كُنتُم مُّؤْمِنِينَۖ ٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (98,1,'14',2,'Al-Baqarah','البَقَرَة',10,11,91,'۞ وَلَقَدْ جَآءَكُم مُّوسَىٰ بِالْبَيِّنَٰتِ ثُمَّ اَ۪تَّخَذتُّمُ اُ۬لْعِجْلَ مِنۢ بَعْدِهِۦ وَأَنتُمْ ظَٰلِمُونَۖ ٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (99,1,'14',2,'Al-Baqarah','البَقَرَة',11,15,92,'وَإِذْ أَخَذْنَا مِيثَٰقَكُمْ وَرَفَعْنَا فَوْقَكُمُ اُ۬لطُّورَۖ خُذُواْ مَا ءَاتَيْنَٰكُم بِقُوَّةٖ وَاسْمَعُواْۖ قَالُواْ سَمِعْنَا وَعَصَيْنَاۖ وَأُشْرِبُواْ فِے قُلُوبِهِمُ اُ۬لْعِجْلَ بِكُفْرِهِمْۖ قُلْ بِئْسَمَا يَأْمُرُكُم بِهِۦ إِيمَٰنُكُمْ إِن كُنتُم مُّؤْمِنِينَۖ ٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (100,1,'15',2,'Al-Baqarah','البَقَرَة',1,2,93,'قُلْ إِن كَانَتْ لَكُمُ اُ۬لدَّارُ اُ۬لْأٓخِرَةُ عِندَ اَ۬للَّهِ خَالِصَةٗ مِّن دُونِ اِ۬لنَّاسِ فَتَمَنَّوُاْ اُ۬لْمَوْتَ إِن كُنتُمْ صَٰدِقِينَۖ ٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (101,1,'15',2,'Al-Baqarah','البَقَرَة',2,3,94,'وَلَنْ يَّتَمَنَّوْهُ أَبَداَۢ بِمَا قَدَّمَتْ أَيْدِيهِمْۖ وَاللَّهُ عَلِيمُۢ بِالظَّٰلِمِينَۖ ٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (102,1,'15',2,'Al-Baqarah','البَقَرَة',4,6,95,'وَلَتَجِدَنَّهُمْ أَحْرَصَ اَ۬لنَّاسِ عَلَىٰ حَيَوٰةٖۖ وَمِنَ اَ۬لذِينَ أَشْرَكُواْ يَوَدُّ أَحَدُهُمْ لَوْ يُعَمَّرُ أَلْفَ سَنَةٖۖ وَمَا هُوَ بِمُزَحْزِحِهِۦ مِنَ اَ۬لْعَذَابِ أَنْ يُّعَمَّرَۖ وَاللَّهُ بَصِيرُۢ بِمَا يَعْمَلُونَۖ ٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (103,1,'15',2,'Al-Baqarah','البَقَرَة',6,8,96,'قُلْ مَن كَانَ عَدُوّاٗ لِّـجِبْرِيلَ فَإِنَّهُۥ نَزَّلَهُۥ عَلَىٰ قَلْبِكَ بِإِذْنِ اِ۬للَّهِ مُصَدِّقاٗ لِّمَا بَيْنَ يَدَيْهِ وَهُدىٗ وَبُشْرَىٰ لِلْمُؤْمِنِينَۖ ٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (104,1,'15',2,'Al-Baqarah','البَقَرَة',9,10,97,'مَن كَانَ عَدُوّاٗ لِّلهِ وَمَلَٰٓئِكَتِهِۦ وَرُسُلِهِۦ وَجِبْرِيلَ وَمِيكَٰٓئِلَ فَإِنَّ اَ۬للَّهَ عَدُوّٞ لِّلْكَٰفِرِينَۖ ٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (105,1,'15',2,'Al-Baqarah','البَقَرَة',10,11,98,'وَلَقَدْ أَنزَلْنَا إِلَيْكَ ءَايَٰتِۢ بَيِّنَٰتٖۖ وَمَا يَكْفُرُ بِهَا إِلَّا اَ۬لْفَٰسِقُونَۖ ٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (106,1,'15',2,'Al-Baqarah','البَقَرَة',12,13,99,'أَوَكُلَّمَا عَٰهَدُواْ عَهْداٗ نَّبَذَهُۥ فَرِيقٞ مِّنْهُمۖ بَلْ أَكْثَرُهُمْ لَا يُؤْمِنُونَۖ ٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (107,1,'15',2,'Al-Baqarah','البَقَرَة',13,15,100,'۞ وَلَمَّا جَآءَهُمْ رَسُولٞ مِّنْ عِندِ اِ۬للَّهِ مُصَدِّقٞ لِّمَا مَعَهُمْ نَبَذَ فَرِيقٞ مِّنَ اَ۬لذِينَ أُوتُواْ اُ۬لْكِتَٰبَ كِتَٰبَ اَ۬للَّهِ وَرَآءَ ظُهُورِهِمْ كَأَنَّهُمْ لَا يَعْلَمُونَۖ ١٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (108,1,'16',2,'Al-Baqarah','البَقَرَة',1,9,101,'وَاتَّبَعُواْ مَا تَتْلُواْ اُ۬لشَّيَٰطِينُ عَلَىٰ مُلْكِ سُلَيْمَٰنَۖ وَمَا كَفَرَ سُلَيْمَٰنُۖ وَلَٰكِنَّ اَ۬لشَّيَٰطِينَ كَفَرُواْ يُعَلِّمُونَ اَ۬لنَّاسَ اَ۬لسِّحْرَ وَمَا أُنزِلَ عَلَى اَ۬لْمَلَكَيْنِ بِبَابِلَ هَارُوتَ وَمَارُوتَۖ وَمَا يُعَلِّمَٰنِ مِنْ أَحَدٍ حَتَّىٰ يَقُولَا إِنَّمَا نَحْنُ فِتْنَةٞ فَلَا تَكْفُرْۖ فَيَتَعَلَّمُونَ مِنْهُمَا مَا يُفَرِّقُونَ بِهِۦ بَيْنَ اَ۬لْمَرْءِ وَزَوْجِهِۦۖ وَمَا هُم بِضَآرِّينَ بِهِۦ مِنْ أَحَدٍ إِلَّا بِإِذْنِ اِ۬للَّهِۖ وَيَتَعَلَّمُونَ مَا يَضُرُّهُمْ وَلَا يَنفَعُهُمْۖ وَلَقَدْ عَلِمُواْ لَمَنِ اِ۪شْتَرَيٰهُ مَا لَهُۥ فِے اِ۬لْأٓخِرَةِ مِنْ خَلَٰقٖۖ وَلَبِئْسَ مَا شَرَوْاْ بِهِۦ أَنفُسَهُمْ لَوْ كَانُواْ يَعْلَمُونَۖ ١٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (109,1,'16',2,'Al-Baqarah','البَقَرَة',9,10,102,'وَلَوْ أَنَّهُمْ ءَامَنُواْ وَاتَّقَوْاْ لَمَثُوبَةٞ مِّنْ عِندِ اِ۬للَّهِ خَيْرٞ لَّوْ كَانُواْ يَعْلَمُونَۖ ١٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (110,1,'16',2,'Al-Baqarah','البَقَرَة',11,12,103,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ لَا تَقُولُواْ رَٰعِنَا وَقُولُواْ اُ۟نظُرْنَا وَاسْمَعُواْۖ وَلِلْكَٰفِرِينَ عَذَابٌ أَلِيمٞۖ ١٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (111,1,'16',2,'Al-Baqarah','البَقَرَة',12,15,104,'مَّا يَوَدُّ اُ۬لذِينَ كَفَرُواْ مِنْ أَهْلِ اِ۬لْكِتَٰبِ وَلَا اَ۬لْمُشْرِكِينَ أَنْ يُّنَزَّلَ عَلَيْكُم مِّنْ خَيْرٖ مِّن رَّبِّكُمْۖ وَاللَّهُ يَخْتَصُّ بِرَحْمَتِهِۦ مَنْ يَّشَآءُۖ وَاللَّهُ ذُو اُ۬لْفَضْلِ اِ۬لْعَظِيمِۖ ١٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (112,1,'17',2,'Al-Baqarah','البَقَرَة',1,2,105,'۞ مَا نَنسَخْ مِنْ ءَايَةٍ أَوْ نُنسِهَا نَأْتِ بِخَيْرٖ مِّنْهَا أَوْ مِثْلِهَاۖ أَلَمْ تَعْلَمْ أَنَّ اَ۬للَّهَ عَلَىٰ كُلِّ شَےْءٖ قَدِيرٌۖ ١٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (113,1,'17',2,'Al-Baqarah','البَقَرَة',2,4,106,'أَلَمْ تَعْلَمْ أَنَّ اَ۬للَّهَ لَهُۥ مُلْكُ اُ۬لسَّمَٰوَٰتِ وَالْأَرْضِ وَمَا لَكُم مِّن دُونِ اِ۬للَّهِ مِنْ وَّلِيّٖ وَلَا نَصِيرٍۖ ١٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (114,1,'17',2,'Al-Baqarah','البَقَرَة',4,6,107,'أَمْ تُرِيدُونَ أَن تَسْـَٔلُواْ رَسُولَكُمْ كَمَا سُئِلَ مُوسَىٰ مِن قَبْلُۖ وَمَنْ يَّتَبَدَّلِ اِ۬لْكُفْرَ بِالْإِيمَٰنِ فَقَدْ ضَلَّ سَوَآءَ اَ۬لسَّبِيلِۖ ١٠٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (115,1,'17',2,'Al-Baqarah','البَقَرَة',6,10,108,'وَدَّ كَثِيرٞ مِّنْ أَهْلِ اِ۬لْكِتَٰبِ لَوْ يَرُدُّونَكُم مِّنۢ بَعْدِ إِيمَٰنِكُمْ كُفَّاراًۖ حَسَداٗ مِّنْ عِندِ أَنفُسِهِم مِّنۢ بَعْدِ مَا تَبَيَّنَ لَهُمُ اُ۬لْحَقُّۖ فَاعْفُواْ وَاصْفَحُواْ حَتَّىٰ يَأْتِيَ اَ۬للَّهُ بِأَمْرِهِۦۖ إِنَّ اَ۬للَّهَ عَلَىٰ كُلِّ شَےْءٖ قَدِيرٞۖ ١٠٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (116,1,'17',2,'Al-Baqarah','البَقَرَة',10,12,109,'وَأَقِيمُواْ اُ۬لصَّلَوٰةَ وَءَاتُواْ اُ۬لزَّكَوٰةَۖ وَمَا تُقَدِّمُواْ لِأَنفُسِكُم مِّنْ خَيْرٖ تَجِدُوهُ عِندَ اَ۬للَّهِۖ إِنَّ اَ۬للَّهَ بِمَا تَعْمَلُونَ بَصِيرٞۖ ١٠٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (117,1,'17',2,'Al-Baqarah','البَقَرَة',12,14,110,'وَقَالُواْ لَنْ يَّدْخُلَ اَ۬لْجَنَّةَ إِلَّا مَن كَانَ هُوداً أَوْ نَصَٰرَىٰۖ تِلْكَ أَمَانِيُّهُمْۖ قُلْ هَاتُواْ بُرْهَٰنَكُمْ إِن كُنتُمْ صَٰدِقِينَۖ ١١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (118,1,'17',2,'Al-Baqarah','البَقَرَة',14,15,111,'بَلَىٰ مَنْ أَسْلَمَ وَجْهَهُۥ لِلهِ وَهْوَ مُحْسِنٞ فَلَهُۥ أَجْرُهُۥ عِندَ رَبِّهِۦ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَۖ ١١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (119,1,'18',2,'Al-Baqarah','البَقَرَة',1,4,112,'وَقَالَتِ اِ۬لْيَهُودُ لَيْسَتِ اِ۬لنَّصَٰرَىٰ عَلَىٰ شَےْءٖ وَقَالَتِ اِ۬لنَّصَٰرَىٰ لَيْسَتِ اِ۬لْيَهُودُ عَلَىٰ شَےْءٖ وَهُمْ يَتْلُونَ اَ۬لْكِتَٰبَۖ كَذَٰلِكَ قَالَ اَ۬لذِينَ لَا يَعْلَمُونَ مِثْلَ قَوْلِهِمْۖ فَاللَّهُ يَحْكُمُ بَيْنَهُمْ يَوْمَ اَ۬لْقِيَٰمَةِ فِيمَا كَانُواْ فِيهِ يَخْتَلِفُونَۖ ١١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (120,1,'18',2,'Al-Baqarah','البَقَرَة',4,7,113,'۞ وَمَنْ أَظْلَمُ مِمَّن مَّنَعَ مَسَٰجِدَ اَ۬للَّهِ أَنْ يُّذْكَرَ فِيهَا اَ۪سْمُهُۥ وَسَعَىٰ فِے خَرَابِهَاۖ أُوْلَٰٓئِكَ مَا كَانَ لَهُمْ أَنْ يَّدْخُلُوهَا إِلَّا خَآئِفِينَۖ لَهُمْ فِے اِ۬لدُّنْيَا خِزْيٞۖ وَلَهُمْ فِے اِ۬لْأٓخِرَةِ عَذَابٌ عَظِيمٞۖ ١١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (121,1,'18',2,'Al-Baqarah','البَقَرَة',7,8,114,'وَلِلهِ اِ۬لْمَشْرِقُ وَالْمَغْرِبُۖ فَأَيْنَمَا تُوَلُّواْ فَثَمَّ وَجْهُ اُ۬للَّهِۖ إِنَّ اَ۬للَّهَ وَٰسِعٌ عَلِيمٞۖ ١١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (122,1,'18',2,'Al-Baqarah','البَقَرَة',9,10,115,'وَقَالُواْ اُ۪تَّخَذَ اَ۬للَّهُ وَلَداٗۖ سُبْحَٰنَهُۥۖ بَل لَّهُۥ مَا فِے اِ۬لسَّمَٰوَٰتِ وَالْأَرْضِۖ كُلّٞ لَّهُۥ قَٰنِتُونَۖ ١١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (123,1,'18',2,'Al-Baqarah','البَقَرَة',10,11,116,'بَدِيعُ اُ۬لسَّمَٰوَٰتِ وَالْأَرْضِۖ وَإِذَا قَضَىٰ أَمْراٗ فَإِنَّمَا يَقُولُ لَهُۥ كُنۖ فَيَكُونُۖ ١١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (124,1,'18',2,'Al-Baqarah','البَقَرَة',11,14,117,'وَقَالَ اَ۬لذِينَ لَا يَعْلَمُونَ لَوْلَا يُكَلِّمُنَا اَ۬للَّهُ أَوْ تَأْتِينَا ءَايَةٞۖ كَذَٰلِكَ قَالَ اَ۬لذِينَ مِن قَبْلِهِم مِّثْلَ قَوْلِهِمْۖ تَشَٰبَهَتْ قُلُوبُهُمْۖ قَدْ بَيَّنَّا اَ۬لْأٓيَٰتِ لِقَوْمٖ يُوقِنُونَۖ ١١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (125,1,'18',2,'Al-Baqarah','البَقَرَة',14,15,118,'إِنَّا أَرْسَلْنَٰكَ بِالْحَقِّ بَشِيراٗ وَنَذِيراٗۖ وَلَا تَسْـَٔلْ عَنْ أَصْحَٰبِ اِ۬لْجَحِيمِۖ ١١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (126,1,'19',2,'Al-Baqarah','البَقَرَة',1,3,119,'وَلَن تَرْضَىٰ عَنكَ اَ۬لْيَهُودُ وَلَا اَ۬لنَّصَٰرَىٰ حَتَّىٰ تَتَّبِعَ مِلَّتَهُمْۖ قُلْ إِنَّ هُدَى اَ۬للَّهِ هُوَ اَ۬لْهُدَىٰۖ وَلَئِنِ اِ۪تَّبَعْتَ أَهْوَآءَهُم بَعْدَ اَ۬لذِے جَآءَكَ مِنَ اَ۬لْعِلْمِ مَا لَكَ مِنَ اَ۬للَّهِ مِنْ وَّلِيّٖ وَلَا نَصِيرٍۖ ١١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (127,1,'19',2,'Al-Baqarah','البَقَرَة',3,5,120,'اِ۬لذِينَ ءَاتَيْنَٰهُمُ اُ۬لْكِتَٰبَ يَتْلُونَهُۥ حَقَّ تِلَٰوَتِهِۦ أُوْلَٰٓئِكَ يُؤْمِنُونَ بِهِۦۖ وَمَنْ يَّكْفُرْ بِهِۦ فَأُوْلَٰٓئِكَ هُمُ اُ۬لْخَٰسِرُونَۖ ١٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (128,1,'19',2,'Al-Baqarah','البَقَرَة',5,6,121,'يَٰبَنِے إِسْرَآءِيلَ اَ۟ذْكُرُواْ نِعْمَتِيَ اَ۬لتِے أَنْعَمْتُ عَلَيْكُمْ وَأَنِّے فَضَّلْتُكُمْ عَلَى اَ۬لْعَٰلَمِينَۖ ١٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (129,1,'19',2,'Al-Baqarah','البَقَرَة',6,8,122,'وَاتَّقُواْ يَوْماٗ لَّا تَجْزِے نَفْسٌ عَن نَّفْسٖ شَئْاٗ وَلَا يُقْبَلُ مِنْهَا عَدْلٞ وَلَا تَنفَعُهَا شَفَٰعَةٞ وَلَا هُمْ يُنصَرُونَۖ ١٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (130,1,'19',2,'Al-Baqarah','البَقَرَة',8,10,123,'۞ وَإِذِ اِ۪بْتَلَىٰ إِبْرَٰهِيمَ رَبُّهُۥ بِكَلِمَٰتٖ فَأَتَمَّهُنَّۖ قَالَ إِنِّے جَاعِلُكَ لِلنَّاسِ إِمَاماٗۖ قَالَ وَمِن ذُرِّيَّتِےۖ قَالَ لَا يَنَالُ عَهْدِيَ اَ۬لظَّٰلِمِينَۖ ١٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (131,1,'19',2,'Al-Baqarah','البَقَرَة',10,13,124,'وَإِذْ جَعَلْنَا اَ۬لْبَيْتَ مَثَابَةٗ لِّلنَّاسِ وَأَمْناٗ وَاتَّخَذُواْ مِن مَّقَامِ إِبْرَٰهِيمَ مُصَلّىٗۖ وَعَهِدْنَا إِلَىٰ إِبْرَٰهِيمَ وَإِسْمَٰعِيلَ أَن طَهِّرَا بَيْتِيَ لِلطَّآئِفِينَ وَالْعَٰكِفِينَ وَالرُّكَّعِ اِ۬لسُّجُودِۖ ١٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (132,1,'19',2,'Al-Baqarah','البَقَرَة',13,15,125,'وَإِذْ قَالَ إِبْرَٰهِيمُ رَبِّ اِ۪جْعَلْ هَٰذَا بَلَداً ءَامِناٗ وَارْزُقْ أَهْلَهُۥ مِنَ اَ۬لثَّمَرَٰتِ مَنْ ءَامَنَ مِنْهُم بِاللَّهِ وَالْيَوْمِ اِ۬لْأٓخِرِۖ قَالَ وَمَن كَفَرَ فَأُمَتِّعُهُۥ قَلِيلاٗ ثُمَّ أَضْطَرُّهُۥ إِلَىٰ عَذَابِ اِ۬لنَّارِۖ وَبِئْسَ اَ۬لْمَصِيرُۖ ١٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (133,1,'20',2,'Al-Baqarah','البَقَرَة',1,2,126,'وَإِذْ يَرْفَعُ إِبْرَٰهِيمُ اُ۬لْقَوَاعِدَ مِنَ اَ۬لْبَيْتِ وَإِسْمَٰعِيلُۖ رَبَّنَا تَقَبَّلْ مِنَّا إِنَّكَ أَنتَ اَ۬لسَّمِيعُ اُ۬لْعَلِيمُۖ ١٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (134,1,'20',2,'Al-Baqarah','البَقَرَة',2,4,127,'رَبَّنَا وَاجْعَلْنَا مُسْلِمَيْنِ لَكَ وَمِن ذُرِّيَّتِنَا أُمَّةٗ مُّسْلِمَةٗ لَّكَۖ وَأَرِنَا مَنَاسِكَنَاۖ وَتُبْ عَلَيْنَاۖ إِنَّكَ أَنتَ اَ۬لتَّوَّابُ اُ۬لرَّحِيمُۖ ١٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (135,1,'20',2,'Al-Baqarah','البَقَرَة',4,6,128,'رَبَّنَا وَابْعَثْ فِيهِمْ رَسُولاٗ مِّنْهُمْ يَتْلُواْ عَلَيْهِمْ ءَايَٰتِكَ وَيُعَلِّمُهُمُ اُ۬لْكِتَٰبَ وَالْحِكْمَةَ وَيُزَكِّيهِمْۖ إِنَّكَ أَنتَ اَ۬لْعَزِيزُ اُ۬لْحَكِيمُۖ ١٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (136,1,'20',2,'Al-Baqarah','البَقَرَة',6,8,129,'وَمَنْ يَّرْغَبُ عَن مِّلَّةِ إِبْرَٰهِيمَ إِلَّا مَن سَفِهَ نَفْسَهُۥۖ وَلَقَدِ اِ۪صْطَفَيْنَٰهُ فِے اِ۬لدُّنْيَاۖ وَإِنَّهُۥ فِے اِ۬لْأٓخِرَةِ لَمِنَ اَ۬لصَّٰلِحِينَۖ ١٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (137,1,'20',2,'Al-Baqarah','البَقَرَة',8,9,130,'إِذْ قَالَ لَهُۥ رَبُّهُۥ أَسْلِمْۖ قَالَ أَسْلَمْتُ لِرَبِّ اِ۬لْعَٰلَمِينَۖ ١٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (138,1,'20',2,'Al-Baqarah','البَقَرَة',9,11,131,'وَأَوْصَىٰ بِهَا إِبْرَٰهِيمُ بَنِيهِۖ وَيَعْقُوبُ يَٰبَنِيَّ إِنَّ اَ۬للَّهَ اَ۪صْطَفَىٰ لَكُمُ اُ۬لدِّينَ فَلَا تَمُوتُنَّ إِلَّا وَأَنتُم مُّسْلِمُونَۖ ١٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (139,1,'20',2,'Al-Baqarah','البَقَرَة',11,14,132,'۞ أَمْ كُنتُمْ شُهَدَآءَ ا۪ذْ حَضَرَ يَعْقُوبَ اَ۬لْمَوْتُ إِذْ قَالَ لِبَنِيهِ مَا تَعْبُدُونَ مِنۢ بَعْدِےۖ قَالُواْ نَعْبُدُ إِلَٰهَكَ وَإِلَٰهَ ءَابَآئِكَ إِبْرَٰهِيمَ وَإِسْمَٰعِيلَ وَإِسْحَٰقَ إِلَٰهاٗ وَٰحِداٗ وَنَحْنُ لَهُۥ مُسْلِمُونَۖ ١٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (140,1,'20',2,'Al-Baqarah','البَقَرَة',14,15,133,'تِلْكَ أُمَّةٞ قَدْ خَلَتْۖ لَهَا مَا كَسَبَتْۖ وَلَكُم مَّا كَسَبْتُمْۖ وَلَا تُسْـَٔلُونَ عَمَّا كَانُواْ يَعْمَلُونَۖ ١٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (141,1,'21',2,'Al-Baqarah','البَقَرَة',1,2,134,'وَقَالُواْ كُونُواْ هُوداً أَوْ نَصَٰرَىٰ تَهْتَدُواْۖ قُلْ بَلْ مِلَّةَ إِبْرَٰهِيمَ حَنِيفاٗۖ وَمَا كَانَ مِنَ اَ۬لْمُشْرِكِينَۖ ١٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (142,1,'21',2,'Al-Baqarah','البَقَرَة',2,5,135,'قُولُواْ ءَامَنَّا بِاللَّهِ وَمَا أُنزِلَ إِلَيْنَا وَمَا أُنزِلَ إِلَىٰ إِبْرَٰهِيمَ وَإِسْمَٰعِيلَ وَإِسْحَٰقَ وَيَعْقُوبَ وَالْأَسْبَاطِ وَمَا أُوتِيَ مُوسَىٰ وَعِيسَىٰ وَمَا أُوتِيَ اَ۬لنَّبِيٓـُٔونَ مِن رَّبِّهِمْ لَا نُفَرِّقُ بَيْنَ أَحَدٖ مِّنْهُمْ وَنَحْنُ لَهُۥ مُسْلِمُونَۖ ١٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (143,1,'21',2,'Al-Baqarah','البَقَرَة',6,7,136,'فَإِنْ ءَامَنُواْ بِمِثْلِ مَا ءَامَنتُم بِهِۦ فَقَدِ اِ۪هْتَدَواْۖ وَّإِن تَوَلَّوْاْ فَإِنَّمَا هُمْ فِے شِقَاقٖۖ فَسَيَكْفِيكَهُمُ اُ۬للَّهُۖ وَهْوَ اَ۬لسَّمِيعُ اُ۬لْعَلِيمُۖ ١٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (144,1,'21',2,'Al-Baqarah','البَقَرَة',8,9,137,'صِبْغَةَ اَ۬للَّهِۖ وَمَنْ أَحْسَنُ مِنَ اَ۬للَّهِ صِبْغَةٗۖ وَنَحْنُ لَهُۥ عَٰبِدُونَۖ ١٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (145,1,'21',2,'Al-Baqarah','البَقَرَة',9,10,138,'قُلْ أَتُحَآجُّونَنَا فِے اِ۬للَّهِ وَهْوَ رَبُّنَا وَرَبُّكُمْ وَلَنَا أَعْمَٰلُنَا وَلَكُمْ أَعْمَٰلُكُمْۖ وَنَحْنُ لَهُۥ مُخْلِصُونَۖ ١٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (146,1,'21',2,'Al-Baqarah','البَقَرَة',11,14,139,'أَمْ يَقُولُونَ إِنَّ إِبْرَٰهِيمَ وَإِسْمَٰعِيلَ وَإِسْحَٰقَ وَيَعْقُوبَ وَالْأَسْبَاطَ كَانُواْ هُوداً أَوْ نَصَٰرَىٰۖ قُلْ ءَٰا۬نتُمْ أَعْلَمُ أَمِ اِ۬للَّهُۖ وَمَنْ أَظْلَمُ مِمَّن كَتَمَ شَهَٰدَةً عِندَهُۥ مِنَ اَ۬للَّهِۖ وَمَا اَ۬للَّهُ بِغَٰفِلٍ عَمَّا تَعْمَلُونَۖ ١٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (147,1,'21',2,'Al-Baqarah','البَقَرَة',14,15,140,'تِلْكَ أُمَّةٞ قَدْ خَلَتْۖ لَهَا مَا كَسَبَتْۖ وَلَكُم مَّا كَسَبْتُمْۖ وَلَا تُسْـَٔلُونَ عَمَّا كَانُواْ يَعْمَلُونَۖ ١٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (148,2,'22',2,'Al-Baqarah','البَقَرَة',1,3,141,'۞ سَيَقُولُ اُ۬لسُّفَهَآءُ مِنَ اَ۬لنَّاسِ مَا وَلَّيٰهُمْ عَن قِبْلَتِهِمُ اُ۬لتِے كَانُواْ عَلَيْهَاۖ قُل لِّلهِ اِ۬لْمَشْرِقُ وَالْمَغْرِبُۖ يَهْدِے مَنْ يَّشَآءُ اِ۪لَىٰ صِرَٰطٖ مُّسْتَقِيمٖۖ ١٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (149,2,'22',2,'Al-Baqarah','البَقَرَة',3,8,142,'وَكَذَٰلِكَ جَعَلْنَٰكُمْ أُمَّةٗ وَسَطاٗ لِّتَكُونُواْ شُهَدَآءَ عَلَى اَ۬لنَّاسِ وَيَكُونَ اَ۬لرَّسُولُ عَلَيْكُمْ شَهِيداٗۖ وَمَا جَعَلْنَا اَ۬لْقِبْلَةَ اَ۬لتِے كُنتَ عَلَيْهَا إِلَّا لِنَعْلَمَ مَنْ يَّتَّبِعُ اُ۬لرَّسُولَ مِمَّنْ يَّنقَلِبُ عَلَىٰ عَقِبَيْهِۖ وَإِن كَانَتْ لَكَبِيرَةً إِلَّا عَلَى اَ۬لذِينَ هَدَى اَ۬للَّهُۖ وَمَا كَانَ اَ۬للَّهُ لِيُضِيعَ إِيمَٰنَكُمْۖ إِنَّ اَ۬للَّهَ بِالنَّاسِ لَرَءُوفٞ رَّحِيمٞۖ ١٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (150,2,'22',2,'Al-Baqarah','البَقَرَة',8,12,143,'قَدْ نَرَىٰ تَقَلُّبَ وَجْهِكَ فِے اِ۬لسَّمَآءِ فَلَنُوَلِّيَنَّكَ قِبْلَةٗ تَرْضَيٰهَاۖ فَوَلِّ وَجْهَكَ شَطْرَ اَ۬لْمَسْجِدِ اِ۬لْحَرَامِۖ وَحَيْثُ مَا كُنتُمْ فَوَلُّواْ وُجُوهَكُمْ شَطْرَهُۥۖ وَإِنَّ اَ۬لذِينَ أُوتُواْ اُ۬لْكِتَٰبَ لَيَعْلَمُونَ أَنَّهُ اُ۬لْحَقُّ مِن رَّبِّهِمْۖ وَمَا اَ۬للَّهُ بِغَٰفِلٍ عَمَّا يَعْمَلُونَۖ ١٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (151,2,'22',2,'Al-Baqarah','البَقَرَة',12,15,144,'وَلَئِنْ أَتَيْتَ اَ۬لذِينَ أُوتُواْ اُ۬لْكِتَٰبَ بِكُلِّ ءَايَةٖ مَّا تَبِعُواْ قِبْلَتَكَۖ وَمَا أَنتَ بِتَابِعٖ قِبْلَتَهُمْۖ وَمَا بَعْضُهُم بِتَابِعٖ قِبْلَةَ بَعْضٖۖ وَلَئِنِ اِ۪تَّبَعْتَ أَهْوَآءَهُم مِّنۢ بَعْدِ مَا جَآءَكَ مِنَ اَ۬لْعِلْمِ إِنَّكَ إِذاٗ لَّمِنَ اَ۬لظَّٰلِمِينَۖ ١٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (152,2,'23',2,'Al-Baqarah','البَقَرَة',1,2,145,'اَ۬لذِينَ ءَاتَيْنَٰهُمُ اُ۬لْكِتَٰبَ يَعْرِفُونَهُۥ كَمَا يَعْرِفُونَ أَبْنَآءَهُمْۖ وَإِنَّ فَرِيقاٗ مِّنْهُمْ لَيَكْتُمُونَ اَ۬لْحَقَّ وَهُمْ يَعْلَمُونَۖ ١٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (153,2,'23',2,'Al-Baqarah','البَقَرَة',2,3,146,'اَ۬لْحَقُّ مِن رَّبِّكَۖ فَلَا تَكُونَنَّ مِنَ اَ۬لْمُمْتَرِينَۖ ١٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (154,2,'23',2,'Al-Baqarah','البَقَرَة',3,5,147,'۞ وَلِكُلّٖ وِجْهَةٌ هُوَ مُوَلِّيهَا فَاسْتَبِقُواْ اُ۬لْخَيْرَٰتِۖ أَيْنَ مَا تَكُونُواْ يَأْتِ بِكُمُ اُ۬للَّهُ جَمِيعاًۖ إِنَّ اَ۬للَّهَ عَلَىٰ كُلِّ شَےْءٖ قَدِيرٞۖ ١٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (155,2,'23',2,'Al-Baqarah','البَقَرَة',5,7,148,'وَمِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ اَ۬لْمَسْجِدِ اِ۬لْحَرَامِۖ وَإِنَّهُۥ لَلْحَقُّ مِن رَّبِّكَۖ وَمَا اَ۬للَّهُ بِغَٰفِلٍ عَمَّا تَعْمَلُونَۖ ١٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (156,2,'23',2,'Al-Baqarah','البَقَرَة',7,11,149,'وَمِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ اَ۬لْمَسْجِدِ اِ۬لْحَرَامِۖ وَحَيْثُ مَا كُنتُمْ فَوَلُّواْ وُجُوهَكُمْ شَطْرَهُۥ لِئَلَّا يَكُونَ لِلنَّاسِ عَلَيْكُمْ حُجَّةٌ إِلَّا اَ۬لذِينَ ظَلَمُواْ مِنْهُمْ فَلَا تَخْشَوْهُمْ وَاخْشَوْنِےۖ وَلِأُتِمَّ نِعْمَتِے عَلَيْكُمْ وَلَعَلَّكُمْ تَهْتَدُونَۖ ١٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (157,2,'23',2,'Al-Baqarah','البَقَرَة',11,13,150,'كَمَا أَرْسَلْنَا فِيكُمْ رَسُولاٗ مِّنكُمْ يَتْلُواْ عَلَيْكُمْ ءَايَٰتِنَا وَيُزَكِّيكُمْ وَيُعَلِّمُكُمُ اُ۬لْكِتَٰبَ وَالْحِكْمَةَ وَيُعَلِّمُكُم مَّا لَمْ تَكُونُواْ تَعْلَمُونَ ١٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (158,2,'23',2,'Al-Baqarah','البَقَرَة',13,14,151,'فَاذْكُرُونِے أَذْكُرْكُمْ وَاشْكُرُواْ لِے وَلَا تَكْفُرُونِۖ ١٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (159,2,'23',2,'Al-Baqarah','البَقَرَة',14,15,152,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ اُ۪سْتَعِينُواْ بِالصَّبْرِ وَالصَّلَوٰةِۖ إِنَّ اَ۬للَّهَ مَعَ اَ۬لصَّٰبِرِينَۖ ١٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (160,2,'24',2,'Al-Baqarah','البَقَرَة',1,2,153,'وَلَا تَقُولُواْ لِمَنْ يُّقْتَلُ فِے سَبِيلِ اِ۬للَّهِ أَمْوَٰتُۢۖ بَلْ أَحْيَآءٞۖ وَلَٰكِن لَّا تَشْعُرُونَۖ ١٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (161,2,'24',2,'Al-Baqarah','البَقَرَة',2,3,154,'وَلَنَبْلُوَنَّكُم بِشَےْءٖ مِّنَ اَ۬لْخَوْفِ وَالْجُوعِ وَنَقْصٖ مِّنَ اَ۬لْأَمْوَٰلِ وَالْأَنفُسِ وَالثَّمَرَٰتِۖ وَبَشِّرِ اِ۬لصَّٰبِرِينَ ١٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (162,2,'24',2,'Al-Baqarah','البَقَرَة',4,4,155,'اَ۬لذِينَ إِذَا أَصَٰبَتْهُم مُّصِيبَةٞ قَالُواْ إِنَّا لِلهِ وَإِنَّا إِلَيْهِ رَٰجِعُونَۖ ١٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (163,2,'24',2,'Al-Baqarah','البَقَرَة',5,6,156,'أُوْلَٰٓئِكَ عَلَيْهِمْ صَلَوَٰتٞ مِّن رَّبِّهِمْ وَرَحْمَةٞۖ وَأُوْلَٰٓئِكَ هُمُ اُ۬لْمُهْتَدُونَۖ ١٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (164,2,'24',2,'Al-Baqarah','البَقَرَة',6,8,157,'۞ إِنَّ اَ۬لصَّفَا وَالْمَرْوَةَ مِن شَعَٰٓئِرِ اِ۬للَّهِۖ فَمَنْ حَجَّ اَ۬لْبَيْتَ أَوِ اِ۪عْتَمَرَ فَلَا جُنَاحَ عَلَيْهِ أَنْ يَّطَّوَّفَ بِهِمَاۖ وَمَن تَطَوَّعَ خَيْراٗ فَإِنَّ اَ۬للَّهَ شَاكِرٌ عَلِيمٌۖ ١٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (165,2,'24',2,'Al-Baqarah','البَقَرَة',8,10,158,'إِنَّ اَ۬لذِينَ يَكْتُمُونَ مَا أَنزَلْنَا مِنَ اَ۬لْبَيِّنَٰتِ وَالْهُدَىٰ مِنۢ بَعْدِ مَا بَيَّنَّٰهُ لِلنَّاسِ فِے اِ۬لْكِتَٰبِ أُوْلَٰٓئِكَ يَلْعَنُهُمُ اُ۬للَّهُ وَيَلْعَنُهُمُ اُ۬للَّٰعِنُونَ ١٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (166,2,'24',2,'Al-Baqarah','البَقَرَة',11,12,159,'إِلَّا اَ۬لذِينَ تَابُواْ وَأَصْلَحُواْ وَبَيَّنُواْ فَأُوْلَٰٓئِكَ أَتُوبُ عَلَيْهِمْ وَأَنَا اَ۬لتَّوَّابُ اُ۬لرَّحِيمُۖ ١٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (167,2,'24',2,'Al-Baqarah','البَقَرَة',12,13,160,'إِنَّ اَ۬لذِينَ كَفَرُواْ وَمَاتُواْ وَهُمْ كُفَّارٌ أُوْلَٰٓئِكَ عَلَيْهِمْ لَعْنَةُ اُ۬للَّهِ وَالْمَلَٰٓئِكَةِ وَالنَّاسِ أَجْمَعِينَ ١٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (168,2,'24',2,'Al-Baqarah','البَقَرَة',14,14,161,'خَٰلِدِينَ فِيهَاۖ لَا يُخَفَّفُ عَنْهُمُ اُ۬لْعَذَابُ وَلَا هُمْ يُنظَرُونَۖ ١٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (169,2,'24',2,'Al-Baqarah','البَقَرَة',15,15,162,'وَإِلَٰهُكُمْ إِلَٰهٞ وَٰحِدٞۖ لَّا إِلَٰهَ إِلَّا هُوَۖ اَ۬لرَّحْمَٰنُ اُ۬لرَّحِيمُۖ ١٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (170,2,'25',2,'Al-Baqarah','البَقَرَة',1,5,163,'إِنَّ فِے خَلْقِ اِ۬لسَّمَٰوَٰتِ وَالْأَرْضِ وَاخْتِلَٰفِ اِ۬ليْلِ وَالنَّهَارِ وَالْفُلْكِ اِ۬لتِے تَجْرِے فِے اِ۬لْبَحْرِ بِمَا يَنفَعُ اُ۬لنَّاسَ وَمَا أَنزَلَ اَ۬للَّهُ مِنَ اَ۬لسَّمَآءِ مِن مَّآءٖ فَأَحْيَا بِهِ اِ۬لْأَرْضَ بَعْدَ مَوْتِهَا وَبَثَّ فِيهَا مِن كُلِّ دَآبَّةٖ وَتَصْرِيفِ اِ۬لرِّيَٰحِ وَالسَّحَابِ اِ۬لْمُسَخَّرِ بَيْنَ اَ۬لسَّمَآءِ وَالْأَرْضِ لَأٓيَٰتٖ لِّقَوْمٖ يَعْقِلُونَۖ ١٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (171,2,'25',2,'Al-Baqarah','البَقَرَة',5,8,164,'وَمِنَ اَ۬لنَّاسِ مَنْ يَّتَّخِذُ مِن دُونِ اِ۬للَّهِ أَندَاداٗ يُحِبُّونَهُمْ كَحُبِّ اِ۬للَّهِۖ وَالذِينَ ءَامَنُواْ أَشَدُّ حُبّاٗ لِّلهِۖ وَلَوْ تَرَى اَ۬لذِينَ ظَلَمُواْ إِذْ يَرَوْنَ اَ۬لْعَذَابَ أَنَّ اَ۬لْقُوَّةَ لِلهِ جَمِيعاٗ وَأَنَّ اَ۬للَّهَ شَدِيدُ اُ۬لْعَذَابِۖ ١٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (172,2,'25',2,'Al-Baqarah','البَقَرَة',9,10,165,'إِذْ تَبَرَّأَ اَ۬لذِينَ اَ۟تُّبِعُواْ مِنَ اَ۬لذِينَ اَ۪تَّبَعُواْ وَرَأَوُاْ اُ۬لْعَذَابَ وَتَقَطَّعَتْ بِهِمُ اُ۬لْأَسْبَٰبُۖ ١٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (173,2,'25',2,'Al-Baqarah','البَقَرَة',10,12,166,'وَقَالَ اَ۬لذِينَ اَ۪تَّبَعُواْ لَوْ أَنَّ لَنَا كَرَّةٗ فَنَتَبَرَّأَ مِنْهُمْ كَمَا تَبَرَّءُواْ مِنَّاۖ كَذَٰلِكَ يُرِيهِمُ اُ۬للَّهُ أَعْمَٰلَهُمْ حَسَرَٰتٍ عَلَيْهِمْۖ وَمَا هُم بِخَٰرِجِينَ مِنَ اَ۬لنَّارِۖ ١٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (174,2,'25',2,'Al-Baqarah','البَقَرَة',13,14,167,'۞ يَٰأَيُّهَا اَ۬لنَّاسُ كُلُواْ مِمَّا فِے اِ۬لْأَرْضِ حَلَٰلاٗ طَيِّباٗۖ وَلَا تَتَّبِعُواْ خُطْوَٰتِ اِ۬لشَّيْطَٰنِۖ إِنَّهُۥ لَكُمْ عَدُوّٞ مُّبِينٌۖ ١٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (175,2,'25',2,'Al-Baqarah','البَقَرَة',14,15,168,'إِنَّمَا يَأْمُرُكُم بِالسُّوٓءِ وَالْفَحْشَآءِ وَأَن تَقُولُواْ عَلَى اَ۬للَّهِ مَا لَا تَعْلَمُونَۖ ١٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (176,2,'26',2,'Al-Baqarah','البَقَرَة',1,3,169,'وَإِذَا قِيلَ لَهُمُ اُ۪تَّبِعُواْ مَا أَنزَلَ اَ۬للَّهُ قَالُواْ بَلْ نَتَّبِعُ مَا أَلْفَيْنَا عَلَيْهِ ءَابَآءَنَاۖ أَوَلَوْ كَانَ ءَابَآؤُهُمْ لَا يَعْقِلُونَ شَئْاٗ وَلَا يَهْتَدُونَۖ ١٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (177,2,'26',2,'Al-Baqarah','البَقَرَة',3,5,170,'وَمَثَلُ اُ۬لذِينَ كَفَرُواْ كَمَثَلِ اِ۬لذِے يَنْعِقُ بِمَا لَا يَسْمَعُ إِلَّا دُعَآءٗ وَنِدَآءٗۖ صُمُّۢ بُكْمٌ عُمْيٞ فَهُمْ لَا يَعْقِلُونَۖ ١٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (178,2,'26',2,'Al-Baqarah','البَقَرَة',5,6,171,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ كُلُواْ مِن طَيِّبَٰتِ مَا رَزَقْنَٰكُمْ وَاشْكُرُواْ لِلهِ إِن كُنتُمْ إِيَّاهُ تَعْبُدُونَۖ ١٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (179,2,'26',2,'Al-Baqarah','البَقَرَة',7,9,172,'إِنَّمَا حَرَّمَ عَلَيْكُمُ اُ۬لْمَيْتَةَ وَالدَّمَ وَلَحْمَ اَ۬لْخِنزِيرِ وَمَا أُهِلَّ بِهِۦ لِغَيْرِ اِ۬للَّهِۖ فَمَنُ اُ۟ضْطُرَّ غَيْرَ بَاغٖ وَلَا عَادٖ فَلَا إِثْمَ عَلَيْهِۖ إِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٌۖ ١٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (180,2,'26',2,'Al-Baqarah','البَقَرَة',9,12,173,'إِنَّ اَ۬لذِينَ يَكْتُمُونَ مَا أَنزَلَ اَ۬للَّهُ مِنَ اَ۬لْكِتَٰبِ وَيَشْتَرُونَ بِهِۦ ثَمَناٗ قَلِيلاً أُوْلَٰٓئِكَ مَا يَأْكُلُونَ فِے بُطُونِهِمْ إِلَّا اَ۬لنَّارَ وَلَا يُكَلِّمُهُمُ اُ۬للَّهُ يَوْمَ اَ۬لْقِيَٰمَةِ وَلَا يُزَكِّيهِمْۖ وَلَهُمْ عَذَابٌ أَلِيمٌۖ ١٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (181,2,'26',2,'Al-Baqarah','البَقَرَة',12,14,174,'أُوْلَٰٓئِكَ اَ۬لذِينَ اَ۪شْتَرَوُاْ اُ۬لضَّلَٰلَةَ بِالْهُدَىٰ وَالْعَذَابَ بِالْمَغْفِرَةِ فَمَا أَصْبَرَهُمْ عَلَى اَ۬لنَّارِۖ ١٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (182,2,'26',2,'Al-Baqarah','البَقَرَة',14,15,175,'ذَٰلِكَ بِأَنَّ اَ۬للَّهَ نَزَّلَ اَ۬لْكِتَٰبَ بِالْحَقِّۖ وَإِنَّ اَ۬لذِينَ اَ۪خْتَلَفُواْ فِے اِ۬لْكِتَٰبِ لَفِے شِقَاقِۢ بَعِيدٖۖ ١٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (183,2,'27',2,'Al-Baqarah','البَقَرَة',1,7,176,'۞ لَّيْسَ اَ۬لْبِرُّ أَن تُوَلُّواْ وُجُوهَكُمْ قِبَلَ اَ۬لْمَشْرِقِ وَالْمَغْرِبِۖ وَلَٰكِنِ اِ۬لْبِرُّ مَنْ ءَامَنَ بِاللَّهِ وَالْيَوْمِ اِ۬لْأٓخِرِ وَالْمَلَٰٓئِكَةِ وَالْكِتَٰبِ وَالنَّبِيٓـِٕۧنَ وَءَاتَى اَ۬لْمَالَ عَلَىٰ حُبِّهِۦ ذَوِے اِ۬لْقُرْبَىٰ وَالْيَتَٰمَىٰ وَالْمَسَٰكِينَ وَابْنَ اَ۬لسَّبِيلِ وَالسَّآئِلِينَ وَفِے اِ۬لرِّقَابِ وَأَقَامَ اَ۬لصَّلَوٰةَ وَءَاتَى اَ۬لزَّكَوٰةَۖ وَالْمُوفُونَ بِعَهْدِهِمْ إِذَا عَٰهَدُواْۖ وَالصَّٰبِرِينَ فِے اِ۬لْبَأْسَآءِ وَالضَّرَّآءِ وَحِينَ اَ۬لْبَأْسِۖ أُوْلَٰٓئِكَ اَ۬لذِينَ صَدَقُواْۖ وَأُوْلَٰٓئِكَ هُمُ اُ۬لْمُتَّقُونَۖ ١٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (184,2,'27',2,'Al-Baqarah','البَقَرَة',7,11,177,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ كُتِبَ عَلَيْكُمُ اُ۬لْقِصَاصُ فِے اِ۬لْقَتْلَىۖ اَ۬لْحُرُّ بِالْحُرِّ وَالْعَبْدُ بِالْعَبْدِ وَالْأُنثَىٰ بِالْأُنثَىٰۖ فَمَنْ عُفِيَ لَهُۥ مِنْ أَخِيهِ شَےْءٞ فَاتِّبَاعُۢ بِالْمَعْرُوفِ وَأَدَآءٌ إِلَيْهِ بِإِحْسَٰنٖۖ ذَٰلِكَ تَخْفِيفٞ مِّن رَّبِّكُمْ وَرَحْمَةٞۖ فَمَنِ اِ۪عْتَدَىٰ بَعْدَ ذَٰلِكَ فَلَهُۥ عَذَابٌ أَلِيمٞۖ ١٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (185,2,'27',2,'Al-Baqarah','البَقَرَة',11,12,178,'وَلَكُمْ فِے اِ۬لْقِصَاصِ حَيَوٰةٞ يَٰأُوْلِے اِ۬لْأَلْبَٰبِ لَعَلَّكُمْ تَتَّقُونَۖ ١٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (186,2,'27',2,'Al-Baqarah','البَقَرَة',12,14,179,'كُتِبَ عَلَيْكُمْ إِذَا حَضَرَ أَحَدَكُمُ اُ۬لْمَوْتُ إِن تَرَكَ خَيْراًۖ اِ۬لْوَصِيَّةُ لِلْوَٰلِدَيْنِ وَالْأَقْرَبِينَ بِالْمَعْرُوفِۖ حَقّاً عَلَى اَ۬لْمُتَّقِينَۖ ١٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (187,2,'27',2,'Al-Baqarah','البَقَرَة',14,15,180,'فَمَنۢ بَدَّلَهُۥ بَعْدَ مَا سَمِعَهُۥ فَإِنَّمَا إِثْمُهُۥ عَلَى اَ۬لذِينَ يُبَدِّلُونَهُۥۖ إِنَّ اَ۬للَّهَ سَمِيعٌ عَلِيمٞۖ ١٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (188,2,'28',2,'Al-Baqarah','البَقَرَة',1,2,181,'فَمَنْ خَافَ مِن مُّوصٖ جَنَفاً أَوْ إِثْماٗ فَأَصْلَحَ بَيْنَهُمْ فَلَا إِثْمَ عَلَيْهِۖ إِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٞۖ ١٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (189,2,'28',2,'Al-Baqarah','البَقَرَة',2,4,182,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ كُتِبَ عَلَيْكُمُ اُ۬لصِّيَامُ كَمَا كُتِبَ عَلَى اَ۬لذِينَ مِن قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُونَ ١٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (190,2,'28',2,'Al-Baqarah','البَقَرَة',4,8,183,'أَيَّاماٗ مَّعْدُودَٰتٖۖ فَمَن كَانَ مِنكُم مَّرِيضاً أَوْ عَلَىٰ سَفَرٖ فَعِدَّةٞ مِّنْ أَيَّامٍ أُخَرَۖ وَعَلَى اَ۬لذِينَ يُطِيقُونَهُۥ فِدْيَةُ طَعَامِ مَسَٰكِينَۖ فَمَن تَطَوَّعَ خَيْراٗ فَهْوَ خَيْرٞ لَّهُۥۖ وَأَن تَصُومُواْ خَيْرٞ لَّكُمْ إِن كُنتُمْ تَعْلَمُونَۖ ١٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (191,2,'28',2,'Al-Baqarah','البَقَرَة',8,13,184,'۞ شَهْرُ رَمَضَانَ اَ۬لذِے أُنزِلَ فِيهِ اِ۬لْقُرْءَانُ هُدىٗ لِّلنَّاسِ وَبَيِّنَٰتٖ مِّنَ اَ۬لْهُدَىٰ وَالْفُرْقَانِۖ فَمَن شَهِدَ مِنكُمُ اُ۬لشَّهْرَ فَلْيَصُمْهُۖ وَمَن كَانَ مَرِيضاً أَوْ عَلَىٰ سَفَرٖ فَعِدَّةٞ مِّنْ أَيَّامٍ أُخَرَۖ يُرِيدُ اُ۬للَّهُ بِكُمُ اُ۬لْيُسْرَ وَلَا يُرِيدُ بِكُمُ اُ۬لْعُسْرَۖ وَلِتُكْمِلُواْ اُ۬لْعِدَّةَ وَلِتُكَبِّرُواْ اُ۬للَّهَ عَلَىٰ مَا هَدَيٰكُمْ وَلَعَلَّكُمْ تَشْكُرُونَۖ ١٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (192,2,'28',2,'Al-Baqarah','البَقَرَة',13,15,185,'وَإِذَا سَأَلَكَ عِبَادِے عَنِّے فَإِنِّے قَرِيبٌۖ أُجِيبُ دَعْوَةَ اَ۬لدَّاعِ إِذَا دَعَانِۖ فَلْيَسْتَجِيبُواْ لِے وَلْيُؤْمِنُواْ بِے لَعَلَّهُمْ يَرْشُدُونَۖ ١٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (193,2,'29',2,'Al-Baqarah','البَقَرَة',1,8,186,'أُحِلَّ لَكُمْ لَيْلَةَ اَ۬لصِّيَامِ اِ۬لرَّفَثُ إِلَىٰ نِسَآئِكُمْۖ هُنَّ لِبَاسٞ لَّكُمْ وَأَنتُمْ لِبَاسٞ لَّهُنَّۖ عَلِمَ اَ۬للَّهُ أَنَّكُمْ كُنتُمْ تَخْتَانُونَ أَنفُسَكُمْ فَتَابَ عَلَيْكُمْ وَعَفَا عَنكُمْۖ فَاءَلْٰنَ بَٰشِرُوهُنَّۖ وَابْتَغُواْ مَا كَتَبَ اَ۬للَّهُ لَكُمْۖ وَكُلُواْ وَاشْرَبُواْ حَتَّىٰ يَتَبَيَّنَ لَكُمُ اُ۬لْخَيْطُ اُ۬لْأَبْيَضُ مِنَ اَ۬لْخَيْطِ اِ۬لْأَسْوَدِ مِنَ اَ۬لْفَجْرِۖ ثُمَّ أَتِمُّواْ اُ۬لصِّيَامَ إِلَى اَ۬ليْلِۖ وَلَا تُبَٰشِرُوهُنَّ وَأَنتُمْ عَٰكِفُونَ فِے اِ۬لْمَسَٰجِدِۖ تِلْكَ حُدُودُ اُ۬للَّهِ فَلَا تَقْرَبُوهَاۖ كَذَٰلِكَ يُبَيِّنُ اُ۬للَّهُ ءَايَٰتِهِۦ لِلنَّاسِ لَعَلَّهُمْ يَتَّقُونَۖ ١٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (194,2,'29',2,'Al-Baqarah','البَقَرَة',9,11,187,'وَلَا تَأْكُلُواْ أَمْوَٰلَكُم بَيْنَكُم بِالْبَٰطِلِ وَتُدْلُواْ بِهَا إِلَى اَ۬لْحُكَّامِ لِتَأْكُلُواْ فَرِيقاٗ مِّنْ أَمْوَٰلِ اِ۬لنَّاسِ بِالْإِثْمِ وَأَنتُمْ تَعْلَمُونَۖ ١٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (195,2,'29',2,'Al-Baqarah','البَقَرَة',11,14,188,'۞ يَسْـَٔلُونَكَ عَنِ اِ۬لْأَهِلَّةِۖ قُلْ هِيَ مَوَٰقِيتُ لِلنَّاسِ وَالْحَجِّۖ وَلَيْسَ اَ۬لْبِرُّ بِأَن تَأْتُواْ اُ۬لْبِيُوتَ مِن ظُهُورِهَاۖ وَلَٰكِنِ اِ۬لْبِرُّ مَنِ اِ۪تَّقَىٰۖ وَأْتُواْ اُ۬لْبِيُوتَ مِنْ أَبْوَٰبِهَاۖ وَاتَّقُواْ اُ۬للَّهَ لَعَلَّكُمْ تُفْلِحُونَۖ ١٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (196,2,'29',2,'Al-Baqarah','البَقَرَة',14,15,189,'وَقَٰتِلُواْ فِے سَبِيلِ اِ۬للَّهِ اِ۬لذِينَ يُقَٰتِلُونَكُمْ وَلَا تَعْتَدُواْۖ إِنَّ اَ۬للَّهَ لَا يُحِبُّ اُ۬لْمُعْتَدِينَۖ ١٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (197,2,'30',2,'Al-Baqarah','البَقَرَة',1,3,190,'وَاقْتُلُوهُمْ حَيْثُ ثَقِفْتُمُوهُمْ وَأَخْرِجُوهُم مِّنْ حَيْثُ أَخْرَجُوكُمْۖ وَالْفِتْنَةُ أَشَدُّ مِنَ اَ۬لْقَتْلِۖ وَلَا تُقَٰتِلُوهُمْ عِندَ اَ۬لْمَسْجِدِ اِ۬لْحَرَامِ حَتَّىٰ يُقَٰتِلُوكُمْ فِيهِۖ فَإِن قَٰتَلُوكُمْ فَاقْتُلُوهُمْۖ كَذَٰلِكَ جَزَآءُ اُ۬لْكَٰفِرِينَۖ ١٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (198,2,'30',2,'Al-Baqarah','البَقَرَة',3,4,191,'فَإِنِ اِ۪نتَهَوْاْ فَإِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٞۖ ١٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (199,2,'30',2,'Al-Baqarah','البَقَرَة',4,5,192,'وَقَٰتِلُوهُمْ حَتَّىٰ لَا تَكُونَ فِتْنَةٞ وَيَكُونَ اَ۬لدِّينُ لِلهِۖ فَإِنِ اِ۪نتَهَوْاْ فَلَا عُدْوَٰنَ إِلَّا عَلَى اَ۬لظَّٰلِمِينَۖ ١٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (200,2,'30',2,'Al-Baqarah','البَقَرَة',5,8,193,'اَ۬لشَّهْرُ اُ۬لْحَرَامُ بِالشَّهْرِ اِ۬لْحَرَامِ وَالْحُرُمَٰتُ قِصَاصٞۖ فَمَنِ اِ۪عْتَدَىٰ عَلَيْكُمْ فَاعْتَدُواْ عَلَيْهِ بِمِثْلِ مَا اَ۪عْتَدَىٰ عَلَيْكُمْۖ وَاتَّقُواْ اُ۬للَّهَ وَاعْلَمُواْ أَنَّ اَ۬للَّهَ مَعَ اَ۬لْمُتَّقِينَۖ ١٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (201,2,'30',2,'Al-Baqarah','البَقَرَة',8,9,194,'وَأَنفِقُواْ فِے سَبِيلِ اِ۬للَّهِ وَلَا تُلْقُواْ بِأَيْدِيكُمْ إِلَى اَ۬لتَّهْلُكَةِ وَأَحْسِنُواْۖ إِنَّ اَ۬للَّهَ يُحِبُّ اُ۬لْمُحْسِنِينَۖ ١٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (202,2,'30',2,'Al-Baqarah','البَقَرَة',9,15,195,'وَأَتِمُّواْ اُ۬لْحَجَّ وَالْعُمْرَةَ لِلهِۖ فَإِنْ أُحْصِرْتُمْ فَمَا اَ۪سْتَيْسَرَ مِنَ اَ۬لْهَدْيِۖ وَلَا تَحْلِقُواْ رُءُوسَكُمْ حَتَّىٰ يَبْلُغَ اَ۬لْهَدْيُ مَحِلَّهُۥۖ فَمَن كَانَ مِنكُم مَّرِيضاً أَوْ بِهِۦ أَذىٗ مِّن رَّأْسِهِۦ فَفِدْيَةٞ مِّن صِيَامٍ أَوْ صَدَقَةٍ أَوْ نُسُكٖۖ فَإِذَا أَمِنتُمْ فَمَن تَمَتَّعَ بِالْعُمْرَةِ إِلَى اَ۬لْحَجِّ فَمَا اَ۪سْتَيْسَرَ مِنَ اَ۬لْهَدْيِۖ ۞ فَمَن لَّمْ يَجِدْ فَصِيَامُ ثَلَٰثَةِ أَيَّامٖ فِے اِ۬لْحَجِّ وَسَبْعَةٍ إِذَا رَجَعْتُمْ تِلْكَ عَشَرَةٞ كَامِلَةٞۖ ذَٰلِكَ لِمَن لَّمْ يَكُنْ أَهْلُهُۥ حَاضِرِے اِ۬لْمَسْجِدِ اِ۬لْحَرَامِۖ وَاتَّقُواْ اُ۬للَّهَ وَاعْلَمُواْ أَنَّ اَ۬للَّهَ شَدِيدُ اُ۬لْعِقَابِۖ ١٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (203,2,'31',2,'Al-Baqarah','البَقَرَة',1,4,196,'اِ۬لْحَجُّ أَشْهُرٞ مَّعْلُومَٰتٞۖ فَمَن فَرَضَ فِيهِنَّ اَ۬لْحَجَّ فَلَا رَفَثَ وَلَا فُسُوقَ وَلَا جِدَالَ فِے اِ۬لْحَجِّۖ وَمَا تَفْعَلُواْ مِنْ خَيْرٖ يَعْلَمْهُ اُ۬للَّهُۖ وَتَزَوَّدُواْ فَإِنَّ خَيْرَ اَ۬لزَّادِ اِ۬لتَّقْوَىٰۖ وَاتَّقُونِ يَٰأُوْلِے اِ۬لْأَلْبَٰبِۖ ١٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (204,2,'31',2,'Al-Baqarah','البَقَرَة',4,8,197,'لَيْسَ عَلَيْكُمْ جُنَاحٌ أَن تَبْتَغُواْ فَضْلاٗ مِّن رَّبِّكُمْۖ فَإِذَا أَفَضْتُم مِّنْ عَرَفَٰتٖ فَاذْكُرُواْ اُ۬للَّهَ عِندَ اَ۬لْمَشْعَرِ اِ۬لْحَرَامِۖ وَاذْكُرُوهُ كَمَا هَدَيٰكُمْ وَإِن كُنتُم مِّن قَبْلِهِۦ لَمِنَ اَ۬لضَّآلِّينَۖ ١٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (205,2,'31',2,'Al-Baqarah','البَقَرَة',8,9,198,'ثُمَّ أَفِيضُواْ مِنْ حَيْثُ أَفَاضَ اَ۬لنَّاسُۖ وَاسْتَغْفِرُواْ اُ۬للَّهَۖ إِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٞۖ ١٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (206,2,'31',2,'Al-Baqarah','البَقَرَة',9,14,199,'فَإِذَا قَضَيْتُم مَّنَٰسِكَكُمْ فَاذْكُرُواْ اُ۬للَّهَ كَذِكْرِكُمْ ءَابَآءَكُمْ أَوْ أَشَدَّ ذِكْراٗۖ فَمِنَ اَ۬لنَّاسِ مَنْ يَّقُولُ رَبَّنَا ءَاتِنَا فِے اِ۬لدُّنْيَا وَمَا لَهُۥ فِے اِ۬لْأٓخِرَةِ مِنْ خَلَٰقٖۖ وَمِنْهُم مَّنْ يَّقُولُ رَبَّنَا ءَاتِنَا فِے اِ۬لدُّنْيَا حَسَنَةٗ وَفِے اِ۬لْأٓخِرَةِ حَسَنَةٗ وَقِنَا عَذَابَ اَ۬لنَّارِۖ ١٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (207,2,'31',2,'Al-Baqarah','البَقَرَة',14,15,200,'أُوْلَٰٓئِكَ لَهُمْ نَصِيبٞ مِّمَّا كَسَبُواْۖ وَاللَّهُ سَرِيعُ اُ۬لْحِسَابِۖ ٢٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (208,2,'32',2,'Al-Baqarah','البَقَرَة',1,3,201,'۞ وَاذْكُرُواْ اُ۬للَّهَ فِے أَيَّامٖ مَّعْدُودَٰتٖۖ فَمَن تَعَجَّلَ فِے يَوْمَيْنِ فَلَا إِثْمَ عَلَيْهِۖ وَمَن تَأَخَّرَ فَلَا إِثْمَ عَلَيْهِ لِمَنِ اِ۪تَّقَىٰۖ وَاتَّقُواْ اُ۬للَّهَ وَاعْلَمُواْ أَنَّكُمْ إِلَيْهِ تُحْشَرُونَۖ ٢٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (209,2,'32',2,'Al-Baqarah','البَقَرَة',4,5,202,'وَمِنَ اَ۬لنَّاسِ مَنْ يُّعْجِبُكَ قَوْلُهُۥ فِے اِ۬لْحَيَوٰةِ اِ۬لدُّنْيَا وَيُشْهِدُ اُ۬للَّهَ عَلَىٰ مَا فِے قَلْبِهِۦ وَهْوَ أَلَدُّ اُ۬لْخِصَامِۖ ٢٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (210,2,'32',2,'Al-Baqarah','البَقَرَة',6,7,203,'وَإِذَا تَوَلَّىٰ سَعَىٰ فِے اِ۬لْأَرْضِ لِيُفْسِدَ فِيهَا وَيُهْلِكَ اَ۬لْحَرْثَ وَالنَّسْلَۖ وَاللَّهُ لَا يُحِبُّ اُ۬لْفَسَادَۖ ٢٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (211,2,'32',2,'Al-Baqarah','البَقَرَة',7,9,204,'وَإِذَا قِيلَ لَهُ اُ۪تَّقِ اِ۬للَّهَ أَخَذَتْهُ اُ۬لْعِزَّةُ بِالْإِثْمِۖ فَحَسْبُهُۥ جَهَنَّمُۖ وَلَبِئْسَ اَ۬لْمِهَادُۖ ٢٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (212,2,'32',2,'Al-Baqarah','البَقَرَة',9,10,205,'وَمِنَ اَ۬لنَّاسِ مَنْ يَّشْرِے نَفْسَهُ اُ۪بْتِغَآءَ مَرْضَاتِ اِ۬للَّهِۖ وَاللَّهُ رَءُوفُۢ بِالْعِبَادِۖ ٢٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (213,2,'32',2,'Al-Baqarah','البَقَرَة',10,12,206,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ اُ۟دْخُلُواْ فِے اِ۬لسَّلْمِ كَآفَّةٗ وَلَا تَتَّبِعُواْ خُطْوَٰتِ اِ۬لشَّيْطَٰنِۖ إِنَّهُۥ لَكُمْ عَدُوّٞ مُّبِينٞۖ ٢٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (214,2,'32',2,'Al-Baqarah','البَقَرَة',12,13,207,'فَإِن زَلَلْتُم مِّنۢ بَعْدِ مَا جَآءَتْكُمُ اُ۬لْبَيِّنَٰتُ فَاعْلَمُواْ أَنَّ اَ۬للَّهَ عَزِيزٌ حَكِيمٌۖ ٢٠٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (215,2,'32',2,'Al-Baqarah','البَقَرَة',14,15,208,'هَلْ يَنظُرُونَ إِلَّا أَنْ يَّأْتِيَهُمُ اُ۬للَّهُ فِے ظُلَلٖ مِّنَ اَ۬لْغَمَٰمِۖ وَالْمَلَٰٓئِكَةُ وَقُضِيَ اَ۬لْأَمْرُۖ وَإِلَى اَ۬للَّهِ تُرْجَعُ اُ۬لْأُمُورُۖ ٢٠٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (216,2,'33',2,'Al-Baqarah','البَقَرَة',1,2,209,'سَلْ بَنِے إِسْرَآءِيلَ كَمْ ءَاتَيْنَٰهُم مِّنْ ءَايَةِۢ بَيِّنَةٖۖ وَمَنْ يُّبَدِّلْ نِعْمَةَ اَ۬للَّهِ مِنۢ بَعْدِ مَا جَآءَتْهُ فَإِنَّ اَ۬للَّهَ شَدِيدُ اُ۬لْعِقَابِۖ ٢٠٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (217,2,'33',2,'Al-Baqarah','البَقَرَة',2,4,210,'زُيِّنَ لِلذِينَ كَفَرُواْ اُ۬لْحَيَوٰةُ اُ۬لدُّنْيَا وَيَسْخَرُونَ مِنَ اَ۬لذِينَ ءَامَنُواْۖ وَالذِينَ اَ۪تَّقَوْاْ فَوْقَهُمْ يَوْمَ اَ۬لْقِيَٰمَةِۖ وَاللَّهُ يَرْزُقُ مَنْ يَّشَآءُ بِغَيْرِ حِسَابٖۖ ٢١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (218,2,'33',2,'Al-Baqarah','البَقَرَة',5,10,211,'۞ كَانَ اَ۬لنَّاسُ أُمَّةٗ وَٰحِدَةٗ فَبَعَثَ اَ۬للَّهُ اُ۬لنَّبِيٓـِٕۧنَ مُبَشِّرِينَ وَمُنذِرِينَ وَأَنزَلَ مَعَهُمُ اُ۬لْكِتَٰبَ بِالْحَقِّ لِيَحْكُمَ بَيْنَ اَ۬لنَّاسِ فِيمَا اَ۪خْتَلَفُواْ فِيهِۖ وَمَا اَ۪خْتَلَفَ فِيهِ إِلَّا اَ۬لذِينَ أُوتُوهُۖ مِنۢ بَعْدِ مَا جَآءَتْهُمُ اُ۬لْبَيِّنَٰتُ بَغْياَۢ بَيْنَهُمْۖ فَهَدَى اَ۬للَّهُ اُ۬لذِينَ ءَامَنُواْ لِمَا اَ۪خْتَلَفُواْ فِيهِ مِنَ اَ۬لْحَقِّ بِإِذْنِهِۦۖ وَاللَّهُ يَهْدِے مَنْ يَّشَآءُ اِ۪لَىٰ صِرَٰطٖ مُّسْتَقِيمٍۖ ٢١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (219,2,'33',2,'Al-Baqarah','البَقَرَة',10,13,212,'أَمْ حَسِبْتُمْ أَن تَدْخُلُواْ اُ۬لْجَنَّةَ وَلَمَّا يَأْتِكُم مَّثَلُ اُ۬لذِينَ خَلَوْاْ مِن قَبْلِكُم مَّسَّتْهُمُ اُ۬لْبَأْسَآءُ وَالضَّرَّآءُ وَزُلْزِلُواْ حَتَّىٰ يَقُولُ اُ۬لرَّسُولُ وَالذِينَ ءَامَنُواْ مَعَهُۥ مَتَىٰ نَصْرُ اُ۬للَّهِۖ أَلَا إِنَّ نَصْرَ اَ۬للَّهِ قَرِيبٞۖ ٢١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (220,2,'33',2,'Al-Baqarah','البَقَرَة',13,15,213,'يَسْـَٔلُونَكَ مَاذَا يُنفِقُونَۖ قُلْ مَا أَنفَقْتُم مِّنْ خَيْرٖ فَلِلْوَٰلِدَيْنِ وَالْأَقْرَبِينَ وَالْيَتَٰمَىٰ وَالْمَسَٰكِينِ وَابْنِ اِ۬لسَّبِيلِۖ وَمَا تَفْعَلُواْ مِنْ خَيْرٖ فَإِنَّ اَ۬للَّهَ بِهِۦ عَلِيمٞۖ ٢١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (221,2,'34',2,'Al-Baqarah','البَقَرَة',1,3,214,'كُتِبَ عَلَيْكُمُ اُ۬لْقِتَالُ وَهْوَ كُرْهٞ لَّكُمْۖ وَعَسَىٰ أَن تَكْرَهُواْ شَئْاٗ وَهْوَ خَيْرٞ لَّكُمْۖ وَعَسَىٰ أَن تُحِبُّواْ شَئْاٗ وَهْوَ شَرّٞ لَّكُمْۖ وَاللَّهُ يَعْلَمُۖ وَأَنتُمْ لَا تَعْلَمُونَۖ ٢١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (222,2,'34',2,'Al-Baqarah','البَقَرَة',3,10,215,'يَسْـَٔلُونَكَ عَنِ اِ۬لشَّهْرِ اِ۬لْحَرَامِ قِتَالٖ فِيهِۖ قُلْ قِتَالٞ فِيهِ كَبِيرٞۖ وَصَدٌّ عَن سَبِيلِ اِ۬للَّهِ وَكُفْرُۢ بِهِۦ وَالْمَسْجِدِ اِ۬لْحَرَامِ وَإِخْرَاجُ أَهْلِهِۦ مِنْهُ أَكْبَرُ عِندَ اَ۬للَّهِۖ وَالْفِتْنَةُ أَكْبَرُ مِنَ اَ۬لْقَتْلِۖ وَلَا يَزَالُونَ يُقَٰتِلُونَكُمْ حَتَّىٰ يَرُدُّوكُمْ عَن دِينِكُمْ إِنِ اِ۪سْتَطَٰعُواْۖ وَمَنْ يَّرْتَدِدْ مِنكُمْ عَن دِينِهِۦ فَيَمُتْ وَهْوَ كَافِرٞ فَأُوْلَٰٓئِكَ حَبِطَتْ أَعْمَٰلُهُمْ فِے اِ۬لدُّنْيَا وَالْأٓخِرَةِۖ وَأُوْلَٰٓئِكَ أَصْحَٰبُ اُ۬لنَّارِ هُمْ فِيهَا خَٰلِدُونَۖ ٢١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (223,2,'34',2,'Al-Baqarah','البَقَرَة',10,12,216,'إِنَّ اَ۬لذِينَ ءَامَنُواْ وَالذِينَ هَاجَرُواْ وَجَٰهَدُواْ فِے سَبِيلِ اِ۬للَّهِ أُوْلَٰٓئِكَ يَرْجُونَ رَحْمَتَ اَ۬للَّهِۖ وَاللَّهُ غَفُورٞ رَّحِيمٞۖ ٢١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (224,2,'34',2,'Al-Baqarah','البَقَرَة',12,15,217,'۞ يَسْـَٔلُونَكَ عَنِ اِ۬لْخَمْرِ وَالْمَيْسِرِۖ قُلْ فِيهِمَا إِثْمٞ كَبِيرٞ وَمَنَٰفِعُ لِلنَّاسِۖ وَإِثْمُهُمَا أَكْبَرُ مِن نَّفْعِهِمَاۖ وَيَسْـَٔلُونَكَ مَاذَا يُنفِقُونَ قُلِ اِ۬لْعَفْوَۖ كَذَٰلِكَ يُبَيِّنُ اُ۬للَّهُ لَكُمُ اُ۬لْأٓيَٰتِ لَعَلَّكُمْ تَتَفَكَّرُونَ ٢١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (225,2,'35',2,'Al-Baqarah','البَقَرَة',1,3,218,'فِے اِ۬لدُّنْيَا وَالْأٓخِرَةِۖ وَيَسْـَٔلُونَكَ عَنِ اِ۬لْيَتَٰمَىٰ قُلْ إِصْلَٰحٞ لَّهُمْ خَيْرٞۖ وَإِن تُخَالِطُوهُمْ فَإِخْوَٰنُكُمْۖ وَاللَّهُ يَعْلَمُ اُ۬لْمُفْسِدَ مِنَ اَ۬لْمُصْلِحِۖ وَلَوْ شَآءَ اَ۬للَّهُ لَأَعْنَتَكُمْۖ إِنَّ اَ۬للَّهَ عَزِيزٌ حَكِيمٞۖ ٢١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (226,2,'35',2,'Al-Baqarah','البَقَرَة',4,8,219,'وَلَا تَنكِحُواْ اُ۬لْمُشْرِكَٰتِ حَتَّىٰ يُؤْمِنَّۖ وَلَأَمَةٞ مُّؤْمِنَةٌ خَيْرٞ مِّن مُّشْرِكَةٖ وَلَوْ أَعْجَبَتْكُمْۖ وَلَا تُنكِحُواْ اُ۬لْمُشْرِكِينَ حَتَّىٰ يُؤْمِنُواْۖ وَلَعَبْدٞ مُّؤْمِنٌ خَيْرٞ مِّن مُّشْرِكٖ وَلَوْ أَعْجَبَكُمْۖ أُوْلَٰٓئِكَ يَدْعُونَ إِلَى اَ۬لنَّارِۖ وَاللَّهُ يَدْعُواْ إِلَى اَ۬لْجَنَّةِ وَالْمَغْفِرَةِ بِإِذْنِهِۦۖ وَيُبَيِّنُ ءَايَٰتِهِۦ لِلنَّاسِ لَعَلَّهُمْ يَتَذَكَّرُونَۖ ٢١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (227,2,'35',2,'Al-Baqarah','البَقَرَة',9,11,220,'وَيَسْـَٔلُونَكَ عَنِ اِ۬لْمَحِيضِ قُلْ هُوَ أَذىٗۖ فَاعْتَزِلُواْ اُ۬لنِّسَآءَ فِے اِ۬لْمَحِيضِۖ وَلَا تَقْرَبُوهُنَّ حَتَّىٰ يَطْهُرْنَۖ فَإِذَا تَطَهَّرْنَ فَأْتُوهُنَّ مِنْ حَيْثُ أَمَرَكُمُ اُ۬للَّهُۖ إِنَّ اَ۬للَّهَ يُحِبُّ اُ۬لتَّوَّٰبِينَ وَيُحِبُّ اُ۬لْمُتَطَهِّرِينَۖ ٢٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (228,2,'35',2,'Al-Baqarah','البَقَرَة',12,14,221,'نِسَآؤُكُمْ حَرْثٞ لَّكُمْ فَأْتُواْ حَرْثَكُمْ أَنَّىٰ شِئْتُمْۖ وَقَدِّمُواْ لِأَنفُسِكُمْۖ وَاتَّقُواْ اُ۬للَّهَۖ وَاعْلَمُواْ أَنَّكُم مُّلَٰقُوهُۖ وَبَشِّرِ اِ۬لْمُؤْمِنِينَۖ ٢٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (229,2,'35',2,'Al-Baqarah','البَقَرَة',14,15,222,'وَلَا تَجْعَلُواْ اُ۬للَّهَ عُرْضَةٗ لِّأَيْمَٰنِكُمْۖ أَن تَبَرُّواْ وَتَتَّقُواْ وَتُصْلِحُواْ بَيْنَ اَ۬لنَّاسِۖ وَاللَّهُ سَمِيعٌ عَلِيمٞۖ ٢٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (230,2,'36',2,'Al-Baqarah','البَقَرَة',1,2,223,'لَّا يُؤَاخِذُكُمُ اُ۬للَّهُ بِاللَّغْوِ فِے أَيْمَٰنِكُمْ وَلَٰكِنْ يُّؤَاخِذُكُم بِمَا كَسَبَتْ قُلُوبُكُمْۖ وَاللَّهُ غَفُورٌ حَلِيمٞۖ ٢٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (231,2,'36',2,'Al-Baqarah','البَقَرَة',2,3,224,'لِّلذِينَ يُؤْلُونَ مِن نِّسَآئِهِمْ تَرَبُّصُ أَرْبَعَةِ أَشْهُرٖۖ فَإِن فَآءُو فَإِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٞۖ ٢٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (232,2,'36',2,'Al-Baqarah','البَقَرَة',3,4,225,'وَإِنْ عَزَمُواْ اُ۬لطَّلَٰقَ فَإِنَّ اَ۬للَّهَ سَمِيعٌ عَلِيمٞۖ ٢٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (233,2,'36',2,'Al-Baqarah','البَقَرَة',4,8,226,'۞ وَالْمُطَلَّقَٰتُ يَتَرَبَّصْنَ بِأَنفُسِهِنَّ ثَلَٰثَةَ قُرُوٓءٖۖ وَلَا يَحِلُّ لَهُنَّ أَنْ يَّكْتُمْنَ مَا خَلَقَ اَ۬للَّهُ فِے أَرْحَامِهِنَّ إِن كُنَّ يُؤْمِنَّ بِاللَّهِ وَالْيَوْمِ اِ۬لْأٓخِرِۖ وَبُعُولَتُهُنَّ أَحَقُّ بِرَدِّهِنَّ فِے ذَٰلِكَ إِنْ أَرَادُواْ إِصْلَٰحاٗۖ وَلَهُنَّ مِثْلُ اُ۬لذِے عَلَيْهِنَّ بِالْمَعْرُوفِۖ وَلِلرِّجَالِ عَلَيْهِنَّ دَرَجَةٞۖ وَاللَّهُ عَزِيزٌ حَكِيمٌۖ ٢٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (234,2,'36',2,'Al-Baqarah','البَقَرَة',8,13,227,'اِ۬لطَّلَٰقُ مَرَّتَٰنِۖ فَإِمْسَاكُۢ بِمَعْرُوفٍ أَوْ تَسْرِيحُۢ بِإِحْسَٰنٖۖ وَلَا يَحِلُّ لَكُمْ أَن تَأْخُذُواْ مِمَّا ءَاتَيْتُمُوهُنَّ شَئْاً إِلَّا أَنْ يَّخَافَا أَلَّا يُقِيمَا حُدُودَ اَ۬للَّهِۖ فَإِنْ خِفْتُمْ أَلَّا يُقِيمَا حُدُودَ اَ۬للَّهِ فَلَا جُنَاحَ عَلَيْهِمَا فِيمَا اَ۪فْتَدَتْ بِهِۦۖ تِلْكَ حُدُودُ اُ۬للَّهِۖ فَلَا تَعْتَدُوهَاۖ وَمَنْ يَّتَعَدَّ حُدُودَ اَ۬للَّهِ فَأُوْلَٰٓئِكَ هُمُ اُ۬لظَّٰلِمُونَۖ ٢٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (235,2,'36',2,'Al-Baqarah','البَقَرَة',13,15,228,'فَإِن طَلَّقَهَا فَلَا تَحِلُّ لَهُۥ مِنۢ بَعْدُ حَتَّىٰ تَنكِحَ زَوْجاً غَيْرَهُۥۖ فَإِن طَلَّقَهَا فَلَا جُنَاحَ عَلَيْهِمَا أَنْ يَّتَرَاجَعَا إِن ظَنَّا أَنْ يُّقِيمَا حُدُودَ اَ۬للَّهِۖ وَتِلْكَ حُدُودُ اُ۬للَّهِ يُبَيِّنُهَا لِقَوْمٖ يَعْلَمُونَۖ ٢٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (236,2,'37',2,'Al-Baqarah','البَقَرَة',1,5,229,'وَإِذَا طَلَّقْتُمُ اُ۬لنِّسَآءَ فَبَلَغْنَ أَجَلَهُنَّ فَأَمْسِكُوهُنَّ بِمَعْرُوفٍ أَوْ سَرِّحُوهُنَّ بِمَعْرُوفٖۖ وَلَا تُمْسِكُوهُنَّ ضِرَاراٗ لِّتَعْتَدُواْۖ وَمَنْ يَّفْعَلْ ذَٰلِكَ فَقَدْ ظَلَمَ نَفْسَهُۥۖ وَلَا تَتَّخِذُواْ ءَايَٰتِ اِ۬للَّهِ هُزُؤاٗۖ وَاذْكُرُواْ نِعْمَتَ اَ۬للَّهِ عَلَيْكُمْ وَمَا أَنزَلَ عَلَيْكُم مِّنَ اَ۬لْكِتَٰبِ وَالْحِكْمَةِ يَعِظُكُم بِهِۦۖ وَاتَّقُواْ اُ۬للَّهَۖ وَاعْلَمُواْ أَنَّ اَ۬للَّهَ بِكُلِّ شَےْءٍ عَلِيمٞۖ ٢٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (237,2,'37',2,'Al-Baqarah','البَقَرَة',5,9,230,'وَإِذَا طَلَّقْتُمُ اُ۬لنِّسَآءَ فَبَلَغْنَ أَجَلَهُنَّ فَلَا تَعْضُلُوهُنَّ أَنْ يَّنكِحْنَ أَزْوَٰجَهُنَّ إِذَا تَرَٰضَوْاْ بَيْنَهُم بِالْمَعْرُوفِۖ ذَٰلِكَ يُوعَظُ بِهِۦ مَن كَانَ مِنكُمْ يُؤْمِنُ بِاللَّهِ وَالْيَوْمِ اِ۬لْأٓخِرِۖ ذَٰلِكُمْ أَزْكَىٰ لَكُمْ وَأَطْهَرُۖ وَاللَّهُ يَعْلَمُۖ وَأَنتُمْ لَا تَعْلَمُونَۖ ٢٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (238,2,'37',2,'Al-Baqarah','البَقَرَة',9,15,231,'۞ وَالْوَٰلِدَٰتُ يُرْضِعْنَ أَوْلَٰدَهُنَّ حَوْلَيْنِ كَامِلَيْنِۖ لِمَنْ أَرَادَ أَنْ يُّتِمَّ اَ۬لرَّضَٰعَةَۖ وَعَلَى اَ۬لْمَوْلُودِ لَهُۥ رِزْقُهُنَّ وَكِسْوَتُهُنَّ بِالْمَعْرُوفِۖ لَا تُكَلَّفُ نَفْسٌ إِلَّا وُسْعَهَاۖ لَا تُضَآرَّ وَٰلِدَةُۢ بِوَلَدِهَا وَلَا مَوْلُودٞ لَّهُۥ بِوَلَدِهِۦۖ وَعَلَى اَ۬لْوَارِثِ مِثْلُ ذَٰلِكَۖ فَإِنْ أَرَادَا فِصَالاً عَن تَرَاضٖ مِّنْهُمَا وَتَشَاوُرٖ فَلَا جُنَاحَ عَلَيْهِمَاۖ وَإِنْ أَرَدتُّمْ أَن تَسْتَرْضِعُواْ أَوْلَٰدَكُمْ فَلَا جُنَاحَ عَلَيْكُمْ إِذَا سَلَّمْتُم مَّا ءَاتَيْتُم بِالْمَعْرُوفِۖ وَاتَّقُواْ اُ۬للَّهَۖ وَاعْلَمُواْ أَنَّ اَ۬للَّهَ بِمَا تَعْمَلُونَ بَصِيرٞۖ ٢٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (239,2,'38',2,'Al-Baqarah','البَقَرَة',1,3,232,'وَالذِينَ يُتَوَفَّوْنَ مِنكُمْ وَيَذَرُونَ أَزْوَٰجاٗ يَتَرَبَّصْنَ بِأَنفُسِهِنَّ أَرْبَعَةَ أَشْهُرٖ وَعَشْراٗۖ فَإِذَا بَلَغْنَ أَجَلَهُنَّ فَلَا جُنَاحَ عَلَيْكُمْ فِيمَا فَعَلْنَ فِے أَنفُسِهِنَّ بِالْمَعْرُوفِۖ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٞۖ ٢٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (240,2,'38',2,'Al-Baqarah','البَقَرَة',4,9,233,'وَلَا جُنَاحَ عَلَيْكُمْ فِيمَا عَرَّضْتُم بِهِۦ مِنْ خِطْبَةِ اِ۬لنِّسَآءِ اَ۬وْ أَكْنَنتُمْ فِے أَنفُسِكُمْۖ عَلِمَ اَ۬للَّهُ أَنَّكُمْ سَتَذْكُرُونَهُنَّ وَلَٰكِن لَّا تُوَاعِدُوهُنَّ سِرّاًۖ إِلَّا أَن تَقُولُواْ قَوْلاٗ مَّعْرُوفاٗۖ ۞ وَلَا تَعْزِمُواْ عُقْدَةَ اَ۬لنِّكَاحِ حَتَّىٰ يَبْلُغَ اَ۬لْكِتَٰبُ أَجَلَهُۥۖ وَاعْلَمُواْ أَنَّ اَ۬للَّهَ يَعْلَمُ مَا فِے أَنفُسِكُمْ فَاحْذَرُوهُۖ وَاعْلَمُواْ أَنَّ اَ۬للَّهَ غَفُورٌ حَلِيمٞۖ ٢٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (241,2,'38',2,'Al-Baqarah','البَقَرَة',9,12,234,'لَّا جُنَاحَ عَلَيْكُمْ إِن طَلَّقْتُمُ اُ۬لنِّسَآءَ مَا لَمْ تَمَسُّوهُنَّ أَوْ تَفْرِضُواْ لَهُنَّ فَرِيضَةٗۖ وَمَتِّعُوهُنَّ عَلَى اَ۬لْمُوسِعِ قَدْرُهُۥ وَعَلَى اَ۬لْمُقْتِرِ قَدْرُهُۥ مَتَٰعاَۢ بِالْمَعْرُوفِۖ حَقّاً عَلَى اَ۬لْمُحْسِنِينَۖ ٢٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (242,2,'38',2,'Al-Baqarah','البَقَرَة',12,15,235,'وَإِن طَلَّقْتُمُوهُنَّ مِن قَبْلِ أَن تَمَسُّوهُنَّ وَقَدْ فَرَضْتُمْ لَهُنَّ فَرِيضَةٗ فَنِصْفُ مَا فَرَضْتُمْ إِلَّا أَنْ يَّعْفُونَ أَوْ يَعْفُوَاْ اَ۬لذِے بِيَدِهِۦ عُقْدَةُ اُ۬لنِّكَاحِۖ وَأَن تَعْفُواْ أَقْرَبُ لِلتَّقْوَىٰۖ وَلَا تَنسَوُاْ اُ۬لْفَضْلَ بَيْنَكُمْۖ إِنَّ اَ۬للَّهَ بِمَا تَعْمَلُونَ بَصِيرٌۖ ٢٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (243,2,'39',2,'Al-Baqarah','البَقَرَة',1,2,236,'حَٰفِظُواْ عَلَى اَ۬لصَّلَوَٰتِ وَالصَّلَوٰةِ اِ۬لْوُسْطَىٰ وَقُومُواْ لِلهِ قَٰنِتِينَۖ ٢٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (244,2,'39',2,'Al-Baqarah','البَقَرَة',2,3,237,'فَإِنْ خِفْتُمْ فَرِجَالاً أَوْ رُكْبَاناٗۖ فَإِذَا أَمِنتُمْ فَاذْكُرُواْ اُ۬للَّهَ كَمَا عَلَّمَكُم مَّا لَمْ تَكُونُواْ تَعْلَمُونَۖ ٢٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (245,2,'39',2,'Al-Baqarah','البَقَرَة',4,7,238,'وَالذِينَ يُتَوَفَّوْنَ مِنكُمْ وَيَذَرُونَ أَزْوَٰجاٗ وَصِيَّةٞ لِّأَزْوَٰجِهِم مَّتَٰعاً إِلَى اَ۬لْحَوْلِ غَيْرَ إِخْرَاجٖۖ فَإِنْ خَرَجْنَ فَلَا جُنَاحَ عَلَيْكُمْ فِے مَا فَعَلْنَ فِے أَنفُسِهِنَّ مِن مَّعْرُوفٖۖ وَاللَّهُ عَزِيزٌ حَكِيمٞۖ ٢٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (246,2,'39',2,'Al-Baqarah','البَقَرَة',7,8,239,'وَلِلْمُطَلَّقَٰتِ مَتَٰعُۢ بِالْمَعْرُوفِۖ حَقّاً عَلَى اَ۬لْمُتَّقِينَۖ ٢٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (247,2,'39',2,'Al-Baqarah','البَقَرَة',8,9,240,'كَذَٰلِكَ يُبَيِّنُ اُ۬للَّهُ لَكُمْ ءَايَٰتِهِۦ لَعَلَّكُمْ تَعْقِلُونَۖ ٢٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (248,2,'39',2,'Al-Baqarah','البَقَرَة',9,12,241,'۞ أَلَمْ تَرَ إِلَى اَ۬لذِينَ خَرَجُواْ مِن دِيَٰرِهِمْ وَهُمْ أُلُوفٌ حَذَرَ اَ۬لْمَوْتِ فَقَالَ لَهُمُ اُ۬للَّهُ مُوتُواْ ثُمَّ أَحْيَاهُمْۖ إِنَّ اَ۬للَّهَ لَذُو فَضْلٍ عَلَى اَ۬لنَّاسِۖ وَلَٰكِنَّ أَكْثَرَ اَ۬لنَّاسِ لَا يَشْكُرُونَۖ ٢٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (249,2,'39',2,'Al-Baqarah','البَقَرَة',13,13,242,'وَقَٰتِلُواْ فِے سَبِيلِ اِ۬للَّهِ وَاعْلَمُواْ أَنَّ اَ۬للَّهَ سَمِيعٌ عَلِيمٞۖ ٢٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (250,2,'39',2,'Al-Baqarah','البَقَرَة',14,15,243,'مَّن ذَا اَ۬لذِے يُقْرِضُ اُ۬للَّهَ قَرْضاً حَسَناٗ فَيُضَٰعِفُهُۥ لَهُۥ أَضْعَافاٗ كَثِيرَةٗۖ وَاللَّهُ يَقْبِضُ وَيَبْصُطُۖ وَإِلَيْهِ تُرْجَعُونَۖ ٢٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (251,2,'40',2,'Al-Baqarah','البَقَرَة',1,6,244,'أَلَمْ تَرَ إِلَى اَ۬لْمَلَإِ مِنۢ بَنِے إِسْرَآءِيلَ مِنۢ بَعْدِ مُوسَىٰ إِذْ قَالُواْ لِنَبِےٓءٖ لَّهُمُ اُ۪بْعَثْ لَنَا مَلِكاٗ نُّقَٰتِلْ فِے سَبِيلِ اِ۬للَّهِۖ قَالَ هَلْ عَسِيتُمْ إِن كُتِبَ عَلَيْكُمُ اُ۬لْقِتَالُ أَلَّا تُقَٰتِلُواْۖ قَالُواْ وَمَا لَنَا أَلَّا نُقَٰتِلَ فِے سَبِيلِ اِ۬للَّهِ وَقَدْ أُخْرِجْنَا مِن دِيَٰرِنَا وَأَبْنَآئِنَاۖ فَلَمَّا كُتِبَ عَلَيْهِمُ اُ۬لْقِتَالُ تَوَلَّوْاْ إِلَّا قَلِيلاٗ مِّنْهُمْۖ وَاللَّهُ عَلِيمُۢ بِالظَّٰلِمِينَۖ ٢٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (252,2,'40',2,'Al-Baqarah','البَقَرَة',6,11,245,'وَقَالَ لَهُمْ نَبِيٓـُٔهُمْ إِنَّ اَ۬للَّهَ قَدْ بَعَثَ لَكُمْ طَالُوتَ مَلِكاٗۖ قَالُواْ أَنَّىٰ يَكُونُ لَهُ اُ۬لْمُلْكُ عَلَيْنَا وَنَحْنُ أَحَقُّ بِالْمُلْكِ مِنْهُ وَلَمْ يُؤْتَ سَعَةٗ مِّنَ اَ۬لْمَالِۖ قَالَ إِنَّ اَ۬للَّهَ اَ۪صْطَفَيٰهُ عَلَيْكُمْ وَزَادَهُۥ بَسْطَةٗ فِے اِ۬لْعِلْمِ وَالْجِسْمِۖ وَاللَّهُ يُؤْتِے مُلْكَهُۥ مَنْ يَّشَآءُۖ وَاللَّهُ وَٰسِعٌ عَلِيمٞۖ ٢٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (253,2,'40',2,'Al-Baqarah','البَقَرَة',12,15,246,'۞ وَقَالَ لَهُمْ نَبِيٓـُٔهُمْ إِنَّ ءَايَةَ مُلْكِهِۦ أَنْ يَّأْتِيَكُمُ اُ۬لتَّابُوتُ فِيهِ سَكِينَةٞ مِّن رَّبِّكُمْ وَبَقِيَّةٞ مِّمَّا تَرَكَ ءَالُ مُوسَىٰ وَءَالُ هَٰرُونَ تَحْمِلُهُ اُ۬لْمَلَٰٓئِكَةُۖ إِنَّ فِے ذَٰلِكَ لَأٓيَةٗ لَّكُمْ إِن كُنتُم مُّؤْمِنِينَۖ ٢٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (254,2,'41',2,'Al-Baqarah','البَقَرَة',1,8,247,'فَلَمَّا فَصَلَ طَالُوتُ بِالْجُنُودِ قَالَ إِنَّ اَ۬للَّهَ مُبْتَلِيكُم بِنَهَرٖ فَمَن شَرِبَ مِنْهُ فَلَيْسَ مِنِّے وَمَن لَّمْ يَطْعَمْهُ فَإِنَّهُۥ مِنِّيَ إِلَّا مَنِ اِ۪غْتَرَفَ غَرْفَةَۢ بِيَدِهِۦۖ فَشَرِبُواْ مِنْهُ إِلَّا قَلِيلاٗ مِّنْهُمْۖ فَلَمَّا جَاوَزَهُۥ هُوَ وَالذِينَ ءَامَنُواْ مَعَهُۥ قَالُواْ لَا طَاقَةَ لَنَا اَ۬لْيَوْمَ بِجَالُوتَ وَجُنُودِهِۦۖ قَالَ اَ۬لذِينَ يَظُنُّونَ أَنَّهُم مُّلَٰقُواْ اُ۬للَّهِ كَم مِّن فِئَةٖ قَلِيلَةٍ غَلَبَتْ فِئَةٗ كَثِيرَةَۢ بِإِذْنِ اِ۬للَّهِۖ وَاللَّهُ مَعَ اَ۬لصَّٰبِرِينَۖ ٢٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (255,2,'41',2,'Al-Baqarah','البَقَرَة',8,10,248,'وَلَمَّا بَرَزُواْ لِجَالُوتَ وَجُنُودِهِۦ قَالُواْ رَبَّنَا أَفْرِغْ عَلَيْنَا صَبْراٗ وَثَبِّتْ أَقْدَامَنَا وَانصُرْنَا عَلَى اَ۬لْقَوْمِ اِ۬لْكَٰفِرِينَ ٢٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (256,2,'41',2,'Al-Baqarah','البَقَرَة',10,14,249,'فَهَزَمُوهُم بِإِذْنِ اِ۬للَّهِۖ وَقَتَلَ دَاوُۥدُ جَالُوتَ وَءَاتَيٰهُ اُ۬للَّهُ اُ۬لْمُلْكَ وَالْحِكْمَةَ وَعَلَّمَهُۥ مِمَّا يَشَآءُۖ وَلَوْلَا دِفَٰعُ اُ۬للَّهِ اِ۬لنَّاسَ بَعْضَهُم بِبَعْضٖ لَّفَسَدَتِ اِ۬لْأَرْضُۖ وَلَٰكِنَّ اَ۬للَّهَ ذُو فَضْلٍ عَلَى اَ۬لْعَٰلَمِينَۖ ٢٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (257,2,'41',2,'Al-Baqarah','البَقَرَة',14,15,250,'تِلْكَ ءَايَٰتُ اُ۬للَّهِ نَتْلُوهَا عَلَيْكَ بِالْحَقِّۖ وَإِنَّكَ لَمِنَ اَ۬لْمُرْسَلِينَۖ ٢٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (258,3,'42',2,'Al-Baqarah','البَقَرَة',1,6,251,'۞ تِلْكَ اَ۬لرُّسُلُ فَضَّلْنَا بَعْضَهُمْ عَلَىٰ بَعْضٖۖ مِّنْهُم مَّن كَلَّمَ اَ۬للَّهُۖ وَرَفَعَ بَعْضَهُمْ دَرَجَٰتٖۖ وَءَاتَيْنَا عِيسَى اَ۪بْنَ مَرْيَمَ اَ۬لْبَيِّنَٰتِ وَأَيَّدْنَٰهُ بِرُوحِ اِ۬لْقُدُسِۖ وَلَوْ شَآءَ اَ۬للَّهُ مَا اَ۪قْتَتَلَ اَ۬لذِينَ مِنۢ بَعْدِهِم مِّنۢ بَعْدِ مَا جَآءَتْهُمُ اُ۬لْبَيِّنَٰتُۖ وَلَٰكِنِ اِ۪خْتَلَفُواْۖ فَمِنْهُم مَّنْ ءَامَنَ وَمِنْهُم مَّن كَفَرَۖ وَلَوْ شَآءَ اَ۬للَّهُ مَا اَ۪قْتَتَلُواْ وَلَٰكِنَّ اَ۬للَّهَ يَفْعَلُ مَا يُرِيدُۖ ٢٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (259,3,'42',2,'Al-Baqarah','البَقَرَة',6,8,252,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ أَنفِقُواْ مِمَّا رَزَقْنَٰكُم مِّن قَبْلِ أَنْ يَّأْتِيَ يَوْمٞ لَّا بَيْعٞ فِيهِ وَلَا خُلَّةٞ وَلَا شَفَٰعَةٞۖ وَالْكَٰفِرُونَ هُمُ اُ۬لظَّٰلِمُونَۖ ٢٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (260,3,'42',2,'Al-Baqarah','البَقَرَة',8,9,253,'اَ۬للَّهُ لَا إِلَٰهَ إِلَّا هُوَۖ اَ۬لْحَيُّ اُ۬لْقَيُّومُۖ ٢٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (261,3,'42',2,'Al-Baqarah','البَقَرَة',9,13,254,'لَا تَأْخُذُهُۥ سِنَةٞ وَلَا نَوْمٞۖ لَّهُۥ مَا فِے اِ۬لسَّمَٰوَٰتِ وَمَا فِے اِ۬لْأَرْضِۖ مَن ذَا اَ۬لذِے يَشْفَعُ عِندَهُۥ إِلَّا بِإِذْنِهِۦۖ يَعْلَمُ مَا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُمْۖ وَلَا يُحِيطُونَ بِشَےْءٖ مِّنْ عِلْمِهِۦ إِلَّا بِمَا شَآءَۖ وَسِعَ كُرْسِيُّهُ اُ۬لسَّمَٰوَٰتِ وَالْأَرْضَۖ وَلَا يَـُٔودُهُۥ حِفْظُهُمَاۖ وَهْوَ اَ۬لْعَلِيُّ اُ۬لْعَظِيمُۖ ٢٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (262,3,'42',2,'Al-Baqarah','البَقَرَة',13,15,255,'لَا إِكْرَاهَ فِے اِ۬لدِّينِۖ قَد تَّبَيَّنَ اَ۬لرُّشْدُ مِنَ اَ۬لْغَيِّۖ فَمَنْ يَّكْفُرْ بِالطَّٰغُوتِ وَيُؤْمِنۢ بِاللَّهِ فَقَدِ اِ۪سْتَمْسَكَ بِالْعُرْوَةِ اِ۬لْوُثْقَىٰ لَا اَ۪نفِصَامَ لَهَاۖ وَاللَّهُ سَمِيعٌ عَلِيمٌۖ ٢٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (263,3,'43',2,'Al-Baqarah','البَقَرَة',1,4,256,'اِ۬للَّهُ وَلِيُّ اُ۬لذِينَ ءَامَنُواْ يُخْرِجُهُم مِّنَ اَ۬لظُّلُمَٰتِ إِلَى اَ۬لنُّورِۖ وَالذِينَ كَفَرُواْ أَوْلِيَآؤُهُمُ اُ۬لطَّٰغُوتُ يُخْرِجُونَهُم مِّنَ اَ۬لنُّورِ إِلَى اَ۬لظُّلُمَٰتِۖ أُوْلَٰٓئِكَ أَصْحَٰبُ اُ۬لنَّارِ هُمْ فِيهَا خَٰلِدُونَۖ ٢٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (264,3,'43',2,'Al-Baqarah','البَقَرَة',4,8,257,'۞ أَلَمْ تَرَ إِلَى اَ۬لذِے حَآجَّ إِبْرَٰهِيمَ فِے رَبِّهِۦ أَنْ ءَاتَيٰهُ اُ۬للَّهُ اُ۬لْمُلْكَ إِذْ قَالَ إِبْرَٰهِيمُ رَبِّيَ اَ۬لذِے يُحْيِۦ وَيُمِيتُۖ قَالَ أَنَا أُحْيِۦ وَأُمِيتُۖ قَالَ إِبْرَٰهِيمُ فَإِنَّ اَ۬للَّهَ يَأْتِے بِالشَّمْسِ مِنَ اَ۬لْمَشْرِقِ فَأْتِ بِهَا مِنَ اَ۬لْمَغْرِبِ فَبُهِتَ اَ۬لذِے كَفَرَۖ وَاللَّهُ لَا يَهْدِے اِ۬لْقَوْمَ اَ۬لظَّٰلِمِينَۖ ٢٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (265,3,'43',2,'Al-Baqarah','البَقَرَة',8,15,258,'أَوْ كَالذِے مَرَّ عَلَىٰ قَرْيَةٖ وَهْيَ خَاوِيَةٌ عَلَىٰ عُرُوشِهَاۖ قَالَ أَنَّىٰ يُحْيِۦ هَٰذِهِ اِ۬للَّهُ بَعْدَ مَوْتِهَاۖ فَأَمَاتَهُ اُ۬للَّهُ مِاْئَةَ عَامٖ ثُمَّ بَعَثَهُۥۖ قَالَ كَمْ لَبِثْتَۖ قَالَ لَبِثْتُ يَوْماً أَوْ بَعْضَ يَوْمٖۖ قَالَ بَل لَّبِثْتَ مِاْئَةَ عَامٖۖ فَانظُرْ إِلَىٰ طَعَامِكَ وَشَرَابِكَ لَمْ يَتَسَنَّهْۖ وَانظُرْ إِلَىٰ حِمَارِكَۖ وَلِنَجْعَلَكَ ءَايَةٗ لِّلنَّاسِۖ وَانظُرْ إِلَى اَ۬لْعِظَٰمِ كَيْفَ نُنشِرُهَا ثُمَّ نَكْسُوهَا لَحْماٗۖ فَلَمَّا تَبَيَّنَ لَهُۥ قَالَ أَعْلَمُ أَنَّ اَ۬للَّهَ عَلَىٰ كُلِّ شَےْءٖ قَدِيرٞۖ ٢٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (266,3,'44',2,'Al-Baqarah','البَقَرَة',1,4,259,'وَإِذْ قَالَ إِبْرَٰهِيمُ رَبِّ أَرِنِے كَيْفَ تُحْيِ اِ۬لْمَوْتَىٰۖ قَالَ أَوَلَمْ تُؤْمِنۖ قَالَ بَلَىٰ وَلَٰكِن لِّيَطْمَئِنَّ قَلْبِےۖ قَالَ فَخُذْ أَرْبَعَةٗ مِّنَ اَ۬لطَّيْرِ فَصُرْهُنَّ إِلَيْكَ ثُمَّ اَ۪جْعَلْ عَلَىٰ كُلِّ جَبَلٖ مِّنْهُنَّ جُزْءاٗ ثُمَّ اَ۟دْعُهُنَّ يَأْتِينَكَ سَعْياٗۖ وَاعْلَمْ أَنَّ اَ۬للَّهَ عَزِيزٌ حَكِيمٞۖ ٢٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (267,3,'44',2,'Al-Baqarah','البَقَرَة',5,7,260,'مَّثَلُ اُ۬لذِينَ يُنفِقُونَ أَمْوَٰلَهُمْ فِے سَبِيلِ اِ۬للَّهِ كَمَثَلِ حَبَّةٍ أَنۢبَتَتْ سَبْعَ سَنَابِلَۖ فِے كُلِّ سُنۢبُلَةٖ مِّاْئَةُ حَبَّةٖۖ وَاللَّهُ يُضَٰعِفُ لِمَنْ يَّشَآءُۖ وَاللَّهُ وَٰسِعٌ عَلِيمٌۖ ٢٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (268,3,'44',2,'Al-Baqarah','البَقَرَة',7,10,261,'اِ۬لذِينَ يُنفِقُونَ أَمْوَٰلَهُمْ فِے سَبِيلِ اِ۬للَّهِ ثُمَّ لَا يُتْبِعُونَ مَا أَنفَقُواْ مَنّاٗ وَلَا أَذىٗ لَّهُمْ أَجْرُهُمْ عِندَ رَبِّهِمْ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَۖ ٢٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (269,3,'44',2,'Al-Baqarah','البَقَرَة',10,11,262,'۞ قَوْلٞ مَّعْرُوفٞ وَمَغْفِرَةٌ خَيْرٞ مِّن صَدَقَةٖ يَتْبَعُهَا أَذىٗۖ وَاللَّهُ غَنِيٌّ حَلِيمٞۖ ٢٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (270,3,'44',2,'Al-Baqarah','البَقَرَة',11,15,263,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ لَا تُبْطِلُواْ صَدَقَٰتِكُم بِالْمَنِّ وَالْأَذَىٰ كَالذِے يُنفِقُ مَالَهُۥ رِئَآءَ اَ۬لنَّاسِ وَلَا يُؤْمِنُ بِاللَّهِ وَالْيَوْمِ اِ۬لْأٓخِرِۖ فَمَثَلُهُۥ كَمَثَلِ صَفْوَانٍ عَلَيْهِ تُرَابٞ فَأَصَابَهُۥ وَابِلٞ فَتَرَكَهُۥ صَلْداٗۖ لَّا يَقْدِرُونَ عَلَىٰ شَےْءٖ مِّمَّا كَسَبُواْۖ وَاللَّهُ لَا يَهْدِے اِ۬لْقَوْمَ اَ۬لْكَٰفِرِينَۖ ٢٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (271,3,'45',2,'Al-Baqarah','البَقَرَة',1,4,264,'وَمَثَلُ اُ۬لذِينَ يُنفِقُونَ أَمْوَٰلَهُمُ اُ۪بْتِغَآءَ مَرْضَاتِ اِ۬للَّهِ وَتَثْبِيتاٗ مِّنْ أَنفُسِهِمْ كَمَثَلِ جَنَّةِۢ بِرُبْوَةٍ أَصَابَهَا وَابِلٞ فَـَٔاتَتْ أُكْلَهَا ضِعْفَيْنِ فَإِن لَّمْ يُصِبْهَا وَابِلٞ فَطَلّٞۖ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌۖ ٢٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (272,3,'45',2,'Al-Baqarah','البَقَرَة',4,8,265,'أَيَوَدُّ أَحَدُكُمْ أَن تَكُونَ لَهُۥ جَنَّةٞ مِّن نَّخِيلٖ وَأَعْنَٰبٖ تَجْرِے مِن تَحْتِهَا اَ۬لْأَنْهَٰرُ لَهُۥ فِيهَا مِن كُلِّ اِ۬لثَّمَرَٰتِ وَأَصَابَهُ اُ۬لْكِبَرُ وَلَهُۥ ذُرِّيَّةٞ ضُعَفَآءُ فَأَصَابَهَا إِعْصَارٞ فِيهِ نَارٞ فَاحْتَرَقَتْۖ كَذَٰلِكَ يُبَيِّنُ اُ۬للَّهُ لَكُمُ اُ۬لْأٓيَٰتِ لَعَلَّكُمْ تَتَفَكَّرُونَۖ ٢٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (273,3,'45',2,'Al-Baqarah','البَقَرَة',8,12,266,'۞ يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ أَنفِقُواْ مِن طَيِّبَٰتِ مَا كَسَبْتُمْ وَمِمَّا أَخْرَجْنَا لَكُم مِّنَ اَ۬لْأَرْضِۖ وَلَا تَيَمَّمُواْ اُ۬لْخَبِيثَ مِنْهُ تُنفِقُونَ وَلَسْتُم بِـَٔاخِذِيهِ إِلَّا أَن تُغْمِضُواْ فِيهِۖ وَاعْلَمُواْ أَنَّ اَ۬للَّهَ غَنِيٌّ حَمِيدٌۖ ٢٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (274,3,'45',2,'Al-Baqarah','البَقَرَة',12,13,267,'اِ۬لشَّيْطَٰنُ يَعِدُكُمُ اُ۬لْفَقْرَ وَيَأْمُرُكُم بِالْفَحْشَآءِۖ وَاللَّهُ يَعِدُكُم مَّغْفِرَةٗ مِّنْهُ وَفَضْلاٗۖ وَاللَّهُ وَٰسِعٌ عَلِيمٞۖ ٢٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (275,3,'45',2,'Al-Baqarah','البَقَرَة',14,15,268,'يُؤْتِے اِ۬لْحِكْمَةَ مَنْ يَّشَآءُۖ وَمَنْ يُّؤْتَ اَ۬لْحِكْمَةَ فَقَدْ أُوتِيَ خَيْراٗ كَثِيراٗۖ وَمَا يَذَّكَّرُ إِلَّا أُوْلُواْ اُ۬لْأَلْبَٰبِۖ ٢٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (276,3,'46',2,'Al-Baqarah','البَقَرَة',1,2,269,'وَمَا أَنفَقْتُم مِّن نَّفَقَةٍ أَوْ نَذَرْتُم مِّن نَّذْرٖ فَإِنَّ اَ۬للَّهَ يَعْلَمُهُۥۖ وَمَا لِلظَّٰلِمِينَ مِنْ أَنصَارٍۖ ٢٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (277,3,'46',2,'Al-Baqarah','البَقَرَة',2,5,270,'إِن تُبْدُواْ اُ۬لصَّدَقَٰتِ فَنِع۪مَّا هِيَۖ وَإِن تُخْفُوهَا وَتُؤْتُوهَا اَ۬لْفُقَرَآءَ فَهْوَ خَيْرٞ لَّكُمْ وَنُكَفِّرْ عَنكُم مِّن سَيِّـَٔاتِكُمْۖ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٞۖ ٢٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (278,3,'46',2,'Al-Baqarah','البَقَرَة',5,9,271,'۞ لَّيْسَ عَلَيْكَ هُدَيٰهُمْۖ وَلَٰكِنَّ اَ۬للَّهَ يَهْدِے مَنْ يَّشَآءُۖ وَمَا تُنفِقُواْ مِنْ خَيْرٖ فَلِأَنفُسِكُمْۖ وَمَا تُنفِقُونَ إِلَّا اَ۪بْتِغَآءَ وَجْهِ اِ۬للَّهِۖ وَمَا تُنفِقُواْ مِنْ خَيْرٖ يُوَفَّ إِلَيْكُمْ وَأَنتُمْ لَا تُظْلَمُونَۖ ٢٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (279,3,'46',2,'Al-Baqarah','البَقَرَة',9,13,272,'لِلْفُقَرَآءِ اِ۬لذِينَ أُحْصِرُواْ فِے سَبِيلِ اِ۬للَّهِ لَا يَسْتَطِيعُونَ ضَرْباٗ فِے اِ۬لْأَرْضِ يَحْسِبُهُمُ اُ۬لْجَاهِلُ أَغْنِيَآءَ مِنَ اَ۬لتَّعَفُّفِۖ تَعْرِفُهُم بِسِيمَٰهُمْ لَا يَسْـَٔلُونَ اَ۬لنَّاسَ إِلْحَافاٗۖ وَمَا تُنفِقُواْ مِنْ خَيْرٖ فَإِنَّ اَ۬للَّهَ بِهِۦ عَلِيمٌۖ ٢٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (280,3,'46',2,'Al-Baqarah','البَقَرَة',13,15,273,'اِ۬لذِينَ يُنفِقُونَ أَمْوَٰلَهُم بِاليْلِ وَالنَّهَارِ سِرّاٗ وَعَلَٰنِيَةٗ فَلَهُمْ أَجْرُهُمْ عِندَ رَبِّهِمْ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَۖ ٢٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (281,3,'47',2,'Al-Baqarah','البَقَرَة',1,5,274,'اَ۬لذِينَ يَأْكُلُونَ اَ۬لرِّبَوٰاْ لَا يَقُومُونَ إِلَّا كَمَا يَقُومُ اُ۬لذِے يَتَخَبَّطُهُ اُ۬لشَّيْطَٰنُ مِنَ اَ۬لْمَسِّۖ ذَٰلِكَ بِأَنَّهُمْ قَالُواْ إِنَّمَا اَ۬لْبَيْعُ مِثْلُ اُ۬لرِّبَوٰاْۖ وَأَحَلَّ اَ۬للَّهُ اُ۬لْبَيْعَ وَحَرَّمَ اَ۬لرِّبَوٰاْۖ فَمَن جَآءَهُۥ مَوْعِظَةٞ مِّن رَّبِّهِۦ فَانتَهَىٰ فَلَهُۥ مَا سَلَفَۖ وَأَمْرُهُۥ إِلَى اَ۬للَّهِۖ وَمَنْ عَادَ فَأُوْلَٰٓئِكَ أَصْحَٰبُ اُ۬لنَّارِ هُمْ فِيهَا خَٰلِدُونَۖ ٢٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (282,3,'47',2,'Al-Baqarah','البَقَرَة',6,7,275,'يَمْحَقُ اُ۬للَّهُ اُ۬لرِّبَوٰاْ وَيُرْبِے اِ۬لصَّدَقَٰتِۖ وَاللَّهُ لَا يُحِبُّ كُلَّ كَفَّارٍ أَثِيمٍۖ ٢٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (283,3,'47',2,'Al-Baqarah','البَقَرَة',7,9,276,'إِنَّ اَ۬لذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ وَأَقَامُواْ اُ۬لصَّلَوٰةَ وَءَاتَوُاْ اُ۬لزَّكَوٰةَ لَهُمْ أَجْرُهُمْ عِندَ رَبِّهِمْ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَۖ ٢٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (284,3,'47',2,'Al-Baqarah','البَقَرَة',9,10,277,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ اُ۪تَّقُواْ اُ۬للَّهَ وَذَرُواْ مَا بَقِيَ مِنَ اَ۬لرِّبَوٰاْ إِن كُنتُم مُّؤْمِنِينَۖ ٢٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (285,3,'47',2,'Al-Baqarah','البَقَرَة',11,12,278,'فَإِن لَّمْ تَفْعَلُواْ فَأْذَنُواْ بِحَرْبٖ مِّنَ اَ۬للَّهِ وَرَسُولِهِۦۖ وَإِن تُبْتُمْ فَلَكُمْ رُءُوسُ أَمْوَٰلِكُمْ لَا تَظْلِمُونَ وَلَا تُظْلَمُونَۖ ٢٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (286,3,'47',2,'Al-Baqarah','البَقَرَة',12,14,279,'۞ وَإِن كَانَ ذُو عُسْرَةٖ فَنَظِرَةٌ إِلَىٰ مَيْسُرَةٖۖ وَأَن تَصَّدَّقُواْ خَيْرٞ لَّكُمْ إِن كُنتُمْ تَعْلَمُونَۖ ٢٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (287,3,'47',2,'Al-Baqarah','البَقَرَة',14,15,280,'وَاتَّقُواْ يَوْماٗ تُرْجَعُونَ فِيهِ إِلَى اَ۬للَّهِۖ ثُمَّ تُوَفَّىٰ كُلُّ نَفْسٖ مَّا كَسَبَتْ وَهُمْ لَا يُظْلَمُونَۖ ٢٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (288,3,'48',2,'Al-Baqarah','البَقَرَة',1,15,281,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ إِذَا تَدَايَنتُم بِدَيْنٍ إِلَىٰ أَجَلٖ مُّسَمّىٗ فَاكْتُبُوهُۖ وَلْيَكْتُب بَّيْنَكُمْ كَاتِبُۢ بِالْعَدْلِۖ وَلَا يَأْبَ كَاتِبٌ أَنْ يَّكْتُبَۖ كَمَا عَلَّمَهُ اُ۬للَّهُ فَلْيَكْتُبْۖ وَلْيُمْلِلِ اِ۬لذِے عَلَيْهِ اِ۬لْحَقُّ وَلْيَتَّقِ اِ۬للَّهَ رَبَّهُۥ وَلَا يَبْخَسْ مِنْهُ شَئْاٗۖ فَإِن كَانَ اَ۬لذِے عَلَيْهِ اِ۬لْحَقُّ سَفِيهاً أَوْ ضَعِيفاً أَوْ لَا يَسْتَطِيعُ أَنْ يُّمِلَّ هُوَ فَلْيُمْلِلْ وَلِيُّهُۥ بِالْعَدْلِۖ وَاسْتَشْهِدُواْ شَهِيدَيْنِ مِن رِّجَالِكُمْۖ فَإِن لَّمْ يَكُونَا رَجُلَيْنِ فَرَجُلٞ وَامْرَأَتَٰنِ مِمَّن تَرْضَوْنَ مِنَ اَ۬لشُّهَدَآءِ اَ۬ن تَضِلَّ إِحْدَيٰهُمَا فَتُذَكِّرَ إِحْدَيٰهُمَا اَ۬لْأُخْرَىٰۖ وَلَا يَأْبَ اَ۬لشُّهَدَآءُ اِ۪ذَا مَا دُعُواْۖ وَلَا تَسْـَٔمُواْ أَن تَكْتُبُوهُ صَغِيراً أَوْ كَبِيراً إِلَىٰ أَجَلِهِۦۖ ذَٰلِكُمْ أَقْسَطُ عِندَ اَ۬للَّهِ وَأَقْوَمُ لِلشَّهَٰدَةِ وَأَدْنَىٰ أَلَّا تَرْتَابُواْ إِلَّا أَن تَكُونَ تِجَٰرَةٌ حَاضِرَةٞ تُدِيرُونَهَا بَيْنَكُمْ فَلَيْسَ عَلَيْكُمْ جُنَاحٌ أَلَّا تَكْتُبُوهَاۖ وَأَشْهِدُواْ إِذَا تَبَايَعْتُمْۖ وَلَا يُضَآرَّ كَاتِبٞ وَلَا شَهِيدٞۖ وَإِن تَفْعَلُواْ فَإِنَّهُۥ فُسُوقُۢ بِكُمْۖ وَاتَّقُواْ اُ۬للَّهَۖ وَيُعَلِّمُكُمُ اُ۬للَّهُۖ وَاللَّهُ بِكُلِّ شَےْءٍ عَلِيمٞۖ ٢٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (289,3,'49',2,'Al-Baqarah','البَقَرَة',1,4,282,'۞ وَإِن كُنتُمْ عَلَىٰ سَفَرٖ وَلَمْ تَجِدُواْ كَاتِباٗ فَرِهَٰنٞ مَّقْبُوضَةٞۖ فَإِنْ أَمِنَ بَعْضُكُم بَعْضاٗ فَلْيُؤَدِّ اِ۬لذِے اِ۟ؤْتُمِنَ أَمَٰنَتَهُۥۖ وَلْيَتَّقِ اِ۬للَّهَ رَبَّهُۥۖ وَلَا تَكْتُمُواْ اُ۬لشَّهَٰدَةَۖ وَمَنْ يَّكْتُمْهَا فَإِنَّهُۥ ءَاثِمٞ قَلْبُهُۥۖ وَاللَّهُ بِمَا تَعْمَلُونَ عَلِيمٞۖ ٢٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (290,3,'49',2,'Al-Baqarah','البَقَرَة',4,7,283,'لِّلهِ مَا فِے اِ۬لسَّمَٰوَٰتِ وَمَا فِے اِ۬لْأَرْضِۖ وَإِن تُبْدُواْ مَا فِے أَنفُسِكُمْ أَوْ تُخْفُوهُ يُحَاسِبْكُم بِهِ اِ۬للَّهُ فَيَغْفِرْ لِمَنْ يَّشَآءُ وَيُعَذِّب مَّنْ يَّشَآءُۖ وَاللَّهُ عَلَىٰ كُلِّ شَےْءٖ قَدِيرٌۖ ٢٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (291,3,'49',2,'Al-Baqarah','البَقَرَة',7,10,284,'ءَامَنَ اَ۬لرَّسُولُ بِمَا أُنزِلَ إِلَيْهِ مِن رَّبِّهِۦ وَالْمُؤْمِنُونَۖ كُلٌّ ءَامَنَ بِاللَّهِ وَمَلَٰٓئِكَتِهِۦ وَكُتُبِهِۦ وَرُسُلِهِۦۖ لَا نُفَرِّقُ بَيْنَ أَحَدٖ مِّن رُّسُلِهِۦۖ وَقَالُواْ سَمِعْنَا وَأَطَعْنَاۖ غُفْرَانَكَ رَبَّنَاۖ وَإِلَيْكَ اَ۬لْمَصِيرُۖ ٢٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (292,3,'49',2,'Al-Baqarah','البَقَرَة',10,15,285,'لَا يُكَلِّفُ اُ۬للَّهُ نَفْساً إِلَّا وُسْعَهَاۖ لَهَا مَا كَسَبَتْۖ وَعَلَيْهَا مَا اَ۪كْتَسَبَتْۖ رَبَّنَا لَا تُؤَاخِذْنَا إِن نَّسِينَا أَوْ أَخْطَأْنَاۖ رَبَّنَا وَلَا تَحْمِلْ عَلَيْنَا إِصْراٗ كَمَا حَمَلْتَهُۥ عَلَى اَ۬لذِينَ مِن قَبْلِنَاۖ رَبَّنَا وَلَا تُحَمِّلْنَا مَا لَا طَاقَةَ لَنَا بِهِۦۖ وَاعْفُ عَنَّاۖ وَاغْفِرْ لَنَاۖ وَارْحَمْنَاۖ أَنتَ مَوْلَيٰنَا فَانصُرْنَا عَلَى اَ۬لْقَوْمِ اِ۬لْكَٰفِرِينَۖ ٢٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (293,3,'50',3,'Āl-‘Imrān','آل عِمران',3,3,1,'أَلَٓمِّٓۖ اَ۬للَّهُ لَا إِلَٰهَ إِلَّا هُوَۖ اَ۬لْحَيُّ اُ۬لْقَيُّومُ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (294,3,'50',3,'Āl-‘Imrān','آل عِمران',3,4,2,'نَزَّلَ عَلَيْكَ اَ۬لْكِتَٰبَ بِالْحَقِّ مُصَدِّقاٗ لِّمَا بَيْنَ يَدَيْهِۖ وَأَنزَلَ اَ۬لتَّوْرَيٰةَ وَالْإِنجِيلَ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (295,3,'50',3,'Āl-‘Imrān','آل عِمران',5,5,3,'مِن قَبْلُ هُدىٗ لِّلنَّاسِ وَأَنزَلَ اَ۬لْفُرْقَانَۖ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (296,3,'50',3,'Āl-‘Imrān','آل عِمران',5,6,4,'إِنَّ اَ۬لذِينَ كَفَرُواْ بِـَٔايَٰتِ اِ۬للَّهِ لَهُمْ عَذَابٞ شَدِيدٞۖ وَاللَّهُ عَزِيزٞ ذُو اُ۪نتِقَامٍۖ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (297,3,'50',3,'Āl-‘Imrān','آل عِمران',6,7,5,'۞ إِنَّ اَ۬للَّهَ لَا يَخْفَىٰ عَلَيْهِ شَےْءٞ فِے اِ۬لْأَرْضِ وَلَا فِے اِ۬لسَّمَآءِۖ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (298,3,'50',3,'Āl-‘Imrān','آل عِمران',7,8,6,'هُوَ اَ۬لذِے يُصَوِّرُكُمْ فِے اِ۬لْأَرْحَامِ كَيْفَ يَشَآءُۖ لَا إِلَٰهَ إِلَّا هُوَۖ اَ۬لْعَزِيزُ اُ۬لْحَكِيمُۖ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (299,3,'50',3,'Āl-‘Imrān','آل عِمران',9,13,7,'هُوَ اَ۬لذِے أَنزَلَ عَلَيْكَ اَ۬لْكِتَٰبَ مِنْهُۖ ءَايَٰتٞ مُّحْكَمَٰتٌ هُنَّ أُمُّ اُ۬لْكِتَٰبِ وَأُخَرُ مُتَشَٰبِهَٰتٞۖ فَأَمَّا اَ۬لذِينَ فِے قُلُوبِهِمْ زَيْغٞ فَيَتَّبِعُونَ مَا تَشَٰبَهَ مِنْهُ اُ۪بْتِغَآءَ اَ۬لْفِتْنَةِ وَابْتِغَآءَ تَأْوِيلِهِۦۖ وَمَا يَعْلَمُ تَأْوِيلَهُۥ إِلَّا اَ۬للَّهُۖ وَالرَّٰسِخُونَ فِے اِ۬لْعِلْمِ يَقُولُونَ ءَامَنَّا بِهِۦ كُلّٞ مِّنْ عِندِ رَبِّنَاۖ وَمَا يَذَّكَّرُ إِلَّا أُوْلُواْ اُ۬لْأَلْبَٰبِۖ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (300,3,'50',3,'Āl-‘Imrān','آل عِمران',13,14,8,'رَبَّنَا لَا تُزِغْ قُلُوبَنَا بَعْدَ إِذْ هَدَيْتَنَا وَهَبْ لَنَا مِن لَّدُنكَ رَحْمَةًۖ إِنَّكَ أَنتَ اَ۬لْوَهَّابُۖ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (301,3,'50',3,'Āl-‘Imrān','آل عِمران',14,15,9,'رَبَّنَا إِنَّكَ جَامِعُ اُ۬لنَّاسِ لِيَوْمٖ لَّا رَيْبَ فِيهِۖ إِنَّ اَ۬للَّهَ لَا يُخْلِفُ اُ۬لْمِيعَادَۖ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (302,3,'51',3,'Āl-‘Imrān','آل عِمران',1,2,10,'إِنَّ اَ۬لذِينَ كَفَرُواْ لَن تُغْنِيَ عَنْهُمْ أَمْوَٰلُهُمْ وَلَا أَوْلَٰدُهُم مِّنَ اَ۬للَّهِ شَئْاٗۖ وَأُوْلَٰٓئِكَ هُمْ وَقُودُ اُ۬لنَّارِ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (303,3,'51',3,'Āl-‘Imrān','آل عِمران',2,4,11,'كَدَأْبِ ءَالِ فِرْعَوْنَۖ وَالذِينَ مِن قَبْلِهِمْ كَذَّبُواْ بِـَٔايَٰتِنَا فَأَخَذَهُمُ اُ۬للَّهُ بِذُنُوبِهِمْۖ وَاللَّهُ شَدِيدُ اُ۬لْعِقَابِۖ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (304,3,'51',3,'Āl-‘Imrān','آل عِمران',4,5,12,'قُل لِّلذِينَ كَفَرُواْ سَتُغْلَبُونَ وَتُحْشَرُونَ إِلَىٰ جَهَنَّمَۖ وَبِئْسَ اَ۬لْمِهَادُۖ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (305,3,'51',3,'Āl-‘Imrān','آل عِمران',6,9,13,'قَدْ كَانَ لَكُمْ ءَايَةٞ فِے فِئَتَيْنِ اِ۪لْتَقَتَاۖ فِئَةٞ تُقَٰتِلُ فِے سَبِيلِ اِ۬للَّهِ وَأُخْرَىٰ كَافِرَةٞ تَرَوْنَهُم مِّثْلَيْهِمْ رَأْيَ اَ۬لْعَيْنِۖ وَاللَّهُ يُؤَيِّدُ بِنَصْرِهِۦ مَنْ يَّشَآءُۖ اِ۪نَّ فِے ذَٰلِكَ لَعِبْرَةٗ لِّأُوْلِے اِ۬لْأَبْصَٰرِۖ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (306,3,'51',3,'Āl-‘Imrān','آل عِمران',9,12,14,'زُيِّنَ لِلنَّاسِ حُبُّ اُ۬لشَّهَوَٰتِ مِنَ اَ۬لنِّسَآءِ وَالْبَنِينَ وَالْقَنَٰطِيرِ اِ۬لْمُقَنطَرَةِ مِنَ اَ۬لذَّهَبِ وَالْفِضَّةِ وَالْخَيْلِ اِ۬لْمُسَوَّمَةِ وَالْأَنْعَٰمِ وَالْحَرْثِۖ ذَٰلِكَ مَتَٰعُ اُ۬لْحَيَوٰةِ اِ۬لدُّنْيَاۖ وَاللَّهُ عِندَهُۥ حُسْنُ اُ۬لْمَـَٔابِۖ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (307,3,'51',3,'Āl-‘Imrān','آل عِمران',12,15,15,'۞ قُلْ أَٰوْ۟نَبِّئُكُم بِخَيْرٖ مِّن ذَٰلِكُمْۖ لِلذِينَ اَ۪تَّقَوْاْ عِندَ رَبِّهِمْ جَنَّٰتٞ تَجْرِے مِن تَحْتِهَا اَ۬لْأَنْهَٰرُ خَٰلِدِينَ فِيهَا وَأَزْوَٰجٞ مُّطَهَّرَةٞ وَرِضْوَٰنٞ مِّنَ اَ۬للَّهِۖ وَاللَّهُ بَصِيرُۢ بِالْعِبَادِۖ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (308,3,'52',3,'Āl-‘Imrān','آل عِمران',1,2,16,'اِ۬لذِينَ يَقُولُونَ رَبَّنَا إِنَّنَا ءَامَنَّا فَاغْفِرْ لَنَا ذُنُوبَنَا وَقِنَا عَذَابَ اَ۬لنَّارِۖ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (309,3,'52',3,'Āl-‘Imrān','آل عِمران',2,3,17,'اِ۬لصَّٰبِرِينَ وَالصَّٰدِقِينَ وَالْقَٰنِتِينَ وَالْمُنفِقِينَ وَالْمُسْتَغْفِرِينَ بِالْأَسْحَارِۖ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (310,3,'52',3,'Āl-‘Imrān','آل عِمران',3,5,18,'شَهِدَ اَ۬للَّهُ أَنَّهُۥ لَا إِلَٰهَ إِلَّا هُوَ وَالْمَلَٰٓئِكَةُ وَأُوْلُواْ اُ۬لْعِلْمِ قَآئِماَۢ بِالْقِسْطِۖ لَا إِلَٰهَ إِلَّا هُوَۖ اَ۬لْعَزِيزُ اُ۬لْحَكِيمُۖ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (311,3,'52',3,'Āl-‘Imrān','آل عِمران',5,8,19,'إِنَّ اَ۬لدِّينَ عِندَ اَ۬للَّهِ اِ۬لْإِسْلَٰمُۖ وَمَا اَ۪خْتَلَفَ اَ۬لذِينَ أُوتُواْ اُ۬لْكِتَٰبَ إِلَّا مِنۢ بَعْدِ مَا جَآءَهُمُ اُ۬لْعِلْمُۖ بَغْياَۢ بَيْنَهُمْۖ وَمَنْ يَّكْفُرْ بِـَٔايَٰتِ اِ۬للَّهِ فَإِنَّ اَ۬للَّهَ سَرِيعُ اُ۬لْحِسَابِۖ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (312,3,'52',3,'Āl-‘Imrān','آل عِمران',8,11,20,'فَإِنْ حَآجُّوكَ فَقُلْ أَسْلَمْتُ وَجْهِيَ لِلهِ وَمَنِ اِ۪تَّبَعَنِۦۖ وَقُل لِّلذِينَ أُوتُواْ اُ۬لْكِتَٰبَ وَالْأُمِّيِّۧنَ ءَٰا۬سْلَمْتُمْۖ فَإِنْ أَسْلَمُواْ فَقَدِ اِ۪هْتَدَواْۖ وَّإِن تَوَلَّوْاْ فَإِنَّمَا عَلَيْكَ اَ۬لْبَلَٰغُۖ وَاللَّهُ بَصِيرُۢ بِالْعِبَادِۖ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (313,3,'52',3,'Āl-‘Imrān','آل عِمران',12,14,21,'إِنَّ اَ۬لذِينَ يَكْفُرُونَ بِـَٔايَٰتِ اِ۬للَّهِ وَيَقْتُلُونَ اَ۬لنَّبِيٓـِٕۧنَ بِغَيْرِ حَقّٖ وَيَقْتُلُونَ اَ۬لذِينَ يَأْمُرُونَ بِالْقِسْطِ مِنَ اَ۬لنَّاسِ فَبَشِّرْهُم بِعَذَابٍ أَلِيمٍۖ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (314,3,'52',3,'Āl-‘Imrān','آل عِمران',14,15,22,'أُوْلَٰٓئِكَ اَ۬لذِينَ حَبِطَتْ أَعْمَٰلُهُمْ فِے اِ۬لدُّنْيَا وَالْأٓخِرَةِۖ وَمَا لَهُم مِّن نَّٰصِرِينَۖ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (315,3,'53',3,'Āl-‘Imrān','آل عِمران',1,2,23,'۞ أَلَمْ تَرَ إِلَى اَ۬لذِينَ أُوتُواْ نَصِيباٗ مِّنَ اَ۬لْكِتَٰبِ يُدْعَوْنَ إِلَىٰ كِتَٰبِ اِ۬للَّهِ لِيَحْكُمَ بَيْنَهُمْ ثُمَّ يَتَوَلَّىٰ فَرِيقٞ مِّنْهُمْ وَهُم مُّعْرِضُونَۖ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (316,3,'53',3,'Āl-‘Imrān','آل عِمران',3,4,24,'ذَٰلِكَ بِأَنَّهُمْ قَالُواْ لَن تَمَسَّنَا اَ۬لنَّارُ إِلَّا أَيَّاماٗ مَّعْدُودَٰتٖ وَغَرَّهُمْ فِے دِينِهِم مَّا كَانُواْ يَفْتَرُونَۖ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (317,3,'53',3,'Āl-‘Imrān','آل عِمران',4,6,25,'فَكَيْفَ إِذَا جَمَعْنَٰهُمْ لِيَوْمٖ لَّا رَيْبَ فِيهِ وَوُفِّيَتْ كُلُّ نَفْسٖ مَّا كَسَبَتْ وَهُمْ لَا يُظْلَمُونَۖ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (318,3,'53',3,'Āl-‘Imrān','آل عِمران',6,8,26,'قُلِ اِ۬للَّهُمَّ مَٰلِكَ اَ۬لْمُلْكِ تُؤْتِے اِ۬لْمُلْكَ مَن تَشَآءُ وَتَنزِعُ اُ۬لْمُلْكَ مِمَّن تَشَآءُ وَتُعِزُّ مَن تَشَآءُ وَتُذِلُّ مَن تَشَآءُ بِيَدِكَ اَ۬لْخَيْرُۖ إِنَّكَ عَلَىٰ كُلِّ شَےْءٖ قَدِيرٞۖ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (319,3,'53',3,'Āl-‘Imrān','آل عِمران',8,10,27,'تُولِجُ اُ۬ليْلَ فِے اِ۬لنَّهَارِ وَتُولِجُ اُ۬لنَّهَارَ فِے اِ۬ليْلِ وَتُخْرِجُ اُ۬لْحَيَّ مِنَ اَ۬لْمَيِّتِ وَتُخْرِجُ اُ۬لْمَيِّتَ مِنَ اَ۬لْحَيِّ وَتَرْزُقُ مَن تَشَآءُ بِغَيْرِ حِسَابٖۖ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (320,3,'53',3,'Āl-‘Imrān','آل عِمران',11,13,28,'لَّا يَتَّخِذِ اِ۬لْمُؤْمِنُونَ اَ۬لْكَٰفِرِينَ أَوْلِيَآءَ مِن دُونِ اِ۬لْمُؤْمِنِينَۖ وَمَنْ يَّفْعَلْ ذَٰلِكَ فَلَيْسَ مِنَ اَ۬للَّهِ فِے شَےْءٍ إِلَّا أَن تَتَّقُواْ مِنْهُمْ تُقَيٰةٗۖ وَيُحَذِّرُكُمُ اُ۬للَّهُ نَفْسَهُۥۖ وَإِلَى اَ۬للَّهِ اِ۬لْمَصِيرُۖ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (321,3,'53',3,'Āl-‘Imrān','آل عِمران',13,15,29,'قُلْ إِن تُخْفُواْ مَا فِے صُدُورِكُمْ أَوْ تُبْدُوهُ يَعْلَمْهُ اُ۬للَّهُۖ وَيَعْلَمُ مَا فِے اِ۬لسَّمَٰوَٰتِ وَمَا فِے اِ۬لْأَرْضِۖ وَاللَّهُ عَلَىٰ كُلِّ شَےْءٖ قَدِيرٞۖ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (322,3,'54',3,'Āl-‘Imrān','آل عِمران',1,3,30,'يَوْمَ تَجِدُ كُلُّ نَفْسٖ مَّا عَمِلَتْ مِنْ خَيْرٖ مُّحْضَراٗۖ وَمَا عَمِلَتْ مِن سُوٓءٖ تَوَدُّ لَوْ أَنَّ بَيْنَهَا وَبَيْنَهُۥ أَمَداَۢ بَعِيداٗۖ وَيُحَذِّرُكُمُ اُ۬للَّهُ نَفْسَهُۥۖ وَاللَّهُ رَءُوفُۢ بِالْعِبَادِۖ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (323,3,'54',3,'Āl-‘Imrān','آل عِمران',3,5,31,'قُلْ إِن كُنتُمْ تُحِبُّونَ اَ۬للَّهَ فَاتَّبِعُونِے يُحْبِبْكُمُ اُ۬للَّهُ وَيَغْفِرْ لَكُمْ ذُنُوبَكُمْۖ وَاللَّهُ غَفُورٞ رَّحِيمٞۖ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (324,3,'54',3,'Āl-‘Imrān','آل عِمران',5,6,32,'قُلْ أَطِيعُواْ اُ۬للَّهَ وَالرَّسُولَۖ فَإِن تَوَلَّوْاْ فَإِنَّ اَ۬للَّهَ لَا يُحِبُّ اُ۬لْكَٰفِرِينَۖ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (325,3,'54',3,'Āl-‘Imrān','آل عِمران',6,7,33,'۞ إِنَّ اَ۬للَّهَ اَ۪صْطَفَىٰ ءَادَمَ وَنُوحاٗ وَءَالَ إِبْرَٰهِيمَ وَءَالَ عِمْرَٰنَ عَلَى اَ۬لْعَٰلَمِينَ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (326,3,'54',3,'Āl-‘Imrān','آل عِمران',7,8,34,'ذُرِّيَّةَۢ بَعْضُهَا مِنۢ بَعْضٖۖ وَاللَّهُ سَمِيعٌ عَلِيمٌۖ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (327,3,'54',3,'Āl-‘Imrān','آل عِمران',8,9,35,'إِذْ قَالَتِ اِ۪مْرَأَتُ عِمْرَٰنَ رَبِّ إِنِّے نَذَرْتُ لَكَ مَا فِے بَطْنِے مُحَرَّراٗ فَتَقَبَّلْ مِنِّيَۖ إِنَّكَ أَنتَ اَ۬لسَّمِيعُ اُ۬لْعَلِيمُۖ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (328,3,'54',3,'Āl-‘Imrān','آل عِمران',10,12,36,'فَلَمَّا وَضَعَتْهَا قَالَتْ رَبِّ إِنِّے وَضَعْتُهَا أُنثَىٰ وَاللَّهُ أَعْلَمُ بِمَا وَضَعَتْ وَلَيْسَ اَ۬لذَّكَرُ كَالْأُنثَىٰۖ وَإِنِّے سَمَّيْتُهَا مَرْيَمَ وَإِنِّيَ أُعِيذُهَا بِكَ وَذُرِّيَّتَهَا مِنَ اَ۬لشَّيْطَٰنِ اِ۬لرَّجِيمِۖ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (329,3,'54',3,'Āl-‘Imrān','آل عِمران',12,15,37,'فَتَقَبَّلَهَا رَبُّهَا بِقَبُولٍ حَسَنٖ وَأَنۢبَتَهَا نَبَاتاً حَسَناٗۖ وَكَفَلَهَا زَكَرِيَّآءُۖ كُلَّمَا دَخَلَ عَلَيْهَا زَكَرِيَّآءُ اُ۬لْمِحْرَابَ وَجَدَ عِندَهَا رِزْقاٗۖ قَالَ يَٰمَرْيَمُ أَنَّىٰ لَكِ هَٰذَاۖ قَالَتْ هُوَ مِنْ عِندِ اِ۬للَّهِۖ إِنَّ اَ۬للَّهَ يَرْزُقُ مَنْ يَّشَآءُ بِغَيْرِ حِسَابٍۖ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (330,3,'55',3,'Āl-‘Imrān','آل عِمران',1,2,38,'هُنَالِكَ دَعَا زَكَرِيَّآءُ رَبَّهُۥۖ قَالَ رَبِّ هَبْ لِے مِن لَّدُنكَ ذُرِّيَّةٗ طَيِّبَةً إِنَّكَ سَمِيعُ اُ۬لدُّعَآءِۖ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (331,3,'55',3,'Āl-‘Imrān','آل عِمران',2,4,39,'فَنَادَتْهُ اُ۬لْمَلَٰٓئِكَةُ وَهْوَ قَآئِمٞ يُصَلِّے فِے اِ۬لْمِحْرَابِ أَنَّ اَ۬للَّهَ يُبَشِّرُكَ بِيَحْيَىٰ مُصَدِّقاَۢ بِكَلِمَةٖ مِّنَ اَ۬للَّهِ وَسَيِّداٗ وَحَصُوراٗ وَنَبِيٓـٔاٗ مِّنَ اَ۬لصَّٰلِحِينَۖ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (332,3,'55',3,'Āl-‘Imrān','آل عِمران',4,6,40,'قَالَ رَبِّ أَنَّىٰ يَكُونُ لِے غُلَٰمٞ وَقَدْ بَلَغَنِيَ اَ۬لْكِبَرُ وَامْرَأَتِے عَاقِرٞۖ قَالَ كَذَٰلِكَۖ اَ۬للَّهُ يَفْعَلُ مَا يَشَآءُۖ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (333,3,'55',3,'Āl-‘Imrān','آل عِمران',6,8,41,'قَالَ رَبِّ اِ۪جْعَل لِّيَ ءَايَةٗۖ قَالَ ءَايَتُكَ أَلَّا تُكَلِّمَ اَ۬لنَّاسَ ثَلَٰثَةَ أَيَّامٍ إِلَّا رَمْزاٗۖ وَاذْكُر رَّبَّكَ كَثِيراٗ وَسَبِّحْ بِالْعَشِيِّ وَالْإِبْكَٰرِۖ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (334,3,'55',3,'Āl-‘Imrān','آل عِمران',8,10,42,'۞ وَإِذْ قَالَتِ اِ۬لْمَلَٰٓئِكَةُ يَٰمَرْيَمُ إِنَّ اَ۬للَّهَ اَ۪صْطَفَيٰكِ وَطَهَّرَكِ وَاصْطَفَيٰكِ عَلَىٰ نِسَآءِ اِ۬لْعَٰلَمِينَۖ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (335,3,'55',3,'Āl-‘Imrān','آل عِمران',10,11,43,'يَٰمَرْيَمُ اُ۟قْنُتِے لِرَبِّكِ وَاسْجُدِے وَارْكَعِے مَعَ اَ۬لرَّٰكِعِينَۖ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (336,3,'55',3,'Āl-‘Imrān','آل عِمران',11,13,44,'ذَٰلِكَ مِنْ أَنۢبَآءِ اِ۬لْغَيْبِ نُوحِيهِ إِلَيْكَۖ وَمَا كُنتَ لَدَيْهِمْ إِذْ يُلْقُونَ أَقْلَٰمَهُمْ أَيُّهُمْ يَكْفُلُ مَرْيَمَۖ وَمَا كُنتَ لَدَيْهِمْ إِذْ يَخْتَصِمُونَ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (337,3,'55',3,'Āl-‘Imrān','آل عِمران',13,15,45,'إِذْ قَالَتِ اِ۬لْمَلَٰٓئِكَةُ يَٰمَرْيَمُ إِنَّ اَ۬للَّهَ يُبَشِّرُكِ بِكَلِمَةٖ مِّنْهُۖ اُ۪سْمُهُ اُ۬لْمَسِيحُ عِيسَى اَ۪بْنُ مَرْيَمَ وَجِيهاٗ فِے اِ۬لدُّنْيَا وَالْأٓخِرَةِ وَمِنَ اَ۬لْمُقَرَّبِينَۖ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (338,3,'56',3,'Āl-‘Imrān','آل عِمران',1,1,46,'وَيُكَلِّمُ اُ۬لنَّاسَ فِے اِ۬لْمَهْدِ وَكَهْلاٗۖ وَمِنَ اَ۬لصَّٰلِحِينَۖ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (339,3,'56',3,'Āl-‘Imrān','آل عِمران',2,3,47,'قَالَتْ رَبِّ أَنَّىٰ يَكُونُ لِے وَلَدٞ وَلَمْ يَمْسَسْنِے بَشَرٞۖ قَالَ كَذَٰلِكِۖ اِ۬للَّهُ يَخْلُقُ مَا يَشَآءُۖ اِ۪ذَا قَضَىٰ أَمْراٗ فَإِنَّمَا يَقُولُ لَهُۥ كُنۖ فَيَكُونُۖ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (340,3,'56',3,'Āl-‘Imrān','آل عِمران',4,9,48,'وَيُعَلِّمُهُ اُ۬لْكِتَٰبَ وَالْحِكْمَةَ وَالتَّوْرَيٰةَ وَالْإِنجِيلَ وَرَسُولاً إِلَىٰ بَنِے إِسْرَآءِيلَ أَنِّے قَدْ جِئْتُكُم بِـَٔايَةٖ مِّن رَّبِّكُمْۖ إِنِّيَ أَخْلُقُ لَكُم مِّنَ اَ۬لطِّينِ كَهَيْـَٔةِ اِ۬لطَّيْرِ فَأَنفُخُ فِيهِ فَيَكُونُ طَٰٓئِراَۢ بِإِذْنِ اِ۬للَّهِۖ وَأُبْرِۓُ اُ۬لْأَكْمَهَ وَالْأَبْرَصَ وَأُحْيِ اِ۬لْمَوْتَىٰ بِإِذْنِ اِ۬للَّهِۖ وَأُنَبِّئُكُم بِمَا تَأْكُلُونَ وَمَا تَدَّخِرُونَ فِے بِيُوتِكُمْۖ إِنَّ فِے ذَٰلِكَ لَأٓيَةٗ لَّكُمْ إِن كُنتُم مُّؤْمِنِينَۖ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (341,3,'56',3,'Āl-‘Imrān','آل عِمران',10,12,49,'وَمُصَدِّقاٗ لِّمَا بَيْنَ يَدَيَّ مِنَ اَ۬لتَّوْرَيٰةِ وَلِأُحِلَّ لَكُم بَعْضَ اَ۬لذِے حُرِّمَ عَلَيْكُمْۖ وَجِئْتُكُم بِـَٔايَةٖ مِّن رَّبِّكُمْۖ فَاتَّقُواْ اُ۬للَّهَ وَأَطِيعُونِۖ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (342,3,'56',3,'Āl-‘Imrān','آل عِمران',12,13,50,'إِنَّ اَ۬للَّهَ رَبِّے وَرَبُّكُمْ فَاعْبُدُوهُۖ هَٰذَا صِرَٰطٞ مُّسْتَقِيمٞۖ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (343,3,'56',3,'Āl-‘Imrān','آل عِمران',13,15,51,'۞ فَلَمَّا أَحَسَّ عِيسَىٰ مِنْهُمُ اُ۬لْكُفْرَ قَالَ مَنْ أَنصَارِيَ إِلَى اَ۬للَّهِۖ قَالَ اَ۬لْحَوَارِيُّونَ نَحْنُ أَنصَارُ اُ۬للَّهِۖ ءَامَنَّا بِاللَّهِ وَاشْهَدْ بِأَنَّا مُسْلِمُونَۖ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (344,3,'57',3,'Āl-‘Imrān','آل عِمران',1,2,52,'رَبَّنَا ءَامَنَّا بِمَا أَنزَلْتَ وَاتَّبَعْنَا اَ۬لرَّسُولَ فَاكْتُبْنَا مَعَ اَ۬لشَّٰهِدِينَۖ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (345,3,'57',3,'Āl-‘Imrān','آل عِمران',2,2,53,'وَمَكَرُواْ وَمَكَرَ اَ۬للَّهُۖ وَاللَّهُ خَيْرُ اُ۬لْمَٰكِرِينَۖ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (346,3,'57',3,'Āl-‘Imrān','آل عِمران',3,6,54,'إِذْ قَالَ اَ۬للَّهُ يَٰعِيسَىٰ إِنِّے مُتَوَفِّيكَ وَرَافِعُكَ إِلَيَّ وَمُطَهِّرُكَ مِنَ اَ۬لذِينَ كَفَرُواْۖ وَجَاعِلُ اُ۬لذِينَ اَ۪تَّبَعُوكَ فَوْقَ اَ۬لذِينَ كَفَرُواْ إِلَىٰ يَوْمِ اِ۬لْقِيَٰمَةِۖ ثُمَّ إِلَيَّ مَرْجِعُكُمْ فَأَحْكُمُ بَيْنَكُمْ فِيمَا كُنتُمْ فِيهِ تَخْتَلِفُونَۖ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (347,3,'57',3,'Āl-‘Imrān','آل عِمران',6,8,55,'فَأَمَّا اَ۬لذِينَ كَفَرُواْ فَأُعَذِّبُهُمْ عَذَاباٗ شَدِيداٗ فِے اِ۬لدُّنْيَا وَالْأٓخِرَةِۖ وَمَا لَهُم مِّن نَّٰصِرِينَۖ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (348,3,'57',3,'Āl-‘Imrān','آل عِمران',8,9,56,'وَأَمَّا اَ۬لذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ فَنُوَفِّيهِمْ أُجُورَهُمْۖ وَاللَّهُ لَا يُحِبُّ اُ۬لظَّٰلِمِينَۖ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (349,3,'57',3,'Āl-‘Imrān','آل عِمران',9,10,57,'ذَٰلِكَ نَتْلُوهُ عَلَيْكَ مِنَ اَ۬لْأٓيَٰتِ وَالذِّكْرِ اِ۬لْحَكِيمِۖ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (350,3,'57',3,'Āl-‘Imrān','آل عِمران',10,12,58,'إِنَّ مَثَلَ عِيسَىٰ عِندَ اَ۬للَّهِ كَمَثَلِ ءَادَمَۖ خَلَقَهُۥ مِن تُرَابٖ ثُمَّ قَالَ لَهُۥ كُنۖ فَيَكُونُۖ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (351,3,'57',3,'Āl-‘Imrān','آل عِمران',12,12,59,'اُ۬لْحَقُّ مِن رَّبِّكَۖ فَلَا تَكُن مِّنَ اَ۬لْمُمْتَرِينَۖ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (352,3,'57',3,'Āl-‘Imrān','آل عِمران',13,15,60,'فَمَنْ حَآجَّكَ فِيهِ مِنۢ بَعْدِ مَا جَآءَكَ مِنَ اَ۬لْعِلْمِ فَقُلْ تَعَالَوْاْ نَدْعُ أَبْنَآءَنَا وَأَبْنَآءَكُمْ وَنِسَآءَنَا وَنِسَآءَكُمْ وَأَنفُسَنَا وَأَنفُسَكُمْ ثُمَّ نَبْتَهِلْ فَنَجْعَل لَّعْنَتَ اَ۬للَّهِ عَلَى اَ۬لْكَٰذِبِينَۖ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (353,3,'58',3,'Āl-‘Imrān','آل عِمران',1,2,61,'إِنَّ هَٰذَا لَهْوَ اَ۬لْقَصَصُ اُ۬لْحَقُّۖ وَمَا مِنْ إِلَٰهٍ إِلَّا اَ۬للَّهُۖ وَإِنَّ اَ۬للَّهَ لَهْوَ اَ۬لْعَزِيزُ اُ۬لْحَكِيمُۖ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (354,3,'58',3,'Āl-‘Imrān','آل عِمران',2,2,62,'فَإِن تَوَلَّوْاْ فَإِنَّ اَ۬للَّهَ عَلِيمُۢ بِالْمُفْسِدِينَۖ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (355,3,'58',3,'Āl-‘Imrān','آل عِمران',3,6,63,'۞ قُلْ يَٰأَهْلَ اَ۬لْكِتَٰبِ تَعَالَوْاْ إِلَىٰ كَلِمَةٖ سَوَآءِۢ بَيْنَنَا وَبَيْنَكُمْ أَلَّا نَعْبُدَ إِلَّا اَ۬للَّهَ وَلَا نُشْرِكَ بِهِۦ شَئْاٗ وَلَا يَتَّخِذَ بَعْضُنَا بَعْضاً أَرْبَاباٗ مِّن دُونِ اِ۬للَّهِۖ فَإِن تَوَلَّوْاْ فَقُولُواْ اُ۪شْهَدُواْ بِأَنَّا مُسْلِمُونَۖ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (356,3,'58',3,'Āl-‘Imrān','آل عِمران',6,7,64,'يَٰأَهْلَ اَ۬لْكِتَٰبِ لِمَ تُحَآجُّونَ فِے إِبْرَٰهِيمَۖ وَمَا أُنزِلَتِ اِ۬لتَّوْرَيٰةُ وَالْإِنجِيلُ إِلَّا مِنۢ بَعْدِهِۦۖ أَفَلَا تَعْقِلُونَۖ ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (357,3,'58',3,'Āl-‘Imrān','آل عِمران',8,10,65,'هَٰا۬نتُمْ هَٰؤُلَآءِ حَٰجَجْتُمْ فِيمَا لَكُم بِهِۦ عِلْمٞ فَلِمَ تُحَآجُّونَ فِيمَا لَيْسَ لَكُم بِهِۦ عِلْمٞۖ وَاللَّهُ يَعْلَمُۖ وَأَنتُمْ لَا تَعْلَمُونَۖ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (358,3,'58',3,'Āl-‘Imrān','آل عِمران',10,11,66,'مَا كَانَ إِبْرَٰهِيمُ يَهُودِيّاٗ وَلَا نَصْرَانِيّاٗ وَلَٰكِن كَانَ حَنِيفاٗ مُّسْلِماٗۖ وَمَا كَانَ مِنَ اَ۬لْمُشْرِكِينَۖ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (359,3,'58',3,'Āl-‘Imrān','آل عِمران',12,13,67,'إِنَّ أَوْلَى اَ۬لنَّاسِ بِإِبْرَٰهِيمَ لَلذِينَ اَ۪تَّبَعُوهُ وَهَٰذَا اَ۬لنَّبِےٓءُ وَالذِينَ ءَامَنُواْۖ وَاللَّهُ وَلِيُّ اُ۬لْمُؤْمِنِينَۖ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (360,3,'58',3,'Āl-‘Imrān','آل عِمران',13,14,68,'وَدَّت طَّآئِفَةٞ مِّنْ أَهْلِ اِ۬لْكِتَٰبِ لَوْ يُضِلُّونَكُمْۖ وَمَا يُضِلُّونَ إِلَّا أَنفُسَهُمْ وَمَا يَشْعُرُونَۖ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (361,3,'58',3,'Āl-‘Imrān','آل عِمران',14,15,69,'يَٰأَهْلَ اَ۬لْكِتَٰبِ لِمَ تَكْفُرُونَ بِـَٔايَٰتِ اِ۬للَّهِ وَأَنتُمْ تَشْهَدُونَۖ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (362,3,'59',3,'Āl-‘Imrān','آل عِمران',1,2,70,'يَٰأَهْلَ اَ۬لْكِتَٰبِ لِمَ تَلْبِسُونَ اَ۬لْحَقَّ بِالْبَٰطِلِ وَتَكْتُمُونَ اَ۬لْحَقَّ وَأَنتُمْ تَعْلَمُونَۖ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (363,3,'59',3,'Āl-‘Imrān','آل عِمران',2,4,71,'وَقَالَت طَّآئِفَةٞ مِّنْ أَهْلِ اِ۬لْكِتَٰبِ ءَامِنُواْ بِالذِے أُنزِلَ عَلَى اَ۬لذِينَ ءَامَنُواْ وَجْهَ اَ۬لنَّهَارِ وَاكْفُرُواْ ءَاخِرَهُۥ لَعَلَّهُمْ يَرْجِعُونَۖ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (364,3,'59',3,'Āl-‘Imrān','آل عِمران',4,7,72,'وَلَا تُؤْمِنُواْ إِلَّا لِمَن تَبِعَ دِينَكُمْۖ قُلْ إِنَّ اَ۬لْهُدَىٰ هُدَى اَ۬للَّهِ أَنْ يُّؤْتَىٰ أَحَدٞ مِّثْلَ مَا أُوتِيتُمْ أَوْ يُحَآجُّوكُمْ عِندَ رَبِّكُمْۖ قُلْ إِنَّ اَ۬لْفَضْلَ بِيَدِ اِ۬للَّهِۖ يُؤْتِيهِ مَنْ يَّشَآءُۖ وَاللَّهُ وَٰسِعٌ عَلِيمٞۖ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (365,3,'59',3,'Āl-‘Imrān','آل عِمران',7,8,73,'يَخْتَصُّ بِرَحْمَتِهِۦ مَنْ يَّشَآءُۖ وَاللَّهُ ذُو اُ۬لْفَضْلِ اِ۬لْعَظِيمِۖ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (366,3,'59',3,'Āl-‘Imrān','آل عِمران',8,11,74,'۞ وَمِنْ أَهْلِ اِ۬لْكِتَٰبِ مَنْ إِن تَأْمَنْهُ بِقِنطَارٖ يُؤَدِّهِ إِلَيْكَۖ وَمِنْهُم مَّنْ إِن تَأْمَنْهُ بِدِينَارٖ لَّا يُؤَدِّهِ إِلَيْكَ إِلَّا مَا دُمْتَ عَلَيْهِ قَآئِماٗۖ ذَٰلِكَ بِأَنَّهُمْ قَالُواْ لَيْسَ عَلَيْنَا فِے اِ۬لْأُمِّيِّۧنَ سَبِيلٞۖ وَيَقُولُونَ عَلَى اَ۬للَّهِ اِ۬لْكَذِبَ وَهُمْ يَعْلَمُونَۖ ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (367,3,'59',3,'Āl-‘Imrān','آل عِمران',12,12,75,'بَلَىٰ مَنْ أَوْفَىٰ بِعَهْدِهِۦ وَاتَّقَىٰ فَإِنَّ اَ۬للَّهَ يُحِبُّ اُ۬لْمُتَّقِينَۖ ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (368,3,'59',3,'Āl-‘Imrān','آل عِمران',13,15,76,'إِنَّ اَ۬لذِينَ يَشْتَرُونَ بِعَهْدِ اِ۬للَّهِ وَأَيْمَٰنِهِمْ ثَمَناٗ قَلِيلاً أُوْلَٰٓئِكَ لَا خَلَٰقَ لَهُمْ فِے اِ۬لْأٓخِرَةِ وَلَا يُكَلِّمُهُمُ اُ۬للَّهُ وَلَا يَنظُرُ إِلَيْهِمْ يَوْمَ اَ۬لْقِيَٰمَةِ وَلَا يُزَكِّيهِمْۖ وَلَهُمْ عَذَابٌ أَلِيمٞۖ ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (369,3,'60',3,'Āl-‘Imrān','آل عِمران',1,4,77,'وَإِنَّ مِنْهُمْ لَفَرِيقاٗ يَلْوُۥنَ أَلْسِنَتَهُم بِالْكِتَٰبِ لِتَحْسِبُوهُ مِنَ اَ۬لْكِتَٰبِ وَمَا هُوَ مِنَ اَ۬لْكِتَٰبِۖ وَيَقُولُونَ هُوَ مِنْ عِندِ اِ۬للَّهِۖ وَمَا هُوَ مِنْ عِندِ اِ۬للَّهِۖ وَيَقُولُونَ عَلَى اَ۬للَّهِ اِ۬لْكَذِبَ وَهُمْ يَعْلَمُونَۖ ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (370,3,'60',3,'Āl-‘Imrān','آل عِمران',4,7,78,'مَا كَانَ لِبَشَرٍ أَنْ يُّؤْتِيَهُ اُ۬للَّهُ اُ۬لْكِتَٰبَ وَالْحُكْمَ وَالنُّبُوٓءَةَ ثُمَّ يَقُولَ لِلنَّاسِ كُونُواْ عِبَاداٗ لِّے مِن دُونِ اِ۬للَّهِ وَلَٰكِن كُونُواْ رَبَّٰنِيِّۧنَ بِمَا كُنتُمْ تَعْلَمُونَ اَ۬لْكِتَٰبَ وَبِمَا كُنتُمْ تَدْرُسُونَۖ ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (371,3,'60',3,'Āl-‘Imrān','آل عِمران',7,9,79,'وَلَا يَأْمُرُكُمْ أَن تَتَّخِذُواْ اُ۬لْمَلَٰٓئِكَةَ وَالنَّبِيٓـِٕۧنَ أَرْبَاباًۖ أَيَأْمُرُكُم بِالْكُفْرِ بَعْدَ إِذْ أَنتُم مُّسْلِمُونَۖ ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (372,3,'60',3,'Āl-‘Imrān','آل عِمران',9,13,80,'وَإِذْ أَخَذَ اَ۬للَّهُ مِيثَٰقَ اَ۬لنَّبِيٓـِٕۧنَ لَمَا ءَاتَيْنَٰكُم مِّن كِتَٰبٖ وَحِكْمَةٖ ثُمَّ جَآءَكُمْ رَسُولٞ مُّصَدِّقٞ لِّمَا مَعَكُمْ لَتُؤْمِنُنَّ بِهِۦ وَلَتَنصُرُنَّهُۥۖ ۞ قَالَ ءَٰا۬قْرَرْتُمْ وَأَخَذتُّمْ عَلَىٰ ذَٰلِكُمْ إِصْرِےۖ قَالُواْ أَقْرَرْنَاۖ قَالَ فَاشْهَدُواْ وَأَنَا مَعَكُم مِّنَ اَ۬لشَّٰهِدِينَۖ ٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (373,3,'60',3,'Āl-‘Imrān','آل عِمران',13,14,81,'فَمَن تَوَلَّىٰ بَعْدَ ذَٰلِكَ فَأُوْلَٰٓئِكَ هُمُ اُ۬لْفَٰسِقُونَۖ ٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (374,3,'60',3,'Āl-‘Imrān','آل عِمران',14,15,82,'أَفَغَيْرَ دِينِ اِ۬للَّهِ تَبْغُونَ وَلَهُۥ أَسْلَمَ مَن فِے اِ۬لسَّمَٰوَٰتِ وَالْأَرْضِ طَوْعاٗ وَكَرْهاٗۖ وَإِلَيْهِ تُرْجَعُونَۖ ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (375,3,'61',3,'Āl-‘Imrān','آل عِمران',1,4,83,'قُلْ ءَامَنَّا بِاللَّهِ وَمَا أُنزِلَ عَلَيْنَا وَمَا أُنزِلَ عَلَىٰ إِبْرَٰهِيمَ وَإِسْمَٰعِيلَ وَإِسْحَٰقَ وَيَعْقُوبَ وَالْأَسْبَاطِ وَمَا أُوتِيَ مُوسَىٰ وَعِيسَىٰ وَالنَّبِيٓـُٔونَ مِن رَّبِّهِمْ لَا نُفَرِّقُ بَيْنَ أَحَدٖ مِّنْهُمْ وَنَحْنُ لَهُۥ مُسْلِمُونَۖ ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (376,3,'61',3,'Āl-‘Imrān','آل عِمران',4,5,84,'وَمَنْ يَّبْتَغِ غَيْرَ اَ۬لْإِسْلَٰمِ دِيناٗ فَلَنْ يُّقْبَلَ مِنْهُۖ وَهْوَ فِے اِ۬لْأٓخِرَةِ مِنَ اَ۬لْخَٰسِرِينَۖ ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (377,3,'61',3,'Āl-‘Imrān','آل عِمران',5,8,85,'كَيْفَ يَهْدِے اِ۬للَّهُ قَوْماٗ كَفَرُواْ بَعْدَ إِيمَٰنِهِمْ وَشَهِدُواْ أَنَّ اَ۬لرَّسُولَ حَقّٞ وَجَآءَهُمُ اُ۬لْبَيِّنَٰتُۖ وَاللَّهُ لَا يَهْدِے اِ۬لْقَوْمَ اَ۬لظَّٰلِمِينَۖ ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (378,3,'61',3,'Āl-‘Imrān','آل عِمران',8,9,86,'أُوْلَٰٓئِكَ جَزَآؤُهُمْ أَنَّ عَلَيْهِمْ لَعْنَةَ اَ۬للَّهِ وَالْمَلَٰٓئِكَةِ وَالنَّاسِ أَجْمَعِينَ ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (379,3,'61',3,'Āl-‘Imrān','آل عِمران',9,10,87,'خَٰلِدِينَ فِيهَا لَا يُخَفَّفُ عَنْهُمُ اُ۬لْعَذَابُ وَلَا هُمْ يُنظَرُونَ ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (380,3,'61',3,'Āl-‘Imrān','آل عِمران',10,11,88,'إِلَّا اَ۬لذِينَ تَابُواْ مِنۢ بَعْدِ ذَٰلِكَ وَأَصْلَحُواْ فَإِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٌۖ ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (381,3,'61',3,'Āl-‘Imrān','آل عِمران',11,13,89,'إِنَّ اَ۬لذِينَ كَفَرُواْ بَعْدَ إِيمَٰنِهِمْ ثُمَّ اَ۪زْدَادُواْ كُفْراٗ لَّن تُقْبَلَ تَوْبَتُهُمْۖ وَأُوْلَٰٓئِكَ هُمُ اُ۬لضَّآلُّونَۖ ٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (382,3,'61',3,'Āl-‘Imrān','آل عِمران',13,15,90,'إِنَّ اَ۬لذِينَ كَفَرُواْ وَمَاتُواْ وَهُمْ كُفَّارٞ فَلَنْ يُّقْبَلَ مِنْ أَحَدِهِم مِّلْءُ اُ۬لْأَرْضِ ذَهَباٗ وَلَوِ اِ۪فْتَدَىٰ بِهِۦۖ أُوْلَٰٓئِكَ لَهُمْ عَذَابٌ أَلِيمٞۖ وَمَا لَهُم مِّن نَّٰصِرِينَۖ ٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (383,4,'62',3,'Āl-‘Imrān','آل عِمران',1,1,91,'۞ لَن تَنَالُواْ اُ۬لْبِرَّ حَتَّىٰ تُنفِقُواْ مِمَّا تُحِبُّونَۖ ٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (384,4,'62',3,'Āl-‘Imrān','آل عِمران',1,2,92,'وَمَا تُنفِقُواْ مِن شَےْءٖ فَإِنَّ اَ۬للَّهَ بِهِۦ عَلِيمٞۖ ٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (385,4,'62',3,'Āl-‘Imrān','آل عِمران',2,4,93,'كُلُّ اُ۬لطَّعَامِ كَانَ حِلّاٗ لِّبَنِے إِسْرَآءِيلَ إِلَّا مَا حَرَّمَ إِسْرَآءِيلُ عَلَىٰ نَفْسِهِۦ مِن قَبْلِ أَن تُنَزَّلَ اَ۬لتَّوْرَيٰةُۖ قُلْ فَأْتُواْ بِالتَّوْرَيٰةِ فَاتْلُوهَا إِن كُنتُمْ صَٰدِقِينَۖ ٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (386,4,'62',3,'Āl-‘Imrān','آل عِمران',5,6,94,'فَمَنِ اِ۪فْتَرَىٰ عَلَى اَ۬للَّهِ اِ۬لْكَذِبَ مِنۢ بَعْدِ ذَٰلِكَ فَأُوْلَٰٓئِكَ هُمُ اُ۬لظَّٰلِمُونَۖ ٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (387,4,'62',3,'Āl-‘Imrān','آل عِمران',6,7,95,'قُلْ صَدَقَ اَ۬للَّهُۖ فَاتَّبِعُواْ مِلَّةَ إِبْرَٰهِيمَ حَنِيفاٗۖ وَمَا كَانَ مِنَ اَ۬لْمُشْرِكِينَۖ ٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (388,4,'62',3,'Āl-‘Imrān','آل عِمران',7,8,96,'إِنَّ أَوَّلَ بَيْتٖ وُضِعَ لِلنَّاسِ لَلذِے بِبَكَّةَ مُبَٰرَكاٗ وَهُدىٗ لِّلْعَٰلَمِينَ ٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (389,4,'62',3,'Āl-‘Imrān','آل عِمران',8,10,97,'فِيهِ ءَايَٰتُۢ بَيِّنَٰتٞۖ مَّقَامُ إِبْرَٰهِيمَۖ وَمَن دَخَلَهُۥ كَانَ ءَامِناٗۖ وَلِلهِ عَلَى اَ۬لنَّاسِ حَجُّ اُ۬لْبَيْتِ مَنِ اِ۪سْتَطَاعَ إِلَيْهِ سَبِيلاٗۖ وَمَن كَفَرَ فَإِنَّ اَ۬للَّهَ غَنِيٌّ عَنِ اِ۬لْعَٰلَمِينَۖ ٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (390,4,'62',3,'Āl-‘Imrān','آل عِمران',11,12,98,'قُلْ يَٰأَهْلَ اَ۬لْكِتَٰبِ لِمَ تَكْفُرُونَ بِـَٔايَٰتِ اِ۬للَّهِ وَاللَّهُ شَهِيدٌ عَلَىٰ مَا تَعْمَلُونَۖ ٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (391,4,'62',3,'Āl-‘Imrān','آل عِمران',12,14,99,'قُلْ يَٰأَهْلَ اَ۬لْكِتَٰبِ لِمَ تَصُدُّونَ عَن سَبِيلِ اِ۬للَّهِ مَنْ ءَامَنَ تَبْغُونَهَا عِوَجاٗ وَأَنتُمْ شُهَدَآءُۖ وَمَا اَ۬للَّهُ بِغَٰفِلٍ عَمَّا تَعْمَلُونَۖ ٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (392,4,'62',3,'Āl-‘Imrān','آل عِمران',14,15,100,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ إِن تُطِيعُواْ فَرِيقاٗ مِّنَ اَ۬لذِينَ أُوتُواْ اُ۬لْكِتَٰبَ يَرُدُّوكُم بَعْدَ إِيمَٰنِكُمْ كَٰفِرِينَۖ ١٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (393,4,'63',3,'Āl-‘Imrān','آل عِمران',1,2,101,'وَكَيْفَ تَكْفُرُونَ وَأَنتُمْ تُتْلَىٰ عَلَيْكُمْ ءَايَٰتُ اُ۬للَّهِ وَفِيكُمْ رَسُولُهُۥۖ وَمَنْ يَّعْتَصِم بِاللَّهِ فَقَدْ هُدِيَ إِلَىٰ صِرَٰطٖ مُّسْتَقِيمٖۖ ١٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (394,4,'63',3,'Āl-‘Imrān','آل عِمران',3,4,102,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ اُ۪تَّقُواْ اُ۬للَّهَ حَقَّ تُقَاتِهِۦۖ وَلَا تَمُوتُنَّ إِلَّا وَأَنتُم مُّسْلِمُونَۖ ١٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (395,4,'63',3,'Āl-‘Imrān','آل عِمران',4,8,103,'۞ وَاعْتَصِمُواْ بِحَبْلِ اِ۬للَّهِ جَمِيعاٗۖ وَلَا تَفَرَّقُواْۖ وَاذْكُرُواْ نِعْمَتَ اَ۬للَّهِ عَلَيْكُمْ إِذْ كُنتُمْ أَعْدَآءٗ فَأَلَّفَ بَيْنَ قُلُوبِكُمْ فَأَصْبَحْتُم بِنِعْمَتِهِۦ إِخْوَٰناٗۖ وَكُنتُمْ عَلَىٰ شَفَا حُفْرَةٖ مِّنَ اَ۬لنَّارِ فَأَنقَذَكُم مِّنْهَاۖ كَذَٰلِكَ يُبَيِّنُ اُ۬للَّهُ لَكُمْ ءَايَٰتِهِۦ لَعَلَّكُمْ تَهْتَدُونَۖ ١٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (396,4,'63',3,'Āl-‘Imrān','آل عِمران',8,9,104,'وَلْتَكُن مِّنكُمْ أُمَّةٞ يَدْعُونَ إِلَى اَ۬لْخَيْرِ وَيَأْمُرُونَ بِالْمَعْرُوفِ وَيَنْهَوْنَ عَنِ اِ۬لْمُنكَرِۖ وَأُوْلَٰٓئِكَ هُمُ اُ۬لْمُفْلِحُونَۖ ١٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (397,4,'63',3,'Āl-‘Imrān','آل عِمران',10,11,105,'وَلَا تَكُونُواْ كَالذِينَ تَفَرَّقُواْ وَاخْتَلَفُواْ مِنۢ بَعْدِ مَا جَآءَهُمُ اُ۬لْبَيِّنَٰتُۖ وَأُوْلَٰٓئِكَ لَهُمْ عَذَابٌ عَظِيمٞ ١٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (398,4,'63',3,'Āl-‘Imrān','آل عِمران',11,13,106,'يَوْمَ تَبْيَضُّ وُجُوهٞ وَتَسْوَدُّ وُجُوهٞۖ فَأَمَّا اَ۬لذِينَ اَ۪سْوَدَّتْ وُجُوهُهُمْ أَكَفَرْتُم بَعْدَ إِيمَٰنِكُمْ فَذُوقُواْ اُ۬لْعَذَابَ بِمَا كُنتُمْ تَكْفُرُونَۖ ١٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (399,4,'63',3,'Āl-‘Imrān','آل عِمران',13,14,107,'وَأَمَّا اَ۬لذِينَ اَ۪بْيَضَّتْ وُجُوهُهُمْ فَفِے رَحْمَةِ اِ۬للَّهِۖ هُمْ فِيهَا خَٰلِدُونَۖ ١٠٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (400,4,'63',3,'Āl-‘Imrān','آل عِمران',14,15,108,'تِلْكَ ءَايَٰتُ اُ۬للَّهِ نَتْلُوهَا عَلَيْكَ بِالْحَقِّۖ وَمَا اَ۬للَّهُ يُرِيدُ ظُلْماٗ لِّلْعَٰلَمِينَۖ ١٠٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (401,4,'64',3,'Āl-‘Imrān','آل عِمران',1,1,109,'وَلِلهِ مَا فِے اِ۬لسَّمَٰوَٰتِ وَمَا فِے اِ۬لْأَرْضِۖ وَإِلَى اَ۬للَّهِ تُرْجَعُ اُ۬لْأُمُورُۖ ١٠٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (402,4,'64',3,'Āl-‘Imrān','آل عِمران',2,5,110,'كُنتُمْ خَيْرَ أُمَّةٍ أُخْرِجَتْ لِلنَّاسِ تَأْمُرُونَ بِالْمَعْرُوفِ وَتَنْهَوْنَ عَنِ اِ۬لْمُنكَرِ وَتُؤْمِنُونَ بِاللَّهِۖ وَلَوْ ءَامَنَ أَهْلُ اُ۬لْكِتَٰبِ لَكَانَ خَيْراٗ لَّهُمۖ مِّنْهُمُ اُ۬لْمُؤْمِنُونَۖ وَأَكْثَرُهُمُ اُ۬لْفَٰسِقُونَۖ ١١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (403,4,'64',3,'Āl-‘Imrān','آل عِمران',5,6,111,'لَنْ يَّضُرُّوكُمْۖ إِلَّا أَذىٗۖ وَإِنْ يُّقَٰتِلُوكُمْ يُوَلُّوكُمُ اُ۬لْأَدْبَٰرَۖ ثُمَّ لَا يُنصَرُونَۖ ١١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (404,4,'64',3,'Āl-‘Imrān','آل عِمران',6,10,112,'ضُرِبَتْ عَلَيْهِمُ اُ۬لذِّلَّةُ أَيْنَ مَا ثُقِفُواْۖ إِلَّا بِحَبْلٖ مِّنَ اَ۬للَّهِ وَحَبْلٖ مِّنَ اَ۬لنَّاسِ وَبَآءُو بِغَضَبٖ مِّنَ اَ۬للَّهِۖ وَضُرِبَتْ عَلَيْهِمُ اُ۬لْمَسْكَنَةُۖ ذَٰلِكَ بِأَنَّهُمْ كَانُواْ يَكْفُرُونَ بِـَٔايَٰتِ اِ۬للَّهِ وَيَقْتُلُونَ اَ۬لْأَنۢبِئَآءَ بِغَيْرِ حَقّٖۖ ذَٰلِكَ بِمَا عَصَواْ وَّكَانُواْ يَعْتَدُونَۖ ١١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (405,4,'64',3,'Āl-‘Imrān','آل عِمران',11,12,113,'۞ لَيْسُواْ سَوَآءٗۖ مِّنْ أَهْلِ اِ۬لْكِتَٰبِ أُمَّةٞ قَآئِمَةٞ يَتْلُونَ ءَايَٰتِ اِ۬للَّهِ ءَانَآءَ اَ۬ليْلِ وَهُمْ يَسْجُدُونَۖ ١١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (406,4,'64',3,'Āl-‘Imrān','آل عِمران',12,14,114,'يُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ اِ۬لْأٓخِرِ وَيَأْمُرُونَ بِالْمَعْرُوفِ وَيَنْهَوْنَ عَنِ اِ۬لْمُنكَرِ وَيُسَٰرِعُونَ فِے اِ۬لْخَيْرَٰتِۖ وَأُوْلَٰٓئِكَ مِنَ اَ۬لصَّٰلِحِينَۖ ١١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (407,4,'64',3,'Āl-‘Imrān','آل عِمران',15,15,115,'وَمَا تَفْعَلُواْ مِنْ خَيْرٖ فَلَن تُكْفَرُوهُۖ وَاللَّهُ عَلِيمُۢ بِالْمُتَّقِينَۖ ١١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (408,4,'65',3,'Āl-‘Imrān','آل عِمران',1,2,116,'إِنَّ اَ۬لذِينَ كَفَرُواْ لَن تُغْنِيَ عَنْهُمْ أَمْوَٰلُهُمْ وَلَا أَوْلَٰدُهُم مِّنَ اَ۬للَّهِ شَئْاٗۖ وَأُوْلَٰٓئِكَ أَصْحَٰبُ اُ۬لنَّارِ هُمْ فِيهَا خَٰلِدُونَۖ ١١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (409,4,'65',3,'Āl-‘Imrān','آل عِمران',3,5,117,'مَثَلُ مَا يُنفِقُونَ فِے هَٰذِهِ اِ۬لْحَيَوٰةِ اِ۬لدُّنْيَا كَمَثَلِ رِيحٖ فِيهَا صِرٌّ أَصَابَتْ حَرْثَ قَوْمٖ ظَلَمُواْ أَنفُسَهُمْ فَأَهْلَكَتْهُۖ وَمَا ظَلَمَهُمُ اُ۬للَّهُۖ وَلَٰكِنْ أَنفُسَهُمْ يَظْلِمُونَۖ ١١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (410,4,'65',3,'Āl-‘Imrān','آل عِمران',5,8,118,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ لَا تَتَّخِذُواْ بِطَانَةٗ مِّن دُونِكُمْ لَا يَأْلُونَكُمْ خَبَالاٗ وَدُّواْ مَا عَنِتُّمْۖ قَدْ بَدَتِ اِ۬لْبَغْضَآءُ مِنْ أَفْوَٰهِهِمْ وَمَا تُخْفِے صُدُورُهُمْ أَكْبَرُۖ قَدْ بَيَّنَّا لَكُمُ اُ۬لْأٓيَٰتِ إِن كُنتُمْ تَعْقِلُونَۖ ١١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (411,4,'65',3,'Āl-‘Imrān','آل عِمران',9,12,119,'هَٰا۬نتُمْ أُوْلَآءِ تُحِبُّونَهُمْ وَلَا يُحِبُّونَكُمْ وَتُؤْمِنُونَ بِالْكِتَٰبِ كُلِّهِۦۖ وَإِذَا لَقُوكُمْ قَالُواْ ءَامَنَّاۖ وَإِذَا خَلَوْاْ عَضُّواْ عَلَيْكُمُ اُ۬لْأَنَامِلَ مِنَ اَ۬لْغَيْظِۖ قُلْ مُوتُواْ بِغَيْظِكُمْۖ إِنَّ اَ۬للَّهَ عَلِيمُۢ بِذَاتِ اِ۬لصُّدُورِۖ ١١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (412,4,'65',3,'Āl-‘Imrān','آل عِمران',12,14,120,'إِن تَمْسَسْكُمْ حَسَنَةٞ تَسُؤْهُمْۖ وَإِن تُصِبْكُمْ سَيِّئَةٞ يَفْرَحُواْ بِهَاۖ وَإِن تَصْبِرُواْ وَتَتَّقُواْ لَا يَضِرْكُمْ كَيْدُهُمْ شَئْاًۖ إِنَّ اَ۬للَّهَ بِمَا يَعْمَلُونَ مُحِيطٞۖ ١٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (413,4,'65',3,'Āl-‘Imrān','آل عِمران',14,15,121,'۞ وَإِذْ غَدَوْتَ مِنْ أَهْلِكَ تُبَوِّۓُ اُ۬لْمُؤْمِنِينَ مَقَٰعِدَ لِلْقِتَالِۖ وَاللَّهُ سَمِيعٌ عَلِيمٌۖ ١٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (414,4,'66',3,'Āl-‘Imrān','آل عِمران',1,2,122,'إِذْ هَمَّت طَّآئِفَتَٰنِ مِنكُمْ أَن تَفْشَلَا وَاللَّهُ وَلِيُّهُمَاۖ وَعَلَى اَ۬للَّهِ فَلْيَتَوَكَّلِ اِ۬لْمُؤْمِنُونَۖ ١٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (415,4,'66',3,'Āl-‘Imrān','آل عِمران',2,3,123,'وَلَقَدْ نَصَرَكُمُ اُ۬للَّهُ بِبَدْرٖ وَأَنتُمْ أَذِلَّةٞۖ فَاتَّقُواْ اُ۬للَّهَ لَعَلَّكُمْ تَشْكُرُونَۖ ١٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (416,4,'66',3,'Āl-‘Imrān','آل عِمران',3,5,124,'إِذْ تَقُولُ لِلْمُؤْمِنِينَ أَلَنْ يَّكْفِيَكُمْ أَنْ يُّمِدَّكُمْ رَبُّكُم بِثَلَٰثَةِ ءَالَٰفٖ مِّنَ اَ۬لْمَلَٰٓئِكَةِ مُنزَلِينَۖ ١٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (417,4,'66',3,'Āl-‘Imrān','آل عِمران',5,6,125,'بَلَىٰ إِن تَصْبِرُواْ وَتَتَّقُواْ وَيَأْتُوكُم مِّن فَوْرِهِمْ هَٰذَا يُمْدِدْكُمْ رَبُّكُم بِخَمْسَةِ ءَالَٰفٖ مِّنَ اَ۬لْمَلَٰٓئِكَةِ مُسَوَّمِينَۖ ١٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (418,4,'66',3,'Āl-‘Imrān','آل عِمران',7,8,126,'وَمَا جَعَلَهُ اُ۬للَّهُ إِلَّا بُشْرَىٰ لَكُمْ وَلِتَطْمَئِنَّ قُلُوبُكُم بِهِۦۖ وَمَا اَ۬لنَّصْرُ إِلَّا مِنْ عِندِ اِ۬للَّهِ اِ۬لْعَزِيزِ اِ۬لْحَكِيمِ ١٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (419,4,'66',3,'Āl-‘Imrān','آل عِمران',8,9,127,'لِيَقْطَعَ طَرَفاٗ مِّنَ اَ۬لذِينَ كَفَرُواْ أَوْ يَكْبِتَهُمْ فَيَنقَلِبُواْ خَآئِبِينَ ١٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (420,4,'66',3,'Āl-‘Imrān','آل عِمران',10,11,128,'لَيْسَ لَكَ مِنَ اَ۬لْأَمْرِ شَےْءٌ أَوْ يَتُوبَ عَلَيْهِمْ أَوْ يُعَذِّبَهُمْ فَإِنَّهُمْ ظَٰلِمُونَۖ ١٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (421,4,'66',3,'Āl-‘Imrān','آل عِمران',11,12,129,'وَلِلهِ مَا فِے اِ۬لسَّمَٰوَٰتِ وَمَا فِے اِ۬لْأَرْضِۖ يَغْفِرُ لِمَنْ يَّشَآءُ وَيُعَذِّبُ مَنْ يَّشَآءُۖ وَاللَّهُ غَفُورٞ رَّحِيمٞۖ ١٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (422,4,'66',3,'Āl-‘Imrān','آل عِمران',13,14,130,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ لَا تَأْكُلُواْ اُ۬لرِّبَوٰاْ أَضْعَٰفاٗ مُّضَٰعَفَةٗۖ وَاتَّقُواْ اُ۬للَّهَ لَعَلَّكُمْ تُفْلِحُونَۖ ١٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (423,4,'66',3,'Āl-‘Imrān','آل عِمران',14,15,131,'وَاتَّقُواْ اُ۬لنَّارَ اَ۬لتِے أُعِدَّتْ لِلْكَٰفِرِينَۖ ١٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (424,4,'66',3,'Āl-‘Imrān','آل عِمران',15,15,132,'وَأَطِيعُواْ اُ۬للَّهَ وَالرَّسُولَ لَعَلَّكُمْ تُرْحَمُونَۖ ١٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (425,4,'67',3,'Āl-‘Imrān','آل عِمران',1,2,133,'۞ سَارِعُواْ إِلَىٰ مَغْفِرَةٖ مِّن رَّبِّكُمْ وَجَنَّةٍ عَرْضُهَا اَ۬لسَّمَٰوَٰتُ وَالْأَرْضُ أُعِدَّتْ لِلْمُتَّقِينَ ١٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (426,4,'67',3,'Āl-‘Imrān','آل عِمران',2,4,134,'اَ۬لذِينَ يُنفِقُونَ فِے اِ۬لسَّرَّآءِ وَالضَّرَّآءِ وَالْكَٰظِمِينَ اَ۬لْغَيْظَ وَالْعَافِينَ عَنِ اِ۬لنَّاسِۖ وَاللَّهُ يُحِبُّ اُ۬لْمُحْسِنِينَۖ ١٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (427,4,'67',3,'Āl-‘Imrān','آل عِمران',4,7,135,'وَالذِينَ إِذَا فَعَلُواْ فَٰحِشَةً أَوْ ظَلَمُواْ أَنفُسَهُمْ ذَكَرُواْ اُ۬للَّهَ فَاسْتَغْفَرُواْ لِذُنُوبِهِمْۖ وَمَنْ يَّغْفِرُ اُ۬لذُّنُوبَ إِلَّا اَ۬للَّهُۖ وَلَمْ يُصِرُّواْ عَلَىٰ مَا فَعَلُواْ وَهُمْ يَعْلَمُونَۖ ١٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (428,4,'67',3,'Āl-‘Imrān','آل عِمران',7,9,136,'أُوْلَٰٓئِكَ جَزَآؤُهُم مَّغْفِرَةٞ مِّن رَّبِّهِمْ وَجَنَّٰتٞ تَجْرِے مِن تَحْتِهَا اَ۬لْأَنْهَٰرُ خَٰلِدِينَ فِيهَاۖ وَنِعْمَ أَجْرُ اُ۬لْعَٰمِلِينَۖ ١٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (429,4,'67',3,'Āl-‘Imrān','آل عِمران',9,10,137,'قَدْ خَلَتْ مِن قَبْلِكُمْ سُنَنٞۖ فَسِيرُواْ فِے اِ۬لْأَرْضِ فَانظُرُواْ كَيْفَ كَانَ عَٰقِبَةُ اُ۬لْمُكَذِّبِينَۖ ١٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (430,4,'67',3,'Āl-‘Imrān','آل عِمران',11,11,138,'هَٰذَا بَيَانٞ لِّلنَّاسِ وَهُدىٗ وَمَوْعِظَةٞ لِّلْمُتَّقِينَۖ ١٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (431,4,'67',3,'Āl-‘Imrān','آل عِمران',12,12,139,'وَلَا تَهِنُواْ وَلَا تَحْزَنُواْ وَأَنتُمُ اُ۬لْأَعْلَوْنَ إِن كُنتُم مُّؤْمِنِينَۖ ١٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (432,4,'67',3,'Āl-‘Imrān','آل عِمران',13,15,140,'إِنْ يَّمْسَسْكُمْ قَرْحٞ فَقَدْ مَسَّ اَ۬لْقَوْمَ قَرْحٞ مِّثْلُهُۥۖ وَتِلْكَ اَ۬لْأَيَّامُ نُدَاوِلُهَا بَيْنَ اَ۬لنَّاسِۖ وَلِيَعْلَمَ اَ۬للَّهُ اُ۬لذِينَ ءَامَنُواْ وَيَتَّخِذَ مِنكُمْ شُهَدَآءَۖ وَاللَّهُ لَا يُحِبُّ اُ۬لظَّٰلِمِينَۖ ١٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (433,4,'68',3,'Āl-‘Imrān','آل عِمران',1,1,141,'وَلِيُمَحِّصَ اَ۬للَّهُ اُ۬لذِينَ ءَامَنُواْ وَيَمْحَقَ اَ۬لْكَٰفِرِينَۖ ١٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (434,4,'68',3,'Āl-‘Imrān','آل عِمران',1,3,142,'أَمْ حَسِبْتُمْ أَن تَدْخُلُواْ اُ۬لْجَنَّةَ وَلَمَّا يَعْلَمِ اِ۬للَّهُ اُ۬لذِينَ جَٰهَدُواْ مِنكُمْ وَيَعْلَمَ اَ۬لصَّٰبِرِينَۖ ١٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (435,4,'68',3,'Āl-‘Imrān','آل عِمران',3,4,143,'وَلَقَدْ كُنتُمْ تَمَنَّوْنَ اَ۬لْمَوْتَ مِن قَبْلِ أَن تَلْقَوْهُ فَقَدْ رَأَيْتُمُوهُ وَأَنتُمْ تَنظُرُونَۖ ١٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (436,4,'68',3,'Āl-‘Imrān','آل عِمران',4,7,144,'۞ وَمَا مُحَمَّدٌ إِلَّا رَسُولٞ قَدْ خَلَتْ مِن قَبْلِهِ اِ۬لرُّسُلُۖ أَفَإِيْن مَّاتَ أَوْ قُتِلَ اَ۪نقَلَبْتُمْ عَلَىٰ أَعْقَٰبِكُمْۖ وَمَنْ يَّنقَلِبْ عَلَىٰ عَقِبَيْهِ فَلَنْ يَّضُرَّ اَ۬للَّهَ شَئْاٗۖ وَسَيَجْزِے اِ۬للَّهُ اُ۬لشَّٰكِرِينَۖ ١٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (437,4,'68',3,'Āl-‘Imrān','آل عِمران',7,10,145,'وَمَا كَانَ لِنَفْسٍ أَن تَمُوتَ إِلَّا بِإِذْنِ اِ۬للَّهِۖ كِتَٰباٗ مُّؤَجَّلاٗۖ وَمَنْ يُّرِدْ ثَوَابَ اَ۬لدُّنْيَا نُؤْتِهِ مِنْهَاۖ وَمَنْ يُّرِدْ ثَوَابَ اَ۬لْأٓخِرَةِ نُؤْتِهِ مِنْهَاۖ وَسَنَجْزِے اِ۬لشَّٰكِرِينَۖ ١٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (438,4,'68',3,'Āl-‘Imrān','آل عِمران',10,12,146,'وَكَأَيِّن مِّن نَّبِےٓءٖ قُتِلَۖ مَعَهُۥ رِبِّيُّونَ كَثِيرٞۖ فَمَا وَهَنُواْ لِمَا أَصَابَهُمْ فِے سَبِيلِ اِ۬للَّهِ وَمَا ضَعُفُواْ وَمَا اَ۪سْتَكَانُواْۖ وَاللَّهُ يُحِبُّ اُ۬لصَّٰبِرِينَۖ ١٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (439,4,'68',3,'Āl-‘Imrān','آل عِمران',12,14,147,'وَمَا كَانَ قَوْلَهُمْ إِلَّا أَن قَالُواْ رَبَّنَا اَ۪غْفِرْ لَنَا ذُنُوبَنَا وَإِسْرَافَنَا فِے أَمْرِنَا وَثَبِّتْ أَقْدَامَنَا وَانصُرْنَا عَلَى اَ۬لْقَوْمِ اِ۬لْكَٰفِرِينَۖ ١٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (440,4,'68',3,'Āl-‘Imrān','آل عِمران',14,15,148,'فَـَٔاتَيٰهُمُ اُ۬للَّهُ ثَوَابَ اَ۬لدُّنْيَا وَحُسْنَ ثَوَابِ اِ۬لْأٓخِرَةِۖ وَاللَّهُ يُحِبُّ اُ۬لْمُحْسِنِينَۖ ١٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (441,4,'69',3,'Āl-‘Imrān','آل عِمران',1,2,149,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ إِن تُطِيعُواْ اُ۬لذِينَ كَفَرُواْ يَرُدُّوكُمْ عَلَىٰ أَعْقَٰبِكُمْ فَتَنقَلِبُواْ خَٰسِرِينَۖ ١٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (442,4,'69',3,'Āl-‘Imrān','آل عِمران',3,3,150,'بَلِ اِ۬للَّهُ مَوْلَيٰكُمْۖ وَهْوَ خَيْرُ اُ۬لنَّٰصِرِينَۖ ١٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (443,4,'69',3,'Āl-‘Imrān','آل عِمران',3,6,151,'سَنُلْقِے فِے قُلُوبِ اِ۬لذِينَ كَفَرُواْ اُ۬لرُّعْبَ بِمَا أَشْرَكُواْ بِاللَّهِ مَا لَمْ يُنَزِّلْ بِهِۦ سُلْطَٰناٗۖ وَمَأْوَيٰهُمُ اُ۬لنَّارُۖ وَبِئْسَ مَثْوَى اَ۬لظَّٰلِمِينَۖ ١٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (444,4,'69',3,'Āl-‘Imrān','آل عِمران',6,11,152,'وَلَقَدْ صَدَقَكُمُ اُ۬للَّهُ وَعْدَهُۥ إِذْ تَحُسُّونَهُم بِإِذْنِهِۦۖ حَتَّىٰ إِذَا فَشِلْتُمْ وَتَنَٰزَعْتُمْ فِے اِ۬لْأَمْرِ وَعَصَيْتُم مِّنۢ بَعْدِ مَا أَرَيٰكُم مَّا تُحِبُّونَۖ مِنكُم مَّنْ يُّرِيدُ اُ۬لدُّنْيَا وَمِنكُم مَّنْ يُّرِيدُ اُ۬لْأٓخِرَةَۖ ثُمَّ صَرَفَكُمْ عَنْهُمْ لِيَبْتَلِيَكُمْۖ وَلَقَدْ عَفَا عَنكُمْۖ وَاللَّهُ ذُو فَضْلٍ عَلَى اَ۬لْمُؤْمِنِينَۖ ١٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (445,4,'69',3,'Āl-‘Imrān','آل عِمران',12,15,153,'۞ إِذْ تُصْعِدُونَ وَلَا تَلْوُۥنَ عَلَىٰ أَحَدٖ وَالرَّسُولُ يَدْعُوكُمْ فِے أُخْرَيٰكُمْ فَأَثَٰبَكُمْ غَمّاَۢ بِغَمّٖ لِّكَيْلَا تَحْزَنُواْ عَلَىٰ مَا فَاتَكُمْ وَلَا مَا أَصَٰبَكُمْۖ وَاللَّهُ خَبِيرُۢ بِمَا تَعْمَلُونَۖ ١٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (446,4,'70',3,'Āl-‘Imrān','آل عِمران',1,8,154,'ثُمَّ أَنزَلَ عَلَيْكُم مِّنۢ بَعْدِ اِ۬لْغَمِّ أَمَنَةٗ نُّعَاساٗ يَغْشَىٰ طَآئِفَةٗ مِّنكُمْۖ وَطَآئِفَةٞ قَدْ أَهَمَّتْهُمْ أَنفُسُهُمْ يَظُنُّونَ بِاللَّهِ غَيْرَ اَ۬لْحَقِّ ظَنَّ اَ۬لْجَٰهِلِيَّةِۖ يَقُولُونَ هَل لَّنَا مِنَ اَ۬لْأَمْرِ مِن شَےْءٖۖ قُلْ إِنَّ اَ۬لْأَمْرَ كُلَّهُۥ لِلهِۖ يُخْفُونَ فِے أَنفُسِهِم مَّا لَا يُبْدُونَ لَكَۖ يَقُولُونَ لَوْ كَانَ لَنَا مِنَ اَ۬لْأَمْرِ شَےْءٞ مَّا قُتِلْنَا هَٰهُنَاۖ قُل لَّوْ كُنتُمْ فِے بِيُوتِكُمْ لَبَرَزَ اَ۬لذِينَ كُتِبَ عَلَيْهِمُ اُ۬لْقَتْلُ إِلَىٰ مَضَاجِعِهِمْ وَلِيَبْتَلِيَ اَ۬للَّهُ مَا فِے صُدُورِكُمْ وَلِيُمَحِّصَ مَا فِے قُلُوبِكُمْۖ وَاللَّهُ عَلِيمُۢ بِذَاتِ اِ۬لصُّدُورِۖ ١٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (447,4,'70',3,'Āl-‘Imrān','آل عِمران',8,10,155,'إِنَّ اَ۬لذِينَ تَوَلَّوْاْ مِنكُمْ يَوْمَ اَ۪لْتَقَى اَ۬لْجَمْعَٰنِ إِنَّمَا اَ۪سْتَزَلَّهُمُ اُ۬لشَّيْطَٰنُ بِبَعْضِ مَا كَسَبُواْۖ وَلَقَدْ عَفَا اَ۬للَّهُ عَنْهُمْۖ إِنَّ اَ۬للَّهَ غَفُورٌ حَلِيمٞۖ ١٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (448,4,'70',3,'Āl-‘Imrān','آل عِمران',10,14,156,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ لَا تَكُونُواْ كَالذِينَ كَفَرُواْ وَقَالُواْ لِإِخْوَٰنِهِمْ إِذَا ضَرَبُواْ فِے اِ۬لْأَرْضِ أَوْ كَانُواْ غُزّىٗ لَّوْ كَانُواْ عِندَنَا مَا مَاتُواْ وَمَا قُتِلُواْ لِيَجْعَلَ اَ۬للَّهُ ذَٰلِكَ حَسْرَةٗ فِے قُلُوبِهِمْۖ وَاللَّهُ يُحْيِۦ وَيُمِيتُۖ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٞۖ ١٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (449,4,'70',3,'Āl-‘Imrān','آل عِمران',14,15,157,'وَلَئِن قُتِلْتُمْ فِے سَبِيلِ اِ۬للَّهِ أَوْ مِتُّمْ لَمَغْفِرَةٞ مِّنَ اَ۬للَّهِ وَرَحْمَةٌ خَيْرٞ مِّمَّا تَجْمَعُونَۖ ١٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (450,4,'71',3,'Āl-‘Imrān','آل عِمران',1,1,158,'وَلَئِن مِّتُّمْ أَوْ قُتِلْتُمْ لَإِلَى اَ۬للَّهِ تُحْشَرُونَۖ ١٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (451,4,'71',3,'Āl-‘Imrān','آل عِمران',1,4,159,'فَبِمَا رَحْمَةٖ مِّنَ اَ۬للَّهِ لِنتَ لَهُمْۖ وَلَوْ كُنتَ فَظّاً غَلِيظَ اَ۬لْقَلْبِ لَانفَضُّواْ مِنْ حَوْلِكَۖ فَاعْفُ عَنْهُمْ وَاسْتَغْفِرْ لَهُمْ وَشَاوِرْهُمْ فِے اِ۬لْأَمْرِۖ فَإِذَا عَزَمْتَ فَتَوَكَّلْ عَلَى اَ۬للَّهِۖ إِنَّ اَ۬للَّهَ يُحِبُّ اُ۬لْمُتَوَكِّلِينَۖ ١٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (452,4,'71',3,'Āl-‘Imrān','آل عِمران',4,6,160,'۞ إِنْ يَّنصُرْكُمُ اُ۬للَّهُ فَلَا غَالِبَ لَكُمْۖ وَإِنْ يَّخْذُلْكُمْ فَمَن ذَا اَ۬لذِے يَنصُرُكُم مِّنۢ بَعْدِهِۦۖ وَعَلَى اَ۬للَّهِ فَلْيَتَوَكَّلِ اِ۬لْمُؤْمِنُونَۖ ١٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (453,4,'71',3,'Āl-‘Imrān','آل عِمران',6,8,161,'وَمَا كَانَ لِنَبِےٓءٍ أَنْ يُّغَلَّۖ وَمَنْ يَّغْلُلْ يَأْتِ بِمَا غَلَّ يَوْمَ اَ۬لْقِيَٰمَةِۖ ثُمَّ تُوَفَّىٰ كُلُّ نَفْسٖ مَّا كَسَبَتْ وَهُمْ لَا يُظْلَمُونَۖ ١٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (454,4,'71',3,'Āl-‘Imrān','آل عِمران',8,10,162,'أَفَمَنِ اِ۪تَّبَعَ رِضْوَٰنَ اَ۬للَّهِ كَمَنۢ بَآءَ بِسَخَطٖ مِّنَ اَ۬للَّهِ وَمَأْوَيٰهُ جَهَنَّمُۖ وَبِئْسَ اَ۬لْمَصِيرُۖ ١٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (455,4,'71',3,'Āl-‘Imrān','آل عِمران',10,10,163,'هُمْ دَرَجَٰتٌ عِندَ اَ۬للَّهِۖ وَاللَّهُ بَصِيرُۢ بِمَا يَعْمَلُونَۖ ١٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (456,4,'71',3,'Āl-‘Imrān','آل عِمران',11,13,164,'لَقَدْ مَنَّ اَ۬للَّهُ عَلَى اَ۬لْمُؤْمِنِينَ إِذْ بَعَثَ فِيهِمْ رَسُولاٗ مِّنْ أَنفُسِهِمْ يَتْلُواْ عَلَيْهِمْ ءَايَٰتِهِۦ وَيُزَكِّيهِمْ وَيُعَلِّمُهُمُ اُ۬لْكِتَٰبَ وَالْحِكْمَةَ وَإِن كَانُواْ مِن قَبْلُ لَفِے ضَلَٰلٖ مُّبِينٍۖ ١٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (457,4,'71',3,'Āl-‘Imrān','آل عِمران',14,15,165,'أَوَلَمَّا أَصَٰبَتْكُم مُّصِيبَةٞ قَدْ أَصَبْتُم مِّثْلَيْهَا قُلْتُمْ أَنَّىٰ هَٰذَاۖ قُلْ هُوَ مِنْ عِندِ أَنفُسِكُمْۖ إِنَّ اَ۬للَّهَ عَلَىٰ كُلِّ شَےْءٖ قَدِيرٞۖ ١٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (458,4,'72',3,'Āl-‘Imrān','آل عِمران',1,2,166,'وَمَا أَصَٰبَكُمْ يَوْمَ اَ۪لْتَقَى اَ۬لْجَمْعَٰنِ فَبِإِذْنِ اِ۬للَّهِ وَلِيَعْلَمَ اَ۬لْمُؤْمِنِينَ ١٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (459,4,'72',3,'Āl-‘Imrān','آل عِمران',2,5,167,'وَلِيَعْلَمَ اَ۬لذِينَ نَافَقُواْۖ وَقِيلَ لَهُمْ تَعَالَوْاْ قَٰتِلُواْ فِے سَبِيلِ اِ۬للَّهِ أَوِ اِ۪دْفَعُواْۖ قَالُواْ لَوْ نَعْلَمُ قِتَالاٗ لَّاتَّبَعْنَٰكُمْۖ هُمْ لِلْكُفْرِ يَوْمَئِذٍ أَقْرَبُ مِنْهُمْ لِلْإِيمَٰنِۖ يَقُولُونَ بِأَفْوَٰهِهِم مَّا لَيْسَ فِے قُلُوبِهِمْۖ وَاللَّهُ أَعْلَمُ بِمَا يَكْتُمُونَۖ ١٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (460,4,'72',3,'Āl-‘Imrān','آل عِمران',5,7,168,'اَ۬لذِينَ قَالُواْ لِإِخْوَٰنِهِمْ وَقَعَدُواْ لَوْ أَطَاعُونَا مَا قُتِلُواْۖ قُلْ فَادْرَءُواْ عَنْ أَنفُسِكُمُ اُ۬لْمَوْتَ إِن كُنتُمْ صَٰدِقِينَۖ ١٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (461,4,'72',3,'Āl-‘Imrān','آل عِمران',7,8,169,'وَلَا تَحْسِبَنَّ اَ۬لذِينَ قُتِلُواْ فِے سَبِيلِ اِ۬للَّهِ أَمْوَٰتاَۢۖ بَلْ أَحْيَآءٌۖ عِندَ رَبِّهِمْ يُرْزَقُونَ ١٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (462,4,'72',3,'Āl-‘Imrān','آل عِمران',8,10,170,'فَرِحِينَ بِمَا ءَاتَيٰهُمُ اُ۬للَّهُ مِن فَضْلِهِۦ وَيَسْتَبْشِرُونَ بِالذِينَ لَمْ يَلْحَقُواْ بِهِم مِّنْ خَلْفِهِمْ أَلَّا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَۖ ١٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (463,4,'72',3,'Āl-‘Imrān','آل عِمران',10,12,171,'۞ يَسْتَبْشِرُونَ بِنِعْمَةٖ مِّنَ اَ۬للَّهِ وَفَضْلٖ وَأَنَّ اَ۬للَّهَ لَا يُضِيعُ أَجْرَ اَ۬لْمُؤْمِنِينَ ١٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (464,4,'72',3,'Āl-‘Imrān','آل عِمران',12,13,172,'اَ۬لذِينَ اَ۪سْتَجَابُواْ لِلهِ وَالرَّسُولِ مِنۢ بَعْدِ مَا أَصَابَهُمُ اُ۬لْقَرْحُۖ لِلذِينَ أَحْسَنُواْ مِنْهُمْ وَاتَّقَوْاْ أَجْرٌ عَظِيمٌۖ ١٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (465,4,'72',3,'Āl-‘Imrān','آل عِمران',14,15,173,'اِ۬لذِينَ قَالَ لَهُمُ اُ۬لنَّاسُ إِنَّ اَ۬لنَّاسَ قَدْ جَمَعُواْ لَكُمْ فَاخْشَوْهُمْ فَزَادَهُمْ إِيمَٰناٗۖ وَقَالُواْ حَسْبُنَا اَ۬للَّهُ وَنِعْمَ اَ۬لْوَكِيلُۖ ١٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (466,4,'73',3,'Āl-‘Imrān','آل عِمران',1,2,174,'فَانقَلَبُواْ بِنِعْمَةٖ مِّنَ اَ۬للَّهِ وَفَضْلٖ لَّمْ يَمْسَسْهُمْ سُوٓءٞ وَاتَّبَعُواْ رِضْوَٰنَ اَ۬للَّهِۖ وَاللَّهُ ذُو فَضْلٍ عَظِيمٍۖ ١٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (467,4,'73',3,'Āl-‘Imrān','آل عِمران',2,3,175,'إِنَّمَا ذَٰلِكُمُ اُ۬لشَّيْطَٰنُ يُخَوِّفُ أَوْلِيَآءَهُۥ فَلَا تَخَافُوهُمْۖ وَخَافُونِ إِن كُنتُم مُّؤْمِنِينَۖ ١٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (468,4,'73',3,'Āl-‘Imrān','آل عِمران',4,6,176,'وَلَا يُحْزِنكَ اَ۬لذِينَ يُسَٰرِعُونَ فِے اِ۬لْكُفْرِۖ إِنَّهُمْ لَنْ يَّضُرُّواْ اُ۬للَّهَ شَئْاٗۖ يُرِيدُ اُ۬للَّهُ أَلَّا يَجْعَلَ لَهُمْ حَظّاٗ فِے اِ۬لْأٓخِرَةِۖ وَلَهُمْ عَذَابٌ عَظِيمٌۖ ١٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (469,4,'73',3,'Āl-‘Imrān','آل عِمران',6,7,177,'إِنَّ اَ۬لذِينَ اَ۪شْتَرَوُاْ اُ۬لْكُفْرَ بِالْإِيمَٰنِ لَنْ يَّضُرُّواْ اُ۬للَّهَ شَئْاٗۖ وَلَهُمْ عَذَابٌ أَلِيمٞۖ ١٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (470,4,'73',3,'Āl-‘Imrān','آل عِمران',7,9,178,'وَلَا يَحْسِبَنَّ اَ۬لذِينَ كَفَرُواْ أَنَّمَا نُمْلِے لَهُمْ خَيْرٞ لِّأَنفُسِهِمْۖ إِنَّمَا نُمْلِے لَهُمْ لِيَزْدَادُواْ إِثْماٗۖ وَلَهُمْ عَذَابٞ مُّهِينٞۖ ١٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (471,4,'73',3,'Āl-‘Imrān','آل عِمران',9,12,179,'۞ مَّا كَانَ اَ۬للَّهُ لِيَذَرَ اَ۬لْمُؤْمِنِينَ عَلَىٰ مَا أَنتُمْ عَلَيْهِ حَتَّىٰ يَمِيزَ اَ۬لْخَبِيثَ مِنَ اَ۬لطَّيِّبِۖ وَمَا كَانَ اَ۬للَّهُ لِيُطْلِعَكُمْ عَلَى اَ۬لْغَيْبِۖ وَلَٰكِنَّ اَ۬للَّهَ يَجْتَبِے مِن رُّسُلِهِۦ مَنْ يَّشَآءُۖ فَـَٔامِنُواْ بِاللَّهِ وَرُسُلِهِۦۖ وَإِن تُؤْمِنُواْ وَتَتَّقُواْ فَلَكُمْ أَجْرٌ عَظِيمٞۖ ١٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (472,4,'73',3,'Āl-‘Imrān','آل عِمران',12,15,180,'وَلَا يَحْسِبَنَّ اَ۬لذِينَ يَبْخَلُونَ بِمَا ءَاتَيٰهُمُ اُ۬للَّهُ مِن فَضْلِهِۦ هُوَ خَيْراٗ لَّهُمۖ بَلْ هُوَ شَرّٞ لَّهُمْۖ سَيُطَوَّقُونَ مَا بَخِلُواْ بِهِۦ يَوْمَ اَ۬لْقِيَٰمَةِۖ وَلِلهِ مِيرَٰثُ اُ۬لسَّمَٰوَٰتِ وَالْأَرْضِۖ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٞۖ ١٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (473,4,'74',3,'Āl-‘Imrān','آل عِمران',1,3,181,'لَّقَدْ سَمِعَ اَ۬للَّهُ قَوْلَ اَ۬لذِينَ قَالُواْ إِنَّ اَ۬للَّهَ فَقِيرٞ وَنَحْنُ أَغْنِيَآءُۖ سَنَكْتُبُ مَا قَالُواْ وَقَتْلَهُمُ اُ۬لْأَنۢبِئَآءَ بِغَيْرِ حَقّٖ وَنَقُولُ ذُوقُواْ عَذَابَ اَ۬لْحَرِيقِۖ ١٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (474,4,'74',3,'Āl-‘Imrān','آل عِمران',3,4,182,'ذَٰلِكَ بِمَا قَدَّمَتْ أَيْدِيكُمْ وَأَنَّ اَ۬للَّهَ لَيْسَ بِظَلَّٰمٖ لِّلْعَبِيدِۖ ١٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (475,4,'74',3,'Āl-‘Imrān','آل عِمران',4,7,183,'اِ۬لذِينَ قَالُواْ إِنَّ اَ۬للَّهَ عَهِدَ إِلَيْنَا أَلَّا نُؤْمِنَ لِرَسُولٍ حَتَّىٰ يَأْتِيَنَا بِقُرْبَانٖ تَأْكُلُهُ اُ۬لنَّارُۖ قُلْ قَدْ جَآءَكُمْ رُسُلٞ مِّن قَبْلِے بِالْبَيِّنَٰتِ وَبِالذِے قُلْتُمْ فَلِمَ قَتَلْتُمُوهُمْ إِن كُنتُمْ صَٰدِقِينَۖ ١٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (476,4,'74',3,'Āl-‘Imrān','آل عِمران',8,9,184,'فَإِن كَذَّبُوكَ فَقَدْ كُذِّبَ رُسُلٞ مِّن قَبْلِكَ جَآءُو بِالْبَيِّنَٰتِ وَالزُّبُرِ وَالْكِتَٰبِ اِ۬لْمُنِيرِۖ ١٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (477,4,'74',3,'Āl-‘Imrān','آل عِمران',9,12,185,'كُلُّ نَفْسٖ ذَآئِقَةُ اُ۬لْمَوْتِۖ وَإِنَّمَا تُوَفَّوْنَ أُجُورَكُمْ يَوْمَ اَ۬لْقِيَٰمَةِۖ فَمَن زُحْزِحَ عَنِ اِ۬لنَّارِ وَأُدْخِلَ اَ۬لْجَنَّةَ فَقَدْ فَازَۖ وَمَا اَ۬لْحَيَوٰةُ اُ۬لدُّنْيَا إِلَّا مَتَٰعُ اُ۬لْغُرُورِۖ ١٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (478,4,'74',3,'Āl-‘Imrān','آل عِمران',12,15,186,'۞ لَتُبْلَوُنَّ فِے أَمْوَٰلِكُمْ وَأَنفُسِكُمْ وَلَتَسْمَعُنَّ مِنَ اَ۬لذِينَ أُوتُواْ اُ۬لْكِتَٰبَ مِن قَبْلِكُمْ وَمِنَ اَ۬لذِينَ أَشْرَكُواْ أَذىٗ كَثِيراٗۖ وَإِن تَصْبِرُواْ وَتَتَّقُواْ فَإِنَّ ذَٰلِكَ مِنْ عَزْمِ اِ۬لْأُمُورِۖ ١٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (479,4,'75',3,'Āl-‘Imrān','آل عِمران',1,3,187,'وَإِذْ أَخَذَ اَ۬للَّهُ مِيثَٰقَ اَ۬لذِينَ أُوتُواْ اُ۬لْكِتَٰبَ لَتُبَيِّنُنَّهُۥ لِلنَّاسِ وَلَا تَكْتُمُونَهُۥ فَنَبَذُوهُ وَرَآءَ ظُهُورِهِمْ وَاشْتَرَوْاْ بِهِۦ ثَمَناٗ قَلِيلاٗۖ فَبِئْسَ مَا يَشْتَرُونَۖ ١٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (480,4,'75',3,'Āl-‘Imrān','آل عِمران',3,5,188,'لَا يَحْسِبَنَّ اَ۬لذِينَ يَفْرَحُونَ بِمَا أَتَواْ وَّيُحِبُّونَ أَنْ يُّحْمَدُواْ بِمَا لَمْ يَفْعَلُواْ فَلَا تَحْسِبَنَّهُم بِمَفَازَةٖ مِّنَ اَ۬لْعَذَابِۖ وَلَهُمْ عَذَابٌ أَلِيمٞۖ ١٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (481,4,'75',3,'Āl-‘Imrān','آل عِمران',5,6,189,'وَلِلهِ مُلْكُ اُ۬لسَّمَٰوَٰتِ وَالْأَرْضِۖ وَاللَّهُ عَلَىٰ كُلِّ شَےْءٖ قَدِيرٌۖ ١٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (482,4,'75',3,'Āl-‘Imrān','آل عِمران',6,8,190,'إِنَّ فِے خَلْقِ اِ۬لسَّمَٰوَٰتِ وَالْأَرْضِ وَاخْتِلَٰفِ اِ۬ليْلِ وَالنَّهَارِ لَأٓيَٰتٖ لِّأُوْلِے اِ۬لْأَلْبَٰبِ ١٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (483,4,'75',3,'Āl-‘Imrān','آل عِمران',8,10,191,'اِ۬لذِينَ يَذْكُرُونَ اَ۬للَّهَ قِيَٰماٗ وَقُعُوداٗ وَعَلَىٰ جُنُوبِهِمْ وَيَتَفَكَّرُونَ فِے خَلْقِ اِ۬لسَّمَٰوَٰتِ وَالْأَرْضِۖ رَبَّنَا مَا خَلَقْتَ هَٰذَا بَٰطِلاٗ سُبْحَٰنَكَۖ فَقِنَا عَذَابَ اَ۬لنَّارِۖ ١٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (484,4,'75',3,'Āl-‘Imrān','آل عِمران',11,12,192,'رَبَّنَا إِنَّكَ مَن تُدْخِلِ اِ۬لنَّارَ فَقَدْ أَخْزَيْتَهُۥۖ وَمَا لِلظَّٰلِمِينَ مِنْ أَنصَارٖۖ ١٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (485,4,'75',3,'Āl-‘Imrān','آل عِمران',12,14,193,'رَّبَّنَا إِنَّنَا سَمِعْنَا مُنَادِياٗ يُنَادِے لِلْإِيمَٰنِ أَنْ ءَامِنُواْ بِرَبِّكُمْ فَـَٔامَنَّاۖ رَبَّنَا فَاغْفِرْ لَنَا ذُنُوبَنَا وَكَفِّرْ عَنَّا سَيِّـَٔاتِنَا وَتَوَفَّنَا مَعَ اَ۬لْأَبْرَارِۖ ١٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (486,4,'75',3,'Āl-‘Imrān','آل عِمران',14,15,194,'رَبَّنَا وَءَاتِنَا مَا وَعَدتَّنَا عَلَىٰ رُسُلِكَ وَلَا تُخْزِنَا يَوْمَ اَ۬لْقِيَٰمَةِۖ إِنَّكَ لَا تُخْلِفُ اُ۬لْمِيعَادَۖ ١٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (487,4,'76',3,'Āl-‘Imrān','آل عِمران',1,6,195,'۞ فَاسْتَجَابَ لَهُمْ رَبُّهُمْ أَنِّے لَا أُضِيعُ عَمَلَ عَٰمِلٖ مِّنكُم مِّن ذَكَرٍ أَوْ أُنثَىٰۖ بَعْضُكُم مِّنۢ بَعْضٖۖ فَالذِينَ هَاجَرُواْ وَأُخْرِجُواْ مِن دِيَٰرِهِمْ وَأُوذُواْ فِے سَبِيلِے وَقَٰتَلُواْ وَقُتِلُواْ لَأُكَفِّرَنَّ عَنْهُمْ سَيِّـَٔاتِهِمْ وَلَأُدْخِلَنَّهُمْ جَنَّٰتٖ تَجْرِے مِن تَحْتِهَا اَ۬لْأَنْهَٰرُ ثَوَاباٗ مِّنْ عِندِ اِ۬للَّهِۖ وَاللَّهُ عِندَهُۥ حُسْنُ اُ۬لثَّوَابِۖ ١٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (488,4,'76',3,'Āl-‘Imrān','آل عِمران',6,7,196,'لَا يَغُرَّنَّكَ تَقَلُّبُ اُ۬لذِينَ كَفَرُواْ فِے اِ۬لْبِلَٰدِۖ ١٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (489,4,'76',3,'Āl-‘Imrān','آل عِمران',7,8,197,'مَتَٰعٞ قَلِيلٞۖ ثُمَّ مَأْوَيٰهُمْ جَهَنَّمُۖ وَبِئْسَ اَ۬لْمِهَادُۖ ١٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (490,4,'76',3,'Āl-‘Imrān','آل عِمران',8,10,198,'لَٰكِنِ اِ۬لذِينَ اَ۪تَّقَوْاْ رَبَّهُمْ لَهُمْ جَنَّٰتٞ تَجْرِے مِن تَحْتِهَا اَ۬لْأَنْهَٰرُ خَٰلِدِينَ فِيهَا نُزُلاٗ مِّنْ عِندِ اِ۬للَّهِۖ وَمَا عِندَ اَ۬للَّهِ خَيْرٞ لِّلْأَبْرَارِۖ ١٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (491,4,'76',3,'Āl-‘Imrān','آل عِمران',10,14,199,'وَإِنَّ مِنْ أَهْلِ اِ۬لْكِتَٰبِ لَمَنْ يُّؤْمِنُ بِاللَّهِ وَمَا أُنزِلَ إِلَيْكُمْ وَمَا أُنزِلَ إِلَيْهِمْ خَٰشِعِينَ لِلهِۖ لَا يَشْتَرُونَ بِـَٔايَٰتِ اِ۬للَّهِ ثَمَناٗ قَلِيلاًۖ أُوْلَٰٓئِكَ لَهُمْ أَجْرُهُمْ عِندَ رَبِّهِمْۖ إِنَّ اَ۬للَّهَ سَرِيعُ اُ۬لْحِسَابِۖ ١٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (492,4,'76',3,'Āl-‘Imrān','آل عِمران',14,15,200,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ اُ۪صْبِرُواْ وَصَابِرُواْ وَرَابِطُواْ وَاتَّقُواْ اُ۬للَّهَ لَعَلَّكُمْ تُفْلِحُونَۖ ٢٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (493,4,'77',4,'An-Nisā’','النِّسَاء',3,5,1,'يَٰأَيُّهَا اَ۬لنَّاسُ اُ۪تَّقُواْ رَبَّكُمُ اُ۬لذِے خَلَقَكُم مِّن نَّفْسٖ وَٰحِدَةٖ وَخَلَقَ مِنْهَا زَوْجَهَا وَبَثَّ مِنْهُمَا رِجَالاٗ كَثِيراٗ وَنِسَآءٗۖ وَاتَّقُواْ اُ۬للَّهَ اَ۬لذِے تَسَّآءَلُونَ بِهِۦ وَالْأَرْحَامَۖ إِنَّ اَ۬للَّهَ كَانَ عَلَيْكُمْ رَقِيباٗۖ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (494,4,'77',4,'An-Nisā’','النِّسَاء',5,6,2,'وَءَاتُواْ اُ۬لْيَتَٰمَىٰ أَمْوَٰلَهُمْۖ وَلَا تَتَبَدَّلُواْ اُ۬لْخَبِيثَ بِالطَّيِّبِۖ وَلَا تَأْكُلُواْ أَمْوَٰلَهُمْ إِلَىٰ أَمْوَٰلِكُمْۖ إِنَّهُۥ كَانَ حُوباٗ كَبِيراٗۖ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (495,4,'77',4,'An-Nisā’','النِّسَاء',7,9,3,'وَإِنْ خِفْتُمْ أَلَّا تُقْسِطُواْ فِے اِ۬لْيَتَٰمَىٰ فَانكِحُواْ مَا طَابَ لَكُم مِّنَ اَ۬لنِّسَآءِ مَثْنَىٰ وَثُلَٰثَ وَرُبَٰعَۖ فَإِنْ خِفْتُمْ أَلَّا تَعْدِلُواْ فَوَٰحِدَةً أَوْ مَا مَلَكَتْ أَيْمَٰنُكُمْۖ ذَٰلِكَ أَدْنَىٰ أَلَّا تَعُولُواْۖ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (496,4,'77',4,'An-Nisā’','النِّسَاء',9,10,4,'وَءَاتُواْ اُ۬لنِّسَآءَ صَدُقَٰتِهِنَّ نِحْلَةٗۖ فَإِن طِبْنَ لَكُمْ عَن شَےْءٖ مِّنْهُ نَفْساٗ فَكُلُوهُۖ هَنِيٓـٔاٗ مَّرِيٓـٔاٗۖ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (497,4,'77',4,'An-Nisā’','النِّسَاء',10,12,5,'وَلَا تُؤْتُواْ اُ۬لسُّفَهَا أَمْوَٰلَكُمُ اُ۬لتِے جَعَلَ اَ۬للَّهُ لَكُمْ قِيَماٗۖ وَارْزُقُوهُمْ فِيهَا وَاكْسُوهُمْ وَقُولُواْ لَهُمْ قَوْلاٗ مَّعْرُوفاٗۖ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (498,4,'77',4,'An-Nisā’','النِّسَاء',12,15,6,'۞ وَابْتَلُواْ اُ۬لْيَتَٰمَىٰ حَتَّىٰ إِذَا بَلَغُواْ اُ۬لنِّكَاحَ فَإِنْ ءَانَسْتُم مِّنْهُمْ رُشْداٗ فَادْفَعُواْ إِلَيْهِمْ أَمْوَٰلَهُمْ وَلَا تَأْكُلُوهَا إِسْرَافاٗ وَبِدَاراً أَنْ يَّكْبَرُواْۖ وَمَن كَانَ غَنِيّاٗ فَلْيَسْتَعْفِفْۖ وَمَن كَانَ فَقِيراٗ فَلْيَأْكُلْ بِالْمَعْرُوفِۖ فَإِذَا دَفَعْتُمْ إِلَيْهِمْ أَمْوَٰلَهُمْ فَأَشْهِدُواْ عَلَيْهِمْۖ وَكَفَىٰ بِاللَّهِ حَسِيباٗۖ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (499,4,'78',4,'An-Nisā’','النِّسَاء',1,3,7,'لِّلرِّجَالِ نَصِيبٞ مِّمَّا تَرَكَ اَ۬لْوَٰلِدَٰنِ وَالْأَقْرَبُونَ وَلِلنِّسَآءِ نَصِيبٞ مِّمَّا تَرَكَ اَ۬لْوَٰلِدَٰنِ وَالْأَقْرَبُونَ مِمَّا قَلَّ مِنْهُ أَوْ كَثُرَۖ نَصِيباٗ مَّفْرُوضاٗۖ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (500,4,'78',4,'An-Nisā’','النِّسَاء',3,4,8,'وَإِذَا حَضَرَ اَ۬لْقِسْمَةَ أُوْلُواْ اُ۬لْقُرْبَىٰ وَالْيَتَٰمَىٰ وَالْمَسَٰكِينُ فَارْزُقُوهُم مِّنْهُۖ وَقُولُواْ لَهُمْ قَوْلاٗ مَّعْرُوفاٗۖ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (501,4,'78',4,'An-Nisā’','النِّسَاء',5,6,9,'وَلْيَخْشَ اَ۬لذِينَ لَوْ تَرَكُواْ مِنْ خَلْفِهِمْ ذُرِّيَّةٗ ضِعَٰفاً خَافُواْ عَلَيْهِمْۖ فَلْيَتَّقُواْ اُ۬للَّهَ وَلْيَقُولُواْ قَوْلاٗ سَدِيداًۖ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (502,4,'78',4,'An-Nisā’','النِّسَاء',6,8,10,'إِنَّ اَ۬لذِينَ يَأْكُلُونَ أَمْوَٰلَ اَ۬لْيَتَٰمَىٰ ظُلْماً إِنَّمَا يَأْكُلُونَ فِے بُطُونِهِمْ نَاراٗۖ وَسَيَصْلَوْنَ سَعِيراٗۖ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (503,4,'78',4,'An-Nisā’','النِّسَاء',8,15,11,'۞ يُوصِيكُمُ اُ۬للَّهُ فِے أَوْلَٰدِكُمْۖ لِلذَّكَرِ مِثْلُ حَظِّ اِ۬لْأُنثَيَيْنِۖ فَإِن كُنَّ نِسَآءٗ فَوْقَ اَ۪ثْنَتَيْنِ فَلَهُنَّ ثُلُثَا مَا تَرَكَۖ وَإِن كَانَتْ وَٰحِدَةٞ فَلَهَا اَ۬لنِّصْفُۖ وَلِأَبَوَيْهِ لِكُلِّ وَٰحِدٖ مِّنْهُمَا اَ۬لسُّدُسُ مِمَّا تَرَكَ إِن كَانَ لَهُۥ وَلَدٞۖ فَإِن لَّمْ يَكُن لَّهُۥ وَلَدٞ وَوَرِثَهُۥ أَبَوَٰهُ فَلِأُمِّهِ اِ۬لثُّلُثُۖ فَإِن كَانَ لَهُۥ إِخْوَةٞ فَلِأُمِّهِ اِ۬لسُّدُسُۖ مِنۢ بَعْدِ وَصِيَّةٖ يُوصِے بِهَا أَوْ دَيْنٍۖ ءَابَآؤُكُمْ وَأَبْنَآؤُكُمْ لَا تَدْرُونَ أَيُّهُمْ أَقْرَبُ لَكُمْ نَفْعاٗۖ فَرِيضَةٗ مِّنَ اَ۬للَّهِۖ إِنَّ اَ۬للَّهَ كَانَ عَلِيماً حَكِيماٗۖ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (504,4,'79',4,'An-Nisā’','النِّسَاء',1,11,12,'وَلَكُمْ نِصْفُ مَا تَرَكَ أَزْوَٰجُكُمْ إِن لَّمْ يَكُن لَّهُنَّ وَلَدٞۖ فَإِن كَانَ لَهُنَّ وَلَدٞ فَلَكُمُ اُ۬لرُّبُعُ مِمَّا تَرَكْنَ مِنۢ بَعْدِ وَصِيَّةٖ يُوصِينَ بِهَا أَوْ دَيْنٖۖ وَلَهُنَّ اَ۬لرُّبُعُ مِمَّا تَرَكْتُمْ إِن لَّمْ يَكُن لَّكُمْ وَلَدٞۖ فَإِن كَانَ لَكُمْ وَلَدٞ فَلَهُنَّ اَ۬لثُّمُنُ مِمَّا تَرَكْتُم مِّنۢ بَعْدِ وَصِيَّةٖ تُوصُونَ بِهَا أَوْ دَيْنٖۖ وَإِن كَانَ رَجُلٞ يُورَثُ كَلَٰلَةً أَوِ اِ۪مْرَأَةٞ وَلَهُۥ أَخٌ أَوْ أُخْتٞ فَلِكُلِّ وَٰحِدٖ مِّنْهُمَا اَ۬لسُّدُسُۖ فَإِن كَانُواْ أَكْثَرَ مِن ذَٰلِكَ فَهُمْ شُرَكَآءُ فِے اِ۬لثُّلُثِ مِنۢ بَعْدِ وَصِيَّةٖ يُوصِے بِهَا أَوْ دَيْنٍ غَيْرَ مُضَآرّٖۖ وَصِيَّةٗ مِّنَ اَ۬للَّهِۖ وَاللَّهُ عَلِيمٌ حَلِيمٞۖ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (505,4,'79',4,'An-Nisā’','النِّسَاء',11,13,13,'۞ تِلْكَ حُدُودُ اُ۬للَّهِۖ وَمَنْ يُّطِعِ اِ۬للَّهَ وَرَسُولَهُۥ نُدْخِلْهُ جَنَّٰتٖ تَجْرِے مِن تَحْتِهَا اَ۬لْأَنْهَٰرُ خَٰلِدِينَ فِيهَاۖ وَذَٰلِكَ اَ۬لْفَوْزُ اُ۬لْعَظِيمُۖ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (506,4,'79',4,'An-Nisā’','النِّسَاء',14,15,14,'وَمَنْ يَّعْصِ اِ۬للَّهَ وَرَسُولَهُۥ وَيَتَعَدَّ حُدُودَهُۥ نُدْخِلْهُ نَاراً خَٰلِداٗ فِيهَاۖ وَلَهُۥ عَذَابٞ مُّهِينٞۖ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (507,4,'80',4,'An-Nisā’','النِّسَاء',1,3,15,'وَالَّٰتِے يَأْتِينَ اَ۬لْفَٰحِشَةَ مِن نِّسَآئِكُمْ فَاسْتَشْهِدُواْ عَلَيْهِنَّ أَرْبَعَةٗ مِّنكُمْۖ فَإِن شَهِدُواْ فَأَمْسِكُوهُنَّ فِے اِ۬لْبِيُوتِ حَتَّىٰ يَتَوَفَّيٰهُنَّ اَ۬لْمَوْتُ أَوْ يَجْعَلَ اَ۬للَّهُ لَهُنَّ سَبِيلاٗۖ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (508,4,'80',4,'An-Nisā’','النِّسَاء',4,5,16,'وَالذَٰنِ يَأْتِيَٰنِهَا مِنكُمْ فَـَٔاذُوهُمَاۖ فَإِن تَابَا وَأَصْلَحَا فَأَعْرِضُواْ عَنْهُمَاۖ إِنَّ اَ۬للَّهَ كَانَ تَوَّاباٗ رَّحِيماًۖ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (509,4,'80',4,'An-Nisā’','النِّسَاء',6,8,17,'إِنَّمَا اَ۬لتَّوْبَةُ عَلَى اَ۬للَّهِ لِلذِينَ يَعْمَلُونَ اَ۬لسُّوٓءَ بِجَهَٰلَةٖ ثُمَّ يَتُوبُونَ مِن قَرِيبٖۖ فَأُوْلَٰٓئِكَ يَتُوبُ اُ۬للَّهُ عَلَيْهِمْۖ وَكَانَ اَ۬للَّهُ عَلِيماً حَكِيماٗۖ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (510,4,'80',4,'An-Nisā’','النِّسَاء',8,11,18,'وَلَيْسَتِ اِ۬لتَّوْبَةُ لِلذِينَ يَعْمَلُونَ اَ۬لسَّيِّـَٔاتِ حَتَّىٰ إِذَا حَضَرَ أَحَدَهُمُ اُ۬لْمَوْتُ قَالَ إِنِّے تُبْتُ اُ۬ءَلْٰنَ وَلَا اَ۬لذِينَ يَمُوتُونَ وَهُمْ كُفَّارٌۖ أُوْلَٰٓئِكَ أَعْتَدْنَا لَهُمْ عَذَاباً أَلِيماٗۖ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (511,4,'80',4,'An-Nisā’','النِّسَاء',11,15,19,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ لَا يَحِلُّ لَكُمْ أَن تَرِثُواْ اُ۬لنِّسَآءَ كَرْهاٗۖ وَلَا تَعْضُلُوهُنَّ لِتَذْهَبُواْ بِبَعْضِ مَا ءَاتَيْتُمُوهُنَّ إِلَّا أَنْ يَّأْتِينَ بِفَٰحِشَةٖ مُّبَيِّنَةٖۖ ۞ وَعَاشِرُوهُنَّ بِالْمَعْرُوفِۖ فَإِن كَرِهْتُمُوهُنَّ فَعَسَىٰ أَن تَكْرَهُواْ شَئْاٗ وَيَجْعَلَ اَ۬للَّهُ فِيهِ خَيْراٗ كَثِيراٗۖ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (512,4,'81',4,'An-Nisā’','النِّسَاء',1,3,20,'وَإِنْ أَرَدتُّمُ اُ۪سْتِبْدَالَ زَوْجٖ مَّكَانَ زَوْجٖ وَءَاتَيْتُمْ إِحْدَيٰهُنَّ قِنطَاراٗ فَلَا تَأْخُذُواْ مِنْهُ شَئْاًۖ أَتَأْخُذُونَهُۥ بُهْتَٰناٗ وَإِثْماٗ مُّبِيناٗۖ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (513,4,'81',4,'An-Nisā’','النِّسَاء',3,4,21,'وَكَيْفَ تَأْخُذُونَهُۥ وَقَدْ أَفْضَىٰ بَعْضُكُمْ إِلَىٰ بَعْضٖ وَأَخَذْنَ مِنكُم مِّيثَٰقاً غَلِيظاٗۖ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (514,4,'81',4,'An-Nisā’','النِّسَاء',5,7,22,'وَلَا تَنكِحُواْ مَا نَكَحَ ءَابَآؤُكُم مِّنَ اَ۬لنِّسَآ۟ إِلَّا مَا قَدْ سَلَفَۖ إِنَّهُۥ كَانَ فَٰحِشَةٗ وَمَقْتاٗ وَسَآءَ سَبِيلاًۖ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (515,4,'81',4,'An-Nisā’','النِّسَاء',7,15,23,'حُرِّمَتْ عَلَيْكُمْ أُمَّهَٰتُكُمْ وَبَنَاتُكُمْ وَأَخَوَٰتُكُمْ وَعَمَّٰتُكُمْ وَخَٰلَٰتُكُمْ وَبَنَاتُ اُ۬لْأَخِ وَبَنَاتُ اُ۬لْأُخْتِۖ وَأُمَّهَٰتُكُمُ اُ۬لَّٰتِے أَرْضَعْنَكُمْ وَأَخَوَٰتُكُم مِّنَ اَ۬لرَّضَٰعَةِۖ وَأُمَّهَٰتُ نِسَآئِكُمْ وَرَبَٰٓئِبُكُمُ اُ۬لَّٰتِے فِے حُجُورِكُم مِّن نِّسَآئِكُمُ اُ۬لَّٰتِے دَخَلْتُم بِهِنَّۖ فَإِن لَّمْ تَكُونُواْ دَخَلْتُم بِهِنَّ فَلَا جُنَاحَ عَلَيْكُمْۖ وَحَلَٰٓئِلُ أَبْنَآئِكُمُ اُ۬لذِينَ مِنْ أَصْلَٰبِكُمْۖ وَأَن تَجْمَعُواْ بَيْنَ اَ۬لْأُخْتَيْنِ إِلَّا مَا قَدْ سَلَفَۖ إِنَّ اَ۬للَّهَ كَانَ غَفُوراٗ رَّحِيماٗۖ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (516,5,'82',4,'An-Nisā’','النِّسَاء',1,6,24,'۞ وَالْمُحْصَنَٰتُ مِنَ اَ۬لنِّسَآ۟ إِلَّا مَا مَلَكَتْ أَيْمَٰنُكُمْۖ كِتَٰبَ اَ۬للَّهِ عَلَيْكُمْۖ وَأَحَلَّ لَكُم مَّا وَرَآءَ ذَٰلِكُمْ أَن تَبْتَغُواْ بِأَمْوَٰلِكُم مُّحْصِنِينَ غَيْرَ مُسَٰفِحِينَۖ فَمَا اَ۪سْتَمْتَعْتُم بِهِۦ مِنْهُنَّ فَـَٔاتُوهُنَّ أُجُورَهُنَّ فَرِيضَةٗۖ وَلَا جُنَاحَ عَلَيْكُمْ فِيمَا تَرَٰضَيْتُم بِهِۦ مِنۢ بَعْدِ اِ۬لْفَرِيضَةِۖ إِنَّ اَ۬للَّهَ كَانَ عَلِيماً حَكِيماٗۖ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (517,5,'82',4,'An-Nisā’','النِّسَاء',6,13,25,'وَمَن لَّمْ يَسْتَطِعْ مِنكُمْ طَوْلاً أَنْ يَّنكِحَ اَ۬لْمُحْصَنَٰتِ اِ۬لْمُؤْمِنَٰتِ فَمِن مَّا مَلَكَتْ أَيْمَٰنُكُم مِّن فَتَيَٰتِكُمُ اُ۬لْمُؤْمِنَٰتِۖ وَاللَّهُ أَعْلَمُ بِإِيمَٰنِكُمۖ بَعْضُكُم مِّنۢ بَعْضٖۖ فَانكِحُوهُنَّ بِإِذْنِ أَهْلِهِنَّۖ وَءَاتُوهُنَّ أُجُورَهُنَّ بِالْمَعْرُوفِ مُحْصَنَٰتٍ غَيْرَ مُسَٰفِحَٰتٖ وَلَا مُتَّخِذَٰتِ أَخْدَانٖۖ فَإِذَا أُحْصِنَّ فَإِنْ أَتَيْنَ بِفَٰحِشَةٖ فَعَلَيْهِنَّ نِصْفُ مَا عَلَى اَ۬لْمُحْصَنَٰتِ مِنَ اَ۬لْعَذَابِۖ ذَٰلِكَ لِمَنْ خَشِيَ اَ۬لْعَنَتَ مِنكُمْۖ وَأَن تَصْبِرُواْ خَيْرٞ لَّكُمْۖ وَاللَّهُ غَفُورٞ رَّحِيمٞۖ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (518,5,'82',4,'An-Nisā’','النِّسَاء',14,15,26,'يُرِيدُ اُ۬للَّهُ لِيُبَيِّنَ لَكُمْ وَيَهْدِيَكُمْ سُنَنَ اَ۬لذِينَ مِن قَبْلِكُمْ وَيَتُوبَ عَلَيْكُمْۖ وَاللَّهُ عَلِيمٌ حَكِيمٞۖ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (519,5,'83',4,'An-Nisā’','النِّسَاء',1,2,27,'وَاللَّهُ يُرِيدُ أَنْ يَّتُوبَ عَلَيْكُمْۖ وَيُرِيدُ اُ۬لذِينَ يَتَّبِعُونَ اَ۬لشَّهَوَٰتِ أَن تَمِيلُواْ مَيْلاً عَظِيماٗۖ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (520,5,'83',4,'An-Nisā’','النِّسَاء',2,3,28,'يُرِيدُ اُ۬للَّهُ أَنْ يُّخَفِّفَ عَنكُمْۖ وَخُلِقَ اَ۬لْإِنسَٰنُ ضَعِيفاٗۖ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (521,5,'83',4,'An-Nisā’','النِّسَاء',3,6,29,'۞ يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ لَا تَأْكُلُواْ أَمْوَٰلَكُم بَيْنَكُم بِالْبَٰطِلِۖ إِلَّا أَن تَكُونَ تِجَٰرَةٌ عَن تَرَاضٖ مِّنكُمْۖ وَلَا تَقْتُلُواْ أَنفُسَكُمْۖ إِنَّ اَ۬للَّهَ كَانَ بِكُمْ رَحِيماٗۖ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (522,5,'83',4,'An-Nisā’','النِّسَاء',6,8,30,'وَمَنْ يَّفْعَلْ ذَٰلِكَ عُدْوَٰناٗ وَظُلْماٗ فَسَوْفَ نُصْلِيهِ نَاراٗۖ وَكَانَ ذَٰلِكَ عَلَى اَ۬للَّهِ يَسِيراًۖ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (523,5,'83',4,'An-Nisā’','النِّسَاء',8,9,31,'إِن تَجْتَنِبُواْ كَبَآئِرَ مَا تُنْهَوْنَ عَنْهُ نُكَفِّرْ عَنكُمْ سَيِّـَٔاتِكُمْ وَنُدْخِلْكُم مَّدْخَلاٗ كَرِيماٗۖ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (524,5,'83',4,'An-Nisā’','النِّسَاء',10,13,32,'وَلَا تَتَمَنَّوْاْ مَا فَضَّلَ اَ۬للَّهُ بِهِۦ بَعْضَكُمْ عَلَىٰ بَعْضٖۖ لِّلرِّجَالِ نَصِيبٞ مِّمَّا اَ۪كْتَسَبُواْۖ وَلِلنِّسَآءِ نَصِيبٞ مِّمَّا اَ۪كْتَسَبْنَۖ وَسْـَٔلُواْ اُ۬للَّهَ مِن فَضْلِهِۦۖ إِنَّ اَ۬للَّهَ كَانَ بِكُلِّ شَےْءٍ عَلِيماٗۖ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (525,5,'83',4,'An-Nisā’','النِّسَاء',13,15,33,'وَلِكُلّٖ جَعَلْنَا مَوَٰلِيَ مِمَّا تَرَكَۖ اَ۬لْوَٰلِدَٰنِ وَالْأَقْرَبُونَۖ وَالذِينَ عَٰقَدَتْ أَيْمَٰنُكُمْ فَـَٔاتُوهُمْ نَصِيبَهُمْۖ إِنَّ اَ۬للَّهَ كَانَ عَلَىٰ كُلِّ شَےْءٖ شَهِيداًۖ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (526,5,'84',4,'An-Nisā’','النِّسَاء',1,6,34,'اِ۬لرِّجَالُ قَوَّٰمُونَ عَلَى اَ۬لنِّسَآءِ بِمَا فَضَّلَ اَ۬للَّهُ بَعْضَهُمْ عَلَىٰ بَعْضٖ وَبِمَا أَنفَقُواْ مِنْ أَمْوَٰلِهِمْۖ فَالصَّٰلِحَٰتُ قَٰنِتَٰتٌ حَٰفِظَٰتٞ لِّلْغَيْبِ بِمَا حَفِظَ اَ۬للَّهُۖ وَالَّٰتِے تَخَافُونَ نُشُوزَهُنَّ فَعِظُوهُنَّ وَاهْجُرُوهُنَّ فِے اِ۬لْمَضَاجِعِ وَاضْرِبُوهُنَّۖ فَإِنْ أَطَعْنَكُمْ فَلَا تَبْغُواْ عَلَيْهِنَّ سَبِيلاًۖ إِنَّ اَ۬للَّهَ كَانَ عَلِيّاٗ كَبِيراٗۖ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (527,5,'84',4,'An-Nisā’','النِّسَاء',6,9,35,'وَإِنْ خِفْتُمْ شِقَاقَ بَيْنِهِمَا فَابْعَثُواْ حَكَماٗ مِّنْ أَهْلِهِۦ وَحَكَماٗ مِّنْ أَهْلِهَا إِنْ يُّرِيدَا إِصْلَٰحاٗ يُوَفِّقِ اِ۬للَّهُ بَيْنَهُمَاۖ إِنَّ اَ۬للَّهَ كَانَ عَلِيماً خَبِيراٗۖ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (528,5,'84',4,'An-Nisā’','النِّسَاء',9,13,36,'۞ وَاعْبُدُواْ اُ۬للَّهَۖ وَلَا تُشْرِكُواْ بِهِۦ شَئْاٗۖ وَبِالْوَٰلِدَيْنِ إِحْسَٰناٗ وَبِذِے اِ۬لْقُرْبَىٰ وَالْيَتَٰمَىٰ وَالْمَسَٰكِينِ وَالْجَارِ ذِے اِ۬لْقُرْبَىٰ وَالْجَارِ اِ۬لْجُنُبِ وَالصَّٰحِبِ بِالْجَنۢبِ وَابْنِ اِ۬لسَّبِيلِ وَمَا مَلَكَتْ أَيْمَٰنُكُمْۖ إِنَّ اَ۬للَّهَ لَا يُحِبُّ مَن كَانَ مُخْتَالاٗ فَخُوراًۖ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (529,5,'84',4,'An-Nisā’','النِّسَاء',13,15,37,'اِ۬لذِينَ يَبْخَلُونَ وَيَأْمُرُونَ اَ۬لنَّاسَ بِالْبُخْلِ وَيَكْتُمُونَ مَا ءَاتَيٰهُمُ اُ۬للَّهُ مِن فَضْلِهِۦۖ وَأَعْتَدْنَا لِلْكَٰفِرِينَ عَذَاباٗ مُّهِيناٗۖ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (530,5,'85',4,'An-Nisā’','النِّسَاء',1,3,38,'وَالذِينَ يُنفِقُونَ أَمْوَٰلَهُمْ رِئَآءَ اَ۬لنَّاسِ وَلَا يُؤْمِنُونَ بِاللَّهِ وَلَا بِالْيَوْمِ اِ۬لْأٓخِرِۖ وَمَنْ يَّكُنِ اِ۬لشَّيْطَٰنُ لَهُۥ قَرِيناٗ فَسَآءَ قَرِيناٗۖ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (531,5,'85',4,'An-Nisā’','النِّسَاء',3,4,39,'وَمَاذَا عَلَيْهِمْ لَوْ ءَامَنُواْ بِاللَّهِ وَالْيَوْمِ اِ۬لْأٓخِرِ وَأَنفَقُواْ مِمَّا رَزَقَهُمُ اُ۬للَّهُۖ وَكَانَ اَ۬للَّهُ بِهِمْ عَلِيماًۖ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (532,5,'85',4,'An-Nisā’','النِّسَاء',4,6,40,'إِنَّ اَ۬للَّهَ لَا يَظْلِمُ مِثْقَالَ ذَرَّةٖۖ وَإِن تَكُ حَسَنَةٞ يُضَٰعِفْهَا وَيُؤْتِ مِن لَّدُنْهُ أَجْراً عَظِيماٗۖ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (533,5,'85',4,'An-Nisā’','النِّسَاء',6,7,41,'فَكَيْفَ إِذَا جِئْنَا مِن كُلِّ أُمَّةِۢ بِشَهِيدٖ وَجِئْنَا بِكَ عَلَىٰ هَٰؤُلَآءِ شَهِيداٗۖ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (534,5,'85',4,'An-Nisā’','النِّسَاء',7,9,42,'يَوْمَئِذٖ يَوَدُّ اُ۬لذِينَ كَفَرُواْ وَعَصَوُاْ اُ۬لرَّسُولَ لَوْ تَسَّوَّىٰ بِهِمُ اُ۬لْأَرْضُ وَلَا يَكْتُمُونَ اَ۬للَّهَ حَدِيثاٗۖ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (535,5,'85',4,'An-Nisā’','النِّسَاء',9,14,43,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ لَا تَقْرَبُواْ اُ۬لصَّلَوٰةَ وَأَنتُمْ سُكَٰرَىٰ حَتَّىٰ تَعْلَمُواْ مَا تَقُولُونَ وَلَا جُنُباً إِلَّا عَابِرِے سَبِيلٍ حَتَّىٰ تَغْتَسِلُواْۖ وَإِن كُنتُم مَّرْضَىٰ أَوْ عَلَىٰ سَفَرٍ أَوْ جَا أَحَدٞ مِّنكُم مِّنَ اَ۬لْغَآئِطِ أَوْ لَٰمَسْتُمُ اُ۬لنِّسَآءَ فَلَمْ تَجِدُواْ مَآءٗ فَتَيَمَّمُواْ صَعِيداٗ طَيِّباٗ فَامْسَحُواْ بِوُجُوهِكُمْ وَأَيْدِيكُمْۖ إِنَّ اَ۬للَّهَ كَانَ عَفُوّاً غَفُوراًۖ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (536,5,'85-86',4,'An-Nisā’','النِّسَاء',14,1,44,'أَلَمْ تَرَ إِلَى اَ۬لذِينَ أُوتُواْ نَصِيباٗ مِّنَ اَ۬لْكِتَٰبِ يَشْتَرُونَ اَ۬لضَّلَٰلَةَ وَيُرِيدُونَ أَن تَضِلُّواْ اُ۬لسَّبِيلَۖ  وَاللَّهُ أَعْلَمُ بِأَعْدَآئِكُمْۖ وَكَفَىٰ بِاللَّهِ وَلِيّاٗ وَكَفَىٰ بِاللَّهِ نَصِيراٗۖ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (537,5,'86',4,'An-Nisā’','النِّسَاء',2,6,45,'۞ مِّنَ اَ۬لذِينَ هَادُواْ يُحَرِّفُونَ اَ۬لْكَلِمَ عَن مَّوَاضِعِهِۦ وَيَقُولُونَ سَمِعْنَا وَعَصَيْنَا وَاسْمَعْ غَيْرَ مُسْمَعٖ وَرَٰعِنَا لَيّاَۢ بِأَلْسِنَتِهِمْ وَطَعْناٗ فِے اِ۬لدِّينِۖ وَلَوْ أَنَّهُمْ قَالُواْ سَمِعْنَا وَأَطَعْنَا وَاسْمَعْ وَانظُرْنَا لَكَانَ خَيْراٗ لَّهُمْ وَأَقْوَمَۖ وَلَٰكِن لَّعَنَهُمُ اُ۬للَّهُ بِكُفْرِهِمْ فَلَا يُؤْمِنُونَ إِلَّا قَلِيلاٗۖ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (538,5,'86',4,'An-Nisā’','النِّسَاء',6,9,46,'يَٰأَيُّهَا اَ۬لذِينَ أُوتُواْ اُ۬لْكِتَٰبَ ءَامِنُواْ بِمَا نَزَّلْنَا مُصَدِّقاٗ لِّمَا مَعَكُم مِّن قَبْلِ أَن نَّطْمِسَ وُجُوهاٗ فَنَرُدَّهَا عَلَىٰ أَدْبَٰرِهَا أَوْ نَلْعَنَهُمْ كَمَا لَعَنَّا أَصْحَٰبَ اَ۬لسَّبْتِۖ وَكَانَ أَمْرُ اُ۬للَّهِ مَفْعُولاًۖ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (539,5,'86',4,'An-Nisā’','النِّسَاء',9,10,47,'إِنَّ اَ۬للَّهَ لَا يَغْفِرُ أَنْ يُّشْرَكَ بِهِۦۖ وَيَغْفِرُ مَا دُونَ ذَٰلِكَ لِمَنْ يَّشَآءُۖ وَمَنْ يُّشْرِكْ بِاللَّهِ فَقَدِ اِ۪فْتَرَىٰ إِثْماً عَظِيماًۖ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (540,5,'86',4,'An-Nisā’','النِّسَاء',11,12,48,'أَلَمْ تَرَ إِلَى اَ۬لذِينَ يُزَكُّونَ أَنفُسَهُمۖ بَلِ اِ۬للَّهُ يُزَكِّے مَنْ يَّشَآءُۖ وَلَا يُظْلَمُونَ فَتِيلاًۖ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (541,5,'86',4,'An-Nisā’','النِّسَاء',12,13,49,'اُ۟نظُرْ كَيْفَ يَفْتَرُونَ عَلَى اَ۬للَّهِ اِ۬لْكَذِبَ وَكَفَىٰ بِهِۦ إِثْماٗ مُّبِيناًۖ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (542,5,'86',4,'An-Nisā’','النِّسَاء',13,15,50,'أَلَمْ تَرَ إِلَى اَ۬لذِينَ أُوتُواْ نَصِيباٗ مِّنَ اَ۬لْكِتَٰبِ يُؤْمِنُونَ بِالْجِبْتِ وَالطَّٰغُوتِ وَيَقُولُونَ لِلذِينَ كَفَرُواْ هَٰؤُلَآءِ اَ۬هْدَىٰ مِنَ اَ۬لذِينَ ءَامَنُواْ سَبِيلاًۖ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (543,5,'87',4,'An-Nisā’','النِّسَاء',1,1,51,'أُوْلَٰٓئِكَ اَ۬لذِينَ لَعَنَهُمُ اُ۬للَّهُۖ وَمَنْ يَّلْعَنِ اِ۬للَّهُ فَلَن تَجِدَ لَهُۥ نَصِيراًۖ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (544,5,'87',4,'An-Nisā’','النِّسَاء',2,2,52,'أَمْ لَهُمْ نَصِيبٞ مِّنَ اَ۬لْمُلْكِ فَإِذاٗ لَّا يُؤْتُونَ اَ۬لنَّاسَ نَقِيراًۖ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (545,5,'87',4,'An-Nisā’','النِّسَاء',2,4,53,'أَمْ يَحْسُدُونَ اَ۬لنَّاسَ عَلَىٰ مَا ءَاتَيٰهُمُ اُ۬للَّهُ مِن فَضْلِهِۦۖ فَقَدْ ءَاتَيْنَا ءَالَ إِبْرَٰهِيمَ اَ۬لْكِتَٰبَ وَالْحِكْمَةَ وَءَاتَيْنَٰهُم مُّلْكاً عَظِيماٗۖ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (546,5,'87',4,'An-Nisā’','النِّسَاء',5,5,54,'فَمِنْهُم مَّنْ ءَامَنَ بِهِۦ وَمِنْهُم مَّن صَدَّ عَنْهُۖ وَكَفَىٰ بِجَهَنَّمَ سَعِيراًۖ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (547,5,'87',4,'An-Nisā’','النِّسَاء',6,8,55,'إِنَّ اَ۬لذِينَ كَفَرُواْ بِـَٔايَٰتِنَا سَوْفَ نُصْلِيهِمْ نَاراٗۖ كُلَّمَا نَضِجَتْ جُلُودُهُم بَدَّلْنَٰهُمْ جُلُوداً غَيْرَهَا لِيَذُوقُواْ اُ۬لْعَذَابَۖ إِنَّ اَ۬للَّهَ كَانَ عَزِيزاً حَكِيماٗۖ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (548,5,'87',4,'An-Nisā’','النِّسَاء',8,10,56,'وَالذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ سَنُدْخِلُهُمْ جَنَّٰتٖ تَجْرِے مِن تَحْتِهَا اَ۬لْأَنْهَٰرُ خَٰلِدِينَ فِيهَا أَبَداٗۖ لَّهُمْ فِيهَا أَزْوَٰجٞ مُّطَهَّرَةٞۖ وَنُدْخِلُهُمْ ظِلّاٗ ظَلِيلاًۖ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (549,5,'87',4,'An-Nisā’','النِّسَاء',10,13,57,'۞ إِنَّ اَ۬للَّهَ يَأْمُرُكُمْ أَن تُؤَدُّواْ اُ۬لْأَمَٰنَٰتِ إِلَىٰ أَهْلِهَا وَإِذَا حَكَمْتُم بَيْنَ اَ۬لنَّاسِ أَن تَحْكُمُواْ بِالْعَدْلِۖ إِنَّ اَ۬للَّهَ نِع۪مَّا يَعِظُكُم بِهِۦۖ إِنَّ اَ۬للَّهَ كَانَ سَمِيعاَۢ بَصِيراٗۖ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (550,5,'87',4,'An-Nisā’','النِّسَاء',13,15,58,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ أَطِيعُواْ اُ۬للَّهَ وَأَطِيعُواْ اُ۬لرَّسُولَ وَأُوْلِے اِ۬لْأَمْرِ مِنكُمْۖ فَإِن تَنَٰزَعْتُمْ فِے شَےْءٖ فَرُدُّوهُ إِلَى اَ۬للَّهِ وَالرَّسُولِ إِن كُنتُمْ تُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ اِ۬لْأٓخِرِۖ ذَٰلِكَ خَيْرٞ وَأَحْسَنُ تَأْوِيلاًۖ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (551,5,'88',4,'An-Nisā’','النِّسَاء',1,4,59,'أَلَمْ تَرَ إِلَى اَ۬لذِينَ يَزْعُمُونَ أَنَّهُمْ ءَامَنُواْ بِمَا أُنزِلَ إِلَيْكَ وَمَا أُنزِلَ مِن قَبْلِكَ يُرِيدُونَ أَنْ يَّتَحَاكَمُواْ إِلَى اَ۬لطَّٰغُوتِ وَقَدْ أُمِرُواْ أَنْ يَّكْفُرُواْ بِهِۦۖ وَيُرِيدُ اُ۬لشَّيْطَٰنُ أَنْ يُّضِلَّهُمْ ضَلَٰلاَۢ بَعِيداٗۖ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (552,5,'88',4,'An-Nisā’','النِّسَاء',4,6,60,'وَإِذَا قِيلَ لَهُمْ تَعَالَوْاْ إِلَىٰ مَا أَنزَلَ اَ۬للَّهُ وَإِلَى اَ۬لرَّسُولِ رَأَيْتَ اَ۬لْمُنَٰفِقِينَ يَصُدُّونَ عَنكَ صُدُوداٗۖ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (553,5,'88',4,'An-Nisā’','النِّسَاء',6,8,61,'فَكَيْفَ إِذَا أَصَٰبَتْهُم مُّصِيبَةُۢ بِمَا قَدَّمَتْ أَيْدِيهِمْ ثُمَّ جَآءُوكَ يَحْلِفُونَ بِاللَّهِ إِنْ أَرَدْنَا إِلَّا إِحْسَٰناٗ وَتَوْفِيقاًۖ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (554,5,'88',4,'An-Nisā’','النِّسَاء',8,10,62,'أُوْلَٰٓئِكَ اَ۬لذِينَ يَعْلَمُ اُ۬للَّهُ مَا فِے قُلُوبِهِمْ فَأَعْرِضْ عَنْهُمْ وَعِظْهُمْ وَقُل لَّهُمْ فِے أَنفُسِهِمْ قَوْلاَۢ بَلِيغاٗۖ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (555,5,'88',4,'An-Nisā’','النِّسَاء',10,13,63,'وَمَا أَرْسَلْنَا مِن رَّسُولٍ إِلَّا لِيُطَاعَ بِإِذْنِ اِ۬للَّهِۖ وَلَوْ أَنَّهُمْ إِذ ظَّلَمُواْ أَنفُسَهُمْ جَآءُوكَ فَاسْتَغْفَرُواْ اُ۬للَّهَ وَاسْتَغْفَرَ لَهُمُ اُ۬لرَّسُولُ لَوَجَدُواْ اُ۬للَّهَ تَوَّاباٗ رَّحِيماٗۖ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (556,5,'88',4,'An-Nisā’','النِّسَاء',13,15,64,'۞ فَلَا وَرَبِّكَ لَا يُؤْمِنُونَ حَتَّىٰ يُحَكِّمُوكَ فِيمَا شَجَرَ بَيْنَهُمْ ثُمَّ لَا يَجِدُواْ فِے أَنفُسِهِمْ حَرَجاٗ مِّمَّا قَضَيْتَ وَيُسَلِّمُواْ تَسْلِيماٗۖ ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (557,5,'89',4,'An-Nisā’','النِّسَاء',1,3,65,'وَلَوْ أَنَّا كَتَبْنَا عَلَيْهِمْ أَنُ اُ۟قْتُلُواْ أَنفُسَكُمْ أَوُ اُ۟خْرُجُواْ مِن دِيَٰرِكُم مَّا فَعَلُوهُ إِلَّا قَلِيلٞ مِّنْهُمْۖ وَلَوْ أَنَّهُمْ فَعَلُواْ مَا يُوعَظُونَ بِهِۦ لَكَانَ خَيْراٗ لَّهُمْ وَأَشَدَّ تَثْبِيتاٗۖ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (558,5,'89',4,'An-Nisā’','النِّسَاء',3,4,66,'وَإِذاٗ لَّأٓتَيْنَٰهُم مِّن لَّدُنَّا أَجْراً عَظِيماٗ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (559,5,'89',4,'An-Nisā’','النِّسَاء',4,4,67,'وَلَهَدَيْنَٰهُمْ صِرَٰطاٗ مُّسْتَقِيماٗۖ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (560,5,'89',4,'An-Nisā’','النِّسَاء',5,7,68,'وَمَنْ يُّطِعِ اِ۬للَّهَ وَالرَّسُولَ فَأُوْلَٰٓئِكَ مَعَ اَ۬لذِينَ أَنْعَمَ اَ۬للَّهُ عَلَيْهِم مِّنَ اَ۬لنَّبِيٓـِٕۧنَ وَالصِّدِّيقِينَ وَالشُّهَدَآءِ وَالصَّٰلِحِينَۖ وَحَسُنَ أُوْلَٰٓئِكَ رَفِيقاٗۖ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (561,5,'89',4,'An-Nisā’','النِّسَاء',7,8,69,'ذَٰلِكَ اَ۬لْفَضْلُ مِنَ اَ۬للَّهِۖ وَكَفَىٰ بِاللَّهِ عَلِيماٗۖ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (562,5,'89',4,'An-Nisā’','النِّسَاء',8,9,70,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ خُذُواْ حِذْرَكُمْ فَانفِرُواْ ثُبَاتٍ أَوِ اِ۪نفِرُواْ جَمِيعاٗۖ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (563,5,'89',4,'An-Nisā’','النِّسَاء',9,11,71,'وَإِنَّ مِنكُمْ لَمَن لَّيُبَطِّئَنَّۖ فَإِنْ أَصَٰبَتْكُم مُّصِيبَةٞ قَالَ قَدْ أَنْعَمَ اَ۬للَّهُ عَلَيَّ إِذْ لَمْ أَكُن مَّعَهُمْ شَهِيداٗۖ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (564,5,'89',4,'An-Nisā’','النِّسَاء',11,13,72,'وَلَئِنْ أَصَٰبَكُمْ فَضْلٞ مِّنَ اَ۬للَّهِ لَيَقُولَنَّ كَأَن لَّمْ يَكُنۢ بَيْنَكُمْ وَبَيْنَهُۥ مَوَدَّةٞ يَٰلَيْتَنِے كُنتُ مَعَهُمْ فَأَفُوزَ فَوْزاً عَظِيماٗۖ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (565,5,'89',4,'An-Nisā’','النِّسَاء',13,15,73,'۞ فَلْيُقَٰتِلْ فِے سَبِيلِ اِ۬للَّهِ اِ۬لذِينَ يَشْرُونَ اَ۬لْحَيَوٰةَ اَ۬لدُّنْيَا بِالْأٓخِرَةِۖ وَمَنْ يُّقَٰتِلْ فِے سَبِيلِ اِ۬للَّهِ فَيُقْتَلْ أَوْ يَغْلِبْ فَسَوْفَ نُؤْتِيهِ أَجْراً عَظِيماٗۖ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (566,5,'90',4,'An-Nisā’','النِّسَاء',1,4,74,'وَمَا لَكُمْ لَا تُقَٰتِلُونَ فِے سَبِيلِ اِ۬للَّهِ وَالْمُسْتَضْعَفِينَ مِنَ اَ۬لرِّجَالِ وَالنِّسَآءِ وَالْوِلْدَٰنِ اِ۬لذِينَ يَقُولُونَ رَبَّنَا أَخْرِجْنَا مِنْ هَٰذِهِ اِ۬لْقَرْيَةِ اِ۬لظَّالِمِ أَهْلُهَا وَاجْعَل لَّنَا مِن لَّدُنكَ وَلِيّاٗ وَاجْعَل لَّنَا مِن لَّدُنكَ نَصِيراًۖ ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (567,5,'90',4,'An-Nisā’','النِّسَاء',4,6,75,'اِ۬لذِينَ ءَامَنُواْ يُقَٰتِلُونَ فِے سَبِيلِ اِ۬للَّهِۖ وَالذِينَ كَفَرُواْ يُقَٰتِلُونَ فِے سَبِيلِ اِ۬لطَّٰغُوتِۖ فَقَٰتِلُواْ أَوْلِيَآءَ اَ۬لشَّيْطَٰنِۖ إِنَّ كَيْدَ اَ۬لشَّيْطَٰنِ كَانَ ضَعِيفاًۖ ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (568,5,'90',4,'An-Nisā’','النِّسَاء',6,10,76,'أَلَمْ تَرَ إِلَى اَ۬لذِينَ قِيلَ لَهُمْ كُفُّواْ أَيْدِيَكُمْ وَأَقِيمُواْ اُ۬لصَّلَوٰةَ وَءَاتُواْ اُ۬لزَّكَوٰةَۖ فَلَمَّا كُتِبَ عَلَيْهِمُ اُ۬لْقِتَالُ إِذَا فَرِيقٞ مِّنْهُمْ يَخْشَوْنَ اَ۬لنَّاسَ كَخَشْيَةِ اِ۬للَّهِ أَوْ أَشَدَّ خَشْيَةٗۖ وَقَالُواْ رَبَّنَا لِمَ كَتَبْتَ عَلَيْنَا اَ۬لْقِتَالَ لَوْلَا أَخَّرْتَنَا إِلَىٰ أَجَلٖ قَرِيبٖۖ قُلْ مَتَٰعُ اُ۬لدُّنْيَا قَلِيلٞۖ وَالْأٓخِرَةُ خَيْرٞ لِّمَنِ اِ۪تَّقَىٰۖ وَلَا تُظْلَمُونَ فَتِيلاًۖ ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (569,5,'90',4,'An-Nisā’','النِّسَاء',10,14,77,'أَيْنَمَا تَكُونُواْ يُدْرِككُّمُ اُ۬لْمَوْتُ وَلَوْ كُنتُمْ فِے بُرُوجٖ مُّشَيَّدَةٖۖ وَإِن تُصِبْهُمْ حَسَنَةٞ يَقُولُواْ هَٰذِهِۦ مِنْ عِندِ اِ۬للَّهِۖ وَإِن تُصِبْهُمْ سَيِّئَةٞ يَقُولُواْ هَٰذِهِۦ مِنْ عِندِكَۖ قُلْ كُلّٞ مِّنْ عِندِ اِ۬للَّهِۖ فَمَالِ هَٰؤُلَآءِ اِ۬لْقَوْمِ لَا يَكَادُونَ يَفْقَهُونَ حَدِيثاٗۖ ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (570,5,'90',4,'An-Nisā’','النِّسَاء',14,15,78,'۞ مَّا أَصَابَكَ مِنْ حَسَنَةٖ فَمِنَ اَ۬للَّهِۖ وَمَا أَصَابَكَ مِن سَيِّئَةٖ فَمِن نَّفْسِكَۖ وَأَرْسَلْنَٰكَ لِلنَّاسِ رَسُولاٗۖ وَكَفَىٰ بِاللَّهِ شَهِيداٗۖ ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (571,5,'91',4,'An-Nisā’','النِّسَاء',1,2,79,'مَّنْ يُّطِعِ اِ۬لرَّسُولَ فَقَدْ أَطَاعَ اَ۬للَّهَۖ وَمَن تَوَلَّىٰ فَمَا أَرْسَلْنَٰكَ عَلَيْهِمْ حَفِيظاٗۖ ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (572,5,'91',4,'An-Nisā’','النِّسَاء',2,4,80,'وَيَقُولُونَ طَاعَةٞۖ فَإِذَا بَرَزُواْ مِنْ عِندِكَ بَيَّتَ طَآئِفَةٞ مِّنْهُمْ غَيْرَ اَ۬لذِے تَقُولُۖ وَاللَّهُ يَكْتُبُ مَا يُبَيِّتُونَۖ فَأَعْرِضْ عَنْهُمْ وَتَوَكَّلْ عَلَى اَ۬للَّهِۖ وَكَفَىٰ بِاللَّهِ وَكِيلاًۖ ٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (573,5,'91',4,'An-Nisā’','النِّسَاء',5,6,81,'أَفَلَا يَتَدَبَّرُونَ اَ۬لْقُرْءَانَۖ وَلَوْ كَانَ مِنْ عِندِ غَيْرِ اِ۬للَّهِ لَوَجَدُواْ فِيهِ اِ۪خْتِلَٰفاٗ كَثِيراٗۖ ٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (574,5,'91',4,'An-Nisā’','النِّسَاء',6,9,82,'وَإِذَا جَآءَهُمْ أَمْرٞ مِّنَ اَ۬لْأَمْنِ أَوِ اِ۬لْخَوْفِ أَذَاعُواْ بِهِۦۖ وَلَوْ رَدُّوهُ إِلَى اَ۬لرَّسُولِ وَإِلَىٰ أُوْلِے اِ۬لْأَمْرِ مِنْهُمْ لَعَلِمَهُ اُ۬لذِينَ يَسْتَنۢبِطُونَهُۥ مِنْهُمْۖ وَلَوْلَا فَضْلُ اُ۬للَّهِ عَلَيْكُمْ وَرَحْمَتُهُۥ لَاتَّبَعْتُمُ اُ۬لشَّيْطَٰنَ إِلَّا قَلِيلاٗۖ ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (575,5,'91',4,'An-Nisā’','النِّسَاء',10,12,83,'فَقَٰتِلْ فِے سَبِيلِ اِ۬للَّهِۖ لَا تُكَلَّفُ إِلَّا نَفْسَكَۖ وَحَرِّضِ اِ۬لْمُؤْمِنِينَۖ عَسَى اَ۬للَّهُ أَنْ يَّكُفَّ بَأْسَ اَ۬لذِينَ كَفَرُواْۖ وَاللَّهُ أَشَدُّ بَأْساٗ وَأَشَدُّ تَنكِيلاٗۖ ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (576,5,'91',4,'An-Nisā’','النِّسَاء',12,14,84,'مَّنْ يَّشْفَعْ شَفَٰعَةً حَسَنَةٗ يَكُن لَّهُۥ نَصِيبٞ مِّنْهَاۖ وَمَنْ يَّشْفَعْ شَفَٰعَةٗ سَيِّئَةٗ يَكُن لَّهُۥ كِفْلٞ مِّنْهَاۖ وَكَانَ اَ۬للَّهُ عَلَىٰ كُلِّ شَےْءٖ مُّقِيتاٗۖ ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (577,5,'91',4,'An-Nisā’','النِّسَاء',14,15,85,'وَإِذَا حُيِّيتُم بِتَحِيَّةٖ فَحَيُّواْ بِأَحْسَنَ مِنْهَا أَوْ رُدُّوهَاۖ إِنَّ اَ۬للَّهَ كَانَ عَلَىٰ كُلِّ شَےْءٍ حَسِيباًۖ ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (578,5,'92',4,'An-Nisā’','النِّسَاء',1,2,86,'۞ اِ۬للَّهُ لَا إِلَٰهَ إِلَّا هُوَۖ لَيَجْمَعَنَّكُمْ إِلَىٰ يَوْمِ اِ۬لْقِيَٰمَةِ لَا رَيْبَ فِيهِۖ وَمَنْ أَصْدَقُ مِنَ اَ۬للَّهِ حَدِيثاٗۖ ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (579,5,'92',4,'An-Nisā’','النِّسَاء',2,4,87,'فَمَا لَكُمْ فِے اِ۬لْمُنَٰفِقِينَ فِئَتَيْنِ وَاللَّهُ أَرْكَسَهُم بِمَا كَسَبُواْۖ أَتُرِيدُونَ أَن تَهْدُواْ مَنْ أَضَلَّ اَ۬للَّهُۖ وَمَنْ يُّضْلِلِ اِ۬للَّهُ فَلَن تَجِدَ لَهُۥ سَبِيلاٗۖ ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (580,5,'92',4,'An-Nisā’','النِّسَاء',4,7,88,'وَدُّواْ لَوْ تَكْفُرُونَ كَمَا كَفَرُواْ فَتَكُونُونَ سَوَآءٗۖ فَلَا تَتَّخِذُواْ مِنْهُمْ أَوْلِيَآءَ حَتَّىٰ يُهَاجِرُواْ فِے سَبِيلِ اِ۬للَّهِۖ فَإِن تَوَلَّوْاْ فَخُذُوهُمْ وَاقْتُلُوهُمْ حَيْثُ وَجَدتُّمُوهُمْ وَلَا تَتَّخِذُواْ مِنْهُمْ وَلِيّاٗ وَلَا نَصِيراً ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (581,5,'92',4,'An-Nisā’','النِّسَاء',7,11,89,'إِلَّا اَ۬لذِينَ يَصِلُونَ إِلَىٰ قَوْمِۢ بَيْنَكُمْ وَبَيْنَهُم مِّيثَٰقٌ أَوْ جَآءُوكُمْۖ حَصِرَتْ صُدُورُهُمْ أَنْ يُّقَٰتِلُوكُمْ أَوْ يُقَٰتِلُواْ قَوْمَهُمْۖ وَلَوْ شَآءَ اَ۬للَّهُ لَسَلَّطَهُمْ عَلَيْكُمْ فَلَقَٰتَلُوكُمْۖ فَإِنِ اِ۪عْتَزَلُوكُمْ فَلَمْ يُقَٰتِلُوكُمْ وَأَلْقَوْاْ إِلَيْكُمُ اُ۬لسَّلَمَ فَمَا جَعَلَ اَ۬للَّهُ لَكُمْ عَلَيْهِمْ سَبِيلاٗۖ ٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (582,5,'92',4,'An-Nisā’','النِّسَاء',12,15,90,'سَتَجِدُونَ ءَاخَرِينَ يُرِيدُونَ أَنْ يَّأْمَنُوكُمْ وَيَأْمَنُواْ قَوْمَهُمْ كُلَّ مَا رُدُّواْ إِلَى اَ۬لْفِتْنَةِ أُرْكِسُواْ فِيهَاۖ فَإِن لَّمْ يَعْتَزِلُوكُمْ وَيُلْقُواْ إِلَيْكُمُ اُ۬لسَّلَمَ وَيَكُفُّواْ أَيْدِيَهُمْ فَخُذُوهُمْ وَاقْتُلُوهُمْ حَيْثُ ثَقِفْتُمُوهُمْۖ وَأُوْلَٰٓئِكُمْ جَعَلْنَا لَكُمْ عَلَيْهِمْ سُلْطَٰناٗ مُّبِيناٗۖ ٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (583,5,'93',4,'An-Nisā’','النِّسَاء',1,8,91,'وَمَا كَانَ لِمُؤْمِنٍ أَنْ يَّقْتُلَ مُؤْمِناًۖ إِلَّا خَطَـٔاٗۖ وَمَن قَتَلَ مُؤْمِناً خَطَـٔاٗ فَتَحْرِيرُ رَقَبَةٖ مُّؤْمِنَةٖ وَدِيَةٞ مُّسَلَّمَةٌ إِلَىٰ أَهْلِهِۦ إِلَّا أَنْ يَّصَّدَّقُواْۖ فَإِن كَانَ مِن قَوْمٍ عَدُوّٖ لَّكُمْ وَهْوَ مُؤْمِنٞ فَتَحْرِيرُ رَقَبَةٖ مُّؤْمِنَةٖۖ وَإِن كَانَ مِن قَوْمِۢ بَيْنَكُمْ وَبَيْنَهُم مِّيثَٰقٞ فَدِيَةٞ مُّسَلَّمَةٌ إِلَىٰ أَهْلِهِۦ وَتَحْرِيرُ رَقَبَةٖ مُّؤْمِنَةٖۖ ۞ فَمَن لَّمْ يَجِدْ فَصِيَامُ شَهْرَيْنِ مُتَتَابِعَيْنِ تَوْبَةٗ مِّنَ اَ۬للَّهِۖ وَكَانَ اَ۬للَّهُ عَلِيماً حَكِيماٗۖ ٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (584,5,'93',4,'An-Nisā’','النِّسَاء',8,10,92,'وَمَنْ يَّقْتُلْ مُؤْمِناٗ مُّتَعَمِّداٗ فَجَزَآؤُهُۥ جَهَنَّمُ خَٰلِداٗ فِيهَا وَغَضِبَ اَ۬للَّهُ عَلَيْهِ وَلَعَنَهُۥ وَأَعَدَّ لَهُۥ عَذَاباً عَظِيماٗۖ ٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (585,5,'93',4,'An-Nisā’','النِّسَاء',10,15,93,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ إِذَا ضَرَبْتُمْ فِے سَبِيلِ اِ۬للَّهِ فَتَبَيَّنُواْۖ وَلَا تَقُولُواْ لِمَنْ أَلْقَىٰ إِلَيْكُمُ اُ۬لسَّلَمَ لَسْتَ مُؤْمِناٗ تَبْتَغُونَ عَرَضَ اَ۬لْحَيَوٰةِ اِ۬لدُّنْيَا فَعِندَ اَ۬للَّهِ مَغَانِمُ كَثِيرَةٞۖ كَذَٰلِكَ كُنتُم مِّن قَبْلُ فَمَنَّ اَ۬للَّهُ عَلَيْكُمْۖ فَتَبَيَّنُواْۖ إِنَّ اَ۬للَّهَ كَانَ بِمَا تَعْمَلُونَ خَبِيراٗۖ ٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (586,5,'94',4,'An-Nisā’','النِّسَاء',1,4,94,'لَّا يَسْتَوِے اِ۬لْقَٰعِدُونَ مِنَ اَ۬لْمُؤْمِنِينَ غَيْرَ أُوْلِے اِ۬لضَّرَرِ وَالْمُجَٰهِدُونَ فِے سَبِيلِ اِ۬للَّهِ بِأَمْوَٰلِهِمْ وَأَنفُسِهِمْۖ فَضَّلَ اَ۬للَّهُ اُ۬لْمُجَٰهِدِينَ بِأَمْوَٰلِهِمْ وَأَنفُسِهِمْ عَلَى اَ۬لْقَٰعِدِينَ دَرَجَةٗۖ وَكُلّاٗ وَعَدَ اَ۬للَّهُ اُ۬لْحُسْنَىٰۖ وَفَضَّلَ اَ۬للَّهُ اُ۬لْمُجَٰهِدِينَ عَلَى اَ۬لْقَٰعِدِينَ أَجْراً عَظِيماٗ ٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (587,5,'94',4,'An-Nisā’','النِّسَاء',4,5,95,'دَرَجَٰتٖ مِّنْهُ وَمَغْفِرَةٗ وَرَحْمَةٗۖ وَكَانَ اَ۬للَّهُ غَفُوراٗ رَّحِيماًۖ ٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (588,5,'94',4,'An-Nisā’','النِّسَاء',5,8,96,'إِنَّ اَ۬لذِينَ تَوَفَّيٰهُمُ اُ۬لْمَلَٰٓئِكَةُ ظَالِمِے أَنفُسِهِمْ قَالُواْ فِيمَ كُنتُمْۖ قَالُواْ كُنَّا مُسْتَضْعَفِينَ فِے اِ۬لْأَرْضِۖ قَالُواْ أَلَمْ تَكُنْ أَرْضُ اُ۬للَّهِ وَٰسِعَةٗ فَتُهَاجِرُواْ فِيهَاۖ فَأُوْلَٰٓئِكَ مَأْوَيٰهُمْ جَهَنَّمُ وَسَآءَتْ مَصِيراً ٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (589,5,'94',4,'An-Nisā’','النِّسَاء',8,9,97,'إِلَّا اَ۬لْمُسْتَضْعَفِينَ مِنَ اَ۬لرِّجَالِ وَالنِّسَآءِ وَالْوِلْدَٰنِ لَا يَسْتَطِيعُونَ حِيلَةٗ وَلَا يَهْتَدُونَ سَبِيلاٗۖ ٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (590,5,'94',4,'An-Nisā’','النِّسَاء',9,10,98,'فَأُوْلَٰٓئِكَ عَسَى اَ۬للَّهُ أَنْ يَّعْفُوَ عَنْهُمْۖ وَكَانَ اَ۬للَّهُ عَفُوّاً غَفُوراٗۖ ٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (591,5,'94',4,'An-Nisā’','النِّسَاء',10,13,99,'۞ وَمَنْ يُّهَاجِرْ فِے سَبِيلِ اِ۬للَّهِ يَجِدْ فِے اِ۬لْأَرْضِ مُرَٰغَماٗ كَثِيراٗ وَسَعَةٗۖ وَمَنْ يَّخْرُجْ مِنۢ بَيْتِهِۦ مُهَاجِراً إِلَى اَ۬للَّهِ وَرَسُولِهِۦ ثُمَّ يُدْرِكْهُ اُ۬لْمَوْتُ فَقَدْ وَقَعَ أَجْرُهُۥ عَلَى اَ۬للَّهِۖ وَكَانَ اَ۬للَّهُ غَفُوراٗ رَّحِيماٗۖ ٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (592,5,'94',4,'An-Nisā’','النِّسَاء',13,15,100,'وَإِذَا ضَرَبْتُمْ فِے اِ۬لْأَرْضِ فَلَيْسَ عَلَيْكُمْ جُنَاحٌ أَن تَقْصُرُواْ مِنَ اَ۬لصَّلَوٰةِ إِنْ خِفْتُمْ أَنْ يَّفْتِنَكُمُ اُ۬لذِينَ كَفَرُواْۖ إِنَّ اَ۬لْكَٰفِرِينَ كَانُواْ لَكُمْ عَدُوّاٗ مُّبِيناٗۖ ١٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (593,5,'95',4,'An-Nisā’','النِّسَاء',1,8,101,'وَإِذَا كُنتَ فِيهِمْ فَأَقَمْتَ لَهُمُ اُ۬لصَّلَوٰةَ فَلْتَقُمْ طَآئِفَةٞ مِّنْهُم مَّعَكَ وَلْيَأْخُذُواْ أَسْلِحَتَهُمْۖ فَإِذَا سَجَدُواْ فَلْيَكُونُواْ مِنْ وَّرَآئِكُمْۖ وَلْتَأْتِ طَآئِفَةٌ أُخْرَىٰ لَمْ يُصَلُّواْ فَلْيُصَلُّواْ مَعَكَ وَلْيَأْخُذُواْ حِذْرَهُمْ وَأَسْلِحَتَهُمْۖ وَدَّ اَ۬لذِينَ كَفَرُواْ لَوْ تَغْفُلُونَ عَنْ أَسْلِحَتِكُمْ وَأَمْتِعَتِكُمْ فَيَمِيلُونَ عَلَيْكُم مَّيْلَةٗ وَٰحِدَةٗۖ وَلَا جُنَاحَ عَلَيْكُمْ إِن كَانَ بِكُمْ أَذىٗ مِّن مَّطَرٍ أَوْ كُنتُم مَّرْضَىٰ أَن تَضَعُواْ أَسْلِحَتَكُمْۖ وَخُذُواْ حِذْرَكُمْۖ إِنَّ اَ۬للَّهَ أَعَدَّ لِلْكَٰفِرِينَ عَذَاباٗ مُّهِيناٗۖ ١٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (594,5,'95',4,'An-Nisā’','النِّسَاء',9,11,102,'فَإِذَا قَضَيْتُمُ اُ۬لصَّلَوٰةَ فَاذْكُرُواْ اُ۬للَّهَ قِيَٰماٗ وَقُعُوداٗ وَعَلَىٰ جُنُوبِكُمْۖ فَإِذَا اَ۪طْمَأْنَنتُمْ فَأَقِيمُواْ اُ۬لصَّلَوٰةَۖ إِنَّ اَ۬لصَّلَوٰةَ كَانَتْ عَلَى اَ۬لْمُؤْمِنِينَ كِتَٰباٗ مَّوْقُوتاٗۖ ١٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (595,5,'95',4,'An-Nisā’','النِّسَاء',11,14,103,'وَلَا تَهِنُواْ فِے اِ۪بْتِغَآءِ اِ۬لْقَوْمِۖ إِن تَكُونُواْ تَأْلَمُونَ فَإِنَّهُمْ يَأْلَمُونَ كَمَا تَأْلَمُونَ وَتَرْجُونَ مِنَ اَ۬للَّهِ مَا لَا يَرْجُونَۖ وَكَانَ اَ۬للَّهُ عَلِيماً حَكِيماًۖ ١٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (596,5,'95',4,'An-Nisā’','النِّسَاء',14,15,104,'۞ إِنَّا أَنزَلْنَا إِلَيْكَ اَ۬لْكِتَٰبَ بِالْحَقِّ لِتَحْكُمَ بَيْنَ اَ۬لنَّاسِ بِمَا أَرَيٰكَ اَ۬للَّهُۖ وَلَا تَكُن لِّلْخَآئِنِينَ خَصِيماٗۖ ١٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (597,5,'96',4,'An-Nisā’','النِّسَاء',1,1,105,'وَاسْتَغْفِرِ اِ۬للَّهَۖ إِنَّ اَ۬للَّهَ كَانَ غَفُوراٗ رَّحِيماٗۖ ١٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (598,5,'96',4,'An-Nisā’','النِّسَاء',1,3,106,'وَلَا تُجَٰدِلْ عَنِ اِ۬لذِينَ يَخْتَانُونَ أَنفُسَهُمْۖ إِنَّ اَ۬للَّهَ لَا يُحِبُّ مَن كَانَ خَوَّاناً أَثِيماٗۖ ١٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (599,5,'96',4,'An-Nisā’','النِّسَاء',3,5,107,'يَسْتَخْفُونَ مِنَ اَ۬لنَّاسِ وَلَا يَسْتَخْفُونَ مِنَ اَ۬للَّهِ وَهْوَ مَعَهُمْ إِذْ يُبَيِّتُونَ مَا لَا يَرْضَىٰ مِنَ اَ۬لْقَوْلِۖ وَكَانَ اَ۬للَّهُ بِمَا يَعْمَلُونَ مُحِيطاًۖ ١٠٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (600,5,'96',4,'An-Nisā’','النِّسَاء',5,7,108,'هَٰا۬نتُمْ هَٰؤُلَآءِ جَٰدَلْتُمْ عَنْهُمْ فِے اِ۬لْحَيَوٰةِ اِ۬لدُّنْيَا فَمَنْ يُّجَٰدِلُ اُ۬للَّهَ عَنْهُمْ يَوْمَ اَ۬لْقِيَٰمَةِ أَم مَّنْ يَّكُونُ عَلَيْهِمْ وَكِيلاٗۖ ١٠٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (601,5,'96',4,'An-Nisā’','النِّسَاء',7,9,109,'وَمَنْ يَّعْمَلْ سُوٓءاً أَوْ يَظْلِمْ نَفْسَهُۥ ثُمَّ يَسْتَغْفِرِ اِ۬للَّهَ يَجِدِ اِ۬للَّهَ غَفُوراٗ رَّحِيماٗۖ ١٠٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (602,5,'96',4,'An-Nisā’','النِّسَاء',9,10,110,'وَمَنْ يَّكْسِبْ إِثْماٗ فَإِنَّمَا يَكْسِبُهُۥ عَلَىٰ نَفْسِهِۦۖ وَكَانَ اَ۬للَّهُ عَلِيماً حَكِيماٗۖ ١١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (603,5,'96',4,'An-Nisā’','النِّسَاء',10,11,111,'وَمَنْ يَّكْسِبْ خَطِيٓـَٔةً أَوْ إِثْماٗ ثُمَّ يَرْمِ بِهِۦ بَرِيٓـٔاٗ فَقَدِ اِ۪حْتَمَلَ بُهْتَٰناٗ وَإِثْماٗ مُّبِيناٗۖ ١١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (604,5,'96',4,'An-Nisā’','النِّسَاء',12,15,112,'وَلَوْلَا فَضْلُ اُ۬للَّهِ عَلَيْكَ وَرَحْمَتُهُۥ لَهَمَّت طَّآئِفَةٞ مِّنْهُمْ أَنْ يُّضِلُّوكَۖ وَمَا يُضِلُّونَ إِلَّا أَنفُسَهُمْ وَمَا يَضُرُّونَكَ مِن شَےْءٖۖ وَأَنزَلَ اَ۬للَّهُ عَلَيْكَ اَ۬لْكِتَٰبَ وَالْحِكْمَةَ وَعَلَّمَكَ مَا لَمْ تَكُن تَعْلَمُۖ وَكَانَ فَضْلُ اُ۬للَّهِ عَلَيْكَ عَظِيماٗۖ ١١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (605,5,'97',4,'An-Nisā’','النِّسَاء',1,3,113,'۞ لَّا خَيْرَ فِے كَثِيرٖ مِّن نَّجْوَيٰهُمْ إِلَّا مَنْ أَمَرَ بِصَدَقَةٍ أَوْ مَعْرُوفٍ أَوْ إِصْلَٰحِۢ بَيْنَ اَ۬لنَّاسِۖ وَمَنْ يَّفْعَلْ ذَٰلِكَ اَ۪بْتِغَآءَ مَرْضَاتِ اِ۬للَّهِ فَسَوْفَ نُؤْتِيهِ أَجْراً عَظِيماٗۖ ١١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (606,5,'97',4,'An-Nisā’','النِّسَاء',3,6,114,'وَمَنْ يُّشَاقِقِ اِ۬لرَّسُولَ مِنۢ بَعْدِ مَا تَبَيَّنَ لَهُ اُ۬لْهُدَىٰ وَيَتَّبِعْ غَيْرَ سَبِيلِ اِ۬لْمُؤْمِنِينَ نُوَلِّهِ مَا تَوَلَّىٰ وَنُصْلِهِ جَهَنَّمَۖ وَسَآءَتْ مَصِيراًۖ ١١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (607,5,'97',4,'An-Nisā’','النِّسَاء',6,8,115,'إِنَّ اَ۬للَّهَ لَا يَغْفِرُ أَنْ يُّشْرَكَ بِهِۦۖ وَيَغْفِرُ مَا دُونَ ذَٰلِكَ لِمَنْ يَّشَآءُۖ وَمَنْ يُّشْرِكْ بِاللَّهِ فَقَدْ ضَلَّ ضَلَٰلاَۢ بَعِيداًۖ ١١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (608,5,'97',4,'An-Nisā’','النِّسَاء',8,9,116,'إِنْ يَّدْعُونَ مِن دُونِهِۦ إِلَّا إِنَٰثاٗ وَإِنْ يَّدْعُونَ إِلَّا شَيْطَٰناٗ مَّرِيداٗ ١١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (609,5,'97',4,'An-Nisā’','النِّسَاء',9,10,117,'لَّعَنَهُ اُ۬للَّهُۖ وَقَالَ لَأَتَّخِذَنَّ مِنْ عِبَادِكَ نَصِيباٗ مَّفْرُوضاٗ ١١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (610,5,'97',4,'An-Nisā’','النِّسَاء',10,13,118,'وَلَأُضِلَّنَّهُمْ وَلَأُمَنِّيَنَّهُمْ وَلَأٓمُرَنَّهُمْ فَلَيُبَتِّكُنَّ ءَاذَانَ اَ۬لْأَنْعَٰمِ وَلَأٓمُرَنَّهُمْ فَلَيُغَيِّرُنَّ خَلْقَ اَ۬للَّهِۖ وَمَنْ يَّتَّخِذِ اِ۬لشَّيْطَٰنَ وَلِيّاٗ مِّن دُونِ اِ۬للَّهِ فَقَدْ خَسِرَ خُسْرَاناٗ مُّبِيناٗۖ ١١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (611,5,'97',4,'An-Nisā’','النِّسَاء',13,14,119,'يَعِدُهُمْ وَيُمَنِّيهِمْۖ وَمَا يَعِدُهُمُ اُ۬لشَّيْطَٰنُ إِلَّا غُرُوراًۖ ١١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (612,5,'97',4,'An-Nisā’','النِّسَاء',14,15,120,'أُوْلَٰٓئِكَ مَأْوَيٰهُمْ جَهَنَّمُۖ وَلَا يَجِدُونَ عَنْهَا مَحِيصاٗۖ ١٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (613,5,'98',4,'An-Nisā’','النِّسَاء',1,3,121,'وَالذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ سَنُدْخِلُهُمْ جَنَّٰتٖ تَجْرِے مِن تَحْتِهَا اَ۬لْأَنْهَٰرُ خَٰلِدِينَ فِيهَا أَبَداٗۖ وَعْدَ اَ۬للَّهِ حَقّاٗۖ وَمَنْ أَصْدَقُ مِنَ اَ۬للَّهِ قِيلاٗۖ ١٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (614,5,'98',4,'An-Nisā’','النِّسَاء',3,5,122,'لَّيْسَ بِأَمَانِيِّكُمْ وَلَا أَمَانِيِّ أَهْلِ اِ۬لْكِتَٰبِۖ مَنْ يَّعْمَلْ سُوٓءاٗ يُجْزَ بِهِۦ وَلَا يَجِدْ لَهُۥ مِن دُونِ اِ۬للَّهِ وَلِيّاٗ وَلَا نَصِيراٗۖ ١٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (615,5,'98',4,'An-Nisā’','النِّسَاء',5,7,123,'۞ وَمَنْ يَّعْمَلْ مِنَ اَ۬لصَّٰلِحَٰتِ مِن ذَكَرٍ أَوْ أُنثَىٰ وَهْوَ مُؤْمِنٞ فَأُوْلَٰٓئِكَ يَدْخُلُونَ اَ۬لْجَنَّةَ وَلَا يُظْلَمُونَ نَقِيراٗۖ ١٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (616,5,'98',4,'An-Nisā’','النِّسَاء',7,9,124,'وَمَنْ أَحْسَنُ دِيناٗ مِّمَّنْ أَسْلَمَ وَجْهَهُۥ لِلهِ وَهْوَ مُحْسِنٞ وَاتَّبَعَ مِلَّةَ إِبْرَٰهِيمَ حَنِيفاٗۖ وَاتَّخَذَ اَ۬للَّهُ إِبْرَٰهِيمَ خَلِيلاٗۖ ١٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (617,5,'98',4,'An-Nisā’','النِّسَاء',9,11,125,'وَلِلهِ مَا فِے اِ۬لسَّمَٰوَٰتِ وَمَا فِے اِ۬لْأَرْضِۖ وَكَانَ اَ۬للَّهُ بِكُلِّ شَےْءٖ مُّحِيطاٗۖ ١٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (618,5,'98',4,'An-Nisā’','النِّسَاء',11,15,126,'وَيَسْتَفْتُونَكَ فِے اِ۬لنِّسَآءِۖ قُلِ اِ۬للَّهُ يُفْتِيكُمْ فِيهِنَّ وَمَا يُتْلَىٰ عَلَيْكُمْ فِے اِ۬لْكِتَٰبِ فِے يَتَٰمَى اَ۬لنِّسَآءِ اِ۬لَّٰتِے لَا تُؤْتُونَهُنَّ مَا كُتِبَ لَهُنَّ وَتَرْغَبُونَ أَن تَنكِحُوهُنَّ وَالْمُسْتَضْعَفِينَ مِنَ اَ۬لْوِلْدَٰنِ وَأَن تَقُومُواْ لِلْيَتَٰمَىٰ بِالْقِسْطِۖ وَمَا تَفْعَلُواْ مِنْ خَيْرٖ فَإِنَّ اَ۬للَّهَ كَانَ بِهِۦ عَلِيماٗۖ ١٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (619,5,'99',4,'An-Nisā’','النِّسَاء',1,4,127,'وَإِنِ اِ۪مْرَأَةٌ خَافَتْ مِنۢ بَعْلِهَا نُشُوزاً أَوْ إِعْرَاضاٗ فَلَا جُنَاحَ عَلَيْهِمَا أَنْ يَّصَّٰلَحَا بَيْنَهُمَا صُلْحاٗۖ وَالصُّلْحُ خَيْرٞۖ وَأُحْضِرَتِ اِ۬لْأَنفُسُ اُ۬لشُّحَّۖ وَإِن تُحْسِنُواْ وَتَتَّقُواْ فَإِنَّ اَ۬للَّهَ كَانَ بِمَا تَعْمَلُونَ خَبِيراٗۖ ١٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (620,5,'99',4,'An-Nisā’','النِّسَاء',4,7,128,'وَلَن تَسْتَطِيعُواْ أَن تَعْدِلُواْ بَيْنَ اَ۬لنِّسَآءِ وَلَوْ حَرَصْتُمْۖ فَلَا تَمِيلُواْ كُلَّ اَ۬لْمَيْلِ فَتَذَرُوهَا كَالْمُعَلَّقَةِۖ وَإِن تُصْلِحُواْ وَتَتَّقُواْ فَإِنَّ اَ۬للَّهَ كَانَ غَفُوراٗ رَّحِيماٗۖ ١٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (621,5,'99',4,'An-Nisā’','النِّسَاء',7,8,129,'۞ وَإِنْ يَّتَفَرَّقَا يُغْنِ اِ۬للَّهُ كُلّاٗ مِّن سَعَتِهِۦۖ وَكَانَ اَ۬للَّهُ وَٰسِعاً حَكِيماٗۖ ١٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (622,5,'99',4,'An-Nisā’','النِّسَاء',8,11,130,'وَلِلهِ مَا فِے اِ۬لسَّمَٰوَٰتِ وَمَا فِے اِ۬لْأَرْضِۖ وَلَقَدْ وَصَّيْنَا اَ۬لذِينَ أُوتُواْ اُ۬لْكِتَٰبَ مِن قَبْلِكُمْ وَإِيَّاكُمْ أَنِ اِ۪تَّقُواْ اُ۬للَّهَۖ وَإِن تَكْفُرُواْ فَإِنَّ لِلهِ مَا فِے اِ۬لسَّمَٰوَٰتِ وَمَا فِے اِ۬لْأَرْضِۖ وَكَانَ اَ۬للَّهُ غَنِيّاً حَمِيداٗۖ ١٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (623,5,'99',4,'An-Nisā’','النِّسَاء',12,12,131,'وَلِلهِ مَا فِے اِ۬لسَّمَٰوَٰتِ وَمَا فِے اِ۬لْأَرْضِۖ وَكَفَىٰ بِاللَّهِ وَكِيلاًۖ ١٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (624,5,'99',4,'An-Nisā’','النِّسَاء',13,14,132,'إِنْ يَّشَأْ يُذْهِبْكُمْ أَيُّهَا اَ۬لنَّاسُ وَيَأْتِ بِـَٔاخَرِينَۖ وَكَانَ اَ۬للَّهُ عَلَىٰ ذَٰلِكَ قَدِيراٗۖ ١٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (625,5,'99',4,'An-Nisā’','النِّسَاء',14,15,133,'مَّن كَانَ يُرِيدُ ثَوَابَ اَ۬لدُّنْيَا فَعِندَ اَ۬للَّهِ ثَوَابُ اُ۬لدُّنْيَا وَالْأٓخِرَةِۖ وَكَانَ اَ۬للَّهُ سَمِيعاَۢ بَصِيراٗۖ ١٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (626,5,'100',4,'An-Nisā’','النِّسَاء',1,4,134,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ كُونُواْ قَوَّٰمِينَ بِالْقِسْطِ شُهَدَآءَ لِلهِ وَلَوْ عَلَىٰ أَنفُسِكُمْ أَوِ اِ۬لْوَٰلِدَيْنِ وَالْأَقْرَبِينَۖ إِنْ يَّكُنْ غَنِيّاً أَوْ فَقِيراٗ فَاللَّهُ أَوْلَىٰ بِهِمَا فَلَا تَتَّبِعُواْ اُ۬لْهَوَىٰ أَن تَعْدِلُواْۖ وَإِن تَلْوُۥاْ أَوْ تُعْرِضُواْ فَإِنَّ اَ۬للَّهَ كَانَ بِمَا تَعْمَلُونَ خَبِيراٗۖ ١٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (627,5,'100',4,'An-Nisā’','النِّسَاء',4,8,135,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ ءَامِنُواْ بِاللَّهِ وَرَسُولِهِۦ وَالْكِتَٰبِ اِ۬لذِے نَزَّلَ عَلَىٰ رَسُولِهِۦ وَالْكِتَٰبِ اِ۬لذِے أَنزَلَ مِن قَبْلُۖ وَمَنْ يَّكْفُرْ بِاللَّهِ وَمَلَٰٓئِكَتِهِۦ وَكُتُبِهِۦ وَرُسُلِهِۦ وَالْيَوْمِ اِ۬لْأٓخِرِ فَقَدْ ضَلَّ ضَلَٰلاَۢ بَعِيداًۖ ١٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (628,5,'100',4,'An-Nisā’','النِّسَاء',8,10,136,'إِنَّ اَ۬لذِينَ ءَامَنُواْ ثُمَّ كَفَرُواْ ثُمَّ ءَامَنُواْ ثُمَّ كَفَرُواْ ثُمَّ اَ۪زْدَادُواْ كُفْراٗ لَّمْ يَكُنِ اِ۬للَّهُ لِيَغْفِرَ لَهُمْ وَلَا لِيَهْدِيَهُمْ سَبِيلاَۢۖ ١٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (629,5,'100',4,'An-Nisā’','النِّسَاء',10,10,137,'بَشِّرِ اِ۬لْمُنَٰفِقِينَ بِأَنَّ لَهُمْ عَذَاباً أَلِيماً ١٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (630,5,'100',4,'An-Nisā’','النِّسَاء',10,12,138,'اِ۬لذِينَ يَتَّخِذُونَ اَ۬لْكَٰفِرِينَ أَوْلِيَآءَ مِن دُونِ اِ۬لْمُؤْمِنِينَۖ أَيَبْتَغُونَ عِندَهُمُ اُ۬لْعِزَّةَ فَإِنَّ اَ۬لْعِزَّةَ لِلهِ جَمِيعاٗۖ ١٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (631,5,'100',4,'An-Nisā’','النِّسَاء',12,15,139,'۞ وَقَدْ نُزِّلَ عَلَيْكُمْ فِے اِ۬لْكِتَٰبِ أَنْ إِذَا سَمِعْتُمْ ءَايَٰتِ اِ۬للَّهِ يُكْفَرُ بِهَا وَيُسْتَهْزَأُ بِهَا فَلَا تَقْعُدُواْ مَعَهُمْ حَتَّىٰ يَخُوضُواْ فِے حَدِيثٍ غَيْرِهِۦۖ إِنَّكُمْ إِذاٗ مِّثْلُهُمْۖ إِنَّ اَ۬للَّهَ جَامِعُ اُ۬لْمُنَٰفِقِينَ وَالْكَٰفِرِينَ فِے جَهَنَّمَ جَمِيعاً ١٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (632,5,'101',4,'An-Nisā’','النِّسَاء',1,5,140,'اِ۬لذِينَ يَتَرَبَّصُونَ بِكُمْ فَإِن كَانَ لَكُمْ فَتْحٞ مِّنَ اَ۬للَّهِ قَالُواْ أَلَمْ نَكُن مَّعَكُمْ وَإِن كَانَ لِلْكَٰفِرِينَ نَصِيبٞ قَالُواْ أَلَمْ نَسْتَحْوِذْ عَلَيْكُمْ وَنَمْنَعْكُم مِّنَ اَ۬لْمُؤْمِنِينَۖ فَاللَّهُ يَحْكُمُ بَيْنَكُمْ يَوْمَ اَ۬لْقِيَٰمَةِۖ وَلَنْ يَّجْعَلَ اَ۬للَّهُ لِلْكَٰفِرِينَ عَلَى اَ۬لْمُؤْمِنِينَ سَبِيلاًۖ ١٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (633,5,'101',4,'An-Nisā’','النِّسَاء',5,7,141,'إِنَّ اَ۬لْمُنَٰفِقِينَ يُخَٰدِعُونَ اَ۬للَّهَ وَهْوَ خَٰدِعُهُمْۖ وَإِذَا قَامُواْ إِلَى اَ۬لصَّلَوٰةِ قَامُواْ كُسَالَىٰ يُرَآءُونَ اَ۬لنَّاسَ وَلَا يَذْكُرُونَ اَ۬للَّهَ إِلَّا قَلِيلاٗ ١٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (634,5,'101',4,'An-Nisā’','النِّسَاء',7,8,142,'مُّذَبْذَبِينَ بَيْنَ ذَٰلِكَ لَا إِلَىٰ هَٰؤُلَآءِ وَلَا إِلَىٰ هَٰؤُلَآءِۖ وَمَنْ يُّضْلِلِ اِ۬للَّهُ فَلَن تَجِدَ لَهُۥ سَبِيلاٗۖ ١٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (635,5,'101',4,'An-Nisā’','النِّسَاء',8,10,143,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ لَا تَتَّخِذُواْ اُ۬لْكَٰفِرِينَ أَوْلِيَآءَ مِن دُونِ اِ۬لْمُؤْمِنِينَۖ أَتُرِيدُونَ أَن تَجْعَلُواْ لِلهِ عَلَيْكُمْ سُلْطَٰناٗ مُّبِيناًۖ ١٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (636,5,'101',4,'An-Nisā’','النِّسَاء',10,11,144,'إِنَّ اَ۬لْمُنَٰفِقِينَ فِے اِ۬لدَّرَكِ اِ۬لْأَسْفَلِ مِنَ اَ۬لنَّارِ وَلَن تَجِدَ لَهُمْ نَصِيراًۖ ١٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (637,5,'101',4,'An-Nisā’','النِّسَاء',12,14,145,'إِلَّا اَ۬لذِينَ تَابُواْ وَأَصْلَحُواْ وَاعْتَصَمُواْ بِاللَّهِ وَأَخْلَصُواْ دِينَهُمْ لِلهِ فَأُوْلَٰٓئِكَ مَعَ اَ۬لْمُؤْمِنِينَۖ وَسَوْفَ يُؤْتِ اِ۬للَّهُ اُ۬لْمُؤْمِنِينَ أَجْراً عَظِيماٗۖ ١٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (638,5,'101',4,'An-Nisā’','النِّسَاء',14,15,146,'مَّا يَفْعَلُ اُ۬للَّهُ بِعَذَابِكُمْ إِن شَكَرْتُمْ وَءَامَنتُمْۖ وَكَانَ اَ۬للَّهُ شَاكِراً عَلِيماٗۖ ١٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (639,6,'102',4,'An-Nisā’','النِّسَاء',1,2,147,'۞ لَّا يُحِبُّ اُ۬للَّهُ اُ۬لْجَهْرَ بِالسُّوٓءِ مِنَ اَ۬لْقَوْلِ إِلَّا مَن ظُلِمَۖ وَكَانَ اَ۬للَّهُ سَمِيعاً عَلِيماًۖ ١٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (640,6,'102',4,'An-Nisā’','النِّسَاء',2,3,148,'إِن تُبْدُواْ خَيْراً أَوْ تُخْفُوهُ أَوْ تَعْفُواْ عَن سُوٓءٖ فَإِنَّ اَ۬للَّهَ كَانَ عَفُوّاٗ قَدِيراًۖ ١٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (641,6,'102',4,'An-Nisā’','النِّسَاء',3,6,149,'إِنَّ اَ۬لذِينَ يَكْفُرُونَ بِاللَّهِ وَرُسُلِهِۦ وَيُرِيدُونَ أَنْ يُّفَرِّقُواْ بَيْنَ اَ۬للَّهِ وَرُسُلِهِۦ وَيَقُولُونَ نُؤْمِنُ بِبَعْضٖ وَنَكْفُرُ بِبَعْضٖ وَيُرِيدُونَ أَنْ يَّتَّخِذُواْ بَيْنَ ذَٰلِكَ سَبِيلاً ١٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (642,6,'102',4,'An-Nisā’','النِّسَاء',6,7,150,'أُوْلَٰٓئِكَ هُمُ اُ۬لْكَٰفِرُونَ حَقّاٗۖ وَأَعْتَدْنَا لِلْكَٰفِرِينَ عَذَاباٗ مُّهِيناٗۖ ١٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (643,6,'102',4,'An-Nisā’','النِّسَاء',7,9,151,'وَالذِينَ ءَامَنُواْ بِاللَّهِ وَرُسُلِهِۦ وَلَمْ يُفَرِّقُواْ بَيْنَ أَحَدٖ مِّنْهُمْ أُوْلَٰٓئِكَ سَوْفَ نُؤْتِيهِمْ أُجُورَهُمْۖ وَكَانَ اَ۬للَّهُ غَفُوراٗ رَّحِيماٗۖ ١٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (644,6,'102',4,'An-Nisā’','النِّسَاء',9,13,152,'يَسْـَٔلُكَ أَهْلُ اُ۬لْكِتَٰبِ أَن تُنَزِّلَ عَلَيْهِمْ كِتَٰباٗ مِّنَ اَ۬لسَّمَآءِۖ فَقَدْ سَأَلُواْ مُوسَىٰ أَكْبَرَ مِن ذَٰلِكَ فَقَالُواْ أَرِنَا اَ۬للَّهَ جَهْرَةٗ فَأَخَذَتْهُمُ اُ۬لصَّٰعِقَةُ بِظُلْمِهِمْۖ ثُمَّ اَ۪تَّخَذُواْ اُ۬لْعِجْلَ مِنۢ بَعْدِ مَا جَآءَتْهُمُ اُ۬لْبَيِّنَٰتُ فَعَفَوْنَا عَن ذَٰلِكَۖ وَءَاتَيْنَا مُوسَىٰ سُلْطَٰناٗ مُّبِيناٗۖ ١٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (645,6,'102',4,'An-Nisā’','النِّسَاء',13,15,153,'وَرَفَعْنَا فَوْقَهُمُ اُ۬لطُّورَ بِمِيثَٰقِهِمْۖ وَقُلْنَا لَهُمُ اُ۟دْخُلُواْ اُ۬لْبَابَ سُجَّداٗۖ وَقُلْنَا لَهُمْ لَا تَع۬دُّواْ فِے اِ۬لسَّبْتِۖ وَأَخَذْنَا مِنْهُم مِّيثَٰقاً غَلِيظاٗۖ ١٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (646,6,'103',4,'An-Nisā’','النِّسَاء',1,3,154,'فَبِمَا نَقْضِهِم مِّيثَٰقَهُمْ وَكُفْرِهِم بِـَٔايَٰتِ اِ۬للَّهِ وَقَتْلِهِمُ اُ۬لْأَنۢبِئَآءَ بِغَيْرِ حَقّٖ وَقَوْلِهِمْ قُلُوبُنَا غُلْفُۢ بَلْ طَبَعَ اَ۬للَّهُ عَلَيْهَا بِكُفْرِهِمْ فَلَا يُؤْمِنُونَ إِلَّا قَلِيلاٗ ١٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (647,6,'103',4,'An-Nisā’','النِّسَاء',3,4,155,'وَبِكُفْرِهِمْ وَقَوْلِهِمْ عَلَىٰ مَرْيَمَ بُهْتَٰناً عَظِيماٗ ١٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (648,6,'103',4,'An-Nisā’','النِّسَاء',4,7,156,'وَقَوْلِهِمْ إِنَّا قَتَلْنَا اَ۬لْمَسِيحَ عِيسَى اَ۪بْنَ مَرْيَمَ رَسُولَ اَ۬للَّهِۖ ۞ وَمَا قَتَلُوهُ وَمَا صَلَبُوهُ وَلَٰكِن شُبِّهَ لَهُمْۖ وَإِنَّ اَ۬لذِينَ اَ۪خْتَلَفُواْ فِيهِ لَفِے شَكّٖ مِّنْهُۖ مَا لَهُم بِهِۦ مِنْ عِلْمٍۖ إِلَّا اَ۪تِّبَاعَ اَ۬لظَّنِّۖ وَمَا قَتَلُوهُ يَقِيناَۢۖ ١٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (649,6,'103',4,'An-Nisā’','النِّسَاء',7,7,157,'بَل رَّفَعَهُ اُ۬للَّهُ إِلَيْهِۖ وَكَانَ اَ۬للَّهُ عَزِيزاً حَكِيماٗۖ ١٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (650,6,'103',4,'An-Nisā’','النِّسَاء',8,9,158,'وَإِن مِّنْ أَهْلِ اِ۬لْكِتَٰبِ إِلَّا لَيُؤْمِنَنَّ بِهِۦ قَبْلَ مَوْتِهِۦ وَيَوْمَ اَ۬لْقِيَٰمَةِ يَكُونُ عَلَيْهِمْ شَهِيداٗۖ ١٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (651,6,'103',4,'An-Nisā’','النِّسَاء',9,11,159,'فَبِظُلْمٖ مِّنَ اَ۬لذِينَ هَادُواْ حَرَّمْنَا عَلَيْهِمْ طَيِّبَٰتٍ أُحِلَّتْ لَهُمْ وَبِصَدِّهِمْ عَن سَبِيلِ اِ۬للَّهِ كَثِيراٗ ١٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (652,6,'103',4,'An-Nisā’','النِّسَاء',11,12,160,'وَأَخْذِهِمُ اُ۬لرِّبَوٰاْ وَقَدْ نُهُواْ عَنْهُ وَأَكْلِهِمْ أَمْوَٰلَ اَ۬لنَّاسِ بِالْبَٰطِلِۖ وَأَعْتَدْنَا لِلْكَٰفِرِينَ مِنْهُمْ عَذَاباً أَلِيماٗۖ ١٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (653,6,'103',4,'An-Nisā’','النِّسَاء',12,15,161,'لَّٰكِنِ اِ۬لرَّٰسِخُونَ فِے اِ۬لْعِلْمِ مِنْهُمْ وَالْمُؤْمِنُونَ يُؤْمِنُونَ بِمَا أُنزِلَ إِلَيْكَ وَمَا أُنزِلَ مِن قَبْلِكَ وَالْمُقِيمِينَ اَ۬لصَّلَوٰةَۖ وَالْمُؤْتُونَ اَ۬لزَّكَوٰةَ وَالْمُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ اِ۬لْأٓخِرِ أُوْلَٰٓئِكَ سَنُؤْتِيهِمْ أَجْراً عَظِيماًۖ ١٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (654,6,'104',4,'An-Nisā’','النِّسَاء',1,4,162,'إِنَّا أَوْحَيْنَا إِلَيْكَ كَمَا أَوْحَيْنَا إِلَىٰ نُوحٖ وَالنَّبِيٓـِٕۧنَ مِنۢ بَعْدِهِۦۖ وَأَوْحَيْنَا إِلَىٰ إِبْرَٰهِيمَ وَإِسْمَٰعِيلَ وَإِسْحَٰقَ وَيَعْقُوبَ وَالْأَسْبَاطِ وَعِيسَىٰ وَأَيُّوبَ وَيُونُسَ وَهَٰرُونَ وَسُلَيْمَٰنَۖ وَءَاتَيْنَا دَاوُۥدَ زَبُوراٗۖ ١٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (655,6,'104',4,'An-Nisā’','النِّسَاء',4,6,163,'وَرُسُلاٗ قَدْ قَصَصْنَٰهُمْ عَلَيْكَ مِن قَبْلُ وَرُسُلاٗ لَّمْ نَقْصُصْهُمْ عَلَيْكَۖ وَكَلَّمَ اَ۬للَّهُ مُوسَىٰ تَكْلِيماٗۖ ١٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (656,6,'104',4,'An-Nisā’','النِّسَاء',6,7,164,'رُّسُلاٗ مُّبَشِّرِينَ وَمُنذِرِينَ لِئَلَّا يَكُونَ لِلنَّاسِ عَلَى اَ۬للَّهِ حُجَّةُۢ بَعْدَ اَ۬لرُّسُلِۖ وَكَانَ اَ۬للَّهُ عَزِيزاً حَكِيماٗۖ ١٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (657,6,'104',4,'An-Nisā’','النِّسَاء',8,9,165,'۞ لَّٰكِنِ اِ۬للَّهُ يَشْهَدُ بِمَا أَنزَلَ إِلَيْكَۖ أَنزَلَهُۥ بِعِلْمِهِۦ وَالْمَلَٰٓئِكَةُ يَشْهَدُونَۖ وَكَفَىٰ بِاللَّهِ شَهِيداًۖ ١٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (658,6,'104',4,'An-Nisā’','النِّسَاء',9,10,166,'إِنَّ اَ۬لذِينَ كَفَرُواْ وَصَدُّواْ عَن سَبِيلِ اِ۬للَّهِ قَدْ ضَلُّواْ ضَلَٰلاَۢ بَعِيداًۖ ١٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (659,6,'104',4,'An-Nisā’','النِّسَاء',10,12,167,'إِنَّ اَ۬لذِينَ كَفَرُواْ وَظَلَمُواْ لَمْ يَكُنِ اِ۬للَّهُ لِيَغْفِرَ لَهُمْ وَلَا لِيَهْدِيَهُمْ طَرِيقاً ١٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (660,6,'104',4,'An-Nisā’','النِّسَاء',12,13,168,'إِلَّا طَرِيقَ جَهَنَّمَ خَٰلِدِينَ فِيهَا أَبَداٗۖ وَكَانَ ذَٰلِكَ عَلَى اَ۬للَّهِ يَسِيراٗۖ ١٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (661,6,'104',4,'An-Nisā’','النِّسَاء',13,15,169,'يَٰأَيُّهَا اَ۬لنَّاسُ قَدْ جَآءَكُمُ اُ۬لرَّسُولُ بِالْحَقِّ مِن رَّبِّكُمْ فَـَٔامِنُواْ خَيْراٗ لَّكُمْۖ وَإِن تَكْفُرُواْ فَإِنَّ لِلهِ مَا فِے اِ۬لسَّمَٰوَٰتِ وَالْأَرْضِۖ وَكَانَ اَ۬للَّهُ عَلِيماً حَكِيماٗۖ ١٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (662,6,'105',4,'An-Nisā’','النِّسَاء',1,6,170,'يَٰأَهْلَ اَ۬لْكِتَٰبِ لَا تَغْلُواْ فِے دِينِكُمْ وَلَا تَقُولُواْ عَلَى اَ۬للَّهِ إِلَّا اَ۬لْحَقَّۖ إِنَّمَا اَ۬لْمَسِيحُ عِيسَى اَ۪بْنُ مَرْيَمَ رَسُولُ اُ۬للَّهِ وَكَلِمَتُهُۥۖ أَلْقَيٰهَا إِلَىٰ مَرْيَمَ وَرُوحٞ مِّنْهُۖ فَـَٔامِنُواْ بِاللَّهِ وَرُسُلِهِۦۖ وَلَا تَقُولُواْ ثَلَٰثَةٌۖ اِ۪نتَهُواْ خَيْراٗ لَّكُمْۖ إِنَّمَا اَ۬للَّهُ إِلَٰهٞ وَٰحِدٞۖ سُبْحَٰنَهُۥ أَنْ يَّكُونَ لَهُۥ وَلَدٞۖ لَّهُۥ مَا فِے اِ۬لسَّمَٰوَٰتِ وَمَا فِے اِ۬لْأَرْضِۖ وَكَفَىٰ بِاللَّهِ وَكِيلاٗۖ ١٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (663,6,'105',4,'An-Nisā’','النِّسَاء',6,9,171,'لَّنْ يَّسْتَنكِفَ اَ۬لْمَسِيحُ أَنْ يَّكُونَ عَبْداٗ لِّلهِ وَلَا اَ۬لْمَلَٰٓئِكَةُ اُ۬لْمُقَرَّبُونَۖ وَمَنْ يَّسْتَنكِفْ عَنْ عِبَادَتِهِۦ وَيَسْتَكْبِرْ فَسَيَحْشُرُهُمْ إِلَيْهِ جَمِيعاٗۖ ١٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (664,6,'105',4,'An-Nisā’','النِّسَاء',9,12,172,'فَأَمَّا اَ۬لذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ فَيُوَفِّيهِمْ أُجُورَهُمْ وَيَزِيدُهُم مِّن فَضْلِهِۦۖ وَأَمَّا اَ۬لذِينَ اَ۪سْتَنكَفُواْ وَاسْتَكْبَرُواْ فَيُعَذِّبُهُمْ عَذَاباً أَلِيماٗ وَلَا يَجِدُونَ لَهُم مِّن دُونِ اِ۬للَّهِ وَلِيّاٗ وَلَا نَصِيراٗۖ ١٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (665,6,'105',4,'An-Nisā’','النِّسَاء',12,13,173,'۞ يَٰأَيُّهَا اَ۬لنَّاسُ قَدْ جَآءَكُم بُرْهَٰنٞ مِّن رَّبِّكُمْ وَأَنزَلْنَا إِلَيْكُمْ نُوراٗ مُّبِيناٗۖ ١٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (666,6,'105',4,'An-Nisā’','النِّسَاء',14,15,174,'فَأَمَّا اَ۬لذِينَ ءَامَنُواْ بِاللَّهِ وَاعْتَصَمُواْ بِهِۦ فَسَيُدْخِلُهُمْ فِے رَحْمَةٖ مِّنْهُ وَفَضْلٖ وَيَهْدِيهِمْ إِلَيْهِ صِرَٰطاٗ مُّسْتَقِيماٗۖ ١٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (667,6,'106',4,'An-Nisā’','النِّسَاء',1,5,175,'يَسْتَفْتُونَكَۖ قُلِ اِ۬للَّهُ يُفْتِيكُمْ فِے اِ۬لْكَلَٰلَةِۖ إِنِ اِ۪مْرُؤٌاْ هَلَكَ لَيْسَ لَهُۥ وَلَدٞ وَلَهُۥ أُخْتٞ فَلَهَا نِصْفُ مَا تَرَكَۖ وَهْوَ يَرِثُهَا إِن لَّمْ يَكُن لَّهَا وَلَدٞۖ فَإِن كَانَتَا اَ۪ثْنَتَيْنِ فَلَهُمَا اَ۬لثُّلُثَٰنِ مِمَّا تَرَكَۖ وَإِن كَانُواْ إِخْوَةٗ رِّجَالاٗ وَنِسَآءٗ فَلِلذَّكَرِ مِثْلُ حَظِّ اِ۬لْأُنثَيَيْنِۖ يُبَيِّنُ اُ۬للَّهُ لَكُمْ أَن تَضِلُّواْۖ وَاللَّهُ بِكُلِّ شَےْءٍ عَلِيمُۢۖ ١٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (668,6,'106',5,'Al-Mā’idah','المَائدة',8,8,1,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ أَوْفُواْ بِالْعُقُودِۖ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (669,6,'106',5,'Al-Mā’idah','المَائدة',8,10,2,'أُحِلَّتْ لَكُم بَهِيمَةُ اُ۬لْأَنْعَٰمِ إِلَّا مَا يُتْلَىٰ عَلَيْكُمْ غَيْرَ مُحِلِّے اِ۬لصَّيْدِ وَأَنتُمْ حُرُمٌۖ إِنَّ اَ۬للَّهَ يَحْكُمُ مَا يُرِيدُۖ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (670,6,'106',5,'Al-Mā’idah','المَائدة',10,15,3,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ لَا تُحِلُّواْ شَعَٰٓئِرَ اَ۬للَّهِ وَلَا اَ۬لشَّهْرَ اَ۬لْحَرَامَ وَلَا اَ۬لْهَدْيَ وَلَا اَ۬لْقَلَٰٓئِدَ وَلَا ءَآمِّينَ اَ۬لْبَيْتَ اَ۬لْحَرَامَ يَبْتَغُونَ فَضْلاٗ مِّن رَّبِّهِمْ وَرِضْوَٰناٗۖ وَإِذَا حَلَلْتُمْ فَاصْطَادُواْۖ وَلَا يَجْرِمَنَّكُمْ شَنَـَٔانُ قَوْمٍ أَن صَدُّوكُمْ عَنِ اِ۬لْمَسْجِدِ اِ۬لْحَرَامِ أَن تَعْتَدُواْۖ وَتَعَاوَنُواْ عَلَى اَ۬لْبِرِّ وَالتَّقْوَىٰۖ وَلَا تَعَاوَنُواْ عَلَى اَ۬لْإِثْمِ وَالْعُدْوَٰنِۖ وَاتَّقُواْ اُ۬للَّهَۖ إِنَّ اَ۬للَّهَ شَدِيدُ اُ۬لْعِقَابِۖ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (671,6,'107',5,'Al-Mā’idah','المَائدة',1,7,4,'۞ حُرِّمَتْ عَلَيْكُمُ اُ۬لْمَيْتَةُ وَالدَّمُ وَلَحْمُ اُ۬لْخِنزِيرِ وَمَا أُهِلَّ لِغَيْرِ اِ۬للَّهِ بِهِۦ وَالْمُنْخَنِقَةُ وَالْمَوْقُوذَةُ وَالْمُتَرَدِّيَةُ وَالنَّطِيحَةُ وَمَا أَكَلَ اَ۬لسَّبُعُ إِلَّا مَا ذَكَّيْتُمْ وَمَا ذُبِحَ عَلَى اَ۬لنُّصُبِ وَأَن تَسْتَقْسِمُواْ بِالْأَزْلَٰمِۖ ذَٰلِكُمْ فِسْقٌۖ اِ۬لْيَوْمَ يَئِسَ اَ۬لذِينَ كَفَرُواْ مِن دِينِكُمْۖ فَلَا تَخْشَوْهُمْ وَاخْشَوْنِۖ اِ۬لْيَوْمَ أَكْمَلْتُ لَكُمْ دِينَكُمْ وَأَتْمَمْتُ عَلَيْكُمْ نِعْمَتِے وَرَضِيتُ لَكُمُ اُ۬لْإِسْلَٰمَ دِيناٗۖ فَمَنُ اُ۟ضْطُرَّ فِے مَخْمَصَةٍ غَيْرَ مُتَجَانِفٖ لِّإِثْمٖ فَإِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٞۖ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (672,6,'107',5,'Al-Mā’idah','المَائدة',8,11,5,'يَسْـَٔلُونَكَ مَاذَا أُحِلَّ لَهُمْۖ قُلْ أُحِلَّ لَكُمُ اُ۬لطَّيِّبَٰتُ وَمَا عَلَّمْتُم مِّنَ اَ۬لْجَوَارِحِ مُكَلِّبِينَ تُعَلِّمُونَهُنَّ مِمَّا عَلَّمَكُمُ اُ۬للَّهُۖ فَكُلُواْ مِمَّا أَمْسَكْنَ عَلَيْكُمْ وَاذْكُرُواْ اُ۪سْمَ اَ۬للَّهِ عَلَيْهِۖ وَاتَّقُواْ اُ۬للَّهَۖ إِنَّ اَ۬للَّهَ سَرِيعُ اُ۬لْحِسَابِۖ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (673,6,'107',5,'Al-Mā’idah','المَائدة',11,15,6,'اِ۬لْيَوْمَ أُحِلَّ لَكُمُ اُ۬لطَّيِّبَٰتُ وَطَعَامُ اُ۬لذِينَ أُوتُواْ اُ۬لْكِتَٰبَ حِلّٞ لَّكُمْ وَطَعَامُكُمْ حِلّٞ لَّهُمْ وَالْمُحْصَنَٰتُ مِنَ اَ۬لْمُؤْمِنَٰتِ وَالْمُحْصَنَٰتُ مِنَ اَ۬لذِينَ أُوتُواْ اُ۬لْكِتَٰبَ مِن قَبْلِكُمْ إِذَا ءَاتَيْتُمُوهُنَّ أُجُورَهُنَّ مُحْصِنِينَ غَيْرَ مُسَٰفِحِينَ وَلَا مُتَّخِذِے أَخْدَانٖۖ وَمَنْ يَّكْفُرْ بِالْإِيمَٰنِ فَقَدْ حَبِطَ عَمَلُهُۥ وَهْوَ فِے اِ۬لْأٓخِرَةِ مِنَ اَ۬لْخَٰسِرِينَۖ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (674,6,'108',5,'Al-Mā’idah','المَائدة',1,8,7,'۞ يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ إِذَا قُمْتُمْ إِلَى اَ۬لصَّلَوٰةِ فَاغْسِلُواْ وُجُوهَكُمْ وَأَيْدِيَكُمْ إِلَى اَ۬لْمَرَافِقِ وَامْسَحُواْ بِرُءُوسِكُمْۖ وَأَرْجُلَكُمْ إِلَى اَ۬لْكَعْبَيْنِۖ وَإِن كُنتُمْ جُنُباٗ فَاطَّهَّرُواْۖ وَإِن كُنتُم مَّرْضَىٰ أَوْ عَلَىٰ سَفَرٍ أَوْ جَا أَحَدٞ مِّنكُم مِّنَ اَ۬لْغَآئِطِ أَوْ لَٰمَسْتُمُ اُ۬لنِّسَآءَ فَلَمْ تَجِدُواْ مَآءٗ فَتَيَمَّمُواْ صَعِيداٗ طَيِّباٗ فَامْسَحُواْ بِوُجُوهِكُمْ وَأَيْدِيكُم مِّنْهُۖ مَا يُرِيدُ اُ۬للَّهُ لِيَجْعَلَ عَلَيْكُم مِّنْ حَرَجٖۖ وَلَٰكِنْ يُّرِيدُ لِيُطَهِّرَكُمْ وَلِيُتِمَّ نِعْمَتَهُۥ عَلَيْكُمْ لَعَلَّكُمْ تَشْكُرُونَۖ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (675,6,'108',5,'Al-Mā’idah','المَائدة',9,11,8,'وَاذْكُرُواْ نِعْمَةَ اَ۬للَّهِ عَلَيْكُمْ وَمِيثَٰقَهُ اُ۬لذِے وَاثَقَكُم بِهِۦ إِذْ قُلْتُمْ سَمِعْنَا وَأَطَعْنَاۖ وَاتَّقُواْ اُ۬للَّهَۖ إِنَّ اَ۬للَّهَ عَلِيمُۢ بِذَاتِ اِ۬لصُّدُورِۖ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (676,6,'108',5,'Al-Mā’idah','المَائدة',11,14,9,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ كُونُواْ قَوَّٰمِينَ لِلهِ شُهَدَآءَ بِالْقِسْطِۖ وَلَا يَجْرِمَنَّكُمْ شَنَـَٔانُ قَوْمٍ عَلَىٰ أَلَّا تَعْدِلُواْۖ اُ۪عْدِلُواْ هُوَ أَقْرَبُ لِلتَّقْوَىٰۖ وَاتَّقُواْ اُ۬للَّهَۖ إِنَّ اَ۬للَّهَ خَبِيرُۢ بِمَا تَعْمَلُونَۖ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (677,6,'108',5,'Al-Mā’idah','المَائدة',14,15,10,'وَعَدَ اَ۬للَّهُ اُ۬لذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ لَهُم مَّغْفِرَةٞ وَأَجْرٌ عَظِيمٞۖ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (678,6,'109',5,'Al-Mā’idah','المَائدة',1,2,11,'وَالذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَا أُوْلَٰٓئِكَ أَصْحَٰبُ اُ۬لْجَحِيمِۖ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (679,6,'109',5,'Al-Mā’idah','المَائدة',2,5,12,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ اُ۟ذْكُرُواْ نِعْمَتَ اَ۬للَّهِ عَلَيْكُمْ إِذْ هَمَّ قَوْمٌ أَنْ يَّبْسُطُواْ إِلَيْكُمْ أَيْدِيَهُمْ فَكَفَّ أَيْدِيَهُمْ عَنكُمْۖ وَاتَّقُواْ اُ۬للَّهَۖ وَعَلَى اَ۬للَّهِ فَلْيَتَوَكَّلِ اِ۬لْمُؤْمِنُونَۖ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (680,6,'109',5,'Al-Mā’idah','المَائدة',5,11,13,'۞ وَلَقَدْ أَخَذَ اَ۬للَّهُ مِيثَٰقَ بَنِے إِسْرَآءِيلَ وَبَعَثْنَا مِنْهُمُ اُ۪ثْنَےْ عَشَرَ نَقِيباٗۖ وَقَالَ اَ۬للَّهُ إِنِّے مَعَكُمْۖ لَئِنْ أَقَمْتُمُ اُ۬لصَّلَوٰةَ وَءَاتَيْتُمُ اُ۬لزَّكَوٰةَ وَءَامَنتُم بِرُسُلِے وَعَزَّرْتُمُوهُمْ وَأَقْرَضْتُمُ اُ۬للَّهَ قَرْضاً حَسَناٗ لَّأُكَفِّرَنَّ عَنكُمْ سَيِّـَٔاتِكُمْ وَلَأُدْخِلَنَّكُمْ جَنَّٰتٖ تَجْرِے مِن تَحْتِهَا اَ۬لْأَنْهَٰرُۖ فَمَن كَفَرَ بَعْدَ ذَٰلِكَ مِنكُمْ فَقَدْ ضَلَّ سَوَآءَ اَ۬لسَّبِيلِۖ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (681,6,'109',5,'Al-Mā’idah','المَائدة',11,15,14,'فَبِمَا نَقْضِهِم مِّيثَٰقَهُمْ لَعَنَّٰهُمْ وَجَعَلْنَا قُلُوبَهُمْ قَٰسِيَةٗ يُحَرِّفُونَ اَ۬لْكَلِمَ عَن مَّوَاضِعِهِۦ وَنَسُواْ حَظّاٗ مِّمَّا ذُكِّرُواْ بِهِۦۖ وَلَا تَزَالُ تَطَّلِعُ عَلَىٰ خَآئِنَةٖ مِّنْهُمْ إِلَّا قَلِيلاٗ مِّنْهُمْۖ فَاعْفُ عَنْهُمْ وَاصْفَحْۖ إِنَّ اَ۬للَّهَ يُحِبُّ اُ۬لْمُحْسِنِينَۖ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (682,6,'110',5,'Al-Mā’idah','المَائدة',1,4,15,'وَمِنَ اَ۬لذِينَ قَالُواْ إِنَّا نَصَٰرَىٰ أَخَذْنَا مِيثَٰقَهُمْ فَنَسُواْ حَظّاٗ مِّمَّا ذُكِّرُواْ بِهِۦ فَأَغْرَيْنَا بَيْنَهُمُ اُ۬لْعَدَٰوَةَ وَالْبَغْضَآءَ ا۪لَىٰ يَوْمِ اِ۬لْقِيَٰمَةِۖ وَسَوْفَ يُنَبِّئُهُمُ اُ۬للَّهُ بِمَا كَانُواْ يَصْنَعُونَۖ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (683,6,'110',5,'Al-Mā’idah','المَائدة',4,6,16,'يَٰأَهْلَ اَ۬لْكِتَٰبِ قَدْ جَآءَكُمْ رَسُولُنَا يُبَيِّنُ لَكُمْ كَثِيراٗ مِّمَّا كُنتُمْ تُخْفُونَ مِنَ اَ۬لْكِتَٰبِ وَيَعْفُواْ عَن كَثِيرٖۖ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (684,6,'110',5,'Al-Mā’idah','المَائدة',7,7,17,'قَدْ جَآءَكُم مِّنَ اَ۬للَّهِ نُورٞ وَكِتَٰبٞ مُّبِينٞ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (685,6,'110',5,'Al-Mā’idah','المَائدة',8,10,18,'يَهْدِے بِهِ اِ۬للَّهُ مَنِ اِ۪تَّبَعَ رِضْوَٰنَهُۥ سُبُلَ اَ۬لسَّلَٰمِۖ وَيُخْرِجُهُم مِّنَ اَ۬لظُّلُمَٰتِ إِلَى اَ۬لنُّورِ بِإِذْنِهِۦۖ وَيَهْدِيهِمْ إِلَىٰ صِرَٰطٖ مُّسْتَقِيمٖۖ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (686,6,'110',5,'Al-Mā’idah','المَائدة',10,15,19,'۞ لَّقَدْ كَفَرَ اَ۬لذِينَ قَالُواْ إِنَّ اَ۬للَّهَ هُوَ اَ۬لْمَسِيحُ اُ۪بْنُ مَرْيَمَۖ قُلْ فَمَنْ يَّمْلِكُ مِنَ اَ۬للَّهِ شَئْاً إِنْ أَرَادَ أَنْ يُّهْلِكَ اَ۬لْمَسِيحَ اَ۪بْنَ مَرْيَمَ وَأُمَّهُۥ وَمَن فِے اِ۬لْأَرْضِ جَمِيعاٗۖ وَلِلهِ مُلْكُ اُ۬لسَّمَٰوَٰتِ وَالْأَرْضِ وَمَا بَيْنَهُمَاۖ يَخْلُقُ مَا يَشَآءُۖ وَاللَّهُ عَلَىٰ كُلِّ شَےْءٖ قَدِيرٞۖ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (687,6,'111',5,'Al-Mā’idah','المَائدة',1,4,20,'وَقَالَتِ اِ۬لْيَهُودُ وَالنَّصَٰرَىٰ نَحْنُ أَبْنَٰٓؤُاْ اُ۬للَّهِ وَأَحِبَّٰٓؤُهُۥۖ قُلْ فَلِمَ يُعَذِّبُكُم بِذُنُوبِكُمۖ بَلْ أَنتُم بَشَرٞ مِّمَّنْ خَلَقَۖ يَغْفِرُ لِمَنْ يَّشَآءُ وَيُعَذِّبُ مَنْ يَّشَآءُۖ وَلِلهِ مُلْكُ اُ۬لسَّمَٰوَٰتِ وَالْأَرْضِ وَمَا بَيْنَهُمَاۖ وَإِلَيْهِ اِ۬لْمَصِيرُۖ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (688,6,'111',5,'Al-Mā’idah','المَائدة',4,7,21,'يَٰأَهْلَ اَ۬لْكِتَٰبِ قَدْ جَآءَكُمْ رَسُولُنَا يُبَيِّنُ لَكُمْ عَلَىٰ فَتْرَةٖ مِّنَ اَ۬لرُّسُلِ أَن تَقُولُواْ مَا جَآءَنَا مِنۢ بَشِيرٖ وَلَا نَذِيرٖ فَقَدْ جَآءَكُم بَشِيرٞ وَنَذِيرٞۖ وَاللَّهُ عَلَىٰ كُلِّ شَےْءٖ قَدِيرٞۖ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (689,6,'111',5,'Al-Mā’idah','المَائدة',7,9,22,'وَإِذْ قَالَ مُوسَىٰ لِقَوْمِهِۦ يَٰقَوْمِ اِ۟ذْكُرُواْ نِعْمَةَ اَ۬للَّهِ عَلَيْكُمْ إِذْ جَعَلَ فِيكُمْ أَنۢبِئَآءَ وَجَعَلَكُم مُّلُوكاٗۖ وَءَاتَيٰكُم مَّا لَمْ يُؤْتِ أَحَداٗ مِّنَ اَ۬لْعَٰلَمِينَۖ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (690,6,'111',5,'Al-Mā’idah','المَائدة',9,11,23,'يَٰقَوْمِ اِ۟دْخُلُواْ اُ۬لْأَرْضَ اَ۬لْمُقَدَّسَةَ اَ۬لتِے كَتَبَ اَ۬للَّهُ لَكُمْۖ وَلَا تَرْتَدُّواْ عَلَىٰ أَدْبَٰرِكُمْ فَتَنقَلِبُواْ خَٰسِرِينَۖ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (691,6,'111',5,'Al-Mā’idah','المَائدة',11,13,24,'قَالُواْ يَٰمُوسَىٰ إِنَّ فِيهَا قَوْماٗ جَبَّارِينَۖ وَإِنَّا لَن نَّدْخُلَهَا حَتَّىٰ يَخْرُجُواْ مِنْهَاۖ فَإِنْ يَّخْرُجُواْ مِنْهَا فَإِنَّا دَٰخِلُونَۖ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (692,6,'111',5,'Al-Mā’idah','المَائدة',13,15,25,'۞ قَالَ رَجُلَٰنِ مِنَ اَ۬لذِينَ يَخَافُونَ أَنْعَمَ اَ۬للَّهُ عَلَيْهِمَا اَ۟دْخُلُواْ عَلَيْهِمُ اُ۬لْبَابَۖ فَإِذَا دَخَلْتُمُوهُ فَإِنَّكُمْ غَٰلِبُونَۖ وَعَلَى اَ۬للَّهِ فَتَوَكَّلُواْ إِن كُنتُم مُّؤْمِنِينَۖ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (693,6,'112',5,'Al-Mā’idah','المَائدة',1,2,26,'قَالُواْ يَٰمُوسَىٰ إِنَّا لَن نَّدْخُلَهَا أَبَداٗ مَّا دَامُواْ فِيهَا فَاذْهَبْ أَنتَ وَرَبُّكَ فَقَٰتِلَا إِنَّا هَٰهُنَا قَٰعِدُونَۖ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (694,6,'112',5,'Al-Mā’idah','المَائدة',2,4,27,'قَالَ رَبِّ إِنِّے لَا أَمْلِكُ إِلَّا نَفْسِےۖ وَأَخِےۖ فَافْرُقْ بَيْنَنَا وَبَيْنَ اَ۬لْقَوْمِ اِ۬لْفَٰسِقِينَۖ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (695,6,'112',5,'Al-Mā’idah','المَائدة',4,5,28,'قَالَ فَإِنَّهَا مُحَرَّمَةٌ عَلَيْهِمْۖ أَرْبَعِينَ سَنَةٗ يَتِيهُونَ فِے اِ۬لْأَرْضِۖ فَلَا تَأْسَ عَلَى اَ۬لْقَوْمِ اِ۬لْفَٰسِقِينَۖ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (696,6,'112',5,'Al-Mā’idah','المَائدة',6,8,29,'وَاتْلُ عَلَيْهِمْ نَبَأَ اَ۪بْنَےْ ءَادَمَ بِالْحَقِّ إِذْ قَرَّبَا قُرْبَاناٗ فَتُقُبِّلَ مِنْ أَحَدِهِمَا وَلَمْ يُتَقَبَّلْ مِنَ اَ۬لْأٓخَرِۖ قَالَ لَأَقْتُلَنَّكَۖ قَالَ إِنَّمَا يَتَقَبَّلُ اُ۬للَّهُ مِنَ اَ۬لْمُتَّقِينَۖ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (697,6,'112',5,'Al-Mā’idah','المَائدة',8,10,30,'لَئِنۢ بَسَطْتَّ إِلَيَّ يَدَكَ لِتَقْتُلَنِے مَا أَنَا بِبَاسِطٖ يَدِيَ إِلَيْكَ لِأَقْتُلَكَۖ إِنِّيَ أَخَافُ اُ۬للَّهَ رَبَّ اَ۬لْعَٰلَمِينَۖ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (698,6,'112',5,'Al-Mā’idah','المَائدة',10,11,31,'إِنِّيَ أُرِيدُ أَن تَبُوٓأَ بِإِثْمِے وَإِثْمِكَ فَتَكُونَ مِنْ أَصْحَٰبِ اِ۬لنَّارِۖ وَذَٰلِكَ جَزَٰٓؤُاْ اُ۬لظَّٰلِمِينَۖ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (699,6,'112',5,'Al-Mā’idah','المَائدة',11,12,32,'فَطَوَّعَتْ لَهُۥ نَفْسُهُۥ قَتْلَ أَخِيهِ فَقَتَلَهُۥ فَأَصْبَحَ مِنَ اَ۬لْخَٰسِرِينَۖ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (700,6,'112',5,'Al-Mā’idah','المَائدة',13,15,33,'فَبَعَثَ اَ۬للَّهُ غُرَاباٗ يَبْحَثُ فِے اِ۬لْأَرْضِ لِيُرِيَهُۥ كَيْفَ يُوَٰرِے سَوْءَةَ أَخِيهِۖ قَالَ يَٰوَيْلَتَىٰ أَعَجَزْتُ أَنْ أَكُونَ مِثْلَ هَٰذَا اَ۬لْغُرَابِ فَأُوَٰرِيَ سَوْءَةَ أَخِے فَأَصْبَحَ مِنَ اَ۬لنَّٰدِمِينَ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (701,6,'113',5,'Al-Mā’idah','المَائدة',1,5,34,'۞ مِنْ أَجْلِ ذَٰلِكَۖ كَتَبْنَا عَلَىٰ بَنِے إِسْرَآءِيلَ أَنَّهُۥ مَن قَتَلَ نَفْساَۢ بِغَيْرِ نَفْسٍ أَوْ فَسَادٖ فِے اِ۬لْأَرْضِ فَكَأَنَّمَا قَتَلَ اَ۬لنَّاسَ جَمِيعاٗۖ وَمَنْ أَحْيَاهَا فَكَأَنَّمَا أَحْيَا اَ۬لنَّاسَ جَمِيعاٗۖ وَلَقَدْ جَآءَتْهُمْ رُسُلُنَا بِالْبَيِّنَٰتِ ثُمَّ إِنَّ كَثِيراٗ مِّنْهُم بَعْدَ ذَٰلِكَ فِے اِ۬لْأَرْضِ لَمُسْرِفُونَۖ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (702,6,'113',5,'Al-Mā’idah','المَائدة',5,9,35,'إِنَّمَا جَزَٰٓؤُاْ اُ۬لذِينَ يُحَارِبُونَ اَ۬للَّهَ وَرَسُولَهُۥ وَيَسْعَوْنَ فِے اِ۬لْأَرْضِ فَسَاداً أَنْ يُّقَتَّلُواْ أَوْ يُصَلَّبُواْ أَوْ تُقَطَّعَ أَيْدِيهِمْ وَأَرْجُلُهُم مِّنْ خِلَٰفٍ أَوْ يُنفَوْاْ مِنَ اَ۬لْأَرْضِۖ ذَٰلِكَ لَهُمْ خِزْيٞ فِے اِ۬لدُّنْيَاۖ وَلَهُمْ فِے اِ۬لْأٓخِرَةِ عَذَابٌ عَظِيمٌ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (703,6,'113',5,'Al-Mā’idah','المَائدة',10,11,36,'إِلَّا اَ۬لذِينَ تَابُواْ مِن قَبْلِ أَن تَقْدِرُواْ عَلَيْهِمْ فَاعْلَمُواْ أَنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٞۖ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (704,6,'113',5,'Al-Mā’idah','المَائدة',11,13,37,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ اُ۪تَّقُواْ اُ۬للَّهَ وَابْتَغُواْ إِلَيْهِ اِ۬لْوَسِيلَةَ وَجَٰهِدُواْ فِے سَبِيلِهِۦ لَعَلَّكُمْ تُفْلِحُونَۖ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (705,6,'113',5,'Al-Mā’idah','المَائدة',13,15,38,'إِنَّ اَ۬لذِينَ كَفَرُواْ لَوْ أَنَّ لَهُم مَّا فِے اِ۬لْأَرْضِ جَمِيعاٗ وَمِثْلَهُۥ مَعَهُۥ لِيَفْتَدُواْ بِهِۦ مِنْ عَذَابِ يَوْمِ اِ۬لْقِيَٰمَةِ مَا تُقُبِّلَ مِنْهُمْۖ وَلَهُمْ عَذَابٌ أَلِيمٞۖ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (706,6,'114',5,'Al-Mā’idah','المَائدة',1,2,39,'يُرِيدُونَ أَنْ يَّخْرُجُواْ مِنَ اَ۬لنَّارِ وَمَا هُم بِخَٰرِجِينَ مِنْهَاۖ وَلَهُمْ عَذَابٞ مُّقِيمٞۖ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (707,6,'114',5,'Al-Mā’idah','المَائدة',2,4,40,'وَالسَّارِقُ وَالسَّارِقَةُ فَاقْطَعُواْ أَيْدِيَهُمَا جَزَآءَۢ بِمَا كَسَبَا نَكَٰلاٗ مِّنَ اَ۬للَّهِۖ وَاللَّهُ عَزِيزٌ حَكِيمٞۖ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (708,6,'114',5,'Al-Mā’idah','المَائدة',4,5,41,'فَمَن تَابَ مِنۢ بَعْدِ ظُلْمِهِۦ وَأَصْلَحَ فَإِنَّ اَ۬للَّهَ يَتُوبُ عَلَيْهِۖ إِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٌۖ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (709,6,'114',5,'Al-Mā’idah','المَائدة',5,7,42,'أَلَمْ تَعْلَمْ أَنَّ اَ۬للَّهَ لَهُۥ مُلْكُ اُ۬لسَّمَٰوَٰتِ وَالْأَرْضِۖ يُعَذِّبُ مَنْ يَّشَآءُ وَيَغْفِرُ لِمَنْ يَّشَآءُۖ وَاللَّهُ عَلَىٰ كُلِّ شَےْءٖ قَدِيرٞۖ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (710,6,'114',5,'Al-Mā’idah','المَائدة',7,15,43,'۞ يَٰأَيُّهَا اَ۬لرَّسُولُ لَا يُحْزِنكَ اَ۬لذِينَ يُسَٰرِعُونَ فِے اِ۬لْكُفْرِ مِنَ اَ۬لذِينَ قَالُواْ ءَامَنَّا بِأَفْوَٰهِهِمْ وَلَمْ تُؤْمِن قُلُوبُهُمْۖ وَمِنَ اَ۬لذِينَ هَادُواْ سَمَّٰعُونَ لِلْكَذِبِ سَمَّٰعُونَ لِقَوْمٍ ءَاخَرِينَ لَمْ يَأْتُوكَۖ يُحَرِّفُونَ اَ۬لْكَلِمَ مِنۢ بَعْدِ مَوَاضِعِهِۦ يَقُولُونَ إِنْ أُوتِيتُمْ هَٰذَا فَخُذُوهُ وَإِن لَّمْ تُؤْتَوْهُ فَاحْذَرُواْۖ وَمَنْ يُّرِدِ اِ۬للَّهُ فِتْنَتَهُۥ فَلَن تَمْلِكَ لَهُۥ مِنَ اَ۬للَّهِ شَئْاًۖ أُوْلَٰٓئِكَ اَ۬لذِينَ لَمْ يُرِدِ اِ۬للَّهُ أَنْ يُّطَهِّرَ قُلُوبَهُمْۖ لَهُمْ فِے اِ۬لدُّنْيَا خِزْيٞۖ وَلَهُمْ فِے اِ۬لْأٓخِرَةِ عَذَابٌ عَظِيمٞۖ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (711,6,'115',5,'Al-Mā’idah','المَائدة',1,4,44,'سَمَّٰعُونَ لِلْكَذِبِ أَكَّٰلُونَ لِلسُّحْتِۖ فَإِن جَآءُوكَ فَاحْكُم بَيْنَهُمْ أَوْ أَعْرِضْ عَنْهُمْۖ وَإِن تُعْرِضْ عَنْهُمْ فَلَنْ يَّضُرُّوكَ شَئْاٗۖ وَإِنْ حَكَمْتَ فَاحْكُم بَيْنَهُم بِالْقِسْطِۖ إِنَّ اَ۬للَّهَ يُحِبُّ اُ۬لْمُقْسِطِينَۖ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (712,6,'115',5,'Al-Mā’idah','المَائدة',4,6,45,'وَكَيْفَ يُحَكِّمُونَكَ وَعِندَهُمُ اُ۬لتَّوْرَيٰةُ فِيهَا حُكْمُ اُ۬للَّهِ ثُمَّ يَتَوَلَّوْنَ مِنۢ بَعْدِ ذَٰلِكَۖ وَمَا أُوْلَٰٓئِكَ بِالْمُؤْمِنِينَۖ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (713,6,'115',5,'Al-Mā’idah','المَائدة',6,11,46,'إِنَّا أَنزَلْنَا اَ۬لتَّوْرَيٰةَ فِيهَا هُدىٗ وَنُورٞ يَحْكُمُ بِهَا اَ۬لنَّبِيٓـُٔونَ اَ۬لذِينَ أَسْلَمُواْ لِلذِينَ هَادُواْ وَالرَّبَّٰنِيُّونَ وَالْأَحْبَارُ بِمَا اَ۟سْتُحْفِظُواْ مِن كِتَٰبِ اِ۬للَّهِ وَكَانُواْ عَلَيْهِ شُهَدَآءَۖ فَلَا تَخْشَوُاْ اُ۬لنَّاسَ وَاخْشَوْنِۖ وَلَا تَشْتَرُواْ بِـَٔايَٰتِے ثَمَناٗ قَلِيلاٗۖ وَمَن لَّمْ يَحْكُم بِمَا أَنزَلَ اَ۬للَّهُ فَأُوْلَٰٓئِكَ هُمُ اُ۬لْكَٰفِرُونَۖ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (714,6,'115',5,'Al-Mā’idah','المَائدة',11,15,47,'۞ وَكَتَبْنَا عَلَيْهِمْ فِيهَا أَنَّ اَ۬لنَّفْسَ بِالنَّفْسِ وَالْعَيْنَ بِالْعَيْنِ وَالْأَنفَ بِالْأَنفِ وَالْأُذْنَ بِالْأُذْنِ وَالسِّنَّ بِالسِّنِّ وَالْجُرُوحَ قِصَاصٞۖ فَمَن تَصَدَّقَ بِهِۦ فَهْوَ كَفَّارَةٞ لَّهُۥۖ وَمَن لَّمْ يَحْكُم بِمَا أَنزَلَ اَ۬للَّهُ فَأُوْلَٰٓئِكَ هُمُ اُ۬لظَّٰلِمُونَۖ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (715,6,'116',5,'Al-Mā’idah','المَائدة',1,3,48,'وَقَفَّيْنَا عَلَىٰ ءَاثَٰرِهِم بِعِيسَى اَ۪بْنِ مَرْيَمَ مُصَدِّقاٗ لِّمَا بَيْنَ يَدَيْهِ مِنَ اَ۬لتَّوْرَيٰةِۖ وَءَاتَيْنَٰهُ اُ۬لْإِنجِيلَ فِيهِ هُدىٗ وَنُورٞ وَمُصَدِّقاٗ لِّمَا بَيْنَ يَدَيْهِ مِنَ اَ۬لتَّوْرَيٰةِ وَهُدىٗ وَمَوْعِظَةٗ لِّلْمُتَّقِينَۖ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (716,6,'116',5,'Al-Mā’idah','المَائدة',4,5,49,'وَلْيَحْكُمْ أَهْلُ اُ۬لْإِنجِيلِ بِمَا أَنزَلَ اَ۬للَّهُ فِيهِۖ وَمَن لَّمْ يَحْكُم بِمَا أَنزَلَ اَ۬للَّهُ فَأُوْلَٰٓئِكَ هُمُ اُ۬لْفَٰسِقُونَۖ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (717,6,'116',5,'Al-Mā’idah','المَائدة',5,11,50,'وَأَنزَلْنَا إِلَيْكَ اَ۬لْكِتَٰبَ بِالْحَقِّ مُصَدِّقاٗ لِّمَا بَيْنَ يَدَيْهِ مِنَ اَ۬لْكِتَٰبِ وَمُهَيْمِناً عَلَيْهِۖ فَاحْكُم بَيْنَهُم بِمَا أَنزَلَ اَ۬للَّهُ وَلَا تَتَّبِعْ أَهْوَآءَهُمْ عَمَّا جَآءَكَ مِنَ اَ۬لْحَقِّۖ لِكُلّٖ جَعَلْنَا مِنكُمْ شِرْعَةٗ وَمِنْهَاجاٗۖ وَلَوْ شَآءَ اَ۬للَّهُ لَجَعَلَكُمْ أُمَّةٗ وَٰحِدَةٗۖ وَلَٰكِن لِّيَبْلُوَكُمْ فِے مَا ءَاتَيٰكُمْۖ فَاسْتَبِقُواْ اُ۬لْخَيْرَٰتِۖ إِلَى اَ۬للَّهِ مَرْجِعُكُمْ جَمِيعاٗ فَيُنَبِّئُكُم بِمَا كُنتُمْ فِيهِ تَخْتَلِفُونَۖ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (718,6,'116',5,'Al-Mā’idah','المَائدة',11,14,51,'۞ وَأَنُ اُ۟حْكُم بَيْنَهُم بِمَا أَنزَلَ اَ۬للَّهُۖ وَلَا تَتَّبِعْ أَهْوَآءَهُمْۖ وَاحْذَرْهُمْ أَنْ يَّفْتِنُوكَ عَنۢ بَعْضِ مَا أَنزَلَ اَ۬للَّهُ إِلَيْكَۖ فَإِن تَوَلَّوْاْ فَاعْلَمْ أَنَّمَا يُرِيدُ اُ۬للَّهُ أَنْ يُّصِيبَهُم بِبَعْضِ ذُنُوبِهِمْۖ وَإِنَّ كَثِيراٗ مِّنَ اَ۬لنَّاسِ لَفَٰسِقُونَۖ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (719,6,'116',5,'Al-Mā’idah','المَائدة',14,15,52,'أَفَحُكْمَ اَ۬لْجَٰهِلِيَّةِ يَبْغُونَۖ وَمَنْ أَحْسَنُ مِنَ اَ۬للَّهِ حُكْماٗ لِّقَوْمٖ يُوقِنُونَۖ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (720,6,'117',5,'Al-Mā’idah','المَائدة',1,3,53,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ لَا تَتَّخِذُواْ اُ۬لْيَهُودَ وَالنَّصَٰرَىٰ أَوْلِيَآءَۖ بَعْضُهُمْ أَوْلِيَآءُ بَعْضٖۖ وَمَنْ يَّتَوَلَّهُم مِّنكُمْ فَإِنَّهُۥ مِنْهُمْۖ إِنَّ اَ۬للَّهَ لَا يَهْدِے اِ۬لْقَوْمَ اَ۬لظَّٰلِمِينَۖ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (721,6,'117',5,'Al-Mā’idah','المَائدة',3,5,54,'فَتَرَى اَ۬لذِينَ فِے قُلُوبِهِم مَّرَضٞ يُسَٰرِعُونَ فِيهِمْ يَقُولُونَ نَخْشَىٰ أَن تُصِيبَنَا دَآئِرَةٞۖ فَعَسَى اَ۬للَّهُ أَنْ يَّأْتِيَ بِالْفَتْحِ أَوْ أَمْرٖ مِّنْ عِندِهِۦ فَيُصْبِحُواْ عَلَىٰ مَا أَسَرُّواْ فِے أَنفُسِهِمْ نَٰدِمِينَۖ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (722,6,'117',5,'Al-Mā’idah','المَائدة',5,7,55,'يَقُولُ اُ۬لذِينَ ءَامَنُواْ أَهَٰؤُلَآءِ اِ۬لذِينَ أَقْسَمُواْ بِاللَّهِ جَهْدَ أَيْمَٰنِهِمْ إِنَّهُمْ لَمَعَكُمْ حَبِطَتْ أَعْمَٰلُهُمْ فَأَصْبَحُواْ خَٰسِرِينَۖ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (723,6,'117',5,'Al-Mā’idah','المَائدة',7,11,56,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ مَنْ يَّرْتَدِدْ مِنكُمْ عَن دِينِهِۦ فَسَوْفَ يَأْتِے اِ۬للَّهُ بِقَوْمٖ يُحِبُّهُمْ وَيُحِبُّونَهُۥ أَذِلَّةٍ عَلَى اَ۬لْمُؤْمِنِينَ أَعِزَّةٍ عَلَى اَ۬لْكَٰفِرِينَ يُجَٰهِدُونَ فِے سَبِيلِ اِ۬للَّهِ وَلَا يَخَافُونَ لَوْمَةَ لَٰٓئِمٖۖ ذَٰلِكَ فَضْلُ اُ۬للَّهِ يُؤْتِيهِ مَنْ يَّشَآءُۖ وَاللَّهُ وَٰسِعٌ عَلِيمٌۖ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (724,6,'117',5,'Al-Mā’idah','المَائدة',11,12,57,'إِنَّمَا وَلِيُّكُمُ اُ۬للَّهُ وَرَسُولُهُۥ وَالذِينَ ءَامَنُواْ اُ۬لذِينَ يُقِيمُونَ اَ۬لصَّلَوٰةَ وَيُؤْتُونَ اَ۬لزَّكَوٰةَ وَهُمْ رَٰكِعُونَۖ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (725,6,'117',5,'Al-Mā’idah','المَائدة',12,13,58,'وَمَنْ يَّتَوَلَّ اَ۬للَّهَ وَرَسُولَهُۥ وَالذِينَ ءَامَنُواْ فَإِنَّ حِزْبَ اَ۬للَّهِ هُمُ اُ۬لْغَٰلِبُونَۖ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (726,6,'117',5,'Al-Mā’idah','المَائدة',13,15,59,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ لَا تَتَّخِذُواْ اُ۬لذِينَ اَ۪تَّخَذُواْ دِينَكُمْ هُزُؤاٗ وَلَعِباٗ مِّنَ اَ۬لذِينَ أُوتُواْ اُ۬لْكِتَٰبَ مِن قَبْلِكُمْ وَالْكُفَّارَ أَوْلِيَآءَۖ وَاتَّقُواْ اُ۬للَّهَ إِن كُنتُم مُّؤْمِنِينَۖ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (727,6,'118',5,'Al-Mā’idah','المَائدة',1,2,60,'۞ وَإِذَا نَادَيْتُمْ إِلَى اَ۬لصَّلَوٰةِ اِ۪تَّخَذُوهَا هُزُؤاٗ وَلَعِباٗۖ ذَٰلِكَ بِأَنَّهُمْ قَوْمٞ لَّا يَعْقِلُونَۖ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (728,6,'118',5,'Al-Mā’idah','المَائدة',2,3,61,'قُلْ يَٰأَهْلَ اَ۬لْكِتَٰبِ هَلْ تَنقِمُونَ مِنَّا إِلَّا أَنْ ءَامَنَّا بِاللَّهِ وَمَا أُنزِلَ إِلَيْنَا وَمَا أُنزِلَ مِن قَبْلُ وَأَنَّ أَكْثَرَكُمْ فَٰسِقُونَۖ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (729,6,'118',5,'Al-Mā’idah','المَائدة',3,6,62,'قُلْ هَلْ أُنَبِّئُكُم بِشَرّٖ مِّن ذَٰلِكَ مَثُوبَةً عِندَ اَ۬للَّهِ مَن لَّعَنَهُ اُ۬للَّهُ وَغَضِبَ عَلَيْهِ وَجَعَلَ مِنْهُمُ اُ۬لْقِرَدَةَ وَالْخَنَازِيرَ وَعَبَدَ اَ۬لطَّٰغُوتَۖ أُوْلَٰٓئِكَ شَرّٞ مَّكَاناٗ وَأَضَلُّ عَن سَوَآءِ اِ۬لسَّبِيلِۖ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (730,6,'118',5,'Al-Mā’idah','المَائدة',6,7,63,'وَإِذَا جَآءُوكُمْ قَالُواْ ءَامَنَّا وَقَد دَّخَلُواْ بِالْكُفْرِ وَهُمْ قَدْ خَرَجُواْ بِهِۦۖ وَاللَّهُ أَعْلَمُ بِمَا كَانُواْ يَكْتُمُونَۖ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (731,6,'118',5,'Al-Mā’idah','المَائدة',8,9,64,'وَتَرَىٰ كَثِيراٗ مِّنْهُمْ يُسَٰرِعُونَ فِے اِ۬لْإِثْمِ وَالْعُدْوَٰنِ وَأَكْلِهِمُ اُ۬لسُّحْتَۖ لَبِئْسَ مَا كَانُواْ يَعْمَلُونَۖ ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (732,6,'118',5,'Al-Mā’idah','المَائدة',9,10,65,'لَوْلَا يَنْهَيٰهُمُ اُ۬لرَّبَّٰنِيُّونَ وَالْأَحْبَارُ عَن قَوْلِهِمُ اُ۬لْإِثْمَ وَأَكْلِهِمُ اُ۬لسُّحْتَۖ لَبِئْسَ مَا كَانُواْ يَصْنَعُونَۖ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (733,6,'118',5,'Al-Mā’idah','المَائدة',11,15,66,'وَقَالَتِ اِ۬لْيَهُودُ يَدُ اُ۬للَّهِ مَغْلُولَةٌۖ غُلَّتْ أَيْدِيهِمْ وَلُعِنُواْ بِمَا قَالُواْۖ بَلْ يَدَٰهُ مَبْسُوطَتَٰنِ يُنفِقُ كَيْفَ يَشَآءُۖ وَلَيَزِيدَنَّ كَثِيراٗ مِّنْهُم مَّا أُنزِلَ إِلَيْكَ مِن رَّبِّكَ طُغْيَٰناٗ وَكُفْراٗۖ وَأَلْقَيْنَا بَيْنَهُمُ اُ۬لْعَدَٰوَةَ وَالْبَغْضَآءَ ا۪لَىٰ يَوْمِ اِ۬لْقِيَٰمَةِۖ كُلَّمَا أَوْقَدُواْ نَاراٗ لِّلْحَرْبِ أَطْفَأَهَا اَ۬للَّهُۖ وَيَسْعَوْنَ فِے اِ۬لْأَرْضِ فَسَاداٗۖ وَاللَّهُ لَا يُحِبُّ اُ۬لْمُفْسِدِينَۖ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (734,6,'119',5,'Al-Mā’idah','المَائدة',1,2,67,'وَلَوْ أَنَّ أَهْلَ اَ۬لْكِتَٰبِ ءَامَنُواْ وَاتَّقَوْاْ لَكَفَّرْنَا عَنْهُمْ سَيِّـَٔاتِهِمْ وَلَأَدْخَلْنَٰهُمْ جَنَّٰتِ اِ۬لنَّعِيمِۖ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (735,6,'119',5,'Al-Mā’idah','المَائدة',2,5,68,'وَلَوْ أَنَّهُمْ أَقَامُواْ اُ۬لتَّوْرَيٰةَ وَالْإِنجِيلَ وَمَا أُنزِلَ إِلَيْهِم مِّن رَّبِّهِمْ لَأَكَلُواْ مِن فَوْقِهِمْ وَمِن تَحْتِ أَرْجُلِهِمۖ مِّنْهُمْ أُمَّةٞ مُّقْتَصِدَةٞۖ وَكَثِيرٞ مِّنْهُمْ سَآءَ مَا يَعْمَلُونَۖ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (736,6,'119',5,'Al-Mā’idah','المَائدة',5,8,69,'۞ يَٰأَيُّهَا اَ۬لرَّسُولُ بَلِّغْ مَا أُنزِلَ إِلَيْكَ مِن رَّبِّكَۖ وَإِن لَّمْ تَفْعَلْ فَمَا بَلَّغْتَ رِسَالَٰتِهِۦۖ وَاللَّهُ يَعْصِمُكَ مِنَ اَ۬لنَّاسِۖ إِنَّ اَ۬للَّهَ لَا يَهْدِے اِ۬لْقَوْمَ اَ۬لْكَٰفِرِينَۖ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (737,6,'119',5,'Al-Mā’idah','المَائدة',8,11,70,'قُلْ يَٰأَهْلَ اَ۬لْكِتَٰبِ لَسْتُمْ عَلَىٰ شَےْءٍ حَتَّىٰ تُقِيمُواْ اُ۬لتَّوْرَيٰةَ وَالْإِنجِيلَ وَمَا أُنزِلَ إِلَيْكُم مِّن رَّبِّكُمْۖ وَلَيَزِيدَنَّ كَثِيراٗ مِّنْهُم مَّا أُنزِلَ إِلَيْكَ مِن رَّبِّكَ طُغْيَٰناٗ وَكُفْراٗۖ فَلَا تَأْسَ عَلَى اَ۬لْقَوْمِ اِ۬لْكَٰفِرِينَۖ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (738,6,'119',5,'Al-Mā’idah','المَائدة',11,13,71,'إِنَّ اَ۬لذِينَ ءَامَنُواْ وَالذِينَ هَادُواْ وَالصَّٰبُونَ وَالنَّصَٰرَىٰ مَنْ ءَامَنَ بِاللَّهِ وَالْيَوْمِ اِ۬لْأٓخِرِ وَعَمِلَ صَٰلِحاٗ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَۖ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (739,6,'119',5,'Al-Mā’idah','المَائدة',13,15,72,'لَقَدْ أَخَذْنَا مِيثَٰقَ بَنِے إِسْرَآءِيلَ وَأَرْسَلْنَا إِلَيْهِمْ رُسُلاٗۖ كُلَّمَا جَآءَهُمْ رَسُولُۢ بِمَا لَا تَهْوَىٰ أَنفُسُهُمْ فَرِيقاٗ كَذَّبُواْ وَفَرِيقاٗ يَقْتُلُونَۖ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (740,6,'120',5,'Al-Mā’idah','المَائدة',1,2,73,'وَحَسِبُواْ أَلَّا تَكُونَ فِتْنَةٞ فَعَمُواْ وَصَمُّواْ ثُمَّ تَابَ اَ۬للَّهُ عَلَيْهِمْ ثُمَّ عَمُواْ وَصَمُّواْۖ كَثِيرٞ مِّنْهُمْۖ وَاللَّهُ بَصِيرُۢ بِمَا يَعْمَلُونَۖ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (741,6,'120',5,'Al-Mā’idah','المَائدة',3,6,74,'لَقَدْ كَفَرَ اَ۬لذِينَ قَالُواْ إِنَّ اَ۬للَّهَ هُوَ اَ۬لْمَسِيحُ اُ۪بْنُ مَرْيَمَۖ وَقَالَ اَ۬لْمَسِيحُ يَٰبَنِے إِسْرَآءِيلَ اَ۟عْبُدُواْ اُ۬للَّهَ رَبِّے وَرَبَّكُمْۖ إِنَّهُۥ مَنْ يُّشْرِكْ بِاللَّهِ فَقَدْ حَرَّمَ اَ۬للَّهُ عَلَيْهِ اِ۬لْجَنَّةَ وَمَأْوَيٰهُ اُ۬لنَّارُۖ وَمَا لِلظَّٰلِمِينَ مِنْ أَنصَارٖۖ ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (742,6,'120',5,'Al-Mā’idah','المَائدة',6,8,75,'۞ لَّقَدْ كَفَرَ اَ۬لذِينَ قَالُواْ إِنَّ اَ۬للَّهَ ثَالِثُ ثَلَٰثَةٖۖ وَمَا مِنْ إِلَٰهٍ إِلَّا إِلَٰهٞ وَٰحِدٞۖ وَإِن لَّمْ يَنتَهُواْ عَمَّا يَقُولُونَ لَيَمَسَّنَّ اَ۬لذِينَ كَفَرُواْ مِنْهُمْ عَذَابٌ أَلِيمٌۖ ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (743,6,'120',5,'Al-Mā’idah','المَائدة',9,9,76,'أَفَلَا يَتُوبُونَ إِلَى اَ۬للَّهِ وَيَسْتَغْفِرُونَهُۥۖ وَاللَّهُ غَفُورٞ رَّحِيمٞۖ ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (744,6,'120',5,'Al-Mā’idah','المَائدة',10,12,77,'مَّا اَ۬لْمَسِيحُ اُ۪بْنُ مَرْيَمَ إِلَّا رَسُولٞ قَدْ خَلَتْ مِن قَبْلِهِ اِ۬لرُّسُلُ وَأُمُّهُۥ صِدِّيقَةٞۖ كَانَا يَأْكُلَٰنِ اِ۬لطَّعَامَۖ اَ۟نظُرْ كَيْفَ نُبَيِّنُ لَهُمُ اُ۬لْأٓيَٰتِ ثُمَّ اَ۟نظُرْ أَنَّىٰ يُؤْفَكُونَۖ ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (745,6,'120',5,'Al-Mā’idah','المَائدة',12,13,78,'قُلْ أَتَعْبُدُونَ مِن دُونِ اِ۬للَّهِ مَا لَا يَمْلِكُ لَكُمْ ضَرّاٗ وَلَا نَفْعاٗۖ وَاللَّهُ هُوَ اَ۬لسَّمِيعُ اُ۬لْعَلِيمُۖ ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (746,6,'120',5,'Al-Mā’idah','المَائدة',13,15,79,'قُلْ يَٰأَهْلَ اَ۬لْكِتَٰبِ لَا تَغْلُواْ فِے دِينِكُمْ غَيْرَ اَ۬لْحَقِّۖ وَلَا تَتَّبِعُواْ أَهْوَآءَ قَوْمٖ قَدْ ضَلُّواْ مِن قَبْلُ وَأَضَلُّواْ كَثِيراٗ وَضَلُّواْ عَن سَوَآءِ اِ۬لسَّبِيلِۖ ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (747,6,'121',5,'Al-Mā’idah','المَائدة',1,3,80,'لُعِنَ اَ۬لذِينَ كَفَرُواْ مِنۢ بَنِے إِسْرَآءِيلَ عَلَىٰ لِسَانِ دَاوُۥدَ وَعِيسَى اَ۪بْنِ مَرْيَمَۖ ذَٰلِكَ بِمَا عَصَواْ وَّكَانُواْ يَعْتَدُونَۖ ٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (748,6,'121',5,'Al-Mā’idah','المَائدة',3,4,81,'كَانُواْ لَا يَتَنَاهَوْنَ عَن مُّنكَرٖ فَعَلُوهُۖ لَبِئْسَ مَا كَانُواْ يَفْعَلُونَۖ ٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (749,6,'121',5,'Al-Mā’idah','المَائدة',4,7,82,'تَرَىٰ كَثِيراٗ مِّنْهُمْ يَتَوَلَّوْنَ اَ۬لذِينَ كَفَرُواْۖ لَبِئْسَ مَا قَدَّمَتْ لَهُمْ أَنفُسُهُمْ أَن سَخِطَ اَ۬للَّهُ عَلَيْهِمْ وَفِے اِ۬لْعَذَابِ هُمْ خَٰلِدُونَۖ ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (750,6,'121',5,'Al-Mā’idah','المَائدة',7,9,83,'وَلَوْ كَانُواْ يُؤْمِنُونَ بِاللَّهِ وَالنَّبِےٓءِ وَمَا أُنزِلَ إِلَيْهِ مَا اَ۪تَّخَذُوهُمْ أَوْلِيَآءَۖ وَلَٰكِنَّ كَثِيراٗ مِّنْهُمْ فَٰسِقُونَۖ ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (751,7,'121',5,'Al-Mā’idah','المَائدة',9,13,84,'۞ لَتَجِدَنَّ أَشَدَّ اَ۬لنَّاسِ عَدَٰوَةٗ لِّلذِينَ ءَامَنُواْ اُ۬لْيَهُودَ وَالذِينَ أَشْرَكُواْۖ وَلَتَجِدَنَّ أَقْرَبَهُم مَّوَدَّةٗ لِّلذِينَ ءَامَنُواْ اُ۬لذِينَ قَالُواْ إِنَّا نَصَٰرَىٰۖ ذَٰلِكَ بِأَنَّ مِنْهُمْ قِسِّيسِينَ وَرُهْبَاناٗ وَأَنَّهُمْ لَا يَسْتَكْبِرُونَۖ ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (752,7,'121',5,'Al-Mā’idah','المَائدة',13,15,85,'وَإِذَا سَمِعُواْ مَا أُنزِلَ إِلَى اَ۬لرَّسُولِ تَرَىٰ أَعْيُنَهُمْ تَفِيضُ مِنَ اَ۬لدَّمْعِ مِمَّا عَرَفُواْ مِنَ اَ۬لْحَقِّ يَقُولُونَ رَبَّنَا ءَامَنَّا فَاكْتُبْنَا مَعَ اَ۬لشَّٰهِدِينَۖ ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (753,7,'122',5,'Al-Mā’idah','المَائدة',1,2,86,'وَمَا لَنَا لَا نُؤْمِنُ بِاللَّهِ وَمَا جَآءَنَا مِنَ اَ۬لْحَقِّ وَنَطْمَعُ أَنْ يُّدْخِلَنَا رَبُّنَا مَعَ اَ۬لْقَوْمِ اِ۬لصَّٰلِحِينَۖ ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (754,7,'122',5,'Al-Mā’idah','المَائدة',2,4,87,'فَأَثَٰبَهُمُ اُ۬للَّهُ بِمَا قَالُواْ جَنَّٰتٖ تَجْرِے مِن تَحْتِهَا اَ۬لْأَنْهَٰرُ خَٰلِدِينَ فِيهَاۖ وَذَٰلِكَ جَزَآءُ اُ۬لْمُحْسِنِينَۖ ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (755,7,'122',5,'Al-Mā’idah','المَائدة',4,5,88,'وَالذِينَ كَفَرُواْ وَكَذَّبُواْ بِـَٔايَٰتِنَا أُوْلَٰٓئِكَ أَصْحَٰبُ اُ۬لْجَحِيمِۖ ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (756,7,'122',5,'Al-Mā’idah','المَائدة',5,6,89,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ لَا تُحَرِّمُواْ طَيِّبَٰتِ مَا أَحَلَّ اَ۬للَّهُ لَكُمْ وَلَا تَعْتَدُواْۖ إِنَّ اَ۬للَّهَ لَا يُحِبُّ اُ۬لْمُعْتَدِينَۖ ٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (757,7,'122',5,'Al-Mā’idah','المَائدة',7,8,90,'وَكُلُواْ مِمَّا رَزَقَكُمُ اُ۬للَّهُ حَلَٰلاٗ طَيِّباٗۖ وَاتَّقُواْ اُ۬للَّهَ اَ۬لذِے أَنتُم بِهِۦ مُؤْمِنُونَۖ ٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (758,7,'122',5,'Al-Mā’idah','المَائدة',8,13,91,'لَا يُؤَاخِذُكُمُ اُ۬للَّهُ بِاللَّغْوِ فِے أَيْمَٰنِكُمْۖ وَلَٰكِنْ يُّؤَاخِذُكُم بِمَا عَقَّدتُّمُ اُ۬لْأَيْمَٰنَۖ فَكَفَّٰرَتُهُۥ إِطْعَامُ عَشَرَةِ مَسَٰكِينَ مِنْ أَوْسَطِ مَا تُطْعِمُونَ أَهْلِيكُمْ أَوْ كِسْوَتُهُمْ أَوْ تَحْرِيرُ رَقَبَةٖۖ فَمَن لَّمْ يَجِدْ فَصِيَامُ ثَلَٰثَةِ أَيَّامٖۖ ذَٰلِكَ كَفَّٰرَةُ أَيْمَٰنِكُمْ إِذَا حَلَفْتُمْۖ وَاحْفَظُواْ أَيْمَٰنَكُمْۖ كَذَٰلِكَ يُبَيِّنُ اُ۬للَّهُ لَكُمْ ءَايَٰتِهِۦ لَعَلَّكُمْ تَشْكُرُونَۖ ٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (759,7,'122',5,'Al-Mā’idah','المَائدة',13,15,92,'۞ يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ إِنَّمَا اَ۬لْخَمْرُ وَالْمَيْسِرُ وَالْأَنصَابُ وَالْأَزْلَٰمُ رِجْسٞ مِّنْ عَمَلِ اِ۬لشَّيْطَٰنِ فَاجْتَنِبُوهُ لَعَلَّكُمْ تُفْلِحُونَۖ ٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (760,7,'123',5,'Al-Mā’idah','المَائدة',1,3,93,'إِنَّمَا يُرِيدُ اُ۬لشَّيْطَٰنُ أَنْ يُّوقِعَ بَيْنَكُمُ اُ۬لْعَدَٰوَةَ وَالْبَغْضَآءَ فِے اِ۬لْخَمْرِ وَالْمَيْسِرِ وَيَصُدَّكُمْ عَن ذِكْرِ اِ۬للَّهِ وَعَنِ اِ۬لصَّلَوٰةِۖ فَهَلْ أَنتُم مُّنتَهُونَۖ ٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (761,7,'123',5,'Al-Mā’idah','المَائدة',3,5,94,'وَأَطِيعُواْ اُ۬للَّهَ وَأَطِيعُواْ اُ۬لرَّسُولَ وَاحْذَرُواْۖ فَإِن تَوَلَّيْتُمْ فَاعْلَمُواْ أَنَّمَا عَلَىٰ رَسُولِنَا اَ۬لْبَلَٰغُ اُ۬لْمُبِينُۖ ٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (762,7,'123',5,'Al-Mā’idah','المَائدة',5,8,95,'لَيْسَ عَلَى اَ۬لذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ جُنَاحٞ فِيمَا طَعِمُواْ إِذَا مَا اَ۪تَّقَواْ وَّءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ ثُمَّ اَ۪تَّقَواْ وَّءَامَنُواْ ثُمَّ اَ۪تَّقَواْ وَّأَحْسَنُواْۖ وَاللَّهُ يُحِبُّ اُ۬لْمُحْسِنِينَۖ ٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (763,7,'123',5,'Al-Mā’idah','المَائدة',8,10,96,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ لَيَبْلُوَنَّكُمُ اُ۬للَّهُ بِشَےْءٖ مِّنَ اَ۬لصَّيْدِ تَنَالُهُۥ أَيْدِيكُمْ وَرِمَاحُكُمْ لِيَعْلَمَ اَ۬للَّهُ مَنْ يَّخَافُهُۥ بِالْغَيْبِۖ فَمَنِ اِ۪عْتَدَىٰ بَعْدَ ذَٰلِكَ فَلَهُۥ عَذَابٌ أَلِيمٞۖ ٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (764,7,'123',5,'Al-Mā’idah','المَائدة',10,15,97,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ لَا تَقْتُلُواْ اُ۬لصَّيْدَ وَأَنتُمْ حُرُمٞۖ وَمَن قَتَلَهُۥ مِنكُم مُّتَعَمِّداٗ فَجَزَآءُ مِثْلِ مَا قَتَلَ مِنَ اَ۬لنَّعَمِ يَحْكُمُ بِهِۦ ذَوَا عَدْلٖ مِّنكُمْ هَدْياَۢ بَٰلِغَ اَ۬لْكَعْبَةِ أَوْ كَفَّٰرَةُ طَعَامِ مَسَٰكِينَ أَوْ عَدْلُ ذَٰلِكَ صِيَاماٗ لِّيَذُوقَ وَبَالَ أَمْرِهِۦۖ عَفَا اَ۬للَّهُ عَمَّا سَلَفَۖ وَمَنْ عَادَ فَيَنتَقِمُ اُ۬للَّهُ مِنْهُۖ وَاللَّهُ عَزِيزٞ ذُو اُ۪نتِقَامٍۖ ٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (765,7,'124',5,'Al-Mā’idah','المَائدة',1,3,98,'أُحِلَّ لَكُمْ صَيْدُ اُ۬لْبَحْرِ وَطَعَامُهُۥۖ مَتَٰعاٗ لَّكُمْ وَلِلسَّيَّارَةِۖ وَحُرِّمَ عَلَيْكُمْ صَيْدُ اُ۬لْبَرِّ مَا دُمْتُمْ حُرُماٗۖ وَاتَّقُواْ اُ۬للَّهَ اَ۬لذِے إِلَيْهِ تُحْشَرُونَۖ ٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (766,7,'124',5,'Al-Mā’idah','المَائدة',3,6,99,'۞ جَعَلَ اَ۬للَّهُ اُ۬لْكَعْبَةَ اَ۬لْبَيْتَ اَ۬لْحَرَامَ قِيَٰماٗ لِّلنَّاسِ وَالشَّهْرَ اَ۬لْحَرَامَ وَالْهَدْيَ وَالْقَلَٰٓئِدَۖ ذَٰلِكَ لِتَعْلَمُواْ أَنَّ اَ۬للَّهَ يَعْلَمُ مَا فِے اِ۬لسَّمَٰوَٰتِ وَمَا فِے اِ۬لْأَرْضِ وَأَنَّ اَ۬للَّهَ بِكُلِّ شَےْءٍ عَلِيمٌۖ ٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (767,7,'124',5,'Al-Mā’idah','المَائدة',6,7,100,'اِ۪عْلَمُواْ أَنَّ اَ۬للَّهَ شَدِيدُ اُ۬لْعِقَابِ وَأَنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٞۖ ١٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (768,7,'124',5,'Al-Mā’idah','المَائدة',7,8,101,'مَّا عَلَى اَ۬لرَّسُولِ إِلَّا اَ۬لْبَلَٰغُۖ وَاللَّهُ يَعْلَمُ مَا تُبْدُونَ وَمَا تَكْتُمُونَۖ ١٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (769,7,'124',5,'Al-Mā’idah','المَائدة',8,10,102,'قُل لَّا يَسْتَوِے اِ۬لْخَبِيثُ وَالطَّيِّبُ وَلَوْ أَعْجَبَكَ كَثْرَةُ اُ۬لْخَبِيثِۖ فَاتَّقُواْ اُ۬للَّهَ يَٰأُوْلِے اِ۬لْأَلْبَٰبِ لَعَلَّكُمْ تُفْلِحُونَۖ ١٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (770,7,'124',5,'Al-Mā’idah','المَائدة',10,12,103,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ لَا تَسْـَٔلُواْ عَنْ أَشْيَآءَ ا۪ن تُبْدَ لَكُمْ تَسُؤْكُمْۖ وَإِن تَسْـَٔلُواْ عَنْهَا حِينَ يُنَزَّلُ اُ۬لْقُرْءَانُ تُبْدَ لَكُمْ عَفَا اَ۬للَّهُ عَنْهَاۖ وَاللَّهُ غَفُورٌ حَلِيمٞۖ ١٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (771,7,'124',5,'Al-Mā’idah','المَائدة',13,13,104,'قَدْ سَأَلَهَا قَوْمٞ مِّن قَبْلِكُمْ ثُمَّ أَصْبَحُواْ بِهَا كَٰفِرِينَۖ ١٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (772,7,'124',5,'Al-Mā’idah','المَائدة',13,15,105,'مَا جَعَلَ اَ۬للَّهُ مِنۢ بَحِيرَةٖ وَلَا سَآئِبَةٖ وَلَا وَصِيلَةٖ وَلَا حَامٖۖ وَلَٰكِنَّ اَ۬لذِينَ كَفَرُواْ يَفْتَرُونَ عَلَى اَ۬للَّهِ اِ۬لْكَذِبَ وَأَكْثَرُهُمْ لَا يَعْقِلُونَۖ ١٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (773,7,'125',5,'Al-Mā’idah','المَائدة',1,3,106,'وَإِذَا قِيلَ لَهُمْ تَعَالَوْاْ إِلَىٰ مَا أَنزَلَ اَ۬للَّهُ وَإِلَى اَ۬لرَّسُولِ قَالُواْ حَسْبُنَا مَا وَجَدْنَا عَلَيْهِ ءَابَآءَنَاۖ أَوَلَوْ كَانَ ءَابَآؤُهُمْ لَا يَعْلَمُونَ شَئْاٗ وَلَا يَهْتَدُونَۖ ١٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (774,7,'125',5,'Al-Mā’idah','المَائدة',3,5,107,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ عَلَيْكُمْ أَنفُسَكُمْۖ لَا يَضُرُّكُم مَّن ضَلَّ إِذَا اَ۪هْتَدَيْتُمْۖ إِلَى اَ۬للَّهِ مَرْجِعُكُمْ جَمِيعاٗ فَيُنَبِّئُكُم بِمَا كُنتُمْ تَعْمَلُونَۖ ١٠٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (775,7,'125',5,'Al-Mā’idah','المَائدة',5,10,108,'۞ يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ شَهَٰدَةُ بَيْنِكُمْ إِذَا حَضَرَ أَحَدَكُمُ اُ۬لْمَوْتُ حِينَ اَ۬لْوَصِيَّةِ اِ۪ثْنَٰنِ ذَوَا عَدْلٖ مِّنكُمْ أَوْ ءَاخَرَٰنِ مِنْ غَيْرِكُمْ إِنْ أَنتُمْ ضَرَبْتُمْ فِے اِ۬لْأَرْضِ فَأَصَٰبَتْكُم مُّصِيبَةُ اُ۬لْمَوْتِۖ تَحْبِسُونَهُمَا مِنۢ بَعْدِ اِ۬لصَّلَوٰةِ فَيُقْسِمَٰنِ بِاللَّهِ إِنِ اِ۪رْتَبْتُمْ لَا نَشْتَرِے بِهِۦ ثَمَناٗ وَلَوْ كَانَ ذَا قُرْبَىٰۖ وَلَا نَكْتُمُ شَهَٰدَةَ اَ۬للَّهِ إِنَّا إِذاٗ لَّمِنَ اَ۬لْأٓثِمِينَۖ ١٠٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (776,7,'125',5,'Al-Mā’idah','المَائدة',10,13,109,'فَإِنْ عُثِرَ عَلَىٰ أَنَّهُمَا اَ۪سْتَحَقَّا إِثْماٗ فَـَٔاخَرَٰنِ يَقُومَٰنِ مَقَامَهُمَا مِنَ اَ۬لذِينَ اَ۟سْتُحِقَّ عَلَيْهِمُ اُ۬لْأَوْلَيَٰنِ فَيُقْسِمَٰنِۖ بِاللَّهِ لَشَهَٰدَتُنَا أَحَقُّ مِن شَهَٰدَتِهِمَا وَمَا اَ۪عْتَدَيْنَاۖ إِنَّا إِذاٗ لَّمِنَ اَ۬لظَّٰلِمِينَۖ ١٠٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (777,7,'125',5,'Al-Mā’idah','المَائدة',13,15,110,'ذَٰلِكَ أَدْنَىٰ أَنْ يَّأْتُواْ بِالشَّهَٰدَةِ عَلَىٰ وَجْهِهَا أَوْ يَخَافُواْ أَن تُرَدَّ أَيْمَٰنُۢ بَعْدَ أَيْمَٰنِهِمْۖ وَاتَّقُواْ اُ۬للَّهَۖ وَاسْمَعُواْۖ وَاللَّهُ لَا يَهْدِے اِ۬لْقَوْمَ اَ۬لْفَٰسِقِينَۖ ١١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (778,7,'126',5,'Al-Mā’idah','المَائدة',1,2,111,'يَوْمَ يَجْمَعُ اُ۬للَّهُ اُ۬لرُّسُلَ فَيَقُولُ مَاذَا أُجِبْتُمْۖ قَالُواْ لَا عِلْمَ لَنَا إِنَّكَ أَنتَ عَلَّٰمُ اُ۬لْغُيُوبِۖ ١١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (779,7,'126',5,'Al-Mā’idah','المَائدة',2,10,112,'إِذْ قَالَ اَ۬للَّهُ يَٰعِيسَى اَ۪بْنَ مَرْيَمَ اَ۟ذْكُرْ نِعْمَتِے عَلَيْكَ وَعَلَىٰ وَٰلِدَتِكَ إِذْ أَيَّدتُّكَ بِرُوحِ اِ۬لْقُدُسِ تُكَلِّمُ اُ۬لنَّاسَ فِے اِ۬لْمَهْدِ وَكَهْلاٗۖ وَإِذْ عَلَّمْتُكَ اَ۬لْكِتَٰبَ وَالْحِكْمَةَ وَالتَّوْرَيٰةَ وَالْإِنجِيلَۖ وَإِذْ تَخْلُقُ مِنَ اَ۬لطِّينِ كَهَيْـَٔةِ اِ۬لطَّيْرِ بِإِذْنِے فَتَنفُخُ فِيهَا فَتَكُونُ طَٰٓئِراَۢ بِإِذْنِےۖ وَتُبْرِۓُ اُ۬لْأَكْمَهَ وَالْأَبْرَصَ بِإِذْنِےۖ وَإِذْ تُخْرِجُ اُ۬لْمَوْتَىٰ بِإِذْنِےۖ وَإِذْ كَفَفْتُ بَنِے إِسْرَآءِيلَ عَنكَ إِذْ جِئْتَهُم بِالْبَيِّنَٰتِ فَقَالَ اَ۬لذِينَ كَفَرُواْ مِنْهُمْ إِنْ هَٰذَا إِلَّا سِحْرٞ مُّبِينٞۖ ١١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (780,7,'126',5,'Al-Mā’idah','المَائدة',10,11,113,'۞ وَإِذْ أَوْحَيْتُ إِلَى اَ۬لْحَوَارِيِّۧنَ أَنْ ءَامِنُواْ بِے وَبِرَسُولِےۖ قَالُواْ ءَامَنَّا وَاشْهَدْ بِأَنَّنَا مُسْلِمُونَۖ ١١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (781,7,'126',5,'Al-Mā’idah','المَائدة',12,14,114,'إِذْ قَالَ اَ۬لْحَوَارِيُّونَ يَٰعِيسَى اَ۪بْنَ مَرْيَمَ هَلْ يَسْتَطِيعُ رَبُّكَ أَنْ يُّنَزِّلَ عَلَيْنَا مَآئِدَةٗ مِّنَ اَ۬لسَّمَآءِۖ قَالَ اَ۪تَّقُواْ اُ۬للَّهَ إِن كُنتُم مُّؤْمِنِينَۖ ١١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (782,7,'126',5,'Al-Mā’idah','المَائدة',14,15,115,'قَالُواْ نُرِيدُ أَن نَّأْكُلَ مِنْهَا وَتَطْمَئِنَّ قُلُوبُنَا وَنَعْلَمَ أَن قَدْ صَدَقْتَنَا وَنَكُونَ عَلَيْهَا مِنَ اَ۬لشَّٰهِدِينَۖ ١١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (783,7,'127',5,'Al-Mā’idah','المَائدة',1,3,116,'قَالَ عِيسَى اَ۪بْنُ مَرْيَمَ اَ۬للَّهُمَّ رَبَّنَا أَنزِلْ عَلَيْنَا مَآئِدَةٗ مِّنَ اَ۬لسَّمَآءِ تَكُونُ لَنَا عِيداٗ لِّأَوَّلِنَا وَءَاخِرِنَا وَءَايَةٗ مِّنكَۖ وَارْزُقْنَاۖ وَأَنتَ خَيْرُ اُ۬لرَّٰزِقِينَۖ ١١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (784,7,'127',5,'Al-Mā’idah','المَائدة',3,4,117,'قَالَ اَ۬للَّهُ إِنِّے مُنَزِّلُهَا عَلَيْكُمْ فَمَنْ يَّكْفُرْ بَعْدُ مِنكُمْ فَإِنِّيَ أُعَذِّبُهُۥ عَذَاباٗ لَّا أُعَذِّبُهُۥ أَحَداٗ مِّنَ اَ۬لْعَٰلَمِينَۖ ١١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (785,7,'127',5,'Al-Mā’idah','المَائدة',5,8,118,'وَإِذْ قَالَ اَ۬للَّهُ يَٰعِيسَى اَ۪بْنَ مَرْيَمَ ءَٰا۬نتَ قُلْتَ لِلنَّاسِ اِ۪تَّخِذُونِے وَأُمِّيَ إِلَٰهَيْنِ مِن دُونِ اِ۬للَّهِۖ قَالَ سُبْحَٰنَكَ مَا يَكُونُ لِيَ أَنْ أَقُولَ مَا لَيْسَ لِے بِحَقٍّۖ إِن كُنتُ قُلْتُهُۥ فَقَدْ عَلِمْتَهُۥۖ تَعْلَمُ مَا فِے نَفْسِے وَلَا أَعْلَمُ مَا فِے نَفْسِكَۖ إِنَّكَ أَنتَ عَلَّٰمُ اُ۬لْغُيُوبِۖ ١١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (786,7,'127',5,'Al-Mā’idah','المَائدة',8,11,119,'مَا قُلْتُ لَهُمْ إِلَّا مَا أَمَرْتَنِے بِهِۦ أَنُ اُ۟عْبُدُواْ اُ۬للَّهَ رَبِّے وَرَبَّكُمْۖ وَكُنتُ عَلَيْهِمْ شَهِيداٗ مَّا دُمْتُ فِيهِمْۖ فَلَمَّا تَوَفَّيْتَنِے كُنتَ أَنتَ اَ۬لرَّقِيبَ عَلَيْهِمْۖ وَأَنتَ عَلَىٰ كُلِّ شَےْءٖ شَهِيدٌۖ ١١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (787,7,'127',5,'Al-Mā’idah','المَائدة',11,12,120,'إِن تُعَذِّبْهُمْ فَإِنَّهُمْ عِبَادُكَۖ وَإِن تَغْفِرْ لَهُمْ فَإِنَّكَ أَنتَ اَ۬لْعَزِيزُ اُ۬لْحَكِيمُۖ ١٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (788,7,'127',5,'Al-Mā’idah','المَائدة',12,14,121,'قَالَ اَ۬للَّهُ هَٰذَا يَوْمَ يَنفَعُ اُ۬لصَّٰدِقِينَ صِدْقُهُمْۖ لَهُمْ جَنَّٰتٞ تَجْرِے مِن تَحْتِهَا اَ۬لْأَنْهَٰرُ خَٰلِدِينَ فِيهَا أَبَداٗۖ رَّضِيَ اَ۬للَّهُ عَنْهُمْ وَرَضُواْ عَنْهُۖ ذَٰلِكَ اَ۬لْفَوْزُ اُ۬لْعَظِيمُۖ ١٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (789,7,'127',5,'Al-Mā’idah','المَائدة',14,15,122,'لِلهِ مُلْكُ اُ۬لسَّمَٰوَٰتِ وَالْأَرْضِ وَمَا فِيهِنَّۖ وَهْوَ عَلَىٰ كُلِّ شَےْءٖ قَدِيرُۢۖ ١٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (790,7,'128',6,'Al-An‘ām','الأنعَام',3,4,1,'اِ۬لْحَمْدُ لِلهِ اِ۬لذِے خَلَقَ اَ۬لسَّمَٰوَٰتِ وَالْأَرْضَ وَجَعَلَ اَ۬لظُّلُمَٰتِ وَالنُّورَ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (791,7,'128',6,'Al-An‘ām','الأنعَام',4,4,2,'ثُمَّ اَ۬لذِينَ كَفَرُواْ بِرَبِّهِمْ يَعْدِلُونَۖ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (792,7,'128',6,'Al-An‘ām','الأنعَام',4,6,3,'هُوَ اَ۬لذِے خَلَقَكُم مِّن طِينٖ ثُمَّ قَضَىٰ أَجَلاٗۖ وَأَجَلٞ مُّسَمّىً عِندَهُۥۖ ثُمَّ أَنتُمْ تَمْتَرُونَۖ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (793,7,'128',6,'Al-An‘ām','الأنعَام',6,7,4,'وَهْوَ اَ۬للَّهُ فِے اِ۬لسَّمَٰوَٰتِ وَفِے اِ۬لْأَرْضِ يَعْلَمُ سِرَّكُمْ وَجَهْرَكُمْ وَيَعْلَمُ مَا تَكْسِبُونَۖ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (794,7,'128',6,'Al-An‘ām','الأنعَام',7,8,5,'وَمَا تَأْتِيهِم مِّنْ ءَايَةٖ مِّنْ ءَايَٰتِ رَبِّهِمْ إِلَّا كَانُواْ عَنْهَا مُعْرِضِينَۖ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (795,7,'128',6,'Al-An‘ām','الأنعَام',8,9,6,'فَقَدْ كَذَّبُواْ بِالْحَقِّ لَمَّا جَآءَهُمْۖ فَسَوْفَ يَأْتِيهِمْ أَنۢبَٰٓؤُاْ مَا كَانُواْ بِهِۦ يَسْتَهْزِءُونَۖ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (796,7,'128',6,'Al-An‘ām','الأنعَام',10,13,7,'أَلَمْ يَرَوْاْ كَمْ أَهْلَكْنَا مِن قَبْلِهِم مِّن قَرْنٖ مَّكَّنَّٰهُمْ فِے اِ۬لْأَرْضِ مَا لَمْ نُمَكِّن لَّكُمْۖ وَأَرْسَلْنَا اَ۬لسَّمَآءَ عَلَيْهِم مِّدْرَاراٗۖ وَجَعَلْنَا اَ۬لْأَنْهَٰرَ تَجْرِے مِن تَحْتِهِمْ فَأَهْلَكْنَٰهُم بِذُنُوبِهِمْۖ وَأَنشَأْنَا مِنۢ بَعْدِهِمْ قَرْناً ءَاخَرِينَۖ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (797,7,'128',6,'Al-An‘ām','الأنعَام',13,14,8,'وَلَوْ نَزَّلْنَا عَلَيْكَ كِتَٰباٗ فِے قِرْطَاسٖ فَلَمَسُوهُ بِأَيْدِيهِمْ لَقَالَ اَ۬لذِينَ كَفَرُواْ إِنْ هَٰذَا إِلَّا سِحْرٞ مُّبِينٞۖ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (798,7,'128',6,'Al-An‘ām','الأنعَام',14,15,9,'وَقَالُواْ لَوْلَا أُنزِلَ عَلَيْهِ مَلَكٞۖ وَلَوْ أَنزَلْنَا مَلَكاٗ لَّقُضِيَ اَ۬لْأَمْرُ ثُمَّ لَا يُنظَرُونَۖ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (799,7,'129',6,'Al-An‘ām','الأنعَام',1,2,10,'وَلَوْ جَعَلْنَٰهُ مَلَكاٗ لَّجَعَلْنَٰهُ رَجُلاٗ وَلَلَبَسْنَا عَلَيْهِم مَّا يَلْبِسُونَۖ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (800,7,'129',6,'Al-An‘ām','الأنعَام',2,3,11,'وَلَقَدُ اُ۟سْتُهْزِۓَ بِرُسُلٖ مِّن قَبْلِكَ فَحَاقَ بِالذِينَ سَخِرُواْ مِنْهُم مَّا كَانُواْ بِهِۦ يَسْتَهْزِءُونَۖ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (801,7,'129',6,'Al-An‘ām','الأنعَام',3,4,12,'قُلْ سِيرُواْ فِے اِ۬لْأَرْضِ ثُمَّ اَ۟نظُرُواْ كَيْفَ كَانَ عَٰقِبَةُ اُ۬لْمُكَذِّبِينَۖ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (802,7,'129',6,'Al-An‘ām','الأنعَام',5,7,13,'قُل لِّمَن مَّا فِے اِ۬لسَّمَٰوَٰتِ وَالْأَرْضِ قُل لِّلهِۖ كَتَبَ عَلَىٰ نَفْسِهِ اِ۬لرَّحْمَةَۖ لَيَجْمَعَنَّكُمْ إِلَىٰ يَوْمِ اِ۬لْقِيَٰمَةِ لَا رَيْبَ فِيهِۖ اِ۬لذِينَ خَسِرُواْ أَنفُسَهُمْ فَهُمْ لَا يُؤْمِنُونَۖ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (803,7,'129',6,'Al-An‘ām','الأنعَام',7,8,14,'۞ وَلَهُۥ مَا سَكَنَ فِے اِ۬ليْلِ وَالنَّهَارِۖ وَهْوَ اَ۬لسَّمِيعُ اُ۬لْعَلِيمُۖ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (804,7,'129',6,'Al-An‘ām','الأنعَام',8,11,15,'قُلْ أَغَيْرَ اَ۬للَّهِ أَتَّخِذُ وَلِيّاٗ فَاطِرِ اِ۬لسَّمَٰوَٰتِ وَالْأَرْضِۖ وَهْوَ يُطْعِمُ وَلَا يُطْعَمُۖ قُلْ إِنِّيَ أُمِرْتُ أَنْ أَكُونَ أَوَّلَ مَنْ أَسْلَمَۖ وَلَا تَكُونَنَّ مِنَ اَ۬لْمُشْرِكِينَۖ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (805,7,'129',6,'Al-An‘ām','الأنعَام',11,12,16,'قُلْ إِنِّيَ أَخَافُ إِنْ عَصَيْتُ رَبِّے عَذَابَ يَوْمٍ عَظِيمٖۖ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (806,7,'129',6,'Al-An‘ām','الأنعَام',12,13,17,'مَّنْ يُّصْرَفْ عَنْهُ يَوْمَئِذٖ فَقَدْ رَحِمَهُۥۖ وَذَٰلِكَ اَ۬لْفَوْزُ اُ۬لْمُبِينُۖ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (807,7,'129',6,'Al-An‘ām','الأنعَام',13,14,18,'وَإِنْ يَّمْسَسْكَ اَ۬للَّهُ بِضُرّٖ فَلَا كَاشِفَ لَهُۥ إِلَّا هُوَۖ وَإِنْ يَّمْسَسْكَ بِخَيْرٖ فَهْوَ عَلَىٰ كُلِّ شَےْءٖ قَدِيرٞۖ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (808,7,'129',6,'Al-An‘ām','الأنعَام',15,15,19,'وَهْوَ اَ۬لْقَاهِرُ فَوْقَ عِبَادِهِۦۖ وَهْوَ اَ۬لْحَكِيمُ اُ۬لْخَبِيرُۖ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (809,7,'130',6,'Al-An‘ām','الأنعَام',1,4,20,'قُلْ أَيُّ شَےْءٍ أَكْبَرُ شَهَٰدَةٗ قُلِ اِ۬للَّهُۖ شَهِيدُۢ بَيْنِے وَبَيْنَكُمْۖ وَأُوحِيَ إِلَيَّ هَٰذَا اَ۬لْقُرْءَانُ لِأُنذِرَكُم بِهِۦ وَمَنۢ بَلَغَۖ أَٰئِنَّكُمْ لَتَشْهَدُونَ أَنَّ مَعَ اَ۬للَّهِ ءَالِهَةً أُخْرَىٰ قُل لَّا أَشْهَدُۖ قُلْ إِنَّمَا هُوَ إِلَٰهٞ وَٰحِدٞۖ وَإِنَّنِے بَرِےٓءٞ مِّمَّا تُشْرِكُونَۖ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (810,7,'130',6,'Al-An‘ām','الأنعَام',4,5,21,'اَ۬لذِينَ ءَاتَيْنَٰهُمُ اُ۬لْكِتَٰبَ يَعْرِفُونَهُۥ كَمَا يَعْرِفُونَ أَبْنَآءَهُمُۖ اُ۬لذِينَ خَسِرُواْ أَنفُسَهُمْ فَهُمْ لَا يُؤْمِنُونَۖ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (811,7,'130',6,'Al-An‘ām','الأنعَام',5,6,22,'وَمَنْ أَظْلَمُ مِمَّنِ اِ۪فْتَرَىٰ عَلَى اَ۬للَّهِ كَذِباً أَوْ كَذَّبَ بِـَٔايَٰتِهِۦۖ إِنَّهُۥ لَا يُفْلِحُ اُ۬لظَّٰلِمُونَۖ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (812,7,'130',6,'Al-An‘ām','الأنعَام',6,8,23,'وَيَوْمَ نَحْشُرُهُمْ جَمِيعاٗ ثُمَّ نَقُولُ لِلذِينَ أَشْرَكُواْ أَيْنَ شُرَكَآؤُكُمُ اُ۬لذِينَ كُنتُمْ تَزْعُمُونَۖ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (813,7,'130',6,'Al-An‘ām','الأنعَام',8,9,24,'ثُمَّ لَمْ تَكُن فِتْنَتَهُمْ إِلَّا أَن قَالُواْ وَاللَّهِ رَبِّنَا مَا كُنَّا مُشْرِكِينَۖ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (814,7,'130',6,'Al-An‘ām','الأنعَام',9,10,25,'اَ۟نظُرْ كَيْفَ كَذَبُواْ عَلَىٰ أَنفُسِهِمْ وَضَلَّ عَنْهُم مَّا كَانُواْ يَفْتَرُونَۖ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (815,7,'130',6,'Al-An‘ām','الأنعَام',10,13,26,'وَمِنْهُم مَّنْ يَّسْتَمِعُ إِلَيْكَۖ وَجَعَلْنَا عَلَىٰ قُلُوبِهِمْ أَكِنَّةً أَنْ يَّفْقَهُوهُ وَفِے ءَاذَانِهِمْ وَقْراٗۖ وَإِنْ يَّرَوْاْ كُلَّ ءَايَةٖ لَّا يُؤْمِنُواْ بِهَاۖ حَتَّىٰ إِذَا جَآءُوكَ يُجَٰدِلُونَكَ يَقُولُ اُ۬لذِينَ كَفَرُواْ إِنْ هَٰذَا إِلَّا أَسَٰطِيرُ اُ۬لْأَوَّلِينَۖ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (816,7,'130',6,'Al-An‘ām','الأنعَام',13,14,27,'۞ وَهُمْ يَنْهَوْنَ عَنْهُ وَيَنْـَٔوْنَ عَنْهُۖ وَإِنْ يُّهْلِكُونَ إِلَّا أَنفُسَهُمْ وَمَا يَشْعُرُونَۖ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (817,7,'130',6,'Al-An‘ām','الأنعَام',14,15,28,'وَلَوْ تَرَىٰ إِذْ وُقِفُواْ عَلَى اَ۬لنَّارِ فَقَالُواْ يَٰلَيْتَنَا نُرَدُّۖ وَلَا نُكَذِّبُ بِـَٔايَٰتِ رَبِّنَا وَنَكُونُ مِنَ اَ۬لْمُؤْمِنِينَۖ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (818,7,'131',6,'Al-An‘ām','الأنعَام',1,2,29,'بَلْ بَدَا لَهُم مَّا كَانُواْ يُخْفُونَ مِن قَبْلُۖ وَلَوْ رُدُّواْ لَعَادُواْ لِمَا نُهُواْ عَنْهُۖ وَإِنَّهُمْ لَكَٰذِبُونَۖ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (819,7,'131',6,'Al-An‘ām','الأنعَام',2,3,30,'وَقَالُواْ إِنْ هِيَ إِلَّا حَيَاتُنَا اَ۬لدُّنْيَا وَمَا نَحْنُ بِمَبْعُوثِينَۖ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (820,7,'131',6,'Al-An‘ām','الأنعَام',3,4,31,'وَلَوْ تَرَىٰ إِذْ وُقِفُواْ عَلَىٰ رَبِّهِمْ قَالَ أَلَيْسَ هَٰذَا بِالْحَقِّۖ قَالُواْ بَلَىٰ وَرَبِّنَاۖ قَالَ فَذُوقُواْ اُ۬لْعَذَابَ بِمَا كُنتُمْ تَكْفُرُونَۖ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (821,7,'131',6,'Al-An‘ām','الأنعَام',5,7,32,'قَدْ خَسِرَ اَ۬لذِينَ كَذَّبُواْ بِلِقَآءِ اِ۬للَّهِ حَتَّىٰ إِذَا جَآءَتْهُمُ اُ۬لسَّاعَةُ بَغْتَةٗ قَالُواْ يَٰحَسْرَتَنَا عَلَىٰ مَا فَرَّطْنَا فِيهَا وَهُمْ يَحْمِلُونَ أَوْزَارَهُمْ عَلَىٰ ظُهُورِهِمْۖ أَلَا سَآءَ مَا يَزِرُونَۖ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (822,7,'131',6,'Al-An‘ām','الأنعَام',7,8,33,'وَمَا اَ۬لْحَيَوٰةُ اُ۬لدُّنْيَا إِلَّا لَعِبٞ وَلَهْوٞۖ وَلَلدَّارُ اُ۬لْأٓخِرَةُ خَيْرٞ لِّلذِينَ يَتَّقُونَۖ أَفَلَا تَعْقِلُونَۖ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (823,7,'131',6,'Al-An‘ām','الأنعَام',9,10,34,'قَدْ نَعْلَمُ إِنَّهُۥ لَيُحْزِنُكَ اَ۬لذِے يَقُولُونَۖ فَإِنَّهُمْ لَا يُكْذِبُونَكَۖ وَلَٰكِنَّ اَ۬لظَّٰلِمِينَ بِـَٔايَٰتِ اِ۬للَّهِ يَجْحَدُونَۖ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (824,7,'131',6,'Al-An‘ām','الأنعَام',10,12,35,'وَلَقَدْ كُذِّبَتْ رُسُلٞ مِّن قَبْلِكَ فَصَبَرُواْ عَلَىٰ مَا كُذِّبُواْ وَأُوذُواْ حَتَّىٰ أَتَيٰهُمْ نَصْرُنَاۖ وَلَا مُبَدِّلَ لِكَلِمَٰتِ اِ۬للَّهِۖ وَلَقَدْ جَآءَكَ مِن نَّبَإِےْ اِ۬لْمُرْسَلِينَۖ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (825,7,'131',6,'Al-An‘ām','الأنعَام',13,15,36,'وَإِن كَانَ كَبُرَ عَلَيْكَ إِعْرَاضُهُمْ فَإِنِ اِ۪سْتَطَعْتَ أَن تَبْتَغِيَ نَفَقاٗ فِے اِ۬لْأَرْضِ أَوْ سُلَّماٗ فِے اِ۬لسَّمَآءِ فَتَأْتِيَهُم بِـَٔايَةٖۖ وَلَوْ شَآءَ اَ۬للَّهُ لَجَمَعَهُمْ عَلَى اَ۬لْهُدَىٰۖ فَلَا تَكُونَنَّ مِنَ اَ۬لْجَٰهِلِينَۖ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (826,7,'132',6,'Al-An‘ām','الأنعَام',1,2,37,'۞ إِنَّمَا يَسْتَجِيبُ اُ۬لذِينَ يَسْمَعُونَۖ وَالْمَوْتَىٰ يَبْعَثُهُمُ اُ۬للَّهُۖ ثُمَّ إِلَيْهِ يُرْجَعُونَۖ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (827,7,'132',6,'Al-An‘ām','الأنعَام',2,3,38,'وَقَالُواْ لَوْلَا نُزِّلَ عَلَيْهِ ءَايَةٞ مِّن رَّبِّهِۦۖ قُلْ إِنَّ اَ۬للَّهَ قَادِرٌ عَلَىٰ أَنْ يُّنَزِّلَ ءَايَةٗۖ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَۖ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (828,7,'132',6,'Al-An‘ām','الأنعَام',3,5,39,'وَمَا مِن دَآبَّةٖ فِے اِ۬لْأَرْضِ وَلَا طَٰٓئِرٖ يَطِيرُ بِجَنَاحَيْهِ إِلَّا أُمَمٌ أَمْثَالُكُمۖ مَّا فَرَّطْنَا فِے اِ۬لْكِتَٰبِ مِن شَےْءٖۖ ثُمَّ إِلَىٰ رَبِّهِمْ يُحْشَرُونَۖ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (829,7,'132',6,'Al-An‘ām','الأنعَام',6,7,40,'وَالذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا صُمّٞ وَبُكْمٞ فِے اِ۬لظُّلُمَٰتِۖ مَنْ يَّشَإِ اِ۬للَّهُ يُضْلِلْهُۖ وَمَنْ يَّشَأْ يَجْعَلْهُ عَلَىٰ صِرَٰطٖ مُّسْتَقِيمٖۖ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (830,7,'132',6,'Al-An‘ām','الأنعَام',7,9,41,'قُلْ أَرَٰ۬يْتَكُمْ إِنْ أَتَيٰكُمْ عَذَابُ اُ۬للَّهِ أَوْ أَتَتْكُمُ اُ۬لسَّاعَةُ أَغَيْرَ اَ۬للَّهِ تَدْعُونَ إِن كُنتُمْ صَٰدِقِينَۖ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (831,7,'132',6,'Al-An‘ām','الأنعَام',9,10,42,'بَلْ إِيَّاهُ تَدْعُونَ فَيَكْشِفُ مَا تَدْعُونَ إِلَيْهِ إِن شَآءَ وَتَنسَوْنَ مَا تُشْرِكُونَۖ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (832,7,'132',6,'Al-An‘ām','الأنعَام',10,12,43,'وَلَقَدْ أَرْسَلْنَا إِلَىٰ أُمَمٖ مِّن قَبْلِكَ فَأَخَذْنَٰهُم بِالْبَأْسَآءِ وَالضَّرَّآءِ لَعَلَّهُمْ يَتَضَرَّعُونَۖ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (833,7,'132',6,'Al-An‘ām','الأنعَام',12,13,44,'فَلَوْلَا إِذْ جَآءَهُم بَأْسُنَا تَضَرَّعُواْۖ وَلَٰكِن قَسَتْ قُلُوبُهُمْ وَزَيَّنَ لَهُمُ اُ۬لشَّيْطَٰنُ مَا كَانُواْ يَعْمَلُونَۖ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (834,7,'132',6,'Al-An‘ām','الأنعَام',13,15,45,'فَلَمَّا نَسُواْ مَا ذُكِّرُواْ بِهِۦ فَتَحْنَا عَلَيْهِمْ أَبْوَٰبَ كُلِّ شَےْءٍۖ حَتَّىٰ إِذَا فَرِحُواْ بِمَا أُوتُواْ أَخَذْنَٰهُم بَغْتَةٗ فَإِذَا هُم مُّبْلِسُونَۖ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (835,7,'133',6,'Al-An‘ām','الأنعَام',1,1,46,'فَقُطِعَ دَابِرُ اُ۬لْقَوْمِ اِ۬لذِينَ ظَلَمُواْۖ وَالْحَمْدُ لِلهِ رَبِّ اِ۬لْعَٰلَمِينَۖ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (836,7,'133',6,'Al-An‘ām','الأنعَام',2,4,47,'قُلْ أَرَٰ۬يْتُمْ إِنْ أَخَذَ اَ۬للَّهُ سَمْعَكُمْ وَأَبْصَٰرَكُمْ وَخَتَمَ عَلَىٰ قُلُوبِكُم مَّنْ إِلَٰهٌ غَيْرُ اُ۬للَّهِ يَأْتِيكُم بِهِۖ اِ۟نظُرْ كَيْفَ نُصَرِّفُ اُ۬لْأٓيَٰتِ ثُمَّ هُمْ يَصْدِفُونَۖ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (837,7,'133',6,'Al-An‘ām','الأنعَام',4,5,48,'قُلْ أَرَٰ۬يْتَكُمْ إِنْ أَتَيٰكُمْ عَذَابُ اُ۬للَّهِ بَغْتَةً أَوْ جَهْرَةً هَلْ يُهْلَكُ إِلَّا اَ۬لْقَوْمُ اُ۬لظَّٰلِمُونَۖ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (838,7,'133',6,'Al-An‘ām','الأنعَام',5,7,49,'۞ وَمَا نُرْسِلُ اُ۬لْمُرْسَلِينَ إِلَّا مُبَشِّرِينَ وَمُنذِرِينَۖ فَمَنْ ءَامَنَ وَأَصْلَحَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَۖ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (839,7,'133',6,'Al-An‘ām','الأنعَام',7,8,50,'وَالذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا يَمَسُّهُمُ اُ۬لْعَذَابُ بِمَا كَانُواْ يَفْسُقُونَۖ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (840,7,'133',6,'Al-An‘ām','الأنعَام',8,11,51,'قُل لَّا أَقُولُ لَكُمْ عِندِے خَزَآئِنُ اُ۬للَّهِ وَلَا أَعْلَمُ اُ۬لْغَيْبَ وَلَا أَقُولُ لَكُمْ إِنِّے مَلَكٌۖ إِنْ أَتَّبِعُ إِلَّا مَا يُوحَىٰ إِلَيَّۖ قُلْ هَلْ يَسْتَوِے اِ۬لْأَعْمَىٰ وَالْبَصِيرُۖ أَفَلَا تَتَفَكَّرُونَۖ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (841,7,'133',6,'Al-An‘ām','الأنعَام',11,12,52,'وَأَنذِرْ بِهِ اِ۬لذِينَ يَخَافُونَ أَنْ يُّحْشَرُواْ إِلَىٰ رَبِّهِمْ لَيْسَ لَهُم مِّن دُونِهِۦ وَلِيّٞ وَلَا شَفِيعٞ لَّعَلَّهُمْ يَتَّقُونَۖ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (842,7,'133',6,'Al-An‘ām','الأنعَام',13,15,53,'وَلَا تَطْرُدِ اِ۬لذِينَ يَدْعُونَ رَبَّهُم بِالْغَدَوٰةِ وَالْعَشِيِّ يُرِيدُونَ وَجْهَهُۥ مَا عَلَيْكَ مِنْ حِسَابِهِم مِّن شَےْءٖ وَمَا مِنْ حِسَابِكَ عَلَيْهِم مِّن شَےْءٖ فَتَطْرُدَهُمْ فَتَكُونَ مِنَ اَ۬لظَّٰلِمِينَۖ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (843,7,'134',6,'Al-An‘ām','الأنعَام',1,2,54,'وَكَذَٰلِكَ فَتَنَّا بَعْضَهُم بِبَعْضٖ لِّيَقُولُواْ أَهَٰؤُلَآءِ مَنَّ اَ۬للَّهُ عَلَيْهِم مِّنۢ بَيْنِنَاۖ أَلَيْسَ اَ۬للَّهُ بِأَعْلَمَ بِالشَّٰكِرِينَۖ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (844,7,'134',6,'Al-An‘ām','الأنعَام',2,5,55,'وَإِذَا جَآءَكَ اَ۬لذِينَ يُؤْمِنُونَ بِـَٔايَٰتِنَا فَقُلْ سَلَٰمٌ عَلَيْكُمْۖ كَتَبَ رَبُّكُمْ عَلَىٰ نَفْسِهِ اِ۬لرَّحْمَةَ أَنَّهُۥ مَنْ عَمِلَ مِنكُمْ سُوٓءاَۢ بِجَهَٰلَةٖ ثُمَّ تَابَ مِنۢ بَعْدِهِۦ وَأَصْلَحَ فَإِنَّهُۥ غَفُورٞ رَّحِيمٞۖ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (845,7,'134',6,'Al-An‘ām','الأنعَام',6,6,56,'وَكَذَٰلِكَ نُفَصِّلُ اُ۬لْأٓيَٰتِ وَلِتَسْتَبِينَ سَبِيلَ اَ۬لْمُجْرِمِينَۖ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (846,7,'134',6,'Al-An‘ām','الأنعَام',7,8,57,'قُلْ إِنِّے نُهِيتُ أَنْ أَعْبُدَ اَ۬لذِينَ تَدْعُونَ مِن دُونِ اِ۬للَّهِۖ قُل لَّا أَتَّبِعُ أَهْوَآءَكُمْۖ قَدْ ضَلَلْتُ إِذاٗ وَمَا أَنَا مِنَ اَ۬لْمُهْتَدِينَۖ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (847,7,'134',6,'Al-An‘ām','الأنعَام',9,11,58,'قُلْ إِنِّے عَلَىٰ بَيِّنَةٖ مِّن رَّبِّے وَكَذَّبْتُم بِهِۦۖ مَا عِندِے مَا تَسْتَعْجِلُونَ بِهِۦۖ إِنِ اِ۬لْحُكْمُ إِلَّا لِلهِۖ يَقُصُّ اُ۬لْحَقَّۖ وَهْوَ خَيْرُ اُ۬لْفَٰصِلِينَۖ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (848,7,'134',6,'Al-An‘ām','الأنعَام',11,12,59,'قُل لَّوْ أَنَّ عِندِے مَا تَسْتَعْجِلُونَ بِهِۦ لَقُضِيَ اَ۬لْأَمْرُ بَيْنِے وَبَيْنَكُمْۖ وَاللَّهُ أَعْلَمُ بِالظَّٰلِمِينَۖ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (849,7,'134',6,'Al-An‘ām','الأنعَام',12,15,60,'۞ وَعِندَهُۥ مَفَاتِحُ اُ۬لْغَيْبِ لَا يَعْلَمُهَا إِلَّا هُوَۖ وَيَعْلَمُ مَا فِے اِ۬لْبَرِّ وَالْبَحْرِۖ وَمَا تَسْقُطُ مِنْ وَّرَقَةٍ إِلَّا يَعْلَمُهَا وَلَا حَبَّةٖ فِے ظُلُمَٰتِ اِ۬لْأَرْضِ وَلَا رَطْبٖ وَلَا يَابِسٍ إِلَّا فِے كِتَٰبٖ مُّبِينٖۖ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (850,7,'135',6,'Al-An‘ām','الأنعَام',1,3,61,'وَهْوَ اَ۬لذِے يَتَوَفَّيٰكُم بِاليْلِ وَيَعْلَمُ مَا جَرَحْتُم بِالنَّهَارِ ثُمَّ يَبْعَثُكُمْ فِيهِ لِيُقْضَىٰ أَجَلٞ مُّسَمّىٗۖ ثُمَّ إِلَيْهِ مَرْجِعُكُمْ ثُمَّ يُنَبِّئُكُم بِمَا كُنتُمْ تَعْمَلُونَۖ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (851,7,'135',6,'Al-An‘ām','الأنعَام',3,5,62,'وَهْوَ اَ۬لْقَاهِرُ فَوْقَ عِبَادِهِۦۖ وَيُرْسِلُ عَلَيْكُمْ حَفَظَةًۖ حَتَّىٰ إِذَا جَا أَحَدَكُمُ اُ۬لْمَوْتُ تَوَفَّتْهُ رُسُلُنَا وَهُمْ لَا يُفَرِّطُونَۖ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (852,7,'135',6,'Al-An‘ām','الأنعَام',5,6,63,'ثُمَّ رُدُّواْ إِلَى اَ۬للَّهِ مَوْلَيٰهُمُ اُ۬لْحَقِّۖ أَلَا لَهُ اُ۬لْحُكْمُۖ وَهْوَ أَسْرَعُ اُ۬لْحَٰسِبِينَۖ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (853,7,'135',6,'Al-An‘ām','الأنعَام',6,8,64,'قُلْ مَنْ يُّنَجِّيكُم مِّن ظُلُمَٰتِ اِ۬لْبَرِّ وَالْبَحْرِ تَدْعُونَهُۥ تَضَرُّعاٗ وَخُفْيَةٗ لَّئِنْ أَنجَيْتَنَا مِنْ هَٰذِهِۦ لَنَكُونَنَّ مِنَ اَ۬لشَّٰكِرِينَۖ ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (854,7,'135',6,'Al-An‘ām','الأنعَام',8,9,65,'قُلِ اِ۬للَّهُ يُنجِيكُم مِّنْهَا وَمِن كُلِّ كَرْبٖ ثُمَّ أَنتُمْ تُشْرِكُونَۖ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (855,7,'135',6,'Al-An‘ām','الأنعَام',9,11,66,'قُلْ هُوَ اَ۬لْقَادِرُ عَلَىٰ أَنْ يَّبْعَثَ عَلَيْكُمْ عَذَاباٗ مِّن فَوْقِكُمْ أَوْ مِن تَحْتِ أَرْجُلِكُمْ أَوْ يَلْبِسَكُمْ شِيَعاٗ وَيُذِيقَ بَعْضَكُم بَأْسَ بَعْضٍۖ اُ۟نظُرْ كَيْفَ نُصَرِّفُ اُ۬لْأٓيَٰتِ لَعَلَّهُمْ يَفْقَهُونَۖ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (856,7,'135',6,'Al-An‘ām','الأنعَام',11,13,67,'وَكَذَّبَ بِهِۦ قَوْمُكَ وَهْوَ اَ۬لْحَقُّۖ قُل لَّسْتُ عَلَيْكُم بِوَكِيلٖۖ لِّكُلِّ نَبَإٖ مُّسْتَقَرّٞۖ وَسَوْفَ تَعْلَمُونَۖ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (857,7,'135',6,'Al-An‘ām','الأنعَام',13,15,68,'وَإِذَا رَأَيْتَ اَ۬لذِينَ يَخُوضُونَ فِے ءَايَٰتِنَا فَأَعْرِضْ عَنْهُمْ حَتَّىٰ يَخُوضُواْ فِے حَدِيثٍ غَيْرِهِۦۖ وَإِمَّا يُنسِيَنَّكَ اَ۬لشَّيْطَٰنُ فَلَا تَقْعُدْ بَعْدَ اَ۬لذِّكْرَىٰ مَعَ اَ۬لْقَوْمِ اِ۬لظَّٰلِمِينَۖ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (858,7,'136',6,'Al-An‘ām','الأنعَام',1,2,69,'وَمَا عَلَى اَ۬لذِينَ يَتَّقُونَ مِنْ حِسَابِهِم مِّن شَےْءٖۖ وَلَٰكِن ذِكْرَىٰ لَعَلَّهُمْ يَتَّقُونَۖ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (859,7,'136',6,'Al-An‘ām','الأنعَام',2,7,70,'۞ وَذَرِ اِ۬لذِينَ اَ۪تَّخَذُواْ دِينَهُمْ لَعِباٗ وَلَهْواٗ وَغَرَّتْهُمُ اُ۬لْحَيَوٰةُ اُ۬لدُّنْيَاۖ وَذَكِّرْ بِهِۦ أَن تُبْسَلَ نَفْسُۢ بِمَا كَسَبَتْ لَيْسَ لَهَا مِن دُونِ اِ۬للَّهِ وَلِيّٞ وَلَا شَفِيعٞۖ وَإِن تَعْدِلْ كُلَّ عَدْلٖ لَّا يُؤْخَذْ مِنْهَاۖ أُوْلَٰٓئِكَ اَ۬لذِينَ أُبْسِلُواْ بِمَا كَسَبُواْ لَهُمْ شَرَابٞ مِّنْ حَمِيمٖ وَعَذَابٌ أَلِيمُۢ بِمَا كَانُواْ يَكْفُرُونَۖ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (860,7,'136',6,'Al-An‘ām','الأنعَام',7,11,71,'قُلْ أَنَدْعُواْ مِن دُونِ اِ۬للَّهِ مَا لَا يَنفَعُنَا وَلَا يَضُرُّنَا وَنُرَدُّ عَلَىٰ أَعْقَابِنَا بَعْدَ إِذْ هَدَيٰنَا اَ۬للَّهُ كَالذِے اِ۪سْتَهْوَتْهُ اُ۬لشَّيَٰطِينُ فِے اِ۬لْأَرْضِ حَيْرَانَۖ لَهُۥ أَصْحَٰبٞ يَدْعُونَهُۥ إِلَى اَ۬لْهُدَى اَ۪ئْتِنَاۖ قُلْ إِنَّ هُدَى اَ۬للَّهِ هُوَ اَ۬لْهُدَىٰ وَأُمِرْنَا لِنُسْلِمَ لِرَبِّ اِ۬لْعَٰلَمِينَ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (861,7,'136',6,'Al-An‘ām','الأنعَام',12,12,72,'وَأَنْ أَقِيمُواْ اُ۬لصَّلَوٰةَ وَاتَّقُوهُۖ وَهْوَ اَ۬لذِے إِلَيْهِ تُحْشَرُونَۖ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (862,7,'136',6,'Al-An‘ām','الأنعَام',13,14,73,'وَهْوَ اَ۬لذِے خَلَقَ اَ۬لسَّمَٰوَٰتِ وَالْأَرْضَ بِالْحَقِّۖ وَيَوْمَ يَقُولُ كُنۖ فَيَكُونُۖ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (863,7,'136',6,'Al-An‘ām','الأنعَام',14,15,74,'قَوْلُهُ اُ۬لْحَقُّۖ وَلَهُ اُ۬لْمُلْكُۖ يَوْمَ يُنفَخُ فِے اِ۬لصُّورِۖ عَٰلِمُ اُ۬لْغَيْبِ وَالشَّهَٰدَةِۖ وَهْوَ اَ۬لْحَكِيمُ اُ۬لْخَبِيرُۖ ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (864,7,'137',6,'Al-An‘ām','الأنعَام',1,2,75,'وَإِذْ قَالَ إِبْرَٰهِيمُ لِأَبِيهِ ءَازَرَ أَتَتَّخِذُ أَصْنَاماً ءَالِهَةًۖ إِنِّيَ أَرَيٰكَ وَقَوْمَكَ فِے ضَلَٰلٖ مُّبِينٖۖ ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (865,7,'137',6,'Al-An‘ām','الأنعَام',2,3,76,'وَكَذَٰلِكَ نُرِے إِبْرَٰهِيمَ مَلَكُوتَ اَ۬لسَّمَٰوَٰتِ وَالْأَرْضِ وَلِيَكُونَ مِنَ اَ۬لْمُوقِنِينَۖ ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (866,7,'137',6,'Al-An‘ām','الأنعَام',4,5,77,'فَلَمَّا جَنَّ عَلَيْهِ اِ۬ليْلُ رَءَا كَوْكَباٗ قَالَ هَٰذَا رَبِّےۖ فَلَمَّا أَفَلَ قَالَ لَا أُحِبُّ اُ۬لْأٓفِلِينَۖ ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (867,7,'137',6,'Al-An‘ām','الأنعَام',5,7,78,'فَلَمَّا رَءَا اَ۬لْقَمَرَ بَازِغاٗ قَالَ هَٰذَا رَبِّےۖ فَلَمَّا أَفَلَ قَالَ لَئِن لَّمْ يَهْدِنِے رَبِّے لَأَكُونَنَّ مِنَ اَ۬لْقَوْمِ اِ۬لضَّآلِّينَۖ ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (868,7,'137',6,'Al-An‘ām','الأنعَام',7,8,79,'فَلَمَّا رَءَا اَ۬لشَّمْسَ بَازِغَةٗ قَالَ هَٰذَا رَبِّے هَٰذَا أَكْبَرُۖ فَلَمَّا أَفَلَتْ قَالَ يَٰقَوْمِ إِنِّے بَرِےٓءٞ مِّمَّا تُشْرِكُونَۖ ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (869,7,'137',6,'Al-An‘ām','الأنعَام',9,10,80,'إِنِّے وَجَّهْتُ وَجْهِيَ لِلذِے فَطَرَ اَ۬لسَّمَٰوَٰتِ وَالْأَرْضَ حَنِيفاٗۖ وَمَا أَنَا مِنَ اَ۬لْمُشْرِكِينَۖ ٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (870,7,'137',6,'Al-An‘ām','الأنعَام',10,13,81,'۞ وَحَآجَّهُۥ قَوْمُهُۥۖ قَالَ أَتُحَٰٓجُّونِے فِے اِ۬للَّهِ وَقَدْ هَدَيٰنِۖ وَلَا أَخَافُ مَا تُشْرِكُونَ بِهِۦ إِلَّا أَنْ يَّشَآءَ رَبِّے شَئْاٗۖ وَسِعَ رَبِّے كُلَّ شَےْءٍ عِلْماًۖ أَفَلَا تَتَذَكَّرُونَۖ ٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (871,7,'137',6,'Al-An‘ām','الأنعَام',13,15,82,'وَكَيْفَ أَخَافُ مَا أَشْرَكْتُمْۖ وَلَا تَخَافُونَ أَنَّكُمْ أَشْرَكْتُم بِاللَّهِ مَا لَمْ يُنَزِّلْ بِهِۦ عَلَيْكُمْ سُلْطَٰناٗۖ فَأَيُّ اُ۬لْفَرِيقَيْنِ أَحَقُّ بِالْأَمْنِ إِن كُنتُمْ تَعْلَمُونَۖ ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (872,7,'138',6,'Al-An‘ām','الأنعَام',1,2,83,'اَ۬لذِينَ ءَامَنُواْ وَلَمْ يَلْبِسُواْ إِيمَٰنَهُم بِظُلْمٍ أُوْلَٰٓئِكَ لَهُمُ اُ۬لْأَمْنُ وَهُم مُّهْتَدُونَۖ ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (873,7,'138',6,'Al-An‘ām','الأنعَام',2,3,84,'وَتِلْكَ حُجَّتُنَا ءَاتَيْنَٰهَا إِبْرَٰهِيمَ عَلَىٰ قَوْمِهِۦۖ نَرْفَعُ دَرَجَٰتِ مَن نَّشَآءُۖ اِ۪نَّ رَبَّكَ حَكِيمٌ عَلِيمٞۖ ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (874,7,'138',6,'Al-An‘ām','الأنعَام',4,6,85,'وَوَهَبْنَا لَهُۥ إِسْحَٰقَ وَيَعْقُوبَۖ كُلّاً هَدَيْنَاۖ وَنُوحاً هَدَيْنَا مِن قَبْلُۖ وَمِن ذُرِّيَّتِهِۦ دَاوُۥدَ وَسُلَيْمَٰنَ وَأَيُّوبَ وَيُوسُفَ وَمُوسَىٰ وَهَٰرُونَۖ وَكَذَٰلِكَ نَجْزِے اِ۬لْمُحْسِنِينَۖ ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (875,7,'138',6,'Al-An‘ām','الأنعَام',7,7,86,'وَزَكَرِيَّآءَ وَيَحْيَىٰ وَعِيسَىٰ وَإِلْيَاسَۖ كُلّٞ مِّنَ اَ۬لصَّٰلِحِينَۖ ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (876,7,'138',6,'Al-An‘ām','الأنعَام',8,9,87,'وَإِسْمَٰعِيلَ وَالْيَسَعَ وَيُونُسَ وَلُوطاٗۖ وَكُلّاٗ فَضَّلْنَا عَلَى اَ۬لْعَٰلَمِينَۖ ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (877,7,'138',6,'Al-An‘ām','الأنعَام',9,10,88,'وَمِنْ ءَابَآئِهِمْ وَذُرِّيَّٰتِهِمْ وَإِخْوَٰنِهِمْۖ وَاجْتَبَيْنَٰهُمْ وَهَدَيْنَٰهُمْ إِلَىٰ صِرَٰطٖ مُّسْتَقِيمٖۖ ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (878,7,'138',6,'Al-An‘ām','الأنعَام',10,12,89,'ذَٰلِكَ هُدَى اَ۬للَّهِ يَهْدِے بِهِۦ مَنْ يَّشَآءُ مِنْ عِبَادِهِۦۖ وَلَوْ أَشْرَكُواْ لَحَبِطَ عَنْهُم مَّا كَانُواْ يَعْمَلُونَۖ ٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (879,7,'138',6,'Al-An‘ām','الأنعَام',12,14,90,'أُوْلَٰٓئِكَ اَ۬لذِينَ ءَاتَيْنَٰهُمُ اُ۬لْكِتَٰبَ وَالْحُكْمَ وَالنُّبُوٓءَةَۖ فَإِنْ يَّكْفُرْ بِهَا هَٰؤُلَآءِ فَقَدْ وَكَّلْنَا بِهَا قَوْماٗ لَّيْسُواْ بِهَا بِكَٰفِرِينَۖ ٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (880,7,'138',6,'Al-An‘ām','الأنعَام',14,15,91,'أُوْلَٰٓئِكَ اَ۬لذِينَ هَدَى اَ۬للَّهُ فَبِهُدَيٰهُمُ اُ۪قْتَدِهْۖ قُل لَّا أَسْـَٔلُكُمْ عَلَيْهِ أَجْراًۖ إِنْ هُوَ إِلَّا ذِكْرَىٰ لِلْعَٰلَمِينَۖ ٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (881,7,'139',6,'Al-An‘ām','الأنعَام',1,5,92,'۞ وَمَا قَدَرُواْ اُ۬للَّهَ حَقَّ قَدْرِهِۦ إِذْ قَالُواْ مَا أَنزَلَ اَ۬للَّهُ عَلَىٰ بَشَرٖ مِّن شَےْءٖۖ قُلْ مَنْ أَنزَلَ اَ۬لْكِتَٰبَ اَ۬لذِے جَآءَ بِهِۦ مُوسَىٰ نُوراٗ وَهُدىٗ لِّلنَّاسِۖ تَجْعَلُونَهُۥ قَرَاطِيسَ تُبْدُونَهَا وَتُخْفُونَ كَثِيراٗۖ وَعُلِّمْتُم مَّا لَمْ تَعْلَمُواْ أَنتُمْ وَلَا ءَابَآؤُكُمْ قُلِ اِ۬للَّهُۖ ثُمَّ ذَرْهُمْ فِے خَوْضِهِمْ يَلْعَبُونَۖ ٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (882,7,'139',6,'Al-An‘ām','الأنعَام',5,7,93,'وَهَٰذَا كِتَٰبٌ أَنزَلْنَٰهُ مُبَٰرَكٞ مُّصَدِّقُ اُ۬لذِے بَيْنَ يَدَيْهِ وَلِتُنذِرَ أُمَّ اَ۬لْقُرَىٰ وَمَنْ حَوْلَهَاۖ وَالذِينَ يُؤْمِنُونَ بِالْأٓخِرَةِ يُؤْمِنُونَ بِهِۦ وَهُمْ عَلَىٰ صَلَاتِهِمْ يُحَافِظُونَۖ ٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (883,7,'139',6,'Al-An‘ām','الأنعَام',7,12,94,'وَمَنْ أَظْلَمُ مِمَّنِ اِ۪فْتَرَىٰ عَلَى اَ۬للَّهِ كَذِباً أَوْ قَالَ أُوحِيَ إِلَيَّ وَلَمْ يُوحَ إِلَيْهِ شَےْءٞ وَمَن قَالَ سَأُنزِلُ مِثْلَ مَا أَنزَلَ اَ۬للَّهُۖ وَلَوْ تَرَىٰ إِذِ اِ۬لظَّٰلِمُونَ فِے غَمَرَٰتِ اِ۬لْمَوْتِ وَالْمَلَٰٓئِكَةُ بَاسِطُواْ أَيْدِيهِمْ أَخْرِجُواْ أَنفُسَكُمُۖ اُ۬لْيَوْمَ تُجْزَوْنَ عَذَابَ اَ۬لْهُونِ بِمَا كُنتُمْ تَقُولُونَ عَلَى اَ۬للَّهِ غَيْرَ اَ۬لْحَقِّ وَكُنتُمْ عَنْ ءَايَٰتِهِۦ تَسْتَكْبِرُونَۖ ٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (884,7,'139',6,'Al-An‘ām','الأنعَام',12,15,95,'وَلَقَدْ جِئْتُمُونَا فُرَٰدَىٰ كَمَا خَلَقْنَٰكُمْ أَوَّلَ مَرَّةٖ وَتَرَكْتُم مَّا خَوَّلْنَٰكُمْ وَرَآءَ ظُهُورِكُمْۖ وَمَا نَرَىٰ مَعَكُمْ شُفَعَآءَكُمُ اُ۬لذِينَ زَعَمْتُمْ أَنَّهُمْ فِيكُمْ شُرَكَٰٓؤُاْۖ لَقَد تَّقَطَّعَ بَيْنَكُمْ وَضَلَّ عَنكُم مَّا كُنتُمْ تَزْعُمُونَۖ ٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (885,7,'140',6,'Al-An‘ām','الأنعَام',1,2,96,'۞ إِنَّ اَ۬للَّهَ فَٰلِقُ اُ۬لْحَبِّ وَالنَّوَىٰۖ يُخْرِجُ اُ۬لْحَيَّ مِنَ اَ۬لْمَيِّتِ وَمُخْرِجُ اُ۬لْمَيِّتِ مِنَ اَ۬لْحَيِّۖ ذَٰلِكُمُ اُ۬للَّهُۖ فَأَنَّىٰ تُؤْفَكُونَۖ ٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (886,7,'140',6,'Al-An‘ām','الأنعَام',2,4,97,'فَالِقُ اُ۬لْإِصْبَاحِ وَجَٰعِلُ اُ۬ليْلِ سَكَناٗۖ وَالشَّمْسَ وَالْقَمَرَ حُسْبَٰناٗۖ ذَٰلِكَ تَقْدِيرُ اُ۬لْعَزِيزِ اِ۬لْعَلِيمِۖ ٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (887,7,'140',6,'Al-An‘ām','الأنعَام',4,5,98,'وَهْوَ اَ۬لذِے جَعَلَ لَكُمُ اُ۬لنُّجُومَ لِتَهْتَدُواْ بِهَا فِے ظُلُمَٰتِ اِ۬لْبَرِّ وَالْبَحْرِۖ قَدْ فَصَّلْنَا اَ۬لْأٓيَٰتِ لِقَوْمٖ يَعْلَمُونَۖ ٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (888,7,'140',6,'Al-An‘ām','الأنعَام',6,7,99,'وَهْوَ اَ۬لذِے أَنشَأَكُم مِّن نَّفْسٖ وَٰحِدَةٖ فَمُسْتَقَرّٞ وَمُسْتَوْدَعٞۖ قَدْ فَصَّلْنَا اَ۬لْأٓيَٰتِ لِقَوْمٖ يَفْقَهُونَۖ ٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (889,7,'140',6,'Al-An‘ām','الأنعَام',7,12,100,'وَهْوَ اَ۬لذِے أَنزَلَ مِنَ اَ۬لسَّمَآءِ مَآءٗ فَأَخْرَجْنَا بِهِۦ نَبَاتَ كُلِّ شَےْءٖ فَأَخْرَجْنَا مِنْهُ خَضِراٗ نُّخْرِجُ مِنْهُ حَبّاٗ مُّتَرَاكِباٗ وَمِنَ اَ۬لنَّخْلِ مِن طَلْعِهَا قِنْوَانٞ دَانِيَةٞ وَجَنَّٰتٖ مِّنْ أَعْنَٰبٖ وَالزَّيْتُونَ وَالرُّمَّانَ مُشْتَبِهاٗ وَغَيْرَ مُتَشَٰبِهٍۖ اُ۟نظُرُواْ إِلَىٰ ثَمَرِهِۦ إِذَا أَثْمَرَ وَيَنْعِهِۦۖ إِنَّ فِے ذَٰلِكُمْ لَأٓيَٰتٖ لِّقَوْمٖ يُؤْمِنُونَۖ ١٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (890,7,'140',6,'Al-An‘ām','الأنعَام',12,13,101,'وَجَعَلُواْ لِلهِ شُرَكَآءَ اَ۬لْجِنَّ وَخَلَقَهُمْۖ وَخَرَّقُواْ لَهُۥ بَنِينَ وَبَنَٰتِۢ بِغَيْرِ عِلْمٖۖ سُبْحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يَصِفُونَۖ ١٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (891,7,'140',6,'Al-An‘ām','الأنعَام',14,15,102,'بَدِيعُ اُ۬لسَّمَٰوَٰتِ وَالْأَرْضِۖ أَنَّىٰ يَكُونُ لَهُۥ وَلَدٞ وَلَمْ تَكُن لَّهُۥ صَٰحِبَةٞۖ وَخَلَقَ كُلَّ شَےْءٖۖ وَهْوَ بِكُلِّ شَےْءٍ عَلِيمٞۖ ١٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (892,7,'141',6,'Al-An‘ām','الأنعَام',1,2,103,'ذَٰلِكُمُ اُ۬للَّهُ رَبُّكُمْۖ لَا إِلَٰهَ إِلَّا هُوَۖ خَٰلِقُ كُلِّ شَےْءٖۖ فَاعْبُدُوهُۖ وَهْوَ عَلَىٰ كُلِّ شَےْءٖ وَكِيلٞۖ ١٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (893,7,'141',6,'Al-An‘ām','الأنعَام',2,3,104,'۞ لَّا تُدْرِكُهُ اُ۬لْأَبْصَٰرُۖ وَهْوَ يُدْرِكُ اُ۬لْأَبْصَٰرَۖ وَهْوَ اَ۬للَّطِيفُ اُ۬لْخَبِيرُۖ ١٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (894,7,'141',6,'Al-An‘ām','الأنعَام',3,5,105,'قَدْ جَآءَكُم بَصَآئِرُ مِن رَّبِّكُمْۖ فَمَنْ أَبْصَرَ فَلِنَفْسِهِۦ وَمَنْ عَمِيَ فَعَلَيْهَاۖ وَمَا أَنَا عَلَيْكُم بِحَفِيظٖۖ ١٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (895,7,'141',6,'Al-An‘ām','الأنعَام',5,6,106,'وَكَذَٰلِكَ نُصَرِّفُ اُ۬لْأٓيَٰتِ وَلِيَقُولُواْ دَرَسْتَ وَلِنُبَيِّنَهُۥ لِقَوْمٖ يَعْلَمُونَۖ ١٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (896,7,'141',6,'Al-An‘ām','الأنعَام',6,7,107,'اَ۪تَّبِعْ مَا أُوحِيَ إِلَيْكَ مِن رَّبِّكَۖ لَا إِلَٰهَ إِلَّا هُوَۖ وَأَعْرِضْ عَنِ اِ۬لْمُشْرِكِينَۖ ١٠٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (897,7,'141',6,'Al-An‘ām','الأنعَام',8,9,108,'وَلَوْ شَآءَ اَ۬للَّهُ مَا أَشْرَكُواْۖ وَمَا جَعَلْنَٰكَ عَلَيْهِمْ حَفِيظاٗۖ وَمَا أَنتَ عَلَيْهِم بِوَكِيلٖۖ ١٠٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (898,7,'141',6,'Al-An‘ām','الأنعَام',9,11,109,'وَلَا تَسُبُّواْ اُ۬لذِينَ يَدْعُونَ مِن دُونِ اِ۬للَّهِ فَيَسُبُّواْ اُ۬للَّهَ عَدْواَۢ بِغَيْرِ عِلْمٖۖ كَذَٰلِكَ زَيَّنَّا لِكُلِّ أُمَّةٍ عَمَلَهُمْۖ ثُمَّ إِلَىٰ رَبِّهِم مَّرْجِعُهُمْ فَيُنَبِّئُهُم بِمَا كَانُواْ يَعْمَلُونَۖ ١٠٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (899,7,'141',6,'Al-An‘ām','الأنعَام',12,14,110,'وَأَقْسَمُواْ بِاللَّهِ جَهْدَ أَيْمَٰنِهِمْ لَئِن جَآءَتْهُمْ ءَايَةٞ لَّيُؤْمِنُنَّ بِهَاۖ قُلْ إِنَّمَا اَ۬لْأٓيَٰتُ عِندَ اَ۬للَّهِۖ وَمَا يُشْعِرُكُمْ أَنَّهَا إِذَا جَآءَتْ لَا يُؤْمِنُونَۖ ١١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (900,7,'141',6,'Al-An‘ām','الأنعَام',14,15,111,'وَنُقَلِّبُ أَفْـِٕدَتَهُمْ وَأَبْصَٰرَهُمْ كَمَا لَمْ يُؤْمِنُواْ بِهِۦ أَوَّلَ مَرَّةٖ وَنَذَرُهُمْ فِے طُغْيَٰنِهِمْ يَعْمَهُونَۖ ١١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (901,8,'142',6,'Al-An‘ām','الأنعَام',1,3,112,'۞ وَلَوْ أَنَّنَا نَزَّلْنَا إِلَيْهِمُ اُ۬لْمَلَٰٓئِكَةَ وَكَلَّمَهُمُ اُ۬لْمَوْتَىٰ وَحَشَرْنَا عَلَيْهِمْ كُلَّ شَےْءٖ قِبَلاٗ مَّا كَانُواْ لِيُؤْمِنُواْ إِلَّا أَنْ يَّشَآءَ اَ۬للَّهُۖ وَلَٰكِنَّ أَكْثَرَهُمْ يَجْهَلُونَۖ ١١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (902,8,'142',6,'Al-An‘ām','الأنعَام',3,6,113,'وَكَذَٰلِكَ جَعَلْنَا لِكُلِّ نَبِےٓءٍ عَدُوّاٗ شَيَٰطِينَ اَ۬لْإِنسِ وَالْجِنِّ يُوحِے بَعْضُهُمْ إِلَىٰ بَعْضٖ زُخْرُفَ اَ۬لْقَوْلِ غُرُوراٗۖ وَلَوْ شَآءَ رَبُّكَ مَا فَعَلُوهُۖ فَذَرْهُمْ وَمَا يَفْتَرُونَۖ ١١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (903,8,'142',6,'Al-An‘ām','الأنعَام',6,7,114,'وَلِتَصْغَىٰ إِلَيْهِ أَفْـِٕدَةُ اُ۬لذِينَ لَا يُؤْمِنُونَ بِالْأٓخِرَةِ وَلِيَرْضَوْهُ وَلِيَقْتَرِفُواْ مَا هُم مُّقْتَرِفُونَۖ ١١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (904,8,'142',6,'Al-An‘ām','الأنعَام',7,10,115,'أَفَغَيْرَ اَ۬للَّهِ أَبْتَغِے حَكَماٗ وَهْوَ اَ۬لذِے أَنزَلَ إِلَيْكُمُ اُ۬لْكِتَٰبَ مُفَصَّلاٗۖ وَالذِينَ ءَاتَيْنَٰهُمُ اُ۬لْكِتَٰبَ يَعْلَمُونَ أَنَّهُۥ مُنزَلٞ مِّن رَّبِّكَ بِالْحَقِّۖ فَلَا تَكُونَنَّ مِنَ اَ۬لْمُمْتَرِينَۖ ١١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (905,8,'142',6,'Al-An‘ām','الأنعَام',10,11,116,'وَتَمَّتْ كَلِمَٰتُ رَبِّكَ صِدْقاٗ وَعَدْلاٗۖ لَّا مُبَدِّلَ لِكَلِمَٰتِهِۦۖ وَهْوَ اَ۬لسَّمِيعُ اُ۬لْعَلِيمُۖ ١١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (906,8,'142',6,'Al-An‘ām','الأنعَام',12,13,117,'وَإِن تُطِعْ أَكْثَرَ مَن فِے اِ۬لْأَرْضِ يُضِلُّوكَ عَن سَبِيلِ اِ۬للَّهِۖ إِنْ يَّتَّبِعُونَ إِلَّا اَ۬لظَّنَّۖ وَإِنْ هُمْ إِلَّا يَخْرُصُونَۖ ١١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (907,8,'142',6,'Al-An‘ām','الأنعَام',13,14,118,'إِنَّ رَبَّكَ هُوَ أَعْلَمُ مَنْ يَّضِلُّ عَن سَبِيلِهِۦۖ وَهْوَ أَعْلَمُ بِالْمُهْتَدِينَۖ ١١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (908,8,'142',6,'Al-An‘ām','الأنعَام',14,15,119,'فَكُلُواْ مِمَّا ذُكِرَ اَ۪سْمُ اُ۬للَّهِ عَلَيْهِ إِن كُنتُم بِـَٔايَٰتِهِۦ مُؤْمِنِينَۖ ١١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (909,8,'143',6,'Al-An‘ām','الأنعَام',1,3,120,'وَمَا لَكُمْ أَلَّا تَأْكُلُواْ مِمَّا ذُكِرَ اَ۪سْمُ اُ۬للَّهِ عَلَيْهِ وَقَدْ فَصَّلَ لَكُم مَّا حَرَّمَ عَلَيْكُمْ إِلَّا مَا اَ۟ضْطُرِرْتُمْ إِلَيْهِۖ وَإِنَّ كَثِيراٗ لَّيَضِلُّونَ بِأَهْوَآئِهِم بِغَيْرِ عِلْمٍۖ إِنَّ رَبَّكَ هُوَ أَعْلَمُ بِالْمُعْتَدِينَۖ ١٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (910,8,'143',6,'Al-An‘ām','الأنعَام',4,5,121,'۞ وَذَرُواْ ظَٰهِرَ اَ۬لْإِثْمِ وَبَاطِنَهُۥۖ إِنَّ اَ۬لذِينَ يَكْسِبُونَ اَ۬لْإِثْمَ سَيُجْزَوْنَ بِمَا كَانُواْ يَقْتَرِفُونَۖ ١٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (911,8,'143',6,'Al-An‘ām','الأنعَام',5,7,122,'وَلَا تَأْكُلُواْ مِمَّا لَمْ يُذْكَرِ اِ۪سْمُ اُ۬للَّهِ عَلَيْهِ وَإِنَّهُۥ لَفِسْقٞۖ وَإِنَّ اَ۬لشَّيَٰطِينَ لَيُوحُونَ إِلَىٰ أَوْلِيَآئِهِمْ لِيُجَٰدِلُوكُمْۖ وَإِنْ أَطَعْتُمُوهُمْ إِنَّكُمْ لَمُشْرِكُونَۖ ١٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (912,8,'143',6,'Al-An‘ām','الأنعَام',8,10,123,'أَوَمَن كَانَ مَيِّتاٗ فَأَحْيَيْنَٰهُ وَجَعَلْنَا لَهُۥ نُوراٗ يَمْشِے بِهِۦ فِے اِ۬لنَّاسِ كَمَن مَّثَلُهُۥ فِے اِ۬لظُّلُمَٰتِ لَيْسَ بِخَارِجٖ مِّنْهَاۖ كَذَٰلِكَ زُيِّنَ لِلْكَٰفِرِينَ مَا كَانُواْ يَعْمَلُونَۖ ١٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (913,8,'143',6,'Al-An‘ām','الأنعَام',10,12,124,'وَكَذَٰلِكَ جَعَلْنَا فِے كُلِّ قَرْيَةٍ أَكَٰبِرَ مُجْرِمِيهَا لِيَمْكُرُواْ فِيهَاۖ وَمَا يَمْكُرُونَ إِلَّا بِأَنفُسِهِمْ وَمَا يَشْعُرُونَۖ ١٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (914,8,'143',6,'Al-An‘ām','الأنعَام',12,15,125,'وَإِذَا جَآءَتْهُمْ ءَايَةٞ قَالُواْ لَن نُّؤْمِنَ حَتَّىٰ نُؤْتَىٰ مِثْلَ مَا أُوتِيَ رُسُلُ اُ۬للَّهِۖ اِ۬للَّهُ أَعْلَمُ حَيْثُ يَجْعَلُ رِسَٰلَٰتِهِۦۖ سَيُصِيبُ اُ۬لذِينَ أَجْرَمُواْ صَغَارٌ عِندَ اَ۬للَّهِ وَعَذَابٞ شَدِيدُۢ بِمَا كَانُواْ يَمْكُرُونَۖ ١٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (915,8,'144',6,'Al-An‘ām','الأنعَام',1,4,126,'فَمَنْ يُّرِدِ اِ۬للَّهُ أَنْ يَّهْدِيَهُۥ يَشْرَحْ صَدْرَهُۥ لِلْإِسْلَٰمِۖ وَمَنْ يُّرِدْ أَنْ يُّضِلَّهُۥ يَجْعَلْ صَدْرَهُۥ ضَيِّقاً حَرِجاٗ كَأَنَّمَا يَصَّعَّدُ فِے اِ۬لسَّمَآءِۖ كَذَٰلِكَ يَجْعَلُ اُ۬للَّهُ اُ۬لرِّجْسَ عَلَى اَ۬لذِينَ لَا يُؤْمِنُونَۖ ١٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (916,8,'144',6,'Al-An‘ām','الأنعَام',4,5,127,'وَهَٰذَا صِرَٰطُ رَبِّكَ مُسْتَقِيماٗۖ قَدْ فَصَّلْنَا اَ۬لْأٓيَٰتِ لِقَوْمٖ يَذَّكَّرُونَۖ ١٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (917,8,'144',6,'Al-An‘ām','الأنعَام',5,6,128,'۞ لَهُمْ دَارُ اُ۬لسَّلَٰمِ عِندَ رَبِّهِمْۖ وَهْوَ وَلِيُّهُم بِمَا كَانُواْ يَعْمَلُونَۖ ١٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (918,8,'144',6,'Al-An‘ām','الأنعَام',6,10,129,'وَيَوْمَ نَحْشُرُهُمْ جَمِيعاٗ يَٰمَعْشَرَ اَ۬لْجِنِّ قَدِ اِ۪سْتَكْثَرْتُم مِّنَ اَ۬لْإِنسِۖ وَقَالَ أَوْلِيَآؤُهُم مِّنَ اَ۬لْإِنسِ رَبَّنَا اَ۪سْتَمْتَعَ بَعْضُنَا بِبَعْضٖ وَبَلَغْنَا أَجَلَنَا اَ۬لذِے أَجَّلْتَ لَنَاۖ قَالَ اَ۬لنَّارُ مَثْوَيٰكُمْ خَٰلِدِينَ فِيهَا إِلَّا مَا شَآءَ اَ۬للَّهُۖ إِنَّ رَبَّكَ حَكِيمٌ عَلِيمٞۖ ١٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (919,8,'144',6,'Al-An‘ām','الأنعَام',10,11,130,'وَكَذَٰلِكَ نُوَلِّے بَعْضَ اَ۬لظَّٰلِمِينَ بَعْضاَۢ بِمَا كَانُواْ يَكْسِبُونَۖ ١٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (920,8,'144',6,'Al-An‘ām','الأنعَام',12,15,131,'يَٰمَعْشَرَ اَ۬لْجِنِّ وَالْإِنسِ أَلَمْ يَأْتِكُمْ رُسُلٞ مِّنكُمْ يَقُصُّونَ عَلَيْكُمْ ءَايَٰتِے وَيُنذِرُونَكُمْ لِقَآءَ يَوْمِكُمْ هَٰذَاۖ قَالُواْ شَهِدْنَا عَلَىٰ أَنفُسِنَاۖ وَغَرَّتْهُمُ اُ۬لْحَيَوٰةُ اُ۬لدُّنْيَا وَشَهِدُواْ عَلَىٰ أَنفُسِهِمْ أَنَّهُمْ كَانُواْ كَٰفِرِينَۖ ١٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (921,8,'145',6,'Al-An‘ām','الأنعَام',1,2,132,'ذَٰلِكَ أَن لَّمْ يَكُن رَّبُّكَ مُهْلِكَ اَ۬لْقُرَىٰ بِظُلْمٖ وَأَهْلُهَا غَٰفِلُونَۖ ١٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (922,8,'145',6,'Al-An‘ām','الأنعَام',2,3,133,'وَلِكُلّٖ دَرَجَٰتٞ مِّمَّا عَمِلُواْۖ وَمَا رَبُّكَ بِغَٰفِلٍ عَمَّا يَعْمَلُونَۖ ١٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (923,8,'145',6,'Al-An‘ām','الأنعَام',3,5,134,'وَرَبُّكَ اَ۬لْغَنِيُّ ذُو اُ۬لرَّحْمَةِۖ إِنْ يَّشَأْ يُذْهِبْكُمْ وَيَسْتَخْلِفْ مِنۢ بَعْدِكُم مَّا يَشَآءُ كَمَا أَنشَأَكُم مِّن ذُرِّيَّةِ قَوْمٍ ءَاخَرِينَۖ ١٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (924,8,'145',6,'Al-An‘ām','الأنعَام',6,6,135,'إِنَّ مَا تُوعَدُونَ لَأٓتٖۖ وَمَا أَنتُم بِمُعْجِزِينَۖ ١٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (925,8,'145',6,'Al-An‘ām','الأنعَام',6,8,136,'۞ قُلْ يَٰقَوْمِ اِ۪عْمَلُواْ عَلَىٰ مَكَانَتِكُمْ إِنِّے عَامِلٞۖ فَسَوْفَ تَعْلَمُونَ مَن تَكُونُ لَهُۥ عَٰقِبَةُ اُ۬لدَّارِۖ إِنَّهُۥ لَا يُفْلِحُ اُ۬لظَّٰلِمُونَۖ ١٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (926,8,'145',6,'Al-An‘ām','الأنعَام',9,12,137,'وَجَعَلُواْ لِلهِ مِمَّا ذَرَأَ مِنَ اَ۬لْحَرْثِ وَالْأَنْعَٰمِ نَصِيباٗ فَقَالُواْ هَٰذَا لِلهِ بِزَعْمِهِمْ وَهَٰذَا لِشُرَكَآئِنَاۖ فَمَا كَانَ لِشُرَكَآئِهِمْ فَلَا يَصِلُ إِلَى اَ۬للَّهِۖ وَمَا كَانَ لِلهِ فَهْوَ يَصِلُ إِلَىٰ شُرَكَآئِهِمْۖ سَآءَ مَا يَحْكُمُونَۖ ١٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (927,8,'145',6,'Al-An‘ām','الأنعَام',12,15,138,'وَكَذَٰلِكَ زَيَّنَ لِكَثِيرٖ مِّنَ اَ۬لْمُشْرِكِينَ قَتْلَ أَوْلَٰدِهِمْ شُرَكَآؤُهُمْ لِيُرْدُوهُمْ وَلِيَلْبِسُواْ عَلَيْهِمْ دِينَهُمْۖ وَلَوْ شَآءَ اَ۬للَّهُ مَا فَعَلُوهُۖ فَذَرْهُمْ وَمَا يَفْتَرُونَۖ ١٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (928,8,'146',6,'Al-An‘ām','الأنعَام',1,4,139,'وَقَالُواْ هَٰذِهِۦ أَنْعَٰمٞ وَحَرْثٌ حِجْرٞ لَّا يَطْعَمُهَا إِلَّا مَن نَّشَآءُ بِزَعْمِهِمْ وَأَنْعَٰمٌ حُرِّمَتْ ظُهُورُهَا وَأَنْعَٰمٞ لَّا يَذْكُرُونَ اَ۪سْمَ اَ۬للَّهِ عَلَيْهَا اَ۪فْتِرَآءً عَلَيْهِۖ سَيَجْزِيهِم بِمَا كَانُواْ يَفْتَرُونَۖ ١٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (929,8,'146',6,'Al-An‘ām','الأنعَام',4,7,140,'وَقَالُواْ مَا فِے بُطُونِ هَٰذِهِ اِ۬لْأَنْعَٰمِ خَالِصَةٞ لِّذُكُورِنَا وَمُحَرَّمٌ عَلَىٰ أَزْوَٰجِنَاۖ وَإِنْ يَّكُن مَّيْتَةٗ فَهُمْ فِيهِ شُرَكَآءُۖ سَيَجْزِيهِمْ وَصْفَهُمْۖ إِنَّهُۥ حَكِيمٌ عَلِيمٞۖ ١٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (930,8,'146',6,'Al-An‘ām','الأنعَام',7,9,141,'۞ قَدْ خَسِرَ اَ۬لذِينَ قَتَلُواْ أَوْلَٰدَهُمْ سَفَهاَۢ بِغَيْرِ عِلْمٖ وَحَرَّمُواْ مَا رَزَقَهُمُ اُ۬للَّهُ اُ۪فْتِرَآءً عَلَى اَ۬للَّهِۖ قَدْ ضَلُّواْ وَمَا كَانُواْ مُهْتَدِينَۖ ١٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (931,8,'146',6,'Al-An‘ām','الأنعَام',9,13,142,'وَهْوَ اَ۬لذِے أَنشَأَ جَنَّٰتٖ مَّعْرُوشَٰتٖ وَغَيْرَ مَعْرُوشَٰتٖ وَالنَّخْلَ وَالزَّرْعَ مُخْتَلِفاً أُكْلُهُۥۖ وَالزَّيْتُونَ وَالرُّمَّانَ مُتَشَٰبِهاٗ وَغَيْرَ مُتَشَٰبِهٖۖ كُلُواْ مِن ثَمَرِهِۦ إِذَا أَثْمَرَ وَءَاتُواْ حَقَّهُۥ يَوْمَ حِصَادِهِۦۖ وَلَا تُسْرِفُواْۖ إِنَّهُۥ لَا يُحِبُّ اُ۬لْمُسْرِفِينَۖ ١٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (932,8,'146',6,'Al-An‘ām','الأنعَام',13,15,143,'وَمِنَ اَ۬لْأَنْعَٰمِ حَمُولَةٗ وَفَرْشاٗۖ كُلُواْ مِمَّا رَزَقَكُمُ اُ۬للَّهُۖ وَلَا تَتَّبِعُواْ خُطْوَٰتِ اِ۬لشَّيْطَٰنِۖ إِنَّهُۥ لَكُمْ عَدُوّٞ مُّبِينٞۖ ١٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (933,8,'147',6,'Al-An‘ām','الأنعَام',1,3,144,'ثَمَٰنِيَةَ أَزْوَٰجٖۖ مِّنَ اَ۬لضَّأْنِ اِ۪ثْنَيْنِ وَمِنَ اَ۬لْمَعْزِ اِ۪ثْنَيْنِۖ قُلْ ءَآلذَّكَرَيْنِ حَرَّمَ أَمِ اِ۬لْأُنثَيَيْنِ أَمَّا اَ۪شْتَمَلَتْ عَلَيْهِ أَرْحَامُ اُ۬لْأُنثَيَيْنِۖ نَبِّـُٔونِے بِعِلْمٍ إِن كُنتُمْ صَٰدِقِينَۖ ١٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (934,8,'147',6,'Al-An‘ām','الأنعَام',4,8,145,'وَمِنَ اَ۬لْإِبِلِ اِ۪ثْنَيْنِ وَمِنَ اَ۬لْبَقَرِ اِ۪ثْنَيْنِۖ قُلْ ءَآلذَّكَرَيْنِ حَرَّمَ أَمِ اِ۬لْأُنثَيَيْنِ أَمَّا اَ۪شْتَمَلَتْ عَلَيْهِ أَرْحَامُ اُ۬لْأُنثَيَيْنِۖ أَمْ كُنتُمْ شُهَدَآءَ ا۪ذْ وَصَّيٰكُمُ اُ۬للَّهُ بِهَٰذَاۖ فَمَنْ أَظْلَمُ مِمَّنِ اِ۪فْتَرَىٰ عَلَى اَ۬للَّهِ كَذِباٗ لِّيُضِلَّ اَ۬لنَّاسَ بِغَيْرِ عِلْمٍۖ إِنَّ اَ۬للَّهَ لَا يَهْدِے اِ۬لْقَوْمَ اَ۬لظَّٰلِمِينَۖ ١٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (935,8,'147',6,'Al-An‘ām','الأنعَام',8,12,146,'۞ قُل لَّا أَجِدُ فِے مَا أُوحِيَ إِلَيَّ مُحَرَّماً عَلَىٰ طَاعِمٖ يَطْعَمُهُۥ إِلَّا أَنْ يَّكُونَ مَيْتَةً أَوْ دَماٗ مَّسْفُوحاً أَوْ لَحْمَ خِنزِيرٖ فَإِنَّهُۥ رِجْسٌۖ أَوْ فِسْقاً أُهِلَّ لِغَيْرِ اِ۬للَّهِ بِهِۦۖ فَمَنُ اُ۟ضْطُرَّ غَيْرَ بَاغٖ وَلَا عَادٖ فَإِنَّ رَبَّكَ غَفُورٞ رَّحِيمٞۖ ١٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (936,8,'147',6,'Al-An‘ām','الأنعَام',12,15,147,'وَعَلَى اَ۬لذِينَ هَادُواْ حَرَّمْنَا كُلَّ ذِے ظُفُرٖۖ وَمِنَ اَ۬لْبَقَرِ وَالْغَنَمِۖ حَرَّمْنَا عَلَيْهِمْ شُحُومَهُمَا إِلَّا مَا حَمَلَتْ ظُهُورُهُمَا أَوِ اِ۬لْحَوَايَا أَوْ مَا اَ۪خْتَلَطَ بِعَظْمٖۖ ذَٰلِكَ جَزَيْنَٰهُم بِبَغْيِهِمْۖ وَإِنَّا لَصَٰدِقُونَۖ ١٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (937,8,'148',6,'Al-An‘ām','الأنعَام',1,2,148,'فَإِن كَذَّبُوكَ فَقُل رَّبُّكُمْ ذُو رَحْمَةٖ وَٰسِعَةٖۖ وَلَا يُرَدُّ بَأْسُهُۥ عَنِ اِ۬لْقَوْمِ اِ۬لْمُجْرِمِينَۖ ١٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (938,8,'148',6,'Al-An‘ām','الأنعَام',2,6,149,'سَيَقُولُ اُ۬لذِينَ أَشْرَكُواْ لَوْ شَآءَ اَ۬للَّهُ مَا أَشْرَكْنَا وَلَا ءَابَآؤُنَا وَلَا حَرَّمْنَا مِن شَےْءٖۖ كَذَٰلِكَ كَذَّبَ اَ۬لذِينَ مِن قَبْلِهِمْ حَتَّىٰ ذَاقُواْ بَأْسَنَاۖ قُلْ هَلْ عِندَكُم مِّنْ عِلْمٖ فَتُخْرِجُوهُ لَنَاۖ إِن تَتَّبِعُونَ إِلَّا اَ۬لظَّنَّۖ وَإِنْ أَنتُمْ إِلَّا تَخْرُصُونَۖ ١٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (939,8,'148',6,'Al-An‘ām','الأنعَام',6,7,150,'قُلْ فَلِلهِ اِ۬لْحُجَّةُ اُ۬لْبَٰلِغَةُۖ فَلَوْ شَآءَ لَهَدَيٰكُمْ أَجْمَعِينَۖ ١٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (940,8,'148',6,'Al-An‘ām','الأنعَام',7,10,151,'قُلْ هَلُمَّ شُهَدَآءَكُمُ اُ۬لذِينَ يَشْهَدُونَ أَنَّ اَ۬للَّهَ حَرَّمَ هَٰذَاۖ فَإِن شَهِدُواْ فَلَا تَشْهَدْ مَعَهُمْۖ وَلَا تَتَّبِعْ أَهْوَآءَ اَ۬لذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَالذِينَ لَا يُؤْمِنُونَ بِالْأٓخِرَةِ وَهُم بِرَبِّهِمْ يَعْدِلُونَۖ ١٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (941,8,'148',6,'Al-An‘ām','الأنعَام',10,15,152,'۞ قُلْ تَعَالَوْاْ أَتْلُ مَا حَرَّمَ رَبُّكُمْ عَلَيْكُمْ أَلَّا تُشْرِكُواْ بِهِۦ شَئْاٗۖ وَبِالْوَٰلِدَيْنِ إِحْسَٰناٗۖ وَلَا تَقْتُلُواْ أَوْلَٰدَكُم مِّنْ إِمْلَٰقٖۖ نَّحْنُ نَرْزُقُكُمْ وَإِيَّاهُمْۖ وَلَا تَقْرَبُواْ اُ۬لْفَوَٰحِشَ مَا ظَهَرَ مِنْهَا وَمَا بَطَنَۖ وَلَا تَقْتُلُواْ اُ۬لنَّفْسَ اَ۬لتِے حَرَّمَ اَ۬للَّهُ إِلَّا بِالْحَقِّۖ ذَٰلِكُمْ وَصَّيٰكُم بِهِۦ لَعَلَّكُمْ تَعْقِلُونَۖ ١٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (942,8,'149',6,'Al-An‘ām','الأنعَام',1,4,153,'وَلَا تَقْرَبُواْ مَالَ اَ۬لْيَتِيمِ إِلَّا بِالتِے هِيَ أَحْسَنُ حَتَّىٰ يَبْلُغَ أَشُدَّهُۥۖ وَأَوْفُواْ اُ۬لْكَيْلَ وَالْمِيزَانَ بِالْقِسْطِۖ لَا نُكَلِّفُ نَفْساً إِلَّا وُسْعَهَاۖ وَإِذَا قُلْتُمْ فَاعْدِلُواْ وَلَوْ كَانَ ذَا قُرْبَىٰۖ وَبِعَهْدِ اِ۬للَّهِ أَوْفُواْۖ ذَٰلِكُمْ وَصَّيٰكُم بِهِۦ لَعَلَّكُمْ تَذَّكَّرُونَۖ ١٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (943,8,'149',6,'Al-An‘ām','الأنعَام',5,7,154,'وَأَنَّ هَٰذَا صِرَٰطِے مُسْتَقِيماٗ فَاتَّبِعُوهُۖ وَلَا تَتَّبِعُواْ اُ۬لسُّبُلَ فَتَفَرَّقَ بِكُمْ عَن سَبِيلِهِۦۖ ذَٰلِكُمْ وَصَّيٰكُم بِهِۦ لَعَلَّكُمْ تَتَّقُونَۖ ١٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (944,8,'149',6,'Al-An‘ām','الأنعَام',7,9,155,'ثُمَّ ءَاتَيْنَا مُوسَى اَ۬لْكِتَٰبَ تَمَاماً عَلَى اَ۬لذِے أَحْسَنَ وَتَفْصِيلاٗ لِّكُلِّ شَےْءٖ وَهُدىٗ وَرَحْمَةٗ لَّعَلَّهُم بِلِقَآءِ رَبِّهِمْ يُؤْمِنُونَۖ ١٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (945,8,'149',6,'Al-An‘ām','الأنعَام',9,10,156,'وَهَٰذَا كِتَٰبٌ أَنزَلْنَٰهُ مُبَٰرَكٞ فَاتَّبِعُوهُ وَاتَّقُواْ لَعَلَّكُمْ تُرْحَمُونَ ١٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (946,8,'149',6,'Al-An‘ām','الأنعَام',10,11,157,'أَن تَقُولُواْ إِنَّمَا أُنزِلَ اَ۬لْكِتَٰبُ عَلَىٰ طَآئِفَتَيْنِ مِن قَبْلِنَا وَإِن كُنَّا عَن دِرَاسَتِهِمْ لَغَٰفِلِينَ ١٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (947,8,'149',6,'Al-An‘ām','الأنعَام',12,15,158,'أَوْ تَقُولُواْ لَوْ أَنَّا أُنزِلَ عَلَيْنَا اَ۬لْكِتَٰبُ لَكُنَّا أَهْدَىٰ مِنْهُمْۖ فَقَدْ جَآءَكُم بَيِّنَةٞ مِّن رَّبِّكُمْ وَهُدىٗ وَرَحْمَةٞۖ فَمَنْ أَظْلَمُ مِمَّن كَذَّبَ بِـَٔايَٰتِ اِ۬للَّهِ وَصَدَفَ عَنْهَاۖ سَنَجْزِے اِ۬لذِينَ يَصْدِفُونَ عَنْ ءَايَٰتِنَا سُوٓءَ اَ۬لْعَذَابِ بِمَا كَانُواْ يَصْدِفُونَۖ ١٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (948,8,'150',6,'Al-An‘ām','الأنعَام',1,4,159,'۞ هَلْ يَنظُرُونَ إِلَّا أَن تَأْتِيَهُمُ اُ۬لْمَلَٰٓئِكَةُ أَوْ يَأْتِيَ رَبُّكَ أَوْ يَأْتِيَ بَعْضُ ءَايَٰتِ رَبِّكَۖ يَوْمَ يَأْتِے بَعْضُ ءَايَٰتِ رَبِّكَ لَا يَنفَعُ نَفْساً إِيمَٰنُهَا لَمْ تَكُنْ ءَامَنَتْ مِن قَبْلُ أَوْ كَسَبَتْ فِے إِيمَٰنِهَا خَيْراٗۖ قُلِ اِ۪نتَظِرُواْۖ إِنَّا مُنتَظِرُونَۖ ١٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (949,8,'150',6,'Al-An‘ām','الأنعَام',4,5,160,'إِنَّ اَ۬لذِينَ فَرَّقُواْ دِينَهُمْ وَكَانُواْ شِيَعاٗ لَّسْتَ مِنْهُمْ فِے شَےْءٍۖ إِنَّمَا أَمْرُهُمْ إِلَى اَ۬للَّهِۖ ثُمَّ يُنَبِّئُهُم بِمَا كَانُواْ يَفْعَلُونَۖ ١٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (950,8,'150',6,'Al-An‘ām','الأنعَام',6,7,161,'مَن جَآءَ بِالْحَسَنَةِ فَلَهُۥ عَشْرُ أَمْثَالِهَاۖ وَمَن جَآءَ بِالسَّيِّئَةِ فَلَا يُجْزَىٰ إِلَّا مِثْلَهَا وَهُمْ لَا يُظْلَمُونَۖ ١٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (951,8,'150',6,'Al-An‘ām','الأنعَام',7,8,162,'قُلْ إِنَّنِے هَدَيٰنِے رَبِّيَ إِلَىٰ صِرَٰطٖ مُّسْتَقِيمٖۖ ١٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (952,8,'150',6,'Al-An‘ām','الأنعَام',8,9,163,'دِيناٗ قَيِّماٗ مِّلَّةَ إِبْرَٰهِيمَ حَنِيفاٗۖ وَمَا كَانَ مِنَ اَ۬لْمُشْرِكِينَۖ ١٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (953,8,'150',6,'Al-An‘ām','الأنعَام',9,10,164,'قُلْ إِنَّ صَلَاتِے وَنُسُكِے وَمَحْيَآےْ وَمَمَاتِيَ لِلهِ رَبِّ اِ۬لْعَٰلَمِينَ ١٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (954,8,'150',6,'Al-An‘ām','الأنعَام',10,10,165,'لَا شَرِيكَ لَهُۥۖ وَبِذَٰلِكَ أُمِرْتُۖ وَأَنَا أَوَّلُ اُ۬لْمُسْلِمِينَۖ ١٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (955,8,'150',6,'Al-An‘ām','الأنعَام',11,13,166,'قُلْ أَغَيْرَ اَ۬للَّهِ أَبْغِے رَبّاٗ وَهْوَ رَبُّ كُلِّ شَےْءٖۖ وَلَا تَكْسِبُ كُلُّ نَفْسٍ إِلَّا عَلَيْهَاۖ وَلَا تَزِرُ وَازِرَةٞ وِزْرَ أُخْرَىٰۖ ثُمَّ إِلَىٰ رَبِّكُم مَّرْجِعُكُمْ فَيُنَبِّئُكُم بِمَا كُنتُمْ فِيهِ تَخْتَلِفُونَۖ ١٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (956,8,'150',6,'Al-An‘ām','الأنعَام',13,15,167,'وَهْوَ اَ۬لذِے جَعَلَكُمْ خَلَٰٓئِفَ اَ۬لْأَرْضِ وَرَفَعَ بَعْضَكُمْ فَوْقَ بَعْضٖ دَرَجَٰتٖ لِّيَبْلُوَكُمْ فِے مَا ءَاتَيٰكُمْۖ إِنَّ رَبَّكَ سَرِيعُ اُ۬لْعِقَابِۖ وَإِنَّهُۥ لَغَفُورٞ رَّحِيمُۢۖ ١٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (957,8,'151',7,'Al-A‘rāf','الأعرَاف',3,4,1,'أَلَٓمِّٓصَٓۖ كِتَٰبٌ أُنزِلَ إِلَيْكَ فَلَا يَكُن فِے صَدْرِكَ حَرَجٞ مِّنْهُ لِتُنذِرَ بِهِۦ وَذِكْرَىٰ لِلْمُؤْمِنِينَۖ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (958,8,'151',7,'Al-A‘rāf','الأعرَاف',4,5,2,'اَ۪تَّبِعُواْ مَا أُنزِلَ إِلَيْكُم مِّن رَّبِّكُمْۖ وَلَا تَتَّبِعُواْ مِن دُونِهِۦ أَوْلِيَآءَۖ قَلِيلاٗ مَّا تَذَّكَّرُونَۖ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (959,8,'151',7,'Al-A‘rāf','الأعرَاف',6,7,3,'وَكَم مِّن قَرْيَةٍ أَهْلَكْنَٰهَا فَجَآءَهَا بَأْسُنَا بَيَٰتاً أَوْ هُمْ قَآئِلُونَۖ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (960,8,'151',7,'Al-A‘rāf','الأعرَاف',7,8,4,'۞ فَمَا كَانَ دَعْوَيٰهُمْ إِذْ جَآءَهُم بَأْسُنَا إِلَّا أَن قَالُواْ إِنَّا كُنَّا ظَٰلِمِينَۖ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (961,8,'151',7,'Al-A‘rāf','الأعرَاف',8,9,5,'فَلَنَسْـَٔلَنَّ اَ۬لذِينَ أُرْسِلَ إِلَيْهِمْ وَلَنَسْـَٔلَنَّ اَ۬لْمُرْسَلِينَۖ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (962,8,'151',7,'Al-A‘rāf','الأعرَاف',9,9,6,'فَلَنَقُصَّنَّ عَلَيْهِم بِعِلْمٖۖ وَمَا كُنَّا غَآئِبِينَۖ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (963,8,'151',7,'Al-A‘rāf','الأعرَاف',10,11,7,'وَالْوَزْنُ يَوْمَئِذٍ اِ۬لْحَقُّۖ فَمَن ثَقُلَتْ مَوَٰزِينُهُۥ فَأُوْلَٰٓئِكَ هُمُ اُ۬لْمُفْلِحُونَۖ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (964,8,'151',7,'Al-A‘rāf','الأعرَاف',11,12,8,'وَمَنْ خَفَّتْ مَوَٰزِينُهُۥ فَأُوْلَٰٓئِكَ اَ۬لذِينَ خَسِرُواْ أَنفُسَهُم بِمَا كَانُواْ بِـَٔايَٰتِنَا يَظْلِمُونَۖ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (965,8,'151',7,'Al-A‘rāf','الأعرَاف',12,13,9,'وَلَقَدْ مَكَّنَّٰكُمْ فِے اِ۬لْأَرْضِ وَجَعَلْنَا لَكُمْ فِيهَا مَعَٰيِشَۖ قَلِيلاٗ مَّا تَشْكُرُونَۖ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (966,8,'151',7,'Al-A‘rāf','الأعرَاف',14,15,10,'وَلَقَدْ خَلَقْنَٰكُمْ ثُمَّ صَوَّرْنَٰكُمْ ثُمَّ قُلْنَا لِلْمَلَٰٓئِكَةِ اِ۟سْجُدُواْ لِأٓدَمَ فَسَجَدُواْۖ إِلَّا إِبْلِيسَ لَمْ يَكُن مِّنَ اَ۬لسَّٰجِدِينَۖ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (967,8,'152',7,'Al-A‘rāf','الأعرَاف',1,2,11,'قَالَ مَا مَنَعَكَ أَلَّا تَسْجُدَ إِذْ أَمَرْتُكَۖ قَالَ أَنَا خَيْرٞ مِّنْهُ خَلَقْتَنِے مِن نَّارٖ وَخَلَقْتَهُۥ مِن طِينٖۖ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (968,8,'152',7,'Al-A‘rāf','الأعرَاف',2,3,12,'قَالَ فَاهْبِطْ مِنْهَا فَمَا يَكُونُ لَكَ أَن تَتَكَبَّرَ فِيهَا فَاخْرُجْ إِنَّكَ مِنَ اَ۬لصَّٰغِرِينَۖ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (969,8,'152',7,'Al-A‘rāf','الأعرَاف',3,3,13,'قَالَ أَنظِرْنِے إِلَىٰ يَوْمِ يُبْعَثُونَۖ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (970,8,'152',7,'Al-A‘rāf','الأعرَاف',4,4,14,'قَالَ إِنَّكَ مِنَ اَ۬لْمُنظَرِينَۖ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (971,8,'152',7,'Al-A‘rāf','الأعرَاف',4,5,15,'قَالَ فَبِمَا أَغْوَيْتَنِے لَأَقْعُدَنَّ لَهُمْ صِرَٰطَكَ اَ۬لْمُسْتَقِيمَ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (972,8,'152',7,'Al-A‘rāf','الأعرَاف',5,6,16,'ثُمَّ لَأٓتِيَنَّهُم مِّنۢ بَيْنِ أَيْدِيهِمْ وَمِنْ خَلْفِهِمْ وَعَنْ أَيْمَٰنِهِمْ وَعَن شَمَآئِلِهِمْۖ وَلَا تَجِدُ أَكْثَرَهُمْ شَٰكِرِينَۖ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (973,8,'152',7,'Al-A‘rāf','الأعرَاف',6,8,17,'قَالَ اَ۟خْرُجْ مِنْهَا مَذْءُوماٗ مَّدْحُوراٗۖ لَّمَن تَبِعَكَ مِنْهُمْ لَأَمْلَأَنَّ جَهَنَّمَ مِنكُمْ أَجْمَعِينَۖ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (974,8,'152',7,'Al-A‘rāf','الأعرَاف',8,9,18,'وَيَٰـَٔادَمُ اُ۟سْكُنْ أَنتَ وَزَوْجُكَ اَ۬لْجَنَّةَ فَكُلَا مِنْ حَيْثُ شِئْتُمَاۖ وَلَا تَقْرَبَا هَٰذِهِ اِ۬لشَّجَرَةَ فَتَكُونَا مِنَ اَ۬لظَّٰلِمِينَۖ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (975,8,'152',7,'Al-A‘rāf','الأعرَاف',9,12,19,'فَوَسْوَسَ لَهُمَا اَ۬لشَّيْطَٰنُ لِيُبْدِيَ لَهُمَا مَا وُۥرِيَ عَنْهُمَا مِن سَوْءَٰتِهِمَاۖ وَقَالَ مَا نَهَيٰكُمَا رَبُّكُمَا عَنْ هَٰذِهِ اِ۬لشَّجَرَةِ إِلَّا أَن تَكُونَا مَلَكَيْنِ أَوْ تَكُونَا مِنَ اَ۬لْخَٰلِدِينَۖ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (976,8,'152',7,'Al-A‘rāf','الأعرَاف',12,12,20,'۞ وَقَاسَمَهُمَا إِنِّے لَكُمَا لَمِنَ اَ۬لنَّٰصِحِينَ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (977,8,'152',7,'Al-A‘rāf','الأعرَاف',13,15,21,'فَدَلَّيٰهُمَا بِغُرُورٖۖ فَلَمَّا ذَاقَا اَ۬لشَّجَرَةَ بَدَتْ لَهُمَا سَوْءَٰتُهُمَا وَطَفِقَا يَخْصِفَٰنِ عَلَيْهِمَا مِنْ وَّرَقِ اِ۬لْجَنَّةِۖ وَنَادَيٰهُمَا رَبُّهُمَا أَلَمْ أَنْهَكُمَا عَن تِلْكُمَا اَ۬لشَّجَرَةِ وَأَقُل لَّكُمَا إِنَّ اَ۬لشَّيْطَٰنَ لَكُمَا عَدُوّٞ مُّبِينٞۖ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (978,8,'153',7,'Al-A‘rāf','الأعرَاف',1,2,22,'قَالَا رَبَّنَا ظَلَمْنَا أَنفُسَنَا وَإِن لَّمْ تَغْفِرْ لَنَا وَتَرْحَمْنَا لَنَكُونَنَّ مِنَ اَ۬لْخَٰسِرِينَۖ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (979,8,'153',7,'Al-A‘rāf','الأعرَاف',2,3,23,'قَالَ اَ۪هْبِطُواْ بَعْضُكُمْ لِبَعْضٍ عَدُوّٞۖ وَلَكُمْ فِے اِ۬لْأَرْضِ مُسْتَقَرّٞ وَمَتَٰعٌ إِلَىٰ حِينٖۖ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (980,8,'153',7,'Al-A‘rāf','الأعرَاف',3,4,24,'قَالَ فِيهَا تَحْيَوْنَ وَفِيهَا تَمُوتُونَ وَمِنْهَا تُخْرَجُونَۖ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (981,8,'153',7,'Al-A‘rāf','الأعرَاف',4,6,25,'يَٰبَنِے ءَادَمَ قَدْ أَنزَلْنَا عَلَيْكُمْ لِبَاساٗ يُوَٰرِے سَوْءَٰتِكُمْ وَرِيشاٗ وَلِبَاسَ اَ۬لتَّقْوَىٰۖ ذَٰلِكَ خَيْرٞۖ ذَٰلِكَ مِنْ ءَايَٰتِ اِ۬للَّهِ لَعَلَّهُمْ يَذَّكَّرُونَۖ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (982,8,'153',7,'Al-A‘rāf','الأعرَاف',6,9,26,'يَٰبَنِے ءَادَمَ لَا يَفْتِنَنَّكُمُ اُ۬لشَّيْطَٰنُ كَمَا أَخْرَجَ أَبَوَيْكُم مِّنَ اَ۬لْجَنَّةِ يَنزِعُ عَنْهُمَا لِبَاسَهُمَا لِيُرِيَهُمَا سَوْءَٰتِهِمَاۖ إِنَّهُۥ يَرَيٰكُمْ هُوَ وَقَبِيلُهُۥ مِنْ حَيْثُ لَا تَرَوْنَهُمْۖ إِنَّا جَعَلْنَا اَ۬لشَّيَٰطِينَ أَوْلِيَآءَ لِلذِينَ لَا يُؤْمِنُونَۖ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (983,8,'153',7,'Al-A‘rāf','الأعرَاف',10,11,27,'وَإِذَا فَعَلُواْ فَٰحِشَةٗ قَالُواْ وَجَدْنَا عَلَيْهَا ءَابَآءَنَا وَاللَّهُ أَمَرَنَا بِهَاۖ قُلْ إِنَّ اَ۬للَّهَ لَا يَأْمُرُ بِالْفَحْشَآءِۖ اَ۬تَقُولُونَ عَلَى اَ۬للَّهِ مَا لَا تَعْلَمُونَۖ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (984,8,'153',7,'Al-A‘rāf','الأعرَاف',12,15,28,'قُلْ أَمَرَ رَبِّے بِالْقِسْطِۖ وَأَقِيمُواْ وُجُوهَكُمْ عِندَ كُلِّ مَسْجِدٖۖ وَادْعُوهُ مُخْلِصِينَ لَهُ اُ۬لدِّينَۖ كَمَا بَدَأَكُمْ تَعُودُونَۖ فَرِيقاً هَدَىٰۖ وَفَرِيقاً حَقَّ عَلَيْهِمُ اُ۬لضَّلَٰلَةُۖ إِنَّهُمُ اُ۪تَّخَذُواْ اُ۬لشَّيَٰطِينَ أَوْلِيَآءَ مِن دُونِ اِ۬للَّهِ وَيَحْسِبُونَ أَنَّهُم مُّهْتَدُونَۖ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (985,8,'154',7,'Al-A‘rāf','الأعرَاف',1,2,29,'يَٰبَنِے ءَادَمَ خُذُواْ زِينَتَكُمْ عِندَ كُلِّ مَسْجِدٖ وَكُلُواْ وَاشْرَبُواْۖ وَلَا تُسْرِفُواْۖ إِنَّهُۥ لَا يُحِبُّ اُ۬لْمُسْرِفِينَۖ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (986,8,'154',7,'Al-A‘rāf','الأعرَاف',2,5,30,'۞ قُلْ مَنْ حَرَّمَ زِينَةَ اَ۬للَّهِ اِ۬لتِے أَخْرَجَ لِعِبَادِهِۦ وَالطَّيِّبَٰتِ مِنَ اَ۬لرِّزْقِۖ قُلْ هِيَ لِلذِينَ ءَامَنُواْ فِے اِ۬لْحَيَوٰةِ اِ۬لدُّنْيَا خَالِصَةٞ يَوْمَ اَ۬لْقِيَٰمَةِۖ كَذَٰلِكَ نُفَصِّلُ اُ۬لْأٓيَٰتِ لِقَوْمٖ يَعْلَمُونَۖ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (987,8,'154',7,'Al-A‘rāf','الأعرَاف',5,7,31,'قُلْ إِنَّمَا حَرَّمَ رَبِّيَ اَ۬لْفَوَٰحِشَ مَا ظَهَرَ مِنْهَا وَمَا بَطَنَ وَالْإِثْمَ وَالْبَغْيَ بِغَيْرِ اِ۬لْحَقِّ وَأَن تُشْرِكُواْ بِاللَّهِ مَا لَمْ يُنَزِّلْ بِهِۦ سُلْطَٰناٗ وَأَن تَقُولُواْ عَلَى اَ۬للَّهِ مَا لَا تَعْلَمُونَۖ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (988,8,'154',7,'Al-A‘rāf','الأعرَاف',7,8,32,'وَلِكُلِّ أُمَّةٍ أَجَلٞۖ فَإِذَا جَا أَجَلُهُمْ لَا يَسْتَٰٔخِرُونَ سَاعَةٗۖ وَلَا يَسْتَقْدِمُونَۖ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (989,8,'154',7,'Al-A‘rāf','الأعرَاف',9,10,33,'يَٰبَنِے ءَادَمَ إِمَّا يَأْتِيَنَّكُمْ رُسُلٞ مِّنكُمْ يَقُصُّونَ عَلَيْكُمْ ءَايَٰتِے فَمَنِ اِ۪تَّقَىٰ وَأَصْلَحَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَۖ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (990,8,'154',7,'Al-A‘rāf','الأعرَاف',10,12,34,'وَالذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَاسْتَكْبَرُواْ عَنْهَا أُوْلَٰٓئِكَ أَصْحَٰبُ اُ۬لنَّارِ هُمْ فِيهَا خَٰلِدُونَۖ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (991,8,'154',7,'Al-A‘rāf','الأعرَاف',12,15,35,'فَمَنْ أَظْلَمُ مِمَّنِ اِ۪فْتَرَىٰ عَلَى اَ۬للَّهِ كَذِباً أَوْ كَذَّبَ بِـَٔايَٰتِهِۦۖ أُوْلَٰٓئِكَ يَنَالُهُمْ نَصِيبُهُم مِّنَ اَ۬لْكِتَٰبِۖ حَتَّىٰ إِذَا جَآءَتْهُمْ رُسُلُنَا يَتَوَفَّوْنَهُمْ قَالُواْ أَيْنَ مَا كُنتُمْ تَدْعُونَ مِن دُونِ اِ۬للَّهِ قَالُواْ ضَلُّواْ عَنَّاۖ وَشَهِدُواْ عَلَىٰ أَنفُسِهِمْ أَنَّهُمْ كَانُواْ كَٰفِرِينَۖ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (992,8,'155',7,'Al-A‘rāf','الأعرَاف',1,4,36,'قَالَ اَ۟دْخُلُواْ فِے أُمَمٖ قَدْ خَلَتْ مِن قَبْلِكُم مِّنَ اَ۬لْجِنِّ وَالْإِنسِ فِے اِ۬لنَّارِۖ كُلَّمَا دَخَلَتْ أُمَّةٞ لَّعَنَتْ أُخْتَهَاۖ حَتَّىٰ إِذَا اَ۪دَّارَكُواْ فِيهَا جَمِيعاٗ قَالَتْ أُخْرَيٰهُمْ لِأُولَيٰهُمْ رَبَّنَا هَٰؤُلَآءِ اَ۬ضَلُّونَا فَـَٔاتِهِمْ عَذَاباٗ ضِعْفاٗ مِّنَ اَ۬لنَّارِۖ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (993,8,'155',7,'Al-A‘rāf','الأعرَاف',4,4,37,'قَالَ لِكُلّٖ ضِعْفٞۖ وَلَٰكِن لَّا تَعْلَمُونَۖ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (994,8,'155',7,'Al-A‘rāf','الأعرَاف',5,6,38,'۞ وَقَالَتْ أُولَيٰهُمْ لِأُخْرَيٰهُمْ فَمَا كَانَ لَكُمْ عَلَيْنَا مِن فَضْلٖ فَذُوقُواْ اُ۬لْعَذَابَ بِمَا كُنتُمْ تَكْسِبُونَۖ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (995,8,'155',7,'Al-A‘rāf','الأعرَاف',6,9,39,'إِنَّ اَ۬لذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَاسْتَكْبَرُواْ عَنْهَا لَا تُفَتَّحُ لَهُمْ أَبْوَٰبُ اُ۬لسَّمَآءِ وَلَا يَدْخُلُونَ اَ۬لْجَنَّةَ حَتَّىٰ يَلِجَ اَ۬لْجَمَلُ فِے سَمِّ اِ۬لْخِيَاطِۖ وَكَذَٰلِكَ نَجْزِے اِ۬لْمُجْرِمِينَۖ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (996,8,'155',7,'Al-A‘rāf','الأعرَاف',9,10,40,'لَهُم مِّن جَهَنَّمَ مِهَادٞۖ وَمِن فَوْقِهِمْ غَوَاشٖۖ وَكَذَٰلِكَ نَجْزِے اِ۬لظَّٰلِمِينَۖ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (997,8,'155',7,'Al-A‘rāf','الأعرَاف',10,12,41,'وَالذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ لَا نُكَلِّفُ نَفْساً إِلَّا وُسْعَهَاۖ أُوْلَٰٓئِكَ أَصْحَٰبُ اُ۬لْجَنَّةِ هُمْ فِيهَا خَٰلِدُونَۖ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (998,8,'155',7,'Al-A‘rāf','الأعرَاف',12,15,42,'وَنَزَعْنَا مَا فِے صُدُورِهِم مِّنْ غِلّٖ تَجْرِے مِن تَحْتِهِمُ اُ۬لْأَنْهَٰرُۖ وَقَالُواْ اُ۬لْحَمْدُ لِلهِ اِ۬لذِے هَدَيٰنَا لِهَٰذَاۖ وَمَا كُنَّا لِنَهْتَدِيَ لَوْلَا أَنْ هَدَيٰنَا اَ۬للَّهُۖ لَقَدْ جَآءَتْ رُسُلُ رَبِّنَا بِالْحَقِّۖ وَنُودُواْ أَن تِلْكُمُ اُ۬لْجَنَّةُ أُورِثْتُمُوهَا بِمَا كُنتُمْ تَعْمَلُونَۖ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (999,8,'156',7,'Al-A‘rāf','الأعرَاف',1,3,43,'وَنَادَىٰ أَصْحَٰبُ اُ۬لْجَنَّةِ أَصْحَٰبَ اَ۬لنَّارِ أَن قَدْ وَجَدْنَا مَا وَعَدَنَا رَبُّنَا حَقّاٗ فَهَلْ وَجَدتُّم مَّا وَعَدَ رَبُّكُمْ حَقّاٗۖ قَالُواْ نَعَمْۖ فَأَذَّنَ مُؤَذِّنُۢ بَيْنَهُمْ أَن لَّعْنَةُ اُ۬للَّهِ عَلَى اَ۬لظَّٰلِمِينَ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1000,8,'156',7,'Al-A‘rāf','الأعرَاف',3,4,44,'اَ۬لذِينَ يَصُدُّونَ عَن سَبِيلِ اِ۬للَّهِ وَيَبْغُونَهَا عِوَجاٗ وَهُم بِالْأٓخِرَةِ كَٰفِرُونَۖ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1001,8,'156',7,'Al-A‘rāf','الأعرَاف',4,6,45,'وَبَيْنَهُمَا حِجَابٞۖ وَعَلَى اَ۬لْأَعْرَافِ رِجَالٞ يَعْرِفُونَ كُلّاَۢ بِسِيمَيٰهُمْۖ وَنَادَوْاْ أَصْحَٰبَ اَ۬لْجَنَّةِ أَن سَلَٰمٌ عَلَيْكُمْۖ لَمْ يَدْخُلُوهَا وَهُمْ يَطْمَعُونَۖ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1002,8,'156',7,'Al-A‘rāf','الأعرَاف',7,8,46,'۞ وَإِذَا صُرِفَتْ أَبْصَٰرُهُمْ تِلْقَا أَصْحَٰبِ اِ۬لنَّارِ قَالُواْ رَبَّنَا لَا تَجْعَلْنَا مَعَ اَ۬لْقَوْمِ اِ۬لظَّٰلِمِينَۖ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1003,8,'156',7,'Al-A‘rāf','الأعرَاف',8,9,47,'وَنَادَىٰ أَصْحَٰبُ اُ۬لْأَعْرَافِ رِجَالاٗ يَعْرِفُونَهُم بِسِيمَيٰهُمْ قَالُواْ مَا أَغْنَىٰ عَنكُمْ جَمْعُكُمْ وَمَا كُنتُمْ تَسْتَكْبِرُونَۖ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1004,8,'156',7,'Al-A‘rāf','الأعرَاف',10,11,48,'أَهَٰؤُلَآءِ اِ۬لذِينَ أَقْسَمْتُمْ لَا يَنَالُهُمُ اُ۬للَّهُ بِرَحْمَةٍۖ اُ۟دْخُلُواْ اُ۬لْجَنَّةَ لَا خَوْفٌ عَلَيْكُمْ وَلَا أَنتُمْ تَحْزَنُونَۖ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1005,8,'156',7,'Al-A‘rāf','الأعرَاف',11,13,49,'وَنَادَىٰ أَصْحَٰبُ اُ۬لنَّارِ أَصْحَٰبَ اَ۬لْجَنَّةِ أَنْ أَفِيضُواْ عَلَيْنَا مِنَ اَ۬لْمَآءِ اَ۬وْ مِمَّا رَزَقَكُمُ اُ۬للَّهُۖ قَالُواْ إِنَّ اَ۬للَّهَ حَرَّمَهُمَا عَلَى اَ۬لْكَٰفِرِينَ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1006,8,'156',7,'Al-A‘rāf','الأعرَاف',13,15,50,'اَ۬لذِينَ اَ۪تَّخَذُواْ دِينَهُمْ لَهْواٗ وَلَعِباٗ وَغَرَّتْهُمُ اُ۬لْحَيَوٰةُ اُ۬لدُّنْيَاۖ فَالْيَوْمَ نَنسَيٰهُمْ كَمَا نَسُواْ لِقَآءَ يَوْمِهِمْ هَٰذَا وَمَا كَانُواْ بِـَٔايَٰتِنَا يَجْحَدُونَۖ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1007,8,'157',7,'Al-A‘rāf','الأعرَاف',1,2,51,'وَلَقَدْ جِئْنَٰهُم بِكِتَٰبٖ فَصَّلْنَٰهُ عَلَىٰ عِلْمٍ هُدىٗ وَرَحْمَةٗ لِّقَوْمٖ يُؤْمِنُونَۖ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1008,8,'157',7,'Al-A‘rāf','الأعرَاف',2,6,52,'هَلْ يَنظُرُونَ إِلَّا تَأْوِيلَهُۥۖ يَوْمَ يَأْتِے تَأْوِيلُهُۥ يَقُولُ اُ۬لذِينَ نَسُوهُ مِن قَبْلُ قَدْ جَآءَتْ رُسُلُ رَبِّنَا بِالْحَقِّ فَهَل لَّنَا مِن شُفَعَآءَ فَيَشْفَعُواْ لَنَا أَوْ نُرَدُّ فَنَعْمَلَ غَيْرَ اَ۬لذِے كُنَّا نَعْمَلُۖ قَدْ خَسِرُواْ أَنفُسَهُمْ وَضَلَّ عَنْهُم مَّا كَانُواْ يَفْتَرُونَۖ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1009,8,'157',7,'Al-A‘rāf','الأعرَاف',6,9,53,'إِنَّ رَبَّكُمُ اُ۬للَّهُ اُ۬لذِے خَلَقَ اَ۬لسَّمَٰوَٰتِ وَالْأَرْضَ فِے سِتَّةِ أَيَّامٖ ثُمَّ اَ۪سْتَوَىٰ عَلَى اَ۬لْعَرْشِۖ يُغْشِے اِ۬ليْلَ اَ۬لنَّهَارَ يَطْلُبُهُۥ حَثِيثاٗ وَالشَّمْسَ وَالْقَمَرَ وَالنُّجُومَ مُسَخَّرَٰتِۢ بِأَمْرِهِۦۖ أَلَا لَهُ اُ۬لْخَلْقُ وَالْأَمْرُۖ تَبَٰرَكَ اَ۬للَّهُ رَبُّ اُ۬لْعَٰلَمِينَۖ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1010,8,'157',7,'Al-A‘rāf','الأعرَاف',10,10,54,'اَ۟دْعُواْ رَبَّكُمْ تَضَرُّعاٗ وَخُفْيَةًۖ إِنَّهُۥ لَا يُحِبُّ اُ۬لْمُعْتَدِينَۖ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1011,8,'157',7,'Al-A‘rāf','الأعرَاف',11,12,55,'وَلَا تُفْسِدُواْ فِے اِ۬لْأَرْضِ بَعْدَ إِصْلَٰحِهَاۖ وَادْعُوهُ خَوْفاٗ وَطَمَعاًۖ إِنَّ رَحْمَتَ اَ۬للَّهِ قَرِيبٞ مِّنَ اَ۬لْمُحْسِنِينَۖ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1012,8,'157',7,'Al-A‘rāf','الأعرَاف',12,15,56,'۞ وَهْوَ اَ۬لذِے يُرْسِلُ اُ۬لرِّيَٰحَ نُشُراَۢ بَيْنَ يَدَےْ رَحْمَتِهِۦۖ حَتَّىٰ إِذَا أَقَلَّتْ سَحَاباٗ ثِقَالاٗ سُقْنَٰهُ لِبَلَدٖ مَّيِّتٖ فَأَنزَلْنَا بِهِ اِ۬لْمَآءَ فَأَخْرَجْنَا بِهِۦ مِن كُلِّ اِ۬لثَّمَرَٰتِۖ كَذَٰلِكَ نُخْرِجُ اُ۬لْمَوْتَىٰ لَعَلَّكُمْ تَذَّكَّرُونَۖ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1013,8,'158',7,'Al-A‘rāf','الأعرَاف',1,2,57,'وَالْبَلَدُ اُ۬لطَّيِّبُ يَخْرُجُ نَبَاتُهُۥ بِإِذْنِ رَبِّهِۦۖ وَالذِے خَبُثَ لَا يَخْرُجُ إِلَّا نَكِداٗۖ كَذَٰلِكَ نُصَرِّفُ اُ۬لْأٓيَٰتِ لِقَوْمٖ يَشْكُرُونَۖ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1014,8,'158',7,'Al-A‘rāf','الأعرَاف',3,4,58,'لَقَدْ أَرْسَلْنَا نُوحاً إِلَىٰ قَوْمِهِۦ فَقَالَ يَٰقَوْمِ اِ۟عْبُدُواْ اُ۬للَّهَ مَا لَكُم مِّنْ إِلَٰهٍ غَيْرُهُۥۖ إِنِّيَ أَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ عَظِيمٖۖ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1015,8,'158',7,'Al-A‘rāf','الأعرَاف',5,5,59,'قَالَ اَ۬لْمَلَأُ مِن قَوْمِهِۦ إِنَّا لَنَرَيٰكَ فِے ضَلَٰلٖ مُّبِينٖۖ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1016,8,'158',7,'Al-A‘rāf','الأعرَاف',5,6,60,'قَالَ يَٰقَوْمِ لَيْسَ بِے ضَلَٰلَةٞ وَلَٰكِنِّے رَسُولٞ مِّن رَّبِّ اِ۬لْعَٰلَمِينَۖ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1017,8,'158',7,'Al-A‘rāf','الأعرَاف',7,8,61,'أُبَلِّغُكُمْ رِسَٰلَٰتِ رَبِّے وَأَنصَحُ لَكُمْ وَأَعْلَمُ مِنَ اَ۬للَّهِ مَا لَا تَعْلَمُونَۖ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1018,8,'158',7,'Al-A‘rāf','الأعرَاف',8,9,62,'أَوَعَجِبْتُمْ أَن جَآءَكُمْ ذِكْرٞ مِّن رَّبِّكُمْ عَلَىٰ رَجُلٖ مِّنكُمْ لِيُنذِرَكُمْ وَلِتَتَّقُواْ وَلَعَلَّكُمْ تُرْحَمُونَۖ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1019,8,'158',7,'Al-A‘rāf','الأعرَاف',10,11,63,'فَكَذَّبُوهُ فَأَنجَيْنَٰهُ وَالذِينَ مَعَهُۥ فِے اِ۬لْفُلْكِۖ وَأَغْرَقْنَا اَ۬لذِينَ كَذَّبُواْ بِـَٔايَٰتِنَاۖ إِنَّهُمْ كَانُواْ قَوْماً عَمِينَۖ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1020,8,'158',7,'Al-A‘rāf','الأعرَاف',11,13,64,'وَإِلَىٰ عَادٍ أَخَاهُمْ هُوداٗۖ قَالَ يَٰقَوْمِ اِ۟عْبُدُواْ اُ۬للَّهَ مَا لَكُم مِّنْ إِلَٰهٍ غَيْرُهُۥۖ أَفَلَا تَتَّقُونَۖ ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1021,8,'158',7,'Al-A‘rāf','الأعرَاف',13,14,65,'قَالَ اَ۬لْمَلَأُ اُ۬لذِينَ كَفَرُواْ مِن قَوْمِهِۦ إِنَّا لَنَرَيٰكَ فِے سَفَاهَةٖ وَإِنَّا لَنَظُنُّكَ مِنَ اَ۬لْكَٰذِبِينَۖ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1022,8,'158',7,'Al-A‘rāf','الأعرَاف',15,15,66,'قَالَ يَٰقَوْمِ لَيْسَ بِے سَفَاهَةٞ وَلَٰكِنِّے رَسُولٞ مِّن رَّبِّ اِ۬لْعَٰلَمِينَۖ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1023,8,'159',7,'Al-A‘rāf','الأعرَاف',1,1,67,'أُبَلِّغُكُمْ رِسَٰلَٰتِ رَبِّے وَأَنَا لَكُمْ نَاصِحٌ أَمِينٌۖ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1024,8,'159',7,'Al-A‘rāf','الأعرَاف',1,4,68,'۞ أَوَعَجِبْتُمْ أَن جَآءَكُمْ ذِكْرٞ مِّن رَّبِّكُمْ عَلَىٰ رَجُلٖ مِّنكُمْ لِيُنذِرَكُمْۖ وَاذْكُرُواْ إِذْ جَعَلَكُمْ خُلَفَآءَ مِنۢ بَعْدِ قَوْمِ نُوحٖ وَزَادَكُمْ فِے اِ۬لْخَلْقِ بَصْطَةٗۖ فَاذْكُرُواْ ءَالَآءَ اَ۬للَّهِ لَعَلَّكُمْ تُفْلِحُونَۖ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1025,8,'159',7,'Al-A‘rāf','الأعرَاف',5,6,69,'قَالُواْ أَجِئْتَنَا لِنَعْبُدَ اَ۬للَّهَ وَحْدَهُۥ وَنَذَرَ مَا كَانَ يَعْبُدُ ءَابَآؤُنَا فَأْتِنَا بِمَا تَعِدُنَا إِن كُنتَ مِنَ اَ۬لصَّٰدِقِينَۖ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1026,8,'159',7,'Al-A‘rāf','الأعرَاف',7,10,70,'قَالَ قَدْ وَقَعَ عَلَيْكُم مِّن رَّبِّكُمْ رِجْسٞ وَغَضَبٌۖ أَتُجَٰدِلُونَنِے فِے أَسْمَآءٖ سَمَّيْتُمُوهَا أَنتُمْ وَءَابَآؤُكُم مَّا نَزَّلَ اَ۬للَّهُ بِهَا مِن سُلْطَٰنٖۖ فَانتَظِرُواْۖ إِنِّے مَعَكُم مِّنَ اَ۬لْمُنتَظِرِينَۖ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1027,8,'159',7,'Al-A‘rāf','الأعرَاف',10,11,71,'فَأَنجَيْنَٰهُ وَالذِينَ مَعَهُۥ بِرَحْمَةٖ مِّنَّا وَقَطَعْنَا دَابِرَ اَ۬لذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَمَا كَانُواْ مُؤْمِنِينَۖ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1028,8,'159',7,'Al-A‘rāf','الأعرَاف',12,15,72,'وَإِلَىٰ ثَمُودَ أَخَاهُمْ صَٰلِحاٗۖ قَالَ يَٰقَوْمِ اِ۟عْبُدُواْ اُ۬للَّهَ مَا لَكُم مِّنْ إِلَٰهٍ غَيْرُهُۥۖ قَدْ جَآءَتْكُم بَيِّنَةٞ مِّن رَّبِّكُمْۖ هَٰذِهِۦ نَاقَةُ اُ۬للَّهِ لَكُمْ ءَايَةٗۖ فَذَرُوهَا تَأْكُلْ فِے أَرْضِ اِ۬للَّهِۖ وَلَا تَمَسُّوهَا بِسُوٓءٖ فَيَأْخُذَكُمْ عَذَابٌ أَلِيمٞۖ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1029,8,'160',7,'Al-A‘rāf','الأعرَاف',1,4,73,'وَاذْكُرُواْ إِذْ جَعَلَكُمْ خُلَفَآءَ مِنۢ بَعْدِ عَادٖ وَبَوَّأَكُمْ فِے اِ۬لْأَرْضِ تَتَّخِذُونَ مِن سُهُولِهَا قُصُوراٗ وَتَنْحِتُونَ اَ۬لْجِبَالَ بِيُوتاٗۖ فَاذْكُرُواْ ءَالَآءَ اَ۬للَّهِۖ وَلَا تَعْثَوْاْ فِے اِ۬لْأَرْضِ مُفْسِدِينَۖ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1030,8,'160',7,'Al-A‘rāf','الأعرَاف',4,7,74,'قَالَ اَ۬لْمَلَأُ اُ۬لذِينَ اَ۪سْتَكْبَرُواْ مِن قَوْمِهِۦ لِلذِينَ اَ۟سْتُضْعِفُواْ لِمَنْ ءَامَنَ مِنْهُمْ أَتَعْلَمُونَ أَنَّ صَٰلِحاٗ مُّرْسَلٞ مِّن رَّبِّهِۦۖ قَالُواْ إِنَّا بِمَا أُرْسِلَ بِهِۦ مُؤْمِنُونَۖ ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1031,8,'160',7,'Al-A‘rāf','الأعرَاف',7,8,75,'قَالَ اَ۬لذِينَ اَ۪سْتَكْبَرُواْ إِنَّا بِالذِے ءَامَنتُم بِهِۦ كَٰفِرُونَۖ ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1032,8,'160',7,'Al-A‘rāf','الأعرَاف',8,10,76,'۞ فَعَقَرُواْ اُ۬لنَّاقَةَ وَعَتَوْاْ عَنْ أَمْرِ رَبِّهِمْۖ وَقَالُواْ يَٰصَٰلِحُ اُ۪ئْتِنَا بِمَا تَعِدُنَا إِن كُنتَ مِنَ اَ۬لْمُرْسَلِينَۖ ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1033,8,'160',7,'Al-A‘rāf','الأعرَاف',10,11,77,'فَأَخَذَتْهُمُ اُ۬لرَّجْفَةُ فَأَصْبَحُواْ فِے دَارِهِمْ جَٰثِمِينَۖ ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1034,8,'160',7,'Al-A‘rāf','الأعرَاف',11,12,78,'فَتَوَلَّىٰ عَنْهُمْ وَقَالَ يَٰقَوْمِ لَقَدْ أَبْلَغْتُكُمْ رِسَالَةَ رَبِّے وَنَصَحْتُ لَكُمْۖ وَلَٰكِن لَّا تُحِبُّونَ اَ۬لنَّٰصِحِينَۖ ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1035,8,'160',7,'Al-A‘rāf','الأعرَاف',13,14,79,'وَلُوطاً إِذْ قَالَ لِقَوْمِهِۦ أَتَأْتُونَ اَ۬لْفَٰحِشَةَ مَا سَبَقَكُم بِهَا مِنْ أَحَدٖ مِّنَ اَ۬لْعَٰلَمِينَۖ ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1036,8,'160',7,'Al-A‘rāf','الأعرَاف',14,15,80,'إِنَّكُمْ لَتَأْتُونَ اَ۬لرِّجَالَ شَهْوَةٗ مِّن دُونِ اِ۬لنِّسَآءِۖ بَلْ أَنتُمْ قَوْمٞ مُّسْرِفُونَۖ ٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1037,8,'161',7,'Al-A‘rāf','الأعرَاف',1,2,81,'وَمَا كَانَ جَوَابَ قَوْمِهِۦ إِلَّا أَن قَالُواْ أَخْرِجُوهُم مِّن قَرْيَتِكُمْۖ إِنَّهُمْ أُنَاسٞ يَتَطَهَّرُونَۖ ٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1038,8,'161',7,'Al-A‘rāf','الأعرَاف',2,3,82,'فَأَنجَيْنَٰهُ وَأَهْلَهُۥ إِلَّا اَ۪مْرَأَتَهُۥ كَانَتْ مِنَ اَ۬لْغَٰبِرِينَۖ ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1039,8,'161',7,'Al-A‘rāf','الأعرَاف',3,4,83,'وَأَمْطَرْنَا عَلَيْهِم مَّطَراٗۖ فَانظُرْ كَيْفَ كَانَ عَٰقِبَةُ اُ۬لْمُجْرِمِينَۖ ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1040,8,'161',7,'Al-A‘rāf','الأعرَاف',5,9,84,'وَإِلَىٰ مَدْيَنَ أَخَاهُمْ شُعَيْباٗۖ قَالَ يَٰقَوْمِ اِ۟عْبُدُواْ اُ۬للَّهَ مَا لَكُم مِّنْ إِلَٰهٍ غَيْرُهُۥۖ قَدْ جَآءَتْكُم بَيِّنَةٞ مِّن رَّبِّكُمْۖ فَأَوْفُواْ اُ۬لْكَيْلَ وَالْمِيزَانَۖ وَلَا تَبْخَسُواْ اُ۬لنَّاسَ أَشْيَآءَهُمْۖ وَلَا تُفْسِدُواْ فِے اِ۬لْأَرْضِ بَعْدَ إِصْلَٰحِهَاۖ ذَٰلِكُمْ خَيْرٞ لَّكُمْ إِن كُنتُم مُّؤْمِنِينَۖ ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1041,8,'161',7,'Al-A‘rāf','الأعرَاف',9,13,85,'وَلَا تَقْعُدُواْ بِكُلِّ صِرَٰطٖ تُوعِدُونَ وَتَصُدُّونَ عَن سَبِيلِ اِ۬للَّهِ مَنْ ءَامَنَ بِهِۦ وَتَبْغُونَهَا عِوَجاٗۖ وَاذْكُرُواْ إِذْ كُنتُمْ قَلِيلاٗ فَكَثَّرَكُمْۖ وَانظُرُواْ كَيْفَ كَانَ عَٰقِبَةُ اُ۬لْمُفْسِدِينَۖ ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1042,8,'161',7,'Al-A‘rāf','الأعرَاف',13,15,86,'وَإِن كَانَ طَآئِفَةٞ مِّنكُمْ ءَامَنُواْ بِالذِے أُرْسِلْتُ بِهِۦ وَطَآئِفَةٞ لَّمْ يُؤْمِنُواْ فَاصْبِرُواْ حَتَّىٰ يَحْكُمَ اَ۬للَّهُ بَيْنَنَاۖ وَهْوَ خَيْرُ اُ۬لْحَٰكِمِينَۖ ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1043,9,'162',7,'Al-A‘rāf','الأعرَاف',1,3,87,'۞ قَالَ اَ۬لْمَلَأُ اُ۬لذِينَ اَ۪سْتَكْبَرُواْ مِن قَوْمِهِۦ لَنُخْرِجَنَّكَ يَٰشُعَيْبُ وَالذِينَ ءَامَنُواْ مَعَكَ مِن قَرْيَتِنَا أَوْ لَتَعُودُنَّ فِے مِلَّتِنَاۖ قَالَ أَوَلَوْ كُنَّا كَٰرِهِينَۖ ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1044,9,'162',7,'Al-A‘rāf','الأعرَاف',3,6,88,'قَدِ اِ۪فْتَرَيْنَا عَلَى اَ۬للَّهِ كَذِباً إِنْ عُدْنَا فِے مِلَّتِكُم بَعْدَ إِذْ نَجَّيٰنَا اَ۬للَّهُ مِنْهَاۖ وَمَا يَكُونُ لَنَا أَن نَّعُودَ فِيهَا إِلَّا أَنْ يَّشَآءَ اَ۬للَّهُ رَبُّنَاۖ وَسِعَ رَبُّنَا كُلَّ شَےْءٍ عِلْماًۖ عَلَى اَ۬للَّهِ تَوَكَّلْنَاۖ رَبَّنَا اَ۪فْتَحْ بَيْنَنَا وَبَيْنَ قَوْمِنَا بِالْحَقِّۖ وَأَنتَ خَيْرُ اُ۬لْفَٰتِحِينَۖ ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1045,9,'162',7,'Al-A‘rāf','الأعرَاف',6,8,89,'وَقَالَ اَ۬لْمَلَأُ اُ۬لذِينَ كَفَرُواْ مِن قَوْمِهِۦ لَئِنِ اِ۪تَّبَعْتُمْ شُعَيْباً إِنَّكُمْ إِذاٗ لَّخَٰسِرُونَۖ ٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1046,9,'162',7,'Al-A‘rāf','الأعرَاف',8,8,90,'فَأَخَذَتْهُمُ اُ۬لرَّجْفَةُ فَأَصْبَحُواْ فِے دَارِهِمْ جَٰثِمِينَۖ ٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1047,9,'162',7,'Al-A‘rāf','الأعرَاف',9,10,91,'اَ۬لذِينَ كَذَّبُواْ شُعَيْباٗ كَأَن لَّمْ يَغْنَوْاْ فِيهَاۖ اَ۬لذِينَ كَذَّبُواْ شُعَيْباٗ كَانُواْ هُمُ اُ۬لْخَٰسِرِينَۖ ٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1048,9,'162',7,'Al-A‘rāf','الأعرَاف',10,12,92,'فَتَوَلَّىٰ عَنْهُمْ وَقَالَ يَٰقَوْمِ لَقَدْ أَبْلَغْتُكُمْ رِسَٰلَٰتِ رَبِّے وَنَصَحْتُ لَكُمْۖ فَكَيْفَ ءَاسَىٰ عَلَىٰ قَوْمٖ كَٰفِرِينَۖ ٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1049,9,'162',7,'Al-A‘rāf','الأعرَاف',12,13,93,'وَمَا أَرْسَلْنَا فِے قَرْيَةٖ مِّن نَّبِےٓءٍ إِلَّا أَخَذْنَا أَهْلَهَا بِالْبَأْسَآءِ وَالضَّرَّآءِ لَعَلَّهُمْ يَضَّرَّعُونَۖ ٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1050,9,'162',7,'Al-A‘rāf','الأعرَاف',13,15,94,'ثُمَّ بَدَّلْنَا مَكَانَ اَ۬لسَّيِّئَةِ اِ۬لْحَسَنَةَ حَتَّىٰ عَفَواْۖ وَّقَالُواْ قَدْ مَسَّ ءَابَآءَنَا اَ۬لضَّرَّآءُ وَالسَّرَّآءُ فَأَخَذْنَٰهُم بَغْتَةٗ وَهُمْ لَا يَشْعُرُونَۖ ٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1051,9,'163',7,'Al-A‘rāf','الأعرَاف',1,3,95,'وَلَوْ أَنَّ أَهْلَ اَ۬لْقُرَىٰ ءَامَنُواْ وَاتَّقَوْاْ لَفَتَحْنَا عَلَيْهِم بَرَكَٰتٖ مِّنَ اَ۬لسَّمَآءِ وَالْأَرْضِۖ وَلَٰكِن كَذَّبُواْ فَأَخَذْنَٰهُم بِمَا كَانُواْ يَكْسِبُونَۖ ٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1052,9,'163',7,'Al-A‘rāf','الأعرَاف',3,4,96,'أَفَأَمِنَ أَهْلُ اُ۬لْقُرَىٰ أَنْ يَّأْتِيَهُم بَأْسُنَا بَيَٰتاٗ وَهُمْ نَآئِمُونَ ٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1053,9,'163',7,'Al-A‘rāf','الأعرَاف',4,5,97,'أَوْ أَمِنَ أَهْلُ اُ۬لْقُرَىٰ أَنْ يَّأْتِيَهُم بَأْسُنَا ضُحىٗ وَهُمْ يَلْعَبُونَۖ ٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1054,9,'163',7,'Al-A‘rāf','الأعرَاف',5,6,98,'أَفَأَمِنُواْ مَكْرَ اَ۬للَّهِۖ فَلَا يَأْمَنُ مَكْرَ اَ۬للَّهِ إِلَّا اَ۬لْقَوْمُ اُ۬لْخَٰسِرُونَۖ ٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1055,9,'163',7,'Al-A‘rāf','الأعرَاف',6,8,99,'۞ أَوَلَمْ يَهْدِ لِلذِينَ يَرِثُونَ اَ۬لْأَرْضَ مِنۢ بَعْدِ أَهْلِهَا أَن لَّوْ نَشَآءُ اَ۬صَبْنَٰهُم بِذُنُوبِهِمْۖ وَنَطْبَعُ عَلَىٰ قُلُوبِهِمْ فَهُمْ لَا يَسْمَعُونَۖ ٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1056,9,'163',7,'Al-A‘rāf','الأعرَاف',9,11,100,'تِلْكَ اَ۬لْقُرَىٰ نَقُصُّ عَلَيْكَ مِنْ أَنۢبَآئِهَاۖ وَلَقَدْ جَآءَتْهُمْ رُسُلُهُم بِالْبَيِّنَٰتِ فَمَا كَانُواْ لِيُؤْمِنُواْ بِمَا كَذَّبُواْ مِن قَبْلُۖ كَذَٰلِكَ يَطْبَعُ اُ۬للَّهُ عَلَىٰ قُلُوبِ اِ۬لْكَٰفِرِينَۖ ١٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1057,9,'163',7,'Al-A‘rāf','الأعرَاف',11,12,101,'وَمَا وَجَدْنَا لِأَكْثَرِهِم مِّنْ عَهْدٖۖ وَإِنْ وَّجَدْنَا أَكْثَرَهُمْ لَفَٰسِقِينَۖ ١٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1058,9,'163',7,'Al-A‘rāf','الأعرَاف',13,14,102,'ثُمَّ بَعَثْنَا مِنۢ بَعْدِهِم مُّوسَىٰ بِـَٔايَٰتِنَا إِلَىٰ فِرْعَوْنَ وَمَلَإِيْهِۦ فَظَلَمُواْ بِهَاۖ فَانظُرْ كَيْفَ كَانَ عَٰقِبَةُ اُ۬لْمُفْسِدِينَۖ ١٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1059,9,'163',7,'Al-A‘rāf','الأعرَاف',15,15,103,'وَقَالَ مُوسَىٰ يَٰفِرْعَوْنُ إِنِّے رَسُولٞ مِّن رَّبِّ اِ۬لْعَٰلَمِينَ ١٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1060,9,'164',7,'Al-A‘rāf','الأعرَاف',1,2,104,'حَقِيقٌۖ عَلَيَّ أَن لَّا أَقُولَ عَلَى اَ۬للَّهِ إِلَّا اَ۬لْحَقَّۖ قَدْ جِئْتُكُم بِبَيِّنَةٖ مِّن رَّبِّكُمْۖ فَأَرْسِلْ مَعِے بَنِے إِسْرَآءِيلَۖ ١٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1061,9,'164',7,'Al-A‘rāf','الأعرَاف',2,3,105,'قَالَ إِن كُنتَ جِئْتَ بِـَٔايَةٖ فَأْتِ بِهَا إِن كُنتَ مِنَ اَ۬لصَّٰدِقِينَۖ ١٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1062,9,'164',7,'Al-A‘rāf','الأعرَاف',3,4,106,'فَأَلْقَىٰ عَصَاهُ فَإِذَا هِيَ ثُعْبَانٞ مُّبِينٞ ١٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1063,9,'164',7,'Al-A‘rāf','الأعرَاف',4,5,107,'وَنَزَعَ يَدَهُۥ فَإِذَا هِيَ بَيْضَآءُ لِلنَّٰظِرِينَۖ ١٠٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1064,9,'164',7,'Al-A‘rāf','الأعرَاف',5,6,108,'قَالَ اَ۬لْمَلَأُ مِن قَوْمِ فِرْعَوْنَ إِنَّ هَٰذَا لَسَٰحِرٌ عَلِيمٞ ١٠٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1065,9,'164',7,'Al-A‘rāf','الأعرَاف',6,6,109,'يُرِيدُ أَنْ يُّخْرِجَكُم مِّنْ أَرْضِكُمْۖ فَمَاذَا تَأْمُرُونَۖ ١٠٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1066,9,'164',7,'Al-A‘rāf','الأعرَاف',7,7,110,'قَالُواْ أَرْجِهِ وَأَخَاهُ وَأَرْسِلْ فِے اِ۬لْمَدَآئِنِ حَٰشِرِينَ ١١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1067,9,'164',7,'Al-A‘rāf','الأعرَاف',7,8,111,'يَأْتُوكَ بِكُلِّ سَٰحِرٍ عَلِيمٖۖ ١١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1068,9,'164',7,'Al-A‘rāf','الأعرَاف',8,9,112,'وَجَآءَ اَ۬لسَّحَرَةُ فِرْعَوْنَ قَالُواْ إِنَّ لَنَا لَأَجْراً إِن كُنَّا نَحْنُ اُ۬لْغَٰلِبِينَۖ ١١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1069,9,'164',7,'Al-A‘rāf','الأعرَاف',9,10,113,'قَالَ نَعَمْ وَإِنَّكُمْ لَمِنَ اَ۬لْمُقَرَّبِينَۖ ١١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1070,9,'164',7,'Al-A‘rāf','الأعرَاف',10,11,114,'قَالُواْ يَٰمُوسَىٰ إِمَّا أَن تُلْقِيَ وَإِمَّا أَن نَّكُونَ نَحْنُ اُ۬لْمُلْقِينَۖ ١١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1071,9,'164',7,'Al-A‘rāf','الأعرَاف',11,12,115,'قَالَ أَلْقُواْۖ فَلَمَّا أَلْقَوْاْ سَحَرُواْ أَعْيُنَ اَ۬لنَّاسِ وَاسْتَرْهَبُوهُمْۖ وَجَآءُو بِسِحْرٍ عَظِيمٖۖ ١١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1072,9,'164',7,'Al-A‘rāf','الأعرَاف',13,14,116,'۞ وَأَوْحَيْنَا إِلَىٰ مُوسَىٰ أَنْ أَلْقِ عَصَاكَۖ فَإِذَا هِيَ تَلَقَّفُ مَا يَأْفِكُونَۖ ١١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1073,9,'164',7,'Al-A‘rāf','الأعرَاف',14,14,117,'فَوَقَعَ اَ۬لْحَقُّ وَبَطَلَ مَا كَانُواْ يَعْمَلُونَۖ ١١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1074,9,'164',7,'Al-A‘rāf','الأعرَاف',14,15,118,'فَغُلِبُواْ هُنَالِكَ وَانقَلَبُواْ صَٰغِرِينَۖ ١١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1075,9,'164',7,'Al-A‘rāf','الأعرَاف',15,15,119,'وَأُلْقِيَ اَ۬لسَّحَرَةُ سَٰجِدِينَۖ ١١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1076,9,'165',7,'Al-A‘rāf','الأعرَاف',1,1,120,'قَالُواْ ءَامَنَّا بِرَبِّ اِ۬لْعَٰلَمِينَ ١٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1077,9,'165',7,'Al-A‘rāf','الأعرَاف',1,1,121,'رَبِّ مُوسَىٰ وَهَٰرُونَۖ ١٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1078,9,'165',7,'Al-A‘rāf','الأعرَاف',1,3,122,'قَالَ فِرْعَوْنُ ءَاٰ۬مَنتُم بِهِۦ قَبْلَ أَنْ ءَاذَنَ لَكُمْ إِنَّ هَٰذَا لَمَكْرٞ مَّكَرْتُمُوهُ فِے اِ۬لْمَدِينَةِ لِتُخْرِجُواْ مِنْهَا أَهْلَهَاۖ فَسَوْفَ تَعْلَمُونَۖ ١٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1079,9,'165',7,'Al-A‘rāf','الأعرَاف',4,5,123,'لَأُقَطِّعَنَّ أَيْدِيَكُمْ وَأَرْجُلَكُم مِّنْ خِلَٰفٖ ثُمَّ لَأُصَلِّبَنَّكُمْ أَجْمَعِينَۖ ١٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1080,9,'165',7,'Al-A‘rāf','الأعرَاف',5,5,124,'قَالُواْ إِنَّا إِلَىٰ رَبِّنَا مُنقَلِبُونَۖ ١٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1081,9,'165',7,'Al-A‘rāf','الأعرَاف',5,7,125,'وَمَا تَنقِمُ مِنَّا إِلَّا أَنْ ءَامَنَّا بِـَٔايَٰتِ رَبِّنَا لَمَّا جَآءَتْنَاۖ رَبَّنَا أَفْرِغْ عَلَيْنَا صَبْراٗ وَتَوَفَّنَا مُسْلِمِينَۖ ١٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1082,9,'165',7,'Al-A‘rāf','الأعرَاف',7,9,126,'وَقَالَ اَ۬لْمَلَأُ مِن قَوْمِ فِرْعَوْنَ أَتَذَرُ مُوسَىٰ وَقَوْمَهُۥ لِيُفْسِدُواْ فِے اِ۬لْأَرْضِ وَيَذَرَكَ وَءَالِهَتَكَۖ قَالَ سَنَقْتُلُ أَبْنَآءَهُمْ وَنَسْتَحْيِۦ نِسَآءَهُمْ وَإِنَّا فَوْقَهُمْ قَٰهِرُونَۖ ١٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1083,9,'165',7,'Al-A‘rāf','الأعرَاف',10,11,127,'قَالَ مُوسَىٰ لِقَوْمِهِ اِ۪سْتَعِينُواْ بِاللَّهِ وَاصْبِرُواْۖ إِنَّ اَ۬لْأَرْضَ لِلهِ يُورِثُهَا مَنْ يَّشَآءُ مِنْ عِبَادِهِۦۖ وَالْعَٰقِبَةُ لِلْمُتَّقِينَۖ ١٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1084,9,'165',7,'Al-A‘rāf','الأعرَاف',12,14,128,'قَالُواْ أُوذِينَا مِن قَبْلِ أَن تَأْتِيَنَا وَمِنۢ بَعْدِ مَا جِئْتَنَاۖ قَالَ عَسَىٰ رَبُّكُمْ أَنْ يُّهْلِكَ عَدُوَّكُمْ وَيَسْتَخْلِفَكُمْ فِے اِ۬لْأَرْضِ فَيَنظُرَ كَيْفَ تَعْمَلُونَۖ ١٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1085,9,'165',7,'Al-A‘rāf','الأعرَاف',14,15,129,'وَلَقَدْ أَخَذْنَا ءَالَ فِرْعَوْنَ بِالسِّنِينَ وَنَقْصٖ مِّنَ اَ۬لثَّمَرَٰتِ لَعَلَّهُمْ يَذَّكَّرُونَۖ ١٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1086,9,'166',7,'Al-A‘rāf','الأعرَاف',1,3,130,'فَإِذَا جَآءَتْهُمُ اُ۬لْحَسَنَةُ قَالُواْ لَنَا هَٰذِهِۦۖ وَإِن تُصِبْهُمْ سَيِّئَةٞ يَطَّيَّرُواْ بِمُوسَىٰ وَمَن مَّعَهُۥۖ أَلَا إِنَّمَا طَٰٓئِرُهُمْ عِندَ اَ۬للَّهِۖ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَۖ ١٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1087,9,'166',7,'Al-A‘rāf','الأعرَاف',3,4,131,'۞ وَقَالُواْ مَهْمَا تَأْتِنَا بِهِۦ مِنْ ءَايَةٖ لِّتَسْحَرَنَا بِهَا فَمَا نَحْنُ لَكَ بِمُؤْمِنِينَۖ ١٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1088,9,'166',7,'Al-A‘rāf','الأعرَاف',4,6,132,'فَأَرْسَلْنَا عَلَيْهِمُ اُ۬لطُّوفَانَ وَالْجَرَادَ وَالْقُمَّلَ وَالضَّفَادِعَ وَالدَّمَ ءَايَٰتٖ مُّفَصَّلَٰتٖ فَاسْتَكْبَرُواْ وَكَانُواْ قَوْماٗ مُّجْرِمِينَۖ ١٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1089,9,'166',7,'Al-A‘rāf','الأعرَاف',7,9,133,'وَلَمَّا وَقَعَ عَلَيْهِمُ اُ۬لرِّجْزُ قَالُواْ يَٰمُوسَى اَ۟دْعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِندَكَ لَئِن كَشَفْتَ عَنَّا اَ۬لرِّجْزَ لَنُؤْمِنَنَّ لَكَ وَلَنُرْسِلَنَّ مَعَكَ بَنِے إِسْرَآءِيلَۖ ١٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1090,9,'166',7,'Al-A‘rāf','الأعرَاف',9,10,134,'فَلَمَّا كَشَفْنَا عَنْهُمُ اُ۬لرِّجْزَ إِلَىٰ أَجَلٍ هُم بَٰلِغُوهُ إِذَا هُمْ يَنكُثُونَۖ ١٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1091,9,'166',7,'Al-A‘rāf','الأعرَاف',10,12,135,'فَانتَقَمْنَا مِنْهُمْ فَأَغْرَقْنَٰهُمْ فِے اِ۬لْيَمِّ بِأَنَّهُمْ كَذَّبُواْ بِـَٔايَٰتِنَا وَكَانُواْ عَنْهَا غَٰفِلِينَۖ ١٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1092,9,'166',7,'Al-A‘rāf','الأعرَاف',12,14,136,'وَأَوْرَثْنَا اَ۬لْقَوْمَ اَ۬لذِينَ كَانُواْ يُسْتَضْعَفُونَ مَشَٰرِقَ اَ۬لْأَرْضِ وَمَغَٰرِبَهَا اَ۬لتِے بَٰرَكْنَا فِيهَاۖ وَتَمَّتْ كَلِمَةُ رَبِّكَ اَ۬لْحُسْنَىٰ عَلَىٰ بَنِے إِسْرَآءِيلَ ١٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1093,9,'166',7,'Al-A‘rāf','الأعرَاف',14,15,137,'بِمَا صَبَرُواْۖ وَدَمَّرْنَا مَا كَانَ يَصْنَعُ فِرْعَوْنُ وَقَوْمُهُۥ وَمَا كَانُواْ يَعْرِشُونَۖ ١٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1094,9,'167',7,'Al-A‘rāf','الأعرَاف',1,3,138,'وَجَٰوَزْنَا بِبَنِے إِسْرَآءِيلَ اَ۬لْبَحْرَ فَأَتَوْاْ عَلَىٰ قَوْمٖ يَعْكُفُونَ عَلَىٰ أَصْنَامٖ لَّهُمْۖ قَالُواْ يَٰمُوسَى اَ۪جْعَل لَّنَا إِلَٰهاٗ كَمَا لَهُمْ ءَالِهَةٞۖ قَالَ إِنَّكُمْ قَوْمٞ تَجْهَلُونَۖ ١٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1095,9,'167',7,'Al-A‘rāf','الأعرَاف',3,4,139,'إِنَّ هَٰؤُلَآءِ مُتَبَّرٞ مَّا هُمْ فِيهِۖ وَبَٰطِلٞ مَّا كَانُواْ يَعْمَلُونَۖ ١٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1096,9,'167',7,'Al-A‘rāf','الأعرَاف',4,5,140,'قَالَ أَغَيْرَ اَ۬للَّهِ أَبْغِيكُمْ إِلَٰهاٗ وَهْوَ فَضَّلَكُمْ عَلَى اَ۬لْعَٰلَمِينَۖ ١٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1097,9,'167',7,'Al-A‘rāf','الأعرَاف',5,8,141,'وَإِذْ أَنجَيْنَٰكُم مِّنْ ءَالِ فِرْعَوْنَ يَسُومُونَكُمْ سُوٓءَ اَ۬لْعَذَابِ يَقْتُلُونَ أَبْنَآءَكُمْ وَيَسْتَحْيُونَ نِسَآءَكُمْۖ وَفِے ذَٰلِكُم بَلَآءٞ مِّن رَّبِّكُمْ عَظِيمٞۖ ١٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1098,9,'167',7,'Al-A‘rāf','الأعرَاف',8,11,142,'۞ وَوَٰعَدْنَا مُوسَىٰ ثَلَٰثِينَ لَيْلَةٗ وَأَتْمَمْنَٰهَا بِعَشْرٖ فَتَمَّ مِيقَٰتُ رَبِّهِۦ أَرْبَعِينَ لَيْلَةٗۖ وَقَالَ مُوسَىٰ لِأَخِيهِ هَٰرُونَ اَ۟خْلُفْنِے فِے قَوْمِے وَأَصْلِحْ وَلَا تَتَّبِعْ سَبِيلَ اَ۬لْمُفْسِدِينَۖ ١٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1099,9,'167',7,'Al-A‘rāf','الأعرَاف',11,15,143,'وَلَمَّا جَآءَ مُوسَىٰ لِمِيقَٰتِنَا وَكَلَّمَهُۥ رَبُّهُۥ قَالَ رَبِّ أَرِنِے أَنظُرْ إِلَيْكَۖ قَالَ لَن تَرَيٰنِے وَلَٰكِنُ اُ۟نظُرْ إِلَى اَ۬لْجَبَلِ فَإِنِ اِ۪سْتَقَرَّ مَكَانَهُۥ فَسَوْفَ تَرَيٰنِےۖ فَلَمَّا تَجَلَّىٰ رَبُّهُۥ لِلْجَبَلِ جَعَلَهُۥ دَكّاٗ وَخَرَّ مُوسَىٰ صَعِقاٗۖ فَلَمَّا أَفَاقَ قَالَ سُبْحَٰنَكَ تُبْتُ إِلَيْكَ وَأَنَا أَوَّلُ اُ۬لْمُؤْمِنِينَۖ ١٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1100,9,'168',7,'Al-A‘rāf','الأعرَاف',1,2,144,'قَالَ يَٰمُوسَىٰ إِنِّے اِ۪صْطَفَيْتُكَ عَلَى اَ۬لنَّاسِ بِرِسَالَتِے وَبِكَلَٰمِے فَخُذْ مَا ءَاتَيْتُكَ وَكُن مِّنَ اَ۬لشَّٰكِرِينَۖ ١٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1101,9,'168',7,'Al-A‘rāf','الأعرَاف',2,5,145,'وَكَتَبْنَا لَهُۥ فِے اِ۬لْأَلْوَاحِ مِن كُلِّ شَےْءٖ مَّوْعِظَةٗ وَتَفْصِيلاٗ لِّكُلِّ شَےْءٖۖ فَخُذْهَا بِقُوَّةٖ وَأْمُرْ قَوْمَكَ يَأْخُذُواْ بِأَحْسَنِهَاۖ سَأُوْرِيكُمْ دَارَ اَ۬لْفَٰسِقِينَۖ ١٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1102,9,'168',7,'Al-A‘rāf','الأعرَاف',5,9,146,'سَأَصْرِفُ عَنْ ءَايَٰتِيَ اَ۬لذِينَ يَتَكَبَّرُونَ فِے اِ۬لْأَرْضِ بِغَيْرِ اِ۬لْحَقِّ وَإِنْ يَّرَوْاْ كُلَّ ءَايَةٖ لَّا يُؤْمِنُواْ بِهَا وَإِنْ يَّرَوْاْ سَبِيلَ اَ۬لرُّشْدِ لَا يَتَّخِذُوهُ سَبِيلاٗ وَإِنْ يَّرَوْاْ سَبِيلَ اَ۬لْغَيِّ يَتَّخِذُوهُ سَبِيلاٗۖ ذَٰلِكَ بِأَنَّهُمْ كَذَّبُواْ بِـَٔايَٰتِنَا وَكَانُواْ عَنْهَا غَٰفِلِينَۖ ١٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1103,9,'168',7,'Al-A‘rāf','الأعرَاف',9,11,147,'وَالذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَلِقَآءِ اِ۬لْأٓخِرَةِ حَبِطَتْ أَعْمَٰلُهُمْۖ هَلْ يُجْزَوْنَ إِلَّا مَا كَانُواْ يَعْمَلُونَۖ ١٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1104,9,'168',7,'Al-A‘rāf','الأعرَاف',11,13,148,'وَاتَّخَذَ قَوْمُ مُوسَىٰ مِنۢ بَعْدِهِۦ مِنْ حُلِيِّهِمْ عِجْلاٗ جَسَداٗ لَّهُۥ خُوَارٌۖ أَلَمْ يَرَوْاْ أَنَّهُۥ لَا يُكَلِّمُهُمْ وَلَا يَهْدِيهِمْ سَبِيلاًۖ اِ۪تَّخَذُوهُ وَكَانُواْ ظَٰلِمِينَۖ ١٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1105,9,'168',7,'Al-A‘rāf','الأعرَاف',14,15,149,'۞ وَلَمَّا سُقِطَ فِے أَيْدِيهِمْ وَرَأَوْاْ أَنَّهُمْ قَدْ ضَلُّواْ قَالُواْ لَئِن لَّمْ يَرْحَمْنَا رَبُّنَا وَيَغْفِرْ لَنَا لَنَكُونَنَّ مِنَ اَ۬لْخَٰسِرِينَۖ ١٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1106,9,'169',7,'Al-A‘rāf','الأعرَاف',1,5,150,'وَلَمَّا رَجَعَ مُوسَىٰ إِلَىٰ قَوْمِهِۦ غَضْبَٰنَ أَسِفاٗ قَالَ بِئْسَمَا خَلَفْتُمُونِے مِنۢ بَعْدِيَۖ أَعَجِلْتُمْ أَمْرَ رَبِّكُمْۖ وَأَلْقَى اَ۬لْأَلْوَاحَ وَأَخَذَ بِرَأْسِ أَخِيهِ يَجُرُّهُۥ إِلَيْهِۖ قَالَ اَ۪بْنَ أُمَّ إِنَّ اَ۬لْقَوْمَ اَ۪سْتَضْعَفُونِے وَكَادُواْ يَقْتُلُونَنِے فَلَا تُشْمِتْ بِيَ اَ۬لْأَعْدَآءَ وَلَا تَجْعَلْنِے مَعَ اَ۬لْقَوْمِ اِ۬لظَّٰلِمِينَۖ ١٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1107,9,'169',7,'Al-A‘rāf','الأعرَاف',5,6,151,'قَالَ رَبِّ اِ۪غْفِرْ لِے وَلِأَخِے وَأَدْخِلْنَا فِے رَحْمَتِكَۖ وَأَنتَ أَرْحَمُ اُ۬لرَّٰحِمِينَۖ ١٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1108,9,'169',7,'Al-A‘rāf','الأعرَاف',6,8,152,'إِنَّ اَ۬لذِينَ اَ۪تَّخَذُواْ اُ۬لْعِجْلَ سَيَنَالُهُمْ غَضَبٞ مِّن رَّبِّهِمْ وَذِلَّةٞ فِے اِ۬لْحَيَوٰةِ اِ۬لدُّنْيَاۖ وَكَذَٰلِكَ نَجْزِے اِ۬لْمُفْتَرِينَۖ ١٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1109,9,'169',7,'Al-A‘rāf','الأعرَاف',8,9,153,'وَالذِينَ عَمِلُواْ اُ۬لسَّيِّـَٔاتِ ثُمَّ تَابُواْ مِنۢ بَعْدِهَا وَءَامَنُواْ إِنَّ رَبَّكَ مِنۢ بَعْدِهَا لَغَفُورٞ رَّحِيمٞۖ ١٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1110,9,'169',7,'Al-A‘rāf','الأعرَاف',10,11,154,'وَلَمَّا سَكَتَ عَن مُّوسَى اَ۬لْغَضَبُ أَخَذَ اَ۬لْأَلْوَاحَ وَفِے نُسْخَتِهَا هُدىٗ وَرَحْمَةٞ لِّلذِينَ هُمْ لِرَبِّهِمْ يَرْهَبُونَۖ ١٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1111,9,'169',7,'Al-A‘rāf','الأعرَاف',11,15,155,'وَاخْتَارَ مُوسَىٰ قَوْمَهُۥ سَبْعِينَ رَجُلاٗ لِّمِيقَٰتِنَاۖ فَلَمَّا أَخَذَتْهُمُ اُ۬لرَّجْفَةُ قَالَ رَبِّ لَوْ شِئْتَ أَهْلَكْتَهُم مِّن قَبْلُ وَإِيَّٰيَۖ أَتُهْلِكُنَا بِمَا فَعَلَ اَ۬لسُّفَهَآءُ مِنَّاۖ إِنْ هِيَ إِلَّا فِتْنَتُكَ تُضِلُّ بِهَا مَن تَشَآءُ وَتَهْدِے مَن تَشَآءُۖ اَ۬نتَ وَلِيُّنَا فَاغْفِرْ لَنَا وَارْحَمْنَاۖ وَأَنتَ خَيْرُ اُ۬لْغَٰفِرِينَۖ ١٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1112,9,'170',7,'Al-A‘rāf','الأعرَاف',1,4,156,'۞ وَاكْتُبْ لَنَا فِے هَٰذِهِ اِ۬لدُّنْيَا حَسَنَةٗ وَفِے اِ۬لْأٓخِرَةِۖ إِنَّا هُدْنَا إِلَيْكَۖ قَالَ عَذَابِيَ أُصِيبُ بِهِۦ مَنْ أَشَآءُۖ وَرَحْمَتِے وَسِعَتْ كُلَّ شَےْءٖۖ فَسَأَكْتُبُهَا لِلذِينَ يَتَّقُونَ وَيُؤْتُونَ اَ۬لزَّكَوٰةَ وَالذِينَ هُم بِـَٔايَٰتِنَا يُؤْمِنُونَ ١٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1113,9,'170',7,'Al-A‘rāf','الأعرَاف',4,10,157,'اَ۬لذِينَ يَتَّبِعُونَ اَ۬لرَّسُولَ اَ۬لنَّبِےٓءَ اَ۬لْأُمِّيَّ اَ۬لذِے يَجِدُونَهُۥ مَكْتُوباً عِندَهُمْ فِے اِ۬لتَّوْرَيٰةِ وَالْإِنجِيلِ يَأْمُرُهُم بِالْمَعْرُوفِ وَيَنْهَيٰهُمْ عَنِ اِ۬لْمُنكَرِ وَيُحِلُّ لَهُمُ اُ۬لطَّيِّبَٰتِ وَيُحَرِّمُ عَلَيْهِمُ اُ۬لْخَبَٰٓئِثَ وَيَضَعُ عَنْهُمْ إِصْرَهُمْ وَالْأَغْلَٰلَ اَ۬لتِے كَانَتْ عَلَيْهِمْۖ فَالذِينَ ءَامَنُواْ بِهِۦ وَعَزَّرُوهُ وَنَصَرُوهُ وَاتَّبَعُواْ اُ۬لنُّورَ اَ۬لذِے أُنزِلَ مَعَهُۥ أُوْلَٰٓئِكَ هُمُ اُ۬لْمُفْلِحُونَۖ ١٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1114,9,'170',7,'Al-A‘rāf','الأعرَاف',11,14,158,'قُلْ يَٰأَيُّهَا اَ۬لنَّاسُ إِنِّے رَسُولُ اُ۬للَّهِ إِلَيْكُمْ جَمِيعاً اِ۬لذِے لَهُۥ مُلْكُ اُ۬لسَّمَٰوَٰتِ وَالْأَرْضِۖ لَا إِلَٰهَ إِلَّا هُوَۖ يُحْيِۦ وَيُمِيتُۖ فَـَٔامِنُواْ بِاللَّهِ وَرَسُولِهِ اِ۬لنَّبِےٓءِ اِ۬لْأُمِّيِّ اِ۬لذِے يُؤْمِنُ بِاللَّهِ وَكَلِمَٰتِهِۦۖ وَاتَّبِعُوهُ لَعَلَّكُمْ تَهْتَدُونَۖ ١٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1115,9,'170',7,'Al-A‘rāf','الأعرَاف',14,15,159,'وَمِن قَوْمِ مُوسَىٰ أُمَّةٞ يَهْدُونَ بِالْحَقِّ وَبِهِۦ يَعْدِلُونَۖ ١٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1116,9,'171',7,'Al-A‘rāf','الأعرَاف',1,6,160,'وَقَطَّعْنَٰهُمُ اُ۪ثْنَتَےْ عَشْرَةَ أَسْبَاطاً أُمَماٗۖ وَأَوْحَيْنَا إِلَىٰ مُوسَىٰ إِذِ اِ۪سْتَسْقَيٰهُ قَوْمُهُۥ أَنِ اِ۪ضْرِب بِّعَصَاكَ اَ۬لْحَجَرَۖ فَانۢبَجَسَتْ مِنْهُ اُ۪ثْنَتَا عَشْرَةَ عَيْناٗۖ قَدْ عَلِمَ كُلُّ أُنَاسٖ مَّشْرَبَهُمْۖ وَظَلَّلْنَا عَلَيْهِمُ اُ۬لْغَمَٰمَ وَأَنزَلْنَا عَلَيْهِمُ اُ۬لْمَنَّ وَالسَّلْوَىٰۖ كُلُواْ مِن طَيِّبَٰتِ مَا رَزَقْنَٰكُمْۖ وَمَا ظَلَمُونَاۖ وَلَٰكِن كَانُواْ أَنفُسَهُمْ يَظْلِمُونَۖ ١٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1117,9,'171',7,'Al-A‘rāf','الأعرَاف',7,9,161,'وَإِذْ قِيلَ لَهُمُ اُ۟سْكُنُواْ هَٰذِهِ اِ۬لْقَرْيَةَ وَكُلُواْ مِنْهَا حَيْثُ شِئْتُمْ وَقُولُواْ حِطَّةٞ وَادْخُلُواْ اُ۬لْبَابَ سُجَّداٗ تُغْفَرْ لَكُمْ خَطِيٓـَٰٔتُكُمْۖ سَنَزِيدُ اُ۬لْمُحْسِنِينَۖ ١٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1118,9,'171',7,'Al-A‘rāf','الأعرَاف',10,12,162,'فَبَدَّلَ اَ۬لذِينَ ظَلَمُواْ مِنْهُمْ قَوْلاً غَيْرَ اَ۬لذِے قِيلَ لَهُمْ فَأَرْسَلْنَا عَلَيْهِمْ رِجْزاٗ مِّنَ اَ۬لسَّمَآءِ بِمَا كَانُواْ يَظْلِمُونَۖ ١٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1119,9,'171',7,'Al-A‘rāf','الأعرَاف',12,15,163,'۞ وَسْـَٔلْهُمْ عَنِ اِ۬لْقَرْيَةِ اِ۬لتِے كَانَتْ حَاضِرَةَ اَ۬لْبَحْرِ إِذْ يَعْدُونَ فِے اِ۬لسَّبْتِ إِذْ تَأْتِيهِمْ حِيتَانُهُمْ يَوْمَ سَبْتِهِمْ شُرَّعاٗ وَيَوْمَ لَا يَسْبِتُونَ لَا تَأْتِيهِمْۖ كَذَٰلِكَ نَبْلُوهُم بِمَا كَانُواْ يَفْسُقُونَۖ ١٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1120,9,'172',7,'Al-A‘rāf','الأعرَاف',1,2,164,'وَإِذْ قَالَتْ أُمَّةٞ مِّنْهُمْ لِمَ تَعِظُونَ قَوْماً اِ۬للَّهُ مُهْلِكُهُمْ أَوْ مُعَذِّبُهُمْ عَذَاباٗ شَدِيداٗۖ قَالُواْ مَعْذِرَةٌ إِلَىٰ رَبِّكُمْ وَلَعَلَّهُمْ يَتَّقُونَۖ ١٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1121,9,'172',7,'Al-A‘rāf','الأعرَاف',3,4,165,'فَلَمَّا نَسُواْ مَا ذُكِّرُواْ بِهِۦ أَنجَيْنَا اَ۬لذِينَ يَنْهَوْنَ عَنِ اِ۬لسُّوٓءِ وَأَخَذْنَا اَ۬لذِينَ ظَلَمُواْ بِعَذَابِۢ بِيسِۢ بِمَا كَانُواْ يَفْسُقُونَۖ ١٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1122,9,'172',7,'Al-A‘rāf','الأعرَاف',5,5,166,'فَلَمَّا عَتَوْاْ عَن مَّا نُهُواْ عَنْهُ قُلْنَا لَهُمْ كُونُواْ قِرَدَةً خَٰسِـِٕينَۖ ١٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1123,9,'172',7,'Al-A‘rāf','الأعرَاف',6,7,167,'وَإِذْ تَأَذَّنَ رَبُّكَ لَيَبْعَثَنَّ عَلَيْهِمْ إِلَىٰ يَوْمِ اِ۬لْقِيَٰمَةِ مَنْ يَّسُومُهُمْ سُوٓءَ اَ۬لْعَذَابِۖ إِنَّ رَبَّكَ لَسَرِيعُ اُ۬لْعِقَابِۖ وَإِنَّهُۥ لَغَفُورٞ رَّحِيمٞۖ ١٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1124,9,'172',7,'Al-A‘rāf','الأعرَاف',8,10,168,'وَقَطَّعْنَٰهُمْ فِے اِ۬لْأَرْضِ أُمَماٗۖ مِّنْهُمُ اُ۬لصَّٰلِحُونَ وَمِنْهُمْ دُونَ ذَٰلِكَۖ وَبَلَوْنَٰهُم بِالْحَسَنَٰتِ وَالسَّيِّـَٔاتِ لَعَلَّهُمْ يَرْجِعُونَۖ ١٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1125,9,'172',7,'Al-A‘rāf','الأعرَاف',10,14,169,'فَخَلَفَ مِنۢ بَعْدِهِمْ خَلْفٞ وَرِثُواْ اُ۬لْكِتَٰبَ يَأْخُذُونَ عَرَضَ هَٰذَا اَ۬لْأَدْنَىٰ وَيَقُولُونَ سَيُغْفَرُ لَنَا وَإِنْ يَّأْتِهِمْ عَرَضٞ مِّثْلُهُۥ يَأْخُذُوهُۖ أَلَمْ يُؤْخَذْ عَلَيْهِم مِّيثَٰقُ اُ۬لْكِتَٰبِ أَن لَّا يَقُولُواْ عَلَى اَ۬للَّهِ إِلَّا اَ۬لْحَقَّۖ وَدَرَسُواْ مَا فِيهِۖ وَالدَّارُ اُ۬لْأٓخِرَةُ خَيْرٞ لِّلذِينَ يَتَّقُونَۖ أَفَلَا تَعْقِلُونَۖ ١٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1126,9,'172',7,'Al-A‘rāf','الأعرَاف',14,15,170,'وَالذِينَ يُمَسِّكُونَ بِالْكِتَٰبِ وَأَقَامُواْ اُ۬لصَّلَوٰةَ إِنَّا لَا نُضِيعُ أَجْرَ اَ۬لْمُصْلِحِينَۖ ١٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1127,9,'173',7,'Al-A‘rāf','الأعرَاف',1,2,171,'۞ وَإِذْ نَتَقْنَا اَ۬لْجَبَلَ فَوْقَهُمْ كَأَنَّهُۥ ظُلَّةٞ وَظَنُّواْ أَنَّهُۥ وَاقِعُۢ بِهِمْ خُذُواْ مَا ءَاتَيْنَٰكُم بِقُوَّةٖ وَاذْكُرُواْ مَا فِيهِ لَعَلَّكُمْ تَتَّقُونَۖ ١٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1128,9,'173',7,'Al-A‘rāf','الأعرَاف',3,5,172,'وَإِذْ أَخَذَ رَبُّكَ مِنۢ بَنِے ءَادَمَ مِن ظُهُورِهِمْ ذُرِّيَّٰتِهِمْ وَأَشْهَدَهُمْ عَلَىٰ أَنفُسِهِمْ أَلَسْتُ بِرَبِّكُمْ قَالُواْ بَلَىٰۖ شَهِدْنَا أَن تَقُولُواْ يَوْمَ اَ۬لْقِيَٰمَةِ إِنَّا كُنَّا عَنْ هَٰذَا غَٰفِلِينَ ١٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1129,9,'173',7,'Al-A‘rāf','الأعرَاف',5,7,173,'أَوْ تَقُولُواْ إِنَّمَا أَشْرَكَ ءَابَآؤُنَا مِن قَبْلُ وَكُنَّا ذُرِّيَّةٗ مِّنۢ بَعْدِهِمْۖ أَفَتُهْلِكُنَا بِمَا فَعَلَ اَ۬لْمُبْطِلُونَۖ ١٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1130,9,'173',7,'Al-A‘rāf','الأعرَاف',7,8,174,'وَكَذَٰلِكَ نُفَصِّلُ اُ۬لْأٓيَٰتِ وَلَعَلَّهُمْ يَرْجِعُونَۖ ١٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1131,9,'173',7,'Al-A‘rāf','الأعرَاف',8,9,175,'وَاتْلُ عَلَيْهِمْ نَبَأَ اَ۬لذِے ءَاتَيْنَٰهُ ءَايَٰتِنَا فَانسَلَخَ مِنْهَا فَأَتْبَعَهُ اُ۬لشَّيْطَٰنُ فَكَانَ مِنَ اَ۬لْغَاوِينَۖ ١٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1132,9,'173',7,'Al-A‘rāf','الأعرَاف',9,13,176,'وَلَوْ شِئْنَا لَرَفَعْنَٰهُ بِهَا وَلَٰكِنَّهُۥ أَخْلَدَ إِلَى اَ۬لْأَرْضِ وَاتَّبَعَ هَوَيٰهُۖ فَمَثَلُهُۥ كَمَثَلِ اِ۬لْكَلْبِ إِن تَحْمِلْ عَلَيْهِ يَلْهَثْ أَوْ تَتْرُكْهُ يَلْهَثۖ ذَّٰلِكَ مَثَلُ اُ۬لْقَوْمِ اِ۬لذِينَ كَذَّبُواْ بِـَٔايَٰتِنَاۖ فَاقْصُصِ اِ۬لْقَصَصَ لَعَلَّهُمْ يَتَفَكَّرُونَۖ ١٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1133,9,'173',7,'Al-A‘rāf','الأعرَاف',13,14,177,'سَآءَ مَثَلاً اِ۬لْقَوْمُ اُ۬لذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا وَأَنفُسَهُمْ كَانُواْ يَظْلِمُونَۖ ١٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1134,9,'173',7,'Al-A‘rāf','الأعرَاف',14,15,178,'مَنْ يَّهْدِ اِ۬للَّهُ فَهْوَ اَ۬لْمُهْتَدِےۖ وَمَنْ يُّضْلِلْ فَأُوْلَٰٓئِكَ هُمُ اُ۬لْخَٰسِرُونَۖ ١٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1135,9,'174',7,'Al-A‘rāf','الأعرَاف',1,3,179,'۞ وَلَقَدْ ذَرَأْنَا لِجَهَنَّمَ كَثِيراٗ مِّنَ اَ۬لْجِنِّ وَالْإِنسِۖ لَهُمْ قُلُوبٞ لَّا يَفْقَهُونَ بِهَاۖ وَلَهُمْ أَعْيُنٞ لَّا يُبْصِرُونَ بِهَاۖ وَلَهُمْ ءَاذَانٞ لَّا يَسْمَعُونَ بِهَاۖ أُوْلَٰٓئِكَ كَالْأَنْعَٰمِ بَلْ هُمْ أَضَلُّۖ أُوْلَٰٓئِكَ هُمُ اُ۬لْغَٰفِلُونَۖ ١٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1136,9,'174',7,'Al-A‘rāf','الأعرَاف',4,5,180,'وَلِلهِ اِ۬لْأَسْمَآءُ اُ۬لْحُسْنَىٰ فَادْعُوهُ بِهَاۖ وَذَرُواْ اُ۬لذِينَ يُلْحِدُونَ فِے أَسْمَٰٓئِهِۦۖ سَيُجْزَوْنَ مَا كَانُواْ يَعْمَلُونَۖ ١٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1137,9,'174',7,'Al-A‘rāf','الأعرَاف',5,6,181,'وَمِمَّنْ خَلَقْنَا أُمَّةٞ يَهْدُونَ بِالْحَقِّ وَبِهِۦ يَعْدِلُونَۖ ١٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1138,9,'174',7,'Al-A‘rāf','الأعرَاف',6,7,182,'وَالذِينَ كَذَّبُواْ بِـَٔايَٰتِنَا سَنَسْتَدْرِجُهُم مِّنْ حَيْثُ لَا يَعْلَمُونَ ١٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1139,9,'174',7,'Al-A‘rāf','الأعرَاف',7,8,183,'وَأُمْلِے لَهُمْۖ إِنَّ كَيْدِے مَتِينٌۖ ١٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1140,9,'174',7,'Al-A‘rāf','الأعرَاف',8,9,184,'أَوَلَمْ يَتَفَكَّرُواْۖ مَا بِصَٰحِبِهِم مِّن جِنَّةٍۖ إِنْ هُوَ إِلَّا نَذِيرٞ مُّبِينٌۖ ١٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1141,9,'174',7,'Al-A‘rāf','الأعرَاف',9,11,185,'أَوَلَمْ يَنظُرُواْ فِے مَلَكُوتِ اِ۬لسَّمَٰوَٰتِ وَالْأَرْضِ وَمَا خَلَقَ اَ۬للَّهُ مِن شَےْءٖ وَأَنْ عَسَىٰ أَنْ يَّكُونَ قَدِ اِ۪قْتَرَبَ أَجَلُهُمْۖ فَبِأَيِّ حَدِيثِۢ بَعْدَهُۥ يُؤْمِنُونَۖ ١٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1142,9,'174',7,'Al-A‘rāf','الأعرَاف',11,12,186,'مَنْ يُّضْلِلِ اِ۬للَّهُ فَلَا هَادِيَ لَهُۥۖ وَنَذَرُهُمْ فِے طُغْيَٰنِهِمْ يَعْمَهُونَۖ ١٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1143,9,'174',7,'Al-A‘rāf','الأعرَاف',12,15,187,'يَسْـَٔلُونَكَ عَنِ اِ۬لسَّاعَةِ أَيَّانَ مُرْسَيٰهَاۖ قُلْ إِنَّمَا عِلْمُهَا عِندَ رَبِّےۖ لَا يُجَلِّيهَا لِوَقْتِهَا إِلَّا هُوَۖ ثَقُلَتْ فِے اِ۬لسَّمَٰوَٰتِ وَالْأَرْضِۖ لَا تَأْتِيكُمْ إِلَّا بَغْتَةٗۖ يَسْـَٔلُونَكَ كَأَنَّكَ حَفِيٌّ عَنْهَاۖ قُلْ إِنَّمَا عِلْمُهَا عِندَ اَ۬للَّهِۖ وَلَٰكِنَّ أَكْثَرَ اَ۬لنَّاسِ لَا يَعْلَمُونَۖ ١٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1144,9,'175',7,'Al-A‘rāf','الأعرَاف',1,3,188,'۞ قُل لَّا أَمْلِكُ لِنَفْسِے نَفْعاٗ وَلَا ضَرّاً إِلَّا مَا شَآءَ اَ۬للَّهُۖ وَلَوْ كُنتُ أَعْلَمُ اُ۬لْغَيْبَ لَاسْتَكْثَرْتُ مِنَ اَ۬لْخَيْرِۖ وَمَا مَسَّنِيَ اَ۬لسُّوٓءُۖ اِ۪نْ أَنَا إِلَّا نَذِيرٞۖ وَبَشِيرٞ لِّقَوْمٖ يُؤْمِنُونَۖ ١٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1145,9,'175',7,'Al-A‘rāf','الأعرَاف',3,6,189,'هُوَ اَ۬لذِے خَلَقَكُم مِّن نَّفْسٖ وَٰحِدَةٖ وَجَعَلَ مِنْهَا زَوْجَهَا لِيَسْكُنَ إِلَيْهَاۖ فَلَمَّا تَغَشَّيٰهَا حَمَلَتْ حَمْلاً خَفِيفاٗ فَمَرَّتْ بِهِۦۖ فَلَمَّا أَثْقَلَت دَّعَوَا اَ۬للَّهَ رَبَّهُمَا لَئِنْ ءَاتَيْتَنَا صَٰلِحاٗ لَّنَكُونَنَّ مِنَ اَ۬لشَّٰكِرِينَۖ ١٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1146,9,'175',7,'Al-A‘rāf','الأعرَاف',7,8,190,'فَلَمَّا ءَاتَيٰهُمَا صَٰلِحاٗ جَعَلَا لَهُۥ شِرْكاٗ فِيمَا ءَاتَيٰهُمَاۖ فَتَعَٰلَى اَ۬للَّهُ عَمَّا يُشْرِكُونَۖ ١٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1147,9,'175',7,'Al-A‘rāf','الأعرَاف',8,8,191,'أَيُشْرِكُونَ مَا لَا يَخْلُقُ شَئْاٗ وَهُمْ يُخْلَقُونَۖ ١٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1148,9,'175',7,'Al-A‘rāf','الأعرَاف',9,9,192,'وَلَا يَسْتَطِيعُونَ لَهُمْ نَصْراٗ وَلَا أَنفُسَهُمْ يَنصُرُونَۖ ١٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1149,9,'175',7,'Al-A‘rāf','الأعرَاف',9,11,193,'وَإِن تَدْعُوهُمْ إِلَى اَ۬لْهُدَىٰ لَا يَتْبَعُوكُمْۖ سَوَآءٌ عَلَيْكُمْ أَدَعَوْتُمُوهُمْ أَمْ أَنتُمْ صَٰمِتُونَۖ ١٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1150,9,'175',7,'Al-A‘rāf','الأعرَاف',11,13,194,'إِنَّ اَ۬لذِينَ تَدْعُونَ مِن دُونِ اِ۬للَّهِ عِبَادٌ أَمْثَالُكُمْۖ فَادْعُوهُمْ فَلْيَسْتَجِيبُواْ لَكُمْ إِن كُنتُمْ صَٰدِقِينَۖ ١٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1151,9,'175',7,'Al-A‘rāf','الأعرَاف',13,15,195,'أَلَهُمْ أَرْجُلٞ يَمْشُونَ بِهَاۖ أَمْ لَهُمْ أَيْدٖ يَبْطِشُونَ بِهَاۖ أَمْ لَهُمْ أَعْيُنٞ يُبْصِرُونَ بِهَاۖ أَمْ لَهُمْ ءَاذَانٞ يَسْمَعُونَ بِهَاۖ قُلُ اُ۟دْعُواْ شُرَكَآءَكُمْ ثُمَّ كِيدُونِ فَلَا تُنظِرُونِۖ ١٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1152,9,'176',7,'Al-A‘rāf','الأعرَاف',1,1,196,'إِنَّ وَلِـِّۧيَ اَ۬للَّهُ اُ۬لذِے نَزَّلَ اَ۬لْكِتَٰبَۖ وَهْوَ يَتَوَلَّى اَ۬لصَّٰلِحِينَۖ ١٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1153,9,'176',7,'Al-A‘rāf','الأعرَاف',2,3,197,'وَالذِينَ تَدْعُونَ مِن دُونِهِۦ لَا يَسْتَطِيعُونَ نَصْرَكُمْ وَلَا أَنفُسَهُمْ يَنصُرُونَۖ ١٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1154,9,'176',7,'Al-A‘rāf','الأعرَاف',3,4,198,'وَإِن تَدْعُوهُمْ إِلَى اَ۬لْهُدَىٰ لَا يَسْمَعُواْۖ وَتَرَيٰهُمْ يَنظُرُونَ إِلَيْكَ وَهُمْ لَا يُبْصِرُونَۖ ١٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1155,9,'176',7,'Al-A‘rāf','الأعرَاف',4,5,199,'خُذِ اِ۬لْعَفْوَۖ وَأْمُرْ بِالْعُرْفِۖ وَأَعْرِضْ عَنِ اِ۬لْجَٰهِلِينَۖ ١٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1156,9,'176',7,'Al-A‘rāf','الأعرَاف',5,6,200,'۞ وَإِمَّا يَنزَغَنَّكَ مِنَ اَ۬لشَّيْطَٰنِ نَزْغٞ فَاسْتَعِذْ بِاللَّهِۖ إِنَّهُۥ سَمِيعٌ عَلِيمٌۖ ٢٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1157,9,'176',7,'Al-A‘rāf','الأعرَاف',6,8,201,'إِنَّ اَ۬لذِينَ اَ۪تَّقَوْاْ إِذَا مَسَّهُمْ طَٰٓئِفٞ مِّنَ اَ۬لشَّيْطَٰنِ تَذَكَّرُواْ فَإِذَا هُم مُّبْصِرُونَۖ ٢٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1158,9,'176',7,'Al-A‘rāf','الأعرَاف',8,9,202,'وَإِخْوَٰنُهُمْ يُمِدُّونَهُمْ فِے اِ۬لْغَيِّۖ ثُمَّ لَا يُقْصِرُونَۖ ٢٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1159,9,'176',7,'Al-A‘rāf','الأعرَاف',9,11,203,'وَإِذَا لَمْ تَأْتِهِم بِـَٔايَةٖ قَالُواْ لَوْلَا اَ۪جْتَبَيْتَهَاۖ قُلْ إِنَّمَا أَتَّبِعُ مَا يُوحَىٰ إِلَيَّ مِن رَّبِّےۖ هَٰذَا بَصَآئِرُ مِن رَّبِّكُمْ وَهُدىٗ وَرَحْمَةٞ لِّقَوْمٖ يُؤْمِنُونَۖ ٢٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1160,9,'176',7,'Al-A‘rāf','الأعرَاف',11,12,204,'وَإِذَا قُرِۓَ اَ۬لْقُرْءَانُ فَاسْتَمِعُواْ لَهُۥ وَأَنصِتُواْ لَعَلَّكُمْ تُرْحَمُونَۖ ٢٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1161,9,'176',7,'Al-A‘rāf','الأعرَاف',12,14,205,'وَاذْكُر رَّبَّكَ فِے نَفْسِكَ تَضَرُّعاٗ وَخِيفَةٗ وَدُونَ اَ۬لْجَهْرِ مِنَ اَ۬لْقَوْلِ بِالْغُدُوِّ وَالْأٓصَالِ وَلَا تَكُن مِّنَ اَ۬لْغَٰفِلِينَۖ ٢٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1162,9,'176',7,'Al-A‘rāf','الأعرَاف',14,15,206,'إِنَّ اَ۬لذِينَ عِندَ رَبِّكَ لَا يَسْتَكْبِرُونَ عَنْ عِبَادَتِهِۦ وَيُسَبِّحُونَهُۥ وَلَهُۥ يَسْجُدُونَۖ۩ ٢٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1163,9,'177',8,'Al-Anfāl','الأنفَال',3,5,1,'يَسْـَٔلُونَكَ عَنِ اِ۬لْأَنفَالِۖ قُلِ اِ۬لْأَنفَالُ لِلهِ وَالرَّسُولِۖ فَاتَّقُواْ اُ۬للَّهَ وَأَصْلِحُواْ ذَاتَ بَيْنِكُمْۖ وَأَطِيعُواْ اُ۬للَّهَ وَرَسُولَهُۥ إِن كُنتُم مُّؤْمِنِينَۖ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1164,9,'177',8,'Al-Anfāl','الأنفَال',5,7,2,'إِنَّمَا اَ۬لْمُؤْمِنُونَ اَ۬لذِينَ إِذَا ذُكِرَ اَ۬للَّهُ وَجِلَتْ قُلُوبُهُمْ وَإِذَا تُلِيَتْ عَلَيْهِمْ ءَايَٰتُهُۥ زَادَتْهُمْ إِيمَٰناٗ وَعَلَىٰ رَبِّهِمْ يَتَوَكَّلُونَۖ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1165,9,'177',8,'Al-Anfāl','الأنفَال',7,8,3,'اَ۬لذِينَ يُقِيمُونَ اَ۬لصَّلَوٰةَ وَمِمَّا رَزَقْنَٰهُمْ يُنفِقُونَ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1166,9,'177',8,'Al-Anfāl','الأنفَال',8,9,4,'أُوْلَٰٓئِكَ هُمُ اُ۬لْمُؤْمِنُونَ حَقّاٗۖ لَّهُمْ دَرَجَٰتٌ عِندَ رَبِّهِمْ وَمَغْفِرَةٞ وَرِزْقٞ كَرِيمٞۖ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1167,9,'177',8,'Al-Anfāl','الأنفَال',9,10,5,'۞ كَمَا أَخْرَجَكَ رَبُّكَ مِنۢ بَيْتِكَ بِالْحَقِّ وَإِنَّ فَرِيقاٗ مِّنَ اَ۬لْمُؤْمِنِينَ لَكَٰرِهُونَ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1168,9,'177',8,'Al-Anfāl','الأنفَال',11,12,6,'يُجَٰدِلُونَكَ فِے اِ۬لْحَقِّ بَعْدَ مَا تَبَيَّنَ كَأَنَّمَا يُسَاقُونَ إِلَى اَ۬لْمَوْتِ وَهُمْ يَنظُرُونَۖ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1169,9,'177',8,'Al-Anfāl','الأنفَال',12,14,7,'وَإِذْ يَعِدُكُمُ اُ۬للَّهُ إِحْدَى اَ۬لطَّآئِفَتَيْنِ أَنَّهَا لَكُمْ وَتَوَدُّونَ أَنَّ غَيْرَ ذَاتِ اِ۬لشَّوْكَةِ تَكُونُ لَكُمْۖ وَيُرِيدُ اُ۬للَّهُ أَنْ يُّحِقَّ اَ۬لْحَقَّ بِكَلِمَٰتِهِۦ وَيَقْطَعَ دَابِرَ اَ۬لْكَٰفِرِينَ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1170,9,'177',8,'Al-Anfāl','الأنفَال',15,15,8,'لِيُحِقَّ اَ۬لْحَقَّ وَيُبْطِلَ اَ۬لْبَٰطِلَ وَلَوْ كَرِهَ اَ۬لْمُجْرِمُونَۖ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1171,9,'178',8,'Al-Anfāl','الأنفَال',1,2,9,'إِذْ تَسْتَغِيثُونَ رَبَّكُمْ فَاسْتَجَابَ لَكُمْ أَنِّے مُمِدُّكُم بِأَلْفٖ مِّنَ اَ۬لْمَلَٰٓئِكَةِ مُرْدَفِينَۖ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1172,9,'178',8,'Al-Anfāl','الأنفَال',2,4,10,'وَمَا جَعَلَهُ اُ۬للَّهُ إِلَّا بُشْرَىٰ وَلِتَطْمَئِنَّ بِهِۦ قُلُوبُكُمْۖ وَمَا اَ۬لنَّصْرُ إِلَّا مِنْ عِندِ اِ۬للَّهِۖ إِنَّ اَ۬للَّهَ عَزِيزٌ حَكِيمٌۖ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1173,9,'178',8,'Al-Anfāl','الأنفَال',4,6,11,'إِذْ يُغْشِيكُمُ اُ۬لنُّعَاسَ أَمَنَةٗ مِّنْهُ وَيُنَزِّلُ عَلَيْكُم مِّنَ اَ۬لسَّمَآءِ مَآءٗ لِّيُطَهِّرَكُم بِهِۦ وَيُذْهِبَ عَنكُمْ رِجْزَ اَ۬لشَّيْطَٰنِ وَلِيَرْبِطَ عَلَىٰ قُلُوبِكُمْ وَيُثَبِّتَ بِهِ اِ۬لْأَقْدَامَۖ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1174,9,'178',8,'Al-Anfāl','الأنفَال',7,9,12,'إِذْ يُوحِے رَبُّكَ إِلَى اَ۬لْمَلَٰٓئِكَةِ أَنِّے مَعَكُمْ فَثَبِّتُواْ اُ۬لذِينَ ءَامَنُواْۖ سَأُلْقِے فِے قُلُوبِ اِ۬لذِينَ كَفَرُواْ اُ۬لرُّعْبَۖ فَاضْرِبُواْ فَوْقَ اَ۬لْأَعْنَاقِ وَاضْرِبُواْ مِنْهُمْ كُلَّ بَنَانٖۖ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1175,9,'178',8,'Al-Anfāl','الأنفَال',9,11,13,'ذَٰلِكَ بِأَنَّهُمْ شَآقُّواْ اُ۬للَّهَ وَرَسُولَهُۥۖ وَمَنْ يُّشَاقِقِ اِ۬للَّهَ وَرَسُولَهُۥ فَإِنَّ اَ۬للَّهَ شَدِيدُ اُ۬لْعِقَابِۖ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1176,9,'178',8,'Al-Anfāl','الأنفَال',11,12,14,'ذَٰلِكُمْ فَذُوقُوهُۖ وَأَنَّ لِلْكَٰفِرِينَ عَذَابَ اَ۬لنَّارِۖ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1177,9,'178',8,'Al-Anfāl','الأنفَال',12,13,15,'۞ يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ إِذَا لَقِيتُمُ اُ۬لذِينَ كَفَرُواْ زَحْفاٗ فَلَا تُوَلُّوهُمُ اُ۬لْأَدْبَٰرَۖ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1178,9,'178',8,'Al-Anfāl','الأنفَال',13,15,16,'وَمَنْ يُّوَلِّهِمْ يَوْمَئِذٖ دُبُرَهُۥ إِلَّا مُتَحَرِّفاٗ لِّقِتَالٍ أَوْ مُتَحَيِّزاً إِلَىٰ فِئَةٖ فَقَدْ بَآءَ بِغَضَبٖ مِّنَ اَ۬للَّهِ وَمَأْوَيٰهُ جَهَنَّمُۖ وَبِئْسَ اَ۬لْمَصِيرُۖ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1179,9,'179',8,'Al-Anfāl','الأنفَال',1,3,17,'فَلَمْ تَقْتُلُوهُمْۖ وَلَٰكِنَّ اَ۬للَّهَ قَتَلَهُمْۖ وَمَا رَمَيْتَ إِذْ رَمَيْتَ وَلَٰكِنَّ اَ۬للَّهَ رَمَىٰۖ وَلِيُبْلِيَ اَ۬لْمُؤْمِنِينَ مِنْهُ بَلَآءً حَسَناًۖ إِنَّ اَ۬للَّهَ سَمِيعٌ عَلِيمٞۖ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1180,9,'179',8,'Al-Anfāl','الأنفَال',3,4,18,'ذَٰلِكُمْ وَأَنَّ اَ۬للَّهَ مُوَهِّنٞ كَيْدَ اَ۬لْكَٰفِرِينَۖ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1181,9,'179',8,'Al-Anfāl','الأنفَال',4,6,19,'إِن تَسْتَفْتِحُواْ فَقَدْ جَآءَكُمُ اُ۬لْفَتْحُۖ وَإِن تَنتَهُواْ فَهْوَ خَيْرٞ لَّكُمْۖ وَإِن تَعُودُواْ نَعُدْۖ وَلَن تُغْنِيَ عَنكُمْ فِئَتُكُمْ شَئْاٗ وَلَوْ كَثُرَتْۖ وَأَنَّ اَ۬للَّهَ مَعَ اَ۬لْمُؤْمِنِينَۖ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1182,9,'179',8,'Al-Anfāl','الأنفَال',7,8,20,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ أَطِيعُواْ اُ۬للَّهَ وَرَسُولَهُۥۖ وَلَا تَوَلَّوْاْ عَنْهُ وَأَنتُمْ تَسْمَعُونَۖ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1183,9,'179',8,'Al-Anfāl','الأنفَال',8,9,21,'وَلَا تَكُونُواْ كَالذِينَ قَالُواْ سَمِعْنَا وَهُمْ لَا يَسْمَعُونَۖ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1184,9,'179',8,'Al-Anfāl','الأنفَال',9,10,22,'۞ إِنَّ شَرَّ اَ۬لدَّوَآبِّ عِندَ اَ۬للَّهِ اِ۬لصُّمُّ اُ۬لْبُكْمُ اُ۬لذِينَ لَا يَعْقِلُونَۖ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1185,9,'179',8,'Al-Anfāl','الأنفَال',10,11,23,'وَلَوْ عَلِمَ اَ۬للَّهُ فِيهِمْ خَيْراٗ لَّأَسْمَعَهُمْۖ وَلَوْ أَسْمَعَهُمْ لَتَوَلَّواْ وَّهُم مُّعْرِضُونَۖ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1186,9,'179',8,'Al-Anfāl','الأنفَال',11,14,24,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ اُ۪سْتَجِيبُواْ لِلهِ وَلِلرَّسُولِ إِذَا دَعَاكُمْ لِمَا يُحْيِيكُمْۖ وَاعْلَمُواْ أَنَّ اَ۬للَّهَ يَحُولُ بَيْنَ اَ۬لْمَرْءِ وَقَلْبِهِۦ وَأَنَّهُۥ إِلَيْهِ تُحْشَرُونَۖ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1187,9,'179',8,'Al-Anfāl','الأنفَال',14,15,25,'وَاتَّقُواْ فِتْنَةٗ لَّا تُصِيبَنَّ اَ۬لذِينَ ظَلَمُواْ مِنكُمْ خَآصَّةٗۖ وَاعْلَمُواْ أَنَّ اَ۬للَّهَ شَدِيدُ اُ۬لْعِقَابِۖ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1188,9,'180',8,'Al-Anfāl','الأنفَال',1,3,26,'وَاذْكُرُواْ إِذْ أَنتُمْ قَلِيلٞ مُّسْتَضْعَفُونَ فِے اِ۬لْأَرْضِ تَخَافُونَ أَنْ يَّتَخَطَّفَكُمُ اُ۬لنَّاسُ فَـَٔاوَيٰكُمْ وَأَيَّدَكُم بِنَصْرِهِۦ وَرَزَقَكُم مِّنَ اَ۬لطَّيِّبَٰتِ لَعَلَّكُمْ تَشْكُرُونَۖ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1189,9,'180',8,'Al-Anfāl','الأنفَال',3,4,27,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ لَا تَخُونُواْ اُ۬للَّهَ وَالرَّسُولَ وَتَخُونُواْ أَمَٰنَٰتِكُمْ وَأَنتُمْ تَعْلَمُونَۖ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1190,9,'180',8,'Al-Anfāl','الأنفَال',5,6,28,'وَاعْلَمُواْ أَنَّمَا أَمْوَٰلُكُمْ وَأَوْلَٰدُكُمْ فِتْنَةٞ وَأَنَّ اَ۬للَّهَ عِندَهُۥ أَجْرٌ عَظِيمٞۖ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1191,9,'180',8,'Al-Anfāl','الأنفَال',6,8,29,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ إِن تَتَّقُواْ اُ۬للَّهَ يَجْعَل لَّكُمْ فُرْقَاناٗ وَيُكَفِّرْ عَنكُمْ سَيِّـَٔاتِكُمْ وَيَغْفِرْ لَكُمْۖ وَاللَّهُ ذُو اُ۬لْفَضْلِ اِ۬لْعَظِيمِۖ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1192,9,'180',8,'Al-Anfāl','الأنفَال',8,10,30,'وَإِذْ يَمْكُرُ بِكَ اَ۬لذِينَ كَفَرُواْ لِيُثْبِتُوكَ أَوْ يَقْتُلُوكَ أَوْ يُخْرِجُوكَۖ وَيَمْكُرُونَ وَيَمْكُرُ اُ۬للَّهُۖ وَاللَّهُ خَيْرُ اُ۬لْمَٰكِرِينَۖ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1193,9,'180',8,'Al-Anfāl','الأنفَال',10,12,31,'وَإِذَا تُتْلَىٰ عَلَيْهِمْ ءَايَٰتُنَا قَالُواْ قَدْ سَمِعْنَا لَوْ نَشَآءُ لَقُلْنَا مِثْلَ هَٰذَا إِنْ هَٰذَا إِلَّا أَسَٰطِيرُ اُ۬لْأَوَّلِينَۖ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1194,9,'180',8,'Al-Anfāl','الأنفَال',12,14,32,'۞ وَإِذْ قَالُواْ اُ۬للَّهُمَّ إِن كَانَ هَٰذَا هُوَ اَ۬لْحَقَّ مِنْ عِندِكَ فَأَمْطِرْ عَلَيْنَا حِجَارَةٗ مِّنَ اَ۬لسَّمَآءِ اَ۬وِ اِ۪ئْتِنَا بِعَذَابٍ أَلِيمٖۖ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1195,9,'180',8,'Al-Anfāl','الأنفَال',14,15,33,'وَمَا كَانَ اَ۬للَّهُ لِيُعَذِّبَهُمْ وَأَنتَ فِيهِمْۖ وَمَا كَانَ اَ۬للَّهُ مُعَذِّبَهُمْ وَهُمْ يَسْتَغْفِرُونَۖ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1196,9,'181',8,'Al-Anfāl','الأنفَال',1,3,34,'وَمَا لَهُمْ أَلَّا يُعَذِّبَهُمُ اُ۬للَّهُ وَهُمْ يَصُدُّونَ عَنِ اِ۬لْمَسْجِدِ اِ۬لْحَرَامِ وَمَا كَانُواْ أَوْلِيَآءَهُۥۖ إِنْ أَوْلِيَآؤُهُۥ إِلَّا اَ۬لْمُتَّقُونَۖ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَۖ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1197,9,'181',8,'Al-Anfāl','الأنفَال',3,5,35,'وَمَا كَانَ صَلَاتُهُمْ عِندَ اَ۬لْبَيْتِ إِلَّا مُكَآءٗ وَتَصْدِيَةٗۖ فَذُوقُواْ اُ۬لْعَذَابَ بِمَا كُنتُمْ تَكْفُرُونَۖ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1198,9,'181',8,'Al-Anfāl','الأنفَال',5,8,36,'إِنَّ اَ۬لذِينَ كَفَرُواْ يُنفِقُونَ أَمْوَٰلَهُمْ لِيَصُدُّواْ عَن سَبِيلِ اِ۬للَّهِۖ فَسَيُنفِقُونَهَا ثُمَّ تَكُونُ عَلَيْهِمْ حَسْرَةٗ ثُمَّ يُغْلَبُونَۖ وَالذِينَ كَفَرُواْ إِلَىٰ جَهَنَّمَ يُحْشَرُونَ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1199,9,'181',8,'Al-Anfāl','الأنفَال',8,10,37,'لِيَمِيزَ اَ۬للَّهُ اُ۬لْخَبِيثَ مِنَ اَ۬لطَّيِّبِ وَيَجْعَلَ اَ۬لْخَبِيثَ بَعْضَهُۥ عَلَىٰ بَعْضٖ فَيَرْكُمَهُۥ جَمِيعاٗ فَيَجْعَلَهُۥ فِے جَهَنَّمَۖ أُوْلَٰٓئِكَ هُمُ اُ۬لْخَٰسِرُونَۖ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1200,9,'181',8,'Al-Anfāl','الأنفَال',10,12,38,'قُل لِّلذِينَ كَفَرُواْ إِنْ يَّنتَهُواْ يُغْفَرْ لَهُم مَّا قَدْ سَلَفَۖ وَإِنْ يَّعُودُواْ فَقَدْ مَضَتْ سُنَّتُ اُ۬لْأَوَّلِينَۖ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1201,9,'181',8,'Al-Anfāl','الأنفَال',12,14,39,'وَقَٰتِلُوهُمْ حَتَّىٰ لَا تَكُونَ فِتْنَةٞ وَيَكُونَ اَ۬لدِّينُ كُلُّهُۥ لِلهِۖ فَإِنِ اِ۪نتَهَوْاْ فَإِنَّ اَ۬للَّهَ بِمَا يَعْمَلُونَ بَصِيرٞۖ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1202,9,'181',8,'Al-Anfāl','الأنفَال',14,15,40,'وَإِن تَوَلَّوْاْ فَاعْلَمُواْ أَنَّ اَ۬للَّهَ مَوْلَيٰكُمْۖ نِعْمَ اَ۬لْمَوْلَىٰۖ وَنِعْمَ اَ۬لنَّصِيرُۖ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1203,10,'182',8,'Al-Anfāl','الأنفَال',1,4,41,'۞ وَاعْلَمُواْ أَنَّمَا غَنِمْتُم مِّن شَےْءٖ فَأَنَّ لِلهِ خُمُسَهُۥ وَلِلرَّسُولِ وَلِذِے اِ۬لْقُرْبَىٰ وَالْيَتَٰمَىٰ وَالْمَسَٰكِينِ وَابْنِ اِ۬لسَّبِيلِ إِن كُنتُمْ ءَامَنتُم بِاللَّهِ وَمَا أَنزَلْنَا عَلَىٰ عَبْدِنَا يَوْمَ اَ۬لْفُرْقَانِ يَوْمَ اَ۪لْتَقَى اَ۬لْجَمْعَٰنِۖ وَاللَّهُ عَلَىٰ كُلِّ شَےْءٖ قَدِيرٌۖ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1204,10,'182',8,'Al-Anfāl','الأنفَال',4,7,42,'إِذْ أَنتُم بِالْعُدْوَةِ اِ۬لدُّنْيَا وَهُم بِالْعُدْوَةِ اِ۬لْقُصْوَىٰ وَالرَّكْبُ أَسْفَلَ مِنكُمْۖ وَلَوْ تَوَاعَدتُّمْ لَاخْتَلَفْتُمْ فِے اِ۬لْمِيعَٰدِۖ وَلَٰكِن لِّيَقْضِيَ اَ۬للَّهُ أَمْراٗ كَانَ مَفْعُولاٗ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1205,10,'182',8,'Al-Anfāl','الأنفَال',7,9,43,'لِّيَهْلِكَ مَنْ هَلَكَ عَنۢ بَيِّنَةٖۖ وَيَحْيَىٰ مَنْ حَـِۧيَ عَنۢ بَيِّنَةٖۖ وَإِنَّ اَ۬للَّهَ لَسَمِيعٌ عَلِيمٌۖ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1206,10,'182',8,'Al-Anfāl','الأنفَال',9,11,44,'إِذْ يُرِيكَهُمُ اُ۬للَّهُ فِے مَنَامِكَ قَلِيلاٗۖ وَلَوْ أَرَيٰكَهُمْ كَثِيراٗ لَّفَشِلْتُمْ وَلَتَنَٰزَعْتُمْ فِے اِ۬لْأَمْرِۖ وَلَٰكِنَّ اَ۬للَّهَ سَلَّمَۖ إِنَّهُۥ عَلِيمُۢ بِذَاتِ اِ۬لصُّدُورِۖ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1207,10,'182',8,'Al-Anfāl','الأنفَال',11,14,45,'وَإِذْ يُرِيكُمُوهُمْ إِذِ اِ۪لْتَقَيْتُمْ فِے أَعْيُنِكُمْ قَلِيلاٗ وَيُقَلِّلُكُمْ فِے أَعْيُنِهِمْ لِيَقْضِيَ اَ۬للَّهُ أَمْراٗ كَانَ مَفْعُولاٗۖ وَإِلَى اَ۬للَّهِ تُرْجَعُ اُ۬لْأُمُورُۖ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1208,10,'182',8,'Al-Anfāl','الأنفَال',14,15,46,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ إِذَا لَقِيتُمْ فِئَةٗ فَاثْبُتُواْ وَاذْكُرُواْ اُ۬للَّهَ كَثِيراٗ لَّعَلَّكُمْ تُفْلِحُونَۖ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1209,10,'183',8,'Al-Anfāl','الأنفَال',1,2,47,'وَأَطِيعُواْ اُ۬للَّهَ وَرَسُولَهُۥۖ وَلَا تَنَٰزَعُواْ فَتَفْشَلُواْ وَتَذْهَبَ رِيحُكُمْۖ وَاصْبِرُواْۖ إِنَّ اَ۬للَّهَ مَعَ اَ۬لصَّٰبِرِينَۖ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1210,10,'183',8,'Al-Anfāl','الأنفَال',2,4,48,'وَلَا تَكُونُواْ كَالذِينَ خَرَجُواْ مِن دِيَٰرِهِم بَطَراٗ وَرِئَآءَ اَ۬لنَّاسِ وَيَصُدُّونَ عَن سَبِيلِ اِ۬للَّهِۖ وَاللَّهُ بِمَا يَعْمَلُونَ مُحِيطٞۖ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1211,10,'183',8,'Al-Anfāl','الأنفَال',4,8,49,'۞ وَإِذْ زَيَّنَ لَهُمُ اُ۬لشَّيْطَٰنُ أَعْمَٰلَهُمْ وَقَالَ لَا غَالِبَ لَكُمُ اُ۬لْيَوْمَ مِنَ اَ۬لنَّاسِ وَإِنِّے جَارٞ لَّكُمْۖ فَلَمَّا تَرَآءَتِ اِ۬لْفِئَتَٰنِ نَكَصَ عَلَىٰ عَقِبَيْهِ وَقَالَ إِنِّے بَرِےٓءٞ مِّنكُمْ إِنِّيَ أَرَىٰ مَا لَا تَرَوْنَ إِنِّيَ أَخَافُ اُ۬للَّهَۖ وَاللَّهُ شَدِيدُ اُ۬لْعِقَابِۖ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1212,10,'183',8,'Al-Anfāl','الأنفَال',8,10,50,'إِذْ يَقُولُ اُ۬لْمُنَٰفِقُونَ وَالذِينَ فِے قُلُوبِهِم مَّرَضٌ غَرَّ هَٰؤُلَآءِ دِينُهُمْۖ وَمَنْ يَّتَوَكَّلْ عَلَى اَ۬للَّهِ فَإِنَّ اَ۬للَّهَ عَزِيزٌ حَكِيمٞۖ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1213,10,'183',8,'Al-Anfāl','الأنفَال',10,12,51,'وَلَوْ تَرَىٰ إِذْ يَتَوَفَّى اَ۬لذِينَ كَفَرُواْۖ اُ۬لْمَلَٰٓئِكَةُ يَضْرِبُونَ وُجُوهَهُمْ وَأَدْبَٰرَهُمْ وَذُوقُواْ عَذَابَ اَ۬لْحَرِيقِۖ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1214,10,'183',8,'Al-Anfāl','الأنفَال',12,13,52,'ذَٰلِكَ بِمَا قَدَّمَتْ أَيْدِيكُمْ وَأَنَّ اَ۬للَّهَ لَيْسَ بِظَلَّٰمٖ لِّلْعَبِيدِۖ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1215,10,'183',8,'Al-Anfāl','الأنفَال',14,15,53,'كَدَأْبِ ءَالِ فِرْعَوْنَۖ وَالذِينَ مِن قَبْلِهِمْ كَفَرُواْ بِـَٔايَٰتِ اِ۬للَّهِ فَأَخَذَهُمُ اُ۬للَّهُ بِذُنُوبِهِمْۖ إِنَّ اَ۬للَّهَ قَوِيّٞ شَدِيدُ اُ۬لْعِقَابِۖ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1216,10,'184',8,'Al-Anfāl','الأنفَال',1,2,54,'ذَٰلِكَ بِأَنَّ اَ۬للَّهَ لَمْ يَكُ مُغَيِّراٗ نِّعْمَةً أَنْعَمَهَا عَلَىٰ قَوْمٍ حَتَّىٰ يُغَيِّرُواْ مَا بِأَنفُسِهِمْ وَأَنَّ اَ۬للَّهَ سَمِيعٌ عَلِيمٞۖ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1217,10,'184',8,'Al-Anfāl','الأنفَال',2,4,55,'كَدَأْبِ ءَالِ فِرْعَوْنَۖ وَالذِينَ مِن قَبْلِهِمْ كَذَّبُواْ بِـَٔايَٰتِ رَبِّهِمْ فَأَهْلَكْنَٰهُم بِذُنُوبِهِمْۖ وَأَغْرَقْنَا ءَالَ فِرْعَوْنَۖ وَكُلّٞ كَانُواْ ظَٰلِمِينَۖ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1218,10,'184',8,'Al-Anfāl','الأنفَال',5,5,56,'إِنَّ شَرَّ اَ۬لدَّوَآبِّ عِندَ اَ۬للَّهِ اِ۬لذِينَ كَفَرُواْ فَهُمْ لَا يُؤْمِنُونَ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1219,10,'184',8,'Al-Anfāl','الأنفَال',6,7,57,'اَ۬لذِينَ عَٰهَدتَّ مِنْهُمْ ثُمَّ يَنقُضُونَ عَهْدَهُمْ فِے كُلِّ مَرَّةٖ وَهُمْ لَا يَتَّقُونَۖ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1220,10,'184',8,'Al-Anfāl','الأنفَال',7,8,58,'فَإِمَّا تَثْقَفَنَّهُمْ فِے اِ۬لْحَرْبِ فَشَرِّدْ بِهِم مَّنْ خَلْفَهُمْ لَعَلَّهُمْ يَذَّكَّرُونَۖ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1221,10,'184',8,'Al-Anfāl','الأنفَال',8,9,59,'وَإِمَّا تَخَافَنَّ مِن قَوْمٍ خِيَانَةٗ فَانۢبِذْ إِلَيْهِمْ عَلَىٰ سَوَآءٍۖ إِنَّ اَ۬للَّهَ لَا يُحِبُّ اُ۬لْخَآئِنِينَۖ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1222,10,'184',8,'Al-Anfāl','الأنفَال',10,10,60,'وَلَا تَحْسِبَنَّ اَ۬لذِينَ كَفَرُواْ سَبَقُواْۖ إِنَّهُمْ لَا يُعْجِزُونَۖ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1223,10,'184',8,'Al-Anfāl','الأنفَال',11,14,61,'۞ وَأَعِدُّواْ لَهُم مَّا اَ۪سْتَطَعْتُم مِّن قُوَّةٖ وَمِن رِّبَاطِ اِ۬لْخَيْلِۖ تُرْهِبُونَ بِهِۦ عَدُوَّ اَ۬للَّهِ وَعَدُوَّكُمْ وَءَاخَرِينَ مِن دُونِهِمْ لَا تَعْلَمُونَهُمُۖ اُ۬للَّهُ يَعْلَمُهُمْۖ وَمَا تُنفِقُواْ مِن شَےْءٖ فِے سَبِيلِ اِ۬للَّهِ يُوَفَّ إِلَيْكُمْ وَأَنتُمْ لَا تُظْلَمُونَۖ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1224,10,'184',8,'Al-Anfāl','الأنفَال',14,15,62,'وَإِن جَنَحُواْ لِلسَّلْمِ فَاجْنَحْ لَهَا وَتَوَكَّلْ عَلَى اَ۬للَّهِۖ إِنَّهُۥ هُوَ اَ۬لسَّمِيعُ اُ۬لْعَلِيمُۖ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1225,10,'185',8,'Al-Anfāl','الأنفَال',1,2,63,'وَإِنْ يُّرِيدُواْ أَنْ يَّخْدَعُوكَ فَإِنَّ حَسْبَكَ اَ۬للَّهُۖ هُوَ اَ۬لذِے أَيَّدَكَ بِنَصْرِهِۦ وَبِالْمُؤْمِنِينَ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1226,10,'185',8,'Al-Anfāl','الأنفَال',2,4,64,'وَأَلَّفَ بَيْنَ قُلُوبِهِمْۖ لَوْ أَنفَقْتَ مَا فِے اِ۬لْأَرْضِ جَمِيعاٗ مَّا أَلَّفْتَ بَيْنَ قُلُوبِهِمْۖ وَلَٰكِنَّ اَ۬للَّهَ أَلَّفَ بَيْنَهُمْۖ إِنَّهُۥ عَزِيزٌ حَكِيمٞۖ ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1227,10,'185',8,'Al-Anfāl','الأنفَال',4,5,65,'يَٰأَيُّهَا‏ اَ۬لنَّبِےٓءُ حَسْبُكَ اَ۬للَّهُۖ وَمَنِ اِ۪تَّبَعَكَ مِنَ اَ۬لْمُؤْمِنِينَۖ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1228,10,'185',8,'Al-Anfāl','الأنفَال',5,8,66,'يَٰأَيُّهَا‏ اَ۬لنَّبِےٓءُ حَرِّضِ اِ۬لْمُؤْمِنِينَ عَلَى اَ۬لْقِتَالِۖ إِنْ يَّكُن مِّنكُمْ عِشْرُونَ صَٰبِرُونَ يَغْلِبُواْ مِاْئَتَيْنِۖ وَإِن تَكُن مِّنكُم مِّاْئَةٞ يَغْلِبُواْ أَلْفاٗ مِّنَ اَ۬لذِينَ كَفَرُواْ بِأَنَّهُمْ قَوْمٞ لَّا يَفْقَهُونَۖ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1229,10,'185',8,'Al-Anfāl','الأنفَال',8,11,67,'اَ۬ءَلْٰنَ خَفَّفَ اَ۬للَّهُ عَنكُمْ وَعَلِمَ أَنَّ فِيكُمْ ضُعْفاٗۖ فَإِن تَكُن مِّنكُم مِّاْئَةٞ صَابِرَةٞ يَغْلِبُواْ مِاْئَتَيْنِۖ وَإِنْ يَّكُن مِّنكُمْ أَلْفٞ يَغْلِبُواْ أَلْفَيْنِ بِإِذْنِ اِ۬للَّهِۖ وَاللَّهُ مَعَ اَ۬لصَّٰبِرِينَۖ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1230,10,'185',8,'Al-Anfāl','الأنفَال',11,13,68,'مَا كَانَ لِنَبِےٓءٍ أَنْ يَّكُونَ لَهُۥ أَسْرَىٰ حَتَّىٰ يُثْخِنَ فِے اِ۬لْأَرْضِۖ تُرِيدُونَ عَرَضَ اَ۬لدُّنْيَاۖ وَاللَّهُ يُرِيدُ اُ۬لْأٓخِرَةَۖ وَاللَّهُ عَزِيزٌ حَكِيمٞۖ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1231,10,'185',8,'Al-Anfāl','الأنفَال',13,14,69,'لَّوْلَا كِتَٰبٞ مِّنَ اَ۬للَّهِ سَبَقَ لَمَسَّكُمْ فِيمَا أَخَذتُّمْ عَذَابٌ عَظِيمٞۖ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1232,10,'185',8,'Al-Anfāl','الأنفَال',14,15,70,'فَكُلُواْ مِمَّا غَنِمْتُمْ حَلَٰلاٗ طَيِّباٗۖ وَاتَّقُواْ اُ۬للَّهَۖ إِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٞۖ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1233,10,'186',8,'Al-Anfāl','الأنفَال',1,3,71,'يَٰأَيُّهَا‏ اَ۬لنَّبِےٓءُ قُل لِّمَن فِے أَيْدِيكُم مِّنَ اَ۬لْأَسْرَىٰ إِنْ يَّعْلَمِ اِ۬للَّهُ فِے قُلُوبِكُمْ خَيْراٗ يُؤْتِكُمْ خَيْراٗ مِّمَّا أُخِذَ مِنكُمْ وَيَغْفِرْ لَكُمْۖ وَاللَّهُ غَفُورٞ رَّحِيمٞۖ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1234,10,'186',8,'Al-Anfāl','الأنفَال',3,4,72,'۞ وَإِنْ يُّرِيدُواْ خِيَانَتَكَ فَقَدْ خَانُواْ اُ۬للَّهَ مِن قَبْلُ فَأَمْكَنَ مِنْهُمْۖ وَاللَّهُ عَلِيمٌ حَكِيمٌۖ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1235,10,'186',8,'Al-Anfāl','الأنفَال',4,9,73,'إِنَّ اَ۬لذِينَ ءَامَنُواْ وَهَاجَرُواْ وَجَٰهَدُواْ بِأَمْوَٰلِهِمْ وَأَنفُسِهِمْ فِے سَبِيلِ اِ۬للَّهِ وَالذِينَ ءَاوَواْ وَّنَصَرُواْ أُوْلَٰٓئِكَ بَعْضُهُمْ أَوْلِيَآءُ بَعْضٖۖ وَالذِينَ ءَامَنُواْ وَلَمْ يُهَاجِرُواْ مَا لَكُم مِّنْ وَّلَٰيَتِهِم مِّن شَےْءٍ حَتَّىٰ يُهَاجِرُواْۖ وَإِنِ اِ۪سْتَنصَرُوكُمْ فِے اِ۬لدِّينِ فَعَلَيْكُمُ اُ۬لنَّصْرُ إِلَّا عَلَىٰ قَوْمِۢ بَيْنَكُمْ وَبَيْنَهُم مِّيثَٰقٞۖ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٞۖ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1236,10,'186',8,'Al-Anfāl','الأنفَال',9,11,74,'وَالذِينَ كَفَرُواْ بَعْضُهُمْ أَوْلِيَآءُ بَعْضٍۖ إِلَّا تَفْعَلُوهُ تَكُن فِتْنَةٞ فِے اِ۬لْأَرْضِ وَفَسَادٞ كَبِيرٞۖ ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1237,10,'186',8,'Al-Anfāl','الأنفَال',11,13,75,'وَالذِينَ ءَامَنُواْ وَهَاجَرُواْ وَجَٰهَدُواْ فِے سَبِيلِ اِ۬للَّهِ وَالذِينَ ءَاوَواْ وَّنَصَرُواْ أُوْلَٰٓئِكَ هُمُ اُ۬لْمُؤْمِنُونَ حَقّاٗۖ لَّهُم مَّغْفِرَةٞ وَرِزْقٞ كَرِيمٞۖ ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1238,10,'186',8,'Al-Anfāl','الأنفَال',13,15,76,'وَالذِينَ ءَامَنُواْ مِنۢ بَعْدُ وَهَاجَرُواْ وَجَٰهَدُواْ مَعَكُمْ فَأُوْلَٰٓئِكَ مِنكُمْۖ وَأُوْلُواْ اُ۬لْأَرْحَامِ بَعْضُهُمْ أَوْلَىٰ بِبَعْضٖ فِے كِتَٰبِ اِ۬للَّهِۖ إِنَّ اَ۬للَّهَ بِكُلِّ شَےْءٍ عَلِيمُۢۖ ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1239,10,'187',9,'At-Taubah','التوبَة',2,2,1,'بَرَآءَةٞ مِّنَ اَ۬للَّهِ وَرَسُولِهِۦ إِلَى اَ۬لذِينَ عَٰهَدتُّم مِّنَ اَ۬لْمُشْرِكِينَۖ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1240,10,'187',9,'At-Taubah','التوبَة',3,4,2,'فَسِيحُواْ فِے اِ۬لْأَرْضِ أَرْبَعَةَ أَشْهُرٖۖ وَاعْلَمُواْ أَنَّكُمْ غَيْرُ مُعْجِزِے اِ۬للَّهِ وَأَنَّ اَ۬للَّهَ مُخْزِے اِ۬لْكَٰفِرِينَۖ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1241,10,'187',9,'At-Taubah','التوبَة',4,7,3,'وَأَذَٰنٞ مِّنَ اَ۬للَّهِ وَرَسُولِهِۦ إِلَى اَ۬لنَّاسِ يَوْمَ اَ۬لْحَجِّ اِ۬لْأَكْبَرِ أَنَّ اَ۬للَّهَ بَرِےٓءٞ مِّنَ اَ۬لْمُشْرِكِينَ وَرَسُولُهُۥۖ فَإِن تُبْتُمْ فَهْوَ خَيْرٞ لَّكُمْۖ وَإِن تَوَلَّيْتُمْ فَاعْلَمُواْ أَنَّكُمْ غَيْرُ مُعْجِزِے اِ۬للَّهِۖ وَبَشِّرِ اِ۬لذِينَ كَفَرُواْ بِعَذَابٍ أَلِيمٍ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1242,10,'187',9,'At-Taubah','التوبَة',8,10,4,'إِلَّا اَ۬لذِينَ عَٰهَدتُّم مِّنَ اَ۬لْمُشْرِكِينَ ثُمَّ لَمْ يَنقُصُوكُمْ شَئْاٗ وَلَمْ يُظَٰهِرُواْ عَلَيْكُمْ أَحَداٗ فَأَتِمُّواْ إِلَيْهِمْ عَهْدَهُمْ إِلَىٰ مُدَّتِهِمْۖ إِنَّ اَ۬للَّهَ يُحِبُّ اُ۬لْمُتَّقِينَۖ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1243,10,'187',9,'At-Taubah','التوبَة',10,13,5,'۞ فَإِذَا اَ۪نسَلَخَ اَ۬لْأَشْهُرُ اُ۬لْحُرُمُ فَاقْتُلُواْ اُ۬لْمُشْرِكِينَ حَيْثُ وَجَدتُّمُوهُمْ وَخُذُوهُمْ وَاحْصُرُوهُمْ وَاقْعُدُواْ لَهُمْ كُلَّ مَرْصَدٖۖ فَإِن تَابُواْ وَأَقَامُواْ اُ۬لصَّلَوٰةَ وَءَاتَوُاْ اُ۬لزَّكَوٰةَ فَخَلُّواْ سَبِيلَهُمْۖ إِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٞۖ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1244,10,'187',9,'At-Taubah','التوبَة',13,15,6,'وَإِنْ أَحَدٞ مِّنَ اَ۬لْمُشْرِكِينَ اَ۪سْتَجَارَكَ فَأَجِرْهُ حَتَّىٰ يَسْمَعَ كَلَٰمَ اَ۬للَّهِ ثُمَّ أَبْلِغْهُ مَأْمَنَهُۥۖ ذَٰلِكَ بِأَنَّهُمْ قَوْمٞ لَّا يَعْلَمُونَۖ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1245,10,'188',9,'At-Taubah','التوبَة',1,3,7,'كَيْفَ يَكُونُ لِلْمُشْرِكِينَ عَهْدٌ عِندَ اَ۬للَّهِ وَعِندَ رَسُولِهِۦ إِلَّا اَ۬لذِينَ عَٰهَدتُّمْ عِندَ اَ۬لْمَسْجِدِ اِ۬لْحَرَامِ فَمَا اَ۪سْتَقَٰمُواْ لَكُمْ فَاسْتَقِيمُواْ لَهُمْۖ إِنَّ اَ۬للَّهَ يُحِبُّ اُ۬لْمُتَّقِينَۖ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1246,10,'188',9,'At-Taubah','التوبَة',4,6,8,'كَيْفَ وَإِنْ يَّظْهَرُواْ عَلَيْكُمْ لَا يَرْقُبُواْ فِيكُمْ إِلّاٗ وَلَا ذِمَّةٗۖ يُرْضُونَكُم بِأَفْوَٰهِهِمْ وَتَأْبَىٰ قُلُوبُهُمْ وَأَكْثَرُهُمْ فَٰسِقُونَۖ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1247,10,'188',9,'At-Taubah','التوبَة',6,7,9,'اَ۪شْتَرَوْاْ بِـَٔايَٰتِ اِ۬للَّهِ ثَمَناٗ قَلِيلاٗ فَصَدُّواْ عَن سَبِيلِهِۦۖ إِنَّهُمْ سَآءَ مَا كَانُواْ يَعْمَلُونَۖ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1248,10,'188',9,'At-Taubah','التوبَة',7,8,10,'لَا يَرْقُبُونَ فِے مُؤْمِنٍ إِلّاٗ وَلَا ذِمَّةٗۖ وَأُوْلَٰٓئِكَ هُمُ اُ۬لْمُعْتَدُونَۖ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1249,10,'188',9,'At-Taubah','التوبَة',8,10,11,'فَإِن تَابُواْ وَأَقَامُواْ اُ۬لصَّلَوٰةَ وَءَاتَوُاْ اُ۬لزَّكَوٰةَ فَإِخْوَٰنُكُمْ فِے اِ۬لدِّينِۖ وَنُفَصِّلُ اُ۬لْأٓيَٰتِ لِقَوْمٖ يَعْلَمُونَۖ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1250,10,'188',9,'At-Taubah','التوبَة',10,13,12,'۞ وَإِن نَّكَثُواْ أَيْمَٰنَهُم مِّنۢ بَعْدِ عَهْدِهِمْ وَطَعَنُواْ فِے دِينِكُمْ فَقَٰتِلُواْ أَي۪مَّةَ اَ۬لْكُفْرِ إِنَّهُمْ لَا أَيْمَٰنَ لَهُمْ لَعَلَّهُمْ يَنتَهُونَۖ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1251,10,'188',9,'At-Taubah','التوبَة',13,15,13,'أَلَا تُقَٰتِلُونَ قَوْماٗ نَّكَثُواْ أَيْمَٰنَهُمْ وَهَمُّواْ بِإِخْرَاجِ اِ۬لرَّسُولِ وَهُم بَدَءُوكُمْ أَوَّلَ مَرَّةٍ أَتَخْشَوْنَهُمْۖ فَاللَّهُ أَحَقُّ أَن تَخْشَوْهُ إِن كُنتُم مُّؤْمِنِينَۖ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1252,10,'189',9,'At-Taubah','التوبَة',1,2,14,'قَٰتِلُوهُمْ يُعَذِّبْهُمُ اُ۬للَّهُ بِأَيْدِيكُمْ وَيُخْزِهِمْ وَيَنصُرْكُمْ عَلَيْهِمْ وَيَشْفِ صُدُورَ قَوْمٖ مُّؤْمِنِينَ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1253,10,'189',9,'At-Taubah','التوبَة',2,3,15,'وَيُذْهِبْ غَيْظَ قُلُوبِهِمْۖ وَيَتُوبُ اُ۬للَّهُ عَلَىٰ مَنْ يَّشَآءُۖ وَاللَّهُ عَلِيمٌ حَكِيمٌۖ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1254,10,'189',9,'At-Taubah','التوبَة',4,6,16,'أَمْ حَسِبْتُمْ أَن تُتْرَكُواْ وَلَمَّا يَعْلَمِ اِ۬للَّهُ اُ۬لذِينَ جَٰهَدُواْ مِنكُمْ وَلَمْ يَتَّخِذُواْ مِن دُونِ اِ۬للَّهِ وَلَا رَسُولِهِۦ وَلَا اَ۬لْمُؤْمِنِينَ وَلِيجَةٗۖ وَاللَّهُ خَبِيرُۢ بِمَا تَعْمَلُونَۖ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1255,10,'189',9,'At-Taubah','التوبَة',6,8,17,'مَا كَانَ لِلْمُشْرِكِينَ أَنْ يَّعْمُرُواْ مَسَٰجِدَ اَ۬للَّهِ شَٰهِدِينَ عَلَىٰ أَنفُسِهِم بِالْكُفْرِۖ أُوْلَٰٓئِكَ حَبِطَتْ أَعْمَٰلُهُمْ وَفِے اِ۬لنَّارِ هُمْ خَٰلِدُونَۖ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1256,10,'189',9,'At-Taubah','التوبَة',8,11,18,'إِنَّمَا يَعْمُرُ مَسَٰجِدَ اَ۬للَّهِ مَنْ ءَامَنَ بِاللَّهِ وَالْيَوْمِ اِ۬لْأٓخِرِ وَأَقَامَ اَ۬لصَّلَوٰةَ وَءَاتَى اَ۬لزَّكَوٰةَ وَلَمْ يَخْشَ إِلَّا اَ۬للَّهَ فَعَسَىٰ أُوْلَٰٓئِكَ أَنْ يَّكُونُواْ مِنَ اَ۬لْمُهْتَدِينَۖ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1257,10,'189',9,'At-Taubah','التوبَة',11,13,19,'۞ أَجَعَلْتُمْ سِقَايَةَ اَ۬لْحَآجِّ وَعِمَارَةَ اَ۬لْمَسْجِدِ اِ۬لْحَرَامِ كَمَنْ ءَامَنَ بِاللَّهِ وَالْيَوْمِ اِ۬لْأٓخِرِ وَجَٰهَدَ فِے سَبِيلِ اِ۬للَّهِۖ لَا يَسْتَوُۥنَ عِندَ اَ۬للَّهِۖ وَاللَّهُ لَا يَهْدِے اِ۬لْقَوْمَ اَ۬لظَّٰلِمِينَۖ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1258,10,'189',9,'At-Taubah','التوبَة',14,15,20,'اَ۬لذِينَ ءَامَنُواْ وَهَاجَرُواْ وَجَٰهَدُواْ فِے سَبِيلِ اِ۬للَّهِ بِأَمْوَٰلِهِمْ وَأَنفُسِهِمْ أَعْظَمُ دَرَجَةً عِندَ اَ۬للَّهِۖ وَأُوْلَٰٓئِكَ هُمُ اُ۬لْفَآئِزُونَۖ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1259,10,'190',9,'At-Taubah','التوبَة',1,2,21,'يُبَشِّرُهُمْ رَبُّهُم بِرَحْمَةٖ مِّنْهُ وَرِضْوَٰنٖ وَجَنَّٰتٖ لَّهُمْ فِيهَا نَعِيمٞ مُّقِيمٌ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1260,10,'190',9,'At-Taubah','التوبَة',2,3,22,'خَٰلِدِينَ فِيهَا أَبَداًۖ إِنَّ اَ۬للَّهَ عِندَهُۥ أَجْرٌ عَظِيمٞۖ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1261,10,'190',9,'At-Taubah','التوبَة',3,5,23,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ لَا تَتَّخِذُواْ ءَابَآءَكُمْ وَإِخْوَٰنَكُمْ أَوْلِيَآءَ ا۪نِ اِ۪سْتَحَبُّواْ اُ۬لْكُفْرَ عَلَى اَ۬لْإِيمَٰنِۖ وَمَنْ يَّتَوَلَّهُم مِّنكُمْ فَأُوْلَٰٓئِكَ هُمُ اُ۬لظَّٰلِمُونَۖ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1262,10,'190',9,'At-Taubah','التوبَة',5,10,24,'قُلْ إِن كَانَ ءَابَآؤُكُمْ وَأَبْنَآؤُكُمْ وَإِخْوَٰنُكُمْ وَأَزْوَٰجُكُمْ وَعَشِيرَتُكُمْ وَأَمْوَٰلٌ اِ۪قْتَرَفْتُمُوهَا وَتِجَٰرَةٞ تَخْشَوْنَ كَسَادَهَا وَمَسَٰكِنُ تَرْضَوْنَهَا أَحَبَّ إِلَيْكُم مِّنَ اَ۬للَّهِ وَرَسُولِهِۦ وَجِهَادٖ فِے سَبِيلِهِۦ فَتَرَبَّصُواْ حَتَّىٰ يَأْتِيَ اَ۬للَّهُ بِأَمْرِهِۦۖ وَاللَّهُ لَا يَهْدِے اِ۬لْقَوْمَ اَ۬لْفَٰسِقِينَۖ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1263,10,'190',9,'At-Taubah','التوبَة',10,13,25,'لَقَدْ نَصَرَكُمُ اُ۬للَّهُ فِے مَوَاطِنَ كَثِيرَةٖ وَيَوْمَ حُنَيْنٍ إِذْ أَعْجَبَتْكُمْ كَثْرَتُكُمْ فَلَمْ تُغْنِ عَنكُمْ شَئْاٗ وَضَاقَتْ عَلَيْكُمُ اُ۬لْأَرْضُ بِمَا رَحُبَتْ ثُمَّ وَلَّيْتُم مُّدْبِرِينَۖ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1264,10,'190',9,'At-Taubah','التوبَة',13,15,26,'ثُمَّ أَنزَلَ اَ۬للَّهُ سَكِينَتَهُۥ عَلَىٰ رَسُولِهِۦ وَعَلَى اَ۬لْمُؤْمِنِينَ وَأَنزَلَ جُنُوداٗ لَّمْ تَرَوْهَا وَعَذَّبَ اَ۬لذِينَ كَفَرُواْۖ وَذَٰلِكَ جَزَآءُ اُ۬لْكَٰفِرِينَۖ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1265,10,'191',9,'At-Taubah','التوبَة',1,2,27,'ثُمَّ يَتُوبُ اُ۬للَّهُ مِنۢ بَعْدِ ذَٰلِكَ عَلَىٰ مَنْ يَّشَآءُۖ وَاللَّهُ غَفُورٞ رَّحِيمٞۖ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1266,10,'191',9,'At-Taubah','التوبَة',2,5,28,'۞ يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ إِنَّمَا اَ۬لْمُشْرِكُونَ نَجَسٞ فَلَا يَقْرَبُواْ اُ۬لْمَسْجِدَ اَ۬لْحَرَامَ بَعْدَ عَامِهِمْ هَٰذَاۖ وَإِنْ خِفْتُمْ عَيْلَةٗ فَسَوْفَ يُغْنِيكُمُ اُ۬للَّهُ مِن فَضْلِهِۦ إِن شَآءَۖ ا۪نَّ اَ۬للَّهَ عَلِيمٌ حَكِيمٞۖ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1267,10,'191',9,'At-Taubah','التوبَة',5,9,29,'قَٰتِلُواْ اُ۬لذِينَ لَا يُؤْمِنُونَ بِاللَّهِ وَلَا بِالْيَوْمِ اِ۬لْأٓخِرِ وَلَا يُحَرِّمُونَ مَا حَرَّمَ اَ۬للَّهُ وَرَسُولُهُۥ وَلَا يَدِينُونَ دِينَ اَ۬لْحَقِّ مِنَ اَ۬لذِينَ أُوتُواْ اُ۬لْكِتَٰبَ حَتَّىٰ يُعْطُواْ اُ۬لْجِزْيَةَ عَنْ يَّدٖ وَهُمْ صَٰغِرُونَۖ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1268,10,'191',9,'At-Taubah','التوبَة',9,12,30,'وَقَالَتِ اِ۬لْيَهُودُ عُزَيْرُ اُ۪بْنُ اُ۬للَّهِۖ وَقَالَتِ اِ۬لنَّصَٰرَى اَ۬لْمَسِيحُ اُ۪بْنُ اُ۬للَّهِۖ ذَٰلِكَ قَوْلُهُم بِأَفْوَٰهِهِمْۖ يُضَٰهُونَ قَوْلَ اَ۬لذِينَ كَفَرُواْ مِن قَبْلُۖ قَٰتَلَهُمُ اُ۬للَّهُۖ أَنَّىٰ يُؤْفَكُونَۖ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1269,10,'191',9,'At-Taubah','التوبَة',12,15,31,'اَ۪تَّخَذُواْ أَحْبَارَهُمْ وَرُهْبَٰنَهُمْ أَرْبَاباٗ مِّن دُونِ اِ۬للَّهِ وَالْمَسِيحَ اَ۪بْنَ مَرْيَمَۖ وَمَا أُمِرُواْ إِلَّا لِيَعْبُدُواْ إِلَٰهاٗ وَٰحِداٗۖ لَّا إِلَٰهَ إِلَّا هُوَۖ سُبْحَٰنَهُۥ عَمَّا يُشْرِكُونَۖ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1270,10,'192',9,'At-Taubah','التوبَة',1,2,32,'يُرِيدُونَ أَنْ يُّطْفِـُٔواْ نُورَ اَ۬للَّهِ بِأَفْوَٰهِهِمْ وَيَأْبَى اَ۬للَّهُ إِلَّا أَنْ يُّتِمَّ نُورَهُۥ وَلَوْ كَرِهَ اَ۬لْكَٰفِرُونَۖ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1271,10,'192',9,'At-Taubah','التوبَة',2,4,33,'هُوَ اَ۬لذِے أَرْسَلَ رَسُولَهُۥ بِالْهُدَىٰ وَدِينِ اِ۬لْحَقِّ لِيُظْهِرَهُۥ عَلَى اَ۬لدِّينِ كُلِّهِۦ وَلَوْ كَرِهَ اَ۬لْمُشْرِكُونَۖ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1272,10,'192',9,'At-Taubah','التوبَة',4,8,34,'۞ يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ إِنَّ كَثِيراٗ مِّنَ اَ۬لْأَحْبَارِ وَالرُّهْبَانِ لَيَأْكُلُونَ أَمْوَٰلَ اَ۬لنَّاسِ بِالْبَٰطِلِ وَيَصُدُّونَ عَن سَبِيلِ اِ۬للَّهِۖ وَالذِينَ يَكْنِزُونَ اَ۬لذَّهَبَ وَالْفِضَّةَ وَلَا يُنفِقُونَهَا فِے سَبِيلِ اِ۬للَّهِ فَبَشِّرْهُم بِعَذَابٍ أَلِيمٖۖ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1273,10,'192',9,'At-Taubah','التوبَة',8,11,35,'يَوْمَ يُحْمَىٰ عَلَيْهَا فِے نَارِ جَهَنَّمَ فَتُكْوَىٰ بِهَا جِبَاهُهُمْ وَجُنُوبُهُمْ وَظُهُورُهُمْ هَٰذَا مَا كَنَزْتُمْ لِأَنفُسِكُمْ فَذُوقُواْ مَا كُنتُمْ تَكْنِزُونَۖ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1274,10,'192',9,'At-Taubah','التوبَة',11,15,36,'إِنَّ عِدَّةَ اَ۬لشُّهُورِ عِندَ اَ۬للَّهِ اِ۪ثْنَا عَشَرَ شَهْراٗ فِے كِتَٰبِ اِ۬للَّهِ يَوْمَ خَلَقَ اَ۬لسَّمَٰوَٰتِ وَالْأَرْضَۖ مِنْهَا أَرْبَعَةٌ حُرُمٞۖ ذَٰلِكَ اَ۬لدِّينُ اُ۬لْقَيِّمُۖ فَلَا تَظْلِمُواْ فِيهِنَّ أَنفُسَكُمْۖ وَقَٰتِلُواْ اُ۬لْمُشْرِكِينَ كَآفَّةٗ كَمَا يُقَٰتِلُونَكُمْ كَآفَّةٗۖ وَاعْلَمُواْ أَنَّ اَ۬للَّهَ مَعَ اَ۬لْمُتَّقِينَۖ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1275,10,'193',9,'At-Taubah','التوبَة',1,4,37,'إِنَّمَا اَ۬لنَّسِےٓءُ زِيَادَةٞ فِے اِ۬لْكُفْرِ يَضِلُّ بِهِ اِ۬لذِينَ كَفَرُواْ يُحِلُّونَهُۥ عَاماٗ وَيُحَرِّمُونَهُۥ عَاماٗ لِّيُوَاطِـُٔواْ عِدَّةَ مَا حَرَّمَ اَ۬للَّهُ فَيُحِلُّواْ مَا حَرَّمَ اَ۬للَّهُۖ زُيِّنَ لَهُمْ سُوٓءُ اَ۬عْمَٰلِهِمْۖ وَاللَّهُ لَا يَهْدِے اِ۬لْقَوْمَ اَ۬لْكَٰفِرِينَۖ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1276,10,'193',9,'At-Taubah','التوبَة',5,8,38,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ مَا لَكُمْ إِذَا قِيلَ لَكُمُ اُ۪نفِرُواْ فِے سَبِيلِ اِ۬للَّهِ اِ۪ثَّاقَلْتُمْ إِلَى اَ۬لْأَرْضِۖ أَرَضِيتُم بِالْحَيَوٰةِ اِ۬لدُّنْيَا مِنَ اَ۬لْأٓخِرَةِۖ فَمَا مَتَٰعُ اُ۬لْحَيَوٰةِ اِ۬لدُّنْيَا فِے اِ۬لْأٓخِرَةِ إِلَّا قَلِيلٌۖ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1277,10,'193',9,'At-Taubah','التوبَة',8,10,39,'إِلَّا تَنفِرُواْ يُعَذِّبْكُمْ عَذَاباً أَلِيماٗ وَيَسْتَبْدِلْ قَوْماً غَيْرَكُمْ وَلَا تَضُرُّوهُ شَئْاٗۖ وَاللَّهُ عَلَىٰ كُلِّ شَےْءٖ قَدِيرٌۖ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1278,10,'193',9,'At-Taubah','التوبَة',10,15,40,'۞ إِلَّا تَنصُرُوهُ فَقَدْ نَصَرَهُ اُ۬للَّهُ إِذْ أَخْرَجَهُ اُ۬لذِينَ كَفَرُواْ ثَانِيَ اَ۪ثْنَيْنِ إِذْ هُمَا فِے اِ۬لْغَارِ إِذْ يَقُولُ لِصَٰحِبِهِۦ لَا تَحْزَنْ إِنَّ اَ۬للَّهَ مَعَنَاۖ فَأَنزَلَ اَ۬للَّهُ سَكِينَتَهُۥ عَلَيْهِ وَأَيَّدَهُۥ بِجُنُودٖ لَّمْ تَرَوْهَاۖ وَجَعَلَ كَلِمَةَ اَ۬لذِينَ كَفَرُواْ اُ۬لسُّفْلَىٰۖ وَكَلِمَةُ اُ۬للَّهِ هِيَ اَ۬لْعُلْيَاۖ وَاللَّهُ عَزِيزٌ حَكِيمٌۖ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1279,10,'194',9,'At-Taubah','التوبَة',1,2,41,'اِ۪نفِرُواْ خِفَافاٗ وَثِقَالاٗ وَجَٰهِدُواْ بِأَمْوَٰلِكُمْ وَأَنفُسِكُمْ فِے سَبِيلِ اِ۬للَّهِۖ ذَٰلِكُمْ خَيْرٞ لَّكُمْ إِن كُنتُمْ تَعْلَمُونَۖ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1280,10,'194',9,'At-Taubah','التوبَة',3,6,42,'لَوْ كَانَ عَرَضاٗ قَرِيباٗ وَسَفَراٗ قَاصِداٗ لَّاتَّبَعُوكَ وَلَٰكِنۢ بَعُدَتْ عَلَيْهِمُ اُ۬لشُّقَّةُۖ وَسَيَحْلِفُونَ بِاللَّهِ لَوِ اِ۪سْتَطَعْنَا لَخَرَجْنَا مَعَكُمْۖ يُهْلِكُونَ أَنفُسَهُمْۖ وَاللَّهُ يَعْلَمُ إِنَّهُمْ لَكَٰذِبُونَۖ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1281,10,'194',9,'At-Taubah','التوبَة',6,7,43,'عَفَا اَ۬للَّهُ عَنكَ لِمَ أَذِنتَ لَهُمْ حَتَّىٰ يَتَبَيَّنَ لَكَ اَ۬لذِينَ صَدَقُواْ وَتَعْلَمَ اَ۬لْكَٰذِبِينَۖ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1282,10,'194',9,'At-Taubah','التوبَة',8,9,44,'لَا يَسْتَٰٔذِنُكَ اَ۬لذِينَ يُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ اِ۬لْأٓخِرِ أَنْ يُّجَٰهِدُواْ بِأَمْوَٰلِهِمْ وَأَنفُسِهِمْۖ وَاللَّهُ عَلِيمُۢ بِالْمُتَّقِينَۖ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1283,10,'194',9,'At-Taubah','التوبَة',10,11,45,'إِنَّمَا يَسْتَٰٔذِنُكَ اَ۬لذِينَ لَا يُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ اِ۬لْأٓخِرِ وَارْتَابَتْ قُلُوبُهُمْ فَهُمْ فِے رَيْبِهِمْ يَتَرَدَّدُونَۖ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1284,10,'194',9,'At-Taubah','التوبَة',11,13,46,'۞ وَلَوْ أَرَادُواْ اُ۬لْخُرُوجَ لَأَعَدُّواْ لَهُۥ عُدَّةٗۖ وَلَٰكِن كَرِهَ اَ۬للَّهُ اُ۪نۢبِعَاثَهُمْ فَثَبَّطَهُمْ وَقِيلَ اَ۟قْعُدُواْ مَعَ اَ۬لْقَٰعِدِينَۖ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1285,10,'194',9,'At-Taubah','التوبَة',13,15,47,'لَوْ خَرَجُواْ فِيكُم مَّا زَادُوكُمْ إِلَّا خَبَالاٗ وَلَأَوْضَعُواْ خِلَٰلَكُمْ يَبْغُونَكُمُ اُ۬لْفِتْنَةَ وَفِيكُمْ سَمَّٰعُونَ لَهُمْۖ وَاللَّهُ عَلِيمُۢ بِالظَّٰلِمِينَۖ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1286,10,'195',9,'At-Taubah','التوبَة',1,2,48,'لَقَدِ اِ۪بْتَغَوُاْ اُ۬لْفِتْنَةَ مِن قَبْلُ وَقَلَّبُواْ لَكَ اَ۬لْأُمُورَ حَتَّىٰ جَآءَ اَ۬لْحَقُّ وَظَهَرَ أَمْرُ اُ۬للَّهِ وَهُمْ كَٰرِهُونَۖ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1287,10,'195',9,'At-Taubah','التوبَة',3,4,49,'وَمِنْهُم مَّنْ يَّقُولُ اُ۪ئْذَن لِّے وَلَا تَفْتِنِّےۖ أَلَا فِے اِ۬لْفِتْنَةِ سَقَطُواْۖ وَإِنَّ جَهَنَّمَ لَمُحِيطَةُۢ بِالْكَٰفِرِينَۖ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1288,10,'195',9,'At-Taubah','التوبَة',4,6,50,'إِن تُصِبْكَ حَسَنَةٞ تَسُؤْهُمْۖ وَإِن تُصِبْكَ مُصِيبَةٞ يَقُولُواْ قَدْ أَخَذْنَا أَمْرَنَا مِن قَبْلُ وَيَتَوَلَّواْ وَّهُمْ فَرِحُونَۖ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1289,10,'195',9,'At-Taubah','التوبَة',6,8,51,'قُل لَّنْ يُّصِيبَنَا إِلَّا مَا كَتَبَ اَ۬للَّهُ لَنَاۖ هُوَ مَوْلَيٰنَاۖ وَعَلَى اَ۬للَّهِ فَلْيَتَوَكَّلِ اِ۬لْمُؤْمِنُونَۖ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1290,10,'195',9,'At-Taubah','التوبَة',8,11,52,'قُلْ هَلْ تَرَبَّصُونَ بِنَا إِلَّا إِحْدَى اَ۬لْحُسْنَيَيْنِۖ وَنَحْنُ نَتَرَبَّصُ بِكُمْ أَنْ يُّصِيبَكُمُ اُ۬للَّهُ بِعَذَابٖ مِّنْ عِندِهِۦ أَوْ بِأَيْدِينَاۖ فَتَرَبَّصُواْۖ إِنَّا مَعَكُم مُّتَرَبِّصُونَۖ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1291,10,'195',9,'At-Taubah','التوبَة',11,12,53,'قُلْ أَنفِقُواْ طَوْعاً أَوْ كَرْهاٗ لَّنْ يُّتَقَبَّلَ مِنكُمْۖ إِنَّكُمْ كُنتُمْ قَوْماٗ فَٰسِقِينَۖ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1292,10,'195',9,'At-Taubah','التوبَة',12,15,54,'وَمَا مَنَعَهُمْ أَن تُقْبَلَ مِنْهُمْ نَفَقَٰتُهُمْ إِلَّا أَنَّهُمْ كَفَرُواْ بِاللَّهِ وَبِرَسُولِهِۦ وَلَا يَأْتُونَ اَ۬لصَّلَوٰةَ إِلَّا وَهُمْ كُسَالَىٰ وَلَا يُنفِقُونَ إِلَّا وَهُمْ كَٰرِهُونَۖ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1293,10,'196',9,'At-Taubah','التوبَة',1,2,55,'۞ فَلَا تُعْجِبْكَ أَمْوَٰلُهُمْ وَلَا أَوْلَٰدُهُمْۖ إِنَّمَا يُرِيدُ اُ۬للَّهُ لِيُعَذِّبَهُم بِهَا فِے اِ۬لْحَيَوٰةِ اِ۬لدُّنْيَا وَتَزْهَقَ أَنفُسُهُمْ وَهُمْ كَٰفِرُونَۖ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1294,10,'196',9,'At-Taubah','التوبَة',3,4,56,'وَيَحْلِفُونَ بِاللَّهِ إِنَّهُمْ لَمِنكُمْ وَمَا هُم مِّنكُمْ وَلَٰكِنَّهُمْ قَوْمٞ يَفْرَقُونَۖ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1295,10,'196',9,'At-Taubah','التوبَة',4,5,57,'لَوْ يَجِدُونَ مَلْجَـٔاً أَوْ مَغَٰرَٰتٍ أَوْ مُدَّخَلاٗ لَّوَلَّوْاْ إِلَيْهِ وَهُمْ يَجْمَحُونَۖ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1296,10,'196',9,'At-Taubah','التوبَة',5,7,58,'وَمِنْهُم مَّنْ يَّلْمِزُكَ فِے اِ۬لصَّدَقَٰتِ فَإِنْ أُعْطُواْ مِنْهَا رَضُواْ وَإِن لَّمْ يُعْطَوْاْ مِنْهَا إِذَا هُمْ يَسْخَطُونَۖ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1297,10,'196',9,'At-Taubah','التوبَة',7,9,59,'وَلَوْ أَنَّهُمْ رَضُواْ مَا ءَاتَيٰهُمُ اُ۬للَّهُ وَرَسُولُهُۥ وَقَالُواْ حَسْبُنَا اَ۬للَّهُ سَيُؤْتِينَا اَ۬للَّهُ مِن فَضْلِهِۦ وَرَسُولُهُۥۖ إِنَّا إِلَى اَ۬للَّهِ رَٰغِبُونَۖ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1298,10,'196',9,'At-Taubah','التوبَة',9,12,60,'إِنَّمَا اَ۬لصَّدَقَٰتُ لِلْفُقَرَآءِ وَالْمَسَٰكِينِ وَالْعَٰمِلِينَ عَلَيْهَا وَالْمُؤَلَّفَةِ قُلُوبُهُمْ وَفِے اِ۬لرِّقَابِ وَالْغَٰرِمِينَ وَفِے سَبِيلِ اِ۬للَّهِ وَابْنِ اِ۬لسَّبِيلِ فَرِيضَةٗ مِّنَ اَ۬للَّهِۖ وَاللَّهُ عَلِيمٌ حَكِيمٞۖ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1299,10,'196',9,'At-Taubah','التوبَة',12,15,61,'۞ وَمِنْهُمُ اُ۬لذِينَ يُؤْذُونَ اَ۬لنَّبِےٓءَ وَيَقُولُونَ هُوَ أُذْنٞۖ قُلْ أُذْنُ خَيْرٖ لَّكُمْ يُؤْمِنُ بِاللَّهِ وَيُؤْمِنُ لِلْمُؤْمِنِينَ وَرَحْمَةٞ لِّلذِينَ ءَامَنُواْ مِنكُمْۖ وَالذِينَ يُؤْذُونَ رَسُولَ اَ۬للَّهِ لَهُمْ عَذَابٌ أَلِيمٞۖ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1300,10,'197',9,'At-Taubah','التوبَة',1,2,62,'يَحْلِفُونَ بِاللَّهِ لَكُمْ لِيُرْضُوكُمْ وَاللَّهُ وَرَسُولُهُۥ أَحَقُّ أَنْ يُّرْضُوهُ إِن كَانُواْ مُؤْمِنِينَۖ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1301,10,'197',9,'At-Taubah','التوبَة',2,4,63,'أَلَمْ يَعْلَمُواْ أَنَّهُۥ مَنْ يُّحَادِدِ اِ۬للَّهَ وَرَسُولَهُۥ فَأَنَّ لَهُۥ نَارَ جَهَنَّمَ خَٰلِداٗ فِيهَاۖ ذَٰلِكَ اَ۬لْخِزْيُ اُ۬لْعَظِيمُۖ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1302,10,'197',9,'At-Taubah','التوبَة',4,6,64,'يَحْذَرُ اُ۬لْمُنَٰفِقُونَ أَن تُنَزَّلَ عَلَيْهِمْ سُورَةٞ تُنَبِّئُهُم بِمَا فِے قُلُوبِهِمْۖ قُلِ اِ۪سْتَهْزِءُواْۖ إِنَّ اَ۬للَّهَ مُخْرِجٞ مَّا تَحْذَرُونَۖ ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1303,10,'197',9,'At-Taubah','التوبَة',6,8,65,'وَلَئِن سَأَلْتَهُمْ لَيَقُولُنَّ إِنَّمَا كُنَّا نَخُوضُ وَنَلْعَبُۖ قُلْ أَبِاللَّهِ وَءَايَٰتِهِۦ وَرَسُولِهِۦ كُنتُمْ تَسْتَهْزِءُونَۖ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1304,10,'197',9,'At-Taubah','التوبَة',8,10,66,'لَا تَعْتَذِرُواْۖ قَدْ كَفَرْتُم بَعْدَ إِيمَٰنِكُمْۖ إِنْ يُّعْفَ عَن طَآئِفَةٖ مِّنكُمْ تُعَذَّبْ طَآئِفَةُۢ بِأَنَّهُمْ كَانُواْ مُجْرِمِينَۖ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1305,10,'197',9,'At-Taubah','التوبَة',10,13,67,'اَ۬لْمُنَٰفِقُونَ وَالْمُنَٰفِقَٰتُ بَعْضُهُم مِّنۢ بَعْضٖ يَأْمُرُونَ بِالْمُنكَرِ وَيَنْهَوْنَ عَنِ اِ۬لْمَعْرُوفِ وَيَقْبِضُونَ أَيْدِيَهُمْ نَسُواْ اُ۬للَّهَ فَنَسِيَهُمْۖ إِنَّ اَ۬لْمُنَٰفِقِينَ هُمُ اُ۬لْفَٰسِقُونَۖ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1306,10,'197',9,'At-Taubah','التوبَة',13,15,68,'وَعَدَ اَ۬للَّهُ اُ۬لْمُنَٰفِقِينَ وَالْمُنَٰفِقَٰتِ وَالْكُفَّارَ نَارَ جَهَنَّمَ خَٰلِدِينَ فِيهَاۖ هِيَ حَسْبُهُمْۖ وَلَعَنَهُمُ اُ۬للَّهُۖ وَلَهُمْ عَذَابٞ مُّقِيمٞۖ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1307,10,'198',9,'At-Taubah','التوبَة',1,5,69,'كَالذِينَ مِن قَبْلِكُمْ كَانُواْ أَشَدَّ مِنكُمْ قُوَّةٗ وَأَكْثَرَ أَمْوَٰلاٗ وَأَوْلَٰداٗ فَاسْتَمْتَعُواْ بِخَلَٰقِهِمْ فَاسْتَمْتَعْتُم بِخَلَٰقِكُمْ كَمَا اَ۪سْتَمْتَعَ اَ۬لذِينَ مِن قَبْلِكُم بِخَلَٰقِهِمْ وَخُضْتُمْ كَالذِے خَاضُواْۖ أُوْلَٰٓئِكَ حَبِطَتْ أَعْمَٰلُهُمْ فِے اِ۬لدُّنْيَا وَالْأٓخِرَةِۖ وَأُوْلَٰٓئِكَ هُمُ اُ۬لْخَٰسِرُونَۖ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1308,10,'198',9,'At-Taubah','التوبَة',5,6,70,'۞ أَلَمْ يَأْتِهِمْ نَبَأُ اُ۬لذِينَ مِن قَبْلِهِمْ قَوْمِ نُوحٖ وَعَادٖ وَثَمُودَ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1309,10,'198',9,'At-Taubah','التوبَة',6,9,71,'وَقَوْمِ إِبْرَٰهِيمَ وَأَصْحَٰبِ مَدْيَنَ وَالْمُؤْتَفِكَٰتِۖ أَتَتْهُمْ رُسُلُهُم بِالْبَيِّنَٰتِۖ فَمَا كَانَ اَ۬للَّهُ لِيَظْلِمَهُمْۖ وَلَٰكِن كَانُواْ أَنفُسَهُمْ يَظْلِمُونَۖ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1310,10,'198',9,'At-Taubah','التوبَة',9,12,72,'وَالْمُؤْمِنُونَ وَالْمُؤْمِنَٰتُ بَعْضُهُمْ أَوْلِيَآءُ بَعْضٖۖ يَأْمُرُونَ بِالْمَعْرُوفِ وَيَنْهَوْنَ عَنِ اِ۬لْمُنكَرِ وَيُقِيمُونَ اَ۬لصَّلَوٰةَ وَيُؤْتُونَ اَ۬لزَّكَوٰةَ وَيُطِيعُونَ اَ۬للَّهَ وَرَسُولَهُۥۖ أُوْلَٰٓئِكَ سَيَرْحَمُهُمُ اُ۬للَّهُۖ إِنَّ اَ۬للَّهَ عَزِيزٌ حَكِيمٞۖ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1311,10,'198',9,'At-Taubah','التوبَة',13,15,73,'وَعَدَ اَ۬للَّهُ اُ۬لْمُؤْمِنِينَ وَالْمُؤْمِنَٰتِ جَنَّٰتٖ تَجْرِے مِن تَحْتِهَا اَ۬لْأَنْهَٰرُ خَٰلِدِينَ فِيهَا وَمَسَٰكِنَ طَيِّبَةٗ فِے جَنَّٰتِ عَدْنٖۖ وَرِضْوَٰنٞ مِّنَ اَ۬للَّهِ أَكْبَرُۖ ذَٰلِكَ هُوَ اَ۬لْفَوْزُ اُ۬لْعَظِيمُۖ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1312,10,'199',9,'At-Taubah','التوبَة',1,2,74,'يَٰأَيُّهَا‏ اَ۬لنَّبِےٓءُ جَٰهِدِ اِ۬لْكُفَّارَ وَالْمُنَٰفِقِينَ وَاغْلُظْ عَلَيْهِمْۖ وَمَأْوَيٰهُمْ جَهَنَّمُۖ وَبِئْسَ اَ۬لْمَصِيرُۖ ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1313,10,'199',9,'At-Taubah','التوبَة',2,7,75,'يَحْلِفُونَ بِاللَّهِ مَا قَالُواْۖ وَلَقَدْ قَالُواْ كَلِمَةَ اَ۬لْكُفْرِ وَكَفَرُواْ بَعْدَ إِسْلَٰمِهِمْ وَهَمُّواْ بِمَا لَمْ يَنَالُواْۖ وَمَا نَقَمُواْ إِلَّا أَنْ أَغْنَيٰهُمُ اُ۬للَّهُ وَرَسُولُهُۥ مِن فَضْلِهِۦۖ فَإِنْ يَّتُوبُواْ يَكُ خَيْراٗ لَّهُمْۖ وَإِنْ يَّتَوَلَّوْاْ يُعَذِّبْهُمُ اُ۬للَّهُ عَذَاباً أَلِيماٗ فِے اِ۬لدُّنْيَا وَالْأٓخِرَةِۖ وَمَا لَهُمْ فِے اِ۬لْأَرْضِ مِنْ وَّلِيّٖ وَلَا نَصِيرٖۖ ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1314,10,'199',9,'At-Taubah','التوبَة',7,8,76,'۞ وَمِنْهُم مَّنْ عَٰهَدَ اَ۬للَّهَ لَئِنْ ءَاتَيٰنَا مِن فَضْلِهِۦ لَنَصَّدَّقَنَّ وَلَنَكُونَنَّ مِنَ اَ۬لصَّٰلِحِينَۖ ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1315,10,'199',9,'At-Taubah','التوبَة',9,10,77,'فَلَمَّا ءَاتَيٰهُم مِّن فَضْلِهِۦ بَخِلُواْ بِهِۦ وَتَوَلَّواْ وَّهُم مُّعْرِضُونَۖ ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1316,10,'199',9,'At-Taubah','التوبَة',10,11,78,'فَأَعْقَبَهُمْ نِفَاقاٗ فِے قُلُوبِهِمْ إِلَىٰ يَوْمِ يَلْقَوْنَهُۥ بِمَا أَخْلَفُواْ اُ۬للَّهَ مَا وَعَدُوهُ وَبِمَا كَانُواْ يَكْذِبُونَۖ ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1317,10,'199',9,'At-Taubah','التوبَة',12,13,79,'أَلَمْ يَعْلَمُواْ أَنَّ اَ۬للَّهَ يَعْلَمُ سِرَّهُمْ وَنَجْوَيٰهُمْ وَأَنَّ اَ۬للَّهَ عَلَّٰمُ اُ۬لْغُيُوبِۖ ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1318,10,'199',9,'At-Taubah','التوبَة',13,15,80,'اِ۬لذِينَ يَلْمِزُونَ اَ۬لْمُطَّوِّعِينَ مِنَ اَ۬لْمُؤْمِنِينَ فِے اِ۬لصَّدَقَٰتِ وَالذِينَ لَا يَجِدُونَ إِلَّا جُهْدَهُمْ فَيَسْخَرُونَ مِنْهُمْ سَخِرَ اَ۬للَّهُ مِنْهُمْۖ وَلَهُمْ عَذَابٌ أَلِيمٌۖ ٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1319,10,'200',9,'At-Taubah','التوبَة',1,3,81,'اِ۪سْتَغْفِرْ لَهُمْ أَوْ لَا تَسْتَغْفِرْ لَهُمْۖ إِن تَسْتَغْفِرْ لَهُمْ سَبْعِينَ مَرَّةٗ فَلَنْ يَّغْفِرَ اَ۬للَّهُ لَهُمْۖ ذَٰلِكَ بِأَنَّهُمْ كَفَرُواْ بِاللَّهِ وَرَسُولِهِۦۖ وَاللَّهُ لَا يَهْدِے اِ۬لْقَوْمَ اَ۬لْفَٰسِقِينَۖ ٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1320,10,'200',9,'At-Taubah','التوبَة',3,6,82,'فَرِحَ اَ۬لْمُخَلَّفُونَ بِمَقْعَدِهِمْ خِلَٰفَ رَسُولِ اِ۬للَّهِ وَكَرِهُواْ أَنْ يُّجَٰهِدُواْ بِأَمْوَٰلِهِمْ وَأَنفُسِهِمْ فِے سَبِيلِ اِ۬للَّهِ وَقَالُواْ لَا تَنفِرُواْ فِے اِ۬لْحَرِّۖ قُلْ نَارُ جَهَنَّمَ أَشَدُّ حَرّاٗ لَّوْ كَانُواْ يَفْقَهُونَۖ ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1321,10,'200',9,'At-Taubah','التوبَة',6,7,83,'فَلْيَضْحَكُواْ قَلِيلاٗ وَلْيَبْكُواْ كَثِيراٗ جَزَآءَۢ بِمَا كَانُواْ يَكْسِبُونَۖ ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1322,10,'200',9,'At-Taubah','التوبَة',7,10,84,'فَإِن رَّجَعَكَ اَ۬للَّهُ إِلَىٰ طَآئِفَةٖ مِّنْهُمْ فَاسْتَٰٔذَنُوكَ لِلْخُرُوجِ فَقُل لَّن تَخْرُجُواْ مَعِيَ أَبَداٗ وَلَن تُقَٰتِلُواْ مَعِے عَدُوّاًۖ إِنَّكُمْ رَضِيتُم بِالْقُعُودِ أَوَّلَ مَرَّةٖۖ فَاقْعُدُواْ مَعَ اَ۬لْخَٰلِفِينَۖ ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1323,10,'200',9,'At-Taubah','التوبَة',10,11,85,'وَلَا تُصَلِّ عَلَىٰ أَحَدٖ مِّنْهُم مَّاتَ أَبَداٗ وَلَا تَقُمْ عَلَىٰ قَبْرِهِۦۖ إِنَّهُمْ كَفَرُواْ بِاللَّهِ وَرَسُولِهِۦ وَمَاتُواْ وَهُمْ فَٰسِقُونَۖ ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1324,10,'200',9,'At-Taubah','التوبَة',12,13,86,'۞ وَلَا تُعْجِبْكَ أَمْوَٰلُهُمْ وَأَوْلَٰدُهُمْۖ إِنَّمَا يُرِيدُ اُ۬للَّهُ أَنْ يُّعَذِّبَهُم بِهَا فِے اِ۬لدُّنْيَا وَتَزْهَقَ أَنفُسُهُمْ وَهُمْ كَٰفِرُونَۖ ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1325,10,'200',9,'At-Taubah','التوبَة',13,15,87,'وَإِذَا أُنزِلَتْ سُورَةٌ أَنْ ءَامِنُواْ بِاللَّهِ وَجَٰهِدُواْ مَعَ رَسُولِهِ اِ۪سْتَٰٔذَنَكَ أُوْلُواْ اُ۬لطَّوْلِ مِنْهُمْ وَقَالُواْ ذَرْنَا نَكُن مَّعَ اَ۬لْقَٰعِدِينَۖ ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1326,10,'201',9,'At-Taubah','التوبَة',1,2,88,'رَضُواْ بِأَنْ يَّكُونُواْ مَعَ اَ۬لْخَوَالِفِۖ وَطُبِعَ عَلَىٰ قُلُوبِهِمْ فَهُمْ لَا يَفْقَهُونَۖ ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1327,10,'201',9,'At-Taubah','التوبَة',2,4,89,'لَٰكِنِ اِ۬لرَّسُولُ وَالذِينَ ءَامَنُواْ مَعَهُۥ جَٰهَدُواْ بِأَمْوَٰلِهِمْ وَأَنفُسِهِمْۖ وَأُوْلَٰٓئِكَ لَهُمُ اُ۬لْخَيْرَٰتُۖ وَأُوْلَٰٓئِكَ هُمُ اُ۬لْمُفْلِحُونَۖ ٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1328,10,'201',9,'At-Taubah','التوبَة',4,5,90,'أَعَدَّ اَ۬للَّهُ لَهُمْ جَنَّٰتٖ تَجْرِے مِن تَحْتِهَا اَ۬لْأَنْهَٰرُ خَٰلِدِينَ فِيهَاۖ ذَٰلِكَ اَ۬لْفَوْزُ اُ۬لْعَظِيمُۖ ٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1329,10,'201',9,'At-Taubah','التوبَة',6,8,91,'وَجَآءَ اَ۬لْمُعَذِّرُونَ مِنَ اَ۬لْأَعْرَابِ لِيُؤْذَنَ لَهُمْ وَقَعَدَ اَ۬لذِينَ كَذَبُواْ اُ۬للَّهَ وَرَسُولَهُۥۖ سَيُصِيبُ اُ۬لذِينَ كَفَرُواْ مِنْهُمْ عَذَابٌ أَلِيمٞۖ ٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1330,10,'201',9,'At-Taubah','التوبَة',8,10,92,'لَّيْسَ عَلَى اَ۬لضُّعَفَآءِ وَلَا عَلَى اَ۬لْمَرْضَىٰ وَلَا عَلَى اَ۬لذِينَ لَا يَجِدُونَ مَا يُنفِقُونَ حَرَجٌ إِذَا نَصَحُواْ لِلهِ وَرَسُولِهِۦۖ مَا عَلَى اَ۬لْمُحْسِنِينَ مِن سَبِيلٖۖ وَاللَّهُ غَفُورٞ رَّحِيمٞۖ ٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1331,10,'201',9,'At-Taubah','التوبَة',11,13,93,'وَلَا عَلَى اَ۬لذِينَ إِذَا مَا أَتَوْكَ لِتَحْمِلَهُمْ قُلْتَ لَا أَجِدُ مَا أَحْمِلُكُمْ عَلَيْهِۖ تَوَلَّواْ وَّأَعْيُنُهُمْ تَفِيضُ مِنَ اَ۬لدَّمْعِ حَزَناً أَلَّا يَجِدُواْ مَا يُنفِقُونَۖ ٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1332,11,'201',9,'At-Taubah','التوبَة',13,15,94,'۞ إِنَّمَا اَ۬لسَّبِيلُ عَلَى اَ۬لذِينَ يَسْتَٰٔذِنُونَكَ وَهُمْ أَغْنِيَآءُۖ رَضُواْ بِأَنْ يَّكُونُواْ مَعَ اَ۬لْخَوَالِفِۖ وَطَبَعَ اَ۬للَّهُ عَلَىٰ قُلُوبِهِمْ فَهُمْ لَا يَعْلَمُونَۖ ٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1333,11,'202',9,'At-Taubah','التوبَة',1,4,95,'يَعْتَذِرُونَ إِلَيْكُمْ إِذَا رَجَعْتُمْ إِلَيْهِمْۖ قُل لَّا تَعْتَذِرُواْ لَن نُّؤْمِنَ لَكُمْۖ قَدْ نَبَّأَنَا اَ۬للَّهُ مِنْ أَخْبَارِكُمْۖ وَسَيَرَى اَ۬للَّهُ عَمَلَكُمْ وَرَسُولُهُۥۖ ثُمَّ تُرَدُّونَ إِلَىٰ عَٰلِمِ اِ۬لْغَيْبِ وَالشَّهَٰدَةِ فَيُنَبِّئُكُم بِمَا كُنتُمْ تَعْمَلُونَۖ ٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1334,11,'202',9,'At-Taubah','التوبَة',4,7,96,'سَيَحْلِفُونَ بِاللَّهِ لَكُمْ إِذَا اَ۪نقَلَبْتُمْ إِلَيْهِمْ لِتُعْرِضُواْ عَنْهُمْۖ فَأَعْرِضُواْ عَنْهُمْۖ إِنَّهُمْ رِجْسٞۖ وَمَأْوَيٰهُمْ جَهَنَّمُۖ جَزَآءَۢ بِمَا كَانُواْ يَكْسِبُونَۖ ٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1335,11,'202',9,'At-Taubah','التوبَة',7,8,97,'يَحْلِفُونَ لَكُمْ لِتَرْضَوْاْ عَنْهُمْ فَإِن تَرْضَوْاْ عَنْهُمْ فَإِنَّ اَ۬للَّهَ لَا يَرْضَىٰ عَنِ اِ۬لْقَوْمِ اِ۬لْفَٰسِقِينَۖ ٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1336,11,'202',9,'At-Taubah','التوبَة',9,10,98,'اَ۬لْأَعْرَابُ أَشَدُّ كُفْراٗ وَنِفَاقاٗ وَأَجْدَرُ أَلَّا يَعْلَمُواْ حُدُودَ مَا أَنزَلَ اَ۬للَّهُ عَلَىٰ رَسُولِهِۦۖ وَاللَّهُ عَلِيمٌ حَكِيمٞۖ ٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1337,11,'202',9,'At-Taubah','التوبَة',10,12,99,'وَمِنَ اَ۬لْأَعْرَابِ مَنْ يَّتَّخِذُ مَا يُنفِقُ مَغْرَماٗ وَيَتَرَبَّصُ بِكُمُ اُ۬لدَّوَآئِرَۖ عَلَيْهِمْ دَآئِرَةُ اُ۬لسَّوْءِۖ وَاللَّهُ سَمِيعٌ عَلِيمٞۖ ٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1338,11,'202',9,'At-Taubah','التوبَة',12,15,100,'وَمِنَ اَ۬لْأَعْرَابِ مَنْ يُّؤْمِنُ بِاللَّهِ وَالْيَوْمِ اِ۬لْأٓخِرِ وَيَتَّخِذُ مَا يُنفِقُ قُرُبَٰتٍ عِندَ اَ۬للَّهِ وَصَلَوَٰتِ اِ۬لرَّسُولِۖ أَلَا إِنَّهَا قُرْبَةٞ لَّهُمْۖ سَيُدْخِلُهُمُ اُ۬للَّهُ فِے رَحْمَتِهِۦۖ إِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٞۖ ١٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1339,11,'203',9,'At-Taubah','التوبَة',1,4,101,'وَالسَّٰبِقُونَ اَ۬لْأَوَّلُونَ مِنَ اَ۬لْمُهَٰجِرِينَ وَالْأَنصَارِ وَالذِينَ اَ۪تَّبَعُوهُم بِإِحْسَٰنٖ رَّضِيَ اَ۬للَّهُ عَنْهُمْ وَرَضُواْ عَنْهُۖ وَأَعَدَّ لَهُمْ جَنَّٰتٖ تَجْرِے تَحْتَهَا اَ۬لْأَنْهَٰرُ خَٰلِدِينَ فِيهَا أَبَداٗۖ ذَٰلِكَ اَ۬لْفَوْزُ اُ۬لْعَظِيمُۖ ١٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1340,11,'203',9,'At-Taubah','التوبَة',4,6,102,'۞ وَمِمَّنْ حَوْلَكُم مِّنَ اَ۬لْأَعْرَابِ مُنَٰفِقُونَۖ وَمِنْ أَهْلِ اِ۬لْمَدِينَةِ مَرَدُواْ عَلَى اَ۬لنِّفَاقِ لَا تَعْلَمُهُمْۖ نَحْنُ نَعْلَمُهُمْۖ سَنُعَذِّبُهُم مَّرَّتَيْنِ ثُمَّ يُرَدُّونَ إِلَىٰ عَذَابٍ عَظِيمٖۖ ١٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1341,11,'203',9,'At-Taubah','التوبَة',7,8,103,'وَءَاخَرُونَ اَ۪عْتَرَفُواْ بِذُنُوبِهِمْ خَلَطُواْ عَمَلاٗ صَٰلِحاٗ وَءَاخَرَ سَيِّئاً عَسَى اَ۬للَّهُ أَنْ يَّتُوبَ عَلَيْهِمْۖ إِنَّ اَ۬للَّهَ غَفُورٞ رَّحِيمٌۖ ١٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1342,11,'203',9,'At-Taubah','التوبَة',9,10,104,'خُذْ مِنْ أَمْوَٰلِهِمْ صَدَقَةٗ تُطَهِّرُهُمْ وَتُزَكِّيهِم بِهَا وَصَلِّ عَلَيْهِمْۖ إِنَّ صَلَوَٰتِكَ سَكَنٞ لَّهُمْۖ وَاللَّهُ سَمِيعٌ عَلِيمٌۖ ١٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1343,11,'203',9,'At-Taubah','التوبَة',10,12,105,'أَلَمْ يَعْلَمُواْ أَنَّ اَ۬للَّهَ هُوَ يَقْبَلُ اُ۬لتَّوْبَةَ عَنْ عِبَادِهِۦ وَيَأْخُذُ اُ۬لصَّدَقَٰتِ وَأَنَّ اَ۬للَّهَ هُوَ اَ۬لتَّوَّابُ اُ۬لرَّحِيمُۖ ١٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1344,11,'203',9,'At-Taubah','التوبَة',12,14,106,'وَقُلِ اِ۪عْمَلُواْ فَسَيَرَى اَ۬للَّهُ عَمَلَكُمْ وَرَسُولُهُۥ وَالْمُؤْمِنُونَۖ وَسَتُرَدُّونَ إِلَىٰ عَٰلِمِ اِ۬لْغَيْبِ وَالشَّهَٰدَةِ فَيُنَبِّئُكُم بِمَا كُنتُمْ تَعْمَلُونَۖ ١٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1345,11,'203',9,'At-Taubah','التوبَة',14,15,107,'وَءَاخَرُونَ مُرْجَوْنَ لِأَمْرِ اِ۬للَّهِ إِمَّا يُعَذِّبُهُمْ وَإِمَّا يَتُوبُ عَلَيْهِمْۖ وَاللَّهُ عَلِيمٌ حَكِيمٌۖ ١٠٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1346,11,'204',9,'At-Taubah','التوبَة',1,4,108,'اِ۬لذِينَ اَ۪تَّخَذُواْ مَسْجِداٗ ضِرَاراٗ وَكُفْراٗ وَتَفْرِيقاَۢ بَيْنَ اَ۬لْمُؤْمِنِينَ وَإِرْصَاداٗ لِّمَنْ حَارَبَ اَ۬للَّهَ وَرَسُولَهُۥ مِن قَبْلُۖ وَلَيَحْلِفُنَّ إِنْ أَرَدْنَا إِلَّا اَ۬لْحُسْنَىٰۖ وَاللَّهُ يَشْهَدُ إِنَّهُمْ لَكَٰذِبُونَۖ ١٠٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1347,11,'204',9,'At-Taubah','التوبَة',4,6,109,'لَا تَقُمْ فِيهِ أَبَداٗۖ لَّمَسْجِدٌ أُسِّسَ عَلَى اَ۬لتَّقْوَىٰ مِنْ أَوَّلِ يَوْمٍ أَحَقُّ أَن تَقُومَ فِيهِۖ فِيهِ رِجَالٞ يُحِبُّونَ أَنْ يَّتَطَهَّرُواْۖ وَاللَّهُ يُحِبُّ اُ۬لْمُطَّهِّرِينَۖ ١٠٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1348,11,'204',9,'At-Taubah','التوبَة',6,9,110,'أَفَمَنْ أُسِّسَ بُنْيَٰنُهُۥ عَلَىٰ تَقْوَىٰ مِنَ اَ۬للَّهِ وَرِضْوَٰنٍ خَيْرٌ أَم مَّنْ أُسِّسَ بُنْيَٰنُهُۥ عَلَىٰ شَفَا جُرُفٍ ه۪ارٖ فَانْهَارَ بِهِۦ فِے نَارِ جَهَنَّمَۖ وَاللَّهُ لَا يَهْدِے اِ۬لْقَوْمَ اَ۬لظَّٰلِمِينَۖ ١١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1349,11,'204',9,'At-Taubah','التوبَة',9,10,111,'لَا يَزَالُ بُنْيَٰنُهُمُ اُ۬لذِے بَنَوْاْ رِيبَةٗ فِے قُلُوبِهِمْ إِلَّا أَن تُقَطَّعَ قُلُوبُهُمْۖ وَاللَّهُ عَلِيمٌ حَكِيمٌۖ ١١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1350,11,'204',9,'At-Taubah','التوبَة',11,15,112,'۞ إِنَّ اَ۬للَّهَ اَ۪شْتَرَىٰ مِنَ اَ۬لْمُؤْمِنِينَ أَنفُسَهُمْ وَأَمْوَٰلَهُم بِأَنَّ لَهُمُ اُ۬لْجَنَّةَۖ يُقَٰتِلُونَ فِے سَبِيلِ اِ۬للَّهِ فَيَقْتُلُونَ وَيُقْتَلُونَ وَعْداً عَلَيْهِ حَقّاٗ فِے اِ۬لتَّوْرَيٰةِ وَالْإِنجِيلِ وَالْقُرْءَانِۖ وَمَنْ أَوْفَىٰ بِعَهْدِهِۦ مِنَ اَ۬للَّهِۖ فَاسْتَبْشِرُواْ بِبَيْعِكُمُ اُ۬لذِے بَايَعْتُم بِهِۦۖ وَذَٰلِكَ هُوَ اَ۬لْفَوْزُ اُ۬لْعَظِيمُۖ ١١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1351,11,'205',9,'At-Taubah','التوبَة',1,3,113,'اُ۬لتَّٰٓئِبُونَ اَ۬لْعَٰبِدُونَ اَ۬لْحَٰمِدُونَ اَ۬لسَّٰٓئِحُونَ اَ۬لرَّٰكِعُونَ اَ۬لسَّٰجِدُونَ اَ۬لْأٓمِرُونَ بِالْمَعْرُوفِ وَالنَّاهُونَ عَنِ اِ۬لْمُنكَرِ وَالْحَٰفِظُونَ لِحُدُودِ اِ۬للَّهِۖ وَبَشِّرِ اِ۬لْمُؤْمِنِينَۖ ١١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1352,11,'205',9,'At-Taubah','التوبَة',3,6,114,'مَا كَانَ لِلنَّبِےٓءِ وَالذِينَ ءَامَنُواْ أَنْ يَّسْتَغْفِرُواْ لِلْمُشْرِكِينَ وَلَوْ كَانُواْ أُوْلِے قُرْبَىٰ مِنۢ بَعْدِ مَا تَبَيَّنَ لَهُمْ أَنَّهُمْ أَصْحَٰبُ اُ۬لْجَحِيمِۖ ١١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1353,11,'205',9,'At-Taubah','التوبَة',6,9,115,'وَمَا كَانَ اَ۪سْتِغْفَارُ إِبْرَٰهِيمَ لِأَبِيهِ إِلَّا عَن مَّوْعِدَةٖ وَعَدَهَا إِيَّاهُۖ فَلَمَّا تَبَيَّنَ لَهُۥ أَنَّهُۥ عَدُوّٞ لِّلهِ تَبَرَّأَ مِنْهُۖ إِنَّ إِبْرَٰهِيمَ لَأَوَّٰهٌ حَلِيمٞۖ ١١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1354,11,'205',9,'At-Taubah','التوبَة',9,11,116,'وَمَا كَانَ اَ۬للَّهُ لِيُضِلَّ قَوْماَۢ بَعْدَ إِذْ هَدَيٰهُمْ حَتَّىٰ يُبَيِّنَ لَهُم مَّا يَتَّقُونَۖ إِنَّ اَ۬للَّهَ بِكُلِّ شَےْءٍ عَلِيمٌۖ ١١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1355,11,'205',9,'At-Taubah','التوبَة',11,12,117,'إِنَّ اَ۬للَّهَ لَهُۥ مُلْكُ اُ۬لسَّمَٰوَٰتِ وَالْأَرْضِۖ يُحْيِۦ وَيُمِيتُۖ وَمَا لَكُم مِّن دُونِ اِ۬للَّهِ مِنْ وَّلِيّٖ وَلَا نَصِيرٖۖ ١١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1356,11,'205',9,'At-Taubah','التوبَة',13,15,118,'۞ لَّقَد تَّابَ اَ۬للَّهُ عَلَى اَ۬لنَّبِےٓءِ وَالْمُهَٰجِرِينَ وَالْأَنصَارِ اِ۬لذِينَ اَ۪تَّبَعُوهُ فِے سَاعَةِ اِ۬لْعُسْرَةِ مِنۢ بَعْدِ مَا كَادَ تَزِيغُ قُلُوبُ فَرِيقٖ مِّنْهُمْ ثُمَّ تَابَ عَلَيْهِمْۖ إِنَّهُۥ بِهِمْ رَءُوفٞ رَّحِيمٞ ١١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1357,11,'206',9,'At-Taubah','التوبَة',1,4,119,'وَعَلَى اَ۬لثَّلَٰثَةِ اِ۬لذِينَ خُلِّفُواْۖ حَتَّىٰ إِذَا ضَاقَتْ عَلَيْهِمُ اُ۬لْأَرْضُ بِمَا رَحُبَتْ وَضَاقَتْ عَلَيْهِمْ أَنفُسُهُمْ وَظَنُّواْ أَن لَّا مَلْجَأَ مِنَ اَ۬للَّهِ إِلَّا إِلَيْهِ ثُمَّ تَابَ عَلَيْهِمْ لِيَتُوبُواْۖ إِنَّ اَ۬للَّهَ هُوَ اَ۬لتَّوَّابُ اُ۬لرَّحِيمُۖ ١١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1358,11,'206',9,'At-Taubah','التوبَة',4,5,120,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ اُ۪تَّقُواْ اُ۬للَّهَ وَكُونُواْ مَعَ اَ۬لصَّٰدِقِينَۖ ١٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1359,11,'206',9,'At-Taubah','التوبَة',5,10,121,'مَا كَانَ لِأَهْلِ اِ۬لْمَدِينَةِ وَمَنْ حَوْلَهُم مِّنَ اَ۬لْأَعْرَابِ أَنْ يَّتَخَلَّفُواْ عَن رَّسُولِ اِ۬للَّهِ وَلَا يَرْغَبُواْ بِأَنفُسِهِمْ عَن نَّفْسِهِۦۖ ذَٰلِكَ بِأَنَّهُمْ لَا يُصِيبُهُمْ ظَمَأٞ وَلَا نَصَبٞ وَلَا مَخْمَصَةٞ فِے سَبِيلِ اِ۬للَّهِ وَلَا يَطَـُٔونَ مَوْطِئاٗ يَغِيظُ اُ۬لْكُفَّارَ وَلَا يَنَالُونَ مِنْ عَدُوّٖ نَّيْلاً إِلَّا كُتِبَ لَهُم بِهِۦ عَمَلٞ صَٰلِحٌۖ إِنَّ اَ۬للَّهَ لَا يُضِيعُ أَجْرَ اَ۬لْمُحْسِنِينَۖ ١٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1360,11,'206',9,'At-Taubah','التوبَة',11,13,122,'وَلَا يُنفِقُونَ نَفَقَةٗ صَغِيرَةٗ وَلَا كَبِيرَةٗ وَلَا يَقْطَعُونَ وَادِياً إِلَّا كُتِبَ لَهُمْۖ لِيَجْزِيَهُمُ اُ۬للَّهُ أَحْسَنَ مَا كَانُواْ يَعْمَلُونَۖ ١٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1361,11,'206',9,'At-Taubah','التوبَة',13,15,123,'وَمَا كَانَ اَ۬لْمُؤْمِنُونَ لِيَنفِرُواْ كَآفَّةٗۖ فَلَوْلَا نَفَرَ مِن كُلِّ فِرْقَةٖ مِّنْهُمْ طَآئِفَةٞ لِّيَتَفَقَّهُواْ فِے اِ۬لدِّينِ وَلِيُنذِرُواْ قَوْمَهُمْ إِذَا رَجَعُواْ إِلَيْهِمْ لَعَلَّهُمْ يَحْذَرُونَۖ ١٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1362,11,'207',9,'At-Taubah','التوبَة',1,3,124,'۞ يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ قَٰتِلُواْ اُ۬لذِينَ يَلُونَكُم مِّنَ اَ۬لْكُفَّارِ وَلْيَجِدُواْ فِيكُمْ غِلْظَةٗۖ وَاعْلَمُواْ أَنَّ اَ۬للَّهَ مَعَ اَ۬لْمُتَّقِينَۖ ١٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1363,11,'207',9,'At-Taubah','التوبَة',3,5,125,'وَإِذَا مَا أُنزِلَتْ سُورَةٞ فَمِنْهُم مَّنْ يَّقُولُ أَيُّكُمْ زَادَتْهُ هَٰذِهِۦ إِيمَٰناٗۖ فَأَمَّا اَ۬لذِينَ ءَامَنُواْ فَزَادَتْهُمْ إِيمَٰناٗ وَهُمْ يَسْتَبْشِرُونَۖ ١٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1364,11,'207',9,'At-Taubah','التوبَة',5,7,126,'وَأَمَّا اَ۬لذِينَ فِے قُلُوبِهِم مَّرَضٞ فَزَادَتْهُمْ رِجْساً إِلَىٰ رِجْسِهِمْ وَمَاتُواْ وَهُمْ كَٰفِرُونَۖ ١٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1365,11,'207',9,'At-Taubah','التوبَة',7,9,127,'أَوَلَا يَرَوْنَ أَنَّهُمْ يُفْتَنُونَ فِے كُلِّ عَامٖ مَّرَّةً أَوْ مَرَّتَيْنِ ثُمَّ لَا يَتُوبُونَ وَلَا هُمْ يَذَّكَّرُونَۖ ١٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1366,11,'207',9,'At-Taubah','التوبَة',9,11,128,'وَإِذَا مَا أُنزِلَتْ سُورَةٞ نَّظَرَ بَعْضُهُمْ إِلَىٰ بَعْضٍ هَلْ يَرَيٰكُم مِّنْ أَحَدٖ ثُمَّ اَ۪نصَرَفُواْۖ صَرَفَ اَ۬للَّهُ قُلُوبَهُم بِأَنَّهُمْ قَوْمٞ لَّا يَفْقَهُونَۖ ١٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1367,11,'207',9,'At-Taubah','التوبَة',11,14,129,'لَقَدْ جَآءَكُمْ رَسُولٞ مِّنْ أَنفُسِكُمْ عَزِيزٌ عَلَيْهِ مَا عَنِتُّمْۖ حَرِيصٌ عَلَيْكُمۖ بِالْمُؤْمِنِينَ رَءُوفٞ رَّحِيمٞۖ ١٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1368,11,'207',9,'At-Taubah','التوبَة',14,15,130,'فَإِن تَوَلَّوْاْ فَقُلْ حَسْبِيَ اَ۬للَّهُۖ لَا إِلَٰهَ إِلَّا هُوَۖ عَلَيْهِ تَوَكَّلْتُۖ وَهْوَ رَبُّ اُ۬لْعَرْشِ اِ۬لْعَظِيمِۖ ١٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1369,11,'208',10,'Yūnus','يُونس',3,3,1,'أَلَٓرَۖ تِلْكَ ءَايَٰتُ اُ۬لْكِتَٰبِ اِ۬لْحَكِيمِۖ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1370,11,'208',10,'Yūnus','يُونس',3,6,2,'أَكَانَ لِلنَّاسِ عَجَباً أَنْ أَوْحَيْنَا إِلَىٰ رَجُلٖ مِّنْهُمْ أَنْ أَنذِرِ اِ۬لنَّاسَۖ وَبَشِّرِ اِ۬لذِينَ ءَامَنُواْ أَنَّ لَهُمْ قَدَمَ صِدْقٍ عِندَ رَبِّهِمْۖ قَالَ اَ۬لْكَٰفِرُونَ إِنَّ هَٰذَا لَسِحْرٞ مُّبِينٌۖ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1371,11,'208',10,'Yūnus','يُونس',6,8,3,'۞ إِنَّ رَبَّكُمُ اُ۬للَّهُ اُ۬لذِے خَلَقَ اَ۬لسَّمَٰوَٰتِ وَالْأَرْضَ فِے سِتَّةِ أَيَّامٖ ثُمَّ اَ۪سْتَوَىٰ عَلَى اَ۬لْعَرْشِۖ يُدَبِّرُ اُ۬لْأَمْرَۖ مَا مِن شَفِيعٍ إِلَّا مِنۢ بَعْدِ إِذْنِهِۦۖ ذَٰلِكُمُ اُ۬للَّهُ رَبُّكُمْۖ فَاعْبُدُوهُۖ أَفَلَا تَذَّكَّرُونَۖ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1372,11,'208',10,'Yūnus','يُونس',9,12,4,'إِلَيْهِ مَرْجِعُكُمْ جَمِيعاٗۖ وَعْدَ اَ۬للَّهِ حَقّاًۖ إِنَّهُۥ يَبْدَؤُاْ اُ۬لْخَلْقَ ثُمَّ يُعِيدُهُۥ لِيَجْزِيَ اَ۬لذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ بِالْقِسْطِۖ وَالذِينَ كَفَرُواْ لَهُمْ شَرَابٞ مِّنْ حَمِيمٖ وَعَذَابٌ أَلِيمُۢ بِمَا كَانُواْ يَكْفُرُونَۖ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1373,11,'208',10,'Yūnus','يُونس',12,14,5,'هُوَ اَ۬لذِے جَعَلَ اَ۬لشَّمْسَ ضِيَآءٗ وَالْقَمَرَ نُوراٗ وَقَدَّرَهُۥ مَنَازِلَ لِتَعْلَمُواْ عَدَدَ اَ۬لسِّنِينَ وَالْحِسَابَۖ مَا خَلَقَ اَ۬للَّهُ ذَٰلِكَ إِلَّا بِالْحَقِّۖ نُفَصِّلُ اُ۬لْأٓيَٰتِ لِقَوْمٖ يَعْلَمُونَۖ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1374,11,'208',10,'Yūnus','يُونس',14,15,6,'إِنَّ فِے اِ۪خْتِلَٰفِ اِ۬ليْلِ وَالنَّهَارِ وَمَا خَلَقَ اَ۬للَّهُ فِے اِ۬لسَّمَٰوَٰتِ وَالْأَرْضِ لَأٓيَٰتٖ لِّقَوْمٖ يَتَّقُونَۖ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1375,11,'209',10,'Yūnus','يُونس',1,2,7,'إِنَّ اَ۬لذِينَ لَا يَرْجُونَ لِقَآءَنَا وَرَضُواْ بِالْحَيَوٰةِ اِ۬لدُّنْيَا وَاطْمَأَنُّواْ بِهَا وَالذِينَ هُمْ عَنْ ءَايَٰتِنَا غَٰفِلُونَ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1376,11,'209',10,'Yūnus','يُونس',2,3,8,'أُوْلَٰٓئِكَ مَأْوَيٰهُمُ اُ۬لنَّارُ بِمَا كَانُواْ يَكْسِبُونَۖ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1377,11,'209',10,'Yūnus','يُونس',3,5,9,'إِنَّ اَ۬لذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ يَهْدِيهِمْ رَبُّهُم بِإِيمَٰنِهِمْۖ تَجْرِے مِن تَحْتِهِمُ اُ۬لْأَنْهَٰرُ فِے جَنَّٰتِ اِ۬لنَّعِيمِۖ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1378,11,'209',10,'Yūnus','يُونس',5,7,10,'دَعْوَيٰهُمْ فِيهَا سُبْحَٰنَكَ اَ۬للَّهُمَّۖ وَتَحِيَّتُهُمْ فِيهَا سَلَٰمٞۖ وَءَاخِرُ دَعْوَيٰهُمْ أَنِ اِ۬لْحَمْدُ لِلهِ رَبِّ اِ۬لْعَٰلَمِينَۖ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1379,11,'209',10,'Yūnus','يُونس',7,9,11,'۞ وَلَوْ يُعَجِّلُ اُ۬للَّهُ لِلنَّاسِ اِ۬لشَّرَّ اَ۪سْتِعْجَالَهُم بِالْخَيْرِ لَقُضِيَ إِلَيْهِمْ أَجَلُهُمْۖ فَنَذَرُ اُ۬لذِينَ لَا يَرْجُونَ لِقَآءَنَا فِے طُغْيَٰنِهِمْ يَعْمَهُونَۖ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1380,11,'209',10,'Yūnus','يُونس',9,12,12,'وَإِذَا مَسَّ اَ۬لْإِنسَٰنَ اَ۬لضُّرُّ دَعَانَا لِجَنۢبِهِۦ أَوْ قَاعِداً أَوْ قَآئِماٗۖ فَلَمَّا كَشَفْنَا عَنْهُ ضُرَّهُۥ مَرَّ كَأَن لَّمْ يَدْعُنَا إِلَىٰ ضُرّٖ مَّسَّهُۥۖ كَذَٰلِكَ زُيِّنَ لِلْمُسْرِفِينَ مَا كَانُواْ يَعْمَلُونَۖ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1381,11,'209',10,'Yūnus','يُونس',12,14,13,'وَلَقَدْ أَهْلَكْنَا اَ۬لْقُرُونَ مِن قَبْلِكُمْ لَمَّا ظَلَمُواْ وَجَآءَتْهُمْ رُسُلُهُم بِالْبَيِّنَٰتِۖ وَمَا كَانُواْ لِيُؤْمِنُواْۖ كَذَٰلِكَ نَجْزِے اِ۬لْقَوْمَ اَ۬لْمُجْرِمِينَۖ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1382,11,'209',10,'Yūnus','يُونس',14,15,14,'ثُمَّ جَعَلْنَٰكُمْ خَلَٰٓئِفَ فِے اِ۬لْأَرْضِ مِنۢ بَعْدِهِمْ لِنَنظُرَ كَيْفَ تَعْمَلُونَۖ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1383,11,'210',10,'Yūnus','يُونس',1,4,15,'وَإِذَا تُتْلَىٰ عَلَيْهِمْ ءَايَاتُنَا بَيِّنَٰتٖ قَالَ اَ۬لذِينَ لَا يَرْجُونَ لِقَآءَنَا اَ۪ئْتِ بِقُرْءَانٍ غَيْرِ هَٰذَا أَوْ بَدِّلْهُۖ قُلْ مَا يَكُونُ لِيَ أَنْ أُبَدِّلَهُۥ مِن تِلْقَآءِےْ نَفْسِيَۖ إِنْ أَتَّبِعُ إِلَّا مَا يُوحَىٰ إِلَيَّۖ إِنِّيَ أَخَافُ إِنْ عَصَيْتُ رَبِّے عَذَابَ يَوْمٍ عَظِيمٖۖ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1384,11,'210',10,'Yūnus','يُونس',5,6,16,'قُل لَّوْ شَآءَ اَ۬للَّهُ مَا تَلَوْتُهُۥ عَلَيْكُمْ وَلَا أَدْرَيٰكُم بِهِۦۖ فَقَدْ لَبِثْتُ فِيكُمْ عُمُراٗ مِّن قَبْلِهِۦۖ أَفَلَا تَعْقِلُونَۖ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1385,11,'210',10,'Yūnus','يُونس',7,8,17,'فَمَنْ أَظْلَمُ مِمَّنِ اِ۪فْتَرَىٰ عَلَى اَ۬للَّهِ كَذِباً أَوْ كَذَّبَ بِـَٔايَٰتِهِۦۖ إِنَّهُۥ لَا يُفْلِحُ اُ۬لْمُجْرِمُونَۖ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1386,11,'210',10,'Yūnus','يُونس',8,11,18,'وَيَعْبُدُونَ مِن دُونِ اِ۬للَّهِ مَا لَا يَضُرُّهُمْ وَلَا يَنفَعُهُمْ وَيَقُولُونَ هَٰؤُلَآءِ شُفَعَٰٓؤُنَا عِندَ اَ۬للَّهِۖ قُلْ أَتُنَبِّـُٔونَ اَ۬للَّهَ بِمَا لَا يَعْلَمُ فِے اِ۬لسَّمَٰوَٰتِ وَلَا فِے اِ۬لْأَرْضِۖ سُبْحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يُشْرِكُونَۖ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1387,11,'210',10,'Yūnus','يُونس',12,13,19,'۞ وَمَا كَانَ اَ۬لنَّاسُ إِلَّا أُمَّةٗ وَٰحِدَةٗ فَاخْتَلَفُواْۖ وَلَوْلَا كَلِمَةٞ سَبَقَتْ مِن رَّبِّكَ لَقُضِيَ بَيْنَهُمْ فِيمَا فِيهِ يَخْتَلِفُونَۖ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1388,11,'210',10,'Yūnus','يُونس',14,15,20,'وَيَقُولُونَ لَوْلَا أُنزِلَ عَلَيْهِ ءَايَةٞ مِّن رَّبِّهِۦۖ فَقُلْ إِنَّمَا اَ۬لْغَيْبُ لِلهِۖ فَانتَظِرُواْۖ إِنِّے مَعَكُم مِّنَ اَ۬لْمُنتَظِرِينَۖ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1389,11,'211',10,'Yūnus','يُونس',1,2,21,'وَإِذَا أَذَقْنَا اَ۬لنَّاسَ رَحْمَةٗ مِّنۢ بَعْدِ ضَرَّآءَ مَسَّتْهُمْ إِذَا لَهُم مَّكْرٞ فِے ءَايَاتِنَاۖ قُلِ اِ۬للَّهُ أَسْرَعُ مَكْراًۖ إِنَّ رُسُلَنَا يَكْتُبُونَ مَا تَمْكُرُونَۖ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1390,11,'211',10,'Yūnus','يُونس',3,7,22,'هُوَ اَ۬لذِے يُسَيِّرُكُمْ فِے اِ۬لْبَرِّ وَالْبَحْرِۖ حَتَّىٰ إِذَا كُنتُمْ فِے اِ۬لْفُلْكِ وَجَرَيْنَ بِهِم بِرِيحٖ طَيِّبَةٖ وَفَرِحُواْ بِهَا جَآءَتْهَا رِيحٌ عَاصِفٞ وَجَآءَهُمُ اُ۬لْمَوْجُ مِن كُلِّ مَكَانٖ وَظَنُّواْ أَنَّهُمْ أُحِيطَ بِهِمْ دَعَوُاْ اُ۬للَّهَ مُخْلِصِينَ لَهُ اُ۬لدِّينَ لَئِنْ أَنجَيْتَنَا مِنْ هَٰذِهِۦ لَنَكُونَنَّ مِنَ اَ۬لشَّٰكِرِينَۖ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1391,11,'211',10,'Yūnus','يُونس',7,9,23,'فَلَمَّا أَنجَيٰهُمْ إِذَا هُمْ يَبْغُونَ فِے اِ۬لْأَرْضِ بِغَيْرِ اِ۬لْحَقِّۖ يَٰأَيُّهَا اَ۬لنَّاسُ إِنَّمَا بَغْيُكُمْ عَلَىٰ أَنفُسِكُمۖ مَّتَٰعُ اُ۬لْحَيَوٰةِ اِ۬لدُّنْيَاۖ ثُمَّ إِلَيْنَا مَرْجِعُكُمْ فَنُنَبِّئُكُم بِمَا كُنتُمْ تَعْمَلُونَۖ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1392,11,'211',10,'Yūnus','يُونس',10,14,24,'إِنَّمَا مَثَلُ اُ۬لْحَيَوٰةِ اِ۬لدُّنْيَا كَمَآءٍ أَنزَلْنَٰهُ مِنَ اَ۬لسَّمَآءِ فَاخْتَلَطَۖ بِهِۦ نَبَاتُ اُ۬لْأَرْضِ مِمَّا يَأْكُلُ اُ۬لنَّاسُ وَالْأَنْعَٰمُۖ حَتَّىٰ إِذَا أَخَذَتِ اِ۬لْأَرْضُ زُخْرُفَهَا وَازَّيَّنَتْ وَظَنَّ أَهْلُهَا أَنَّهُمْ قَٰدِرُونَ عَلَيْهَا أَتَيٰهَا أَمْرُنَا لَيْلاً أَوْ نَهَاراٗ فَجَعَلْنَٰهَا حَصِيداٗ كَأَن لَّمْ تَغْنَ بِالْأَمْسِۖ كَذَٰلِكَ نُفَصِّلُ اُ۬لْأٓيَٰتِ لِقَوْمٖ يَتَفَكَّرُونَۖ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1393,11,'211',10,'Yūnus','يُونس',15,15,25,'وَاللَّهُ يَدْعُواْ إِلَىٰ دَارِ اِ۬لسَّلَٰمِ وَيَهْدِے مَنْ يَّشَآءُ اِ۪لَىٰ صِرَٰطٖ مُّسْتَقِيمٖۖ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1394,11,'212',10,'Yūnus','يُونس',1,2,26,'۞ لِّلذِينَ أَحْسَنُواْ اُ۬لْحُسْنَىٰ وَزِيَادَةٞۖ وَلَا يَرْهَقُ وُجُوهَهُمْ قَتَرٞ وَلَا ذِلَّةٌۖ أُوْلَٰٓئِكَ أَصْحَٰبُ اُ۬لْجَنَّةِۖ هُمْ فِيهَا خَٰلِدُونَۖ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1395,11,'212',10,'Yūnus','يُونس',2,5,27,'وَالذِينَ كَسَبُواْ اُ۬لسَّيِّـَٔاتِ جَزَآءُ سَيِّئَةِۢ بِمِثْلِهَا وَتَرْهَقُهُمْ ذِلَّةٞ مَّا لَهُم مِّنَ اَ۬للَّهِ مِنْ عَاصِمٖ كَأَنَّمَا أُغْشِيَتْ وُجُوهُهُمْ قِطَعاٗ مِّنَ اَ۬ليْلِ مُظْلِماًۖ أُوْلَٰٓئِكَ أَصْحَٰبُ اُ۬لنَّارِ هُمْ فِيهَا خَٰلِدُونَۖ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1396,11,'212',10,'Yūnus','يُونس',5,7,28,'وَيَوْمَ نَحْشُرُهُمْ جَمِيعاٗ ثُمَّ نَقُولُ لِلذِينَ أَشْرَكُواْ مَكَانَكُمْ أَنتُمْ وَشُرَكَآؤُكُمْ فَزَيَّلْنَا بَيْنَهُمْۖ وَقَالَ شُرَكَآؤُهُم مَّا كُنتُمْ إِيَّانَا تَعْبُدُونَۖ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1397,11,'212',10,'Yūnus','يُونس',7,8,29,'فَكَفَىٰ بِاللَّهِ شَهِيداَۢ بَيْنَنَا وَبَيْنَكُمْ إِن كُنَّا عَنْ عِبَادَتِكُمْ لَغَٰفِلِينَۖ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1398,11,'212',10,'Yūnus','يُونس',9,10,30,'هُنَالِكَ تَبْلُواْ كُلُّ نَفْسٖ مَّا أَسْلَفَتْۖ وَرُدُّواْ إِلَى اَ۬للَّهِ مَوْلَيٰهُمُ اُ۬لْحَقِّۖ وَضَلَّ عَنْهُم مَّا كَانُواْ يَفْتَرُونَۖ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1399,11,'212',10,'Yūnus','يُونس',10,13,31,'قُلْ مَنْ يَّرْزُقُكُم مِّنَ اَ۬لسَّمَآءِ وَالْأَرْضِۖ أَمَّنْ يَّمْلِكُ اُ۬لسَّمْعَ وَالْأَبْصَٰرَ وَمَنْ يُّخْرِجُ اُ۬لْحَيَّ مِنَ اَ۬لْمَيِّتِ وَيُخْرِجُ اُ۬لْمَيِّتَ مِنَ اَ۬لْحَيِّ وَمَنْ يُّدَبِّرُ اُ۬لْأَمْرَۖ فَسَيَقُولُونَ اَ۬للَّهُۖ فَقُلْ أَفَلَا تَتَّقُونَۖ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1400,11,'212',10,'Yūnus','يُونس',13,14,32,'فَذَٰلِكُمُ اُ۬للَّهُ رَبُّكُمُ اُ۬لْحَقُّۖ فَمَاذَا بَعْدَ اَ۬لْحَقِّ إِلَّا اَ۬لضَّلَٰلُۖ فَأَنَّىٰ تُصْرَفُونَۖ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1401,11,'212',10,'Yūnus','يُونس',14,15,33,'كَذَٰلِكَ حَقَّتْ كَلِمَٰتُ رَبِّكَ عَلَى اَ۬لذِينَ فَسَقُواْ أَنَّهُمْ لَا يُؤْمِنُونَۖ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1402,11,'213',10,'Yūnus','يُونس',1,2,34,'قُلْ هَلْ مِن شُرَكَآئِكُم مَّنْ يَّبْدَؤُاْ اُ۬لْخَلْقَ ثُمَّ يُعِيدُهُۥۖ قُلِ اِ۬للَّهُ يَبْدَؤُاْ اُ۬لْخَلْقَ ثُمَّ يُعِيدُهُۥۖ فَأَنَّىٰ تُؤْفَكُونَۖ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1403,11,'213',10,'Yūnus','يُونس',2,4,35,'قُلْ هَلْ مِن شُرَكَآئِكُم مَّنْ يَّهْدِے إِلَى اَ۬لْحَقِّۖ قُلِ اِ۬للَّهُ يَهْدِے لِلْحَقِّۖ أَفَمَنْ يَّهْدِے إِلَى اَ۬لْحَقِّ أَحَقُّ أَنْ يُّتَّبَعَ أَمَّن لَّا يَه۬دِّے إِلَّا أَنْ يُّهْدَىٰۖ فَمَا لَكُمْۖ كَيْفَ تَحْكُمُونَۖ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1404,11,'213',10,'Yūnus','يُونس',5,6,36,'وَمَا يَتَّبِعُ أَكْثَرُهُمْ إِلَّا ظَنّاًۖ إِنَّ اَ۬لظَّنَّ لَا يُغْنِے مِنَ اَ۬لْحَقِّ شَئْاًۖ إِنَّ اَ۬للَّهَ عَلِيمُۢ بِمَا يَفْعَلُونَۖ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1405,11,'213',10,'Yūnus','يُونس',6,8,37,'۞ وَمَا كَانَ هَٰذَا اَ۬لْقُرْءَانُ أَنْ يُّفْتَرَىٰ مِن دُونِ اِ۬للَّهِۖ وَلَٰكِن تَصْدِيقَ اَ۬لذِے بَيْنَ يَدَيْهِ وَتَفْصِيلَ اَ۬لْكِتَٰبِ لَا رَيْبَ فِيهِ مِن رَّبِّ اِ۬لْعَٰلَمِينَۖ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1406,11,'213',10,'Yūnus','يُونس',8,9,38,'أَمْ يَقُولُونَ اَ۪فْتَرَيٰهُۖ قُلْ فَأْتُواْ بِسُورَةٖ مِّثْلِهِۦۖ وَادْعُواْ مَنِ اِ۪سْتَطَعْتُم مِّن دُونِ اِ۬للَّهِ إِن كُنتُمْ صَٰدِقِينَۖ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1407,11,'213',10,'Yūnus','يُونس',10,11,39,'بَلْ كَذَّبُواْ بِمَا لَمْ يُحِيطُواْ بِعِلْمِهِۦ وَلَمَّا يَأْتِهِمْ تَأْوِيلُهُۥۖ كَذَٰلِكَ كَذَّبَ اَ۬لذِينَ مِن قَبْلِهِمْۖ فَانظُرْ كَيْفَ كَانَ عَٰقِبَةُ اُ۬لظَّٰلِمِينَۖ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1408,11,'213',10,'Yūnus','يُونس',12,13,40,'وَمِنْهُم مَّنْ يُّؤْمِنُ بِهِۦ وَمِنْهُم مَّن لَّا يُؤْمِنُ بِهِۦۖ وَرَبُّكَ أَعْلَمُ بِالْمُفْسِدِينَۖ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1409,11,'213',10,'Yūnus','يُونس',13,14,41,'وَإِن كَذَّبُوكَ فَقُل لِّے عَمَلِے وَلَكُمْ عَمَلُكُمْ أَنتُم بَرِيٓـُٔونَ مِمَّا أَعْمَلُ وَأَنَا بَرِےٓءٞ مِّمَّا تَعْمَلُونَۖ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1410,11,'213',10,'Yūnus','يُونس',14,15,42,'وَمِنْهُم مَّنْ يَّسْتَمِعُونَ إِلَيْكَۖ أَفَأَنتَ تُسْمِعُ اُ۬لصُّمَّ وَلَوْ كَانُواْ لَا يَعْقِلُونَۖ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1411,11,'214',10,'Yūnus','يُونس',1,2,43,'وَمِنْهُم مَّنْ يَّنظُرُ إِلَيْكَۖ أَفَأَنتَ تَهْدِے اِ۬لْعُمْيَ وَلَوْ كَانُواْ لَا يُبْصِرُونَۖ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1412,11,'214',10,'Yūnus','يُونس',2,3,44,'إِنَّ اَ۬للَّهَ لَا يَظْلِمُ اُ۬لنَّاسَ شَئْاٗۖ وَلَٰكِنَّ اَ۬لنَّاسَ أَنفُسَهُمْ يَظْلِمُونَۖ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1413,11,'214',10,'Yūnus','يُونس',3,5,45,'وَيَوْمَ نَحْشُرُهُمْ كَأَن لَّمْ يَلْبَثُواْ إِلَّا سَاعَةٗ مِّنَ اَ۬لنَّهَارِ يَتَعَارَفُونَ بَيْنَهُمْۖ قَدْ خَسِرَ اَ۬لذِينَ كَذَّبُواْ بِلِقَآءِ اِ۬للَّهِ وَمَا كَانُواْ مُهْتَدِينَۖ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1414,11,'214',10,'Yūnus','يُونس',5,6,46,'وَإِمَّا نُرِيَنَّكَ بَعْضَ اَ۬لذِے نَعِدُهُمْ أَوْ نَتَوَفَّيَنَّكَ فَإِلَيْنَا مَرْجِعُهُمْۖ ثُمَّ اَ۬للَّهُ شَهِيدٌ عَلَىٰ مَا يَفْعَلُونَۖ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1415,11,'214',10,'Yūnus','يُونس',7,8,47,'وَلِكُلِّ أُمَّةٖ رَّسُولٞۖ فَإِذَا جَآءَ رَسُولُهُمْ قُضِيَ بَيْنَهُم بِالْقِسْطِ وَهُمْ لَا يُظْلَمُونَۖ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1416,11,'214',10,'Yūnus','يُونس',8,8,48,'وَيَقُولُونَ مَتَىٰ هَٰذَا اَ۬لْوَعْدُ إِن كُنتُمْ صَٰدِقِينَۖ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1417,11,'214',10,'Yūnus','يُونس',9,10,49,'۞ قُل لَّا أَمْلِكُ لِنَفْسِے ضَرّاٗ وَلَا نَفْعاً إِلَّا مَا شَآءَ اَ۬للَّهُۖ لِكُلِّ أُمَّةٍ أَجَلٌۖ إِذَا جَا أَجَلُهُمْ فَلَا يَسْتَٰٔخِرُونَ سَاعَةٗۖ وَلَا يَسْتَقْدِمُونَۖ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1418,11,'214',10,'Yūnus','يُونس',11,12,50,'قُلْ أَرَٰ۬يْتُمْ إِنْ أَتَيٰكُمْ عَذَابُهُۥ بَيَٰتاً أَوْ نَهَاراٗ مَّاذَا يَسْتَعْجِلُ مِنْهُ اُ۬لْمُجْرِمُونَۖ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1419,11,'214',10,'Yūnus','يُونس',12,13,51,'أَثُمَّ إِذَا مَا وَقَعَ ءَامَنتُم بِهِۦۖ ءَآلَٰنَ وَقَدْ كُنتُم بِهِۦ تَسْتَعْجِلُونَۖ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1420,11,'214',10,'Yūnus','يُونس',13,14,52,'ثُمَّ قِيلَ لِلذِينَ ظَلَمُواْ ذُوقُواْ عَذَابَ اَ۬لْخُلْدِۖ هَلْ تُجْزَوْنَ إِلَّا بِمَا كُنتُمْ تَكْسِبُونَۖ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1421,11,'214',10,'Yūnus','يُونس',14,15,53,'وَيَسْتَنۢبِـُٔونَكَ أَحَقٌّ هُوَۖ قُلْ إِے وَرَبِّيَ إِنَّهُۥ لَحَقّٞ وَمَا أَنتُم بِمُعْجِزِينَۖ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1422,11,'215',10,'Yūnus','يُونس',1,3,54,'وَلَوْ أَنَّ لِكُلِّ نَفْسٖ ظَلَمَتْ مَا فِے اِ۬لْأَرْضِ لَافْتَدَتْ بِهِۦۖ وَأَسَرُّواْ اُ۬لنَّدَامَةَ لَمَّا رَأَوُاْ اُ۬لْعَذَابَ وَقُضِيَ بَيْنَهُم بِالْقِسْطِ وَهُمْ لَا يُظْلَمُونَۖ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1423,11,'215',10,'Yūnus','يُونس',3,4,55,'أَلَا إِنَّ لِلهِ مَا فِے اِ۬لسَّمَٰوَٰتِ وَالْأَرْضِۖ أَلَا إِنَّ وَعْدَ اَ۬للَّهِ حَقّٞۖ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَۖ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1424,11,'215',10,'Yūnus','يُونس',4,5,56,'هُوَ يُحْيِۦ وَيُمِيتُۖ وَإِلَيْهِ تُرْجَعُونَۖ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1425,11,'215',10,'Yūnus','يُونس',5,6,57,'يَٰأَيُّهَا اَ۬لنَّاسُ قَدْ جَآءَتْكُم مَّوْعِظَةٞ مِّن رَّبِّكُمْ وَشِفَآءٞ لِّمَا فِے اِ۬لصُّدُورِ وَهُدىٗ وَرَحْمَةٞ لِّلْمُؤْمِنِينَۖ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1426,11,'215',10,'Yūnus','يُونس',7,8,58,'قُلْ بِفَضْلِ اِ۬للَّهِ وَبِرَحْمَتِهِۦ فَبِذَٰلِكَ فَلْيَفْرَحُواْۖ هُوَ خَيْرٞ مِّمَّا يَجْمَعُونَۖ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1427,11,'215',10,'Yūnus','يُونس',8,10,59,'قُلْ أَرَٰ۬يْتُم مَّا أَنزَلَ اَ۬للَّهُ لَكُم مِّن رِّزْقٖ فَجَعَلْتُم مِّنْهُ حَرَاماٗ وَحَلَٰلاٗۖ قُلْ ءَآللَّهُ أَذِنَ لَكُمْ أَمْ عَلَى اَ۬للَّهِ تَفْتَرُونَۖ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1428,11,'215',10,'Yūnus','يُونس',10,12,60,'وَمَا ظَنُّ اُ۬لذِينَ يَفْتَرُونَ عَلَى اَ۬للَّهِ اِ۬لْكَذِبَ يَوْمَ اَ۬لْقِيَٰمَةِۖ إِنَّ اَ۬للَّهَ لَذُو فَضْلٍ عَلَى اَ۬لنَّاسِۖ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَشْكُرُونَۖ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1429,11,'215',10,'Yūnus','يُونس',12,15,61,'۞ وَمَا تَكُونُ فِے شَأْنٖ وَمَا تَتْلُواْ مِنْهُ مِن قُرْءَانٖ وَلَا تَعْمَلُونَ مِنْ عَمَلٍ إِلَّا كُنَّا عَلَيْكُمْ شُهُوداً إِذْ تُفِيضُونَ فِيهِۖ وَمَا يَعْزُبُ عَن رَّبِّكَ مِن مِّثْقَالِ ذَرَّةٖ فِے اِ۬لْأَرْضِ وَلَا فِے اِ۬لسَّمَآءِۖ وَلَا أَصْغَرَ مِن ذَٰلِكَ وَلَا أَكْبَرَ إِلَّا فِے كِتَٰبٖ مُّبِينٍۖ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1430,11,'216',10,'Yūnus','يُونس',1,1,62,'أَلَا إِنَّ أَوْلِيَآءَ اَ۬للَّهِ لَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَۖ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1431,11,'216',10,'Yūnus','يُونس',2,2,63,'اَ۬لذِينَ ءَامَنُواْ وَكَانُواْ يَتَّقُونَ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1432,11,'216',10,'Yūnus','يُونس',2,4,64,'لَهُمُ اُ۬لْبُشْرَىٰ فِے اِ۬لْحَيَوٰةِ اِ۬لدُّنْيَا وَفِے اِ۬لْأٓخِرَةِۖ لَا تَبْدِيلَ لِكَلِمَٰتِ اِ۬للَّهِۖ ذَٰلِكَ هُوَ اَ۬لْفَوْزُ اُ۬لْعَظِيمُۖ ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1433,11,'216',10,'Yūnus','يُونس',4,5,65,'وَلَا يُحْزِنكَ قَوْلُهُمْۖ إِنَّ اَ۬لْعِزَّةَ لِلهِ جَمِيعاًۖ هُوَ اَ۬لسَّمِيعُ اُ۬لْعَلِيمُۖ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1434,11,'216',10,'Yūnus','يُونس',5,8,66,'أَلَا إِنَّ لِلهِ مَن فِے اِ۬لسَّمَٰوَٰتِ وَمَن فِے اِ۬لْأَرْضِۖ وَمَا يَتَّبِعُ اُ۬لذِينَ يَدْعُونَ مِن دُونِ اِ۬للَّهِ شُرَكَآءَۖ ا۪نْ يَّتَّبِعُونَ إِلَّا اَ۬لظَّنَّۖ وَإِنْ هُمْ إِلَّا يَخْرُصُونَۖ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1435,11,'216',10,'Yūnus','يُونس',8,10,67,'هُوَ اَ۬لذِے جَعَلَ لَكُمُ اُ۬ليْلَ لِتَسْكُنُواْ فِيهِ وَالنَّهَارَ مُبْصِراًۖ إِنَّ فِے ذَٰلِكَ لَأٓيَٰتٖ لِّقَوْمٖ يَسْمَعُونَۖ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1436,11,'216',10,'Yūnus','يُونس',10,13,68,'قَالُواْ اُ۪تَّخَذَ اَ۬للَّهُ وَلَداٗۖ سُبْحَٰنَهُۥۖ هُوَ اَ۬لْغَنِيُّۖ لَهُۥ مَا فِے اِ۬لسَّمَٰوَٰتِ وَمَا فِے اِ۬لْأَرْضِۖ إِنْ عِندَكُم مِّن سُلْطَٰنِۢ بِهَٰذَاۖ أَتَقُولُونَ عَلَى اَ۬للَّهِ مَا لَا تَعْلَمُونَۖ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1437,11,'216',10,'Yūnus','يُونس',13,14,69,'قُلْ إِنَّ اَ۬لذِينَ يَفْتَرُونَ عَلَى اَ۬للَّهِ اِ۬لْكَذِبَ لَا يُفْلِحُونَۖ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1438,11,'216',10,'Yūnus','يُونس',14,15,70,'مَتَٰعٞ فِے اِ۬لدُّنْيَاۖ ثُمَّ إِلَيْنَا مَرْجِعُهُمْ ثُمَّ نُذِيقُهُمُ اُ۬لْعَذَابَ اَ۬لشَّدِيدَ بِمَا كَانُواْ يَكْفُرُونَۖ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1439,11,'217',10,'Yūnus','يُونس',1,4,71,'۞ وَاتْلُ عَلَيْهِمْ نَبَأَ نُوحٍ إِذْ قَالَ لِقَوْمِهِۦ يَٰقَوْمِ إِن كَانَ كَبُرَ عَلَيْكُم مَّقَامِے وَتَذْكِيرِے بِـَٔايَٰتِ اِ۬للَّهِ فَعَلَى اَ۬للَّهِ تَوَكَّلْتُۖ فَأَجْمِعُواْ أَمْرَكُمْ وَشُرَكَآءَكُمْۖ ثُمَّ لَا يَكُنْ أَمْرُكُمْ عَلَيْكُمْ غُمَّةٗۖ ثُمَّ اَ۪قْضُواْ إِلَيَّ وَلَا تُنظِرُونِۖ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1440,11,'217',10,'Yūnus','يُونس',4,5,72,'فَإِن تَوَلَّيْتُمْ فَمَا سَأَلْتُكُم مِّنْ أَجْرٍۖ إِنْ أَجْرِيَ إِلَّا عَلَى اَ۬للَّهِۖ وَأُمِرْتُ أَنْ أَكُونَ مِنَ اَ۬لْمُسْلِمِينَۖ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1441,11,'217',10,'Yūnus','يُونس',6,7,73,'فَكَذَّبُوهُ فَنَجَّيْنَٰهُ وَمَن مَّعَهُۥ فِے اِ۬لْفُلْكِ وَجَعَلْنَٰهُمْ خَلَٰٓئِفَۖ وَأَغْرَقْنَا اَ۬لذِينَ كَذَّبُواْ بِـَٔايَٰتِنَاۖ فَانظُرْ كَيْفَ كَانَ عَٰقِبَةُ اُ۬لْمُنذَرِينَۖ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1442,11,'217',10,'Yūnus','يُونس',8,10,74,'ثُمَّ بَعَثْنَا مِنۢ بَعْدِهِۦ رُسُلاً إِلَىٰ قَوْمِهِمْ فَجَآءُوهُم بِالْبَيِّنَٰتِ فَمَا كَانُواْ لِيُؤْمِنُواْ بِمَا كَذَّبُواْ بِهِۦ مِن قَبْلُۖ كَذَٰلِكَ نَطْبَعُ عَلَىٰ قُلُوبِ اِ۬لْمُعْتَدِينَۖ ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1443,11,'217',10,'Yūnus','يُونس',10,11,75,'ثُمَّ بَعَثْنَا مِنۢ بَعْدِهِم مُّوسَىٰ وَهَٰرُونَ إِلَىٰ فِرْعَوْنَ وَمَلَإِيْهِۦ بِـَٔايَٰتِنَا فَاسْتَكْبَرُواْ وَكَانُواْ قَوْماٗ مُّجْرِمِينَۖ ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1444,11,'217',10,'Yūnus','يُونس',11,12,76,'فَلَمَّا جَآءَهُمُ اُ۬لْحَقُّ مِنْ عِندِنَا قَالُواْ إِنَّ هَٰذَا لَسِحْرٞ مُّبِينٞۖ ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1445,11,'217',10,'Yūnus','يُونس',12,13,77,'قَالَ مُوسَىٰ أَتَقُولُونَ لِلْحَقِّ لَمَّا جَآءَكُمْۖ أَسِحْرٌ هَٰذَاۖ وَلَا يُفْلِحُ اُ۬لسَّٰحِرُونَۖ ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1446,11,'217',10,'Yūnus','يُونس',14,15,78,'قَالُواْ أَجِئْتَنَا لِتَلْفِتَنَا عَمَّا وَجَدْنَا عَلَيْهِ ءَابَآءَنَا وَتَكُونَ لَكُمَا اَ۬لْكِبْرِيَآءُ فِے اِ۬لْأَرْضِ وَمَا نَحْنُ لَكُمَا بِمُؤْمِنِينَۖ ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1447,11,'218',10,'Yūnus','يُونس',1,1,79,'وَقَالَ فِرْعَوْنُ اُ۪ئْتُونِے بِكُلِّ سَٰحِرٍ عَلِيمٖۖ ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1448,11,'218',10,'Yūnus','يُونس',1,2,80,'فَلَمَّا جَآءَ اَ۬لسَّحَرَةُ قَالَ لَهُم مُّوسَىٰ أَلْقُواْ مَا أَنتُم مُّلْقُونَۖ ٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1449,11,'218',10,'Yūnus','يُونس',2,4,81,'فَلَمَّا أَلْقَوْاْ قَالَ مُوسَىٰ مَا جِئْتُم بِهِ اِ۬لسِّحْرُۖ إِنَّ اَ۬للَّهَ سَيُبْطِلُهُۥۖ إِنَّ اَ۬للَّهَ لَا يُصْلِحُ عَمَلَ اَ۬لْمُفْسِدِينَۖ ٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1450,11,'218',10,'Yūnus','يُونس',4,5,82,'وَيُحِقُّ اُ۬للَّهُ اُ۬لْحَقَّ بِكَلِمَٰتِهِۦ وَلَوْ كَرِهَ اَ۬لْمُجْرِمُونَۖ ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1451,11,'218',10,'Yūnus','يُونس',5,7,83,'۞ فَمَا ءَامَنَ لِمُوسَىٰ إِلَّا ذُرِّيَّةٞ مِّن قَوْمِهِۦ عَلَىٰ خَوْفٖ مِّن فِرْعَوْنَ وَمَلَإِيْهِمْ أَنْ يَّفْتِنَهُمْۖ وَإِنَّ فِرْعَوْنَ لَعَالٖ فِے اِ۬لْأَرْضِۖ وَإِنَّهُۥ لَمِنَ اَ۬لْمُسْرِفِينَۖ ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1452,11,'218',10,'Yūnus','يُونس',7,8,84,'وَقَالَ مُوسَىٰ يَٰقَوْمِ إِن كُنتُمْ ءَامَنتُم بِاللَّهِ فَعَلَيْهِ تَوَكَّلُواْ إِن كُنتُم مُّسْلِمِينَۖ ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1453,11,'218',10,'Yūnus','يُونس',9,9,85,'فَقَالُواْ عَلَى اَ۬للَّهِ تَوَكَّلْنَاۖ رَبَّنَا لَا تَجْعَلْنَا فِتْنَةٗ لِّلْقَوْمِ اِ۬لظَّٰلِمِينَۖ ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1454,11,'218',10,'Yūnus','يُونس',10,10,86,'وَنَجِّنَا بِرَحْمَتِكَ مِنَ اَ۬لْقَوْمِ اِ۬لْكَٰفِرِينَۖ ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1455,11,'218',10,'Yūnus','يُونس',10,12,87,'وَأَوْحَيْنَا إِلَىٰ مُوسَىٰ وَأَخِيهِ أَن تَبَوَّءَا لِقَوْمِكُمَا بِمِصْرَ بِيُوتاٗ وَاجْعَلُواْ بِيُوتَكُمْ قِبْلَةٗ وَأَقِيمُواْ اُ۬لصَّلَوٰةَۖ وَبَشِّرِ اِ۬لْمُؤْمِنِينَۖ ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1456,11,'218',10,'Yūnus','يُونس',12,15,88,'وَقَالَ مُوسَىٰ رَبَّنَا إِنَّكَ ءَاتَيْتَ فِرْعَوْنَ وَمَلَأَهُۥ زِينَةٗ وَأَمْوَٰلاٗ فِے اِ۬لْحَيَوٰةِ اِ۬لدُّنْيَاۖ رَبَّنَا لِيَضِلُّواْ عَن سَبِيلِكَۖ رَبَّنَا اَ۪طْمِسْ عَلَىٰ أَمْوَٰلِهِمْ وَاشْدُدْ عَلَىٰ قُلُوبِهِمْ فَلَا يُؤْمِنُواْ حَتَّىٰ يَرَوُاْ اُ۬لْعَذَابَ اَ۬لْأَلِيمَۖ ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1457,11,'219',10,'Yūnus','يُونس',1,2,89,'قَالَ قَدْ أُجِيبَت دَّعْوَتُكُمَا فَاسْتَقِيمَاۖ وَلَا تَتَّبِعَٰٓنِّ سَبِيلَ اَ۬لذِينَ لَا يَعْلَمُونَۖ ٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1458,11,'219',10,'Yūnus','يُونس',2,5,90,'وَجَٰوَزْنَا بِبَنِے إِسْرَآءِيلَ اَ۬لْبَحْرَ فَأَتْبَعَهُمْ فِرْعَوْنُ وَجُنُودُهُۥ بَغْياٗ وَعَدْواًۖ حَتَّىٰ إِذَا أَدْرَكَهُ اُ۬لْغَرَقُ قَالَ ءَامَنتُ أَنَّهُۥ لَا إِلَٰهَ إِلَّا اَ۬لذِے ءَامَنَتْ بِهِۦ بَنُواْ إِسْرَآءِيلَ وَأَنَا مِنَ اَ۬لْمُسْلِمِينَۖ ٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1459,11,'219',10,'Yūnus','يُونس',5,6,91,'ءَآلَٰنَ وَقَدْ عَصَيْتَ قَبْلُ وَكُنتَ مِنَ اَ۬لْمُفْسِدِينَۖ ٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1460,11,'219',10,'Yūnus','يُونس',6,7,92,'فَالْيَوْمَ نُنَجِّيكَ بِبَدَنِكَ لِتَكُونَ لِمَنْ خَلْفَكَ ءَايَةٗۖ وَإِنَّ كَثِيراٗ مِّنَ اَ۬لنَّاسِ عَنْ ءَايَٰتِنَا لَغَٰفِلُونَۖ ٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1461,11,'219',10,'Yūnus','يُونس',8,10,93,'۞ وَلَقَدْ بَوَّأْنَا بَنِے إِسْرَآءِيلَ مُبَوَّأَ صِدْقٖ وَرَزَقْنَٰهُم مِّنَ اَ۬لطَّيِّبَٰتِ فَمَا اَ۪خْتَلَفُواْ حَتَّىٰ جَآءَهُمُ اُ۬لْعِلْمُۖ إِنَّ رَبَّكَ يَقْضِے بَيْنَهُمْ يَوْمَ اَ۬لْقِيَٰمَةِ فِيمَا كَانُواْ فِيهِ يَخْتَلِفُونَۖ ٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1462,11,'219',10,'Yūnus','يُونس',10,12,94,'فَإِن كُنتَ فِے شَكّٖ مِّمَّا أَنزَلْنَا إِلَيْكَ فَسْـَٔلِ اِ۬لذِينَ يَقْرَءُونَ اَ۬لْكِتَٰبَ مِن قَبْلِكَۖ لَقَدْ جَآءَكَ اَ۬لْحَقُّ مِن رَّبِّكَۖ فَلَا تَكُونَنَّ مِنَ اَ۬لْمُمْتَرِينَۖ ٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1463,11,'219',10,'Yūnus','يُونس',12,13,95,'وَلَا تَكُونَنَّ مِنَ اَ۬لذِينَ كَذَّبُواْ بِـَٔايَٰتِ اِ۬للَّهِ فَتَكُونَ مِنَ اَ۬لْخَٰسِرِينَۖ ٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1464,11,'219',10,'Yūnus','يُونس',14,14,96,'إِنَّ اَ۬لذِينَ حَقَّتْ عَلَيْهِمْ كَلِمَٰتُ رَبِّكَ لَا يُؤْمِنُونَ ٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1465,11,'219',10,'Yūnus','يُونس',15,15,97,'وَلَوْ جَآءَتْهُمْ كُلُّ ءَايَةٍ حَتَّىٰ يَرَوُاْ اُ۬لْعَذَابَ اَ۬لْأَلِيمَۖ ٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1466,11,'220',10,'Yūnus','يُونس',1,3,98,'فَلَوْلَا كَانَتْ قَرْيَةٌ ءَامَنَتْ فَنَفَعَهَا إِيمَٰنُهَا إِلَّا قَوْمَ يُونُسَ لَمَّا ءَامَنُواْ كَشَفْنَا عَنْهُمْ عَذَابَ اَ۬لْخِزْيِ فِے اِ۬لْحَيَوٰةِ اِ۬لدُّنْيَا وَمَتَّعْنَٰهُمْ إِلَىٰ حِينٖۖ ٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1467,11,'220',10,'Yūnus','يُونس',3,4,99,'وَلَوْ شَآءَ رَبُّكَ لَأٓمَنَ مَن فِے اِ۬لْأَرْضِ كُلُّهُمْ جَمِيعاًۖ أَفَأَنتَ تُكْرِهُ اُ۬لنَّاسَ حَتَّىٰ يَكُونُواْ مُؤْمِنِينَۖ ٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1468,11,'220',10,'Yūnus','يُونس',5,6,100,'وَمَا كَانَ لِنَفْسٍ أَن تُؤْمِنَ إِلَّا بِإِذْنِ اِ۬للَّهِۖ وَيَجْعَلُ اُ۬لرِّجْسَ عَلَى اَ۬لذِينَ لَا يَعْقِلُونَۖ ١٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1469,11,'220',10,'Yūnus','يُونس',6,7,101,'قُلُ اُ۟نظُرُواْ مَاذَا فِے اِ۬لسَّمَٰوَٰتِ وَالْأَرْضِ وَمَا تُغْنِے اِ۬لْأٓيَٰتُ وَالنُّذُرُ عَن قَوْمٖ لَّا يُؤْمِنُونَۖ ١٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1470,11,'220',10,'Yūnus','يُونس',8,9,102,'فَهَلْ يَنتَظِرُونَ إِلَّا مِثْلَ أَيَّامِ اِ۬لذِينَ خَلَوْاْ مِن قَبْلِهِمْۖ قُلْ فَانتَظِرُواْۖ إِنِّے مَعَكُم مِّنَ اَ۬لْمُنتَظِرِينَۖ ١٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1471,11,'220',10,'Yūnus','يُونس',9,10,103,'ثُمَّ نُنَجِّے رُسُلَنَا وَالذِينَ ءَامَنُواْ كَذَٰلِكَۖ حَقّاً عَلَيْنَا نُنَجِّ اِ۬لْمُؤْمِنِينَۖ ١٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1472,11,'220',10,'Yūnus','يُونس',11,13,104,'۞ قُلْ يَٰأَيُّهَا اَ۬لنَّاسُ إِن كُنتُمْ فِے شَكّٖ مِّن دِينِے فَلَا أَعْبُدُ اُ۬لذِينَ تَعْبُدُونَ مِن دُونِ اِ۬للَّهِۖ وَلَٰكِنْ أَعْبُدُ اُ۬للَّهَ اَ۬لذِے يَتَوَفَّيٰكُمْۖ وَأُمِرْتُ أَنْ أَكُونَ مِنَ اَ۬لْمُؤْمِنِينَ ١٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1473,11,'220',10,'Yūnus','يُونس',13,14,105,'وَأَنْ أَقِمْ وَجْهَكَ لِلدِّينِ حَنِيفاٗۖ وَلَا تَكُونَنَّ مِنَ اَ۬لْمُشْرِكِينَۖ ١٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1474,11,'220',10,'Yūnus','يُونس',14,15,106,'وَلَا تَدْعُ مِن دُونِ اِ۬للَّهِ مَا لَا يَنفَعُكَ وَلَا يَضُرُّكَۖ فَإِن فَعَلْتَ فَإِنَّكَ إِذاٗ مِّنَ اَ۬لظَّٰلِمِينَۖ ١٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1475,11,'221',10,'Yūnus','يُونس',1,3,107,'وَإِنْ يَّمْسَسْكَ اَ۬للَّهُ بِضُرّٖ فَلَا كَاشِفَ لَهُۥ إِلَّا هُوَۖ وَإِنْ يُّرِدْكَ بِخَيْرٖ فَلَا رَآدَّ لِفَضْلِهِۦۖ يُصِيبُ بِهِۦ مَنْ يَّشَآءُ مِنْ عِبَادِهِۦۖ وَهْوَ اَ۬لْغَفُورُ اُ۬لرَّحِيمُۖ ١٠٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1476,11,'221',10,'Yūnus','يُونس',3,5,108,'قُلْ يَٰأَيُّهَا اَ۬لنَّاسُ قَدْ جَآءَكُمُ اُ۬لْحَقُّ مِن رَّبِّكُمْۖ فَمَنِ اِ۪هْتَدَىٰ فَإِنَّمَا يَهْتَدِے لِنَفْسِهِۦۖ وَمَن ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيْهَاۖ وَمَا أَنَا عَلَيْكُم بِوَكِيلٖۖ ١٠٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1477,11,'221',10,'Yūnus','يُونس',5,6,109,'وَاتَّبِعْ مَا يُوحَىٰ إِلَيْكَ وَاصْبِرْ حَتَّىٰ يَحْكُمَ اَ۬للَّهُۖ وَهْوَ خَيْرُ اُ۬لْحَٰكِمِينَۖ ١٠٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1478,11,'221',11,'Hūd','هُود',9,9,1,'أَلَٓرَۖ كِتَٰبٌ أُحْكِمَتْ ءَايَٰتُهُۥ ثُمَّ فُصِّلَتْ مِن لَّدُنْ حَكِيمٍ خَبِيرٍ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1479,11,'221',11,'Hūd','هُود',10,10,2,'أَلَّا تَعْبُدُواْ إِلَّا اَ۬للَّهَۖ إِنَّنِے لَكُم مِّنْهُ نَذِيرٞ وَبَشِيرٞ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1480,11,'221',11,'Hūd','هُود',10,13,3,'وَأَنِ اِ۪سْتَغْفِرُواْ رَبَّكُمْ ثُمَّ تُوبُواْ إِلَيْهِ يُمَتِّعْكُم مَّتَٰعاً حَسَناً إِلَىٰ أَجَلٖ مُّسَمّىٗ وَيُؤْتِ كُلَّ ذِے فَضْلٖ فَضْلَهُۥۖ وَإِن تَوَلَّوْاْ فَإِنِّيَ أَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٖ كَبِيرٍۖ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1481,11,'221',11,'Hūd','هُود',13,13,4,'إِلَى اَ۬للَّهِ مَرْجِعُكُمْۖ وَهْوَ عَلَىٰ كُلِّ شَےْءٖ قَدِيرٌۖ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1482,11,'221',11,'Hūd','هُود',14,15,5,'أَلَا إِنَّهُمْ يَثْنُونَ صُدُورَهُمْ لِيَسْتَخْفُواْ مِنْهُۖ أَلَا حِينَ يَسْتَغْشُونَ ثِيَابَهُمْ يَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَۖ إِنَّهُۥ عَلِيمُۢ بِذَاتِ اِ۬لصُّدُورِۖ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1483,12,'222',11,'Hūd','هُود',1,2,6,'۞ وَمَا مِن دَآبَّةٖ فِے اِ۬لْأَرْضِ إِلَّا عَلَى اَ۬للَّهِ رِزْقُهَا وَيَعْلَمُ مُسْتَقَرَّهَا وَمُسْتَوْدَعَهَاۖ كُلّٞ فِے كِتَٰبٖ مُّبِينٖۖ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1484,12,'222',11,'Hūd','هُود',2,6,7,'وَهْوَ اَ۬لذِے خَلَقَ اَ۬لسَّمَٰوَٰتِ وَالْأَرْضَ فِے سِتَّةِ أَيَّامٖ وَكَانَ عَرْشُهُۥ عَلَى اَ۬لْمَآءِ لِيَبْلُوَكُمْ أَيُّكُمْ أَحْسَنُ عَمَلاٗۖ وَلَئِن قُلْتَ إِنَّكُم مَّبْعُوثُونَ مِنۢ بَعْدِ اِ۬لْمَوْتِ لَيَقُولَنَّ اَ۬لذِينَ كَفَرُواْ إِنْ هَٰذَا إِلَّا سِحْرٞ مُّبِينٞۖ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1485,12,'222',11,'Hūd','هُود',6,8,8,'وَلَئِنْ أَخَّرْنَا عَنْهُمُ اُ۬لْعَذَابَ إِلَىٰ أُمَّةٖ مَّعْدُودَةٖ لَّيَقُولُنَّ مَا يَحْبِسُهُۥۖ أَلَا يَوْمَ يَأْتِيهِمْ لَيْسَ مَصْرُوفاً عَنْهُمْۖ وَحَاقَ بِهِم مَّا كَانُواْ بِهِۦ يَسْتَهْزِءُونَۖ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1486,12,'222',11,'Hūd','هُود',9,10,9,'وَلَئِنْ أَذَقْنَا اَ۬لْإِنسَٰنَ مِنَّا رَحْمَةٗ ثُمَّ نَزَعْنَٰهَا مِنْهُ إِنَّهُۥ لَيَـُٔوسٞ كَفُورٞۖ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1487,12,'222',11,'Hūd','هُود',10,11,10,'وَلَئِنْ أَذَقْنَٰهُ نَعْمَآءَ بَعْدَ ضَرَّآءَ مَسَّتْهُ لَيَقُولَنَّ ذَهَبَ اَ۬لسَّيِّـَٔاتُ عَنِّيَۖ إِنَّهُۥ لَفَرِحٞ فَخُورٌۖ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1488,12,'222',11,'Hūd','هُود',11,13,11,'إِلَّا اَ۬لذِينَ صَبَرُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ أُوْلَٰٓئِكَ لَهُم مَّغْفِرَةٞ وَأَجْرٞ كَبِيرٞۖ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1489,12,'222',11,'Hūd','هُود',13,15,12,'فَلَعَلَّكَ تَارِكُۢ بَعْضَ مَا يُوحَىٰ إِلَيْكَ وَضَآئِقُۢ بِهِۦ صَدْرُكَ أَنْ يَّقُولُواْ لَوْلَا أُنزِلَ عَلَيْهِ كَنزٌ أَوْ جَآءَ مَعَهُۥ مَلَكٌۖ إِنَّمَا أَنتَ نَذِيرٞۖ وَاللَّهُ عَلَىٰ كُلِّ شَےْءٖ وَكِيلٌۖ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1490,12,'223',11,'Hūd','هُود',1,2,13,'أَمْ يَقُولُونَ اَ۪فْتَرَيٰهُۖ قُلْ فَأْتُواْ بِعَشْرِ سُوَرٖ مِّثْلِهِۦ مُفْتَرَيَٰتٖ وَادْعُواْ مَنِ اِ۪سْتَطَعْتُم مِّن دُونِ اِ۬للَّهِ إِن كُنتُمْ صَٰدِقِينَۖ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1491,12,'223',11,'Hūd','هُود',3,4,14,'فَإِلَّمْ يَسْتَجِيبُواْ لَكُمْ فَاعْلَمُواْ أَنَّمَا أُنزِلَ بِعِلْمِ اِ۬للَّهِ وَأَن لَّا إِلَٰهَ إِلَّا هُوَۖ فَهَلْ أَنتُم مُّسْلِمُونَۖ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1492,12,'223',11,'Hūd','هُود',4,6,15,'۞ مَن كَانَ يُرِيدُ اُ۬لْحَيَوٰةَ اَ۬لدُّنْيَا وَزِينَتَهَا نُوَفِّ إِلَيْهِمْ أَعْمَٰلَهُمْ فِيهَا وَهُمْ فِيهَا لَا يُبْخَسُونَۖ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1493,12,'223',11,'Hūd','هُود',6,7,16,'أُوْلَٰٓئِكَ اَ۬لذِينَ لَيْسَ لَهُمْ فِے اِ۬لْأٓخِرَةِ إِلَّا اَ۬لنَّارُۖ وَحَبِطَ مَا صَنَعُواْ فِيهَاۖ وَبَٰطِلٞ مَّا كَانُواْ يَعْمَلُونَۖ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1494,12,'223',11,'Hūd','هُود',8,11,17,'أَفَمَن كَانَ عَلَىٰ بَيِّنَةٖ مِّن رَّبِّهِۦ وَيَتْلُوهُ شَاهِدٞ مِّنْهُۖ وَمِن قَبْلِهِۦ كِتَٰبُ مُوسَىٰ إِمَاماٗ وَرَحْمَةًۖ أُوْلَٰٓئِكَ يُؤْمِنُونَ بِهِۦۖ وَمَنْ يَّكْفُرْ بِهِۦ مِنَ اَ۬لْأَحْزَابِ فَالنَّارُ مَوْعِدُهُۥۖ فَلَا تَكُ فِے مِرْيَةٖ مِّنْهُۖ إِنَّهُ اُ۬لْحَقُّ مِن رَّبِّكَۖ وَلَٰكِنَّ أَكْثَرَ اَ۬لنَّاسِ لَا يُؤْمِنُونَۖ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1495,12,'223',11,'Hūd','هُود',12,14,18,'وَمَنْ أَظْلَمُ مِمَّنِ اِ۪فْتَرَىٰ عَلَى اَ۬للَّهِ كَذِباًۖ أُوْلَٰٓئِكَ يُعْرَضُونَ عَلَىٰ رَبِّهِمْ وَيَقُولُ اُ۬لْأَشْهَٰدُ هَٰؤُلَآءِ اِ۬لذِينَ كَذَبُواْ عَلَىٰ رَبِّهِمْۖ أَلَا لَعْنَةُ اُ۬للَّهِ عَلَى اَ۬لظَّٰلِمِينَ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1496,12,'223',11,'Hūd','هُود',14,15,19,'اَ۬لذِينَ يَصُدُّونَ عَن سَبِيلِ اِ۬للَّهِ وَيَبْغُونَهَا عِوَجاٗ وَهُم بِالْأٓخِرَةِ هُمْ كَٰفِرُونَۖ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1497,12,'224',11,'Hūd','هُود',1,3,20,'أُوْلَٰٓئِكَ لَمْ يَكُونُواْ مُعْجِزِينَ فِے اِ۬لْأَرْضِ وَمَا كَانَ لَهُم مِّن دُونِ اِ۬للَّهِ مِنْ أَوْلِيَآءَۖ يُضَٰعَفُ لَهُمُ اُ۬لْعَذَابُۖ مَا كَانُواْ يَسْتَطِيعُونَ اَ۬لسَّمْعَ وَمَا كَانُواْ يُبْصِرُونَۖ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1498,12,'224',11,'Hūd','هُود',3,4,21,'أُوْلَٰٓئِكَ اَ۬لذِينَ خَسِرُواْ أَنفُسَهُمْ وَضَلَّ عَنْهُم مَّا كَانُواْ يَفْتَرُونَۖ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1499,12,'224',11,'Hūd','هُود',4,5,22,'لَا جَرَمَ أَنَّهُمْ فِے اِ۬لْأٓخِرَةِ هُمُ اُ۬لْأَخْسَرُونَۖ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1500,12,'224',11,'Hūd','هُود',5,7,23,'إِنَّ اَ۬لذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ وَأَخْبَتُواْ إِلَىٰ رَبِّهِمْ أُوْلَٰٓئِكَ أَصْحَٰبُ اُ۬لْجَنَّةِۖ هُمْ فِيهَا خَٰلِدُونَۖ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1501,12,'224',11,'Hūd','هُود',7,8,24,'۞ مَثَلُ اُ۬لْفَرِيقَيْنِ كَالْأَعْمَىٰ وَالْأَصَمِّ وَالْبَصِيرِ وَالسَّمِيعِۖ هَلْ يَسْتَوِيَٰنِ مَثَلاًۖ أَفَلَا تَذَّكَّرُونَۖ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1502,12,'224',11,'Hūd','هُود',9,9,25,'وَلَقَدْ أَرْسَلْنَا نُوحاً إِلَىٰ قَوْمِهِۦۖ إِنِّے لَكُمْ نَذِيرٞ مُّبِينٌ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1503,12,'224',11,'Hūd','هُود',10,10,26,'أَن لَّا تَعْبُدُواْ إِلَّا اَ۬للَّهَۖ إِنِّيَ أَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ أَلِيمٖۖ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1504,12,'224',11,'Hūd','هُود',11,13,27,'فَقَالَ اَ۬لْمَلَأُ اُ۬لذِينَ كَفَرُواْ مِن قَوْمِهِۦ مَا نَرَيٰكَ إِلَّا بَشَراٗ مِّثْلَنَا وَمَا نَرَيٰكَ اَ۪تَّبَعَكَ إِلَّا اَ۬لذِينَ هُمْ أَرَاذِلُنَا بَادِيَ اَ۬لرَّأْيِۖ وَمَا نَرَىٰ لَكُمْ عَلَيْنَا مِن فَضْلِۢ بَلْ نَظُنُّكُمْ كَٰذِبِينَۖ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1505,12,'224',11,'Hūd','هُود',13,15,28,'قَالَ يَٰقَوْمِ أَرَٰ۬يْتُمْ إِن كُنتُ عَلَىٰ بَيِّنَةٖ مِّن رَّبِّے وَءَاتَيٰنِے رَحْمَةٗ مِّنْ عِندِهِۦ فَعَمِيَتْ عَلَيْكُمْ أَنُلْزِمُكُمُوهَا وَأَنتُمْ لَهَا كَٰرِهُونَۖ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1506,12,'225',11,'Hūd','هُود',1,3,29,'وَيَٰقَوْمِ لَا أَسْـَٔلُكُمْ عَلَيْهِ مَالاًۖ إِنْ أَجْرِيَ إِلَّا عَلَى اَ۬للَّهِۖ وَمَا أَنَا بِطَارِدِ اِ۬لذِينَ ءَامَنُواْ إِنَّهُم مُّلَٰقُواْ رَبِّهِمْۖ وَلَٰكِنِّيَ أَرَيٰكُمْ قَوْماٗ تَجْهَلُونَۖ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1507,12,'225',11,'Hūd','هُود',3,4,30,'وَيَٰقَوْمِ مَنْ يَّنصُرُنِے مِنَ اَ۬للَّهِ إِن طَرَدتُّهُمْۖ أَفَلَا تَذَّكَّرُونَۖ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1508,12,'225',11,'Hūd','هُود',4,7,31,'وَلَا أَقُولُ لَكُمْ عِندِے خَزَآئِنُ اُ۬للَّهِ وَلَا أَعْلَمُ اُ۬لْغَيْبَ وَلَا أَقُولُ إِنِّے مَلَكٞۖ وَلَا أَقُولُ لِلذِينَ تَزْدَرِے أَعْيُنُكُمْ لَنْ يُّؤْتِيَهُمُ اُ۬للَّهُ خَيْراًۖ اِ۬للَّهُ أَعْلَمُ بِمَا فِے أَنفُسِهِمْۖ إِنِّيَ إِذاٗ لَّمِنَ اَ۬لظَّٰلِمِينَۖ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1509,12,'225',11,'Hūd','هُود',7,8,32,'۞ قَالُواْ يَٰنُوحُ قَدْ جَٰدَلْتَنَا فَأَكْثَرْتَ جِدَٰلَنَا فَأْتِنَا بِمَا تَعِدُنَا إِن كُنتَ مِنَ اَ۬لصَّٰدِقِينَۖ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1510,12,'225',11,'Hūd','هُود',8,9,33,'قَالَ إِنَّمَا يَأْتِيكُم بِهِ اِ۬للَّهُ إِن شَآءَ وَمَا أَنتُم بِمُعْجِزِينَۖ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1511,12,'225',11,'Hūd','هُود',9,11,34,'وَلَا يَنفَعُكُمْ نُصْحِيَ إِنْ أَرَدتُّ أَنْ أَنصَحَ لَكُمْ إِن كَانَ اَ۬للَّهُ يُرِيدُ أَنْ يُّغْوِيَكُمْۖ هُوَ رَبُّكُمْۖ وَإِلَيْهِ تُرْجَعُونَۖ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1512,12,'225',11,'Hūd','هُود',11,12,35,'أَمْ يَقُولُونَ اَ۪فْتَرَيٰهُۖ قُلْ إِنِ اِ۪فْتَرَيْتُهُۥ فَعَلَيَّ إِجْرَامِے وَأَنَا بَرِےٓءٞ مِّمَّا تُجْرِمُونَۖ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1513,12,'225',11,'Hūd','هُود',13,14,36,'وَأُوحِيَ إِلَىٰ نُوحٍ أَنَّهُۥ لَنْ يُّؤْمِنَ مِن قَوْمِكَ إِلَّا مَن قَدْ ءَامَنَۖ فَلَا تَبْتَئِسْ بِمَا كَانُواْ يَفْعَلُونَۖ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1514,12,'225',11,'Hūd','هُود',14,15,37,'وَاصْنَعِ اِ۬لْفُلْكَ بِأَعْيُنِنَا وَوَحْيِنَاۖ وَلَا تُخَٰطِبْنِے فِے اِ۬لذِينَ ظَلَمُواْ إِنَّهُم مُّغْرَقُونَۖ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1515,12,'226',11,'Hūd','هُود',1,2,38,'وَيَصْنَعُ اُ۬لْفُلْكَ وَكُلَّمَا مَرَّ عَلَيْهِ مَلَأٞ مِّن قَوْمِهِۦ سَخِرُواْ مِنْهُۖ قَالَ إِن تَسْخَرُواْ مِنَّا فَإِنَّا نَسْخَرُ مِنكُمْ كَمَا تَسْخَرُونَۖ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1516,12,'226',11,'Hūd','هُود',3,4,39,'فَسَوْفَ تَعْلَمُونَ مَنْ يَّأْتِيهِ عَذَابٞ يُخْزِيهِ وَيَحِلُّ عَلَيْهِ عَذَابٞ مُّقِيمٌۖ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1517,12,'226',11,'Hūd','هُود',4,6,40,'حَتَّىٰ إِذَا جَا أَمْرُنَا وَفَارَ اَ۬لتَّنُّورُ قُلْنَا اَ۪حْمِلْ فِيهَا مِن كُلِّ زَوْجَيْنِ اِ۪ثْنَيْنِ وَأَهْلَكَ إِلَّا مَن سَبَقَ عَلَيْهِ اِ۬لْقَوْلُ وَمَنْ ءَامَنَۖ وَمَا ءَامَنَ مَعَهُۥ إِلَّا قَلِيلٞۖ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1518,12,'226',11,'Hūd','هُود',6,7,41,'۞ وَقَالَ اَ۪رْكَبُواْ فِيهَا بِسْمِ اِ۬للَّهِ مُجْرَيٰهَا وَمُرْسَيٰهَاۖ إِنَّ رَبِّے لَغَفُورٞ رَّحِيمٞۖ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1519,12,'226',11,'Hūd','هُود',7,9,42,'وَهْيَ تَجْرِے بِهِمْ فِے مَوْجٖ كَالْجِبَالِۖ وَنَادَىٰ نُوحٌ اِ۪بْنَهُۥ وَكَانَ فِے مَعْزِلٖ يَٰبُنَيِّ اِ۪رْكَب مَّعَنَا وَلَا تَكُن مَّعَ اَ۬لْكَٰفِرِينَۖ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1520,12,'226',11,'Hūd','هُود',9,12,43,'قَالَ سَـَٔاوِے إِلَىٰ جَبَلٖ يَعْصِمُنِے مِنَ اَ۬لْمَآءِۖ قَالَ لَا عَٰصِمَ اَ۬لْيَوْمَ مِنْ أَمْرِ اِ۬للَّهِ إِلَّا مَن رَّحِمَۖ وَحَالَ بَيْنَهُمَا اَ۬لْمَوْجُ فَكَانَ مِنَ اَ۬لْمُغْرَقِينَۖ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1521,12,'226',11,'Hūd','هُود',12,14,44,'وَقِيلَ يَٰأَرْضُ اُ۪بْلَعِے مَآءَكِ وَيَٰسَمَآءُ اَ۬قْلِعِےۖ وَغِيضَ اَ۬لْمَآءُ وَقُضِيَ اَ۬لْأَمْرُ وَاسْتَوَتْ عَلَى اَ۬لْجُودِيِّۖ وَقِيلَ بُعْداٗ لِّلْقَوْمِ اِ۬لظَّٰلِمِينَۖ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1522,12,'226',11,'Hūd','هُود',14,15,45,'وَنَادَىٰ نُوحٞ رَّبَّهُۥ فَقَالَ رَبِّ إِنَّ اَ۪بْنِے مِنْ أَهْلِے وَإِنَّ وَعْدَكَ اَ۬لْحَقُّۖ وَأَنتَ أَحْكَمُ اُ۬لْحَٰكِمِينَۖ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1523,12,'227',11,'Hūd','هُود',1,3,46,'قَالَ يَٰنُوحُ إِنَّهُۥ لَيْسَ مِنْ أَهْلِكَۖ إِنَّهُۥ عَمَلٌ غَيْرُ صَٰلِحٖۖ فَلَا تَسْـَٔلَنِّ مَا لَيْسَ لَكَ بِهِۦ عِلْمٌۖ إِنِّيَ أَعِظُكَ أَن تَكُونَ مِنَ اَ۬لْجَٰهِلِينَۖ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1524,12,'227',11,'Hūd','هُود',3,4,47,'قَالَ رَبِّ إِنِّيَ أَعُوذُ بِكَ أَنْ أَسْـَٔلَكَ مَا لَيْسَ لِے بِهِۦ عِلْمٞۖ وَإِلَّا تَغْفِرْ لِے وَتَرْحَمْنِے أَكُن مِّنَ اَ۬لْخَٰسِرِينَۖ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1525,12,'227',11,'Hūd','هُود',4,6,48,'قِيلَ يَٰنُوحُ اُ۪هْبِطْ بِسَلَٰمٖ مِّنَّا وَبَرَكَٰتٍ عَلَيْكَ وَعَلَىٰ أُمَمٖ مِّمَّن مَّعَكَۖ وَأُمَمٞ سَنُمَتِّعُهُمْ ثُمَّ يَمَسُّهُم مِّنَّا عَذَابٌ أَلِيمٞۖ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1526,12,'227',11,'Hūd','هُود',7,8,49,'تِلْكَ مِنْ أَنۢبَآءِ اِ۬لْغَيْبِ نُوحِيهَا إِلَيْكَۖ مَا كُنتَ تَعْلَمُهَا أَنتَ وَلَا قَوْمُكَ مِن قَبْلِ هَٰذَاۖ فَاصْبِرْۖ إِنَّ اَ۬لْعَٰقِبَةَ لِلْمُتَّقِينَۖ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1527,12,'227',11,'Hūd','هُود',9,10,50,'وَإِلَىٰ عَادٍ أَخَاهُمْ هُوداٗۖ قَالَ يَٰقَوْمِ اِ۟عْبُدُواْ اُ۬للَّهَ مَا لَكُم مِّنْ إِلَٰهٍ غَيْرُهُۥۖ إِنْ أَنتُمْ إِلَّا مُفْتَرُونَۖ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1528,12,'227',11,'Hūd','هُود',10,11,51,'يَٰقَوْمِ لَا أَسْـَٔلُكُمْ عَلَيْهِ أَجْراًۖ إِنْ أَجْرِيَ إِلَّا عَلَى اَ۬لذِے فَطَرَنِيَۖ أَفَلَا تَعْقِلُونَۖ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1529,12,'227',11,'Hūd','هُود',12,14,52,'وَيَٰقَوْمِ اِ۪سْتَغْفِرُواْ رَبَّكُمْ ثُمَّ تُوبُواْ إِلَيْهِ يُرْسِلِ اِ۬لسَّمَآءَ عَلَيْكُم مِّدْرَاراٗ وَيَزِدْكُمْ قُوَّةً إِلَىٰ قُوَّتِكُمْ وَلَا تَتَوَلَّوْاْ مُجْرِمِينَۖ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1530,12,'227',11,'Hūd','هُود',14,15,53,'۞ قَالُواْ يَٰهُودُ مَا جِئْتَنَا بِبَيِّنَةٖ وَمَا نَحْنُ بِتَارِكِے ءَالِهَتِنَا عَن قَوْلِكَ وَمَا نَحْنُ لَكَ بِمُؤْمِنِينَۖ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1531,12,'228',11,'Hūd','هُود',1,3,54,'إِن نَّقُولُ إِلَّا اَ۪عْتَرَيٰكَ بَعْضُ ءَالِهَتِنَا بِسُوٓءٖۖ قَالَ إِنِّيَ أُشْهِدُ اُ۬للَّهَۖ وَاشْهَدُواْ أَنِّے بَرِےٓءٞ مِّمَّا تُشْرِكُونَ مِن دُونِهِۦۖ فَكِيدُونِے جَمِيعاٗ ثُمَّ لَا تُنظِرُونِۖ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1532,12,'228',11,'Hūd','هُود',3,4,55,'إِنِّے تَوَكَّلْتُ عَلَى اَ۬للَّهِ رَبِّے وَرَبِّكُمۖ مَّا مِن دَآبَّةٍ إِلَّا هُوَ ءَاخِذُۢ بِنَاصِيَتِهَاۖ إِنَّ رَبِّے عَلَىٰ صِرَٰطٖ مُّسْتَقِيمٖۖ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1533,12,'228',11,'Hūd','هُود',4,6,56,'فَإِن تَوَلَّوْاْ فَقَدْ أَبْلَغْتُكُم مَّا أُرْسِلْتُ بِهِۦ إِلَيْكُمْۖ وَيَسْتَخْلِفُ رَبِّے قَوْماً غَيْرَكُمْ وَلَا تَضُرُّونَهُۥ شَئْاًۖ إِنَّ رَبِّے عَلَىٰ كُلِّ شَےْءٍ حَفِيظٞۖ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1534,12,'228',11,'Hūd','هُود',7,8,57,'وَلَمَّا جَا أَمْرُنَا نَجَّيْنَا هُوداٗ وَالذِينَ ءَامَنُواْ مَعَهُۥ بِرَحْمَةٖ مِّنَّا وَنَجَّيْنَٰهُم مِّنْ عَذَابٍ غَلِيظٖۖ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1535,12,'228',11,'Hūd','هُود',8,9,58,'وَتِلْكَ عَادٞ جَحَدُواْ بِـَٔايَٰتِ رَبِّهِمْ وَعَصَوْاْ رُسُلَهُۥ وَاتَّبَعُواْ أَمْرَ كُلِّ جَبَّارٍ عَنِيدٖۖ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1536,12,'228',11,'Hūd','هُود',9,11,59,'وَأُتْبِعُواْ فِے هَٰذِهِ اِ۬لدُّنْيَا لَعْنَةٗ وَيَوْمَ اَ۬لْقِيَٰمَةِۖ أَلَا إِنَّ عَاداٗ كَفَرُواْ رَبَّهُمْۖ أَلَا بُعْداٗ لِّعَادٖ قَوْمِ هُودٖۖ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1537,12,'228',11,'Hūd','هُود',11,13,60,'وَإِلَىٰ ثَمُودَ أَخَاهُمْ صَٰلِحاٗۖ قَالَ يَٰقَوْمِ اِ۟عْبُدُواْ اُ۬للَّهَ مَا لَكُم مِّنْ إِلَٰهٍ غَيْرُهُۥۖ هُوَ أَنشَأَكُم مِّنَ اَ۬لْأَرْضِ وَاسْتَعْمَرَكُمْ فِيهَا فَاسْتَغْفِرُوهُ ثُمَّ تُوبُواْ إِلَيْهِۖ إِنَّ رَبِّے قَرِيبٞ مُّجِيبٞۖ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1538,12,'228',11,'Hūd','هُود',14,15,61,'۞ قَالُواْ يَٰصَٰلِحُ قَدْ كُنتَ فِينَا مَرْجُوّاٗ قَبْلَ هَٰذَا أَتَنْهَيٰنَا أَن نَّعْبُدَ مَا يَعْبُدُ ءَابَآؤُنَا وَإِنَّنَا لَفِے شَكّٖ مِّمَّا تَدْعُونَا إِلَيْهِ مُرِيبٖۖ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1539,12,'229',11,'Hūd','هُود',1,3,62,'قَالَ يَٰقَوْمِ أَرَٰ۬يْتُمْ إِن كُنتُ عَلَىٰ بَيِّنَةٖ مِّن رَّبِّے وَءَاتَيٰنِے مِنْهُ رَحْمَةٗ فَمَنْ يَّنصُرُنِے مِنَ اَ۬للَّهِ إِنْ عَصَيْتُهُۥۖ فَمَا تَزِيدُونَنِے غَيْرَ تَخْسِيرٖۖ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1540,12,'229',11,'Hūd','هُود',3,5,63,'وَيَٰقَوْمِ هَٰذِهِۦ نَاقَةُ اُ۬للَّهِ لَكُمْ ءَايَةٗۖ فَذَرُوهَا تَأْكُلْ فِے أَرْضِ اِ۬للَّهِۖ وَلَا تَمَسُّوهَا بِسُوٓءٖ فَيَأْخُذَكُمْ عَذَابٞ قَرِيبٞۖ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1541,12,'229',11,'Hūd','هُود',5,6,64,'فَعَقَرُوهَا فَقَالَ تَمَتَّعُواْ فِے دَارِكُمْ ثَلَٰثَةَ أَيَّامٖۖ ذَٰلِكَ وَعْدٌ غَيْرُ مَكْذُوبٖۖ ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1542,12,'229',11,'Hūd','هُود',6,8,65,'فَلَمَّا جَا أَمْرُنَا نَجَّيْنَا صَٰلِحاٗ وَالذِينَ ءَامَنُواْ مَعَهُۥ بِرَحْمَةٖ مِّنَّا وَمِنْ خِزْيِ يَوْمَئِذٍۖ إِنَّ رَبَّكَ هُوَ اَ۬لْقَوِيُّ اُ۬لْعَزِيزُۖ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1543,12,'229',11,'Hūd','هُود',8,9,66,'وَأَخَذَ اَ۬لذِينَ ظَلَمُواْ اُ۬لصَّيْحَةُ فَأَصْبَحُواْ فِے دِيَٰرِهِمْ جَٰثِمِينَ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1544,12,'229',11,'Hūd','هُود',10,11,67,'كَأَن لَّمْ يَغْنَوْاْ فِيهَاۖ أَلَا إِنَّ ثَمُوداٗ كَفَرُواْ رَبَّهُمْۖ أَلَا بُعْداٗ لِّثَمُودَۖ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1545,12,'229',11,'Hūd','هُود',11,12,68,'وَلَقَدْ جَآءَتْ رُسُلُنَا إِبْرَٰهِيمَ بِالْبُشْرَىٰ قَالُواْ سَلَٰماٗۖ قَالَ سَلَٰمٞۖ فَمَا لَبِثَ أَن جَآءَ بِعِجْلٍ حَنِيذٖۖ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1546,12,'229',11,'Hūd','هُود',12,14,69,'فَلَمَّا رَءَا أَيْدِيَهُمْ لَا تَصِلُ إِلَيْهِ نَكِرَهُمْ وَأَوْجَسَ مِنْهُمْ خِيفَةٗۖ قَالُواْ لَا تَخَفْۖ إِنَّا أُرْسِلْنَا إِلَىٰ قَوْمِ لُوطٖۖ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1547,12,'229',11,'Hūd','هُود',14,15,70,'وَامْرَأَتُهُۥ قَآئِمَةٞ فَضَحِكَتْۖ فَبَشَّرْنَٰهَا بِإِسْحَٰقَۖ وَمِنْ وَّرَآ۟ إِسْحَٰقَ يَعْقُوبُۖ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1548,12,'230',11,'Hūd','هُود',1,2,71,'قَالَتْ يَٰوَيْلَتَىٰ ءَٰا۬لِدُ وَأَنَا عَجُوزٞ وَهَٰذَا بَعْلِے شَيْخاًۖ إِنَّ هَٰذَا لَشَےْءٌ عَجِيبٞۖ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1549,12,'230',11,'Hūd','هُود',2,3,72,'۞ قَالُواْ أَتَعْجَبِينَ مِنْ أَمْرِ اِ۬للَّهِۖ رَحْمَتُ اُ۬للَّهِ وَبَرَكَٰتُهُۥ عَلَيْكُمْ أَهْلَ اَ۬لْبَيْتِۖ إِنَّهُۥ حَمِيدٞ مَّجِيدٞۖ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1550,12,'230',11,'Hūd','هُود',3,5,73,'فَلَمَّا ذَهَبَ عَنْ إِبْرَٰهِيمَ اَ۬لرَّوْعُ وَجَآءَتْهُ اُ۬لْبُشْرَىٰ يُجَٰدِلُنَا فِے قَوْمِ لُوطٍۖ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1551,12,'230',11,'Hūd','هُود',5,5,74,'إِنَّ إِبْرَٰهِيمَ لَحَلِيمٌ أَوَّٰهٞ مُّنِيبٞۖ ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1552,12,'230',11,'Hūd','هُود',5,6,75,'يَٰإِبْرَٰهِيمُ أَعْرِضْ عَنْ هَٰذَاۖ إِنَّهُۥ قَدْ جَا أَمْرُ رَبِّكَۖ وَإِنَّهُمْ ءَاتِيهِمْ عَذَابٌ غَيْرُ مَرْدُودٖۖ ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1553,12,'230',11,'Hūd','هُود',7,8,76,'وَلَمَّا جَآءَتْ رُسُلُنَا لُوطاٗ س۬ےٓءَ بِهِمْ وَضَاقَ بِهِمْ ذَرْعاٗ وَقَالَ هَٰذَا يَوْمٌ عَصِيبٞۖ ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1554,12,'230',11,'Hūd','هُود',8,10,77,'وَجَآءَهُۥ قَوْمُهُۥ يُهْرَعُونَ إِلَيْهِ وَمِن قَبْلُ كَانُواْ يَعْمَلُونَ اَ۬لسَّيِّـَٔاتِۖ قَالَ يَٰقَوْمِ هَٰؤُلَآءِ بَنَاتِے هُنَّ أَطْهَرُ لَكُمْ فَاتَّقُواْ اُ۬للَّهَ وَلَا تُخْزُونِ فِے ضَيْفِيَۖ أَلَيْسَ مِنكُمْ رَجُلٞ رَّشِيدٞۖ ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1555,12,'230',11,'Hūd','هُود',10,11,78,'قَالُواْ لَقَدْ عَلِمْتَ مَا لَنَا فِے بَنَاتِكَ مِنْ حَقّٖ وَإِنَّكَ لَتَعْلَمُ مَا نُرِيدُۖ ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1556,12,'230',11,'Hūd','هُود',12,12,79,'قَالَ لَوْ أَنَّ لِے بِكُمْ قُوَّةً أَوْ ءَاوِے إِلَىٰ رُكْنٖ شَدِيدٖۖ ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1557,12,'230',11,'Hūd','هُود',12,15,80,'قَالُواْ يَٰلُوطُ إِنَّا رُسُلُ رَبِّكَ لَنْ يَّصِلُواْ إِلَيْكَۖ فَاسْرِ بِأَهْلِكَ بِقِطْعٖ مِّنَ اَ۬ليْلِ وَلَا يَلْتَفِتْ مِنكُمْ أَحَدٌ إِلَّا اَ۪مْرَأَتَكَۖ إِنَّهُۥ مُصِيبُهَا مَا أَصَابَهُمْۖ إِنَّ مَوْعِدَهُمُ اُ۬لصُّبْحُۖ أَلَيْسَ اَ۬لصُّبْحُ بِقَرِيبٖۖ ٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1558,12,'231',11,'Hūd','هُود',1,2,81,'فَلَمَّا جَا أَمْرُنَا جَعَلْنَا عَٰلِيَهَا سَافِلَهَا وَأَمْطَرْنَا عَلَيْهَا حِجَارَةٗ مِّن سِجِّيلٖ ٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1559,12,'231',11,'Hūd','هُود',2,3,82,'مَّنضُودٖ مُّسَوَّمَةً عِندَ رَبِّكَۖ وَمَا هِيَ مِنَ اَ۬لظَّٰلِمِينَ بِبَعِيدٖۖ ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1560,12,'231',11,'Hūd','هُود',3,6,83,'۞ وَإِلَىٰ مَدْيَنَ أَخَاهُمْ شُعَيْباٗۖ قَالَ يَٰقَوْمِ اِ۟عْبُدُواْ اُ۬للَّهَ مَا لَكُم مِّنْ إِلَٰهٍ غَيْرُهُۥۖ وَلَا تَنقُصُواْ اُ۬لْمِكْيَالَ وَالْمِيزَانَۖ إِنِّيَ أَرَيٰكُم بِخَيْرٖ وَإِنِّيَ أَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٖ مُّحِيطٖۖ ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1561,12,'231',11,'Hūd','هُود',6,8,84,'وَيَٰقَوْمِ أَوْفُواْ اُ۬لْمِكْيَالَ وَالْمِيزَانَ بِالْقِسْطِۖ وَلَا تَبْخَسُواْ اُ۬لنَّاسَ أَشْيَآءَهُمْ وَلَا تَعْثَوْاْ فِے اِ۬لْأَرْضِ مُفْسِدِينَۖ ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1562,12,'231',11,'Hūd','هُود',8,9,85,'بَقِيَّتُ اُ۬للَّهِ خَيْرٞ لَّكُمْ إِن كُنتُم مُّؤْمِنِينَۖ ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1563,12,'231',11,'Hūd','هُود',9,9,86,'وَمَا أَنَا عَلَيْكُم بِحَفِيظٖۖ ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1564,12,'231',11,'Hūd','هُود',9,12,87,'قَالُواْ يَٰشُعَيْبُ أَصَلَوَٰتُكَ تَأْمُرُكَ أَن نَّتْرُكَ مَا يَعْبُدُ ءَابَآؤُنَا أَوْ أَن نَّفْعَلَ فِے أَمْوَٰلِنَا مَا نَشَٰٓؤُاْۖ اِ۪نَّكَ لَأَنتَ اَ۬لْحَلِيمُ اُ۬لرَّشِيدُۖ ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1565,12,'231',11,'Hūd','هُود',12,15,88,'قَالَ يَٰقَوْمِ أَرَٰ۬يْتُمْ إِن كُنتُ عَلَىٰ بَيِّنَةٖ مِّن رَّبِّے وَرَزَقَنِے مِنْهُ رِزْقاً حَسَناٗۖ وَمَا أُرِيدُ أَنْ أُخَالِفَكُمْ إِلَىٰ مَا أَنْهَيٰكُمْ عَنْهُۖ إِنْ أُرِيدُ إِلَّا اَ۬لْإِصْلَٰحَ مَا اَ۪سْتَطَعْتُۖ وَمَا تَوْفِيقِيَ إِلَّا بِاللَّهِۖ عَلَيْهِ تَوَكَّلْتُۖ وَإِلَيْهِ أُنِيبُۖ ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1566,12,'232',11,'Hūd','هُود',1,3,89,'وَيَٰقَوْمِ لَا يَجْرِمَنَّكُمْ شِقَاقِيَ أَنْ يُّصِيبَكُم مِّثْلُ مَا أَصَابَ قَوْمَ نُوحٍ أَوْ قَوْمَ هُودٍ أَوْ قَوْمَ صَٰلِحٖۖ وَمَا قَوْمُ لُوطٖ مِّنكُم بِبَعِيدٖۖ ٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1567,12,'232',11,'Hūd','هُود',3,4,90,'وَاسْتَغْفِرُواْ رَبَّكُمْ ثُمَّ تُوبُواْ إِلَيْهِۖ إِنَّ رَبِّے رَحِيمٞ وَدُودٞۖ ٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1568,12,'232',11,'Hūd','هُود',4,6,91,'قَالُواْ يَٰشُعَيْبُ مَا نَفْقَهُ كَثِيراٗ مِّمَّا تَقُولُ وَإِنَّا لَنَرَيٰكَ فِينَا ضَعِيفاٗ وَلَوْلَا رَهْطُكَ لَرَجَمْنَٰكَ وَمَا أَنتَ عَلَيْنَا بِعَزِيزٖۖ ٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1569,12,'232',11,'Hūd','هُود',6,8,92,'قَالَ يَٰقَوْمِ أَرَهْطِيَ أَعَزُّ عَلَيْكُم مِّنَ اَ۬للَّهِ وَاتَّخَذتُّمُوهُ وَرَآءَكُمْ ظِهْرِيّاًۖ إِنَّ رَبِّے بِمَا تَعْمَلُونَ مُحِيطٞۖ ٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1570,12,'232',11,'Hūd','هُود',8,10,93,'۞ وَيَٰقَوْمِ اِ۪عْمَلُواْ عَلَىٰ مَكَانَتِكُمْ إِنِّے عَٰمِلٞۖ سَوْفَ تَعْلَمُونَ مَنْ يَّأْتِيهِ عَذَابٞ يُخْزِيهِ وَمَنْ هُوَ كَٰذِبٞۖ وَارْتَقِبُواْۖ إِنِّے مَعَكُمْ رَقِيبٞۖ ٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1571,12,'232',11,'Hūd','هُود',10,12,94,'وَلَمَّا جَا أَمْرُنَا نَجَّيْنَا شُعَيْباٗ وَالذِينَ ءَامَنُواْ مَعَهُۥ بِرَحْمَةٖ مِّنَّاۖ وَأَخَذَتِ اِ۬لذِينَ ظَلَمُواْ اُ۬لصَّيْحَةُ فَأَصْبَحُواْ فِے دِيَٰرِهِمْ جَٰثِمِينَ ٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1572,12,'232',11,'Hūd','هُود',13,13,95,'كَأَن لَّمْ يَغْنَوْاْ فِيهَاۖ أَلَا بُعْداٗ لِّمَدْيَنَ كَمَا بَعِدَتْ ثَمُودُۖ ٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1573,12,'232',11,'Hūd','هُود',14,14,96,'وَلَقَدْ أَرْسَلْنَا مُوسَىٰ بِـَٔايَٰتِنَا وَسُلْطَٰنٖ مُّبِينٍ ٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1574,12,'232',11,'Hūd','هُود',14,15,97,'إِلَىٰ فِرْعَوْنَ وَمَلَإِيْهِۦ فَاتَّبَعُواْ أَمْرَ فِرْعَوْنَۖ وَمَا أَمْرُ فِرْعَوْنَ بِرَشِيدٖۖ ٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1575,12,'233',11,'Hūd','هُود',1,2,98,'يَقْدُمُ قَوْمَهُۥ يَوْمَ اَ۬لْقِيَٰمَةِ فَأَوْرَدَهُمُ اُ۬لنَّارَۖ وَبِئْسَ اَ۬لْوِرْدُ اُ۬لْمَوْرُودُۖ ٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1576,12,'233',11,'Hūd','هُود',2,3,99,'وَأُتْبِعُواْ فِے هَٰذِهِۦ لَعْنَةٗ وَيَوْمَ اَ۬لْقِيَٰمَةِۖ بِئْسَ اَ۬لرِّفْدُ اُ۬لْمَرْفُودُۖ ٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1577,12,'233',11,'Hūd','هُود',3,4,100,'ذَٰلِكَ مِنْ أَنۢبَآءِ اِ۬لْقُرَىٰ نَقُصُّهُۥ عَلَيْكَ مِنْهَا قَآئِمٞ وَحَصِيدٞۖ ١٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1578,12,'233',11,'Hūd','هُود',4,6,101,'وَمَا ظَلَمْنَٰهُمْ وَلَٰكِن ظَلَمُواْ أَنفُسَهُمْۖ فَمَا أَغْنَتْ عَنْهُمْ ءَالِهَتُهُمُ اُ۬لتِے يَدْعُونَ مِن دُونِ اِ۬للَّهِ مِن شَےْءٖ لَّمَّا جَا أَمْرُ رَبِّكَۖ وَمَا زَادُوهُمْ غَيْرَ تَتْبِيبٖۖ ١٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1579,12,'233',11,'Hūd','هُود',7,8,102,'وَكَذَٰلِكَ أَخْذُ رَبِّكَ إِذَا أَخَذَ اَ۬لْقُرَىٰ وَهْيَ ظَالِمَةٌۖ إِنَّ أَخْذَهُۥ أَلِيمٞ شَدِيدٌۖ ١٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1580,12,'233',11,'Hūd','هُود',8,9,103,'إِنَّ فِے ذَٰلِكَ لَأٓيَةٗ لِّمَنْ خَافَ عَذَابَ اَ۬لْأٓخِرَةِۖ ذَٰلِكَ يَوْمٞ مَّجْمُوعٞ لَّهُ اُ۬لنَّاسُۖ وَذَٰلِكَ يَوْمٞ مَّشْهُودٞۖ ١٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1581,12,'233',11,'Hūd','هُود',10,10,104,'وَمَا نُؤَخِّرُهُۥ إِلَّا لِأَجَلٖ مَّعْدُودٖۖ ١٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1582,12,'233',11,'Hūd','هُود',10,11,105,'۞ يَوْمَ يَأْتِۦ لَا تَكَلَّمُ نَفْسٌ إِلَّا بِإِذْنِهِۦۖ فَمِنْهُمْ شَقِيّٞ وَسَعِيدٞۖ ١٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1583,12,'233',11,'Hūd','هُود',11,12,106,'فَأَمَّا اَ۬لذِينَ شَقُواْ فَفِے اِ۬لنَّارِ لَهُمْ فِيهَا زَفِيرٞ وَشَهِيقٌ ١٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1584,12,'233',11,'Hūd','هُود',12,13,107,'خَٰلِدِينَ فِيهَا مَا دَامَتِ اِ۬لسَّمَٰوَٰتُ وَالْأَرْضُ إِلَّا مَا شَآءَ رَبُّكَۖ إِنَّ رَبَّكَ فَعَّالٞ لِّمَا يُرِيدُۖ ١٠٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1585,12,'233',11,'Hūd','هُود',14,15,108,'وَأَمَّا اَ۬لذِينَ سَعِدُواْ فَفِے اِ۬لْجَنَّةِ خَٰلِدِينَ فِيهَا مَا دَامَتِ اِ۬لسَّمَٰوَٰتُ وَالْأَرْضُ إِلَّا مَا شَآءَ رَبُّكَۖ عَطَآءً غَيْرَ مَجْذُوذٖۖ ١٠٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1586,12,'234',11,'Hūd','هُود',1,3,109,'فَلَا تَكُ فِے مِرْيَةٖ مِّمَّا يَعْبُدُ هَٰؤُلَآءِۖ مَا يَعْبُدُونَ إِلَّا كَمَا يَعْبُدُ ءَابَآؤُهُم مِّن قَبْلُۖ وَإِنَّا لَمُوَفُّوهُمْ نَصِيبَهُمْ غَيْرَ مَنقُوصٖۖ ١٠٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1587,12,'234',11,'Hūd','هُود',3,5,110,'وَلَقَدْ ءَاتَيْنَا مُوسَى اَ۬لْكِتَٰبَ فَاخْتُلِفَ فِيهِۖ وَلَوْلَا كَلِمَةٞ سَبَقَتْ مِن رَّبِّكَ لَقُضِيَ بَيْنَهُمْۖ وَإِنَّهُمْ لَفِے شَكّٖ مِّنْهُ مُرِيبٖۖ ١١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1588,12,'234',11,'Hūd','هُود',5,6,111,'وَإِن كُلّاٗ لَّمَا لَيُوَفِّيَنَّهُمْ رَبُّكَ أَعْمَٰلَهُمْۖ إِنَّهُۥ بِمَا يَعْمَلُونَ خَبِيرٞۖ ١١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1589,12,'234',11,'Hūd','هُود',6,7,112,'فَاسْتَقِمْ كَمَا أُمِرْتَ وَمَن تَابَ مَعَكَ وَلَا تَطْغَوْاْۖ إِنَّهُۥ بِمَا تَعْمَلُونَ بَصِيرٞۖ ١١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1590,12,'234',11,'Hūd','هُود',7,9,113,'وَلَا تَرْكَنُواْ إِلَى اَ۬لذِينَ ظَلَمُواْ فَتَمَسَّكُمُ اُ۬لنَّارُۖ وَمَا لَكُم مِّن دُونِ اِ۬للَّهِ مِنْ أَوْلِيَآءَۖ ثُمَّ لَا تُنصَرُونَۖ ١١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1591,12,'234',11,'Hūd','هُود',9,11,114,'وَأَقِمِ اِ۬لصَّلَوٰةَ طَرَفَيِ اِ۬لنَّهَارِ وَزُلَفاٗ مِّنَ اَ۬ليْلِۖ إِنَّ اَ۬لْحَسَنَٰتِ يُذْهِبْنَ اَ۬لسَّيِّـَٔاتِۖ ذَٰلِكَ ذِكْرَىٰ لِلذَّٰكِرِينَۖ ١١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1592,12,'234',11,'Hūd','هُود',11,11,115,'وَاصْبِرْۖ فَإِنَّ اَ۬للَّهَ لَا يُضِيعُ أَجْرَ اَ۬لْمُحْسِنِينَۖ ١١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1593,12,'234',11,'Hūd','هُود',12,14,116,'فَلَوْلَا كَانَ مِنَ اَ۬لْقُرُونِ مِن قَبْلِكُمْ أُوْلُواْ بَقِيَّةٖ يَنْهَوْنَ عَنِ اِ۬لْفَسَادِ فِے اِ۬لْأَرْضِ إِلَّا قَلِيلاٗ مِّمَّنْ أَنجَيْنَا مِنْهُمْۖ وَاتَّبَعَ اَ۬لذِينَ ظَلَمُواْ مَا أُتْرِفُواْ فِيهِ وَكَانُواْ مُجْرِمِينَۖ ١١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1594,12,'234',11,'Hūd','هُود',14,15,117,'۞ وَمَا كَانَ رَبُّكَ لِيُهْلِكَ اَ۬لْقُرَىٰ بِظُلْمٖ وَأَهْلُهَا مُصْلِحُونَۖ ١١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1595,12,'235',11,'Hūd','هُود',1,3,118,'وَلَوْ شَآءَ رَبُّكَ لَجَعَلَ اَ۬لنَّاسَ أُمَّةٗ وَٰحِدَةٗۖ وَلَا يَزَالُونَ مُخْتَلِفِينَ إِلَّا مَن رَّحِمَ رَبُّكَۖ وَلِذَٰلِكَ خَلَقَهُمْۖ وَتَمَّتْ كَلِمَةُ رَبِّكَ لَأَمْلَأَنَّ جَهَنَّمَ مِنَ اَ۬لْجِنَّةِ وَالنَّاسِ أَجْمَعِينَۖ ١١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1596,12,'235',11,'Hūd','هُود',3,5,119,'وَكُلّاٗ نَّقُصُّ عَلَيْكَ مِنْ أَنۢبَآءِ اِ۬لرُّسُلِ مَا نُثَبِّتُ بِهِۦ فُؤَادَكَۖ وَجَآءَكَ فِے هَٰذِهِ اِ۬لْحَقُّ وَمَوْعِظَةٞ وَذِكْرَىٰ لِلْمُؤْمِنِينَۖ ١١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1597,12,'235',11,'Hūd','هُود',5,6,120,'وَقُل لِّلذِينَ لَا يُؤْمِنُونَ اَ۪عْمَلُواْ عَلَىٰ مَكَانَتِكُمْ إِنَّا عَٰمِلُونَ وَانتَظِرُواْ إِنَّا مُنتَظِرُونَۖ ١٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1598,12,'235',11,'Hūd','هُود',7,8,121,'وَلِلهِ غَيْبُ اُ۬لسَّمَٰوَٰتِ وَالْأَرْضِۖ وَإِلَيْهِ يُرْجَعُ اُ۬لْأَمْرُ كُلُّهُۥۖ فَاعْبُدْهُ وَتَوَكَّلْ عَلَيْهِۖ وَمَا رَبُّكَ بِغَٰفِلٍ عَمَّا تَعْمَلُونَۖ ١٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1599,12,'235',12,'Yūsuf','يُوسُف',11,11,1,'أَلَٓرَۖ تِلْكَ ءَايَٰتُ اُ۬لْكِتَٰبِ اِ۬لْمُبِينِۖ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1600,12,'235',12,'Yūsuf','يُوسُف',11,12,2,'إِنَّا أَنزَلْنَٰهُ قُرْءَٰناً عَرَبِيّاٗ لَّعَلَّكُمْ تَعْقِلُونَۖ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1601,12,'235',12,'Yūsuf','يُوسُف',12,14,3,'نَحْنُ نَقُصُّ عَلَيْكَ أَحْسَنَ اَ۬لْقَصَصِ بِمَا أَوْحَيْنَا إِلَيْكَ هَٰذَا اَ۬لْقُرْءَانَ وَإِن كُنتَ مِن قَبْلِهِۦ لَمِنَ اَ۬لْغَٰفِلِينَۖ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1602,12,'235',12,'Yūsuf','يُوسُف',14,15,4,'إِذْ قَالَ يُوسُفُ لِأَبِيهِ يَٰأَبَتِ إِنِّے رَأَيْتُ أَحَدَ عَشَرَ كَوْكَباٗ وَالشَّمْسَ وَالْقَمَرَۖ رَأَيْتُهُمْ لِے سَٰجِدِينَۖ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1603,12,'236',12,'Yūsuf','يُوسُف',1,2,5,'قَالَ يَٰبُنَيِّ لَا تَقْصُصْ رُءْيَاكَ عَلَىٰ إِخْوَتِكَ فَيَكِيدُواْ لَكَ كَيْداًۖ إِنَّ اَ۬لشَّيْطَٰنَ لِلْإِنسَٰنِ عَدُوّٞ مُّبِينٞۖ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1604,12,'236',12,'Yūsuf','يُوسُف',2,5,6,'وَكَذَٰلِكَ يَجْتَبِيكَ رَبُّكَ وَيُعَلِّمُكَ مِن تَأْوِيلِ اِ۬لْأَحَادِيثِۖ وَيُتِمُّ نِعْمَتَهُۥ عَلَيْكَ وَعَلَىٰ ءَالِ يَعْقُوبَ كَمَا أَتَمَّهَا عَلَىٰ أَبَوَيْكَ مِن قَبْلُ إِبْرَٰهِيمَ وَإِسْحَٰقَۖ إِنَّ رَبَّكَ عَلِيمٌ حَكِيمٞۖ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1605,12,'236',12,'Yūsuf','يُوسُف',5,6,7,'لَّقَدْ كَانَ فِے يُوسُفَ وَإِخْوَتِهِۦ ءَايَٰتٞ لِّلسَّآئِلِينَۖ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1606,12,'236',12,'Yūsuf','يُوسُف',6,7,8,'إِذْ قَالُواْ لَيُوسُفُ وَأَخُوهُ أَحَبُّ إِلَىٰ أَبِينَا مِنَّا وَنَحْنُ عُصْبَةٌۖ إِنَّ أَبَانَا لَفِے ضَلَٰلٖ مُّبِينٍۖ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1607,12,'236',12,'Yūsuf','يُوسُف',8,9,9,'اُ۟قْتُلُواْ يُوسُفَ أَوِ اِ۪طْرَحُوهُ أَرْضاٗ يَخْلُ لَكُمْ وَجْهُ أَبِيكُمْ وَتَكُونُواْ مِنۢ بَعْدِهِۦ قَوْماٗ صَٰلِحِينَۖ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1608,12,'236',12,'Yūsuf','يُوسُف',9,11,10,'۞ قَالَ قَآئِلٞ مِّنْهُمْ لَا تَقْتُلُواْ يُوسُفَ وَأَلْقُوهُ فِے غَيَٰبَٰتِ اِ۬لْجُبِّ يَلْتَقِطْهُ بَعْضُ اُ۬لسَّيَّارَةِ إِن كُنتُمْ فَٰعِلِينَۖ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1609,12,'236',12,'Yūsuf','يُوسُف',11,12,11,'قَالُواْ يَٰأَبَانَا مَا لَكَ لَا تَأْمَ۬نَّا عَلَىٰ يُوسُفَ وَإِنَّا لَهُۥ لَنَٰصِحُونَۖ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1610,12,'236',12,'Yūsuf','يُوسُف',12,13,12,'أَرْسِلْهُ مَعَنَا غَداٗ يَرْتَعِ وَيَلْعَبْ وَإِنَّا لَهُۥ لَحَٰفِظُونَۖ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1611,12,'236',12,'Yūsuf','يُوسُف',13,14,13,'قَالَ إِنِّے لَيُحْزِنُنِيَ أَن تَذْهَبُواْ بِهِۦ وَأَخَافُ أَنْ يَّأْكُلَهُ اُ۬لذِّئْبُ وَأَنتُمْ عَنْهُ غَٰفِلُونَۖ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1612,12,'236',12,'Yūsuf','يُوسُف',14,15,14,'قَالُواْ لَئِنْ أَكَلَهُ اُ۬لذِّئْبُ وَنَحْنُ عُصْبَةٌ إِنَّا إِذاٗ لَّخَٰسِرُونَۖ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1613,12,'237',12,'Yūsuf','يُوسُف',1,2,15,'فَلَمَّا ذَهَبُواْ بِهِۦ وَأَجْمَعُواْ أَنْ يَّجْعَلُوهُ فِے غَيَٰبَٰتِ اِ۬لْجُبِّ وَأَوْحَيْنَا إِلَيْهِ لَتُنَبِّئَنَّهُم بِأَمْرِهِمْ هَٰذَا وَهُمْ لَا يَشْعُرُونَۖ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1614,12,'237',12,'Yūsuf','يُوسُف',2,3,16,'وَجَآءُو أَبَاهُمْ عِشَآءٗ يَبْكُونَ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1615,12,'237',12,'Yūsuf','يُوسُف',3,5,17,'قَالُواْ يَٰأَبَانَا إِنَّا ذَهَبْنَا نَسْتَبِقُ وَتَرَكْنَا يُوسُفَ عِندَ مَتَٰعِنَا فَأَكَلَهُ اُ۬لذِّئْبُۖ وَمَا أَنتَ بِمُؤْمِنٖ لَّنَا وَلَوْ كُنَّا صَٰدِقِينَۖ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1616,12,'237',12,'Yūsuf','يُوسُف',5,7,18,'وَجَآءُو عَلَىٰ قَمِيصِهِۦ بِدَمٖ كَذِبٖۖ قَالَ بَلْ سَوَّلَتْ لَكُمْ أَنفُسُكُمْ أَمْراٗۖ فَصَبْرٞ جَمِيلٞۖ وَاللَّهُ اُ۬لْمُسْتَعَانُ عَلَىٰ مَا تَصِفُونَۖ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1617,12,'237',12,'Yūsuf','يُوسُف',7,9,19,'وَجَآءَتْ سَيَّارَةٞ فَأَرْسَلُواْ وَارِدَهُمْ فَأَدْلَىٰ دَلْوَهُۥ قَالَ يَٰبُشْرَٰيَ هَٰذَا غُلَٰمٞۖ وَأَسَرُّوهُ بِضَٰعَةٗۖ وَاللَّهُ عَلِيمُۢ بِمَا يَعْمَلُونَۖ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1618,12,'237',12,'Yūsuf','يُوسُف',9,10,20,'وَشَرَوْهُ بِثَمَنِۢ بَخْسٖ دَرَٰهِمَ مَعْدُودَةٖ وَكَانُواْ فِيهِ مِنَ اَ۬لزَّٰهِدِينَۖ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1619,12,'237',12,'Yūsuf','يُوسُف',10,14,21,'وَقَالَ اَ۬لذِے اِ۪شْتَرَيٰهُ مِن مِّصْرَ لِامْرَأَتِهِۦ أَكْرِمِے مَثْوَيٰهُ عَسَىٰ أَنْ يَّنفَعَنَا أَوْ نَتَّخِذَهُۥ وَلَداٗۖ وَكَذَٰلِكَ مَكَّنَّا لِيُوسُفَ فِے اِ۬لْأَرْضِ وَلِنُعَلِّمَهُۥ مِن تَأْوِيلِ اِ۬لْأَحَادِيثِۖ وَاللَّهُ غَالِبٌ عَلَىٰ أَمْرِهِۦۖ وَلَٰكِنَّ أَكْثَرَ اَ۬لنَّاسِ لَا يَعْلَمُونَۖ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1620,12,'237',12,'Yūsuf','يُوسُف',14,15,22,'وَلَمَّا بَلَغَ أَشُدَّهُۥ ءَاتَيْنَٰهُ حُكْماٗ وَعِلْماٗۖ وَكَذَٰلِكَ نَجْزِے اِ۬لْمُحْسِنِينَۖ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1621,12,'238',12,'Yūsuf','يُوسُف',1,3,23,'۞ وَرَٰوَدَتْهُ اُ۬لتِے هُوَ فِے بَيْتِهَا عَن نَّفْسِهِۦ وَغَلَّقَتِ اِ۬لْأَبْوَٰبَ وَقَالَتْ هِيتَ لَكَۖ قَالَ مَعَاذَ اَ۬للَّهِۖ إِنَّهُۥ رَبِّيَ أَحْسَنَ مَثْوَايَۖ إِنَّهُۥ لَا يُفْلِحُ اُ۬لظَّٰلِمُونَۖ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1622,12,'238',12,'Yūsuf','يُوسُف',3,5,24,'وَلَقَدْ هَمَّتْ بِهِۦۖ وَهَمَّ بِهَا لَوْلَا أَن رَّءَا بُرْهَٰنَ رَبِّهِۦۖ كَذَٰلِكَ لِنَصْرِفَ عَنْهُ اُ۬لسُّوٓءَ وَالْفَحْشَآءَۖ ا۪نَّهُۥ مِنْ عِبَادِنَا اَ۬لْمُخْلَصِينَۖ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1623,12,'238',12,'Yūsuf','يُوسُف',5,8,25,'وَاسْتَبَقَا اَ۬لْبَابَ وَقَدَّتْ قَمِيصَهُۥ مِن دُبُرٖ وَأَلْفَيَا سَيِّدَهَا لَدَا اَ۬لْبَابِۖ قَالَتْ مَا جَزَآءُ مَنْ أَرَادَ بِأَهْلِكَ سُوٓءاً إِلَّا أَنْ يُّسْجَنَ أَوْ عَذَابٌ أَلِيمٞۖ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1624,12,'238',12,'Yūsuf','يُوسُف',8,10,26,'قَالَ هِيَ رَٰوَدَتْنِے عَن نَّفْسِےۖ وَشَهِدَ شَاهِدٞ مِّنْ أَهْلِهَا إِن كَانَ قَمِيصُهُۥ قُدَّ مِن قُبُلٖ فَصَدَقَتْ وَهْوَ مِنَ اَ۬لْكَٰذِبِينَ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1625,12,'238',12,'Yūsuf','يُوسُف',10,11,27,'وَإِن كَانَ قَمِيصُهُۥ قُدَّ مِن دُبُرٖ فَكَذَبَتْۖ وَهْوَ مِنَ اَ۬لصَّٰدِقِينَۖ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1626,12,'238',12,'Yūsuf','يُوسُف',11,12,28,'فَلَمَّا رَءَا قَمِيصَهُۥ قُدَّ مِن دُبُرٖ قَالَ إِنَّهُۥ مِن كَيْدِكُنَّ إِنَّ كَيْدَكُنَّ عَظِيمٞۖ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1627,12,'238',12,'Yūsuf','يُوسُف',12,13,29,'يُوسُفُ أَعْرِضْ عَنْ هَٰذَاۖ وَاسْتَغْفِرِے لِذَنۢبِكِ إِنَّكِ كُنتِ مِنَ اَ۬لْخَاطِـِٕينَۖ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1628,12,'238',12,'Yūsuf','يُوسُف',14,15,30,'وَقَالَ نِسْوَةٞ فِے اِ۬لْمَدِينَةِ اِ۪مْرَأَتُ اُ۬لْعَزِيزِ تُرَٰوِدُ فَتَيٰهَا عَن نَّفْسِهِۦ قَدْ شَغَفَهَا حُبّاًۖ إِنَّا لَنَرَيٰهَا فِے ضَلَٰلٖ مُّبِينٖۖ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1629,12,'239',12,'Yūsuf','يُوسُف',1,4,31,'فَلَمَّا سَمِعَتْ بِمَكْرِهِنَّ أَرْسَلَتْ إِلَيْهِنَّ وَأَعْتَدَتْ لَهُنَّ مُتَّكَـٔاٗ وَءَاتَتْ كُلَّ وَٰحِدَةٖ مِّنْهُنَّ سِكِّيناٗ وَقَالَتُ اُ۟خْرُجْ عَلَيْهِنَّۖ فَلَمَّا رَأَيْنَهُۥ أَكْبَرْنَهُۥ وَقَطَّعْنَ أَيْدِيَهُنَّ وَقُلْنَ حَٰشَ لِلهِ مَا هَٰذَا بَشَراًۖ إِنْ هَٰذَا إِلَّا مَلَكٞ كَرِيمٞۖ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1630,12,'239',12,'Yūsuf','يُوسُف',4,6,32,'قَالَتْ فَذَٰلِكُنَّ اَ۬لذِے لُمْتُنَّنِے فِيهِۖ وَلَقَدْ رَٰوَدتُّهُۥ عَن نَّفْسِهِۦ فَاسْتَعْصَمَۖ وَلَئِن لَّمْ يَفْعَلْ مَا ءَامُرُهُۥ لَيُسْجَنَنَّ وَلَيَكُوناٗ مِّنَ اَ۬لصَّٰغِرِينَۖ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1631,12,'239',12,'Yūsuf','يُوسُف',6,8,33,'۞ قَالَ رَبِّ اِ۬لسِّجْنُ أَحَبُّ إِلَيَّ مِمَّا يَدْعُونَنِے إِلَيْهِ وَإِلَّا تَصْرِفْ عَنِّے كَيْدَهُنَّ أَصْبُ إِلَيْهِنَّ وَأَكُن مِّنَ اَ۬لْجَٰهِلِينَۖ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1632,12,'239',12,'Yūsuf','يُوسُف',8,9,34,'فَاسْتَجَابَ لَهُۥ رَبُّهُۥ فَصَرَفَ عَنْهُ كَيْدَهُنَّۖ إِنَّهُۥ هُوَ اَ۬لسَّمِيعُ اُ۬لْعَلِيمُۖ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1633,12,'239',12,'Yūsuf','يُوسُف',9,10,35,'ثُمَّ بَدَا لَهُم مِّنۢ بَعْدِ مَا رَأَوُاْ اُ۬لْأٓيَٰتِ لَيَسْجُنُنَّهُۥ حَتَّىٰ حِينٖۖ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1634,12,'239',12,'Yūsuf','يُوسُف',10,13,36,'وَدَخَلَ مَعَهُ اُ۬لسِّجْنَ فَتَيَٰنِۖ قَالَ أَحَدُهُمَا إِنِّيَ أَرَيٰنِيَ أَعْصِرُ خَمْراٗۖ وَقَالَ اَ۬لْأٓخَرُ إِنِّيَ أَرَيٰنِيَ أَحْمِلُ فَوْقَ رَأْسِے خُبْزاٗ تَأْكُلُ اُ۬لطَّيْرُ مِنْهُۖ نَبِّئْنَا بِتَأْوِيلِهِۦ إِنَّا نَرَيٰكَ مِنَ اَ۬لْمُحْسِنِينَۖ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1635,12,'239',12,'Yūsuf','يُوسُف',13,15,37,'قَالَ لَا يَأْتِيكُمَا طَعَامٞ تُرْزَقَٰنِهِۦ إِلَّا نَبَّأْتُكُمَا بِتَأْوِيلِهِۦ قَبْلَ أَنْ يَّأْتِيَكُمَاۖ ذَٰلِكُمَا مِمَّا عَلَّمَنِے رَبِّيَۖ إِنِّے تَرَكْتُ مِلَّةَ قَوْمٖ لَّا يُؤْمِنُونَ بِاللَّهِ وَهُم بِالْأٓخِرَةِ هُمْ كَٰفِرُونَۖ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1636,12,'240',12,'Yūsuf','يُوسُف',1,3,38,'وَاتَّبَعْتُ مِلَّةَ ءَابَآءِيَ إِبْرَٰهِيمَ وَإِسْحَٰقَ وَيَعْقُوبَۖ مَا كَانَ لَنَا أَن نُّشْرِكَ بِاللَّهِ مِن شَےْءٖۖ ذَٰلِكَ مِن فَضْلِ اِ۬للَّهِ عَلَيْنَا وَعَلَى اَ۬لنَّاسِۖ وَلَٰكِنَّ أَكْثَرَ اَ۬لنَّاسِ لَا يَشْكُرُونَۖ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1637,12,'240',12,'Yūsuf','يُوسُف',3,4,39,'يَٰصَٰحِبَيِ اِ۬لسِّجْنِ ءَٰا۬رْبَابٞ مُّتَفَرِّقُونَ خَيْرٌ أَمِ اِ۬للَّهُ اُ۬لْوَٰحِدُ اُ۬لْقَهَّارُۖ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1638,12,'240',12,'Yūsuf','يُوسُف',5,8,40,'مَا تَعْبُدُونَ مِن دُونِهِۦ إِلَّا أَسْمَآءٗ سَمَّيْتُمُوهَا أَنتُمْ وَءَابَآؤُكُم مَّا أَنزَلَ اَ۬للَّهُ بِهَا مِن سُلْطَٰنٍۖ إِنِ اِ۬لْحُكْمُ إِلَّا لِلهِۖ أَمَرَ أَلَّا تَعْبُدُواْ إِلَّا إِيَّاهُۖ ذَٰلِكَ اَ۬لدِّينُ اُ۬لْقَيِّمُۖ وَلَٰكِنَّ أَكْثَرَ اَ۬لنَّاسِ لَا يَعْلَمُونَۖ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1639,12,'240',12,'Yūsuf','يُوسُف',8,10,41,'يَٰصَٰحِبَيِ اِ۬لسِّجْنِ أَمَّا أَحَدُكُمَا فَيَسْقِے رَبَّهُۥ خَمْراٗۖ وَأَمَّا اَ۬لْأٓخَرُ فَيُصْلَبُ فَتَأْكُلُ اُ۬لطَّيْرُ مِن رَّأْسِهِۦۖ قُضِيَ اَ۬لْأَمْرُ اُ۬لذِے فِيهِ تَسْتَفْتِيَٰنِۖ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1640,12,'240',12,'Yūsuf','يُوسُف',10,12,42,'۞ وَقَالَ لِلذِے ظَنَّ أَنَّهُۥ نَاجٖ مِّنْهُمَا اَ۟ذْكُرْنِے عِندَ رَبِّكَۖ فَأَنسَيٰهُ اُ۬لشَّيْطَٰنُ ذِكْرَ رَبِّهِۦ فَلَبِثَ فِے اِ۬لسِّجْنِ بِضْعَ سِنِينَۖ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1641,12,'240',12,'Yūsuf','يُوسُف',13,15,43,'وَقَالَ اَ۬لْمَلِكُ إِنِّيَ أَرَىٰ سَبْعَ بَقَرَٰتٖ سِمَانٖ يَأْكُلُهُنَّ سَبْعٌ عِجَافٞ وَسَبْعَ سُنۢبُلَٰتٍ خُضْرٖ وَأُخَرَ يَابِسَٰتٖۖ يَٰأَيُّهَا اَ۬لْمَلَأُ اَ۬فْتُونِے فِے رُءْيَٰيَ إِن كُنتُمْ لِلرُّءْيَا تَعْبُرُونَۖ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1642,12,'241',12,'Yūsuf','يُوسُف',1,1,44,'قَالُواْ أَضْغَٰثُ أَحْلَٰمٖۖ وَمَا نَحْنُ بِتَأْوِيلِ اِ۬لْأَحْلَٰمِ بِعَٰلِمِينَۖ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1643,12,'241',12,'Yūsuf','يُوسُف',2,3,45,'وَقَالَ اَ۬لذِے نَجَا مِنْهُمَا وَادَّكَرَ بَعْدَ أُمَّةٍ أَنَا أُنَبِّئُكُم بِتَأْوِيلِهِۦ فَأَرْسِلُونِۖ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1644,12,'241',12,'Yūsuf','يُوسُف',3,5,46,'يُوسُفُ أَيُّهَا اَ۬لصِّدِّيقُ أَفْتِنَا فِے سَبْعِ بَقَرَٰتٖ سِمَانٖ يَأْكُلُهُنَّ سَبْعٌ عِجَافٞ وَسَبْعِ سُنۢبُلَٰتٍ خُضْرٖ وَأُخَرَ يَابِسَٰتٖ لَّعَلِّيَ أَرْجِعُ إِلَى اَ۬لنَّاسِ لَعَلَّهُمْ يَعْلَمُونَۖ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1645,12,'241',12,'Yūsuf','يُوسُف',5,7,47,'قَالَ تَزْرَعُونَ سَبْعَ سِنِينَ دَأْباٗۖ فَمَا حَصَدتُّمْ فَذَرُوهُ فِے سُنۢبُلِهِۦ إِلَّا قَلِيلاٗ مِّمَّا تَأْكُلُونَۖ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1646,12,'241',12,'Yūsuf','يُوسُف',7,8,48,'ثُمَّ يَأْتِے مِنۢ بَعْدِ ذَٰلِكَ سَبْعٞ شِدَادٞ يَأْكُلْنَ مَا قَدَّمْتُمْ لَهُنَّ إِلَّا قَلِيلاٗ مِّمَّا تُحْصِنُونَۖ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1647,12,'241',12,'Yūsuf','يُوسُف',8,9,49,'ثُمَّ يَأْتِے مِنۢ بَعْدِ ذَٰلِكَ عَامٞ فِيهِ يُغَاثُ اُ۬لنَّاسُ وَفِيهِ يَعْصِرُونَۖ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1648,12,'241',12,'Yūsuf','يُوسُف',9,11,50,'وَقَالَ اَ۬لْمَلِكُ اُ۪ئْتُونِے بِهِۦۖ فَلَمَّا جَآءَهُ اُ۬لرَّسُولُ قَالَ اَ۪رْجِعْ إِلَىٰ رَبِّكَ فَسْـَٔلْهُ مَا بَالُ اُ۬لنِّسْوَةِ اِ۬لَّٰتِے قَطَّعْنَ أَيْدِيَهُنَّۖ إِنَّ رَبِّے بِكَيْدِهِنَّ عَلِيمٞۖ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1649,12,'241',12,'Yūsuf','يُوسُف',12,14,51,'قَالَ مَا خَطْبُكُنَّ إِذْ رَٰوَدتُّنَّ يُوسُفَ عَن نَّفْسِهِۦۖ قُلْنَ حَٰشَ لِلهِ مَا عَلِمْنَا عَلَيْهِ مِن سُوٓءٖۖ قَالَتِ اِ۪مْرَأَتُ اُ۬لْعَزِيزِ اِ۬ءَلْٰنَ حَصْحَصَ اَ۬لْحَقُّ أَنَا رَٰوَدتُّهُۥ عَن نَّفْسِهِۦ وَإِنَّهُۥ لَمِنَ اَ۬لصَّٰدِقِينَۖ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1650,12,'241',12,'Yūsuf','يُوسُف',14,15,52,'ذَٰلِكَ لِيَعْلَمَ أَنِّے لَمْ أَخُنْهُ بِالْغَيْبِ وَأَنَّ اَ۬للَّهَ لَا يَهْدِے كَيْدَ اَ۬لْخَآئِنِينَۖ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1651,13,'242',12,'Yūsuf','يُوسُف',1,2,53,'۞ وَمَا أُبَرِّۓُ نَفْسِيَۖ إِنَّ اَ۬لنَّفْسَ لَأَمَّارَةُۢ بِالسُّوِّ إِلَّا مَا رَحِمَ رَبِّيَۖ إِنَّ رَبِّے غَفُورٞ رَّحِيمٞۖ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1652,13,'242',12,'Yūsuf','يُوسُف',2,3,54,'وَقَالَ اَ۬لْمَلِكُ اُ۪ئْتُونِے بِهِۦ أَسْتَخْلِصْهُ لِنَفْسِےۖ فَلَمَّا كَلَّمَهُۥ قَالَ إِنَّكَ اَ۬لْيَوْمَ لَدَيْنَا مَكِينٌ أَمِينٞۖ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1653,13,'242',12,'Yūsuf','يُوسُف',4,4,55,'قَالَ اَ۪جْعَلْنِے عَلَىٰ خَزَآئِنِ اِ۬لْأَرْضِ إِنِّے حَفِيظٌ عَلِيمٞۖ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1654,13,'242',12,'Yūsuf','يُوسُف',5,6,56,'وَكَذَٰلِكَ مَكَّنَّا لِيُوسُفَ فِے اِ۬لْأَرْضِ يَتَبَوَّأُ مِنْهَا حَيْثُ يَشَآءُۖ نُصِيبُ بِرَحْمَتِنَا مَن نَّشَآءُ وَلَا نُضِيعُ أَجْرَ اَ۬لْمُحْسِنِينَۖ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1655,13,'242',12,'Yūsuf','يُوسُف',7,7,57,'وَلَأَجْرُ اُ۬لْأٓخِرَةِ خَيْرٞ لِّلذِينَ ءَامَنُواْ وَكَانُواْ يَتَّقُونَۖ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1656,13,'242',12,'Yūsuf','يُوسُف',8,8,58,'وَجَآءَ ا۪خْوَةُ يُوسُفَ فَدَخَلُواْ عَلَيْهِ فَعَرَفَهُمْ وَهُمْ لَهُۥ مُنكِرُونَۖ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1657,13,'242',12,'Yūsuf','يُوسُف',9,10,59,'وَلَمَّا جَهَّزَهُم بِجَهَازِهِمْ قَالَ اَ۪ئْتُونِے بِأَخٖ لَّكُم مِّنْ أَبِيكُمْۖ أَلَا تَرَوْنَ أَنِّيَ أُوفِے اِ۬لْكَيْلَ وَأَنَا خَيْرُ اُ۬لْمُنزِلِينَۖ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1658,13,'242',12,'Yūsuf','يُوسُف',10,11,60,'فَإِن لَّمْ تَأْتُونِے بِهِۦ فَلَا كَيْلَ لَكُمْ عِندِے وَلَا تَقْرَبُونِۖ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1659,13,'242',12,'Yūsuf','يُوسُف',11,12,61,'قَالُواْ سَنُرَٰوِدُ عَنْهُ أَبَاهُ وَإِنَّا لَفَٰعِلُونَۖ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1660,13,'242',12,'Yūsuf','يُوسُف',12,14,62,'وَقَالَ لِفِتْيَتِهِ اِ۪جْعَلُواْ بِضَٰعَتَهُمْ فِے رِحَالِهِمْ لَعَلَّهُمْ يَعْرِفُونَهَا إِذَا اَ۪نقَلَبُواْ إِلَىٰ أَهْلِهِمْ لَعَلَّهُمْ يَرْجِعُونَۖ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1661,13,'242',12,'Yūsuf','يُوسُف',14,15,63,'فَلَمَّا رَجَعُواْ إِلَىٰ أَبِيهِمْ قَالُواْ يَٰأَبَانَا مُنِعَ مِنَّا اَ۬لْكَيْلُ فَأَرْسِلْ مَعَنَا أَخَانَا نَكْتَلْ وَإِنَّا لَهُۥ لَحَٰفِظُونَۖ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1662,13,'243',12,'Yūsuf','يُوسُف',1,2,64,'قَالَ هَلْ ءَامَنُكُمْ عَلَيْهِ إِلَّا كَمَا أَمِنتُكُمْ عَلَىٰ أَخِيهِ مِن قَبْلُۖ فَاللَّهُ خَيْرٌ حِفْظاٗۖ وَهْوَ أَرْحَمُ اُ۬لرَّٰحِمِينَۖ ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1663,13,'243',12,'Yūsuf','يُوسُف',2,5,65,'وَلَمَّا فَتَحُواْ مَتَٰعَهُمْ وَجَدُواْ بِضَٰعَتَهُمْ رُدَّتْ إِلَيْهِمْ قَالُواْ يَٰأَبَانَا مَا نَبْغِےۖ هَٰذِهِۦ بِضَٰعَتُنَا رُدَّتْ إِلَيْنَا وَنَمِيرُ أَهْلَنَا وَنَحْفَظُ أَخَانَا وَنَزْدَادُ كَيْلَ بَعِيرٖۖ ذَٰلِكَ كَيْلٞ يَسِيرٞۖ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1664,13,'243',12,'Yūsuf','يُوسُف',6,8,66,'۞ قَالَ لَنْ أُرْسِلَهُۥ مَعَكُمْ حَتَّىٰ تُؤْتُونِ مَوْثِقاٗ مِّنَ اَ۬للَّهِ لَتَأْتُنَّنِے بِهِۦ إِلَّا أَنْ يُّحَاطَ بِكُمْۖ فَلَمَّا ءَاتَوْهُ مَوْثِقَهُمْ قَالَ اَ۬للَّهُ عَلَىٰ مَا نَقُولُ وَكِيلٞۖ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1665,13,'243',12,'Yūsuf','يُوسُف',8,11,67,'وَقَالَ يَٰبَنِيَّ لَا تَدْخُلُواْ مِنۢ بَابٖ وَٰحِدٖ وَادْخُلُواْ مِنْ أَبْوَٰبٖ مُّتَفَرِّقَةٖۖ وَمَا أُغْنِے عَنكُم مِّنَ اَ۬للَّهِ مِن شَےْءٍۖ إِنِ اِ۬لْحُكْمُ إِلَّا لِلهِۖ عَلَيْهِ تَوَكَّلْتُۖ وَعَلَيْهِ فَلْيَتَوَكَّلِ اِ۬لْمُتَوَكِّلُونَۖ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1666,13,'243',12,'Yūsuf','يُوسُف',11,14,68,'وَلَمَّا دَخَلُواْ مِنْ حَيْثُ أَمَرَهُمْ أَبُوهُم مَّا كَانَ يُغْنِے عَنْهُم مِّنَ اَ۬للَّهِ مِن شَےْءٍ إِلَّا حَاجَةٗ فِے نَفْسِ يَعْقُوبَ قَضَيٰهَاۖ وَإِنَّهُۥ لَذُو عِلْمٖ لِّمَا عَلَّمْنَٰهُۖ وَلَٰكِنَّ أَكْثَرَ اَ۬لنَّاسِ لَا يَعْلَمُونَۖ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1667,13,'243',12,'Yūsuf','يُوسُف',14,15,69,'وَلَمَّا دَخَلُواْ عَلَىٰ يُوسُفَ ءَاوَىٰ إِلَيْهِ أَخَاهُۖ قَالَ إِنِّيَ أَنَا أَخُوكَ فَلَا تَبْتَئِسْ بِمَا كَانُواْ يَعْمَلُونَۖ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1668,13,'244',12,'Yūsuf','يُوسُف',1,2,70,'فَلَمَّا جَهَّزَهُم بِجَهَازِهِمْ جَعَلَ اَ۬لسِّقَايَةَ فِے رَحْلِ أَخِيهِ ثُمَّ أَذَّنَ مُؤَذِّنٌ أَيَّتُهَا اَ۬لْعِيرُ إِنَّكُمْ لَسَٰرِقُونَۖ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1669,13,'244',12,'Yūsuf','يُوسُف',2,3,71,'قَالُواْ وَأَقْبَلُواْ عَلَيْهِم مَّاذَا تَفْقِدُونَۖ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1670,13,'244',12,'Yūsuf','يُوسُف',3,4,72,'قَالُواْ نَفْقِدُ صُوَاعَ اَ۬لْمَلِكِۖ وَلِمَن جَآءَ بِهِۦ حِمْلُ بَعِيرٖ وَأَنَا بِهِۦ زَعِيمٞۖ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1671,13,'244',12,'Yūsuf','يُوسُف',4,5,73,'قَالُواْ تَاللَّهِ لَقَدْ عَلِمْتُم مَّا جِئْنَا لِنُفْسِدَ فِے اِ۬لْأَرْضِ وَمَا كُنَّا سَٰرِقِينَۖ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1672,13,'244',12,'Yūsuf','يُوسُف',6,6,74,'قَالُواْ فَمَا جَزَٰٓؤُهُۥ إِن كُنتُمْ كَٰذِبِينَۖ ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1673,13,'244',12,'Yūsuf','يُوسُف',6,7,75,'قَالُواْ جَزَٰٓؤُهُۥ مَنْ وُّجِدَ فِے رَحْلِهِۦ فَهْوَ جَزَٰٓؤُهُۥۖ كَذَٰلِكَ نَجْزِے اِ۬لظَّٰلِمِينَۖ ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1674,13,'244',12,'Yūsuf','يُوسُف',8,11,76,'فَبَدَأَ بِأَوْعِيَتِهِمْ قَبْلَ وِعَآءِ اَ۬خِيهِ ثُمَّ اَ۪سْتَخْرَجَهَا مِنْ وِّعَآءِ اَ۬خِيهِۖ كَذَٰلِكَ كِدْنَا لِيُوسُفَۖ مَا كَانَ لِيَأْخُذَ أَخَاهُ فِے دِينِ اِ۬لْمَلِكِ إِلَّا أَنْ يَّشَآءَ اَ۬للَّهُۖ نَرْفَعُ دَرَجَٰتِ مَن نَّشَآءُۖ وَفَوْقَ كُلِّ ذِے عِلْمٍ عَلِيمٞۖ ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1675,13,'244',12,'Yūsuf','يُوسُف',11,14,77,'۞ قَالُواْ إِنْ يَّسْرِقْ فَقَدْ سَرَقَ أَخٞ لَّهُۥ مِن قَبْلُۖ فَأَسَرَّهَا يُوسُفُ فِے نَفْسِهِۦ وَلَمْ يُبْدِهَا لَهُمْۖ قَالَ أَنتُمْ شَرّٞ مَّكَاناٗۖ وَاللَّهُ أَعْلَمُ بِمَا تَصِفُونَۖ ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1676,13,'244',12,'Yūsuf','يُوسُف',14,15,78,'قَالُواْ يَٰأَيُّهَا اَ۬لْعَزِيزُ إِنَّ لَهُۥ أَباٗ شَيْخاٗ كَبِيراٗ فَخُذْ أَحَدَنَا مَكَانَهُۥ إِنَّا نَرَيٰكَ مِنَ اَ۬لْمُحْسِنِينَۖ ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1677,13,'245',12,'Yūsuf','يُوسُف',1,2,79,'قَالَ مَعَاذَ اَ۬للَّهِ أَن نَّأْخُذَ إِلَّا مَنْ وَّجَدْنَا مَتَٰعَنَا عِندَهُۥ إِنَّا إِذاٗ لَّظَٰلِمُونَۖ ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1678,13,'245',12,'Yūsuf','يُوسُف',2,5,80,'فَلَمَّا اَ۪سْتَيْـَٔسُواْ مِنْهُ خَلَصُواْ نَجِيّاٗۖ قَالَ كَبِيرُهُمْ أَلَمْ تَعْلَمُواْ أَنَّ أَبَاكُمْ قَدْ أَخَذَ عَلَيْكُم مَّوْثِقاٗ مِّنَ اَ۬للَّهِۖ وَمِن قَبْلُ مَا فَرَّطْتُّمْ فِے يُوسُفَۖ فَلَنْ أَبْرَحَ اَ۬لْأَرْضَ حَتَّىٰ يَأْذَنَ لِيَ أَبِيَ أَوْ يَحْكُمَ اَ۬للَّهُ لِےۖ وَهْوَ خَيْرُ اُ۬لْحَٰكِمِينَۖ ٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1679,13,'245',12,'Yūsuf','يُوسُف',6,7,81,'اَ۪رْجِعُواْ إِلَىٰ أَبِيكُمْ فَقُولُواْ يَٰأَبَانَا إِنَّ اَ۪بْنَكَ سَرَقَۖ وَمَا شَهِدْنَا إِلَّا بِمَا عَلِمْنَا وَمَا كُنَّا لِلْغَيْبِ حَٰفِظِينَۖ ٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1680,13,'245',12,'Yūsuf','يُوسُف',8,9,82,'وَسْـَٔلِ اِ۬لْقَرْيَةَ اَ۬لتِے كُنَّا فِيهَا وَالْعِيرَ اَ۬لتِے أَقْبَلْنَا فِيهَا وَإِنَّا لَصَٰدِقُونَۖ ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1681,13,'245',12,'Yūsuf','يُوسُف',9,11,83,'قَالَ بَلْ سَوَّلَتْ لَكُمْ أَنفُسُكُمْ أَمْراٗۖ فَصَبْرٞ جَمِيلٌۖ عَسَى اَ۬للَّهُ أَنْ يَّأْتِيَنِے بِهِمْ جَمِيعاًۖ إِنَّهُۥ هُوَ اَ۬لْعَلِيمُ اُ۬لْحَكِيمُۖ ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1682,13,'245',12,'Yūsuf','يُوسُف',11,12,84,'وَتَوَلَّىٰ عَنْهُمْ وَقَالَ يَٰأَسَفَىٰ عَلَىٰ يُوسُفَۖ وَابْيَضَّتْ عَيْنَٰهُ مِنَ اَ۬لْحُزْنِ فَهْوَ كَظِيمٞۖ ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1683,13,'245',12,'Yūsuf','يُوسُف',13,14,85,'قَالُواْ تَاللَّهِ تَفْتَؤُاْ تَذْكُرُ يُوسُفَ حَتَّىٰ تَكُونَ حَرَضاً أَوْ تَكُونَ مِنَ اَ۬لْهَٰلِكِينَۖ ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1684,13,'245',12,'Yūsuf','يُوسُف',14,15,86,'قَالَ إِنَّمَا أَشْكُواْ بَثِّے وَحُزْنِيَ إِلَى اَ۬للَّهِۖ وَأَعْلَمُ مِنَ اَ۬للَّهِ مَا لَا تَعْلَمُونَۖ ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1685,13,'246',12,'Yūsuf','يُوسُف',1,3,87,'يَٰبَنِيَّ اَ۪ذْهَبُواْ فَتَحَسَّسُواْ مِنْ يُّوسُفَ وَأَخِيهِ وَلَا تَاْيْـَٔسُواْ مِن رَّوْحِ اِ۬للَّهِۖ إِنَّهُۥ لَا يَاْيْـَٔسُ مِن رَّوْحِ اِ۬للَّهِ إِلَّا اَ۬لْقَوْمُ اُ۬لْكَٰفِرُونَۖ ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1686,13,'246',12,'Yūsuf','يُوسُف',3,5,88,'۞ فَلَمَّا دَخَلُواْ عَلَيْهِ قَالُواْ يَٰأَيُّهَا اَ۬لْعَزِيزُ مَسَّنَا وَأَهْلَنَا اَ۬لضُّرُّ وَجِئْنَا بِبِضَٰعَةٖ مُّزْجَيٰةٖۖ فَأَوْفِ لَنَا اَ۬لْكَيْلَ وَتَصَدَّقْ عَلَيْنَاۖ إِنَّ اَ۬للَّهَ يَجْزِے اِ۬لْمُتَصَدِّقِينَۖ ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1687,13,'246',12,'Yūsuf','يُوسُف',6,7,89,'قَالَ هَلْ عَلِمْتُم مَّا فَعَلْتُم بِيُوسُفَ وَأَخِيهِ إِذْ أَنتُمْ جَٰهِلُونَۖ ٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1688,13,'246',12,'Yūsuf','يُوسُف',7,9,90,'قَالُواْ أَٰ۟نَّكَ لَأَنتَ يُوسُفُۖ قَالَ أَنَا يُوسُفُ وَهَٰذَا أَخِےۖ قَدْ مَنَّ اَ۬للَّهُ عَلَيْنَاۖ إِنَّهُۥ مَنْ يَّتَّقِ وَيَصْبِرْ فَإِنَّ اَ۬للَّهَ لَا يُضِيعُ أَجْرَ اَ۬لْمُحْسِنِينَۖ ٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1689,13,'246',12,'Yūsuf','يُوسُف',9,10,91,'قَالُواْ تَاللَّهِ لَقَدْ ءَاثَرَكَ اَ۬للَّهُ عَلَيْنَا وَإِن كُنَّا لَخَٰطِـِٕينَۖ ٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1690,13,'246',12,'Yūsuf','يُوسُف',10,11,92,'قَالَ لَا تَثْرِيبَ عَلَيْكُمُۖ اُ۬لْيَوْمَ يَغْفِرُ اُ۬للَّهُ لَكُمْۖ وَهْوَ أَرْحَمُ اُ۬لرَّٰحِمِينَۖ ٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1691,13,'246',12,'Yūsuf','يُوسُف',12,13,93,'اَ۪ذْهَبُواْ بِقَمِيصِے هَٰذَا فَأَلْقُوهُ عَلَىٰ وَجْهِ أَبِے يَأْتِ بَصِيراٗ وَأْتُونِے بِأَهْلِكُمْ أَجْمَعِينَۖ ٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1692,13,'246',12,'Yūsuf','يُوسُف',13,15,94,'وَلَمَّا فَصَلَتِ اِ۬لْعِيرُ قَالَ أَبُوهُمْ إِنِّے لَأَجِدُ رِيحَ يُوسُفَ لَوْلَا أَن تُفَنِّدُونِۖ ٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1693,13,'246',12,'Yūsuf','يُوسُف',15,15,95,'قَالُواْ تَاللَّهِ إِنَّكَ لَفِے ضَلَٰلِكَ اَ۬لْقَدِيمِۖ ٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1694,13,'247',12,'Yūsuf','يُوسُف',1,2,96,'فَلَمَّا أَن جَآءَ اَ۬لْبَشِيرُ أَلْقَيٰهُ عَلَىٰ وَجْهِهِۦ فَارْتَدَّ بَصِيراٗۖ قَالَ أَلَمْ أَقُل لَّكُمْ إِنِّيَ أَعْلَمُ مِنَ اَ۬للَّهِ مَا لَا تَعْلَمُونَۖ ٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1695,13,'247',12,'Yūsuf','يُوسُف',2,3,97,'قَالُواْ يَٰأَبَانَا اَ۪سْتَغْفِرْ لَنَا ذُنُوبَنَا إِنَّا كُنَّا خَٰطِـِٕينَۖ ٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1696,13,'247',12,'Yūsuf','يُوسُف',3,4,98,'قَالَ سَوْفَ أَسْتَغْفِرُ لَكُمْ رَبِّيَۖ إِنَّهُۥ هُوَ اَ۬لْغَفُورُ اُ۬لرَّحِيمُۖ ٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1697,13,'247',12,'Yūsuf','يُوسُف',4,6,99,'فَلَمَّا دَخَلُواْ عَلَىٰ يُوسُفَ ءَاوَىٰ إِلَيْهِ أَبَوَيْهِ وَقَالَ اَ۟دْخُلُواْ مِصْرَ إِن شَآءَ اَ۬للَّهُ ءَامِنِينَۖ ٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1698,13,'247',12,'Yūsuf','يُوسُف',6,10,100,'وَرَفَعَ أَبَوَيْهِ عَلَى اَ۬لْعَرْشِ وَخَرُّواْ لَهُۥ سُجَّداٗۖ وَقَالَ يَٰأَبَتِ هَٰذَا تَأْوِيلُ رُءْيَٰيَ مِن قَبْلُ قَدْ جَعَلَهَا رَبِّے حَقّاٗۖ وَقَدْ أَحْسَنَ بِيَ إِذْ أَخْرَجَنِے مِنَ اَ۬لسِّجْنِ وَجَآءَ بِكُم مِّنَ اَ۬لْبَدْوِ مِنۢ بَعْدِ أَن نَّزَغَ اَ۬لشَّيْطَٰنُ بَيْنِے وَبَيْنَ إِخْوَتِےۖ إِنَّ رَبِّے لَطِيفٞ لِّمَا يَشَآءُۖ اِ۪نَّهُۥ هُوَ اَ۬لْعَلِيمُ اُ۬لْحَكِيمُۖ ١٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1699,13,'247',12,'Yūsuf','يُوسُف',10,13,101,'۞ رَبِّ قَدْ ءَاتَيْتَنِے مِنَ اَ۬لْمُلْكِ وَعَلَّمْتَنِے مِن تَأْوِيلِ اِ۬لْأَحَادِيثِۖ فَاطِرَ اَ۬لسَّمَٰوَٰتِ وَالْأَرْضِ أَنتَ وَلِيِّۦ فِے اِ۬لدُّنْيَا وَالْأٓخِرَةِ تَوَفَّنِے مُسْلِماٗ وَأَلْحِقْنِے بِالصَّٰلِحِينَۖ ١٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1700,13,'247',12,'Yūsuf','يُوسُف',13,15,102,'ذَٰلِكَ مِنْ أَنۢبَآءِ اِ۬لْغَيْبِ نُوحِيهِ إِلَيْكَۖ وَمَا كُنتَ لَدَيْهِمْ إِذْ أَجْمَعُواْ أَمْرَهُمْ وَهُمْ يَمْكُرُونَۖ ١٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1701,13,'247',12,'Yūsuf','يُوسُف',15,15,103,'وَمَا أَكْثَرُ اُ۬لنَّاسِ وَلَوْ حَرَصْتَ بِمُؤْمِنِينَۖ ١٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1702,13,'248',12,'Yūsuf','يُوسُف',1,1,104,'وَمَا تَسْـَٔلُهُمْ عَلَيْهِ مِنْ أَجْرٍۖ إِنْ هُوَ إِلَّا ذِكْرٞ لِّلْعَٰلَمِينَۖ ١٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1703,13,'248',12,'Yūsuf','يُوسُف',2,3,105,'وَكَأَيِّن مِّنْ ءَايَةٖ فِے اِ۬لسَّمَٰوَٰتِ وَالْأَرْضِ يَمُرُّونَ عَلَيْهَا وَهُمْ عَنْهَا مُعْرِضُونَۖ ١٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1704,13,'248',12,'Yūsuf','يُوسُف',3,4,106,'وَمَا يُؤْمِنُ أَكْثَرُهُم بِاللَّهِ إِلَّا وَهُم مُّشْرِكُونَۖ ١٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1705,13,'248',12,'Yūsuf','يُوسُف',4,5,107,'أَفَأَمِنُواْ أَن تَأْتِيَهُمْ غَٰشِيَةٞ مِّنْ عَذَابِ اِ۬للَّهِ أَوْ تَأْتِيَهُمُ اُ۬لسَّاعَةُ بَغْتَةٗ وَهُمْ لَا يَشْعُرُونَۖ ١٠٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1706,13,'248',12,'Yūsuf','يُوسُف',5,7,108,'قُلْ هَٰذِهِۦ سَبِيلِيَ أَدْعُواْ إِلَى اَ۬للَّهِۖ عَلَىٰ بَصِيرَةٍ أَنَا وَمَنِ اِ۪تَّبَعَنِےۖ وَسُبْحَٰنَ اَ۬للَّهِۖ وَمَا أَنَا مِنَ اَ۬لْمُشْرِكِينَۖ ١٠٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1707,13,'248',12,'Yūsuf','يُوسُف',7,10,109,'وَمَا أَرْسَلْنَا مِن قَبْلِكَ إِلَّا رِجَالاٗ يُوحَىٰ إِلَيْهِم مِّنْ أَهْلِ اِ۬لْقُرَىٰۖ أَفَلَمْ يَسِيرُواْ فِے اِ۬لْأَرْضِ فَيَنظُرُواْ كَيْفَ كَانَ عَٰقِبَةُ اُ۬لذِينَ مِن قَبْلِهِمْۖ وَلَدَارُ اُ۬لْأٓخِرَةِ خَيْرٞ لِّلذِينَ اَ۪تَّقَوْاْۖ أَفَلَا تَعْقِلُونَۖ ١٠٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1708,13,'248',12,'Yūsuf','يُوسُف',11,13,110,'حَتَّىٰ إِذَا اَ۪سْتَيْـَٔسَ اَ۬لرُّسُلُ وَظَنُّواْ أَنَّهُمْ قَدْ كُذِّبُواْ جَآءَهُمْ نَصْرُنَا فَنُۨجِے مَن نَّشَآءُۖ وَلَا يُرَدُّ بَأْسُنَا عَنِ اِ۬لْقَوْمِ اِ۬لْمُجْرِمِينَۖ ١١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1709,13,'248',12,'Yūsuf','يُوسُف',13,15,111,'۞ لَقَدْ كَانَ فِے قَصَصِهِمْ عِبْرَةٞ لِّأُوْلِے اِ۬لْأَلْبَٰبِۖ مَا كَانَ حَدِيثاٗ يُفْتَرَىٰۖ وَلَٰكِن تَصْدِيقَ اَ۬لذِے بَيْنَ يَدَيْهِ وَتَفْصِيلَ كُلِّ شَےْءٖ وَهُدىٗ وَرَحْمَةٗ لِّقَوْمٖ يُؤْمِنُونَۖ ١١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1710,13,'249',13,'Ar-Ra‘d','الرَّعد',3,4,1,'أَلَٓمِّٓرَۖ تِلْكَ ءَايَٰتُ اُ۬لْكِتَٰبِۖ وَالذِے أُنزِلَ إِلَيْكَ مِن رَّبِّكَ اَ۬لْحَقُّۖ وَلَٰكِنَّ أَكْثَرَ اَ۬لنَّاسِ لَا يُؤْمِنُونَۖ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1711,13,'249',13,'Ar-Ra‘d','الرَّعد',4,7,2,'اَ۬للَّهُ اُ۬لذِے رَفَعَ اَ۬لسَّمَٰوَٰتِ بِغَيْرِ عَمَدٖۖ تَرَوْنَهَاۖ ثُمَّ اَ۪سْتَوَىٰ عَلَى اَ۬لْعَرْشِۖ وَسَخَّرَ اَ۬لشَّمْسَ وَالْقَمَرَۖ كُلّٞ يَجْرِے لِأَجَلٖ مُّسَمّىٗۖ يُدَبِّرُ اُ۬لْأَمْرَۖ يُفَصِّلُ اُ۬لْأٓيَٰتِ لَعَلَّكُم بِلِقَآءِ رَبِّكُمْ تُوقِنُونَۖ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1712,13,'249',13,'Ar-Ra‘d','الرَّعد',7,9,3,'وَهْوَ اَ۬لذِے مَدَّ اَ۬لْأَرْضَ وَجَعَلَ فِيهَا رَوَٰسِيَ وَأَنْهَٰراٗۖ وَمِن كُلِّ اِ۬لثَّمَرَٰتِ جَعَلَ فِيهَا زَوْجَيْنِ اِ۪ثْنَيْنِۖ يُغْشِے اِ۬ليْلَ اَ۬لنَّهَارَۖ إِنَّ فِے ذَٰلِكَ لَأٓيَٰتٖ لِّقَوْمٖ يَتَفَكَّرُونَۖ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1713,13,'249',13,'Ar-Ra‘d','الرَّعد',9,12,4,'وَفِے اِ۬لْأَرْضِ قِطَعٞ مُّتَجَٰوِرَٰتٞ وَجَنَّٰتٞ مِّنْ أَعْنَٰبٖ وَزَرْعٖ وَنَخِيلٖ صِنْوَانٖ وَغَيْرِ صِنْوَانٖ تُسْقَىٰ بِمَآءٖ وَٰحِدٖۖ وَنُفَضِّلُ بَعْضَهَا عَلَىٰ بَعْضٖ فِے اِ۬لْأُكْلِۖ إِنَّ فِے ذَٰلِكَ لَأٓيَٰتٖ لِّقَوْمٖ يَعْقِلُونَۖ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1714,13,'249',13,'Ar-Ra‘d','الرَّعد',12,13,5,'۞ وَإِن تَعْجَبْ فَعَجَبٞ قَوْلُهُمْ أَٰ۟ذَا كُنَّا تُرَٰباً إِنَّا لَفِے خَلْقٖ جَدِيدٍۖ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1715,13,'249',13,'Ar-Ra‘d','الرَّعد',14,15,6,'أُوْلَٰٓئِكَ اَ۬لذِينَ كَفَرُواْ بِرَبِّهِمْۖ وَأُوْلَٰٓئِكَ اَ۬لْأَغْلَٰلُ فِے أَعْنَٰقِهِمْۖ وَأُوْلَٰٓئِكَ أَصْحَٰبُ اُ۬لنَّارِ هُمْ فِيهَا خَٰلِدُونَۖ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1716,13,'250',13,'Ar-Ra‘d','الرَّعد',1,3,7,'وَيَسْتَعْجِلُونَكَ بِالسَّيِّئَةِ قَبْلَ اَ۬لْحَسَنَةِ وَقَدْ خَلَتْ مِن قَبْلِهِمُ اُ۬لْمَثُلَٰتُۖ وَإِنَّ رَبَّكَ لَذُو مَغْفِرَةٖ لِّلنَّاسِ عَلَىٰ ظُلْمِهِمْۖ وَإِنَّ رَبَّكَ لَشَدِيدُ اُ۬لْعِقَابِۖ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1717,13,'250',13,'Ar-Ra‘d','الرَّعد',3,5,8,'وَيَقُولُ اُ۬لذِينَ كَفَرُواْ لَوْلَا أُنزِلَ عَلَيْهِ ءَايَةٞ مِّن رَّبِّهِۦۖ إِنَّمَا أَنتَ مُنذِرٞۖ وَلِكُلِّ قَوْمٍ هَادٍۖ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1718,13,'250',13,'Ar-Ra‘d','الرَّعد',5,6,9,'اِ۬للَّهُ يَعْلَمُ مَا تَحْمِلُ كُلُّ أُنثَىٰ وَمَا تَغِيضُ اُ۬لْأَرْحَامُ وَمَا تَزْدَادُۖ وَكُلُّ شَےْءٍ عِندَهُۥ بِمِقْدَارٍۖ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1719,13,'250',13,'Ar-Ra‘d','الرَّعد',6,7,10,'عَٰلِمُ اُ۬لْغَيْبِ وَالشَّهَٰدَةِۖ اِ۬لْكَبِيرُ اُ۬لْمُتَعَالِۖ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1720,13,'250',13,'Ar-Ra‘d','الرَّعد',7,9,11,'سَوَآءٞ مِّنكُم مَّنْ أَسَرَّ اَ۬لْقَوْلَ وَمَن جَهَرَ بِهِۦ وَمَنْ هُوَ مُسْتَخْفِۢ بِاليْلِ وَسَارِبُۢ بِالنَّهَارِۖ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1721,13,'250',13,'Ar-Ra‘d','الرَّعد',9,12,12,'لَهُۥ مُعَقِّبَٰتٞ مِّنۢ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهِۦ يَحْفَظُونَهُۥ مِنْ أَمْرِ اِ۬للَّهِۖ إِنَّ اَ۬للَّهَ لَا يُغَيِّرُ مَا بِقَوْمٍ حَتَّىٰ يُغَيِّرُواْ مَا بِأَنفُسِهِمْۖ وَإِذَا أَرَادَ اَ۬للَّهُ بِقَوْمٖ سُوٓءاٗ فَلَا مَرَدَّ لَهُۥۖ وَمَا لَهُم مِّن دُونِهِۦ مِنْ وَّالٍۖ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1722,13,'250',13,'Ar-Ra‘d','الرَّعد',12,13,13,'هُوَ اَ۬لذِے يُرِيكُمُ اُ۬لْبَرْقَ خَوْفاٗ وَطَمَعاٗ وَيُنشِۓُ اُ۬لسَّحَابَ اَ۬لثِّقَالَ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1723,13,'250',13,'Ar-Ra‘d','الرَّعد',13,15,14,'وَيُسَبِّحُ اُ۬لرَّعْدُ بِحَمْدِهِۦ وَالْمَلَٰٓئِكَةُ مِنْ خِيفَتِهِۦۖ وَيُرْسِلُ اُ۬لصَّوَٰعِقَ فَيُصِيبُ بِهَا مَنْ يَّشَآءُ وَهُمْ يُجَٰدِلُونَ فِے اِ۬للَّهِ وَهْوَ شَدِيدُ اُ۬لْمِحَالِۖ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1724,13,'251',13,'Ar-Ra‘d','الرَّعد',1,3,15,'۞ لَهُۥ دَعْوَةُ اُ۬لْحَقِّۖ وَالذِينَ يَدْعُونَ مِن دُونِهِۦ لَا يَسْتَجِيبُونَ لَهُم بِشَےْءٍ إِلَّا كَبَٰسِطِ كَفَّيْهِ إِلَى اَ۬لْمَآءِ لِيَبْلُغَ فَاهُ وَمَا هُوَ بِبَٰلِغِهِۦۖ وَمَا دُعَآءُ اُ۬لْكَٰفِرِينَ إِلَّا فِے ضَلَٰلٖۖ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1725,13,'251',13,'Ar-Ra‘d','الرَّعد',3,4,16,'وَلِلهِ يَسْجُدُ مَن فِے اِ۬لسَّمَٰوَٰتِ وَالْأَرْضِ طَوْعاٗ وَكَرْهاٗ وَظِلَٰلُهُم بِالْغُدُوِّ وَالْأٓصَالِۖ۩ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1726,13,'251',13,'Ar-Ra‘d','الرَّعد',4,7,17,'قُلْ مَن رَّبُّ اُ۬لسَّمَٰوَٰتِ وَالْأَرْضِ قُلِ اِ۬للَّهُۖ قُلْ أَفَاتَّخَذتُّم مِّن دُونِهِۦ أَوْلِيَآءَ لَا يَمْلِكُونَ لِأَنفُسِهِمْ نَفْعاٗ وَلَا ضَرّاٗۖ قُلْ هَلْ يَسْتَوِے اِ۬لْأَعْمَىٰ وَالْبَصِيرُۖ أَمْ هَلْ تَسْتَوِے اِ۬لظُّلُمَٰتُ وَالنُّورُۖ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1727,13,'251',13,'Ar-Ra‘d','الرَّعد',7,9,18,'أَمْ جَعَلُواْ لِلهِ شُرَكَآءَ خَلَقُواْ كَخَلْقِهِۦ فَتَشَٰبَهَ اَ۬لْخَلْقُ عَلَيْهِمْۖ قُلِ اِ۬للَّهُ خَٰلِقُ كُلِّ شَےْءٖۖ وَهْوَ اَ۬لْوَٰحِدُ اُ۬لْقَهَّٰرُۖ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1728,13,'251',13,'Ar-Ra‘d','الرَّعد',9,13,19,'أَنزَلَ مِنَ اَ۬لسَّمَآءِ مَآءٗ فَسَالَتْ أَوْدِيَةُۢ بِقَدَرِهَا فَاحْتَمَلَ اَ۬لسَّيْلُ زَبَداٗ رَّابِياٗۖ وَمِمَّا تُوقِدُونَ عَلَيْهِ فِے اِ۬لنَّارِ اِ۪بْتِغَآءَ حِلْيَةٍ أَوْ مَتَٰعٖ زَبَدٞ مِّثْلُهُۥۖ كَذَٰلِكَ يَضْرِبُ اُ۬للَّهُ اُ۬لْحَقَّ وَالْبَٰطِلَۖ فَأَمَّا اَ۬لزَّبَدُ فَيَذْهَبُ جُفَآءٗۖ وَأَمَّا مَا يَنفَعُ اُ۬لنَّاسَ فَيَمْكُثُ فِے اِ۬لْأَرْضِۖ كَذَٰلِكَ يَضْرِبُ اُ۬للَّهُ اُ۬لْأَمْثَالَۖ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1729,13,'251',13,'Ar-Ra‘d','الرَّعد',13,15,20,'لِلذِينَ اَ۪سْتَجَابُواْ لِرَبِّهِمُ اُ۬لْحُسْنَىٰۖ وَالذِينَ لَمْ يَسْتَجِيبُواْ لَهُۥ لَوْ أَنَّ لَهُم مَّا فِے اِ۬لْأَرْضِ جَمِيعاٗ وَمِثْلَهُۥ مَعَهُۥ لَافْتَدَوْاْ بِهِۦۖ أُوْلَٰٓئِكَ لَهُمْ سُوٓءُ اُ۬لْحِسَابِۖ وَمَأْوَيٰهُمْ جَهَنَّمُۖ وَبِئْسَ اَ۬لْمِهَادُۖ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1730,13,'252',13,'Ar-Ra‘d','الرَّعد',1,2,21,'۞ أَفَمَنْ يَّعْلَمُ أَنَّمَا أُنزِلَ إِلَيْكَ مِن رَّبِّكَ اَ۬لْحَقُّ كَمَنْ هُوَ أَعْمَىٰۖ إِنَّمَا يَتَذَكَّرُ أُوْلُواْ اُ۬لْأَلْبَٰبِ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1731,13,'252',13,'Ar-Ra‘d','الرَّعد',2,3,22,'اِ۬لذِينَ يُوفُونَ بِعَهْدِ اِ۬للَّهِ وَلَا يَنقُضُونَ اَ۬لْمِيثَٰقَ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1732,13,'252',13,'Ar-Ra‘d','الرَّعد',3,4,23,'وَالذِينَ يَصِلُونَ مَا أَمَرَ اَ۬للَّهُ بِهِۦ أَنْ يُّوصَلَ وَيَخْشَوْنَ رَبَّهُمْ وَيَخَافُونَ سُوٓءَ اَ۬لْحِسَابِ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1733,13,'252',13,'Ar-Ra‘d','الرَّعد',4,6,24,'وَالذِينَ صَبَرُواْ اُ۪بْتِغَآءَ وَجْهِ رَبِّهِمْ وَأَقَامُواْ اُ۬لصَّلَوٰةَ وَأَنفَقُواْ مِمَّا رَزَقْنَٰهُمْ سِرّاٗ وَعَلَٰنِيَةٗ وَيَدْرَءُونَ بِالْحَسَنَةِ اِ۬لسَّيِّئَةَۖ أُوْلَٰٓئِكَ لَهُمْ عُقْبَى اَ۬لدَّارِۖ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1734,13,'252',13,'Ar-Ra‘d','الرَّعد',7,9,25,'جَنَّٰتُ عَدْنٖ يَدْخُلُونَهَا وَمَن صَلَحَ مِنْ ءَابَآئِهِمْ وَأَزْوَٰجِهِمْ وَذُرِّيَّٰتِهِمْۖ وَالْمَلَٰٓئِكَةُ يَدْخُلُونَ عَلَيْهِم مِّن كُلِّ بَابٖۖ سَلَٰمٌ عَلَيْكُم بِمَا صَبَرْتُمْۖ فَنِعْمَ عُقْبَى اَ۬لدَّارِۖ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1735,13,'252',13,'Ar-Ra‘d','الرَّعد',9,11,26,'وَالذِينَ يَنقُضُونَ عَهْدَ اَ۬للَّهِ مِنۢ بَعْدِ مِيثَٰقِهِۦ وَيَقْطَعُونَ مَا أَمَرَ اَ۬للَّهُ بِهِۦ أَنْ يُّوصَلَ وَيُفْسِدُونَ فِے اِ۬لْأَرْضِ أُوْلَٰٓئِكَ لَهُمُ اُ۬للَّعْنَةُ وَلَهُمْ سُوٓءُ اُ۬لدَّارِۖ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1736,13,'252',13,'Ar-Ra‘d','الرَّعد',11,13,27,'اِ۬للَّهُ يَبْسُطُ اُ۬لرِّزْقَ لِمَنْ يَّشَآءُ وَيَقْدِرُۖ وَفَرِحُواْ بِالْحَيَوٰةِ اِ۬لدُّنْيَاۖ وَمَا اَ۬لْحَيَوٰةُ اُ۬لدُّنْيَا فِے اِ۬لْأٓخِرَةِ إِلَّا مَتَٰعٞۖ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1737,13,'252',13,'Ar-Ra‘d','الرَّعد',13,14,28,'وَيَقُولُ اُ۬لذِينَ كَفَرُواْ لَوْلَا أُنزِلَ عَلَيْهِ ءَايَةٞ مِّن رَّبِّهِۦۖ قُلْ إِنَّ اَ۬للَّهَ يُضِلُّ مَنْ يَّشَآءُ وَيَهْدِے إِلَيْهِ مَنْ أَنَابَۖ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1738,13,'252',13,'Ar-Ra‘d','الرَّعد',14,15,29,'اَ۬لذِينَ ءَامَنُواْ وَتَطْمَئِنُّ قُلُوبُهُم بِذِكْرِ اِ۬للَّهِۖ أَلَا بِذِكْرِ اِ۬للَّهِ تَطْمَئِنُّ اُ۬لْقُلُوبُۖ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1739,13,'253',13,'Ar-Ra‘d','الرَّعد',1,1,30,'اُ۬لذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ طُوبَىٰ لَهُمْ وَحُسْنُ مَـَٔابٖۖ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1740,13,'253',13,'Ar-Ra‘d','الرَّعد',2,4,31,'۞ كَذَٰلِكَ أَرْسَلْنَٰكَ فِے أُمَّةٖ قَدْ خَلَتْ مِن قَبْلِهَا أُمَمٞ لِّتَتْلُوَاْ عَلَيْهِمُ اُ۬لذِے أَوْحَيْنَا إِلَيْكَ وَهُمْ يَكْفُرُونَ بِالرَّحْمَٰنِۖ قُلْ هُوَ رَبِّےۖ لَا إِلَٰهَ إِلَّا هُوَۖ عَلَيْهِ تَوَكَّلْتُۖ وَإِلَيْهِ مَتَابِۖ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1741,13,'253',13,'Ar-Ra‘d','الرَّعد',4,9,32,'وَلَوْ أَنَّ قُرْءَاناٗ سُيِّرَتْ بِهِ اِ۬لْجِبَالُ أَوْ قُطِّعَتْ بِهِ اِ۬لْأَرْضُ أَوْ كُلِّمَ بِهِ اِ۬لْمَوْتَىٰۖ بَل لِّلهِ اِ۬لْأَمْرُ جَمِيعاًۖ أَفَلَمْ يَاْيْـَٔسِ اِ۬لذِينَ ءَامَنُواْ أَن لَّوْ يَشَآءُ اُ۬للَّهُ لَهَدَى اَ۬لنَّاسَ جَمِيعاٗۖ وَلَا يَزَالُ اُ۬لذِينَ كَفَرُواْ تُصِيبُهُم بِمَا صَنَعُواْ قَارِعَةٌ أَوْ تَحُلُّ قَرِيباٗ مِّن دَارِهِمْ حَتَّىٰ يَأْتِيَ وَعْدُ اُ۬للَّهِۖ إِنَّ اَ۬للَّهَ لَا يُخْلِفُ اُ۬لْمِيعَادَۖ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1742,13,'253',13,'Ar-Ra‘d','الرَّعد',9,11,33,'وَلَقَدُ اُ۟سْتُهْزِۓَ بِرُسُلٖ مِّن قَبْلِكَ فَأَمْلَيْتُ لِلذِينَ كَفَرُواْ ثُمَّ أَخَذتُّهُمْۖ فَكَيْفَ كَانَ عِقَابِۖ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1743,13,'253',13,'Ar-Ra‘d','الرَّعد',11,14,34,'أَفَمَنْ هُوَ قَآئِمٌ عَلَىٰ كُلِّ نَفْسِۢ بِمَا كَسَبَتْۖ وَجَعَلُواْ لِلهِ شُرَكَآءَ قُلْ سَمُّوهُمْۖ أَمْ تُنَبِّـُٔونَهُۥ بِمَا لَا يَعْلَمُ فِے اِ۬لْأَرْضِ أَم بِظَٰهِرٖ مِّنَ اَ۬لْقَوْلِۖ بَلْ زُيِّنَ لِلذِينَ كَفَرُواْ مَكْرُهُمْ وَصَدُّواْ عَنِ اِ۬لسَّبِيلِۖ وَمَنْ يُّضْلِلِ اِ۬للَّهُ فَمَا لَهُۥ مِنْ هَادٖۖ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1744,13,'253',13,'Ar-Ra‘d','الرَّعد',14,15,35,'لَّهُمْ عَذَابٞ فِے اِ۬لْحَيَوٰةِ اِ۬لدُّنْيَاۖ وَلَعَذَابُ اُ۬لْأٓخِرَةِ أَشَقُّۖ وَمَا لَهُم مِّنَ اَ۬للَّهِ مِنْ وَّاقٖۖ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1745,13,'254',13,'Ar-Ra‘d','الرَّعد',1,3,36,'۞ مَّثَلُ اُ۬لْجَنَّةِ اِ۬لتِے وُعِدَ اَ۬لْمُتَّقُونَ تَجْرِے مِن تَحْتِهَا اَ۬لْأَنْهَٰرُ أُكْلُهَا دَآئِمٞ وَظِلُّهَاۖ تِلْكَ عُقْبَى اَ۬لذِينَ اَ۪تَّقَواْۖ وَّعُقْبَى اَ۬لْكَٰفِرِينَ اَ۬لنَّارُۖ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1746,13,'254',13,'Ar-Ra‘d','الرَّعد',3,5,37,'وَالذِينَ ءَاتَيْنَٰهُمُ اُ۬لْكِتَٰبَ يَفْرَحُونَ بِمَا أُنزِلَ إِلَيْكَۖ وَمِنَ اَ۬لْأَحْزَابِ مَنْ يُّنكِرُ بَعْضَهُۥۖ قُلْ إِنَّمَا أُمِرْتُ أَنْ أَعْبُدَ اَ۬للَّهَ وَلَا أُشْرِكَ بِهِۦۖ إِلَيْهِ أَدْعُواْۖ وَإِلَيْهِ مَـَٔابِۖ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1747,13,'254',13,'Ar-Ra‘d','الرَّعد',6,7,38,'وَكَذَٰلِكَ أَنزَلْنَٰهُ حُكْماً عَرَبِيّاٗۖ وَلَئِنِ اِ۪تَّبَعْتَ أَهْوَآءَهُم بَعْدَ مَا جَآءَكَ مِنَ اَ۬لْعِلْمِ مَا لَكَ مِنَ اَ۬للَّهِ مِنْ وَّلِيّٖ وَلَا وَاقٖۖ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1748,13,'254',13,'Ar-Ra‘d','الرَّعد',7,9,39,'وَلَقَدْ أَرْسَلْنَا رُسُلاٗ مِّن قَبْلِكَ وَجَعَلْنَا لَهُمْ أَزْوَٰجاٗ وَذُرِّيَّةٗۖ وَمَا كَانَ لِرَسُولٍ أَنْ يَّأْتِيَ بِـَٔايَةٍ إِلَّا بِإِذْنِ اِ۬للَّهِۖ لِكُلِّ أَجَلٖ كِتَابٞۖ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1749,13,'254',13,'Ar-Ra‘d','الرَّعد',10,10,40,'يَمْحُواْ اُ۬للَّهُ مَا يَشَآءُ وَيُثَبِّتُۖ وَعِندَهُۥ أُمُّ اُ۬لْكِتَٰبِۖ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1750,13,'254',13,'Ar-Ra‘d','الرَّعد',10,12,41,'وَإِن مَّا نُرِيَنَّكَ بَعْضَ اَ۬لذِے نَعِدُهُمْ أَوْ نَتَوَفَّيَنَّكَ فَإِنَّمَا عَلَيْكَ اَ۬لْبَلَٰغُ وَعَلَيْنَا اَ۬لْحِسَابُۖ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1751,13,'254',13,'Ar-Ra‘d','الرَّعد',12,13,42,'أَوَلَمْ يَرَوْاْ أَنَّا نَأْتِے اِ۬لْأَرْضَ نَنقُصُهَا مِنْ أَطْرَافِهَاۖ وَاللَّهُ يَحْكُمُ لَا مُعَقِّبَ لِحُكْمِهِۦۖ وَهْوَ سَرِيعُ اُ۬لْحِسَابِۖ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1752,13,'254',13,'Ar-Ra‘d','الرَّعد',14,15,43,'وَقَدْ مَكَرَ اَ۬لذِينَ مِن قَبْلِهِمْ فَلِلهِ اِ۬لْمَكْرُ جَمِيعاٗۖ يَعْلَمُ مَا تَكْسِبُ كُلُّ نَفْسٖۖ وَسَيَعْلَمُ اُ۬لْكَٰفِرُ لِمَنْ عُقْبَى اَ۬لدَّارِۖ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1753,13,'255',13,'Ar-Ra‘d','الرَّعد',1,2,44,'وَيَقُولُ اُ۬لذِينَ كَفَرُواْ لَسْتَ مُرْسَلاٗۖ قُلْ كَفَىٰ بِاللَّهِ شَهِيداَۢ بَيْنِے وَبَيْنَكُمْ وَمَنْ عِندَهُۥ عِلْمُ اُ۬لْكِتَٰبِۖ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1754,13,'255',14,'Ibrāhīm','إبراهِيم',5,6,1,'أَلَٓرَۖ كِتَٰبٌ أَنزَلْنَٰهُ إِلَيْكَ لِتُخْرِجَ اَ۬لنَّاسَ مِنَ اَ۬لظُّلُمَٰتِ إِلَى اَ۬لنُّورِ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1755,13,'255',14,'Ibrāhīm','إبراهِيم',6,6,2,'بِإِذْنِ رَبِّهِمْ إِلَىٰ صِرَٰطِ اِ۬لْعَزِيزِ اِ۬لْحَمِيدِۖ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1756,13,'255',14,'Ibrāhīm','إبراهِيم',7,8,3,'اِ۬للَّهُ اُ۬لذِے لَهُۥ مَا فِے اِ۬لسَّمَٰوَٰتِ وَمَا فِے اِ۬لْأَرْضِۖ وَوَيْلٞ لِّلْكَٰفِرِينَ مِنْ عَذَابٖ شَدِيدٍۖ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1757,13,'255',14,'Ibrāhīm','إبراهِيم',8,10,4,'اِ۬لذِينَ يَسْتَحِبُّونَ اَ۬لْحَيَوٰةَ اَ۬لدُّنْيَا عَلَى اَ۬لْأٓخِرَةِ وَيَصُدُّونَ عَن سَبِيلِ اِ۬للَّهِ وَيَبْغُونَهَا عِوَجاً أُوْلَٰٓئِكَ فِے ضَلَٰلِۢ بَعِيدٖۖ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1758,13,'255',14,'Ibrāhīm','إبراهِيم',10,13,5,'وَمَا أَرْسَلْنَا مِن رَّسُولٍ إِلَّا بِلِسَانِ قَوْمِهِۦ لِيُبَيِّنَ لَهُمْۖ فَيُضِلُّ اُ۬للَّهُ مَنْ يَّشَآءُ وَيَهْدِے مَنْ يَّشَآءُۖ وَهْوَ اَ۬لْعَزِيزُ اُ۬لْحَكِيمُۖ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1759,13,'255',14,'Ibrāhīm','إبراهِيم',13,14,6,'وَلَقَدْ أَرْسَلْنَا مُوسَىٰ بِـَٔايَٰتِنَا أَنْ أَخْرِجْ قَوْمَكَ مِنَ اَ۬لظُّلُمَٰتِ إِلَى اَ۬لنُّورِ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1760,13,'255',14,'Ibrāhīm','إبراهِيم',14,15,7,'وَذَكِّرْهُم بِأَييَّٰمِ اِ۬للَّهِۖ إِنَّ فِے ذَٰلِكَ لَأٓيَٰتٖ لِّكُلِّ صَبَّارٖ شَكُورٖۖ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1761,13,'256',14,'Ibrāhīm','إبراهِيم',1,4,8,'وَإِذْ قَالَ مُوسَىٰ لِقَوْمِهِ اِ۟ذْكُرُواْ نِعْمَةَ اَ۬للَّهِ عَلَيْكُمْ إِذْ أَنجَيٰكُم مِّنْ ءَالِ فِرْعَوْنَ يَسُومُونَكُمْ سُوٓءَ اَ۬لْعَذَابِ وَيُذَبِّحُونَ أَبْنَآءَكُمْ وَيَسْتَحْيُونَ نِسَآءَكُمْۖ وَفِے ذَٰلِكُم بَلَآءٞ مِّن رَّبِّكُمْ عَظِيمٞۖ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1762,13,'256',14,'Ibrāhīm','إبراهِيم',4,6,9,'وَإِذْ تَأَذَّنَ رَبُّكُمْ لَئِن شَكَرْتُمْ لَأَزِيدَنَّكُمْ وَلَئِن كَفَرْتُمْ إِنَّ عَذَابِے لَشَدِيدٞۖ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1763,13,'256',14,'Ibrāhīm','إبراهِيم',6,7,10,'وَقَالَ مُوسَىٰ إِن تَكْفُرُواْ أَنتُمْ وَمَن فِے اِ۬لْأَرْضِ جَمِيعاٗ فَإِنَّ اَ۬للَّهَ لَغَنِيٌّ حَمِيدٌۖ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1764,13,'256',14,'Ibrāhīm','إبراهِيم',7,8,11,'أَلَمْ يَأْتِكُمْ نَبَؤُاْ اُ۬لذِينَ مِن قَبْلِكُمْ قَوْمِ نُوحٖ وَعَادٖ وَثَمُودَ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1765,13,'256',14,'Ibrāhīm','إبراهِيم',8,11,12,'وَالذِينَ مِنۢ بَعْدِهِمْۖ لَا يَعْلَمُهُمْ إِلَّا اَ۬للَّهُۖ جَآءَتْهُمْ رُسُلُهُم بِالْبَيِّنَٰتِ فَرَدُّواْ أَيْدِيَهُمْ فِے أَفْوَٰهِهِمْ وَقَالُواْ إِنَّا كَفَرْنَا بِمَا أُرْسِلْتُم بِهِۦ وَإِنَّا لَفِے شَكّٖ مِّمَّا تَدْعُونَنَا إِلَيْهِ مُرِيبٖۖ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1766,13,'256',14,'Ibrāhīm','إبراهِيم',12,15,13,'۞ قَالَتْ رُسُلُهُمْ أَفِے اِ۬للَّهِ شَكّٞ فَاطِرِ اِ۬لسَّمَٰوَٰتِ وَالْأَرْضِ يَدْعُوكُمْ لِيَغْفِرَ لَكُم مِّن ذُنُوبِكُمْ وَيُؤَخِّرَكُمْ إِلَىٰ أَجَلٖ مُّسَمّىٗۖ قَالُواْ إِنْ أَنتُمْ إِلَّا بَشَرٞ مِّثْلُنَا تُرِيدُونَ أَن تَصُدُّونَا عَمَّا كَانَ يَعْبُدُ ءَابَآؤُنَا فَأْتُونَا بِسُلْطَٰنٖ مُّبِينٖۖ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1767,13,'257',14,'Ibrāhīm','إبراهِيم',1,3,14,'قَالَتْ لَهُمْ رُسُلُهُمْ إِن نَّحْنُ إِلَّا بَشَرٞ مِّثْلُكُمْ وَلَٰكِنَّ اَ۬للَّهَ يَمُنُّ عَلَىٰ مَنْ يَّشَآءُ مِنْ عِبَادِهِۦۖ وَمَا كَانَ لَنَا أَن نَّأْتِيَكُم بِسُلْطَٰنٍ إِلَّا بِإِذْنِ اِ۬للَّهِۖ وَعَلَى اَ۬للَّهِ فَلْيَتَوَكَّلِ اِ۬لْمُؤْمِنُونَۖ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1768,13,'257',14,'Ibrāhīm','إبراهِيم',4,5,15,'وَمَا لَنَا أَلَّا نَتَوَكَّلَ عَلَى اَ۬للَّهِ وَقَدْ هَدَيٰنَا سُبُلَنَاۖ وَلَنَصْبِرَنَّ عَلَىٰ مَا ءَاذَيْتُمُونَاۖ وَعَلَى اَ۬للَّهِ فَلْيَتَوَكَّلِ اِ۬لْمُتَوَكِّلُونَۖ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1769,13,'257',14,'Ibrāhīm','إبراهِيم',6,8,16,'وَقَالَ اَ۬لذِينَ كَفَرُواْ لِرُسُلِهِمْ لَنُخْرِجَنَّكُم مِّنْ أَرْضِنَا أَوْ لَتَعُودُنَّ فِے مِلَّتِنَاۖ فَأَوْحَىٰ إِلَيْهِمْ رَبُّهُمْ لَنُهْلِكَنَّ اَ۬لظَّٰلِمِينَ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1770,13,'257',14,'Ibrāhīm','إبراهِيم',8,9,17,'وَلَنُسْكِنَنَّكُمُ اُ۬لْأَرْضَ مِنۢ بَعْدِهِمْۖ ذَٰلِكَ لِمَنْ خَافَ مَقَامِے وَخَافَ وَعِيدِۖ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1771,13,'257',14,'Ibrāhīm','إبراهِيم',9,10,18,'وَاسْتَفْتَحُواْۖ وَخَابَ كُلُّ جَبَّارٍ عَنِيدٖ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1772,13,'257',14,'Ibrāhīm','إبراهِيم',10,11,19,'مِّنْ وَّرَآئِهِۦ جَهَنَّمُ وَيُسْقَىٰ مِن مَّآءٖ صَدِيدٖ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1773,13,'257',14,'Ibrāhīm','إبراهِيم',11,13,20,'يَتَجَرَّعُهُۥ وَلَا يَكَادُ يُسِيغُهُۥ وَيَأْتِيهِ اِ۬لْمَوْتُ مِن كُلِّ مَكَانٖ وَمَا هُوَ بِمَيِّتٖ وَمِنْ وَّرَآئِهِۦ عَذَابٌ غَلِيظٞۖ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1774,13,'257',14,'Ibrāhīm','إبراهِيم',13,15,21,'مَّثَلُ اُ۬لذِينَ كَفَرُواْ بِرَبِّهِمْۖ أَعْمَٰلُهُمْ كَرَمَادٍ اِ۪شْتَدَّتْ بِهِ اِ۬لرِّيَٰحُ فِے يَوْمٍ عَاصِفٖ لَّا يَقْدِرُونَ مِمَّا كَسَبُواْ عَلَىٰ شَےْءٖۖ ذَٰلِكَ هُوَ اَ۬لضَّلَٰلُ اُ۬لْبَعِيدُۖ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1775,13,'258',14,'Ibrāhīm','إبراهِيم',1,2,22,'۞ أَلَمْ تَرَ أَنَّ اَ۬للَّهَ خَلَقَ اَ۬لسَّمَٰوَٰتِ وَالْأَرْضَ بِالْحَقِّۖ إِنْ يَّشَأْ يُذْهِبْكُمْ وَيَأْتِ بِخَلْقٖ جَدِيدٖۖ وَمَا ذَٰلِكَ عَلَى اَ۬للَّهِ بِعَزِيزٖۖ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1776,13,'258',14,'Ibrāhīm','إبراهِيم',3,6,23,'وَبَرَزُواْ لِلهِ جَمِيعاٗ فَقَالَ اَ۬لضُّعَفَٰٓؤُاْ لِلذِينَ اَ۪سْتَكْبَرُواْ إِنَّا كُنَّا لَكُمْ تَبَعاٗ فَهَلْ أَنتُم مُّغْنُونَ عَنَّا مِنْ عَذَابِ اِ۬للَّهِ مِن شَےْءٖۖ قَالُواْ لَوْ هَدَيٰنَا اَ۬للَّهُ لَهَدَيْنَٰكُمْۖ سَوَآءٌ عَلَيْنَا أَجَزِعْنَا أَمْ صَبَرْنَا مَا لَنَا مِن مَّحِيصٖۖ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1777,13,'258',14,'Ibrāhīm','إبراهِيم',6,12,24,'وَقَالَ اَ۬لشَّيْطَٰنُ لَمَّا قُضِيَ اَ۬لْأَمْرُ إِنَّ اَ۬للَّهَ وَعَدَكُمْ وَعْدَ اَ۬لْحَقِّ وَوَعَدتُّكُمْ فَأَخْلَفْتُكُمْ وَمَا كَانَ لِے عَلَيْكُم مِّن سُلْطَٰنٍ إِلَّا أَن دَعَوْتُكُمْ فَاسْتَجَبْتُمْ لِے فَلَا تَلُومُونِے وَلُومُواْ أَنفُسَكُم مَّا أَنَا بِمُصْرِخِكُمْ وَمَا أَنتُم بِمُصْرِخِيَّ إِنِّے كَفَرْتُ بِمَا أَشْرَكْتُمُونِ مِن قَبْلُۖ إِنَّ اَ۬لظَّٰلِمِينَ لَهُمْ عَذَابٌ أَلِيمٞۖ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1778,13,'258',14,'Ibrāhīm','إبراهِيم',12,14,25,'وَأُدْخِلَ اَ۬لذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ جَنَّٰتٖ تَجْرِے مِن تَحْتِهَا اَ۬لْأَنْهَٰرُ خَٰلِدِينَ فِيهَا بِإِذْنِ رَبِّهِمْۖ تَحِيَّتُهُمْ فِيهَا سَلَٰمٌۖ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1779,13,'258',14,'Ibrāhīm','إبراهِيم',14,15,26,'أَلَمْ تَرَ كَيْفَ ضَرَبَ اَ۬للَّهُ مَثَلاٗ كَلِمَةٗ طَيِّبَةٗ كَشَجَرَةٖ طَيِّبَةٍ أَصْلُهَا ثَابِتٞ وَفَرْعُهَا فِے اِ۬لسَّمَآءِ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1780,13,'259',14,'Ibrāhīm','إبراهِيم',1,2,27,'تُؤْتِے أُكْلَهَا كُلَّ حِينِۢ بِإِذْنِ رَبِّهَاۖ وَيَضْرِبُ اُ۬للَّهُ اُ۬لْأَمْثَالَ لِلنَّاسِ لَعَلَّهُمْ يَتَذَكَّرُونَۖ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1781,13,'259',14,'Ibrāhīm','إبراهِيم',2,4,28,'وَمَثَلُ كَلِمَةٍ خَبِيثَةٖ كَشَجَرَةٍ خَبِيثَةٍ اُ۟جْتُثَّتْ مِن فَوْقِ اِ۬لْأَرْضِ مَا لَهَا مِن قَرَارٖۖ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1782,13,'259',14,'Ibrāhīm','إبراهِيم',4,6,29,'يُثَبِّتُ اُ۬للَّهُ اُ۬لذِينَ ءَامَنُواْ بِالْقَوْلِ اِ۬لثَّابِتِ فِے اِ۬لْحَيَوٰةِ اِ۬لدُّنْيَا وَفِے اِ۬لْأٓخِرَةِۖ وَيُضِلُّ اُ۬للَّهُ اُ۬لظَّٰلِمِينَۖ وَيَفْعَلُ اُ۬للَّهُ مَا يَشَآءُۖ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1783,13,'259',14,'Ibrāhīm','إبراهِيم',6,7,30,'۞ اَ۬لَمْ تَرَ إِلَى اَ۬لذِينَ بَدَّلُواْ نِعْمَتَ اَ۬للَّهِ كُفْراٗ وَأَحَلُّواْ قَوْمَهُمْ دَارَ اَ۬لْبَوَارِ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1784,13,'259',14,'Ibrāhīm','إبراهِيم',7,8,31,'جَهَنَّمَ يَصْلَوْنَهَاۖ وَبِئْسَ اَ۬لْقَرَارُۖ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1785,13,'259',14,'Ibrāhīm','إبراهِيم',8,9,32,'وَجَعَلُواْ لِلهِ أَندَاداٗ لِّيُضِلُّواْ عَن سَبِيلِهِۦۖ قُلْ تَمَتَّعُواْ فَإِنَّ مَصِيرَكُمْ إِلَى اَ۬لنَّارِۖ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1786,13,'259',14,'Ibrāhīm','إبراهِيم',9,11,33,'قُل لِّعِبَادِيَ اَ۬لذِينَ ءَامَنُواْ يُقِيمُواْ اُ۬لصَّلَوٰةَ وَيُنفِقُواْ مِمَّا رَزَقْنَٰهُمْ سِرّاٗ وَعَلَٰنِيَةٗ مِّن قَبْلِ أَنْ يَّأْتِيَ يَوْمٞ لَّا بَيْعٞ فِيهِ وَلَا خِلَٰلٌۖ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1787,13,'259',14,'Ibrāhīm','إبراهِيم',11,14,34,'اِ۬للَّهُ اُ۬لذِے خَلَقَ اَ۬لسَّمَٰوَٰتِ وَالْأَرْضَ وَأَنزَلَ مِنَ اَ۬لسَّمَآءِ مَآءٗ فَأَخْرَجَ بِهِۦ مِنَ اَ۬لثَّمَرَٰتِ رِزْقاٗ لَّكُمْۖ وَسَخَّرَ لَكُمُ اُ۬لْفُلْكَ لِتَجْرِيَ فِے اِ۬لْبَحْرِ بِأَمْرِهِۦۖ وَسَخَّرَ لَكُمُ اُ۬لْأَنْهَٰرَۖ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1788,13,'259',14,'Ibrāhīm','إبراهِيم',14,15,35,'وَسَخَّرَ لَكُمُ اُ۬لشَّمْسَ وَالْقَمَرَ دَآئِبَيْنِۖ وَسَخَّرَ لَكُمُ اُ۬ليْلَ وَالنَّهَارَۖ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1789,13,'260',14,'Ibrāhīm','إبراهِيم',1,2,36,'وَءَاتَيٰكُم مِّن كُلِّ مَا سَأَلْتُمُوهُۖ وَإِن تَعُدُّواْ نِعْمَتَ اَ۬للَّهِ لَا تُحْصُوهَاۖ إِنَّ اَ۬لْإِنسَٰنَ لَظَلُومٞ كَفَّارٞۖ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1790,13,'260',14,'Ibrāhīm','إبراهِيم',2,4,37,'وَإِذْ قَالَ إِبْرَٰهِيمُ رَبِّ اِ۪جْعَلْ هَٰذَا اَ۬لْبَلَدَ ءَامِناٗ وَاجْنُبْنِے وَبَنِيَّ أَن نَّعْبُدَ اَ۬لْأَصْنَامَۖ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1791,13,'260',14,'Ibrāhīm','إبراهِيم',4,5,38,'رَبِّ إِنَّهُنَّ أَضْلَلْنَ كَثِيراٗ مِّنَ اَ۬لنَّاسِ فَمَن تَبِعَنِے فَإِنَّهُۥ مِنِّے وَمَنْ عَصَانِے فَإِنَّكَ غَفُورٞ رَّحِيمٞۖ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1792,13,'260',14,'Ibrāhīm','إبراهِيم',5,8,39,'رَّبَّنَا إِنِّيَ أَسْكَنتُ مِن ذُرِّيَّتِے بِوَادٍ غَيْرِ ذِے زَرْعٍ عِندَ بَيْتِكَ اَ۬لْمُحَرَّمِۖ رَبَّنَا لِيُقِيمُواْ اُ۬لصَّلَوٰةَ فَاجْعَلْ أَفْـِٕدَةٗ مِّنَ اَ۬لنَّاسِ تَهْوِے إِلَيْهِمْ وَارْزُقْهُم مِّنَ اَ۬لثَّمَرَٰتِ لَعَلَّهُمْ يَشْكُرُونَۖ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1793,13,'260',14,'Ibrāhīm','إبراهِيم',9,10,40,'رَبَّنَا إِنَّكَ تَعْلَمُ مَا نُخْفِے وَمَا نُعْلِنُۖ وَمَا يَخْفَىٰ عَلَى اَ۬للَّهِ مِن شَےْءٖ فِے اِ۬لْأَرْضِ وَلَا فِے اِ۬لسَّمَآءِۖ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1794,13,'260',14,'Ibrāhīm','إبراهِيم',10,12,41,'۞ اِ۬لْحَمْدُ لِلهِ اِ۬لذِے وَهَبَ لِے عَلَى اَ۬لْكِبَرِ إِسْمَٰعِيلَ وَإِسْحَٰقَۖ إِنَّ رَبِّے لَسَمِيعُ اُ۬لدُّعَآءِۖ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1795,13,'260',14,'Ibrāhīm','إبراهِيم',12,13,42,'رَبِّ اِ۪جْعَلْنِے مُقِيمَ اَ۬لصَّلَوٰةِ وَمِن ذُرِّيَّتِےۖ رَبَّنَا وَتَقَبَّلْ دُعَآءِۖ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1796,13,'260',14,'Ibrāhīm','إبراهِيم',13,14,43,'رَبَّنَا اَ۪غْفِرْ لِے وَلِوَٰلِدَيَّ وَلِلْمُؤْمِنِينَ يَوْمَ يَقُومُ اُ۬لْحِسَابُۖ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1797,13,'260',14,'Ibrāhīm','إبراهِيم',14,15,44,'وَلَا تَحْسِبَنَّ اَ۬للَّهَ غَٰفِلاً عَمَّا يَعْمَلُ اُ۬لظَّٰلِمُونَۖ إِنَّمَا يُؤَخِّرُهُمْ لِيَوْمٖ تَشْخَصُ فِيهِ اِ۬لْأَبْصَٰرُ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1798,13,'261',14,'Ibrāhīm','إبراهِيم',1,2,45,'مُهْطِعِينَ مُقْنِعِے رُءُوسِهِمْ لَا يَرْتَدُّ إِلَيْهِمْ طَرْفُهُمْ وَأَفْـِٕدَتُهُمْ هَوَآءٞۖ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1799,13,'261',14,'Ibrāhīm','إبراهِيم',2,5,46,'وَأَنذِرِ اِ۬لنَّاسَ يَوْمَ يَأْتِيهِمُ اُ۬لْعَذَابُ فَيَقُولُ اُ۬لذِينَ ظَلَمُواْ رَبَّنَا أَخِّرْنَا إِلَىٰ أَجَلٖ قَرِيبٖ نُّجِبْ دَعْوَتَكَ وَنَتَّبِعِ اِ۬لرُّسُلَۖ أَوَلَمْ تَكُونُواْ أَقْسَمْتُم مِّن قَبْلُ مَا لَكُم مِّن زَوَالٖۖ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1800,13,'261',14,'Ibrāhīm','إبراهِيم',5,7,47,'وَسَكَنتُمْ فِے مَسَٰكِنِ اِ۬لذِينَ ظَلَمُواْ أَنفُسَهُمْ وَتَبَيَّنَ لَكُمْ كَيْفَ فَعَلْنَا بِهِمْ وَضَرَبْنَا لَكُمُ اُ۬لْأَمْثَالَۖ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1801,13,'261',14,'Ibrāhīm','إبراهِيم',7,8,48,'وَقَدْ مَكَرُواْ مَكْرَهُمْۖ وَعِندَ اَ۬للَّهِ مَكْرُهُمْۖ وَإِن كَانَ مَكْرُهُمْ لِتَزُولَ مِنْهُ اُ۬لْجِبَالُۖ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1802,13,'261',14,'Ibrāhīm','إبراهِيم',8,10,49,'فَلَا تَحْسِبَنَّ اَ۬للَّهَ مُخْلِفَ وَعْدِهِۦ رُسُلَهُۥۖ إِنَّ اَ۬للَّهَ عَزِيزٞ ذُو اُ۪نتِقَامٖۖ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1803,13,'261',14,'Ibrāhīm','إبراهِيم',10,11,50,'يَوْمَ تُبَدَّلُ اُ۬لْأَرْضُ غَيْرَ اَ۬لْأَرْضِ وَالسَّمَٰوَٰتُۖ وَبَرَزُواْ لِلهِ اِ۬لْوَٰحِدِ اِ۬لْقَهَّارِۖ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1804,13,'261',14,'Ibrāhīm','إبراهِيم',11,12,51,'وَتَرَى اَ۬لْمُجْرِمِينَ يَوْمَئِذٖ مُّقَرَّنِينَ فِے اِ۬لْأَصْفَادِ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1805,13,'261',14,'Ibrāhīm','إبراهِيم',12,13,52,'سَرَابِيلُهُم مِّن قَطِرَانٖ وَتَغْشَىٰ وُجُوهَهُمُ اُ۬لنَّارُ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1806,13,'261',14,'Ibrāhīm','إبراهِيم',13,14,53,'لِيَجْزِيَ اَ۬للَّهُ كُلَّ نَفْسٖ مَّا كَسَبَتْۖ إِنَّ اَ۬للَّهَ سَرِيعُ اُ۬لْحِسَابِۖ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1807,13,'261',14,'Ibrāhīm','إبراهِيم',14,15,54,'هَٰذَا بَلَٰغٞ لِّلنَّاسِ وَلِيُنذَرُواْ بِهِۦ وَلِيَعْلَمُواْ أَنَّمَا هُوَ إِلَٰهٞ وَٰحِدٞ وَلِيَذَّكَّرَ أُوْلُواْ اُ۬لْأَلْبَٰبِۖ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1808,14,'262',15,'Al-Ḥijr','الحِجر',3,3,1,'أَلَٓرَۖ تِلْكَ ءَايَٰتُ اُ۬لْكِتَٰبِ وَقُرْءَانٖ مُّبِينٖۖ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1809,14,'262',15,'Al-Ḥijr','الحِجر',3,4,2,'رُّبَمَا يَوَدُّ اُ۬لذِينَ كَفَرُواْ لَوْ كَانُواْ مُسْلِمِينَۖ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1810,14,'262',15,'Al-Ḥijr','الحِجر',4,5,3,'ذَرْهُمْ يَأْكُلُواْ وَيَتَمَتَّعُواْ وَيُلْهِهِمُ اُ۬لْأَمَلُۖ فَسَوْفَ يَعْلَمُونَۖ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1811,14,'262',15,'Al-Ḥijr','الحِجر',5,6,4,'وَمَا أَهْلَكْنَا مِن قَرْيَةٍ إِلَّا وَلَهَا كِتَابٞ مَّعْلُومٞۖ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1812,14,'262',15,'Al-Ḥijr','الحِجر',6,7,5,'مَّا تَسْبِقُ مِنْ أُمَّةٍ أَجَلَهَا وَمَا يَسْتَٰٔخِرُونَۖ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1813,14,'262',15,'Al-Ḥijr','الحِجر',7,8,6,'وَقَالُواْ يَٰأَيُّهَا اَ۬لذِے نُزِّلَ عَلَيْهِ اِ۬لذِّكْرُ إِنَّكَ لَمَجْنُونٞ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1814,14,'262',15,'Al-Ḥijr','الحِجر',8,9,7,'لَّوْ مَا تَأْتِينَا بِالْمَلَٰٓئِكَةِ إِن كُنتَ مِنَ اَ۬لصَّٰدِقِينَۖ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1815,14,'262',15,'Al-Ḥijr','الحِجر',9,10,8,'مَا تَنَزَّلُ اُ۬لْمَلَٰٓئِكَةُ إِلَّا بِالْحَقِّۖ وَمَا كَانُواْ إِذاٗ مُّنظَرِينَۖ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1816,14,'262',15,'Al-Ḥijr','الحِجر',10,10,9,'إِنَّا نَحْنُ نَزَّلْنَا اَ۬لذِّكْرَۖ وَإِنَّا لَهُۥ لَحَٰفِظُونَۖ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1817,14,'262',15,'Al-Ḥijr','الحِجر',11,11,10,'وَلَقَدْ أَرْسَلْنَا مِن قَبْلِكَ فِے شِيَعِ اِ۬لْأَوَّلِينَۖ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1818,14,'262',15,'Al-Ḥijr','الحِجر',11,12,11,'وَمَا يَأْتِيهِم مِّن رَّسُولٍ إِلَّا كَانُواْ بِهِۦ يَسْتَهْزِءُونَۖ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1819,14,'262',15,'Al-Ḥijr','الحِجر',12,13,12,'كَذَٰلِكَ نَسْلُكُهُۥ فِے قُلُوبِ اِ۬لْمُجْرِمِينَ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1820,14,'262',15,'Al-Ḥijr','الحِجر',13,13,13,'لَا يُؤْمِنُونَ بِهِۦ وَقَدْ خَلَتْ سُنَّةُ اُ۬لْأَوَّلِينَۖ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1821,14,'262',15,'Al-Ḥijr','الحِجر',14,14,14,'وَلَوْ فَتَحْنَا عَلَيْهِم بَاباٗ مِّنَ اَ۬لسَّمَآءِ فَظَلُّواْ فِيهِ يَعْرُجُونَ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1822,14,'262',15,'Al-Ḥijr','الحِجر',15,15,15,'لَقَالُواْ إِنَّمَا سُكِّرَتْ أَبْصَٰرُنَا بَلْ نَحْنُ قَوْمٞ مَّسْحُورُونَۖ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1823,14,'263',15,'Al-Ḥijr','الحِجر',1,1,16,'وَلَقَدْ جَعَلْنَا فِے اِ۬لسَّمَآءِ بُرُوجاٗ وَزَيَّنَّٰهَا لِلنَّٰظِرِينَ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1824,14,'263',15,'Al-Ḥijr','الحِجر',2,2,17,'وَحَفِظْنَٰهَا مِن كُلِّ شَيْطَٰنٖ رَّجِيمٍ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1825,14,'263',15,'Al-Ḥijr','الحِجر',2,3,18,'إِلَّا مَنِ اِ۪سْتَرَقَ اَ۬لسَّمْعَ فَأَتْبَعَهُۥ شِهَابٞ مُّبِينٞۖ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1826,14,'263',15,'Al-Ḥijr','الحِجر',3,4,19,'وَالْأَرْضَ مَدَدْنَٰهَا وَأَلْقَيْنَا فِيهَا رَوَٰسِيَ وَأَنۢبَتْنَا فِيهَا مِن كُلِّ شَےْءٖ مَّوْزُونٖ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1827,14,'263',15,'Al-Ḥijr','الحِجر',4,5,20,'وَجَعَلْنَا لَكُمْ فِيهَا مَعَٰيِشَۖ وَمَن لَّسْتُمْ لَهُۥ بِرَٰزِقِينَۖ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1828,14,'263',15,'Al-Ḥijr','الحِجر',5,6,21,'وَإِن مِّن شَےْءٍ إِلَّا عِندَنَا خَزَآئِنُهُۥۖ وَمَا نُنَزِّلُهُۥ إِلَّا بِقَدَرٖ مَّعْلُومٖۖ ٢١');
