INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (1990,14,'276',16,'An-Naḥl','النَّحل',9,10,84,'وَيَوْمَ نَبْعَثُ مِن كُلِّ أُمَّةٖ شَهِيداٗ ثُمَّ لَا يُؤْذَنُ لِلذِينَ كَفَرُواْ وَلَا هُمْ يُسْتَعْتَبُونَۖ ٨٤');
