INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2002,14,'278',16,'An-Naḥl','النَّحل',4,6,96,'مَا عِندَكُمْ يَنفَدُۖ وَمَا عِندَ اَ۬للَّهِ بَاقٖۖ وَلَيَجْزِيَنَّ اَ۬لذِينَ صَبَرُواْ أَجْرَهُم بِأَحْسَنِ مَا كَانُواْ يَعْمَلُونَۖ ٩٦');
