INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3159,19,'377',27,'An-Naml','النَّمل',7,7,6,'۞ وَإِنَّكَ لَتُلَقَّى اَ۬لْقُرْءَانَ مِن لَّدُنْ حَكِيمٍ عَلِيمٍۖ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3160,19,'377',27,'An-Naml','النَّمل',7,9,7,'إِذْ قَالَ مُوسَىٰ لِأَهْلِهِۦ إِنِّيَ ءَانَسْتُ نَاراٗ سَـَٔاتِيكُم مِّنْهَا بِخَبَرٍ أَوْ ءَاتِيكُم بِشِهَابِ قَبَسٖ لَّعَلَّكُمْ تَصْطَلُونَۖ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3161,19,'377',27,'An-Naml','النَّمل',9,10,8,'فَلَمَّا جَآءَهَا نُودِيَ أَنۢ بُورِكَ مَن فِے اِ۬لنَّارِ وَمَنْ حَوْلَهَاۖ وَسُبْحَٰنَ اَ۬للَّهِ رَبِّ اِ۬لْعَٰلَمِينَۖ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3162,19,'377',27,'An-Naml','النَّمل',10,10,9,'يَٰمُوسَىٰ إِنَّهُۥ أَنَا اَ۬للَّهُ اُ۬لْعَزِيزُ اُ۬لْحَكِيمُ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3163,19,'377',27,'An-Naml','النَّمل',11,12,10,'وَأَلْقِ عَصَاكَۖ فَلَمَّا رَءَاهَا تَهْتَزُّ كَأَنَّهَا جَآنّٞ وَلَّىٰ مُدْبِراٗ وَلَمْ يُعَقِّبْۖ يَٰمُوسَىٰ لَا تَخَفْۖ إِنِّے لَا يَخَافُ لَدَيَّ اَ۬لْمُرْسَلُونَۖ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3164,19,'377',27,'An-Naml','النَّمل',12,13,11,'إِلَّا مَن ظَلَمَ ثُمَّ بَدَّلَ حُسْناَۢ بَعْدَ سُوٓءٖ فَإِنِّے غَفُورٞ رَّحِيمٞۖ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3165,19,'377',27,'An-Naml','النَّمل',13,15,12,'وَأَدْخِلْ يَدَكَ فِے جَيْبِكَ تَخْرُجْ بَيْضَآءَ مِنْ غَيْرِ سُوٓءٖۖ فِے تِسْعِ ءَايَٰتٍ إِلَىٰ فِرْعَوْنَ وَقَوْمِهِۦۖ إِنَّهُمْ كَانُواْ قَوْماٗ فَٰسِقِينَۖ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3166,19,'377',27,'An-Naml','النَّمل',15,15,13,'فَلَمَّا جَآءَتْهُمْ ءَايَٰتُنَا مُبْصِرَةٗ قَالُواْ هَٰذَا سِحْرٞ مُّبِينٞۖ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3167,19,'378',27,'An-Naml','النَّمل',1,2,14,'وَجَحَدُواْ بِهَا وَاسْتَيْقَنَتْهَا أَنفُسُهُمْ ظُلْماٗ وَعُلُوّاٗۖ فَانظُرْ كَيْفَ كَانَ عَٰقِبَةُ اُ۬لْمُفْسِدِينَۖ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3168,19,'378',27,'An-Naml','النَّمل',2,3,15,'وَلَقَدْ ءَاتَيْنَا دَاوُۥدَ وَسُلَيْمَٰنَ عِلْماٗ وَقَالَا اَ۬لْحَمْدُ لِلهِ اِ۬لذِے فَضَّلَنَا عَلَىٰ كَثِيرٖ مِّنْ عِبَادِهِ اِ۬لْمُؤْمِنِينَۖ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3169,19,'378',27,'An-Naml','النَّمل',4,5,16,'وَوَرِثَ سُلَيْمَٰنُ دَاوُۥدَ وَقَالَ يَٰأَيُّهَا اَ۬لنَّاسُ عُلِّمْنَا مَنطِقَ اَ۬لطَّيْرِ وَأُوتِينَا مِن كُلِّ شَےْءٍۖ إِنَّ هَٰذَا لَهْوَ اَ۬لْفَضْلُ اُ۬لْمُبِينُۖ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3170,19,'378',27,'An-Naml','النَّمل',6,7,17,'۞ وَحُشِرَ لِسُلَيْمَٰنَ جُنُودُهُۥ مِنَ اَ۬لْجِنِّ وَالْإِنسِ وَالطَّيْرِ فَهُمْ يُوزَعُونَۖ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3171,19,'378',27,'An-Naml','النَّمل',7,9,18,'حَتَّىٰ إِذَا أَتَوْاْ عَلَىٰ وَادِ اِ۬لنَّمْلِ قَالَتْ نَمْلَةٞ يَٰأَيُّهَا اَ۬لنَّمْلُ اُ۟دْخُلُواْ مَسَٰكِنَكُمْ لَا يَحْطِمَنَّكُمْ سُلَيْمَٰنُ وَجُنُودُهُۥ وَهُمْ لَا يَشْعُرُونَۖ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3172,19,'378',27,'An-Naml','النَّمل',9,11,19,'فَتَبَسَّمَ ضَاحِكاٗ مِّن قَوْلِهَا وَقَالَ رَبِّ أَوْزِعْنِے أَنْ أَشْكُرَ نِعْمَتَكَ اَ۬لتِے أَنْعَمْتَ عَلَيَّ وَعَلَىٰ وَٰلِدَيَّ وَأَنْ أَعْمَلَ صَٰلِحاٗ تَرْضَيٰهُ وَأَدْخِلْنِے بِرَحْمَتِكَ فِے عِبَادِكَ اَ۬لصَّٰلِحِينَۖ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3173,19,'378',27,'An-Naml','النَّمل',12,13,20,'وَتَفَقَّدَ اَ۬لطَّيْرَ فَقَالَ مَا لِے لَا أَرَى اَ۬لْهُدْهُدَ أَمْ كَانَ مِنَ اَ۬لْغَآئِبِينَۖ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3174,19,'378',27,'An-Naml','النَّمل',13,14,21,'لَأُعَذِّبَنَّهُۥ عَذَاباٗ شَدِيداً أَوْ لَأَاْذْبَحَنَّهُۥ أَوْ لَيَأْتِيَنِّے بِسُلْطَٰنٖ مُّبِينٖۖ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3175,19,'378',27,'An-Naml','النَّمل',14,15,22,'فَمَكُثَ غَيْرَ بَعِيدٖۖ فَقَالَ أَحَطْتُّ بِمَا لَمْ تُحِطْ بِهِۦ وَجِئْتُكَ مِن سَبَإِۢ بِنَبَإٖ يَقِينٍۖ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3176,19,'379',27,'An-Naml','النَّمل',1,2,23,'إِنِّے وَجَدتُّ اُ۪مْرَأَةٗ تَمْلِكُهُمْ وَأُوتِيَتْ مِن كُلِّ شَےْءٖۖ وَلَهَا عَرْشٌ عَظِيمٞۖ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3177,19,'379',27,'An-Naml','النَّمل',2,4,24,'وَجَدتُّهَا وَقَوْمَهَا يَسْجُدُونَ لِلشَّمْسِ مِن دُونِ اِ۬للَّهِ وَزَيَّنَ لَهُمُ اُ۬لشَّيْطَٰنُ أَعْمَٰلَهُمْ فَصَدَّهُمْ عَنِ اِ۬لسَّبِيلِ فَهُمْ لَا يَهْتَدُونَ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3178,19,'379',27,'An-Naml','النَّمل',4,5,25,'أَلَّا يَسْجُدُواْ لِلهِ اِ۬لذِے يُخْرِجُ اُ۬لْخَبْءَ فِے اِ۬لسَّمَٰوَٰتِ وَالْأَرْضِ وَيَعْلَمُ مَا يُخْفُونَ وَمَا يُعْلِنُونَۖ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3179,19,'379',27,'An-Naml','النَّمل',6,6,26,'اَ۬للَّهُ لَا إِلَٰهَ إِلَّا هُوَ رَبُّ اُ۬لْعَرْشِ اِ۬لْعَظِيمِۖ۩ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3180,19,'379',27,'An-Naml','النَّمل',6,7,27,'۞ قَالَ سَنَنظُرُ أَصَدَقْتَ أَمْ كُنتَ مِنَ اَ۬لْكَٰذِبِينَۖ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3181,19,'379',27,'An-Naml','النَّمل',7,8,28,'اَ۪ذْهَب بِّكِتَٰبِے هَٰذَا فَأَلْقِهِ إِلَيْهِمْ ثُمَّ تَوَلَّ عَنْهُمْ فَانظُرْ مَاذَا يَرْجِعُونَۖ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3182,19,'379',27,'An-Naml','النَّمل',8,9,29,'قَالَتْ يَٰأَيُّهَا اَ۬لْمَلَؤُاْ اِ۪نِّيَ أُلْقِيَ إِلَيَّ كِتَٰبٞ كَرِيمٌ ٢٩');
