INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3437,21,'408',30,'Ar-Rūm','الرُّوم',1,2,32,'وَإِذَا مَسَّ اَ۬لنَّاسَ ضُرّٞ دَعَوْاْ رَبَّهُم مُّنِيبِينَ إِلَيْهِۖ ثُمَّ إِذَا أَذَاقَهُم مِّنْهُ رَحْمَةً إِذَا فَرِيقٞ مِّنْهُم بِرَبِّهِمْ يُشْرِكُونَۖ ٣٢');
