INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3507,21,'415',32,'As-Sajdah','السَّجدة',14,14,10,'بَلْ هُم بِلِقَآءِ رَبِّهِمْ كَٰفِرُونَۖ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3508,21,'415',32,'As-Sajdah','السَّجدة',14,15,11,'۞ قُلْ يَتَوَفَّيٰكُم مَّلَكُ اُ۬لْمَوْتِ اِ۬لذِے وُكِّلَ بِكُمْ ثُمَّ إِلَىٰ رَبِّكُمْ تُرْجَعُونَۖ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3509,21,'416',32,'As-Sajdah','السَّجدة',1,2,12,'وَلَوْ تَرَىٰ إِذِ اِ۬لْمُجْرِمُونَ نَاكِسُواْ رُءُوسِهِمْ عِندَ رَبِّهِمْ رَبَّنَا أَبْصَرْنَا وَسَمِعْنَا فَارْجِعْنَا نَعْمَلْ صَٰلِحاً إِنَّا مُوقِنُونَۖ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3510,21,'416',32,'As-Sajdah','السَّجدة',3,4,13,'وَلَوْ شِئْنَا لَأٓتَيْنَا كُلَّ نَفْسٍ هُدَيٰهَاۖ وَلَٰكِنْ حَقَّ اَ۬لْقَوْلُ مِنِّے لَأَمْلَأَنَّ جَهَنَّمَ مِنَ اَ۬لْجِنَّةِ وَالنَّاسِ أَجْمَعِينَۖ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3511,21,'416',32,'As-Sajdah','السَّجدة',4,6,14,'فَذُوقُواْ بِمَا نَسِيتُمْ لِقَآءَ يَوْمِكُمْ هَٰذَا إِنَّا نَسِينَٰكُمْۖ وَذُوقُواْ عَذَابَ اَ۬لْخُلْدِ بِمَا كُنتُمْ تَعْمَلُونَۖ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3512,21,'416',32,'As-Sajdah','السَّجدة',6,8,15,'إِنَّمَا يُؤْمِنُ بِـَٔايَٰتِنَا اَ۬لذِينَ إِذَا ذُكِّرُواْ بِهَا خَرُّواْ سُجَّداٗ وَسَبَّحُواْ بِحَمْدِ رَبِّهِمْ وَهُمْ لَا يَسْتَكْبِرُونَۖ۩ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3513,21,'416',32,'As-Sajdah','السَّجدة',8,9,16,'تَتَجَافَىٰ جُنُوبُهُمْ عَنِ اِ۬لْمَضَاجِعِ يَدْعُونَ رَبَّهُمْ خَوْفاٗ وَطَمَعاٗ وَمِمَّا رَزَقْنَٰهُمْ يُنفِقُونَۖ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3514,21,'416',32,'As-Sajdah','السَّجدة',10,11,17,'فَلَا تَعْلَمُ نَفْسٞ مَّا أُخْفِيَ لَهُم مِّن قُرَّةِ أَعْيُنٖ جَزَآءَۢ بِمَا كَانُواْ يَعْمَلُونَۖ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3515,21,'416',32,'As-Sajdah','السَّجدة',11,12,18,'أَفَمَن كَانَ مُؤْمِناٗ كَمَن كَانَ فَاسِقاٗۖ لَّا يَسْتَوُۥنَۖ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3516,21,'416',32,'As-Sajdah','السَّجدة',12,13,19,'أَمَّا اَ۬لذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ فَلَهُمْ جَنَّٰتُ اُ۬لْمَأْوَىٰ نُزُلاَۢ بِمَا كَانُواْ يَعْمَلُونَۖ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3517,21,'416',32,'As-Sajdah','السَّجدة',13,15,20,'وَأَمَّا اَ۬لذِينَ فَسَقُواْ فَمَأْوَيٰهُمُ اُ۬لنَّارُۖ كُلَّمَا أَرَادُواْ أَنْ يَّخْرُجُواْ مِنْهَا أُعِيدُواْ فِيهَا وَقِيلَ لَهُمْ ذُوقُواْ عَذَابَ اَ۬لنَّارِ اِ۬لذِے كُنتُم بِهِۦ تُكَذِّبُونَۖ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3518,21,'417',32,'As-Sajdah','السَّجدة',1,2,21,'۞ وَلَنُذِيقَنَّهُم مِّنَ اَ۬لْعَذَابِ اِ۬لْأَدْنَىٰ دُونَ اَ۬لْعَذَابِ اِ۬لْأَكْبَرِ لَعَلَّهُمْ يَرْجِعُونَۖ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3519,21,'417',32,'As-Sajdah','السَّجدة',2,3,22,'وَمَنْ أَظْلَمُ مِمَّن ذُكِّرَ بِـَٔايَٰتِ رَبِّهِۦ ثُمَّ أَعْرَضَ عَنْهَاۖ إِنَّا مِنَ اَ۬لْمُجْرِمِينَ مُنتَقِمُونَۖ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3520,21,'417',32,'As-Sajdah','السَّجدة',3,5,23,'وَلَقَدْ ءَاتَيْنَا مُوسَى اَ۬لْكِتَٰبَۖ فَلَا تَكُن فِے مِرْيَةٖ مِّن لِّقَآئِهِۦۖ وَجَعَلْنَٰهُ هُدىٗ لِّبَنِے إِسْرَآءِيلَۖ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3521,21,'417',32,'As-Sajdah','السَّجدة',5,6,24,'وَجَعَلْنَا مِنْهُمْ أَئِمَّةٗ يَهْدُونَ بِأَمْرِنَا لَمَّا صَبَرُواْۖ وَكَانُواْ بِـَٔايَٰتِنَا يُوقِنُونَۖ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3522,21,'417',32,'As-Sajdah','السَّجدة',7,8,25,'إِنَّ رَبَّكَ هُوَ يَفْصِلُ بَيْنَهُمْ يَوْمَ اَ۬لْقِيَٰمَةِ فِيمَا كَانُواْ فِيهِ يَخْتَلِفُونَۖ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3523,21,'417',32,'As-Sajdah','السَّجدة',8,10,26,'أَوَلَمْ يَهْدِ لَهُمْ كَمْ أَهْلَكْنَا مِن قَبْلِهِم مِّنَ اَ۬لْقُرُونِ يَمْشُونَ فِے مَسَٰكِنِهِمْۖ إِنَّ فِے ذَٰلِكَ لَأٓيَٰتٍۖ أَفَلَا يَسْمَعُونَۖ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3524,21,'417',32,'As-Sajdah','السَّجدة',10,12,27,'أَوَلَمْ يَرَوْاْ أَنَّا نَسُوقُ اُ۬لْمَآءَ ا۪لَى اَ۬لْأَرْضِ اِ۬لْجُرُزِ فَنُخْرِجُ بِهِۦ زَرْعاٗ تَأْكُلُ مِنْهُ أَنْعَٰمُهُمْ وَأَنفُسُهُمْۖ أَفَلَا يُبْصِرُونَۖ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3525,21,'417',32,'As-Sajdah','السَّجدة',13,13,28,'وَيَقُولُونَ مَتَىٰ هَٰذَا اَ۬لْفَتْحُ إِن كُنتُمْ صَٰدِقِينَۖ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3526,21,'417',32,'As-Sajdah','السَّجدة',14,15,29,'قُلْ يَوْمَ اَ۬لْفَتْحِ لَا يَنفَعُ اُ۬لذِينَ كَفَرُواْ إِيمَٰنُهُمْ وَلَا هُمْ يُنظَرُونَۖ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3527,21,'417',32,'As-Sajdah','السَّجدة',15,15,30,'فَأَعْرِضْ عَنْهُمْ وَانتَظِرْۖ إِنَّهُم مُّنتَظِرُونَۖ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3528,21,'418',33,'Al-Aḥzāb','الأحزَاب',3,4,1,'يَٰأَيُّهَا‏ اَ۬لنَّبِےٓءُ اُ۪تَّقِ اِ۬للَّهَۖ وَلَا تُطِعِ اِ۬لْكَٰفِرِينَ وَالْمُنَٰفِقِينَۖ إِنَّ اَ۬للَّهَ كَانَ عَلِيماً حَكِيماٗۖ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3529,21,'418',33,'Al-Aḥzāb','الأحزَاب',4,5,2,'وَاتَّبِعْ مَا يُوحَىٰ إِلَيْكَ مِن رَّبِّكَۖ إِنَّ اَ۬للَّهَ كَانَ بِمَا تَعْمَلُونَ خَبِيراٗۖ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3530,21,'418',33,'Al-Aḥzāb','الأحزَاب',5,5,3,'وَتَوَكَّلْ عَلَى اَ۬للَّهِۖ وَكَفَىٰ بِاللَّهِ وَكِيلاٗۖ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3531,21,'418',33,'Al-Aḥzāb','الأحزَاب',5,8,4,'مَّا جَعَلَ اَ۬للَّهُ لِرَجُلٖ مِّن قَلْبَيْنِ فِے جَوْفِهِۦۖ وَمَا جَعَلَ أَزْوَٰجَكُمُ اُ۬لَّٰٓئِے تَظَّهَّرُونَ مِنْهُنَّ أُمَّهَٰتِكُمْۖ وَمَا جَعَلَ أَدْعِيَآءَكُمْ أَبْنَآءَكُمْۖ ذَٰلِكُمْ قَوْلُكُم بِأَفْوَٰهِكُمْۖ وَاللَّهُ يَقُولُ اُ۬لْحَقَّۖ وَهْوَ يَهْدِے اِ۬لسَّبِيلَۖ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3532,21,'418',33,'Al-Aḥzāb','الأحزَاب',9,12,5,'اَ۟دْعُوهُمْ لِأٓبَآئِهِمْ هُوَ أَقْسَطُ عِندَ اَ۬للَّهِۖ فَإِن لَّمْ تَعْلَمُواْ ءَابَآءَهُمْ فَإِخْوَٰنُكُمْ فِے اِ۬لدِّينِ وَمَوَٰلِيكُمْۖ وَلَيْسَ عَلَيْكُمْ جُنَاحٞ فِيمَا أَخْطَأْتُم بِهِۦۖ وَلَٰكِن مَّا تَعَمَّدَتْ قُلُوبُكُمْۖ وَكَانَ اَ۬للَّهُ غَفُوراٗ رَّحِيماًۖ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3533,21,'418',33,'Al-Aḥzāb','الأحزَاب',12,15,6,'اِ۬لنَّبِےٓءُ اَ۬وْلَىٰ بِالْمُؤْمِنِينَ مِنْ أَنفُسِهِمْۖ وَأَزْوَٰجُهُۥ أُمَّهَٰتُهُمْۖ وَأُوْلُواْ اُ۬لْأَرْحَامِ بَعْضُهُمْ أَوْلَىٰ بِبَعْضٖ فِے كِتَٰبِ اِ۬للَّهِ مِنَ اَ۬لْمُؤْمِنِينَ وَالْمُهَٰجِرِينَ إِلَّا أَن تَفْعَلُواْ إِلَىٰ أَوْلِيَآئِكُم مَّعْرُوفاٗۖ كَانَ ذَٰلِكَ فِے اِ۬لْكِتَٰبِ مَسْطُوراٗۖ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3534,21,'419',33,'Al-Aḥzāb','الأحزَاب',1,2,7,'وَإِذْ أَخَذْنَا مِنَ اَ۬لنَّبِيٓـِٕۧنَ مِيثَٰقَهُمْ وَمِنكَ وَمِن نُّوحٖ وَإِبْرَٰهِيمَ وَمُوسَىٰ وَعِيسَى اَ۪بْنِ مَرْيَمَۖ وَأَخَذْنَا مِنْهُم مِّيثَٰقاً غَلِيظاٗ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3535,21,'419',33,'Al-Aḥzāb','الأحزَاب',3,3,8,'لِّيَسْـَٔلَ اَ۬لصَّٰدِقِينَ عَن صِدْقِهِمْۖ وَأَعَدَّ لِلْكَٰفِرِينَ عَذَاباً أَلِيماٗۖ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3536,21,'419',33,'Al-Aḥzāb','الأحزَاب',4,6,9,'۞ يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ اُ۟ذْكُرُواْ نِعْمَةَ اَ۬للَّهِ عَلَيْكُمْ إِذْ جَآءَتْكُمْ جُنُودٞ فَأَرْسَلْنَا عَلَيْهِمْ رِيحاٗ وَجُنُوداٗ لَّمْ تَرَوْهَاۖ وَكَانَ اَ۬للَّهُ بِمَا تَعْمَلُونَ بَصِيراًۖ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3537,21,'419',33,'Al-Aḥzāb','الأحزَاب',6,8,10,'إِذْ جَآءُوكُم مِّن فَوْقِكُمْ وَمِنْ أَسْفَلَ مِنكُمْۖ وَإِذْ زَاغَتِ اِ۬لْأَبْصَٰرُ وَبَلَغَتِ اِ۬لْقُلُوبُ اُ۬لْحَنَاجِرَ وَتَظُنُّونَ بِاللَّهِ اِ۬لظُّنُونَاۖ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3538,21,'419',33,'Al-Aḥzāb','الأحزَاب',8,9,11,'هُنَالِكَ اَ۟بْتُلِيَ اَ۬لْمُؤْمِنُونَ وَزُلْزِلُواْ زِلْزَالاٗ شَدِيداٗۖ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3539,21,'419',33,'Al-Aḥzāb','الأحزَاب',9,10,12,'وَإِذْ يَقُولُ اُ۬لْمُنَٰفِقُونَ وَالذِينَ فِے قُلُوبِهِم مَّرَضٞ مَّا وَعَدَنَا اَ۬للَّهُ وَرَسُولُهُۥ إِلَّا غُرُوراٗۖ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3540,21,'419',33,'Al-Aḥzāb','الأحزَاب',10,13,13,'وَإِذْ قَالَت طَّآئِفَةٞ مِّنْهُمْ يَٰأَهْلَ يَثْرِبَ لَا مَقَامَ لَكُمْ فَارْجِعُواْۖ وَيَسْتَٰٔذِنُ فَرِيقٞ مِّنْهُمُ اُ۬لنَّبِےٓءَ يَقُولُونَ إِنَّ بِيُوتَنَا عَوْرَةٞ وَمَا هِيَ بِعَوْرَةٍۖ إِنْ يُّرِيدُونَ إِلَّا فِرَاراٗۖ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3541,21,'419',33,'Al-Aḥzāb','الأحزَاب',13,14,14,'وَلَوْ دُخِلَتْ عَلَيْهِم مِّنْ أَقْطَارِهَا ثُمَّ سُئِلُواْ اُ۬لْفِتْنَةَ لَأَتَوْهَا وَمَا تَلَبَّثُواْ بِهَا إِلَّا يَسِيراٗۖ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3542,21,'419',33,'Al-Aḥzāb','الأحزَاب',14,15,15,'وَلَقَدْ كَانُواْ عَٰهَدُواْ اُ۬للَّهَ مِن قَبْلُ لَا يُوَلُّونَ اَ۬لْأَدْبَٰرَۖ وَكَانَ عَهْدُ اُ۬للَّهِ مَسْـُٔولاٗۖ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3543,21,'420',33,'Al-Aḥzāb','الأحزَاب',1,2,16,'قُل لَّنْ يَّنفَعَكُمُ اُ۬لْفِرَارُ إِن فَرَرْتُم مِّنَ اَ۬لْمَوْتِ أَوِ اِ۬لْقَتْلِۖ وَإِذاٗ لَّا تُمَتَّعُونَ إِلَّا قَلِيلاٗۖ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3544,21,'420',33,'Al-Aḥzāb','الأحزَاب',2,4,17,'قُلْ مَن ذَا اَ۬لذِے يَعْصِمُكُم مِّنَ اَ۬للَّهِ إِنْ أَرَادَ بِكُمْ سُوٓءاً أَوْ أَرَادَ بِكُمْ رَحْمَةٗۖ وَلَا يَجِدُونَ لَهُم مِّن دُونِ اِ۬للَّهِ وَلِيّاٗ وَلَا نَصِيراٗۖ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3545,21,'420',33,'Al-Aḥzāb','الأحزَاب',4,5,18,'۞ قَدْ يَعْلَمُ اُ۬للَّهُ اُ۬لْمُعَوِّقِينَ مِنكُمْ وَالْقَآئِلِينَ لِإِخْوَٰنِهِمْ هَلُمَّ إِلَيْنَا وَلَا يَأْتُونَ اَ۬لْبَأْسَ إِلَّا قَلِيلاً ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3546,21,'420',33,'Al-Aḥzāb','الأحزَاب',6,9,19,'أَشِحَّةً عَلَيْكُمْۖ فَإِذَا جَآءَ اَ۬لْخَوْفُ رَأَيْتَهُمْ يَنظُرُونَ إِلَيْكَ تَدُورُ أَعْيُنُهُمْ كَالذِے يُغْشَىٰ عَلَيْهِ مِنَ اَ۬لْمَوْتِۖ فَإِذَا ذَهَبَ اَ۬لْخَوْفُ سَلَقُوكُم بِأَلْسِنَةٍ حِدَادٍ أَشِحَّةً عَلَى اَ۬لْخَيْرِۖ أُوْلَٰٓئِكَ لَمْ يُؤْمِنُواْ فَأَحْبَطَ اَ۬للَّهُ أَعْمَٰلَهُمْۖ وَكَانَ ذَٰلِكَ عَلَى اَ۬للَّهِ يَسِيراٗۖ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3547,21,'420',33,'Al-Aḥzāb','الأحزَاب',9,12,20,'يَحْسِبُونَ اَ۬لْأَحْزَابَ لَمْ يَذْهَبُواْۖ وَإِنْ يَّأْتِ اِ۬لْأَحْزَابُ يَوَدُّواْ لَوْ أَنَّهُم بَادُونَ فِے اِ۬لْأَعْرَابِۖ يَسْـَٔلُونَ عَنْ أَنۢبَآئِكُمْ وَلَوْ كَانُواْ فِيكُم مَّا قَٰتَلُواْ إِلَّا قَلِيلاٗۖ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3548,21,'420',33,'Al-Aḥzāb','الأحزَاب',12,13,21,'لَّقَدْ كَانَ لَكُمْ فِے رَسُولِ اِ۬للَّهِ إِسْوَةٌ حَسَنَةٞ لِّمَن كَانَ يَرْجُواْ اُ۬للَّهَ وَالْيَوْمَ اَ۬لْأٓخِرَ وَذَكَرَ اَ۬للَّهَ كَثِيراٗۖ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3549,21,'420',33,'Al-Aḥzāb','الأحزَاب',13,15,22,'وَلَمَّا رَءَا اَ۬لْمُؤْمِنُونَ اَ۬لْأَحْزَابَ قَالُواْ هَٰذَا مَا وَعَدَنَا اَ۬للَّهُ وَرَسُولُهُۥ وَصَدَقَ اَ۬للَّهُ وَرَسُولُهُۥۖ وَمَا زَادَهُمْ إِلَّا إِيمَٰناٗ وَتَسْلِيماٗۖ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3550,21,'421',33,'Al-Aḥzāb','الأحزَاب',1,2,23,'مِّنَ اَ۬لْمُؤْمِنِينَ رِجَالٞ صَدَقُواْ مَا عَٰهَدُواْ اُ۬للَّهَ عَلَيْهِۖ فَمِنْهُم مَّن قَضَىٰ نَحْبَهُۥ وَمِنْهُم مَّنْ يَّنتَظِرُۖ وَمَا بَدَّلُواْ تَبْدِيلاٗ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3551,21,'421',33,'Al-Aḥzāb','الأحزَاب',2,4,24,'لِّيَجْزِيَ اَ۬للَّهُ اُ۬لصَّٰدِقِينَ بِصِدْقِهِمْ وَيُعَذِّبَ اَ۬لْمُنَٰفِقِينَ إِن شَا أَوْ يَتُوبَ عَلَيْهِمْۖ إِنَّ اَ۬للَّهَ كَانَ غَفُوراٗ رَّحِيماٗۖ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3552,21,'421',33,'Al-Aḥzāb','الأحزَاب',4,6,25,'۞ وَرَدَّ اَ۬للَّهُ اُ۬لذِينَ كَفَرُواْ بِغَيْظِهِمْ لَمْ يَنَالُواْ خَيْراٗۖ وَكَفَى اَ۬للَّهُ اُ۬لْمُؤْمِنِينَ اَ۬لْقِتَالَۖ وَكَانَ اَ۬للَّهُ قَوِيّاً عَزِيزاٗۖ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3553,21,'421',33,'Al-Aḥzāb','الأحزَاب',6,8,26,'وَأَنزَلَ اَ۬لذِينَ ظَٰهَرُوهُم مِّنْ أَهْلِ اِ۬لْكِتَٰبِ مِن صَيَاصِيهِمْ وَقَذَفَ فِے قُلُوبِهِمُ اُ۬لرُّعْبَۖ فَرِيقاٗ تَقْتُلُونَ وَتَأْسِرُونَ فَرِيقاٗۖ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3554,21,'421',33,'Al-Aḥzāb','الأحزَاب',8,10,27,'وَأَوْرَثَكُمْ أَرْضَهُمْ وَدِيَٰرَهُمْ وَأَمْوَٰلَهُمْ وَأَرْضاٗ لَّمْ تَطَـُٔوهَاۖ وَكَانَ اَ۬للَّهُ عَلَىٰ كُلِّ شَےْءٖ قَدِيراٗۖ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3555,21,'421',33,'Al-Aḥzāb','الأحزَاب',10,12,28,'يَٰأَيُّهَا‏ اَ۬لنَّبِےٓءُ قُل لِّأَزْوَٰجِكَ إِن كُنتُنَّ تُرِدْنَ اَ۬لْحَيَوٰةَ اَ۬لدُّنْيَا وَزِينَتَهَا فَتَعَالَيْنَ أُمَتِّعْكُنَّ وَأُسَرِّحْكُنَّ سَرَاحاٗ جَمِيلاٗۖ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3556,21,'421',33,'Al-Aḥzāb','الأحزَاب',12,13,29,'وَإِن كُنتُنَّ تُرِدْنَ اَ۬للَّهَ وَرَسُولَهُۥ وَالدَّارَ اَ۬لْأٓخِرَةَ فَإِنَّ اَ۬للَّهَ أَعَدَّ لِلْمُحْسِنَٰتِ مِنكُنَّ أَجْراً عَظِيماٗۖ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3557,21,'421',33,'Al-Aḥzāb','الأحزَاب',14,15,30,'يَٰنِسَآءَ اَ۬لنَّبِےٓءِ مَنْ يَّأْتِ مِنكُنَّ بِفَٰحِشَةٖ مُّبَيِّنَةٖ يُضَٰعَفْ لَهَا اَ۬لْعَذَابُ ضِعْفَيْنِۖ وَكَانَ ذَٰلِكَ عَلَى اَ۬للَّهِ يَسِيراٗۖ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3558,22,'422',33,'Al-Aḥzāb','الأحزَاب',1,2,31,'۞ وَمَنْ يَّقْنُتْ مِنكُنَّ لِلهِ وَرَسُولِهِۦ وَتَعْمَلْ صَٰلِحاٗ نُّؤْتِهَا أَجْرَهَا مَرَّتَيْنِ وَأَعْتَدْنَا لَهَا رِزْقاٗ كَرِيماٗۖ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3559,22,'422',33,'Al-Aḥzāb','الأحزَاب',2,4,32,'يَٰنِسَآءَ اَ۬لنَّبِےٓءِ لَسْتُنَّ كَأَحَدٖ مِّنَ اَ۬لنِّسَآ۟ إِنِ اِ۪تَّقَيْتُنَّۖ فَلَا تَخْضَعْنَ بِالْقَوْلِ فَيَطْمَعَ اَ۬لذِے فِے قَلْبِهِۦ مَرَضٞ وَقُلْنَ قَوْلاٗ مَّعْرُوفاٗ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3560,22,'422',33,'Al-Aḥzāb','الأحزَاب',4,8,33,'وَقَرْنَ فِے بِيُوتِكُنَّۖ وَلَا تَبَرَّجْنَ تَبَرُّجَ اَ۬لْجَٰهِلِيَّةِ اِ۬لْأُولَىٰۖ وَأَقِمْنَ اَ۬لصَّلَوٰةَ وَءَاتِينَ اَ۬لزَّكَوٰةَۖ وَأَطِعْنَ اَ۬للَّهَ وَرَسُولَهُۥۖ إِنَّمَا يُرِيدُ اُ۬للَّهُ لِيُذْهِبَ عَنكُمُ اُ۬لرِّجْسَ أَهْلَ اَ۬لْبَيْتِ وَيُطَهِّرَكُمْ تَطْهِيراٗۖ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3561,22,'422',33,'Al-Aḥzāb','الأحزَاب',8,9,34,'وَاذْكُرْنَ مَا يُتْلَىٰ فِے بِيُوتِكُنَّ مِنْ ءَايَٰتِ اِ۬للَّهِ وَالْحِكْمَةِۖ إِنَّ اَ۬للَّهَ كَانَ لَطِيفاً خَبِيراًۖ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3562,22,'422',33,'Al-Aḥzāb','الأحزَاب',10,15,35,'إِنَّ اَ۬لْمُسْلِمِينَ وَالْمُسْلِمَٰتِ وَالْمُؤْمِنِينَ وَالْمُؤْمِنَٰتِ وَالْقَٰنِتِينَ وَالْقَٰنِتَٰتِ وَالصَّٰدِقِينَ وَالصَّٰدِقَٰتِ وَالصَّٰبِرِينَ وَالصَّٰبِرَٰتِ وَالْخَٰشِعِينَ وَالْخَٰشِعَٰتِ وَالْمُتَصَدِّقِينَ وَالْمُتَصَدِّقَٰتِ وَالصَّٰٓئِمِينَ وَالصَّٰٓئِمَٰتِ وَالْحَٰفِظِينَ فُرُوجَهُمْ وَالْحَٰفِظَٰتِ وَالذَّٰكِرِينَ اَ۬للَّهَ كَثِيراٗ وَالذَّٰكِرَٰتِ أَعَدَّ اَ۬للَّهُ لَهُم مَّغْفِرَةٗ وَأَجْراً عَظِيماٗۖ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3563,22,'423',33,'Al-Aḥzāb','الأحزَاب',1,3,36,'وَمَا كَانَ لِمُؤْمِنٖ وَلَا مُؤْمِنَةٍ إِذَا قَضَى اَ۬للَّهُ وَرَسُولُهُۥ أَمْراً أَن تَكُونَ لَهُمُ اُ۬لْخِيَرَةُ مِنْ أَمْرِهِمْۖ وَمَنْ يَّعْصِ اِ۬للَّهَ وَرَسُولَهُۥ فَقَدْ ضَلَّ ضَلَٰلاٗ مُّبِيناٗۖ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3564,22,'423',33,'Al-Aḥzāb','الأحزَاب',3,8,37,'وَإِذْ تَقُولُ لِلذِے أَنْعَمَ اَ۬للَّهُ عَلَيْهِ وَأَنْعَمْتَ عَلَيْهِ أَمْسِكْ عَلَيْكَ زَوْجَكَ وَاتَّقِ اِ۬للَّهَ وَتُخْفِے فِے نَفْسِكَ مَا اَ۬للَّهُ مُبْدِيهِ وَتَخْشَى اَ۬لنَّاسَ وَاَ۬للَّهُ أَحَقُّ أَن تَخْشَيٰهُۖ ۞ فَلَمَّا قَضَىٰ زَيْدٞ مِّنْهَا وَطَراٗ زَوَّجْنَٰكَهَا لِكَےْ لَا يَكُونَ عَلَى اَ۬لْمُؤْمِنِينَ حَرَجٞ فِے أَزْوَٰجِ أَدْعِيَآئِهِمْ إِذَا قَضَوْاْ مِنْهُنَّ وَطَراٗۖ وَكَانَ أَمْرُ اُ۬للَّهِ مَفْعُولاٗۖ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3565,22,'423',33,'Al-Aḥzāb','الأحزَاب',8,9,38,'مَّا كَانَ عَلَى اَ۬لنَّبِےٓءِ مِنْ حَرَجٖ فِيمَا فَرَضَ اَ۬للَّهُ لَهُۥۖ سُنَّةَ اَ۬للَّهِ فِے اِ۬لذِينَ خَلَوْاْ مِن قَبْلُۖ وَكَانَ أَمْرُ اُ۬للَّهِ قَدَراٗ مَّقْدُوراًۖ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3566,22,'423',33,'Al-Aḥzāb','الأحزَاب',9,11,39,'اِ۬لذِينَ يُبَلِّغُونَ رِسَٰلَٰتِ اِ۬للَّهِ وَيَخْشَوْنَهُۥ وَلَا يَخْشَوْنَ أَحَداً إِلَّا اَ۬للَّهَۖ وَكَفَىٰ بِاللَّهِ حَسِيباٗۖ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3567,22,'423',33,'Al-Aḥzāb','الأحزَاب',11,12,40,'مَّا كَانَ مُحَمَّدٌ أَبَا أَحَدٖ مِّن رِّجَالِكُمْۖ وَلَٰكِن رَّسُولَ اَ۬للَّهِ وَخَاتِمَ اَ۬لنَّبِيٓـِٕۧنَۖ وَكَانَ اَ۬للَّهُ بِكُلِّ شَےْءٍ عَلِيماٗۖ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3568,22,'423',33,'Al-Aḥzāb','الأحزَاب',13,13,41,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ اُ۟ذْكُرُواْ اُ۬للَّهَ ذِكْراٗ كَثِيراٗ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3569,22,'423',33,'Al-Aḥzāb','الأحزَاب',13,14,42,'وَسَبِّحُوهُ بُكْرَةٗ وَأَصِيلاًۖ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3570,22,'423',33,'Al-Aḥzāb','الأحزَاب',14,15,43,'هُوَ اَ۬لذِے يُصَلِّے عَلَيْكُمْ وَمَلَٰٓئِكَتُهُۥ لِيُخْرِجَكُم مِّنَ اَ۬لظُّلُمَٰتِ إِلَى اَ۬لنُّورِۖ وَكَانَ بِالْمُؤْمِنِينَ رَحِيماٗۖ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3571,22,'424',33,'Al-Aḥzāb','الأحزَاب',1,1,44,'تَحِيَّتُهُمْ يَوْمَ يَلْقَوْنَهُۥ سَلَٰمٞۖ وَأَعَدَّ لَهُمْ أَجْراٗ كَرِيماٗۖ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3572,22,'424',33,'Al-Aḥzāb','الأحزَاب',1,2,45,'يَٰأَيُّهَا اَ۬لنَّبِےٓءُ اِ۪نَّا أَرْسَلْنَٰكَ شَٰهِداٗ وَمُبَشِّراٗ وَنَذِيراٗ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3573,22,'424',33,'Al-Aḥzāb','الأحزَاب',2,3,46,'وَدَاعِياً إِلَى اَ۬للَّهِ بِإِذْنِهِۦ وَسِرَاجاٗ مُّنِيراٗۖ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3574,22,'424',33,'Al-Aḥzāb','الأحزَاب',3,4,47,'وَبَشِّرِ اِ۬لْمُؤْمِنِينَ بِأَنَّ لَهُم مِّنَ اَ۬للَّهِ فَضْلاٗ كَبِيراٗۖ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3575,22,'424',33,'Al-Aḥzāb','الأحزَاب',4,5,48,'وَلَا تُطِعِ اِ۬لْكَٰفِرِينَ وَالْمُنَٰفِقِينَۖ وَدَعْ أَذَيٰهُمْۖ وَتَوَكَّلْ عَلَى اَ۬للَّهِۖ وَكَفَىٰ بِاللَّهِ وَكِيلاٗۖ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3576,22,'424',33,'Al-Aḥzāb','الأحزَاب',6,8,49,'۞ يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ إِذَا نَكَحْتُمُ اُ۬لْمُؤْمِنَٰتِ ثُمَّ طَلَّقْتُمُوهُنَّ مِن قَبْلِ أَن تَمَسُّوهُنَّ فَمَا لَكُمْ عَلَيْهِنَّ مِنْ عِدَّةٖ تَعْتَدُّونَهَاۖ فَمَتِّعُوهُنَّ وَسَرِّحُوهُنَّ سَرَاحاٗ جَمِيلاٗۖ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3577,22,'424',33,'Al-Aḥzāb','الأحزَاب',8,15,50,'يَٰأَيُّهَا‏ اَ۬لنَّبِےٓءُ اِ۪نَّا أَحْلَلْنَا لَكَ أَزْوَٰجَكَ اَ۬لَّٰتِے ءَاتَيْتَ أُجُورَهُنَّ وَمَا مَلَكَتْ يَمِينُكَ مِمَّا أَفَآءَ اَ۬للَّهُ عَلَيْكَ وَبَنَاتِ عَمِّكَ وَبَنَاتِ عَمَّٰتِكَ وَبَنَاتِ خَالِكَ وَبَنَاتِ خَٰلَٰتِكَ اَ۬لَّٰتِے هَاجَرْنَ مَعَكَ وَامْرَأَةٗ مُّؤْمِنَةً إِنْ وَّهَبَتْ نَفْسَهَا لِلنَّبِيِّ إِنْ أَرَادَ‏ اَ۬لنَّبِےٓءُ اَ۬نْ يَّسْتَنكِحَهَاۖ خَالِصَةٗ لَّكَ مِن دُونِ اِ۬لْمُؤْمِنِينَۖ قَدْ عَلِمْنَا مَا فَرَضْنَا عَلَيْهِمْ فِے أَزْوَٰجِهِمْ وَمَا مَلَكَتْ أَيْمَٰنُهُمْ لِكَيْلَا يَكُونَ عَلَيْكَ حَرَجٞۖ وَكَانَ اَ۬للَّهُ غَفُوراٗ رَّحِيماٗۖ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3578,22,'425',33,'Al-Aḥzāb','الأحزَاب',1,4,51,'تُرْجِے مَن تَشَآءُ مِنْهُنَّ وَتُـْٔوِے إِلَيْكَ مَن تَشَآءُۖ وَمَنِ اِ۪بْتَغَيْتَ مِمَّنْ عَزَلْتَ فَلَا جُنَاحَ عَلَيْكَۖ ذَٰلِكَ أَدْنَىٰ أَن تَقَرَّ أَعْيُنُهُنَّ وَلَا يَحْزَنَّ وَيَرْضَيْنَ بِمَا ءَاتَيْتَهُنَّ كُلُّهُنَّۖ وَاللَّهُ يَعْلَمُ مَا فِے قُلُوبِكُمْۖ وَكَانَ اَ۬للَّهُ عَلِيماً حَلِيماٗۖ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3579,22,'425',33,'Al-Aḥzāb','الأحزَاب',4,7,52,'لَّا يَحِلُّ لَكَ اَ۬لنِّسَآءُ مِنۢ بَعْدُ وَلَا أَن تَبَدَّلَ بِهِنَّ مِنْ أَزْوَٰجٖ وَلَوْ أَعْجَبَكَ حُسْنُهُنَّ إِلَّا مَا مَلَكَتْ يَمِينُكَۖ وَكَانَ اَ۬للَّهُ عَلَىٰ كُلِّ شَےْءٖ رَّقِيباٗۖ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3580,22,'425',33,'Al-Aḥzāb','الأحزَاب',7,14,53,'۞ يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ لَا تَدْخُلُواْ بِيُوتَ اَ۬لنَّبِيِّ إِلَّا أَنْ يُّؤْذَنَ لَكُمْ إِلَىٰ طَعَامٍ غَيْرَ نَٰظِرِينَ إِنَيٰهُۖ وَلَٰكِنْ إِذَا دُعِيتُمْ فَادْخُلُواْ فَإِذَا طَعِمْتُمْ فَانتَشِرُواْ وَلَا مُسْتَٰٔنِسِينَ لِحَدِيثٍۖ إِنَّ ذَٰلِكُمْ كَانَ يُؤْذِے اِ۬لنَّبِےٓءَ فَيَسْتَحْيِۦ مِنكُمْۖ وَاللَّهُ لَا يَسْتَحْيِۦ مِنَ اَ۬لْحَقِّۖ وَإِذَا سَأَلْتُمُوهُنَّ مَتَٰعاٗ فَسْـَٔلُوهُنَّ مِنْ وَّرَآءِ حِجَابٖۖ ذَٰلِكُمْ أَطْهَرُ لِقُلُوبِكُمْ وَقُلُوبِهِنَّۖ وَمَا كَانَ لَكُمْ أَن تُؤْذُواْ رَسُولَ اَ۬للَّهِ وَلَا أَن تَنكِحُواْ أَزْوَٰجَهُۥ مِنۢ بَعْدِهِۦ أَبَداًۖ إِنَّ ذَٰلِكُمْ كَانَ عِندَ اَ۬للَّهِ عَظِيماًۖ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3581,22,'425',33,'Al-Aḥzāb','الأحزَاب',15,15,54,'إِن تُبْدُواْ شَئْاً أَوْ تُخْفُوهُ فَإِنَّ اَ۬للَّهَ كَانَ بِكُلِّ شَےْءٍ عَلِيماٗۖ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3582,22,'426',33,'Al-Aḥzāb','الأحزَاب',1,3,55,'لَّا جُنَاحَ عَلَيْهِنَّ فِے ءَابَآئِهِنَّ وَلَا أَبْنَآئِهِنَّ وَلَا إِخْوَٰنِهِنَّ وَلَا أَبْنَآ۟ إِخْوَٰنِهِنَّ وَلَا أَبْنَآءِ اَ۬خَوَٰتِهِنَّ وَلَا نِسَآئِهِنَّ وَلَا مَا مَلَكَتْ أَيْمَٰنُهُنَّۖ وَاتَّقِينَ اَ۬للَّهَۖ إِنَّ اَ۬للَّهَ كَانَ عَلَىٰ كُلِّ شَےْءٖ شَهِيداًۖ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3583,22,'426',33,'Al-Aḥzāb','الأحزَاب',4,5,56,'إِنَّ اَ۬للَّهَ وَمَلَٰٓئِكَتَهُۥ يُصَلُّونَ عَلَى اَ۬لنَّبِےٓءِۖ يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ صَلُّواْ عَلَيْهِ وَسَلِّمُواْ تَسْلِيماًۖ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3584,22,'426',33,'Al-Aḥzāb','الأحزَاب',5,7,57,'إِنَّ اَ۬لذِينَ يُؤْذُونَ اَ۬للَّهَ وَرَسُولَهُۥ لَعَنَهُمُ اُ۬للَّهُ فِے اِ۬لدُّنْيَا وَالْأٓخِرَةِ وَأَعَدَّ لَهُمْ عَذَاباٗ مُّهِيناٗۖ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3585,22,'426',33,'Al-Aḥzāb','الأحزَاب',7,8,58,'وَالذِينَ يُؤْذُونَ اَ۬لْمُؤْمِنِينَ وَالْمُؤْمِنَٰتِ بِغَيْرِ مَا اَ۪كْتَسَبُواْ فَقَدِ اِ۪حْتَمَلُواْ بُهْتَٰناٗ وَإِثْماٗ مُّبِيناٗۖ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3586,22,'426',33,'Al-Aḥzāb','الأحزَاب',9,11,59,'يَٰأَيُّهَا‏ اَ۬لنَّبِےٓءُ قُل لِّأَزْوَٰجِكَ وَبَنَاتِكَ وَنِسَآءِ اِ۬لْمُؤْمِنِينَ يُدْنِينَ عَلَيْهِنَّ مِن جَلَٰبِيبِهِنَّۖ ذَٰلِكَ أَدْنَىٰ أَنْ يُّعْرَفْنَ فَلَا يُؤْذَيْنَۖ وَكَانَ اَ۬للَّهُ غَفُوراٗ رَّحِيماٗۖ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3587,22,'426',33,'Al-Aḥzāb','الأحزَاب',11,13,60,'۞ لَّئِن لَّمْ يَنتَهِ اِ۬لْمُنَٰفِقُونَ وَالذِينَ فِے قُلُوبِهِم مَّرَضٞ وَالْمُرْجِفُونَ فِے اِ۬لْمَدِينَةِ لَنُغْرِيَنَّكَ بِهِمْ ثُمَّ لَا يُجَاوِرُونَكَ فِيهَا إِلَّا قَلِيلاٗ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3588,22,'426',33,'Al-Aḥzāb','الأحزَاب',14,14,61,'مَّلْعُونِينَۖ أَيْنَمَا ثُقِفُواْ أُخِذُواْ وَقُتِّلُواْ تَقْتِيلاٗۖ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3589,22,'426',33,'Al-Aḥzāb','الأحزَاب',14,15,62,'سُنَّةَ اَ۬للَّهِ فِے اِ۬لذِينَ خَلَوْاْ مِن قَبْلُۖ وَلَن تَجِدَ لِسُنَّةِ اِ۬للَّهِ تَبْدِيلاٗۖ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3590,22,'427',33,'Al-Aḥzāb','الأحزَاب',1,2,63,'يَسْـَٔلُكَ اَ۬لنَّاسُ عَنِ اِ۬لسَّاعَةِۖ قُلْ إِنَّمَا عِلْمُهَا عِندَ اَ۬للَّهِۖ وَمَا يُدْرِيكَۖ لَعَلَّ اَ۬لسَّاعَةَ تَكُونُ قَرِيباًۖ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3591,22,'427',33,'Al-Aḥzāb','الأحزَاب',2,3,64,'إِنَّ اَ۬للَّهَ لَعَنَ اَ۬لْكَٰفِرِينَ وَأَعَدَّ لَهُمْ سَعِيراً ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3592,22,'427',33,'Al-Aḥzāb','الأحزَاب',3,3,65,'خَٰلِدِينَ فِيهَا أَبَداٗ لَّا يَجِدُونَ وَلِيّاٗ وَلَا نَصِيراٗۖ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3593,22,'427',33,'Al-Aḥzāb','الأحزَاب',4,5,66,'يَوْمَ تُقَلَّبُ وُجُوهُهُمْ فِے اِ۬لنَّارِ يَقُولُونَ يَٰلَيْتَنَا أَطَعْنَا اَ۬للَّهَ وَأَطَعْنَا اَ۬لرَّسُولَاۖ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3594,22,'427',33,'Al-Aḥzāb','الأحزَاب',5,6,67,'وَقَالُواْ رَبَّنَا إِنَّا أَطَعْنَا سَادَتَنَا وَكُبَرَآءَنَا فَأَضَلُّونَا اَ۬لسَّبِيلَاۖ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3595,22,'427',33,'Al-Aḥzāb','الأحزَاب',6,7,68,'رَبَّنَا ءَاتِهِمْ ضِعْفَيْنِ مِنَ اَ۬لْعَذَابِ وَالْعَنْهُمْ لَعْناٗ كَثِيراٗۖ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3596,22,'427',33,'Al-Aḥzāb','الأحزَاب',7,8,69,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ لَا تَكُونُواْ كَالذِينَ ءَاذَوْاْ مُوسَىٰ فَبَرَّأَهُ اُ۬للَّهُ مِمَّا قَالُواْۖ وَكَانَ عِندَ اَ۬للَّهِ وَجِيهاٗۖ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3597,22,'427',33,'Al-Aḥzāb','الأحزَاب',9,9,70,'يَٰأَيُّهَا اَ۬لذِينَ ءَامَنُواْ اُ۪تَّقُواْ اُ۬للَّهَ وَقُولُواْ قَوْلاٗ سَدِيداٗ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3598,22,'427',33,'Al-Aḥzāb','الأحزَاب',9,11,71,'يُصْلِحْ لَكُمْ أَعْمَٰلَكُمْ وَيَغْفِرْ لَكُمْ ذُنُوبَكُمْۖ وَمَنْ يُّطِعِ اِ۬للَّهَ وَرَسُولَهُۥ فَقَدْ فَازَ فَوْزاً عَظِيماًۖ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3599,22,'427',33,'Al-Aḥzāb','الأحزَاب',11,13,72,'إِنَّا عَرَضْنَا اَ۬لْأَمَانَةَ عَلَى اَ۬لسَّمَٰوَٰتِ وَالْأَرْضِ وَالْجِبَالِ فَأَبَيْنَ أَنْ يَّحْمِلْنَهَا وَأَشْفَقْنَ مِنْهَاۖ وَحَمَلَهَا اَ۬لْإِنسَٰنُ إِنَّهُۥ كَانَ ظَلُوماٗ جَهُولاٗ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3600,22,'427',33,'Al-Aḥzāb','الأحزَاب',13,15,73,'لِّيُعَذِّبَ اَ۬للَّهُ اُ۬لْمُنَٰفِقِينَ وَالْمُنَٰفِقَٰتِ وَالْمُشْرِكِينَ وَالْمُشْرِكَٰتِ وَيَتُوبَ اَ۬للَّهُ عَلَى اَ۬لْمُؤْمِنِينَ وَالْمُؤْمِنَٰتِۖ وَكَانَ اَ۬للَّهُ غَفُوراٗ رَّحِيماَۢۖ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3601,22,'428',34,'Saba’','سَبإ',3,4,1,'اِ۬لْحَمْدُ لِلهِ اِ۬لذِے لَهُۥ مَا فِے اِ۬لسَّمَٰوَٰتِ وَمَا فِے اِ۬لْأَرْضِۖ وَلَهُ اُ۬لْحَمْدُ فِے اِ۬لْأٓخِرَةِۖ وَهْوَ اَ۬لْحَكِيمُ اُ۬لْخَبِيرُۖ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3602,22,'428',34,'Saba’','سَبإ',4,6,2,'يَعْلَمُ مَا يَلِجُ فِے اِ۬لْأَرْضِ وَمَا يَخْرُجُ مِنْهَا وَمَا يَنزِلُ مِنَ اَ۬لسَّمَآءِ وَمَا يَعْرُجُ فِيهَاۖ وَهْوَ اَ۬لرَّحِيمُ اُ۬لْغَفُورُۖ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3603,22,'428',34,'Saba’','سَبإ',6,9,3,'وَقَالَ اَ۬لذِينَ كَفَرُواْ لَا تَأْتِينَا اَ۬لسَّاعَةُۖ قُلْ بَلَىٰ وَرَبِّے لَتَأْتِيَنَّكُمْۖ عَٰلِمُ اُ۬لْغَيْبِۖ لَا يَعْزُبُ عَنْهُ مِثْقَالُ ذَرَّةٖ فِے اِ۬لسَّمَٰوَٰتِ وَلَا فِے اِ۬لْأَرْضِ وَلَا أَصْغَرُ مِن ذَٰلِكَ وَلَا أَكْبَرُ إِلَّا فِے كِتَٰبٖ مُّبِينٖ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3604,22,'428',34,'Saba’','سَبإ',9,11,4,'لِّيَجْزِيَ اَ۬لذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِۖ أُوْلَٰٓئِكَ لَهُم مَّغْفِرَةٞ وَرِزْقٞ كَرِيمٞۖ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3605,22,'428',34,'Saba’','سَبإ',11,12,5,'وَالذِينَ سَعَوْ فِے ءَايَٰتِنَا مُعَٰجِزِينَ أُوْلَٰٓئِكَ لَهُمْ عَذَابٞ مِّن رِّجْزٍ أَلِيمٖۖ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3606,22,'428',34,'Saba’','سَبإ',12,14,6,'وَيَرَى اَ۬لذِينَ أُوتُواْ اُ۬لْعِلْمَ اَ۬لذِے أُنزِلَ إِلَيْكَ مِن رَّبِّكَ هُوَ اَ۬لْحَقَّ وَيَهْدِے إِلَىٰ صِرَٰطِ اِ۬لْعَزِيزِ اِ۬لْحَمِيدِۖ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3607,22,'428',34,'Saba’','سَبإ',14,15,7,'وَقَالَ اَ۬لذِينَ كَفَرُواْ هَلْ نَدُلُّكُمْ عَلَىٰ رَجُلٖ يُنَبِّئُكُمْ إِذَا مُزِّقْتُمْ كُلَّ مُمَزَّقٍ إِنَّكُمْ لَفِے خَلْقٖ جَدِيدٍۖ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3608,22,'429',34,'Saba’','سَبإ',1,2,8,'أَفْتَرَىٰ عَلَى اَ۬للَّهِ كَذِباً أَم بِهِۦ جِنَّةُۢۖ بَلِ اِ۬لذِينَ لَا يُؤْمِنُونَ بِالْأٓخِرَةِ فِے اِ۬لْعَذَابِ وَالضَّلَٰلِ اِ۬لْبَعِيدِۖ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3609,22,'429',34,'Saba’','سَبإ',2,5,9,'أَفَلَمْ يَرَوْاْ إِلَىٰ مَا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُم مِّنَ اَ۬لسَّمَآءِ وَالْأَرْضِۖ إِن نَّشَأْ نَخْسِفْ بِهِمُ اُ۬لْأَرْضَ أَوْ نُسْقِطْ عَلَيْهِمْ كِسْفاٗ مِّنَ اَ۬لسَّمَآ۟ۖ إِنَّ فِے ذَٰلِكَ لَأٓيَةٗ لِّكُلِّ عَبْدٖ مُّنِيبٖۖ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3610,22,'429',34,'Saba’','سَبإ',5,6,10,'۞ وَلَقَدْ ءَاتَيْنَا دَاوُۥدَ مِنَّا فَضْلاٗ يَٰجِبَالُ أَوِّبِے مَعَهُۥ وَالطَّيْرَۖ وَأَلَنَّا لَهُ اُ۬لْحَدِيدَ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3611,22,'429',34,'Saba’','سَبإ',6,8,11,'أَنِ اِ۪عْمَلْ سَٰبِغَٰتٖ وَقَدِّرْ فِے اِ۬لسَّرْدِۖ وَاعْمَلُواْ صَٰلِحاًۖ إِنِّے بِمَا تَعْمَلُونَ بَصِيرٞۖ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3612,22,'429',34,'Saba’','سَبإ',8,10,12,'وَلِسُلَيْمَٰنَ اَ۬لرِّيحَ غُدُوُّهَا شَهْرٞ وَرَوَاحُهَا شَهْرٞۖ وَأَسَلْنَا لَهُۥ عَيْنَ اَ۬لْقِطْرِۖ وَمِنَ اَ۬لْجِنِّ مَنْ يَّعْمَلُ بَيْنَ يَدَيْهِ بِإِذْنِ رَبِّهِۦۖ وَمَنْ يَّزِغْ مِنْهُمْ عَنْ أَمْرِنَا نُذِقْهُ مِنْ عَذَابِ اِ۬لسَّعِيرِۖ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3613,22,'429',34,'Saba’','سَبإ',11,13,13,'يَعْمَلُونَ لَهُۥ مَا يَشَآءُ مِن مَّحَٰرِيبَ وَتَمَٰثِيلَ وَجِفَانٖ كَالْجَوَابِ وَقُدُورٖ رَّاسِيَٰتٍۖ اِ۪عْمَلُواْ ءَالَ دَاوُۥدَۖ شُكْراٗۖ وَقَلِيلٞ مِّنْ عِبَادِيَ اَ۬لشَّكُورُۖ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3614,22,'429',34,'Saba’','سَبإ',13,15,14,'فَلَمَّا قَضَيْنَا عَلَيْهِ اِ۬لْمَوْتَ مَا دَلَّهُمْ عَلَىٰ مَوْتِهِۦ إِلَّا دَآبَّةُ اُ۬لْأَرْضِ تَأْكُلُ مِنسَاتَهُۥۖ فَلَمَّا خَرَّ تَبَيَّنَتِ اِ۬لْجِنُّ أَن لَّوْ كَانُواْ يَعْلَمُونَ اَ۬لْغَيْبَ مَا لَبِثُواْ فِے اِ۬لْعَذَابِ اِ۬لْمُهِينِۖ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3615,22,'430',34,'Saba’','سَبإ',1,2,15,'لَقَدْ كَانَ لِسَبَإٖ فِے مَسَٰكِنِهِمْ ءَايَةٞۖ جَنَّتَٰنِ عَنْ يَّمِينٖ وَشِمَالٖۖ كُلُواْ مِن رِّزْقِ رَبِّكُمْ وَاشْكُرُواْ لَهُۥۖ بَلْدَةٞ طَيِّبَةٞ وَرَبٌّ غَفُورٞۖ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3616,22,'430',34,'Saba’','سَبإ',3,4,16,'فَأَعْرَضُواْ فَأَرْسَلْنَا عَلَيْهِمْ سَيْلَ اَ۬لْعَرِمِۖ وَبَدَّلْنَٰهُم بِجَنَّتَيْهِمْ جَنَّتَيْنِ ذَوَاتَےْ أُكْلٍ خَمْطٖ وَأَثْلٖ وَشَےْءٖ مِّن سِدْرٖ قَلِيلٖۖ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3617,22,'430',34,'Saba’','سَبإ',5,5,17,'ذَٰلِكَ جَزَيْنَٰهُم بِمَا كَفَرُواْۖ وَهَلْ يُجَٰزَىٰ إِلَّا اَ۬لْكَفُورُۖ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3618,22,'430',34,'Saba’','سَبإ',6,7,18,'۞ وَجَعَلْنَا بَيْنَهُمْ وَبَيْنَ اَ۬لْقُرَى اَ۬لتِے بَٰرَكْنَا فِيهَا قُرىٗ ظَٰهِرَةٗ وَقَدَّرْنَا فِيهَا اَ۬لسَّيْرَۖ سِيرُواْ فِيهَا لَيَالِيَ وَأَيَّاماً ءَامِنِينَۖ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3619,22,'430',34,'Saba’','سَبإ',8,10,19,'فَقَالُواْ رَبَّنَا بَٰعِدْ بَيْنَ أَسْفَارِنَا وَظَلَمُواْ أَنفُسَهُمْ فَجَعَلْنَٰهُمْ أَحَادِيثَ وَمَزَّقْنَٰهُمْ كُلَّ مُمَزَّقٍۖ إِنَّ فِے ذَٰلِكَ لَأٓيَٰتٖ لِّكُلِّ صَبَّارٖ شَكُورٖۖ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3620,22,'430',34,'Saba’','سَبإ',10,11,20,'وَلَقَدْ صَدَقَ عَلَيْهِمْ إِبْلِيسُ ظَنَّهُۥ فَاتَّبَعُوهُ إِلَّا فَرِيقاٗ مِّنَ اَ۬لْمُؤْمِنِينَۖ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3621,22,'430',34,'Saba’','سَبإ',11,13,21,'وَمَا كَانَ لَهُۥ عَلَيْهِم مِّن سُلْطَٰنٍ إِلَّا لِنَعْلَمَ مَنْ يُّؤْمِنُ بِالْأٓخِرَةِ مِمَّنْ هُوَ مِنْهَا فِے شَكّٖۖ وَرَبُّكَ عَلَىٰ كُلِّ شَےْءٍ حَفِيظٞۖ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3622,22,'430',34,'Saba’','سَبإ',13,15,22,'قُلُ اُ۟دْعُواْ اُ۬لذِينَ زَعَمْتُم مِّن دُونِ اِ۬للَّهِۖ لَا يَمْلِكُونَ مِثْقَالَ ذَرَّةٖ فِے اِ۬لسَّمَٰوَٰتِ وَلَا فِے اِ۬لْأَرْضِۖ وَمَا لَهُمْ فِيهِمَا مِن شِرْكٖۖ وَمَا لَهُۥ مِنْهُم مِّن ظَهِيرٖۖ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3623,22,'431',34,'Saba’','سَبإ',1,3,23,'وَلَا تَنفَعُ اُ۬لشَفَٰعَةُ عِندَهُۥ إِلَّا لِمَنْ أَذِنَ لَهُۥۖ حَتَّىٰ إِذَا فُزِّعَ عَن قُلُوبِهِمْ قَالُواْ مَاذَا قَالَ رَبُّكُمْ قَالُواْ اُ۬لْحَقَّۖ وَهْوَ اَ۬لْعَلِيُّ اُ۬لْكَبِيرُۖ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3624,22,'431',34,'Saba’','سَبإ',3,4,24,'۞ قُلْ مَنْ يَّرْزُقُكُم مِّنَ اَ۬لسَّمَٰوَٰتِ وَالْأَرْضِ قُلِ اِ۬للَّهُۖ وَإِنَّا أَوْ إِيَّاكُمْ لَعَلَىٰ هُدىً أَوْ فِے ضَلَٰلٖ مُّبِينٖۖ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3625,22,'431',34,'Saba’','سَبإ',4,5,25,'قُل لَّا تُسْـَٔلُونَ عَمَّا أَجْرَمْنَا وَلَا نُسْـَٔلُ عَمَّا تَعْمَلُونَۖ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3626,22,'431',34,'Saba’','سَبإ',5,6,26,'قُلْ يَجْمَعُ بَيْنَنَا رَبُّنَا ثُمَّ يَفْتَحُ بَيْنَنَا بِالْحَقِّۖ وَهْوَ اَ۬لْفَتَّاحُ اُ۬لْعَلِيمُۖ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3627,22,'431',34,'Saba’','سَبإ',7,8,27,'قُلْ أَرُونِيَ اَ۬لذِينَ أَلْحَقْتُم بِهِۦ شُرَكَآءَ كَلَّاۖ بَلْ هُوَ اَ۬للَّهُ اُ۬لْعَزِيزُ اُ۬لْحَكِيمُۖ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3628,22,'431',34,'Saba’','سَبإ',8,9,28,'وَمَا أَرْسَلْنَٰكَ إِلَّا كَآفَّةٗ لِّلنَّاسِ بَشِيراٗ وَنَذِيراٗۖ وَلَٰكِنَّ أَكْثَرَ اَ۬لنَّاسِ لَا يَعْلَمُونَۖ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3629,22,'431',34,'Saba’','سَبإ',10,10,29,'وَيَقُولُونَ مَتَىٰ هَٰذَا اَ۬لْوَعْدُ إِن كُنتُمْ صَٰدِقِينَۖ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3630,22,'431',34,'Saba’','سَبإ',10,11,30,'قُل لَّكُم مِّيعَادُ يَوْمٖ لَّا تَسْتَٰٔخِرُونَ عَنْهُ سَاعَةٗ وَلَا تَسْتَقْدِمُونَۖ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3631,22,'431',34,'Saba’','سَبإ',12,15,31,'وَقَالَ اَ۬لذِينَ كَفَرُواْ لَن نُّؤْمِنَ بِهَٰذَا اَ۬لْقُرْءَانِ وَلَا بِالذِے بَيْنَ يَدَيْهِۖ وَلَوْ تَرَىٰ إِذِ اِ۬لظَّٰلِمُونَ مَوْقُوفُونَ عِندَ رَبِّهِمْ يَرْجِعُ بَعْضُهُمْ إِلَىٰ بَعْضٍ اِ۬لْقَوْلَۖ يَقُولُ اُ۬لذِينَ اَ۟سْتُضْعِفُواْ لِلذِينَ اَ۪سْتَكْبَرُواْ لَوْلَا أَنتُمْ لَكُنَّا مُؤْمِنِينَۖ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3632,22,'432',34,'Saba’','سَبإ',1,2,32,'قَالَ اَ۬لذِينَ اَ۪سْتَكْبَرُواْ لِلذِينَ اَ۟سْتُضْعِفُواْ أَنَحْنُ صَدَدْنَٰكُمْ عَنِ اِ۬لْهُدَىٰ بَعْدَ إِذْ جَآءَكُم بَلْ كُنتُم مُّجْرِمِينَۖ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3633,22,'432',34,'Saba’','سَبإ',2,6,33,'وَقَالَ اَ۬لذِينَ اَ۟سْتُضْعِفُواْ لِلذِينَ اَ۪سْتَكْبَرُواْ بَلْ مَكْرُ اُ۬ليْلِ وَالنَّهَارِ إِذْ تَأْمُرُونَنَا أَن نَّكْفُرَ بِاللَّهِ وَنَجْعَلَ لَهُۥ أَندَاداٗۖ وَأَسَرُّواْ اُ۬لنَّدَامَةَ لَمَّا رَأَوُاْ اُ۬لْعَذَابَۖ وَجَعَلْنَا اَ۬لْأَغْلَٰلَ فِے أَعْنَاقِ اِ۬لذِينَ كَفَرُواْۖ هَلْ يُجْزَوْنَ إِلَّا مَا كَانُواْ يَعْمَلُونَۖ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3634,22,'432',34,'Saba’','سَبإ',6,7,34,'وَمَا أَرْسَلْنَا فِے قَرْيَةٖ مِّن نَّذِيرٍ إِلَّا قَالَ مُتْرَفُوهَا إِنَّا بِمَا أُرْسِلْتُم بِهِۦ كَٰفِرُونَۖ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3635,22,'432',34,'Saba’','سَبإ',8,8,35,'وَقَالُواْ نَحْنُ أَكْثَرُ أَمْوَٰلاٗ وَأَوْلَٰداٗ وَمَا نَحْنُ بِمُعَذَّبِينَۖ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3636,22,'432',34,'Saba’','سَبإ',8,10,36,'قُلْ إِنَّ رَبِّے يَبْسُطُ اُ۬لرِّزْقَ لِمَنْ يَّشَآءُ وَيَقْدِرُۖ وَلَٰكِنَّ أَكْثَرَ اَ۬لنَّاسِ لَا يَعْلَمُونَۖ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3637,22,'432',34,'Saba’','سَبإ',10,12,37,'۞ وَمَا أَمْوَٰلُكُمْ وَلَا أَوْلَٰدُكُم بِالتِے تُقَرِّبُكُمْ عِندَنَا زُلْفَىٰ إِلَّا مَنْ ءَامَنَ وَعَمِلَ صَٰلِحاٗ فَأُوْلَٰٓئِكَ لَهُمْ جَزَآءُ اُ۬لضِّعْفِ بِمَا عَمِلُواْ وَهُمْ فِے اِ۬لْغُرُفَٰتِ ءَامِنُونَۖ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3638,22,'432',34,'Saba’','سَبإ',12,13,38,'وَالذِينَ يَسْعَوْنَ فِے ءَايَٰتِنَا مُعَٰجِزِينَ أُوْلَٰٓئِكَ فِے اِ۬لْعَذَابِ مُحْضَرُونَۖ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3639,22,'432',34,'Saba’','سَبإ',14,15,39,'قُلْ إِنَّ رَبِّے يَبْسُطُ اُ۬لرِّزْقَ لِمَنْ يَّشَآءُ مِنْ عِبَادِهِۦ وَيَقْدِرُ لَهُۥۖ وَمَا أَنفَقْتُم مِّن شَےْءٖ فَهْوَ يُخْلِفُهُۥۖ وَهْوَ خَيْرُ اُ۬لرَّٰزِقِينَۖ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3640,22,'433',34,'Saba’','سَبإ',1,2,40,'وَيَوْمَ نَحْشُرُهُمْ جَمِيعاٗ ثُمَّ نَقُولُ لِلْمَلَٰٓئِكَةِ أَهَٰؤُلَآ۟ إِيَّاكُمْ كَانُواْ يَعْبُدُونَۖ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3641,22,'433',34,'Saba’','سَبإ',2,3,41,'قَالُواْ سُبْحَٰنَكَ أَنتَ وَلِيُّنَا مِن دُونِهِمۖ بَلْ كَانُواْ يَعْبُدُونَ اَ۬لْجِنَّۖ أَكْثَرُهُم بِهِم مُّؤْمِنُونَۖ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3642,22,'433',34,'Saba’','سَبإ',3,5,42,'فَالْيَوْمَ لَا يَمْلِكُ بَعْضُكُمْ لِبَعْضٖ نَّفْعاٗ وَلَا ضَرّاٗ وَنَقُولُ لِلذِينَ ظَلَمُواْ ذُوقُواْ عَذَابَ اَ۬لنَّارِ اِ۬لتِے كُنتُم بِهَا تُكَذِّبُونَۖ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3643,22,'433',34,'Saba’','سَبإ',5,8,43,'وَإِذَا تُتْلَىٰ عَلَيْهِمْ ءَايَٰتُنَا بَيِّنَٰتٖ قَالُواْ مَا هَٰذَا إِلَّا رَجُلٞ يُرِيدُ أَنْ يَّصُدَّكُمْ عَمَّا كَانَ يَعْبُدُ ءَابَآؤُكُمْ وَقَالُواْ مَا هَٰذَا إِلَّا إِفْكٞ مُّفْتَرىٗۖ وَقَالَ اَ۬لذِينَ كَفَرُواْ لِلْحَقِّ لَمَّا جَآءَهُمْ إِنْ هَٰذَا إِلَّا سِحْرٞ مُّبِينٞۖ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3644,22,'433',34,'Saba’','سَبإ',8,9,44,'وَمَا ءَاتَيْنَٰهُم مِّن كُتُبٖ يَدْرُسُونَهَاۖ وَمَا أَرْسَلْنَا إِلَيْهِمْ قَبْلَكَ مِن نَّذِيرٖۖ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3645,22,'433',34,'Saba’','سَبإ',10,11,45,'وَكَذَّبَ اَ۬لذِينَ مِن قَبْلِهِمْ وَمَا بَلَغُواْ مِعْشَارَ مَا ءَاتَيْنَٰهُمْۖ فَكَذَّبُواْ رُسُلِےۖ فَكَيْفَ كَانَ نَكِيرِۖ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3646,22,'433',34,'Saba’','سَبإ',11,13,46,'۞ قُلْ إِنَّمَا أَعِظُكُم بِوَٰحِدَةٍۖ أَن تَقُومُواْ لِلهِ مَثْنَىٰ وَفُرَٰدَىٰ ثُمَّ تَتَفَكَّرُواْۖ مَا بِصَٰحِبِكُم مِّن جِنَّةٍۖ إِنْ هُوَ إِلَّا نَذِيرٞ لَّكُم بَيْنَ يَدَےْ عَذَابٖ شَدِيدٖۖ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3647,22,'433',34,'Saba’','سَبإ',13,15,47,'قُلْ مَا سَأَلْتُكُم مِّنْ أَجْرٖ فَهْوَ لَكُمْۖ إِنْ أَجْرِيَ إِلَّا عَلَى اَ۬للَّهِۖ وَهْوَ عَلَىٰ كُلِّ شَےْءٖ شَهِيدٞۖ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3648,22,'433',34,'Saba’','سَبإ',15,15,48,'قُلْ إِنَّ رَبِّے يَقْذِفُ بِالْحَقِّۖ عَلَّٰمُ اُ۬لْغُيُوبِۖ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3649,22,'434',34,'Saba’','سَبإ',1,1,49,'قُلْ جَآءَ اَ۬لْحَقُّ وَمَا يُبْدِۓُ اُ۬لْبَٰطِلُ وَمَا يُعِيدُۖ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3650,22,'434',34,'Saba’','سَبإ',1,3,50,'قُلْ إِن ضَلَلْتُ فَإِنَّمَا أَضِلُّ عَلَىٰ نَفْسِے وَإِنِ اِ۪هْتَدَيْتُ فَبِمَا يُوحِے إِلَيَّ رَبِّيَۖ إِنَّهُۥ سَمِيعٞ قَرِيبٞۖ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3651,22,'434',34,'Saba’','سَبإ',3,4,51,'وَلَوْ تَرَىٰ إِذْ فَزِعُواْ فَلَا فَوْتَ وَأُخِذُواْ مِن مَّكَانٖ قَرِيبٖۖ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3652,22,'434',34,'Saba’','سَبإ',4,5,52,'وَقَالُواْ ءَامَنَّا بِهِۦۖ وَأَنَّىٰ لَهُمُ اُ۬لتَّنَاوُشُ مِن مَّكَانِۢ بَعِيدٖ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3653,22,'434',34,'Saba’','سَبإ',5,6,53,'وَقَدْ كَفَرُواْ بِهِۦ مِن قَبْلُۖ وَيَقْذِفُونَ بِالْغَيْبِ مِن مَّكَانِۢ بَعِيدٖۖ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3654,22,'434',34,'Saba’','سَبإ',6,7,54,'وَحِيلَ بَيْنَهُمْ وَبَيْنَ مَا يَشْتَهُونَ كَمَا فُعِلَ بِأَشْيَاعِهِم مِّن قَبْلُ إِنَّهُمْ كَانُواْ فِے شَكّٖ مُّرِيبِۢۖ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3655,22,'434',35,'Fāṭir','فَاطِر',10,12,1,'اِ۬لْحَمْدُ لِلهِ فَاطِرِ اِ۬لسَّمَٰوَٰتِ وَالْأَرْضِ جَاعِلِ اِ۬لْمَلَٰٓئِكَةِ رُسُلاً أُوْلِے أَجْنِحَةٖ مَّثْنَىٰ وَثُلَٰثَ وَرُبَٰعَۖ يَزِيدُ فِے اِ۬لْخَلْقِ مَا يَشَآءُۖ اِ۪نَّ اَ۬للَّهَ عَلَىٰ كُلِّ شَےْءٖ قَدِيرٞۖ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3656,22,'434',35,'Fāṭir','فَاطِر',12,13,2,'مَّا يَفْتَحِ اِ۬للَّهُ لِلنَّاسِ مِن رَّحْمَةٖ فَلَا مُمْسِكَ لَهَاۖ وَمَا يُمْسِكْ فَلَا مُرْسِلَ لَهُۥ مِنۢ بَعْدِهِۦۖ وَهْوَ اَ۬لْعَزِيزُ اُ۬لْحَكِيمُۖ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3657,22,'434',35,'Fāṭir','فَاطِر',14,15,3,'يَٰأَيُّهَا اَ۬لنَّاسُ اُ۟ذْكُرُواْ نِعْمَتَ اَ۬للَّهِ عَلَيْكُمْۖ هَلْ مِنْ خَٰلِقٍ غَيْرُ اُ۬للَّهِ يَرْزُقُكُم مِّنَ اَ۬لسَّمَآءِ وَالْأَرْضِۖ لَا إِلَٰهَ إِلَّا هُوَۖ فَأَنَّىٰ تُؤْفَكُونَۖ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3658,22,'435',35,'Fāṭir','فَاطِر',1,2,4,'وَإِنْ يُّكَذِّبُوكَ فَقَدْ كُذِّبَتْ رُسُلٞ مِّن قَبْلِكَۖ وَإِلَى اَ۬للَّهِ تُرْجَعُ اُ۬لْأُمُورُۖ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3659,22,'435',35,'Fāṭir','فَاطِر',2,3,5,'يَٰأَيُّهَا اَ۬لنَّاسُ إِنَّ وَعْدَ اَ۬للَّهِ حَقّٞۖ فَلَا تَغُرَّنَّكُمُ اُ۬لْحَيَوٰةُ اُ۬لدُّنْيَاۖ وَلَا يَغُرَّنَّكُم بِاللَّهِ اِ۬لْغَرُورُۖ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3660,22,'435',35,'Fāṭir','فَاطِر',3,4,6,'إِنَّ اَ۬لشَّيْطَٰنَ لَكُمْ عَدُوّٞ فَاتَّخِذُوهُ عَدُوّاًۖ إِنَّمَا يَدْعُواْ حِزْبَهُۥ لِيَكُونُواْ مِنْ أَصْحَٰبِ اِ۬لسَّعِيرِۖ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3661,22,'435',35,'Fāṭir','فَاطِر',4,6,7,'اِ۬لذِينَ كَفَرُواْ لَهُمْ عَذَابٞ شَدِيدٞۖ وَالذِينَ ءَامَنُواْ وَعَمِلُواْ اُ۬لصَّٰلِحَٰتِ لَهُم مَّغْفِرَةٞ وَأَجْرٞ كَبِيرٌۖ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3662,22,'435',35,'Fāṭir','فَاطِر',6,8,8,'۞ أَفَمَن زُيِّنَ لَهُۥ سُوٓءُ عَمَلِهِۦ فَرَءَاهُ حَسَناٗۖ فَإِنَّ اَ۬للَّهَ يُضِلُّ مَنْ يَّشَآءُ وَيَهْدِے مَنْ يَّشَآءُۖ فَلَا تَذْهَبْ نَفْسُكَ عَلَيْهِمْ حَسَرَٰتٍۖ إِنَّ اَ۬للَّهَ عَلِيمُۢ بِمَا يَصْنَعُونَۖ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3663,22,'435',35,'Fāṭir','فَاطِر',8,10,9,'وَاللَّهُ اُ۬لذِے أَرْسَلَ اَ۬لرِّيَٰحَ فَتُثِيرُ سَحَاباٗ فَسُقْنَٰهُ إِلَىٰ بَلَدٖ مَّيِّتٖ فَأَحْيَيْنَا بِهِ اِ۬لْأَرْضَ بَعْدَ مَوْتِهَاۖ كَذَٰلِكَ اَ۬لنُّشُورُۖ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3664,22,'435',35,'Fāṭir','فَاطِر',10,12,10,'مَن كَانَ يُرِيدُ اُ۬لْعِزَّةَ فَلِلهِ اِ۬لْعِزَّةُ جَمِيعاًۖ إِلَيْهِ يَصْعَدُ اُ۬لْكَلِمُ اُ۬لطَّيِّبُۖ وَالْعَمَلُ اُ۬لصَّٰلِحُ يَرْفَعُهُۥۖ وَالذِينَ يَمْكُرُونَ اَ۬لسَّيِّـَٔاتِ لَهُمْ عَذَابٞ شَدِيدٞۖ وَمَكْرُ أُوْلَٰٓئِكَ هُوَ يَبُورُۖ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3665,22,'435',35,'Fāṭir','فَاطِر',13,15,11,'وَاللَّهُ خَلَقَكُم مِّن تُرَابٖ ثُمَّ مِن نُّطْفَةٖ ثُمَّ جَعَلَكُمْ أَزْوَٰجاٗۖ وَمَا تَحْمِلُ مِنْ أُنثَىٰ وَلَا تَضَعُ إِلَّا بِعِلْمِهِۦۖ وَمَا يُعَمَّرُ مِن مُّعَمَّرٖ وَلَا يُنقَصُ مِنْ عُمُرِهِۦ إِلَّا فِے كِتَٰبٍۖ إِنَّ ذَٰلِكَ عَلَى اَ۬للَّهِ يَسِيرٞۖ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3666,22,'436',35,'Fāṭir','فَاطِر',1,4,12,'وَمَا يَسْتَوِے اِ۬لْبَحْرَٰنِۖ هَٰذَا عَذْبٞ فُرَاتٞ سَآئِغٞ شَرَابُهُۥ وَهَٰذَا مِلْحٌ أُجَاجٞۖ وَمِن كُلّٖ تَأْكُلُونَ لَحْماٗ طَرِيّاٗ وَتَسْتَخْرِجُونَ حِلْيَةٗ تَلْبَسُونَهَاۖ وَتَرَى اَ۬لْفُلْكَ فِيهِ مَوَاخِرَ لِتَبْتَغُواْ مِن فَضْلِهِۦ وَلَعَلَّكُمْ تَشْكُرُونَۖ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3667,22,'436',35,'Fāṭir','فَاطِر',4,7,13,'يُولِجُ اُ۬ليْلَ فِے اِ۬لنَّهَارِ وَيُولِجُ اُ۬لنَّهَارَ فِے اِ۬ليْلِۖ وَسَخَّرَ اَ۬لشَّمْسَ وَالْقَمَرَۖ كُلّٞ يَجْرِے لِأَجَلٖ مُّسَمّىٗۖ ذَٰلِكُمُ اُ۬للَّهُ رَبُّكُمْ لَهُ اُ۬لْمُلْكُۖ وَالذِينَ تَدْعُونَ مِن دُونِهِۦ مَا يَمْلِكُونَ مِن قِطْمِيرٍۖ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3668,22,'436',35,'Fāṭir','فَاطِر',8,10,14,'إِن تَدْعُوهُمْ لَا يَسْمَعُواْ دُعَآءَكُمْ وَلَوْ سَمِعُواْ مَا اَ۪سْتَجَابُواْ لَكُمْۖ وَيَوْمَ اَ۬لْقِيَٰمَةِ يَكْفُرُونَ بِشِرْكِكُمْۖ وَلَا يُنَبِّئُكَ مِثْلُ خَبِيرٖۖ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3669,22,'436',35,'Fāṭir','فَاطِر',10,11,15,'۞ يَٰأَيُّهَا اَ۬لنَّاسُ أَنتُمُ اُ۬لْفُقَرَآءُ اِ۪لَى اَ۬للَّهِۖ وَاللَّهُ هُوَ اَ۬لْغَنِيُّ اُ۬لْحَمِيدُۖ ١٥');
