INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4139,24,'469',40,'Ghāfir','غَافِر',2,3,17,'وَأَنذِرْهُمْ يَوْمَ اَ۬لْأٓزِفَةِ إِذِ اِ۬لْقُلُوبُ لَدَى اَ۬لْحَنَاجِرِ كَٰظِمِينَۖ ١٧');
