INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4174,24,'473',40,'Ghāfir','غَافِر',4,5,52,'يَوْمَ لَا يَنفَعُ اُ۬لظَّٰلِمِينَ مَعْذِرَتُهُمْ وَلَهُمُ اُ۬للَّعْنَةُ وَلَهُمْ سُوٓءُ اُ۬لدَّارِۖ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4175,24,'473',40,'Ghāfir','غَافِر',5,7,53,'۞ وَلَقَدْ ءَاتَيْنَا مُوسَى اَ۬لْهُدَىٰ وَأَوْرَثْنَا بَنِے إِسْرَآءِيلَ اَ۬لْكِتَٰبَ هُدىٗ وَذِكْرَىٰ لِأُوْلِے اِ۬لْأَلْبَٰبِۖ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4176,24,'473',40,'Ghāfir','غَافِر',7,9,54,'فَاصْبِرْ إِنَّ وَعْدَ اَ۬للَّهِ حَقّٞۖ وَاسْتَغْفِرْ لِذَنۢبِكَ وَسَبِّحْ بِحَمْدِ رَبِّكَ بِالْعَشِيِّ وَالْإِبْكَٰرِۖ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4177,24,'473',40,'Ghāfir','غَافِر',9,12,55,'إِنَّ اَ۬لذِينَ يُجَٰدِلُونَ فِے ءَايَٰتِ اِ۬للَّهِ بِغَيْرِ سُلْطَٰنٍ أَتَيٰهُمْ إِن فِے صُدُورِهِمْ إِلَّا كِبْرٞ مَّا هُم بِبَٰلِغِيهِۖ فَاسْتَعِذْ بِاللَّهِۖ إِنَّهُۥ هُوَ اَ۬لسَّمِيعُ اُ۬لْبَصِيرُۖ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4178,24,'473',40,'Ghāfir','غَافِر',12,13,56,'لَخَلْقُ اُ۬لسَّمَٰوَٰتِ وَالْأَرْضِ أَكْبَرُ مِنْ خَلْقِ اِ۬لنَّاسِۖ وَلَٰكِنَّ أَكْثَرَ اَ۬لنَّاسِ لَا يَعْلَمُونَۖ ٥٦');
