INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4181,24,'474',40,'Ghāfir','غَافِر',1,2,59,'إِنَّ اَ۬لسَّاعَةَ لَأٓتِيَةٞ لَّا رَيْبَ فِيهَاۖ وَلَٰكِنَّ أَكْثَرَ اَ۬لنَّاسِ لَا يُؤْمِنُونَۖ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4182,24,'474',40,'Ghāfir','غَافِر',2,4,60,'وَقَالَ رَبُّكُمُ اُ۟دْعُونِے أَسْتَجِبْ لَكُمْۖ إِنَّ اَ۬لذِينَ يَسْتَكْبِرُونَ عَنْ عِبَادَتِے سَيَدْخُلُونَ جَهَنَّمَ دَاخِرِينَۖ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4183,24,'474',40,'Ghāfir','غَافِر',4,6,61,'اَ۬للَّهُ اُ۬لذِے جَعَلَ لَكُمُ اُ۬ليْلَ لِتَسْكُنُواْ فِيهِ وَالنَّهَارَ مُبْصِراًۖ إِنَّ اَ۬للَّهَ لَذُو فَضْلٍ عَلَى اَ۬لنَّاسِۖ وَلَٰكِنَّ أَكْثَرَ اَ۬لنَّاسِ لَا يَشْكُرُونَۖ ٦١');
