INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4311,25,'489',43,'Az-Zukhruf','الزُّخرُف',7,8,2,'إِنَّا جَعَلْنَٰهُ قُرْءَٰناً عَرَبِيّاٗ لَّعَلَّكُمْ تَعْقِلُونَۖ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4312,25,'489',43,'Az-Zukhruf','الزُّخرُف',8,9,3,'وَإِنَّهُۥ فِے أُمِّ اِ۬لْكِتَٰبِ لَدَيْنَا لَعَلِيٌّ حَكِيمٌۖ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4313,25,'489',43,'Az-Zukhruf','الزُّخرُف',9,10,4,'أَفَنَضْرِبُ عَنكُمُ اُ۬لذِّكْرَ صَفْحاً إِن كُنتُمْ قَوْماٗ مُّسْرِفِينَۖ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4314,25,'489',43,'Az-Zukhruf','الزُّخرُف',10,11,5,'وَكَمْ أَرْسَلْنَا مِن نَّبِےٓءٖ فِے اِ۬لْأَوَّلِينَۖ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4315,25,'489',43,'Az-Zukhruf','الزُّخرُف',11,11,6,'وَمَا يَأْتِيهِم مِّن نَّبِےٓءٍ إِلَّا كَانُواْ بِهِۦ يَسْتَهْزِءُونَۖ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4316,25,'489',43,'Az-Zukhruf','الزُّخرُف',12,12,7,'فَأَهْلَكْنَا أَشَدَّ مِنْهُم بَطْشاٗۖ وَمَضَىٰ مَثَلُ اُ۬لْأَوَّلِينَۖ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4317,25,'489',43,'Az-Zukhruf','الزُّخرُف',13,14,8,'وَلَئِن سَأَلْتَهُم مَّنْ خَلَقَ اَ۬لسَّمَٰوَٰتِ وَالْأَرْضَ لَيَقُولُنَّ خَلَقَهُنَّ اَ۬لْعَزِيزُ اُ۬لْعَلِيمُۖ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4318,25,'489',43,'Az-Zukhruf','الزُّخرُف',14,15,9,'اُ۬لذِے جَعَلَ لَكُمُ اُ۬لْأَرْضَ مِهَٰداٗ وَجَعَلَ لَكُمْ فِيهَا سُبُلاٗ لَّعَلَّكُمْ تَهْتَدُونَۖ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4319,25,'490',43,'Az-Zukhruf','الزُّخرُف',1,2,10,'۞ وَالذِے نَزَّلَ مِنَ اَ۬لسَّمَآءِ مَآءَۢ بِقَدَرٖ فَأَنشَرْنَا بِهِۦ بَلْدَةٗ مَّيْتاٗۖ كَذَٰلِكَ تُخْرَجُونَۖ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4320,25,'490',43,'Az-Zukhruf','الزُّخرُف',2,3,11,'وَالذِے خَلَقَ اَ۬لْأَزْوَٰجَ كُلَّهَا وَجَعَلَ لَكُم مِّنَ اَ۬لْفُلْكِ وَالْأَنْعَٰمِ مَا تَرْكَبُونَ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4321,25,'490',43,'Az-Zukhruf','الزُّخرُف',3,5,12,'لِتَسْتَوُۥاْ عَلَىٰ ظُهُورِهِۦ ثُمَّ تَذْكُرُواْ نِعْمَةَ رَبِّكُمْ إِذَا اَ۪سْتَوَيْتُمْ عَلَيْهِ وَتَقُولُواْ سُبْحَٰنَ اَ۬لذِے سَخَّرَ لَنَا هَٰذَا وَمَا كُنَّا لَهُۥ مُقْرِنِينَ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4322,25,'490',43,'Az-Zukhruf','الزُّخرُف',5,6,13,'وَإِنَّا إِلَىٰ رَبِّنَا لَمُنقَلِبُونَۖ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4323,25,'490',43,'Az-Zukhruf','الزُّخرُف',6,7,14,'وَجَعَلُواْ لَهُۥ مِنْ عِبَادِهِۦ جُزْءاًۖ إِنَّ اَ۬لْإِنسَٰنَ لَكَفُورٞ مُّبِينٌۖ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4324,25,'490',43,'Az-Zukhruf','الزُّخرُف',7,8,15,'أَمِ اِ۪تَّخَذَ مِمَّا يَخْلُقُ بَنَاتٖ وَأَصْفَيٰكُم بِالْبَنِينَۖ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4325,25,'490',43,'Az-Zukhruf','الزُّخرُف',8,9,16,'وَإِذَا بُشِّرَ أَحَدُهُم بِمَا ضَرَبَ لِلرَّحْمَٰنِ مَثَلاٗ ظَلَّ وَجْهُهُۥ مُسْوَدّاٗ وَهْوَ كَظِيمٌۖ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4326,25,'490',43,'Az-Zukhruf','الزُّخرُف',9,10,17,'أَوَمَنْ يَّنشَؤُاْ فِے اِ۬لْحِلْيَةِ وَهْوَ فِے اِ۬لْخِصَامِ غَيْرُ مُبِينٖۖ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4327,25,'490',43,'Az-Zukhruf','الزُّخرُف',10,12,18,'وَجَعَلُواْ اُ۬لْمَلَٰٓئِكَةَ اَ۬لذِينَ هُمْ عِندَ اَ۬لرَّحْمَٰنِ إِنَٰثاًۖ أَٰ۟شْهِدُواْ خَلْقَهُمْۖ سَتُكْتَبُ شَهَٰدَتُهُمْ وَيُسْـَٔلُونَۖ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4328,25,'490',43,'Az-Zukhruf','الزُّخرُف',12,13,19,'وَقَالُواْ لَوْ شَآءَ اَ۬لرَّحْمَٰنُ مَا عَبَدْنَٰهُمۖ مَّا لَهُم بِذَٰلِكَ مِنْ عِلْمٍۖ إِنْ هُمْ إِلَّا يَخْرُصُونَۖ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4329,25,'490',43,'Az-Zukhruf','الزُّخرُف',13,14,20,'أَمْ ءَاتَيْنَٰهُمْ كِتَٰباٗ مِّن قَبْلِهِۦ فَهُم بِهِۦ مُسْتَمْسِكُونَۖ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4330,25,'490',43,'Az-Zukhruf','الزُّخرُف',14,15,21,'بَلْ قَالُواْ إِنَّا وَجَدْنَا ءَابَآءَنَا عَلَىٰ أُمَّةٖ وَإِنَّا عَلَىٰ ءَاثَٰرِهِم مُّهْتَدُونَۖ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4331,25,'491',43,'Az-Zukhruf','الزُّخرُف',1,2,22,'وَكَذَٰلِكَ مَا أَرْسَلْنَا مِن قَبْلِكَ فِے قَرْيَةٖ مِّن نَّذِيرٍ إِلَّا قَالَ مُتْرَفُوهَا إِنَّا وَجَدْنَا ءَابَآءَنَا عَلَىٰ أُمَّةٖ وَإِنَّا عَلَىٰ ءَاثَٰرِهِم مُّقْتَدُونَۖ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4332,25,'491',43,'Az-Zukhruf','الزُّخرُف',3,4,23,'۞ قُلْ أَوَلَوْ جِئْتُكُم بِأَهْدَىٰ مِمَّا وَجَدتُّمْ عَلَيْهِ ءَابَآءَكُمْۖ قَالُواْ إِنَّا بِمَا أُرْسِلْتُم بِهِۦ كَٰفِرُونَۖ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4333,25,'491',43,'Az-Zukhruf','الزُّخرُف',4,5,24,'فَانتَقَمْنَا مِنْهُمْ فَانظُرْ كَيْفَ كَانَ عَٰقِبَةُ اُ۬لْمُكَذِّبِينَۖ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4334,25,'491',43,'Az-Zukhruf','الزُّخرُف',5,6,25,'وَإِذْ قَالَ إِبْرَٰهِيمُ لِأَبِيهِ وَقَوْمِهِۦ إِنَّنِے بَرَآءٞ مِّمَّا تَعْبُدُونَ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4335,25,'491',43,'Az-Zukhruf','الزُّخرُف',6,6,26,'إِلَّا اَ۬لذِے فَطَرَنِے فَإِنَّهُۥ سَيَهْدِينِۖ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4336,25,'491',43,'Az-Zukhruf','الزُّخرُف',7,7,27,'وَجَعَلَهَا كَلِمَةَۢ بَاقِيَةٗ فِے عَقِبِهِۦ لَعَلَّهُمْ يَرْجِعُونَۖ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4337,25,'491',43,'Az-Zukhruf','الزُّخرُف',7,8,28,'بَلْ مَتَّعْتُ هَٰؤُلَآءِ وَءَابَآءَهُمْ حَتَّىٰ جَآءَهُمُ اُ۬لْحَقُّ وَرَسُولٞ مُّبِينٞۖ ٢٨');
