INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2019,14,'280',16,'An-Naḥl','النَّحل',5,6,113,'وَلَقَدۡ جَآءَهُمُۥ رَسُولٞ مِّنۡهُمُۥ فَكَذَّبُوهُۥ فَأَخَذَهُمُ ٱلۡعَذَابُ وَهُمُۥ ظَٰلِمُونَ ١١٣');
