INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2021,14,'280',16,'An-Naḥl','النَّحل',8,11,115,'إِنَّمَا حَرَّمَ عَلَيۡكُمُ ٱلۡمَيۡتَةَ وَٱلدَّمَ وَلَحۡمَ ٱلۡخِنزِيرِ وَمَا أُهِلَّ لِغَيۡرِ ٱللَّهِ بِهِۦۖ فَمَنُ ٱضۡطُرَّ غَيۡرَ بَاغٖ وَلَا عَادٖ فَإِنَّ ٱللَّهَ غَفُورٞ رَّحِيمٞ ١١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2022,14,'280',16,'An-Naḥl','النَّحل',11,13,116,'وَلَا تَقُولُواْ لِمَا تَصِفُ أَلۡسِنَتُكُمُ ٱلۡكَذِبَ هَٰذَا حَلَٰلٞ وَهَٰذَا حَرَامٞ لِّتَفۡتَرُواْ عَلَى ٱللَّهِ ٱلۡكَذِبَۚ إِنَّ ٱلَّذِينَ يَفۡتَرُونَ عَلَى ٱللَّهِ ٱلۡكَذِبَ لَا يُفۡلِحُونَ ١١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2023,14,'280',16,'An-Naḥl','النَّحل',13,14,117,'مَتَٰعٞ قَلِيلٞ وَلَهُمُۥ عَذَابٌ أَلِيمٞ ١١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2024,14,'280',16,'An-Naḥl','النَّحل',14,15,118,'وَعَلَى ٱلَّذِينَ هَادُواْ حَرَّمۡنَا مَا قَصَصۡنَا عَلَيۡكَ مِن قَبۡلُۖ وَمَا ظَلَمۡنَٰهُمُۥ وَلَٰكِن كَانُواْ أَنفُسَهُمُۥ يَظۡلِمُونَ ١١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2025,14,'281',16,'An-Naḥl','النَّحل',1,2,119,'ثُمَّ إِنَّ رَبَّكَ لِلَّذِينَ عَمِلُواْ ٱلسُّوٓءَ بِجَهَٰلَةٖ ثُمَّ تَابُواْ مِنۢ بَعۡدِ ذَٰلِكَ وَأَصۡلَحُواْ إِنَّ رَبَّكَ مِنۢ بَعۡدِهَا لَغَفُورٞ رَّحِيمٌ ١١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2026,14,'281',16,'An-Naḥl','النَّحل',2,3,120,'إِنَّ إِبۡرَٰهِيمَ كَانَ أُمَّةٗ قَانِتٗا لِّلَّهِ حَنِيفٗا وَلَمۡ يَكُ مِنَ ٱلۡمُشۡرِكِينَ ١٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2027,14,'281',16,'An-Naḥl','النَّحل',4,4,121,'شَاكِرٗا لِّأَنۡعُمِهِۚ ٱجۡتَبَىٰهُۥ وَهَدَىٰهُۥ إِلَىٰ صِۜرَٰطٖ مُّسۡتَقِيمٖ ١٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2028,14,'281',16,'An-Naḥl','النَّحل',5,5,122,'وَءَاتَيۡنَٰهُۥ فِي ٱلدُّنۡيَا حَسَنَةٗۖ وَإِنَّهُۥ فِي ٱلۡأٓخِرَةِ لَمِنَ ٱلصَّٰلِحِينَ ١٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2029,14,'281',16,'An-Naḥl','النَّحل',6,7,123,'ثُمَّ أَوۡحَيۡنَا إِلَيۡكَ أَنِ ٱتَّبِعۡ مِلَّةَ إِبۡرَٰهِيمَ حَنِيفٗاۖ وَمَا كَانَ مِنَ ٱلۡمُشۡرِكِينَ ١٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2030,14,'281',16,'An-Naḥl','النَّحل',7,9,124,'إِنَّمَا جُعِلَ ٱلسَّبۡتُ عَلَى ٱلَّذِينَ ٱخۡتَلَفُواْ فِيهِۦۚ وَإِنَّ رَبَّكَ لَيَحۡكُمُ بَيۡنَهُمُۥ يَوۡمَ ٱلۡقِيَٰمَةِ فِيمَا كَانُواْ فِيهِۦ يَخۡتَلِفُونَ ١٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2031,14,'281',16,'An-Naḥl','النَّحل',9,11,125,'ٱدۡعُ إِلَىٰ سَبِيلِ رَبِّكَ بِٱلۡحِكۡمَةِ وَٱلۡمَوۡعِظَةِ ٱلۡحَسَنَةِۖ وَجَٰدِلۡهُمُۥ بِٱلَّتِي هِيَ أَحۡسَنُۚ إِنَّ رَبَّكَ هُوَ أَعۡلَمُ بِمَن ضَلَّ عَن سَبِيلِهِۦ وَهُوَ أَعۡلَمُ بِٱلۡمُهۡتَدِينَ ١٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2032,14,'281',16,'An-Naḥl','النَّحل',12,13,126,'وَإِنۡ عَاقَبۡتُمُۥ فَعَاقِبُواْ بِمِثۡلِ مَا عُوقِبۡتُمُۥ بِهِۦۖ وَلَئِن صَبَرۡتُمُۥ لَهُوَ خَيۡرٞ لِّلصَّٰبِرِينَ ١٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2033,14,'281',16,'An-Naḥl','النَّحل',13,14,127,'وَٱصۡبِرۡ وَمَا صَبۡرُكَ إِلَّا بِٱللَّهِۚ وَلَا تَحۡزَنۡ عَلَيۡهِمُۥ وَلَا تَكُ فِي ضِيقٖ مِّمَّا يَمۡكُرُونَ ١٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2034,14,'281',16,'An-Naḥl','النَّحل',15,15,128,'إِنَّ ٱللَّهَ مَعَ ٱلَّذِينَ ٱتَّقَواْ وَّٱلَّذِينَ هُمُۥ مُحۡسِنُونَ ١٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2035,15,'282',17,'Al-Isrā’','الإسرَاء',3,5,1,'سُبۡحَٰنَ ٱلَّذِي أَسۡرَىٰ بِعَبۡدِهِۦ لَيۡلٗا مِّنَ ٱلۡمَسۡجِدِ ٱلۡحَرَامِ إِلَى ٱلۡمَسۡجِدِ ٱلۡأَقۡصَا ٱلَّذِي بَٰرَكۡنَا حَوۡلَهُۥ لِنُرِيَهُۥ مِنۡ ءَايَٰتِنَاۚ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلۡبَصِيرُ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2036,15,'282',17,'Al-Isrā’','الإسرَاء',5,6,2,'وَءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ وَجَعَلۡنَٰهُۥ هُدٗى لِّبَنِي إِسۡرَٰٓءِيلَ أَلَّا تَتَّخِذُواْ مِن دُونِي وَكِيلٗا ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2037,15,'282',17,'Al-Isrā’','الإسرَاء',7,7,3,'ذُرِّيَّةَ مَنۡ حَمَلۡنَا مَعَ نُوحٍۚ إِنَّهُۥ كَانَ عَبۡدٗا شَكُورٗا ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2038,15,'282',17,'Al-Isrā’','الإسرَاء',8,9,4,'وَقَضَيۡنَا إِلَىٰ بَنِي إِسۡرَٰٓءِيلَ فِي ٱلۡكِتَٰبِ لَتُفۡسِدُنَّ فِي ٱلۡأَرۡضِ مَرَّتَيۡنِ وَلَتَعۡلُنَّ عُلُوّٗا كَبِيرٗا ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2039,15,'282',17,'Al-Isrā’','الإسرَاء',9,11,5,'فَإِذَا جَآءَ وَعۡدُ أُولَىٰهُمَا بَعَثۡنَا عَلَيۡكُمُۥ عِبَادٗا لَّنَا أُوْلِي بَأۡسٖ شَدِيدٖ فَجَاسُواْ خِلَٰلَ ٱلدِّيَارِۚ وَكَانَ وَعۡدٗا مَّفۡعُولٗا ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2040,15,'282',17,'Al-Isrā’','الإسرَاء',11,12,6,'ثُمَّ رَدَدۡنَا لَكُمُ ٱلۡكَرَّةَ عَلَيۡهِمُۥ وَأَمۡدَدۡنَٰكُمُۥ بِأَمۡوَٰلٖ وَبَنِينَ وَجَعَلۡنَٰكُمُۥ أَكۡثَرَ نَفِيرًا ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2041,15,'282',17,'Al-Isrā’','الإسرَاء',13,15,7,'إِنۡ أَحۡسَنتُمُۥ أَحۡسَنتُمُۥ لِأَنفُسِكُمُۥۖ وَإِنۡ أَسَأۡتُمُۥ فَلَهَاۚ فَإِذَا جَآءَ وَعۡدُ ٱلۡأٓخِرَةِ لِيَسُـُٔواْ وُجُوهَكُمُۥ وَلِيَدۡخُلُواْ ٱلۡمَسۡجِدَ كَمَا دَخَلُوهُۥ أَوَّلَ مَرَّةٖ وَلِيُتَبِّرُواْ مَا عَلَوۡاْ تَتۡبِيرًا ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2042,15,'283',17,'Al-Isrā’','الإسرَاء',1,2,8,'عَسَىٰ رَبُّكُمُۥ أَن يَرۡحَمَكُمُۥۚ وَإِنۡ عُدتُّمُۥ عُدۡنَاۚ وَجَعَلۡنَا جَهَنَّمَ لِلۡكَٰفِرِينَ حَصِيرًا ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2043,15,'283',17,'Al-Isrā’','الإسرَاء',2,3,9,'إِنَّ هَٰذَا ٱلۡقُرَانَ يَهۡدِي لِلَّتِي هِيَ أَقۡوَمُ وَيُبَشِّرُ ٱلۡمُؤۡمِنِينَ ٱلَّذِينَ يَعۡمَلُونَ ٱلصَّٰلِحَٰتِ أَنَّ لَهُمُۥ أَجۡرٗا كَبِيرٗا ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2044,15,'283',17,'Al-Isrā’','الإسرَاء',4,4,10,'وَأَنَّ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ أَعۡتَدۡنَا لَهُمُۥ عَذَابًا أَلِيمٗا ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2045,15,'283',17,'Al-Isrā’','الإسرَاء',5,5,11,'وَيَدۡعُ ٱلۡإِنسَٰنُ بِٱلشَّرِّ دُعَآءَهُۥ بِٱلۡخَيۡرِۖ وَكَانَ ٱلۡإِنسَٰنُ عَجُولٗا ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2046,15,'283',17,'Al-Isrā’','الإسرَاء',6,8,12,'وَجَعَلۡنَا ٱلَّيۡلَ وَٱلنَّهَارَ ءَايَتَيۡنِۖ فَمَحَوۡنَا ءَايَةَ ٱلَّيۡلِ وَجَعَلۡنَا ءَايَةَ ٱلنَّهَارِ مُبۡصِرَةٗ لِّتَبۡتَغُواْ فَضۡلٗا مِّن رَّبِّكُمُۥ وَلِتَعۡلَمُواْ عَدَدَ ٱلسِّنِينَ وَٱلۡحِسَابَۚ وَكُلَّ شَيۡءٖ فَصَّلۡنَٰهُۥ تَفۡصِيلٗا ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2047,15,'283',17,'Al-Isrā’','الإسرَاء',8,10,13,'وَكُلَّ إِنسَٰنٍ أَلۡزَمۡنَٰهُۥ طَٰٓئِرَهُۥ فِي عُنُقِهِۦۖ وَنُخۡرِجُ لَهُۥ يَوۡمَ ٱلۡقِيَٰمَةِ كِتَٰبٗا يَلۡقَىٰهُۥ مَنشُورًا ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2048,15,'283',17,'Al-Isrā’','الإسرَاء',10,10,14,'ٱقۡرَأۡ كِتَٰبَكَ كَفَىٰ بِنَفۡسِكَ ٱلۡيَوۡمَ عَلَيۡكَ حَسِيبٗا ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2049,15,'283',17,'Al-Isrā’','الإسرَاء',11,13,15,'مَّنِ ٱهۡتَدَىٰ فَإِنَّمَا يَهۡتَدِي لِنَفۡسِهِۦۖ وَمَن ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيۡهَاۚ وَلَا تَزِرُ وَازِرَةٞ وِزۡرَ أُخۡرَىٰۗ وَمَا كُنَّا مُعَذِّبِينَ حَتَّىٰ نَبۡعَثَ رَسُولٗا ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2050,15,'283',17,'Al-Isrā’','الإسرَاء',13,14,16,'وَإِذَا أَرَدۡنَا أَن نُّهۡلِكَ قَرۡيَةً أَمَرۡنَا مُتۡرَفِيهَا فَفَسَقُواْ فِيهَا فَحَقَّ عَلَيۡهَا ٱلۡقَوۡلُ فَدَمَّرۡنَٰهَا تَدۡمِيرٗا ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2051,15,'283',17,'Al-Isrā’','الإسرَاء',14,15,17,'وَكَمۡ أَهۡلَكۡنَا مِنَ ٱلۡقُرُونِ مِنۢ بَعۡدِ نُوحٖۗ وَكَفَىٰ بِرَبِّكَ بِذُنُوبِ عِبَادِهِۦ خَبِيرَۢا بَصِيرٗا ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2052,15,'284',17,'Al-Isrā’','الإسرَاء',1,2,18,'مَّن كَانَ يُرِيدُ ٱلۡعَاجِلَةَ عَجَّلۡنَا لَهُۥ فِيهَا مَا نَشَآءُ لِمَن نُّرِيدُ ثُمَّ جَعَلۡنَا لَهُۥ جَهَنَّمَ يَصۡلَىٰهَا مَذۡمُومٗا مَّدۡحُورٗا ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2053,15,'284',17,'Al-Isrā’','الإسرَاء',2,4,19,'وَمَنۡ أَرَادَ ٱلۡأٓخِرَةَ وَسَعَىٰ لَهَا سَعۡيَهَا وَهُوَ مُؤۡمِنٞ فَأُوْلَٰٓئِكَ كَانَ سَعۡيُهُمُۥ مَشۡكُورٗا ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2054,15,'284',17,'Al-Isrā’','الإسرَاء',4,5,20,'كُلّٗا نُّمِدُّ هَٰؤُلَآءِ وَهَٰؤُلَآءِ مِنۡ عَطَآءِ رَبِّكَۚ وَمَا كَانَ عَطَآءُ رَبِّكَ مَحۡظُورًا ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2055,15,'284',17,'Al-Isrā’','الإسرَاء',5,7,21,'ٱنظُرۡ كَيۡفَ فَضَّلۡنَا بَعۡضَهُمُۥ عَلَىٰ بَعۡضٖۚ وَلَلۡأٓخِرَةُ أَكۡبَرُ دَرَجَٰتٖ وَأَكۡبَرُ تَفۡضِيلٗا ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2056,15,'284',17,'Al-Isrā’','الإسرَاء',7,8,22,'لَّا تَجۡعَلۡ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ فَتَقۡعُدَ مَذۡمُومٗا مَّخۡذُولٗا ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2057,15,'284',17,'Al-Isrā’','الإسرَاء',8,10,23,'۞ وَقَضَىٰ رَبُّكَ أَلَّا تَعۡبُدُواْ إِلَّا إِيَّاهُۥ وَبِٱلۡوَٰلِدَيۡنِ إِحۡسَٰنًاۚ إِمَّا يَبۡلُغَنَّ عِندَكَ ٱلۡكِبَرَ أَحَدُهُمَا أَوۡ كِلَاهُمَا فَلَا تَقُل لَّهُمَا أُفَّ وَلَا تَنۡهَرۡهُمَا وَقُل لَّهُمَا قَوۡلٗا كَرِيمٗا ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2058,15,'284',17,'Al-Isrā’','الإسرَاء',10,12,24,'وَٱخۡفِضۡ لَهُمَا جَنَاحَ ٱلذُّلِّ مِنَ ٱلرَّحۡمَةِ وَقُل رَّبِّ ٱرۡحَمۡهُمَا كَمَا رَبَّيَانِي صَغِيرٗا ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2059,15,'284',17,'Al-Isrā’','الإسرَاء',12,13,25,'رَّبُّكُمُۥ أَعۡلَمُ بِمَا فِي نُفُوسِكُمُۥۚ إِن تَكُونُواْ صَٰلِحِينَ فَإِنَّهُۥ كَانَ لِلۡأَوَّٰبِينَ غَفُورٗا ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2060,15,'284',17,'Al-Isrā’','الإسرَاء',13,14,26,'وَءَاتِ ذَا ٱلۡقُرۡبَىٰ حَقَّهُۥ وَٱلۡمِسۡكِينَ وَٱبۡنَ ٱلسَّبِيلِ وَلَا تُبَذِّرۡ تَبۡذِيرًا ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2061,15,'284',17,'Al-Isrā’','الإسرَاء',14,15,27,'إِنَّ ٱلۡمُبَذِّرِينَ كَانُواْ إِخۡوَٰنَ ٱلشَّيَٰطِينِۖ وَكَانَ ٱلشَّيۡطَٰنُ لِرَبِّهِۦ كَفُورٗا ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2062,15,'285',17,'Al-Isrā’','الإسرَاء',1,2,28,'وَإِمَّا تُعۡرِضَنَّ عَنۡهُمُ ٱبۡتِغَآءَ رَحۡمَةٖ مِّن رَّبِّكَ تَرۡجُوهَا فَقُل لَّهُمُۥ قَوۡلٗا مَّيۡسُورٗا ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2063,15,'285',17,'Al-Isrā’','الإسرَاء',2,3,29,'وَلَا تَجۡعَلۡ يَدَكَ مَغۡلُولَةً إِلَىٰ عُنُقِكَ وَلَا تَبۡسُطۡهَا كُلَّ ٱلۡبَسۡطِ فَتَقۡعُدَ مَلُومٗا مَّحۡسُورًا ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2064,15,'285',17,'Al-Isrā’','الإسرَاء',3,4,30,'إِنَّ رَبَّكَ يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُۚ إِنَّهُۥ كَانَ بِعِبَادِهِۦ خَبِيرَۢا بَصِيرٗا ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2065,15,'285',17,'Al-Isrā’','الإسرَاء',4,6,31,'وَلَا تَقۡتُلُواْ أَوۡلَٰدَكُمُۥ خَشۡيَةَ إِمۡلَٰقٖۖ نَّحۡنُ نَرۡزُقُهُمُۥ وَإِيَّاكُمُۥۚ إِنَّ قَتۡلَهُمُۥ كَانَ خِطَآءٗ كَبِيرٗا ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2066,15,'285',17,'Al-Isrā’','الإسرَاء',6,7,32,'وَلَا تَقۡرَبُواْ ٱلزِّنَىٰۖ إِنَّهُۥ كَانَ فَٰحِشَةٗ وَسَآءَ سَبِيلٗا ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2067,15,'285',17,'Al-Isrā’','الإسرَاء',7,9,33,'وَلَا تَقۡتُلُواْ ٱلنَّفۡسَ ٱلَّتِي حَرَّمَ ٱللَّهُ إِلَّا بِٱلۡحَقِّۗ وَمَن قُتِلَ مَظۡلُومٗا فَقَدۡ جَعَلۡنَا لِوَلِيِّهِۦ سُلۡطَٰنٗا فَلَا يُسۡرِف فِّي ٱلۡقَتۡلِۖ إِنَّهُۥ كَانَ مَنصُورٗا ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2068,15,'285',17,'Al-Isrā’','الإسرَاء',9,11,34,'وَلَا تَقۡرَبُواْ مَالَ ٱلۡيَتِيمِ إِلَّا بِٱلَّتِي هِيَ أَحۡسَنُ حَتَّىٰ يَبۡلُغَ أَشُدَّهُۥۚ وَأَوۡفُواْ بِٱلۡعَهۡدِۖ إِنَّ ٱلۡعَهۡدَ كَانَ مَسۡـُٔولٗا ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2069,15,'285',17,'Al-Isrā’','الإسرَاء',11,12,35,'وَأَوۡفُواْ ٱلۡكَيۡلَ إِذَا كِلۡتُمُۥ وَزِنُواْ بِٱلۡقُسۡطَاسِ ٱلۡمُسۡتَقِيمِۚ ذَٰلِكَ خَيۡرٞ وَأَحۡسَنُ تَأۡوِيلٗا ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2070,15,'285',17,'Al-Isrā’','الإسرَاء',12,13,36,'وَلَا تَقۡفُ مَا لَيۡسَ لَكَ بِهِۦ عِلۡمٌۚ إِنَّ ٱلسَّمۡعَ وَٱلۡبَصَرَ وَٱلۡفُؤَادَ كُلُّ أُوْلَٰٓئِكَ كَانَ عَنۡهُۥ مَسۡـُٔولٗا ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2071,15,'285',17,'Al-Isrā’','الإسرَاء',14,15,37,'وَلَا تَمۡشِ فِي ٱلۡأَرۡضِ مَرَحًاۖ إِنَّكَ لَن تَخۡرِقَ ٱلۡأَرۡضَ وَلَن تَبۡلُغَ ٱلۡجِبَالَ طُولٗا ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2072,15,'285',17,'Al-Isrā’','الإسرَاء',15,15,38,'كُلُّ ذَٰلِكَ كَانَ سَيِّئَةً عِندَ رَبِّكَ مَكۡرُوهٗا ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2073,15,'286',17,'Al-Isrā’','الإسرَاء',1,2,39,'ذَٰلِكَ مِمَّا أَوۡحَىٰ إِلَيۡكَ رَبُّكَ مِنَ ٱلۡحِكۡمَةِۗ وَلَا تَجۡعَلۡ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ فَتُلۡقَىٰ فِي جَهَنَّمَ مَلُومٗا مَّدۡحُورًا ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2074,15,'286',17,'Al-Isrā’','الإسرَاء',2,3,40,'أَفَأَصۡفَىٰكُمُۥ رَبُّكُمُۥ بِٱلۡبَنِينَ وَٱتَّخَذَ مِنَ ٱلۡمَلَٰٓئِكَةِ إِنَٰثًاۚ إِنَّكُمُۥ لَتَقُولُونَ قَوۡلًا عَظِيمٗا ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2075,15,'286',17,'Al-Isrā’','الإسرَاء',4,4,41,'وَلَقَدۡ صَرَّفۡنَا فِي هَٰذَا ٱلۡقُرَانِ لِيَذَّكَّرُواْ وَمَا يَزِيدُهُمُۥ إِلَّا نُفُورٗا ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2076,15,'286',17,'Al-Isrā’','الإسرَاء',5,5,42,'قُل لَّوۡ كَانَ مَعَهُۥ ءَالِهَةٞ كَمَا يَقُولُونَ إِذٗا لَّٱبۡتَغَوۡاْ إِلَىٰ ذِي ٱلۡعَرۡشِ سَبِيلٗا ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2077,15,'286',17,'Al-Isrā’','الإسرَاء',6,6,43,'سُبۡحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يَقُولُونَ عُلُوّٗا كَبِيرٗا ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2078,15,'286',17,'Al-Isrā’','الإسرَاء',6,8,44,'يُسَبِّحُ لَهُ ٱلسَّمَٰوَٰتُ ٱلسَّبۡعُ وَٱلۡأَرۡضُ وَمَن فِيهِنَّۚ وَإِن مِّن شَيۡءٍ إِلَّا يُسَبِّحُ بِحَمۡدِهِۦ وَلَٰكِن لَّا تَفۡقَهُونَ تَسۡبِيحَهُمُۥۚ إِنَّهُۥ كَانَ حَلِيمًا غَفُورٗا ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2079,15,'286',17,'Al-Isrā’','الإسرَاء',8,10,45,'وَإِذَا قَرَأۡتَ ٱلۡقُرَانَ جَعَلۡنَا بَيۡنَكَ وَبَيۡنَ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ حِجَابٗا مَّسۡتُورٗا ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2080,15,'286',17,'Al-Isrā’','الإسرَاء',10,11,46,'وَجَعَلۡنَا عَلَىٰ قُلُوبِهِمُۥ أَكِنَّةً أَن يَفۡقَهُوهُۥ وَفِي ءَاذَانِهِمُۥ وَقۡرٗاۚ وَإِذَا ذَكَرۡتَ رَبَّكَ فِي ٱلۡقُرَانِ وَحۡدَهُۥ وَلَّوۡاْ عَلَىٰ أَدۡبَٰرِهِمُۥ نُفُورٗا ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2081,15,'286',17,'Al-Isrā’','الإسرَاء',12,13,47,'نَّحۡنُ أَعۡلَمُ بِمَا يَسۡتَمِعُونَ بِهِۦ إِذۡ يَسۡتَمِعُونَ إِلَيۡكَ وَإِذۡ هُمُۥ نَجۡوَىٰ إِذۡ يَقُولُ ٱلظَّٰلِمُونَ إِن تَتَّبِعُونَ إِلَّا رَجُلٗا مَّسۡحُورًا ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2082,15,'286',17,'Al-Isrā’','الإسرَاء',13,14,48,'ٱنظُرۡ كَيۡفَ ضَرَبُواْ لَكَ ٱلۡأَمۡثَالَ فَضَلُّواْ فَلَا يَسۡتَطِيعُونَ سَبِيلٗا ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2083,15,'286',17,'Al-Isrā’','الإسرَاء',15,15,49,'وَقَالُواْ أَ۟ذَا كُنَّا عِظَٰمٗا وَرُفَٰتًا أَ۟نَّا لَمَبۡعُوثُونَ خَلۡقٗا جَدِيدٗا ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2084,15,'287',17,'Al-Isrā’','الإسرَاء',1,1,50,'۞ قُلۡ كُونُواْ حِجَارَةً أَوۡ حَدِيدًا ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2085,15,'287',17,'Al-Isrā’','الإسرَاء',1,4,51,'أَوۡ خَلۡقٗا مِّمَّا يَكۡبُرُ فِي صُدُورِكُمُۥۚ فَسَيَقُولُونَ مَن يُعِيدُنَاۖ قُلِ ٱلَّذِي فَطَرَكُمُۥ أَوَّلَ مَرَّةٖۚ فَسَيُنۡغِضُونَ إِلَيۡكَ رُءُوسَهُمُۥ وَيَقُولُونَ مَتَىٰ هُوَۖ قُلۡ عَسَىٰ أَن يَكُونَ قَرِيبٗا ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2086,15,'287',17,'Al-Isrā’','الإسرَاء',4,5,52,'يَوۡمَ يَدۡعُوكُمُۥ فَتَسۡتَجِيبُونَ بِحَمۡدِهِۦ وَتَظُنُّونَ إِن لَّبِثۡتُمُۥ إِلَّا قَلِيلٗا ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2087,15,'287',17,'Al-Isrā’','الإسرَاء',5,7,53,'وَقُل لِّعِبَادِي يَقُولُواْ ٱلَّتِي هِيَ أَحۡسَنُۚ إِنَّ ٱلشَّيۡطَٰنَ يَنزَغُ بَيۡنَهُمُۥۚ إِنَّ ٱلشَّيۡطَٰنَ كَانَ لِلۡإِنسَٰنِ عَدُوّٗا مُّبِينٗا ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2088,15,'287',17,'Al-Isrā’','الإسرَاء',7,8,54,'رَّبُّكُمُۥ أَعۡلَمُ بِكُمُۥۖ إِن يَشَأۡ يَرۡحَمۡكُمُۥ أَوۡ إِن يَشَأۡ يُعَذِّبۡكُمُۥۚ وَمَا أَرۡسَلۡنَٰكَ عَلَيۡهِمُۥ وَكِيلٗا ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2089,15,'287',17,'Al-Isrā’','الإسرَاء',8,10,55,'وَرَبُّكَ أَعۡلَمُ بِمَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۗ وَلَقَدۡ فَضَّلۡنَا بَعۡضَ ٱلنَّبِيِّـۧنَ عَلَىٰ بَعۡضٖۖ وَءَاتَيۡنَا دَاوُۥدَ زَبُورٗا ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2090,15,'287',17,'Al-Isrā’','الإسرَاء',10,11,56,'قُلُ ٱدۡعُواْ ٱلَّذِينَ زَعَمۡتُمُۥ مِن دُونِهِۦ فَلَا يَمۡلِكُونَ كَشۡفَ ٱلضُّرِّ عَنكُمُۥ وَلَا تَحۡوِيلًا ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2091,15,'287',17,'Al-Isrā’','الإسرَاء',11,14,57,'أُوْلَٰٓئِكَ ٱلَّذِينَ يَدۡعُونَ يَبۡتَغُونَ إِلَىٰ رَبِّهِمُ ٱلۡوَسِيلَةَ أَيُّهُمُۥ أَقۡرَبُ وَيَرۡجُونَ رَحۡمَتَهُۥ وَيَخَافُونَ عَذَابَهُۥۚ إِنَّ عَذَابَ رَبِّكَ كَانَ مَحۡذُورٗا ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2092,15,'287',17,'Al-Isrā’','الإسرَاء',14,15,58,'وَإِن مِّن قَرۡيَةٍ إِلَّا نَحۡنُ مُهۡلِكُوهَا قَبۡلَ يَوۡمِ ٱلۡقِيَٰمَةِ أَوۡ مُعَذِّبُوهَا عَذَابٗا شَدِيدٗاۚ كَانَ ذَٰلِكَ فِي ٱلۡكِتَٰبِ مَسۡطُورٗا ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2093,15,'288',17,'Al-Isrā’','الإسرَاء',1,3,59,'وَمَا مَنَعَنَا أَن نُّرۡسِلَ بِٱلۡأٓيَٰتِ إِلَّا أَن كَذَّبَ بِهَا ٱلۡأَوَّلُونَۚ وَءَاتَيۡنَا ثَمُودَ ٱلنَّاقَةَ مُبۡصِرَةٗ فَظَلَمُواْ بِهَاۚ وَمَا نُرۡسِلُ بِٱلۡأٓيَٰتِ إِلَّا تَخۡوِيفٗا ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2094,15,'288',17,'Al-Isrā’','الإسرَاء',3,5,60,'وَإِذۡ قُلۡنَا لَكَ إِنَّ رَبَّكَ أَحَاطَ بِٱلنَّاسِۚ وَمَا جَعَلۡنَا ٱلرُّءۡيَا ٱلَّتِي أَرَيۡنَٰكَ إِلَّا فِتۡنَةٗ لِّلنَّاسِ وَٱلشَّجَرَةَ ٱلۡمَلۡعُونَةَ فِي ٱلۡقُرَانِۚ وَنُخَوِّفُهُمُۥ فَمَا يَزِيدُهُمُۥ إِلَّا طُغۡيَٰنٗا كَبِيرٗا ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2095,15,'288',17,'Al-Isrā’','الإسرَاء',6,7,61,'وَإِذۡ قُلۡنَا لِلۡمَلَٰٓئِكَةِ ٱسۡجُدُواْ لِأٓدَمَ فَسَجَدُواْ إِلَّا إِبۡلِيسَ قَالَ ءَا۬سۡجُدُ لِمَنۡ خَلَقۡتَ طِينٗا ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2096,15,'288',17,'Al-Isrā’','الإسرَاء',7,9,62,'قَالَ أَرَءَيۡتَكَ هَٰذَا ٱلَّذِي كَرَّمۡتَ عَلَيَّ لَئِنۡ أَخَّرۡتَنِۦ إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ لَأَحۡتَنِكَنَّ ذُرِّيَّتَهُۥ إِلَّا قَلِيلٗا ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2097,15,'288',17,'Al-Isrā’','الإسرَاء',9,10,63,'قَالَ ٱذۡهَبۡ فَمَن تَبِعَكَ مِنۡهُمُۥ فَإِنَّ جَهَنَّمَ جَزَآؤُكُمُۥ جَزَآءٗ مَّوۡفُورٗا ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2098,15,'288',17,'Al-Isrā’','الإسرَاء',10,13,64,'وَٱسۡتَفۡزِزۡ مَنِ ٱسۡتَطَعۡتَ مِنۡهُمُۥ بِصَوۡتِكَ وَأَجۡلِبۡ عَلَيۡهِمُۥ بِخَيۡلِكَ وَرَجۡلِكَ وَشَارِكۡهُمُۥ فِي ٱلۡأَمۡوَٰلِ وَٱلۡأَوۡلَٰدِ وَعِدۡهُمُۥۚ وَمَا يَعِدُهُمُ ٱلشَّيۡطَٰنُ إِلَّا غُرُورًا ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2099,15,'288',17,'Al-Isrā’','الإسرَاء',13,14,65,'إِنَّ عِبَادِي لَيۡسَ لَكَ عَلَيۡهِمُۥ سُلۡطَٰنٞۚ وَكَفَىٰ بِرَبِّكَ وَكِيلٗا ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2100,15,'288',17,'Al-Isrā’','الإسرَاء',14,15,66,'رَّبُّكُمُ ٱلَّذِي يُزۡجِي لَكُمُ ٱلۡفُلۡكَ فِي ٱلۡبَحۡرِ لِتَبۡتَغُواْ مِن فَضۡلِهِۦۚ إِنَّهُۥ كَانَ بِكُمُۥ رَحِيمٗا ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2101,15,'289',17,'Al-Isrā’','الإسرَاء',1,2,67,'وَإِذَا مَسَّكُمُ ٱلضُّرُّ فِي ٱلۡبَحۡرِ ضَلَّ مَن تَدۡعُونَ إِلَّا إِيَّاهُۥۖ فَلَمَّا نَجَّىٰكُمُۥ إِلَى ٱلۡبَرِّ أَعۡرَضۡتُمُۥۚ وَكَانَ ٱلۡإِنسَٰنُ كَفُورًا ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2102,15,'289',17,'Al-Isrā’','الإسرَاء',2,4,68,'أَفَأَمِنتُمُۥ أَن نَّخۡسِفَ بِكُمُۥ جَانِبَ ٱلۡبَرِّ أَوۡ نُرۡسِلَ عَلَيۡكُمُۥ حَاصِبٗا ثُمَّ لَا تَجِدُواْ لَكُمُۥ وَكِيلًا ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2103,15,'289',17,'Al-Isrā’','الإسرَاء',4,6,69,'أَمۡ أَمِنتُمُۥ أَن نُّعِيدَكُمُۥ فِيهِۦ تَارَةً أُخۡرَىٰ فَنُرۡسِلَ عَلَيۡكُمُۥ قَاصِفٗا مِّنَ ٱلرِّيحِ فَنُغۡرِقَكُمُۥ بِمَا كَفَرۡتُمُۥ ثُمَّ لَا تَجِدُواْ لَكُمُۥ عَلَيۡنَا بِهِۦ تَبِيعٗا ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2104,15,'289',17,'Al-Isrā’','الإسرَاء',6,8,70,'۞ وَلَقَدۡ كَرَّمۡنَا بَنِي ءَادَمَ وَحَمَلۡنَٰهُمُۥ فِي ٱلۡبَرِّ وَٱلۡبَحۡرِ وَرَزَقۡنَٰهُمُۥ مِنَ ٱلطَّيِّبَٰتِ وَفَضَّلۡنَٰهُمُۥ عَلَىٰ كَثِيرٖ مِّمَّنۡ خَلَقۡنَا تَفۡضِيلٗا ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2105,15,'289',17,'Al-Isrā’','الإسرَاء',8,10,71,'يَوۡمَ نَدۡعُواْ كُلَّ أُنَاسِۭ بِإِمَٰمِهِمُۥۖ فَمَنۡ أُوتِيَ كِتَٰبَهُۥ بِيَمِينِهِۦ فَأُوْلَٰٓئِكَ يَقۡرَءُونَ كِتَٰبَهُمُۥ وَلَا يُظۡلَمُونَ فَتِيلٗا ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2106,15,'289',17,'Al-Isrā’','الإسرَاء',10,11,72,'وَمَن كَانَ فِي هَٰذِهِۦ أَعۡمَىٰ فَهُوَ فِي ٱلۡأٓخِرَةِ أَعۡمَىٰ وَأَضَلُّ سَبِيلٗا ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2107,15,'289',17,'Al-Isrā’','الإسرَاء',11,13,73,'وَإِن كَادُواْ لَيَفۡتِنُونَكَ عَنِ ٱلَّذِي أَوۡحَيۡنَا إِلَيۡكَ لِتَفۡتَرِيَ عَلَيۡنَا غَيۡرَهُۥۖ وَإِذٗا لَّٱتَّخَذُوكَ خَلِيلٗا ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2108,15,'289',17,'Al-Isrā’','الإسرَاء',13,14,74,'وَلَوۡلَا أَن ثَبَّتۡنَٰكَ لَقَدۡ كِدتَّ تَرۡكَنُ إِلَيۡهِمُۥ شَيۡـٔٗا قَلِيلًا ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2109,15,'289',17,'Al-Isrā’','الإسرَاء',14,15,75,'إِذٗا لَّأَذَقۡنَٰكَ ضِعۡفَ ٱلۡحَيَوٰةِ وَضِعۡفَ ٱلۡمَمَاتِ ثُمَّ لَا تَجِدُ لَكَ عَلَيۡنَا نَصِيرٗا ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2110,15,'290',17,'Al-Isrā’','الإسرَاء',1,2,76,'وَإِن كَادُواْ لَيَسۡتَفِزُّونَكَ مِنَ ٱلۡأَرۡضِ لِيُخۡرِجُوكَ مِنۡهَاۖ وَإِذٗا لَّا يَلۡبَثُونَ خَلۡفَكَ إِلَّا قَلِيلٗا ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2111,15,'290',17,'Al-Isrā’','الإسرَاء',2,3,77,'سُنَّةَ مَن قَدۡ أَرۡسَلۡنَا قَبۡلَكَ مِن رُّسُلِنَاۖ وَلَا تَجِدُ لِسُنَّتِنَا تَحۡوِيلًا ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2112,15,'290',17,'Al-Isrā’','الإسرَاء',3,5,78,'أَقِمِ ٱلصَّلَوٰةَ لِدُلُوكِ ٱلشَّمۡسِ إِلَىٰ غَسَقِ ٱلَّيۡلِ وَقُرَانَ ٱلۡفَجۡرِۖ إِنَّ قُرَانَ ٱلۡفَجۡرِ كَانَ مَشۡهُودٗا ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2113,15,'290',17,'Al-Isrā’','الإسرَاء',5,6,79,'وَمِنَ ٱلَّيۡلِ فَتَهَجَّدۡ بِهِۦ نَافِلَةٗ لَّكَ عَسَىٰ أَن يَبۡعَثَكَ رَبُّكَ مَقَامٗا مَّحۡمُودٗا ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2114,15,'290',17,'Al-Isrā’','الإسرَاء',7,8,80,'وَقُل رَّبِّ أَدۡخِلۡنِي مُدۡخَلَ صِدۡقٖ وَأَخۡرِجۡنِي مُخۡرَجَ صِدۡقٖ وَٱجۡعَل لِّي مِن لَّدُنكَ سُلۡطَٰنٗا نَّصِيرٗا ٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2115,15,'290',17,'Al-Isrā’','الإسرَاء',8,9,81,'وَقُلۡ جَآءَ ٱلۡحَقُّ وَزَهَقَ ٱلۡبَٰطِلُۚ إِنَّ ٱلۡبَٰطِلَ كَانَ زَهُوقٗا ٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2116,15,'290',17,'Al-Isrā’','الإسرَاء',9,10,82,'وَنُنَزِّلُ مِنَ ٱلۡقُرَانِ مَا هُوَ شِفَآءٞ وَرَحۡمَةٞ لِّلۡمُؤۡمِنِينَ وَلَا يَزِيدُ ٱلظَّٰلِمِينَ إِلَّا خَسَارٗا ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2117,15,'290',17,'Al-Isrā’','الإسرَاء',11,12,83,'وَإِذَا أَنۡعَمۡنَا عَلَى ٱلۡإِنسَٰنِ أَعۡرَضَ وَنَـَٔا بِجَانِبِهِۦ وَإِذَا مَسَّهُ ٱلشَّرُّ كَانَ يَـُٔوسٗا ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2118,15,'290',17,'Al-Isrā’','الإسرَاء',12,13,84,'قُلۡ كُلّٞ يَعۡمَلُ عَلَىٰ شَاكِلَتِهِۦ فَرَبُّكُمُۥ أَعۡلَمُ بِمَنۡ هُوَ أَهۡدَىٰ سَبِيلٗا ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2119,15,'290',17,'Al-Isrā’','الإسرَاء',13,14,85,'وَيَسۡـَٔلُونَكَ عَنِ ٱلرُّوحِۖ قُلِ ٱلرُّوحُ مِنۡ أَمۡرِ رَبِّي وَمَا أُوتِيتُمُۥ مِنَ ٱلۡعِلۡمِ إِلَّا قَلِيلٗا ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2120,15,'290',17,'Al-Isrā’','الإسرَاء',14,15,86,'وَلَئِن شِئۡنَا لَنَذۡهَبَنَّ بِٱلَّذِي أَوۡحَيۡنَا إِلَيۡكَ ثُمَّ لَا تَجِدُ لَكَ بِهِۦ عَلَيۡنَا وَكِيلًا ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2121,15,'291',17,'Al-Isrā’','الإسرَاء',1,1,87,'إِلَّا رَحۡمَةٗ مِّن رَّبِّكَۚ إِنَّ فَضۡلَهُۥ كَانَ عَلَيۡكَ كَبِيرٗا ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2122,15,'291',17,'Al-Isrā’','الإسرَاء',1,3,88,'قُل لَّئِنِ ٱجۡتَمَعَتِ ٱلۡإِنسُ وَٱلۡجِنُّ عَلَىٰ أَن يَأۡتُواْ بِمِثۡلِ هَٰذَا ٱلۡقُرَانِ لَا يَأۡتُونَ بِمِثۡلِهِۦ وَلَوۡ كَانَ بَعۡضُهُمُۥ لِبَعۡضٖ ظَهِيرٗا ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2123,15,'291',17,'Al-Isrā’','الإسرَاء',4,5,89,'وَلَقَدۡ صَرَّفۡنَا لِلنَّاسِ فِي هَٰذَا ٱلۡقُرَانِ مِن كُلِّ مَثَلٖ فَأَبَىٰ أَكۡثَرُ ٱلنَّاسِ إِلَّا كُفُورٗا ٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2124,15,'291',17,'Al-Isrā’','الإسرَاء',5,6,90,'وَقَالُواْ لَن نُّؤۡمِنَ لَكَ حَتَّىٰ تُفَجِّرَ لَنَا مِنَ ٱلۡأَرۡضِ يَنۢبُوعًا ٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2125,15,'291',17,'Al-Isrā’','الإسرَاء',6,7,91,'أَوۡ تَكُونَ لَكَ جَنَّةٞ مِّن نَّخِيلٖ وَعِنَبٖ فَتُفَجِّرَ ٱلۡأَنۡهَٰرَ خِلَٰلَهَا تَفۡجِيرًا ٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2126,15,'291',17,'Al-Isrā’','الإسرَاء',7,9,92,'أَوۡ تُسۡقِطَ ٱلسَّمَآءَ كَمَا زَعَمۡتَ عَلَيۡنَا كِسۡفًا أَوۡ تَأۡتِيَ بِٱللَّهِ وَٱلۡمَلَٰٓئِكَةِ قَبِيلًا ٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2127,15,'291',17,'Al-Isrā’','الإسرَاء',9,11,93,'أَوۡ يَكُونَ لَكَ بَيۡتٞ مِّن زُخۡرُفٍ أَوۡ تَرۡقَىٰ فِي ٱلسَّمَآءِ وَلَن نُّؤۡمِنَ لِرُقِيِّكَ حَتَّىٰ تُنَزِّلَ عَلَيۡنَا كِتَٰبٗا نَّقۡرَؤُهُۥۗ قَالَ سُبۡحَانَ رَبِّي هَلۡ كُنتُ إِلَّا بَشَرٗا رَّسُولٗا ٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2128,15,'291',17,'Al-Isrā’','الإسرَاء',11,13,94,'وَمَا مَنَعَ ٱلنَّاسَ أَن يُؤۡمِنُواْ إِذۡ جَآءَهُمُ ٱلۡهُدَىٰ إِلَّا أَن قَالُواْ أَبَعَثَ ٱللَّهُ بَشَرٗا رَّسُولٗا ٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2129,15,'291',17,'Al-Isrā’','الإسرَاء',13,14,95,'قُل لَّوۡ كَانَ فِي ٱلۡأَرۡضِ مَلَٰٓئِكَةٞ يَمۡشُونَ مُطۡمَئِنِّينَ لَنَزَّلۡنَا عَلَيۡهِمُۥ مِنَ ٱلسَّمَآءِ مَلَكٗا رَّسُولٗا ٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2130,15,'291',17,'Al-Isrā’','الإسرَاء',14,15,96,'قُلۡ كَفَىٰ بِٱللَّهِ شَهِيدَۢا بَيۡنِي وَبَيۡنَكُمُۥۚ إِنَّهُۥ كَانَ بِعِبَادِهِۦ خَبِيرَۢا بَصِيرٗا ٩٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2131,15,'292',17,'Al-Isrā’','الإسرَاء',1,3,97,'وَمَن يَهۡدِ ٱللَّهُ فَهُوَ ٱلۡمُهۡتَدِۖ وَمَن يُضۡلِلۡ فَلَن تَجِدَ لَهُمُۥ أَوۡلِيَآءَ مِن دُونِهِۦۖ وَنَحۡشُرُهُمُۥ يَوۡمَ ٱلۡقِيَٰمَةِ عَلَىٰ وُجُوهِهِمُۥ عُمۡيٗا وَبُكۡمٗا وَصُمّٗاۖ مَّأۡوَىٰهُمُۥ جَهَنَّمُۖ كُلَّمَا خَبَتۡ زِدۡنَٰهُمُۥ سَعِيرٗا ٩٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2132,15,'292',17,'Al-Isrā’','الإسرَاء',4,5,98,'ذَٰلِكَ جَزَآؤُهُمُۥ بِأَنَّهُمُۥ كَفَرُواْ بِـَٔايَٰتِنَا وَقَالُواْ أَ۟ذَا كُنَّا عِظَٰمٗا وَرُفَٰتًا أَ۟نَّا لَمَبۡعُوثُونَ خَلۡقٗا جَدِيدًا ٩٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2133,15,'292',17,'Al-Isrā’','الإسرَاء',5,7,99,'۞ أَوَلَمۡ يَرَوۡاْ أَنَّ ٱللَّهَ ٱلَّذِي خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ قَادِرٌ عَلَىٰ أَن يَخۡلُقَ مِثۡلَهُمُۥ وَجَعَلَ لَهُمُۥ أَجَلٗا لَّا رَيۡبَ فِيهِۦ فَأَبَى ٱلظَّٰلِمُونَ إِلَّا كُفُورٗا ٩٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2134,15,'292',17,'Al-Isrā’','الإسرَاء',8,9,100,'قُل لَّوۡ أَنتُمُۥ تَمۡلِكُونَ خَزَآئِنَ رَحۡمَةِ رَبِّي إِذٗا لَّأَمۡسَكۡتُمُۥ خَشۡيَةَ ٱلۡإِنفَاقِۚ وَكَانَ ٱلۡإِنسَٰنُ قَتُورٗا ١٠٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2135,15,'292',17,'Al-Isrā’','الإسرَاء',9,11,101,'وَلَقَدۡ ءَاتَيۡنَا مُوسَىٰ تِسۡعَ ءَايَٰتِۭ بَيِّنَٰتٖۖ فَسَلۡ بَنِي إِسۡرَٰٓءِيلَ إِذۡ جَآءَهُمُۥ فَقَالَ لَهُۥ فِرۡعَوۡنُ إِنِّي لَأَظُنُّكَ يَٰمُوسَىٰ مَسۡحُورٗا ١٠١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2136,15,'292',17,'Al-Isrā’','الإسرَاء',11,13,102,'قَالَ لَقَدۡ عَلِمۡتَ مَا أَنزَلَ هَٰؤُلَآءِ ا۪لَّا رَبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ بَصَآئِرَ وَإِنِّي لَأَظُنُّكَ يَٰفِرۡعَوۡنُ مَثۡبُورٗا ١٠٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2137,15,'292',17,'Al-Isrā’','الإسرَاء',13,14,103,'فَأَرَادَ أَن يَسۡتَفِزَّهُمُۥ مِنَ ٱلۡأَرۡضِ فَأَغۡرَقۡنَٰهُۥ وَمَن مَّعَهُۥ جَمِيعٗا ١٠٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2138,15,'292',17,'Al-Isrā’','الإسرَاء',14,15,104,'وَقُلۡنَا مِنۢ بَعۡدِهِۦ لِبَنِي إِسۡرَٰٓءِيلَ ٱسۡكُنُواْ ٱلۡأَرۡضَ فَإِذَا جَآءَ وَعۡدُ ٱلۡأٓخِرَةِ جِئۡنَا بِكُمُۥ لَفِيفٗا ١٠٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2139,15,'293',17,'Al-Isrā’','الإسرَاء',1,1,105,'وَبِٱلۡحَقِّ أَنزَلۡنَٰهُۥ وَبِٱلۡحَقِّ نَزَلَۗ وَمَا أَرۡسَلۡنَٰكَ إِلَّا مُبَشِّرٗا وَنَذِيرٗا ١٠٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2140,15,'293',17,'Al-Isrā’','الإسرَاء',2,2,106,'وَقُرَانٗا فَرَقۡنَٰهُۥ لِتَقۡرَأَهُۥ عَلَى ٱلنَّاسِ عَلَىٰ مُكۡثٖ وَنَزَّلۡنَٰهُۥ تَنزِيلٗا ١٠٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2141,15,'293',17,'Al-Isrā’','الإسرَاء',3,5,107,'قُلۡ ءَامِنُواْ بِهِۦ أَوۡ لَا تُؤۡمِنُواْۚ إِنَّ ٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ مِن قَبۡلِهِۦ إِذَا يُتۡلَىٰ عَلَيۡهِمُۥ يَخِرُّونَۤ لِلۡأَذۡقَانِۤ سُجَّدٗاۤ وَيَقُولُونَ سُبۡحَٰنَ رَبِّنَا إِن كَانَ وَعۡدُ رَبِّنَا لَمَفۡعُولٗا ١٠٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2142,15,'293',17,'Al-Isrā’','الإسرَاء',5,6,108,'وَيَخِرُّونَ لِلۡأَذۡقَانِ يَبۡكُونَ وَيَزِيدُهُمُۥ خُشُوعٗا۩ ١٠٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2143,15,'293',17,'Al-Isrā’','الإسرَاء',6,8,109,'قُلُ ٱدۡعُواْ ٱللَّهَ أَوُ ٱدۡعُواْ ٱلرَّحۡمَٰنَۖ أَيّٗا مَّا تَدۡعُواْ فَلَهُ ٱلۡأَسۡمَآءُ ٱلۡحُسۡنَىٰۚ وَلَا تَجۡهَرۡ بِصَلَاتِكَ وَلَا تُخَافِتۡ بِهَا وَٱبۡتَغِ بَيۡنَ ذَٰلِكَ سَبِيلٗا ١٠٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2144,15,'293',17,'Al-Isrā’','الإسرَاء',8,9,110,'وَقُلِ ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي لَمۡ يَتَّخِذۡ وَلَدٗا وَلَمۡ يَكُن لَّهُۥ شَرِيكٞ فِي ٱلۡمُلۡكِ وَلَمۡ يَكُن لَّهُۥ وَلِيّٞ مِّنَ ٱلذُّلِّۖ وَكَبِّرۡهُۥ تَكۡبِيرَۢا ١١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2145,15,'293',18,'Al-Kahf','الكَهف',12,12,1,'ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي أَنزَلَ عَلَىٰ عَبۡدِهِ ٱلۡكِتَٰبَ وَلَمۡ يَجۡعَل لَّهُۥ عِوَجٗا ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2146,15,'293',18,'Al-Kahf','الكَهف',13,14,2,'قَيِّمٗا لِّيُنذِرَ بَأۡسٗا شَدِيدٗا مِّن لَّدُنۡهُۥ وَيُبَشِّرَ ٱلۡمُؤۡمِنِينَ ٱلَّذِينَ يَعۡمَلُونَ ٱلصَّٰلِحَٰتِ أَنَّ لَهُمُۥ أَجۡرًا حَسَنٗا ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2147,15,'293',18,'Al-Kahf','الكَهف',15,15,3,'مَّٰكِثِينَ فِيهِۦ أَبَدٗا ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2148,15,'293',18,'Al-Kahf','الكَهف',15,15,4,'وَيُنذِرَ ٱلَّذِينَ قَالُواْ ٱتَّخَذَ ٱللَّهُ وَلَدٗا ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2149,15,'294',18,'Al-Kahf','الكَهف',1,2,5,'مَّا لَهُمُۥ بِهِۦ مِنۡ عِلۡمٖ وَلَا لِأٓبَآئِهِمُۥۚ كَبُرَتۡ كَلِمَةٗ تَخۡرُجُ مِنۡ أَفۡوَٰهِهِمُۥۚ إِن يَقُولُونَ إِلَّا كَذِبٗا ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2150,15,'294',18,'Al-Kahf','الكَهف',2,3,6,'فَلَعَلَّكَ بَٰخِعٞ نَّفۡسَكَ عَلَىٰ ءَاثَٰرِهِمُۥ إِن لَّمۡ يُؤۡمِنُواْ بِهَٰذَا ٱلۡحَدِيثِ أَسَفًا ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2151,15,'294',18,'Al-Kahf','الكَهف',3,4,7,'إِنَّا جَعَلۡنَا مَا عَلَى ٱلۡأَرۡضِ زِينَةٗ لَّهَا لِنَبۡلُوَهُمُۥ أَيُّهُمُۥ أَحۡسَنُ عَمَلٗا ٧');
