INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2153,15,'294',18,'Al-Kahf','الكَهف',5,6,9,'أَمۡ حَسِبۡتَ أَنَّ أَصۡحَٰبَ ٱلۡكَهۡفِ وَٱلرَّقِيمِ كَانُواْ مِنۡ ءَايَٰتِنَا عَجَبًا ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2154,15,'294',18,'Al-Kahf','الكَهف',7,8,10,'إِذۡ أَوَى ٱلۡفِتۡيَةُ إِلَى ٱلۡكَهۡفِ فَقَالُواْ رَبَّنَا ءَاتِنَا مِن لَّدُنكَ رَحۡمَةٗ وَهَيِّئۡ لَنَا مِنۡ أَمۡرِنَا رَشَدٗا ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2155,15,'294',18,'Al-Kahf','الكَهف',8,9,11,'فَضَرَبۡنَا عَلَىٰ ءَاذَانِهِمُۥ فِي ٱلۡكَهۡفِ سِنِينَ عَدَدٗا ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2156,15,'294',18,'Al-Kahf','الكَهف',9,10,12,'ثُمَّ بَعَثۡنَٰهُمُۥ لِنَعۡلَمَ أَيُّ ٱلۡحِزۡبَيۡنِ أَحۡصَىٰ لِمَا لَبِثُواْ أَمَدٗا ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2157,15,'294',18,'Al-Kahf','الكَهف',10,11,13,'نَّحۡنُ نَقُصُّ عَلَيۡكَ نَبَأَهُمُۥ بِٱلۡحَقِّۚ إِنَّهُمُۥ فِتۡيَةٌ ءَامَنُواْ بِرَبِّهِمُۥ وَزِدۡنَٰهُمُۥ هُدٗى ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2158,15,'294',18,'Al-Kahf','الكَهف',12,13,14,'وَرَبَطۡنَا عَلَىٰ قُلُوبِهِمُۥ إِذۡ قَامُواْ فَقَالُواْ رَبُّنَا رَبُّ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ لَن نَّدۡعُوَاْ مِن دُونِهِۦ إِلَٰهٗاۖ لَّقَدۡ قُلۡنَا إِذٗا شَطَطًا ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2159,15,'294',18,'Al-Kahf','الكَهف',14,15,15,'هَٰؤُلَآءِ قَوۡمُنَا ٱتَّخَذُواْ مِن دُونِهِۦ ءَالِهَةٗۖ لَّوۡلَا يَأۡتُونَ عَلَيۡهِمُۥ بِسُلۡطَٰنِۭ بَيِّنٖۖ فَمَنۡ أَظۡلَمُ مِمَّنِ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبٗا ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2160,15,'295',18,'Al-Kahf','الكَهف',1,2,16,'وَإِذِ ٱعۡتَزَلۡتُمُوهُمُۥ وَمَا يَعۡبُدُونَ إِلَّا ٱللَّهَ فَأۡوُۥاْ إِلَى ٱلۡكَهۡفِ يَنشُرۡ لَكُمُۥ رَبُّكُمُۥ مِن رَّحۡمَتِهِۦ وَيُهَيِّئۡ لَكُمُۥ مِنۡ أَمۡرِكُمُۥ مِرۡفَقٗا ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2161,15,'295',18,'Al-Kahf','الكَهف',3,6,17,'۞ وَتَرَى ٱلشَّمۡسَ إِذَا طَلَعَت تَّزَّٰوَرُ عَن كَهۡفِهِمُۥ ذَاتَ ٱلۡيَمِينِ وَإِذَا غَرَبَت تَّقۡرِضُهُمُۥ ذَاتَ ٱلشِّمَالِ وَهُمُۥ فِي فَجۡوَةٖ مِّنۡهُۥۚ ذَٰلِكَ مِنۡ ءَايَٰتِ ٱللَّهِۗ مَن يَهۡدِ ٱللَّهُ فَهُوَ ٱلۡمُهۡتَدِۖ وَمَن يُضۡلِلۡ فَلَن تَجِدَ لَهُۥ وَلِيّٗا مُّرۡشِدٗا ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2162,15,'295',18,'Al-Kahf','الكَهف',6,9,18,'وَتَحۡسِبُهُمُۥ أَيۡقَاظٗا وَهُمُۥ رُقُودٞۚ وَنُقَلِّبُهُمُۥ ذَاتَ ٱلۡيَمِينِ وَذَاتَ ٱلشِّمَالِۖ وَكَلۡبُهُمُۥ بَٰسِطٞ ذِرَاعَيۡهِۦ بِٱلۡوَصِيدِۚ لَوِ ٱطَّلَعۡتَ عَلَيۡهِمُۥ لَوَلَّيۡتَ مِنۡهُمُۥ فِرَارٗا وَلَمُلِّئۡتَ مِنۡهُمُۥ رُعۡبٗا ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2163,15,'295',18,'Al-Kahf','الكَهف',9,14,19,'وَكَذَٰلِكَ بَعَثۡنَٰهُمُۥ لِيَتَسَآءَلُواْ بَيۡنَهُمُۥۚ قَالَ قَآئِلٞ مِّنۡهُمُۥ كَمۡ لَبِثۡتُمُۥۖ قَالُواْ لَبِثۡنَا يَوۡمًا أَوۡ بَعۡضَ يَوۡمٖۚ قَالُواْ رَبُّكُمُۥ أَعۡلَمُ بِمَا لَبِثۡتُمُۥ فَٱبۡعَثُواْ أَحَدَكُمُۥ بِوَرِقِكُمُۥ هَٰذِهِۦ إِلَى ٱلۡمَدِينَةِ فَلۡيَنظُرۡ أَيُّهَا أَزۡكَىٰ طَعَامٗا فَلۡيَأۡتِكُمُۥ بِرِزۡقٖ مِّنۡهُۥ وَلۡيَتَلَطَّفۡ وَلَا يُشۡعِرَنَّ بِكُمُۥ أَحَدًا ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2164,15,'295',18,'Al-Kahf','الكَهف',14,15,20,'إِنَّهُمُۥ إِن يَظۡهَرُواْ عَلَيۡكُمُۥ يَرۡجُمُوكُمُۥ أَوۡ يُعِيدُوكُمُۥ فِي مِلَّتِهِمُۥ وَلَن تُفۡلِحُواْ إِذًا أَبَدٗا ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2165,15,'296',18,'Al-Kahf','الكَهف',1,4,21,'وَكَذَٰلِكَ أَعۡثَرۡنَا عَلَيۡهِمُۥ لِيَعۡلَمُواْ أَنَّ وَعۡدَ ٱللَّهِ حَقّٞ وَأَنَّ ٱلسَّاعَةَ لَا رَيۡبَ فِيهَا إِذۡ يَتَنَٰزَعُونَ بَيۡنَهُمُۥ أَمۡرَهُمُۥۖ فَقَالُواْ ٱبۡنُواْ عَلَيۡهِمُۥ بُنۡيَٰنٗاۖ رَّبُّهُمُۥ أَعۡلَمُ بِهِمُۥۚ قَالَ ٱلَّذِينَ غَلَبُواْ عَلَىٰ أَمۡرِهِمُۥ لَنَتَّخِذَنَّ عَلَيۡهِمُۥ مَسۡجِدٗا ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2166,15,'296',18,'Al-Kahf','الكَهف',4,8,22,'سَيَقُولُونَ ثَلَٰثَةٞ رَّابِعُهُمُۥ كَلۡبُهُمُۥ وَيَقُولُونَ خَمۡسَةٞ سَادِسُهُمُۥ كَلۡبُهُمُۥ رَجۡمَۢا بِٱلۡغَيۡبِۖ وَيَقُولُونَ سَبۡعَةٞ وَثَامِنُهُمُۥ كَلۡبُهُمُۥۚ قُل رَّبِّيَ أَعۡلَمُ بِعِدَّتِهِمُۥ مَا يَعۡلَمُهُمُۥ إِلَّا قَلِيلٞۗ فَلَا تُمَارِ فِيهِمُۥ إِلَّا مِرَآءٗ ظَٰهِرٗا وَلَا تَسۡتَفۡتِ فِيهِمُۥ مِنۡهُمُۥ أَحَدٗا ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2167,15,'296',18,'Al-Kahf','الكَهف',8,9,23,'وَلَا تَقُولَنَّ لِشَاْيۡءٍ إِنِّي فَاعِلٞ ذَٰلِكَ غَدًا ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2168,15,'296',18,'Al-Kahf','الكَهف',9,10,24,'إِلَّا أَن يَشَآءَ ٱللَّهُۚ وَٱذۡكُر رَّبَّكَ إِذَا نَسِيتَ وَقُلۡ عَسَىٰ أَن يَهۡدِيَنِۦ رَبِّي لِأَقۡرَبَ مِنۡ هَٰذَا رَشَدٗا ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2169,15,'296',18,'Al-Kahf','الكَهف',11,11,25,'وَلَبِثُواْ فِي كَهۡفِهِمُۥ ثَلَٰثَ مِاْئَةٖ سِنِينَ وَٱزۡدَادُواْ تِسۡعٗا ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2170,15,'296',18,'Al-Kahf','الكَهف',12,14,26,'قُلِ ٱللَّهُ أَعۡلَمُ بِمَا لَبِثُواْۖ لَهُۥ غَيۡبُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ أَبۡصِرۡ بِهِۦ وَأَسۡمِعۡۚ مَا لَهُمُۥ مِن دُونِهِۦ مِن وَلِيّٖ وَلَا يُشۡرِكُ فِي حُكۡمِهِۦ أَحَدٗا ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2171,15,'296',18,'Al-Kahf','الكَهف',14,15,27,'وَٱتۡلُ مَا أُوحِيَ إِلَيۡكَ مِن كِتَابِ رَبِّكَۖ لَا مُبَدِّلَ لِكَلِمَٰتِهِۦ وَلَن تَجِدَ مِن دُونِهِۦ مُلۡتَحَدٗا ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2172,15,'297',18,'Al-Kahf','الكَهف',1,4,28,'وَٱصۡبِرۡ نَفۡسَكَ مَعَ ٱلَّذِينَ يَدۡعُونَ رَبَّهُمُۥ بِٱلۡغَدَوٰةِ وَٱلۡعَشِيِّ يُرِيدُونَ وَجۡهَهُۥۖ وَلَا تَعۡدُ عَيۡنَاكَ عَنۡهُمُۥ تُرِيدُ زِينَةَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ وَلَا تُطِعۡ مَنۡ أَغۡفَلۡنَا قَلۡبَهُۥ عَن ذِكۡرِنَا وَٱتَّبَعَ هَوَىٰهُۥ وَكَانَ أَمۡرُهُۥ فُرُطٗا ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2173,15,'297',18,'Al-Kahf','الكَهف',4,7,29,'وَقُلِ ٱلۡحَقُّ مِن رَّبِّكُمُۥۖ فَمَن شَآءَ فَلۡيُؤۡمِن وَمَن شَآءَ فَلۡيَكۡفُرۡۚ إِنَّا أَعۡتَدۡنَا لِلظَّٰلِمِينَ نَارًا أَحَاطَ بِهِمُۥ سُرَادِقُهَاۚ وَإِن يَسۡتَغِيثُواْ يُغَاثُواْ بِمَآءٖ كَٱلۡمُهۡلِ يَشۡوِي ٱلۡوُجُوهَۚ بِئۡسَ ٱلشَّرَابُ وَسَآءَتۡ مُرۡتَفَقًا ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2174,15,'297',18,'Al-Kahf','الكَهف',7,8,30,'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّٰلِحَٰتِ إِنَّا لَا نُضِيعُ أَجۡرَ مَنۡ أَحۡسَنَ عَمَلًا ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2175,15,'297',18,'Al-Kahf','الكَهف',8,11,31,'أُوْلَٰٓئِكَ لَهُمُۥ جَنَّٰتُ عَدۡنٖ تَجۡرِي مِن تَحۡتِهِمُ ٱلۡأَنۡهَٰرُ يُحَلَّوۡنَ فِيهَا مِنۡ أَسَاوِرَ مِن ذَهَبٖ وَيَلۡبَسُونَ ثِيَابًا خُضۡرٗا مِّن سُندُسٖ وَإِسۡتَبۡرَقٖ مُّتَّكِـِٔينَ فِيهَا عَلَى ٱلۡأَرَآئِكِۚ نِعۡمَ ٱلثَّوَابُ وَحَسُنَتۡ مُرۡتَفَقٗا ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2176,15,'297',18,'Al-Kahf','الكَهف',11,14,32,'۞ وَٱضۡرِبۡ لَهُمُۥ مَثَلٗا رَّجُلَيۡنِ جَعَلۡنَا لِأَحَدِهِمَا جَنَّتَيۡنِ مِنۡ أَعۡنَٰبٖ وَحَفَفۡنَٰهُمَا بِنَخۡلٖ وَجَعَلۡنَا بَيۡنَهُمَا زَرۡعٗاۚ كِلۡتَا ٱلۡجَنَّتَيۡنِ ءَاتَتۡ أُكۡلَهَا وَلَمۡ تَظۡلِم مِّنۡهُۥ شَيۡـٔٗاۚ وَفَجَّرۡنَا خِلَٰلَهُمَا نَهَرٗا ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2177,15,'297',18,'Al-Kahf','الكَهف',14,15,33,'وَكَانَ لَهُۥ ثُمُرٞ فَقَالَ لِصَٰحِبِهِۦ وَهُوَ يُحَاوِرُهُۥ أَنَا۠ أَكۡثَرُ مِنكَ مَالٗا وَأَعَزُّ نَفَرٗا ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2178,15,'298',18,'Al-Kahf','الكَهف',1,2,34,'وَدَخَلَ جَنَّتَهُۥ وَهُوَ ظَالِمٞ لِّنَفۡسِهِۦ قَالَ مَا أَظُنُّ أَن تَبِيدَ هَٰذِهِۦ أَبَدٗا ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2179,15,'298',18,'Al-Kahf','الكَهف',2,3,35,'وَمَا أَظُنُّ ٱلسَّاعَةَ قَآئِمَةٗ وَلَئِن رُّدِدتُّ إِلَىٰ رَبِّي لَأَجِدَنَّ خَيۡرٗا مِّنۡهُمَا مُنقَلَبٗا ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2180,15,'298',18,'Al-Kahf','الكَهف',3,4,36,'قَالَ لَهُۥ صَاحِبُهُۥ وَهُوَ يُحَاوِرُهُۥ أَكَفَرۡتَ بِٱلَّذِي خَلَقَكَ مِن تُرَابٖ ثُمَّ مِن نُّطۡفَةٖ ثُمَّ سَوَّىٰكَ رَجُلٗا ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2181,15,'298',18,'Al-Kahf','الكَهف',5,5,37,'لَّٰكِنَّا۠ هُوَ ٱللَّهُ رَبِّي وَلَا أُشۡرِكُ بِرَبِّيَ أَحَدٗا ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2182,15,'298',18,'Al-Kahf','الكَهف',5,7,38,'وَلَوۡلَا إِذۡ دَخَلۡتَ جَنَّتَكَ قُلۡتَ مَا شَآءَ ٱللَّهُ لَا قُوَّةَ إِلَّا بِٱللَّهِۚ إِن تَرَنِۦ أَنَا۠ أَقَلَّ مِنكَ مَالٗا وَوَلَدٗا ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2183,15,'298',18,'Al-Kahf','الكَهف',7,8,39,'فَعَسَىٰ رَبِّيَ أَن يُؤۡتِيَنِۦ خَيۡرٗا مِّن جَنَّتِكَ وَيُرۡسِلَ عَلَيۡهَا حُسۡبَانٗا مِّنَ ٱلسَّمَآءِ فَتُصۡبِحَ صَعِيدٗا زَلَقًا ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2184,15,'298',18,'Al-Kahf','الكَهف',8,9,40,'أَوۡ يُصۡبِحَ مَآؤُهَا غَوۡرٗا فَلَن تَسۡتَطِيعَ لَهُۥ طَلَبٗا ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2185,15,'298',18,'Al-Kahf','الكَهف',9,11,41,'وَأُحِيطَ بِثُمُرِهِۦ فَأَصۡبَحَ يُقَلِّبُ كَفَّيۡهِۦ عَلَىٰ مَا أَنفَقَ فِيهَا وَهِيَ خَاوِيَةٌ عَلَىٰ عُرُوشِهَا وَيَقُولُ يَٰلَيۡتَنِي لَمۡ أُشۡرِكۡ بِرَبِّيَ أَحَدٗا ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2186,15,'298',18,'Al-Kahf','الكَهف',11,12,42,'وَلَمۡ تَكُن لَّهُۥ فِئَةٞ يَنصُرُونَهُۥ مِن دُونِ ٱللَّهِ وَمَا كَانَ مُنتَصِرًا ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2187,15,'298',18,'Al-Kahf','الكَهف',12,13,43,'هُنَالِكَ ٱلۡوَلَٰيَةُ لِلَّهِ ٱلۡحَقِّۚ هُوَ خَيۡرٞ ثَوَابٗا وَخَيۡرٌ عُقُبٗا ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2188,15,'298',18,'Al-Kahf','الكَهف',13,15,44,'وَٱضۡرِبۡ لَهُمُۥ مَثَلَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا كَمَآءٍ أَنزَلۡنَٰهُۥ مِنَ ٱلسَّمَآءِ فَٱخۡتَلَطَ بِهِۦ نَبَاتُ ٱلۡأَرۡضِ فَأَصۡبَحَ هَشِيمٗا تَذۡرُوهُ ٱلرِّيَٰحُۗ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَيۡءٖ مُّقۡتَدِرًا ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2189,15,'299',18,'Al-Kahf','الكَهف',1,2,45,'ٱلۡمَالُ وَٱلۡبَنُونَ زِينَةُ ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ وَٱلۡبَٰقِيَٰتُ ٱلصَّٰلِحَٰتُ خَيۡرٌ عِندَ رَبِّكَ ثَوَابٗا وَخَيۡرٌ أَمَلٗا ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2190,15,'299',18,'Al-Kahf','الكَهف',2,3,46,'وَيَوۡمَ تُسَيَّرُ ٱلۡجِبَالُ وَتَرَى ٱلۡأَرۡضَ بَارِزَةٗ وَحَشَرۡنَٰهُمُۥ فَلَمۡ نُغَادِرۡ مِنۡهُمُۥ أَحَدٗا ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2191,15,'299',18,'Al-Kahf','الكَهف',3,5,47,'وَعُرِضُواْ عَلَىٰ رَبِّكَ صَفّٗا لَّقَدۡ جِئۡتُمُونَا كَمَا خَلَقۡنَٰكُمُۥ أَوَّلَ مَرَّةِۭۚ بَلۡ زَعَمۡتُمُۥ أَلَّن نَّجۡعَلَ لَكُمُۥ مَوۡعِدٗا ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2192,15,'299',18,'Al-Kahf','الكَهف',5,8,48,'وَوُضِعَ ٱلۡكِتَٰبُ فَتَرَى ٱلۡمُجۡرِمِينَ مُشۡفِقِينَ مِمَّا فِيهِۦ وَيَقُولُونَ يَٰوَيۡلَتَنَا مَالِ هَٰذَا ٱلۡكِتَٰبِ لَا يُغَادِرُ صَغِيرَةٗ وَلَا كَبِيرَةً إِلَّا أَحۡصَىٰهَاۚ وَوَجَدُواْ مَا عَمِلُواْ حَاضِرٗاۗ وَلَا يَظۡلِمُ رَبُّكَ أَحَدٗا ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2193,15,'299',18,'Al-Kahf','الكَهف',8,11,49,'وَإِذۡ قُلۡنَا لِلۡمَلَٰٓئِكَةِ ٱسۡجُدُواْ لِأٓدَمَ فَسَجَدُواْ إِلَّا إِبۡلِيسَ كَانَ مِنَ ٱلۡجِنِّ فَفَسَقَ عَنۡ أَمۡرِ رَبِّهِۦۗ  أَفَتَتَّخِذُونَهُۥ وَذُرِّيَّتَهُۥ أَوۡلِيَآءَ مِن دُونِي وَهُمُۥ لَكُمُۥ عَدُوُّۢۚ بِئۡسَ لِلظَّٰلِمِينَ بَدَلٗا ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2194,15,'299',18,'Al-Kahf','الكَهف',11,12,50,'۞ مَّا أَشۡهَدتُّهُمُۥ خَلۡقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَلَا خَلۡقَ أَنفُسِهِمُۥ وَمَا كُنتُ مُتَّخِذَ ٱلۡمُضِلِّينَ عَضُدٗا ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2195,15,'299',18,'Al-Kahf','الكَهف',13,14,51,'وَيَوۡمَ يَقُولُ نَادُواْ شُرَكَآءِيَ ٱلَّذِينَ زَعَمۡتُمُۥ فَدَعَوۡهُمُۥ فَلَمۡ يَسۡتَجِيبُواْ لَهُمُۥ وَجَعَلۡنَا بَيۡنَهُمُۥ مَوۡبِقٗا ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2196,15,'299',18,'Al-Kahf','الكَهف',14,15,52,'وَرَءَا ٱلۡمُجۡرِمُونَ ٱلنَّارَ فَظَنُّواْ أَنَّهُمُۥ مُوَاقِعُوهَا وَلَمۡ يَجِدُواْ عَنۡهَا مَصۡرِفٗا ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2197,15,'300',18,'Al-Kahf','الكَهف',1,2,53,'وَلَقَدۡ صَرَّفۡنَا فِي هَٰذَا ٱلۡقُرَانِ لِلنَّاسِ مِن كُلِّ مَثَلٖۚ وَكَانَ ٱلۡإِنسَٰنُ أَكۡثَرَ شَيۡءٖ جَدَلٗا ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2198,15,'300',18,'Al-Kahf','الكَهف',2,4,54,'وَمَا مَنَعَ ٱلنَّاسَ أَن يُؤۡمِنُواْ إِذۡ جَآءَهُمُ ٱلۡهُدَىٰ وَيَسۡتَغۡفِرُواْ رَبَّهُمُۥ إِلَّا أَن تَأۡتِيَهُمُۥ سُنَّةُ ٱلۡأَوَّلِينَ أَوۡ يَأۡتِيَهُمُ ٱلۡعَذَابُ قِبَلٗا ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2199,15,'300',18,'Al-Kahf','الكَهف',4,6,55,'وَمَا نُرۡسِلُ ٱلۡمُرۡسَلِينَ إِلَّا مُبَشِّرِينَ وَمُنذِرِينَۚ وَيُجَٰدِلُ ٱلَّذِينَ كَفَرُواْ بِٱلۡبَٰطِلِ لِيُدۡحِضُواْ بِهِ ٱلۡحَقَّۖ وَٱتَّخَذُواْ ءَايَٰتِي وَمَا أُنذِرُواْ هُزُؤٗا ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2200,15,'300',18,'Al-Kahf','الكَهف',7,10,56,'وَمَنۡ أَظۡلَمُ مِمَّن ذُكِّرَ بِـَٔايَٰتِ رَبِّهِۦ فَأَعۡرَضَ عَنۡهَا وَنَسِيَ مَا قَدَّمَتۡ يَدَاهُۥۚ إِنَّا جَعَلۡنَا عَلَىٰ قُلُوبِهِمُۥ أَكِنَّةً أَن يَفۡقَهُوهُۥ وَفِي ءَاذَانِهِمُۥ وَقۡرٗاۖ وَإِن تَدۡعُهُمُۥ إِلَى ٱلۡهُدَىٰ فَلَن يَهۡتَدُواْ إِذًا أَبَدٗا ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2201,15,'300',18,'Al-Kahf','الكَهف',10,12,57,'وَرَبُّكَ ٱلۡغَفُورُ ذُو ٱلرَّحۡمَةِۖ لَوۡ يُؤَاخِذُهُمُۥ بِمَا كَسَبُواْ لَعَجَّلَ لَهُمُ ٱلۡعَذَابَۚ بَل لَّهُمُۥ مَوۡعِدٞ لَّن يَجِدُواْ مِن دُونِهِۦ مَوۡئِلٗا ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2202,15,'300',18,'Al-Kahf','الكَهف',12,13,58,'وَتِلۡكَ ٱلۡقُرَىٰ أَهۡلَكۡنَٰهُمُۥ لَمَّا ظَلَمُواْ وَجَعَلۡنَا لِمُهۡلَكِهِمُۥ مَوۡعِدٗا ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2203,15,'300',18,'Al-Kahf','الكَهف',13,14,59,'وَإِذۡ قَالَ مُوسَىٰ لِفَتَىٰهُۥ لَا أَبۡرَحُ حَتَّىٰ أَبۡلُغَ مَجۡمَعَ ٱلۡبَحۡرَيۡنِ أَوۡ أَمۡضِيَ حُقُبٗا ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2204,15,'300',18,'Al-Kahf','الكَهف',14,15,60,'فَلَمَّا بَلَغَا مَجۡمَعَ بَيۡنِهِمَا نَسِيَا حُوتَهُمَا فَٱتَّخَذَ سَبِيلَهُۥ فِي ٱلۡبَحۡرِ سَرَبٗا ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2205,15,'301',18,'Al-Kahf','الكَهف',1,2,61,'فَلَمَّا جَاوَزَا قَالَ لِفَتَىٰهُۥ ءَاتِنَا غَدَآءَنَا لَقَدۡ لَقِينَا مِن سَفَرِنَا هَٰذَا نَصَبٗا ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2206,15,'301',18,'Al-Kahf','الكَهف',2,4,62,'قَالَ أَرَءَيۡتَ إِذۡ أَوَيۡنَا إِلَى ٱلصَّخۡرَةِ فَإِنِّي نَسِيتُ ٱلۡحُوتَ وَمَا أَنسَىٰنِيهِۦ إِلَّا ٱلشَّيۡطَٰنُ أَنۡ أَذۡكُرَهُۥۚ وَٱتَّخَذَ سَبِيلَهُۥ فِي ٱلۡبَحۡرِ عَجَبٗا ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2207,15,'301',18,'Al-Kahf','الكَهف',4,5,63,'قَالَ ذَٰلِكَ مَا كُنَّا نَبۡغِۦۚ فَٱرۡتَدَّا عَلَىٰ ءَاثَارِهِمَا قَصَصٗا ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2208,15,'301',18,'Al-Kahf','الكَهف',5,6,64,'فَوَجَدَا عَبۡدٗا مِّنۡ عِبَادِنَا ءَاتَيۡنَٰهُۥ رَحۡمَةٗ مِّنۡ عِندِنَا وَعَلَّمۡنَٰهُۥ مِن لَّدُنَّا عِلۡمٗا ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2209,15,'301',18,'Al-Kahf','الكَهف',6,7,65,'قَالَ لَهُۥ مُوسَىٰ هَلۡ أَتَّبِعُكَ عَلَىٰ أَن تُعَلِّمَنِۦ مِمَّا عُلِّمۡتَ رُشۡدٗا ٦٥');
