INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2237,16,'303',18,'Al-Kahf','الكَهف',15,15,93,'فَمَا ٱسۡطَٰعُواْ أَن يَظۡهَرُوهُۥ وَمَا ٱسۡتَطَٰعُواْ لَهُۥ نَقۡبٗا ٩٣');
