INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2239,16,'304',18,'Al-Kahf','الكَهف',2,3,95,'۞ وَتَرَكۡنَا بَعۡضَهُمُۥ يَوۡمَئِذٖ يَمُوجُ فِي بَعۡضٖۖ وَنُفِخَ فِي ٱلصُّورِ فَجَمَعۡنَٰهُمُۥ جَمۡعٗا ٩٥');
