INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2242,16,'304',18,'Al-Kahf','الكَهف',5,6,98,'أَفَحَسِبَ ٱلَّذِينَ كَفَرُواْ أَن يَتَّخِذُواْ عِبَادِي مِن دُونِي أَوۡلِيَآءَۚ ا۪نَّا أَعۡتَدۡنَا جَهَنَّمَ لِلۡكَٰفِرِينَ نُزُلٗا ٩٨');
