INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (2797,18,'350',24,'An-Nūr','النور',13,14,8,'وَيَدۡرَؤُاْ عَنۡهَا ٱلۡعَذَابَ أَن تَشۡهَدَ أَرۡبَعَ شَهَٰدَٰتِۭ بِٱللَّهِ إِنَّهُۥ لَمِنَ ٱلۡكَٰذِبِينَ ٨');
