INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3190,19,'379',27,'An-Naml','النَّمل',15,15,36,'وَإِنِّي مُرۡسِلَةٌ إِلَيۡهِمُۥ بِهَدِيَّةٖ فَنَاظِرَةُۢ بِمَ يَرۡجِعُ ٱلۡمُرۡسَلُونَ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3191,19,'380',27,'An-Naml','النَّمل',1,2,37,'فَلَمَّا جَآءَ سُلَيۡمَٰنَ قَالَ أَتُمِدُّونَنِۦ بِمَالٖ فَمَا ءَاتَىٰنِ ٱللَّهُ خَيۡرٞ مِّمَّا ءَاتَىٰكُمُۥۚ بَلۡ أَنتُمُۥ بِهَدِيَّتِكُمُۥ تَفۡرَحُونَ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3192,19,'380',27,'An-Naml','النَّمل',2,3,38,'ٱرۡجِعۡ إِلَيۡهِمُۥ فَلَنَأۡتِيَنَّهُمُۥ بِجُنُودٖ لَّا قِبَلَ لَهُمُۥ بِهَا وَلَنُخۡرِجَنَّهُمُۥ مِنۡهَا أَذِلَّةٗ وَهُمُۥ صَٰغِرُونَ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3193,19,'380',27,'An-Naml','النَّمل',4,4,39,'قَالَ يَٰأَيُّهَا ٱلۡمَلَؤُاْ اَ۬يُّكُمُۥ يَأۡتِينِي بِعَرۡشِهَا قَبۡلَ أَن يَأۡتُونِي مُسۡلِمِينَ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3194,19,'380',27,'An-Naml','النَّمل',5,6,40,'قَالَ عِفۡرِيتٞ مِّنَ ٱلۡجِنِّ أَنَا۠ ءَاتِيكَ بِهِۦ قَبۡلَ أَن تَقُومَ مِن مَّقَامِكَۖ وَإِنِّي عَلَيۡهِۦ لَقَوِيٌّ أَمِينٞ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3195,19,'380',27,'An-Naml','النَّمل',6,9,41,'قَالَ ٱلَّذِي عِندَهُۥ عِلۡمٞ مِّنَ ٱلۡكِتَٰبِ أَنَا۠ ءَاتِيكَ بِهِۦ قَبۡلَ أَن يَرۡتَدَّ إِلَيۡكَ طَرۡفُكَۚ فَلَمَّا رَءَاهُۥ مُسۡتَقِرًّا عِندَهُۥ قَالَ هَٰذَا مِن فَضۡلِ رَبِّي لِيَبۡلُوَنِي ءَا۬شۡكُرُ أَمۡ أَكۡفُرُۖ وَمَن شَكَرَ فَإِنَّمَا يَشۡكُرُ لِنَفۡسِهِۦۖ وَمَن كَفَرَ فَإِنَّ رَبِّي غَنِيّٞ كَرِيمٞ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3196,19,'380',27,'An-Naml','النَّمل',9,10,42,'قَالَ نَكِّرُواْ لَهَا عَرۡشَهَا نَنظُرۡ أَتَهۡتَدِي أَمۡ تَكُونُ مِنَ ٱلَّذِينَ لَا يَهۡتَدُونَ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3197,19,'380',27,'An-Naml','النَّمل',10,12,43,'فَلَمَّا جَآءَتۡ قِيلَ أَهَٰكَذَا عَرۡشُكِۖ قَالَتۡ كَأَنَّهُۥ هُوَۚ وَأُوتِينَا ٱلۡعِلۡمَ مِن قَبۡلِهَا وَكُنَّا مُسۡلِمِينَ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3198,19,'380',27,'An-Naml','النَّمل',12,13,44,'وَصَدَّهَا مَا كَانَت تَّعۡبُدُ مِن دُونِ ٱللَّهِۖ إِنَّهَا كَانَتۡ مِن قَوۡمٖ كَٰفِرِينَ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3199,19,'380',27,'An-Naml','النَّمل',13,14,45,'قِيلَ لَهَا ٱدۡخُلِي ٱلصَّرۡحَۖ فَلَمَّا رَأَتۡهُۥ حَسِبَتۡهُۥ لُجَّةٗ وَكَشَفَتۡ عَن سَأۡقَيۡهَاۚ قَالَ إِنَّهُۥ صَرۡحٞ مُّمَرَّدٞ مِّن قَوَارِيرَ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3200,19,'380',27,'An-Naml','النَّمل',14,15,46,'قَالَتۡ رَبِّ إِنِّي ظَلَمۡتُ نَفۡسِي وَأَسۡلَمۡتُ مَعَ سُلَيۡمَٰنَ لِلَّهِ رَبِّ ٱلۡعَٰلَمِينَ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3201,19,'381',27,'An-Naml','النَّمل',1,2,47,'وَلَقَدۡ أَرۡسَلۡنَا إِلَىٰ ثَمُودَ أَخَاهُمُۥ صَٰلِحًا أَنُ ٱعۡبُدُواْ ٱللَّهَ فَإِذَا هُمُۥ فَرِيقَانِ يَخۡتَصِمُونَ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3202,19,'381',27,'An-Naml','النَّمل',2,4,48,'قَالَ يَٰقَوۡمِ لِمَ تَسۡتَعۡجِلُونَ بِٱلسَّيِّئَةِ قَبۡلَ ٱلۡحَسَنَةِۖ لَوۡلَا تَسۡتَغۡفِرُونَ ٱللَّهَ لَعَلَّكُمُۥ تُرۡحَمُونَ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3203,19,'381',27,'An-Naml','النَّمل',4,5,49,'قَالُواْ ٱطَّيَّرۡنَا بِكَ وَبِمَن مَّعَكَۚ قَالَ طَٰٓئِرُكُمُۥ عِندَ ٱللَّهِۖ بَلۡ أَنتُمُۥ قَوۡمٞ تُفۡتَنُونَ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3204,19,'381',27,'An-Naml','النَّمل',5,6,50,'وَكَانَ فِي ٱلۡمَدِينَةِ تِسۡعَةُ رَهۡطٖ يُفۡسِدُونَ فِي ٱلۡأَرۡضِ وَلَا يُصۡلِحُونَ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3205,19,'381',27,'An-Naml','النَّمل',7,8,51,'قَالُواْ تَقَاسَمُواْ بِٱللَّهِ لَنُبَيِّتَنَّهُۥ وَأَهۡلَهُۥ ثُمَّ لَنَقُولَنَّ لِوَلِيِّهِۦ مَا شَهِدۡنَا مُهۡلَكَ أَهۡلِهِۦ وَإِنَّا لَصَٰدِقُونَ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3206,19,'381',27,'An-Naml','النَّمل',8,9,52,'وَمَكَرُواْ مَكۡرٗا وَمَكَرۡنَا مَكۡرٗا وَهُمُۥ لَا يَشۡعُرُونَ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3207,19,'381',27,'An-Naml','النَّمل',9,11,53,'فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ مَكۡرِهِمُۥ إِنَّا دَمَّرۡنَٰهُمُۥ وَقَوۡمَهُمُۥ أَجۡمَعِينَ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3208,19,'381',27,'An-Naml','النَّمل',11,12,54,'فَتِلۡكَ بِيُوتُهُمُۥ خَاوِيَةَۢ بِمَا ظَلَمُواْۚ إِنَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّقَوۡمٖ يَعۡلَمُونَ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3209,19,'381',27,'An-Naml','النَّمل',12,13,55,'وَأَنجَيۡنَا ٱلَّذِينَ ءَامَنُواْ وَكَانُواْ يَتَّقُونَ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3210,19,'381',27,'An-Naml','النَّمل',13,14,56,'وَلُوطًا إِذۡ قَالَ لِقَوۡمِهِۦ أَتَأۡتُونَ ٱلۡفَٰحِشَةَ وَأَنتُمُۥ تُبۡصِرُونَ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3211,19,'381',27,'An-Naml','النَّمل',14,15,57,'أَى۪نَّكُمُۥ لَتَأۡتُونَ ٱلرِّجَالَ شَهۡوَةٗ مِّن دُونِ ٱلنِّسَآءِۚ بَلۡ أَنتُمُۥ قَوۡمٞ تَجۡهَلُونَ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3212,20,'382',27,'An-Naml','النَّمل',1,2,58,'۞ فَمَا كَانَ جَوَابَ قَوۡمِهِۦ إِلَّا أَن قَالُواْ أَخۡرِجُواْ ءَالَ لُوطٖ مِّن قَرۡيَتِكُمُۥۖ إِنَّهُمُۥ أُنَاسٞ يَتَطَهَّرُونَ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3213,20,'382',27,'An-Naml','النَّمل',2,3,59,'فَأَنجَيۡنَٰهُۥ وَأَهۡلَهُۥ إِلَّا ٱمۡرَأَتَهُۥ قَدَّرۡنَٰهَا مِنَ ٱلۡغَٰبِرِينَ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3214,20,'382',27,'An-Naml','النَّمل',3,4,60,'وَأَمۡطَرۡنَا عَلَيۡهِمُۥ مَطَرٗاۖ فَسَآءَ مَطَرُ ٱلۡمُنذَرِينَ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3215,20,'382',27,'An-Naml','النَّمل',4,5,61,'قُلِ ٱلۡحَمۡدُ لِلَّهِ وَسَلَٰمٌ عَلَىٰ عِبَادِهِ ٱلَّذِينَ ٱصۡطَفَىٰۗ ءَآللَّهُ خَيۡرٌ أَمَّا تُشۡرِكُونَ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3216,20,'382',27,'An-Naml','النَّمل',6,8,62,'أَمَّنۡ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضَ وَأَنزَلَ لَكُمُۥ مِنَ ٱلسَّمَآءِ مَآءٗ فَأَنۢبَتۡنَا بِهِۦ حَدَآئِقَ ذَاتَ بَهۡجَةٖ مَّا كَانَ لَكُمُۥ أَن تُنۢبِتُواْ شَجَرَهَاۗ أَ۟لَٰهٞ مَّعَ ٱللَّهِۚ بَلۡ هُمُۥ قَوۡمٞ يَعۡدِلُونَ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3217,20,'382',27,'An-Naml','النَّمل',9,11,63,'أَمَّن جَعَلَ ٱلۡأَرۡضَ قَرَارٗا وَجَعَلَ خِلَٰلَهَا أَنۡهَٰرٗا وَجَعَلَ لَهَا رَوَٰسِيَ وَجَعَلَ بَيۡنَ ٱلۡبَحۡرَيۡنِ حَاجِزًاۗ أَ۟لَٰهٞ مَّعَ ٱللَّهِۚ بَلۡ أَكۡثَرُهُمُۥ لَا يَعۡلَمُونَ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3218,20,'382',27,'An-Naml','النَّمل',11,13,64,'أَمَّن يُجِيبُ ٱلۡمُضۡطَرَّ إِذَا دَعَاهُۥ وَيَكۡشِفُ ٱلسُّوٓءَ وَيَجۡعَلُكُمُۥ خُلَفَآءَ ٱلۡأَرۡضِۗ أَ۟لَٰهٞ مَّعَ ٱللَّهِۚ قَلِيلٗا مَّا تَذَّكَّرُونَ ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3219,20,'382',27,'An-Naml','النَّمل',13,15,65,'أَمَّن يَهۡدِيكُمُۥ فِي ظُلُمَٰتِ ٱلۡبَرِّ وَٱلۡبَحۡرِ وَمَن يُرۡسِلُ ٱلرِّيحَ نُشُرَۢا بَيۡنَ يَدَيۡ رَحۡمَتِهِۦۗ أَ۟لَٰهٞ مَّعَ ٱللَّهِۚ تَعَٰلَى ٱللَّهُ عَمَّا يُشۡرِكُونَ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3220,20,'383',27,'An-Naml','النَّمل',1,2,66,'أَمَّن يَبۡدَؤُاْ ٱلۡخَلۡقَ ثُمَّ يُعِيدُهُۥ وَمَن يَرۡزُقُكُمُۥ مِنَ ٱلسَّمَآءِ وَٱلۡأَرۡضِۗ أَ۟لَٰهٞ مَّعَ ٱللَّهِۚ قُلۡ هَاتُواْ بُرۡهَٰنَكُمُۥ إِن كُنتُمُۥ صَٰدِقِينَ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3221,20,'383',27,'An-Naml','النَّمل',2,4,67,'قُل لَّا يَعۡلَمُ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ ٱلۡغَيۡبَ إِلَّا ٱللَّهُۚ وَمَا يَشۡعُرُونَ أَيَّانَ يُبۡعَثُونَ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3222,20,'383',27,'An-Naml','النَّمل',4,5,68,'بَلۡ أَدۡرَكَ عِلۡمُهُمُۥ فِي ٱلۡأٓخِرَةِۚ بَلۡ هُمُۥ فِي شَكّٖ مِّنۡهَاۖ بَلۡ هُمُۥ مِنۡهَا عَمُونَ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3223,20,'383',27,'An-Naml','النَّمل',5,6,69,'وَقَالَ ٱلَّذِينَ كَفَرُواْ أَ۟ذَا كُنَّا تُرَٰبٗا وَءَابَآؤُنَا أَى۪نَّا لَمُخۡرَجُونَ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3224,20,'383',27,'An-Naml','النَّمل',6,7,70,'لَقَدۡ وُعِدۡنَا هَٰذَا نَحۡنُ وَءَابَآؤُنَا مِن قَبۡلُ إِنۡ هَٰذَا إِلَّا أَسَٰطِيرُ ٱلۡأَوَّلِينَ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3225,20,'383',27,'An-Naml','النَّمل',8,8,71,'قُلۡ سِيرُواْ فِي ٱلۡأَرۡضِ فَٱنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلۡمُجۡرِمِينَ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3226,20,'383',27,'An-Naml','النَّمل',9,9,72,'وَلَا تَحۡزَنۡ عَلَيۡهِمُۥ وَلَا تَكُن فِي ضِيقٖ مِّمَّا يَمۡكُرُونَ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3227,20,'383',27,'An-Naml','النَّمل',10,10,73,'وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلۡوَعۡدُ إِن كُنتُمُۥ صَٰدِقِينَ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3228,20,'383',27,'An-Naml','النَّمل',10,11,74,'قُلۡ عَسَىٰ أَن يَكُونَ رَدِفَ لَكُمُۥ بَعۡضُ ٱلَّذِي تَسۡتَعۡجِلُونَ ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3229,20,'383',27,'An-Naml','النَّمل',11,12,75,'وَإِنَّ رَبَّكَ لَذُو فَضۡلٍ عَلَى ٱلنَّاسِ وَلَٰكِنَّ أَكۡثَرَهُمُۥ لَا يَشۡكُرُونَ ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3230,20,'383',27,'An-Naml','النَّمل',12,13,76,'وَإِنَّ رَبَّكَ لَيَعۡلَمُ مَا تُكِنُّ صُدُورُهُمُۥ وَمَا يُعۡلِنُونَ ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3231,20,'383',27,'An-Naml','النَّمل',13,14,77,'وَمَا مِنۡ غَآئِبَةٖ فِي ٱلسَّمَآءِ وَٱلۡأَرۡضِ إِلَّا فِي كِتَٰبٖ مُّبِينٍ ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3232,20,'383',27,'An-Naml','النَّمل',14,15,78,'إِنَّ هَٰذَا ٱلۡقُرَانَ يَقُصُّ عَلَىٰ بَنِي إِسۡرَٰٓءِيلَ أَكۡثَرَ ٱلَّذِي هُمُۥ فِيهِۦ يَخۡتَلِفُونَ ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3233,20,'384',27,'An-Naml','النَّمل',1,1,79,'وَإِنَّهُۥ لَهُدٗى وَرَحۡمَةٞ لِّلۡمُؤۡمِنِينَ ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3234,20,'384',27,'An-Naml','النَّمل',1,2,80,'إِنَّ رَبَّكَ يَقۡضِي بَيۡنَهُمُۥ بِحُكۡمِهِۦۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡعَلِيمُ ٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3235,20,'384',27,'An-Naml','النَّمل',2,3,81,'فَتَوَكَّلۡ عَلَى ٱللَّهِۖ إِنَّكَ عَلَى ٱلۡحَقِّ ٱلۡمُبِينِ ٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3236,20,'384',27,'An-Naml','النَّمل',3,4,82,'إِنَّكَ لَا تُسۡمِعُ ٱلۡمَوۡتَىٰ وَلَا يَسۡمَعُ ٱلصُّمُّ ٱلدُّعَآءَ ا۪ذَا وَلَّوۡاْ مُدۡبِرِينَ ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3237,20,'384',27,'An-Naml','النَّمل',4,5,83,'وَمَا أَنتَ بِهَٰدِي ٱلۡعُمۡيِ عَن ضَلَٰلَتِهِمُۥۖ إِن تُسۡمِعُ إِلَّا مَن يُؤۡمِنُ بِـَٔايَٰتِنَا فَهُمُۥ مُسۡلِمُونَ ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3238,20,'384',27,'An-Naml','النَّمل',5,7,84,'۞ وَإِذَا وَقَعَ ٱلۡقَوۡلُ عَلَيۡهِمُۥ أَخۡرَجۡنَا لَهُمُۥ دَآبَّةٗ مِّنَ ٱلۡأَرۡضِ تُكَلِّمُهُمُۥ إِنَّ ٱلنَّاسَ كَانُواْ بِـَٔايَٰتِنَا لَا يُوقِنُونَ ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3239,20,'384',27,'An-Naml','النَّمل',7,8,85,'وَيَوۡمَ نَحۡشُرُ مِن كُلِّ أُمَّةٖ فَوۡجٗا مِّمَّن يُكَذِّبُ بِـَٔايَٰتِنَا فَهُمُۥ يُوزَعُونَ ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3240,20,'384',27,'An-Naml','النَّمل',8,9,86,'حَتَّىٰ إِذَا جَآءُو قَالَ أَكَذَّبۡتُمُۥ بِـَٔايَٰتِي وَلَمۡ تُحِيطُواْ بِهَا عِلۡمًا أَمَّاذَا كُنتُمُۥ تَعۡمَلُونَ ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3241,20,'384',27,'An-Naml','النَّمل',10,10,87,'وَوَقَعَ ٱلۡقَوۡلُ عَلَيۡهِمُۥ بِمَا ظَلَمُواْ فَهُمُۥ لَا يَنطِقُونَ ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3242,20,'384',27,'An-Naml','النَّمل',10,12,88,'أَلَمۡ يَرَوۡاْ أَنَّا جَعَلۡنَا ٱلَّيۡلَ لِيَسۡكُنُواْ فِيهِۦ وَٱلنَّهَارَ مُبۡصِرًاۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يُؤۡمِنُونَ ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3243,20,'384',27,'An-Naml','النَّمل',12,14,89,'وَيَوۡمَ يُنفَخُ فِي ٱلصُّورِ فَفَزِعَ مَن فِي ٱلسَّمَٰوَٰتِ وَمَن فِي ٱلۡأَرۡضِ إِلَّا مَن شَآءَ ٱللَّهُۚ وَكُلٌّ ءَاتُوهُۥ دَٰخِرِينَ ٨٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3244,20,'384',27,'An-Naml','النَّمل',14,15,90,'وَتَرَى ٱلۡجِبَالَ تَحۡسِبُهَا جَامِدَةٗ وَهِيَ تَمُرُّ مَرَّ ٱلسَّحَابِۚ صُنۡعَ ٱللَّهِ ٱلَّذِي أَتۡقَنَ كُلَّ شَيۡءٍۚ إِنَّهُۥ خَبِيرُۢ بِمَا يَفۡعَلُونَ ٩٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3245,20,'385',27,'An-Naml','النَّمل',1,1,91,'مَن جَآءَ بِٱلۡحَسَنَةِ فَلَهُۥ خَيۡرٞ مِّنۡهَا وَهُمُۥ مِن فَزَعِ يَوۡمِئِذٍ ءَامِنُونَ ٩١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3246,20,'385',27,'An-Naml','النَّمل',2,3,92,'وَمَن جَآءَ بِٱلسَّيِّئَةِ فَكُبَّتۡ وُجُوهُهُمُۥ فِي ٱلنَّارِ هَلۡ تُجۡزَوۡنَ إِلَّا مَا كُنتُمُۥ تَعۡمَلُونَ ٩٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3247,20,'385',27,'An-Naml','النَّمل',3,4,93,'إِنَّمَا أُمِرۡتُ أَنۡ أَعۡبُدَ رَبَّ هَٰذِهِ ٱلۡبَلۡدَةِ ٱلَّذِي حَرَّمَهَا وَلَهُۥ كُلُّ شَيۡءٖۖ وَأُمِرۡتُ أَنۡ أَكُونَ مِنَ ٱلۡمُسۡلِمِينَ ٩٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3248,20,'385',27,'An-Naml','النَّمل',5,6,94,'وَأَنۡ أَتۡلُوَاْ ٱلۡقُرَانَۖ فَمَنِ ٱهۡتَدَىٰ فَإِنَّمَا يَهۡتَدِي لِنَفۡسِهِۦۖ وَمَن ضَلَّ فَقُلۡ إِنَّمَا أَنَا۠ مِنَ ٱلۡمُنذِرِينَ ٩٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3249,20,'385',27,'An-Naml','النَّمل',6,7,95,'وَقُلِ ٱلۡحَمۡدُ لِلَّهِ سَيُرِيكُمُۥ ءَايَٰتِهِۦ فَتَعۡرِفُونَهَاۚ وَمَا رَبُّكَ بِغَٰفِلٍ عَمَّا يَعۡمَلُونَ ٩٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3250,20,'385',28,'Al-Qaṣaṣ','القَصَص',10,10,1,'طسٓمٓۚ تِلۡكَ ءَايَٰتُ ٱلۡكِتَٰبِ ٱلۡمُبِينِ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3251,20,'385',28,'Al-Qaṣaṣ','القَصَص',10,11,2,'نَتۡلُواْ عَلَيۡكَ مِن نَّبَإِ مُوسَىٰ وَفِرۡعَوۡنَ بِٱلۡحَقِّ لِقَوۡمٖ يُؤۡمِنُونَ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3252,20,'385',28,'Al-Qaṣaṣ','القَصَص',11,14,3,'إِنَّ فِرۡعَوۡنَ عَلَا فِي ٱلۡأَرۡضِ وَجَعَلَ أَهۡلَهَا شِيَعٗا يَسۡتَضۡعِفُ طَآئِفَةٗ مِّنۡهُمُۥ يُذَبِّحُ أَبۡنَآءَهُمُۥ وَيَسۡتَحۡيِۦ نِسَآءَهُمُۥۚ إِنَّهُۥ كَانَ مِنَ ٱلۡمُفۡسِدِينَ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3253,20,'385',28,'Al-Qaṣaṣ','القَصَص',14,15,4,'وَنُرِيدُ أَن نَّمُنَّ عَلَى ٱلَّذِينَ ٱسۡتُضۡعِفُواْ فِي ٱلۡأَرۡضِ وَنَجۡعَلَهُمُۥ أَى۪مَّةٗ وَنَجۡعَلَهُمُ ٱلۡوَٰرِثِينَ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3254,20,'386',28,'Al-Qaṣaṣ','القَصَص',1,2,5,'وَنُمَكِّنَ لَهُمُۥ فِي ٱلۡأَرۡضِ وَنُرِيَ فِرۡعَوۡنَ وَهَٰمَٰنَ وَجُنُودَهُمَا مِنۡهُمُۥ مَا كَانُواْ يَحۡذَرُونَ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3255,20,'386',28,'Al-Qaṣaṣ','القَصَص',2,4,6,'وَأَوۡحَيۡنَا إِلَىٰ أُمِّ مُوسَىٰ أَنۡ أَرۡضِعِيهِۦۖ فَإِذَا خِفۡتِ عَلَيۡهِۦ فَأَلۡقِيهِۦ فِي ٱلۡيَمِّ وَلَا تَخَافِي وَلَا تَحۡزَنِيۖ إِنَّا رَآدُّوهُۥ إِلَيۡكِ وَجَاعِلُوهُۥ مِنَ ٱلۡمُرۡسَلِينَ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3256,20,'386',28,'Al-Qaṣaṣ','القَصَص',5,6,7,'فَٱلۡتَقَطَهُۥ ءَالُ فِرۡعَوۡنَ لِيَكُونَ لَهُمُۥ عَدُوّٗا وَحَزَنًاۗ إِنَّ فِرۡعَوۡنَ وَهَٰمَٰنَ وَجُنُودَهُمَا كَانُواْ خَٰطِـِٔينَ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3257,20,'386',28,'Al-Qaṣaṣ','القَصَص',7,8,8,'وَقَالَتِ ٱمۡرَأَتُ فِرۡعَوۡنَ قُرَّتُ عَيۡنٖ لِّي وَلَكَۖ لَا تَقۡتُلُوهُۥ عَسَىٰ أَن يَنفَعَنَا أَوۡ نَتَّخِذَهُۥ وَلَدٗا وَهُمُۥ لَا يَشۡعُرُونَ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3258,20,'386',28,'Al-Qaṣaṣ','القَصَص',9,10,9,'وَأَصۡبَحَ فُؤَادُ أُمِّ مُوسَىٰ فَٰرِغًاۖ إِن كَادَتۡ لَتُبۡدِي بِهِۦ لَوۡلَا أَن رَّبَطۡنَا عَلَىٰ قَلۡبِهَا لِتَكُونَ مِنَ ٱلۡمُؤۡمِنِينَ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3259,20,'386',28,'Al-Qaṣaṣ','القَصَص',10,11,10,'وَقَالَتۡ لِأُخۡتِهِۦ قُصِّيهِۦۖ فَبَصُرَتۡ بِهِۦ عَن جُنُبٖ وَهُمُۥ لَا يَشۡعُرُونَ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3260,20,'386',28,'Al-Qaṣaṣ','القَصَص',12,13,11,'۞ وَحَرَّمۡنَا عَلَيۡهِ ٱلۡمَرَاضِعَ مِن قَبۡلُ فَقَالَتۡ هَلۡ أَدُلُّكُمُۥ عَلَىٰ أَهۡلِ بَيۡتٖ يَكۡفُلُونَهُۥ لَكُمُۥ وَهُمُۥ لَهُۥ نَٰصِحُونَ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3261,20,'386',28,'Al-Qaṣaṣ','القَصَص',14,15,12,'فَرَدَدۡنَٰهُۥ إِلَىٰ أُمِّهِۦ كَيۡ تَقَرَّ عَيۡنُهَا وَلَا تَحۡزَنَ وَلِتَعۡلَمَ أَنَّ وَعۡدَ ٱللَّهِ حَقّٞ وَلَٰكِنَّ أَكۡثَرَهُمُۥ لَا يَعۡلَمُونَ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3262,20,'387',28,'Al-Qaṣaṣ','القَصَص',1,2,13,'وَلَمَّا بَلَغَ أَشُدَّهُۥ وَٱسۡتَوَىٰ ءَاتَيۡنَٰهُۥ حُكۡمٗا وَعِلۡمٗاۚ وَكَذَٰلِكَ نَجۡزِي ٱلۡمُحۡسِنِينَ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3263,20,'387',28,'Al-Qaṣaṣ','القَصَص',2,6,14,'وَدَخَلَ ٱلۡمَدِينَةَ عَلَىٰ حِينِ غَفۡلَةٖ مِّنۡ أَهۡلِهَا فَوَجَدَ فِيهَا رَجُلَيۡنِ يَقۡتَتِلَانِ هَٰذَا مِن شِيعَتِهِۦ وَهَٰذَا مِنۡ عَدُوِّهِۦۖ فَٱسۡتَغَٰثَهُ ٱلَّذِي مِن شِيعَتِهِۦ عَلَى ٱلَّذِي مِنۡ عَدُوِّهِۦ فَوَكَزَهُۥ مُوسَىٰ فَقَضَىٰ عَلَيۡهِۦۖ قَالَ هَٰذَا مِنۡ عَمَلِ ٱلشَّيۡطَٰنِۖ إِنَّهُۥ عَدُوّٞ مُّضِلّٞ مُّبِينٞ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3264,20,'387',28,'Al-Qaṣaṣ','القَصَص',6,7,15,'قَالَ رَبِّ إِنِّي ظَلَمۡتُ نَفۡسِي فَٱغۡفِرۡ لِي فَغَفَرَ لَهُۥۚ إِنَّهُۥ هُوَ ٱلۡغَفُورُ ٱلرَّحِيمُ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3265,20,'387',28,'Al-Qaṣaṣ','القَصَص',7,8,16,'قَالَ رَبِّ بِمَا أَنۡعَمۡتَ عَلَيَّ فَلَنۡ أَكُونَ ظَهِيرٗا لِّلۡمُجۡرِمِينَ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3266,20,'387',28,'Al-Qaṣaṣ','القَصَص',8,10,17,'فَأَصۡبَحَ فِي ٱلۡمَدِينَةِ خَآئِفٗا يَتَرَقَّبُ فَإِذَا ٱلَّذِي ٱسۡتَنصَرَهُۥ بِٱلۡأَمۡسِ يَسۡتَصۡرِخُهُۥۚ قَالَ لَهُۥ مُوسَىٰ إِنَّكَ لَغَوِيّٞ مُّبِينٞ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3267,20,'387',28,'Al-Qaṣaṣ','القَصَص',10,13,18,'فَلَمَّا أَنۡ أَرَادَ أَن يَبۡطِشَ بِٱلَّذِي هُوَ عَدُوّٞ لَّهُمَا قَالَ يَٰمُوسَىٰ أَتُرِيدُ أَن تَقۡتُلَنِي كَمَا قَتَلۡتَ نَفۡسَۢا بِٱلۡأَمۡسِۖ إِن تُرِيدُ إِلَّا أَن تَكُونَ جَبَّارٗا فِي ٱلۡأَرۡضِ وَمَا تُرِيدُ أَن تَكُونَ مِنَ ٱلۡمُصۡلِحِينَ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3268,20,'387',28,'Al-Qaṣaṣ','القَصَص',13,14,19,'وَجَآءَ رَجُلٞ مِّنۡ أَقۡصَا ٱلۡمَدِينَةِ يَسۡعَىٰ قَالَ يَٰمُوسَىٰ إِنَّ ٱلۡمَلَأَ يَأۡتَمِرُونَ بِكَ لِيَقۡتُلُوكَ فَٱخۡرُجۡ إِنِّي لَكَ مِنَ ٱلنَّٰصِحِينَ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3269,20,'387',28,'Al-Qaṣaṣ','القَصَص',15,15,20,'فَخَرَجَ مِنۡهَا خَآئِفٗا يَتَرَقَّبُۖ قَالَ رَبِّ نَجِّنِي مِنَ ٱلۡقَوۡمِ ٱلظَّٰلِمِينَ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3270,20,'388',28,'Al-Qaṣaṣ','القَصَص',1,2,21,'وَلَمَّا تَوَجَّهَ تِلۡقَآءَ مَدۡيَنَ قَالَ عَسَىٰ رَبِّيَ أَن يَهۡدِيَنِي سَوَآءَ ٱلسَّبِيلِ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3271,20,'388',28,'Al-Qaṣaṣ','القَصَص',2,3,22,'وَلَمَّا وَرَدَ مَآءَ مَدۡيَنَ وَجَدَ عَلَيۡهِۦ أُمَّةٗ مِّنَ ٱلنَّاسِ يَسۡقُونَ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3272,20,'388',28,'Al-Qaṣaṣ','القَصَص',3,5,23,'وَوَجَدَ مِن دُونِهِمُ ٱمۡرَأَتَيۡنِ تَذُودَانِۖ قَالَ مَا خَطۡبُكُمَاۖ قَالَتَا لَا نَسۡقِي حَتَّىٰ يُصۡدِرَ ٱلرِّعَآءُۖ وَأَبُونَا شَيۡخٞ كَبِيرٞ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3273,20,'388',28,'Al-Qaṣaṣ','القَصَص',5,6,24,'فَسَقَىٰ لَهُمَا ثُمَّ تَوَلَّىٰ إِلَى ٱلظِّلِّ فَقَالَ رَبِّ إِنِّي لِمَا أَنزَلۡتَ إِلَيَّ مِنۡ خَيۡرٖ فَقِيرٞ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3274,20,'388',28,'Al-Qaṣaṣ','القَصَص',6,9,25,'فَجَآءَتۡهُۥ إِحۡدَىٰهُمَا تَمۡشِي عَلَى ٱسۡتِحۡيَآءٖ قَالَتۡ إِنَّ أَبِي يَدۡعُوكَ لِيَجۡزِيَكَ أَجۡرَ مَا سَقَيۡتَ لَنَاۚ فَلَمَّا جَآءَهُۥ وَقَصَّ عَلَيۡهِ ٱلۡقَصَصَ قَالَ لَا تَخَفۡۖ نَجَوۡتَ مِنَ ٱلۡقَوۡمِ ٱلظَّٰلِمِينَ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3275,20,'388',28,'Al-Qaṣaṣ','القَصَص',9,10,26,'قَالَتۡ إِحۡدَىٰهُمَا يَٰأَبَتِ ٱسۡتَـٔۡجِرۡهُۥۖ إِنَّ خَيۡرَ مَنِ ٱسۡتَـٔۡجَرۡتَ ٱلۡقَوِيُّ ٱلۡأَمِينُ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3276,20,'388',28,'Al-Qaṣaṣ','القَصَص',11,14,27,'قَالَ إِنِّي أُرِيدُ أَنۡ أُنكِحَكَ إِحۡدَى ٱبۡنَتَيَّ هَٰتَيۡنِّ عَلَىٰ أَن تَأۡجُرَنِي ثَمَٰنِيَ حِجَجٖۖ فَإِنۡ أَتۡمَمۡتَ عَشۡرٗا فَمِنۡ عِندِكَۖ وَمَا أُرِيدُ أَنۡ أَشُقَّ عَلَيۡكَۚ سَتَجِدُنِي إِن شَآءَ ٱللَّهُ مِنَ ٱلصَّٰلِحِينَ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3277,20,'388',28,'Al-Qaṣaṣ','القَصَص',14,15,28,'قَالَ ذَٰلِكَ بَيۡنِي وَبَيۡنَكَۖ أَيَّمَا ٱلۡأَجَلَيۡنِ قَضَيۡتُ فَلَا عُدۡوَٰنَ عَلَيَّۖ وَٱللَّهُ عَلَىٰ مَا نَقُولُ وَكِيلٞ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3278,20,'389',28,'Al-Qaṣaṣ','القَصَص',1,3,29,'۞ فَلَمَّا قَضَىٰ مُوسَى ٱلۡأَجَلَ وَسَارَ بِأَهۡلِهِۦ ءَانَسَ مِن جَانِبِ ٱلطُّورِ نَارٗاۖ قَالَ لِأَهۡلِهِ ٱمۡكُثُواْ إِنِّيَ ءَانَسۡتُ نَارٗا لَّعَلِّيَ ءَاتِيكُمُۥ مِنۡهَا بِخَبَرٍ أَوۡ جِذۡوَةٖ مِّنَ ٱلنَّارِ لَعَلَّكُمُۥ تَصۡطَلُونَ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3279,20,'389',28,'Al-Qaṣaṣ','القَصَص',4,6,30,'فَلَمَّا أَتَىٰهَا نُودِيَ مِن شَٰطِيِٕ ٱلۡوَادِ ٱلۡأَيۡمَنِ فِي ٱلۡبُقۡعَةِ ٱلۡمُبَٰرَكَةِ مِنَ ٱلشَّجَرَةِ أَن يَٰمُوسَىٰ إِنِّيَ أَنَا ٱللَّهُ رَبُّ ٱلۡعَٰلَمِينَ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3280,20,'389',28,'Al-Qaṣaṣ','القَصَص',6,8,31,'وَأَنۡ أَلۡقِ عَصَاكَۚ فَلَمَّا رَءَاهَا تَهۡتَزُّ كَأَنَّهَا جَآنّٞ وَلَّىٰ مُدۡبِرٗا وَلَمۡ يُعَقِّبۡۚ يَٰمُوسَىٰ أَقۡبِلۡ وَلَا تَخَفۡۖ إِنَّكَ مِنَ ٱلۡأٓمِنِينَ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3281,20,'389',28,'Al-Qaṣaṣ','القَصَص',8,11,32,'ٱسۡلُكۡ يَدَكَ فِي جَيۡبِكَ تَخۡرُجۡ بَيۡضَآءَ مِنۡ غَيۡرِ سُوٓءٖ وَٱضۡمُمۡ إِلَيۡكَ جَنَاحَكَ مِنَ ٱلرَّهَبِۖ فَذَٰٓنِّكَ بُرۡهَٰنَانِ مِن رَّبِّكَ إِلَىٰ فِرۡعَوۡنَ وَمَلَإِيْهِۦۚ إِنَّهُمُۥ كَانُواْ قَوۡمٗا فَٰسِقِينَ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3282,20,'389',28,'Al-Qaṣaṣ','القَصَص',11,12,33,'قَالَ رَبِّ إِنِّي قَتَلۡتُ مِنۡهُمُۥ نَفۡسٗا فَأَخَافُ أَن يَقۡتُلُونِ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3283,20,'389',28,'Al-Qaṣaṣ','القَصَص',12,13,34,'وَأَخِي هَٰرُونُ هُوَ أَفۡصَحُ مِنِّي لِسَانٗا فَأَرۡسِلۡهُۥ مَعِي رِدۡءٗا يُصَدِّقۡنِيۖ إِنِّيَ أَخَافُ أَن يُكَذِّبُونِ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3284,20,'389',28,'Al-Qaṣaṣ','القَصَص',14,15,35,'قَالَ سَنَشُدُّ عَضُدَكَ بِأَخِيكَ وَنَجۡعَلُ لَكُمَا سُلۡطَٰنٗا فَلَا يَصِلُونَ إِلَيۡكُمَا بِـَٔايَٰتِنَاۚ أَنتُمَا وَمَنِ ٱتَّبَعَكُمَا ٱلۡغَٰلِبُونَ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3285,20,'390',28,'Al-Qaṣaṣ','القَصَص',1,2,36,'فَلَمَّا جَآءَهُمُۥ مُوسَىٰ بِـَٔايَٰتِنَا بَيِّنَٰتٖ قَالُواْ مَا هَٰذَا إِلَّا سِحۡرٞ مُّفۡتَرٗى وَمَا سَمِعۡنَا بِهَٰذَا فِي ءَابَآئِنَا ٱلۡأَوَّلِينَ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3286,20,'390',28,'Al-Qaṣaṣ','القَصَص',3,4,37,'قَالَ مُوسَىٰ رَبِّيَ أَعۡلَمُ بِمَن جَآءَ بِٱلۡهُدَىٰ مِنۡ عِندِهِۦ وَمَن تَكُونُ لَهُۥ عَٰقِبَةُ ٱلدَّارِۚ إِنَّهُۥ لَا يُفۡلِحُ ٱلظَّٰلِمُونَ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3287,20,'390',28,'Al-Qaṣaṣ','القَصَص',5,7,38,'وَقَالَ فِرۡعَوۡنُ يَٰأَيُّهَا ٱلۡمَلَأُ مَا عَلِمۡتُ لَكُمُۥ مِنۡ إِلَٰهٍ غَيۡرِي فَأَوۡقِدۡ لِي يَٰهَٰمَٰنُ عَلَى ٱلطِّينِ فَٱجۡعَل لِّي صَرۡحٗا لَّعَلِّيَ أَطَّلِعُ إِلَىٰ إِلَٰهِ مُوسَىٰ وَإِنِّي لَأَظُنُّهُۥ مِنَ ٱلۡكَٰذِبِينَ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3288,20,'390',28,'Al-Qaṣaṣ','القَصَص',8,9,39,'وَٱسۡتَكۡبَرَ هُوَ وَجُنُودُهُۥ فِي ٱلۡأَرۡضِ بِغَيۡرِ ٱلۡحَقِّ وَظَنُّواْ أَنَّهُمُۥ إِلَيۡنَا لَا يُرۡجَعُونَ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3289,20,'390',28,'Al-Qaṣaṣ','القَصَص',9,10,40,'فَأَخَذۡنَٰهُۥ وَجُنُودَهُۥ فَنَبَذۡنَٰهُمُۥ فِي ٱلۡيَمِّۖ فَٱنظُرۡ كَيۡفَ كَانَ عَٰقِبَةُ ٱلظَّٰلِمِينَ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3290,20,'390',28,'Al-Qaṣaṣ','القَصَص',11,12,41,'وَجَعَلۡنَٰهُمُۥ أَى۪مَّةٗ يَدۡعُونَ إِلَى ٱلنَّارِۖ وَيَوۡمَ ٱلۡقِيَٰمَةِ لَا يُنصَرُونَ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3291,20,'390',28,'Al-Qaṣaṣ','القَصَص',12,13,42,'وَأَتۡبَعۡنَٰهُمُۥ فِي هَٰذِهِ ٱلدُّنۡيَا لَعۡنَةٗۖ وَيَوۡمَ ٱلۡقِيَٰمَةِ هُمُۥ مِنَ ٱلۡمَقۡبُوحِينَ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3292,20,'390',28,'Al-Qaṣaṣ','القَصَص',13,15,43,'وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ مِنۢ بَعۡدِ مَا أَهۡلَكۡنَا ٱلۡقُرُونَ ٱلۡأُولَىٰ بَصَآئِرَ لِلنَّاسِ وَهُدٗى وَرَحۡمَةٗ لَّعَلَّهُمُۥ يَتَذَكَّرُونَ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3293,20,'391',28,'Al-Qaṣaṣ','القَصَص',1,2,44,'وَمَا كُنتَ بِجَانِبِ ٱلۡغَرۡبِيِّ إِذۡ قَضَيۡنَا إِلَىٰ مُوسَى ٱلۡأَمۡرَ وَمَا كُنتَ مِنَ ٱلشَّٰهِدِينَ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3294,20,'391',28,'Al-Qaṣaṣ','القَصَص',2,4,45,'وَلَٰكِنَّا أَنشَأۡنَا قُرُونٗا فَتَطَاوَلَ عَلَيۡهِمُ ٱلۡعُمُرُۚ وَمَا كُنتَ ثَاوِيٗا فِي أَهۡلِ مَدۡيَنَ تَتۡلُواْ عَلَيۡهِمُۥ ءَايَٰتِنَا وَلَٰكِنَّا كُنَّا مُرۡسِلِينَ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3295,20,'391',28,'Al-Qaṣaṣ','القَصَص',4,6,46,'وَمَا كُنتَ بِجَانِبِ ٱلطُّورِ إِذۡ نَادَيۡنَا وَلَٰكِن رَّحۡمَةٗ مِّن رَّبِّكَ لِتُنذِرَ قَوۡمٗا مَّا أَتَىٰهُمُۥ مِن نَّذِيرٖ مِّن قَبۡلِكَ لَعَلَّهُمُۥ يَتَذَكَّرُونَ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3296,20,'391',28,'Al-Qaṣaṣ','القَصَص',7,9,47,'وَلَوۡلَا أَن تُصِيبَهُمُۥ مُصِيبَةُۢ بِمَا قَدَّمَتۡ أَيۡدِيهِمُۥ فَيَقُولُواْ رَبَّنَا لَوۡلَا أَرۡسَلۡتَ إِلَيۡنَا رَسُولٗا فَنَتَّبِعَ ءَايَٰتِكَ وَنَكُونَ مِنَ ٱلۡمُؤۡمِنِينَ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3297,20,'391',28,'Al-Qaṣaṣ','القَصَص',9,11,48,'فَلَمَّا جَآءَهُمُ ٱلۡحَقُّ مِنۡ عِندِنَا قَالُواْ لَوۡلَا أُوتِيَ مِثۡلَ مَا أُوتِيَ مُوسَىٰۚ أَوَلَمۡ يَكۡفُرُواْ بِمَا أُوتِيَ مُوسَىٰ مِن قَبۡلُۖ قَالُواْ سَٰحِرَانِ تَظَٰهَرَا وَقَالُواْ إِنَّا بِكُلّٖ كَٰفِرُونَ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3298,20,'391',28,'Al-Qaṣaṣ','القَصَص',12,13,49,'قُلۡ فَأۡتُواْ بِكِتَٰبٖ مِّنۡ عِندِ ٱللَّهِ هُوَ أَهۡدَىٰ مِنۡهُمَا أَتَّبِعۡهُۥ إِن كُنتُمُۥ صَٰدِقِينَ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3299,20,'391',28,'Al-Qaṣaṣ','القَصَص',13,15,50,'فَإِن لَّمۡ يَسۡتَجِيبُواْ لَكَ فَٱعۡلَمۡ أَنَّمَا يَتَّبِعُونَ أَهۡوَآءَهُمُۥۚ وَمَنۡ أَضَلُّ مِمَّنِ ٱتَّبَعَ هَوَىٰهُۥ بِغَيۡرِ هُدٗى مِّنَ ٱللَّهِۚ إِنَّ ٱللَّهَ لَا يَهۡدِي ٱلۡقَوۡمَ ٱلظَّٰلِمِينَ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3300,20,'392',28,'Al-Qaṣaṣ','القَصَص',1,1,51,'۞ وَلَقَدۡ وَصَّلۡنَا لَهُمُ ٱلۡقَوۡلَ لَعَلَّهُمُۥ يَتَذَكَّرُونَ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3301,20,'392',28,'Al-Qaṣaṣ','القَصَص',1,2,52,'ٱلَّذِينَ ءَاتَيۡنَٰهُمُ ٱلۡكِتَٰبَ مِن قَبۡلِهِۦ هُمُۥ بِهِۦ يُؤۡمِنُونَ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3302,20,'392',28,'Al-Qaṣaṣ','القَصَص',2,4,53,'وَإِذَا يُتۡلَىٰ عَلَيۡهِمُۥ قَالُواْ ءَامَنَّا بِهِۦ إِنَّهُ ٱلۡحَقُّ مِن رَّبِّنَا إِنَّا كُنَّا مِن قَبۡلِهِۦ مُسۡلِمِينَ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3303,20,'392',28,'Al-Qaṣaṣ','القَصَص',4,5,54,'أُوْلَٰٓئِكَ يُؤۡتَوۡنَ أَجۡرَهُمُۥ مَرَّتَيۡنِ بِمَا صَبَرُواْ وَيَدۡرَءُونَ بِٱلۡحَسَنَةِ ٱلسَّيِّئَةَ وَمِمَّا رَزَقۡنَٰهُمُۥ يُنفِقُونَ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3304,20,'392',28,'Al-Qaṣaṣ','القَصَص',5,7,55,'وَإِذَا سَمِعُواْ ٱللَّغۡوَ أَعۡرَضُواْ عَنۡهُۥ وَقَالُواْ لَنَا أَعۡمَٰلُنَا وَلَكُمُۥ أَعۡمَٰلُكُمُۥ سَلَٰمٌ عَلَيۡكُمُۥ لَا نَبۡتَغِي ٱلۡجَٰهِلِينَ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3305,20,'392',28,'Al-Qaṣaṣ','القَصَص',7,8,56,'إِنَّكَ لَا تَهۡدِي مَنۡ أَحۡبَبۡتَ وَلَٰكِنَّ ٱللَّهَ يَهۡدِي مَن يَشَآءُۚ وَهُوَ أَعۡلَمُ بِٱلۡمُهۡتَدِينَ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3306,20,'392',28,'Al-Qaṣaṣ','القَصَص',9,11,57,'وَقَالُواْ إِن نَّتَّبِعِ ٱلۡهُدَىٰ مَعَكَ نُتَخَطَّفۡ مِنۡ أَرۡضِنَاۚ أَوَلَمۡ نُمَكِّن لَّهُمُۥ حَرَمًا ءَامِنٗا يُجۡبَىٰ إِلَيۡهِۦ ثَمَرَٰتُ كُلِّ شَيۡءٖ رِّزۡقٗا مِّن لَّدُنَّا وَلَٰكِنَّ أَكۡثَرَهُمُۥ لَا يَعۡلَمُونَ ٥٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3307,20,'392',28,'Al-Qaṣaṣ','القَصَص',11,13,58,'وَكَمۡ أَهۡلَكۡنَا مِن قَرۡيَةِۭ بَطِرَتۡ مَعِيشَتَهَاۖ فَتِلۡكَ مَسَٰكِنُهُمُۥ لَمۡ تُسۡكَن مِّنۢ بَعۡدِهِمُۥ إِلَّا قَلِيلٗاۖ وَكُنَّا نَحۡنُ ٱلۡوَٰرِثِينَ ٥٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3308,20,'392',28,'Al-Qaṣaṣ','القَصَص',13,15,59,'وَمَا كَانَ رَبُّكَ مُهۡلِكَ ٱلۡقُرَىٰ حَتَّىٰ يَبۡعَثَ فِي أُمِّهَا رَسُولٗا يَتۡلُواْ عَلَيۡهِمُۥ ءَايَٰتِنَاۚ وَمَا كُنَّا مُهۡلِكِي ٱلۡقُرَىٰ إِلَّا وَأَهۡلُهَا ظَٰلِمُونَ ٥٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3309,20,'393',28,'Al-Qaṣaṣ','القَصَص',1,2,60,'وَمَا أُوتِيتُمُۥ مِن شَيۡءٖ فَمَتَٰعُ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَزِينَتُهَاۚ وَمَا عِندَ ٱللَّهِ خَيۡرٞ وَأَبۡقَىٰۚ أَفَلَا تَعۡقِلُونَ ٦٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3310,20,'393',28,'Al-Qaṣaṣ','القَصَص',2,4,61,'أَفَمَن وَعَدۡنَٰهُۥ وَعۡدًا حَسَنٗا فَهُوَ لَٰقِيهِۦ كَمَن مَّتَّعۡنَٰهُۥ مَتَٰعَ ٱلۡحَيَوٰةِ ٱلدُّنۡيَا ثُمَّ هُوَ يَوۡمَ ٱلۡقِيَٰمَةِ مِنَ ٱلۡمُحۡضَرِينَ ٦١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3311,20,'393',28,'Al-Qaṣaṣ','القَصَص',4,5,62,'وَيَوۡمَ يُنَادِيهِمُۥ فَيَقُولُ أَيۡنَ شُرَكَآءِيَ ٱلَّذِينَ كُنتُمُۥ تَزۡعُمُونَ ٦٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3312,20,'393',28,'Al-Qaṣaṣ','القَصَص',5,7,63,'قَالَ ٱلَّذِينَ حَقَّ عَلَيۡهِمُ ٱلۡقَوۡلُ رَبَّنَا هَٰؤُلَآءِ ٱلَّذِينَ أَغۡوَيۡنَا أَغۡوَيۡنَٰهُمُۥ كَمَا غَوَيۡنَاۖ تَبَرَّأۡنَا إِلَيۡكَۖ مَا كَانُواْ إِيَّانَا يَعۡبُدُونَ ٦٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3313,20,'393',28,'Al-Qaṣaṣ','القَصَص',7,8,64,'وَقِيلَ ٱدۡعُواْ شُرَكَآءَكُمُۥ فَدَعَوۡهُمُۥ فَلَمۡ يَسۡتَجِيبُواْ لَهُمُۥ وَرَأَوُاْ ٱلۡعَذَابَۚ لَوۡ أَنَّهُمُۥ كَانُواْ يَهۡتَدُونَ ٦٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3314,20,'393',28,'Al-Qaṣaṣ','القَصَص',9,9,65,'وَيَوۡمَ يُنَادِيهِمُۥ فَيَقُولُ مَاذَا أَجَبۡتُمُ ٱلۡمُرۡسَلِينَ ٦٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3315,20,'393',28,'Al-Qaṣaṣ','القَصَص',9,10,66,'فَعَمِيَتۡ عَلَيۡهِمُ ٱلۡأَنۢبَآءُ يَوۡمَئِذٖ فَهُمُۥ لَا يَتَسَآءَلُونَ ٦٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3316,20,'393',28,'Al-Qaṣaṣ','القَصَص',10,11,67,'فَأَمَّا مَن تَابَ وَءَامَنَ وَعَمِلَ صَٰلِحٗا فَعَسَىٰ أَن يَكُونَ مِنَ ٱلۡمُفۡلِحِينَ ٦٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3317,20,'393',28,'Al-Qaṣaṣ','القَصَص',12,13,68,'وَرَبُّكَ يَخۡلُقُ مَا يَشَآءُ وَيَخۡتَارُۗ مَا كَانَ لَهُمُ ٱلۡخِيَرَةُۚ سُبۡحَٰنَ ٱللَّهِ وَتَعَٰلَىٰ عَمَّا يُشۡرِكُونَ ٦٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3318,20,'393',28,'Al-Qaṣaṣ','القَصَص',13,14,69,'وَرَبُّكَ يَعۡلَمُ مَا تُكِنُّ صُدُورُهُمُۥ وَمَا يُعۡلِنُونَ ٦٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3319,20,'393',28,'Al-Qaṣaṣ','القَصَص',14,15,70,'وَهُوَ ٱللَّهُ لَا إِلَٰهَ إِلَّا هُوَۖ لَهُ ٱلۡحَمۡدُ فِي ٱلۡأُولَىٰ وَٱلۡأٓخِرَةِۖ وَلَهُ ٱلۡحُكۡمُ وَإِلَيۡهِۦ تُرۡجَعُونَ ٧٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3320,20,'394',28,'Al-Qaṣaṣ','القَصَص',1,2,71,'قُلۡ أَرَءَيۡتُمُۥ إِن جَعَلَ ٱللَّهُ عَلَيۡكُمُ ٱلَّيۡلَ سَرۡمَدًا إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ مَنۡ إِلَٰهٌ غَيۡرُ ٱللَّهِ يَأۡتِيكُمُۥ بِضِئَآءٍۚ أَفَلَا تَسۡمَعُونَ ٧١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3321,20,'394',28,'Al-Qaṣaṣ','القَصَص',3,5,72,'قُلۡ أَرَءَيۡتُمُۥ إِن جَعَلَ ٱللَّهُ عَلَيۡكُمُ ٱلنَّهَارَ سَرۡمَدًا إِلَىٰ يَوۡمِ ٱلۡقِيَٰمَةِ مَنۡ إِلَٰهٌ غَيۡرُ ٱللَّهِ يَأۡتِيكُمُۥ بِلَيۡلٖ تَسۡكُنُونَ فِيهِۦۚ أَفَلَا تُبۡصِرُونَ ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3322,20,'394',28,'Al-Qaṣaṣ','القَصَص',5,7,73,'وَمِن رَّحۡمَتِهِۦ جَعَلَ لَكُمُ ٱلَّيۡلَ وَٱلنَّهَارَ لِتَسۡكُنُواْ فِيهِۦ وَلِتَبۡتَغُواْ مِن فَضۡلِهِۦ وَلَعَلَّكُمُۥ تَشۡكُرُونَ ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3323,20,'394',28,'Al-Qaṣaṣ','القَصَص',7,8,74,'وَيَوۡمَ يُنَادِيهِمُۥ فَيَقُولُ أَيۡنَ شُرَكَآءِيَ ٱلَّذِينَ كُنتُمُۥ تَزۡعُمُونَ ٧٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3324,20,'394',28,'Al-Qaṣaṣ','القَصَص',8,10,75,'وَنَزَعۡنَا مِن كُلِّ أُمَّةٖ شَهِيدٗا فَقُلۡنَا هَاتُواْ بُرۡهَٰنَكُمُۥ فَعَلِمُواْ أَنَّ ٱلۡحَقَّ لِلَّهِ وَضَلَّ عَنۡهُمُۥ مَا كَانُواْ يَفۡتَرُونَ ٧٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3325,20,'394',28,'Al-Qaṣaṣ','القَصَص',10,13,76,'۞ إِنَّ قَٰرُونَ كَانَ مِن قَوۡمِ مُوسَىٰ فَبَغَىٰ عَلَيۡهِمُۥۖ وَءَاتَيۡنَٰهُۥ مِنَ ٱلۡكُنُوزِ مَا إِنَّ مَفَاتِحَهُۥ لَتَنُوٓأُ بِٱلۡعُصۡبَةِ أُوْلِي ٱلۡقُوَّةِ إِذۡ قَالَ لَهُۥ قَوۡمُهُۥ لَا تَفۡرَحۡۖ إِنَّ ٱللَّهَ لَا يُحِبُّ ٱلۡفَرِحِينَ ٧٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3326,20,'394',28,'Al-Qaṣaṣ','القَصَص',13,15,77,'وَٱبۡتَغِ فِيمَا ءَاتَىٰكَ ٱللَّهُ ٱلدَّارَ ٱلۡأٓخِرَةَۖ وَلَا تَنسَ نَصِيبَكَ مِنَ ٱلدُّنۡيَاۖ وَأَحۡسِن كَمَا أَحۡسَنَ ٱللَّهُ إِلَيۡكَۖ وَلَا تَبۡغِ ٱلۡفَسَادَ فِي ٱلۡأَرۡضِۖ إِنَّ ٱللَّهَ لَا يُحِبُّ ٱلۡمُفۡسِدِينَ ٧٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3327,20,'395',28,'Al-Qaṣaṣ','القَصَص',1,3,78,'قَالَ إِنَّمَا أُوتِيتُهُۥ عَلَىٰ عِلۡمٍ عِندِيَۚ أَوَلَمۡ يَعۡلَمۡ أَنَّ ٱللَّهَ قَدۡ أَهۡلَكَ مِن قَبۡلِهِۦ مِنَ ٱلۡقُرُونِ مَنۡ هُوَ أَشَدُّ مِنۡهُۥ قُوَّةٗ وَأَكۡثَرُ جَمۡعٗاۚ وَلَا يُسۡـَٔلُ عَن ذُنُوبِهِمُ ٱلۡمُجۡرِمُونَ ٧٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3328,20,'395',28,'Al-Qaṣaṣ','القَصَص',3,5,79,'فَخَرَجَ عَلَىٰ قَوۡمِهِۦ فِي زِينَتِهِۦۖ قَالَ ٱلَّذِينَ يُرِيدُونَ ٱلۡحَيَوٰةَ ٱلدُّنۡيَا يَٰلَيۡتَ لَنَا مِثۡلَ مَا أُوتِيَ قَٰرُونُ إِنَّهُۥ لَذُو حَظٍّ عَظِيمٖ ٧٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3329,20,'395',28,'Al-Qaṣaṣ','القَصَص',5,7,80,'وَقَالَ ٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ وَيۡلَكُمُۥ ثَوَابُ ٱللَّهِ خَيۡرٞ لِّمَنۡ ءَامَنَ وَعَمِلَ صَٰلِحٗاۚ وَلَا يُلَقَّىٰهَا إِلَّا ٱلصَّٰبِرُونَ ٨٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3330,20,'395',28,'Al-Qaṣaṣ','القَصَص',7,9,81,'فَخَسَفۡنَا بِهِۦ وَبِدَارِهِ ٱلۡأَرۡضَ فَمَا كَانَ لَهُۥ مِن فِئَةٖ يَنصُرُونَهُۥ مِن دُونِ ٱللَّهِ وَمَا كَانَ مِنَ ٱلۡمُنتَصِرِينَ ٨١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3331,20,'395',28,'Al-Qaṣaṣ','القَصَص',9,12,82,'وَأَصۡبَحَ ٱلَّذِينَ تَمَنَّوۡاْ مَكَانَهُۥ بِٱلۡأَمۡسِ يَقُولُونَ وَيۡكَأَنَّ ٱللَّهَ يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ مِنۡ عِبَادِهِۦ وَيَقۡدِرُۖ لَوۡلَا أَن مَّنَّ ٱللَّهُ عَلَيۡنَا لَخُسِفَ بِنَاۖ وَيۡكَأَنَّهُۥ لَا يُفۡلِحُ ٱلۡكَٰفِرُونَ ٨٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3332,20,'395',28,'Al-Qaṣaṣ','القَصَص',12,13,83,'تِلۡكَ ٱلدَّارُ ٱلۡأٓخِرَةُ نَجۡعَلُهَا لِلَّذِينَ لَا يُرِيدُونَ عُلُوّٗا فِي ٱلۡأَرۡضِ وَلَا فَسَادٗاۚ وَٱلۡعَٰقِبَةُ لِلۡمُتَّقِينَ ٨٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3333,20,'395',28,'Al-Qaṣaṣ','القَصَص',14,15,84,'مَن جَآءَ بِٱلۡحَسَنَةِ فَلَهُۥ خَيۡرٞ مِّنۡهَاۖ وَمَن جَآءَ بِٱلسَّيِّئَةِ فَلَا يُجۡزَى ٱلَّذِينَ عَمِلُواْ ٱلسَّيِّـَٔاتِ إِلَّا مَا كَانُواْ يَعۡمَلُونَ ٨٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3334,20,'396',28,'Al-Qaṣaṣ','القَصَص',1,2,85,'إِنَّ ٱلَّذِي فَرَضَ عَلَيۡكَ ٱلۡقُرَانَ لَرَآدُّكَ إِلَىٰ مَعَادٖۚ قُل رَّبِّيَ أَعۡلَمُ مَن جَآءَ بِٱلۡهُدَىٰ وَمَنۡ هُوَ فِي ضَلَٰلٖ مُّبِينٖ ٨٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3335,20,'396',28,'Al-Qaṣaṣ','القَصَص',2,4,86,'وَمَا كُنتَ تَرۡجُواْ أَن يُلۡقَىٰ إِلَيۡكَ ٱلۡكِتَٰبُ إِلَّا رَحۡمَةٗ مِّن رَّبِّكَۖ فَلَا تَكُونَنَّ ظَهِيرٗا لِّلۡكَٰفِرِينَ ٨٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3336,20,'396',28,'Al-Qaṣaṣ','القَصَص',4,6,87,'وَلَا يَصُدُّنَّكَ عَنۡ ءَايَٰتِ ٱللَّهِ بَعۡدَ إِذۡ أُنزِلَتۡ إِلَيۡكَۖ وَٱدۡعُ إِلَىٰ رَبِّكَۖ وَلَا تَكُونَنَّ مِنَ ٱلۡمُشۡرِكِينَ ٨٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3337,20,'396',28,'Al-Qaṣaṣ','القَصَص',6,7,88,'وَلَا تَدۡعُ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَۘ لَا إِلَٰهَ إِلَّا هُوَۚ كُلُّ شَيۡءٍ هَالِكٌ إِلَّا وَجۡهَهُۥۚ لَهُ ٱلۡحُكۡمُ وَإِلَيۡهِۦ تُرۡجَعُونَ ٨٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3338,20,'396',29,'Al-‘Ankabūt','العَنكبُوت',10,11,1,'الٓمٓۚ أَحَسِبَ ٱلنَّاسُ أَن يُتۡرَكُواْ أَن يَقُولُواْ ءَامَنَّا وَهُمُۥ لَا يُفۡتَنُونَ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3339,20,'396',29,'Al-‘Ankabūt','العَنكبُوت',11,12,2,'وَلَقَدۡ فَتَنَّا ٱلَّذِينَ مِن قَبۡلِهِمُۥۖ فَلَيَعۡلَمَنَّ ٱللَّهُ ٱلَّذِينَ صَدَقُواْ وَلَيَعۡلَمَنَّ ٱلۡكَٰذِبِينَ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3340,20,'396',29,'Al-‘Ankabūt','العَنكبُوت',12,13,3,'أَمۡ حَسِبَ ٱلَّذِينَ يَعۡمَلُونَ ٱلسَّيِّـَٔاتِ أَن يَسۡبِقُونَاۚ سَآءَ مَا يَحۡكُمُونَ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3341,20,'396',29,'Al-‘Ankabūt','العَنكبُوت',13,14,4,'مَن كَانَ يَرۡجُواْ لِقَآءَ ٱللَّهِ فَإِنَّ أَجَلَ ٱللَّهِ لَأٓتٖۚ وَهُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3342,20,'396',29,'Al-‘Ankabūt','العَنكبُوت',14,15,5,'وَمَن جَٰهَدَ فَإِنَّمَا يُجَٰهِدُ لِنَفۡسِهِۦۚ إِنَّ ٱللَّهَ لَغَنِيٌّ عَنِ ٱلۡعَٰلَمِينَ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3343,20,'397',29,'Al-‘Ankabūt','العَنكبُوت',1,2,6,'وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّٰلِحَٰتِ لَنُكَفِّرَنَّ عَنۡهُمُۥ سَيِّـَٔاتِهِمُۥ وَلَنَجۡزِيَنَّهُمُۥ أَحۡسَنَ ٱلَّذِي كَانُواْ يَعۡمَلُونَ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3344,20,'397',29,'Al-‘Ankabūt','العَنكبُوت',2,4,7,'وَوَصَّيۡنَا ٱلۡإِنسَٰنَ بِوَٰلِدَيۡهِۦ حُسۡنٗاۖ وَإِن جَٰهَدَاكَ لِتُشۡرِكَ بِي مَا لَيۡسَ لَكَ بِهِۦ عِلۡمٞ فَلَا تُطِعۡهُمَاۚ إِلَيَّ مَرۡجِعُكُمُۥ فَأُنَبِّئُكُمُۥ بِمَا كُنتُمُۥ تَعۡمَلُونَ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3345,20,'397',29,'Al-‘Ankabūt','العَنكبُوت',5,5,8,'وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّٰلِحَٰتِ لَنُدۡخِلَنَّهُمُۥ فِي ٱلصَّٰلِحِينَ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3346,20,'397',29,'Al-‘Ankabūt','العَنكبُوت',6,8,9,'وَمِنَ ٱلنَّاسِ مَن يَقُولُ ءَامَنَّا بِٱللَّهِ فَإِذَا أُوذِيَ فِي ٱللَّهِ جَعَلَ فِتۡنَةَ ٱلنَّاسِ كَعَذَابِ ٱللَّهِۖ وَلَئِن جَآءَ نَصۡرٞ مِّن رَّبِّكَ لَيَقُولُنَّ إِنَّا كُنَّا مَعَكُمُۥۚ أَوَلَيۡسَ ٱللَّهُ بِأَعۡلَمَ بِمَا فِي صُدُورِ ٱلۡعَٰلَمِينَ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3347,20,'397',29,'Al-‘Ankabūt','العَنكبُوت',9,9,10,'وَلَيَعۡلَمَنَّ ٱللَّهُ ٱلَّذِينَ ءَامَنُواْ وَلَيَعۡلَمَنَّ ٱلۡمُنَٰفِقِينَ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3348,20,'397',29,'Al-‘Ankabūt','العَنكبُوت',10,12,11,'وَقَالَ ٱلَّذِينَ كَفَرُواْ لِلَّذِينَ ءَامَنُواْ ٱتَّبِعُواْ سَبِيلَنَا وَلۡنَحۡمِلۡ خَطَٰيَٰكُمُۥ وَمَا هُمُۥ بِحَٰمِلِينَ مِنۡ خَطَٰيَٰهُمُۥ مِن شَيۡءٍۖ إِنَّهُمُۥ لَكَٰذِبُونَ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3349,20,'397',29,'Al-‘Ankabūt','العَنكبُوت',12,13,12,'وَلَيَحۡمِلُنَّ أَثۡقَالَهُمُۥ وَأَثۡقَالٗا مَّعَ أَثۡقَالِهِمُۥۖ وَلَيُسۡـَٔلُنَّ يَوۡمَ ٱلۡقِيَٰمَةِ عَمَّا كَانُواْ يَفۡتَرُونَ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3350,20,'397',29,'Al-‘Ankabūt','العَنكبُوت',14,15,13,'وَلَقَدۡ أَرۡسَلۡنَا نُوحًا إِلَىٰ قَوۡمِهِۦ فَلَبِثَ فِيهِمُۥ أَلۡفَ سَنَةٍ إِلَّا خَمۡسِينَ عَامٗا فَأَخَذَهُمُ ٱلطُّوفَانُ وَهُمُۥ ظَٰلِمُونَ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3351,20,'398',29,'Al-‘Ankabūt','العَنكبُوت',1,1,14,'فَأَنجَيۡنَٰهُۥ وَأَصۡحَٰبَ ٱلسَّفِينَةِ وَجَعَلۡنَٰهَا ءَايَةٗ لِّلۡعَٰلَمِينَ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3352,20,'398',29,'Al-‘Ankabūt','العَنكبُوت',2,3,15,'وَإِبۡرَٰهِيمَ إِذۡ قَالَ لِقَوۡمِهِ ٱعۡبُدُواْ ٱللَّهَ وَٱتَّقُوهُۥۖ ذَٰلِكُمُۥ خَيۡرٞ لَّكُمُۥ إِن كُنتُمُۥ تَعۡلَمُونَ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3353,20,'398',29,'Al-‘Ankabūt','العَنكبُوت',3,6,16,'إِنَّمَا تَعۡبُدُونَ مِن دُونِ ٱللَّهِ أَوۡثَٰنٗا وَتَخۡلُقُونَ إِفۡكًاۚ إِنَّ ٱلَّذِينَ تَعۡبُدُونَ مِن دُونِ ٱللَّهِ لَا يَمۡلِكُونَ لَكُمُۥ رِزۡقٗا فَٱبۡتَغُواْ عِندَ ٱللَّهِ ٱلرِّزۡقَ وَٱعۡبُدُوهُۥ وَٱشۡكُرُواْ لَهُۥۖ إِلَيۡهِۦ تُرۡجَعُونَ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3354,20,'398',29,'Al-‘Ankabūt','العَنكبُوت',6,8,17,'وَإِن تُكَذِّبُواْ فَقَدۡ كَذَّبَ أُمَمٞ مِّن قَبۡلِكُمُۥۖ وَمَا عَلَى ٱلرَّسُولِ إِلَّا ٱلۡبَلَٰغُ ٱلۡمُبِينُ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3355,20,'398',29,'Al-‘Ankabūt','العَنكبُوت',8,9,18,'أَوَلَمۡ يَرَوۡاْ كَيۡفَ يُبۡدِئُ ٱللَّهُ ٱلۡخَلۡقَ ثُمَّ يُعِيدُهُۥۚ إِنَّ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٞ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3356,20,'398',29,'Al-‘Ankabūt','العَنكبُوت',9,11,19,'قُلۡ سِيرُواْ فِي ٱلۡأَرۡضِ فَٱنظُرُواْ كَيۡفَ بَدَأَ ٱلۡخَلۡقَۚ ثُمَّ ٱللَّهُ يُنشِئُ ٱلنَّشَآءَةَ ٱلۡأٓخِرَةَۚ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3357,20,'398',29,'Al-‘Ankabūt','العَنكبُوت',11,12,20,'يُعَذِّبُ مَن يَشَآءُ وَيَرۡحَمُ مَن يَشَآءُۖ وَإِلَيۡهِۦ تُقۡلَبُونَ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3358,20,'398',29,'Al-‘Ankabūt','العَنكبُوت',12,14,21,'وَمَا أَنتُمُۥ بِمُعۡجِزِينَ فِي ٱلۡأَرۡضِ وَلَا فِي ٱلسَّمَآءِۖ وَمَا لَكُمُۥ مِن دُونِ ٱللَّهِ مِن وَلِيّٖ وَلَا نَصِيرٖ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3359,20,'398',29,'Al-‘Ankabūt','العَنكبُوت',14,15,22,'وَٱلَّذِينَ كَفَرُواْ بِـَٔايَٰتِ ٱللَّهِ وَلِقَآئِهِۦ أُوْلَٰٓئِكَ يَئِسُواْ مِن رَّحۡمَتِي وَأُوْلَٰٓئِكَ لَهُمُۥ عَذَابٌ أَلِيمٞ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3360,20,'399',29,'Al-‘Ankabūt','العَنكبُوت',1,2,23,'فَمَا كَانَ جَوَابَ قَوۡمِهِۦ إِلَّا أَن قَالُواْ ٱقۡتُلُوهُۥ أَوۡ حَرِّقُوهُۥ فَأَنجَىٰهُ ٱللَّهُ مِنَ ٱلنَّارِۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يُؤۡمِنُونَ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3361,20,'399',29,'Al-‘Ankabūt','العَنكبُوت',3,6,24,'وَقَالَ إِنَّمَا ٱتَّخَذۡتُمُۥ مِن دُونِ ٱللَّهِ أَوۡثَٰنٗا مَّوَدَّةُ بَيۡنِكُمُۥ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ ثُمَّ يَوۡمَ ٱلۡقِيَٰمَةِ يَكۡفُرُ بَعۡضُكُمُۥ بِبَعۡضٖ وَيَلۡعَنُ بَعۡضُكُمُۥ بَعۡضٗا وَمَأۡوَىٰكُمُ ٱلنَّارُ وَمَا لَكُمُۥ مِن نَّٰصِرِينَ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3362,20,'399',29,'Al-‘Ankabūt','العَنكبُوت',6,7,25,'۞ فَـَٔامَنَ لَهُۥ لُوطٞۘ وَقَالَ إِنِّي مُهَاجِرٌ إِلَىٰ رَبِّيۖ إِنَّهُۥ هُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3363,20,'399',29,'Al-‘Ankabūt','العَنكبُوت',8,10,26,'وَوَهَبۡنَا لَهُۥ إِسۡحَٰقَ وَيَعۡقُوبَ وَجَعَلۡنَا فِي ذُرِّيَّتِهِ ٱلنُّبُوَّةَ وَٱلۡكِتَٰبَ وَءَاتَيۡنَٰهُۥ أَجۡرَهُۥ فِي ٱلدُّنۡيَاۖ وَإِنَّهُۥ فِي ٱلۡأٓخِرَةِ لَمِنَ ٱلصَّٰلِحِينَ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3364,20,'399',29,'Al-‘Ankabūt','العَنكبُوت',10,12,27,'وَلُوطًا إِذۡ قَالَ لِقَوۡمِهِۦ إِنَّكُمُۥ لَتَأۡتُونَ ٱلۡفَٰحِشَةَ مَا سَبَقَكُمُۥ بِهَا مِنۡ أَحَدٖ مِّنَ ٱلۡعَٰلَمِينَ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3365,20,'399',29,'Al-‘Ankabūt','العَنكبُوت',12,13,28,'أَى۪نَّكُمُۥ لَتَأۡتُونَ ٱلرِّجَالَ وَتَقۡطَعُونَ ٱلسَّبِيلَ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3366,20,'399',29,'Al-‘Ankabūt','العَنكبُوت',13,15,29,'وَتَأۡتُونَ فِي نَادِيكُمُ ٱلۡمُنكَرَۖ فَمَا كَانَ جَوَابَ قَوۡمِهِۦ إِلَّا أَن قَالُواْ ٱئۡتِنَا بِعَذَابِ ٱللَّهِ إِن كُنتَ مِنَ ٱلصَّٰدِقِينَ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3367,20,'399',29,'Al-‘Ankabūt','العَنكبُوت',15,15,30,'قَالَ رَبِّ ٱنصُرۡنِي عَلَى ٱلۡقَوۡمِ ٱلۡمُفۡسِدِينَ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3368,20,'400',29,'Al-‘Ankabūt','العَنكبُوت',1,2,31,'وَلَمَّا جَآءَتۡ رُسُلُنَا إِبۡرَٰهِيمَ بِٱلۡبُشۡرَىٰ قَالُواْ إِنَّا مُهۡلِكُواْ أَهۡلِ هَٰذِهِ ٱلۡقَرۡيَةِۖ إِنَّ أَهۡلَهَا كَانُواْ ظَٰلِمِينَ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3369,20,'400',29,'Al-‘Ankabūt','العَنكبُوت',3,4,32,'قَالَ إِنَّ فِيهَا لُوطٗاۚ قَالُواْ نَحۡنُ أَعۡلَمُ بِمَن فِيهَاۖ لَنُنَجِّيَنَّهُۥ وَأَهۡلَهُۥ إِلَّا ٱمۡرَأَتَهُۥ كَانَتۡ مِنَ ٱلۡغَٰبِرِينَ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3370,20,'400',29,'Al-‘Ankabūt','العَنكبُوت',4,7,33,'وَلَمَّا أَن جَآءَتۡ رُسُلُنَا لُوطٗا سِيٓءَ بِهِمُۥ وَضَاقَ بِهِمُۥ ذَرۡعٗاۖ وَقَالُواْ لَا تَخَفۡ وَلَا تَحۡزَنۡ إِنَّا مُنجُوكَ وَأَهۡلَكَ إِلَّا ٱمۡرَأَتَكَ كَانَتۡ مِنَ ٱلۡغَٰبِرِينَ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3371,20,'400',29,'Al-‘Ankabūt','العَنكبُوت',7,8,34,'إِنَّا مُنزِلُونَ عَلَىٰ أَهۡلِ هَٰذِهِ ٱلۡقَرۡيَةِ رِجۡزٗا مِّنَ ٱلسَّمَآءِ بِمَا كَانُواْ يَفۡسُقُونَ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3372,20,'400',29,'Al-‘Ankabūt','العَنكبُوت',9,9,35,'وَلَقَد تَّرَكۡنَا مِنۡهَا ءَايَةَۢ بَيِّنَةٗ لِّقَوۡمٖ يَعۡقِلُونَ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3373,20,'400',29,'Al-‘Ankabūt','العَنكبُوت',10,11,36,'وَإِلَىٰ مَدۡيَنَ أَخَاهُمُۥ شُعَيۡبٗا فَقَالَ يَٰقَوۡمِ ٱعۡبُدُواْ ٱللَّهَ وَٱرۡجُواْ ٱلۡيَوۡمَ ٱلۡأٓخِرَ وَلَا تَعۡثَوۡاْ فِي ٱلۡأَرۡضِ مُفۡسِدِينَ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3374,20,'400',29,'Al-‘Ankabūt','العَنكبُوت',12,13,37,'فَكَذَّبُوهُۥ فَأَخَذَتۡهُمُ ٱلرَّجۡفَةُ فَأَصۡبَحُواْ فِي دَارِهِمُۥ جَٰثِمِينَ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3375,20,'400',29,'Al-‘Ankabūt','العَنكبُوت',13,15,38,'وَعَادٗا وَثَمُودٗا وَقَد تَّبَيَّنَ لَكُمُۥ مِن مَّسَٰكِنِهِمُۥۖ وَزَيَّنَ لَهُمُ ٱلشَّيۡطَٰنُ أَعۡمَٰلَهُمُۥ فَصَدَّهُمُۥ عَنِ ٱلسَّبِيلِ وَكَانُواْ مُسۡتَبۡصِرِينَ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3376,20,'401',29,'Al-‘Ankabūt','العَنكبُوت',1,2,39,'وَقَٰرُونَ وَفِرۡعَوۡنَ وَهَٰمَٰنَۖ وَلَقَدۡ جَآءَهُمُۥ مُوسَىٰ بِٱلۡبَيِّنَٰتِ فَٱسۡتَكۡبَرُواْ فِي ٱلۡأَرۡضِ وَمَا كَانُواْ سَٰبِقِينَ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3377,20,'401',29,'Al-‘Ankabūt','العَنكبُوت',3,6,40,'فَكُلًّا أَخَذۡنَا بِذَنۢبِهِۦۖ فَمِنۡهُمُۥ مَنۡ أَرۡسَلۡنَا عَلَيۡهِۦ حَاصِبٗا وَمِنۡهُمُۥ مَنۡ أَخَذَتۡهُ ٱلصَّيۡحَةُ وَمِنۡهُمُۥ مَنۡ خَسَفۡنَا بِهِ ٱلۡأَرۡضَ وَمِنۡهُمُۥ مَنۡ أَغۡرَقۡنَاۚ وَمَا كَانَ ٱللَّهُ لِيَظۡلِمَهُمُۥ وَلَٰكِن كَانُواْ أَنفُسَهُمُۥ يَظۡلِمُونَ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3378,20,'401',29,'Al-‘Ankabūt','العَنكبُوت',6,9,41,'مَثَلُ ٱلَّذِينَ ٱتَّخَذُواْ مِن دُونِ ٱللَّهِ أَوۡلِيَآءَ كَمَثَلِ ٱلۡعَنكَبُوتِ ٱتَّخَذَتۡ بَيۡتٗاۖ وَإِنَّ أَوۡهَنَ ٱلۡبِيُوتِ لَبَيۡتُ ٱلۡعَنكَبُوتِۚ لَوۡ كَانُواْ يَعۡلَمُونَ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3379,20,'401',29,'Al-‘Ankabūt','العَنكبُوت',9,10,42,'إِنَّ ٱللَّهَ يَعۡلَمُ مَا تَدۡعُونَ مِن دُونِهِۦ مِن شَيۡءٖۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ ٤٢');
