INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3414,21,'405',30,'Ar-Rūm','الرُّوم',5,9,8,'أَوَلَمۡ يَسِيرُواْ فِي ٱلۡأَرۡضِ فَيَنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبۡلِهِمُۥۚ كَانُواْ أَشَدَّ مِنۡهُمُۥ قُوَّةٗ وَأَثَارُواْ ٱلۡأَرۡضَ وَعَمَرُوهَا أَكۡثَرَ مِمَّا عَمَرُوهَا وَجَآءَتۡهُمُۥ رُسُلُهُمُۥ بِٱلۡبَيِّنَٰتِۖ فَمَا كَانَ ٱللَّهُ لِيَظۡلِمَهُمُۥ وَلَٰكِن كَانُواْ أَنفُسَهُمُۥ يَظۡلِمُونَ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3415,21,'405',30,'Ar-Rūm','الرُّوم',9,11,9,'ثُمَّ كَانَ عَٰقِبَةُ ٱلَّذِينَ أَسَٰٓـُٔواْ ٱلسُّوٓأَىٰ أَن كَذَّبُواْ بِـَٔايَٰتِ ٱللَّهِ وَكَانُواْ بِهَا يَسۡتَهۡزِءُونَ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3416,21,'405',30,'Ar-Rūm','الرُّوم',11,11,10,'ٱللَّهُ يَبۡدَؤُاْ ٱلۡخَلۡقَ ثُمَّ يُعِيدُهُۥ ثُمَّ إِلَيۡهِۦ تُرۡجَعُونَ ١٠');
