INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3424,21,'406',30,'Ar-Rūm','الرُّوم',4,5,18,'يُخۡرِجُ ٱلۡحَيَّ مِنَ ٱلۡمَيۡتِ وَيُخۡرِجُ ٱلۡمَيۡتَ مِنَ ٱلۡحَيِّ وَيُحۡيِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَاۚ وَكَذَٰلِكَ تُخۡرَجُونَ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3425,21,'406',30,'Ar-Rūm','الرُّوم',6,7,19,'وَمِنۡ ءَايَٰتِهِۦ أَنۡ خَلَقَكُمُۥ مِن تُرَابٖ ثُمَّ إِذَا أَنتُمُۥ بَشَرٞ تَنتَشِرُونَ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3426,21,'406',30,'Ar-Rūm','الرُّوم',7,9,20,'وَمِنۡ ءَايَٰتِهِۦ أَنۡ خَلَقَ لَكُمُۥ مِنۡ أَنفُسِكُمُۥ أَزۡوَٰجٗا لِّتَسۡكُنُواْ إِلَيۡهَا وَجَعَلَ بَيۡنَكُمُۥ مَوَدَّةٗ وَرَحۡمَةًۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَتَفَكَّرُونَ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3427,21,'406',30,'Ar-Rūm','الرُّوم',9,11,21,'وَمِنۡ ءَايَٰتِهِۦ خَلۡقُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَٱخۡتِلَٰفُ أَلۡسِنَتِكُمُۥ وَأَلۡوَٰنِكُمُۥۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّلۡعَٰلَمِينَ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3428,21,'406',30,'Ar-Rūm','الرُّوم',11,13,22,'وَمِنۡ ءَايَٰتِهِۦ مَنَامُكُمُۥ بِٱلَّيۡلِ وَٱلنَّهَارِ وَٱبۡتِغَآؤُكُمُۥ مِن فَضۡلِهِۦۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَسۡمَعُونَ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3429,21,'406',30,'Ar-Rūm','الرُّوم',13,15,23,'وَمِنۡ ءَايَٰتِهِۦ يُرِيكُمُ ٱلۡبَرۡقَ خَوۡفٗا وَطَمَعٗا وَيُنزِلُ مِنَ ٱلسَّمَآءِ مَآءٗ فَيُحۡيِۦ بِهِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَاۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يَعۡقِلُونَ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3430,21,'407',30,'Ar-Rūm','الرُّوم',1,2,24,'وَمِنۡ ءَايَٰتِهِۦ أَن تَقُومَ ٱلسَّمَآءُ وَٱلۡأَرۡضُ بِأَمۡرِهِۦۚ ثُمَّ إِذَا دَعَاكُمُۥ دَعۡوَةٗ مِّنَ ٱلۡأَرۡضِ إِذَا أَنتُمُۥ تَخۡرُجُونَ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3431,21,'407',30,'Ar-Rūm','الرُّوم',2,3,25,'وَلَهُۥ مَن فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ كُلّٞ لَّهُۥ قَٰنِتُونَ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3432,21,'407',30,'Ar-Rūm','الرُّوم',3,5,26,'وَهُوَ ٱلَّذِي يَبۡدَؤُاْ ٱلۡخَلۡقَ ثُمَّ يُعِيدُهُۥ وَهُوَ أَهۡوَنُ عَلَيۡهِۦۚ وَلَهُ ٱلۡمَثَلُ ٱلۡأَعۡلَىٰ فِي ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3433,21,'407',30,'Ar-Rūm','الرُّوم',5,9,27,'ضَرَبَ لَكُمُۥ مَثَلٗا مِّنۡ أَنفُسِكُمُۥۖ هَل لَّكُمُۥ مِن مَّا مَلَكَتۡ أَيۡمَٰنُكُمُۥ مِن شُرَكَآءَ فِي مَا رَزَقۡنَٰكُمُۥ فَأَنتُمُۥ فِيهِۦ سَوَآءٞ تَخَافُونَهُمُۥ كَخِيفَتِكُمُۥ أَنفُسَكُمُۥۚ كَذَٰلِكَ نُفَصِّلُ ٱلۡأٓيَٰتِ لِقَوۡمٖ يَعۡقِلُونَ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3434,21,'407',30,'Ar-Rūm','الرُّوم',9,10,28,'بَلِ ٱتَّبَعَ ٱلَّذِينَ ظَلَمُواْ أَهۡوَآءَهُمُۥ بِغَيۡرِ عِلۡمٖۖ فَمَن يَهۡدِي مَنۡ أَضَلَّ ٱللَّهُۖ وَمَا لَهُمُۥ مِن نَّٰصِرِينَ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3435,21,'407',30,'Ar-Rūm','الرُّوم',10,13,29,'فَأَقِمۡ وَجۡهَكَ لِلدِّينِ حَنِيفٗاۚ فِطۡرَتَ ٱللَّهِ ٱلَّتِي فَطَرَ ٱلنَّاسَ عَلَيۡهَاۚ لَا تَبۡدِيلَ لِخَلۡقِ ٱللَّهِۚ ذَٰلِكَ ٱلدِّينُ ٱلۡقَيِّمُ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3436,21,'407',30,'Ar-Rūm','الرُّوم',13,14,30,'۞ مُنِيبِينَ إِلَيۡهِۦ وَٱتَّقُوهُۥ وَأَقِيمُواْ ٱلصَّلَوٰةَ وَلَا تَكُونُواْ مِنَ ٱلۡمُشۡرِكِينَ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3437,21,'407',30,'Ar-Rūm','الرُّوم',14,15,31,'مِنَ ٱلَّذِينَ فَرَّقُواْ دِينَهُمُۥ وَكَانُواْ شِيَعٗاۖ كُلُّ حِزۡبِۭ بِمَا لَدَيۡهِمُۥ فَرِحُونَ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3438,21,'408',30,'Ar-Rūm','الرُّوم',1,2,32,'وَإِذَا مَسَّ ٱلنَّاسَ ضُرّٞ دَعَوۡاْ رَبَّهُمُۥ مُنِيبِينَ إِلَيۡهِۦ ثُمَّ إِذَا أَذَاقَهُمُۥ مِنۡهُۥ رَحۡمَةً إِذَا فَرِيقٞ مِّنۡهُمُۥ بِرَبِّهِمُۥ يُشۡرِكُونَ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3439,21,'408',30,'Ar-Rūm','الرُّوم',2,3,33,'لِيَكۡفُرُواْ بِمَا ءَاتَيۡنَٰهُمُۥۚ فَتَمَتَّعُواْ فَسَوۡفَ تَعۡلَمُونَ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3440,21,'408',30,'Ar-Rūm','الرُّوم',3,4,34,'أَمۡ أَنزَلۡنَا عَلَيۡهِمُۥ سُلۡطَٰنٗا فَهُوَ يَتَكَلَّمُ بِمَا كَانُواْ بِهِۦ يُشۡرِكُونَ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3441,21,'408',30,'Ar-Rūm','الرُّوم',4,6,35,'وَإِذَا أَذَقۡنَا ٱلنَّاسَ رَحۡمَةٗ فَرِحُواْ بِهَاۖ وَإِن تُصِبۡهُمُۥ سَيِّئَةُۢ بِمَا قَدَّمَتۡ أَيۡدِيهِمُۥ إِذَا هُمُۥ يَقۡنَطُونَ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3442,21,'408',30,'Ar-Rūm','الرُّوم',6,7,36,'أَوَلَمۡ يَرَوۡاْ أَنَّ ٱللَّهَ يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّقَوۡمٖ يُؤۡمِنُونَ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3443,21,'408',30,'Ar-Rūm','الرُّوم',7,9,37,'فَـَٔاتِ ذَا ٱلۡقُرۡبَىٰ حَقَّهُۥ وَٱلۡمِسۡكِينَ وَٱبۡنَ ٱلسَّبِيلِۚ ذَٰلِكَ خَيۡرٞ لِّلَّذِينَ يُرِيدُونَ وَجۡهَ ٱللَّهِۖ وَأُوْلَٰٓئِكَ هُمُ ٱلۡمُفۡلِحُونَ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3444,21,'408',30,'Ar-Rūm','الرُّوم',9,11,38,'وَمَا أَتَيۡتُمُۥ مِن رِّبٗا لِّيَرۡبُوَاْ فِي أَمۡوَٰلِ ٱلنَّاسِ فَلَا يَرۡبُواْ عِندَ ٱللَّهِۖ وَمَا ءَاتَيۡتُمُۥ مِن زَكَوٰةٖ تُرِيدُونَ وَجۡهَ ٱللَّهِ فَأُوْلَٰٓئِكَ هُمُ ٱلۡمُضۡعِفُونَ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3445,21,'408',30,'Ar-Rūm','الرُّوم',12,14,39,'ٱللَّهُ ٱلَّذِي خَلَقَكُمُۥ ثُمَّ رَزَقَكُمُۥ ثُمَّ يُمِيتُكُمُۥ ثُمَّ يُحۡيِيكُمُۥۖ هَلۡ مِن شُرَكَآئِكُمُۥ مَن يَفۡعَلُ مِن ذَٰلِكُمُۥ مِن شَيۡءٖۚ سُبۡحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يُشۡرِكُونَ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3446,21,'408',30,'Ar-Rūm','الرُّوم',14,15,40,'ظَهَرَ ٱلۡفَسَادُ فِي ٱلۡبَرِّ وَٱلۡبَحۡرِ بِمَا كَسَبَتۡ أَيۡدِي ٱلنَّاسِ لِنُذِيقَهُمُۥ بَعۡضَ ٱلَّذِي عَمِلُواْ لَعَلَّهُمُۥ يَرۡجِعُونَ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3447,21,'409',30,'Ar-Rūm','الرُّوم',1,2,41,'قُلۡ سِيرُواْ فِي ٱلۡأَرۡضِ فَٱنظُرُواْ كَيۡفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبۡلُۚ كَانَ أَكۡثَرُهُمُۥ مُشۡرِكِينَ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3448,21,'409',30,'Ar-Rūm','الرُّوم',2,3,42,'فَأَقِمۡ وَجۡهَكَ لِلدِّينِ ٱلۡقَيِّمِ مِن قَبۡلِ أَن يَأۡتِيَ يَوۡمٞ لَّا مَرَدَّ لَهُۥ مِنَ ٱللَّهِۖ يَوۡمَئِذٖ يَصَّدَّعُونَ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3449,21,'409',30,'Ar-Rūm','الرُّوم',3,4,43,'مَن كَفَرَ فَعَلَيۡهِۦ كُفۡرُهُۥۖ وَمَنۡ عَمِلَ صَٰلِحٗا فَلِأَنفُسِهِمُۥ يَمۡهَدُونَ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3450,21,'409',30,'Ar-Rūm','الرُّوم',5,6,44,'لِيَجۡزِيَ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّٰلِحَٰتِ مِن فَضۡلِهِۦۚ إِنَّهُۥ لَا يُحِبُّ ٱلۡكَٰفِرِينَ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3451,21,'409',30,'Ar-Rūm','الرُّوم',6,8,45,'وَمِنۡ ءَايَٰتِهِۦ أَن يُرۡسِلَ ٱلرِّيَاحَ مُبَشِّرَٰتٖ وَلِيُذِيقَكُمُۥ مِن رَّحۡمَتِهِۦ وَلِتَجۡرِيَ ٱلۡفُلۡكُ بِأَمۡرِهِۦ وَلِتَبۡتَغُواْ مِن فَضۡلِهِۦ وَلَعَلَّكُمُۥ تَشۡكُرُونَ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3452,21,'409',30,'Ar-Rūm','الرُّوم',8,10,46,'وَلَقَدۡ أَرۡسَلۡنَا مِن قَبۡلِكَ رُسُلًا إِلَىٰ قَوۡمِهِمُۥ فَجَآءُوهُمُۥ بِٱلۡبَيِّنَٰتِ فَٱنتَقَمۡنَا مِنَ ٱلَّذِينَ أَجۡرَمُواْۖ وَكَانَ حَقًّا عَلَيۡنَا نَصۡرُ ٱلۡمُؤۡمِنِينَ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3453,21,'409',30,'Ar-Rūm','الرُّوم',10,12,47,'ٱللَّهُ ٱلَّذِي يُرۡسِلُ ٱلرِّيحَ فَتُثِيرُ سَحَابٗا فَيَبۡسُطُهُۥ فِي ٱلسَّمَآءِ كَيۡفَ يَشَآءُ وَيَجۡعَلُهُۥ كِسَفٗا فَتَرَى ٱلۡوَدۡقَ يَخۡرُجُ مِنۡ خِلَٰلِهِۦۖ فَإِذَا أَصَابَ بِهِۦ مَن يَشَآءُ مِنۡ عِبَادِهِۦ إِذَا هُمُۥ يَسۡتَبۡشِرُونَ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3454,21,'409',30,'Ar-Rūm','الرُّوم',13,13,48,'وَإِن كَانُواْ مِن قَبۡلِ أَن يُنزَلَ عَلَيۡهِمُۥ مِن قَبۡلِهِۦ لَمُبۡلِسِينَ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3455,21,'409',30,'Ar-Rūm','الرُّوم',14,15,49,'فَٱنظُرۡ إِلَىٰ أَثَرِ رَحۡمَتِ ٱللَّهِ كَيۡفَ يُحۡيِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَاۚ إِنَّ ذَٰلِكَ لَمُحۡيِ ٱلۡمَوۡتَىٰۖ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3456,21,'410',30,'Ar-Rūm','الرُّوم',1,1,50,'وَلَئِنۡ أَرۡسَلۡنَا رِيحٗا فَرَأَوۡهُۥ مُصۡفَرّٗا لَّظَلُّواْ مِنۢ بَعۡدِهِۦ يَكۡفُرُونَ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3457,21,'410',30,'Ar-Rūm','الرُّوم',2,3,51,'فَإِنَّكَ لَا تُسۡمِعُ ٱلۡمَوۡتَىٰ وَلَا يَسۡمَعُ ٱلصُّمُّ ٱلدُّعَآءَ ا۪ذَا وَلَّوۡاْ مُدۡبِرِينَ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3458,21,'410',30,'Ar-Rūm','الرُّوم',3,4,52,'وَمَا أَنتَ بِهَٰدِ ٱلۡعُمۡيِ عَن ضَلَٰلَتِهِمُۥۖ إِن تُسۡمِعُ إِلَّا مَن يُؤۡمِنُ بِـَٔايَٰتِنَا فَهُمُۥ مُسۡلِمُونَ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3459,21,'410',30,'Ar-Rūm','الرُّوم',4,6,53,'۞ ٱللَّهُ ٱلَّذِي خَلَقَكُمُۥ مِن ضُعۡفٖ ثُمَّ جَعَلَ مِنۢ بَعۡدِ ضُعۡفٖ قُوَّةٗ ثُمَّ جَعَلَ مِنۢ بَعۡدِ قُوَّةٖ ضُعۡفٗا وَشَيۡبَةٗۚ يَخۡلُقُ مَا يَشَآءُۚ وَهُوَ ٱلۡعَلِيمُ ٱلۡقَدِيرُ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3460,21,'410',30,'Ar-Rūm','الرُّوم',7,8,54,'وَيَوۡمَ تَقُومُ ٱلسَّاعَةُ يُقۡسِمُ ٱلۡمُجۡرِمُونَ مَا لَبِثُواْ غَيۡرَ سَاعَةٖۚ كَذَٰلِكَ كَانُواْ يُؤۡفَكُونَ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3461,21,'410',30,'Ar-Rūm','الرُّوم',8,10,55,'وَقَالَ ٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ وَٱلۡإِيمَٰنَ لَقَدۡ لَبِثۡتُمُۥ فِي كِتَٰبِ ٱللَّهِ إِلَىٰ يَوۡمِ ٱلۡبَعۡثِۖ فَهَٰذَا يَوۡمُ ٱلۡبَعۡثِ وَلَٰكِنَّكُمُۥ كُنتُمُۥ لَا تَعۡلَمُونَ ٥٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3462,21,'410',30,'Ar-Rūm','الرُّوم',10,11,56,'فَيَوۡمَئِذٖ لَّا تَنفَعُ ٱلَّذِينَ ظَلَمُواْ مَعۡذِرَتُهُمُۥ وَلَا هُمُۥ يُسۡتَعۡتَبُونَ ٥٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3463,21,'410',30,'Ar-Rūm','الرُّوم',12,14,57,'وَلَقَدۡ ضَرَبۡنَا لِلنَّاسِ فِي هَٰذَا ٱلۡقُرَانِ مِن كُلِّ مَثَلٖۚ وَلَئِن جِئۡتَهُمُۥ بِـَٔايَةٖ لَّيَقُولَنَّ ٱلَّذِينَ كَفَرُواْ إِنۡ أَنتُمُۥ إِلَّا مُبۡطِلُونَ ٥٧');
