INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3600,22,'427',33,'Al-Aḥzāb','الأحزَاب',11,13,72,'إِنَّا عَرَضۡنَا ٱلۡأَمَانَةَ عَلَى ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَٱلۡجِبَالِ فَأَبَيۡنَ أَن يَحۡمِلۡنَهَا وَأَشۡفَقۡنَ مِنۡهَا وَحَمَلَهَا ٱلۡإِنسَٰنُۖ إِنَّهُۥ كَانَ ظَلُومٗا جَهُولٗا ٧٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3601,22,'427',33,'Al-Aḥzāb','الأحزَاب',13,15,73,'لِّيُعَذِّبَ ٱللَّهُ ٱلۡمُنَٰفِقِينَ وَٱلۡمُنَٰفِقَٰتِ وَٱلۡمُشۡرِكِينَ وَٱلۡمُشۡرِكَٰتِ وَيَتُوبَ ٱللَّهُ عَلَى ٱلۡمُؤۡمِنِينَ وَٱلۡمُؤۡمِنَٰتِۗ وَكَانَ ٱللَّهُ غَفُورٗا رَّحِيمَۢا ٧٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3602,22,'428',34,'Saba’','سَبإ',3,4,1,'ٱلۡحَمۡدُ لِلَّهِ ٱلَّذِي لَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِ وَلَهُ ٱلۡحَمۡدُ فِي ٱلۡأٓخِرَةِۚ وَهُوَ ٱلۡحَكِيمُ ٱلۡخَبِيرُ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3603,22,'428',34,'Saba’','سَبإ',4,6,2,'يَعۡلَمُ مَا يَلِجُ فِي ٱلۡأَرۡضِ وَمَا يَخۡرُجُ مِنۡهَا وَمَا يَنزِلُ مِنَ ٱلسَّمَآءِ وَمَا يَعۡرُجُ فِيهَاۚ وَهُوَ ٱلرَّحِيمُ ٱلۡغَفُورُ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3604,22,'428',34,'Saba’','سَبإ',6,9,3,'وَقَالَ ٱلَّذِينَ كَفَرُواْ لَا تَأۡتِينَا ٱلسَّاعَةُۖ قُلۡ بَلَىٰ وَرَبِّي لَتَأۡتِيَنَّكُمُۥ عَٰلِمِ ٱلۡغَيۡبِۖ لَا يَعۡزُبُ عَنۡهُۥ مِثۡقَالُ ذَرَّةٖ فِي ٱلسَّمَٰوَٰتِ وَلَا فِي ٱلۡأَرۡضِ وَلَا أَصۡغَرُ مِن ذَٰلِكَ وَلَا أَكۡبَرُ إِلَّا فِي كِتَٰبٖ مُّبِينٖ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3605,22,'428',34,'Saba’','سَبإ',9,11,4,'لِّيَجۡزِيَ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّٰلِحَٰتِۚ أُوْلَٰٓئِكَ لَهُمُۥ مَغۡفِرَةٞ وَرِزۡقٞ كَرِيمٞ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3606,22,'428',34,'Saba’','سَبإ',11,12,5,'وَٱلَّذِينَ سَعَوۡ فِي ءَايَٰتِنَا مُعَجِّزِينَ أُوْلَٰٓئِكَ لَهُمُۥ عَذَابٞ مِّن رِّجۡزٍ أَلِيمٞ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3607,22,'428',34,'Saba’','سَبإ',12,14,6,'وَيَرَى ٱلَّذِينَ أُوتُواْ ٱلۡعِلۡمَ ٱلَّذِي أُنزِلَ إِلَيۡكَ مِن رَّبِّكَ هُوَ ٱلۡحَقَّ وَيَهۡدِي إِلَىٰ صِۜرَٰطِ ٱلۡعَزِيزِ ٱلۡحَمِيدِ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3608,22,'428',34,'Saba’','سَبإ',14,15,7,'وَقَالَ ٱلَّذِينَ كَفَرُواْ هَلۡ نَدُلُّكُمُۥ عَلَىٰ رَجُلٖ يُنَبِّئُكُمُۥ إِذَا مُزِّقۡتُمُۥ كُلَّ مُمَزَّقٍ إِنَّكُمُۥ لَفِي خَلۡقٖ جَدِيدٍ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3609,22,'429',34,'Saba’','سَبإ',1,2,8,'أَفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَم بِهِۦ جِنَّةُۢۗ بَلِ ٱلَّذِينَ لَا يُؤۡمِنُونَ بِٱلۡأٓخِرَةِ فِي ٱلۡعَذَابِ وَٱلضَّلَٰلِ ٱلۡبَعِيدِ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3610,22,'429',34,'Saba’','سَبإ',2,5,9,'أَفَلَمۡ يَرَوۡاْ إِلَىٰ مَا بَيۡنَ أَيۡدِيهِمُۥ وَمَا خَلۡفَهُمُۥ مِنَ ٱلسَّمَآءِ وَٱلۡأَرۡضِۚ إِن نَّشَأۡ نَخۡسِفۡ بِهِمُ ٱلۡأَرۡضَ أَوۡ نُسۡقِطۡ عَلَيۡهِمُۥ كِسۡفٗا مِّنَ ٱلسَّمَآءِۚ ا۪نَّ فِي ذَٰلِكَ لَأٓيَةٗ لِّكُلِّ عَبۡدٖ مُّنِيبٖ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3611,22,'429',34,'Saba’','سَبإ',5,6,10,'۞ وَلَقَدۡ ءَاتَيۡنَا دَاوُۥدَ مِنَّا فَضۡلٗاۖ يَٰجِبَالُ أَوِّبِي مَعَهُۥ وَٱلطَّيۡرَۖ وَأَلَنَّا لَهُ ٱلۡحَدِيدَ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3612,22,'429',34,'Saba’','سَبإ',6,8,11,'أَنِ ٱعۡمَلۡ سَٰبِغَٰتٖ وَقَدِّرۡ فِي ٱلسَّرۡدِۖ وَٱعۡمَلُواْ صَٰلِحًاۖ إِنِّي بِمَا تَعۡمَلُونَ بَصِيرٞ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3613,22,'429',34,'Saba’','سَبإ',8,10,12,'وَلِسُلَيۡمَٰنَ ٱلرِّيحَ غُدُوُّهَا شَهۡرٞ وَرَوَاحُهَا شَهۡرٞۖ وَأَسَلۡنَا لَهُۥ عَيۡنَ ٱلۡقِطۡرِۖ وَمِنَ ٱلۡجِنِّ مَن يَعۡمَلُ بَيۡنَ يَدَيۡهِۦ بِإِذۡنِ رَبِّهِۦۖ وَمَن يَزِغۡ مِنۡهُمُۥ عَنۡ أَمۡرِنَا نُذِقۡهُۥ مِنۡ عَذَابِ ٱلسَّعِيرِ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3614,22,'429',34,'Saba’','سَبإ',11,13,13,'يَعۡمَلُونَ لَهُۥ مَا يَشَآءُ مِن مَّحَٰرِيبَ وَتَمَٰثِيلَ وَجِفَانٖ كَٱلۡجَوَابِۦ وَقُدُورٖ رَّاسِيَٰتٍۚ ٱعۡمَلُواْ ءَالَ دَاوُۥدَ شُكۡرٗاۚ وَقَلِيلٞ مِّنۡ عِبَادِيَ ٱلشَّكُورُ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3615,22,'429',34,'Saba’','سَبإ',13,15,14,'فَلَمَّا قَضَيۡنَا عَلَيۡهِ ٱلۡمَوۡتَ مَا دَلَّهُمُۥ عَلَىٰ مَوۡتِهِۦ إِلَّا دَآبَّةُ ٱلۡأَرۡضِ تَأۡكُلُ مِنسَأَتَهُۥۖ فَلَمَّا خَرَّ تَبَيَّنَتِ ٱلۡجِنُّ أَن لَّوۡ كَانُواْ يَعۡلَمُونَ ٱلۡغَيۡبَ مَا لَبِثُواْ فِي ٱلۡعَذَابِ ٱلۡمُهِينِ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3616,22,'430',34,'Saba’','سَبإ',1,2,15,'لَقَدۡ كَانَ لِسَبَأۡ فِي مَسَٰكِنِهِمُۥ ءَايَةٞۖ جَنَّتَانِ عَن يَمِينٖ وَشِمَالٖۖ كُلُواْ مِن رِّزۡقِ رَبِّكُمُۥ وَٱشۡكُرُواْ لَهُۥۚ بَلۡدَةٞ طَيِّبَةٞ وَرَبٌّ غَفُورٞ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3617,22,'430',34,'Saba’','سَبإ',3,4,16,'فَأَعۡرَضُواْ فَأَرۡسَلۡنَا عَلَيۡهِمُۥ سَيۡلَ ٱلۡعَرِمِ وَبَدَّلۡنَٰهُمُۥ بِجَنَّتَيۡهِمُۥ جَنَّتَيۡنِ ذَوَاتَيۡ أُكۡلٍ خَمۡطٖ وَأَثۡلٖ وَشَيۡءٖ مِّن سِدۡرٖ قَلِيلٖ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3618,22,'430',34,'Saba’','سَبإ',5,5,17,'ذَٰلِكَ جَزَيۡنَٰهُمُۥ بِمَا كَفَرُواْۖ وَهَلۡ يُجَٰزَيٰ إِلَّا ٱلۡكَفُورُ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3619,22,'430',34,'Saba’','سَبإ',6,7,18,'وَجَعَلۡنَا بَيۡنَهُمُۥ وَبَيۡنَ ٱلۡقُرَى ٱلَّتِي بَٰرَكۡنَا فِيهَا قُرٗى ظَٰهِرَةٗ وَقَدَّرۡنَا فِيهَا ٱلسَّيۡرَۖ سِيرُواْ فِيهَا لَيَالِيَ وَأَيَّامًا ءَامِنِينَ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3620,22,'430',34,'Saba’','سَبإ',8,10,19,'فَقَالُواْ رَبَّنَا بَعِّدۡ بَيۡنَ أَسۡفَارِنَا وَظَلَمُواْ أَنفُسَهُمُۥ فَجَعَلۡنَٰهُمُۥ أَحَادِيثَ وَمَزَّقۡنَٰهُمُۥ كُلَّ مُمَزَّقٍۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّكُلِّ صَبَّارٖ شَكُورٖ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3621,22,'430',34,'Saba’','سَبإ',10,11,20,'وَلَقَدۡ صَدَقَ عَلَيۡهِمُۥ إِبۡلِيسُ ظَنَّهُۥ فَٱتَّبَعُوهُۥ إِلَّا فَرِيقٗا مِّنَ ٱلۡمُؤۡمِنِينَ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3622,22,'430',34,'Saba’','سَبإ',11,13,21,'وَمَا كَانَ لَهُۥ عَلَيۡهِمُۥ مِن سُلۡطَٰنٍ إِلَّا لِنَعۡلَمَ مَن يُؤۡمِنُ بِٱلۡأٓخِرَةِ مِمَّنۡ هُوَ مِنۡهَا فِي شَكّٖۗ وَرَبُّكَ عَلَىٰ كُلِّ شَيۡءٍ حَفِيظٞ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3623,22,'430',34,'Saba’','سَبإ',13,15,22,'قُلُ ٱدۡعُواْ ٱلَّذِينَ زَعَمۡتُمُۥ مِن دُونِ ٱللَّهِ لَا يَمۡلِكُونَ مِثۡقَالَ ذَرَّةٖ فِي ٱلسَّمَٰوَٰتِ وَلَا فِي ٱلۡأَرۡضِ وَمَا لَهُمُۥ فِيهِمَا مِن شِرۡكٖ وَمَا لَهُۥ مِنۡهُمُۥ مِن ظَهِيرٖ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3624,22,'431',34,'Saba’','سَبإ',1,2,23,'وَلَا تَنفَعُ ٱلشَّفَٰعَةُ عِندَهُۥ إِلَّا لِمَنۡ أَذِنَ لَهُۥۚ حَتَّىٰ إِذَا فُزِّعَ عَن قُلُوبِهِمُۥ قَالُواْ مَاذَا قَالَ رَبُّكُمُۥۖ قَالُواْ ٱلۡحَقَّۖ وَهُوَ ٱلۡعَلِيُّ ٱلۡكَبِيرُ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3625,22,'431',34,'Saba’','سَبإ',3,4,24,'۞ قُلۡ مَن يَرۡزُقُكُمُۥ مِنَ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ قُلِ ٱللَّهُۖ وَإِنَّا أَوۡ إِيَّاكُمُۥ لَعَلَىٰ هُدًى أَوۡ فِي ضَلَٰلٖ مُّبِينٖ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3626,22,'431',34,'Saba’','سَبإ',4,5,25,'قُل لَّا تُسۡـَٔلُونَ عَمَّا أَجۡرَمۡنَا وَلَا نُسۡـَٔلُ عَمَّا تَعۡمَلُونَ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3627,22,'431',34,'Saba’','سَبإ',5,6,26,'قُلۡ يَجۡمَعُ بَيۡنَنَا رَبُّنَا ثُمَّ يَفۡتَحُ بَيۡنَنَا بِٱلۡحَقِّ وَهُوَ ٱلۡفَتَّاحُ ٱلۡعَلِيمُ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3628,22,'431',34,'Saba’','سَبإ',7,8,27,'قُلۡ أَرُونِيَ ٱلَّذِينَ أَلۡحَقۡتُمُۥ بِهِۦ شُرَكَآءَۖ كَلَّاۚ بَلۡ هُوَ ٱللَّهُ ٱلۡعَزِيزُ ٱلۡحَكِيمُ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3629,22,'431',34,'Saba’','سَبإ',8,9,28,'وَمَا أَرۡسَلۡنَٰكَ إِلَّا كَآفَّةٗ لِّلنَّاسِ بَشِيرٗا وَنَذِيرٗا وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3630,22,'431',34,'Saba’','سَبإ',10,10,29,'وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلۡوَعۡدُ إِن كُنتُمُۥ صَٰدِقِينَ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3631,22,'431',34,'Saba’','سَبإ',11,11,30,'قُل لَّكُمُۥ مِيعَادُ يَوۡمٖ لَّا تَسۡتَـٔۡخِرُونَ عَنۡهُۥ سَاعَةٗ وَلَا تَسۡتَقۡدِمُونَ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3632,22,'431',34,'Saba’','سَبإ',12,15,31,'وَقَالَ ٱلَّذِينَ كَفَرُواْ لَن نُّؤۡمِنَ بِهَٰذَا ٱلۡقُرَانِ وَلَا بِٱلَّذِي بَيۡنَ يَدَيۡهِۦۗ وَلَوۡ تَرَىٰ إِذِ ٱلظَّٰلِمُونَ مَوۡقُوفُونَ عِندَ رَبِّهِمُۥ يَرۡجِعُ بَعۡضُهُمُۥ إِلَىٰ بَعۡضٍ ٱلۡقَوۡلَ يَقُولُ ٱلَّذِينَ ٱسۡتُضۡعِفُواْ لِلَّذِينَ ٱسۡتَكۡبَرُواْ لَوۡلَا أَنتُمُۥ لَكُنَّا مُؤۡمِنِينَ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3633,22,'432',34,'Saba’','سَبإ',1,2,32,'قَالَ ٱلَّذِينَ ٱسۡتَكۡبَرُواْ لِلَّذِينَ ٱسۡتُضۡعِفُواْ أَنَحۡنُ صَدَدۡنَٰكُمُۥ عَنِ ٱلۡهُدَىٰ بَعۡدَ إِذۡ جَآءَكُمُۥۖ بَلۡ كُنتُمُۥ مُجۡرِمِينَ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3634,22,'432',34,'Saba’','سَبإ',2,6,33,'وَقَالَ ٱلَّذِينَ ٱسۡتُضۡعِفُواْ لِلَّذِينَ ٱسۡتَكۡبَرُواْ بَلۡ مَكۡرُ ٱلَّيۡلِ وَٱلنَّهَارِ إِذۡ تَأۡمُرُونَنَا أَن نَّكۡفُرَ بِٱللَّهِ وَنَجۡعَلَ لَهُۥ أَندَادٗاۚ وَأَسَرُّواْ ٱلنَّدَامَةَ لَمَّا رَأَوُاْ ٱلۡعَذَابَۚ وَجَعَلۡنَا ٱلۡأَغۡلَٰلَ فِي أَعۡنَاقِ ٱلَّذِينَ كَفَرُواْۖ هَلۡ يُجۡزَوۡنَ إِلَّا مَا كَانُواْ يَعۡمَلُونَ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3635,22,'432',34,'Saba’','سَبإ',6,7,34,'وَمَا أَرۡسَلۡنَا فِي قَرۡيَةٖ مِّن نَّذِيرٍ إِلَّا قَالَ مُتۡرَفُوهَا إِنَّا بِمَا أُرۡسِلۡتُمُۥ بِهِۦ كَٰفِرُونَ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3636,22,'432',34,'Saba’','سَبإ',8,8,35,'وَقَالُواْ نَحۡنُ أَكۡثَرُ أَمۡوَٰلٗا وَأَوۡلَٰدٗا وَمَا نَحۡنُ بِمُعَذَّبِينَ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3637,22,'432',34,'Saba’','سَبإ',9,10,36,'قُلۡ إِنَّ رَبِّي يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُ وَلَٰكِنَّ أَكۡثَرَ ٱلنَّاسِ لَا يَعۡلَمُونَ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3638,22,'432',34,'Saba’','سَبإ',10,12,37,'وَمَا أَمۡوَٰلُكُمُۥ وَلَا أَوۡلَٰدُكُمُۥ بِٱلَّتِي تُقَرِّبُكُمُۥ عِندَنَا زُلۡفَىٰ إِلَّا مَنۡ ءَامَنَ وَعَمِلَ صَٰلِحٗا فَأُوْلَٰٓئِكَ لَهُمُۥ جَزَآءُ ٱلضِّعۡفِ بِمَا عَمِلُواْ وَهُمُۥ فِي ٱلۡغُرُفَٰتِ ءَامِنُونَ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3639,22,'432',34,'Saba’','سَبإ',12,13,38,'وَٱلَّذِينَ يَسۡعَوۡنَ فِي ءَايَٰتِنَا مُعَجِّزِينَ أُوْلَٰٓئِكَ فِي ٱلۡعَذَابِ مُحۡضَرُونَ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3640,22,'432',34,'Saba’','سَبإ',14,15,39,'قُلۡ إِنَّ رَبِّي يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ مِنۡ عِبَادِهِۦ وَيَقۡدِرُ لَهُۥۚ وَمَا أَنفَقۡتُمُۥ مِن شَيۡءٖ فَهُوَ يُخۡلِفُهُۥۖ وَهُوَ خَيۡرُ ٱلرَّٰزِقِينَ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3641,22,'433',34,'Saba’','سَبإ',1,2,40,'وَيَوۡمَ نَحۡشُرُهُمُۥ جَمِيعٗا ثُمَّ نَقُولُ لِلۡمَلَٰٓئِكَةِ أَهَٰؤُلَآءِ ا۪يَّاكُمُۥ كَانُواْ يَعۡبُدُونَ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3642,22,'433',34,'Saba’','سَبإ',2,3,41,'قَالُواْ سُبۡحَٰنَكَ أَنتَ وَلِيُّنَا مِن دُونِهِمُۥۖ بَلۡ كَانُواْ يَعۡبُدُونَ ٱلۡجِنَّۖ أَكۡثَرُهُمُۥ بِهِمُۥ مُؤۡمِنُونَ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3643,22,'433',34,'Saba’','سَبإ',3,5,42,'فَٱلۡيَوۡمَ لَا يَمۡلِكُ بَعۡضُكُمُۥ لِبَعۡضٖ نَّفۡعٗا وَلَا ضَرّٗا وَنَقُولُ لِلَّذِينَ ظَلَمُواْ ذُوقُواْ عَذَابَ ٱلنَّارِ ٱلَّتِي كُنتُمُۥ بِهَا تُكَذِّبُونَ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3644,22,'433',34,'Saba’','سَبإ',5,8,43,'وَإِذَا تُتۡلَىٰ عَلَيۡهِمُۥ ءَايَٰتُنَا بَيِّنَٰتٖ قَالُواْ مَا هَٰذَا إِلَّا رَجُلٞ يُرِيدُ أَن يَصُدَّكُمُۥ عَمَّا كَانَ يَعۡبُدُ ءَابَآؤُكُمُۥ وَقَالُواْ مَا هَٰذَا إِلَّا إِفۡكٞ مُّفۡتَرٗىۚ وَقَالَ ٱلَّذِينَ كَفَرُواْ لِلۡحَقِّ لَمَّا جَآءَهُمُۥ إِنۡ هَٰذَا إِلَّا سِحۡرٞ مُّبِينٞ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3645,22,'433',34,'Saba’','سَبإ',8,9,44,'وَمَا ءَاتَيۡنَٰهُمُۥ مِن كُتُبٖ يَدۡرُسُونَهَاۖ وَمَا أَرۡسَلۡنَا إِلَيۡهِمُۥ قَبۡلَكَ مِن نَّذِيرٖ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3646,22,'433',34,'Saba’','سَبإ',9,11,45,'وَكَذَّبَ ٱلَّذِينَ مِن قَبۡلِهِمُۥ وَمَا بَلَغُواْ مِعۡشَارَ مَا ءَاتَيۡنَٰهُمُۥ فَكَذَّبُواْ رُسُلِيۖ فَكَيۡفَ كَانَ نَكِيرِ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3647,22,'433',34,'Saba’','سَبإ',11,13,46,'۞ قُلۡ إِنَّمَا أَعِظُكُمُۥ بِوَٰحِدَةٍۖ أَن تَقُومُواْ لِلَّهِ مَثۡنَىٰ وَفُرَٰدَىٰ ثُمَّ تَتَفَكَّرُواْۚ مَا بِصَاحِبِكُمُۥ مِن جِنَّةٍۚ إِنۡ هُوَ إِلَّا نَذِيرٞ لَّكُمُۥ بَيۡنَ يَدَيۡ عَذَابٖ شَدِيدٖ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3648,22,'433',34,'Saba’','سَبإ',13,15,47,'قُلۡ مَا سَأَلۡتُكُمُۥ مِنۡ أَجۡرٖ فَهُوَ لَكُمُۥۖ إِنۡ أَجۡرِي إِلَّا عَلَى ٱللَّهِۖ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدٞ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3649,22,'433',34,'Saba’','سَبإ',15,15,48,'قُلۡ إِنَّ رَبِّي يَقۡذِفُ بِٱلۡحَقِّ عَلَّٰمُ ٱلۡغُيُوبِ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3650,22,'434',34,'Saba’','سَبإ',1,1,49,'قُلۡ جَآءَ ٱلۡحَقُّ وَمَا يُبۡدِئُ ٱلۡبَٰطِلُ وَمَا يُعِيدُ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3651,22,'434',34,'Saba’','سَبإ',1,3,50,'قُلۡ إِن ضَلَلۡتُ فَإِنَّمَا أَضِلُّ عَلَىٰ نَفۡسِيۖ وَإِنِ ٱهۡتَدَيۡتُ فَبِمَا يُوحِي إِلَيَّ رَبِّيۚ إِنَّهُۥ سَمِيعٞ قَرِيبٞ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3652,22,'434',34,'Saba’','سَبإ',3,4,51,'وَلَوۡ تَرَىٰ إِذۡ فَزِعُواْ فَلَا فَوۡتَ وَأُخِذُواْ مِن مَّكَانٖ قَرِيبٖ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3653,22,'434',34,'Saba’','سَبإ',4,5,52,'وَقَالُواْ ءَامَنَّا بِهِۦ وَأَنَّىٰ لَهُمُ ٱلتَّنَاوُشُ مِن مَّكَانِۭ بَعِيدٖ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3654,22,'434',34,'Saba’','سَبإ',5,6,53,'وَقَدۡ كَفَرُواْ بِهِۦ مِن قَبۡلُۖ وَيَقۡذِفُونَ بِٱلۡغَيۡبِ مِن مَّكَانِۭ بَعِيدٖ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3655,22,'434',34,'Saba’','سَبإ',6,7,54,'وَحِيلَ بَيۡنَهُمُۥ وَبَيۡنَ مَا يَشۡتَهُونَ كَمَا فُعِلَ بِأَشۡيَاعِهِمُۥ مِن قَبۡلُۚ إِنَّهُمُۥ كَانُواْ فِي شَكّٖ مُّرِيبِۭ ٥٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3656,22,'434',35,'Fāṭir','فَاطِر',10,12,1,'ٱلۡحَمۡدُ لِلَّهِ فَاطِرِ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ جَاعِلِ ٱلۡمَلَٰٓئِكَةِ رُسُلًا أُوْلِي أَجۡنِحَةٖ مَّثۡنَىٰ وَثُلَٰثَ وَرُبَٰعَۚ يَزِيدُ فِي ٱلۡخَلۡقِ مَا يَشَآءُۚ اِ۪نَّ ٱللَّهَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3657,22,'434',35,'Fāṭir','فَاطِر',12,13,2,'مَّا يَفۡتَحِ ٱللَّهُ لِلنَّاسِ مِن رَّحۡمَةٖ فَلَا مُمۡسِكَ لَهَاۖ وَمَا يُمۡسِكۡ فَلَا مُرۡسِلَ لَهُۥ مِنۢ بَعۡدِهِۦۚ وَهُوَ ٱلۡعَزِيزُ ٱلۡحَكِيمُ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3658,22,'434',35,'Fāṭir','فَاطِر',14,15,3,'يَٰأَيُّهَا ٱلنَّاسُ ٱذۡكُرُواْ نِعۡمَتَ ٱللَّهِ عَلَيۡكُمُۥۚ هَلۡ مِنۡ خَٰلِقٍ غَيۡرُ ٱللَّهِ يَرۡزُقُكُمُۥ مِنَ ٱلسَّمَآءِ وَٱلۡأَرۡضِۚ لَا إِلَٰهَ إِلَّا هُوَۖ فَأَنَّىٰ تُؤۡفَكُونَ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3659,22,'435',35,'Fāṭir','فَاطِر',1,2,4,'وَإِن يُكَذِّبُوكَ فَقَدۡ كُذِّبَتۡ رُسُلٞ مِّن قَبۡلِكَۚ وَإِلَى ٱللَّهِ تُرۡجَعُ ٱلۡأُمُورُ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3660,22,'435',35,'Fāṭir','فَاطِر',2,3,5,'يَٰأَيُّهَا ٱلنَّاسُ إِنَّ وَعۡدَ ٱللَّهِ حَقّٞۖ فَلَا تَغُرَّنَّكُمُ ٱلۡحَيَوٰةُ ٱلدُّنۡيَا وَلَا يَغُرَّنَّكُمُۥ بِٱللَّهِ ٱلۡغَرُورُ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3661,22,'435',35,'Fāṭir','فَاطِر',3,4,6,'إِنَّ ٱلشَّيۡطَٰنَ لَكُمُۥ عَدُوّٞ فَٱتَّخِذُوهُۥ عَدُوًّاۚ إِنَّمَا يَدۡعُواْ حِزۡبَهُۥ لِيَكُونُواْ مِنۡ أَصۡحَٰبِ ٱلسَّعِيرِ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3662,22,'435',35,'Fāṭir','فَاطِر',4,6,7,'ٱلَّذِينَ كَفَرُواْ لَهُمُۥ عَذَابٞ شَدِيدٞۖ وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّٰلِحَٰتِ لَهُمُۥ مَغۡفِرَةٞ وَأَجۡرٞ كَبِيرٌ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3663,22,'435',35,'Fāṭir','فَاطِر',6,8,8,'أَفَمَن زُيِّنَ لَهُۥ سُوٓءُ عَمَلِهِۦ فَرَءَاهُۥ حَسَنٗاۖ فَإِنَّ ٱللَّهَ يُضِلُّ مَن يَشَآءُ وَيَهۡدِي مَن يَشَآءُۖ فَلَا تَذۡهَبۡ نَفۡسُكَ عَلَيۡهِمُۥ حَسَرَٰتٍۚ إِنَّ ٱللَّهَ عَلِيمُۢ بِمَا يَصۡنَعُونَ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3664,22,'435',35,'Fāṭir','فَاطِر',8,10,9,'وَٱللَّهُ ٱلَّذِي أَرۡسَلَ ٱلرِّيحَ فَتُثِيرُ سَحَابٗا فَسُقۡنَٰهُۥ إِلَىٰ بَلَدٖ مَّيۡتٖ فَأَحۡيَيۡنَا بِهِ ٱلۡأَرۡضَ بَعۡدَ مَوۡتِهَاۚ كَذَٰلِكَ ٱلنُّشُورُ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3665,22,'435',35,'Fāṭir','فَاطِر',10,12,10,'مَن كَانَ يُرِيدُ ٱلۡعِزَّةَ فَلِلَّهِ ٱلۡعِزَّةُ جَمِيعًاۚ إِلَيۡهِۦ يَصۡعَدُ ٱلۡكَلِمُ ٱلطَّيِّبُ وَٱلۡعَمَلُ ٱلصَّٰلِحُ يَرۡفَعُهُۥۚ وَٱلَّذِينَ يَمۡكُرُونَ ٱلسَّيِّـَٔاتِ لَهُمُۥ عَذَابٞ شَدِيدٞۖ وَمَكۡرُ أُوْلَٰٓئِكَ هُوَ يَبُورُ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3666,22,'435',35,'Fāṭir','فَاطِر',13,15,11,'وَٱللَّهُ خَلَقَكُمُۥ مِن تُرَابٖ ثُمَّ مِن نُّطۡفَةٖ ثُمَّ جَعَلَكُمُۥ أَزۡوَٰجٗاۚ وَمَا تَحۡمِلُ مِنۡ أُنثَىٰ وَلَا تَضَعُ إِلَّا بِعِلۡمِهِۦۚ وَمَا يُعَمَّرُ مِن مُّعَمَّرٖ وَلَا يُنقَصُ مِنۡ عُمُرِهِۦ إِلَّا فِي كِتَٰبٍۚ إِنَّ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٞ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3667,22,'436',35,'Fāṭir','فَاطِر',1,4,12,'وَمَا يَسۡتَوِي ٱلۡبَحۡرَانِ هَٰذَا عَذۡبٞ فُرَاتٞ سَآئِغٞ شَرَابُهُۥ وَهَٰذَا مِلۡحٌ أُجَاجٞۖ وَمِن كُلّٖ تَأۡكُلُونَ لَحۡمٗا طَرِيّٗا وَتَسۡتَخۡرِجُونَ حِلۡيَةٗ تَلۡبَسُونَهَاۖ وَتَرَى ٱلۡفُلۡكَ فِيهِۦ مَوَاخِرَ لِتَبۡتَغُواْ مِن فَضۡلِهِۦ وَلَعَلَّكُمُۥ تَشۡكُرُونَ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3668,22,'436',35,'Fāṭir','فَاطِر',4,7,13,'يُولِجُ ٱلَّيۡلَ فِي ٱلنَّهَارِ وَيُولِجُ ٱلنَّهَارَ فِي ٱلَّيۡلِ وَسَخَّرَ ٱلشَّمۡسَ وَٱلۡقَمَرَۖ كُلّٞ يَجۡرِي لِأَجَلٖ مُّسَمّٗىۚ ذَٰلِكُمُ ٱللَّهُ رَبُّكُمُۥ لَهُ ٱلۡمُلۡكُۚ وَٱلَّذِينَ تَدۡعُونَ مِن دُونِهِۦ مَا يَمۡلِكُونَ مِن قِطۡمِيرٍ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3669,22,'436',35,'Fāṭir','فَاطِر',7,9,14,'إِن تَدۡعُوهُمُۥ لَا يَسۡمَعُواْ دُعَآءَكُمُۥ وَلَوۡ سَمِعُواْ مَا ٱسۡتَجَابُواْ لَكُمُۥۖ وَيَوۡمَ ٱلۡقِيَٰمَةِ يَكۡفُرُونَ بِشِرۡكِكُمُۥۚ وَلَا يُنَبِّئُكَ مِثۡلُ خَبِيرٖ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3670,22,'436',35,'Fāṭir','فَاطِر',10,11,15,'۞ يَٰأَيُّهَا ٱلنَّاسُ أَنتُمُ ٱلۡفُقَرَآءُ اِ۪لَى ٱللَّهِۖ وَٱللَّهُ هُوَ ٱلۡغَنِيُّ ٱلۡحَمِيدُ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3671,22,'436',35,'Fāṭir','فَاطِر',11,11,16,'إِن يَشَأۡ يُذۡهِبۡكُمُۥ وَيَأۡتِ بِخَلۡقٖ جَدِيدٖ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3672,22,'436',35,'Fāṭir','فَاطِر',12,12,17,'وَمَا ذَٰلِكَ عَلَى ٱللَّهِ بِعَزِيزٖ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3673,22,'436',35,'Fāṭir','فَاطِر',12,15,18,'وَلَا تَزِرُ وَازِرَةٞ وِزۡرَ أُخۡرَىٰۚ وَإِن تَدۡعُ مُثۡقَلَةٌ إِلَىٰ حِمۡلِهَا لَا يُحۡمَلۡ مِنۡهُۥ شَيۡءٞ وَلَوۡ كَانَ ذَا قُرۡبَىٰۗ إِنَّمَا تُنذِرُ ٱلَّذِينَ يَخۡشَوۡنَ رَبَّهُمُۥ بِٱلۡغَيۡبِ وَأَقَامُواْ ٱلصَّلَوٰةَۚ وَمَن تَزَكَّىٰ فَإِنَّمَا يَتَزَكَّىٰ لِنَفۡسِهِۦۚ وَإِلَى ٱللَّهِ ٱلۡمَصِيرُ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3674,22,'437',35,'Fāṭir','فَاطِر',1,1,19,'وَمَا يَسۡتَوِي ٱلۡأَعۡمَىٰ وَٱلۡبَصِيرُ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3675,22,'437',35,'Fāṭir','فَاطِر',1,1,20,'وَلَا ٱلظُّلُمَٰتُ وَلَا ٱلنُّورُ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3676,22,'437',35,'Fāṭir','فَاطِر',2,2,21,'وَلَا ٱلظِّلُّ وَلَا ٱلۡحَرُورُ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (3677,22,'437',35,'Fāṭir','فَاطِر',2,4,22,'وَمَا يَسۡتَوِي ٱلۡأَحۡيَآءُ وَلَا ٱلۡأَمۡوَٰتُۚ إِنَّ ٱللَّهَ يُسۡمِعُ مَن يَشَآءُۖ وَمَا أَنتَ بِمُسۡمِعٖ مَّن فِي ٱلۡقُبُورِ ٢٢');
