INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4144,24,'469',40,'Ghāfir','غَافِر',9,11,22,'ذَٰلِكَ بِأَنَّهُمُۥ كَانَت تَّأۡتِيهِمُۥ رُسُلُهُمُۥ بِٱلۡبَيِّنَٰتِ فَكَفَرُواْ فَأَخَذَهُمُ ٱللَّهُۚ إِنَّهُۥ قَوِيّٞ شَدِيدُ ٱلۡعِقَابِ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4145,24,'469',40,'Ghāfir','غَافِر',11,12,23,'وَلَقَدۡ أَرۡسَلۡنَا مُوسَىٰ بِـَٔايَٰتِنَا وَسُلۡطَٰنٖ مُّبِينٍ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4146,24,'469',40,'Ghāfir','غَافِر',12,13,24,'إِلَىٰ فِرۡعَوۡنَ وَهَٰمَٰنَ وَقَٰرُونَ فَقَالُواْ سَٰحِرٞ كَذَّابٞ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4147,24,'469',40,'Ghāfir','غَافِر',13,15,25,'فَلَمَّا جَآءَهُمُۥ بِٱلۡحَقِّ مِنۡ عِندِنَا قَالُواْ ٱقۡتُلُواْ أَبۡنَآءَ ٱلَّذِينَ ءَامَنُواْ مَعَهُۥ وَٱسۡتَحۡيُواْ نِسَآءَهُمُۥۚ وَمَا كَيۡدُ ٱلۡكَٰفِرِينَ إِلَّا فِي ضَلَٰلٖ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4148,24,'470',40,'Ghāfir','غَافِر',1,2,26,'وَقَالَ فِرۡعَوۡنُ ذَرُونِيَ أَقۡتُلۡ مُوسَىٰ وَلۡيَدۡعُ رَبَّهُۥۖ إِنِّيَ أَخَافُ أَن يُبَدِّلَ دِينَكُمُۥ وَأَن يَظۡهَرَ فِي ٱلۡأَرۡضِ ٱلۡفَسَادُ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4149,24,'470',40,'Ghāfir','غَافِر',3,4,27,'وَقَالَ مُوسَىٰ إِنِّي عُذۡتُ بِرَبِّي وَرَبِّكُمُۥ مِن كُلِّ مُتَكَبِّرٖ لَّا يُؤۡمِنُ بِيَوۡمِ ٱلۡحِسَابِ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4150,24,'470',40,'Ghāfir','غَافِر',4,8,28,'وَقَالَ رَجُلٞ مُّؤۡمِنٞ مِّنۡ ءَالِ فِرۡعَوۡنَ يَكۡتُمُ إِيمَٰنَهُۥ أَتَقۡتُلُونَ رَجُلًا أَن يَقُولَ رَبِّيَ ٱللَّهُ وَقَدۡ جَآءَكُمُۥ بِٱلۡبَيِّنَٰتِ مِن رَّبِّكُمُۥۖ وَإِن يَكُ كَٰذِبٗا فَعَلَيۡهِۦ كَذِبُهُۥۖ وَإِن يَكُ صَادِقٗا يُصِبۡكُمُۥ بَعۡضُ ٱلَّذِي يَعِدُكُمُۥۖ إِنَّ ٱللَّهَ لَا يَهۡدِي مَنۡ هُوَ مُسۡرِفٞ كَذَّابٞ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4151,24,'470',40,'Ghāfir','غَافِر',8,11,29,'يَٰقَوۡمِ لَكُمُ ٱلۡمُلۡكُ ٱلۡيَوۡمَ ظَٰهِرِينَ فِي ٱلۡأَرۡضِ فَمَن يَنصُرُنَا مِنۢ بَأۡسِ ٱللَّهِ إِن جَآءَنَاۚ قَالَ فِرۡعَوۡنُ مَا أُرِيكُمُۥ إِلَّا مَا أَرَىٰ وَمَا أَهۡدِيكُمُۥ إِلَّا سَبِيلَ ٱلرَّشَادِ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4152,24,'470',40,'Ghāfir','غَافِر',11,12,30,'وَقَالَ ٱلَّذِي ءَامَنَ يَٰقَوۡمِ إِنِّيَ أَخَافُ عَلَيۡكُمُۥ مِثۡلَ يَوۡمِ ٱلۡأَحۡزَابِ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4153,24,'470',40,'Ghāfir','غَافِر',12,13,31,'مِثۡلَ دَأۡبِ قَوۡمِ نُوحٖ وَعَادٖ وَثَمُودَ وَٱلَّذِينَ مِنۢ بَعۡدِهِمُۥۚ وَمَا ٱللَّهُ يُرِيدُ ظُلۡمٗا لِّلۡعِبَادِ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4154,24,'470',40,'Ghāfir','غَافِر',14,14,32,'وَيَٰقَوۡمِ إِنِّيَ أَخَافُ عَلَيۡكُمُۥ يَوۡمَ ٱلتَّنَادِۦ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4155,24,'470',40,'Ghāfir','غَافِر',14,15,33,'يَوۡمَ تُوَلُّونَ مُدۡبِرِينَ مَا لَكُمُۥ مِنَ ٱللَّهِ مِنۡ عَاصِمٖۗ وَمَن يُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِنۡ هَادٖ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4156,24,'471',40,'Ghāfir','غَافِر',1,4,34,'وَلَقَدۡ جَآءَكُمُۥ يُوسُفُ مِن قَبۡلُ بِٱلۡبَيِّنَٰتِ فَمَا زِلۡتُمُۥ فِي شَكّٖ مِّمَّا جَآءَكُمُۥ بِهِۦۖ حَتَّىٰ إِذَا هَلَكَ قُلۡتُمُۥ لَن يَبۡعَثَ ٱللَّهُ مِنۢ بَعۡدِهِۦ رَسُولٗاۚ كَذَٰلِكَ يُضِلُّ ٱللَّهُ مَنۡ هُوَ مُسۡرِفٞ مُّرۡتَابٌ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4157,24,'471',40,'Ghāfir','غَافِر',4,6,35,'ٱلَّذِينَ يُجَٰدِلُونَ فِي ءَايَٰتِ ٱللَّهِ بِغَيۡرِ سُلۡطَٰنٍ أَتَىٰهُمُۥۖ كَبُرَ مَقۡتًا عِندَ ٱللَّهِ وَعِندَ ٱلَّذِينَ ءَامَنُواْۚ كَذَٰلِكَ يَطۡبَعُ ٱللَّهُ عَلَىٰ كُلِّ قَلۡبِ مُتَكَبِّرٖ جَبَّارٖ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4158,24,'471',40,'Ghāfir','غَافِر',6,7,36,'وَقَالَ فِرۡعَوۡنُ يَٰهَٰمَٰنُ ٱبۡنِ لِي صَرۡحٗا لَّعَلِّيَ أَبۡلُغُ ٱلۡأَسۡبَٰبَ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4159,24,'471',40,'Ghāfir','غَافِر',7,10,37,'أَسۡبَٰبَ ٱلسَّمَٰوَٰتِ فَأَطَّلِعُ إِلَىٰ إِلَٰهِ مُوسَىٰ وَإِنِّي لَأَظُنُّهُۥ كَٰذِبٗاۚ وَكَذَٰلِكَ زُيِّنَ لِفِرۡعَوۡنَ سُوٓءُ عَمَلِهِۦ وَصَدَّ عَنِ ٱلسَّبِيلِۚ وَمَا كَيۡدُ فِرۡعَوۡنَ إِلَّا فِي تَبَابٖ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4160,24,'471',40,'Ghāfir','غَافِر',10,11,38,'وَقَالَ ٱلَّذِي ءَامَنَ يَٰقَوۡمِ ٱتَّبِعُونِۦ أَهۡدِكُمُۥ سَبِيلَ ٱلرَّشَادِ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4161,24,'471',40,'Ghāfir','غَافِر',11,13,39,'يَٰقَوۡمِ إِنَّمَا هَٰذِهِ ٱلۡحَيَوٰةُ ٱلدُّنۡيَا مَتَٰعٞ وَإِنَّ ٱلۡأٓخِرَةَ هِيَ دَارُ ٱلۡقَرَارِ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4162,24,'471',40,'Ghāfir','غَافِر',13,15,40,'مَنۡ عَمِلَ سَيِّئَةٗ فَلَا يُجۡزَىٰ إِلَّا مِثۡلَهَاۖ وَمَنۡ عَمِلَ صَٰلِحٗا مِّن ذَكَرٍ أَوۡ أُنثَىٰ وَهُوَ مُؤۡمِنٞ فَأُوْلَٰٓئِكَ يُدۡخَلُونَ ٱلۡجَنَّةَ يُرۡزَقُونَ فِيهَا بِغَيۡرِ حِسَابٖ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4163,24,'472',40,'Ghāfir','غَافِر',1,1,41,'۞ وَيَٰقَوۡمِ مَا لِيَ أَدۡعُوكُمُۥ إِلَى ٱلنَّجَوٰةِ وَتَدۡعُونَنِي إِلَى ٱلنَّارِ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4164,24,'472',40,'Ghāfir','غَافِر',2,3,42,'تَدۡعُونَنِي لِأَكۡفُرَ بِٱللَّهِ وَأُشۡرِكَ بِهِۦ مَا لَيۡسَ لِي بِهِۦ عِلۡمٞ وَأَنَا۠ أَدۡعُوكُمُۥ إِلَى ٱلۡعَزِيزِ ٱلۡغَفَّٰرِ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4165,24,'472',40,'Ghāfir','غَافِر',3,5,43,'لَا جَرَمَ أَنَّمَا تَدۡعُونَنِي إِلَيۡهِۦ لَيۡسَ لَهُۥ دَعۡوَةٞ فِي ٱلدُّنۡيَا وَلَا فِي ٱلۡأٓخِرَةِ وَأَنَّ مَرَدَّنَا إِلَى ٱللَّهِ وَأَنَّ ٱلۡمُسۡرِفِينَ هُمُۥ أَصۡحَٰبُ ٱلنَّارِ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4166,24,'472',40,'Ghāfir','غَافِر',6,7,44,'فَسَتَذۡكُرُونَ مَا أَقُولُ لَكُمُۥۚ وَأُفَوِّضُ أَمۡرِي إِلَى ٱللَّهِۚ إِنَّ ٱللَّهَ بَصِيرُۢ بِٱلۡعِبَادِ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4167,24,'472',40,'Ghāfir','غَافِر',7,8,45,'فَوَقَىٰهُ ٱللَّهُ سَيِّـَٔاتِ مَا مَكَرُواْۖ وَحَاقَ بِـَٔالِ فِرۡعَوۡنَ سُوٓءُ ٱلۡعَذَابِ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4168,24,'472',40,'Ghāfir','غَافِر',8,10,46,'ٱلنَّارُ يُعۡرَضُونَ عَلَيۡهَا غُدُوّٗا وَعَشِيّٗاۚ وَيَوۡمَ تَقُومُ ٱلسَّاعَةُ ٱدۡخُلُواْ ءَالَ فِرۡعَوۡنَ أَشَدَّ ٱلۡعَذَابِ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4169,24,'472',40,'Ghāfir','غَافِر',10,12,47,'وَإِذۡ يَتَحَآجُّونَ فِي ٱلنَّارِ فَيَقُولُ ٱلضُّعَفَٰٓؤُاْ لِلَّذِينَ ٱسۡتَكۡبَرُواْ إِنَّا كُنَّا لَكُمُۥ تَبَعٗا فَهَلۡ أَنتُمُۥ مُغۡنُونَ عَنَّا نَصِيبٗا مِّنَ ٱلنَّارِ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4170,24,'472',40,'Ghāfir','غَافِر',12,14,48,'قَالَ ٱلَّذِينَ ٱسۡتَكۡبَرُواْ إِنَّا كُلّٞ فِيهَا إِنَّ ٱللَّهَ قَدۡ حَكَمَ بَيۡنَ ٱلۡعِبَادِ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4171,24,'472',40,'Ghāfir','غَافِر',14,15,49,'وَقَالَ ٱلَّذِينَ فِي ٱلنَّارِ لِخَزَنَةِ جَهَنَّمَ ٱدۡعُواْ رَبَّكُمُۥ يُخَفِّفۡ عَنَّا يَوۡمٗا مِّنَ ٱلۡعَذَابِ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4172,24,'473',40,'Ghāfir','غَافِر',1,2,50,'قَالُواْ أَوَلَمۡ تَكُ تَأۡتِيكُمُۥ رُسُلُكُمُۥ بِٱلۡبَيِّنَٰتِۖ قَالُواْ بَلَىٰۚ قَالُواْ فَٱدۡعُواْۗ وَمَا دُعَٰٓؤُاْ ٱلۡكَٰفِرِينَ إِلَّا فِي ضَلَٰلٍ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4173,24,'473',40,'Ghāfir','غَافِر',3,4,51,'إِنَّا لَنَنصُرُ رُسُلَنَا وَٱلَّذِينَ ءَامَنُواْ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَيَوۡمَ يَقُومُ ٱلۡأَشۡهَٰدُ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4174,24,'473',40,'Ghāfir','غَافِر',4,5,52,'يَوۡمَ لَا تَنفَعُ ٱلظَّٰلِمِينَ مَعۡذِرَتُهُمُۥۖ وَلَهُمُ ٱللَّعۡنَةُ وَلَهُمُۥ سُوٓءُ ٱلدَّارِ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4175,24,'473',40,'Ghāfir','غَافِر',5,6,53,'وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡهُدَىٰ وَأَوۡرَثۡنَا بَنِي إِسۡرَٰٓءِيلَ ٱلۡكِتَٰبَ ٥٣');
