INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4211,24,'477',41,'Fuṣṣilat','فُصِّلَت',7,8,5,'قُلۡ إِنَّمَا أَنَا۠ بَشَرٞ مِّثۡلُكُمُۥ يُوحَىٰ إِلَيَّ أَنَّمَا إِلَٰهُكُمُۥ إِلَٰهٞ وَٰحِدٞ فَٱسۡتَقِيمُواْ إِلَيۡهِۦ وَٱسۡتَغۡفِرُوهُۥۗ وَوَيۡلٞ لِّلۡمُشۡرِكِينَ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4212,24,'477',41,'Fuṣṣilat','فُصِّلَت',8,9,6,'ٱلَّذِينَ لَا يُؤۡتُونَ ٱلزَّكَوٰةَ وَهُمُۥ بِٱلۡأٓخِرَةِ هُمُۥ كَٰفِرُونَ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4213,24,'477',41,'Fuṣṣilat','فُصِّلَت',9,10,7,'إِنَّ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّٰلِحَٰتِ لَهُمُۥ أَجۡرٌ غَيۡرُ مَمۡنُونٖ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4214,24,'477',41,'Fuṣṣilat','فُصِّلَت',10,12,8,'۞ قُلۡ أَى۪نَّكُمُۥ لَتَكۡفُرُونَ بِٱلَّذِي خَلَقَ ٱلۡأَرۡضَ فِي يَوۡمَيۡنِ وَتَجۡعَلُونَ لَهُۥ أَندَادٗاۚ ذَٰلِكَ رَبُّ ٱلۡعَٰلَمِينَ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4215,24,'477',41,'Fuṣṣilat','فُصِّلَت',12,14,9,'وَجَعَلَ فِيهَا رَوَٰسِيَ مِن فَوۡقِهَا وَبَٰرَكَ فِيهَا وَقَدَّرَ فِيهَا أَقۡوَٰتَهَا فِي أَرۡبَعَةِ أَيَّامٖ سَوَآءٗ لِّلسَّآئِلِينَ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4216,24,'477',41,'Fuṣṣilat','فُصِّلَت',14,15,10,'ثُمَّ ٱسۡتَوَىٰ إِلَى ٱلسَّمَآءِ وَهِيَ دُخَانٞ فَقَالَ لَهَا وَلِلۡأَرۡضِ ٱئۡتِيَا طَوۡعًا أَوۡ كَرۡهٗا قَالَتَا أَتَيۡنَا طَآئِعِينَ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4217,24,'478',41,'Fuṣṣilat','فُصِّلَت',1,3,11,'فَقَضَىٰهُنَّ سَبۡعَ سَمَٰوَاتٖ فِي يَوۡمَيۡنِ وَأَوۡحَىٰ فِي كُلِّ سَمَآءٍ أَمۡرَهَاۚ وَزَيَّنَّا ٱلسَّمَآءَ ٱلدُّنۡيَا بِمَصَٰبِيحَ وَحِفۡظٗاۚ ذَٰلِكَ تَقۡدِيرُ ٱلۡعَزِيزِ ٱلۡعَلِيمِ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4218,24,'478',41,'Fuṣṣilat','فُصِّلَت',3,4,12,'فَإِنۡ أَعۡرَضُواْ فَقُلۡ أَنذَرۡتُكُمُۥ صَٰعِقَةٗ مِّثۡلَ صَٰعِقَةِ عَادٖ وَثَمُودَ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4219,24,'478',41,'Fuṣṣilat','فُصِّلَت',4,6,13,'إِذۡ جَآءَتۡهُمُ ٱلرُّسُلُ مِنۢ بَيۡنِ أَيۡدِيهِمُۥ وَمِنۡ خَلۡفِهِمُۥ أَلَّا تَعۡبُدُواْ إِلَّا ٱللَّهَۖ قَالُواْ لَوۡ شَآءَ رَبُّنَا لَأَنزَلَ مَلَٰٓئِكَةٗ فَإِنَّا بِمَا أُرۡسِلۡتُمُۥ بِهِۦ كَٰفِرُونَ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4220,24,'478',41,'Fuṣṣilat','فُصِّلَت',6,8,14,'فَأَمَّا عَادٞ فَٱسۡتَكۡبَرُواْ فِي ٱلۡأَرۡضِ بِغَيۡرِ ٱلۡحَقِّ وَقَالُواْ مَنۡ أَشَدُّ مِنَّا قُوَّةًۖ أَوَلَمۡ يَرَوۡاْ أَنَّ ٱللَّهَ ٱلَّذِي خَلَقَهُمُۥ هُوَ أَشَدُّ مِنۡهُمُۥ قُوَّةٗۖ وَكَانُواْ بِـَٔايَٰتِنَا يَجۡحَدُونَ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4221,24,'478',41,'Fuṣṣilat','فُصِّلَت',9,11,15,'فَأَرۡسَلۡنَا عَلَيۡهِمُۥ رِيحٗا صَرۡصَرٗا فِي أَيَّامٖ نَّحۡسَاتٖ لِّنُذِيقَهُمُۥ عَذَابَ ٱلۡخِزۡيِ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۖ وَلَعَذَابُ ٱلۡأٓخِرَةِ أَخۡزَىٰۖ وَهُمُۥ لَا يُنصَرُونَ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4222,24,'478',41,'Fuṣṣilat','فُصِّلَت',11,12,16,'وَأَمَّا ثَمُودُ فَهَدَيۡنَٰهُمُۥ فَٱسۡتَحَبُّواْ ٱلۡعَمَىٰ عَلَى ٱلۡهُدَىٰ فَأَخَذَتۡهُمُۥ صَٰعِقَةُ ٱلۡعَذَابِ ٱلۡهُونِ بِمَا كَانُواْ يَكۡسِبُونَ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4223,24,'478',41,'Fuṣṣilat','فُصِّلَت',13,13,17,'وَنَجَّيۡنَا ٱلَّذِينَ ءَامَنُواْ وَكَانُواْ يَتَّقُونَ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4224,24,'478',41,'Fuṣṣilat','فُصِّلَت',13,14,18,'وَيَوۡمَ يُحۡشَرُ أَعۡدَآءُ ٱللَّهِ إِلَى ٱلنَّارِ فَهُمُۥ يُوزَعُونَ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4225,24,'478',41,'Fuṣṣilat','فُصِّلَت',14,15,19,'حَتَّىٰ إِذَا مَا جَآءُوهَا شَهِدَ عَلَيۡهِمُۥ سَمۡعُهُمُۥ وَأَبۡصَٰرُهُمُۥ وَجُلُودُهُمُۥ بِمَا كَانُواْ يَعۡمَلُونَ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4226,24,'479',41,'Fuṣṣilat','فُصِّلَت',1,2,20,'وَقَالُواْ لِجُلُودِهِمُۥ لِمَ شَهِدتُّمُۥ عَلَيۡنَاۖ قَالُواْ أَنطَقَنَا ٱللَّهُ ٱلَّذِي أَنطَقَ كُلَّ شَيۡءٖۚ وَهُوَ خَلَقَكُمُۥ أَوَّلَ مَرَّةٖ وَإِلَيۡهِۦ تُرۡجَعُونَ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4227,24,'479',41,'Fuṣṣilat','فُصِّلَت',3,4,21,'وَمَا كُنتُمُۥ تَسۡتَتِرُونَ أَن يَشۡهَدَ عَلَيۡكُمُۥ سَمۡعُكُمُۥ وَلَا أَبۡصَٰرُكُمُۥ وَلَا جُلُودُكُمُۥ وَلَٰكِن ظَنَنتُمُۥ أَنَّ ٱللَّهَ لَا يَعۡلَمُ كَثِيرٗا مِّمَّا تَعۡمَلُونَ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4228,24,'479',41,'Fuṣṣilat','فُصِّلَت',5,6,22,'وَذَٰلِكُمُۥ ظَنُّكُمُ ٱلَّذِي ظَنَنتُمُۥ بِرَبِّكُمُۥ أَرۡدَىٰكُمُۥ فَأَصۡبَحۡتُمُۥ مِنَ ٱلۡخَٰسِرِينَ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4229,24,'479',41,'Fuṣṣilat','فُصِّلَت',6,7,23,'فَإِن يَصۡبِرُواْ فَٱلنَّارُ مَثۡوٗى لَّهُمُۥۖ وَإِن يَسۡتَعۡتِبُواْ فَمَا هُمُۥ مِنَ ٱلۡمُعۡتَبِينَ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4230,24,'479',41,'Fuṣṣilat','فُصِّلَت',7,9,24,'۞ وَقَيَّضۡنَا لَهُمُۥ قُرَنَآءَ فَزَيَّنُواْ لَهُمُۥ مَا بَيۡنَ أَيۡدِيهِمُۥ وَمَا خَلۡفَهُمُۥ وَحَقَّ عَلَيۡهِمُ ٱلۡقَوۡلُ فِي أُمَمٖ قَدۡ خَلَتۡ مِن قَبۡلِهِمُۥ مِنَ ٱلۡجِنِّ وَٱلۡإِنسِۖ إِنَّهُمُۥ كَانُواْ خَٰسِرِينَ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4231,24,'479',41,'Fuṣṣilat','فُصِّلَت',10,11,25,'وَقَالَ ٱلَّذِينَ كَفَرُواْ لَا تَسۡمَعُواْ لِهَٰذَا ٱلۡقُرَانِ وَٱلۡغَوۡاْ فِيهِۦ لَعَلَّكُمُۥ تَغۡلِبُونَ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4232,24,'479',41,'Fuṣṣilat','فُصِّلَت',11,12,26,'فَلَنُذِيقَنَّ ٱلَّذِينَ كَفَرُواْ عَذَابٗا شَدِيدٗا وَلَنَجۡزِيَنَّهُمُۥ أَسۡوَأَ ٱلَّذِي كَانُواْ يَعۡمَلُونَ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4233,24,'479',41,'Fuṣṣilat','فُصِّلَت',12,13,27,'ذَٰلِكَ جَزَآءُ اَ۬عۡدَآءِ ٱللَّهِ ٱلنَّارُۖ لَهُمُۥ فِيهَا دَارُ ٱلۡخُلۡدِ جَزَآءَۢ بِمَا كَانُواْ بِـَٔايَٰتِنَا يَجۡحَدُونَ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4234,24,'479',41,'Fuṣṣilat','فُصِّلَت',14,15,28,'وَقَالَ ٱلَّذِينَ كَفَرُواْ رَبَّنَا أَرۡنَا ٱلَّذَيۡٓنِّ أَضَلَّانَا مِنَ ٱلۡجِنِّ وَٱلۡإِنسِ نَجۡعَلۡهُمَا تَحۡتَ أَقۡدَامِنَا لِيَكُونَا مِنَ ٱلۡأَسۡفَلِينَ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4235,24,'480',41,'Fuṣṣilat','فُصِّلَت',1,3,29,'إِنَّ ٱلَّذِينَ قَالُواْ رَبُّنَا ٱللَّهُ ثُمَّ ٱسۡتَقَٰمُواْ تَتَنَزَّلُ عَلَيۡهِمُ ٱلۡمَلَٰٓئِكَةُ أَلَّا تَخَافُواْ وَلَا تَحۡزَنُواْ وَأَبۡشِرُواْ بِٱلۡجَنَّةِ ٱلَّتِي كُنتُمُۥ تُوعَدُونَ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4236,24,'480',41,'Fuṣṣilat','فُصِّلَت',3,5,30,'نَحۡنُ أَوۡلِيَآؤُكُمُۥ فِي ٱلۡحَيَوٰةِ ٱلدُّنۡيَا وَفِي ٱلۡأٓخِرَةِۖ وَلَكُمُۥ فِيهَا مَا تَشۡتَهِي أَنفُسُكُمُۥ وَلَكُمُۥ فِيهَا مَا تَدَّعُونَ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4237,24,'480',41,'Fuṣṣilat','فُصِّلَت',5,5,31,'نُزُلٗا مِّنۡ غَفُورٖ رَّحِيمٖ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4238,24,'480',41,'Fuṣṣilat','فُصِّلَت',5,7,32,'وَمَنۡ أَحۡسَنُ قَوۡلٗا مِّمَّن دَعَا إِلَى ٱللَّهِ وَعَمِلَ صَٰلِحٗا وَقَالَ إِنَّنِي مِنَ ٱلۡمُسۡلِمِينَ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4239,24,'480',41,'Fuṣṣilat','فُصِّلَت',7,9,33,'وَلَا تَسۡتَوِي ٱلۡحَسَنَةُ وَلَا ٱلسَّيِّئَةُۚ ٱدۡفَعۡ بِٱلَّتِي هِيَ أَحۡسَنُ فَإِذَا ٱلَّذِي بَيۡنَكَ وَبَيۡنَهُۥ عَدَٰوَةٞ كَأَنَّهُۥ وَلِيٌّ حَمِيمٞ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4240,24,'480',41,'Fuṣṣilat','فُصِّلَت',9,10,34,'وَمَا يُلَقَّىٰهَا إِلَّا ٱلَّذِينَ صَبَرُواْ وَمَا يُلَقَّىٰهَا إِلَّا ذُو حَظٍّ عَظِيمٖ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4241,24,'480',41,'Fuṣṣilat','فُصِّلَت',10,11,35,'وَإِمَّا يَنزَغَنَّكَ مِنَ ٱلشَّيۡطَٰنِ نَزۡغٞ فَٱسۡتَعِذۡ بِٱللَّهِۖ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلۡعَلِيمُ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4242,24,'480',41,'Fuṣṣilat','فُصِّلَت',11,14,36,'وَمِنۡ ءَايَٰتِهِ ٱلَّيۡلُ وَٱلنَّهَارُ وَٱلشَّمۡسُ وَٱلۡقَمَرُۚ لَا تَسۡجُدُواْ لِلشَّمۡسِ وَلَا لِلۡقَمَرِ وَٱسۡجُدُواْۤ لِلَّهِۤ ٱلَّذِي خَلَقَهُنَّ إِن كُنتُمُۥ إِيَّاهُۥ تَعۡبُدُونَ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4243,24,'480',41,'Fuṣṣilat','فُصِّلَت',14,15,37,'فَإِنِ ٱسۡتَكۡبَرُواْ فَٱلَّذِينَ عِندَ رَبِّكَ يُسَبِّحُونَ لَهُۥ بِٱلَّيۡلِ وَٱلنَّهَارِ وَهُمُۥ لَا يَسۡـَٔمُونَ۩ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4244,24,'481',41,'Fuṣṣilat','فُصِّلَت',1,3,38,'وَمِنۡ ءَايَٰتِهِۦ أَنَّكَ تَرَى ٱلۡأَرۡضَ خَٰشِعَةٗ فَإِذَا أَنزَلۡنَا عَلَيۡهَا ٱلۡمَآءَ ٱهۡتَزَّتۡ وَرَبَتۡۚ إِنَّ ٱلَّذِي أَحۡيَاهَا لَمُحۡيِ ٱلۡمَوۡتَىٰۚ إِنَّهُۥ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٌ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4245,24,'481',41,'Fuṣṣilat','فُصِّلَت',3,5,39,'إِنَّ ٱلَّذِينَ يُلۡحِدُونَ فِي ءَايَٰتِنَا لَا يَخۡفَوۡنَ عَلَيۡنَاۗ أَفَمَن يُلۡقَىٰ فِي ٱلنَّارِ خَيۡرٌ أَم مَّن يَأۡتِي ءَامِنٗا يَوۡمَ ٱلۡقِيَٰمَةِۚ ٱعۡمَلُواْ مَا شِئۡتُمُۥ إِنَّهُۥ بِمَا تَعۡمَلُونَ بَصِيرٌ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4246,24,'481',41,'Fuṣṣilat','فُصِّلَت',5,6,40,'إِنَّ ٱلَّذِينَ كَفَرُواْ بِٱلذِّكۡرِ لَمَّا جَآءَهُمُۥۖ وَإِنَّهُۥ لَكِتَٰبٌ عَزِيزٞ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4247,24,'481',41,'Fuṣṣilat','فُصِّلَت',6,7,41,'لَّا يَأۡتِيهِ ٱلۡبَٰطِلُ مِنۢ بَيۡنِ يَدَيۡهِۦ وَلَا مِنۡ خَلۡفِهِۦۖ تَنزِيلٞ مِّنۡ حَكِيمٍ حَمِيدٖ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4248,24,'481',41,'Fuṣṣilat','فُصِّلَت',7,8,42,'مَّا يُقَالُ لَكَ إِلَّا مَا قَدۡ قِيلَ لِلرُّسُلِ مِن قَبۡلِكَۚ إِنَّ رَبَّكَ لَذُو مَغۡفِرَةٖ وَذُو عِقَابٍ أَلِيمٖ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4249,24,'481',41,'Fuṣṣilat','فُصِّلَت',9,12,43,'وَلَوۡ جَعَلۡنَٰهُۥ قُرَانًا أَعۡجَمِيّٗا لَّقَالُواْ لَوۡلَا فُصِّلَتۡ ءَايَٰتُهُۥۖ ءَا۬عۡجَمِيّٞ وَعَرَبِيّٞۗ قُلۡ هُوَ لِلَّذِينَ ءَامَنُواْ هُدٗى وَشِفَآءٞۚ وَٱلَّذِينَ لَا يُؤۡمِنُونَ فِي ءَاذَانِهِمُۥ وَقۡرٞ وَهُوَ عَلَيۡهِمُۥ عَمًىۚ أُوْلَٰٓئِكَ يُنَادَوۡنَ مِن مَّكَانِۭ بَعِيدٖ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4250,24,'481',41,'Fuṣṣilat','فُصِّلَت',12,14,44,'وَلَقَدۡ ءَاتَيۡنَا مُوسَى ٱلۡكِتَٰبَ فَٱخۡتُلِفَ فِيهِۦۚ وَلَوۡلَا كَلِمَةٞ سَبَقَتۡ مِن رَّبِّكَ لَقُضِيَ بَيۡنَهُمُۥۚ وَإِنَّهُمُۥ لَفِي شَكّٖ مِّنۡهُۥ مُرِيبٖ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4251,24,'481',41,'Fuṣṣilat','فُصِّلَت',14,15,45,'مَّنۡ عَمِلَ صَٰلِحٗا فَلِنَفۡسِهِۦۖ وَمَنۡ أَسَآءَ فَعَلَيۡهَاۗ وَمَا رَبُّكَ بِظَلَّٰمٖ لِّلۡعَبِيدِ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4252,25,'482',41,'Fuṣṣilat','فُصِّلَت',1,3,46,'۞ إِلَيۡهِۦ يُرَدُّ عِلۡمُ ٱلسَّاعَةِۚ وَمَا تَخۡرُجُ مِن ثَمَرَتٖ مِّنۡ أَكۡمَامِهَا وَمَا تَحۡمِلُ مِنۡ أُنثَىٰ وَلَا تَضَعُ إِلَّا بِعِلۡمِهِۦۚ وَيَوۡمَ يُنَادِيهِمُۥ أَيۡنَ شُرَكَآءِيَ قَالُواْ ءَاذَنَّٰكَ مَا مِنَّا مِن شَهِيدٖ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4253,25,'482',41,'Fuṣṣilat','فُصِّلَت',3,4,47,'وَضَلَّ عَنۡهُمُۥ مَا كَانُواْ يَدۡعُونَ مِن قَبۡلُۖ وَظَنُّواْ مَا لَهُمُۥ مِن مَّحِيصٖ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4254,25,'482',41,'Fuṣṣilat','فُصِّلَت',5,6,48,'لَّا يَسۡـَٔمُ ٱلۡإِنسَٰنُ مِن دُعَآءِ ٱلۡخَيۡرِ وَإِن مَّسَّهُ ٱلشَّرُّ فَيَـُٔوسٞ قَنُوطٞ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4255,25,'482',41,'Fuṣṣilat','فُصِّلَت',6,9,49,'وَلَئِنۡ أَذَقۡنَٰهُۥ رَحۡمَةٗ مِّنَّا مِنۢ بَعۡدِ ضَرَّآءَ مَسَّتۡهُۥ لَيَقُولَنَّ هَٰذَا لِي وَمَا أَظُنُّ ٱلسَّاعَةَ قَآئِمَةٗ وَلَئِن رُّجِعۡتُ إِلَىٰ رَبِّي إِنَّ لِي عِندَهُۥ لَلۡحُسۡنَىٰۚ فَلَنُنَبِّئَنَّ ٱلَّذِينَ كَفَرُواْ بِمَا عَمِلُواْ وَلَنُذِيقَنَّهُمُۥ مِنۡ عَذَابٍ غَلِيظٖ ٤٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4256,25,'482',41,'Fuṣṣilat','فُصِّلَت',9,11,50,'وَإِذَا أَنۡعَمۡنَا عَلَى ٱلۡإِنسَٰنِ أَعۡرَضَ وَنَـَٔا بِجَانِبِهِۦ وَإِذَا مَسَّهُ ٱلشَّرُّ فَذُو دُعَآءٍ عَرِيضٖ ٥٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4257,25,'482',41,'Fuṣṣilat','فُصِّلَت',11,12,51,'قُلۡ أَرَءَيۡتُمُۥ إِن كَانَ مِنۡ عِندِ ٱللَّهِ ثُمَّ كَفَرۡتُمُۥ بِهِۦ مَنۡ أَضَلُّ مِمَّنۡ هُوَ فِي شِقَاقِۭ بَعِيدٖ ٥١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4258,25,'482',41,'Fuṣṣilat','فُصِّلَت',12,14,52,'سَنُرِيهِمُۥ ءَايَٰتِنَا فِي ٱلۡأٓفَاقِ وَفِي أَنفُسِهِمُۥ حَتَّىٰ يَتَبَيَّنَ لَهُمُۥ أَنَّهُ ٱلۡحَقُّۗ أَوَلَمۡ يَكۡفِ بِرَبِّكَ أَنَّهُۥ عَلَىٰ كُلِّ شَيۡءٖ شَهِيدٌ ٥٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4259,25,'482',41,'Fuṣṣilat','فُصِّلَت',14,15,53,'أَلَا إِنَّهُمُۥ فِي مِرۡيَةٖ مِّن لِّقَآءِ رَبِّهِمُۥۗ أَلَا إِنَّهُۥ بِكُلِّ شَيۡءٖ مُّحِيطُۢ ٥٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4260,25,'483',42,'Ash-Shūra','الشُّوري',3,4,1,'حمٓۚ عٓسٓقٓۚ كَذَٰلِكَ يُوحَيٰ إِلَيۡكَ وَإِلَى ٱلَّذِينَ مِن قَبۡلِكَ ٱللَّهُ ٱلۡعَزِيزُ ٱلۡحَكِيمُ ١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4261,25,'483',42,'Ash-Shūra','الشُّوري',4,5,2,'لَهُۥ مَا فِي ٱلسَّمَٰوَٰتِ وَمَا فِي ٱلۡأَرۡضِۖ وَهُوَ ٱلۡعَلِيُّ ٱلۡعَظِيمُ ٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4262,25,'483',42,'Ash-Shūra','الشُّوري',5,7,3,'تَكَادُ ٱلسَّمَٰوَٰتُ يَتَفَطَّرۡنَ مِن فَوۡقِهِنَّۚ وَٱلۡمَلَٰٓئِكَةُ يُسَبِّحُونَ بِحَمۡدِ رَبِّهِمُۥ وَيَسۡتَغۡفِرُونَ لِمَن فِي ٱلۡأَرۡضِۗ أَلَا إِنَّ ٱللَّهَ هُوَ ٱلۡغَفُورُ ٱلرَّحِيمُ ٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4263,25,'483',42,'Ash-Shūra','الشُّوري',7,8,4,'وَٱلَّذِينَ ٱتَّخَذُواْ مِن دُونِهِۦ أَوۡلِيَآءَ ٱللَّهُ حَفِيظٌ عَلَيۡهِمُۥ وَمَا أَنتَ عَلَيۡهِمُۥ بِوَكِيلٖ ٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4264,25,'483',42,'Ash-Shūra','الشُّوري',9,11,5,'وَكَذَٰلِكَ أَوۡحَيۡنَا إِلَيۡكَ قُرَانًا عَرَبِيّٗا لِّتُنذِرَ أُمَّ ٱلۡقُرَىٰ وَمَنۡ حَوۡلَهَا وَتُنذِرَ يَوۡمَ ٱلۡجَمۡعِ لَا رَيۡبَ فِيهِۦۚ فَرِيقٞ فِي ٱلۡجَنَّةِ وَفَرِيقٞ فِي ٱلسَّعِيرِ ٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4265,25,'483',42,'Ash-Shūra','الشُّوري',11,12,6,'وَلَوۡ شَآءَ ٱللَّهُ لَجَعَلَهُمُۥ أُمَّةٗ وَٰحِدَةٗ وَلَٰكِن يُدۡخِلُ مَن يَشَآءُ فِي رَحۡمَتِهِۦۚ وَٱلظَّٰلِمُونَ مَا لَهُمُۥ مِن وَلِيّٖ وَلَا نَصِيرٍ ٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4266,25,'483',42,'Ash-Shūra','الشُّوري',12,14,7,'أَمِ ٱتَّخَذُواْ مِن دُونِهِۦ أَوۡلِيَآءَۖ فَٱللَّهُ هُوَ ٱلۡوَلِيُّ وَهُوَ يُحۡيِ ٱلۡمَوۡتَىٰ وَهُوَ عَلَىٰ كُلِّ شَيۡءٖ قَدِيرٞ ٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4267,25,'483',42,'Ash-Shūra','الشُّوري',14,15,8,'وَمَا ٱخۡتَلَفۡتُمُۥ فِيهِۦ مِن شَيۡءٖ فَحُكۡمُهُۥ إِلَى ٱللَّهِۚ ذَٰلِكُمُ ٱللَّهُ رَبِّي عَلَيۡهِۦ تَوَكَّلۡتُ وَإِلَيۡهِۦ أُنِيبُ ٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4268,25,'484',42,'Ash-Shūra','الشُّوري',1,3,9,'فَاطِرُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ جَعَلَ لَكُمُۥ مِنۡ أَنفُسِكُمُۥ أَزۡوَٰجٗا وَمِنَ ٱلۡأَنۡعَٰمِ أَزۡوَٰجٗا يَذۡرَؤُكُمُۥ فِيهِۦۚ لَيۡسَ كَمِثۡلِهِۦ شَيۡءٞۖ وَهُوَ ٱلسَّمِيعُ ٱلۡبَصِيرُ ٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4269,25,'484',42,'Ash-Shūra','الشُّوري',3,4,10,'لَهُۥ مَقَالِيدُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۖ يَبۡسُطُ ٱلرِّزۡقَ لِمَن يَشَآءُ وَيَقۡدِرُۚ إِنَّهُۥ بِكُلِّ شَيۡءٍ عَلِيمٞ ١٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4270,25,'484',42,'Ash-Shūra','الشُّوري',4,8,11,'۞ شَرَعَ لَكُمُۥ مِنَ ٱلدِّينِ مَا وَصَّىٰ بِهِۦ نُوحٗا وَٱلَّذِي أَوۡحَيۡنَا إِلَيۡكَ وَمَا وَصَّيۡنَا بِهِۦ إِبۡرَٰهِيمَ وَمُوسَىٰ وَعِيسَىٰۖ أَنۡ أَقِيمُواْ ٱلدِّينَ وَلَا تَتَفَرَّقُواْ فِيهِۦۚ كَبُرَ عَلَى ٱلۡمُشۡرِكِينَ مَا تَدۡعُوهُمُۥ إِلَيۡهِۚ ٱللَّهُ يَجۡتَبِي إِلَيۡهِۦ مَن يَشَآءُ وَيَهۡدِي إِلَيۡهِۦ مَن يُنِيبُ ١١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4271,25,'484',42,'Ash-Shūra','الشُّوري',8,11,12,'وَمَا تَفَرَّقُواْ إِلَّا مِنۢ بَعۡدِ مَا جَآءَهُمُ ٱلۡعِلۡمُ بَغۡيَۢا بَيۡنَهُمُۥۚ وَلَوۡلَا كَلِمَةٞ سَبَقَتۡ مِن رَّبِّكَ إِلَىٰ أَجَلٖ مُّسَمّٗى لَّقُضِيَ بَيۡنَهُمُۥۚ وَإِنَّ ٱلَّذِينَ أُورِثُواْ ٱلۡكِتَٰبَ مِنۢ بَعۡدِهِمُۥ لَفِي شَكّٖ مِّنۡهُۥ مُرِيبٖ ١٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4272,25,'484',42,'Ash-Shūra','الشُّوري',11,15,13,'فَلِذَٰلِكَ فَٱدۡعُۖ وَٱسۡتَقِمۡ كَمَا أُمِرۡتَۖ وَلَا تَتَّبِعۡ أَهۡوَآءَهُمُۥۖ وَقُلۡ ءَامَنتُ بِمَا أَنزَلَ ٱللَّهُ مِن كِتَٰبٖۖ وَأُمِرۡتُ لِأَعۡدِلَ بَيۡنَكُمُۖ ٱللَّهُ رَبُّنَا وَرَبُّكُمُۥۖ لَنَا أَعۡمَٰلُنَا وَلَكُمُۥ أَعۡمَٰلُكُمُۥۖ لَا حُجَّةَ بَيۡنَنَا وَبَيۡنَكُمُۖ ٱللَّهُ يَجۡمَعُ بَيۡنَنَاۖ وَإِلَيۡهِ ٱلۡمَصِيرُ ١٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4273,25,'485',42,'Ash-Shūra','الشُّوري',1,2,14,'وَٱلَّذِينَ يُحَآجُّونَ فِي ٱللَّهِ مِنۢ بَعۡدِ مَا ٱسۡتُجِيبَ لَهُۥ حُجَّتُهُمُۥ دَاحِضَةٌ عِندَ رَبِّهِمُۥ وَعَلَيۡهِمُۥ غَضَبٞ وَلَهُمُۥ عَذَابٞ شَدِيدٌ ١٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4274,25,'485',42,'Ash-Shūra','الشُّوري',3,4,15,'ٱللَّهُ ٱلَّذِي أَنزَلَ ٱلۡكِتَٰبَ بِٱلۡحَقِّ وَٱلۡمِيزَانَۗ وَمَا يُدۡرِيكَ لَعَلَّ ٱلسَّاعَةَ قَرِيبٞ ١٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4275,25,'485',42,'Ash-Shūra','الشُّوري',4,6,16,'يَسۡتَعۡجِلُ بِهَا ٱلَّذِينَ لَا يُؤۡمِنُونَ بِهَاۖ وَٱلَّذِينَ ءَامَنُواْ مُشۡفِقُونَ مِنۡهَا وَيَعۡلَمُونَ أَنَّهَا ٱلۡحَقُّۗ أَلَا إِنَّ ٱلَّذِينَ يُمَارُونَ فِي ٱلسَّاعَةِ لَفِي ضَلَٰلِۭ بَعِيدٍ ١٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4276,25,'485',42,'Ash-Shūra','الشُّوري',7,7,17,'ٱللَّهُ لَطِيفُۢ بِعِبَادِهِۦ يَرۡزُقُ مَن يَشَآءُۖ وَهُوَ ٱلۡقَوِيُّ ٱلۡعَزِيزُ ١٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4277,25,'485',42,'Ash-Shūra','الشُّوري',8,10,18,'مَن كَانَ يُرِيدُ حَرۡثَ ٱلۡأٓخِرَةِ نَزِدۡ لَهُۥ فِي حَرۡثِهِۦۖ وَمَن كَانَ يُرِيدُ حَرۡثَ ٱلدُّنۡيَا نُؤۡتِهِۦ مِنۡهَا وَمَا لَهُۥ فِي ٱلۡأٓخِرَةِ مِن نَّصِيبٍ ١٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4278,25,'485',42,'Ash-Shūra','الشُّوري',10,12,19,'أَمۡ لَهُمُۥ شُرَكَٰٓؤُاْ شَرَعُواْ لَهُمُۥ مِنَ ٱلدِّينِ مَا لَمۡ يَأۡذَنۢ بِهِ ٱللَّهُۚ وَلَوۡلَا كَلِمَةُ ٱلۡفَصۡلِ لَقُضِيَ بَيۡنَهُمُۥۗ وَإِنَّ ٱلظَّٰلِمِينَ لَهُمُۥ عَذَابٌ أَلِيمٞ ١٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4279,25,'485',42,'Ash-Shūra','الشُّوري',12,15,20,'تَرَى ٱلظَّٰلِمِينَ مُشۡفِقِينَ مِمَّا كَسَبُواْ وَهُوَ وَاقِعُۢ بِهِمُۥۗ وَٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّٰلِحَٰتِ فِي رَوۡضَاتِ ٱلۡجَنَّاتِۖ لَهُمُۥ مَا يَشَآءُونَ عِندَ رَبِّهِمُۥۚ ذَٰلِكَ هُوَ ٱلۡفَضۡلُ ٱلۡكَبِيرُ ٢٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4280,25,'486',42,'Ash-Shūra','الشُّوري',1,3,21,'ذَٰلِكَ ٱلَّذِي يَبۡشُرُ ٱللَّهُ عِبَادَهُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّٰلِحَٰتِۗ قُل لَّا أَسۡـَٔلُكُمُۥ عَلَيۡهِۦ أَجۡرًا إِلَّا ٱلۡمَوَدَّةَ فِي ٱلۡقُرۡبَىٰۗ وَمَن يَقۡتَرِفۡ حَسَنَةٗ نَّزِدۡ لَهُۥ فِيهَا حُسۡنًاۚ إِنَّ ٱللَّهَ غَفُورٞ شَكُورٌ ٢١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4281,25,'486',42,'Ash-Shūra','الشُّوري',3,5,22,'أَمۡ يَقُولُونَ ٱفۡتَرَىٰ عَلَى ٱللَّهِ كَذِبٗاۖ فَإِن يَشَإِ ٱللَّهُ يَخۡتِمۡ عَلَىٰ قَلۡبِكَۗ وَيَمۡحُ ٱللَّهُ ٱلۡبَٰطِلَ وَيُحِقُّ ٱلۡحَقَّ بِكَلِمَٰتِهِۦۚ إِنَّهُۥ عَلِيمُۢ بِذَاتِ ٱلصُّدُورِ ٢٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4282,25,'486',42,'Ash-Shūra','الشُّوري',6,7,23,'وَهُوَ ٱلَّذِي يَقۡبَلُ ٱلتَّوۡبَةَ عَنۡ عِبَادِهِۦ وَيَعۡفُواْ عَنِ ٱلسَّيِّـَٔاتِ وَيَعۡلَمُ مَا يَفۡعَلُونَ ٢٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4283,25,'486',42,'Ash-Shūra','الشُّوري',7,9,24,'وَيَسۡتَجِيبُ ٱلَّذِينَ ءَامَنُواْ وَعَمِلُواْ ٱلصَّٰلِحَٰتِ وَيَزِيدُهُمُۥ مِن فَضۡلِهِۦۚ وَٱلۡكَٰفِرُونَ لَهُمُۥ عَذَابٞ شَدِيدٞ ٢٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4284,25,'486',42,'Ash-Shūra','الشُّوري',9,10,25,'۞ وَلَوۡ بَسَطَ ٱللَّهُ ٱلرِّزۡقَ لِعِبَادِهِۦ لَبَغَوۡاْ فِي ٱلۡأَرۡضِ وَلَٰكِن يُنزِلُ بِقَدَرٖ مَّا يَشَآءُۚ اِ۪نَّهُۥ بِعِبَادِهِۦ خَبِيرُۢ بَصِيرٞ ٢٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4285,25,'486',42,'Ash-Shūra','الشُّوري',10,11,26,'وَهُوَ ٱلَّذِي يُنزِلُ ٱلۡغَيۡثَ مِنۢ بَعۡدِ مَا قَنَطُواْ وَيَنشُرُ رَحۡمَتَهُۥۚ وَهُوَ ٱلۡوَلِيُّ ٱلۡحَمِيدُ ٢٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4286,25,'486',42,'Ash-Shūra','الشُّوري',12,13,27,'وَمِنۡ ءَايَٰتِهِۦ خَلۡقُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِ وَمَا بَثَّ فِيهِمَا مِن دَآبَّةٖۚ وَهُوَ عَلَىٰ جَمۡعِهِمُۥ إِذَا يَشَآءُ قَدِيرٞ ٢٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4287,25,'486',42,'Ash-Shūra','الشُّوري',13,14,28,'وَمَا أَصَٰبَكُمُۥ مِن مُّصِيبَةٖ فَبِمَا كَسَبَتۡ أَيۡدِيكُمُۥ وَيَعۡفُواْ عَن كَثِيرٖ ٢٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4288,25,'486',42,'Ash-Shūra','الشُّوري',14,15,29,'وَمَا أَنتُمُۥ بِمُعۡجِزِينَ فِي ٱلۡأَرۡضِۖ وَمَا لَكُمُۥ مِن دُونِ ٱللَّهِ مِن وَلِيّٖ وَلَا نَصِيرٖ ٢٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4289,25,'487',42,'Ash-Shūra','الشُّوري',1,2,30,'وَمِنۡ ءَايَٰتِهِ ٱلۡجَوَارِۦ فِي ٱلۡبَحۡرِ كَٱلۡأَعۡلَٰمِ إِن يَشَأۡ يُسۡكِنِ ٱلرِّيحَ فَيَظۡلَلۡنَ رَوَاكِدَ عَلَىٰ ظَهۡرِهِۦۚ إِنَّ فِي ذَٰلِكَ لَأٓيَٰتٖ لِّكُلِّ صَبَّارٖ شَكُورٍ ٣٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4290,25,'487',42,'Ash-Shūra','الشُّوري',3,3,31,'أَوۡ يُوبِقۡهُنَّ بِمَا كَسَبُواْ وَيَعۡفُ عَن كَثِيرٖ ٣١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4291,25,'487',42,'Ash-Shūra','الشُّوري',3,4,32,'وَيَعۡلَمَ ٱلَّذِينَ يُجَٰدِلُونَ فِي ءَايَٰتِنَا مَا لَهُمُۥ مِن مَّحِيصٖ ٣٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4292,25,'487',42,'Ash-Shūra','الشُّوري',4,6,33,'فَمَا أُوتِيتُمُۥ مِن شَيۡءٖ فَمَتَٰعُ ٱلۡحَيَوٰةِ ٱلدُّنۡيَاۚ وَمَا عِندَ ٱللَّهِ خَيۡرٞ وَأَبۡقَىٰ لِلَّذِينَ ءَامَنُواْ وَعَلَىٰ رَبِّهِمُۥ يَتَوَكَّلُونَ ٣٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4293,25,'487',42,'Ash-Shūra','الشُّوري',6,7,34,'وَٱلَّذِينَ يَجۡتَنِبُونَ كَبَٰٓئِرَ ٱلۡإِثۡمِ وَٱلۡفَوَٰحِشَ وَإِذَا مَا غَضِبُواْ هُمُۥ يَغۡفِرُونَ ٣٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4294,25,'487',42,'Ash-Shūra','الشُّوري',7,8,35,'وَٱلَّذِينَ ٱسۡتَجَابُواْ لِرَبِّهِمُۥ وَأَقَامُواْ ٱلصَّلَوٰةَ وَأَمۡرُهُمُۥ شُورَىٰ بَيۡنَهُمُۥ وَمِمَّا رَزَقۡنَٰهُمُۥ يُنفِقُونَ ٣٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4295,25,'487',42,'Ash-Shūra','الشُّوري',8,9,36,'وَٱلَّذِينَ إِذَا أَصَابَهُمُ ٱلۡبَغۡيُ هُمُۥ يَنتَصِرُونَ ٣٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4296,25,'487',42,'Ash-Shūra','الشُّوري',9,10,37,'وَجَزَٰٓؤُاْ سَيِّئَةٖ سَيِّئَةٞ مِّثۡلُهَاۖ فَمَنۡ عَفَا وَأَصۡلَحَ فَأَجۡرُهُۥ عَلَى ٱللَّهِۚ إِنَّهُۥ لَا يُحِبُّ ٱلظَّٰلِمِينَ ٣٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4297,25,'487',42,'Ash-Shūra','الشُّوري',10,11,38,'وَلَمَنِ ٱنتَصَرَ بَعۡدَ ظُلۡمِهِۦ فَأُوْلَٰٓئِكَ مَا عَلَيۡهِمُۥ مِن سَبِيلٍ ٣٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4298,25,'487',42,'Ash-Shūra','الشُّوري',11,13,39,'إِنَّمَا ٱلسَّبِيلُ عَلَى ٱلَّذِينَ يَظۡلِمُونَ ٱلنَّاسَ وَيَبۡغُونَ فِي ٱلۡأَرۡضِ بِغَيۡرِ ٱلۡحَقِّۚ أُوْلَٰٓئِكَ لَهُمُۥ عَذَابٌ أَلِيمٞ ٣٩');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4299,25,'487',42,'Ash-Shūra','الشُّوري',13,14,40,'وَلَمَن صَبَرَ وَغَفَرَ إِنَّ ذَٰلِكَ لَمِنۡ عَزۡمِ ٱلۡأُمُورِ ٤٠');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4300,25,'487',42,'Ash-Shūra','الشُّوري',14,15,41,'وَمَن يُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِن وَلِيّٖ مِّنۢ بَعۡدِهِۦۗ وَتَرَى ٱلظَّٰلِمِينَ لَمَّا رَأَوُاْ ٱلۡعَذَابَ يَقُولُونَ هَلۡ إِلَىٰ مَرَدّٖ مِّن سَبِيلٖ ٤١');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4301,25,'488',42,'Ash-Shūra','الشُّوري',1,4,42,'وَتَرَىٰهُمُۥ يُعۡرَضُونَ عَلَيۡهَا خَٰشِعِينَ مِنَ ٱلذُّلِّ يَنظُرُونَ مِن طَرۡفٍ خَفِيّٖۗ وَقَالَ ٱلَّذِينَ ءَامَنُواْ إِنَّ ٱلۡخَٰسِرِينَ ٱلَّذِينَ خَسِرُواْ أَنفُسَهُمُۥ وَأَهۡلِيهِمُۥ يَوۡمَ ٱلۡقِيَٰمَةِۗ أَلَا إِنَّ ٱلظَّٰلِمِينَ فِي عَذَابٖ مُّقِيمٖ ٤٢');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4302,25,'488',42,'Ash-Shūra','الشُّوري',4,5,43,'وَمَا كَانَ لَهُمُۥ مِنۡ أَوۡلِيَآءَ يَنصُرُونَهُمُۥ مِن دُونِ ٱللَّهِۗ وَمَن يُضۡلِلِ ٱللَّهُ فَمَا لَهُۥ مِن سَبِيلٍ ٤٣');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4303,25,'488',42,'Ash-Shūra','الشُّوري',5,7,44,'ٱسۡتَجِيبُواْ لِرَبِّكُمُۥ مِن قَبۡلِ أَن يَأۡتِيَ يَوۡمٞ لَّا مَرَدَّ لَهُۥ مِنَ ٱللَّهِۚ مَا لَكُمُۥ مِن مَّلۡجَإٖ يَوۡمَئِذٖ وَمَا لَكُمُۥ مِن نَّكِيرٖ ٤٤');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4304,25,'488',42,'Ash-Shūra','الشُّوري',7,10,45,'فَإِنۡ أَعۡرَضُواْ فَمَا أَرۡسَلۡنَٰكَ عَلَيۡهِمُۥ حَفِيظًاۖ إِنۡ عَلَيۡكَ إِلَّا ٱلۡبَلَٰغُۗ وَإِنَّا إِذَا أَذَقۡنَا ٱلۡإِنسَٰنَ مِنَّا رَحۡمَةٗ فَرِحَ بِهَاۖ وَإِن تُصِبۡهُمُۥ سَيِّئَةُۢ بِمَا قَدَّمَتۡ أَيۡدِيهِمُۥ فَإِنَّ ٱلۡإِنسَٰنَ كَفُورٞ ٤٥');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4305,25,'488',42,'Ash-Shūra','الشُّوري',10,12,46,'لِّلَّهِ مُلۡكُ ٱلسَّمَٰوَٰتِ وَٱلۡأَرۡضِۚ يَخۡلُقُ مَا يَشَآءُۚ يَهَبُ لِمَن يَشَآءُ اِ۪نَٰثٗا وَيَهَبُ لِمَن يَشَآءُ ٱلذُّكُورَ ٤٦');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4306,25,'488',42,'Ash-Shūra','الشُّوري',12,13,47,'أَوۡ يُزَوِّجُهُمُۥ ذُكۡرَانٗا وَإِنَٰثٗاۖ وَيَجۡعَلُ مَن يَشَآءُ عَقِيمًاۚ إِنَّهُۥ عَلِيمٞ قَدِيرٞ ٤٧');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4307,25,'488',42,'Ash-Shūra','الشُّوري',13,15,48,'۞ وَمَا كَانَ لِبَشَرٍ أَن يُكَلِّمَهُ ٱللَّهُ إِلَّا وَحۡيًا أَوۡ مِن وَرَآيِٕ حِجَابٍ أَوۡ يُرۡسِلَ رَسُولٗا فَيُوحِيَ بِإِذۡنِهِۦ مَا يَشَآءُۚ اِ۪نَّهُۥ عَلِيٌّ حَكِيمٞ ٤٨');
INSERT INTO `` (`id`,`jozz`,`page`,`sura_no`,`sura_name_en`,`sura_name_ar`,`line_start`,`line_end`,`aya_no`,`aya_text`) VALUES (4308,25,'489',42,'Ash-Shūra','الشُّوري',1,3,49,'وَكَذَٰلِكَ أَوۡحَيۡنَا إِلَيۡكَ رُوحٗا مِّنۡ أَمۡرِنَاۚ مَا كُنتَ تَدۡرِي مَا ٱلۡكِتَٰبُ وَلَا ٱلۡإِيمَٰنُ وَلَٰكِن جَعَلۡنَٰهُۥ نُورٗا نَّهۡدِي بِهِۦ مَن نَّشَآءُ مِنۡ عِبَادِنَاۚ وَإِنَّكَ لَتَهۡدِي إِلَىٰ صِۜرَٰطٖ مُّسۡتَقِيمٖ ٤٩');
